INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,1,1,' جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,1,2,' اﷲ ناھايىتى شەپقەتلىك ۋە مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,1,3,' قىيامەت كۈنىنىڭ ئىگىسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,1,4,' (رەببىمىز) ساڭىلا ئىبادەت قىلىمىز ۋە سەندىنلا ياردەم تىلەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,1,5,' بىزنى توغرا يولغا باشلىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,1,6,' سەن ئىنئام قىلغانلارنىڭ يولىغا (باشلىغىن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,1,7,' غەزىپىڭگە يولۇققانلارنىڭ ۋە ئازغانلارنىڭ يولىغا ئەمەس،');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,1,'ئەلىف، لام، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,2,' بۇ كىتابتا (يەنى قۇرئاندا) ھېچ شەك يوق، (ئۇ) تەقۋادارلارغا يېتەكچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,3,' ئۇلار غەيبكە ئىشىنىدۇ، نامازنى ئادا قىلىدۇ، ئۇلار بىز بەرگەن مال ـ مۈلۈكتىن (خۇدا يولىغا) سەرپ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,4,' ئۇلار ساڭا نازىل قىلىنغان كىتابقا، سەندىن ئىلگىرىكى (پەيغەمبەرلەرگە) نازىل قىلىنغان كىتابلارغا ئىشىنىدۇ ۋە ئاخىرەتكە شەكسىز ئىشىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,5,' ئەنە شۇلار پەرۋەردىگارىنىڭ توغرا يولىدا بولغۇچىلاردۇر، ئەنە شۇلار بەختكە ئېرىشكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,6,' شۈبھىسىزكى، كاپىرلارنى ئاگاھلاندۇرامسەن، ئاگاھلاندۇرمامسەن، بەرىبىر، ئۇلار ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,7,' اﷲ ئۇلارنىڭ دىللىرىنى ۋە قۇلاقلىرىنى پېچەتلىۋەتكەن (ئىماننىڭ نۇرى كىرمەيدۇ)، ئۇلارنىڭ كۆزلىرى پەردىلەنگەن (ھەقىقەتنى كۆرمەيدۇ)، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,8,' كىشىلەر ئارىسىدا اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشەندۇق دېگۈچىلەر بار، ھەقىقەتتە ئۇلار ئىشەنمەيدۇ (يەنى ئاغزىدا ئىشەندۇق دېگىنى بىلەن، كۆڭلىدە ئىشەنمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,9,' ئۇلار اﷲ نى ۋە مۆمىنلەرنى ئالدىماقچى بولىدۇ، ھەقىقەتتە ئۇلار تۇيماستىن ئۆزلىرىنىلا ئالدايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,10,' ئۇلارنىڭ دىللىرىدا كېسەل (يەنى مۇناپىقلىق ۋە شەكلىنىش) بار، اﷲ ئۇلارنىڭ كېسىلىنى كۈچەيتىۋەتتى؛ يالغان سۆزلىگەنلىكلىرى (يەنى يالغاندىن ئىماننى دەۋا قىلغانلىقلىرى ۋە اﷲ نىڭ ئايەتلىرىنى مەسخىرە قىلغانلىقلىرى) ئۈچۈن ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,11,' ئۇلارغا: «يەر يۈزىدە بۇزغۇنچىلىق قىلماڭلار» دېيىلسە، «بىز ئىسلاھ قىلغۇچىلارمىز» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,12,' بىلىڭلاركى، ئۇلار ھەقىقەتەن بۇزغۇنچىلاردۇر، لېكىن بۇنى ئۆزلىرى تۇيمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,13,' ئۇلارغا (يەنى مۇناپىقلارغا): «ئىمان ئېيتقان كىشىلەردەك (يەنى ساھابىلەردەك چىن كۆڭلۈڭلار بىلەن) ئىمان ئېيتىڭلار» دېيىلسە، «بىز ئىمان ئېيتقان ئەخمەقلەرگە ئوخشاش ئىمان ئېيتامدۇق؟» دەيدۇ بىلىڭلاركى ئۇلارنىڭ ئۆزلىرىلا ئەخمەقلەر، لېكىن (بۇنى) ئۇلار تۇيمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,14,' ئۇلار مۆمىنلەر بىلەن ئۇچراشقىنىدا: «بىز ئىمان ئېيتتۇق» دېيىشىدۇ، شاياتۇنلىرى (يەنى مۇناپىق كاتتىباشلىرى) بىلەن يالغۇز جايدا تېپىشقاندا بولسا «بىز ھەقىقەتەن سىلەر بىلەن بىللىمىز، پەقەت (تىلىمىزنىڭ ئۇچىدىلا ئىمان ئېيتىپ قويۇپ) مۆمىنلەرنى مەسخىرە قىلىمىز» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,15,' مەسخىرە قىلغانلىقلىرى ئۈچۈن اﷲ ئۇلارنى جازالايدۇ، ئۇلارنى گۇمراھلىقلىرىدا قويۇپ بېرىدۇكى، ئۇلار تېڭىرقىغان ھالدا يۈرۈشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,16,' ئەنە شۇلار ھىدايەتنى بېرىپ گۇمراھلىقنى ئالدى شۇڭا سودىسى پايدا كەلتۈرمىدى، ئۇلار ھىدايەت تاپقۇچى بولمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,17,' ئۇلار گوياكى (كېچىدە ئىسسىنىش ۋە يورۇقلۇق ئېلىش ئۈچۈن) ئوت ياققان كىشىلەرگە ئوخشايدۇ، ئوت ئۇلارنىڭ ئەتراپىنى يورۇتقاندا، اﷲ ئۇلارنىڭ (ئوتىنىڭ) يورۇقىنى ئۆچۈرۈۋەتتى، ئۇلارنى (ئەتراپىدىكى ھېچ نەرسىنى) كۆرەلمەيدىغان قاراڭغۇلۇقتا قالدۇردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,18,' ئۇلار گاستۇر (يەنى گاس ئاڭلىمىغاندەك، ياخشىلىقنى ئاڭلىمايدۇ)، گاچىدۇر (يەنى گاچا سۆزلىيەلمىگەندەك، ياخشى ئىشنى سۆزلىيەلمەيدۇ)، كوردۇر (يەنى كور بولۇپ قالغاندەك، توغرا يولنى كۆرمەيدۇ)، شۇڭا ئۇلار (گۇمراھلىقتىن) قايتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,19,' ياكى ئۇلار زۇلمەتلىك، گۈلدۈرمامىلىق ۋە چاقماقلىق قاتتىق يامغۇردا قالغان، گويا چاقماق سوقۇۋېتىپ ئۆلۈپ كېتىشتىن قورقۇپ، قۇلاقلىرىنى بارماقلىرى بىلەن ئېتىۋالغان كىشىلەرگە ئوخشايدۇ اﷲ كاپىرلارنى (يەنى ئۇلارنىڭ ھەممە ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,20,' چاقماق چاققاندا ئۇلار كۆز نۇرىدىن ئايرىلىپ قالغىلى تاس قالىدۇ ئۇلار چاقماق يورۇقىدا مېڭىۋالىدۇ؛ قاراڭغۇلۇق قاپلىغاندا تۇرۇپ قالىدۇ، اﷲ خالىسا ئىدى، ئۇلارنى ئاڭلاش ۋە كۆرۈش قۇۋۋىتىدىن ئەلۋەتتە مەھرۇم قىلاتتى اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,21,' ئى ئىنسانلار! تەقۋادارلاردىن بولۇشۇڭلار ئۈچۈن، سىلەرنى ۋە سىلەردىن بۇرۇنقىلار (يەنى ئۆتكەنكى ئۈممەتلەر) نى ياراتقان پەرۋەردىگارىڭلارغا ئىبادەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,22,' اﷲ سىلەرگە زېمىننى تۆشەك ۋە ئاسماننى بىنا (يەنى يۇلتۇزلارنىڭ زىچلىقىدا بىناغا ئوخشاش) قىلىپ بەردى، بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، سىلەرگە رىزىق بولۇش ئۈچۈن يامغۇر ئارقىلىق تۈرلۈك مېۋىلەرنى ئۆستۈرۈپ بەردى ئەمدى اﷲ قا شېرىك كەلتۈرمەڭلار، ھالبۇكى، سىلەر (اﷲ نىڭ شېرىكى يوقلۇقىنى) بىلىپ تۇرىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,23,' بەندىمىز (مۇھەممەد) گە بىز نازىل قىلغان قۇرئاندىن شەكلەنسەڭلار، قۇرئانغا ئوخشاش بىرەر سۈرىنى مەيدانغا چىقىرىپ بېقىڭلار (قۇرئانغا تەئەررۇز قىلىشتا) اﷲ تىن باشقا ياردەمچىڭلارنىڭ ھەممىسىنى (ياردەمگە) چاقىرىڭلار، (قۇرئان ئىنساننىڭ سۆزى دېگەن گېپىڭلاردا) راستچىل بولساڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,24,' ئەگەر مۇنداق قىلالمىساڭلار ـ ھەرگىزغۇ قىلالمايسىلەر ـ كاپىرلار ئۈچۈن تەييارلانغان، ئىنسان ۋە تاشلار يېقىلغۇ بولغان دوزاختىن ساقلىنىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,25,' (ئى مۇھەممەد!) ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا ئۇلارنىڭ ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرىدىغانلىقى بىلەن خۇش خەۋەر بەرگىن ئۇلار جەننەتنىڭ بىرەر مېۋىسىدىن رىزىقلاندۇرۇلغان چاغدا: «بۇنىڭ بىلەن بۇرۇن (دۇنيادىمۇ) رىزىقلاندۇرۇلغان ئىدۇق» دەيدۇ ئۇلارغا كۆرۈنۈشى دۇنيانىڭ مېۋىلىرىگە ئوخشايدىغان، تەمى ئوخشىمايدىغان مېۋىلەر بېرىلىدۇ جەننەتلەردە ئۇلارغا پاك جۈپتىلەر (يەنى ھۆرلەر) بېرىلىدۇ، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,26,' اﷲ ھەقىقەتەن پاشا ۋە ئۇنىڭدىنمۇ كىچىك نەرسىلەرنى مىسال قىلىپ كەلتۈرۈشتىن تارتىنىپ قالمايدۇ، مۆمىنلەر بولسا ئۇنى پەرۋەردىگارى تەرىپىدىن نازىل بولغان ھەقىقەت دەپ بىلىدۇ؛ كاپىرلار بولسا: «اﷲ بۇ نەرسىلەرنى مىسال قىلىپ كەلتۈرۈشتىن نېمىنى ئىرادە قىلىدۇ؟» دەيدۇ اﷲ بۇ مىسال بىلەن نۇرغۇن كىشىلەرنى (ئۇنى ئىنكار قىلغانلىقتىن) ئازدۇرىدۇ ۋە نۇرغۇن كىشىلەرنى (ئۇنى تەستىقلىغانلىقتىن) ھىدايەت قىلىدۇ؛ شۇ ئارقىلىق پەقەت پاسىقلارنىلا ئازدۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,27,' پاسىقلار اﷲ ۋەدە ئالغاندىن كېيىن بەرگەن ۋەدىسىنى بۇزىدۇ اﷲ ئۇلاشقا بۇيرۇغان شەيئىنى ئۈزۈپ قويىدۇ (يەنى سىلە ـ رەھىم قىلمايدۇ)، يەر يۈزىدە بۇزۇقچىلىق قىلىدۇ؛ ئەنە شۇلار زىيان تارتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,28,' اﷲ نى قانداقمۇ ئىنكار قىلىسىلەركى، جانسىز ئىدىڭلار، سىلەرگە جان بەردى (يەنى ئاتاڭلارنىڭ پۇشتىدا، ئاناڭلارنىڭ قارنىدا ئابىمەنىي ئىدىڭلار)، كېيىن (ئەجىلىڭلار توشقاندا) اﷲ جېنىڭلارنى ئالىدۇ، ئاندىن كېيىن تىرىلدۈرىدۇ، ئاندىن كېيىن اﷲ نىڭ دەرگاھىغا (ھېساب بېرىش ئۈچۈن) قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,29,' اﷲ يەر يۈزىدىكى ھەممە نەرسىنى سىلەر (نىڭ پايدىلىنىشىڭلار) ئۈچۈن ياراتتى، ئاندىن ئاسماننى يارىتىشقا يۈزلىنىپ، ئۇنى قۇسۇرسىز يەتتە ئاسمان قىلىپ تاماملىدى اﷲ ھەرنەرسىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,30,' ئۆز ۋاقتىدا پەرۋەردىگارىڭ پەرىشتىلەرگە: «مەن يەر يۈزىدە خەلىپە (يەنى ئورۇنباسار) يارىتىمەن» دېدى پەرىشتىلەر: «يەر يۈزىدە بۇزۇقچىلىق قىلىدىغان، قان تۆكىدىغان (شەخسنى) خەلىپە قىلامسەن؟ ھالبۇكى بىز سىنى پاك دەپ مەدھىيىلەيمىز، مۇقەددەس دەپ مەدھىيىلەيمىز» دېدى اﷲ: «مەن ھەقىقەتەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,31,' اﷲ پۈتۈن شەيئىلەرنىڭ ناملىرىنى ئادەمگە ئۆگەتتى، ئاندىن ئۇلارنى پەرىشتىلەرگە كۆرسىتىپ: «(خەلىپە بولۇشقا ئادەمگە قارىغاندا بىز ھەقلىق دەيدىغان قارشىڭلاردا) راستچىل بولساڭلار، بۇ شەيئىلەرنىڭ ناملىرىنى ماڭا ئېيتىپ بېرىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,32,' پەرىشتىلەر: «سېنى (پۈتۈن كەمچىلىكلەردىن) پاك دەپ تونۇيمىز بىز سەن بىلدۈرگەندىن باشقىنى بىلمەيمىز، ھەقىقەتەن سەن ھەممىنى بىلگۈچىدۇرسەن، ھېكمەت بىلەن ئىش قىلغۇچىدۇرسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,33,' اﷲ: «ئى ئادەم! ئۇلارغا بۇ نەرسىلەرنىڭ ناملىرىنى ئېيتىپ بەرگىن» دېدى ئادەم ئۇلارغا بۇ نەرسىلەرنىڭ ناملىرىنى ئېيتىپ بەرگەن چاغدا، اﷲ: «مەن سىلەرگە، ئاسمانلاردىكى ۋە زېمىندىكى غەيبلەرنى ھەقىقەتەن بىلىپ تۇرىمەن، ئاشكارا ۋە يوشۇرۇن ئىشىڭلارنى بىلىپ تۇرىمەن، دېمىگەنمىدىم» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,34,' ئۆز ۋاقتىدا پەرىشتىلەرگە: «ئادەمگە سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى، ئىبلىس (سەجدە قىلىشتىن) باش تارتتى، تەكەببۇرلۇق قىلدى، ئۇ كاپىرلاردىن بولۇپ كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,35,' بىز(ئادەمگە): «ئى ئادەم! سەن خوتۇنۇڭ (يەنى ھەۋۋا) بىلەن جەننەتتە تۇرۇڭلار! جەننەتتىكى نەرسىلەردىن خالىغىنىڭلارچە كەڭتاشا يەپ ـ ئىچىڭلار، بۇ دەرەخكە يېقىنلاشماڭلار (يەنى مېۋىسىدىن يېمەڭلار)، بولمىسا (ئۆزۈڭلارغا) زۇلۇم قىلغۇچىلاردىن بولۇپ قالىسىلەر» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,36,' شەيتان ئۇ ئىككىسىنى تېيىلدۇردى (يەنى مەنئى قىلىنغان دەرەخنىڭ مېۋىسىنى يېگۈزۈپ خاتالاشتۇردى)، تۇرۇۋاتقان جەننەتتىن چىقاردى (ئادەمگە، ھەۋۋاغا، ئىبلىسقا) «بىر ـ بېرىڭلار بىلەن ئۆچەكەشكەن ھالدا (جەننەتتىن يەر يۈزىگە) چۈشۈڭلار، يەر يۈزىدە ۋاقىتلىق (ئەجىلىڭلار توشقۇچە) يەرلىشىڭلار ۋە (نېمەتلەردىن) بەھرىمەن بولۇڭلار» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,37,' ئادەم پەرۋەردىگارىدىن بىرقانچە سۆز تەلىم ئالدى (يەنى گۇناھىنىڭ كەچۈرۈلۈشى ئۈچۈن ئوقۇيدىغان دۇئا ئۇنىڭغا ئىلھام ئارقىلىق بىلدۈرۈلدى)، اﷲ ئۇنىڭ تەۋبىسىنى قوبۇل قىلدى اﷲ ھەقىقەتەن تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر (بەندىلىرىگە) ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,38,' (ئۇلارغا) «ھەممىڭلار بۇ يەردىن چۈشۈڭلار، سىلەرگە مەن تەرەپتىن بىر يول كۆرسەتكۈچى (يەنى پەيغەمبەر) كېلىدۇ، يولۇمغا ئەگەشكەنلەرگە (ئاخىرەتتە) قورقۇنچ ۋە غەم ـ قايغۇ بولمايدۇ» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,39,' كاپىرلار ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,40,' ئى ئسرائىل ئەۋلادى! مېنىڭ سىلەرگە بەرگەن نېمىتىمنى ياد ئېتىڭلار، ماڭا بەرگەن (ئىمان ئېيتىش ۋە تائەت ـ ئىبادەت قىلىش ھەققىدىكى) ۋەدەڭلارغا ۋاپا قىلىڭلار، مەنمۇ سىلەرگە قىلغان (ساۋاب بېرىش ھەققىدىكى) ۋەدەمگە ۋاپا قىلىمەن، مەندىنلا قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,41,' مەن نازىل قىلغان تەۋراتنى تەستىق قىلغۇچى قۇرئانغا ئىمان كەلتۈرۈڭلار، قۇرئاننى بىرىنچى بولۇپ ئىنكار قىلغۇچى بولماڭلار، ئايەتلىرىمنى ئەرزان باھادا سېتىۋەتمەڭلار، ماڭىلا تەقۋادارلىق قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,42,' ھەقنى باتىلغا (يەنى اﷲ نازىل قىلغان ھەقىقەتنى ئۆزۈڭلار ئويدۇرۇپ چىققان نەرسىگە) ئارىلاشتۇرماڭلار ، ھەقنى بىلىپ تۇرۇپ يوشۇرماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,43,' نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، رۇكۇ قىلغۇچىلار بىلەن بىللە رۇكۇ قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,44,' كىشىلەرنى ياخشى ئىشقا بۇيرۇپ ئۆزۈڭلارنى ئۇنتۇمسىلەر؟ ھالبۇكى، سىلەر كىتاب (يەنى تەۋرات) نى ئوقۇپ تۇرىسىلەر ، چۈشەنمەمسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,45,' سەۋر قىلىش، ناماز ئوقۇش ئارقىلىق (اﷲ تىن) ياردەم تىلەڭلار ناماز اﷲ تىن قورققۇچىلار (يەنى كەمتەرلىك بىلەن اﷲ قا بويسۇنغۇچىلار) دىن باشقىلارغا ھەقىقەتەن مۇشەققەتلىك ئىشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,46,' اﷲ تىن قورققۇچىلار پەرۋەردىگارىغا چوقۇم مۇلاقات بولىدىغانلىقىغا ۋە اﷲ نىڭ دەرگاھىغا چوقۇم قايتىپ بارىدىغانلىقىغا جەزمەن ئىشىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,47,' ئى ئىسرائىل ئەۋلادى! سىلەرگە بەرگەن نېمىتىمنى ۋە سىلەرنى (بىر زامانلاردا) تامام جاھان ئەھلىدىن ئۈستۈن قىلغانلىقىمنى ياد ئېتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,48,' كىشى كىشىگە ئەسقاتمايدىغان، ھېچ كىشىنىڭ شاپائىتى قوبۇل قىلىنمايدىغان، ھېچ كىشىدىن فىدىيە ئېلىنمايدىغان، ئۇلار (يەنى كاپىرلار ۋە گۇناھكار بەندىلەر) ياردەمگە ئېرىشەلمەيدىغان كۈندىن قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,49,' ئۆز ۋاقتىدا سىلەرنى قاتتىق قىينىغان، ئوغۇللىرىڭلارنى بوغۇزلاپ، قىزلىرىڭلارنى (ئىشقا سېلىش ئۈچۈن) تىرىك قالدۇرغان پىرئەۋن گۇرۇھىدىن سىلەرنى قۇتقۇزدۇق بۇ، پەرۋەردىگارىڭلارنىڭ (ياخشى ئادەم بىلەن يامان ئادەمنى ئاجرىتىدىغان) چوڭ سىنىقى ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,50,' ئۆز ۋاقتىدا دېڭىزنى يېرىپ سىلەرنى قۇتقۇزدۇق (يەنى دېڭىزنىڭ سۈيىنى ئوتتۇرىدىن بۆلۈپ، قۇرۇق يول ھاسىل قىلىپ، شۇ يەردىن سىلەرنى سالامەت ئۆتكۈزدۇق) ۋە پىرئەۋن گۇرۇھىنى كۆز ئالدىڭلاردا غەرق قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,51,' ئۆز ۋاقتىدا مۇساغا  كېچە (كۈتكەندىن كېيىن تەۋراتنى نازىل قىلىشنى) ۋەدە قىلدۇق ئۇ سىلەردىن ئايرىلغاندىن كېيىن، ئۆزۈڭلارغا زۇلۇم قىلىپ، موزاينى مەبۇد قىلىۋالدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,52,' شۇنىڭدىن كېيىن، شۈكۈر قىلسۇن دەپ، سىلەرنى ئەپۇ قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,53,' ئۆز ۋاقتىدا سىلەرنى ھىدايەت تاپسۇن دەپ، مۇساغا ھەقنى باتىلدىن ئايرىغۇچى كىتابنى (يەنى تەۋراتنى) بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,54,' ئۆز ۋاقتىدا مۇسا ئۆز قەۋمىگە: «ئى قەۋمىم! سىلەر موزاينى مەبۇد قىلىپ، ھەقىقەتەن ئۆزۈڭلارغا زۇلۇم قىلدىڭلار، ياراتقۇچىڭلارغا تەۋبە قىلىڭلار، موزايغا چوقۇنمىغانلار چوقۇنغانلارنى ئۆلتۈرسۇن» دېدى مۇنداق قىلىش ياراتقۇچىڭلارنىڭ دەرگاھىدا سىلەر ئۈچۈن ياخشىدۇر اﷲ تەۋبەڭلارنى قوبۇل قىلدى اﷲ تەۋبىنى ھەقىقەتەن بەكمۇ قوبۇل قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,55,' ئۆز ۋاقتىدا: «ئى مۇسا! اﷲ نى ئوپئوچۇق كۆرمىگىچە ساڭا ھەرگىز ئىشەنمەيمىز» دېدىڭلار ـ دە، (سىلەرگە نازىل بولغان ئازابنى) كۆرۈپ تۇرغىنىڭلار ھالدا سىلەرنى چاقماق سوقتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,56,' ئاندىن كېيىن، سىلەرنى شۈكۈر قىلسۇن، دەپ ئۆلگىنىڭلاردىن كېيىن قايتا تىرىلدۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,57,' سىلەرگە بۇلۇتنى سايىۋەن قىلىپ بەردۇق، سىلەرگە تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق (بىز سىلەرگە) «رىزىق قىلىپ بەرگەن پاك، شېرىن نەرسىلەردىن يەپ ـ ئىچىڭلار» (دېدۇق) ئۇلار (نېمەتلىرىمىزگە نانكورلۇق قىلىش بىلەن) بىزگە ئەمەس، پەقەت ئۆزلىرىگىلا زۇلۇم قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,58,' ئۆز ۋاقتىدا (مۇسا ئارقىلىق ئەجدادلىرىڭلارغا): «بۇ شەھەرگە (يەنى بەيتۇلمۇقەددەسكە) كىرىڭلار، ئۇ يەردە خالىغىنىڭلارچە كەڭتاشا يەپ ـ ئىچىڭلار، (بەيتۇلمۇقەددەس) دەرۋازىسىدىن (شۈكۈر قىلىش يۈزىسىدىن) سەجدە قىلغان ھالدا كىرىڭلار، (پەرۋەردىگارىمىز) گۇناھلىرىمىزنى كەچۈرگىن دەڭلار، گۇناھىڭلارنى مەغپىرەت قىلىمىز، ياخشىلارغا تېخىمۇ ئارتۇق ساۋاب بېرىمىز» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,59,' زۇلۇم قىلغۇچىلار ئۆزلىرىگە سۆزلەنگەن سۆزنى باشقا سۆزگە ئۆزگەرتىۋەتتى، زۇلۇم قىلغۇچىلار يولدىن چىققانلىقلىرى (يەنى اﷲ قا ئاسىيلىق قىلغانلىقلىرى) ئۈچۈن، ئۇلارغا ئاسماندىن ئازاب چۈشۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,60,' ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمى ئۈچۈن سۇ تەلەپ قىلغان ئىدى، بىز ئۇنىڭغا: «ھاساڭ بىلەن تاشنى ئۇرغىن» دېدۇق تاشتىن  بۇلاق ئېتىلىپ چىقتى، ھەممە ئادەم (يەنى قەبىلە) ئۆزىنىڭ سۇ ئىچىدىغان ئورنىنى بىلدى، (ئۇلارغا) «اﷲ بەرگەن رىزىقتىن يەڭلار ۋە ئىچىڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلماڭلار» (دېدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,61,' ئۆز ۋاقتىدا (سىنا چۆلىدە تەرەنجىبىن ۋە بۆدۈنە بىلەنلا ئوزۇقلىنىۋاتقىنىڭلاردا) سىلەر: «ئى مۇسا! بىز بىر خىل يېمەكلىككە چىداپ تۇرالمايمىز، بىز ئۈچۈن پەرۋەردىگارىڭغا دۇئا قىلغىن، بىزگە زېمىننىڭ كۆكتاتلىرىدىن تەرخەمەك، سامساق، يېسىمۇق ۋە پىيازلارنى ئۆستۈرۈپ بەرسۇن» دېدىڭلار، مۇسا: «سىلەر ياخشىنى ناچارغا تېگىشەمسىلەر؟ (يەنى پىياز، سامساق، كۆكتاتنى تەرەنجىبىن بىلەن بۆدۈنىدىن ئارتۇق كۆرەمسىلەر؟) بىرەر شەھەرگە كىرىڭلار، (شۇ يەردە) تىلىگىنىڭلار بار» دېدى ئۇلار خارلىقتا، موھتاجلىقتا قالدى اﷲ نىڭ غەزىپىگە تېگىشلىك بولدى بۇ ئۇلارنىڭ اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى، پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى تۈپەيلىدىن بولدى بۇ اﷲ قا ئاسىيلىق قىلغانلىقلىرى ۋە ھەددىدىن ئاشقانلىقلىرى تۈپەيلىدىن بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,62,' شۈبھىسىزكى، مۆمىنلەر، يەھۇدىيلار، ناسارالار (ئارىسىدىن) ۋە پەرىشتىلەرگە، يۇلتۇزلارغا چوقۇنغۇچىلار ئارىسىدىن اﷲ قا، ئاخىرەت كۈنىگە ئىمان كەلتۈرگەن ۋە ياخشى ئەمەللەرنى قىلغانلار پەرۋەردىگارىنىڭ قېشىدا ئەجرىدىن بەھرىمەن بولىدۇ ئۇلارغا (ئاخىرەتتە) قورقۇنچ ۋە غەم ـ قايغۇ يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,63,' ئۆز ۋاقتىدا، بىز سىلەردىن چىن ۋەدە ئالغان ۋە تۇر تېغىنى ئۈستۈڭلارغا تىكلەپ قويغان، سىلەرگە: «(دۇنيادا ھالاك بولۇشتىن، ئاخىرەتتە ئازابقا قېلىشتىن) ساقلىنىشىڭلار ئۈچۈن بىز بەرگەن كىتابنى مەھكەم تۇتۇڭلار (يەنى تەۋراتقا ئەمەل قىلىڭلار)، ئۇنىڭدىكى ئەھكاملارنى ئېسىڭلاردا ساقلاڭلار» (دېگەن ئىدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,64,' شۇنىڭدىن كېيىن (بەرگەن ۋەدەڭلاردىن) يۈز ئۆرۈدۈڭلار، سىلەرگە اﷲ نىڭ پەزلى ـ مەرھەمىتى بولمىسا ئىدى، چوقۇم زىيان تارتقۇچىلاردىن بولاتتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,65,' ئاراڭلاردىكى شەنبە كۈنى (بېلىق تۇتماسلىق) توغرىسىدىكى شەرىئەت چەكلىمىسىدىن چىقىپ كەتكۈچىلەرنى (قانداق قىلغانلىقىمىزنى)، ئەلۋەتتە بىلىسىلەر، بىز ئۇلارغا: «خار مايمۇن بولۇپ كېتىڭلار» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,66,' ئۇنى (يەنى ئۇلارغا بېرىلگەن بۇ جازانى) زامانداشلىرى ۋە كېيىنكىلەر ئۈچۈن ئىبرەت، تەقۋادارلار ئۈچۈن ۋەز ـ نەسىھەت قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,67,' ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمىگە: «اﷲ ھەقىقەتەن سىلەرنى بىر كالا بوغۇزلاشقا بۇيرۇيدۇ» دېگەن ئىدى ئۇلار: «بىزنى مەسخىرە قىلىۋاتامسەن؟» دېدى مۇسا: «جاھىللاردىن (يەنى مەسخىرە قىلغۇچىلاردىن) بولۇپ قېلىشتىن اﷲ قا سېغىنىپ پاناھ تىلەيمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,68,' ئۇلار: «بىز ئۈچۈن پەرۋەردىگارىڭغا ئىلتىجا قىلغىنكى، بىزگە قانداق كالا ئىكەنلىكىنى بايان قىلسۇن» دېدى مۇسا: «اﷲ ئۇنى قېرىمۇ ئەمەس، ياشمۇ ئەمەس، ئوتتۇرا ياش بولسۇن دەپ ئېيتتى، سىلەر بۇيرۇلغاننى قىلىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,69,' ئۇلار: «بىز ئۈچۈن پەرۋەردىگارىڭغا ئىلتىجا قىلغىنكى، بىزگە ئۇنىڭ رەڭگىنى بايان قىلسۇن» دېدى مۇسا ئېيتتىكى، «اﷲ ھەقىقەتەن ئۇنىڭ رەڭگى قارىغۇچىلارنى زوقلاندۇرىدىغان ساپسېرىق بولسۇن دېدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,70,' ئۇلار: «پەرۋەردىگارىڭغا ئىلتىجا قىلغىن، ئۇنىڭ قانداق ئىكەنلىكىنى بىزگە بايان قىلسۇن، (يەنى دېھقانچىلىققا ئىشلىتىلگەن كالىمۇ، ياكى سەمرىتىش ئۈچۈن بېقىلغان كالىمۇ؟) بۇ بىزگە مۈجمەل بولۇپ قالدى (بۇ ئېنىق بولىدىغان بولسا) خۇدا خالىسا (ئۇنى) بىز چوقۇم تاپىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,71,' مۇسا: «اﷲ ئۇنىڭ يەر ھەيدەپ، ئېكىن سۇغۇرۇپ كۆندۈرۈلگەن بولماسلىقىنى، بېجىرىم، ئالىسى يوق (يەنى رەڭگىدە سېرىقلىقتىن باشقا رەڭ يوق) بولۇشىنى ئېيتتى» دېدى ئۇلار: «ئەمدى تولۇق بايان قىلدىڭ» دېدى ئۇلار (كالىنى) تېپىپ كېلىپ بوغۇزلىدى، (ئۇلار سوئال سوراۋەرگەنلىكتىن) بۇنى ئورۇنلىيالمىغىلى تاس قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,72,' ئۆز ۋاقتىدا سىلەر بىر كىشىنى ئۆلتۈرگەن ۋە (قاتىلنىڭ كىملىكى) توغرىسىدا دەتالاش قىلىشقان ئىدىڭلار سىلەر يوشۇرماقچى بولغان نەرسىنى (يەنى قاتىلنىڭ ئىشىنى) اﷲ ئاشكارىلىغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,73,' (سىلەرگە مۇسا ئارقىلىق) دېدۇقكى، كالىنىڭ بىر پارچىسى بىلەن ئۆلۈكنى ئۇرۇڭلار (ئۇ تىرىلىپ قاتىلنى ئېيتىپ بېرىدۇ)، اﷲ ئۆلۈكلەرنى شۇنداق (يەنى كۆز ئالدىڭلاردا بۇ ئۆلۈكنى تىرىلدۈرگەندەك) تىرىلدۈرىدۇ، سىلەرنى چۈشەنسۇن دپ قۇدرىتىنىڭ ئالامەتلىرىنى سىلەرگە كۆرسىتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,74,' (ئى يەھۇدىيلار جامائەسى!) شۇنىڭدىن كېيىن (يەنى شانلىق مۆجىزىلەرنى كۆرگەندىن كېيىن) دىلىڭلار قېتىپ كەتتى، تاشتەك ياكى تاشتىنمۇ قاتتىق قېتىپ كەتتى تاشلار ئارىسىدا ئىچىدىن (سۇ ئېتىلىپ چىقىپ) ئېرىقلار ھاسىل بولىدىغانلىرى بار، يېرىلىپ ئارىسىدىن سۇ چىقىدىغانلىرىمۇ بار؛ اﷲ تىن قورقۇپ (تاغ چوققىلىرىدىن) دومىلاپ چۈشىدىغانلىرىمۇ بار (دېمەك، تاش يۇمشايدۇ، سىلەرنىڭ دىلىڭلار بولسا يۇمشىمايدۇ ۋە تەسىرلەنمەيدۇ)، اﷲ قىلمىشىڭلاردىن غاپىل ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,75,' (ئى مۆمىنلەر جامائەسى! دەۋىتىڭلار ئارقىلىق) يەھۇدىيلارنىڭ ئىمان ئېيتىشىنى ئۈمىد قىلامسىلەر؟ ھالبۇكى، ئۇلاردىن بىر گۇرۇھ ئادەم، اﷲ نىڭ كالامىنى ئاڭلاپ چۈشەنگەندىن كېيىن ، ئۇنى بىلىپ تۇرۇپ (قەستەن) ئۆزگەرتىۋەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,76,' ئۇلار مۆمىنلەر بىلەن ئۇچراشقانلىرىدا: «ئىمان ئېيتتۇق» دەيتتى بىر ـ بىرى بىلەن يالغۇز جايدا تېپىشقانلىرىدا: «پەرۋەردىگارىڭلار قېشىدا مۆمىنلەر سىلەرگە قارشى پاكىت كۆرسەتسۇن دەپ اﷲ سىلەرگە بىلدۈرگەن ۋەھيىنى (يەنى مۇھەممەد ئەلەيھسسالامنىڭ تەۋراتتىكى سۈپەتلىرىنى) ئۇلارغا سۆزلەپ بېرەمسىلەر؟ (بۇنى) چۈشەنمەمسىلەر!» دەيتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,77,' يەھۇدىيلار ئۆزلىرىنىڭ يوشۇرۇن ۋە ئاشكارا ئىشلىرىنى اﷲ نىڭ بىلىپ تۇرىدىغانلىقىنى ئۇقمامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,78,' ئۇلارنىڭ ساۋاتسىزلىرى كىتابنى (يەنى تەۋراتنى) ئۇقمايدۇ، (ئەھبارلىرى ئويدۇرۇپ چىققان) ئويدۇرمىلارنىلا بىلىدۇ، گۇمان بىلەنلا ئىش قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,79,' كىتابنى ئۆز قوللىرى بىلەن يېزىپ ئۇنى ئاز پۇلغا سېتىش (يەنى دۇنيانىڭ ئازغىنا مەنپەئىتى) ئۈچۈن، بۇ اﷲ تەرىپىدىن نازىل بولغان دېگۈچىلەرگە ۋاي! قولى بىلەن يازغانلىرى (يەنى تەۋراتنى ئۆزگەرتكەنلىكلىرى) ئۈچۈن ئۇلارغا ۋاي! (بۇنىڭ بىلەن) ئېرىشكەن (ھارام) نەرسىلىرى ئۈچۈن ئۇلارغا ۋاي');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,80,' ئۇلار: «بىزنى دوزاخ ئوتى ساناقلىق كۈنلەرلا كۆيدۈرىدۇ» دەيدۇ (ئۇلارغا) «سىلەرگە اﷲ شۇنداق قىلىشقا ۋەدە بەرگەنمۇ، (ۋەدە بەرگەن بولسا) اﷲ ۋەدىسىگە ھەرگىزمۇ خىلاپلىق قىلمايدۇ، ياكى ئۆزۈڭلار بىلمەيدىغاننى اﷲ نامىدىن دەۋاتامسىلەر؟» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,81,' ئۇنداق ئەمەس (سىلەرنى دوزاخ ئوتى كۆيدۈرىدۇ)، گۇناھ قىلغان ۋە گۇناھقا چۆمۈپ كەتكەنلەر ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,82,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار ئەھلى جەننەتتۇر ئۇلار جەننەتتە مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,83,' ئۆز ۋاقتىدا بىز ئىسرائىل ئەۋلادىدىن اﷲ تىن باشقىغا ئىبادەت قىلماسلىققا، ئاتا ـ ئانىغا، خىش ـ ئەقرىبالارغا، يېتىملەرگە، مىسكىنلەرگە ياخشىلىق قىلىشقا، كىشىلەرگە ياخشى سۆز قىلىشقا، ناماز ئۆتەشكە، زاكات بېرىشكە ئەھدە ئالدۇق ئاراڭلاردىكى ئازغىنا كىشىدىن باشقىڭلار ئەھدىدىن يېنىۋالدىڭلار، سىلەر ھەمىشە (ئەھدەڭلاردىن) يېنىۋالىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,84,' ئۆز ۋاقتىدا بىر ـ بىرىڭلارنىڭ قېنىنى تۆكمەسلىككە، بىر ـ بىرىڭلارنى يۇرتلىرىڭلاردىن ھەيدەپ چىقارماسلىققا، سىلەردىن چىن ئەھدە ئالغان ئىدۇق كېيىن بۇنى (يەنى ئەھدىنى) ئېتىراپ قىلغان ئىدىڭلار، بۇنىڭغا ئۆزۈڭلار گۇۋاھسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,85,' كېيىن سىلەر (ئەھدىنى بۇزۇپ) بىر ـ بىرىڭلارنى ئۆلتۈردۈڭلار، ئىچىڭلاردىن بىر تۈركۈم كىشىلەرنى يۇرتىدىن ھەيدەپ چىقاردىڭلار، گۇناھ ۋە دۈشمەنلىك قىلىش بىلەن، ئۇلارنىڭ قارشى تەرىپىدىكىلەرگە ياردەم بەردىڭلار، ئۇلار ئەسىر چۈشۈپ كەلسە، فىدىيە بېرىپ قۇتقۇزۇۋالىسىلەر، ھالبۇكى، ئۇلارنى يۇرتلىرىدىن ھەيدەپ چىقىرىش سىلەرگە ھارام قىلىنغان ئىدى (يەنى بىر ـ بىرىڭلارنى ئۆلتۈرۈش ۋە يۇرتىدىن ھەيدەپ چىقىرىشنى راۋا كۆرۈسىلەرۇ ، ئۇلارنىڭ ئەسىرگە چۈشۈپ دۈشمەنلىرىڭلارنىڭ قولىدا بولۇپ قېلىشىنى راۋا كۆرمەيسىلەر) سىلەر كىتابنىڭ (يەنى تەۋراتنىڭ) بىر قىسىم ئەھكاملىرىغا ئىشىنىپ، بىر قىسىم ئەھكاملىرىنى ئىنكار قىلامسىلەر؟ سىلەردىن شۇنداق قىلغانلارنىڭ جازاسى پەقەت ھاياتىي دۇنيادا خارلىققا قېلىش، قىيامەت كۈنى قاتتىق ئازابقا دۇچار بولۇشتۇر اﷲ قىلمىشىڭلاردىن غاپىل ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,86,' ئەنە شۇلار ئاخىرەتنى بېرىپ، دۇنيا تىرىكچىلىكىنى سېتىۋالغان كىشىلەردۇر شۇنىڭ ئۈچۈن، ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ، ئۇلارغا ياردەممۇ قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,87,' شەك ـ شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋرات) بەردۇق ئۇنىڭدىن كېيىن ئارقىمۇئارقا پەيغەمبەرلەر ئەۋەتتۇق، مەريەم ئوغلى ئىساغا مۆجىزىلەر بەردۇق، ھەمدە ئۇنى روھۇلقۇددۇس (يەنى جىبرىئىل) بىلەن يۆلىدۇق ھەرقاچان بىرەر پەيغەمبەر كۆڭلۈڭلارغا ياقمايدىغان بىرنەرسە ئېلىپ كەلسە، تەكەببۇرلۇق قىلىۋېرەمسىلەر؟ بىر قىسىم پەيغەمبەرلەرنى ئىنكار قىلدىڭلار، يەنە بىر قىسىم پەيغەمبەرلەرنى ئۆلتۈردۈڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,88,' ئۇلار: «دىللىرىمىز پەردىلەنگەن» دېدى ئۇنداق ئەمەس، ئۇلارنىڭ كۇفرى سەۋەبلىك اﷲ ئۇلارغا لەنەت قىلدى، ئۇلاردىن ئىمان ئېيتىدىغانلار ناھايىتى ئاز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,89,' ئۇلارغا اﷲ تەرىپىدىن ئۇلاردىكى كىتاب (تەۋرات) نى تەستىقلايدىغان كىتاب (قۇرئان) نازىل بولغان چاغدا (ئۇنىڭغا ئىشەنمىدى) ئىلگىرى ئۇلار (مۇھەممەد ئەلەيھىسسالامنى ۋاسىتە قىلىپ) كاپىرلارغا قارشى ئۆزلىرىگە ياردەم كېلىشىنى تىلەيتتى، ئۇلار بىلىدىغان (يەنى تەۋراتتا سۈپىتى بايان قىلىنغان) پەيغەمبەر كەلگەن چاغدا ئۇنى ئىنكار قىلدى اﷲ كاپىرلارغا (يەنى پەيغەمبەرلەرنىڭ تۈگەنچىسىنى ئىنكار قىلغان يەھۇدىيلارغا) لەنەت قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,90,' يەھۇدىيلارنىڭ اﷲ نازىل قىلغان قۇرئاننى ئىنكار قىلىشى اﷲ نىڭ ئۆز پەزلىنى (يەنى پەيغەمبەرلىكنى) خالىغان بەندىسىگە چۈشۈرگەنلىكىگە ھەسەت قىلىش يۈزىسىدىندۇر ئۇلارنىڭ ئۆزلىرىنى (يەنى ئىمانىنى) كۇفرىغا سېتىشى ئەجەب يامان ئىشتۇر ئۇلار ئۈستى ـ ئۈستىگە غەزەپكە تېگىشلىك بولدى كاپىرلار خارلىغۇچى ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,91,' ئۇلارغا: «اﷲ نازىل قىلغان كىتابقا (يەنى قۇرئانغا) ئىمان كەلتۈرۈڭلار» دېيىلسە، ئۇلار: «ئۆزىمىزگە نازىل قىلىنغان كىتابقا (يەنى تەۋراتقا) ئىمان كەلتۈرىمىز» دەيدۇ ئۇنىڭدىن كېيىنكى كىتابقا (يەنى قۇرئانغا) ئىشەنمەيدۇ، ھالبۇكى، ئۇ (يەنى قۇرئان) ھەقتۇر، ئۇلارنىڭ قولىدىكى كىتابنى تەستىق قىلغۇچىدۇر (ئۇلارغا) «سىلەر (تەۋراتقا) ئىشىنىدىغان بولساڭلار، ئىلگىرى نېمە ئۈچۈن اﷲ نىڭ پەيغەمبەرلىرىنى ئۆلتۈردۈڭلار؟» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,92,' مۇسا سىلەرگە ھەقىقەتەن ئېنىق مۆجىزىلەرنى كەلتۈردى، مۇسا (تۇر تېغىغا كەتكەن) دىن كېيىن، ئۆزۈڭلارغا زۇلۇم قىلىپ موزاينى مەبۇد قىلىۋالدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,93,' ئۆز ۋاقتىدا بىز سىلەردىن (تەۋراتقا ئەمەل قىلىشقا) چىن ۋەدە ئالغان ئىدۇق، سىلەرگە: «مەن نازىل قىلغان كىتابنى مەھكەم تۇتۇڭلار (بولمىسا ئۈستۈڭلارغا تاغنى تاشلايمىز)» دەپ تۇر تېغىنى ئۈستۈڭلارغا تىكلىدۇق ۋە (تەۋراتقا) قۇلاق سېلىڭلار (دېدۇق) ئۇلار: «سۆزۈڭگە قۇلاق سالدۇق ئەمرىڭگە ئاسىيلىق قىلدۇق» دېيىشتى ئۇلارنىڭ كۇفرى تۈپەيلىدىن دىللىرىغا موزاينىڭ مۇھەببىتى سىڭىپ كەتتى ئۇلارغا: «ئەگەر سىلەر مۆمىن بولساڭلار، ئىمانىڭلار سىلەرنى ئەجەب يامان ئىشقا بۇيرۇيدىكىنا؟ (سىلەر مۆمىن ئەمەس، چۈنكى ئىمان موزايغا چوقۇنۇشقا بۇيرۇمايدۇ)» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,94,' ئۇلارغا: «ئەگەر ئاخىرەت يۇرتى (يەنى جەننەت) اﷲ نىڭ قېشىدا (سىلەر گۇمان قىلغاندەك) باشقىلارغا ئەمەس، يالغۇز سىلەرگىلا خاس بولسا، (سىلەرنى جەننەتكە ئۇلاشتۇرىدىغان) ئۆلۈمنى ئارزۇ قىلىپ بېقىڭلار! (جەننەت بىزگىلا خاس دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,95,' ئۇلار قىلغان يامان ئەمەللىرى سەۋەبلىك، ئۆلۈمنى ھەرگىزمۇ ئارزۇ قىلمايدۇ اﷲ زالىملارنى ئوبدان بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,96,' شۈبھىسىزكى، ئۇلارنى ھاياتقا ھەممە كىشىدىن، ھەتتا مۇشرىكلاردىنمۇ ھېرىس كۆرۈسەن؛ ھەر بىرى مىڭ يىل ئۆمۈر كۆرۈشنى ئارزۇ قىلىدۇ، ئۇزۇن ئۆمۈر كۆرۈش ئۇلارنى ئازابتىن قۇتۇلدۇرۇپ قالالمايدۇ اﷲ ئۇلار نىڭ قىلمىشلىرىنى ئېنىق كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,97,' ئېيتقىنكى، «جىبرىئىلغا دۈشمەن بولغان ئادەم (اﷲ قا دۈشمەندۇر)، چۈنكى ئۇ (يەنى جىبرىئىل) اﷲ نىڭ ئەمرى بىلەن ئۆزىدىن ئىلگىرىكىنى (يەنى ساماۋى كىتابلارنى) تەستىق قىلغۇچى، توغرا يول كۆرسەتكۈچى، مۆمىنلەرگە بېشارەت بەرگۈچى قۇرئاننى سېنىڭ قەلبىڭگە نازىل قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,98,' كىمكى اﷲ نى، اﷲ نىڭ پەرىشتىلىرىنى، پەيغەمبەرلىرىنى، جىبرىئىلنى، مىكائىلنى دۈشمەن تۇتىدىكەن (اﷲ نى دۈشمەن تۇتقان بولىدۇ)، شۈبھىسىزكى، اﷲ كاپىرلارنى دۈشمەن تۇتىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,99,' شەك ـ شۈبھىسىزكى، ساڭا بىز روشەن ئايەتلەرنى نازىل قىلدۇق، ئۇلارنى پەقەت توغرا يولدىن چىققانلارلا ئىنكار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,100,' ھەر قاچان ئۇلار بىرەر ئەھدە تۈزۈشسە، بىر تۈركۈمى ئۇنى بۇزىدۇ، بەلكى ئۇلارنىڭ (يەنى يەھۇدىيلارنىڭ) تولىسى (تەۋراتقا) ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,101,' ئۇلارغا اﷲ تەرىپىدىن قوللىرىدىكى كىتابنى تەستىقلايدىغان بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلسە، (ئۇنىڭ پەيغەمبەرلىك دەلىللىرىنى) بىلمەيدىغاندەك، اﷲ نىڭ كىتابىنى (يەنى تەۋراتنى) ئارقىسىغا چۆرۈۋېتىدۇ (يەنى تەۋراتنىڭ سۆزلىرىگە ئەمەل قىلمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,102,' ئۇلار سۇلايماننىڭ پادىشاھلىق زامانىدىكى شەيتانلارنىڭ (سېھرىگەرلىكىگە ئائىت) سۆزلىرىگە ئەگەشتى سۇلايمان (سېھرىگەر بولغىنى ۋە سېھىر ئۆگىنىش بىلەن) كاپىر بولغىنى يوق، لېكىن شەيتانلار كىشىلەرگە سېھىر ئۆگىتىپ كاپىر بولدى ئۇلار بابىلدىكى ھارۇت ـ مارۇت دېيىلىدىغان ئىككى پەرىشتىگە نازىل قىلىنغان سېھىرنى ئۈگىتەتتى (بۇ ئىككى پەرىشتە) بىز كىشىلەرنى سىنايمىز كاپىر بولمىغىن، دېمىگىچە ھېچ كىشىگە سېھىر ئۆگەتمەيتتى ئۇلار بۇ ئىككىسىدىن ئەر ـ خوتۇننى بىر ـ بىرىدىن ئايرىۋېتىدىغان نەرسىلەرنى (يەنى سېھىرگەرلىكنى) ئۆگىنەتتى، ئۇلار (سېھىر ئارقىلىق) اﷲ نىڭ رۇخسىتىسىز ھېچ كىشىگە زىيان يەتكۈزەلمەيتتى ھالبۇكى، ئۇلار ئۆزلىرىگە زىيىنى بار، پايدىسى يوق نەرسىنى ئۆگىنەتتى يەھۇدىيلار (تەۋراتنى تاشلاپ) سېھىرنى سېتىۋالغۇچىغا ئاخىرەتتە (اﷲ نىڭ رەھمىتىدىن ۋە جەننىتىدىن) ھېچ نېسىۋە يوق ئىكەنلىكىنى ئوبدان بېلەتتى، ئۇلار جانلىرىنىڭ بەدىلىگە سېتىۋالغان نەرسىسىنىڭ (يەنى ئۆزلىرى ئۈچۈن سېھىر ئۆگىنىشنى تاللىشىنىڭ) ئەجەبمۇ يامان ئىش ئىكەنلىكىنى كاشكى بىلسە ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,103,' ئەگەر ئۇلار ئىمان كەلتۈرسە (سېھىرنى تاشلاپ) اﷲ تىن قورقسا، (ئۇلار ئۈچۈن) اﷲ نىڭ دەرگاھىدىكى ساۋاب ئەلۋەتتە ياخشى ئىدى، كاشكى ئۇلار بۇنى بىلسە ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,104,' ئى مۆمىنلەر! (پەيغەمبەرگە) رائىنا دەپ خىتاب قىلماي، ئۇنزۇرنا دەپ خىتاب قىلىڭلار، (سۆزىگە) قۇلاق سېلىڭلار، كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,105,' ئەھلى كىتاب ۋە مۇشرىكلاردىن بولغان كاپىرلار پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە بىرەر ياخشىلىقنىڭ چۈشۈشىنى ياقتۇرمايدۇ، اﷲ ئۆزىنىڭ خالىغان بەندىسىگە پەيغەمبەرلىكنى خاس قىلىدۇ، اﷲ چوڭ پەزل ئىگىسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,106,' قانداقلا بىر ئايەتنى ئەمەلدىن قالدۇرساق ياكى ئۇنتۇلدۇرساق، (ئورنىغا) ئۇنىڭدىن ئارتۇق ياكى شۇنىڭغا ئوخشاش بىر ئايەتنى كەلتۈرىمىز اﷲ نىڭ ھەر نەرسىگە قادىر ئىكەنلىكىنى بىلمەمسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,107,' ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا مەنسۇپ ئىكەنلىكىنى بىلمەمسەن؟ سىلەرگە اﷲ تىن باشقا ھېچقانداق دوست ۋە مەدەتكار يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,108,' ئۇلار (يەنى يەھۇدىيلار) ئىلگىرى مۇسادىن سوئال سورىغاندەك، سىلەرمۇ پەيغەمبىرىڭلاردىن سوئال سورىماقچى بولامسىلەر؟ كىمكى ئىمانىنى كۇفرىغا تېگىشسە، ئۇ ھەقىقەتەن توغرا يولدىن ئازغان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,109,' ئەھلى كىتاب ئىچىدىكى نۇرغۇن كىشىلەر ئۆزلىرىگە ھەقىقەت (يەنى دىنىڭلارنىڭ ھەقلىقى) ئاشكارا بولغاندىن كېيىن، ھەسەت قىلىش يۈزىسىدىن سىلەرنى ئىمانىڭلاردىن قايتۇرۇپ كاپىر قىلىشنى ئارزۇ قىلىدۇ اﷲ نىڭ ئەمرى كەلگەنگە قەدەر (يەنى اﷲ سىلەرگە ئۇلار بىلەن ئۇرۇش قىلىشقا رۇخسەت قىلغۇچە) ئۇلارنى ئەپۇ قىلىڭلار ۋە كەچۈرۈڭلار اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,110,' نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، ئۆزۈڭلار ئۈچۈن ئىشلىگەن ھەرقانداق ياخشى ئەمەل بولسا، اﷲ نىڭ دەرگاھىدا ئۇنىڭ ساۋابىنى تاپىسىلەر، اﷲ ھەقىقەتەن قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,111,' ئۇلار: «يەھۇدىي ياكى ناسارا بولمىغان ئادەم (يەنى يەھۇدىيلار يەھۇدىي بولمىغان ئادەم، ناسارالار ناسارا بولمىغان ئادەم) ھەرگىزمۇ جەننەتكە كىرمەيدۇ» دېيىشتى بۇ، ئۇلارنىڭ قۇرۇق ئارزۇسىدۇر «ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار، دەلىلىڭلارنى كەلتۈرۈڭلار» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,112,' ئۇنداق ئەمەس، كىمكى ياخشى ئەمەللەرنى قىلغان ھالدا ئۆزىنى اﷲ قا تاپشۇرىدىكەن (يەنى اﷲ نىڭ ئەمرىگە بويسۇنۇپ، ئەمەلىنى خالىس اﷲ ئۈچۈن قىلىدىكەن)، ئۇ پەرۋەردىگارىنىڭ دەرگاھىدا ساۋابتىن بەھرىمەن بولىدۇ ئۇلارغا (ئاخىرەتتە) ھېچ قورقۇنچ ۋە غەم ـ قايغۇ بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,113,' يەھۇدىيلار: «ناسارالارنىڭ ھېچقانداق ئاساسى يوق (يەنى ئۇلار توغرا دىندا ئەمەس)» دېدى ناسارالارمۇ: «يەھۇدىيلارنىڭ ھېچقانداق ئاساسى يوق (يەنى ئۇلار توغرا دىندا ئەمەس)» دېدى ھالبۇكى، ئۇلار كىتابنى (يەنى يەھۇدىيلار تەۋراتنى، ناسارالار ئىنجىلنى) ئوقۇيدۇ، بىلىمسىز ئادەملەر (يەنى ئەرەب مۇشرىكلىرى) مۇ ئۇلار دېگەندەك دېدى، اﷲ قىيامەت كۈنى ئۇلارنىڭ ئۆزئارا ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,114,' اﷲ نىڭ مەسجىدلىرىدە اﷲ نىڭ نامىنىڭ ياد قىلىنىشىنى توسقان ۋە ئۇلارنى ۋەيران قىلىشقا ئورۇنغانلاردىنمۇ زالىم كىشى بارمۇ؟ مەسجىدلەرگە ئۇلارنىڭ پەقەت قورققان ھالدىلا كىرىشى لايىق ئىدى، ئۇلار دۇنيادا رەسۋا بولىدۇ ئاخىرەتتە چوڭ ئازابقا (يەنى دوزاخ ئازابىغا) دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,115,' مەشرىقمۇ، مەغرىبمۇ (يەنى پۈتۈن يەر يۈزى) اﷲ نىڭدۇر، قايسى تەرەپكە يۈزلەنسەڭلەرمۇ، ئۇ اﷲ نىڭ تەرىپى (يەنى سىلەرگە ئىختىيار قىلغان قىبلىسى) دۇر اﷲ (نىڭ مەغپىرىتى) ھەقىقەتەن كەڭدۇر، (اﷲ) ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,116,' ئۇلار (يەنى يەھۇدىيلار، ناسارالار، مۇشرىكلار): «اﷲ نىڭ بالىسى بار» دەيدۇ اﷲ (ئۇلارنىڭ گۇمان قىلغان نەرسىسىدىن) پاكتۇر، ئۇنداق ئەمەس، ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ نىڭدۇر، ھەممىسى ئۇنىڭغا بويسۇنغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,117,' اﷲ ئاسمانلار ۋە زېمىننى يوقتىن بار قىلغۇچىدۇر بىر ئىشنى ۋۇجۇدقا كەلتۈرۈشنى ئىرادە قىلسا، «ۋۇجۇدقا كەل» دەيدۇ ـ دە، ئۇ ۋۇجۇدقا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,118,' بىلمەيدىغانلار (يەنى قۇرەيش كاپىرلىرى): «اﷲ نېمە ئۈچۈن بىز بىلەن سۆزلەشمەيدۇ؟ ياكى نېمە ئۈچۈن بىزگە (راست پەيغەمبەرلىكنىڭ دەلىلى سۈپتىدە) بىرەر مۆجىزە كەلمەيدۇ؟» دەيدۇ ئۇلاردىن بۇرۇن ئۆتكەنلەرمۇ (پەيغەمبەرلىرىگە) شۇنداق سۆزنى قىلغان ئىدى؛ ئۇلارنىڭ دىللىرى بىر ـ بىرىگە ئوخشايدۇ ئايەتلىرىمىزنى جەزمەن ئىشىنىدىغان (يەنى ھەقىقەت ئىزدەيدىغان) قەۋم ئۈچۈن ھەقىقەتەن بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,119,' بىز سېنى ھەقىقەتەن (مۆمىنلەرگە جەننەت بىلەن) خوش خەۋەر بەرگۈچى، (كاپىرلارنى دوزاخ ئازابىدىن) ئاگاھلاندۇرغۇچى قىلىپ ھەق (دىن) بىلەن ئەۋەتتۇق سەن ئەھلى دوزاخ (يەنى كاپىرلاردىن ئىمان ئېيتمىغانلار) توغرىسىدا جاۋابكار ئەمەسسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,120,' سەن يەھۇدىيلار ۋە ناسارالارنىڭ دىنىغا كىرمىگىچە ئۇلار سەندىن ھەرگىزمۇ رازى بولمايدۇ (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «توغرا يول پەقەت اﷲ نىڭ يولىدۇر» ساڭا ئىلىم كەلگەندىن كېيىن (يەنى پاكىتلىق ھەقىقەت ساڭا ئاشكارا بولغاندىن كېيىن)، ئەگەر سەن ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگىشىدىغان بولساڭ، (سېنى اﷲ نىڭ ئازابىدىن قۇتقۇزىدىغان) ھېچقانداق دوستمۇ بولمايدۇ، مەدەتكارمۇ بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,121,' بىز ئاتا قىلغان كىتابنى تېگىشلىك رەۋىشتە ئوقۇيدىغانلار بار، ئەنە شۇلار ئۇنىڭغا ئىشىنىدۇ كىملەركى كىتابنى (يەنى قۇرئاننى) ئىنكار قىلىدىكەن، ئۇلار زىيان تارتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,122,' ئى ئىسرائىل ئەۋلادى! سىلەرگە بەرگەن نېمىتىمنى ۋە سىلەرنى (بىر زامانلاردا) ھەممە جاھان ئەھلىدىن ئۈستۈن قىلغانلىقىمنى ياد ئېتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,123,' بىراۋغا بىراۋ قىلچىلىك ياردەم قىلالمايدىغان، ھېچ كىشىنىڭ فىدىيىسى قوبۇل قىلىنمايدىغان، ھېچ كىشىنىڭ شاپائىتى پايدا قىلمايدىغان، ئۇلارغا (يەنى كاپىرلار ۋە گۇناھكارلارغا) ياردەم قىلىنمايدىغان كۈندىن قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,124,' ئۆز ۋاقتىدا ئىبراھىمنى پەرۋەردىگارى بىر قانچە ئەمرلەر (يەنى شەرىئەت تەكلىپلىرى) بىلەن سىنىدى، ئىبراھىم ئۇلارنى بەجا كەلتۈردى اﷲ ئۇنىڭغا: «سېنى چوقۇم كىشىلەرگە (دىندا) پېشۋا قىلىمەن» دېدى، ئىبراھىم: «مېنىڭ بىر قىسىم ئەۋلادىمنىمۇ پېشۋا قىلساڭ» دېدى اﷲ: «مېنىڭ (پېشۋا قىلىش) ئەھدەمگە زالىملار (يەنى كاپىرلار) ئېرىشەلمەيدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,125,' ئۆز ۋاقتىدا بىز كەبىنى كىشىلەر ئۈچۈن جەم بولىدىغان جاي ۋە ئامان جاي قىلىپ بەردۇق (كىشىلەرگە) ماقامى ئىبراھىمنى نامازگاھ قىلىڭلار (يەنى شۇ يەردە ناماز ئوقۇڭلار دېدۇق) ئىبراھىم بىلەن ئىسمائىلغا ئۆيۈمنى (يەنى كەبەمنى) تاۋاپ قىلغۇچىلار، ئېتىكاپتا ئولتۇرغۇچىلار، رۇكۇ قىلغۇچىلار، سەجدە قىلغۇچىلار ئۈچۈن پاك تۇتۇشنى بۇيرۇدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,126,' ئۆز ۋاقتىدا ئىبراھىم: «پەرۋەردىگارىم! بۇ يەرنى (يەنى مەككىنى) تېنىچ شەھەر قىلىپ بەرگىن، ئاھالىسىدىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقانلارنى تۈرلۈك مېۋىلەر بىلەن رىزىقلاندۇرغىن» دېدى اﷲ: «كاپىر بولغان ئادەمنىمۇ (رىزىقتىن) ئازغىنا مۇددەت (يەنى ھاياتلىقىدا) بەھرىمەن قىلىمەن، ئاندىن كېيىن ئۇنى (ئاخىرەتتە) دوزاخقا ھەيدەيمەن» دېدى (بۇ) نېمىدېگەن يامان ئاقىۋەت!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,127,' ئۆز ۋاقتىدا ئىبراھىم بىلەن ئىسمائىل كەبىنىڭ ئۇلىنى قوپۇرۇۋېتىپ: «پەرۋەردىگارىمىز بىزنىڭ (خىزمىتىمىزنى) قوبۇل قىلغىن، سەن ھەقىقەتەن (دۇئايىمىزنى) ئاڭلاپ تۇرغۇچىسەن، (نىيىتىمىزنى) بىلىپ تۇرغۇچىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,128,' پەرۋەردىگارىمىز! ئىككىمىزنى ئۆزۈڭگە ئىتائەتمەن قىلغىن، بىزنىڭ ئەۋلادلىرىمىزدىنمۇ ئۆزۈڭگە ئىتائەتمەن ئۈممەت چىقارغىن، بىزگە ھەجىمىزنىڭ قائىدىلىرىنى بىلدۈرگىن، تەۋبىمىزنى قوبۇل قىلغىن، چۈنكى سەن تەۋبىنى ناھايىتى قوبۇل قىلغۇچىسەن، ناھايىتى مېھرىبانسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,129,' پەرۋەردىگارىمىز! ئۇلارنىڭ ئىچىدىن ئايەتلىرىڭنى ئۇلارغا تىلاۋەت قىلىپ بېرىدىغان، كىتابىڭنى (يەنى قۇرئاننى)، ھېكمەتنى (يەنى پاك سۈننەتنى) ئۇلارغا ئۆگىتىدىغان، ئۇلارنى (مۇشرىكلىك ۋە گۇناھلاردىن) پاك قىلىدىغان بىر پەيغەمبەر ئەۋەتكىن، ھەقىقەتەن سەن غالىبسەن، ھېكمەت بىلەن ئىش قىلغۇچىسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,130,' ئۆزىنى ئەخمەق قىلغانلاردىن باشقا كىم ئىبراھىمنىڭ دىنىدىن يۈز ئۆرۈيدۇ، ھەقىقەتەن بىز ئۇنى بۇ دۇنيادا (پەيغەمبەرلىككە) تاللىدۇق شەك ـ شۈبھىسىزكى، ئاخىرەتتە ئۇ ياخشىلار قاتارىدا بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,131,' ئۆز ۋاقتىدا پەرۋەردىگارى ئۇنىڭغا: «(پەرۋەردىگارىڭنىڭ ئەمرىگە) ئىتائەت قىلغىن» دېدى ئۇ: «ئالەملەرنىڭ پەرۋەردىگارىغا ئىتائەت قىلدىم» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,132,' ئىبراھىم ۋە يەئقۇب ئۆز ئوغۇللىرىغا ۋەسىيەت قىلىپ: «ئى ئوغۇللىرىم! اﷲ سىلەرگە مۇشۇ دىنىنى (يەنى ئىسلام دىنىنى) تاللىدى، پەقەت مۇسۇلمان پىتىڭلارچە ئۆلۈڭلار (يەنى ئىمانىڭلاردا مەھكەم تۇرۇڭلار، تاكى ئىمان بىلەن كېتىڭلار)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,133,' يەئقۇب جان ئۈزۈۋاتقان ۋاقتىدا سىلەر يېنىدا بارمىدىڭلار؟ شۇ چاغدا يەئقۇب ئوغۇللىرىدىن: «مەن ئۆلگەندىن كېيىن سىلەر كىمگە ئىبادەت قىلىسىلەر؟» دەپ سورىدى ئۇلار: «سېنىڭ ئىلاھىڭ ۋە ئاتا ـ بوۋىلىرىڭ ئىبراھىم، ئىسمائىل، ئىسھاقلارنىڭ ئىلاھى بولغان بىر اﷲ قا ئىبادەت قىلىمىز، بىز ئۇنىڭغا ئىتائەت قىلغۇچىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,134,' ئۇلار ئۆتكەن بىر ئۈممەتتۇر، ئۇلارنىڭ ئەمەللىرى (نىڭ ساۋابى) ئۆزلىرى ئۈچۈندۇر سىلەرنىڭ ئەمەلىڭلار (نىڭمۇ ساۋابى) ئۆزۈڭلار ئۈچۈندۇر، ئۇلارنىڭ قىلمىشىغا سىلەر جاۋابكار ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,135,' ئۇلار: «يەھۇدىي ياكى ناسارا بولۇڭلار، توغرا يول تاپىسىلەر» دەيدۇ «ئۇنداق ئەمەس، بىز ئىبراھىمنىڭ ئىسلامغا ئۇيغۇن دىنىغا ئەگىشىمىز، ئۇ مۇشرىكلاردىن ئەمەس ئىدى» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,136,' ئېيتىڭلاركى، «اﷲ قا ئىمان ئېيتتۇق، بىزگە نازىل قىلىنغان ۋەھيىگە، ئىبراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا ۋە ئۇلارنىڭ ئەۋلادلىرىغا نازىل قىلىنغان ۋەھيىگە، مۇساغا بېرىلگەن (تەۋراتقا)، ئىساغا بېرىلگەن (ئىنجىلغا) ۋە پەيغەمبەرلەرگە پەرۋەردىگارى تەرىپىدىن بېرىلگەن (كىتابلارغا) ئىمان ئېيتتۇق، ئۇلاردىن ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى يەھۇدىيلار ۋە ناسارالارغا ئوخشاش، ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ بەزىسىنى ئىنكار قىلمايمىز)، بىز اﷲ قا بويسۇنغۇچىلارمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,137,' ئەگەر ئۇلار (يەنى ئەھلى كىتاب) سىلەر ئىمان ئېيتقاندەك ئىمان ئېيتسا، ئەلۋەتتە، توغرا يول تاپقان بولىدۇ ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە، (سەن بىلەن) ئاداۋەتتە بولغان بولىدۇ، ئۇلارغا قارشى اﷲ ساڭا كۇپايىدۇر اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ھىيلە ـ مىكرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,138,' اﷲ نىڭ دىنىدا چىڭ تۇرۇڭلار، اﷲ نىڭ دىنىدىن ياخشى كىمنىڭ دىنى بار؟ بىز اﷲ قا ئىبادەت قىلغۇچىلارمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,139,' «بىز بىلەن اﷲ توغرۇلۇق مۇنازىرىلىشەمسىلەر؟ ھالبۇكى، ئۇ ھەم بىزنىڭ پەرۋەردىگارىمىز، ھەم سىلەرنىڭ پەرۋەردىگارىڭلاردۇر بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈندۇر، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، بىز اﷲ قا سادىقمىز» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,140,' «ياكى ئىبراھىم، ئىسمائىل، ئىسھاق، يەئقۇب ۋە ئۇنىڭ ئەۋلادى يەھۇدىي ئىدى ياكى ناسارا ئىدى» دەمسىلەر؟ ئېيتقىنكى، «(ئۇلارنىڭ قايسى دىندا ئىكەنلىكىنى) سىلەر ئوبدان بىلەمسىلەر؟ ياكى اﷲ ئوبدان بىلەمدۇ؟ قولىدا اﷲ نازىل قىلغان دەلىل ـ ئىسپات تۇرۇپ ئۇنى يوشۇرغان ئادەمدىن زالىم كىم بار؟ اﷲ سىلەرنىڭ قىلمىشىڭلاردىن غاپىل ئەمەستۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,141,' ئۇلار ئۆتكەن بىر ئۈممەتتۇر، ئۇلارنىڭ ئەمەللىرى ئۆزلىرى ئۈچۈندۇر سىلەرنىڭ ئەمەلىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، ئۇلارنىڭ قىلمىشىغا سىلەر جاۋابكار ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,142,' بەزى ئەخمەق كىشىلەر: «ئۇلارنى (يەنى پەيغەمبەر ئەلەيھىسسالام بىلەن مۆمىنلەرنى) قاراپ كېلىۋاتقان قىبلىسىدىن (يەنى بەيتۇلمۇقەددەستىن) نېمە يۈز ئۆرۈگۈزگەندۇ؟» دەيدۇ (ئى مۇھەممەد!) ئېيتقىنكى، «مەشرىق ۋە مەغرىب (يەنى ھەممە تەرەپ) اﷲ نىڭدۇر، اﷲ خالىغان كىشىنى توغرا يولغا باشلايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,143,' شۇنىڭدەك (يەنى سىلەرنى ئىسلامغا ھىدايەت قىلغاندەك) كىشىلەرگە (يەنى ئۆتكەنكى ئۈممەتلەرگە) شاھىت بولۇشۇڭلار ئۈچۈن ۋە پەيغەمبەرنىڭ سىلەرگە شاھىت بولۇشى ئۈچۈن، بىز سىلەرنى ياخشى ئۈممەت قىلدۇق سەن يۈز كەلتۈرۈپ كېلىۋاتقان تەرەپنى (يەنى بەيتۇلمۇقەددەسنى) قىبلە قىلغانلىقىمىز پەيغەمبەرگە ئەگەشكەنلەرنى ئاسىيلىق قىلغانلاردىن (يەنى مۇرتەد بولۇپ كەتكەنلەردىن) ئايرىۋېلىشىمىز ئۈچۈنلا ئىدى اﷲ ھىدايەت قىلغانلاردىن باشقىلارغا بۇ (يەنى قىبلىنىڭ ئۆزگەرتىلىشى) ھەقىقەتەن ئېغىردۇر اﷲ سىلەرنىڭ ئىمانىڭلارنى (يەنى ئىلگىرى بەيتۇلمۇقەددەسكە قاراپ ئوقۇغان نامىزىڭلارنى) بىكار قىلىۋەتمەيدۇ اﷲ كىشىلەرگە ھەقىقەتەن مېھرىباندۇر، ناھايىتى كۆيۈمچاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,144,' بىز سېنىڭ (كەبە قىبلەڭ بولۇشىنى تىلەپ) قايتا ـ قايتا ئاسمانغا قارىغانلىقىڭنى كۆرۈپ تۇرۇۋاتىمىز سېنى چوقۇم سەن ياقتۇرىدىغان قىبلىگە يۈزلەندۈرىمىز (نامازدا) يۈزۈڭنى مەسجىدى ھەرەم تەرەپكە قىلغىن (ئى مۆمىنلەر!) قەيەردە بولماڭلار (نامازدا) يۈزۈڭلارنى مەسجىدى ھەرەم تەرەپكە قىلىڭلار ھەقىقەتەن كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) بۇنىڭ (يەنى قىبلىنىڭ كەبىگە ئۆزگەرتىلىشىنىڭ) پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەت ئىكەنلىكىنى، چوقۇم بىلىدۇ، اﷲ ئۇلارنىڭ قىلمىشىدىن غاپىل ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,145,' كىتاب بېرىلگەنلەرگە بارچە دەلىلنى كەلتۈرسەڭمۇ، ئۇلار سېنىڭ قىبلەڭگە بويسۇنمايدۇ، سەنمۇ ئۇلارنىڭ قىبلىسىگە بويسۇنمايسەن؛ ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) ھەم ئۆز ئارا بىر ـ بىرىنىڭ قىبلىسىگە بويسۇنمايدۇ ساڭا ئىلىم (يەنى ۋەھيى) كەلگەندىن كېيىن، مۇبادا ئۇلارنىڭ نەپسى خاھىشلىرىغا بويسۇنساڭ، ئۇ چاغدا سەن چوقۇم زۇلۇم قىلغانلاردىن بولىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,146,' بىز كىتاب بەرگەنلەر (يەنى يەھۇدىيلار بىلەن ناسارالار) ئۇنى (يەنى پەيغەمبەرنى) ئۆز ئوغۇللىرىنى تونۇغاندەك تونۇيدۇ ئۇلارنىڭ بىر پىرقىسى، شۈبھىسىزكى، ھەقىقەتنى بىلىپ تۇرۇپ يوشۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,147,' ھەقىقەت اﷲ تەرىپىدىن كەلگەندۇر، ھەرگىز شەك كەلتۈرگۈچىلەردىن بولمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,148,' ھەر (ئۈممەت) نىڭ يۈز كەلتۈرىدىغان تەرىپى (يەنى قىبلىسى) بار، (ئى مۆمىنلەر!) ياخشى ئەمەللەرنى قىلىشقا ئالدىراڭلار، قەيەردە بولماڭلار، (قىيامەت كۈنى) اﷲ ھەممەڭلارنى (ھېساب ئۈچۈن) يىغىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,149,' (قەيەردىن سەپەرگە چىقمىغىن، نامىزىڭدا) يۈزۈڭنى مەسجىدى ھەرەم (يەنى كەبە) تەرەپكە قىلغىن؛ بۇ، ھەقىقەتەن، پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەتتۇر، اﷲ سىلەرنىڭ قىلمىشىڭلاردىن غاپىل ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,150,' كىشىلەر (يەنى يەھۇدىيلار ۋە مۇشرىكلار) گە سىلەرگە قارشى دەلىل ـ ئىسپات بولماسلىقى ئۈچۈن، (ئى مۇھەممەد! سەپەر قىلىشقا) قەيەردىن چىقمىغىن، (نامىزىڭدا) يۈزۈڭنى مەسجىدى ھەرەم تەرەپكە قىلغىن (ئى مۆمىنلەر!) سىلەرمۇ قەيەردە بولماڭلار، (نامىزىڭلاردا) يۈزۈڭلارنى مەسجىدى ھەرەم تەرەپكە قىلىڭلار پەقەت ئۇلارنىڭ ئىچىدىكى زۇلۇم قىلغۇچىلارلا (قىبلىنىڭ ئۆزگەرگەنلىكىنى سىلەرگە قارشى دەلىل قىلىۋالىدۇ)، ئۇلار (يەنى زالىملار) دىن قورقماڭلار، (مېنىڭ ئەمرىمنى تۇتۇش بىلەن) مەندىن قورقۇڭلار (قىبلىنى ئۆزگەرتىشكە ئەمر قىلىشىم) سىلەرگە بەرگەن نېمىتىمنى كامالەتكە يەتكۈزۈشۈم ئۈچۈن ۋە توغرا يولدا بولۇشۇڭلار ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,151,' شۇنىڭدەك (يەنى سىلەرگە بەرگەن نېمىتىمنى كامالەتكە يەتكۈزگىنىمدەك) ئۆز ئىچىڭلاردىن سىلەرگە بىزنىڭ ئايەتلىرىمىزنى تىلاۋەت قىلىپ بېرىدىغان، سىلەرنى (مۇشرىكلىك ۋە گۇناھدىن) پاك قىلىدىغان، سىلەرگە كىتابنى (يەنى قۇرئاننى) ۋە ھېكمەتنى (يەنى دىنىي ئەھكاملارنى) ئۆگىتىدىغان، سىلەرگە بىلمىگەنلىرىڭلارنى بىلدۈرىدىغان بىر پەيغەمبەر ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,152,' مېنى (تائەت ـ ئىبادەت بىلەن) ياد ئېتىڭلار، (مېنى ياد ئەتسەڭلار) مەنمۇ سىلەرنى (ساۋاب بېرىش بىلەن، مەغپىرەت قىلىش بىلەن) ياد ئېتىمەن ماڭا شۈكۈر قىلىڭلار، ناشۈكۈرلۈك قىلماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,153,' ئى مۆمىنلەر! سەۋر ئارقىلىق ۋە ناماز ئارقىلىق ياردەم تىلەڭلار اﷲ ھەقىقەتەن سەۋر قىلغۇچىلار بىلەن بىللىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,154,' اﷲ نىڭ يولىدا ئۆلتۈرۈلگەنلەرنى (يەنى شېھىتلەرنى) ئۆلۈك دېمەڭلار، بەلكى ئۇلار تىرىكتۇر، لېكىن سىلەر بۇنى سەزمەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,155,' بىز سىلەرنى بىرئاز قورقۇنچ بىلەن، بىرئاز قەھەتچىلىك بىلەن ۋە ماللىرىڭلارغا، جانلىرىڭلارغا، بالىلىرىڭلارغا، زىرائەتلىرىڭلارغا يېتىدىغان زىيان بىلەن چوقۇم سىنايمىز (بېشىغا كەلگەن مۇسىبەت، زىيان ـ زەخمەتلەرگە) سەۋر قىلغۇچىلارغا (جەننەت بىلەن) خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,156,' ئۇلارغا بىرەر مۇسىبەت كەلگەن چاغدا، ئۇلار: «بىز ئەلۋەتتە اﷲ نىڭ ئىگىدارچىلىقىدىمىز (يەنى اﷲ نىڭ بەندىلىرىمىز)، چوقۇم اﷲ نىڭ دەرگاھىغا قايتىمىز» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,157,' ئەنە شۇلار پەرۋەردىگارىنىڭ مەغپىرىتى ۋە رەھمىتىگە ئېرىشكۈچىلەردۇر، ئەنە شۇلار ھىدايەت تاپقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,158,' سەفا بىلەن مەرۋە ھەقىقەتەن اﷲ نىڭ (دىنىنىڭ) ئالامەتلىرىدۇر ھەج قىلغان ياكى ئۆمرە قىلغان ئادەم ئۇلارنىڭ ئارىسىدا سەئيى قىلسا ھېچ باك يوق كىمكى ئۆز ئىختىيارى بىلەن بىرەر ياخشىلىق قىلىدىكەن (اﷲ ئۇنىڭ مۇكاپاتىنى بېرىدۇ)، اﷲ ئەلۋەتتە شۈكۈرنىڭ مۇكاپاتىنى بەرگۈچىدۇر، اﷲ ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,159,' بۇ كىتابتا (يەنى تەۋراتتا) كىشىلەرگە توغرا يولنى ئېنىق بايان قىلغىنىمىزدىن كېيىن، بىز نازىل قىلغان (مۇھەممەد ئەلەيھىسسالامنىڭ راست پەيغەمبەر ئىكەنلىكىنى ئىسپاتلايدىغان) روشەن دەلىللەرنى ۋە توغرا يولنى يوشۇرىدىغانلارغا اﷲ لەنەت قىلىدۇ (يەنى رەھمىتىدىن يىراق قىلىدۇ)؛ لەنەت قىلغۇچىلار (يەنى پەرىشتىلەر ۋە مۆمىنلەر) مۇ ئۇلارغا لەنەت قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,160,' پەقەت تەۋبە قىلغانلار، (ئەمەلىنى) تۈزەتكەنلەر، يەھۇدىيلارنىڭ تەۋرات ئەھكاملىرىدىن يوشۇرغانلىرىنى) بايان قىلغانلارلا بۇنىڭدىن مۇستەسنا ئەنە شۇلارنىڭ تەۋبىسىنى قوبۇل قىلىمەن، مەن تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇرمەن ناھايىتى مېھرىباندۇرمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,161,' شۈبھىسىزكى، كاپىر بولۇپ، كاپىر پېتى ئۆلگەنلەر بار، ئەنە شۇلار چوقۇم اﷲ نىڭ، پەرىشتىلەرنىڭ ۋە ئىنسانلارنىڭ ھەممىسىنىڭ لەنىتىگە ئۇچرايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,162,' ئۇلار دوزاختا مەڭگۈ قالىدۇ، ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ، ئۇلارغا بېرىلىدىغان ئازاب كېچىكتۈرۈلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,163,' سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر؛ ئۇ ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,164,' ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىدا، كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىشىدە، كىشىلەرگە پايدىلىق نەرسىلەرنى ئېلىپ دېڭىزدا ئۈزۈپ يۈرگەن كېمىلەردە، اﷲ بۇلۇتتىن ياغدۇرۇپ بەرگەن، ئۆلگەن زېمىننى تىرىلدۈرىدىغان يامغۇردا، يەر يۈزىگە تارقىتىۋەتكەن ھايۋاناتلاردا، شامال يۆنۈلۈشلىرىنىڭ ئۆزگىرىپ تۇرۇشىدا، ئاسمان ـ زېمىن ئارىسىدا بويسۇندۇرۇلغان بۇلۇتلاردا، چۈشىنىدىغان كىشىلەر ئۈچۈن، ئەلۋەتتە (اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان) دەلىللەر باردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,165,' بەزى ئادەملەر اﷲ تىن غەيرىيلەرنى (يەنى بۇتلارنى) اﷲ قا شېرىك قىلىۋالىدۇ، ئۇلارنى (مۆمىنلەرنىڭ) اﷲ نى دوست تۇتقىنىدەك دوست تۇتىدۇ، (يەنى ئۇلۇغلايدۇ ۋە ئۇلارغا بويسۇنىدۇ) مۆمىنلەر اﷲ نى ھەممىدىن بەك دوست تۇتقۇچىلاردۇر، زالىملار (قىيامەت كۈنى ئۆزلىرىگە تەييارلانغان) ئازابنى كۆرگەن چاغدا پۈتۈن كۈچ ـ قۇۋۋەتنىڭ اﷲ قا مەنسۈپ ئىكەنلىكىنى ۋە اﷲ نىڭ ئازابىنىڭ قاتتىق ئىكەنلىكىنى كاشكى بىلسە ئىدى (دۇنيادا اﷲ قا شېرىك كەلتۈرگەنلىكلىرىگە ھەددىدىن زىيادە پۇشايمان قىلاتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,166,' ئەينى ۋاقىتتا ئەگەشتۈرگۈچىلەر ئازابنى كۆرۈپ، ئەگەشكۈچىلەردىن ئادا ـ جۇدا بولىدۇ ئۇلارنىڭ ئۆزئارا مۇناسىۋىتى (يەنى دۇنيادىكى دوستلۇقى) ئۈزۈلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,167,' ئەگەشكۈچىلەر: «كاشكى بىزلەرگە (دۇنياغا) قايتىشقا بولسا ئىدى، ئۇلار بىزدىن ئادا ـ جۇدا بولغاندەك بىزمۇ ئۇلاردىن ئادا ـ جۇدا بولاتتۇق» دەيدۇ اﷲ ئۇلارغا (يامان) ئىشلىرىنىڭ ھەسرەت بولغانلىقىنى مۇشۇنداق كۆرسىتىدۇ، ئۇلار دوزاختىن چىقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,168,' ئى ئىنسانلار! يەر يۈزىدىكى ھالال ـ پاكىز نەرسىلەردىن يەڭلار، شەيتاننىڭ يوللىرىغا ئەگەشمەڭلار، چۈنكى شەيتان سىلەرگە ئوچۇق دۈشمەندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,169,' شەيتان سىلەرنى ھەقىقەتەن يامانلىقلارغا، قەبىھ گۇناھلارغا ۋە اﷲ نامىدىن، يالغاندىن (اﷲ سىلەرگە ھالال قىلغان نەرسىلەرنى ھارام دەپ ۋە ھارام قىلغان نەرسىلەرنى ھالال دەپ)، سىلەر بىلمەيدىغان نەرسىلەرنى ئېيتىشقا بۇيرۇيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,170,' ھەرقاچان ئۇلارغا (يەنى مۇشرىكلارغا): «اﷲ نازىل قىلغان نەرسىگە (يەنى اﷲ پەيغەمبىرىگە نازىل قىلغان ۋەھيىگە، قۇرئانغا) ئەگىشىڭلار» دېيىلسە، ئۇلار: «ياق، ئاتا ـ بوۋىلىرىمىزدىن قالغان دىنغا ئەگىشىمىز» دېيىشىدۇ ئاتا ـ بوۋىلىرى ھېچ نەرسىنى چۈشەنمىگەن ۋە توغرا يولدا بولمىغان تۇرسا، يەنە ئۇلارغا ئەگىشەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,171,' كاپىرلەرنى ھىدايەتكە دەۋەت قىلغۇچى خۇددى مەنىسىنى چۈشەنمەي تاۋۇشىنى ۋە نىدانىلا ئاڭلايدىغان چاھارپايلارنى چاقىرغان ئادەمگە ئوخشايدۇ (كاپىرلار ھەقنى ئاڭلاشتىن) گاستۇر، (ھەقنى سۆزلەشتىن) گاچىدۇر، (ھەقنى كۆرۈشتىن) كوردۇر، ئۇلار (ۋەز ـ نەسىھەتنى چۈشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,172,' ئى مۆمىنلەر! بىز سىلەرنى رىزىقلاندۇرغان ھالال نەرسىلەردىن يەڭلار، ئەگەر اﷲ غىلا ئىبادەت قىلىدىغان بولساڭلار، اﷲ قا (يەنى اﷲ نىڭ ھەددى ـ ھېسابسىز نېمەتلىرىگە) شۈكۈر قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,173,' اﷲ سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋاننى، قاننى، چوشقا گۆشىنى، اﷲ تىن غەيرىينىڭ (يەنى بۇتلارنىڭ) نامى ئېيتىلىپ بوغۇزلانغان ھايۋاننى يېيىشنى ھارام قىلدى، كىمكى ئۆز ئىختىيارىچە ئەمەس، ئىلاجىسىزلىقتىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) ھاياتىنى ساقلاپ قالغۇدەك يېسە، ئۇنىڭغا ھېچ گۇناھ بولمايدۇ ھەقىقەتەن اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,174,' اﷲ كىتابتا (يەنى تەۋراتتا) نازىل قىلغان نەرسىلەرنى يوشۇرىدىغان ۋە ئۇنى ئازغىنا پۇلغا ساتىدىغانلارنىڭ قارنىغا يېگىنى پەقەت ئوتتۇر (چۈنكى ئۇلارنىڭ يېگەن شۇ ھارام مېلى ئۇلارنى دوزاخقا ئېلىپ بارىدۇ)، قىيامەت كۈنى اﷲ ئۇلارغا (ئۇلارنى خۇشال قىلىدىغان) سۆز قىلمايدۇ، ئۇلارنى (گۇناھلىرىدىن) پاكلىمايدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,175,' ئەنە شۇلار ھىدايەتنى گۇمراھلىققا، مەغپىرەتنى ئازابقا (يەنى جەننەتنى دوزاخقا) تېگىشتى، ئۇلار دوزاخ ئازابىغا نېمىدېگەن چىداملىق!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,176,' بۇ (قاتتىق ئازاب) شۇنىڭ ئۈچۈنكى، اﷲ ھەقىقەتەن ھەق كىتابنى (تەۋراتنى) نازىل قىلدى (ئۇلار تەۋراتتىكى نەرسىلەرنى يوشۇردى ۋە ئۆزگەرتىۋەتتى)، كىتاب توغرىسىدا ئىختىلاپ قىلغۇچىلار چۇڭقۇر نىزادىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,177,' سىلەرنىڭ كۈن چىققان ۋە كۈن پاتقان تەرەپكە يۈزكەلتۈرۈشۈڭلارنىڭ ئۆزىلا ياخشى ئەمەلگە ياتمايدۇ بەلكى اﷲ قا، ئاخىرەت كۈنىگە، پەرىشتىلەرگە، كىتابقا (يەنى اﷲ نازىل قىلغان كىتابلارغا)، پەبغەمبەرلەرگە ئىمان كەلتۈرۈش، اﷲ نى سۆيۈش يۈزىسىدىن خىش ـ ئەقرىبالارغا، يېتىملەرگە، مىسكىنلەرگە، ئىبن سەبىللەرگە (يەنى پۇل ـ مېلىدىن ئالاقىسى ئۈزۈلۈپ قالغان مۇساپىرلارغا)، سائىللارغا ۋە قۇللارنىڭ ئازادلىققا ئېرىشىشىگە پۇل ـ مال ياردەم بېرىش، ناماز ئوقۇش، زاكات بېرىش، ئەھدىگە ۋاپا قىلىش، يوقسۇزلۇققا، كېسەللىككە ۋە (اﷲ نىڭ يولىدا قىلىنغان) ئۇرۇشقا بەرداشلىق بېرىش ياخشى ئەمەلگە كىرىدۇ ئەنە شۇلار (يەنى يۇقىرىقى سۈپەتلەرگە ئىگە كىشىلەر) (ئىمانىدا) راستچىل ئادەملەردۇر، ئەنە شۇلار تەقۋادار ئادەملەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,178,' ئى مۆمىنلەر! ئۆلتۈرۈلگەنلەر ئۈچۈن قىساس ئېلىش سىلەرگە پەرز قىلىندى، ھۆر ئادەم ئۈچۈن ھۆر ئادەمدىن، قۇل ئۈچۈن قۇلدىن، ئايال ئۈچۈن ئايالدىن قىساس ئېلىنىدۇ قاتىل ئۈچۈن (دىنىي) قېرىندىشى تەرىپىدىن بىرنەرسە كەچۈرۈم قىلىنسا (يەنى ئۆلتۈرۈلگۈچىنىڭ ئىگىسى قاتىلدىن دىيەت ئېلىشقا رازى بولۇپ قىساس ئېلىشتىن ۋاز كەچسە، ئۇ، جىنايەتچىدىن دىيەتنى) چىرايلىقچە تەلەپ قىلىشى لازىم، (جىنايەتچىمۇ دىيەتنى) ياخشىلىقچە (يەنى كېچىكتۈرمەستىن، كېمەيتىۋەتمەستىن تولۇق) بېرىشى لازىم بۇ (ھۆكۈم) پەرۋەردىگارىڭلار تەرىپىدىن (سىلەرگە بېرىلگەن) يېنىكلىتىشتۇر ۋە رەھمەتتۇر شۇنىڭدىن كېيىن (يەنى دىيەتنى قوبۇل قىلغاندىن كېيىن)، چېكىدىن ئېشىپ كەتكەن (يەنى قاتىلنى ئۆلتۈرگەن) ئادەم قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,179,' ئى ئەقىل ئىگىلىرى! سىلەرگە قىساستا ھاياتلىق بار، (يەنى كىشى بىراۋنى ئۆلتۈرگەن تەقدىردە ئۆزىنىڭمۇ ئۆلتۈرۈلىدىغانلىقىنى بىلسە، ئۇ، ئادەم ئۆلتۈرۈشتىن يانىدۇ، شۇنىڭ بىلەن، ئۇنىڭ ئۆزىمۇ، ئۇ ئۆلتۈرمەكچى بولغان ئادەممۇ ئۆلۈشتىن ساقلىنىپ قالىدۇ) (ناھەق قان تۆكۈشتىن) ساقلىنىشىڭلار ئۈچۈن (قىساس يولغا قويۇلدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,180,' ئەگەر سىلەردىن بىرەيلەن ئۆلۈش ئالدىدا كۆپ مال قالدۇرسا، ئۇنىڭ ئاتا ـ ئانىسىغا ۋە خىش ـ ئەقرىبالىرىغا ئادىللىق بىلەن ۋەسىيەت قىلىشى تەقۋادارلارنىڭ ئۆتەشكە تېگىشلىك بۇرچى سۈپىتىدە پەرز قىلىندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,181,' ۋەسىيەتنى ئاڭلىغاندىن كىيىن، كىمكى ئۇنى ئۆزگەرتىدىكەن، ئۇنىڭ گۇناھى ئۆزگەرتكەنلەرگە يۈكلىنىدۇ اﷲ ھەقىقەتەن ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,182,' كىمكى ۋەسىيەت قىلغۇچىنىڭ سەۋەنلىكى ياكى قەستەن ناتوغرا قىلغانلىقىنى بىلىپ، ئۇلارنىڭ (يەنى ۋەسىيەت قىلغۇچىلار بىلەن ۋەسىيەت قىلىنغۇچىلارنىڭ) ئارىسىنى تۈزەپ قويسا، ئۇنىڭغا ھېچ گۇناھ بولمايدۇ، اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,183,' ئى مۆمىنلەر! (گۇناھلاردىن) ساقلىنىشىڭلار ئۈچۈن، سىلەردىن ئىلگىرىكىلەرگە (يەنى ئىلگىرىكى ئۈممەتلەرگە) روزا پەرز قىلىنغاندەك، سىلەرگىمۇ (رامىزان روزىسى) پەرز قىلىندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,184,' (بۇ پەرز قىلىنغان روزا) ساناقلىق كۈنلەردۇر، سىلەردىن كىمكى كېسەل ياكى سەپەر ئۈستىدە بولۇپ (روزا تۇتمىغان بولسا)، تۇتمىغان كۈنلەرنى (يەنى قازاسىنى) باشقا كۈنلەردە تۇتسۇن، روزىنى (قېرىلىق ياكى ئاجىزلىق تۈپەيلىدىن مۇشەققەت بىلەن) ئاران تۇتىدىغان كىشىلەر تۇتمىسا، (كۈنلۈكى ئۈچۈن) بىر مىسكىن تويغۇدەك تاماق فىدىيە بېرىشى لازىم كىمكى فىدىيىنى (بەلگىلەنگەن مىقداردىن) ئارتۇق بەرسە، بۇ ئۆزى ئۈچۈن ياخشىدۇر، ئەگەر بىلسەڭلار، روزا تۇتۇش سىلەر ئۈچۈن (ئېغىز ئوچۇق يۈرۈشتىن ۋە فىدىيە بېرىشتىن) ياخشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,185,' رامىزان ئېيىدا قۇرئان نازىل بولۇشقا باشلىدى، قۇرئان ئىنسانلارغا يېتەكچىدۇر، ھىدايەت قىلغۇچى ۋە ھەق بىلەن ناھەقنى ئايرىغۇچى روشەن ئايەتلەردۇر، سىلەردىن كىمكى رامىزان ئېيىدا ھازىر بولسا رامىزان روزىسىنى تۇتسۇن؛ كىمكى كېسەل ياكى سەپەر ئۈستىدە (يەنى مۇساپىر) بولۇپ (تۇتمىغان بولسا، تۇتمىغان كۈنلەر ئۈچۈن) باشقا كۈنلەردە تۇتسۇن اﷲ سىلەرگە ئاسانلىقنى خالايدۇ، تەسلىكنى خالىمايدۇ، (ئاغزىڭلار ئوچۇق يۈرگەن كۈنلەرنىڭ قازاسىنى قىلىش بىلەن رامىزان روزىسىنىڭ) سانىنى تولدۇرشۇڭلارنى، سىلەرنى ھىدايەت قىلغانلىقىغا اﷲ نى ئۇلۇغلىشىڭلارنى، (ئۇنىڭ ئىنئاملىرىغا) شۈكۈر قىلىشىڭلارنى خالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,186,'مېنىڭ بەندىلىرىم سەندىن مەن توغرۇلۇق سورىسا (ئۇلارغا ئېيتقىنكى)، مەن ھەقىقەتەن ئۇلارغا يېقىنمەن (يەنى ئۇلارنىڭ ئەھۋالىنى، سۆزلىرىنى بىلىپ تۇرىمەن)، ماڭا دۇئا قىلسا، مەن دۇئا قىلغۇچىنىڭ دۇئاسىنى ئىجابەت قىلىمەن، ئۇلار توغرا يول تېپىش ئۈچۈن مېنىڭ دەۋىتىمنى قوبۇل قىلسۇن ۋە ماڭا ئىمان ئېيتسۇن (يەنى ھەمىشە ئىمان بىلەن بولسۇن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,187,' روزا كېچىلىرىدە ئاياللىرىڭلارغا يېقىنچىلىق قىلىش سىلەرگە ھالال قىلىندى، ئۇلار سىلەر ئۈچۈن كىيىمدۇر (يەنى ئۇلار سىلەرگە ئارىلىشىپ ياشايدۇ ۋە كىيىم بەدەنگە يېپىشقاندەك يېپىشىدۇ)، سىلەرمۇ ئۇلار ئۈچۈن كىيىمسىلەر (يەنى سىلەرمۇ ئۇلارغا ئارىلىشىپ ياشايسىلەر ۋە كىيىم بەدەنگە يېپىشقاندەك يېپىشىسىلەر) اﷲ ئۆزۈڭلارنىڭ ئۆزۈڭلارنى ئالدىغانلىقىڭلارنى (يەنى روزا كېچىسى يېقىنچىلىق قىلغىنىڭلارنى) بىلدى  اﷲ تەۋبەڭلارنى قوبۇل قىلدى، سىلەرنى ئەپۇ قىلدى ئەمدى ئۇلارغا (يەنى ئاياللىرىڭلارغا) يېقىنچىلىق قىلىڭلار، اﷲ سىلەرگە تەقدىر قىلغان نەرسىنى (يەنى پەرزەنتنى) تەلەپ قىلىڭلار، تاكى تاڭنىڭ ئاق يىپى قارا يىپىدىن ئايرىلغانغا (يەنى تاڭ يورۇغانغا) قەدەر يەڭلار، ئىچىڭلار، ئاندىن كەچ كىرگىچە روزا تۇتۇڭلار، سىلەر مەسجىدتە ئېتىكاپتا ئولتۇرغان چېغىڭلاردا (كۈندۈز بولسۇن كېچە بولسۇن)، ئۇلارغا يېقىنچىلىق قىلماڭلار، ئەنە شۇلار اﷲ بەلگىلىگەن چېگرىلاردۇر (يەنى مەنئى قىلغان ئىشلاردۇر)، ئۇنىڭغا يېقىنلاشماڭلار كىشىلەرنىڭ (ھارام قىلىنغان ئىشلاردىن) ساقلىنىشلىرى ئۈچۈن، اﷲ ئايەتلىرىنى ئۇلارغا مۇشۇنداق بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,188,' بىر ـ بىرىڭلارنىڭ ماللىرىنى ناھەق يەۋالماڭلار، بىلىپ تۇرۇپ كىشىلەرنىڭ بىر قىسىم ماللىرىنى زۇلۇم بىلەن يەۋېلىش ئۈچۈن ھاكىملارغا پارا بەرمەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,189,' (ئى مۇھەممەد!) سەندىن يېڭى چىققان ئاي (يەنى ئاينىڭ كىچىك چىقىپ چوڭىيىدىغانلىقى، ئاندىن كىچىكلەيدىغانلىقى) توغرۇلۇق سورىشىدۇ، (ئۇلارغا) ئېيتقىنكى، «ئۇ كىشىلەرنىڭ (تىرىكچىلىك، سودا ـ سېتىق، مۇئامىلە، روزا تۇتۇش، ئېغىز ئېچىش قاتارلىق) ئىشلارنىڭ ۋە ھەجنىڭ ۋاقىتلىرىنى ھېسابلىشى ئۈچۈندۇر» (جاھىلىيەت دەۋرىدە قىلغىنىڭلاردەك ئېھرامدىكى چېغىڭلاردا) ئۆيلەرنىڭ ئارقىسىدىن (تۆشۈك ئېچىپ) كىرىش ياخشىلىققا ياتمايدۇ (اﷲ نىڭ ئەمرىگە مۇخالىپ ئىش قىلىشتىن) قورققان ئادەم ياخشى ئەمەل (ئىگىسى) دۇر (ئېھرامدىكى چېغىڭلاردا ئادەتتىكىدەك) ئۆيلەرنىڭ دەرۋازىلىرىدىن كىرىڭلار، بەختكە ئېرىشىشڭلار ئۈچۈن اﷲ قا تەقۋادارلىق قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,190,' سىلەرگە ئۇرۇش ئاچقان ئادەملەرگە قارشى اﷲ يولىدا جىھاد قىلىڭلار، تاجاۋۇز قىلماڭلار، تاجاۋۇز قىلغۇچىلارنى اﷲ ھەقىقەتەن دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,191,' ئۇلارنى (يەنى مۇشرىكلارنى) (ئۇلار ھەرەمدە بولسۇن، باشقا يەردە بولسۇن، ئۇرۇش ھارام قىلىنغان ئايلاردا بولسۇن) ئۇچراتقان يەردە ئۆلتۈرۈڭلار، سىلەرنى يۇرتۇڭلاردىن (يەنى مەككىدىن) چىقىرىۋەتكەندەك، سىلەرمۇ ئۇلارنى يۇرتىدىن چىقىرىۋېتىڭلار، زىيانكەشلىك قىلىش ئۇرۇشتىنمۇ ياماندۇر تاكى ئۇلار ئۆزلىرى ئۇرۇش ئاچمىغىچە مەسجىدى ھەرەم ئەتراپىدا ئۇلار بىلەن ئۇرۇش قىلماڭلار، ئەگەر ئۇلار (مەسجىدى ھەرەم ئەتراپىدا) ئۆزلىرى ئۇرۇش ئاچسا، ئۇلارنى ئۆلتۈرۈۋېرىڭلار كاپىرلارنىڭ جازاسى شۇنداق بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,192,' ئەگەر ئۇلار ئۇرۇشنى توختاتسا (سىلەرمۇ ئۇرۇشتىن قول يىغىڭلار)، اﷲ (تەۋبە قىلغۇچىلارغا) ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,193,' تاكى زىيانكەشلىك تۈگىگەن ۋە اﷲ نىڭ دىنى يولغا قويۇلغانغا قەدەر ئۇلار بىلەن ئۇرۇشۇڭلار؛ ئەگەر ئۇلار ئۇرۇشنى توختاتسا، زۇلۇم قىلغۇچىلاردىن باشقىلارغا دۈشمەنلىك قىلىشقا بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,194,' (ئۇرۇش) ھارام قىلىنغان ئاي (ئۇرۇش) ھارام قىلىنغان ئايغا تەڭدۇر (يەنى ئۇرۇش ھارام قىلىنغان ئايلاردا دۈشمىنىڭلار سىلەرگە چېقىلسا، شۇ ئايلاردا سىلەرمۇ ئۇلارغا چېقىلساڭلار بولىدۇ) ھۆرمىتى ساقلىنىشقا تېگىشلىك نەرسىلەر تەڭ ـ باراۋەردۇر( يەنى ھۆرمىتى ساقلىنىشقا تېگىشلىك نەرسىلەرنى باشقىلار دەپسەندە قىلسا، ئىنتىقام ئېلىش يۈزسىدىن، سىلەرمۇ ئۇلارنى دەپسەندە قىلساڭلار ھېچ يامىنى يوق (بىراۋ سىلەرگە قانچىلىك چېقىلغان بولسا، سىلەرمۇ ئۇنىڭغا شۇنچىلىك چېقىلىڭلار؛ اﷲ قا تەقۋادارلىق قىلىڭلار، بىلىڭلاركى، اﷲ تەقۋادارلار بىلەن بىللىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,195,' اﷲ نىڭ يولىدا (پۇل ـ مال) سەرپ قىلىڭلار، ئۆزۈڭلارنى ھالاكەتكە تاشلىماڭلار، ئېھسان قىلىڭلار، ئېھسان قىلغۇچىلارنى اﷲ ھەقىقەتەن دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,196,' ھەج بىلەن ئۆمرەنى اﷲ ئۈچۈن تولۇق ئورۇنلاڭلار، ئەگەر (دۈشمەن ياكى كېسەللىك سەۋەبىدىن) ھەجنى ياكى ئۆمرەنى ئادا قىلىشتا توسقۇنلۇققا ئۇچرىساڭلار، ئۇ ھالدا نېمە ئوڭاي بولسا (يەنى تۆگە، كالا، قويدىن نېمىنى تاپالىساڭلار)، شۇنى قۇربانلىق قىلىڭلار، قۇربانلىق قىلىنىدىغان مال جايىغا (يەنى بەلگىلەنگەن جايغا) يەتمىگىچە بېشىڭلارنى چۈشۈرمەڭلار، سىلەردىن كىمكى كېسەل (يەنى چېچىنى چۈشۈرسە زىيان قىلىدىغان كېسەل) ياكى بېشىدا ئىللەت (يەنى باش ئاغرىقى، پىتقا ئوخشاشلار) بولۇپ (بېشىنى چۈشۈرمىسە)، فىدىيە قىلىش يۈزىسىدىن (ئۈچ كۈن) روزا تۇتسۇن، ياكى (ئالتە مىسكىنگە ئۈچ سا) سەدىقە بەرسۇن، ياكى (بىرەر مال) قۇربانلىق قىلسۇن، ئەمىنلىكتە بولغان چېغىڭلاردا (ئاراڭلاردىن) ئۆمرە قىلغاندىن كېيىن ھەج قىلىشتىن بەھرىمەن بولغان ئادەم (يەنى ھەجدىن بۇرۇن ئۆمرە قىلىپ تاماملىغان ئادەم) نېمە ئوڭاي بولسا، شۇنى قۇربانلىق قىلسۇن، (پۇلى يوقلۇقتىن ياكى قۇربانلىق مال يوقلۇقىدىن قۇربانلىق مال) تاپالمىغان ئادەم ھەج جەريانىدا ئۈچ كۈن روزا تۇتسۇن، (ھەجدىن) قايتقاندىن كېيىن يەتتە كۈن روزا تۇتسۇن، ئۇنىڭ ھەممىسى ئون كۈن بولىدۇ مانا بۇ (يەنى ئۆمرە قىلغان ئادەمگە قۇربانلىق قىلىش لازىملىقى ياكى روزا تۇتۇش ھەققىدىكى ھۆكۈم) مەسجىدى ھەرەم دائىرىسىدە (يەنى ھەرەمدە) ئولتۇرۇشلۇق بولمىغان كىشىلەر ئۈچۈندۇر اﷲ قا تەقۋادارلىق قىلىڭلار، بىلىڭلاركى، اﷲ نىڭ ئازابى قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,197,' ھەج ۋاقتى مەلۇم بىرقانچە ئايدۇر (يەنى شەۋۋال، زۇلقەئدە ئايلىرى ۋە زۇلھەججە ئېيىنىڭ ئون كۈنىدۇر) بۇ ئايلاردا ھەج قىلىشنى نىيەت قىلغان (يەنى ئېھرام باغلىغان) ئادەمنىڭ جىنسىي ئالاقە قىلىشى، گۇناھ قىلىشى ۋە جاڭجال قىلىشى مەنئى قىلىنىدۇ، سىلەر قانداقلىكى ياخشى ئەمەلنى قىلساڭلار، اﷲ ئۇنى بىلىپ تۇرىدۇ (ئاخىرەتلىكىڭلار ئۈچۈن) زاد ـ راھىلە ئېلىۋېلىڭلار، ئەڭ ياخشى زاد ـ راھىلە تەقۋادارلىقتۇر ئى ئەقىل ئىگىلىرى! ماڭا تەقۋادارلىق قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,198,' پەرۋەردىگارىڭلاردىن (ھەج مەۋسۈمىدە تىجارەت ۋە باشقا ئوقەت ئارقىلىق) رىزىق تەلەپ قىلساڭلار، سىلەرگە ھېچ گۇناھ بولمايدۇ ئەرەفاتتىن قايتقان چېغىڭلاردا مەشئەرى ھەرەمدا (يەنى مۇزدەلىفەدە) اﷲ نى ياد ئېتىڭلار، اﷲ سىلەرنى ھىدايەت قىلغانلىقى ئۈچۈن، ئۇنى ياد ئېتىڭلار، ئىلگىرى سىلەر ھەقىقەتەن گۇمراھ ئىدىڭلار (يەنى اﷲ سىلەرنى ھىدايەت قىلىشتىن ئىلگىرى گۇمراھلار قاتارىدا ئىدىڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,199,' ئاندىن كېيىن، سىلەر (ئەرەفاتتىن) كىشىلەر قايتقان جاي بىلەن قايتىڭلار، اﷲ تىن مەغپىرەت تەلەپ قىلىڭلار اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,200,' سىلەر ھەجگە ئائىت ئىبادەتلەرنى ئادا قىلغاندىن كېيىن، اﷲ نى بولسا ئاتا ـ بوۋاڭلارنى (پەخىرلىنىپ) ياد ئەتكەندەك ياكى ئۇنىڭدىنمۇ زىيادە ياد ئېتىڭلار، بەزى كىشىلەر: «ئى پەرۋەردىگارىمىز! بىزگە (نېسىۋىمىزنى) بۇ دۇنيادىلا بەرگىن» دەيدۇ ھالبۇكى، ئۇنىڭغا ئاخىرەتتە (ياخشىلىقتىن) ھېچ نېسىۋە يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,201,' بەزى كىشىلەر: «پەرۋەردىگارىمىز! بىزگە دۇنيادا ياخشىلىق ئاتا قىلغىن، ئاخىرەتتىمۇ ياخشىلىق ئاتا قىلغىن، بىزنى دوزاخ ئازابىدىن ساقلىغىن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,202,' ئەنە شۇلار ئۆزلىرى قىلغان ئەمەللىرىنىڭ مول مېۋىسىنى كۆرىدۇ اﷲ نىڭ ھېساب ئېلىشى تېزدۇر (يەنى شۇنچە كۆپ خالايىقتىن قىسقا ۋاقىت ئىچىدە ھېساب ئېلىپ بولىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,203,' ساناقلىق كۈنلەردە (يەنى تەشرىق كۈنلىرىدە) اﷲ نى ياد ئېتىڭلار (يەنى نامازلىرىڭلارنىڭ ئاخىرىدا تەكبىر ئېيتىڭلار)، (مىنادىن) ئالدىراپ ئىككى كۈندە قايتقان ئادەمگە ھېچ گۇناھ بولمايدۇ، تەخىر قىلىپ قايتقان ئادەمگىمۇ ھېچ گۇناھ بولمايدۇ (يۇقىرىقى ئەھكاملار) تەقۋادارلىق قىلىپ (ھەجنى تولۇق ئادا قىلماقچى بولغان) ئادەم ئۈچۈندۇر اﷲ تىن قورقۇڭلار؛ بىلىڭلاركى، سىلەر ھېساب ئۈچۈن اﷲ نىڭ دەرگاھىغا يىغىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,204,' (ئى مۇھەممەد!) كىشىلەر ئارىسىدا شۇنداق ئادەم باركى، ئۇنىڭ دۇنيا تىرىكچىلىكى توغرىسىدىكى سۆزى سېنى قىزىقتۇرىدۇ (لېكىن ئۇ يالغانچى مۇناپىقتۇر)، ئۇ دىلىدىكى نەرسىگە (يەنى دىلى باشقا تىلى باشقا ئەمەسلىكىگە) اﷲ نى گۇۋاھ قىلىدۇ ھالبۇكى، ئۇ (ساڭا ۋە ساڭا ئەگەشكۈچىلەرگە) ئەشەددىي دۈشمەندۇر (كۆرۈنۈشتە ئۇ شېرىن سۆزى ئارقىلىق دىندار قىياپەتكە كىرىۋالىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,205,' ئۇ (ھۇزۇرۇڭدىن) قايتقاندىن كېيىن، زېمىندا بۇزۇقچىلىق قىلىش ئۈچۈن ۋە زىرائەتلەرنى، ھايۋاناتلارنى ھالاك قىلىش ئۈچۈن تىرىشىدۇ (ئۇنىڭ بۇزغۇنچىلىقى ئەمەلدە ئىنسانلارنى ھالاك قىلىش ئۈچۈندۇر، چۈنكى زىرائەتلەرسىز ۋە ھايۋاناتلارسىز ئىنسانلارنىڭ ياشىيالىشى مۇمكىن ئەمەس) اﷲ بۇزۇقچىلىقنى ياقتۇرمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,206,' ئۇنىڭغا (ۋەز ـ نەسىھەت قىلىنىپ يامان سۆز ـ ھەرىكەتلىرىڭدىن قايتىپ) اﷲ تىن قورققىن دېيىلسە، غۇرۇرى ئۇنىڭغا گۇناھ يۈكلەيدۇ (يەنى ھەقتىن تەكەببۇرلۇق بىلەن باش تارتىپ، پىتنە ـ پاساتتا تېخىمۇ ئەزۋەيلەيدۇ)، ئۇنىڭغا جەھەننەم يېتەرلىكتۇر، جەھەننەم ناھايىتى يامان جايدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,207,' بەزى كىشىلەر باركى، ئۇلار اﷲ نىڭ رەزاسى ئۈچۈن جېنىنى پىدا قىلىدۇ (قىلغان ئەمەلى بىلەن پەقەت اﷲ نىڭ رازىلىقىنىلا تىلەيدۇ) اﷲ بەندىلىرىگە تولىمۇ مەرھەمەتلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,208,' ئى مۆمىنلەر! ئىسلام دىنىغا پۈتۈنلەي كىرىڭلار (يەنى ئسلام دىنىنىڭ پۈتۈن ئەھكاملىرىغا بويسۇنۇڭلار، بەزى ئەھكاملىرىغا ئەمەل قىلىپ، بەزى ئەھكاملىرىنى تەرك ئەتمەڭلار)، شەيتاننىڭ يوللىرىغا (ۋە ئىغۋاسىغا) ئەگەشمەڭلار، شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,209,' سىلەرگە (ئىسلام دىنىنىڭ ھەقلىقىنى كۆرسىتىدىغان) روشەن دەلىللەر كەلگەندىن كېيىن (توغرا يولدىن) تېيىپ كەتسەڭلار، بىلىڭلاركى، (سىلەر جازالىنىسىلەر، چۈنكى) اﷲ غالىبتۇر (يەنى سىلەردىن ئىنتىقام ئېلىشتىن ئاجىز ئەمەستۇر)، ھېكمەت بىلەن ئش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,210,' ئۇلار (يەنى ئسلامغا كىرىشنى تەرك ئەتكەنلەر) پەقەت اﷲ نىڭ (قىيامەت كۈنى خالايىقنىڭ ئارىسىدا ھۆكۈم چىقىرىش ئۈچۈن) بۇلۇت پارچىلىرى ئىچىدە كېلىشىنى، پەرىشتلەرنىڭ كېلىشىنى ۋە (خالايىقنىڭ ئارىسىدا ھۆكۈم چىقىرىلىپ، بىر گۇرۇھ جەننەتى، بىر گۇرۇھ دوزىخى بولۇپ ئايرىلىش بىلەن خالايىقنىڭ) ئىشىنىڭ پۈتۈشىنى كۈتەمدۇ؟ (بەندىلەرنىڭ) ھەممە ئىشى اﷲ قا قايتۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,211,' (ئى مۇھەممەد!) ئىسرائىل ئەۋلادىدىن سورىغىنكى، بىز ئۇلارغا نەقەدەر كۆپ روشەن دەلىللەرنى ئاتا قىلدۇق كىمكى اﷲ نىڭ نېمىتىگە ئېرىشكەندىن كېيىن، ئۇنى (كۇفرىلىق قىلىپ) ئۆزگەرتىۋەتسە (اﷲ ئۇنىڭغا ئازاب قىلىدۇ)، اﷲ نىڭ ئازابى ھەقىقەتەن قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,212,' كاپىرلارغا دۇنيا تىرىكچىلىكى چىرايلىق كۆرسىتىلدى (يەنى ئۇلار دۇنيانىڭ شەھۋەتلىرىگە، نېمەتلىرىگە مەپتۇن بولىدۇ)، مۆمىنلەرنى مەسخىرە قىلىدۇ، ھالبۇكى قىيامەت كۈنى تەقۋادارلار ئۇلاردىن (دەرىجە جەھەتتە) ئۈستۈن تۇرىدۇ؛ اﷲ خالىغان كىشىگە ھېسابسىز رىزىق بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,213,' ئىنسانلار (دەسلەپتە) بىر ئۈممەت (يەنى ھەق دىندا) ئىدى (كېيىن ئۇلارنىڭ بەزىسى ئىمان ئېيتىپ، بەزىسى ئىمان ئېيتماي ئىختىلاپ قىلىشتى)، اﷲ (مۆمىنلەرگە جەننەت بىلەن) خۇش خەۋەر بەرگۈچى، (كۇففارلارنى دوزاختىن) ئاگاھلاندۇرغۇچى پەيغەمبەرلەرنى ئەۋەتتى؛ (اﷲ) كىشىلەرنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىش ئۈچۈن، ئۇلارغا ھەق كىتابنى نازىل قىلدى، پەقەت كىتاب بېرىلگەن كىشىلەرلا ئۆزلىرىگە روشەن دەلىللەر كەلگەندىن كېيىن ئۆزئارا ھەسەت قىلىشىپ (يەنى كاپىرلار مۆمىنلەرگە ھەسەت قىلىپ)، كىتاب توغرىسىدا ئىختىلاپ قىلىشتى، اﷲ ئۆز ئىرادىسى بويىچە مۆمىنلەرنى ئۇلار (يەنى گۇمراھلار) ئىختىلاپ قىلىشقان ھەقىقەتكە ھىدايەت قىلدى اﷲ خالىغان كىشىنى توغرا يولغا باشلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,214,' سىلەر تېخى ئىلگىرىكىلەر ئۇچرىغان كۈلپەتلەرگە ئۇچرىماي تۇرۇپ جەننەتكە كىرىشنى ئويلامسىلەر؟ سىلەردىن ئىلگىرى ئۆتكەن (مۆمىن) لەر ئېغىرچىلىقلارغا، كۈلپەتلەرگە ئۇچرىغان ۋە چۆچۈتۈۋېتىلگەن ئىدى، ھەتتا پەيغەمبەر ۋە مۆمىنلەر: «اﷲ نىڭ (بىزگە ۋەدە قىلغان) ياردىمى قاچان كېلىدۇ؟» دېگەن ئىدى بىلىڭلاركى، اﷲ نىڭ ياردىمى ھەقىقەتەن يېقىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,215,' (ئى مۇھەممەد!) سەندىن (ماللىرىنى) قانداق سەرپ قىلسا بولدىغانلىقىنى سورايدۇ، ئېيتقىنكى، «مال ـ مۈلكىڭلاردىن نېمىنى سەرپ قىلماڭلار، ئۇنى ئاتا ئاناڭلارغا، خىش ـ ئەقرىبالىرىڭلارغا، مىسكىنلەرگە ۋە ئىبن سەبىللەرگە سەرپ قىلساڭلار بولىدۇ ياخشىلىقتىن نېمىنى قىلماڭلار، اﷲ ئۇنى بىلىپ تۇرىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,216,' سىلەرگە جىھاد پەرز قىلىندى، ھالبۇكى، سىلەر ئۇنى ياقتۇرمايسىلەر، سىلەر بىرەر نەرسىنى ياقتۇرماسلىقىڭلار مۇمكىن، ئەمما ئۇ سىلەر ئۈچۈن پايدىلىقتۇر؛ سىلەر بىرەر نەرسىنى ياقتۇرۇشۇڭلار مۇمكىن، ئەمما ئۇ سىلەر ئۈچۈن زىيانلىقتۇر (سىلەرگە نېمىنىڭ پايدىلىق ئىكەنلىكىنى) اﷲ بىلىدۇ، سىلەر بىلمەيسىلەر، (شۇنىڭ ئۈچۈن) اﷲ بۇيرۇغانغا ئالدىراڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,217,' (ئى مۇھەممەد!) ئۇلار سەندىن: «ھارام قىلىنغان ئايدا ئۇرۇش قىلىشقا بولامدۇ؟» دەپ سورايدۇ، ئېيتقىنكى، «بۇ ئايدا ئۇرۇش قىلىش چوڭ گۇناھتۇر؛ اﷲ نىڭ يولىدىن توسۇش، اﷲ قا كۇفرىلىق قىلىش، مەسجىدى ھەرەمدا (ئىبادەت قىلىشتىن) توسۇش ۋە مەسجىدى ھەرەمدىن ئاھالىنى ھەيدەپ چىقىرىش اﷲ نىڭ دەرگاھىدا تېخىمۇ چوڭ گۇناھتۇر، پىتنە (يەنى مۆمىنلەرگە زىيانداشلىق قىلىش) ئادەم ئۆلتۈرۈشتىنمۇ قاتتىق گۇناھتۇر» ئۇلار (يەنى كۇففارلار) قولىدىن كەلسىلا سىلەرنى دىنىڭلاردىن قايتۇرۇۋەتكەنگە قەدەر سىلەر بىلەن داۋاملىق ئۇرۇشىدۇ سىلەردىن كىمكى ئۆز دىنىدىن قايتىپ كاپىر پېتى ئۆلسە، ئۇنىڭ ئەمەللىرى دۇنيا ۋە ئاخىرەتتە بىكار بولۇپ كېتىدۇ بۇنداق ئادەملەر ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,218,' شۈبھىسىزكى، ئمان ئېيتقانلار، ھىجرەت قىلغانلار ۋە اﷲ نىڭ يولىدا جىھاد قىلغانلار ئەنە شۇنداق كىشىلەر اﷲ نىڭ رەھمىتىنى ئۈمىد قىلىدۇ، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,219,' (ئى مۇھەممەد!) سەندىن ھاراق ۋە قىمار توغرىسىدا سورىشىدۇ، سەن ئۇلارغا: «بۇنىڭ ھەر ئىككىسىدە چوڭ گۇناھ ۋە كىشىلەرگە (ئازغىنا ماددى) پايدىمۇ بار، لېكىن ئۇلاردىكى گۇناھ پايدىغا قارىغاندا تېخىمۇ چوڭ» دېگىن، ئۇلار سەندىن نېمىنى سەدىقە قىلىش توغرۇلۇق سورايدۇ، «(ئېھتىياجدىن) ئارتۇقىنى (سەدىقە قىلىڭلار)» دېگىن (دۇنيا ۋە ئاخىرەتنىڭ ئىشلىرىنى) تەپەككۇر قىلىشىڭلار ئۈچۈن اﷲ ئۆز ئايەتلىرىنى سىلەرگە شۇنداق بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,220,' (ئى مۇھەممەد!) ئۇلار سەندىن يېتىملەر توغرۇلۇق سورىشىدۇ، ئېيتقىنكى، «(تەربىيىلەش، ماللىرىنى ئۆستۈرۈپ) ئۇلارنى تۈزەش ياخشىدۇر، ئۇلار بىلەن (ئۇلارغا پايدىلىق بولغان رەۋىشتە) ئارىلىشىپ ياشىساڭلار، ئۇ چاغدا (يەنى ئۇلارغا قېرىنداشلارچە مۇئامىلە قىلىڭلار)، ئۇلار سىلەرنىڭ دىنىي قېرىنداشلىرىڭلاردۇر اﷲ (يېتىملەرنىڭ ئىشلىرىنى) بۇزغۇچى ۋە تۈزىگۈچىنى بىلىپ تۇرىدۇ ئەگەر اﷲ خالىسا، سىلەرنى (قىيىن ئىشقا تەكلىپ قىلىش بىلەن) چوقۇم جاپاغا سالاتتى» اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,221,' (ئى مۇسۇلمانلار!) مۇشرىك ئاياللار ئىمان ئېيتمىغىچە ئۇلارنى نىكاھىڭلارغا ئالماڭلار (ھۆر) مۇشرىك ئايال (ھۆسن ـ جامالى ۋە مېلى بىلەن) سىلەرنى مەپتۇن قىلغان تەقدىردىمۇ، مۆمىن دېدەك، ئەلۋەتتە، ئۇنىڭدىن ئارتۇقتۇر مۇشرىك ئەرلەر ئىمان ئېيتمىغىچە مۆمىن ئاياللارنى ئۇلارغا ياتلىق قىلماڭلار، (ھۆر) مۇشرىك ئەر سىلەرنى مەپتۇن قىلغان تەقدىردىمۇ مۆمىن قۇل، ئەلۋەتتە، ئۇنىڭدىن ئارتۇقتۇر ئەنە شۇلار (يەنى مۇشرىك ئەر ۋە ئاياللار سىلەرنى) دوزاخقا چاقىرىدۇ، اﷲ ئۆز ئىرادىسى بويىچە (سىلەرنى) جەننەتكە، مەغپىرەتكە چاقىرىدۇ، كىشىلەر پەند ـ نەسىھەت ئالسۇن دەپ اﷲ ئۇلارغا ئۆز ئايەتلىرىنى بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,222,' (ئى مۇھەممەد!) ئۇلار سەندىن ھەيز توغرۇلۇق (يەنى ھەيزدار ئايال بىلەن جىنسىي ئالاقە قىلىشنىڭ دۇرۇسلۇقى ياكى دۇرۇس ئەمەسلىكى توغرۇلۇق) سورايدۇ ئېيتقىنكى، «ھەيز زىيانلىقتۇر (يەنى ھەيز مەزگىلىدە جىنسىي ئالاقە قىلىش ئەر ـ خوتۇن ھەر ئىككىسىگە زىيانلىقتۇر)، ھەيز مەزگىلىدە ئايالىڭلاردىن نېرى تۇرۇڭلار، (ھەيزدىن) پاك بولغۇچە ئۇلارغا يېقىنچىلىق قىلماڭلار، پاك بولغاندا، ئۇلارغا اﷲ بۇيرۇغان جايدىن يېقىنچىلىق قىلىڭلار» اﷲ ھەقىقەتەن تەۋبە قىلغۇچىلارنى دوست تۇتىدۇ (ھارامدىن ۋە نىجاسەتتىن) پاك بولغۇچىلارنى ھەقىقەتەن دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,223,' ئاياللىرىڭلار سىلەر ئۈچۈن (خۇددى) ئېكىنزارلىقتۇر (يەنى نەسىل تېرىيدىغان جايدۇر) ئېكىنزارلىقىڭلارغا خالىغان رەۋىشتە كېلىڭلار، ئۆزۈڭلار ئۈچۈن ئالدىنئالا ياخشى ئەمەل تەييارلاڭلار، (گۇناھتىن چەكلىنىش بىلەن) اﷲ تىن قورقۇڭلار، بىلىڭلاركى، سىلەر اﷲ قا مۇلاقات بولۇسىلەر، مۆمىنلەرگە (جەننەت بىلەن) خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,224,' ياخشىلىق قىلماسلىققا، تەقۋادار بولماسلىققا ۋە كىشىلەرنى ئەپلەشتۈرمەسلىككە قەسەم قىلىش شەكلى بىلەن (ئۇلارنى قىلىشقا) اﷲ نى توسالغۇ قىلماڭلار (يەنى اﷲ بىلەن قەسەم قىلىشنى ياخشى ئىشلارنى قىلماسلىقنىڭ سەۋەبى قىلماڭلار)، اﷲ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,225,' مەقسەتسىز ئىچكەن قەسىمىڭلار ئۈچۈن اﷲ سىلەرنى جازاغا تارتمايدۇ، قەستەن ئىچكەن قەسىمىڭلار ئۈچۈن اﷲ سىلەرنى جازاغا تارتىدۇ اﷲ مەغپىرەت قىلغۇچىدۇر، ھەلىمدۇر (يەنى بەندىلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,226,' ئاياللىرىغا يېقىنچىلىق قىلماسلىققا قەسەم ئىچكەن كىشىلەر تۆت ئاي كۈتىدۇ؛ ئەگەر ئۇلار (بۇ مۇددەت ئىچىدە ئاياللىرىغا يېقىنچىلىق قىلىشقا) قايتسا، (ئاياللىرىغا يامانلىق قىلغان گۇناھىنى) اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر اﷲ ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,227,' ئەگەر ئۇلار تالاق قىلىش نىيىتىگە كەلسە، اﷲ ھەقىقەتەن (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,228,' تالاق قىلىنغان ئاياللار ئۈچ ھەيز ئۆتكۈچە ئىددەت تۇتىدۇ ئەگەر ئۇلار اﷲ قا، ئاخىرەت كۈنىگە ئىشىنىدىكەن، بەچچىدانلىرىدىكى اﷲ ياراتقان نەرسىنى (يەنى بالىنى ياكى ھەيزنى) يوشۇرۇشى دۇرۇس ئەمەس، ئۇلارنىڭ ئەرلىرى ئەگەر ئەپلىشىپ قېلىشنى خالىسا، شۇ مۇددەت ئىچىدە ئۇلارنى قايتۇرۇپ كېلىشكە (باشقىلارغا قارىغاندا) ئەڭ ھەقلىقتۇر ئاياللىرى ئۈستىدە ئەرلەرنىڭ ھەقلىرى بولغىنىدەك، ئەرلىرى ئۈستىدە ئاياللارنىڭمۇ ھەقلىرى بار (يەنى ئاياللار مۇۋاپىق دەرىجىدە ھوقۇقتىن بەھرىمەن بولۇشى كېرەك، مۇۋاپىق دەرىجىدە مەجبۇرىيەتمۇ ئۆتىشى كېرەك) (ئاياللارغا مەھرى بېرىش ۋە ئۇلارنىڭ تۇرمۇشىنى قامداش مەجبۇرىيەتلىرى ئەرلەرگە يۈكلەنگەنلىكتىن) ئەرلەر ئاياللاردىن بىر دەرىجە ئارتۇقلۇققا ئىگە اﷲ غالىبتۇر ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,229,' (قايتا يارىشىشقا بولىدىغان) تالاق ئىككى قېتىمدۇر، ئۇنىڭدىن كېيىن (خوتۇنىنى) چىرايلىقچە تۇتۇش، ياكى ياخشىلىق بىلەن (يەنى زۇلۇم قىلماي، يامان گېپىنى قىلماي، كىشىلەرنى ئۇنىڭدىن يىراقلاشتۇرماي) قويۇۋېتىش لازىم، اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلالماسلىق خەۋپى (يەنى ياخشى ئۆتەلمەسلىك، اﷲ بەلگىلىگەن ئەر ـ خوتۇنلۇق ھوقۇقلىرىغا رىئايە قىلالماسلىق خەۋپى) بولمىسىلا، ئۆزۈڭلارنىڭ ئاياللىرىڭلارغا (مەھرى ئۈچۈن) بەرگەن مال ـ مۈلۈكتىن ھېچقانداق نەرسىنى قايتۇرۇۋېلىش دۇرۇس بولمايدۇ ئەگەر سىلەر ئۇلار (يەنى ئەر ـ ئايال) نىڭ اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلالماسلىقىدىن قورقساڭلار، خوتۇننىڭ (تالاق خېتىنى ئېلىش مەقسىتىدە) مال بەرگەنلىكى ئۈچۈن ئۇ ئىككىسىگە (يەنى ئەر ـ خوتۇنغا) گۇناھ بولمايدۇ ئەنە شۇلار اﷲ نىڭ بەلگىلىمىلىرىدۇر، ئۇنىڭغا خىلاپلىق قىلماڭلار اﷲ نىڭ بەلگىلىمىلىرىگە خىلاپلىق قىلغۇچىلار زالىملاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,230,' ئەگەر (ئۇ) ئۇنى (يەنى خوتۇنىنى) (ئۈچ قېتىم) تالاق قىلسا، (خوتۇن) باشقا ئەرگە تەگمىگىچە ئۇنىڭغا دۇرۇس بولمايدۇ، كېيىنكى ئەر ئۇنى تالاق قىلغاندىن كېيىن (ئىددىتىنى توشقۇزۇپ)، بۇرۇنقى ئەر بىلەن قايتا ياراشسا، ئۈلارغا ھېچ گۇناھ بولمايدۇ ئەگەر ئۇلار اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلالايدىغانلىقىنى (يەنى قايتا ياراشقاندىن كېيىن ياخشى ئۆتەلەيدىغانلىقىنى) تەسەۋۋۇر قىلسا بۇ اﷲ نىڭ بەلگىلىمىلىرىدۇركى، اﷲ ئۇنى بىلىدىغان (يەنى ئىشنىڭ ئاقىۋىتىگە پەمى يېتىدىغان) قەۋم ئۈچۈن بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,231,' سىلەر ئاياللارنى تالاق قىلغان بولساڭلار، ئۇلارنىڭ ئىددىتى توشاي دەپ قالغان بولسا، ئۇلار بىلەن چىرايلىقچە يارىشىپ، (ئۇلارنى نىكاھىڭلاردا) تۇتۇڭلار، ياكى ئۇلارنى ياخشىلىق بىلەن قويۇۋېتىڭلار، زۇلۇم قىلىش مەقسىتىدە زىيان يەتكۈزۈپ ئۇلارنى تۇتۇۋالماڭلار كىمكى شۇنداق قىلىدىكەن (يەنى كىمكى چىرايلىقچە ئۆي تۇتۇش نىيىتى بىلەن ئەمەس، بەلكى ئىددىتىنى ئۇزارتىپ زۇلۇم قىلىش نىيىتىدە خوتۇنى بىلەن يارىشىدىكەن)، ئۆزىگە ئۆزى زۇلۇم قىلغان بولىدۇ اﷲ نىڭ ئايەتلىرىنى كۈلكە قىلىۋالماڭلار (يەنى اﷲ نىڭ ئەھكاملىرىنى خىلاپلىق قىلىش يولى بىلەن مەسخىرە قىلماڭلار)، اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ۋە سىلەرگە ۋەز ـ نەسىھەت قىلىش ئۈچۈن نازىل قىلغان كىتابنى، ھېكمىتىنى (يەنى كىتابدىكى ئەھكاملارنى) ياد ئېتىڭلار، اﷲ قا تەقۋادارلىق قىلىڭلار بىلىڭلاركى، اﷲ ھەر نەرسىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,232,' ئەگەر سىلەر ئاياللارنى تالاق قىلغان بولۇپ، ئۇلارنىڭ ئىددىتى توشقان ۋە قائىدە بويىچە (تەگمەكچى بولغان) ئېرى بىلەن پۈتۈشكەن بولسا، ئۇلارنى نىكاھلىنىشتىن توسماڭلار بۇنىڭ بىلەن، ئىچىڭلاردىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان كەلتۈرگەنلەرگە نەسىھەت قىلىنىدۇ، بۇ (يەنى ۋەز ـ نەسىھەت ئېلىش، اﷲ نىڭ ئەمرىلىرىنى تۇتۇش)، سىلەر ئۈچۈن ئەڭ پايدىلىقتۇر ۋە ئەڭ پاكتۇر، (شەرىئەت ئەھكاملىرىدىن سىلەرگە نېمىلەر ئەڭ پايدىلىق ئىكەنلىكىنى) اﷲ بىلىدۇ، سىلەر بىلمەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,233,' ئانىلار (بالىلارنى ئېمىتىش مۇددىتىدە) تولۇق ئېمىتمەكچى بولسا، تولۇق ئىككى يىل ئېمىتىشى لازىم ئاتىلار ئانىلارنى قائىدە بويىچە يېمەك ـ ئىچمەك ۋە كىيىم ـ كېچەك بىلەن تەمىنلەپ تۇرۇشى كېرەك، كىشى پەقەت قولىدىن كېلىدىغان ئىشقىلا تەكلىپ قىلىنىدۇ ئانىنى بالىسى سەۋەبلىك زىيان تارتقۇزماسلىق لازىم (ئەگەر ئاتا ئۆلۈپ كەتسە) ئۇنىڭ ۋارىسى ئاتىغا ئوخشاش مەسئۇلىيەتنى (يەنى ئانا بولغۇچىغا نەپىقە بېرىش ۋە ئۇنىڭ ھەقلىرىگە رىئايە قىلىش قاتارلىقلارنى) ئۆز ئۈستىگە ئېلىشى كېرەك ئەگەر ئاتا ـ ئانا كېڭىشىپ (بالىنى ئىككى يىل توشماستىنلا) ئايرىۋەتمەكچى بولسا، ئۇلارغا ھېچبىر گۇناھ بولمايدۇ ئەگەر بالىلىرىڭلارنى ئىنىكئانىلارغا ئېمىتمەكچى بولساڭلار، قائىدە بويىچە ئۇلارنىڭ ھەققىنى بەرسەڭلارلا سىلەرگە ھېچ گۇناھ بولمايدۇ اﷲ تىن (يەنى اﷲ نىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىشتىن) قورقۇڭلار، بىلىڭلاركى، اﷲ سىلەرنىڭ قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,234,' ئىچىڭلاردا ئۆزى ۋاپات بولۇپ ئاياللىرى قېلىپ قالغان كىشىلەر بولسا، ئاياللىرى تۆت ئاي ئون كۈن ئىددەت تۇتۇشى لازىم، ئىددىتى توشقاندىن كېيىن ئۆزلىرى توغرۇلۇق قائىدە بويىچە ئىش قىلسا، سىلەرگە (يەنى شۇ ئاياللارغا ئىگىدارچىلىق قىلغۇچىلارغا) ھېچ گۇناھ بولمايدۇ اﷲ سىلەرنىڭ قىلغان ئەمەلىڭلاردىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,235,' ئىددىتى توشمىغان ئاياللارغا ئۇلارنى ئالىدىغانلىقىڭلارنى بۇرىتىپ ئۆتسەڭلار ياكى بۇنى دىلىڭلاردا يوشۇرۇن تۇتساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ ئۇلارغا ئېغىز ئاچىدىغانلىقىڭلار اﷲ قا مەلۇمدۇر، لېكىن ئۇلار بىلەن يوشۇرۇن ۋەدىلىشىپ قويماڭلار، پەقەت (ئاشكارا ئېيتىلسا كىشى خىجىل بولمايدىغان) مۇۋاپىق سۆزنى قىلساڭلار بولىدۇ، ئىددىتى توشمىغىچە ئۇلارنى نىكاھىڭلارغا ئېلىشقا بەل باغلىماڭلار، بىلىڭلاركى، اﷲ دىلىڭلاردىكىنى بىلىپ تۇرىدۇ، ئۇنىڭدىن (يەنى اﷲ نىڭ ئەمرىگە خىلاپلىق قىلىپ جازالىنىشتىن) ھەزەر قىلىڭلار، بىلىڭلاركى، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، (اﷲ تىن قورقۇپ گۇناھتىن چەكلەنگەنلەرگە) ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,236,' ئەگەر سىلەر ئاياللىرىڭلارغا يېقىنچىلىق قىلماي ياكى مەھرى تەيىنلىمەي تۇرۇپ ئۇلارنى تالاق قىلساڭلار (يەنى نىكاھىڭلارغا ئالغان ئايال بىلەن بىر يەردە بولمىغان ياكى مەھرىنى مۇئەييەنلەشتۈرمىگەن شارائىتتا) ئۇنى قويۇۋەتسەڭلار، سىلەرگە ھېچ گۇناھ بولمايدۇ، لېكىن ئۇنىڭغا مۇتئە (يەنى بەلگىلىك مىقداردا پۇل ـ مال) بېرىشىڭلار كېرەك، ئۇلارغا نەرسە بېرىڭلار، باي ئۆز ھالىغا يارىشا، كەمبەغەلمۇ ئۆز ھالىغا يارىشا قائىدە بويىچە بەرسۇن، (بۇ) ياخشى ئادەملەرنىڭ ئۆتەشكە تېگىشلىك مەجبۇرىيىتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,237,' ئەگەر سىلەر ئۇلارغا يېقىنچىلىق قىلىشتىن ئىلگىرى ۋە مەھرى تەيىنلىنىپ بولغاندىن كېيىن تالاق قىلساڭلار، تەيىنلەنگەن مەھرىنىڭ يېرىمىنى بېرىڭلار، ئەمما ئاياللار ئۆتۈنسە (يەنى مەھرىدىن ۋازكېچىپ پۈتۈنلەي ئالمىسا) ياكى نىكاھنىڭ تۈگۈنى ئۆز ئىلكىدە بولغان ئەرلەر ئۆتۈنسە (يەنى تەيىنلەنگەن مەھرىنىڭ ھەممىسىنى بەرسە)، شۇ بويىچە بىر تەرەپ قىلىش لازىم سىلەر ئۆتۈنسەڭلار، (ئۆتۈنگەن ئادەم) تەقۋادارلىققا ئەڭ يېقىندۇر ئۆزئارا ئېھسان قىلىشنى ئۇنتۇماڭلار اﷲ ھەقىقەتەن قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,238,' (بەش) نامازنى بولۇپمۇ نامازدىگەرنى داۋاملاشتۇرۇڭلار (يەنى پۈتۈن شەرتلىرى بىلەن ۋاقتىدا تولۇق ئادا قىلىڭلار) اﷲ نىڭ ھۇزۇرىدا (يەنى نامازدا) ئىتائەتمەنلىك بىلەن تۇرۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,239,' قورقۇنچتا قالغىنىڭلاردا، پىيادە ياكى ئۇلاغلىق كېتىپ بېرىپ (ناماز ئوقۇڭلار)، ئامانلىق تاپقىنىڭلاردا اﷲ نىڭ سىلەرگە ئۆگەتكىنى بويىچە اﷲ نى ياد ئېتىڭلار (يەنى قورقۇنچ تۈگىگەندە، اﷲ نامازنى قانداق ئۆتەشكە ئەمر قىلغان بولسا، تولۇق ئەركانلىرى بىلەن شۇنداق ئۆتەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,240,' سىلەرنىڭ ئىچىڭلاردا ئۆزى ۋاپات بولۇپ ئايالى قېلىپ قالىدىغانلار ئۇلارنىڭ (يەنى ئاياللىرىنىڭ) بىر يىلغىچە يېمەك ـ ئىچمەك، كىيىم ـ كېچەك بىلەن تەمىنلىنىشىنى، ئۆيىدىن ھەيدەپ چىقىرىلماسلىقىنى ۋەسىيەت قىلسۇن ئەگەر (ئاياللىرى) ئۆزلىرى چىقىپ كەتسە، ئۇلارنىڭ ئۆزلىرى توغرىسىدا قىلغان قائىدىلىك ئشلىرى ئۈچۈن سىلەرگە (يەنى مېيىتنىڭ ئىگىلىرىگە) ھېچ گۇناھ بولمايدۇ اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,241,' تالاق قىلىنغان ئاياللارغا قائىدە بويىچە نەپىقە بېرىش لازىم (ئۇ) تەقۋادارلارنىڭ ئۆتەشكە تېگىشلىك مەجبۇرىيىتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,242,' سىلەرنىڭ چۈشىنىشىڭلار ئۈچۈن اﷲ (ئۆز ئەھكاملىرىنى كۆرسىتىپ بېرىدىغان) ئايەتلىرىنى سىلەرگە مۇشۇنداق بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,243,' ئۆزلىرى مىڭلارچە تۇرۇقلۇق، ئۆلۈمدىن قورقۇپ، يۇرتلىرىدىن قېچىپ چىققان كىشىلەردىن خەۋىرىڭ يوقمۇ؟  اﷲ ئۇلارنى «ئۆلۈڭلار» دېدى (ئۇلار ئۆلدى) ئاندىن كېيىن اﷲ ئۇلارنى تىرىلدۈردى، اﷲ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر ۋە لېكىن ئىنسانلارنىڭ كۆپچىلىكى (اﷲ نىڭ نېمىتىگە) شۈكۈر قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,244,' اﷲ نىڭ يولىدا جىھاد قىلىڭلار، بىلىڭلاركى، اﷲ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيىتىڭلارنى، ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,245,' كىمكى اﷲ قا قەرزىي ھەسەنە بېرىدىكەن (يەنى اﷲ نىڭ يولىدا پۇل ـ مېلىنى خۇشاللىق بىلەن سەرپ قىلىدىكەن)، اﷲ ئۇنىڭغا نەچچە ھەسسە (يەنى ئون ھەسسىدىن يەتتە يۈز ھەسسىگىچە) كۆپ قايتۇرىدۇ اﷲ (سىناش ئۈچۈن، بەزى كىشىلەرنىڭ رىزقىنى) تار قىلىدۇ، (بەزى كىشىلەرنىڭ رىزقىنى) كەڭ قىلىدۇ، (قىيامەت كۈنى) اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,246,' ساڭا مۇسا (ۋاپاتى) دىن كېيىن ئىسرائىل ئەۋلادىدىدىن بولغان بىر جامائەنىڭ خەۋىرى يەتمىدىمۇ؟ ئۇلار ئۆز ۋاقتىدا ئۆزلىرىنىڭ پەيغەمبىرىگە؛ «بىزگە پادىشاھ تىكلەپ بەرگىن، (ئۇنىڭ بىلەن دۈشمەنلەرگە قارشى) اﷲ نىڭ يولىدا جىھاد قىلايلى» دېيىشتى پەيغەمبەر: « سىلەرگە جىھاد پەرز قىلىنسا جىھاد قىلماي قالارسىلەرمۇ؟» دېدى ئۇلار: «يۇرتلىرىمىزدىن ھەيدەپ چىقىرىلغان ۋە ئوغۇللىرىمىزدىن جۇدا قىلىنغان تۇرساق قانداقمۇ اﷲ نىڭ يولىدا جىھاد قىلمايلى؟» دېدى ئۇلارغا جىھاد پەرز قىلىنغان چاغدا، ئازغىنىسىدىن باشقا ھەممىسى جىھادتىن باش تارتتى اﷲ زالىملارنى ئوبدان بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,247,' پەيغەمبىرى ئۇلارغا: «اﷲ ھەقىقەتەن سىلەرگە تالۇتنى پادىشاھ قىلىپ ئەۋەتتى» دېدى ئۇلار (پەيغەمبىرىگە ئېتىراز بىلدۈرۈپ): «ئۇ قانداقمۇ بىزگە پادىشاھ بولىدۇ (ئارىمىزدا پادىشاھ ئەۋلاتلىرى بولغانلىقتىن) پادىشاھلىققا بىز ئۇنىڭدىن ھەقلىقمىز، ئۇنىڭ ئۈستىگە ئۇنىڭ مال ـ مۈلكى كۆپ بولمىغان تۇرسا» دېدى پەيغەمبەر: «اﷲ ھەقىقەتەن سىلەرگە (پادىشاھ قىلىشقا) ئۇنى ئىختىيار قىلدى، ئۇنىڭ ئىلمىنى زىيادە ۋە بەدىنىنى قابىل قىلدى، اﷲ سەلتەنەتنى خالىغان ئادەمگە بېرىدۇ اﷲ (نىڭ پەزلى) كەڭدۇر، (ئۇنىڭغا كىمنىڭ لايىق بولىدىغانلىقىنى) ئوبدان بىلىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,248,' ئۇلارغا پەيغەمبىرى ئېيتتىكى: «ئۇنىڭ پادىشاھلىقىنىڭ ئالامىتى شۇكى، سىلەرگە بىر ساندۇق كېلىدۇ، ئۇنىڭدا رەببىڭلار تەرىپىدىن سىلەرنى تەسكىن تاپقۇزىدىغان نەرسە ۋە مۇسانىڭ ھارۇننىڭ تەۋەلىرى قالدۇرۇپ كەتكەن نەرسىلەر (يەنى مۇسانىڭ ھاسىسى، كىيىمى ۋە تەۋرات يېزىلغان بەزى تاختىلار) قاچىلانغان بولىدۇ، ئۇنى پەرىشتىلەر كۆتۈرۈپ كېلىدۇ ئەگەر (اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغان) بولساڭلار، بۇنىڭدا (يەنى تابۇتنىڭ نازىل بولۇشىدا اﷲ نىڭ تالۇتنى سىلەرگە پادىشاھلىققا تاللىغانلىقىغا) ئەلۋەتتە سىلەر ئۈچۈن (روشەن) ئالامەت بار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,249,' تالۇت ئەسكەرلىرى بىلەن (بەيتۇلمۇقەددەستىن) ئايرىلغان چاغدا: «اﷲ سىلەرنى بىر دەريا بىلەن سىنايدۇ، كىمكى ئۇنىڭدىن ئىچىدىكەن، ئۇ ماڭا تەۋە ئەمەس، كىمكى دەريا سۈيىنى تېتىمايدىكەن، ئۇ ھەقىقەتەن ماڭا تەۋەدۇر، ئۇنىڭدىن (ئۇسسۇزلۇقىنى بىرئاز بېسىش ئۈچۈن) ئىچكەن ئادەم (بۇيرۇققا خىلاپلىق قىلغان بولمايدۇ)» دېدى ئۇلارنىڭ ئىچىدىكى ئازغىنا كىشىدىن باشقا ھەممىسى ئۇنىڭدىن ئىچتى تالۇت ۋە ئۇنىڭغا ئىمان ئېيتقان كىشىلەر دەريادىن ئۆتكەن چاغدا (دۈشمەننىڭ كۆپلۈكىنى كۆرۈپ قورقۇنچقا چۈشۈپ، ئۇلارنىڭ بىر پىرقىسى): «بۈگۈن بىز جالۇت ۋە ئۇنىڭ ئەسكەرلىرى بىلەن ئۇرۇشۇشقا تاقەت كەلتۈرەلمەيمىز» دېدى (تالۇتنىڭ تەۋەلىرىدىن) اﷲ قا مۇلاقات بولۇشقا ئېتىقاد قىلىدىغانلار: «اﷲ نىڭ ئىرادىسى بىلەن ئاز جامائە كۆپ جامائە ئۈستىدىن غەلىبە قىلىدۇ» دېدى اﷲ چىداملىق كۆرسەتكۈچىلەر بىلەن بىللىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,250,' ئۇلار جالۇت ۋە ئۇنىڭ ئەسكەرلىرى بىلەن ئۇچراشقان چاغدا: «پەرۋەردىگارىمىز! قەلبىمىزنى چىداملىقلىق بىلەن تولدۇرغىن، قەدەملىرىمىزنى (ئۇرۇش مەيدانىدا) ساباتلىق قىلغىن، كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,251,' مۆمىنلەر اﷲ نىڭ ئىرادىسى بىلەن كاپىرلارنى مەغلۇپ قىلدى، داۋۇد جالۇتنى ئۆلتۈردى اﷲ داۋۇدقا سەلتەنەتنى، ھېكمەتنى (يەنى پەيغەمبەرلىكنى) بەردى ئۇنىڭغا خالىغان نەرسىلىرىنى (يەنى پايدىلىق ئىلىملەرنى) بىلدۈردى اﷲ ئىنسانلارنىڭ بەزىسىنى بەزىسى بىلەن مۇداپىئە قىلىپ تۇرمىسا (يەنى كۈچلۈك تاجاۋۇزچىغا ئۇنىڭدىنمۇ كۈچلۈك بولغان بىرسىنى ئاپىرىدە قىلمىسا) ئىدى، يەر يۈزى ئەلۋەتتە پاساتقا ئايلىناتتى (يەنى خارابلىققا يۈزلىنەتتى)، لېكىن اﷲ (يامانلىقنى ئۈستۈنلۈككە ئىگە قىلماسلىق بىلەن) پۈتۈن جاھان ئەھلىگە مەرھەمەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,252,' بۇ اﷲ نىڭ ئايەتلىرىدۇركى، ئۇنى بىز ساڭا توغرا رەۋىشتە ئوقۇپ بېرىمىز (ئى مۇھەممەد!) سەن ئەلۋەتتە (اﷲ نىڭ دەۋىتىنى يەتكۈزۈش ئۈچۈن ئەۋەتىلگەن) پەيغەمبەرلەرنىڭ بىرىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,253,' ئەنە شۇ پەيغەمبەرلەرنىڭ بەزىسىنى بەزىسىدىن ئۈستۈن قىلدۇق؛ اﷲ ئۇلارنىڭ بەزىسى (يەنى مۇسا ئەلەيھىسسالام) بىلەن سۆزلەشتى ۋە بەزىسىنىڭ (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ) دەرىجىسىنى يۇقىرى كۆتۈردى مەريەم ئوغلى ئىساغا (ئۆلۈكلەرنى تىرىلدۈرۈش، گاچىلارنى، بەرەس كېسىلىنى ساقايتىشقا ئوخشاش) روشەن مۆجىزىلەرنى ئاتا قىلدۇق ئۇنى روھۇلقۇدۇس (يەنى جىبرىئىل ئەلەيھىسسالام) بىلەن كۈچلەندۈردۇق، ئەگەر اﷲ خالىغان بولسا ئىدى، ئۇلاردىن كېيىن كەلگەن ئۈممەتلەر ئۇلارغا روشەن دەلىللەر كەلگەندىن كېيىن ئۆزئارا ئۇرۇشماس ئىدى، لېكىن ئۇلار ئىختىلاپ قىلىشتى، بەزىسى ئىمان ئېيتتى، بەزىسى كاپىر بولدى ئەگەر اﷲ خالىغا بولسا ئىدى، ئۇلار ئۆزئارا ئۇرۇشماس ئىدى، لېكىن اﷲ خالىغىنىنى قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,254,' ئى مۆمىنلەر! سودا ـ سېتىق، دوستلۇق ۋە (اﷲ نىڭ ئىزنىسىز) شاپائەت بولمايدىغان كۈنى (يەنى قىيامەت كۈنى) يىتىپ كېلىشتىن بۇرۇن، سىلەرگە بىز رىزىق قىلىپ بەرگەن مال ـ مۈلۈكلەردىن (ياخشىلىق يوللىرىغا) سەرپ قىلىڭلار كۇفرانى نېمەت قىلغۇچىلار (ئۆزلىرىگە) زۇلۇم قىلغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,255,' بىر اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر؛ اﷲ ھەمىشە تىرىكتۇر، ھەممىنى ئىدارە قىلىپ تۇرغۇچىدۇر؛ ئۇ مۈگدەپ قالمايدۇ، ئۇنى ئۇيقۇ باسمايدۇ؛ ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭ (مۈلكى) دۇر؛ اﷲ نىڭ رۇخسىتىسىز كىممۇ اﷲ نىڭ ئالدىدا شاپائەت قىلالىسۇن؛ اﷲ ئۇلارنىڭ ئالدىدىكى (يەنى دۇنيادا قىلغان)، كەينىدىكى (يەنى ئۇلار ئۈچۈن ئاخىرەتتە تەييارلىغان) ئىشلارنىڭ ھەممىسىنى بىلىپ تۇرىدۇ؛ ئۇلار اﷲ نىڭ مەلۇماتىدىن (اﷲ) ئۇلارغا بىلدۈرۈشنى خالىغان نەرسىلەردىن (يەنى پەيغەمبەرلەرنىڭ تىلى ئارقىلىق بىلدۈرگەن نەرسىلەردىن) باشقا ھېچ نەرسىنى بىلمەيدۇ، اﷲ نىڭ كۇرسى (مەلۇماتى) ئاسمانلارنى ۋە زېمىننى ئۆز ئىچىگە ئالىدۇ ئاسمان ـ زېمىننى ساقلاش ئۇنىڭغا ئېغىر كەلمەيدۇ ئۇ يۇقىرى مەرتىۋىلىكتۇر، ھەممىدىن ئۇلۇغدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,256,' دىندا (ئۇنىڭغا كىرىشكە) زورلاش يوقتۇر، ھىدايەت گۇمراھلىقتىن ئېنىق ئايرىلدى، كىمكى تاغۇت (يەنى اﷲ تىن باشقا بارچە مەبۇد) نى ئىنكار قىلىپ، اﷲ قا ئىمان ئېيتىدىكەن، ئۇ سۇنماس، مەھكەم تۇتقىنى تۇتقان بولىدۇ، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,257,' اﷲ مۆمىنلەرنىڭ ئىگىسىدۇ، ئۇلارنى (كۇفرىنىڭ) زۇلمىتىدىن (ئىماننىڭ) يورۇقلۇقىغا چىقىرىدۇ؛ كاپىرلارنىڭ ئىگىسى شەيتاندۇر ئۇلارنى (ئىماننىڭ) يورۇقلۇقىدىن (گۇمراھلىقنىڭ) قاراڭغۇلۇقىغا چىقىرىدۇ ئەنە شۇلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,258,' پەرۋەردىگارى توغرىسىدا ئىبراھىم بىلەن مۇنازىرىلەشكەن، اﷲ نىڭ ئاتا قىلغان پادىشاھلىقى ئۇنى ئىبراھىم بىلەن مۇنازىرىلىشىشكە ئېلىپ كەلگەن ئادەم (يەنى نەمرۇد) نى كۆرمىدىڭمۇ؟ ئەينى زاماندا ئىبراھىم (اﷲ نىڭ بارلىقىغا دەلىل كۆرسىتىپ): «مېنىڭ پەرۋەردىگارىم (ئۆلۈكنى) تىرىلدۈرەلەيدۇ، (تىرىكنى) ئۆلتۈرەلەيدۇ» دېدى ئۇ: «مەنمۇ (ئۆلۈكنى) تىرىلدۈرەلەيمەن، (تىرىكنى) ئۆلتۈرەلەيمەن» دېدى (نەمرۇد ئۆلۈمگە ھۆكۈم قىلىنغان ئىككى ئادەمنى چاقىرتىپ كىلىپ بىرىنى ئۆلتۈردى، بىرىنى قويۇپ بەردى) ئىبراھىم (نەمرۇدنىڭ بۇنداق ھاماقەتلىكىنى كۆرۈپ): «اﷲ ھەقىقەتەن كۈننى شەرقتىن چىقىرالايدۇ، (خۇدالىق دەۋاسى قىلىدىغان بولساڭ) سەن ئۇنى غەربتىن چىقىرىپ باققىن» دېدى (بۇنداق پاكىت ئالدىدا) كاپىر ئېغىز ئاچالماي قالدى اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,259,' ياكى سەن تام، ئۆگزىلىرى يىقىلىپ ۋەيران بولغان (خارابە) شەھەر (يەنى بەيتۇلمۇقەددەس) دىن ئۆتكەن كىشىنى (يەنى ئۇزەيرنى) كۆرمىدىڭمۇ؟ ئۇ (ئەجەبلىنىپ): «اﷲ بۇ شەھەرنىڭ ئۆلگەن ئاھالىسىنى قانداق تىرىلدۈرەر؟» دېدى اﷲ ئۇنى يۈز يىل ئۆلۈك ھالىتىدە تۇرغۇزۇپ ئاندىن (كامالى قۇدرىتىنى كۆرسىتىش ئۈچۈن) تىرىلدۈردى اﷲ (پەرىشتە ئارقىلىق) ئۇنىڭدىن: «(بۇ ھالەتتە) قانچىلىك تۇردۇڭ؟» دەپ سورىدى ئۇ: «بىر كۈن ياكى بىر كۈنگە يەتمەيدىغان ۋاقىت تۇردۇم» دېدى اﷲ ئېيتتى: «ئۇنداق ئەمەس، (تولۇق)  يىل تۇردۇڭ! يېمەك ئىچمىكىڭگە قارىغىنكى، بۇزۇلمىغان؛ ئېشىكىڭگە قارىغىنكى (ئۇنىڭ سۆڭەكلىرى چىرىپ كەتكەن) (ساڭا يۇقىرىقىدەك ئادەتتىن تاشقىرى ئىشلارنى قىلغانلىقىمىز) سېنى بىز كىشىلەرگە (قۇدرىتىمىزنىڭ) دەلىلى قىلىش ئۈچۈندۇر بۇ سۆڭەكلەرگە قارىغىنكى، ئۇنى قانداق قۇراشتۇرىمىز، ئاندىن ئۇنىڭغا گۆش قوندۇرىمىز (يۇقىرىقى ئىشلار) ئۇنىڭغا ئېنىق بولغان چاغدا (يەنى ئۇ روشەن مۆجىزىلەرنى كۆرگەن چاغدا)، ئۇ: «مەن اﷲ نىڭ ھەممە نەرسىگە قادىر ئىكەنلىكىنى بىلدىم» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,260,' ئۆز ۋاقتىدا ئىبراھىم: «پەرۋەردىگارىم، ئۆلۈكلەرنى قانداق تىرىلدۈرىدىغانلىقىڭنى ماڭا كۆرسەتكىن» دېدى اﷲ: «(ئۆلۈكلەرنى تىرىلدۈرەلەيدىغانلىقىمغا) ئىشەنمىدىڭمۇ؟» دېدى ئىبراھىم: «ئىشەندىم، لېكىن كۆڭلۈم (تېخىمۇ) قارار تاپسۇن ئۈچۈن (كۆرۈشنى تىلەيمەن)» دېدى اﷲ ئېيتتى: «قۇشتىن تۆتنى ئالغىن، ئۇلارنى ئۆزۈڭگە توپلىغىن (يەنى ئۇلارنى پارچىلاپ گۆشلىرى بىلەن پەيلىرىنى ئارىلاشتۇرغىن)، ئاندىن ھەربىر تاغقا ئۇلاردىن بىر بۆلۈكىنى قويغىن، ئاندىن ئۇلارنى چاقىرغىن، سېنىڭ ئالدىڭغا ئۇلار چاپسان كېلىدۇ» بىلگىنكى، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,261,' اﷲ نىڭ يولىدا پۇل ـ مېلىنى سەرپ قىلغانلارنىڭ (سەرپ قىلغان نەرسىسى يەرگە تېرىلىپ) يەتتە باشاق چىقارغان، ھەر باشىقىدا  دان تۇتقان بىر دانغا ئوخشايدۇ  اﷲ خالىغان بەندىسىگە ھەسسىلەپ ساۋاب بېرىدۇ اﷲ نىڭ مەرھەمىتى (چەكلىك ئەمەس) كەڭدۇر (اﷲ مال سەرپ قىلغۇچىنىڭ نىيىتىنى بىلگۈچىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,262,' پۇل ـ مېلىنى اﷲ يولىدا سەرپ قىلىدىغان، ئارقىسىدىن ئۇنىڭغا مىننەت قىلمايدىغان، ۋە (خەير ـ ساخاۋىتىگە ئېرىشكەنلەرنى) رەنجىتمەيدىغان كىشىلەر پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب تاپىدۇ، ئۇلارغا (قىيامەت كۈنى) قورقۇنچ ۋە غەم ـ قايغۇ بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,263,' ياخشى سۆز ۋە كەچۈرۈش كېيىن ئەزىيەت يەتكۈزىدىغان سەدىقىدىن ئەۋزەلدۇر اﷲ (بەندىلەرنىڭ سەدىقىسىدىن) بىھاجەتتۇر، ھەلىمدۇر (يەنى ئەمرىگە خىلاپلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,264,' ئى مۆمىنلەر! پۇل ـ مېلىنى كىشىلەرگە كۆرسىتىش ئۈچۈن سەرپ قىلىدىغان، اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان كەلتۈرمەيدىغان كىشى (نىڭ قىلغان ئەمەلىنى بىكار قىلىۋەتكىنىگە) ئوخشاش، بەرگەن سەدىقەڭلارنى مىننەت قىلىش ۋە ئەزىيەت يەتكۈزۈش بىلەن بىكار قىلىۋەتمەڭلار بۇنداق (پۇل ـ مېلىنى باشقىلارغا كۆرسىتىش ئۈچۈن سەرپ قىلىدىغان) ئادەم خۇددى ئۈستىگە توپا ـ چاڭ قونۇپ قالغان، قاتتىق يامغۇردىن كېيىن (يۇيۇلۇپ) بۇرۇنقىدەك بولۇپ قالغان سىلىق تاشقا ئوخشايدۇ ئۇلار قىلغان ئەمەللىرى ئۈچۈن (ئاخىرەتتە) ھېچقانداق ساۋابقا ئىگە بولالمايدۇ اﷲ كاپىر قەۋمىنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,265,' ماللىرىنى اﷲ نىڭ رازىلىقىنى تىلەش ۋە نەپسىلىرىنى (سەدىقە ـ ئېھسانغا) ئادەتلەندۈرۈش يۈزىسىدىن سەرپ قىلىدىغانلار ئېگىزلىككە جايلاشقان، قاتتىق يامغۇردىن بەھرىمەن بولسا مېۋىسى ئىككى ھەسسە كۆپ بولىدىغان، قاتتىق يامغۇردىن بەھرىمەن بولمىسا شەبنەم بىلەنمۇ قانىدىغان (يەنى قايسى ھالدا بولسۇن مېۋە بېرىدىغان) بىر باغقا ئوخشايدۇ (دېمەك، بۇنداق خەير ـ ساخاۋەتلىك ئادەم مېلى كۆپ بولسا كۆپكە يارىشا، ئاز بولسا ئازغا يارىشا بېرىدۇ) اﷲ قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,266,' ئاراڭلاردا كىممۇ ياقتۇرسۇنكى، ئۇنىڭ خورمىلىق، ئۈزۈملۈك، ئاستىدىن ئېرىقلار ئېقىپ تۇرىدىغان، مېۋىلەرنىڭ ھەممە خىللىرى بولغان بىر بېغى بولسۇن، ئۇنىڭ ئۆزى قېرىپ قالغان، بالىلىرى ئۇششاق بولۇپ، (بۇ باغ) ئوت ئېلىپ كەلگەن بىر قارا بوراندا كۆيۈپ كەتسۇن سىلەرنىڭ ئويلىنىشىڭلار ئۈچۈن، اﷲ سىلەرگە ئايەتلىرىنى شۇنداق (گۈزەل مىسال بىلەن) بايان قىلىدۇ (يەنى پۇل ـ مېلىنى رىيا ئۈچۈن سەرپ قىلغان ياكى باشقىلارغا رەنجىتىش، مىننەت قىلىش بىلەن سەرپ قىلغان ئادەم ساۋابىنى ئەڭ موھتاج تۇرۇقلۇق يوقىتىپ قويغان بولىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,267,' ئى مۆمىنلەر! سىلەر ئېرىشكەن نەرسىلەرنىڭ ۋە بىز سىلەرگە زېمىندىن چىقىرىپ بەرگەن نەرسىلەر (يەنى ئاشلىقلار، مېۋىلەر) نىڭ ياخشىلىرىدىن سەدىقە قىلىڭلار؛ ئۆزۈڭلارمۇ پەقەت كۆزۈڭلارنى يۇمۇپ تۇرۇپ ئالىدىغان ناچارلىرىنى ئىلغاپ سەدىقە قىلماڭلار بىلىڭلاركى، اﷲ (بۇنداق سەدىقەڭلاردىن) بىھاجەتتۇر، ھەمدىگە لايىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,268,' شەيتان سىلەرنى پېقىرلىقتىن قورقىتىدۇ، يامان ئىشلارغا (يەنى بېخىللىققا، زاكات بەرمەسلىككە) بۇيرۇيدۇ؛ اﷲ سىلەرگە ئۆز مەغپىرىتىنى ۋە پەزلىنى ۋەدە قىلىدۇ اﷲ نىڭ مەرھەمىتى كەڭدۇر، ئۇ ھەممىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,269,' ھېكمەتنى (يەنى ياخشى ئەمەلگە يېتەكلەيدىغان پايدىلىق ئىلىمنى) خالىغان بەندىسىگە ئاتا قىلىدۇ؛ ھېكمەت ئاتا قىلىنغان ئادەمگە كۆپ ياخشىلىق ئاتا قىلىنغان بولىدۇ پەقەت ئەقىللىق ئادەملەرلا بۇنىڭدىن پەند ـ نەسىھەت ئالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,270,' نېمىنى خەير ـ ئېھسان قىلماڭلار ياكى (اﷲ نىڭ يولىدا) نېمىنى ۋەدە قىلماڭلار، اﷲ ئۇنى ھەقىقەتەن بىلىپ تۇرىدۇ زالىملار (يەنى زاكاتنى مەنئى قىلغۇچىلار ياكى پۇل ـ مېلىنى گۇناھ ئىشلارغا سەرپ قىلغۇچىلار) غا ھېچقانداق مەدەتكار چىقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,271,' سەدىقىنى ئاشكارا بەرسەڭلار، بۇ ياخشىدۇر، ئەگەر ئۇنى مەخپى بەرسەڭلار ۋە يوقسۇللارغا بەرسەڭلار، تېخىمۇ ياخشىدۇر بۇ، سىلەرنىڭ بەزى گۇناھلىرىڭلارغا كەففارەت بولىدۇ، اﷲ سىلەرنىڭ قىلغان ئەمەلىڭلاردىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,272,' (ئى مۇھەممەد!) ئۇلارنى ھىدايەت قىلىش سېنىڭ مەسئۇلىيىتىڭ ئەمەس (ساڭا تاپشۇرۇلغان ۋەزىپە پەقەت كىشىلەرگە اﷲ نىڭ ۋەھىسىنى يەتكۈزۈشتۇر) لېكىن اﷲ خالىغان بەندىسىنى ھىدايەت قىلىدۇ مېلىڭلاردىن نېمىنى خەير ـ ئېھسان قىلماڭلار، ئۇ ئۆزۈڭلارنىڭ پايدىسى ئۈچۈندۇر، سىلەر پەقەت اﷲ نىڭ رازىلىقى ئۈچۈنلا خەير ـ ئېھسان قىلىسىلەر (يەنى خەير ـ ئېھسانىڭلار ئۈچۈن اﷲ نىڭ رازىلىقىدىن باشقىنى كۆزدە تۇتماڭلار)، مېلىڭلاردىن نېمىنى خەير ـ ئېھسان قىلماڭلار، سىلەرگە ئۇنىڭ ساۋابى تولۇق بېرىلىدۇ، سىلەرگە زۇلۇم قىلىنمايدۇ (يەنى قىلغان ياخشى ئەمەلىڭلارنىڭ ساۋابى كېمەيتىلمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,273,' ئۆزىنى اﷲ يولىغا ئاتىغان، (تىرىكچىلىك قىلىش ئۈچۈن) يىراق جايلارغا بارالمايدىغان يوقسۇللارغا خەير ـ ئېھسان قىلىنىشى كېرەك ئەھۋالنى ئۇقمىغانلار، ئۇلارنىڭ نەرسە تىلىمىگەنلىكلىرىگە قاراپ، ئۇلارنى باي دەپ گۇمان قىلىدۇ، ئۇلارنىڭ چىرايىدىن تونۇيسەن، ئۇلار كىشىلەرگە چاپلىشىۋېلىپ تىلەمچىلىك قىلمايدۇ، مېلىڭلاردىن نېمىنى خەير ـ ئېھسان قىلماڭلار، اﷲ ھەقىقەتەن ئۇنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,274,' ماللىرىنى كېچە ـ كۈندۈز (يەنى ھەممە ۋاقىت)، يوشۇرۇن ۋە ئاشكارا يوسۇندا خەير ـ ئېھسان قىلىدىغانلار پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب تاپىدۇ، ئۇلارغا (ئاخىرەتتە) قورقۇنچ ۋە غەم ـ قايغۇ بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,275,' جازانە، ئۆسۈم يېگەن ئادەملەر (قىيامەت كۈنى گۆرلىرىدىن) جىن چېپىلىپ قالغان ساراڭ ئادەملەردەك قوپىدۇ بۇ شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ ھارام قىلغان ئىشنى ھالال بىلىپ: «سودا ـ سېتىق، جازانىگە ئوخشاش (جازانە نېمىشقا ھارام بولىدۇ؟)» دېدى اﷲ سودا ـ سېتىقنى ھالال قىلدى، جازانىنى، (بەدەلسىز بولغانلىقى، شەخسكە ۋە جەمئىيەتكە زىيانلىق بولغانلىقى ئۈچۈن) ھارام قىلدى كىمكى پەرۋەردىگارى تەرىپىدىن ۋەز ـ نەسىھەت كەلگەندىن كېيىن (يەنى جازانە مەنئى قىلىنغاندىن كېيىن ئۇنىڭدىن) يانسا، بۇرۇن ئالغىنى ئۆزىنىڭ بولىدۇ، ئۇنىڭ ئىشى اﷲ قا تاپشۇرۇلىدۇ (يەنى اﷲ خالىسا ئۈنى كەچۈرىدۇ، خالىسا جازالايدۇ) قايتا جازانە قىلغانلار ئەھلى دوزاخ بولۇپ، دوزاختا مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,276,' اﷲ جازانىنىڭ (بەرىكىتىنى) ئۆچۈرۈۋېتىدۇ، سەدىقىنىڭ (بەرىكىتىنى) زىيادە قىلىدۇ اﷲ ھەربىر ناشۈكۈر (يەنى جازانىنى ھالال سانىغۇچى) گۇناھكارنى دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,277,' ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، ناماز ئوقۇغان زاكات بەرگەن كىشىلەر پەرۋەردىگارىنىڭ دەرگاھىدا (چوڭ) ساۋابقا ئېرىشىدۇ، ئۇلاردا (ئاخىرەتتە) قورقۇنچ ۋە غەم ـ قايغۇ بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,278,' ئى ئىمان ئېيتقان كىشىلەر! (ھەقىقي) مۆمىن بولساڭلار، اﷲ (نىڭ ئەمرىگە مۇخالىپەتچىلىك) قىلىشتىن ساقلىنىڭلار، (كىشىلەرنىڭ زىممىسىدە) قېلىپ قالغان جازانىنى (يەنى ئۆسۈمنى) ئالماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,279,' ئەگەر ئۇنداق قىلساڭلار، بىلىڭلاركى، اﷲ ۋە ئۇنىڭ رەسۇلى سىلەرگە ئۇرۇش ئېلان قىلىدۇ، ئەگەر (جازانە قىلىشتىن) تەۋبە قىلساڭلار، (قەرز بەرگەن) سەرمايەڭلار ئۆزۈڭلارغا قايتىدۇ، باشقىلارنى زىيان تارتقۇزمايسىلەر، ئۆزۈڭلارمۇ زىيان تارتمايسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,280,' ئەگەر قەرزدارنىڭ قولى قىسقا بولسا، ئۇنىڭ ھالى ياخشىلانغۇچە كۈتۈڭلار، ئەگەر (خەيرلىك ئىش ئىكەنلىكىنى بىلسەڭلار، قىيىنچىلىقتا قالغان قەرزداردىن ئالىدىغان قەرزنى ئۇنىڭغا) سەدىقە قىلىپ بېرىۋەتكىنىڭلار سىلەر ئۈچۈن تېخىمۇ ياخشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,281,' سىلەر شۇنداق بىر كۈندىن (يەنى قىيامەت كۈنىدىن) قورقۇڭلاركى، ئۇ كۈندە سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، ئاندىن ھەر كىشى قىلغان (ياخشى ـ يامان) ئەمەلىنىڭ نەتىجىسىنى تولۇق كۆرىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ، (يەنى ياخشى ئەمەلى كېمەيتىلىپ، يامان ئەمەلى كۆپەيتىلمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,282,' ئى مۆمىنلەر! مۇددەت بەلگىلەپ ئۆزئارا قەرز بېرىشسەڭلار، ئۇنى (ھۆججەت قىلىپ) يېزىپ قويۇڭلار، ئاراڭلاردىكى خەت بىلىدىغان كىشى ئۇنى (كەم ـ زىيادە قىلماي) ئادىللىق بىلەن يازسۇن، خەت بىلىدىغان كىشى (اﷲ ئۇنىڭغا بىلدۈرگەندەك توغرىلىق بىلەن يېزىشىنى) رەت قىلمىسۇن، ئۇ يازسۇن، زىممىسىدە باشقىلارنىڭ ھەققى بولغان ئادەم (يازىدىغان كىشىگە) ئېيتىپ بەرسۇن، (قەرزدار) پەرۋەردىگارى اﷲ تىن قورقسۇن، قەرز ئالغان نەرسىدىن ھېچنېمىنى كېمەيتىۋەتمىسۇن، ئەگەر قەرز ئالغۇچى ئادەم ئەخمەق (يەنى خۇدبىن، ئەقىلسىز، ئىسراپخور) ياكى ئاجىز (يەنى كىچىك بالا ياكى بەكمۇ قېرى) بولسا، ياكى (گەپ قىلالماسلىقى، گاچىلىقى، كېكەچلىكى تۈپەيلىدىن) ئۆزى ئېيتىپ بېرەلمىسە، ئۇنىڭ ئىشلىرىنى باشقۇرغۇچى ئادەم ئادىللىق بىلەن ئېيتىپ بەرسۇن، سىلەر ئەر كىشىڭلاردىن ئىككى كىشىنى گۇۋاھلىققا تەكلىپ قىلىڭلار؛ ئەگەر ئىككى ئەر كىشى يوق بولسا، سىلەر (ئادالىتىگە، دىيانىتىگە) رازى بولىدىغان كىشىلەردىن بىر ئەر، ئىككى ئايالنى گۇۋاھلىققا تەكلىپ قىلىڭلار، بۇ ئىككى ئايالنىڭ بىرسى ئۇنتۇپ قالسا، ئككىنچىسى ئېسىگە سالىدۇ گۇۋاھچىلار (گۇۋاھلىققا) چاقىرىلغان ۋاقتىدا (گۇۋاھ بولۇشتىن) باش تارتمىسۇن قەرز مەيلى ئاز بولسۇن، مەيلى كۆپ بولسۇن، ئۇنى قايتۇرۇش ۋاقتى بىلەن قوشۇپ يېزىشتىن ئېرىنمەڭلار اﷲ نىڭ نەزىرىدە (يەنى ھۆكمىدە)، بۇ ئەڭ ئادىللىق ۋە ئەڭ ئىسپاتلىقتۇر (يەنى گۇۋاھلىقنىڭ ئۇنتۇلماسلىقىنى ئەڭ ئىسپاتلىغۇچىدۇر)، گۇمانلانماسلىقىڭلارغا ئەڭ يېقىندۇر لېكىن ئاراڭلاردا قىلىشقان قولمۇقول سودىدا ھۆججەت يازمىساڭلارمۇ ھېچ گۇناھ بولمايدۇ ئۆزئارا سودا قىلىشقان ۋاقتىڭلاردا گۇۋاھچى تەكلىپ قىلىڭلار، پۈتۈكچىگىمۇ، گۇۋاھچىغىمۇ زىيان يەتكۈزۈلمىسۇن؛ ئەگەر زىيان يەتكۈزسەڭلار گۇناھ ئۆتكۈزگەن بولىسىلەر، اﷲ نىڭ (ئەمرىگە ۋە نەھيىسىگە مۇخالىپەتچىلىك قىلىشتىن) ساقلىنىڭلار، اﷲ سىلەرگە ئۆگىتىدۇ (يەنى سىلەرگە ئىككىلا دۇنيادا پايدىلىق بولغان ئىلىمنى بېرىدۇ)، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,283,' ئەگەر سىلەر سەپەر ئۈستىدە بولۇپ، خەت يازالايدىغان ئادەمنى تاپالمىساڭلار، بۇ چاغدا (قەرز ھۆججىتى يېزىپ بېرىشنىڭ ئورنىغا) گۆرۈ قويىدىغان نەرسىنى تاپشۇرساڭلار بولىدۇ؛ ئاراڭلاردا بىرىڭلار بىرىڭلارغا ئىشىنىپ ئامانەت قويسا، ئۇ چاغدا ئامانەتنى ئالغۇچى ئادەم ئامانەتنى تاپشۇرسۇن (يەنى قەرز بەرگۈچى قەرز ئالغۇچىغا ئىشىنىپ ئۇنىڭدىن بىر نەرسىنى گۆرۈ ئالمىسا، قەرزدار قەرزنى دېيىشكەن ۋاقتىدا قايتۇرسۇن)، (قەرزدار ئامانەتكە رىئايە قىلىشتا) پەرۋەردىگارىدىن قورقسۇن گۇۋاھلىقنى يوشۇرماڭلار، كىمكى ئۇنى يوشۇرىدىكەن، ھەقىقەتەن ئۇنىڭ دىلى گۇناھكار بولىدۇ اﷲ قىلمىشىڭلارنى تامامەن بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,284,' ئاسمانلاردىكى، زېمىندىكى شەيئىلەر (ئىگىدارچىلىق قىلىش جەھەتتىن بولسۇن، تەسەررۇپ قىلىش جەھەتتىن بولسۇن) اﷲ نىڭ ئىلكىدىدۇر دىلىڭلاردىكى (يامانلىقنى) مەيلى ئاشكارا قىلىڭلار، مەيلى يوشۇرۇڭلار، ئۇنىڭ ئۈچۈن اﷲ سىلەردىن ھېساب ئالىدۇ اﷲ خالىغان كىشىگە مەغپىرەت قىلىدۇ، خالىغان كىشىگە ئازاب قىلىدۇ اﷲ ھەممە نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,285,' پەيغەمبەر پەرۋەردىگارى تەرىپىدىن ئۇنىڭغا نازىل قىلىنغان كىتابغا ئىمان كەلتۈردى، مۆمىنلەرمۇ ئىمان كەلتۈردى، ئۇلارنىڭ ھەممىسى اﷲ قا ۋە اﷲ نىڭ پەرىشتىلىرىگە، كىتابلىرىغا ۋە پەيغەمبەرلىرىگە ئىمان كەلتۈردى (ئۇلار) «اﷲ نىڭ پەيغەمبەرلىرىنىڭ ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ، بەزىسىگە ئىمان ئېيتماي قالمايمىز)» دەيدۇ ئۇلار: «بىز (دەۋىتىڭنى) ئاڭلىدۇق ۋە (ئەمرىڭگە) ئىتائەت قىلدۇق، پەرۋەردىگارىمىز، مەغپىرىتىڭنى تىلەيمىز، ئاخىر يانىدىغان جايىمىز سېنىڭ دەرگاھىڭدۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,2,286,' اﷲ ھېچكىمنى تاقىتى يەتمەيدىغان ئىشقا تەكلىپ قىلمايدۇ كىشىنىڭ قىلغان ياخشىلىقى (نىڭ ساۋابى) ئۆزىگىدۇر، يامانلىقى (نىڭ جازاسى) مۇ ئۆزىگىدۇر (ئۇلار) «پەرۋەردىگارىمىز! ئەگەر بىز ئۇنتۇساق ياكى خاتالاشساق (يەنى بىز ئۇنتۇش ياكى سەۋەنلىك سەۋەبىدىن ئەمرىڭنى تولۇق ئورۇنلىيالمىساق)، بىزنى جازاغا تارتمىغىن پەرۋەردىگارىمىز! بىزدىن ئىلگىرىكىلەرگە يۈكلىگىنىڭگە ئوخشاش، بىزگە ئېغىر يۈك يۈكلىمىگىن (يەنى بىزنى قىيىن ئىشلارغا تەكلىپ قىلمىغىن)، پەرۋەردىگارىمىز! كۈچىمىز يەتمەيدىغان نەرسىنى بىزگە ئارتمىغىن، بىزنى كەچۈرگىن، بىزگە مەغپىرەت قىلغىن، بىزگە رەھىم قىلغىن، سەن بىزنىڭ ئىگىمىزسەن، كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,1,'ئەلىف، لام، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,2,' اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، اﷲ (ھەمىشە) تىرىكتۇر، مەخلۇقاتنى ئىدارە قىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,3,' (ئى مۇھەممەد!) ئۆزىدىن ئىلگىرىكى كىتابلارنى تەستىق قىلغۇچى ھەق كىتابنى (يەنى قۇرئاننى) (اﷲ) ساڭا نازىل قىلدى ئىلگىرى، كىشىلەرگە يول كۆرسەتكۈچى قىلىپ تەۋرات بىلەن ئىنجىلنى نازىل قىلغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,4,' ھەق بىلەن باتىلنى ئايرىغۇچى كىتابنى (يەنى بارلىق ساماۋى كىتابلارنى) نازىل قىلدى شۈبھىسىزكى، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ اﷲ غالىبتۇر، (يامانلارنى) جازالىغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,5,' شۈبھىسىزكى، ئاسمان ۋە زېمىندىكى ھېچ نەرسە اﷲ قا مەخپىي ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,6,' ئۇ سىلەرنى بەچچىدانلاردا (يەنى ئاناڭلارنىڭ قورسىقىدىكى چېغىڭلاردا) ئۆزى خالىغان شەكىلگە كىرگۈزىدۇ ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,7,' (ئى مۇھەممەد!) ئۇ (اﷲ) ساڭا كىتابنى (يەنى قۇرئاننى) نازىل قىلدى ئۇنىڭدا مەھكەم (يەنى مەنىسى ئوچۇق) ئايەتلەر باركى، ئۇلار كىتابنىڭ (يەنى پۈتۈن قۇرئاننىڭ) ئاساسىدۇر؛ يەنە باشقا مۇتەشابىھ (يەنى مەنىسى مۇئەييەن ئەمەس) ئايەتلەر باردۇر دىللىرىدا ئەگرىلىك بار (يەنى گۇمراھلىققا مايىل) كىشىلەر پىتنە قوزغاش ۋە ئۆز رايى بويىچە مەنە بېرىش ئۈچۈن، مۇتەشابىھ ئايەتلەرگە ئەگىشىدۇ (يەنى مۇتەشابىھ ئايەتلەرنى ئۆز نەپسى خاھىشى بويىچە چۈشەندۈرىدۇ) بۇنداق ئايەتلەرنىڭ (ھەقىقىي) مەنىسىنى پەقەت اﷲ بىلىدۇ ئىلىمدا توشقانلار ئېيتىدۇ: «ئۇنىڭغا ئىشەندۇق، ھەممىسى پەرۋەردىگارىمىز تەرىپىدىن نازىل بولغان (بۇنى) پەقەت ئەقىل ئىگىلىرىلا چۈشىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,8,' پەرۋەردىگارىمىز! بىزنى ھىدايەت قىلغىنىڭدىن كېيىن دىللىرىمىزنى توغرا يولدىن بۇرىۋەتمىگىن، بىزگە دەرگاھىڭدىن رەھمەت بېغىشلىغىن شۈبھىسىزكى، سەن (بەندىلىرىڭگە ئاتالارنى) بەكمۇ بېغىشلىغۇچىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,9,' پەرۋەردىگارىمىز! شۈبھىسىزكى، كېلىشى شەكسىز بولغان كۈندە (يەنى ھېساب ئېلىنىدىغان قىيامەت كۈنىدە) كىشىلەرنى توپلىغۇچىسەن شۈبھىسىزكى، اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,10,' كاپىرلارنىڭ ماللىرى، بالىلىرى اﷲ نىڭ ئازابى ئالدىدا ھەقىقەتەن ھېچ نەرسىگە دال بولالمايدۇ ئۇلار (يەنى كۇففارلار) دوزاخنىڭ يېقىلغۇسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,11,' (بۇ كۇففارلارنىڭ ھالى) پىرئەۋن تەۋەلىرىنىڭ ۋە ئۇلاردىن ئىلگىرىكىلەرنىڭ ھالىغا ئوخشايدۇ ئۇلار بىزنىڭ ئايەتلىرىمىزنى يالغانغا چىقاردى، گۇناھى تۈپەيلىدىن اﷲ ئۇلارنى جازالىدى، اﷲ نىڭ جازاسى قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,12,' (ئى مۇھەممەد!) كاپىرلارغا ئېيتقىنكى، «سىلەر (دۇنيادا) مەغلۇپ قىلىنىسىلەر ۋە (ئاخىرەتتە) جەھەننەمگە توپلىنىسىلەر، جەھەننەم نېمىدېگەن يامان تۆشەك»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,13,' (ئى يەھۇدىيلار جامائەسى! بەدرىدە جەڭ ئۈچۈن) ئۇچراشقان ئىككى گۇرۇھتا سىلەر ئۈچۈن ئىبرەت بار، بىرى اﷲ يولىدا ئۇرۇشۇۋاتقان گۇرۇھتۇر، يەنى بىر مۇسۇلمانلارنى (سانىنى) ئۆزلىرىدەك ئىككى باراۋەر ئوپئوچۇق كۆرگەن كاپىرلار گۇرۇھىدۇر (يەنى اﷲ كاپىرلارغا قورقۇنچ سېلىش بىلەن ئۇلارنى ئۇرۇشتىن قول يىغدۇرۇش ئۈچۈن، كاپىرلارغا مۇسۇلمانلارنىڭ سانىنى جىق كۆرسەتتى) اﷲ خالىغان كىشىگە ياردەم بېرىپ قوللايدۇ بۇنىڭدا ھەقىقەتەن ئەقىل ئىگىلىرى ئۈچۈن ئىبرەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,14,' ئاياللار، ئوغۇللار، ئالتۇن ـ كۈمۈشتىن توپلانغان كۆپ ماللار، ئارغىماقلار، چارۋىلار ۋە ئېكىنلەردىن ئىبارەت كۆڭۈل تارتىدىغان نەرسىلەرنىڭ مۇھەببىتى ئىنسانلارغا چىرايلىق كۆرسىتىلدى ئۇلار دۇنيا تىرىكچىلىكىدە مەنپەئەتلىنىدىغان (باقاسى يوق) شەيئىلەردۇر؛ اﷲ نىڭ دەرگاھىدا بولسا قايتىدىغان گۈزەل جاي (يەنى جەننەت) باردۇر (شۇنىڭ ئۈچۈن باشقىغا ئەمەس، جەننەتكە قىزىقىش كېرەك)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,15,' (ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، «سىلەرگە ئۇلاردىنمۇ (يەنى دۇنيانىڭ زىبۇزىننەتلىرى ۋە نېمەتلىرىدىنمۇ) ياخشى بولغان نەرسىلەرنى ئېيتىپ بېرەيمۇ؟ تەقۋادارلار ئۈچۈن پەرۋەردىگارى ھۇزۇرىدا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەر بولۇپ، (ئۇلار) جەننەتلەردە مەڭگۈ قالىدۇ، (جەننەتلەردە) پاك جۈپتىلەر بار، (تەقۋادارلار ئۈچۈن) يەنە اﷲ نىڭ رەزاسى بار» اﷲ بەندىلىرىنى (يەنى ئۇلارنىڭ ھەممە ئىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,16,' ئۇلار (يەنى تەقۋادارلار): «پەرۋەردىگارىمىز! بىز شۈبھىسىز ئىمان ئېيتتۇق، بىزنىڭ گۇناھلىرىمىزنى مەغپىرەت قىلغىن، بىزنى دوزاخ ئازابىدىن ساقلىغىن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,17,' ئۇلار (يەنى تەقۋادارلار) سەۋر قىلغۇچىلاردۇر، راستچىللاردۇر، (اﷲ قا) ئىتائەت قىلغۇچىلاردۇر، (ياخشىلىق يوللىرىغا پۇل ـ ماللىرىنى) سەرپ قىلغۇچىلاردۇر ۋە سەھەرلەردە ئىستىغپار ئېيتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,18,' اﷲ ئادالەتنى بەرپا قىلغان ھالدا گۇۋاھلىق بەردىكى، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر پەرىشتىلەرمۇ، ئىلىم ئەھلىلىرىمۇ شۇنداق گۇۋاھلىق بەردى: ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر ئۇ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,19,' ھەقىقەتەن اﷲ نىڭ نەزىرىدە مەقبۇل دىن ئىسلام (دىنى) دۇر كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) پەقەت ئۆزلىرىگە ئىلىم كەلگەندىن كېيىنلا ئۆزئارا ھەسەت قىلىشىپ ئىختىلاپ قىلىشتى (يەنى ئىسلامنىڭ ھەقلىقىنى، مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىنى روشەن پاكىتلار ئارقىلىق بىلىپ تۇرۇپ، كۆرەلمەسلىكتىن ئىنكار قىلىشتى)، كىمكى اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلىدىكەن (اﷲ ئۇنى يېقىندا جازالايدۇ)، اﷲ تېز ھېساب ئالغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,20,' (ئى مۇھەممەد!) ئەگەر ئۇلار سەن بىلەن (دىن بارىسىدا) مۇنازىرەلەشسە: «ماڭا ئەگەشكەنلەر بىلەن بىرلىكتە، مەن ئۆزۈمنى اﷲ قا تاپشۇردۇم (يەنى اﷲ نىڭ ئەمرلىرىگە بويسۇنغۇچىمىز)» دېگىن كىتاب بېرىلگەنلەرگە (يەنى يەھۇدىيلار ۋە ناسارالارغا) ۋە ئۈممىلەرگە (يەنى ئەرەب مۇشرىكلىرىغا) : «مۇسۇلمان بولدۇڭلارمۇ؟» دېگىن، ئەگەر ئۇلار مۇسۇلمان بولسا، توغرا يول تاپقان بولىدۇ ئەگەر ئۇلار يۈز ئۆرۈسە (ساڭا ھېچ زىيىنى يوق)، سېنىڭ پەقەت تەبلىغ قىلىش مەسئۇلىيىتىڭ بار، اﷲ بەندىلىرىنى (يەنى ھەممە ئەھۋالىنى) كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,21,' اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلىدىغانلار، پەيغەمبەرلەرنى ناھەق ئۆلتۈرىدىغانلار، كىشىلەرنىڭ ئىچىدىكى ئادالەتكە بۇيرۇيدىغانلار (يەنى اﷲ قا دەۋەت قىلغۇچىلار) نى ئۆلتۈرىدىغانلارغا چوقۇم قاتتىق ئازاب بىلەن خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,22,' ئەنە شۇلارنىڭ دۇنيا ۋە ئاخىرەتتىكى ئەمەللىرى بىكار بولدى، ئۇلارغا ھېچ ياردەم قىلغۇچى يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,23,' كىتابتن نېسىۋە بېرىلگەنلەر (يەنى يەھۇدىيلارنىڭ ئۆلىمالىرى) نى كۆرمىدىڭمۇ؟ ئۇلار ئۆز ئارىسىدا (دەتالاش قىلىشقان ئىشلار توغرىسىدا) اﷲ نىڭ كىتابى بويىچە ھۆكۈم چىقىرىشقا چاقىرىلسا، ئۇلارنىڭ بىر تۈركۈمى بۇنىڭدىن (يەنى اﷲ نىڭ ھۆكىمىنى قوبۇل قىلىشتىن) يۈز ئۆرۈيدۇ، ئۇلار ھەقتىن يۈز ئۆرۈگۈچىلەردۇر (يەنى ھەقتىن يۈز ئۆرۈش ئۇلارنىڭ تەبىئىتىگە سىڭىپ كەتكەن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,24,' بۇ ئۇلارنىڭ «دوزاخ ئوتى بىزنى ساناقلىق كۈنلەرلا (يەنى موزايغا چوقۇنغان  كۈنلۈك ۋاقىتلا) كۆيدۈرىدۇ» دېگەنلىكلىرى ۋە دىنىي جەھەتتە ئويدۇرۇپ چىقارغان نەرسىلىرىنىڭ ئۆزلىرىنى ئالدىغانلىقى ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,25,' كېلىشى شەكسىز بولغان كۈندە (يەنى قىيامەت كۈنىدە) ئۇلارنى توپلايمىز، ھەر بىر كىشىنىڭ قىلغان ئىشىنىڭ جازاسى ۋە مۇكاپاتى تولۇق بېرىلىدىغان، ئۇلارغا (ئازابنى زىيادە قىلىۋېتىش ياكى ساۋابىنى كېمەيتىۋېتىش بىلەن) زۇلۇم قىلىنمايدىغان شۇ چاغدا (ئۇلارنىڭ ھالى) قانداق بولىدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,26,' ئېيتىقىنكى، «پادىشاھلىقنىڭ ئىگىسى بولغان ئى اﷲ! خالىغان ئادەمگە پادىشاھلىقنى بېرىسەن، خالىغان ئادەمدىن پادىشاھلىقنى تارتىپ ئالىسەن؛ خالىغان ئادەمنى ئەزىز قىلىسەن، خالىغان ئادەمنى خار قىلىسەن؛ ھەممە ياخشىلىق (نىڭ خەزىنىسى يالغۇز) سېنىڭ قولۇڭدىدۇر سەن ھەقىقەتەن ھەممىگە قادىرسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,27,' كېچىنى كۈندۈزگە كىرگۈزىسەن، كۈندۈزنى كېچىگە كىرگۈزىسەن (شۇنىڭ بىلەن، كېچە ـ كۈندۈزنىڭ ئۇزۇن ـ قىسقا بولۇشى پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)؛ جانلىقنى (يەنى تىرىك بولغان ئىنساننى، ھايۋاننى) جانسىزدىن (يەنى جانلىق بولغان ئابىمەنىيدىن) چىقىرىسەن، جانسىزنى (يەنى جانسىز بولغان ئابىمەنىينى) جانلىقتىن (يەنى جانلىق بولغان ئىنساندىن، ھايۋاندىن) چىقىرىسەن؛ ئۆزۈڭ خالىغان كىشىگە ھېسابسىز رىزىق بېرىسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,28,' مۆمىنلەر مۆمىنلەرنى قويۇپ كاپىرلارنى دوست تۇتمىسۇن، كىمكى شۇنداق قىلىدىكەن، ئۇ اﷲ نىڭ دوستلۇقىغا ئېرىشەلمەيدۇ، ئۇلاردىن قورقۇپ دوستلۇق ئىزھار قىلىشىڭلار بۇنىڭدىن مۇستەسنا اﷲ سىلەرنى ئۆزىنىڭ ئازابىدىن قورقۇتىدۇ، ئاخىر قايتىدىغان جاي اﷲ نىڭ دەرگاھىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,29,' ئېيتقىنكى، «دىلىڭلاردىكىنى مەيلى يوشۇرۇڭلار، مەيلى ئاشكارىلاڭلار، اﷲ ئۇنى بىلىپ تۇرىدۇ اﷲ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىمۇ بىلىپ تۇرىدۇ اﷲ ھەر نەرسىگە قادىردۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,30,' ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ھەر ئادەمنىڭ قىلغان ياخشى ئىشلىرى، يامان ئىشلىرى ئۆزىنىڭ ئالدىدا ئايان بولىدۇ، (ئۇلار يامان ئىشلىرىنى كۆرمەسلىكنى)، ئۆزى بىلەن يامان ئىشلىرىنىڭ ئارىسىدا (مەشرىق بىلەن مەغرىبنىڭ ئارىسىدەك) ئۇزۇن مۇساپە بولۇشىنى ئارزۇ قىلىدۇ اﷲ سىلەرنى ئۆزىنىڭ ئازابىدىن قورقۇتىدۇ اﷲ بەندىلىرىگە مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,31,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئەگەر سىلەر اﷲ نى دوست تۇتساڭلار، ماڭا ئەگىشىڭلاركى، اﷲ سىلەرنى دوست تۇتىدۇ، (ئۆتكەنكى) گۇناھىڭلارنى مەغپىرەت قىلىدۇ اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,32,' ئېيتقىنكى، «اﷲ قا ۋە پەيغەمبەرگە ئىتائەت قىلىڭلار» ئەگەر ئۇلار (ئىتائەت قىلىشتىن) يۈز ئۆرۈسە، اﷲ كاپىرلارنى (يەنى اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلارنى) دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,33,' اﷲ ھەقىقەتەن ئادەمنى، نۇھنى، ئىبراھىم ئەۋلادىنى، ئىمران ئەۋلادىنى (پەيغەمبەرلىككە) تاللاپ، (ئۇلارنى ئۆزلىرىنىڭ زامانىدىكى) جاھان ئەھلىدىن ئارتۇق قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,34,' ئۇ ئەۋلادلار بىر ـ بىرى بىلەن قانداشتۇر، اﷲ (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (بەندىلەرنىڭ نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,35,' ئۆز ۋاقتىدا ئىمراننىڭ ئايالى ئېيتتى: «پەرۋەردىگارىم! مەن قورسىقىمدىكى پەرزەنتىمنى (دۇنيا ئىشلىرىدىن) ئازاد قىلىنغان ھالدا چوقۇم سېنىڭ خىزمىتىڭگە ئاتىدىم، (بۇ نەزرەمنى) قوبۇل قىلغىن، سەن ھەقىقەتەن (دۇئايىمنى) ئاڭلاپ تۇرغۇچىسەن، (نىيىتىمنى) بىلىپ تۇرغۇچىسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,36,' ئۇ قىز تۇغقان ۋاقتىدا: «ئى پەرۋەردىگارىم! قىز تۇغدۇم» دېدى، ئۇنىڭ نېمە تۇغقانلىقىنى اﷲ ئوبدان بىلىدۇ (ئۇ تەلەپ قىلغان) ئوغۇل (ئاتا قىلىنغان) قىزغا ئوخشاش ئەمەس (بەلكى بۇ قىز ئارتۇقتۇر) (ئىمراننىڭ ئايالى ئېيتتىكى) «مەن ئۇنى مەريەم ئاتىدىم، مەن ھەقىقەتەن ئۇنىڭ ئۈچۈن، ئۇنىڭ ئەۋلادلىرى ئۈچۈن ساڭا سېغىنىپ، قوغلاندى شەيتاندىن پاناھ تىلەيمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,37,' ئۇنى پەرۋەردىگارى چىرايلىقچە قوبۇل قىلدى، ئۇنى ئوبدان ئۆستۈردى، ئۇنىڭغا زەكەرىيانى كېپىل قىلدى (يەنى ئۇنىڭ خىزمىتىنى قىلىشقا زەكەرىيانى قويدى) زەكەرىيا ھەر قېتىم (ئۇنىڭ) ئىبادەتگاھىغا ئۇنى كۆرگىلى كىرسە، ئۇنىڭ يېنىدا بىرەر يېمەكلىك ئۇچرىتاتتى ئۇ: «ئى مەريەم! بۇ ساڭا قەيەردىن كەلدى؟» دەيتتى، مەريەم: «ئۇ، اﷲ تەرىپىدىن كەلدى» دەيتتى اﷲ ھەقىقەتەن خالىغان ئادەمگە (جاپا ـ مۇشەققەتسىزلا) ھېسابسىز رىزىق بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,38,' شۇ ۋاقىتتا (يەنى مەريەمنىڭ كارامىتىنى كۆرگەن چاغدا) زەكەرىيا پەرۋەردىگارىغا دۇئا قىلىپ: «ئى پەرۋەردىگارىم! ماڭا ئۆز دەرگاھىڭدىن بىر ياخشى پەرزەنت ئاتا قىلغىن، ھەقىقەتەن دۇئانى ئىشىتكۈچىسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,39,' ئۇ ئىبادەتگاھىدا ناماز ئوقۇۋاتقاندا پەرىشتىلەر ئۇنىڭغا: «ساڭا اﷲ نىڭ كەلىمىسى (يەنى ئىسا) نى تەستىق قىلغۇچى، سەييىد، پەرھىزكار ۋە ياخشىلاردىن بولغان يەھيا ئىسىملىك بىر پەيغەمبەر بىلەن خۇش خەۋەر بېرىدۇ» دەپ نىدا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,40,' ئۇ: «پەرۋەردىگارىم! مەن قېرىپ قالغان تۇرسام، ئايالىم تۇغۇتتىن قالغان تۇرسا قانداقمۇ مېنىڭ ئوغلۇم بولسۇن؟» دېدى پەرىشتىلەر: «اﷲ شۇنىڭدەك خالىغىنىنى قىلىدۇ (يەنى قۇدرىتى ئىلاھىيە ھېچ نەرسىدىن ئاجىز ئەمەس)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,41,' ئۇ: ‹‹پەرۋەردىگارىم! ماڭا (ئايالىمنىڭ ھامىلىدار بولغانلىقىغا) بىر نىشانە ئاتا قىلغىن» دېدى اﷲ: ‹‹ساڭا بولىدىغان نىشانە شۇكى، ئۈچ كۈنگىچە كىشىلەرگە سۆز قىلالمايسەن (يەنى زىكرى تەسبىھ ئېيتىشتىن باشقا سۆزگە تىلىڭ كەلمەيدۇ) پەقەت ئىشارەتلا قىلىسەن، (نېمىتىمگە شۈكۈر قىلىش يۈزىسىدىن) پەرۋەردىگارىڭنى كۆپ ياد ئەت، كەچتە، ئەتىگەندە تەسبىھ ئېيت» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,42,' پەرىشتىلەر ئېيتتى: «ئى مەريەم! اﷲ ھەقىقەتەن سېنى (پۈتۈن ئاياللار ئىچىدىن) تاللىدى، سېنى پاك قىلدى، سېنى پۈتۈن جاھان ئاياللىرىدىن ئارتۇق قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,43,' ئى مەريەم! پەرۋەردىگارىڭغا ئىتائەت قىل، سەجدە قىل ۋە رۇكۇ قىلغۇچىلار بىلەن بىللە رۇكۇ قىل»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,44,' (ئى مۇھەممەد!) بۇ ـ ساڭا ۋەھيى قىلىۋاتقىنىمىز ـ غەيب خەۋەرلىرىدىندۇر مەريەمنى ئۇلارنىڭ قايسىسى تەربىيىسىگە ئېلىش (مەسىلىسىدە چەك تاشلاش يۈزىسىدىن) قەلەملىرىنى (سۇغا) تاشلىغان چاغدا، سەن ئۇلارنىڭ يېنىدا يوق ئىدىڭ، (مەريەمنىڭ كىمنىڭ تەربىيىسىدە بولۇشىنى) ئۆزئارا جاڭجال قىلىشقانلىرىدا سەن ئۇلارنىڭ يېنىدا يوق ئىدىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,45,' ئۆز ۋاقتىدا پەرىشتىلەر ئېيتتى: «ئى مەريەم! اﷲ ساڭا (ئاتىنىڭ ۋاسىتىسىز) اﷲ نىڭ بىر كەلىمىسى (دىن تۆرەلگەن بىر بوۋاق) بىلەن خۇش خەۋەر بېرىدۇكى، ئۇنىڭ ئىسمى، مەسىھ مەريەم ئوغلى ئىسادۇر، ئۇ دۇنيا ۋە ئاخىرەتتە ئابرۇيلۇق ۋە اﷲ قا يېقىنلاردىن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,46,' ئۇ بۆشۈكتىمۇ (يەنى بوۋاقلىق چېغىدىمۇ)، ئوتتۇرا ياش بولغاندىمۇ كىشىلەرگە (پەيغەمبەرلەرنىڭ سۆزىنى) سۆزلەيدۇ ۋە (تەقۋادارلىقتا كامىل) ياخشى ئادەملەردىن بولىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,47,' مەريەم: «پەرۋەردىگارىم! ماڭا ئىنساننىڭ قولى تەگمىگەن تۇرسا (يەنى ئەرلىك بولمىسام) قانداق بالام بولىدۇ؟» دېدى پەرىشتە: «اﷲ خالىغىنىنى شۇنداق يارىتىدۇ (يەنى ئاتا ـ ئانا، ئارقىلىقمۇ ۋە ئۇنىڭسىزمۇ يارىتىدۇ) بىرەر ئىشنىڭ بولۇشىنى ئىرادە قىلسا، ۋۇجۇدقا كېلىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,48,' اﷲ ئۇنىڭغا خەتنى، ھېكمەتنى (يەنى سۆز ھەرىكەتتە توغرا بولۇشىنى)، تەۋراتنى، ئىنجىلنى ئۆگىتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,49,' ئۇنى بەنى ئىسرائىلغا پەيغەمبەر قىلىپ ئەۋەتىدۇ (ئۇ دەيدۇ) «شۈبھىسىزكى، سىلەرگە مەن رەببىڭلار تەرىپىدىن بولغان (پەيغەمبەرلىكىمنىڭ راستلىقىنى كۆرسىتىدىغان) بىر مۆجىزە ئېلىپ كەلدىم، مەن سىلەرگە لايدىن قۇشنىڭ شەكلىدەك بىر نەرسە ياسايمەن، ئاندىن ئۇنىڭغا پۈۋلەيمەن ـ دە، اﷲ نىڭ ئىزنى بىلەن، ئۇ قۇش بولىدۇ تۇغما كورنى، بەرەس كېسىلىنى ساقايتىمەن، اﷲ نىڭ ئىزنى بىلەن، ئۆلۈكلەرنى تىرىلدۈرىمەن، سىلەر يەيدىغان ۋە ئۆيۈڭلاردا ساقلايدىغان يېمەكلىكلەردىن خەۋەر بېرىمەن (يەنى سىلەرنىڭ يوشۇرۇن ئەھۋالىڭلارنى ئېيتىپ بېرەلەيمەن) شۈبھىسىزكى، ئەگەر سىلەر اﷲ نىڭ مۆجىزىلىرىگە ئىشەنگۈچى بولساڭلار، بۇنىڭدا (يەنى مەن كەلتۈرگەن مۆجىزىلەردە) سىلەر ئۈچۈن ئەلۋەتتە (مېنىڭ راستلىقىمنى كۆرسىتىدىغان روشەن) ئالامەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,50,' مەن ئىلگىرى كەلگەن تەۋراتنى تەستىق قىلغان ھالدا (كەلدىم)، سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن دەلىل ئېلىپ كەلدىم اﷲ تىن قورقۇڭلار ۋە (مېنىڭ ئەمرىمگە) ئىتائەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,51,' اﷲ ھەقىقەتەن مېنىڭ پەرۋەردىگارىمدۇر، سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,52,' ئىسا ئۇلارنى (يەنى يەھۇدىيلار) دىن كۇفرىنى (كۇفرىدا چىڭ تۇرۇپ، ئۇنى ئۆلتۈرۈش نىيىتىگە كەلگەنلىكىنى) سەزگەن چاغدا: «اﷲ ئۈچۈن (اﷲ قا دەۋەت قىلىش يولىدا) ماڭا كىملەر ياردەم بېرىدۇ؟» دېدى ھەۋارىلار (يەنى ئۇنىڭ تەۋەلىرىدىن ھەقىقىي مۆمىنلەر) ئېيتتى: «اﷲ ئۈچۈن ساڭا بىز ياردەم بېرىمىز، اﷲ قا ئىمان ئېيتتۇق گۇۋاھ بولغىنكى، بىز (پەيغەمبەرلىكىڭگە) بويسۇنغۇچىلارمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,53,' پەرۋەردىگارىمىز! سەن نازىل قىلغان كىتابقا ئىشەندۇق، پەيغەمبەرگە ئەگەشتۇق، بىزنى (ھىدايىتىڭگە) شاھىت بولغانلار قاتارىدا قىلغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,54,' ئۇلار مىكىر ئىشلەتتى (يەنى ئىسا ئەلەيھىسسالامنى ئۆلتۈرمەكچى بولدى)، اﷲ ئۇلارنىڭ مىكرىنى بەربات قىلدى (ئىسا ئەلەيھىسسالامنى قۇتقۇزۇپ ئاسمانغا ئېلىپ چىقىپ كەتتى) اﷲ مىكىر قىلغۇچىلارنىڭ جازاسىنى ئوبدان بەرگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,55,' ئۆز ۋاقتىدا اﷲ ئېيتتى: «ئى ئىسا! مەن سېنى (ئەجىلىڭ يەتكەندە) قەبزى روھ قىلىمەن، سېنى دەرگاھىمغا كۆتۈرىمەن (يەنى ئاسمانغا ئېلىپ چىقىمەن) سېنى كاپىرلاردىن پاك قىلىمەن (يەنى سېنى ئۆلتۈرمەكچى بولغان يامانلارنىڭ شەررىدىن ساقلايمەن)، ساڭا ئەگەشكەنلەرنى قىيامەتكىچە كاپىرلاردىن ئۈستۈن قىلىمەن ئاندىن مېنىڭ دەرگاھىمغا قايتىسىلەر، سىلەر ئىختىلاپ قىلىشقان ئىش (يەنى ئىسانىڭ ئىشى) ئۈستىدە ئاراڭلاردا مەن ھۆكۈم چىقىرىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,56,' كاپىرلارغا بولسا دۇنيا ۋە ئاخىرەتتە قاتتىق ئازاب قىلىمەن، ئۇلارغا (ئۇلاردىن اﷲ نىڭ ئازابىنى توسىدىغان) ھېچ مەدەتكار بولمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,57,' ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا كەلسەك، اﷲ ئۇلارنىڭ (قىلغان ياخشى ئەمەللىرىنىڭ) ئەجرىنى تولۇق بېرىدۇ اﷲ زالىملارنى دوست تۇتمايدۇ (زالىملارنى دوست تۇتمايدىغان زات قانداقمۇ بەندىلىرىگە زۇلۇم قىلسۇن؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,58,' (ئى مۇھەممەد!) بۇ بولسا ساڭا بىز ئوقۇپ بېرىۋاتقان ئايەتلەردۇر ۋە ھېكمەتلىك قۇرئاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,59,' شۈبھىسىزكى، اﷲ نىڭ نەزىرىدە ئىسانىڭ مىسالى (ئۇ ئاتىسىز يارىتىلغانلىقتىن) ئادەمنىڭ (يەنى ئادەم ئەلەيھىسسالامنىڭ) مىسالىغا ئوخشايدۇ ئادەمنى اﷲ (ئاتا ـ ئانىسىز) تۇپراقتىن ياراتتى، ئاندىن ئۇنىڭغا: «ۋۇجۇدقا كەل» دېدى ـ دە، ئۇ ۋۇجۇدقا كەلدى (ئىسانىڭ ئىشى ئادەمنىڭ ئىشىدىن ئەجەبلىنەرلىك ئەمەس)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,60,' (ئىسا ھەققىدىكى بۇ سۆز) اﷲ تەرىپىدىن نازىل بولغان ھەقىقەتتۇر، سەن شەك قىلغۇچىلاردىن بولمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,61,' سەن ئىسا توغرۇلۇق ھەقىقىي مەلۇماتقا ئىگە بولغىنىڭدىن كېيىن، كىملەركى سەن بىلەن مۇنازىرىلەشسە، سەن ئۇلارغا:«كېلىڭلار، ئوغۇللىرىمىزنى ۋە ئوغۇللىرىڭلارنى، ئاياللىرىمىزنى ۋە ئاياللىرىڭلارنى، ئۆزلىرىمىزنى ۋە ئۆزلىرىڭلارنى يىغىپ، اﷲ نىڭ لەنىتى يالغانچىلارغا بولسۇن، دەپ اﷲ قا يالۋۇرۇپ دۇئا قىلايلى» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,62,' شۈبھىسىزكى، بۇ ئەلۋەتتە راست قىسسىدۇر بىر اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,63,' ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە (ئۇلار بۇزغۇنچىلاردۇر)، شۈبھىسىزكى، اﷲ بۇزغۇنچىلارنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,64,' «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) پەقەت اﷲ غىلا ئىبادەت قىلىش، اﷲ قا ھېچ نەرسىنى شېرىك كەلتۈرمەسلىك، اﷲ نى قويۇپ بىر ـ بىرىمىزنى خۇدا قىلىۋالماسلىقتەك ھەممىمىزگە ئورتاق بولغان بىر سۆزگە (يەنى بىرخىل ئەقىدىگە) ئەمەل قىلايلى» دېگىن، ئەگەر ئۇلار يۈز ئۆرۈسە (يەنى بۇنى قوبۇل قىلمىسا): «(ئى يەھۇدىيلار ۋە ناسارالار جامائەسى!) بىزنىڭ مۇسۇلمان ئىكەنلىكىمىزگە گۇۋاھ بولۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,65,' ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) نېمە ئۈچۈن ئىبراھىم توغرىسىدا مۇنازىرىلىشىسىلەر؟ (ئىبراھىمنى ئۆزۈڭلارنىڭ دىنىدا دەپ گۇمان قىلىسىلەر؟) ھالبۇكى، تەۋرات بىلەن ئىنجىل ئىبراھىمدىن كېيىن نازىل بولدى (سۆزۈڭلارنىڭ ئاساسسىز ئىكەنلىكىنى) چۈشەنمەمسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,66,' (ئى يەھۇدىيلار ۋە ناسارالار جامائەسى!) سىلەر شۇنداق كىشىلەر سىلەركى، بىلىدىغان نەرسەڭلار (ئىسا) ئۈستىدە مۇنازىرىلەشتىڭلار، ئەمدى سىلەر بىلمەيدىغان نەرسەڭلار (ئىبراھىم ۋە ئۇنىڭ دىنى) ئۈستىدم نېمىشقا مۇنازىرىلىشىسىلەر؟ (ئىبراھىمنىڭ ئىشىدىكى ھەقىقەتنى) اﷲ بىلىدۇ، سىلەر بىلمەيسىلەر» دەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,67,' ئىبراھىم يەھۇدىيمۇ ئەمەس، ناسارامۇ ئەمەس ۋە لېكىن توغرا دىنغا ئېتىقاد قىلغۇچى، اﷲ قا بويسۇنغۇچى ئىدى، مۇشرىكلاردىن ئەمەس ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,68,' ئىبراھىمغا ئەڭ يېقىن بولغانلار، شۈبھىسىزكى، (ئىبراھىمنىڭ زامانىدا ۋە ئۇنىڭدىن كېيىن) ئۇنىڭغا (يەنى ئۇنىڭ يولىغا) ئەگەشكەنلەردۇر، بۇ پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) دۇر، (بۇ پەيغەمبەرنىڭ ئۈممىتى بولغان) مۆمىنلەردۇر اﷲ مۆمىنلەرنىڭ مەدەتكارىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,69,' ئەھلى كىتابتىن بىر تۈركۈم كىشىلەر سىلەرنى ئازدۇرۇشنى ئۈمىد قىلىدۇ، ئۇلار پەقەت ئۆزلىرىنىلا ئازدۇرىدۇ (بۇنىڭ ۋابالى ئۇلارغا ھەسسىلەپ ئازاب قىلىنىش بىلەن ئۆزلىرىگىلا قايتىدۇ) (لېكىن) ئۇلار (بۇنى) تۇيمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,70,' ئى ئەھلى كىتاب! اﷲ نىڭ ئايەتلىرىنى (يەنى مۇھەممەد ئەلەيھىسسالامغا نازىل قىلىنغان قۇرئاننىڭ ھەقلىقىنى) بىلىپ تۇرۇپ نېمىشقا ئىنكار قىلىسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,71,' ئى ئەھلى كىتاب! نېمە ئۈچۈن ھەقنى باتىلغا ئارىلاشتۇرىسىلەر ۋە بىلىپ تۇرۇپ ھەقنى (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ كىتابلىرىڭلاردىكى سۈپىتىنى) يوشۇرىسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,72,' ئەھلى كىتابتىن بىر تۈركۈمى: «مۆمىنلەرگە نازىل قىلىنغان ۋەھيىگە ئەتىگەندە ئىمان ئېيتىڭلار، كەچقۇرۇنلۇقى يېنىۋېلىڭلار، (شۇنداق قىلساڭلار) ئۇلار (ئىماندىن) قايتىشى مۇمكىن پەقەت سىلەرنىڭ دىنىڭلارغا ئەگەشكەنلەرگىلا ئىشىنىڭلار» دېدى (ئى مۇھەممەد!) ئېيتقىنكى، «توغرا يول اﷲ نىڭ يولىدۇر» (يەھۇدىيلار بى ـ بىرىگە ئېيتىدۇ) بىرەرسىگە سىلەرگە بېرىلگەنگە ئوخشاش ۋەھىي بېرىلىشتىن ياكى ئۇلارنىڭ (يەنى مۇسۇلمانلارنىڭ) (قىيامەت كۈنى) پەرۋەردىگارىڭلارنىڭ دەرگاھىدا سىلەر بىلەن مۇنازىرىلىشىشىدىن (يەنى مۇنازىرىلىشىپ سىلەرنى يېڭىپ قويۇشىدىن قورقۇپ) پەقەت دىنىڭلارغا ئەگەشكەن ئادەمگىلا ئىشىنىڭلار (دىنىڭلاردا بولمىغان ھېچ ئادەمنىڭ پەيغەمبەرلىكىنى ئېتىراپ قىلماڭلار ئەگەر سىلەر مۇھەممەدنىڭ پەيغەمبەرلىكىنى ئىقرار قىلىپ ئۇنىڭ دىنىغا كىرمىسەڭلار، بۇ قىيامەت كۈنى سىلەرگە قارشى پاكىت بولىدۇ) (ئى مۇھەممەد!) ئېيتقىنكى، «پەزل ـ كەرەم (يەنى پەيغەمبەرلىك) اﷲ نىڭ ئىلكىدىدۇر اﷲ ئۇنى خالىغان كىشىگە بېرىدۇ» اﷲ (نىڭ پەزلى) كەڭدۇر، (اﷲ) ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,73,' اﷲ رەھمىتىنى ئۆزى خالىغان كىشىگە خاس قىلىدۇ، اﷲ ئۇلۇغ پەزل ئىگىسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,74,' ئەھلى كىتابتىن بەزى كىشىلەر باركى، ئۇنىڭدا كۆپ مال ئامانەت قويساڭمۇ ساڭا ئۇنى قايتۇرۇپ بېرىدۇ؛ ئۇلاردىن يەنە بەزى كىشىلەرمۇ باركى، ئۇنىڭدا بىر دىنار ئامانەت قويساڭمۇ ئۇنىڭ بېشىدا تۇرمىغىچە (يەنى قايتا ـ قايتا سۈيلىمىگىچە) ئۇنى ساڭا قايتۇرۇپ بەرمەيدۇ بۇ شۇنىڭ ئۈچۈنكى، ئۇلار: «ئۈممىلەرنىڭ مېلىغا خىيانەت قىلساق بىزگە گۇناھ بولمايدۇ (يەنى بىزنىڭ دىنىمىزدا بولمىغانلارنىڭ ماللىرى بىزگە ھارام ئەمەس)» دېدى، ئۇلار بىلىپ تۇرۇپ اﷲ نامىدىن يالغاننى توقۇيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,75,' ئۇنداق ئەمەس (يەنى ئۇلار باشقا دىندىكىلەرگە زۇلۇم قىلسا گۇناھ بولىدۇ) ئەھدىگە ۋاپا قىلغان ۋە گۇناھتىن ساقلانغانلار (اﷲ دوست تۇتقان كىشىلەردۇر)، چۈنكى اﷲ تەقۋادارلارنى ھەقىقەتەن دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,76,' اﷲ قا بەرگەن ئەھدىنى ۋە قەسەملىرىنى ئازغىنا بەدەلگە تېگىشىدىغانلار ئاخىرەتتە (اﷲ نىڭ رەھمىتىدىن) ھېچقانداق نېسىۋىگە ئېرىشەلمەيدۇ، قىيامەت كۈنى اﷲ ئۇلارغا (ئۇلارنى خۇش قىلىدىغان) سۆز قىلمايدۇ، ئۇلارغا (رەھمەت كۆزى بىلەن) قارىمايدۇ، ئۇلارنى (گۇناھلىرىدىن) پاكلىمايدۇ، ئۇلار قاتتىق ئازابقا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,77,' ئۇلاردىن (يەنى يەھۇدىيلاردىن) بىر تۈركۈمى كىتابتا بولمىغان نەرسىلەرنى، سىلەرنىڭ كىتابتا بار ئىكەن دەپ ئويلىشىڭلار ئۈچۈن، تىللىرىنى ئەگرى ـ بۈگرى قىلىپ ئوقۇيدۇ ۋە ئۇنى اﷲ تەرىپىدىن نازىل بولغان دەيدۇ ھالبۇكى، ئۇ اﷲ تەرىپىدىن نازىل بولغان ئەمەس، ئۇلار بىلىپ تۇرۇپ اﷲ نامىدىن يالغاننى توقۇيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,78,' اﷲ بىرەر ئىنسانغا كىتابنى، ھېكمەتنى، پەيغەمبەرلىكنى بەرگەندىن كېيىن، ئۇنىڭ كىشىلەرگە: «اﷲ نى قويۇپ ماڭا بەندە بولۇڭلار» دېيىشى لايىق بولمايدۇ (يەنى پەيغەمبەردىن مۇنداق گەپنىڭ سادىر بولۇشىنى تەسەۋۋۇر قىلغىلى بولمايدۇ) ۋە لېكىن (ئۇ): «كىتابنى ئۆگەتكەنلىكىڭلار، ئوقۇغانلىقىڭلار ئۈچۈن رەببانى بولۇڭلار» (دەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,79,' شۇنىڭدەك ئۇنىڭ پەرىشتىلەرنى، پەيغەمبەرلەرنى رەب قىلىۋېلىشقا بۇيرۇشى لايىق بولمايدۇ مۇسۇلمان بولغىنىڭلاردىن كېيىن پەيغەمبىرىڭلار سىلەرنى كۇفرىغا بۇيرۇمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,80,' ئۆز ۋاقتىدىلا اﷲ پەيغەمبەرلەردىن چىن ئەھدە ئېلىپ: «سىلەرگە مەن كىتابنى ۋە ھېكمەتنى ئاتا قىلدىم، كېيىن سىلەرگە، سىلەردىكى نەرسىلەرنى (يەنى كىتاب بىلەن ھېكمەتنى) ئېتىراپ قىلغۇچى بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلسە ئۇنىڭغا، ئەلۋەتتە، ئىمان ئېيتىشىڭلار كېرەك ۋە ئۇنىڭغا ئەلۋەتتە ياردەم بېرىشىڭلار كېرەك» (دېدى) اﷲ: «(بۇ ئەھدىنى) ئىقرار (يەنى ئېتىراپ) قىلدىڭلارمۇ؟ شۇنىڭ (يەنە بۇ ئىش) ئۈچۈن مېنىڭ ئەھدىمنى قوبۇل قىلدىڭلارمۇ؟» دېدى ئۇلار: «ئېتىراپ قىلدۇق» دېدى اﷲ: «(ئۆزۈڭلارنىڭ ۋە تەۋەلىرىڭلارنىڭ ئېتىراپ قىلغانلىقىغا) گۇۋاھ بولۇڭلار، مەنمۇ سىلەر بىلەن بىللە گۇۋاھ بولغۇچىلاردىندۇرمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,81,' كىمكى شۇنىڭدىن كېيىن يۈز ئۆرۈسە (يەنى ئەھدىنى بۇزسا)، ئۇلار اﷲ نىڭ ئىتائىتىدىن چىققۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,82,' ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) اﷲ نىڭ دىنىدىن (يەنى ئىسلام دىنىدىن) باشقا دىننى تىلەمدۇ؟ ھالبۇكى، ئاسمانلاردىكىلەر ئىختىيارىي ۋە ئىختىيارسىز رەۋىشتە اﷲ قا بويسۇنغان تۇرسا، ئۇلار (قىيامەت كۈنى) اﷲ تەرىپىگە قايتۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,83,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «بىز اﷲ قا ئىمان ئېيتتۇق، بىزگە نازىل قىلىنغانغا (يەنى قۇرئانغا)، ئىبراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا ۋە ئۇنىڭ ئەۋلادلىرىغا نازىل قىلىنغان ۋەھيىگە، مۇساغا بېرىلگەنگە (يەنى ئىنجىلغا) ۋە پەيغەمبەرلەرگە پەرۋەردىگارى تەرىپىدىن بېرىلگەنگە (يەنى كىتابلارغا) ئىمان ئېيتتۇق، ئۇلارنىڭ ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ، بەزىسىنى ئىنكار قىلمايمىز)، بىز اﷲ قا بويسۇنغۇچىلارمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,84,' كىمكى ئىسلام (دىنىدىن) غەيرىي دىننى تىلەيدىكەن، ھەرگىز ئۇ (يەنى ئۇنىڭ دىنى) قوبۇل قىلىنمايدۇ، ئۇ ئاخىرەتتە زىيان تارتقۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,85,' ئىمان ئېيتقاندىن، پەيغەمبەرنىڭ ھەق ئىكەنلىكىگە شاھىت بولغاندىن ۋە ئۆزلىرىگە روشەن دەلىللەر كەلگەندىن كېيىن كاپىر بولغان قەۋمنى اﷲ قانداقمۇ ھىدايەت قىلسۇن! اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,86,' اﷲ نىڭ، پەرىشتىلەرنىڭ ۋە ئىنسانلارنىڭ ھەممىسىنىڭ لەنىتىگە ئۇچراش ئۇلارغا بېرىلگەن جازادۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,87,' ئۇلار دوزاختا مەڭگۈ قالىدۇ ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ كېچىكتۈرۈلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,88,' پەقەت تەۋبە قىلغانلار ۋە (بۇزۇق ئەمەللىرىنى) تۈزەتكەنلەر بۇنىڭدىن مۇستەسنا، اﷲ (مۇنداقلارغا) مەغپىرەت قىلغۇچىدۇر ۋە مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,89,' شۈبھىسىزكى، ئىمان ئېيتقاندىن كېيىن كاپىر بولغانلار، ئاندىن كۇفرىنى تېخىمۇ ئاشۇرغانلارنىڭ تەۋبىسى ھەرگىزمۇ قوبۇل قىلىنمايدۇ ئەنە شۇلار گۇمراھلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,90,' شۈبھىسىزكى، كاپىر بولۇپ (تەۋبە قىلماستىن) كۇفرى بىلەن ئۆلگەنلەرنىڭ ھېچبىرىدىن يەر يۈزىگە بىر كېلىدىغان ئالتۇن فىدىيە بەرسىمۇ قوبۇل قىلىنمايدۇ، ئەنە شۇلار قاتتىق ئازابقا قالىدۇ ئۇلارغا (ئۇلارنى اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچبىر مەدەتكار بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,91,' ياخشى كۆرگەن نەرسەڭلاردىن سەرپ قىلمىغۇچە (يەنى ماللىرىڭلارنىڭ ياخشىسىنى سەدىقە قىلمىغۇچە) ھەرگىز ياخشىلىققا (يەنى جەننەتكە) ئېرىشەلمەيسىلەر، (اﷲ نىڭ يولىدا) نېمىنى سەرپ قىلماڭلار اﷲ ئۇنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,92,' تەۋرات نازىل قىلىنىشتىن ئىلگىرى، ئىسرائىل (يەنى يەئقۇب ئەلەيھىسسالام) ئۆزىگە ھارام قىلغان يېمەكلىكلەر (يەنى تۆگىنىڭ گۆشى ۋە سۈتى) دىن باشقا يېمەكلىكلەرنىڭ ھەممىسى ئىسرائىل ئەۋلادىغا ھالال ئىدى (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئەگەر راستچىل بولساڭلار، تەۋراتنى ئېلىپ كېلىپ ئوقۇپ بېقىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,93,' شۇنىڭدىن كېيىن (يەنى روشەن دەلىل مەيدانغا كەلگەندىن كېيىن)، كىمكى اﷲ نامىدىن يالغاننى توقۇيدىكەن، ئۇلار زالىملاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,94,' ئېيتقىنكى، «اﷲ راست ئېيتتى، (ئىسلام دىنىدىن ئىبارەت) ئىبراھىمنىڭ توغرا دىنىغا ئەگىشىڭلار، ئۇ (يەنى ئىبراھىم) مۇشرىكلاردىن ئەمەس ئىدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,95,' ھەقىقەتەن ئىنسانلارغا (ئىبادەت ئۈچۈن) تۇنجى سېلىنغان ئۆي (يەنى بەيتۇللا) مەككىدىدۇر، مۇبارەكتۇر، جاھان ئەھلىگە ھىدايەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,96,' ئۇنىڭدا ئوچۇق ئالامەتلەر باركى، ماقامى ئىبراھىم شۇلارنىڭ بىرى، بەيتۇللاغا كىرگەن ئادەم ئەمىن بولىدۇ قادىر بولالىغان كىشىلەرنىڭ اﷲ ئۈچۈن كەبىنى زىيارەت قىلىشى ئۇلارغا پەرز قىلىندى كىمكى ئىنكار قىلىدىكەن (يەنى ھەجنى تەرك ئېتىدىكەن، زىيىنى ئۆزىگە)، شۈبھىسىزكى، اﷲ ئەھلى جاھاندىن (يەنى ئۇلارنىڭ ئىبادىتىدىن) بىھاجەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,97,' ئى مۇھەممەد! ئېيتقىنكى: «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار، ناسارالار) نېمە ئۈچۈن اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلىسىلەر؟ ۋەھالەنكى، اﷲ قىلمىشىڭلارغا شاھىتتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,98,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار، ناسارالار) (ئىسلام دىنىنىڭ ھەقلىقىغا) شاھىت بولۇپ تۇرۇپ نېمە ئۈچۈن اﷲ نىڭ يولىنى ئەگرى كۆرسىتىشنى قەستلەپ، مۆمىنلەرنى ئۇنىڭدىن توسىسىلەر؟ اﷲ قىلمىشىڭلاردىن غاپىل ئەمەستۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,99,' ئى مۆمىنلەر! ئەگەر ئەھلى كىتابتىن بىر پىرقىغە ئىتائەت قىلساڭلار، مۆمىن بولغىنىڭلاردىن كېيىن، ئۇلار سىلەرنى كاپىر قىلىپ قويىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,100,' سىلەرگە اﷲ نىڭ ئايەتلىرى ئوقۇلۇۋاتقان (يەنى ۋەھيى ئۈزۈلۈپ قالماي اﷲ نىڭ ئايەتلىرى نازىل بولۇۋاتقان) ۋە ئاراڭلاردا اﷲ نىڭ پەيغەمبىرى (ھايات) تۇرغان تۇرسا، قانداقمۇ كاپىر بولۇسىلەر؟ كىمكى اﷲ قا (يەنى اﷲ نىڭ دىنىغا) مەھكەم يېپىشىدىكەن، ئۇ توغرا يولغا يېتەكلەنگەن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,101,' ئى مۆمىنلەر! اﷲ قا لايىق رەۋىشتە تەقۋادارلىق قىلىڭلار، پەقەت مۇسۇلمانلىق ھالىتىڭلار بىلەنلا ۋاپات بولۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,102,' ھەممىڭلار اﷲ نىڭ ئاغامچىسىغا (يەنى اﷲ نىڭ دىنىغا) مەھكەم يېپىشىڭلار، ئايرىلماڭلار (يەنى سىلەردىن ئىلگىرى يەھۇدىيلار ۋە ناسارالار ئىختىلاپ قىلىشقاندەك، دىندا ئىختىلاپ قىلىشماڭلار) اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ئەسلەڭلار، ئۆز ۋاقتىدا سىلەر ئۆزئارا دۈشمەن ئىدىڭلار، اﷲ دىلىڭلارنى بىرلەشتۈردى، اﷲ نىڭ نېمىتى بىلەن ئۆزئارا قېرىنداش بولدۇڭلار، سىلەر دوزاخ چۇقۇرىنىڭ گىرۋىكىدە ئىدىڭلار، اﷲ سىلەرنى (ئىسلام ئارقىلىق) ئۇنىڭدىن قۇتقۇزدى سىلەرنىڭ ھىدايەت تېپىشىڭلار ئۈچۈن، اﷲ ئايەتلىرىنى سىلەرگە شۇنداق بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,103,' سىلەرنىڭ ئاراڭلاردا خەيرلىك ئىشلارغا دەۋەت قىلىدىغان، ياخشى ئىشلارغا بۇيرۇپ، يامان ئىشلارنى مەنئى قىلىدىغان بىر جامائە بولسۇن؛ ئەنە شۇلار مەقسىتىگە ئېرىشكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,104,' روشەن دەلىللەر كەلگەندىن كېيىن ئايرىلىپ كېتىشكەن ۋە ئىختىلاپ قىلىشقان كىشىلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دەك بولماڭلار، ئەنە شۇلار چوڭ ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,105,' شۇ كۈندە (يەنى قىيامەت كۈنىدە) بەزى يۈزلەر (يەنى مۆمىنلەرنىڭ يۈزلىرى) ئاقىرىدۇ، بەزى يۈزلەر (يەنى كۇففارلارنىڭ يۈزلىرى) قارىيىدۇ يۈزى قارايغانلارغا (پەرىشتىلەر ئېيتىدۇكى) «ئىمان ئېيتقىنىڭلاردىن كېيىن كاپىر بولدۇڭلارمۇ؟ كاپىر بولغانلىقىڭلار ئۈچۈن ئازابنى تېتىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,106,' يۈزى ئاقارغانلار اﷲ نىڭ رەھمىتىدە (يەنى جەننەتتە) بولىدۇ، ئۇلار ئۇنىڭدا (يەنى جەننەتتە) مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,107,' بۇ، اﷲ نىڭ ئايەتلىرىدۇر، ئۇلارنى (ئى مۇھەممەد!) ساڭا راست ئوقۇپ بېرىمىز، اﷲ جاھان ئەھلىگە زۇلۇم قىلىشنى خالىمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,108,' ئاسمانلار ۋە زېمىندىكى شەيئىلەر اﷲ نىڭ (مۈلكى، مەخلۇقاتى ۋە بەندىلىرى) دۇر، ھەممە ئىشلار اﷲ قا قايتۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,109,' (ئى مۇھەممەد ئۈممىتى!) سىلەر ئىنسانلار مەنپەئىتى ئۈچۈن ئوتتۇرىغا چىقىرىلغان ياخشىلىققا بۇيرۇپ يامانلىقتىن توسىدىغان اﷲ قا ئىمان ئېيتىدىغان ئەڭ ياخشى ئۈممەتىسلەر ئەگەر ئەھلى كىتاب (يەنى يەھۇدىيلار، ناسارالار) ئىمان ئېيتسا (يەنى مۇھەممەد ئەلەيھىسسالامغا نازىل قىلىنغان ۋەھيىگە ئىشەنسە)، ئۇلار ئۈچۈن (دۇنيا ۋە ئاخىرەتتە) ئەلۋەتتە ياخشى بولاتتى ئۇلارنىڭ ئىچىدە مۆمىنلەرمۇ بار، ئۇلارنىڭ تولىسى پاسىقلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,110,' ئۇلار سىلەرگە ئازار بېرىشتىن باشقا ھېچقانداق زىيان ـ زەخمەت يەتكۈزەلمەيدۇ؛ ئەگەر سىلەر بىلەن ئۇرۇشسا، سىلەرگە ئارقىسىنى قىلىپ قاچىدۇ، ئاندىن ئۇلارغا ياردەم بېرىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,111,' ئۇلارنىڭ قەيەردە بولمىسۇن اﷲ نىڭ ۋە مۇسۇلمانلارنىڭ ھىمايىسىدە بولغانلىرىدىن باشقىلىرى خارلىققا پېتىپ قالدى، اﷲ نىڭ غەزىپىگە ئۇچرىدى موھتاجلىققا چۆمدى بۇ ئۇلارنىڭ اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى ۋە پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى ئۈچۈندۇر، ئۇلارنىڭ اﷲ نىڭ پەرمانىدىن چىقىپ كەتكەنلىكلىرى ۋە اﷲ نىڭ چەكلىمىسىدىن ئېشىپ كەتكەنلىكلىرى ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,112,' ئۇلار بىرخىل ئەمەس، ئەھلى كىتابتىن (يەنى يەھۇدىيلار، ناسارالاردىن) كېچىلىرى اﷲ نىڭ ئايەتلىرىنى تىلاۋەت قىلىپ تۇرىدىغان ۋە سەجدە قىلىپ تۇرىدىغان بىر جامائە كىشىلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,113,' ئۇلار اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشىنىدۇ، ياخشى ئىشلارغا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، خەيرلىك ئىشلارنى قىلىشقا ئالدىرايدۇ؛ ئەنە شۇلار ياخشىلاردىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,114,' ئۇلار ھەر قانداق ياخشىلىقنى قىلسۇن، ئۇ بىكار كەتمەيدۇ اﷲ تەقۋادارلارنى ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,115,' شۈبھىسىزكى، كاپىرلارنىڭ ماللىرى، بالىلىرى اﷲ نىڭ ئازابى ئالدىدا ھەرگىز ھېچ نەرسىگە دال بولالمايدۇ؛ ئۇلار دوزاخ ئەھلىدۇر، دوزاختا مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,116,' ئۇلارنىڭ بۇ دۇنيادا (رىيا قىلىپ) بەرگەن سەدىقىلىرى ئۆزلىرىگە زۇلۇم قىلغان بىر قەۋمنىڭ زىرائىتىگە تېگىپ ئۇنى نابۇت قىلىۋەتكەن سوغۇق شامالغا ئوخشايدۇ (ئىگىلىرىنىڭ گۇناھى تۈپەيلىدىن نابۇت قىلىنغان بۇ زىرائەتكە ئوخشاش، اﷲ ئۇلارنىڭ ياخشى ئەمەللىرىنى بەربات قىلىدۇ) ئۇلارغا اﷲ زۇلۇم قىلمىدى ۋە لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,117,' ئى مۆمىنلەر! ئۆزۈڭلاردىن بولمىغانلارنى (يەنى مۇناپىقلارنى) سىرداش قىلىۋالماڭلار، ئۇلار سىلەرگە بۇزغۇنچىلىق قىلىشتا بوشاڭلىق قىلىپ قالمايدۇ ئۇلار سىلەرنىڭ مۇشەققەتتە قېلىشىڭلارنى ئارزۇ قىلىدۇ، ئۇلارنىڭ دۈشمەنلىكى ئېغىزلىرىدىن مەلۇم بولۇپ تۇرىدۇ، ئۇلارنىڭ دىللىرىدا يوشۇرغان دۈشمەنلىكى تېخىمۇ چوڭدۇر شۈبھىسىزكى، ئەگەر چۈشىنىدىغان بولساڭلار، سىلەرگە نۇرغۇن ئايەتلەرنى بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,118,' (ئى مۆمىنلەر جامائەسى!) سىلەر ئۇلارنى دوست تۇتىسىلەر، ئۇلار سىلەرنى دوست تۇتمايدۇ (سىلەرگە بولغان دۈشمەنلىكنى يوشۇرۇن تۇتىدۇ)، سىلەر(ھەممە ساماۋى) كىتابقا ئىشىنىسىلەر (شۇنداق تۇرۇقلۇق ئۇلار سىلەرنى ئۆچ كۆرىدۇ)، ئۇلار سىلەر بىلەن ئۇچراشقاندا، ئىمان ئېيتتۇق، دەيدۇ ئۆزلىرى يالغۇز قالغاندا سىلەرگە بولغان ئاچچىقىدىن بارماقلىرىنى چىشلەيدۇ (ئى مۇھەممەد!) ئېيتىقىنكى، «ئاچچىقىڭلار بىلەن ئۆلۈڭلار! (يەنى اﷲ ئۆلگىنىڭلارغا قەدەر ئاچچىقىڭلارنى داۋاملاشتۇرسۇن!)» اﷲ ھەقىقەتەن دىللاردىكىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,119,' ئەگەر سىلەرگە (كەڭچىلىك، مولچىلىق، نۇسرەت، غەنىمەت قاتارلىق) بىرەر ياخشىلىق يەتسە، ئۇلار بۇنىڭدىن قايغۇرىدۇ؛ ئەگەر سىلەرگە (ئېغىرچىلىق، قەھەتچىلىك ۋە مەغلۇبىيەت قاتارلىق) بىرەر يامانلىق يەتسە، ئۇلار بۇنىڭدىن خۇشال بولىدۇ ئەگەر سىلەر (ئۇلارنىڭ ئەزىيىتىگە) سەۋر قىلساڭلار ۋە (سۆزۈڭلاردا، ھەرىكىتىڭلاردا اﷲ تىن) قورقساڭلار، ئۇلارنىڭ ھىيلىسى سىلەرگە قىلچە زىيان يەتكۈزەلمەيدۇ اﷲ ئۇلارنىڭ قىلمىشلىرىنى ھەقىقەتەن تولۇق بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,120,' (ئى مۇھەممەد!) ئۆز ۋاقتىدا سەن ئەتىگەندە ئۆيۈڭدىن (ئۇھۇدقا) چىقىپ مۆمىنلەرنى ئۇرۇش سەپلىرىگە ئورۇنلاشتۇردۇڭ، اﷲ (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,121,' ئۆز ۋاقتىدا سىلەردىن ئىككى گۇرۇھ (يەنى سەلەمە ئايمىقى بىلەن بەنى ھارىسە ئايمىقى) ئاجىزلىق كۆرسەتمەكچى (يەنى قورقۇپ ئۇرۇش مەيدانىدىن قايتماقچى) بولدى؛ اﷲ ئۇلارنىڭ مەدەتكارى ئىدى (مۆمىنلەر جىمى ئەھۋالى ۋە جىمى ئىشلىرىدا) اﷲ غىلا تەۋەككۈل قىلسۇن!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,122,' اﷲ سىلەرگە بەدرىدە نۇسرەت ئاتا قىلدى، ھالبۇكى، سىلەر كۈچسىز ئىدىڭلار (سانىڭلار ۋە قورالىڭلار ئاز ئىدى) اﷲ نىڭ سىلەرگە بەرگەن نۇسرىتىگە شۈكۈر قىلىش يۈزىسىدىن اﷲ تىن قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,123,' (ئى مۇھەممەد!) ئۆز ۋاقتىدا سەن مۆمىنلەرگە: «پەرۋەردىگارىڭلار سىلەرگە ئۈچ مىڭ پەرىشتە چۈشۈرۈپ ياردەم بەرسە يەنە يەتمەمدۇ؟» دەيتتىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,124,' شۇنداق، ئۇ يېتىدۇ ئەگەر (جەڭدە) سەۋر قىلساڭلار، (اﷲ تىن) قورقۇپ (ئۇنىڭ ئەمرىگە ئىتائەت قىلساڭلار)، دۈشمەن دەرھال ھۇجۇم قىلىپ كەلگەندە پەرۋەردىگارىڭلار سىلەرگە بەش مىڭ نىشانلىق پەرىشتە بىلەن ياردەم بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,125,' اﷲ ئۇنى (يەنى پەرىشتىلەر ئارقىلىق ياردەم بېرىشنى) پەقەت سىلەرگە خۇش خەۋەر بولسۇن ۋە دىلىڭلار شۇنىڭ بىلەن خاتىرجەم بولسۇن (شۇنىڭ بىلەن دۈشمەننىڭ سانىنىڭ كۆپلۈكىدىن قورقمىسۇن) دەپ قىلدى ياردەم پەقەت غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ تەرىپىدىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,126,' (اﷲ نىڭ ياردىمى) كاپىرلارنىڭ بىر تۈركۈمىنى ھالاك قىلىش ئۈچۈن ياكى ئۇلارنى رەسۋا قىلىپ، ئۇلارنىڭ ئۈمىدسىز ھالدا مەغلۇپ بولۇشلىرى ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,127,' (ئى مۇھەممەد!) ھېچ ئىش (يەنى بەندىلەرنىڭ ئىشلىرىنىڭ تەدبىرى) سېنىڭ ئىختىيارىڭدا ئەمەس، اﷲ يا ئۇلارنىڭ (مۇسۇلمان بولسا) تەۋبىسىنى قوبۇل قىلىدۇ، يا ئۇلار زالىم بولغانلىقى ئۈچۈن (كۇفرىدا چىڭ تۇرسا) ئۇلارنى ئازابقا دۇچار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,128,' ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ نىڭ مۈلكىدۇر اﷲ خالىغان كىشىگە مەغپىرەت قىلىدۇ، خالىغان كىشىگە ئازاب قىلىدۇ اﷲ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,129,' ئى مۆمىنلەر! جازانىنى قاتمۇقات ئاشۇرۇپ يېمەڭلار، مەقسىدىڭلارغا ئېرىشىش ئۈچۈن اﷲ تىن (نەھيى قىلغان ئىشلارنى تەرك ئېتىپ) قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,130,' كاپىرلار ئۈچۈن تەييارلانغان دوزاختىن (يەنى دوزاخقا كىرىشكە سەۋەب بولىدىغان يامان ئىشلاردىن) ساقلىنىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,131,' سىلەرگە رەھمەت قىلىنىشى ئۈچۈن، اﷲ قا ۋە پەيغەمبەرگە ئىتائەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,132,' پەرۋەردىگارىڭلارنىڭ مەغپىرىتىگە ۋە تەقۋادارلار ئۈچۈن تەييارلانغان، كەڭلىكى ئاسمان ـ زېمىنچە كېلىدىغان جەننەتكە ئالدىراڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,133,' تەقۋادارلار كەڭچىلىكتىمۇ، قىسىنچىلىقتىمۇ اﷲ يولىدا (پۇل ـ مال) سەرپ قىلىدىغانلار، (ئۆچ ئېلىشقا قادىر تۇرۇقلۇق) ئاچچىقىنى يۇتىدىغانلار، (يامانلىق قىلغان ياكى بوزەك قىلغان) كىشىلەرنى كەچۈرىدىغانلاردۇر اﷲ ياخشىلىق قىلغۇچىلارنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,134,' تەقۋادارلار يامان بىر گۇناھ قىلىپ قالسا ياكى ئۆزلىرىگە زۇلۇم قىلسا اﷲ نى ياد ئېتىدۇ، گۇناھلىرى ئۈچۈن مەغپىرەت تەلەپ قىلىدۇ، گۇناھنى كەچۈرىدىغان اﷲ تىن باشقا كىم بار؟ ئۇلار قىلمىشلىرىنى بىلىپ تۇرۇپ داۋاملاشتۇرمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,135,' ئۇلارنىڭ (يەنى يۇقىرىقى خىسلەتلەرگە ئىگە كىشىلەرنىڭ) مۇكاپاتى پەرۋەردىگارى تەرىپىدىن بولغان مەغپىرەت ۋە ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردۇر بۇ يەرلەردە ئۇلار مەڭگۈ قالىدۇ ياخشى ئىش قىلغۇچىلارنىڭ ئېرىشىدىغان ساۋابى نېمىدېگەن ياخشى!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,136,' سىلەردىن ئىلگىرى نۇرغۇن ئۈممەتلەر كېلىپ كەتتى؛ يەر يۈزىدە سەير قىلىڭلار، (ھەقنى) يالغانغا چىقارغانلارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,137,' بۇ (قۇرئان) (ئومۇمىي) ئىنسانلارغا باياندۇر (يەنى ھالال بىلەن ھارامنى ئوچۇق بايان قىلىدۇ)، تەقۋادارلارغا توغرا يول كۆرسەتكۈچىدۇر ۋە ۋەز ـ نەسىھەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,138,' (جىھادتىن) بوشىشىپ قالماڭلار، شېھىت بولغانلار ئۈچۈن قايغۇرماڭلار، ئەگەر مۆمىن بولساڭلار، ئۈستۈنلۈك قازىنىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,139,' ئەگەر سىلەرگە شىكەستىلىك يەتكەن بولسا (يەنى بۇ جەڭدە سىلەر يارىلانغان ۋە شېھىت بولغان بولساڭلار)، دۈشمەنلەرگىمۇ (بەدرى جېڭىدە) ئوخشاشلا شىكەستلىك يەتتى (يەنى ئۇلار يارىلاندى ۋە ئۆلتۈردى) بۇ كۈنلەرنى ئىنسانلار ئارىسىدا ئايلاندۇرۇپ تۇرىمىز (يەنى غەلبىنى ھەمىشە بىر پىرقىدە قىلماي، بىر كۈن بۇ پىرقىدە، بىركۈن ئۇ پىرقىدە قىلىمىز)، (بۇ) اﷲ نىڭ (ھەقىقىي) مۆمىنلەرنى (مۇناپىقلاردىن) ئايرىشى ئۈچۈندۇر، سىلەرنى شېھىتلەردىن قىلىشى (يەنى سىلەردىن بەزىلەرنى شېھىتلىك دەرىجىسىگە مۇشەررەپ قىلىشى) ئۈچۈندۇر، ـ اﷲ زالىملارنى دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,140,' مۆمىنلەرنى (ئۇلارغا يەتكەن مۇسىبەت ئارقىلىق) پاكلىشى ئۈچۈندۇر، كاپىرلارنى يوق قىلىشى ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,141,' (ئى مۆمىنلەر جامائەسى!) اﷲ سىلەردىن (ھەقىقىي) جىھاد قىلغانلارنى ۋە (جىھادنىڭ جاپا ـ مۇشەققەتلىرىگە) چىدىغۇچىلارنى بىلمەي (يەنى ئايرىماي تۇرۇپ)، (پەقەت ئاغزىڭلاردا ئىمان ئېيتتۇق دېيىش بىلەنلا) جەننەتكە كىرىشنى ئويلامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,142,' سىلەر ئۆلۈم (يەنى دۈشمەن) بىلەن ئۇچرىشىشتىن ئىلگىرى ئۇنى ئارزۇ قىلاتتىڭلار، مانا ئۇنى ئۆز كۆزۈڭلار بىلەن كۆردۈڭلار (ئەمدى نېمىشقا مەغلۇپ بولىسىلەر؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,143,' مۇھەممەد پەقەت بىر پەيغەمبەردۇر، ئۇنىڭدىن بۇرۇن كۆپ پەيغەمبەرلەر ئۆتتى (ئۇلارنىڭ بەزىلىرى ئۆز ئەجىلى بىلەن ئۆلگەن، بەزىلىرى باشقىلار تەرىپىدىن ئۆلتۈرۈلگەن)؛ ئۇ ۋاپات بولسا ياكى ئۆلتۈرۈلسە، ئارقاڭلارغا يېنىۋالامسىلەر؟ (يەنى ئىماندىن يېنىپ مۇرتەد بولامسىلەر؟) كىمكى ئارقىسىغا يېنىۋالىدىكەن (يەنى مۇرتەد بولىدىكەن)، ئۇ اﷲ قا قىلچە زىيان يەتكۈزەلمەيدۇ اﷲ شۈكۈر قىلغۇچىلارنى مۇكاپاتلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,144,' ھېچ كىشى اﷲ نىڭ ئىزنىسىز ئۆلمەيدۇ، اﷲ ھەر ئادەمنىڭ ئەجىلىنى پۈتۈۋەتكەن (ئادەمنىڭ ئۆمرىنى قورقۇنچاقلىق ئۇزارتالمايدۇ، باتۇرلۇق قىسقارتالمايدۇ) كىمكى دۇنيا ساۋابىنى كۆزلىسە، ئۇنىڭغا دۇنيانىڭ ساۋابىنى بېرىمىز (ئۇنىڭ ئاخىرەتلىكتىن نېسىۋىسى بولمايدۇ)؛ كىمكى ئاخىرەتنىڭ ساۋابىنى كۆزلىسە، ئۇنىڭغا ئاخىرەتنىڭ ساۋابىنى بېرىمىز، شۈكۈر قىلغۇچىلارنى مۇكاپاتلايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,145,' نۇرغۇن پەيغەمبەرلەر بىلەن كۆپلىگەن خۇدا جۇي ئۆلىمالار بىرلىكتە جەڭ قىلدى ئۇلار اﷲ يولىدا يەتكەن كۈلپەتلەردىن روھسىزلانمىدى، بوشاشمىدى، باش ئەگمىدى، اﷲ ئۆزىنىڭ يولىدا دۇچ كەلگەن قىيىنچىلىقلارغا چىداشلىق بەرگۈچىلەرنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,146,' ئۇلارنىڭ سۆزى پەقەت: «پەرۋەردىگارىمىز! گۇناھلىرىمىزنى، ئىشىمىزدا چەكتىن ئاشقانلىقىمىزنى مەغپىرەت قىلغىن، قەدەملىرىمىزنى (جەڭ مەيدانىدا) مۇستەھكەم قىلغىن ۋە كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن» دېگەندىن باشقا بولمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,147,' اﷲ ئۇلارغا دۇنيانىڭ ساۋابىنى (يەنى دۈشمەنلەر ئۈستىدىن غەلىبە قىلىشنى) ۋە ئاخىرەتنىڭ ياخشى ساۋابىنى (يەنى جەننەت ۋە ئۇنىڭ نېمەتلىرىنى) ئاتا قىلدى، اﷲ ياخشىلىق قىلغۇچىلارنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,148,' ئى مۆمىنلەر! ئەگەر سىلەر كاپىرلارغا ئىتائەت قىلساڭلار، ئۇلار سىلەرنى ئارقاڭلارغا (يەنى كۇفرىغا) ياندۇرىدۇ، شۇنىڭ بىلەن زىيان تارتىپ قالىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,149,' ئۇنداق ئەمەس (يەنى ئۇلار سىلەرنىڭ ئىتائەت قىلىدىغان مەدەتكارىڭلار ئەمەس)، اﷲ سىلەرنىڭ مەدەتكارىڭلاردۇر (ئۇنىڭغا ئىتائەت قىلىڭلار)، ئۇ ياردەم قىلغۇچىلارنىڭ ئەڭ ياخشىسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,150,' (ئىلاھلىقى) ھەققىدە ھېچبىر دەلىل چۈشۈرمىگەن نەرسىلەرنى (يەنى بۇتلارنى) اﷲ قا شېرىك قىلغانلىقلىرى ئۈچۈن، كاپىرلارنىڭ دىللىرىغا قورقۇنچ سالىمىز، ئۇلارنىڭ بارىدىغان جايى دوزاختۇر، زالىملارنىڭ جايى (يەنى دوزاخ) ھەقىقەتەن يامان جايدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,151,' اﷲ سىلەرگە قىلغان (دۈشمىنىڭلارغا قارشى نۇسرەت ئاتا قىلىشتىن ئىبارەت) ۋەدىسىدە تۇردى؛ اﷲ نىڭ ئىزنى بىلەن ئۇلارنى قىرىۋاتاتتىڭلار، اﷲ سىلەر ياقتۇرىدىغان غەلىبىنى كۆرسەتكەندىن كېيىن زەئىپلىشىپ قالدىڭلار، پەيغەمبەرنىڭ ئەمرى توغرىسىدا جاڭجاللاشتىڭلار، (پەيغەمبەرنىڭ ئەمرىگە) بويسۇنمىدىڭلار، بەزىلىرىڭلار دۇنيانى كۆزلىدىڭلار، بەزىلىرىڭلار ئاخىرەتنى كۆزلىدىڭلار، ئاندىن اﷲ سىناش ئۈچۈن سىلەردىن دۈشمەننى قايتۇردى (يەنى سىلەرنى غەلىبىدىن مەغلۇبىيەتكە يۈزلەندۈردى) ۋە سىلەرنى ھەقىقەتەن ئەپۇ قىلدى اﷲ مۆمىنلەرگە مەرھەمەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,152,' ئۆز ۋاقتىدا پەيغەمبەر (ئى اﷲ نىڭ بەندىلىرى! مېنىڭ تەرىپىمگە كېلىڭلار، مەن رەسۇلۇللاھتۇرمەن، كىمكى جىھاد مەيدانىغا قايتسا، ئۇ جەننەتكە كىرىدۇ دەپ) ئارقاڭلاردىن سىلەرنى چاقىرىپ تۇراتتى، سىلەر ھېچ كىشىگە قارىماي قاچاتتىڭلار، (بۇنىڭدىن كېيىن) قولۇڭلاردىن كەتكەن نەرسىگە (يەنى غەنىيمەتكە)، بېشىڭلارغا كەلگەن مۇسىبەتكە (يەنى مەغلۇبىيەتكە) قايغۇرماسلىقىڭلار ئۈچۈن، اﷲ سىلەرگە غەم ئۈستىگە غەم بەردى (يەنى سىلەرنى غەنىيمەتتىن مەھرۇم قىلغاننىڭ ئۈستىگە مەغلۇبىيەت بىلەن جازالىدى) اﷲ قىلمىشىڭلاردىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,153,' غەمدىن كېيىن اﷲ سىلەرگە ئەمىنلىك چۈشۈرۈپ بەردى، سىلەرنىڭ بىر قىسمىڭلارنى (يەنى ھەقىقىي مۆمىنلەرنى) مۈگدەك باستى، يەنە بىر قىسمىڭلار (يەنى مۇناپىقلار) ئۆزلىرى بىلەن بولۇپ كەتتى (يەنى كۇففارلار قايتا ھۇجۇم قىلارمۇ، دەپ جان قايغۇسى بىلەن بولۇپ كەتتى)، بۇلار، جاھىلىيەت دەۋرى كىشىلىرىگە ئوخشاش، اﷲ قا قارىتا ناتوغرا گۇمانلاردا بولدى، ئۇلار: «بىزگە اﷲ ۋەدە قىلغان غەلىبىدىن نېسىۋە بارمۇ؟» دەيدۇ ئېيتىقىنكى، «ھەممە ئىش ھەقىقەتەن اﷲ نىڭ ئىلكىدە» ئۇلار ساڭا ئاشكارا قىلمايدىغان نەرسىلەرنى ئۆز ئىچىدە يوشۇرۇشىدۇ؛ ئۇلار: «ئەگەر بىزگە غەلىبىدىن ئازراق نېسىۋە بولسا ئىدى، بۇ يەردە ئۆلتۈرەلمەيتتۇق» دەيدۇ (ئى مۇھەممەد!) ئېيتقىنكى، «ئەگەر سىلەر ئۆيلىرىڭلاردا بولغان تەقدىردىمۇ (ئاراڭلاردىكى) ئۆلتۈرۈلۈشى پۈتۈۋېتىلگەن كىشىلەر چوقۇم ئۆزلىرىنىڭ ئۆلىدىغان جايلىرىغا بېرىشىدۇ (اﷲ نىڭ قازاسىدىن قېچىپ قۇتۇلغىلى بولمايدۇ) (اﷲ نىڭ مۇنداق قىلىشى) كۆڭلۈڭلاردىكىنى سىناش ۋە قەلبىڭلاردىكى نەرسىلەرنى پاكلاش ئۈچۈندۇر اﷲ كۆڭۈللەردىكىنى (ياخشىلىق بولسۇن، يامانلىق بولسۇن) بىلگۈچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,154,' ئىككى قوشۇن (يەنى مۇسۇلمانلار بىلەن مۇشرىكلار) بىر ـ بىرى بىلەن ئۇچراشقان كۈندە، ئىچىڭلاردىكى (جەڭدىن) ھەقىقەتەن يۈز ئۆرۈگەن كىشىلەرنى، بەزى قىلمىشلىرى (يەنى پەيغەمبەرنىڭ ئەمرىگە مۇخالىپەتچىلىك قىلغانلىقى) تۈپەيلىدىن، شەيتان تېيىلدۇرۇشقا ئورۇندى؛ اﷲ ھەقىقەتەن ئۇلارنى ئەپۇ قىلدى اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,155,' ئى مۆمىنلەر! سەپەرگە چىققان ياكى غازاتقا چىققان قېرىنداشلىرى ھەققىدە، ئۇلار يېنىمىزدا بولغاندا ئۆلمەيتتى ۋە ئۆلتۈرۈلمەيتتى، دېگەن كاپىرلارغا ئوخشاش بولماڭلار، اﷲ بۇنى ئۇلارنىڭ دىللىرىدا ھەسرەت قىلسۇن اﷲ تىرىلدۈرىدۇ ۋە ئۆلتۈرىدۇ (ئۇلارنىڭ غازاتقا چىقماسلىقى ئۇلارنى ئۆلۈمدىن قۇتۇلدۇرالمايدۇ) اﷲ قىلمىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,156,' شۈبھىسىزكى، ئەگەر سىلەر اﷲ يولىدا (يەنى غازاتتا) ئۆلتۈرۈلسەڭلار ياكى (غازاتقا كېتىپ بېرىپ ئۆز ئەجىلىڭلار بىلەن) ئۆلسەڭلار، سىلەرگە اﷲ تەرىپىدىن بولغان مەغپىرەت ۋە رەھمەت، ئەلۋەتتە، ئۇلارنىڭ توپلايدىغان دۇنياسىدىن ئارتۇقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,157,' ئەگەر سىلەر (ئۆز ئەجىلىڭلار بىلەن) ئۆلسەڭلار ياكى (جەڭ مەيدانىدا) ئۆلتۈرۈلسەڭلار، (ئاخىرەتتە) چوقۇم اﷲ نىڭ دەرگاھىغا توپلىنىسىلەر (اﷲ سىلەرنى ئەمەلىڭلارغا قاراپ مۇكاپاتلايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,158,' اﷲ نىڭ رەھمىتى بىلەن سەن ئۇلارغا مۇلايىم بولدۇڭ؛ ئەگەر قوپال، باغرى قاتتىق بولغان بولساڭ، ئۇلار چۆرەڭدىن تارقاپ كېتەتتى؛ ئۇلارنى ئەپۇ قىل، ئۇلار ئۈچۈن مەغپىرەت تىلىگىن، ئىشتا ئۇلار بىلەن كېڭەشكىن؛ (كېڭەشكەندىن كېيىن) بىر ئىشقا بەل باغلىساڭ، اﷲ قا تەۋەككۈل قىلغىن اﷲ ھەقىقەتەن تەۋەككۈل قىلغۇچىلارنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,159,' ئەگەر اﷲ سىلەرگە ياردەم بەرسە، سىلەرنى ھېچ كىشى يېڭەلمەيدۇ ئەگەر اﷲ سىلەرگە ياردىمىنى تەرك ئەتسە، اﷲ تىن باشقا سىلەرگە كىم ياردەم بېرىدۇ؟ مۆمىنلەر يالغۇز اﷲ قا تەۋەككۈل قىلسۇن! (يەنى ھەممە ئىشنى اﷲ قا تاپشۇرسۇن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,160,' ھېچقانداق بىر پەيغەمبەر غەنىيمەتكە خىيانەت قىلمايدۇ (يەنى بۇنى تەسەۋۋۇر قىلغىلى بولمايدۇ) كىمكى غەنىيمەتكە خىيانەت قىلىدىكەن، قىيامەت كۈنى ئۇ خىيانەت قىلغان نەرسىسى بىلەن (يەنى خىيانەت قىلغان نەرسىسى گەجگىسىگە ئارتىلغان ھالدا) كېلىدۇ ھەر كىشنىڭ قىلغان ئەمەلىگە تولۇق مۇكاپات، (ياكى) تولۇق جازا بېرىلىدۇ، ئۇلارغا (ياخشىلارنىڭ ساۋابىنى كېمەيتىۋېتىپ، ئاسىيلارنىڭ جازاسىنى ئاشۇرۇۋېتىپ) زۇلۇم قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,161,' اﷲ نىڭ رازىلىقىنى ئىزدىگەن كىشى اﷲ نىڭ غەزىپىگە ئۇچراشقا تېگىشلىك بولغان كىشىگە ئوخشامدۇ؟ اﷲ نىڭ غەزىپىگە ئۇچراشقا تېگىشلىك بولغان كىشىنىڭ بارىدىغان جايى جەھەننەمدۇر، جەھەننەم نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,162,' اﷲ نىڭ دەرگاھىدا ئۇلارنىڭ دەرىجىسى تۈرلۈك بولىدۇ اﷲ ئۇلارنىڭ قىلمىشلىرىنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,163,' اﷲ مۆمىنلەرگە اﷲ نىڭ ئايەتلىرىنى تىلاۋەت قىلىدىغان، ئۇلارنى (گۇناھلاردىن) پاك قىلىدىغان، ئۇلارغا كىتابنى ۋە ھېكمەتنى (يەنى قۇرئان ۋە سۈننەتنى) ئۆگىتىدىغان، ئۆزلىرىدىن بولغان بىر پەيغەمبەر ئەۋەتىپ، ئۇلارغا چوڭ ئېھسان قىلدى؛ ھالبۇكى، ئۇلار بۇرۇن ئوچۇق گۇمراھلىقتا ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,164,' سىلەرگە يەتكەن مۇسىبەت سىلەر دۈشمەنگە يەتكۈزگەن مۇسىبەتنىڭ يېرىمىغا تەڭ كېلىدىغان تۇرسا (يەنى ئۇھۇد غازىتىدا سىلەردىن  كىشى شېھىت بولغان تۇرسا، بەدرى غازىتىدا بولسا مۇشرىكلاردىن  كىشىنى ئۆلتۈرگەن،  كىشىنى ئەسىر ئالغان تۇرساڭلار)، سىلەر يەنە: «بۇ مۇسىبەت قەيەردىن كەلدى؟» دېدىڭلار ئېيتقىنكى، «ئۇ سىلەرنىڭ ئۆزلىرىڭلاردىن (يەنى پەيغەمبەرنىڭ ئەمرىگە خىلاپلىق قىلغانلىقىڭلار ۋە غەنمەتكە ھېرىس بولغانلىقىڭلاردىن) بولدى» اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,165,' ئىككى قوشۇن ئۇچراشقان كۈندە (يەنى مۇسۇلمانلار بىلەن مۇشرىكلار توقۇنۇشقان ئۇھۇد جېڭىدە) سىلەرگە كەلگەن مۇسىبەت اﷲ نىڭ ئىرادىسى بىلەن كەلگەندۇر، بۇ ھەقىقىي مۆمىنلەرنى بىلىش (يەنى ئايرىش) ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,166,' ۋە مۇناپىقلارنى بىلىش (يەنى ئايرىش) ئۈچۈندۇركى، ئۇلارغا: «كېلىڭلار، اﷲ يولىدا ئۇرۇش قىلىڭلار ياكى (ئۆزۈڭلارنى) قوغداڭلار» دېيىلسە، ئۇلار: «ئۇرۇشتىن خەۋەر تاپقان بولساق، ئەلۋەتتە، سىلەرگە ئەگىشەتتۇق (يەنى سىلەر بىلەن بىر سەپتە تۇرۇپ ئۇرۇش قىلاتتۇق)» دېدى ئۇ كۈندە ئۇلار ئىمانغا قارىغاندا كۇفرىغا يېقىن ئىدى ئۇلار كۆڭۈللىرىدە يوق نەرسىلەرنى ئېغىزلىرىدا دېدى، ئۇلار يوشۇررغاننى (يەنى نىفاق بىلەن مۇشرىكلىكنى) اﷲ ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,167,' (بۇ يەنە اﷲ نىڭ) ئۇرۇشقا چىقىشنى خالىمىغان (مۇناپىقلارنى ئايرىشى ئۈچۈندۇركى، ئۇلار ئۆيلىرىدە) ئولتۇرۇپ قېرىنداشلىرىغا: «ئەگەر ئۇلار (يەنى مۆمىنلەر) بىزگە بويسۇنغان بولسا (يەنى نەسىھىتىمىزنى ئاڭلاپ ئۇرۇشتىن قايتقان بولسا)، ئۆلتۈرۈلمەيتتى» دېدى (ئى مۇھەممەد! بۇ مۇناپىقلارغا) ئېيتقىنكى، «(ئەگەر ئۇرۇشقا چىقماسلىق ئۆلۈمدىن قۇتۇلدۇرىدىغان بولسا) ئۆلۈمنى ئۆزۈڭلاردىن دەپئى قىلىپ بېقىڭلار، ئەگەر (دەۋايىڭلاردا) راستچىل بولساڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,168,' اﷲ نىڭ يولىدا شېھىت بولغانلارنى ئۆلۈك دەپ گۇمان قىلمىغىن، بەلكى ئۇلار تىرىك بولۇپ، اﷲ نىڭ دەرگاھىدىكى رىزىقتىن بەھرىمەن قىلىنىدۇ (يەنى جەننەتنىڭ نېمەتلىرىدىن ئەتىگەن ـ ئاخشامدا مەڭگۈلۈك رىزىقلاندۇرۇپ تۇرۇلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,169,' ئۇلار اﷲ نىڭ ئۆزلىرىگە بەرگەن پەزلىدىن خۇرسەندۇر، ئۆزلىرىنىڭ ئارقىسىدىن تېخى يېتىپ كەلمىگەن (يەنى شېھىت بولماي تىرىك قالغان) قېرىنداشلىرىغا (ئاخىرەتتە) نە قورقۇنچ، (دۇنيادىن ئايرىلغانلىقىغا) نە قايغۇ يوق ئىكەنلىكى بىلەن خۇش خەۋەر بېرىشنى تىلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,170,' ئۇلار اﷲ تەرىپىدىن بېرىلگەن نېمەت ۋە پەزلنى، مۆمىنلەرنىڭ ئەجرىنى اﷲ نىڭ بىكار قىلىۋەتمەيدىغانلىقى بىلەن خۇش خەۋەر بېرىشنى تىلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,171,' (ئۇھۇد غازىتىدا) يارىلانغاندىن كېيىن اﷲ نىڭ ۋە پەيغەمبەرنىڭ چاقىرىقىغا ئاۋاز قوشقانلار، ئۇلار (يەنى مۆمىنلەر) دىن ياخشى ئەمەللەرنى قىلغانلار ۋە تەقۋادارلىق قىلغانلار چوڭ ساۋابقا ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,172,' (مۇشرىكلار تەرەپدارى بولغان) ئادەملەر ئۇلارغا: «شەك ـ شۈبھىسىزكى، كىشىلەر (يەنى قۇرەيشلەر) سىلەرگە قارشى قوشۇن توپلىدى، ئۇلاردىن قورقۇڭلار» دېدى بۇ سۆز ئۇلارنىڭ ئىمانىنى كۈچەيتتى ئۇلار: «بىزگە اﷲ كۇپايە، اﷲ نېمىدېگەن ياخشى ھامىي!» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,173,' ئۇلار اﷲ نىڭ نېمىتى ۋە پەزلى بىلەن قايتىپ كەلدى، ھېچقانداق زىيان ـ زەخمەتكە ئۇچرىمىدى، ئۇلار اﷲ نىڭ رازىلىقىنى ئىزدىدى اﷲ ئۇلۇغ پەزل ئىگىسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,174,' ئەنە شۇ شەيتان ئۆز دوستلىرىنى (يەنى كۇففارلارنى) قورقىتىدۇ ئەگەر مۆمىن بولساڭلار، ئۇلاردىن قورقماڭلار، مەندىن قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,175,' (ئى مۇھەممەد!) كۇفرىغا ئۆزلىرىنى ئاتىغانلار (يەنى سۆزلىرى ۋە ھەرىكەتلىرى بىلەن كۇفرىغا شاپاشلايدىغان مۇناپىقلار) سېنى غەمكىن قىلمىسۇن، ئۇلار ھەقىقەتەن (كۇفرى بىلەن) اﷲ قا قىلچە زىيان يەتكۈزەلمەيدۇ اﷲ ئاخىرەتتە ئۇلارغا (ساۋابتىن) ھېچبىر نېسىۋە بەرمەسلىكنى خالايدۇ؛ ئۇلار چوڭ ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,176,' ئىماننى كۇفرىغا تېگىشكەنلەر (يەنى مەزكۇر مۇناپىقلار) (كۇفرى بىلەن) اﷲ قا قىلچە زىيان يەتكۈزەلمەيدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,177,' كاپىرلار ئۆزلىرىگە (جازا بەرمەستىن) مۆھلەت بېرىشىمىزنى (يەنى ئۆمرىنى ئۇزۇن قىلىشىمىزنى) ھەرگىز ئۆزلىرى ئۈچۈن پايدىلىق دەپ گۇمان قىلمىسۇن، ئۇلارغا مۆھلەت بېرىشىمىز (ۋە ئەجىلىنى كېچىكتۈرۈشىمىز) پەقەت ئۇلارنىڭ گۇناھىنىڭ كۆپىيىشى ئۈچۈندۇر ئۇلار (ئاخىرەتتە) خار قىلغۇچى ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,178,' اﷲ ياماننى (يەنى مۇناپىقنى) ياخشىدىن (يەنى مۆمىندىن) ئايرىمىغۇچە مۆمىنلەرنى (يەنى سىلەرنى) ھازىرقى پېتىڭلارچە قويمايدۇ اﷲ سىلەرنى غەيبتىنمۇ خەۋەردار قىلمايدۇ ۋە لېكىن اﷲ پەيغەمبەرلىرىدىن خالىغىنىنى تاللاپ، ئۇنىڭغا (مۇھەممەد ئەلەيھىسسالامغا مۇناپىقلارنىڭ ھالىنى بىلدۈرگەندەك) غەيبنى بىلدۈرىدۇ؛ اﷲ قا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتىڭلار، ئەگەر سىلەر ئىمان ئېيتساڭلار ۋە (پەرۋەردىگارىڭلاردىن) قورقساڭلار، چوڭ ساۋابقا ئېرىشىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,179,' اﷲ ئۆز پەزلىدىن بەرگەن نەرسىلەرگە (يەنى پۇل ـ مالغا) بېخىللىق قىلىدىغانلار بېخىللىقنى ئۆزلىرى ئۈچۈن پايدىلىق دەپ گۇمان قىلمىسۇن، ئەمەلدە بۇ ئۇلار ئۈچۈن زىيانلىقتۇر؛ ئۇلارنىڭ بېخىللىق قىلغان نەرسىسى قىيامەت كۈنى ئۇلارنىڭ بوينىغا تاقاق قىلىپ سېلىنىدۇ ئاسمانلارنىڭ ۋە يەرنىڭ مىراسى اﷲ نىڭدۇر (يەنى كائىناتتىكى ھەممە نەرسە اﷲ نىڭ مۈلكى بولۇپ، ئۇلار پانىي بولغاندىن كېيىن اﷲ نىڭ دەرگاھىغا قايتىدۇ) اﷲ قىلغان ئەمەللىرىڭلاردىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,180,' «اﷲ بولسا ھەقىقەتەن پېقىر، بىز بولساق باي» دېگەن كىشىلەر (يەنى يەھۇدىيلار) نىڭ سۆزىنى اﷲ ھەقىقەتەن ئاڭلىدى، ئۇلارنىڭ سۆزلىرىنى ۋە پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرىنى خاتىرىلەپ قويىمىز (يەنى نامە ـ ئەمالىغا مۇئەككەل پەرىشتىلەرگە بۇنى ئۇلارنىڭ نامە ـ ئەمالىغا يېزىشنى بۇيرۇيمىز) ھەمدە ئاخىرەتتە پەرىشتىلەر تىلى ئارقىلىق، كۆيدۈرگۈچى ئازابنى تېتىڭلار، دەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,181,' بۇ سىلەرنىڭ قىلمىشىڭلار تۈپەيلىدىندۇر، اﷲ بەندىلىرىگە زۇلۇم قىلغۇچى ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,182,' ئۇلار: «(تەۋراتتا) اﷲ بىزنى (ئاسماندىن چۈشكەن) ئوت يەپ كېتىدىغان بىر قۇربانلىق كەلتۈرمىگىچە ھەقىقەتەن ھېچبىر پەيغەمبەرگە ئىمان كەلتۈرمەسلىككە بۇيرۇدى» دېدى (بۇ ئىپتىرا بولۇپ، اﷲ ئۇلارنى بۇيرۇغان ئەمەس) (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەرگە مۆجىزىلەر ۋە سىلەر ئېيتقان مۆجىزە بىلەن ھەقىقەتەن نۇرغۇن پەيغەمبەر كەلدى؛ ئەگەر سىلەر (دەۋايىڭلاردا) راستچىل بولساڭلار، ئۇلارنى نېمىشقا ئۆلتۈردۈڭلار؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,183,' (ئى مۇھەممەد!) ئۇلار سېنى يالغانغا چىقارغان بولسا (قايغۇرۇپ كەتمىگىن)، سەندىن ئىلگىرى ئۆتكەن، مۆجىزىلەرنى، كىتابلارنى (يەنى ئىبراھىم ئەلەيھىسسالامغا نازىل قىلىنغان سەھىپىلەرگە ئوخشاش ساماۋى كىتابلارنى) ۋە نۇرلۇق كىتابنى (يەنى تەۋرات، ئىنجىلغا ئوخشاشلارنى) ئېلىپ كەلگەن نۇرغۇن پەيغەمبەرلەرمۇ يالغانغا چىقىرىلغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,184,' ھەر بىرجان ئىگىسى ئۆلۈمنىڭ تەمىنى تېتىغۇچىدۇر سىلەرنىڭ ئەجرىڭلار (يەنى ئەمەللىرىڭلارنىڭ ساۋابى) قىيامەت كۈنى تولۇق بېرىلىدۇ، كىمكى دوزاختىن يىراق قىلىنغان ۋە جەننەتكە كىرگۈزۈلگەن ئىكەن، ئۇ مۇرادىغا يەتكەن بولىدۇ دۇنيا تىرىكچىلىكى پەقەت ئالدايدىغان مالدىنلا ئىبارەتتۇر (يەنى ئالدايدىغان نەرسە بولۇپ، ئازغىنا بەھرىمەن بولغاندىن كېيىن تۈگەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,185,' سىلەر ماللىرىڭلاردا (سەدىقىغە بۇيرۇلۇش ۋە ئاپەت يېتىشى بىلەن) ۋە جانلىرىڭلاردا (قەتل قىلىنىش، ئەسىر ئېلىنىش، كېسەل بولۇش بىلەن) چوقۇم سىنىلىسىلەر، سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دىن، مۇشرىكلاردىن چوقۇم نۇرغۇن يامان سۆز ئاڭلايسىلەر، ئەگەر (يۇقىرىقى ئەھۋاللار يۈز بەرگەندە) سەۋر قىلساڭلار (سۆزۈڭلار ۋە ھەرىكىتىڭلاردا اﷲ تىن) قورقساڭلار، ئۇنداقتا بۇ ھەقىقەتەن ئىرادە بىلەن قىلىنىشقا تېگىشلىك ئىشلاردىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,186,' ئۆز ۋاقتىدا، اﷲ كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار) دىن كىتابنى (يەنى كىتابتىكى اﷲ نىڭ ئەھكاملىرىنى) كىشىلەرگە چوقۇم بايان قىلىپ بېرىشكە ۋە يوشۇرماسلىققا ئەھدە ئالدى، لېكىن بۇنى (يەنى ئەھدىنى) ئۇلار ئارقىسىغا چۆرۈۋەتتى ۋە ئۇنى ئازغىنا بەدەلگە (يەنى دۇنيانىڭ ئەرزىمەس نەرسىسىگە) تېگىشتى ئۇلارنىڭ تېگىشىپ ئالغىنى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,187,' (ئى مۇھەممەد!) قىلغان ئىشلىرىدىن (يەنى ھەقنى ياپقانلىقىدىن) خۇشال بولىدىغان، قىلمىغان ئىشلىرى بىلەن مەدھىيىلىنىشنى ياخشى كۆرىدىغان كىشىلەرنى ھەرگىز اﷲ نىڭ ئازابىدىن قۇتۇلىدۇ دەپ گۇمان قىلمىغىن، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,188,' ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ ھەممە نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,189,' شۈبھىسىزكى، ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىدا، كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىپ تۇرۇشىدا ئەقىل ئىگىلىرى ئۈچۈن، ئەلۋەتتە، روشەن دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,190,' ئۇلار ئۆرە تۇرغاندىمۇ، ئولتۇرغاندىمۇ، ياتقاندىمۇ اﷲ نى ئەسلەپ تۇرىدۇ، ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشى توغرىسىدا پىكىر يۈرگۈزىدۇ (ئۇلار ئېيتىدۇ) «پەرۋەردىگارىمىز! بۇنى بىكار ياراتمىدىڭ، سەن پاكتۇرسەن، بىزنى دوزاخ ئازابىدىن ساقلىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,191,' پەرۋەردىگارىمىز! سەن كىمنىكى دوزاخقا كىرگۈزىدىكەنسەن، ئۇنى ئەلۋەتتە خار قىلغان بولىسەن زالىملارغا ھېچقانداق ياردەمچى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,192,' پەرۋەردىگارىمىز! بىز ھەقىقەتەن بىر چاقىرغۇچىنىڭ (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ) 'رەببىڭلارغا ئىمان ئېيتىڭلار' دەپ ئىمانغا چاقىرغانلىقىنى ئاڭلىدۇق، ئىمان ئېيتتۇق، پەرۋەردىگارىمىز! بىزنىڭ گۇناھلىرىمىزنى مەغپىرەت قىلغىن، يامانلىقلىرىمىزنى يوققا چىقارغىن، بىزنى ياخشىلارنىڭ قاتارىدا قەبزى روھ قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,193,' پەرۋەردىگارىمىز! بىزگە پەيغەمبەرلىرىڭ ئارقىلىق ۋەدە قىلغان نەرسىنى (يەنى ئىتائەت قىلغانلارغا خاس بولغان جەننەتنى) بەرگىن، قىيامەت كۈنى بىزنى رەسۋا قىلمىغىن سەن ھەقىقەتەن ۋەدەڭگە خىلاپلىق قىلمايسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,194,' ئۇلارنىڭ دۇئاسىنى پەرۋەردىگارى ئىجابەت قىلدى: «مەن سىلەردىن ئەر بولسۇن، ئايال بولسۇن، ھەر قانداق بىر ياخشى ئىش قىلغۇچىنىڭ قىلغان ئەمەلىنى بىكار قىلىۋەتمەيمەن، سىلەر بىر ـ بىرىڭلاردىن تۆرەلگەن ھىجرەت قىلغانلار، يۇرتلىرىدىن ھەيدەپ چىقىرىلغانلار، مېنىڭ يولۇمدا (يەنى اﷲ نىڭ دىنى ئۈچۈن) ئەزىيەت تارتقانلار، ئۇرۇشقا قاتناشقانلار، (يەنى مېنىڭ يولۇمدا ئۇرۇشقانلار) ۋە مېنىڭ يولۇمدا ئۆلتۈرۈلگەنلەرنىڭ گۇناھلىرىنى (مەغپىرىتىم ۋە رەھمىتىم بىلەن) ئەلۋەتتە، ئۇلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمەن» بۇ (ئۇلارنىڭ ياخشى ئەمەللىرى ئۈچۈن) اﷲ تەرىپىدىن بېرىلگەن مۇكاپاتتۇر اﷲ نىڭ دەرگاھىدا ياخشى مۇكاپات (يەنى جەننەت) بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,195,' كاپىرلارنىڭ (پۇل ـ مال، ئابرۇي، مەرتىۋە ئىزدەش يۈزىسىدىن) شەھەرلەردە كېزىپ يۈرۈشلىرى سېنى ئالدىمىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,196,' بۇ ئازغىنا بەھرىمەن بولۇشتۇر، كەلگۈسىدە ئۇلارنىڭ جايى جەھەننەم بولىدۇ ئۇ نېمىدېگەن يامان تۆشەك!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,197,' پەرۋەردىگارىغا تەقۋادارلىق قىلغان كىشىلەر ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردىن بەھرىمەن بولۇپ، ئۇ يەردە مەڭگۈ قالىدۇ بۇ، اﷲ تەرىپىدىن بولغان كۈتۈۋېلىشتۇر اﷲ نىڭ دەرگاھىدىكى نەرسىلەر (يەنى مۇكاپات ۋە ھۆرمەت) ياخشى بەندىلەر ئۈچۈن (كۇففارلار بەھرىمەن بولغان باقاسى يوق ئازغىنا راھەتتىن) ئارتۇقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,198,' ئەھلى كىتاب ئارىسىدا ھەقىقەتەن اﷲ قا ئىمان كەلتۈرىدىغان، سىلەرگە نازىل بولغان كىتابقا (يەنى قۇرئانغا)، ئۆزلىرىگە نازىل بولغان كىتابقا (يەنى تەۋراتقا ۋە ئىنجىلغا) ئىشىنىدىغانلار بار؛ ئۇلار اﷲ تىن قورقىدۇ، اﷲ نىڭ ئايەتلىرىنى ئازغىنا بەدەلگە تېگىشمەيدۇ (يەنى ئۇلارنىڭ كىتابلىرىدىكى مۇھەممەد ئەلەيھىسسالامنىڭ سۈپەتلىرى ۋە شەرىئەت ئەھكاملىرىنى، راھىبلارغا ئوخشاش، دۇنيانىڭ ئەرزىمەس نەرسىلىرىنى دەپ ئۆزگەرتىۋەتمەيدۇ)، ئۇلارغا پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب بېرىلىدۇ اﷲ ھەقىقەتەن تېز ھېساب ئالغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,3,199,' ئى مۆمىنلەر! تائەت ـ ئىبادەتنىڭ مۇشەققەتلىرىگە ۋە سىلەرگە يەتكەن ئېغىرچىلىقلارغا سەۋر قىلىڭلار، دۈشمەنلەرگە زىيادە چىداملىق بولۇڭلار، (چېگرالىرىڭلارنى ساقلاپ) جىھادقا تەييار تۇرۇڭلار، مەقسىتىڭلارغا يېتىش ئۈچۈن اﷲ تىن قورقۇڭلار (يەنى ئۇنىڭ ئەمرىگە مۇخالىپەتچىلىك قىلماڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,1,'ئى ئىنسانلار! سىلەرنى بىر ئىنساندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتقان، شۇ ئىنساندىن (يەنى ئۆز جىنسىدىن) ئۇنىڭ جۈپتىنى (يەنى ھەۋۋانى) ياراتقان ۋە ئۇلاردىن (يەنى ئادەم بىلەن ھەۋۋادىن) نۇرغۇن ئەر ـ ئاياللارنى ياراتقان پەرۋەردىگارىڭلاردىن قورقۇڭلار، بىر ـ بىرىڭلاردىن نەرسە سورىغاندا نامى بىلەن سورايدىغان اﷲ تىن قورقۇڭلار، سىلە ـ رەھىمنى ئۈزۈپ قويۇشتىن ساقلىنىڭلار اﷲ ھەقىقەتەن سىلەرنى (يەنى پۈتۈن ئەھۋالىڭلارنى) كۆزىتىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,2,' يېتىملەرنىڭ مال ـ مۈلكىنى (ئۇلار بالاغەتكە يەتكەن چاغدا) بېرىڭلار، ياخشىسىنى يامانغا (يەنى ئۇلارنىڭ ماللىرىنىڭ ياخشىسىنى ئۆزۈڭلارنىڭ ماللىرىڭلارنىڭ يامىنىغا) تېگىشىۋالماڭلار؛ ئۇلارنىڭ مال ـ مۈلكىنى ئۆزۈڭلارنىڭ مال ـ مۈلكىگە قوشۇپ يەۋالماڭلار بۇ ھەقىقەتەن چوڭ گۇناھتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,3,' ئەگەر يېتىم قىزلارغا (ئۆيلىنىپ) ئادىل مۇئامىلىدە بولالماسلىقىڭلاردىن (يەنى تەربىيەڭلاردا بىرەر يېتىم قىز بولۇپ، ئۇنىڭغا شۇ قىزغا باراۋەر تۇرىدىغان قىزغا بېرىلىدىغان مەھرىنى بېرەلمەسلىكىڭلاردىن) قورقساڭلار، ئۇنى تەرك ئېتىپ، ئۆزۈڭلار ياقتۇرىدىغان باشقا ئاياللاردىن ئىككىنى، ئۈچنى ۋە تۆتنى ئېلىشىڭلارغا بولىدۇ، ئەگەر (ئۇلارنىڭ ئارىسىدا) ئادىل بولالماسلىقىڭلاردىن قورقساڭلار، بىر خوتۇن بىلەن ياكى قول ئاستىڭلاردىكى چۆرىلەر بىلەن كۇپايىلەنسەڭلار بولىدۇ بىر خوتۇن بىلەن كۇپايىلىنىش زۇلۇم قىلماسلىققا ئەڭ يېقىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,4,' ئاياللارغا ئۇلارنىڭ مەھرىلىرىنى خۇشاللىق بىلەن سوۋغا قىلىپ بېرىڭلار ئەگەر ئۇلار ئۇنىڭدىن بىر قىسمىنى سىلەرگە ئۆتۈنۈپ بەرسە، ئۇنى مەززىلىك، سىڭىشلىق (يەنى پاك ـ ھالال بىلىپ) يەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,5,' اﷲ تىرىكچىلىكىڭلارنىڭ ئاساسى قىلغان ماللىرىڭلارنى ئەخمەقلەرگە تۇتقۇزۇپ قويماڭلار، ئۇلارنى يېمەك ـ ئىچمەك، كىيىم ـ كېچەكلەر بىلەن تەمىنلەڭلار ئۇلارغا چىرايلىق سۆز قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,6,' يېتىملەرنى بالاغەتكە يەتكەنگە قەدەر سىناپ تۇرۇڭلار، ئۇلاردا ماللىرىنى باشقۇرالايدىغان ھالەتنى بايقىساڭلار، ئۇلارغا مال ـ مۈلكىنى تاپشۇرۇپ بېرىڭلار؛ ئۇلارنىڭ چوڭ بولۇپ قېلىشىدىن قورقۇپ مال ـ مۈلكىنى بۇزۇپ ـ چېچىپ يەۋالماڭلار (يېتىمگە ۋەسى بولغانلاردىن) كىمكى باي ئىكەن، (ۋەسى بولغانلىق ھەققى ئۈچۈن) يېتىمنىڭ مال ـ مۈلكىنى يېيىشتىن ئۆزىنى ساقلىسۇن، (سىلەردىن) كىمكى يوقسۇل ئىكەن، ئۇ (ئۆز ئەمگىكىنىڭ ھەققى ئۈچۈن) مۇۋاپىق رەۋىشتە يېسۇن، ئۇلار (يەنى بالاغەتكە يەتكەن يېتىملەر) نىڭ مال ـ مۈلكىنى تاپشۇرۇپ بېرىدىغان چاغدا باشقىلارنى گۇۋاھ قىلىپ قويۇڭلار اﷲ ھېساب ئېلىشقا يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,7,' ئاتا ـ ئانىسى ۋە تۇغقانلىرى قالدۇرغان مىراستا (يەنى مېيىتنىڭ تەرەكىسىدە)  ئەرلەرنىڭ ھەسسىسى بار مەيلى ئۇ (يەنى تەرەكە) ئاز بولسۇن ياكى كۆپ بولسۇن، ھەر ئادەم اﷲ نىڭ ئادىل شەرىئىتىدە) بەلگىلەنگەن ھەسسىسىنى ئالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,8,' تەقسىمات ۋاقتىدا (ۋارىس ئەمەس) تۇغقانلار، (تۇغقان ئەمەس) يېتىملەر ۋە مىسكىنلەر ھازىر بولسا، ئۇلارغا بۇنىڭدىن (يەنى تەرەكىدىن ئۇلارنىڭ كۆڭلىنى ئېلىش ئۈچۈن بىر ئاز نەرسە) بېرىڭلار، ئۇلارغا چىرايلىق سۆز قىلىڭلار (يەنى بۇ كىچىكلەرنىڭ مېلى، بىزنىڭ ئەمەس دېگەنگە ئوخشاش ئۆزرىنى ئېيتىڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,9,' (كىملەركى) ئۆزلىرىدىن قېلىپ قالىدىغان كىچىك بالىلىرى بولۇپ، ئۇلاردىن قانداق ئەندىشە قىلىدىغان بولسا، باشقىلارنىڭ قېلىپ قالغان كىچىك بالىلىرى ئۈچۈنمۇ شۇنداق ئەندىشە قىلسۇن، (يېتىملەرنىڭ ئىشىدا) اﷲ تىن قورقسۇن ۋە توغرا سۆزنى ئېيتسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,10,' زۇلۇم قىلىپ يېتىملەرنىڭ مال ـ مۈلكىنى (ناھەق) يەۋالىدىغانلار، شۈبھىسىزكى، قورسىقىغا (قىيامەت كۈنى يېنىپ تۇرىدىغان) ئوتنى يەۋالغان بولىدۇ، ئۇلار يېنىپ تۇرغان ئوتقا (يەنى دوزاخقا) كىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,11,' اﷲ بالىلىرىڭلار (غا تېگىدىغان مىراس) ھەققىدە تەۋسىيە قىلىدۇكى، بىر ئەرگە ئىككى ئايالنىڭ ھەسسىسى تېگىدۇ، ئەگەر مېيىتنىڭ قىزى (ئىككى ياكى) ئىككىدىن كۆپ بولسا، ئۇ چاغدا ئۇلارغا مىراسنىڭ ئۈچتىن ئىككى ھەسسىسى تېگىدۇ، ئەگەر (ۋارىس) بىر قىز بولىدىغان بولسا، ئۇنىڭغا مىراسنىڭ يېرىمى تېگىدۇ، ئەگەر مېيىتنىڭ بالىلىرى بولسا، بۇ چاغدا ئۇنىڭ ئاتا ـ ئانىسىنىڭ ھەر بىرىگە مىراسنىڭ ئالتىدىن بىرى تېگىدۇ ئەگەر ئۇنىڭ بالىلىرى بولماي مىراسقا پەقەت ئۇنىڭ ئاتا ـ ئانىسى ۋارىسلىق قىلىدىغان بولسا، بۇ چاغدا ئۇنىڭ ئانىسىغا مىراسنىڭ ئۈچتىن بىرى تېگىدۇ (قالغىنى ئاتىسىغا تېگىدۇ) ئەگەر مېيىتنىڭ (ئاتا ـ ئانىسىدىن باشقا يەنە) قېرىنداشلىرى بولسا، ئانىسىغا مىراسنىڭ ئالتىدىن بىرى تېگىدۇ (قالغىنىنى ئاتىسى ئالىدۇ) (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ۋە قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ ئاتا ـ ئاناڭلار ۋە بالىلىرىڭلاردىن (يەنى بۇ ئىككىسىدىن) قايسىسىنىڭ مەنپەئەت جەھەتتىن ئۆزۈڭلارغا ئەڭ يېقىن ئىكەنلىكىنى بىلمەيسىلەر، بۇلار اﷲ تەرىپىدىن بېكىتىلگەن بەلگىلىمىدۇر اﷲ ھەقىقەتەن ھەممىنى بىلگۈچى، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,12,' ئەگەر ئاياللىرىڭلارنىڭ بالىلىرى بولمىسا، بۇ چاغدا ئۇلار قالدۇرغان مىراسنىڭ يېرىمى (ئى ئەرلەر!) سىلەرگە تېگىدۇ ئەگەر ئۇلارنىڭ بالىلىرى بولسا، ئۇلارغا قالدۇرغان مىراسنىڭ تۆتتىن بىرى سىلەرگە تېگىدۇ (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ياكى مېيىتنىڭ قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ ئەگەر سىلەرنىڭ بالىلىرىڭلار بولمىسا، بۇ چاغدا ئاياللىرىڭلارغا سىلەر قالدۇرغان مىراسنىڭ تۆتتىن بىرى تېگىدۇ، ئەگەر سىلەرنىڭ بالىلىرىڭلار بولسا، بۇ چاغدا ئايالىڭلارغا سىلەر قالدۇرغان مىراستىن سەككىزدىن بىرى تېگىدۇ (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ياكى قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ ئەگەر مىراس قالدۇرغۇچى ئاتا ـ ئانىسىز، بالىسىز ئەر ياكى ئايال بولۇپ، ئۇنىڭ پەقەت ئانا بىر قېرىندىشى ۋە ئانا بىر ھەمشىرىسى بولسا، ئۇلارنىڭ ھەر بىرىگە مىراسنىڭ ئالتىدىن بىرى تېگىدۇ ئەگەر مىراس قالدۇرغۇچىنىڭ (يەنى مىراس قالدۇرغۇچى مېيىتنىڭ ئانا بىر، ئاتا باشقا) قېرىنداشلىرى ۋە ھەمشىرىلىرى بۇنىڭدىن (يەنى بىردىن) كۆپ بولسا، ئۇ چاغدا مىراسنىڭ ئۈچتىن بىرى ئۇلارنىڭ ئارىسىدا ئورتاق تەقسىم قىلىنىدۇ (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ياكى قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ (مىراس قالدۇرغۇچى ئادەمنىڭ) ۋەسىيىتى ۋارىسلارغا زىيان يەتكۈزمەيدىغان بولۇشى كېرەك ئۇ اﷲ تەرىپىدىن قىلىنغان تەۋسىيىدۇر، اﷲ ھەممىنى بىلگۈچىدۇر، ھەلىمدۇر (يەنى ئەمرىگە خىلاپلىق قىلغۇچىلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,13,' ئەنە شۇلار (يەنى اﷲ بەندىلىرىنىڭ ئەمەل قىلىشى ئۈچۈن بەلگىلەپ بەرگەن يۇقىرىدىكى ئەھكاملار) اﷲ نىڭ قانۇنلىرىدۇر، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىدىكەن، اﷲ ئۇنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇ ئۇ يەرلەردە مەڭگۈ قالىدۇ بۇ چوڭ مۇۋەپپەقىيەت قازىنىشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,14,' كىمكى اﷲ قا ۋە پەيغەمبىرىگە ئاسىيلىق قىلىپ، اﷲ نىڭ قانۇنلىرىنىڭ سىرتىغا چىقىپ كېتىدىكەن، اﷲ ئۇنى دوزاخقا كىرگۈزىدۇ، ئۇ دوزاختا مەڭگۈ قالىدۇ، خورلىغۇچى ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,15,' ئاياللىرىڭلاردىن پاھىشە قىلغانلارنى (ئىسپاتلاش ئۈچۈن) ئاراڭلاردىن تۆت گۇۋاھچى كەلتۈرۈڭلار؛ ئەگەر ئۇلار گۇۋاھلىق بەرسە (يەنى ئۇلار گۇۋاھلىق بېرىپ ئايالنىڭ جىنايىتىنى ئىسپاتلىسا)، تاكى ئاياللىرىڭلار ۋاپات بولغانغا ياكى اﷲ ئۇلارغا بىر چىقىش يولى بەرگەنگە قەدەر ئۇلارنى ئۆيدە تۇتۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,16,' ئاراڭلاردا ئىككى كىشى (يەنى ئەر بىلەن ئايال) پاھىشە قىلسا، ئۇلارنى ئەيىبلەڭلار؛ ئەگەر ئۇلار تەۋبە قىلىپ تۈزەلسە، ئۇلارنى كەچۈرۈڭلار، اﷲ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، تولىمۇ مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,17,' اﷲ قوبۇل قىلىشنى ۋەدە قىلغان تەۋبە يامانلىقنى نادانلىقتىن قىلىپ سېلىپ، ئاندىن ئۇزۇنغا قالماي (يەنى ئۆلۈم كېلىشتىن بۇرۇن) تەۋبە قىلغانلارنىڭ تەۋبىسىدۇر؛ اﷲ ئەنە شۇلارنىڭ تەۋبىسىنى قوبۇل قىلىدۇ، اﷲ ھەممىنى بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,18,' داۋاملىق يامان ئىشلارنى قىلىپ بېشىغا ئۆلۈم كەلگەن چاغدا، ئەمدى تەۋبە قىلدىم، دېگۈچىلەرنىڭ تەۋبىسى ۋە كاپىر پېتى ئۆلگۈچىلەرنىڭ تەۋبىسى (يەنى ئۆلۈم ئالدىدا ئىمان ئېيتتىم دېگۈچىلەرنىڭ ئىمانى) قوبۇل قىلىنمايدۇ ئەنە شۇلار قاتتىق ئازاب تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,19,' ئى مۆمىنلەر! ئاياللارغا زورلۇق قىلىپ، ئۇلارنى مىراس قىلىپ ئالماق (يەنى بىر ئادەمنىڭ قولىدىن يەنە بىر ئادەمنىڭ قولىغا ئۆتۈپ تۇرىدىغان مال ئورنىدا قىلىۋالماق) سىلەرگە دۇرۇس بولمايدۇ؛ ئۇلار ئوپئوچۇق بىر پاھىشە ئىشىنى قىلمىغان ھالەتتە، سىلەر ئۇلارغا بەرگەن مەھرىدىن بىر قىسمىنى يۇلۇۋېلىش ئۈچۈن، ئۇلارغا بېسىم ئىشلەتمەڭلار، ئۇلار بىلەن چىرايلىقچە تىرىكچىلىك قىلىڭلار، ئەگەر ئۇلارنى ياقتۇرمىساڭلار (سەۋر قىلىڭلار)، چۈنكى سىلەر ياقتۇرمايدىغان بىر ئىشتا اﷲ كۆپ خەيرىيەتلەرنى پەيدا قىلىشى مۇمكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,20,' ئەگەر بىر خوتۇننى قويۇۋېتىپ، ئورنىغا يەنە بىر خوتۇننى ئالماقچى بولساڭلار، ئۇلارنىڭ بىرىگە (مەھرى قىلىپ) كۆپ مال بەرگەن بولساڭلارمۇ ئۇنىڭدىن ھېچ نەرسىنى قايتۇرۇۋالماڭلار ئۇنى (ئايالغا) قارا چاپلاپ ۋە ئوچۇق زۇلۇم قىلىپ ئالامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,21,' ئۆزئارا خىلۋەتتە بولۇشقان تۇرساڭلار ۋە ئاياللار سىلەردىن (نىكاھ ئەقدىدىن ئىبارەت) مۇستەھكەم ئەھدە ئالغان تۇرسا، ئۇنى قانداقمۇ قايتۇرۇۋالىسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,22,' ئاتىلىرىڭلار ئالغان ئاياللارنى ئالماڭلار، لېكىن بۇنىڭدىن ئىلگىرى ئالغان بولساڭلار (ئەپۇ قىلىنىدۇ)، بۇ ھەقىقەتەن يامان ئىشتۇر، ئۆچ كۆرۈلىدىغان قىلىقتۇر، بۇ نېمىدېگەن يامان ئادەت!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,23,' سىلەرگە ئانىلىرىڭلارنى، قىزلىرىڭلارنى، ھەمشىرىلىرىڭلارنى، ئاتاڭلارنىڭ ھەمشىرىلىرىنى، ئاناڭلارنىڭ ھەمشىرىلىرىنى، قېرىندىشىڭلارنىڭ قىزلىرىنى، ھەمشىرەڭلارنىڭ قىزلىرىنى، سۈت ئانىلىرىڭلارنى، ئېمىلدەش ھەمشىرىڭلارنى، قېيىن ئاناڭلارنى، سىلەر بىر يەردە بولغان ئاياللىرىڭلارنىڭ (باشقا ئەردىن بولغان) ئۆيۈڭلاردا تەربىيىلەنگەن قىزلىرىنى (بۇ شەرت ئەمەس، ئۆيدە تەربىيىلەنمىگەن بولسىمۇ) ئېلىش ھارام قىلىندى ئەگەر سىلەر ئۇلارنىڭ ئانىلىرى بىلەن بىر تۆشەكتە بولمىغان بولساڭلار، بۇ چاغدا ئۇلارنى ئالساڭلار ھېچ باك يوقتۇر، يەنە ئۆز پۇشتىڭلاردىن بولغان بالىلىرىڭلارنىڭ ئاياللىرىنى ئېلىشىڭلار ۋە ئىككى ھەمشىرىنى بىر ۋاقىتتا ئەمرىڭلاردا ساقلىشىڭلار ھارام قىلىندى لېكىن ئۆتكەنكىلەر (يەنى جاھىلىيەت دەۋرىدە قىلغىنىڭلار) ئەپۇ قىلىنىدۇ اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (بەندىلەرگە) ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,24,' ئەرلىك ئاياللارنى ئېلىشمۇ سىلەرگە ھارام قىلىندى پەقەت سىلەرنىڭ باشقۇرۇشۇڭلار ئاستىدىكى چۆرىلەر بۇنىڭدىن مۇستەسنا بۇ اﷲ نىڭ سىلەرگە پەرز قىلغان ھۆكىملىرىدۇر، بۇلاردىن (يەنى يۇقىرىدىكى ئېلىش دۇرۇس بولمايدىغان ئاياللاردىن) باشقىلىرىنى زىنادىن ساقلىنىپ، ئىپپەتلىك بولغىنىڭلار ھالدا، ماللىرىڭلارنى سەرپ قىلىپ (يەنى مەھرىنى بېرىپ) نىكاھلاپ ئېلىشىڭلار ھالال قىلىندى قايسىكى ئاياللارنى (نىكاھلاپ ئېلىپ) پايدىلانساڭلار، ئۇلارغا (نىكاھ ۋاقتىدا) بېكىتىلگەن مەھرىلىرىنى بېرىڭلار، مەھرى بېكىتىلگەندىن كېيىن، ئۆزئارا پۈتۈشكەن نەرسەڭلار توغرىسىدا (يەنى ئايالنىڭ رازىلىقى بىلەن ساقىت قىلىنغان مەھرى ئۈچۈن) سىلەرگە ھېچ گۇناھ بولمايدۇ اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,25,' سىلەردىن مۆمىن ئاياللارنى ئېلىشقا قۇربى يەتمەيدىغانلار قول ئاستىدىكى مۆمىن چۆرىلەردىن ئالسۇن اﷲ ئىمانىڭلارنى ئوبدان بىلىدۇ (يەنى ئۇلارنىڭ سىرتقى كۆرۈنۈشىگە قاراپ ئېلىۋېرىڭلار، ئىچكى ئەھۋالىنى اﷲ قا تاپشۇرۇڭلار) سىلەر بىر ـ بېرىڭلار بىلەن دىنداشسىلەر، ئۇلارنىڭ خوجىلىرىنىڭ قوشۇلۇشى بىلەن ئۇلارنى خوتۇنلۇققا ئالساڭلار بولىدۇ، ئۇلارغا مەھرىلىرىنى كېمەيتىۋەتمەستىن ۋە كېچىكتۈرمەستىن بېرىڭلار لېكىن ئۇلار ئاشكارا زىنا قىلمايدىغان، يوشۇرۇن ئاشنا تۇتمايدىغان ئەفىفە بولۇشلىرى كېرەك ئەگەر ئۇلار ئەرگە تەگكەندىن كېيىن پاھىشە قىلسا، ئۇلارغا ھۆر ئاياللارغا بېرىلىدىغان جازانىڭ يېرىمى بېرىلىشى كېرەك بۇ (يەنى چۆرىلەرنى نىكاھلاپ ئېلىش)، ئاراڭلاردا زىناغا مۇپتىلا بولۇپ قېلىشتىن قورققان كىشى ئۈچۈندۇر سەۋر قىلالىساڭلار سىلەر ئۈچۈن ياخشىدۇر اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,26,' اﷲ سىلەرگە (ھالال ۋە ھارامغا دائىر ئەھكاملارنى) بايان قىلىشنى، سىلەردىن بۇرۇنقىلار (يەنى پەيغەمبەرلەر ۋە ياخشىلار) نىڭ يوللىرىغا سىلەرنى ھىدايەت قىلىشنى، (گۇناھىڭلاردىن قىلغان) تەۋبەڭلارنى قوبۇل قىلىشنى خالايدۇ اﷲ ھەممىنى (يەنى بەندىلىرىنىڭ ھەممە ئەھۋالىنى) بىلگۈچىدۇر ۋە ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,27,' اﷲ سىلەرنىڭ تەۋبەڭلارنى قوبۇل قىلىشنى خالايدۇ (بۇ اﷲ نىڭ رەھمىتىنىڭ كەڭلىكىنى كۆرسىتىش ئۈچۈن تەكرارلاندى) شەھۋەتلەرگە ئەگىشىدىغانلار (يەنى شەيتانغا ئەگىشىدىغان فاجىرلار) توغرا يولدىن بۇرۇلۇپ كېتىشىڭلارنى (شۇلارغا ئوخشاش فاجىر بولۇشۇڭلارنى) خالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,28,' اﷲ سىلەرنىڭ يۈكۈڭلارنى يېنىكلىتىشنى (يەنى شەرىئەت ئەھكاملىرىنى سىلەرگە ئاسانلاشتۇرۇشنى) خالايدۇ ئىنسان (نەپسى خاھىشىغا خىلاپلىق قىلىشتىن) ئاجىز يارىتىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,29,' ئى مۆمىنلەر! بىر ـ بىرىڭلارنىڭ ماللىرىنى (ئوغرىلىق، خىيانەت قىلىش، بۇلاش، جازانىخورلۇق، قىمار ئويناش قاتارلىق) ناھەق يول بىلەن يەۋالماڭلار، ئىككى تەرەپ رازى بولۇشۇپ قىلىشقان سودا ـ سېتىق ئارقىلىق ئېرىشىلگەن نەرسە بۇنىڭدىن مۇستەسنا سىلەر ئۆزۈڭلارنى (يەنى بىر ـ بىرىڭلارنى) ئۆلتۈرمەڭلار، اﷲ ھەقىقەتەن سىلەرگە ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,30,' كىمكى چەكتىن ئېشىپ ۋە زۇلۇم قىلىپ ئۇنى (يەنە اﷲ چەكلىگەن ئىشلارنى) قىلىدىكەن، ئۇنى دوزاخقا كىرگۈزىمىز، بۇ اﷲ قا ئاساندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,31,' ئەگەر سىلەر مەنئى قىلىنغان چوڭ گۇناھلاردىن ساقلانساڭلار، (كەرىمىمىزگە ئېلىپ) كىچىك گۇناھلىرىڭلارنى يوققا چىقىرىمىز ۋە سىلەرنى ئېسىل ماكانغا (يەنى جەننەتكە) كىرگۈزىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,32,' ئى مۆمىنلەر! اﷲ سىلەردىكى بىر ـ بىرىڭلاردىن ئارتۇق قىلغان نەرسىلەرنى (ھەسەت قىلىش يۈزىسىدىن) ئارزۇ قىلماڭلار، ئەرلەر قىلغان ئەمەللىرىدىن ھەسسىدار بولىدۇ، ئاياللارمۇ قىلغان ئەمەللىرىدىن ھەسسىدار بولىدۇ اﷲ تىن ئۇنىڭ پەزلىنى تىلەڭلار (اﷲ بېرىدۇ) اﷲ ھەقىقەتەن ھەممە نەرسىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,33,' ھەر بىر ئادەمنىڭ قالدۇرغان تەرەكىسى ئۈچۈن ۋارىسلار تەيىنلىدۇق، ئۇلار ئاتا ـ ئانىلار ۋە يېقىنلاردۇر سىلەر بىلەن (ئۆزئارا ياردەم بېرىش ۋە مىراسخور بولۇشقا) ئەھدۇپەيمان قىلىشقان كىشىلەرگە ئۇلارنىڭ (مىراستىن) ھەسسىسىنى بېرىڭلار  اﷲ ھەقىقەتەن ھەممە نەرسىنى بايقاپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,34,' ئەرلەر ئاياللارنىڭ ھامىيلىرىدۇر، بۇ اﷲ نىڭ ئۇلارنىڭ بەزىسىنى بەزىسىدىن (يەنى ئەرلەرنى كۈچ ـ قۇۋۋەت، غازاتقا چىقىش قاتارلىق جەھەتلەردە ئاياللاردىن) ئارتۇق قىلغانلىقىدىندۇر ۋە ئەرلەرنىڭ ئۆز پۇل ـ ماللىرىنى سەرپ قىلغانلىقىدىندۇر (يەنى ئاياللارنىڭ نەپىقىسى ئەرلەرنىڭ زىممىسىگە يۈكلەنگەنلىكتىندۇر) ياخشى ئاياللار ئىتائەت قىلغۇچىلاردۇر، ئەرلىرى يېنىدا بولمىغان چاغلاردا اﷲ نىڭ پاناھىدا ئەرلىرىنىڭ ھەقلىرىنى ساقلىغۇچىلاردۇر، سىلەر سەركەشلىك قىلىشلىرىدىن قورقىدىغان ئاياللارغا نەسىھەت قىلىڭلار، (بۇ ئۈنۈم بەرمىسە) بىر تۆشەكتە بىللە ياتماڭلار، (بۇمۇ ئۈنۈم بەرمىسە) ئۇلارنى ئەدەبلەش مەقسىتىدە ئاستىراق ئۇرۇڭلار ئەگەر سىلەرگە ئىتائەت قىلسا، ئۇلارنى بوزەك قىلىش خىيالىدا بولماڭلار اﷲ ھەقىقەتەن سىلەردىن ئۈستۈندۇر، بۈيۈكتۇر (اﷲ ئاياللارغا زۇلۇم قىلغاننى جازالايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,35,' ئەگەر ئەر ـ خوتۇن ئىككىيلەننىڭ ئىناقسىز بولۇپ قېلىشىدىن قورقساڭلار، ئەرنىڭ تۇغقانلىرىدىن بىر ھەققانىي كىشىنى، خوتۇننىڭ تۇغقانلىرىدىن بىر ھەققانىي كىشىنى ئەۋەتىڭلار، اﷲ ئەگەر بۇ ئىككى كىشىنى ئەپلەشتۈرۈشنى خالىسا، ئەر ـ خوتۇن ئىككىسىنىڭ ئارىسىغا ئىناقلىق سالىدۇ اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىدىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,36,' اﷲ قا ئىبادەت قىلىڭلار، ئۇنىڭغا ھېچ نەرسىنى شېرىك كەلتۈرمەڭلار، ئاتا ـ ئاناڭلارغا، خىش ـ ئەقرىبالىرىڭلارغا، يېتىملەرگە، مىسكىنلەرگە، يېقىن قوشنىغا، يىراق قوشنىغا، ياندىكى ھەمراھقا (يەنى سەپەرداشقا، ساۋاقداشقا)، مۇساپىرغا، قول ئاستىڭلاردىكى قۇل ـ چۆرىلەرگە ياخشىلىق قىلىڭلار، شۈبھىسىزكى، اﷲ مۇتەكەببىر ، ماختانچاقنى ياقتۇرمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,37,' ئۇلار (يەنى مۇتەكەببىرلەر ، ماختانچاقلار) بېخىللىق قىلىدۇ، كىشىلەرنى بېخىللىققا بۇيرۇيدۇ، اﷲ ئۇلارغا ئۆز پەزلىدىن بەرگەن نەرسىلەرنى (يەنى پۇل ـ مالنى، ۋە مۇھەممەد ئەلەيھىسسالامنىڭ تەۋراتتىكى سۈپىتىنى) يوشۇرىدۇ، (اﷲ نىڭ نېمىتىنى) ئىنكار قىلغۇچىلارغا خورلىغۇچى ئازابنى تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,38,' ئۇلار ماللىرىنى كىشىلەرگە كۆرسىتىش ئۈچۈن سەرپ قىلىدۇ، ئۇلار اﷲ قا ئىشەنمەيدۇ، ئاخىرەت كۈنىگىمۇ ئىشەنمەيدۇ كىمكى ئۆزىگە شەيتاننى ھەمراھ قىلىدىكەن (ئۇ شەيتاننىڭ بۇيرۇقى بويىچە ئىش قىلىدۇ)، شەيتان نېمىدېگەن يامان ھەمراھ!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,39,' ئەگەر ئۇلار اﷲ قا، ئاخىرەت كۈنىگە ئىشەنسە ۋە اﷲ ئۆزلىرىگە بەرگەن مالدىن سەرپ قىلسا، ئۇلارغا نېمە زىيىنى بولاتتى؟ اﷲ ئۇلارنى ئوبدان بىلىدۇ (قىلمىشىغا قاراپ جازالايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,40,' اﷲ ھەقىقەتەن زەررىچە زۇلۇم قىلمايدۇ ئەگەر كىشىنىڭ زەررىچە ياخشىلىقى بولسا، اﷲ ئۇنى ھەسسىلەپ زىيادە قىلىدۇ، ئۆز دەرگاھىدىن بۈيۈك ئەجىر (يەنى جەننەت) ئاتا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,41,' بىز ھەر بىر ئۈممەتتىن بىر گۇۋاھچىنى كەلتۈرگەن ۋە (ئى مۇھەممەد!) سېنى بۇلارغا (يەنى ئۈممىتىڭ ئىچىدىكى ئىمانسىزلارغا ۋە گۇناھكارلارغا) گۇۋاھچى قىلىپ كەلتۈرگەن چېغىمىزدا ئۇلارنىڭ ھالى قانداق بولىدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,42,' كاپىرلار ۋە پەيغەمبەرگە ئاسىيلىق قىلغانلار ئۇ كۈندە (ئۇلار قىيامەتنىڭ دەھشىتىنى كۆرگەنلىكتىن) يەر بىلەن تۈپتۈز قىلىۋېتىلىشىنى (يەنى يەرگە كۆمۈلۈپ تۇپراققا ئايلىنىپ كېتىشىنى) ئارزۇ قىلىدۇ، (ئۇلارنىڭ ھەممە ئەزاسى گۇۋاھ بولغانلىقتىن) اﷲ تىن ھېچبىر سۆزنى يوشۇرالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,43,' ئى مۆمىنلەر! سىلەر مەست بولساڭلار ، نېمە دەۋاتقىنىڭلارنى بىلگىنىڭلارغىچە، جۇنۇپ بولساڭلار، ـ يول ئۈستىدە بولغانلار (بۇنىڭدىن) مۇستەسنا، ـ غۇسلى قىلمىغۇچە نامازغا يېقىنلاشماڭلار، ئەگەر كېسەل (يەنى كېسەلگە سۇ زىيان قىلىدىغان بولسا) ياكى سەپەر ئۈستىدە بولساڭلار، ياكى ھاجەت قىلساڭلار، ياكى ئاياللار بىلەن مۇناسىۋەت ئۆتكۈزسەڭلار، (مۇشۇنداق ئەھۋال ئاستىدا) سۇ تاپالمىساڭلار، پاك تۇپراقنى يۈزۈڭلارغا، قولۇڭلارغا سۈرتۈپ تەيەممۇم قىلىڭلار، اﷲ ھەقىقەتەن ئەپۇ قىلغۇچىدۇر، مەغپىرەت قىلغۇچىدۇر (يەنى اﷲ بەندىلىرىگە ھەرەج بولماسلىقى ئۈچۈن ئىبادەتنى ئاسانلاشتۇرۇپ بەرگۈچىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,44,' (ئى مۇھەممەد!) كىتابتىن (يەنى تەۋراتتىن) نېسىۋە بېرىلگەن كىشىلەر (يەنى يەھۇدىي موللىلىرى) نىڭ گۇمراھلىقنى سېتىۋالغانلىقىنى ۋە سىلەرنىڭ توغرا يولدىن ئېزىشىڭلارنى خالايدىغانلىقىنى كۆرمىدىڭمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,45,' اﷲ سىلەرنىڭ دۈشمەنلىرىڭلارنى ئوبدان بىلىدۇ اﷲ سىلەرنى ساقلاشقا يېتەرلىكتۇر ۋە سىلەرگە مەدەتكار بولۇشقا يېتەرلىكتۇر (يەنى سىلەرنى يەھۇدىيلارنىڭ مىكرىدىن ساقلاشقا اﷲ كۇپايىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,46,' يەھۇدىيلارنىڭ ئىچىدە كىتابنىڭ (يەنى تەۋراتنىڭ) سۆزلىرىنى ئۆزگەرتىۋېتىدىغانلارمۇ بار، ئۇلار: «بىز (سۆزۈڭنى) ئاڭلىدۇق، (ئەمرىڭگە) بويسۇنمىدۇق، بىزگە قۇلاق سال، بىز ساڭا قۇلاق سالمايمىز» دەيدۇ، دىنغا تەنە قىلىش يۈزىسىدىن، رائىنا دېگەن سۆزنى تىللىرىنى ئەگرى قىلىپ ئېيتىدۇ ئەگەر ئۇلار (يۇقىرىقى سۆزلىرىنىڭ ئورنىغا) ئاڭلىدۇق ۋە بويسۇندۇق، قۇلاق سال، ئۇنزۇرنا دېسە، ئۇلار ئۈچۈن (اﷲ نىڭ دەرگاھىدا) تېخىمۇ ياخشى، تېخىمۇ توغرا بولاتتى، لېكىن كۇفرى سەۋەبلىك اﷲ ئۇلارنى رەھمىتىدىن يىراق قىلدى ئۇلارنىڭ ئازغىنىسىدىن باشقىسى ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,47,' ئى كىتاب بېرىلگەنلەر! (يەنى يەھۇدىيلار) بىز نۇرغۇن يۈزلەرنى ئۆزگەرتىپ ئارقىسىغا قىلىپ قويۇشىمىزدىن (يەنى يۈزدىكى بۇرۇن، كۆز، قاش قاتارلىق سەزگۈ ئەزالىرىنى ئۆز جايلىرىدىن يوق قىلىۋېتىپ، ئۇلارنى ئارقا تەرەپكە پەيدا قىلىپ قويۇشىمىزدىن، سۈرەتلىرىنى مۇبەددەل قىلىشىمىزدىن)، ئىلگىرى، ياكى ئۇلارغا شەنبە كۈنىنىڭ ھۆرمىتىنى ساقلىمىغانلارغا لەنەت قىلغاندەك لەنەت قىلىشىمىزدىن ئىلگىرى، ئۆزۈڭلاردىكى كىتابنى (يەنى تەۋراتنى) تەستىق قىلىدىغان، بىز نازىل قىلغان كىتابقا (يەنى قۇرئانغا) ئىمان كەلتۈرۈڭلار (بولمىسا يۇقىرىقى ئىككى بالانىڭ بىرىگە ئۇچرايسىلەر)، اﷲ نىڭ ھۆكىمى ئىجرا قىلىنىدۇ (يەنى اﷲ بىرەر ئىشقا ئەمر قىلسا، ئۇ ئىش چوقۇم بولماي قالمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,48,' اﷲ ھەقىقەتەن اﷲ قا شېرىك كەلتۈرۈش گۇناھىنى مەغپىرەت قىلمايدۇ، خالىغان ئادەمنىڭ ئۇنىڭدىن باشقا گۇناھىنى مەغپىرەت قىلىدۇ كىمكى اﷲ قا شېرىك كەلتۈرىدىكەن، چوڭ گۇناھ قىلغان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,49,' ئۆزلىرىنى پاك دەپ قارايدىغانلار (يەنى ئۆزلىرىنى تەقۋادارلىق بىلەن مەدھىيىلەيدىغانلارنى) نى كۆرمىدىڭمۇ؟ ئۇنداق ئەمەس (يەنى ئىش ئۇلارنىڭ دېگىنىدەك ئەمەس)، اﷲ خالىغان بەندىسىنى پاك قىلىدۇ، ئۇلارغا قىلچىلىكمۇ زۇلۇم قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,50,' (ئى مۇھەممەد!) ئۇلارنىڭ اﷲ قا تۆھمەت چاپلاۋاتقانلىقىغا قارىغىن! بۇ ئوپئوچۇق گۇناھتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,51,' كىتابتىن نېسىۋە بېرىلگەنلەر (يەنى يەھۇدىيلار) نى كۆرمىدىڭمۇ؟ ئۇلار بۇتقا ۋە شەيتانغا ئىشىنىدۇ، كاپىرلارنى كۆرسىتىپ: «بۇلار (يەنى قۇرەيش كۇففارلىرى) نىڭ يولى ئىمان ئېيتقانلارنىڭكىدىن تېخىمۇ توغرىدۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,52,' ئەنە شۇلار اﷲ رەھمىتىدىن يىراق قىلغان كىشىلەردۇر؛ اﷲ كىمنى رەھمىتىدىن يىراق قىلىدىكەن، ئۇنىڭغا (اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچبىر مەدەتكار تاپالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,53,' ياكى ئۇلارنىڭ پادىشاھلىقتىن نېسىۋىسى بارمۇ؟ (ھېچقانداق نېسىۋىسى يوق) ئەگەر بولىدىغان بولسا، (زىيادە بېخىللىقتىن) ئۇلار كىشىلەرگە قىلچىلىك نەرسە بەرمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,54,' ياكى ئۇلار اﷲ ئۆز پەزلىدىن كىشىلەرگە بەرگەن نەرسىگە (يەنى مۇھەممەد ئەلەيھىسسالامغا بېرىلگەن پەيغەمبەرلىككە) ھەسەت قىلىشامدۇ؟ بىز ھەقىقەتەن ئىبىراھىم ئەۋلادىغا كىتاب، ھېكمەت بەردۇق ۋە ئۇلارغا بۈيۈك پادىشاھلىق بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,55,' ئۇلار (يەنى يەھۇدىيلار) نىڭ ئىچىدە ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) ئىمان ئېيتقانلارمۇ بار، ئۇنىڭدىن يۈز ئۆرۈگەنلەرمۇ بار، ئۇلارغا (ئۇلارنىڭ كۇفرىغا جازا بېرىش يۈزىسىدىن) جەھەننەمنىڭ يېنىپ تۇرغان ئوتى يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,56,' شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى دوزاخقا كىرگۈزىمىز، ئۇلارنىڭ تېرىلىرى پىشىپ تۈگىگەن چاغدا ئازابنى تېتىتىش ئۈچۈن ئورنىغا باشقا تېرە يەڭگۈشلەيمىز اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,57,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمىز، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ ئۇ يەردە ئۇلارغا پاك جۈپتىلەر بولىدۇ، ئۇلارنى جەننەتنىڭ مەڭگۈلۈك سايىسىگە داخىل قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,58,' شۈبھىسىزكى، اﷲ سىلەرنى ئامانەتلەرنى ئىگىسىگە قايتۇرۇشقا، كىشىلەر ئارىسىدا ھۆكۈم قىلغاندا ئادىل ھۆكۈم قىلىشقا بۇيرۇيدۇ، اﷲ سىلەرگە نەسىھەت قىلغان ئىشلار نېمىدېگەن ياخشى، اﷲ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,59,' ئى مۆمىنلەر! اﷲ قا، پەيغەمبەرگە ۋە ئۆزۈڭلاردىن بولغان ئىش ئۈستىدىكىلەرگە ئىتائەت قىلىڭلار، ئەگەر سىلەر بىر شەيئىدە ئىختىلاپ قىلىشىپ قالساڭلار، بۇ توغرىدا اﷲ قا ۋە پەيغەمبەرگە مۇراجىئەت قىلىڭلار، بۇ (يەنى اﷲ نىڭ كىتابىغا ۋە پەيغەمبىرىنىڭ سۈننىتىگە مۇراجىئەت قىلىش) سىلەر ئۈچۈن پايدىلىقتۇر، نەتىجە ئېتىبارى بىلەن گۈزەلدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,60,' ساڭا نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا) ۋە سەندىن بۇرۇن نازىل قىلىنغان كىتابلارغا (يەنى تەۋراتقا ۋە ئىنجىلغا) ئىمان كەلتۈردۇق دەۋالغان كىشىلەر (يەنى مۇناپىقلار) نى كۆرمىدىڭمۇ؟ ئۇلار ئەرزىنى شەيتاننىڭ ئالدىغا ئېلىپ بارماقچى بولۇۋاتىدۇ؛ ھالبۇكى، ئۇلار شەيتاننى ئىنكار قىلىشقا بۇيرۇلغان، شەيتان ئۇلارنى چوڭقۇر ئازدۇرۇشنى خالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,61,' ئۇلار (يەنى مۇناپىقلار) غا: «اﷲ نازىل قىلغان كىتاب تەرەپكە ۋە پەيغەمبەر تەرەپكە كېلىڭلار» دېيىلسە، مۇناپىقلارنىڭ سەندىن قاتتىق يۈز ئۆرۈگەنلىكىنى كۆرىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,62,' قىلمىشلىرى تۈپەيلىدىن ئۆز بېشىغا بىرەر مۇسىبەت كەلگەن چاغدا، سېنىڭ ئالدىڭغا كېلىپ اﷲ بىلەن قەسەم قىلغان ھالدا: «بىز پەقەت ياخشىلىق ۋە يارىشىشنىلا ئىرادە قىلغان ئىدۇق» دېگەنلەرنىڭ ھالى قانداق بولىدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,63,' ئەنە شۇلار (يەنى مۇناپىقلار) (يالغان ئېيتىدۇ)، اﷲ ئۇلارنىڭ دىللىرىدىكىنى (يەنى مۇناپىقلىقىنى، مىكرىنى) بىلىدۇ، ئۇلاردىن يۈز ئۆرۈگىن، ئۇلارغا نەسىھەت قىلغىن، ئۇلارغا تەسىرلىك سۆزلەرنى قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,64,' بىز ھەر قانداق پەيغەمبەرنى پەقەت ئىتائەت قىلىنىش ئۈچۈنلا ئەۋەتتۇق (ئۇنىڭغا ئىتائەت قىلغانلىق اﷲ قا ئىتائەت قىلغانلىق بولىدۇ)، ئۇلار (يەنى مۇناپىقلار) (سېنىڭ ھۆكىمىڭنى قوبۇل قىلماي) ئۆزلىرىگە زۇلۇم قىلغاندا، ساڭا كېلىپ اﷲ تىن مەغپىرەت تىلىسە، پەيغەمبەرمۇ ئۇلار ئۈچۈن مەغپىرەت تىلىسە ئىدى، ئەلۋەتتە، ئۇلار اﷲ نى كەچۈرۈم قىلغۇچى، ناھايىتى مېھرىبان تاپاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,65,' (ئى مۇھەممەد!) پەرۋەردىگارىڭ بىلەن قەسەمكى، ئۇلار (يەنى مۇناپىقلار) ئۆز ئارىسىدىكى دەتالاشقا سېنى ھۆكۈم چىقىرىشقا تەكلىپ قىلمىغۇچە، ئاندىن سېنىڭ چىقارغان ھۆكىمىڭگە ئۇلارنىڭ دىللىرىدىكى قىلچە غۇم بولسىمۇ يوقالمىغىچە ۋە ئۇلار پۈتۈنلەي بويسۇنمىغىچە ئىمان ئېيتقان بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,66,' ئەگەر بىز ئۇلارغا: «ئۆزۈڭلارنى ئۆلتۈرۈڭلار ياكى يۇرتۇڭلاردىن چىقىپ كېتىڭلار» دەپ ئەمر قىلغان بولساق (يەنى ئىلگىرىكىلەرگە يۈكلىگەندەك ئېغىر تەكلىپنى ئۇلارغا يۈكلىسەك) ئىدۇق، ئۇلارنىڭ ئازغىنىسىدىن باشقىسى بۇنى ئىجرا قىلمايتتى، ئەگەر ئۇلار قىلىنغان ۋەز ـ نەسىھەتكە ئەمەل قىلسا ئىدى، ئەلۋەتتە ئۇلارغا ياخشى بولاتتى ۋە ئىمانى مۇستەھكەم بولغان بولاتتى (گۇمراھلىق ۋە نىفاقتىن ئەڭ يىراق بولغان بولاتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,67,' ئۇ چاغدا دەرگاھىمىزدىن ئۇلارغا ئەلۋەتتە بۈيۈك ئەجىر (يەنى جەننەت) ئاتا قىلاتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,68,' ئۇلارنى ئەلۋەتتە توغرا يولغا ھىدايەت قىلاتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,69,' كىملەركى اﷲ قا ۋە پەيغەمبەرگە ئىتائەت قىلىدىكەن، ئۇلار (ئاخىرەتتە) اﷲ نىڭ نېمىتىگە ئېرىشكەن پەيغەمبەرلەر، سىددىقلار (يەنى يۇقىرى دەرىجىلىك پەيغەمبەرلەر ۋە شېھىتلەر)، شېھىتلەر ۋە ياخشىلار بىلەن بىللە بولىدۇ، ئۇلار نېمىدېگەن ياخشى ھەمراھلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,70,' بۇ پەزل اﷲ تەرىپىدىندۇر، اﷲ (پەزل ـ ئېھسانغا تېگىشلىك بولغانلارنى) بىلىشتە يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,71,' ئى مۆمىنلەر! (دۈشمەنلەرنىڭ شەررىدىن ساقلىنىش ئۈچۈن) تەييارلىق ھالىتىدە تۇرۇڭلار، ئۇرۇشقا تۈركۈم ـ تۈركۈم بولۇپ ياكى ھەممىڭلار بىر بولۇپ چىقىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,72,' شۈبھىسىزكى، ئاراڭلاردا ئۇرۇشقا چىقماي قېلىپ قالغانلار (يەنى مۇناپىقلار) بار، ئەگەر سىلەرگە بىرەر مۇسىبەت (يەنى مەغلۇبىيەت ياكى قىرغىنلىق) كەلسە، (ئۇ مۇناپىق) «اﷲ ماڭا ھەقىقەتەن مەرھەمەت قىلدى، چۈنكى مەن ئۇلار بىلەن (ئۇرۇشتا) بىللە بولمىدىم (بولمىسا ئۆلتۈرۈلگەنلەر قاتارىدا ئۆلتۈرۈلگەن بولاتتىم)» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,73,' ئەگەر سىلەرگە اﷲ نىڭ پەزلى (يەنى غەلىبە ۋە غەنىيمەت) يەتسە، گويا سىلەر بىلەن ئۇ (يەنى مۇناپىق) نىڭ ئارىسىدا ھېچقانداق دوستلۇق يوقتىكىدەك، «كاشكى مەن (ئۇرۇشتا) ئۇلار بىلەن بىللە بولغان بولسام، چوڭ بىر مۇۋەپپەقىيەتكە (يەنى غەنىيمەتتىن زور نېسىۋىگە) ئېرىشكەن بولاتتىم» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,74,' دۇنيا تىرىكچىلىكىنى ئاخىرەتكە تېگىشىدىغانلار اﷲ يولىدا (جېنىنى، پۇل ـ مېلىنى پىدا قىلىپ) جىھاد قىلسۇن، كىمكى اﷲ يولىدا جىھاد قىلىپ ئۆلتۈرۈلسە ياكى غەلىبە قىلسا، بىز ئۇنىڭغا كاتتا ئەجىر ئاتا قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,75,' (ئى مۆمىنلەر!) سىلەرگە نېمە بولدىكى، اﷲ نىڭ يولىدا جىھاد قىلمايسىلەر ۋە (دۇئا قىلىپ): «ئى پەرۋەردىگارىمىز! بۇ ئاھالىسى زالىم شەھەردىن  بىزنى چىقارغىن، ئۆز دەرگاھىڭدىن بىزگە بىر ئىگىدارچىلىق قىلغۇچىنى مۇيەسسەر قىل، ئۆز دەرگاھىڭدىن بىزگە بىر مەدەتكارنى مۇيەسسەر قىل» دەيدىغان ئاجىز ئەرلەر، ئاجىز ئاياللار ۋە بالىلارنى قۇتقۇزۇش ئۈچۈن جىھاد قىلمايسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,76,' مۆمىنلەر اﷲ يولىدا جىھاد قىلىدۇ، كاپىرلار شەيتاننىڭ يولىدا ئۇرۇش قىلىدۇ؛ شەيتاننىڭ دوستلىرى بىلەن ئۇرۇش قىلىڭلار (سىلەر ئۇلارنى يېڭىسىلەر)، شەيتاننىڭ تەدبىرى ھەقىقەتەن ئاجىزدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,77,' (ئى مۇھەممەد!) ئۇلارغا (يەنى مەككىدە تۇرۇپ ئۇرۇشۇشنى تەلەپ قىلغانلارغا): «(ئۇرۇشتىن) قولۇڭلارنى يىغىڭلار، ناماز ئوقۇڭلار، زاكات بېرىڭلار» دېگەن كىشىلەرنى كۆرمىدىڭمۇ؟ (يەنى ئۇلاردىن ئەجەبلەنمەمسەن؟) بۇلارغا جىھاد پەرز قىلىنغان چاغدا، ئۇلارنىڭ ئارىسىدىكى بىر تۈركۈم كىشىلەر دۈشمەندىن گويا اﷲ (نىڭ ئازابى) دىن قورققاندەك بەلكى ئۇنىڭدىنمۇ بەتتەررەك قورقىدۇ ئۇلار (ئۆلۈمدىن قورقۇپ): «پەرۋەردىگارىمىز! بىزگە نېمە ئۈچۈن جىھادنى پەرز قىلدىڭ؟ نېمە ئۈچۈن بىزنى ئۇزاققا قالماي كېلىدىغان ئەجەلگىچە تەخىر قىلمىدىڭ؟ (يەنى ئەجىلىمىز يەتكۈچە تەخىر قىلساڭ، ئۇرۇشتا ئۆلتۈرۈلمىسەك بولماسمىدى؟)» دېدى (ئى مۇھەممەد!) ئېيتقىنكى، «دۇنيانىڭ مەنپەئىتى ئازدۇر (يەنى دۇنيانىڭ نېمىتى پانىيدۇر)، (اﷲ تىن) قورققان (ۋە ئۇنىڭ ئەمرىنى تۇتقانلار) ئۈچۈن ئاخىرەت ياخشىدۇر، سىلەرگە قىلچىلىك زۇلۇم قىلىنمايدۇ (يەنى قىلغان ئەمەلىڭلارنىڭ ساۋابى كېمەيتىلمەيدۇ)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,78,' قەيەردە بولماڭلار، (ئەجەل كەلگەندە) ئۆلۈم سىلەرنى تاپىدۇ، سىلەر مۇستەھكەم قەلئەلەردە بولغان تەقدىردىمۇ ئەگەر ئۇلار (يەنى مۇناپىقلار) بىرەر ياخشىلىققا ئېرىشسە، «بۇ اﷲ تىن بولدى» دەيدۇ ئەگەر ئۇلار بىرەر زىيان ـ زەخمەتكە ئۇچرىسا، «بۇ سەندىن (يەنى سېنىڭ دىنىڭغا كىرگەنلىكىمىزدىن) بولدى» دەيدۇ (ئى مۇھەممەد! بۇ ئەخمەقلەرگە) ئېيتقىنكى، «(ياخشىلىق ۋە يامانلىقنىڭ) ھەممىسى اﷲ تەرىپىدىندۇر (يەنى ھەممىسىنى اﷲ ياراتقاندۇر)» بۇ قەۋمگە نېمە بولدىكىن، ئۇلار ھېچبىر سۆزنى (يەنى ھەممە شەيئىنىڭ اﷲ نىڭ تەقدىرى بىلەن بولىدىغانلىقىنى) چۈشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,79,' (ئى ئىنسان!) ساڭا يەتكەن ياخشىلىق (مەرھەمەت قىلىش يۈزىسىدىن) اﷲ تەرىپىدىندۇر، ساڭا يەتكەن يامانلىق (قىلمىشلىرىڭ تۈپەيلىدىن) ئۆزۈڭدىندۇر (ئى مۇھەممەد!) سېنى بىز (پۈتۈن) ئىنسانلارغا پەيغەمبەر قىلىپ ئەۋەتتۇق، (سېنىڭ پەيغەمبەرلىكىڭگە) شاھىتىلىققا اﷲ يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,80,' كىمكى پەيغەمبەرگە ئىتائەت قىلىدىكەن، ئۇ اﷲ قا ئىتائەت قىلغان بولىدۇ (چۈنكى پەيغەمبەر اﷲ نىڭ ئەمرىنى يەتكۈزىدۇ) كىمكى (ئى مۇھەممەد!) سەندىن يۈز ئۆرۈيدىكەن، (بىلگىنكى) بىز سېنى ئۇلارغا كۆزەتچى (يەنى ئۇلارنىڭ ئەمەللىرىنى كۆزىتىپ، ئەمەللىرىگە قاراپ ھېساب ئالغۇچى) قىلىپ ئەۋەتمىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,81,' ئۇلار (يەنى مۇناپىقلار): «ئەمرىڭگە ئىتائەت قىلدۇق» دەيدۇ، سېنىڭ يېنىڭدىن چىقىپ كەتكەن چاغدا، ئۇلارنىڭ بىر تۈركۈمى ئۆزلىرىنىڭ ئېيتقان سۆزلىرىنى ئۆزگەرتىۋېتىدۇ (يەنى ئەمرىڭگە خىلاپلىق قىلىدۇ)، اﷲ ئۇلارنىڭ ئۆزگەرتكەنلىرىنى خاتىرىلەپ تۇرىدۇ (يەنى پەرىشتىلەرنى ئۇلارنىڭ ئەمەللىرىنى يېزىشقا بۇيرۇيدۇ) سەن ئۇلاردىن يۈز ئۆرۈگىن (يەنى كەچۈرگىن)، اﷲ قا تەۋەككۈل قىلغىن (تەۋەككۈل قىلغۇچىغا) اﷲ نىڭ ھامىيلىقى يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,82,' ئۇلار قۇرئان ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ ئەگەر قۇرئان (مۇشرىكلار بىلەن مۇناپىقلار گۇمان قىلغاندەك) اﷲ تىن غەيرىنىڭ تەرىپىدىن بولغان بولسا، ئەلۋەتتە، ئۇنىڭدىن نۇرغۇن زىددىيەتلەرنى تاپقان بولاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,83,' (مۆمىنلەرگە ئائىت) ئامانلىقنىڭ ياكى قورقۇنچنىڭ بىرەر خەۋىرى ئۇلارغا (يەنى مۇناپىقلارغا) يەتسە، ئۇنى تارقىتىدۇ، ئەگەر ئۇنى پەيغەمبەرگە ۋە ئۇلار (يەنى مۆمىنلەر) نىڭ ئىچىدىكى ئىش ئۈستىدىكىلەرگە مەلۇم قىلسا، (شۇ) خەۋەرنى چىقارغۇچىلار ئۇنى (يەنى شۇ خەۋەرنىڭ ھەقىقىي ئەھۋالىنى) ئۇلاردىن ئەلۋەتتە بىلىۋالاتتى (ئى مۆمىنلەر!) ئەگەر سىلەرگە اﷲ نىڭ پەزلى ۋە مەرھەمىتى بولمىسا ئىدى، سىلەرنىڭ ئازغىنا كىشىدىن باشقىلىرىڭلار، ئەلۋەتتە، شەيتانغا ئەگىشىپ كېتەتتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,84,' (ئى مۇھەممەد!) اﷲ نىڭ يولىدا جىھاد قىلغىن (يەنى ئۆزۈڭ يالغۇز بولساڭمۇ جىھاد قىلغىن، ساڭا غەلىبە ۋەدە قىلىنغان مۇناپىقلارنىڭ جىھادتىن قېلىپ قالغانلىقىغا ئەھمىيەت بېرىپ كەتمە) سەن پەقەت ئۆزۈڭگىلا جاۋابكار سەن، مۆمىنلەرنى (جىھادقا) قىزىقتۇرغىن، اﷲ نىڭ كاپىرلار كۈچىنى توسىدىغانلىقىمۇ ھەقىقەتتۇر، اﷲ نىڭ كۈچى ئەڭ زوردۇر اﷲ نىڭ جازاسى ئەڭ قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,85,' كىمكى (كىشىلەر ئارىسىدا) ياخشى (ئىش ئۈچۈن) شاپائەت قىلسا، ئۇنىڭدىن (يەنى ياخشى ئىشتىن) ئۇنىڭ نېسىۋىسى بولىدۇ؛ كىمكى بىر يامان (ئىش ئۈچۈن) شاپائەت قىلسا، ئۇنىڭدىن (يەنى يامان ئىشتىن) ئۇنىڭ نېسىۋىسى بولىدۇ اﷲ ھەممە ئىشقا قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,86,' سىلەرگە بىر كىشى سالام بەرسە، ئۇنىڭغا تېخىمۇ ياخشى سالام بىلەن جاۋاب قايتۇرۇڭلار (يەنى بىر كىشى ئەسسالامۇئەلەيكۇم دەپ سالام بەرسە، ئۇنىڭغا ئەسسالامۇ ئەلەيكۇم ۋە رەھمەتۇللاھى ۋەبەرەكاتۇھۇ دەڭلار)، ياكى ئۇنىڭ سالامىنى ئەينەن قايتۇرۇڭلار (يەنى ۋەئەلەيكۇم ئەسسالام دەڭلار) اﷲ ھەقىقەتەن ھەممە نەرسىدىن (يەنى بەندىلىرىنىڭ چوڭ ـ كىچىك ئىشلىرىدىن ھېساب ئالغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,87,' اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر اﷲ شەكسىز بولىدىغان قىيامەت كۈنىدە سىلەرنى (ھېساب ئېلىش ئۈچۈن مەھشەرگاھقا)جەزمەن يىغىدۇ اﷲ تىنمۇ توغرا سۆزلۈك كىم بار؟ (يەنى سۆزىدە، ۋەدىسىدە اﷲ تىنمۇ سادىق ھېچكىم يوق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,88,' ئى مۆمىنلەر! سىلەر نېمىشقا مۇناپىقلار توغرىسىدا ئىككى گۇرۇھقا بۆلۈنۈپ كېتىسىلەر؟ اﷲ ئۇلارنىڭ قىلمىشلىرى تۈپەيلىدىن ئۇلارنى كاپىرلار ھۆكمىدە قىلدى، اﷲ ئازدۇرغان كىشىلەرنى سىلەر ھىدايەت قىلماقچى بولامسىلەر؟ كىمنىكى اﷲ گۇمراھ قىلىدىكەن، سەن ئۇنىڭغا ھەرگىز توغرا يول تېپىپ بېرەلمەيسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,89,' ئۇلار سىلەرنىڭ ئۆزلىرىدەك كاپىر بولۇشۇڭلارنى، شۇنىڭ بىلەن ئۆزلىرىگە ئوخشاش بولۇشۇڭلارنى ئۈمىد قىلىدۇ، ئۇلار اﷲ يولىدا ھىجرەت قىلمىغۇچە (يەنى ھىجرەت قىلىش ئارقىلىق ئىمانىنى ئىسپات قىلمىغۇچە) ئۇلارنى دوست تۇتماڭلار، ئەگەر ئۇلار اﷲ يولىدا ھىجرەت قىلىشتىن يۈز ئۆرۈسە، ئۇلارنى قەيەردە تاپساڭلار شۇ يەردە تۇتۇپ ئۆلتۈرۈڭلار، ئۇلارنى دوستمۇ تۇتماڭلار، ياردەمچىمۇ قىلماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,90,' پەقەت سىلەر بىلەن ئۆزلىرى ئارىسىدا ئەھدە بولغان بىر قەۋمگە سېغىنغانلار ياكى سىلەرگە قارشى ئۇرۇش قىلىشتىن ياكى ئۆز قەۋمگە قارشى ئۇرۇش قىلىشتىن يۈرەكلىرى سىقىلىپ (يەنى سىلەر تەرەپمۇ ئەمەس، ئۆز قەۋمى تەرەپمۇ ئەمەس بولۇپ) سىلەرگە كەلگەنلەر بۇنىڭدىن مۇستەسنا (يەنى مۇنداقلارنى ئۆلتۈرمەڭلار ۋە ئەسىر ئالماڭلار) ئەگەر اﷲ خالىسا، ئۇلارنى سىلەرگە مۇسەللەت قىلاتتى، ئۇلار، ئەلۋەتتە، سىلەر بىلەن ئۇرۇشاتتى (يەنى اﷲ ئۇلارنى كۈچلۈك قىلپ سىلەرگە قارشى ئۇرۇش قىلىشقا جۈرئەتلىك قىلاتتى)، ئەگەر ئۇلار سىلەردىن يىراق تۇرسا (يەنى سىلەرگە چېقىلمىسا)، سىلەر بىلەن ئۇرۇشمىسا ۋە سىلەرگە تەسلىم بولسا، اﷲ سىلەرنىڭ ئۇلارغا ھۇجۇم قىلىشىڭلارنى ھەرگىز رۇخسەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,91,' ئىمانىنى ئىزھار قىلىش بىلەن سىلەردىنمۇ ئامانلىققا ئېرىشمەكچى ۋە قەۋمىگە قايتقاندا (كۇفرىنى ئىزھار قىلىش بىلەن) ئۆز قەۋمىدىنمۇ ئامانلىققا ئېرىشمەكچى بولغان ئىككىنچى بىر خىل كىشىلەر (يەنى مۇناپىقلار) نى كۆرىسىلەر، ئۇلار ھەر قاچان پىتنە ـ پاساتقا (يەنى كۇفرىغا ياكى مۇسۇلمانلار بىلەن ئۇرۇش قىلىشقا) چاقىرىلسا ئۆزلىرىنى ئۇنىڭدىن تارتىدۇ، ئەگەر ئۇلار سىلەردىن يىراق تۇرمىسا، سىلەرگە تەسلىم بولمىسا، (سىلەر بىلەن) ئۇرۇشۇشتىن قول يىغمىسا، بۇ چاغدا ئۇلارنى قەيەردە كۆرسەڭلار شۇ يەردە (ئەسىر ئېلىڭلار) تۇتۇپ ئۆلتۈرۈڭلار، (ئۇلارنىڭ خىيانىتى سەۋەبلىك) سىلەرگە ئۇلارغا قارشى (يەنى ئەسىر ئېلىشقا، ئۆلتۈرۈشكە) روشەن پاكىت ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,92,' مۆمىن ئادەمنىڭ مۆمىن ئادەمنى سەۋەنلىك بىلەن ئەمەس، قەستەن ئۆلتۈرۈشى ھېچ دۇرۇس ئەمەس، كىمكى بىرەر مۆمىننى سەۋەنلىك بىلەن ئۆلتۈرۈپ قويىدىكەن، ئۇ بىر مۆمىن قۇلنى ئازاد قىلىشى ۋە ئۆلتۈرۈلگۈچىنىڭ ۋارىسلىرىغا دىيەت تۆلىشى كېرەك، ئۇلار (يەنى ئۆلتۈرۈلگۈچىنىڭ ۋارىسلىرى) دىيەتنى كەچۈرۈم قىلسام (شۇنىڭ بىلەن قاتىل ئۇنى) تۆلىمىسە ھېچ باك يوق، ئەگەر ئۆلتۈرۈلگۈچى مۆمىن سىلەرگە دۈشمەن (كۇففار) قەۋمدىن بولسا، بۇ چاغدا ئۆلتۈرۈپ قويغۇچى بىر نەپەر مۆمىن قۇلنى ئازاد قىلىشى لازىم (دىيەت كەلمەيدۇ چۈنكى ئۆلتۈرۈلگۈچىنىڭ ئىگىسى مۇسۇلمان بولمىغاچقا ئۇنىڭغا ۋارىس بولمايدۇ)، ئەگەر ئۆلتۈرۈلگۈچى ئاراڭلاردا مۇئاھىدە بولغان (كۇففار) قەۋمدىن بولسا، ئۇنىڭ ۋارىسلىرىغا دىيەت تۆلەش ۋە بىر نەپەر قۇلنى ئازاد قىلىش كېرەك، ئازاد قىلىدىغان قۇل تاپالمىغان ئادەم (يەنى قۇل ئازاد قىلىشقا قادىر بولالمىغان ئادەم) (تەۋبىسىنى اﷲ نىڭ قوبۇل قىلىشى ئۈچۈن) ئۈزۈلدۈرمەي ئىككى ئاي روزا تۇتۇشى لازىم اﷲ ھەممىنى بىلىپ تۇرغۇچى، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,93,' كىمكى بىر مۆمىننى قەستەن ئۆلتۈرىدىكەن، ئۇنىڭ جازاسى جەھەننەم بولىدۇ، ئۇ جەھەننەمدە مەڭگۈ قالىدۇ، اﷲ نىڭ غەزىپىگە ۋە لەنىتىگە دۇچار بولىدۇ اﷲ ئۇنىڭغا (ئاخىرەتتە) قاتتىق ئازاب تەييارلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,94,' ئى مۆمىنلەر! اﷲ يولىغا (يەنى غازاتقا) ئاتلانغان ۋاقتىڭلاردا ھەقىقەتلەپ ئىش قىلىڭلار (يەنى مۆمىن ياكى كاپىرلىقى ئېنىق بولغۇچە ئۆلتۈرۈشكە ئالدىراپ كەتمەڭلار)، سىلەرگە مۇسۇلمان بولغانلىقىنى بىلدۈرۈپ سالام بەرگەن ئادەمگە ـ دۇنيانىڭ مال ـ مۈلكىنى (غەنىيمەتنى) كۆزلەپ ـ سەن مۆمىن ئەمەسسەن دېمەڭلار (يەنى ئۆلۈشتىن قورقۇپ سالام بەردىڭ دەپ ئۇنى ئۆلتۈرۈۋەتمەڭلار)، اﷲ نىڭ دەرگاھىدا (مۇنداقلارنى ئۆلتۈرۈشتىن سىلەرنى بىھاجەت قىلىدىغان) نۇرغۇن غەنىيمەتلەر (يەنى ساۋاب) بار، ئىلگىرى سىلەرمۇ ئەنە شۇنداق (كاپىر) ئىدىڭلار، كېيىن اﷲ سىلەرگە (ئىماننى) ئىلتىپات قىلدى، (ئۇلارنى ئۆزۈڭلارغا سېلىشتۇرۇپ) ھەقىقەتلەپ ئىش قىلىڭلار اﷲ سىلەرنىڭ قىلمىشىڭلاردىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,95,' مۆمىنلەردىن ئۆزرىسىز (ئەما، توكۇر، كېسەلگە ئوخشاش ئۆزرىسى بارلار بۇنىڭدىن مۇستەسنا) جىھادقا چىقمىغانلار اﷲ يولىدا ماللىرىنى، جانلىرىنى تىكىپ جىھاد قىلغۇچىلار بىلەن باراۋەر بولمايدۇ اﷲ ماللىرىنى، جانلىرىنى تىكىپ جىھاد قىلغۇچىلارنى جىھادقا چىقمىغانلاردىن بىر دەرىجە ئۈستۈن قىلدى بۇ ئىككى خىل كىشىلەر (يەنى ئۆزرىسى بولۇپ جىھادقا چىقالمىغانلار ۋە جىھادقا چىققۇچىلار) نىڭ ھەممىسىگە اﷲ جەننەتنى ۋەدە قىلدى اﷲ جىھاد قىلغۇچىلارغا بۈيۈك ئەجىر ئاتا قىلىپ، ئۇلارنى (ئۆزرىسىز تۇرۇپ جىھادقا چىقمىغانلاردىن ئارتۇق) قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,96,' اﷲ (ئۇلارغا) بەلەن مەرتىۋىلەر، مەغپىرەت ۋە رەھمەت ئاتا قىلدى، اﷲ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,97,' ئۆزلىرىگە زۇلۇم قىلغۇچىلار (يەنى ھىجرەتنى تەرك ئېتىپ كۇففارلار بىلەن بىللە تۇرغۇچىلار) نىڭ جانلىرى پەرىشتىلەر تەرىپىدىن ئېلىنىدىغان چاغدا، پەرىشتىلەر ئۇلاردىن: «(دىنىڭلارنىڭ ئىشىدا) قايسى ھالەتتە ئىدىڭلار؟» دەپ سورايدۇ ئۇلار: «زېمىندا (يەنى مەككى زېمىنىدا) بىز (دىننى بەرپا قىلىشتىن) بوزەك قىلىنغان ئىدۇق» دەيدۇ پەرىشتىلەر: «ھىجرەت قىلساڭلار اﷲ نىڭ زېمىنى كەڭرى ئەمەسمىدى؟» دەيدۇ ئەنە شۇ (ھىجرەت قىلمىغان) لارنىڭ بارىدىغان جايى جەھەننەمدۇر جەھەننەم نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,98,' پەقەت ئەرلەردىن، ئاياللاردىن، بالىلاردىن چارىسىز قالغان (يەنى ھىجرەت قىلىشقا چارە تاپالمىغان)، يول بىلمەيدىغان ئاجىزلار بۇنىڭدىن مۇستەسنا');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,99,' ئەنە شۇلارنى اﷲ ئەپۇ قىلغاي اﷲ ناھايىتى ئەپۇ قىلغۇچىدۇر، ناھايىتى مەغپىرەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,100,' كىمكى اﷲ يولىدا ھىجرەت قىلىدىكەن، ئۇ زېمىندا چىقىش يولى ۋە كەڭچىلىك تاپىدۇ كىمكى ئۆيىدىن اﷲ نىڭ ۋە پەيغەمبەرنىڭ تەرىپىگە ھىجرەت قىلىپ چىقسا، ئاندىن يولدا ئۆلسە، ئۇنىڭ ئەجرىنى چوقۇم اﷲ بېرىدۇ اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر ۋە ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,101,' سەپەرگە چىققان ۋاقتىڭلاردا كاپىرلارنىڭ ئۆزۈڭلارغا زىيان ـ زەخمەت يەتكۈزۈپ قويۇشىدىن قورقۇپ، نامازنى قەسرى قىلىپ (يەنى تۆت رەكئەتنى ئىككى رەكئەت قىلىپ) ئوقۇساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ كاپىرلار سىلەرگە ھەقىقەتەن ئوپئوچۇق دۈشمەندۇر (ناماز ئوقۇۋاتقان پۇرسەتتىن پايدىلىنىپ سىلەرنى ئۆلتۈرۈۋېتىشتىن يانمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,102,' (ئى مۇھەممەد!) سەن مۆمىنلەر بىلەن (جىھادقا) بىللە بولۇپ ئۇلار بىلەن ناماز ئوقۇماقچى بولغىنىڭدا، ئۇلارنىڭ بىر پىرقىسى سەن بىلەن بىرلىكتە نامازغا تۇرسۇن، قوراللىرىنى (ئېھتىيات يۈزىسىدىن) يېنىدا تۇتسۇن، سەجدىگە بېرىپ بولغاندىن كېيىن ئۇلار ئارقاڭلارغا ئۆتۈپ تۇرسۇن (يەنى ناماز ئوقۇپ بولغان پىرقە ئارقاڭلاردا كۆزەتچىلىك قىلىپ تۇرسۇن، ئۇلارنىڭ ئورنىغا) ناماز ئوقۇمىغان ئىككىنچى بىر پىرقە كېلىپ سەن بىلەن بىرلىكتە (يەنى ئارقاڭدا) ناماز ئوقۇسۇن، (دۈشمەنلىرىدىن) ئېھتىياجلىق بىلەن ھوشيار تۇرسۇن (دۈشمەنلىرى بىلەن ئۇرۇشۇشقا تەييار تۇرۇش يۈزىسىدىن) قوراللىرىنى يېنىدا تۇتسۇن، كاپىرلار سىلەرنىڭ قوراللىرىڭلاردىن، ئەشيالىرىڭلاردىن غەپلەتتە قېلىشىڭلارنى، شۇنىڭ بىلەن سىلەرگە بىردىنلا (تۇيۇقسىزدىن) ھۇجۇم قىلىشنى ئارزۇ قىلىدۇ (يەنى دۈشمەنلەرنىڭ سىلەرگە ھۇجۇم قىلىش پۇرسىتىگە ئىگە بولالماسلىقى ئۈچۈن، ھەممەڭلار بىرلا ۋاقىتتا يىغىلىپ ناماز ئوقۇماي، يۇقىرىقى ئۇسۇل بويىچە ئوقۇڭلار) ئەگەر سىلەرگە يامغۇر سەۋەبلىك ھەرەج بولىدىغان بولسا ياكى كېسەل بولساڭلار، (بۇ چاغدا) قوراللىرىڭلارنى قويۇپ قويساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ، ئېھتىياتچان ۋە ھوشيار بولۇڭلار، اﷲ ھەقىقەتەن كاپىرلار ئۈچۈن خور قىلغۇچى ئازاب تەييارلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,103,' نامازدىن فارىغ بولغىنىڭلاردا ئۆرە تۇرغان ۋە ياتقان ھالەتلىرىڭلاردا اﷲ نى ياد ئېتىڭلار (اﷲ سىلەرگە دۈشمىنىڭلارغا قارشى ياردەم بېرىشى مۇمكىن) خاتىرجەم بولغان چېغىڭلاردا نامازنى (پۈتۈن شەرتلىرى بىلەن) مۇكەممەل ئادا قىلىڭلار شۈبھىسىزكى، ناماز مۆمىنلەرگە ۋاقتى بەلگىلەنگەن پەرز قىلىندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,104,' دۈشمەننىڭ ئارقىسىدىن قوغلاشقا سۇسلۇق قىلماڭلار؛ ئەگەر سىلەر قىينالساڭلار، ئۇلارمۇ خۇددى سىلەرگە ئوخشاش قىينىلىدۇ، سىلەر ئۇلار ئۈمىد قىلمايدىغان نەرسىنى (يەنى شېھىت بولۇشىنى، ساۋابنى ۋە غەلىبىنى) اﷲ تىن ئۈمىد قىلىسىلەر اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,105,' (ئى مۇھەممەد!) سېنى كىشىلەر ئارىسىدا اﷲ نىڭ كۆرسەتكىنى بويىچە ھۆكۈم قىلسۇن دەپ، ساڭا ھەقىقەتەن ھەق كىتابنى نازىل قىلدۇق خائىنلارنىڭ تەرىپىنى ئالمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,106,' اﷲ تىن مەغپىرەت تىلىگىن اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,107,' (گۇناھ قىلىش بىلەن) ئۆزلىرىگە خىيانەت قىلغۇچىلارنىڭ تەرىپىنى ئالمىغىن خىيانەتكە ئادەتلەنگەن، گۇناھقا چۆمگەن كىشىنى اﷲ ھەقىقەتەن دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,108,' ئۇلار (خىيانەتلىرىنى) ئىنسانلاردىن (خىجىل بولۇپ) يوشۇرىدۇ، اﷲ تىن (خىجىل بولۇپ) يوشۇرمايدۇ؛ ئۇلار اﷲ رازى بولمايدىغان سۆزنى (يەنى بوھتان چاپلاش، يالغان گۇۋاھلىق بېرىش ۋە يالغان قەسەمنى) پىلانلىغان چاغدا، اﷲ ئۇلار بىلەن بىللە ئىدى (يەنى ئۇلارنىڭ ئەھۋالىنى بىلىپ تۇراتتى، سۆزىنى ئاڭلاپ تۇراتتى) ئۇلارنىڭ قىلمىشلىرىنى اﷲ تامامەن بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,109,' سىلەر شۇنداق كىشىلەر سىلەركى، ھاياتىي دۇنيادا ئۇلارنىڭ (يەنى ئوغرى ۋە خائىنلارنىڭ) تەرىپىنى ئالدىڭلار، قىيامەت كۈنى كىم اﷲ قا قارشى ئۇلارنىڭ تەرىپىنى ئالىدۇ؟ (يەنى اﷲ نىڭ ئازابىنى كىم ئۇلاردىن دەپئى قىلىدۇ؟) ياكى كىم ئۇلارغا ھامىي بولىدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,110,' كىمكى بىرەر يامانلىق ياكى ئۆزىگە بىرەر زۇلۇم قىلىپ قويۇپ، ئاندىن اﷲ تىن مەغپىرەت تەلەپ قىلسا، ئۇ اﷲ نىڭ مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان ئىكەنلىكىنى كۆرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,111,' كىمكى (قەستەن) بىرەر گۇناھ قىلسا، ئۇنى ئۆزىنىڭ زىيىنىغا قىلىدۇ (يەنى ئۇنىڭ ۋابالىنى ئۆزى تارتىدۇ) اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,112,' كىمكى بىر كىچىك گۇناھ ياكى بىر چوڭ گۇناھ قىلىپ قويۇپ، ئۇنى بىگۇناھ ئادەمگە چاپلىسا، ئۇ شۇ بوھتاننى ۋە ئوپئوچۇق بىر گۇناھنى ئۆزىگە يۈكلىۋالغان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,113,' (ئى مۇھەممەد!) ئەگەر ساڭا اﷲ نىڭ پەزلى ۋە رەھمىتى بولمىسا ئىدى، ئۇلاردىن بىر گۇرۇھ ئادەم چوقۇم سېنى ئازدۇرۇشنى قەستلەيتتى؛ ئۇلار پەقەت ئۆزلىرىنى ئازدۇرىدۇ، ساڭا قىلچە زىيان يەتكۈزەلمەيدۇ اﷲ ساڭا كىتابنى (يەنى قۇرئاننى) ۋە ھېكمەتنى (يەنى سۈننەتنى) نازىل قىلدى، ساڭا سەن بىلمىگەن نەرسىلەرنى (يەنى شەرىئەت ئەھكاملىرىنى ۋە غەيب ئىشلارنى) بىلدۈردى اﷲ نىڭ ساڭا پەزلى چوڭدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,114,' ئۇلارنىڭ يوشۇرۇن سۆھبىتىىنىڭ تولىسىدا خەيرىيەت يوقتۇر پەقەت سەدىقىغە ياكى ياخشىلىققا ياكى كىشىلەرنى ئەپلەشتۈرۈشكە ئەمر قىلغان كىشىلەر (نىڭ يوشۇرۇن سۆھبىتى) بۇنىڭدىن مۇستەسنا كىمكى ئۇ (ئىش) لارنى اﷲ نىڭ رازىلىقىنى تىلەش يۈزىسىدىن قىلىدىكەن، ئۇنىڭغا بۈيۈك ئەجىر ئاتا قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,115,' كىمكى ئۆزىگە (مۆجىزىلەر ئارقىلىق) توغرا يول ئېنىق بولغاندىن كېيىن، پەيغەمبەرگە (يەنى ئۇنىڭ ئەمرىگە) مۇخالىپەتچىلىك قىلىدىكەن، مۆمىنلەرنىڭ يولىدىن غەيرىيگە ئەگىشىدىكەن، ئۇنى ئۆز يولىغا قويۇپ بېرىمىز، ئۇنى (ئاخىرەتتە) جەھەننەمگە كىرگۈزىمىز، جەھەننەم نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,116,' اﷲ ئۆزىگە بىرەر نەرسىنىڭ شېرىك كەلتۈرۈلۈشىنى (يەنى مۇشرىكلىك گۇناھىنى) ئەلۋەتتە مەغپىرەت قىلمايدۇ، بۇنىڭدىن باشقىنى (اﷲ ئۆزى) خالىغان ئادەمگە مەغپىرەت قىلىدۇ، كىمكى اﷲ قا شېرىك كەلتۈرىدىكەن، ئۇ ھەقىقەتەن قاتتىق ئازغان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,117,' ئۇلار (يەنى مۇشرىكلار) اﷲ نى قويۇپ، پەقەت (ئۆزلىرى) چىشى (ناملار بىلەن ئاتىۋالغان) بۇتلارغا ئىبادەت قىلىدۇ، پەقەت اﷲ نىڭ ئىبادىتىدىن باش تارتقان شەيتانغا ئىبادەت قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,118,' اﷲ شەيتاننى رەھمىتىدىن يىراق قىلدى، شەيتان ئېيتتى: «مەن ئەلۋەتتە سېنىڭ بەندىلىرىڭدىن مۇئەييەن ساندىكىلەرنى ئىگىلەيمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,119,' شۇنداقلا چوقۇم ئۇلارنى ئازدۇرىمەن، ئۇلارنى خام خىيال قىلدۇرىمەن (يەنى ھايات ئۇزۇن، قىيامەت، ھېساب ئېلىش يوق دېگەندەك باتىل ئەقىدىلەرنى كۆڭلىگە سالىمەن)، ئۇلارنى چوقۇم چاھار پايلارنىڭ (بۇتلارغا نەزەر قىلىپ بەلگە ئۈچۈن) قۇلاقلىرىنى يېرىشقا بۇيرۇيمەن، ئۇلارنى چوقۇم اﷲ نىڭ مەخلۇقاتىنى ئۆزگەرتىۋېتىشكە (يەنى قۇللارنى ئاختا قىلىش، ئۇلارغا مەڭ ئويدۇرۇشقا ئوخشاش) ئىشلارغا بۇيرۇيمەن» كىمكى اﷲ نى قويۇپ شەيتاننى دوست تۇتىدىكەن، ئۇ (دۇنيا ۋە ئاخىرەتتە) ئوپئوچۇق زىيان تارتقان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,120,' شەيتان ئۇلارغا (يالغان) ۋەدىلەرنى بېرىدۇ ۋە ئۇلارنى خام خىيالغا سالىدۇ (يەنى ئەمەلدە ئىشقا ئاشمايدىغان ئارزۇلارنى كۆڭلىگە سالىدۇ)، شەيتان ئۇلارغا پەقەت يالغاننىلا ۋەدە قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,121,' ئەنە شۇلارنىڭ بارىدىغان جايى جەھەننەمدۇر، ئۇلار جەھەننەمدىن قاچىدىغان جاي تاپالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,122,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتكە كىرگۈزىمىز ئۇلار جەننەتتە مەڭگۈ قالىدۇ اﷲ نىڭ ۋەدىسى ھەقتۇر، اﷲ تىنمۇ راست سۆزلۈك كىم بار؟ (يەنى اﷲ تىنمۇ راست سۆزلۈك ھېچ ئەھەدى بولمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,123,' (اﷲ ۋەدە قىلغان ساۋاب) سىلەرنىڭ قۇرۇق ئارزۇيۇڭلار ۋە ئەھلى كىتابنىڭ قۇرۇق ئارزۇسى بىلەن قولغا كەلمەيدۇ، كىمكى بىر يامانلىق قىلىدىكەن، بۇنىڭ ئۈچۈن (دۇنيادا ياكى ئاخىرەتتە) جازالىنىدۇ، ئۇ ئۆزى ئۈچۈن اﷲ تىن باشقا (اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچقانداق دوست ۋە ھېچقانداق مەدەتكار تاپالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,124,' ئەر ـ ئاياللاردىن مۆمىن بولۇپ تۇرۇپ ياخشى ئىشلارنى قىلغانلار جەننەتكە داخىل بولىدۇ، ئۇلارغا قىلچە زۇلۇم قىلىنمايدۇ (يەنى ئۇلارنىڭ قىلغان ئەمەللىرىنىڭ ساۋابى قىلچە كېمەيتىۋېتىلمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,125,' ياخشى ئەمەللەرنى قىلغان ھالدا ئۆزىنى اﷲ قا تاپشۇرغان (يەنى اﷲ نىڭ ئەمرىگە بويسۇنۇپ ئەمەلىنى خالىس اﷲ ئۈچۈن قىلغان)، باتىل دىنلاردىن بۇرالغان ھالدا ئىبىراھىمنىڭ دىنىغا ئەگەشكەن كىشىدىن (دىنىي جەھەتتە) ياخشىراق ئادەم بارمۇ؟ اﷲ ئىبىراھىمنى دوست تۇتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,126,' ئاسمانلاردىكى، زېمىندىكى شەيئىلەرىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، بەندىلىرىدۇر، مەخلۇقاتلىرىدۇر) اﷲ ھەممە نەرسىنى تولۇق بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,127,' (ئى مۇھەممەد!) سەندىن ئاياللار توغرىسىدا پەتىۋا سورايدۇ، ئېيتقىنكى، ئۇلار توغرىسىدا اﷲ سىلەرگە پەتىۋا بېرىدۇ قۇرئاندا سىلەرگە تىلاۋەت قىلىنىدىغان (ئاياللارنىڭ مىراسىغا ئائىت) ئايەتلەرمۇ پەتىۋا بېرىدۇ شۇنداق يېتىملەر توغرىسىدا پەتىۋا بېرىدۇكى، ئۇلارنىڭ تەيىن قىلىنغان مىراسىنى بەرمەيسىلەر(يەنى مۇنداق قىلماسلىقىڭلارغا پەتىۋا بېرىدۇ)، بوزەك قىلىنغان بالىلار(نىڭ ھەققىنى بېرىشىڭلار) ۋە يېتىملەرگە ھەققانىي بولۇشىڭلار ھەققىدە پەتىۋا بېرىدۇ  (ئاياللارغا ۋە يېتىملەرگە) قانداقلا ياخشىلىق قىلساڭلار، شۈبھىسىزكى، اﷲ ئۇنى بىلىپ تۇرىدۇ (يەنى شۇنىڭغا ئاساسەن اﷲ سىلەرنى مۇكاپاتلايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,128,' ئەگەر بىرەر ئايال ئېرىنىڭ كۆڭۈلسىز بولۇشىدىن ياكى يۈز ئۆرۈشىدىن ئەندىشە قىلسا، ئۇلارنىڭ ئۆزئارا كېلىشىشى ھېچ گۇناھ ئەمەس، كېلىشىش (ئۈزلۈشۈپ كېتىشتىن) ياخشى ئىنسانلارنىڭ تەبىئىتىگە بېخىللىق سىڭىپ كەتكەن، ئەگەر (ئاياللىرىڭلارغا) ياخشىلىق قىلساڭلار، (ئۇلارغا زۇلۇم قىلىشتىن) ساقلانساڭلار (اﷲ سىلەرنى مۇكاپاتلايدۇ) اﷲ ھەقىقەتەن قىلمىشىڭلاردىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,129,' قانچە تىرىشساڭلارمۇ ئاياللىرىڭلارغا (مۇھەببەتتە ۋە دىلنىڭ مايىللىقىدا) باراۋەر مۇئامىلىدە بولۇشقا ھەرگىز قادىر بولالمايسىلەر، شۇنىڭ ئۈچۈن بىرسىگە پۈتۈنلەي مايىل بولۇپ، ئىككىنچىسىنى (ئېرى باردەكمۇ ئەمەس، يوقتەكمۇ ئەمەس) ئېسىپ قويۇلغان ئايالدەك تاشلاپ قويماڭلار، ئەگەر (بۇ جەھەتتىكى قۇسۇرلىرىڭلارنى) تۈزەتسەڭلار، (ئۇلارغا زۇلۇم قىلىشتىن) ساقلانساڭلار، اﷲ ئەلۋەتتە (سىلەرگە) مەغپىرەت قىلىدۇ، رەھىم قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,130,' ئەگەر ئىككىسى ئۈزلۈشۈپ كەتسە، اﷲ ئۆز پەزلى بىلەن ئۇلارنىڭ بىرسىنى ئىككىنچىسىدىن بىھاجەت قىلىدۇ، اﷲ (نىڭ پەزلى) كەڭدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,131,' ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەر اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، مەخلۇقاتىدۇر) سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەرگە (يەنى يەھۇدىيلار ۋە ناسارالارغا) ۋە سىلەرگە اﷲ قا تەقۋادارلىق قىلىشنى تەۋسىيە قىلدۇق، ئەگەر كاپىر بولساڭلار (كۇفرىڭلار اﷲ قا زىيان يەتكۈزەلمەيدۇ)، ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەر اﷲ نىڭ (مۈلكىدۇر، مەخلۇقاتىدۇر) اﷲ (مەخلۇقاتتىن) بىھاجەتتۇر، مەدھىيىگە لايىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,132,' ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەر اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، مەخلۇقاتىدۇر) ھەممە ئىشنى باشقۇرۇشقا اﷲ يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,133,' ئى ئىنسانلار! ئەگەر اﷲ خالىسا، سىلەرنى يوق قىلىپ ئورنۇڭلارغا باشقىلارنى كەلتۈرىدۇ، اﷲ بۇنىڭغا قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,134,' كىمكى (قىلغان ئەمەلى بىلەن) دۇنيانىڭ نېمىتىنى تىلەيدىكەن، (ئۇنىڭغا ئېيتىپ قويغىنكى) دۇنيا ۋە ئاخىرەتنىڭ نېمىتى اﷲ نىڭ دەرگاھىدىدۇر (بەندە ئۆز پەرۋەردىگارىدىن دۇنيا ۋە ئاخىرەتنىڭ نېمىتىنى تىلىسۇن) اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (بەندىلىرىنىڭ ئەمەللىرىنى) كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,135,' ئى مۆمىنلەر! خۇدالىق ئۈچۈن گۇۋاھلىق بېرىشتە، ئۆزۈڭلارنىڭ ياكى ئاتا ـ ئاناڭلارنىڭ ياكى تۇغقانلىرىڭلارنىڭ زىيىنىغا (گۇۋاھلىق بېرىشكە) توغرا كەلگەن تەقدىردىمۇ، ئادالەتنى بەرپا قىلىشقا تىرىشىڭلار، (گۇۋاھلىق بېرىلگۈچى) باي بولسا (ئۇنىڭغا رىئايە قىلماستىن)، ياكى پېقىر بولسا (ئۇنىڭغا ئىچ ئاغرىتماستىن) ھامان ئادىل گۇۋاھ بولۇڭلار، اﷲ سىلەردىن ئۇلارغا يېقىندۇر (يەنى ئۇلارنىڭ مەنپەئىتىنىڭ نېمىدە بولىدىغانلىقىنى ئوبدان بىلىدۇ)، نەپسى خاھىشىڭلارغا ئەگىشىپ (ھەقىقەتتىن) بۇرۇلۇپ كەتمەڭلار ئەگەر تىلىڭلارنى تولغۇساڭلار (يەنى گۇۋاھلىقتىكى پاكىتنى بۇرمىلىساڭلار)، ياكى گۇۋاھلىقتىن باش تارتساڭلار، مۇنداقتا اﷲ ھەقىقەتەن سىلەرنىڭ قىلمىشىڭلاردىن خەۋەردار بولۇپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,136,' ئى مۆمىنلەر! اﷲ قا، اﷲ نىڭ پەيغەمبىرىگە ۋە اﷲ ئۇنىڭغا نازىل قىلغان كىتابقا (يەنى قۇرئانغا) ۋە ئىلگىرى اﷲ نازىل قىلغان كىتابلارغا (يەنى قۇرئاندىن ئىلگىرى نازىل قىلىنغان ساماۋى كىتابلارغا) ئىمان كەلتۈرۈڭلار كىمكى اﷲ نى، اﷲ نىڭ پەرىشتىلىرىنى، كىتابلىرىنى، پەيغەمبەرلىرىنى ۋە ئاخىرەت كۈنىنى ئىنكار قىلىدىكەن، ئۇ قاتتىق ئازغان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,137,' ئىمان ئېيتقان، ئاندىن كاپىر بولغان، ئاندىن ئىمان ئېيتقان، ئاندىن كاپىر بولغان، ئاندىن كۇفرىدا ئەزۋەيلىگەن (يەنى شۇ كۇفرى بىلەن ئۆلگەن) كىشىلەرنى اﷲ مەغپىرەت قىلمايدۇ ۋە ئۇلارنى توغرا يولغا (يەنى جەننەتكە) يېتەكلىمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,138,' (ئى مۇھەممەد!) مۇناپىقلارغا بېشارەت بەرگىنكى، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,139,' ئۇلار مۆمىنلەرنى قويۇپ، كاپىرلارنى دوست تۇتىدۇ، ئۇلار كاپىرلارنىڭ قېشىدىن ئىززەت ۋە قۇدرەت تەلەپ قىلامدۇ؟ (يەنى كۇففارلارنى دوستلۇقىدىن ئىززەت ۋە غەلىبە تىلەمدۇ؟) ئىززەت ۋە قۇدرەتنىڭ ھەممىسى اﷲ قا خاستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,140,' اﷲ كىتابتا (يەنى قۇرئاندا) سىلەرگە نازىل قىلدىكى، سىلەر اﷲ نىڭ ئايەتلىرىنىڭ ئىنكار قىلىنىۋاتقان ۋە مەسخىرە قىلىنىۋاتقانلىقىنى ئاڭلىغان چېغىڭلاردا، ئۇلار (يەنى اﷲ نىڭ ئايەتلىرىنى مەسخىرە قىلىۋاتقان كاپىرلار) باشقا پاراڭغا كىرىشمىگىچە ئۇلار بىلەن بىللە ئولتۇرماڭلار، بولمىسا (گۇناھتا) ئۇلارغا ئوخشاش بولۇپ قالىسىلەر، اﷲ ھەقىقەتەن مۇناپىقلارنىڭ ۋە كاپىرلارنىڭ ھەممىسىنى (ئاخىرەتتە) جەھەننەمگە توپلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,141,' ئۇلار (يەنى مۇناپىقلار) سىلەرگە ھادىسە كېلىشىنى كۈتىدۇ، ئەگەر سىلەر اﷲ نىڭ نۇسرىتىگە ئېرىشسەڭلار، ئۇلار: « بىز سىلەر بىلەن بىللە ئەمەسمىدۇق؟ (كاپىرلاردىن ئالغان غەنىيمەتلەردىن بىزگىمۇ بېرىڭلار)» دەيدۇ ئەگەر كاپىرلار زەپەر تاپسا، ئۇلار (كاپىرلارغا): «بىز سىلەردىن غالىب بولۇپ تۇرۇپ (سىلەرنى ئۆلتۈرمىدۇق) ئەمەسمۇ؟ سىلەرنى مۆمىنلەردىن ساقلىمىدىقمۇ؟» دەيدۇ اﷲ قىيامەت كۈنى ئاراڭلاردا (ھەققانىي) ھۆكۈم چىقىرىدۇ، اﷲ كاپىرلارغا ھەرگىز مۆمىنلەرگە قارشى يول بەرمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,142,' شۈبھىسىزكى، مۇناپىقلار اﷲ نى ئالدىماقچى بولۇشىدۇ، اﷲ ئۇلارنىڭ ئالدامچىلىقىغا يارىشا جازا بېرىدۇ، ئۇلار ناماز ئۈچۈن تۇرغاندا، خۇش ياقماسلىق بىلەن تۇرىدۇ (ساۋاب ئۈمىد قىلمايدۇ)، (نامازنى) كىشىلەرگە كۆرسىتىش ئۈچۈن ئوقۇيدۇ (يەنى رىياكارلىق قىلىدۇ)، اﷲ نى پەقەت ئازغىنا ياد ئېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,143,' مۇناپىقلار كۇفرى بىلەن ئىمان ئارىسىدا ئارىسالدى بولۇپ تۇرىدۇ، نە ئۇلارغا، نە بۇلارغا مەنسۈپ ئەمەس (يەنى مۆمىنلەرگىمۇ، كاپىرلارغىمۇ مەنسۈپ ئەمەس)، كىمنى اﷲ گۇمراھ قىلغان ئىكەن، ئۇنىڭغا ھەرگىزمۇ توغرا يول تاپالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,144,' ئى مۆمىنلەر! مۆمىنلەرنى (دوست تۇتۇشنى) قويۇپ، كاپىرلارنى دوست تۇتماڭلار، سىلەرنىڭ زىيىنىڭلارغا (يەنى مۇناپىق ئىكەنلىكىڭلارغا) اﷲ نىڭ روشەن پاكىتى بولۇشىنى خالامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,145,' مۇناپىقلار چوقۇم دوزاخنىڭ ئەڭ ئاستىنقى قەۋىتىگە (يەنى قەئرىگە) تاشلىنىدۇ، ئۇلارغا ھەرگىزمۇ (ئازابتىن قۇتقۇزىدىغان) مەدەتكار تاپالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,146,' پەقەت (مۇناپىقلىقتىن) تەۋبە قىلغان، (ئەمەللىرىنى) تۈزەتكەن، اﷲ (نىڭ كىتابى) غا چىڭ يېپىشقان، دىننى اﷲ ئۈچۈن خالىس قىلغان (يەنى قىلغان ئەمەلىدىن پەقەت اﷲ نىڭ رازىلىقىنى كۆزلىگەن) كىشىلەر بۇنىڭدىن مۇستەسنا ئەنە شۇلار مۆمىنلەر بىلەن بىللە (يەنى ئۇلارنىڭ قاتارىدا) دۇر اﷲ مۆمىنلەرگە (ئاخىرەتتە جەننەتتىن ئىبارەت) بۈيۈك ئەجىر ئاتا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,147,' ئەگەر (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلساڭلار ۋە ئىمان ئېيتساڭلار، اﷲ سىلەرگە ئازاب قىلىپ نېمە قىلىدۇ؟ اﷲ شۈكۈر قىلغۇچىنى مۇكاپاتلىغۇچىدۇر، ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,148,' زۇلۇمغا ئۇچرىغۇچىدىن باشقا (ھەر قانداق كىشىنىڭ) ئاشكارا يامان سۆز قىلىشىنى اﷲ ياقتۇرمايدۇ (زۇلۇمغا ئۇچرىغۇچىنىڭ زالىمدىن شىكايەت قىلىشىغا ۋە ئۇنىڭغا دۇئايىبەت قىلىشىغا بولىدۇ)، اﷲ (زۇلۇمغا ئۇچرىغۇچىنىڭ دۇئاسىنى) ئاڭلىغۇچىدۇر، (زالىمنى) بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,149,' سىلەر ئاشكارا ياكى يوشۇرۇن بىر ياخشىلىقنى قىلساڭلار ياكى بىرەر يامانلىقنى كەچۈرسەڭلار (ساۋاب تاپىسىلەر) شۈبھىسىزكى، اﷲ ئەپۇ قىلغۇچىدۇر، قۇدرەتلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,150,' شۈبھىسىزكى، اﷲ نى ۋە ئۇنىڭ پەيغەمبەرلىرىنى ئىنكار قىلغانلار، اﷲ بىلەن ئۇنىڭ پەيغەمبەرلىرىنىڭ ئارىسىنى (اﷲ قا ئىشىنىپ پەيغەمبەرلىرىگە ئىشەنمەيمىز دېيىش بىلەن) ئاجرىتىۋەتمەكچى بولغانلار، (پەيغەمبەرلەرنىڭ) بەزىسىگە ئىشىنىمىز، بەزىسىگە ئىشەنمەيمىز دېگۈچىلەر ـ ئۇنىڭ (يەنى ئىمان بىلەن كۇفرىنىڭ) ئارىسىدا (ئوتتۇرا) يول تۇتماقچى بولغانلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,151,' ئەنە شۇلار (ئىماننى دەۋا قىلغان تەقدىردىمۇ) راستىنلا كاپىرلاردۇر، كاپىرلارغا خورلىغۇچى ئازاب تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,152,' اﷲ قا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتقان، ئۇلاردىن ھېچبىرىنىڭ ئارىسىنى ئاجرىتىۋەتمىگەن (يەنى ھەممىسىگە ئىمان ئېيتقان) كىشىلەرگە اﷲ ئۇلارنىڭ (تېگىشلىك) ئەجىرلىرىنى ئاتا قىلىدۇ، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,153,' ئەھلى كىتاب (يەنى يەھۇدىيلار ۋە ناسارالار) سەندىن ئۆزلىرىگە ئاسماندىن بىر كىتاب چۈشۈرۈشنى سورايدۇ، ئۇلار مۇسادىن بۇنىڭدىنمۇ چوڭراقىنى سوراپ: «بىزگە اﷲ نى ئاپئاشكارا كۆرسەتكىن» دېگەن ئىدى شۇنىڭ بىلەن، ئۇلارنىڭ زۇلمى سەۋەبلىك ئۇلارنى چاقماق سوقتى (يەنى ئاسماندىن بىر ئوت كېلىپ ئۇلارنى ھالاك قىلدى) ئۇلار روشەن مۆجىزىلەر كەلگەندىن كېيىن موزاينى (مەبۇد) قىلىۋالدى كېيىن بىز ئۇلارنى ئەپۇ قىلدۇق بىز مۇساغا (ئۇنىڭ راست پەيغەمبەرلىكىنى ئىسپاتلايدىغان مۆجىزىلەردىن) روشەن پاكىت ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,154,' (تەۋرات شەرىئىتىنى قوبۇل قىلمىغانلىقلىرى سەۋەبلىك) ئۇلاردىن ئەھدە ئالماق ئۈچۈن ئۈستىگە تۇر (تېغى) نى تىكلەپ قويدۇق، ئۇلارغا: «(بەيتۇلمۇقەددەس) دەرۋازىسىدىن سەجدە قىلغان ھالدا كىرىڭلار» دېدۇق ھەمدە ئۇلارغا: «شەنبىنىڭ ھۆرمىتىنى ساقلاڭلار (يەنى شەنبىدە بېلىق تۇتۇش بىلەن ھەددىڭلاردىن ئاشماڭلار)» دېدۇق ئۇلاردىن (بۇ ھەقتە) مەھكەم ۋەدە ئالدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,155,' ئۇلار ئەھدىنى بۇزغانلىقلىرى، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى، پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى ۋە (مۇھەممەد ئەلەيھىسسالامغا) دىللىرىمىز پەردىلەنگەن (يەنى سۆزۈڭنى چۈشەنمەيدۇ) دېگەنلىكلىرى ئۈچۈن (ئۇلارغا لەنەت قىلدۇق)، بەلكى اﷲ ئۇلارنىڭ كۇفرى سەۋەبىدىن دىللىرىنى پېچەتلىۋەتتى، شۇڭا ئۇلارنىڭ ئازغىنىسىدىن باشقىنى ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,156,' يەنە ئۇلارنىڭ كۇفرى (يەنى ئىسا ئەلەيھىسسالامنى ئىنكار قىلغانلىقلىرى) ئۈچۈن ۋە مەريەمگە چوڭ بوھتان چاپلىغانلىقلىرى ئۈچۈن (ئۇلارغا لەنەت قىلدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,157,' يەنە اﷲ نىڭ رەسۇلى ئىسا ئىبن مەريەمنى ھەقىقەتەن ئۆلتۈردۇق دېگەنلىكلىرى ئۈچۈن (ئۇلارغا لەنەت قىلدۇق) ۋاھالەنكى، ئۇلارنىڭ ئىسانى ئۆلتۈرگىنىمۇ يوق، دارغا ئاسقىنىمۇ يوق ۋە لېكىن ئۇلارغا شۈبھە سېلىندى (ئىسانى ئاستۇق دەپ گۇمان قىلىپ، ئىسا ئەلەيھىسسالامغا ئوخشاپ قالغان باشقا بىرسىنى ئاستى)، ئىسا توغرىسىدا ئىختىلاپ قىلىشقانلار ھەقىقەتەن ئۇنىڭ ئۆلتۈرۈلگەنلىكى (مەسلىسى) دە شۈبھىدىدۇر ئۇلار بۇ (ئىشنىڭ ھەقىقىتى) نى بىلمەيدۇ، گۇمانغىلا ئاساسلىنىدۇ، ئۇلار ئىسانى جەزمەن ئۆلتۈرمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,158,' بەلكى اﷲ ئۇنى ئۆز تەرىپىگە كۆتۈردى (يەنى ئىسا ئەلەيھىسسالامنى اﷲ ئۇلارنىڭ شەررىدىن قۇتۇلدۇرۇپ تىرىك ھالدا ئاسمانغا ئېلىپ چىقىپ كەتتى) اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,159,' ئەھلى كىتابتىن (يەنى يەھۇدىيلار ۋە ناسارالاردىن) ھەر قاندىقى جان ئۈزۈش ئالدىدا ئۇنىڭغا (يەنى ئىسانىڭ ھەق پەيغەمبەرلىكى ۋە اﷲ نىڭ بەندىسى ئىكەنلىكىگە) ئىمان ئېيتىدۇ ئىسا قىيامەت كۈنى ئۇلار (يەھۇدىيلارنىڭ ئىسانى دارغا ئاستۇق دېگەن دەۋاسىنىڭ ۋە ناسارالارنىڭ ئىسا اﷲ نىڭ ئوغلى دېگەن دەۋاسىنىڭ يالغانلىقى) غا گۇۋاھ بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,160,' يەھۇدىيلارنىڭ قىلغان ھەقسىزلىقلىرى (يەنى زۇلمى ۋە قىلغان گۇناھلىرى)، نۇرغۇن كىشىلەرنى اﷲ نىڭ يولىدىن (يەنى اﷲ نىڭ دىنىغا كىرىشتىن) توسقانلىقلىرى، چەكلەنگەن جازانىنى ئالغانلىقلىرى ۋە كىشىلەرنىڭ پۇل ـ ماللىرىنى ناھەق يېگەنلىكلىرى ئۈچۈن، ئۇلارغا (ئىلگىرى) ھالال قىلىنغان پاكىز نەرسىلەرنى ھارام قىلدۇق، ئۇلارنىڭ ئىچىدىن كاپىر بولغانلارغا قاتتىق ئازاب ھازىرلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,161,' ئۇلارنىڭ ئىچىدىكى بىلىمى چوڭقۇر بولغانلارغا، ساڭا نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا)، سەندىن ئىلگىرى (ئۆتكەن پەيغەمبەرلەرگە) نازىل قىلىنغان كىتابلارغا ئىمان ئېيتىدىغان مۆمىنلەرگە، نامازنى ئۆتىگۈچىلەرگە، زاكات بەرگۈچىلەرگە، اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقۇچىلارغا (جەننەتتە مەڭگۈ قېلىشتىن ئىبارەت) كاتتا ئەجىر ئاتا قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,162,' (ئى مۇھەممەد!) بىز نۇھقا ۋە ئۇنىڭدىن كېيىنكى پەيغەمبەرلەرگە ۋەھيى قىلغاندەك، ھەقىقەتەن ساڭا ۋەھيى قىلدۇق ھەمدە ئىبىراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا، يەئقۇبنىڭ ئەۋلادلىرىغا، ئىساغا، ئەييۇبقا، يۇنۇسقا، ھارۇنغا، سۇلايمانغا ۋەھيى قىلدۇق، داۋۇدقا زەبۇرنى ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,163,' بىز نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارنىڭ ئارىسىدا ئىلگىرى (ئى مۇھەممەد!) ساڭا بايان قىلغانلىرىمىزمۇ بار، ساڭا بايان قىلمىغانلىرىمىزمۇ بار، اﷲ مۇساغا (بىۋاسىتە) سۆز قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,164,' ئەۋەتىلگەن پەيغەمبەرلەردىن كېيىن اﷲ نىڭ ئالدىدا كىشىلەر (يەنى پەيغەمبەر ئەۋەتىلگەن بولسا ئەلۋەتتە ئىمان ئېيتاتتىم ۋە ئىتائەت قىلاتتىم دېگۈچىلەر) گە باھانە بولۇپ قالماسلىقى ئۈچۈن، (ئىتائەت قىلغۇچىلارغا جەننەت بىلەن) خۇش خەۋەر بەرگۈچى، (ئاسىيلىق قىلغۇچىلارنى دوزاختىن) ئاگاھلاندۇرغۇچى پەيغەمبەرلەرنى ئەۋەتتۇق، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,165,' (يەھۇدىيلار ۋە ناسارالار سېنىڭ پەيغەمبەرلىكىڭگە گۇۋاھلىق بەرمىسە گۇناھى ئۆزىگە) لېكىن اﷲ ساڭا نازىل قىلغان قۇرئان ئارقىلىق (پەيغەمبەرلىكىڭگە) گۇۋاھلىق بېرىدۇ، اﷲ قۇرئاننى (باشقىلار بىلمەيدىغان ئۆزىگە خاس) ئىلمى بىلەن نازىل قىلدى پەرىشتىلەرمۇ (اﷲ ساڭا نازىل قىلغان قۇرئانغا ۋە پەيغەمبەرلىكىڭگە) گۇۋاھلىق بېرىدۇ، (باشقىلار گۇۋاھلىق بەرمىگەندىمۇ) اﷲ نىڭ گۇۋاھلىقى يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,166,' شۈبھىسىزكى، كاپىر بولغانلار ۋە (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلار ھەقىقەتەن قاتتىق ئازدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,167,' شۈبھىسىزكى، كاپىر بولغانلار ۋە زۇلۇم قىلغانلارنى اﷲ مەغپىرەت قىلمايدۇ، توغرا يولغىمۇ (يەنى جەننەتنىڭ يولىغىمۇ) باشلىمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,168,' (ئۇلارنى) پەقەت جەھەننەمنىڭ يولىغا باشلايدۇ، ئۇلار جەھەننەمدە مەڭگۈ قالىدۇ، بۇ (يەنى ئۇلارنى جەھەننەمدە مەڭگۈ قالدۇرۇش) اﷲ قا ئاسان');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,169,' ئى ئىنسانلار! پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) سىلەرگە پەرۋەردىگارىڭلاردىن ھەق (دىن) ئېلىپ كەلدى، ئىمان ئېيتىڭلاركى، (بۇ) سىلەر ئۈچۈن پايدىلىق، ئەگەر ئىنكار قىلساڭلار (يەنى كۇفرىنى داۋاملاشتۇرساڭلار) (اﷲ سىلەردىن بىھاجەت) چۈنكى ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مەخلۇقاتىدۇر، مۈلكىدۇر) اﷲ (بەندىلىرىنىڭ ئەھۋالىنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,170,' ئى ئەھلى كىتاب! (يەنى ناسارالار جامائەسى) دىنىڭلاردا ھەددىدىن ئاشماڭلار، اﷲ نىڭ شەنىگە ھەق (سۆز) دىن باشقىنى ئېيتماڭلار (يەنى اﷲ قا شېرىكى ۋە بالىسى بولۇشتىن پاك دەپ ئېتىقاد قىلىڭلار) مەسىھ ئىسا ـ مەريەمنىڭ ئوغلى، پەقەت اﷲ نىڭ رەسۇلىدۇر (سىلەر گۇمان قىلغاندەك اﷲ نىڭ ئوغلى ئەمەستۇر)، مەريەمگە اﷲ نىڭ ئىلقا قىلغان كەلىمىسىدۇر (يەنى ئاتىنىڭ ۋاسىتىسىز، اﷲ نىڭ «ۋۇجۇدقا كەل» دېگەن سۆزىدىن يارىتىلغاندۇر)، اﷲ تەرىپىدىن كەلگەن بىر روھتۇر، اﷲ قا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتىڭلار، (اﷲ ، ئىسا، مەريەمدىن ئىبارەت) ئۈچتۇر دېمەڭلار، (اﷲ ئۈچ دەيدىغان ئېتىقادتىن) قايتىڭلار، (بۇ) سىلەرگە پايدىلىقتۇر، اﷲ پەقەت بىر ئىلاھتۇر، بالىسى بولۇشتىن اﷲ پاكتۇر، ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، مەخلۇقاتىدۇر)، اﷲ يېتەرلىك ھامىيدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,171,' مەسىھمۇ، (اﷲ قا) يېقىن پەرىشتىلەرمۇ اﷲ قا بەندە بولۇشتىن ھەرگىز باش تارتمايدۇ، كىملەركى اﷲ قا بەندىچىلىك قىلىشتىن باش تارتىدىكەن ۋە كۆرەڭلەپ كېتىدىكەن، (بىلسۇنكى) اﷲ ئۇلارنىڭ ھەممىسىنى (قىيامەت كۈنى ھېساب ئېلىش ۋە جازالاش ئۈچۈن) ئۆز دەرگاھىغا توپلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,172,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا كەلسەك، اﷲ ئۇلارنىڭ (ئەمەللىرىنىڭ) ئەجرىنى تولۇق بېرىدۇ، نېمىتىنى ئۇلارغا بېرىدۇ، (اﷲ قا قۇلچىلىق قىلىشتىن) باش تارتقان ۋە كۆرەڭلەپ كەتكەنلەرگە كەلسەك، اﷲ ئۇلارغا قاتتىق ئازاب قىلىدۇ ئۇلار ئۆزلىرى ئۈچۈن اﷲ تىن باشقا (اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچقانداق دوست ۋە ھېچقانداق مەدەتكار تاپالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,173,' ئى ئىنسانلار! سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن (شانلىق مۆجىزىلەر بىلەن كۈچلەندۈرۈلگەن مۇھەممەد ئەلەيھىسسالامدىن ئىبارەت) ئوچۇق دەلىل كەلدى، سىلەرگە روشەن نۇرنى (يەنى قۇرئاننى) نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,174,' اﷲ قا ئىمان ئېيتقان ۋە ئۇنىڭغا (يەنى قۇرئانغا) چىڭ يېپىشقانلارغا كەلسەك، اﷲ ئۇلارنى ئۆز رەھمىتىگە ۋە نېمىتىگە (يەنى جەننىتىگە) داخىل قىلىدۇ ۋە ئۇلارنى توغرا يولغا باشلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,4,175,' (ئى مۇھەممەد!) سەندىن (ئاتا ـ ئانىسى، بالىسى يوق مېيىتنىڭ مىراسى توغرۇلۇق) پەتىۋا سورىشىدۇ، ئېيتقىنكى، «اﷲ ئاتا ـ ئانىسى، بالىسى يوق كىشى (قالدۇرغان مىراس) توغرىسىدا پەتىۋا بېرىدۇ، ئەگەر بىر كىشى ۋاپات بولسا، بالىسى بولمىسا، پەقەت بىر ھەمشىرىسى بولسا، ئۇ تەرەكىنىڭ يېرىمىنى ئالىدۇ، ئەگەر ئۇ ئايالنىڭ بالىسى بولمىسا، ئەر بىر تۇغقىنى ئۇنىڭ ۋارىسى بولىدۇ (يەنى مىراسنىڭ ھەممىسىنى ئالىدۇ)، ئەگەر ئىككى ھەمشىرىسى بولسا، ئۇلار تەرەكىنىڭ ئۈچتىن ئىككى ھەسسىسىنى ئالىدۇ، ئەگەر ۋارىسلار ھەم ئەر، ھەم ئايال بىر تۇغقانلار بولسا (ئۇلارنىڭ ئىچىدىكى) بىر ئەرگە ئىككى ئايالنىڭ ھەسسىسى بېرىلىدۇ، ئاداشماسلىقىڭلار ئۈچۈن اﷲ سىلەرگە (شەرىئەت ئەھكاملىرىنى) بايان قىلىپ بېرىدۇ اﷲ ھەممە نەرسىنى بىلگۈچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,1,'ئى مۆمىنلەر! ئەھدىلەرگە (يەنى اﷲ بىلەن سىلەرنىڭ ئاراڭلاردىكى ۋە سىلەرنىڭ ئۆز ئاراڭلاردىكى ئەھدىلەرگە) ۋاپا قىلىڭلار سىلەرگە (بۇ سۈرىدە تۆۋەندە) ئوقۇپ بېرىلىدىغانلاردىن باشقا، ھايۋانلار، (يەنى تۆگە، كالا، قويلار) نىڭ ھەممىسىنى (زەبھى قىلىنغاندىن كېيىن يېيىش) ھالال قىلىندى لېكىن سىلەر ئېھرام (ياكى ھەرەم) دە بولغىنىڭلاردا شىكارنى ھالال سانىماڭلار، شۈبھىسىزكى، اﷲ (ھالال ـ ھارام توغرۇلۇق) خالىغان نەرسىنى ھۆكۈم قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,2,' ئى مۆمىنلەر! اﷲ نىڭ دىنىنىڭ ئالامەتلىرىنى (يەنى ھەجدە تائەتنىڭ بەلگىسى ھېسابلانغان ئىشلارنى) (تەرك ئېتىش بىلەن) شەھرى ھەرەم (دا ئۇرۇش قىلىش) نى، ھەدىيىنى (يەنى كەبىگە ئاتالغان قۇربانلىققا دەخلى ـ تەرۇز قىلىشنى)، قەلادە (يەنى ھەجدە قۇربانلىق قىلىنىدىغان مال ئىكەنلىكىنى بىلدۈرۈش ئۈچۈن ھايۋانغا ئېسىپ قويۇلغان نەرسە) لەرگە (چېقىلىشنى)، پەرۋەردىگارىنىڭ پەزلىنى ۋە رازىلىقىنى تىلەپ بەيتۇللاھقا (ھەج ئۈچۈن ياكى ئۆمرە ئۈچۈن) كەلگۈچىلەرگە (چېقىلىشنى) ھالال سانىماڭلار، ئىھرامدىن چىققان چېغىڭلاردا شىكار قىلساڭلار بولىدۇ، بىرەر قەۋم (يەنى مۇشرىكلار) نىڭ سىلەرنى مەسجىدى ھەرەم (زىيارىتى) دىن توسقانلىقتىن ئىبارەت دۈشمەنلىكى ھەرگىزمۇ سىلەرنىڭ ئۇلارغا چېقىلىشىڭلارغا سەۋەبچى بولمىسۇن، ياخشى ئىشقا ۋە تەقۋادارلىققا ياردەملىشىڭلار، گۇناھقا ۋە زۇلۇمغا ياردەملەشمەڭلار، اﷲ (نىڭ ئازابى) دىن قورقۇڭلار، اﷲ نىڭ ئازابى ھەقىقەتەن قاتتىق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,3,' سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋان، قان، چوشقا گۆشى، اﷲ تىن غەيرىينىڭ (يەنى بۇتلارنىڭ) نامى تىلغا ئېلىنىپ بوغۇزلانغان ھايۋان، بوغۇپ ئۆلتۈرۈلگەن ھايۋان، ئۇرۇپ ئۆلتۈرۈلگەن ھايۋان، (ئېگىزدىن) يىقىلىپ ئۆلگەن ھايۋان، (ھايۋانلار تەرىپىدىن) ئۈسۈپ ئۆلتۈرۈلگەن ھايۋان، يىرتقۇچ ھايۋانلار يېرىپ ئۆلتۈرۈپ يېگەن ھايۋان (نىڭ گۆشىنى يېيىش) ھارام قىلىندى لېكىن (يۇقىرىقى بەش تۈرلۈك ھايۋاندىن جېنى چىقمىغان چاغدا) بوغۇزلىغانلىرىڭلار ھالال بولىدۇ ھەمدە بۇتلارغا ئېلىپ بېرىلىپ ئۇنىڭ يېنىدا بوغۇزلانغان ھايۋانلار ھارام قىلىندى ئەزلام (يەنى جاھىلىيەت دەۋرىدە پال ئۈچۈن ئىشلىتىدىغان ئۈچ پارچە ياغاچ) بىلەن پال سېلىشىڭلار ھارام قىلىندى، بۇ گۇناھتۇر، كاپىرلار بۈگۈن سىلەرنىڭ دىنىڭلار (نى يوقىتىش) دىن ئۈمىدىنى ئۈزدى، ئۇلاردىن قورقماڭلار، مەندىن قورقۇڭلار بۈگۈن سىلەرنىڭ دىنىڭلارنى پۈتۈن قىلدىم، سىلەرگە نېمىتىمنى تاماملىدىم، ئىسلام دىنىنى سىلەرنىڭ دىنىڭلار بولۇشقا تاللىدىم، كىمكى ئاچلىقتا ئىلاجىسىزلىقتىن، گۇناھنى مەقسەت قىلماستىن (ھارام قىلىنغان نەرسىلەرنى يېسە گۇناھ بولمايدۇ)، اﷲ مەغپىرەت قىلغۇچىدۇر، بەندىلىرىگە ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,4,' (ئى مۇھەممەد!) ئۇلار سەندىن ئۆزلىرىگە (يېمەك ـ ئىچمەكتىن) نېمىلەرنىڭ ھالال قىلىنغانلىقىنى سورايدۇ، ئېيتقىنكى، «سىلەرگە پاك نەرسىلەر ۋە (ئىت، قارچىغا، قۇشقا ئوخشاش) شىكارغا ئۆگىتىلگەن، سىلەر اﷲ نىڭ ئۆزۈڭلارغا ئۆگەتكىنى بويىچە ئۆگەتكەن ئوۋچى جانىۋارلارنىڭ شىكار قىلغان نەرسىلىرى ھالال قىلىندى، ئۇلارنىڭ سىلەر ئۈچۈن ئوۋلىغان نەرسىلىرىدىن يەڭلار (ئوۋچى جانىۋارلارنى شىكارغا قويۇپ بەرگەن چېغىڭلاردا) اﷲ نىڭ نامىنى ياد قىلىڭلار (يەنى بىسمىللا دەڭلار)، اﷲ (نىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىش) تىن ساقلىنىڭلار، اﷲ ھەقىقەتەن تېز ھېساب ئالغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,5,' بۈگۈن سىلەرگە پاك نەرسىلەر ھالال قىلىندى، كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) نىڭ تامىقى سىلەرگە ھالالدۇر ۋە سىلەرنىڭ تامىقىڭلار ئۇلارغا ھالالدۇر، مۆمىنلەردىن بولغان ئەفىفە ئاياللارنىڭ، سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دىن بولغان ئەفىفە ئاياللارنىڭ مەھرىنى بەرسەڭلار، ئىپپەتلىك بولغىنىڭلار ۋە ئاشكارا ـ يوشۇرۇن پاھىشىنى كۆزلىمىگىنىڭلار ھالدا (ئۇلارنى ئالساڭلار)، ئۇلار سىلەرگە ھالالدۇر، كىمكى ئىماندىن يېنىۋالىدىكەن (ياكى شەرىئەت ئەھكاملىرىنى ئىنكار قىلىدىكەن)، ئۇنىڭ ئەمەلى (نىڭ ساۋابى) يوققا چىقىرىلىدۇ، ئاخىرەتتە ئۇ زىيان تارتقۇچىلاردىن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,6,' ئى مۆمىنلەر! (سىلەر تاھارەتسىز بولۇپ) ناماز (ئوقۇماقچى بولۇپ) تۇرغىنىڭلاردا، يۈزۈڭلارنى يۇيۇڭلار، قولۇڭلارنى جەينىكىڭلار بىلەن قوشۇپ يۇيۇڭلار، بېشىڭلارغا مەسھى قىلىڭلار، پۇتۇڭلارنى ئوشۇقۇڭلار بىلەن قوشۇپ يۇيۇڭلار، ئەگەر جۇنۇپ بولساڭلار پاكلىنىڭلار (يەنى غۇسلى قىلىڭلار)، ئەگەر كېسەل بولساڭلار (سۇ زىيان قىلىدىغان بولسا)، ياكى سەپەر ئۈستىدە بولۇپ (سۇ تاپالمىساڭلار)، ياكى سىلەرنىڭ بىرسىڭلار ھاجەتخانىدىن كەلگەن (يەنى تاھارەت سۇندۇرغان) بولساڭلار، ياكى ئاياللىرىڭلار بىلەن يېقىنچىلىق قىلغاندىن كېيىن (غۇسلى تاھارەت ئۈچۈن) سۇ تاپالمىساڭلار، پاك تۇپراقتا تەيەممۇم قىلىڭلار، ئۇنىڭ بىلەن يۈزۈڭلارغا، قولۇڭلارغا مەسھى قىلىڭلار، اﷲ سىلەرگە سىلەرگە مۇشەققەتنى خالىمايدۇ، لېكىن اﷲ شۈكۈر قىلىشىڭلار ئۈچۈن سىلەرنى پاك قىلىشنى، (ئىسلام شەرىئىتىنى بايان قىلىش بىلەن) نېمىتىنى سىلەرگە تاماملاشنى خالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,7,' (ئى مۆمىنلەر!) اﷲ نىڭ سىلەرگە بەرگەن (ئۇلۇغ) نېمىتىنى ۋە سىلەر بىلەن قىلغان مۇئاھىدىسىنى ئەسلەڭلار، ئەينى زاماندا سىلەر: «ئاڭلىدۇق ۋە ئىتائەت قىلدۇق» دېگەن ئىدىڭلار، اﷲ تىن قورقۇڭلار (يەنى اﷲ قا بەرگەن ئەھدىنى بۇزۇشتىن ساقلىنىڭلار) اﷲ ھەقىقەتەن (بەندىلىرىنىڭ) دىللىرىدىكى سىرلىرىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,8,' ئى مۆمىنلەر! اﷲ نىڭ (ھەقلىرىنى) ئادا قىلىشقا تىرىشىڭلار، ئادىللىق بىلەن گۇۋاھلىق بېرىڭلار، بىرەر قەۋمگە بولغان ئۆچمەنلىكىڭلار (ئۇلارغا) ئادىل بولماسلىقىڭلارغا سەۋەب بولمىسۇن، (دۈشمىنىڭلارغا) ئادىل بولۇڭلار، بۇ (يەنى ئۇلارغا ئۆچمەن تۇرۇپ ئادىل بولۇشۇڭلار) تەقۋادارلىققا ئەڭ يېقىندۇر، اﷲ تىن قورقۇڭلار، اﷲ ھەقىقەتەن قىلمىشىڭلاردىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,9,' اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا مەغپىرەت ۋە كاتتا ساۋاب ۋەدە قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,10,' كاپىر بولغان ۋە بىزنىڭ ئايەتلىرىمىزنى يالغانغا چىقارغان كىشىلەر ئەھلى دوزاختۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,11,' ئى مۆمىنلەر! اﷲ نىڭ سىلەرگە بەرگەن (يەنى سىلەرنى دۈشمىنىڭلاردىن ساقلىغانلىق) نېمىتىنى ئەسلەڭلار، ئەينى زاماندا بىر جامائە سىلەرگە قوللىرىنى ئۇزاتماقچى (يەنى سىلەرنى ئۆلتۈرمەكچى) بولغان ئىدى، اﷲ ئۇلارنىڭ قوللىرىنى سىلەردىن توستى (يەنى سىلەرنىڭ ئۇلارنى زىيانكەشلىك قىلىشىدىن ساقلىدى) اﷲ تىن (اﷲ نىڭ ئەمرىنى ئورۇنلاش، مەنئى قىلغان ئىشلىرىدىن چەكلىنىش بىلەن) قورقۇڭلار، مۆمىنلەر اﷲ قا تەۋەككۈل قىلسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,12,' شۈبھىسىزكى، اﷲ ئىسرائىل ئەۋلادىدىن مەھكەم ئەھدە ئالدى، ئۇلارنىڭ ئارىسىدىن (يەنى ئون ئىككى ئايماقنىڭ ھەر بىرسىدىن بىردىن) ئون ئىككى باشلىقنى (كېپىللىك ئۈچۈن جەببارلارغا يەنى ئەمالىقەلەرگە) ئەۋەتتۇق ھەمدە اﷲ : «مەن سىلەرگە ياردەم بېرىمەن، (ئى ئىسرائىل ئەۋلادى! اﷲ بىلەن قەسەمكى) ئەگەر سىلەر نامازنى (تەئدىل ئەركان بىلەن) ئۆتىسەڭلار، زاكات بەرسەڭلار، پەيغەمبەرلىرىمگە ئىمان ئېيتساڭلار ۋە ئۇلارغا ياردەم بەرسەڭلار، اﷲ قا قەرزى ھەسەنە بەرسەڭلار (يەنى اﷲ نىڭ يولىدا پۇل ـ مېلىڭلارنى سەرپ قىلساڭلار)، ئەلۋەتتە سىلەرنىڭ گۇناھىڭلارنى يوققا چىقىرىمەن، سىلەرنى ئەلۋەتتە ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمەن شۇنىڭدىن (يەنى مەھكەم ئەھدىدىن) كېيىن سىلەردىن كىمكى (ئەمر قىلغان نەرسىلىرىمنى) ئىنكار قىلىدىكەن، ئۇ ئەلۋەتتە توغرا يولدىن ئازغان بولىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,13,' ئەھدىنى بۇزغانلىقلىرى ئۈچۈن ئۇلارنى رەھمىتىمىزدىن يىراق قىلدۇق، ئۇلارنىڭ دىللىرىنى (ئىماننى قوبۇل قىلىشقا يۇمشىمايدىغان دەرىجىدە) قاتتىق قىلدۇق، ئۇلار (تەۋراتنىڭ) سۆزلىرىنى ئۆزگەرتىۋېتىدۇ، ئۇلار (تەۋراتتا) ئۆزلىرى ئۈچۈن قىلىنغان نەسىھەتنىڭ بىر قىسمىنى ئۇنتۇدى (ئى مۇھەممەد!) ئۇلارنىڭ ئازغىنىسىدىن باشقىسىنىڭ خىيانەت قىلغانلىقىنى بايقاپ تۇرىسەن، (ئەگەر تەۋبە قىلسا) ئۇلارنى ئەپۇ قىلغىن، (ئەگەر ئىمان ئېيتسا) ئۇلارنى كەچۈرگىن، اﷲ ھەقىقەتەن ياخشىلىق قىلغۇچىلارنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,14,' بىز ناسارامىز دېگەنلەردىنمۇ (اﷲ نى بىر دەپ تونۇشقا ۋە مۇھەممەد ئەلەيھىسسالامغا ئىمان ئىېيتىشقا) مەھكەم ئەھدە ئالدۇق، ئۇلار (ئىنجىلدا) ئۆزلىرىگە قىلىنغان نەسىھەتنىڭ بىر قىسمىنى ئۇنتۇدى، (بۇنىڭ جازاسى ئۈچۈن) ئۇلارنىڭ ئارىسىغا قىيامەتكىچە ئاداۋەت ۋە دۈشمەنلىك سالدۇق، (شۇ چاغدا) اﷲ ئۇلارغا قىلمىشلىرىنى ئېيتىپ بېرىدۇ (يەنى ئۇلار ئۆزلىرىنىڭ قەبىھ ئەمەللىرىنىڭ جازاسىنى تارتىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,15,' ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) سىلەرگە سىلەر كىتابتا (ئىنجىلدا ۋە تەۋراتتا) يوشۇرغان نۇرغۇن نەرسىلەرنى بايان قىلىپ بېرىدىغان ۋە (يوشۇرغان نەرسىلەردىن) نۇرغۇنىنى كەچۈرىدىغان (ئاشكارىلاپ سىلەرنى پاش قىلمايدىغان) رەسۇلىمىز كەلدى، سىلەرگە اﷲ تەرىپىدىن نۇر (يەنى مۇھەممەد ئەلەيھىسسالام) ۋە روشەن كىتاب (يەنى قۇرئان) كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,16,' اﷲ شۇ كىتاب (يەنى قۇرئان) ئارقىلىق رازىلىقىنى تىلىگەنلەرنى سالامەتلىك يوللىرى (اﷲ نىڭ شەرىئىتى ۋە ئۇنىڭ ئەھكاملىرى) غا يېتەكلەيدۇ، ئىرادىسى بويىچە ئۇلارنى (كۇفرى) قاراڭغۇلۇقىدىن (ئىماننىڭ) نۇرىغا چىقىرىدۇ ۋە ئۇلارنى توغرا يولغا ھىدايەت قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,17,' مەريەم ئوغلى مەسىھ (يەنى ئىسا) اﷲ دۇر دېگەن كىشىلەر (يەنى ناسارالارنىڭ بىر پىرقىسى) ھەقىقەتەن كاپىر بولدى (ئى مۇھەممەد!) ئېيتقىنكى، «ئەگەر اﷲ مەريەم ئوغلى مەسىھنى، مەسىھنىڭ ئانىسىنى (يەنى مەريەمنى) ۋە يەر يۈزىدىكى بارلىق كىشىلەرنى ھالاك قىلىشنى ئىرادە قىلسا، اﷲ (نىڭ ئىرادىسى) دىن بىرەر نەرسىنى توسۇشقا كىم قادىر بولالايدۇ؟» ئاسمانلارنىڭ ۋە زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ خالىغىنىنى خەلق ئېتىدۇ، اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,18,' يەھۇدىيلار ۋە ناسارالار: «بىز اﷲ نىڭ ئوغۇللىرىدۇرمىز (يەنى ئاتا ـ بالىدەك يېقىنمىز) ۋە (بىز اﷲ نىڭ دىنىدا بولغاچقا) دوستلىرىدۇرمىز» دېدى ئېيتقىنكى، «(ئۇنداق بولسا) اﷲ نېمە ئۈچۈن سىلەرگە گۇناھىڭلار تۈپەيلىدىن ئازاب قىلىدۇ؟» بەلكى سىلەر باشقا كىشىلەرگە ئوخشاش اﷲ ياراتقان ئىنسانسىلەر، اﷲ خالىغان ئادەمگە مەغپىرەت قىلىدۇ؛ خالىغان ئادەمگە ئازاب قىلىدۇ ئاسمانلارنىڭ، يەر يۈزىنىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقى اﷲ قا خاستۇر، ئەڭ ئاخىر بارىدىغان جاي اﷲ نىڭ دەرگاھىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,19,' ئى ئەھلى كىتاب! (يەنى يەھۇدىي ۋە ناسارالار) بىزگە (ساۋاب بىلەن) خۇش خەۋەر بەرگۈچى، (ئازابتىن) ئاگاھلاندۇرغۇچى كەلمىدى دېمەسلىكىڭلار ئۈچۈن، پەيغەمبەرلەر ئۈزۈلۈپ قالغان بىر زاماندا سىلەرگە (شەرىئەتنى ۋە ئۇنىڭ ئەھكاملىرىنى) بايان قىلىپ بېرىدىغان رەسۇلىمىز كەلدى، ھەقىقەتەن سىلەرگە خۇش خەۋەر بەرگۈچى ۋە ئاگاھلاندۇرغۇچى (يەنى مۇھەممەد ئەلەيھىسسالام) كەلدى (ئەمدى سىلەرگە ئۆزرە قالمىدى) اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,20,' ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمگە ئېيتتى: «ئى قەۋمىم! اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ياد قىلىڭلار، اﷲ ئۆز ۋاقتىدا ئىچىڭلاردا (سىلەرنى توغرا دىنغا يېتەكلەيدىغان) نۇرغۇن پەيغەمبەرلەرنى بارلىققا كەلتۈردى سىلەردىن پادىشاھلار قىلدى (يەنى ئۆزۈڭلارنى ئۆزۈڭلارغا خوجا قىلدى) سىلەرگە جاھان ئەھلىدىن ھېچ كىشىگە بەرمىگەن (دەريانى يېرىپ ئوتتۇرىسىدىن يول ئېچىپ بېرىش، بۇلۇتلارنى سايىۋەن قىلىپ بېرىش، ئاسماندىن يېمەكلىك چۈشۈرۈپ بېرىش قاتارلىق) نېمەتلەرنى بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,21,' ئى قەۋمىم! اﷲ سىلەرگە تەقدىر قىلغان (يەنى سىلەرنى كىرىشكە ئەمر قىلغان) مۇقەددەس زېمىنغا (يەنى بەيتۇلمۇقەددەسكە) كىرىڭلار، ئارقىڭلارغا چېكىنمەڭلار، بولمىسا (ئىككىلا دۇنيادا) زىيان تارتقۇچىلاردىن بولۇپ قالىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,22,' ئۇلار: «ئى مۇسا! مۇقەددەس زېمىندا كۈچلۈك (يەنى جاسامەتلىك، قامەتلىك) بىر قەۋم بار (ئۇلار ئاد قەۋمىنىڭ قالدۇقى بولغان ئەمالىقەلەر)، ئۇلار چىقىپ كەتمىگىچە بىز ئۇ يەرگە ھەرگىز كىرمەيمىز، ئۇلار ئۇيەردىن چىقىپ كەتسە چوقۇم كىرىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,23,' اﷲ تىن قورققۇچىلاردىن بولغان، اﷲ نىڭ (ئىمان تائەتتىن ئىبارەت) نېمىتىگە ئېرىشكەن ئىككى ئادەم (يەنى كالب ئىبن يەفۇننە بىلەن يۇشە ئىبن نۇن) ئۇلارغا: «(شەھەرنىڭ) دەرۋازىسىدىن ھۇجۇم قىلىپ كىرىڭلار، (شەھەرنىڭ) دەرۋازىسىدىن ھۇجۇم قىلىپ كىرسەڭلار، چوقۇم غەلىبە قىلىسىلەر، ئەگەر (اﷲ نىڭ ۋەدە قىلغان ياردىمىگە) ئىشەنسەڭلار، اﷲ قا تەۋەككۈل قىلىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,24,' ئۇلار: « ئى مۇسا! مادامىكى، ئۇلار مۇقەددەس يەردە ئىكەن، بىز ھەرگىز ئۇ يەرگە كىرمەيمىز، سەن پەرۋەردىگارىڭ بىلەن بىللە بېرىپ ئىككىڭلار ئۇرۇشۇڭلار، بىز بۇ يەردە ئولتۇرۇپ تۇرايلى» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,25,' مۇسا دېدى: «پەرۋەردىگارىم! مەن پەقەت ئۆزۈمگە ۋە قېرىنداشلىرىمغىلا ئىگىمەن، سەن بىز بىلەن (ئىتائىتىڭدىن باش تارتقان) قەۋمنىڭ ئارىسىنى ئايرىۋەتكىن’');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,26,' اﷲ دېدى: « ئۇلارنىڭ مۇقەددەس يەرگە كىرىشى  يىلغىچە ھارام قىلىندى، (بۇ جەرياندا) ئۇلار زېمىندا ئادىشىپ يۈرىدۇ  پاسىق قەۋم ئۈچۈن قايغۇرمىغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,27,' (ئى مۇھەممەد!) ئۇلارغا (يەنى ئەھلى كىتابقا ۋە قەۋمڭگە) ئادەمنىڭ ئىككى ئوغلى (يەنى ھابىل بىلەن قابىل) نىڭ ھېكايىسىنى راستلىق بىلەن ئوقۇپ بەرگىن، ئۆز ۋاقتىدا ئۇلار (يەنى ھابىل بىلەن قابىل) ئىككىيلەن قۇربانلىق قىلغان ئىدى بىرسى (يەنى ھابىل) نىڭ قوبۇل بولدى، يەنە بىرسى (يەنى قابىل) نىڭ قوبۇل بولمىدى (قابىل ھابىلغا) «مەن سېنى چوقۇم ئۆلتۈرىمەن» دېدى، ھابىل نېمىشقا دېۋىدى، قابىل: «اﷲ سېنىڭ قۇربانلىقىڭنى قوبۇل قىلىپ، مېنىڭكىنى قوبۇل قىلمىدى» دېدى (ھابىل) ئېيتتى: «اﷲ پەقەت تەقۋادارلارنىڭ (قۇربانلىقىنى) قوبۇل قىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,28,' ئەگەر سەن مېنى ئۆلتۈرۈشكە قولۇڭنى سوزىدىغان بولساڭ، مەن سېنى ئۆلتۈرۈشكە قولۇمنى سوزمايمەن، مەن ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارى اﷲ تىن قورقىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,29,' مەن ھەقىقەتەن سەن بىلەن بولغان گۇناھنى (يەنى مېنى ئۆلتۈرگەنلىك گۇناھىڭنى) ۋە سېنىڭ (ئىلگىرىكى) گۇناھىڭنى (يەنى ئاتاڭ ئادەمنىڭ ئەمرىگە ئاسىيلىق قىلغانلىق گۇناھىڭنى) ئۈستۈڭگە ئېلىپ ئەھلى دوزاخلاردىن بولۇشۇڭلارنى تىلەيمەن، (كىشىلەرگە) زۇلۇم قىلغۇچىلارنىڭ جازاسى شۇدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,30,' ئۇ (يەنى قابىل) نىڭ نەپسى ئۇنىڭغا قېرىندىشىنى (يەنى ھابىلنى) ئۆلتۈرۈشنى چىرايلىق كۆرسەتتى، شۇنىڭ بىلەن، ئۇنى ئۆلتۈرۈپ (دۇنيا ۋە ئاخىرەتتە) زىيان تارتقۇچىلاردىن بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,31,' اﷲ ئۇنىڭغا قېرىندىشىنىڭ جەسىتىنى قانداق كۆمۈشنى كۆرسىتىش ئۈچۈن بىر قاغا ئەۋەتتى ئۇ (تۇمشۇقى ۋە ئىككى پۇتى بىلەن) يەرنى كولىدى ئۇ (بۇنى كۆرۈپ): «ۋاي مەن قېرىندىشىمنىڭ جەسىتىنى كۆمۈشتە مۇشۇ قاغىچىلىك بولۇشتىن ئاجىز كەلدىممۇ؟» دېدى شۇنىڭ بىلەن ئۇ نادامەت قىلغۇچىلاردىن بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,32,' شۇ (يەنى قابىلنىڭ ھابىلنى ناھەق ئۆلتۈرگەنلىكى) سەۋەبتىن (تەۋراتتا) ئىسرائىل ئەۋلادىغا ھۆكۈم قىلدۇقكى، كىمكى ناھەق ئادەم ئۆلتۈرمىگەن ياكى يەر يۈزىدە بۇزغۇنچىلىق قىلمىغان بىر ئادەمنى ئۆلتۈرسە، ئۇ پۈتۈن ئىنسانلارنى ئۆلتۈرگەندەك بولىدۇ، كىمكى بىر ئادەمنى تىرىلدۈرسە (يەنى قۇتقۇزسا ياكى ھايات قېلىشىغا سەۋەبچى بولسا)، ئۇ پۈتۈن ئىنسانلارنى تىرىلدۈرگەندەك بولىدۇ، پەيغەمبەرلىرىمىز بەنى ئىسرائىلغا راستلا (شانلىق) مۆجىزىلەرنى ئېلىپ كەلدى، شۇنىڭدىن كېيىن ئۇلارنىڭ (يەنى ئىسرائىل ئەۋلادىنىڭ) تولىسى (كۇفرىدا، ئادەم ئۆلتۈرۈشتە) زېمىندا ھەددىدىن ئاشقۇچىلاردىن بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,33,' اﷲ ۋە ئۇنىڭ رەسۇلى بىلەن ئۇرۇش قىلىدىغانلارنىڭ، يەر يۈزىدە بۇزغۇنچىلىق قىلىدىغانلارنىڭ جازاسى شۇكى، ئۇلار ئۆلتۈرۈلۈشى ياكى دارغا ئېسىلىشى ياكى ئوڭ قوللىرى ۋە سول پۇتلىرى كېسىلىشى ياكى سۈرگۈن قىلىنىشى كېرەك بۇ (يەنى جازا) ئۇلار ئۈچۈن بۇ دۇنيادا رەسۋالىق (ئېلىپ كەلگۈچىدۇر)، ئاخىرەتتە ئۇلار چوڭ ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,34,' سىلەر تۇتۇۋېلىشتىن ئىلگىرى تەۋبە قىلغانلار بۇنىڭدىن مۇستەسنا، بىلىڭلاركى، اﷲ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,35,' ئى مۆمىنلەر! مەقسەتكە ئېرىشىشىڭلار ئۈچۈن اﷲ (نىڭ ئازابى) دىن قورقۇڭلار، اﷲ قا (تائەت ـ ئىبادەت قىلىش، گۇناھتىن ساقلىنىش بىلەن) يېقىنچىلىقنى تىلەڭلار، اﷲ يولىدا جىھاد قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,36,' يەر يۈزىدىكى بارلىق نەرسىلەر، يەنە شۇنىڭدەك بىر ھەسسە كېلىدىغان نەرسىلەر كاپىرلارنىڭ بولۇپ، ئۇلار قىيامەت كۈنى (اﷲ) نىڭ ئازابىدىن قۇتۇلۇش ئۈچۈن ئۇنى فىدىيە قىلىپ بەرگەن تەقدىردىمۇ، ئۇلارنىڭ (فىدىيىسى) قوبۇل قىلىنمايدۇ، ئۇلار قىينىغۇچى ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,37,' ئۇلار دوزاختىن چىقماقچى بولىدۇيۇ، ئەمما ھەرگىز چىقالمايدۇ، ئۇلار (ئۈزۈلۈپ قالمايدىغان) دائىملىق ئازابقا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,38,' ئوغرىلىق قىلغۇچى ئەرنىڭ ۋە ئوغرىلىق قىلغۇچى ئايالنىڭ قىلمىشىنى جازالاپ، اﷲ تەرىپىدىن ئىبرەت قىلىش يۈزىسىدىن قوللىرىنى كېسىڭلار، اﷲ (ئۆز ئەمرىنى ئىشقا ئاشۇرۇشتا) غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,39,' كىمكى (ئوغرىلىق قىلىپ كىشىلەرگە) زۇلۇم قىلغاندىن كېيىن تەۋبە قىلسا، (ئەمەلىنى) تۈزىسە (يەنى ئوغرىلىق قىلىشتىن يانسا)، اﷲ ئۇنىڭ تەۋبىسىنى قوبۇل قىلىدۇ، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,40,' بىلەمسەنكى، ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ خالىغان ئادەمگە ئازاب قىلىدۇ، خالىغان ئادەمگە مەغپىرەت قىلىدۇ، اﷲ ھەممە نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,41,' ئى پەيغەمبەر! دىللىرىدا ئىشەنمەي، ئېغىزلىرىدا: «ئىمان ئېيتتۇق» دەپ قويىدىغانلار (يەنى مۇناپىقلار) دىن ۋە يەھۇدىيلاردىن كۇفرىغا (پۇرسەت تاپسىلا كۇفرىنى ئىزھار قىلىشقا) ئالدىرايدىغان كىشىلەر سېنى غەمكىن قىلمىسۇن، ئۇلار (ئەھبارلىرى ئوقۇغان) يالغانغا قۇلاق سالىدۇ، ساڭا كەلمىگەن (يەنى ئۆچلۈك قىلىپ سېنىڭ سۆھبىتىڭگە ھازىر بولمىغان) باشقا بىر قەۋمگە (يەنى خەيبەر يەھۇدىيلىرىنىڭ سۆزىگە) قۇلاق سالىدۇ، ئۇلار (تەۋراتنىڭ) سۆزلىرىنى جايلىرىدىن ئۆزگەرتىۋېتىدۇ (يەنى اﷲ نىڭ ئەھكاملىرىنى باشقىلارغا ئالماشتۇرۇۋېتىدۇ) ئۇلار: «ئەگەر سىلەرگە بۇ (ھۆكۈم) بېرىلسە، ئۇنى قوبۇل قىلىڭلار، ئەگەر ئۇ بېرىلمىسە قوبۇل قىلماڭلار» دەيدۇ، اﷲ بىركىمنىڭ گۇمراھلىقىنى ئىرادە قىلىدىكەن، ئۇنىڭ ئۈچۈن اﷲ قا قارشى قولۇڭدىن ھېچ ئىش كەلمەيدۇ (يەنى ھېچ ئادەم ئۇنىڭدىن گۇمراھلىقنى دەپئى قىلىشقا قادىر بولالمايدۇ) دىللىرىنى (قىلمىشى قەبىھ بولغانلىقتىن كۇفرىدىن) پاك قىلىشنى اﷲ خالىمىغان كىشىلەر ئەنە شۇلاردۇر، ئۇلار دۇنيادا رەسۋالىققا، ئاخىرەتتە چوڭ ئازابقا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,42,' ئۇلار يالغانغا قۇلاق سالغۇچىلاردۇر، (پارا ۋە جازانىغا ئوخشاش) ھارامنى يېگۈچىلەردۇر (ئى مۇھەممەد!) ئەگەر ئۇلار سېنىڭ ئالدىڭغا (دەۋالىشىپ كەلسە)، ئۇلارنىڭ ئارىسىدا ھۆكۈم قىلىپ قويساڭ، ياكى ئۇنى رەت قىلساڭ بولىدۇ (يەنى ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىش ياكى ئۇلاردىن يۈز ئۆرۈش سېنىڭ ئىختىيارىڭ)، ئەگەر ئۇلارنى رەت قىلساڭ، ئۇلار ساڭا قىلچە زىيان يەتكۈزەلمەيدۇ (چۈنكى اﷲ سېنى كىشىلەرنىڭ شەررىدىن ساقلايدۇ)، ئەگەر ئۇلارنىڭ ئارىسىدا ھۆكۈم قىلساڭ، ئادىللىق بىلەن ھۆكۈم قىلغىن، اﷲ ھەقىقەتەن ئادىللارنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,43,' ئۇلارنىڭ يېنىدا اﷲ نىڭ ھۆكمىنى ئۆز ئىچىگە ئالغان تەۋرات تۇرسا (يەنى تەۋراتتىكى ھۆكۈملەرنى كۆرۈپ تۇرۇپ ئەمەل قىلمايۋاتسا)، (ئى مۇھەممەد) قانداقچە سېنى ھۆكۈم چىقىرىشقا تەكلىپ قىلىدۇ؟ ئاندىن كېيىن (يەنى ھەقىقەت ئاشكارا بولغاندىن كېيىن) سېنىڭ ھۆكمىڭدىن يۈز ئۆرۈيدۇ، ئەنە شۇلار (تەۋراتقىمۇ، ساڭىمۇ) ئىشەنمىگۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,44,' بىز ھەقىقەتەن (مۇساغا) تەۋراتنى نازىل قىلدۇق، تەۋراتتا (توغرا يولغا يېتەكلەيدىغان) ھىدايەت ۋە نۇر بار، (اﷲ نىڭ ھۆكمىگە) بويسۇنغان پەيغەمبەرلەر يەھۇدىيلار ئارىسىدا (تەۋرات بىلەن) ھۆكۈم قىلىدۇ، (يەھۇدىيلارنىڭ) زاھىتلىرى ۋە ئۆلىمالىرىمۇ (ئۆزگەرتىشتىن) ساقلاشقا بۇيرۇلغان كىتابۇللاھ (يەنى تەۋرات) بويىچە ھۆكۈم قىلىدۇ، ئۇلار تەۋراتنى (ئۆزگەرتىشتىن ساقلاشقا) نازارەتچى ئىدى (ئى يەھۇدىيلارنىڭ ئۆلىمالىرى!) كىشىلەردىن قورقماڭلار، مەندىن قورقۇڭلار مېنىڭ ئايەتلىرىمنى ئاز پۇلغا تېگىشمەڭلار (يەنى پۇل ـ مال، مەنسەپ ۋە پارا ئۈچۈن ئايەتلىرىمنىڭ ھۆكمىنى ئۆزگەرتىۋەتمەڭلار)، كىملەركى اﷲ نازىل قىلغان ئايەتلەر بويىچە ھۆكۈم قىلمايدىكەن، ئۇلار كاپىرلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,45,' ئۇلار (يەنى ئىسرائىل ئەۋلادى) غا تەۋراتتا شۇنداق بەلگىلىدۇقكى، جانغا جان (يەنى ناھەق ئادەم ئۆلتۈرگەن ئۆلتۈرۈلىدۇ)، كۆزگە كۆز (يەنى كىشىنىڭ كۆزىنى ناھەق قۇيۇۋەتكەن ئادەمنىڭ كۆزىنى قۇيۇۋېتىلىدۇ)، بۇرۇنغا بۇرۇن (يەنى كىشىنىڭ بۇرنىنى ناھەق كەسكەننىڭ بۇرنى كېسىلىدۇ)، قۇلاققا قۇلاق (يەنى كىشىنىڭ قۇلىقىنى ناھەق كەسكەننىڭ قۇلىقى كېسىلىدۇ)، چىشقا چىش (يەنى كىشىنىڭ چىشىنى ناھەق تۆككەننىڭ چىشى تۆكۈلىدۇ) ۋە (كىشىنى) قانداق يارىدار قىلغان بولسا شۇنداق يارىدار قىلىنىش بىلەن قىساس ئېلىنىدۇ، كىمكى (جىنايەتچىنى) ئەپۇ قىلسا (يەنى ئۇنىڭدىن قىساس ئالمىسا)، بۇ ئۇنىڭ (گۇناھى) غا كەففارەت بولىدۇ، اﷲ نازىل قىلغان بويىچە ھۆكۈم قىلمايدىغان كىشىلەر زالىملاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,46,' ئۇلارنىڭ (يەنى بەنى ئىسرائىل پەيغەمبەرلىرىنىڭ) ئارقىسىدىن ئۆزىدىن ئىلگىرى نازىل قىلىنغان تەۋراتنى (يەنى ئۇنىڭ اﷲ تەرىپىدىن نازىل قىلىنغانلىقىنى) ئېتىراپ قىلغۇچى ئىسا ئىبن مەريەمنى ئەۋەتتۇق، ئۇنىڭغا ھىدايەت بىلەن نۇرنى ئۆز ئىچىگە ئالغان ئىنجىلنى ئاتا قىلدۇق، (ئىنجىل) ئۆزىدىن ئىلگىرى نازىل قىلىنغان تەۋراتنى ئېتىراپ قىلغۇچىدۇر (يەنى ئۇنىڭغا مۇۋاپىقتۇر)، تەقۋادارلارغا ھىدايەت ۋە پەند ـ نەسىھەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,47,' ئەھلى ئىنجىللار (يەنى ناسارالار) اﷲ ئىنجىلدا نازىل قىلغان ئەھكاملار بويىچە ھۆكۈم قىلسۇن، اﷲ نازىل قىلغان ئايەتلەر بويىچە ھۆكۈم قىلمىغانلار پاسىقلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,48,' (ئى مۇھەممەد!) بىز ساڭا ئۆزىدىن ئىلگىرى (ساماۋى) كىتابلارنى ئېتىراپ قىلغۇچى ۋە ئۇلارغا شاھىت بولغۇچى ھەق كىتابنى (يەنى قۇرئاننى) نازى قىلدۇق (ئى مۇھەممەد!) ئۇلارنىڭ ئارىسىدا اﷲ ساڭا نازىل قىلغان قۇرئان (ئەھكامى) بويىچە ھۆكۈم قىلغىن، ساڭا كەلگەن ھەقتىن بۇرۇلۇپ، ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن (ئى ئۈممەتلەر!) سىلەرنىڭ ھەر بىرىڭلارغا بىرخىل شەرىئەت ۋە ئوچۇق يول تەيىن قىلدۇق ئەگەر اﷲ خالىسا، ئەلۋەتتە، سىلەرنى بىر ئۈممەت قىلاتتى (يەنى پۈتۈن ئىنسانلارنى بىر دىندا قىلاتتى) لېكىن اﷲ سىلەرگە بەرگەن شەرىئەتلەر ئارىسىدا سىلەرنى سىناش ئۈچۈن (كۆپ ئۈممەت قىلىپ ئايرىدى) ياخشى ئىشلارغا ئالدىراڭلار ھەممىڭلار اﷲ نىڭ دەرگاھىغا قايتىسىلەر، سىلەر ئىختىلاپ قىلىشقان نەرسىلەرنى (ئۇنىڭ قايسى ھەق، قايسى ناھەق ئىكەنلىكىنى) اﷲ سىلەرگە ئېيتىپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,49,' (ئۇلار يەنى يەھۇدىيلار ۋە ناسارالار) نىڭ ئارىسىدا اﷲ ساڭا نازىل قىلغان قۇرئاننىڭ (ئەھكامى) بويىچە ھۆكۈم قىلغىن، ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن، ئۇلارنىڭ اﷲ ساڭا نازىل قىلغان قۇرئاننىڭ بىر قىسمىدىن سېنى ۋاز كەچۈرۈشىدىن ساقلانغىن ئەگەر ئۇلار (اﷲ ساڭا نازىل قىلغان ھۆكۈمدىن) يۈز ئۆرۈسە، (ئى مۇھەممەد!) بىلگىنكى، ئۇلارنىڭ بىر قىسىم گۇناھلىرى تۈپەيلىدىن اﷲ ئۇلارنى جازالاشنى ئىرادە قىلىدۇ شەك ـ شۈبھىسىزكى، كىشىلەردىن نۇرغۇنى پاسىقتۇر (يەنى پەرۋەردىگارىنىڭ تائىتىدىن باش تارتىپ، ھەققە خىلاپلىق قىلىپ گۇناھقا چۆمگۈچىلەردۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,50,' ئۇلار جاھىلىيەت دەۋرىنىڭ ھۆكمىنى تەلەپ قىلامدۇ؟ (اﷲ قا) چىن ئىشىنىدىغان قەۋمنىڭ نەزىرىدە ھۆكۈمدە اﷲ تىنمۇ ئادىل كىم بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,51,' ئى مۆمىنلەر! يەھۇدىي ۋە ناسارالارنى دوست تۇتماڭلار ئۇلار بىر ـ بىرىنى دوست تۇتىدۇ سىلەردىن كىمكى ئۇلارنى دوست تۇتىدىكەن، ئۇمۇ ئەنە شۇلاردىن سانىلىدۇ، اﷲ زالىم قەۋمنى ھەقىقەتەن ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,52,' دىللىرىدا كېسەللىك (يەنى مۇناپىقلىق) بارلارنىڭ (زامان ئۆزگىرىپ كاپىرلار زەپەر تاپسا) ئۆزىمىزگە بىر پالاكەتچىلىك كېلىشىدىن قورقىمىز دەپ، ئۇلار بىلەن دوست بولۇشقا ئالدىرىغانلىقىنى كۆرىسەن، اﷲ (رەسۇلۇللاھقا، مۇسۇلمانلارغا) غەلىبە (يەنى مەككىنى پەتھى قىلىشنى) ئاتا قىلىدۇ، ياكى بىر ئىشنى (يەنى ئۇلارنىڭ مۇناپىقلىقىنى پاش قىلىشنى) مەيدانغا كەلتۈرىدۇ ـ دە، كۆڭلىدە يوشۇرغىنى (يەنى مۆمىنلەرنىڭ دۈشمەنلىرىنى دوست تۇتقانلىقى) ئۈچۈن ئۇلار نادامەت چېكىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,53,' (بۇ مۇناپىقلارنىڭ مۇسۇلمانلارغا قىلغان ھىيلە ـ مىكرى پاش قىلىنغاندا) مۆمىنلەر (بىر ـ بىرىگە): «سىلەر بىلەن چوقۇم بىللە ئىكەنلىكلىرىگە اﷲ نامى بىلەن قاتتىق قەسەم ئىچىشكەنلەرمۇ شۇ كىشىلەرمۇ؟» دەيدۇ ئۇلارنىڭ (يەنى مۇناپىقلارنىڭ) قىلغان ئەمەللىرى بىكار بولدى، ئۇلار (دۇنيا ۋە ئاخىرەتتە) زىيان تارتقۇچى بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,54,' ئى مۆمىنلەر! سىلەردىن كىمكى مۇرتەد بولىدىكەن، اﷲ (ئۇنىڭ ئورنىغا) اﷲ ئۇلارنى دوست تۇتىدىغان، ئۇلارمۇ اﷲ نى دوست تۇتىدىغان، مۆمىنلەرگە كۆيۈنىدىغان، كاپىرلارغا شەپقەتسىز، اﷲ نىڭ يولىدا جىھاد قىلىدىغان ۋە مالامەت قىلغۇچىنىڭ مالامىتىدىن قورقمايدىغان بىر قەۋمنى كەلتۈرىدۇ بۇ (يەنى يۇقىرىدىكى سۈپەتلەر بىلەن سۈپەتلىنىش) اﷲ نىڭ پەزلىدۇركى، (اﷲ) ئۇنى (بەندىلىرىدىن) خالىغان كىشىگە بېرىدۇ، اﷲ (نىڭ پەزلى) كەڭدۇر، (اﷲ) ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,55,' شۈبھىسىزكى، سىلەرنىڭ دوستۇڭلار اﷲ دۇر، اﷲ نىڭ رەسۇلىدۇر ۋە مۆمىنلەردۇركى، مۆمىنلەر تەئدىل ئەركان بىلەن ناماز ئۆتەيدۇ، زاكات بېرىدۇ، ئۇلار (اﷲ نىڭ ئەمرىگە) كەمتەرلىك بىلەن بويسۇنغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,56,' كىمكى اﷲ نى، اﷲ نىڭ پەيغەمبىرىنى ۋە مۆمىنلەرنى دوست تۇتىدىكەن، (ئۇ اﷲ نىڭ جامائەسىدىندۇر) اﷲ نىڭ جامائەسى (ئەلۋەتتە) غالىبتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,57,' ئى مۆمىنلەر! سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دىن دىنىڭلارنى مەسخىرە ۋە ئويۇنچۇق قىلغانلارنى دوست قىلىۋالماڭلار ئەگەر (ھەقىقىي) مۆمىن بولساڭلار، اﷲ تىن قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,58,' سىلەر نامازغا چاقىرغان (يەنى ئەزان ئېيتقان) چېغىڭلاردا، ئۇلار نامازنى (ھەم سىلەرنى) مەسخىرە ۋە ئويۇنچۇق قىلىدۇ، بۇ شۇنىڭ ئۈچۈنكى، ئۇلار (نامازنىڭ ھېكمىتىنى ۋە نامازنىڭ نەپسىنى پاكلاشتىكى غايىسىنى) چۈشەنمەس قەۋمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,59,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئەھلى كىتاب! (يەنى يەھۇدىي ۋە ناسارالار جامائەسى) پەقەت بىزنىڭ اﷲ قا، بىزگە نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا)، بۇنىڭدىن ئىلگىرى (پەيغەمبەرلەرگە) نازىل قىلىنغان كىتابلارغا ئىمان ئېيتقانلىقىمىز ئۈچۈن ۋە سىلەرنىڭ كۆپچىلىكىڭلار پاسىق بولغانلىقىڭلار ئۈچۈن، بىزنى ئەيىبلەمسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,60,' (ئى مۇھەممەد!) ئېيتقىنكى، «سىلەرگە اﷲ نىڭ نەزىرىدىكى بۇنىڭدىنمۇ يامان جازانى ئېيتىپ بېرەيمۇ؟ (ئۇ شۇنداق) كىشىلەرنىڭ (جازاسىكى)، اﷲ ئۇلارنى رەھمىتىدىن يىراق قىلدى ۋە ئۇلارغا غەزەپ قىلدى، اﷲ ئۇلارنىڭ بىر قىسمىنى مايمۇن ۋە چوشقا قىلدى، بىر قىسمىنى شەيتانغا ئىبادەت قىلىدىغان قىلدى، ئەنە شۇلارنىڭ ئورنى ئەڭ ياماندۇر (يەنى دوزاختۇر)، (ئۇلار) توغرا يولدىن ئەڭ ئاداشقاندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,61,' (يەھۇدىيلارنىڭ مۇناپىقلىرى) يېنىڭلارغا كەلگەن چاغدا، بىز ئىمان ئېيتتۇق، دەيدۇ، ۋاھالەنكى، ئۇلار (ئى مۇھەممەد! يېنىڭغا) كۇفرى بىلەن كىرىپ كۇفرى بىلەن چىقىپ كەتتى (يەنى سەندىن ئاڭلىغان ئىلىمدىن پايدىلانمىدى) اﷲ ئۇلارنىڭ يوشۇرغان نەرسىسىنى (يەنى نىفاقىنى ۋە كۇفرىنى) ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,62,' ئۇلار (يەنى يەھۇدىيلار) نىڭ ئارىسىدىكى نۇرغۇن كىشىلەرنىڭ گۇناھقا، زۇلۇم سېلىشقا ۋە ھارام يېيىشكە ئالدىرايدىغانلىقىنى كۆرىسەن، ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,63,' ئۇلارنىڭ زاھىتلىرى، ئەھبارلىرى نېمىشقا ئۇلارنى يالغان سۆزلەشتىن، ھارام يېيىشتىن توسمايدۇ؟ ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,64,' يەھۇدىيلار: «اﷲ نىڭ قولى باغلاقلىقتۇر (يەنى اﷲ بېخىلدۇر ياكى پېقىردۇر)» دېدى يەھۇدىيلارنىڭ قوللىرى باغلانسۇن! ئۇلار بۇ سۆزلىرى سەۋەبلىك اﷲ نىڭ رەھمىتىدىن يىراق قىلىندى، ئەمەلدە اﷲ نىڭ ئىككى قولى ئوچۇقتۇر، قانداق خالىسا شۇنداق رىزىق بېرىدۇ، پەرۋەردىگارىڭ تەرىپىدىن ساڭا نازىل قىلىنغان قۇرئان ئۇلارنىڭ كۆپچىلىكىدە يامانلىق بىلەن كۇفرىنى ئاشۇرىدۇ (يەنى ئۇلارنىڭ ساڭا دۈشمەنلىكىنى ۋە قۇرئاننى ئىنكار قىلىشىنى زىيادە قىلىدۇ) بىز ئۇلارنىڭ ئارىسىغا قىيامەتكىچە داۋاملىشىدىغان ئۆچمەنلىك ۋە دۈشمەنلىكنى سالدۇق، ئۇلار ھەر قاچان (رەسۇلۇللاھقا قارشى) ئۇرۇش ئوتىنى ياقماقچى بولسا، اﷲ ئۇنى ئۆچۈرىدۇ، ئۇلار يەر يۈزىدە (ئىسلامغا سۇيىقەست قىلىش ۋە مۇسۇلمانلار ئارىسىدا پىتنە قوزغاش بىلەن) بۇزغۇنچىلىق قىلغۇچىلارنى ياقتۇرمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,65,' ئەگەر ئەھلى كىتاب (يەنى يەھۇدىيلار ۋە ناسارالار) (اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ھەقىقىي) ئىمان ئېيتسا ۋە (اﷲ چەكلىگەن ئىشلاردىن) پەرھىز قىلسا ئىدى، ئۇلارنىڭ گۇناھلىرىنى بىز چوقۇم كەچۈرەتتۇق، ئۇلارنى چوقۇم نازۇنېمەتلىك جەننەتكە كىرگۈزەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,66,' ئەگەر ئۇلار تەۋراتقا، ئىنجىلغا ۋە پەرۋەردىگارى تەرىپىدىن ئۇلارغا نازىل قىلىنغان باشقا كىتابلارغا ئەمەل قىلسا ئىدى، ئۇلار چوقۇم ئۈستىلىرىدىن ۋە ئاياغلىرى ئاستىدىن يەر ئىدى (يەنى ئۇلارغا ئاسمان ـ زېمىندىن كەڭ رىزىق بېرىلەتتى)، ئۇلاردىن بىر جامائە (يەنى مۇھەممەد ئەلەيھىسسالامغا ئىمان ئېيتقانلار) توغرا يولدىدۇر ئۇلارنىڭ نۇرغۇنلىرى نېمىدېگەن يامان ئىش قىلغۇچىلار!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,67,' ئى پەيغەمبەر! پەرۋەردىگارىڭ تەرىپىدىن ساڭا نازىل قىلىنغان ئەھكاملارغا ھەممىسىنى يەتكۈزگىن، ئەگەر تولۇق يەتكۈزمىسەڭ، اﷲ تاپشۇرغان ۋەزىپىنى ئادا قىلمىغان بولىسەن اﷲ سېنى كىشىلەرنىڭ زىيانكەشلىكىدىن ساقلايدۇ اﷲ ھەقىقەتەن كاپىر قەۋمنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,68,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) سىلەر تەۋراتقا، ئىنجىلغا ۋە سىلەرگە پەرۋەردىگارىڭلاردىن نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا) تولۇق ئەمەل قىلمىغۇچە، سىلەر ئېتىبارغا ئالغۇدەك دىندا بولغان بولمايسىلەر» پەرۋەردىگارىڭدىن نازىل قىلىنغان ئەھكاملار ئۇلارنىڭ كۆپچىلىكىدە يامانلىق ۋە كۇفرىنى ئاشۇرىدۇ، كاپىر قەۋم ئۈچۈن قايغۇرمىغىن (چۈنكى پەيغەمبەرلەرنى يالغانغا چىقىرىش ئۇلارنىڭ ئادىتىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,69,' مۆمىنلەردىن، يەھۇدىيلاردىن، پەرىشتىلەرگە ۋە يۇلتۇزلارغا چوقۇنغۇچىلاردىن، ناسارالاردىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,70,' بىز بەنى ئىسرائىلدىن ھەقىقەتەن چىن ئەھدە ئالدۇق ئۇلارغا (دىننىڭ ئىشلىرىنى بايان قىلىپ بېرىش ئۈچۈن) پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارغا ھەر قاچان بىرەر پەيغەمبەر ئۇلار ياقتۇرمايدىغان ئەھكامنى ئېلىپ كەلسە، (ئۇلار پەيغەمبەرلەرنىڭ) بىر بۆلۈكىنى يالغانچى قىلدى، بىر بۆلۈكىنى ئۆلتۈردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,71,' ئۇلار، (بىز ئىسرائىل ئەۋلادى پەيغەمبەرلەرنى ئۆلتۈرۈش بىلەن) ئازابقا دۇچار بولمايمىز، دەپ ئويلىدى ئۇلار كور بولدى (يەنى ھەقىقەتنى كۆرمىدى)، ئۇلار گاس بولدى (يەنى ھەقىقەتنى ئاڭلىمىدى)، ئاندىن كېيىن (ئۇلار تەۋبە قىلدى)، اﷲ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلدى، ئاندىن ئۇلارنىڭ نۇرغۇنى يەنە (ھەقنى كۆرۈشتىن) كور بولدى، يەنە (پەند ـ نەسىھەت ئاڭلاشتىن) گاس بولى، اﷲ ئۇلارنىڭ قىلىۋاتقان ئىشلىرىنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,72,' شۈبھىسىزكى، اﷲ مەريەمنىڭ ئوغلى ئىسادۇر، دېگۈچىلەر كاپىر بولدى، ئىسا: «ئى بەنى ئىسرائىل! مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار بولغان اﷲ قا ئىبادەت قىلىڭلار، كىمكى اﷲ قا شېرىك كەلتۈرىدىكەن (يەنى اﷲ تىن غەيرىيگە ئىلاھ دەپ ئېتىقاد قىلىدىكەن)، اﷲ ئۇنىڭغا جەننەتنى ھارام قىلىدۇ، ئۇنىڭ جايى دوزاخ بولىدۇ، زالىملارغا ھېچبىر مەدەتكار (يەنى اﷲ نىڭ ئازابىدىن قۇتۇلدۇرغۇچى) بولمايدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,73,' اﷲ ئۈچ ئىلاھنىڭ بىرىدۇر، دېگۈچىلەر شەكسىز كاپىر بولدى بىر ئىلاھتىن باشقا ھېچ ئىلاھ يوقتۇر ئەگەر ئۇلار ئېيتىۋاتقان سۆزلىرىدىن قايتمىسا، ئۇلارنىڭ ئىچىدىكى كاپىر بولغانلار (دۇنيا ۋە ئاخىرەتتە) قاتتىق ئازابقا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,74,' ئۇلار اﷲ قا تەۋبە قىلمامدۇ؟ اﷲ تىن مەغپىرەت تەلەپ قىلمامدۇ؟ اﷲ (تەۋبە قىلغۇچىلارغا) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,75,' مەريەم ئوغلى ئىسا پەقەت (ئۆتكەن پەيغەمبەرلەرگە ئوخشاش) پەيغەمبەردۇر، ئۇنىڭدىن ئىلگىرى نۇرغۇن پەيغەمبەرلەر ئۆتكەن، ئۇنىڭ ئانىسى ناھايىتى راستچىل خوتۇندۇر، ئۇ ئىككىسى تاماق يەيتتى (يەنە ئۇلارمۇ باشقا مەخلۇقلارغا ئوخشاشلا گۆش، سۆڭەك، تومۇر ۋە پەيلەردىن تەركىب تاپقان ئىدى) ئۇلارغا (يەنى ئۇلارنىڭ ئېتىقادىنىڭ باتىللىقىغا) ئايەتلىرىمىزنى قانداق بايان قىلىدىغانلىقىمىزغا قارىغىن؛ ئاندىن ئۇلارنىڭ ھەقتىن قانداق باش تارتقانلىقىغا قارىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,76,' (ئى مۇھەممەد!) «سىلەر اﷲ تىن باشقا، سىلەرگە پايدا ـ زىيان يەتكۈزۈشكە قادىر بولالمايدىغان نەرسىلەرگە چوقۇنامسىلەر؟» دېگىن اﷲ بولسا (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,77,' (ئى مۇھەممەد!) »ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) دىنىڭلاردا ھەقسىز رەۋىشتە چەكتىن ئېشىپ كەتمەڭلار، ئىلگىرى ئۆزلىرى ئازغان، نۇرغۇن كىشىلەرنى ئازدۇرغان ۋە توغرا يولدىن ئاداشقان قەۋمنىڭ نەپسى خاھىشلىرىغا ئەگەشمەڭلار» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,78,' بەنى ئىسرائىلدىن كاپىر بولغانلارغا داۋۇدنىڭ ۋە مەريەم ئوغلى ئىسانىڭ تىلى بىلەن (يەنى زەبۇردا ۋە ئىنجىلدا) لەنەت قىلىندى بۇ (يەنى ئۇلارنىڭ لەنەتكە ئۇچرىشى) ئۇلارنىڭ ئاسىيلىق قىلغانلىقلىرى ۋە ھەددىدىن ئېشىپ كەتكەنلىكلىرىدىن بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,79,' ئۇلار ئۆزلىرى قىلغان يامان ئىشلاردىن بىر ـ بىرىنى توسمايتتى؛ ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,80,' ئۇلار (يەنى يەھۇدىيلار) نىڭ ئىچىدە نۇرغۇنلىرىنىڭ كاپىرلارنى (يەنى رەسۇلۇللاھنى ۋە مۇسۇلمانلارنى ئۆچ كۆرۈش يۈزىسىدىن، مۇشرىكلارنى) دوست تۇتقانلىقىنى كۆرىسەن ئۇلارنىڭ ئۆزلىرى ئۈچۈن ئالدىن تەييارلىغان ئەمەللىرى نېمىدېگەن يامان! (بۇ ئەمەللەر) ئۇلارغا اﷲ نىڭ غەزىپىنى ئېلىپ كەلدى ئۇلار مەڭگۈ ئازابقا قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,81,' ئەگەر ئۇلار اﷲ قا، پەيغەمبەرگە ۋە پەيغەمبەرگە نازىل قىلىنغان كىتابقا ئىمان ئېيتسا ئىدى، كاپىرلارنى دوست تۇتمايتتى، لېكىن ئۇلارنىڭ تولىسى پاسىقلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,82,' (ئى مۇھەممەد!) يەھۇدىيلار ۋە مۇشرىكلارنىڭ مۆمىنلەرگە ھەممىدىن قاتتىق دۈشمەن ئىكەنلىكىنى چوقۇم بايقايسەن، بىز ناسارا دېگەن كىشىلەرنىڭ دوستلۇق جەھەتتە مۆمىنلەرگە ھەممىدىن يېقىن ئىكەنلىكىنىمۇ چوقۇم بايقايسەن، بۇ، ناسارالارنىڭ ئىچىدە ئۆلىمالار، راھىبلار بولغانلىقى ۋە (ھەقنى قوبۇل قىلىشتا) تەكەببۇرلۇق قىلمايدىغانلىقلىرى ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,83,' ئۇلار پەيغەمبەرگە نازىل قىلىنغان قۇرئاننى ئاڭلىغان چاغلىرىدا ھەقىقەتنى تونۇغانلىقلىرىدىن، ئۇلارنىڭ كۆزلىرىدىن ياش قويۇلغانلىقىنى كۆرىسەن، ئۇلار ئېيتىدۇ: «رەببىمىز، بىز ئىمان ئېيتتۇق، بىزنى (پەيغەمبىرىڭنى، كىتابىڭنى) ئېتىراپ قىلغۇچىلار قاتارىدا قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,84,' بىز نېمىشقا اﷲ قا، بىزگە كەلگەن ھەقىقەتكە ئىشەنمەيلى؟ ھالبۇكى، بىز پەرۋەردىگارىمىزنىڭ بىزنى ياخشى كىشىلەر قاتارىدا (جەننەتكە) كىرگۈزۈشنى ئۈمىد قىلىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,85,' بۇ سۆزلىرى ئۈچۈن اﷲ ئۇلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەر بىلەن مۇكاپاتلايدۇ ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ ياخشىلىق قىلغۇچىلارغا بېرىلىدىغان مۇكاپات ئەنە شۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,86,' كاپىر بولغانلار، بىزنىڭ ئايەتلىرىمىزنى يالغانغا چىقارغانلار دوزاخ ئەھلىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,87,' ئى مۆمىنلەر! اﷲ سىلەرگە ھالال قىلغان پاك نەرسىلەرنى (تەركى دۇنيا بولۇش يۈزىسىدىن ئۆزۈڭلارغا) ھارام قىلماڭلار، (اﷲ بەلگىلەپ بەرگەن) چەكتىن ئاشماڭلار، اﷲ چەكتىن ئاشقۇچىلارنى ھەقىقەتەن دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,88,' اﷲ سىلەرگە رىزىق قىلىپ بەرگەن ھالال، پاك نەرسىلەردىن يەڭلار، سىلەر ئىمان ئېيتقان اﷲ قا تەقۋادارلىق قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,89,' اﷲ سىلەرنى سەۋەنلىك قەسىمىڭلار ئۈچۈن جاۋابكارلىققا تارتىدۇ (مۇنداق قەسىمىڭلارنى بۇزساڭلار) ئۇنىڭ كەففارىتى ئائىلەڭلارغا بېرىدىغان ئوتتۇرا دەرىجىلىك تاماق بىلەن ئون مىسكىننى بىر ۋاخ غىزالاندۇرۇشتۇر، ياكى ئۇلارغا (يەنى ئون مىسكىنگە بەدىنىنى يېپىپ تۇرىدىغان) بىر قۇر كىيىم بېرىشتۇر، ياكى بىر قۇل ياكى بىر چۆرىنى ئازاد قىلىشتۇر، كىمكى بۇنداق قىلىشقا كۈچى يەتمىسە، ئۈچ كۈن روزا تۇتۇشى لازىم بۇ ئەنە شۇ ئىچكەن قەسىمىڭلارنى بۇزغانلىقىڭلارنىڭ كەففارىتىدۇر، قەسىمىڭلارغا رىئايە قىلىڭلار (يەنى كەلسە ـ كەلمەس قەسەم ئىچمەڭلار) سىلەرنىڭ شۈكۈر قىلىشىڭلار ئۈچۈن اﷲ ئايەتلىرىنى سىلەرگە ئەنە شۇنداق بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,90,' ئى مۆمىنلەر! ھاراق ئىچىش، قىمار ئويناش، بۇتلار (يەنى چوقۇنۇش ئۈچۈن تىكلەنگەن تاشلار) غا چوقۇنۇش، پال ئوقلىرى بىلەن پال سېلىش شەيتاننىڭ ئىشى، پاسكىنا قىلىقلاردۇر، بەختكە ئېرىشىشىڭلار ئۈچۈن شەيتاننىڭ ئىشىدىن يىراق بولۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,91,' شەيتان ھاراق، قىمار ئارقىلىق ئاراڭلاردا دۈشمەنلىك، ئاداۋەت تۇغدۇرماقچى، سىلەرنى نامازدىن ۋە اﷲ نى ياد ئېتىشتىن توسماقچى بولىدۇ، سىلەر ئەمدى (ھاراقتىن، قىماردىن) يانمامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,92,' اﷲ ۋە پەيغەمبەرگە ئىتائەت قىلىڭلار، (ئۇلارغا مۇخالىپەتچىلىك قىلىشتىن) ھەزەر قىلىڭلار، ئەگەر (ئىتائەت قىلىشتىن) باش تارتساڭلار، بىلىڭلاركى، رەسۇلىمىزنىڭ مەسئۇلىيىتى پەقەت ئوچۇق تەبلىغ قىلىشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,93,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەر (ھارام قىلىنمىغان نەرسىلەرنى) يېسە، ئىچسە ھېچ گۇناھ بولمايدۇ، قاچانكى ئۇلار (ھارام نەرسىلەردىن) ساقلانسا، ئىمانىدا ۋە ياخشى ئەمەللىرىدە ئىزچىل بولسا، ئاندىن (ھارام قىلىنغان نەرسىلەردىن) يەنە ساقلانسا ۋە ھارام دەپ ئېتىقاد قىلسا، ئاندىن (ھارام قىلىنغان نەرسىلەردىن داۋاملىق) ساقلانسا ۋە ياخشى ئىشلارنى قىلسا، اﷲ ياخشى ئىش قىلغۇچىلارنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,94,' ئى مۆمىنلەر! كۆرمەي تۇرۇپ اﷲ تىن قورقىدىغانلارنى بىلىش ئۈچۈن، قولۇڭلار ۋە نەيزەڭلار بىلەن ئوۋلىنىدىغان بىر ئاز ئوۋ بىلەن اﷲ سىلەرنى چوقۇم سىنايدۇ، كىمكى بۇنىڭدىن كېيىن ھەددىدىن ئاشسا (يەنى ئىھرامدا تۇرۇپ ئوۋ ئوۋلىسا)، ئۇ قىينىغۇچى ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,95,' ئى مۆمىنلەر! سىلەر ئوۋلىغان ھايۋانلارنى ئىھرامدا ياكى ھەرەمدە تۇرۇپ ئۆلتۈرمەڭلار سىلەردىن كىمكى ئۇنى قەستەن ئۆلتۈرىدىكەن، ئىشنىڭ ۋابالىنى تېتىشى ئۈچۈن، ئۇنىڭ جازاسى ئىككى ئادىل كىشىنىڭ باھالىشى بويىچە ھېلىقى ئۆلتۈرۈلگەن ئوۋغا ئوخشاش ئۆي ھايۋانلىرىدىن بىرنى كەبىگە ئېلىپ بېرىپ قۇربانلىق قىلىشتۇر، ياكى كەففارەت بېرىش يۈزىسىدىن مىسكىنلەرگە ئاش بېرىشتۇر، ياكى ھەر مىسكىنگە بېرىدىغان ئاشنىڭ باراۋىرىگە بىر كۈن روزا تۇتۇشتۇر ئۆتكەنكى گۇناھنى (يەنى بۇ ئايەت چۈشۈشتىن بۇرۇن ئىھرامدا تۇرۇپ ئوۋ ئۆلتۈرگەن بولساڭلار، بۇنى) اﷲ ئەپۇ قىلدى، كىمكى قايتا گۇناھ ئۆتكۈزسە (يەنى قايتىدىن ئوۋ ئۆلتۈرسە)، اﷲ ئۇنى جازالايدۇ، اﷲ غالىبتۇر، (ئاسىيلىق قىلغۇچىلارنى) جازالىغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,96,' سىلەرنىڭ ۋە يولۇچىلارنىڭ مەنپەئەتلىنىشى ئۈچۈن سىلەرگە دېڭىز ھايۋانلىرى، دېڭىزدىن چىقىدىغان يېمەكلىكلەر ھالال قىلىندى، مادامىكى سىلەر ئېھرامدا ياكى ھەرەمدە ئىكەنسىلەر، قۇرۇقلۇق ھايۋانلىرىنى ئوۋلاش سىلەرگە ھارام قىلىندى، سىلەر (قىيامەت كۈنى) دەرگاھىغا توپلىنىدىغان اﷲ تىن قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,97,' اﷲ بەيتۇلھەرەم بولغان كەبىنى، ھەج ئېيىنى، ھەجدە قىلىنىدىغان قۇربانلىقنى، (قۇربانلىقنىڭ ئالامىتى سۈپىتىدە) بويۇنلىرىغا بەلگە ئېسىلغان تۆگىلەرنى ئىنسانلارنىڭ دۇنيا ۋە ئاخىرەتلىك ئىشلىرىنىڭ تۇتقۇسى قىلدى، بۇ، اﷲ نىڭ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى بىلىپ تۇرىدىغانلىقىنى ۋە اﷲ نىڭ ھەر شەيئىنى بىلگۈچى ئىكەنلىكىنى سىلەرنىڭ بىلىشىڭلار ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,98,' بىلىڭلاركى، اﷲ نىڭ ئازابى قاتتىقتۇر، (ئىتائەت قىلغۇچىلارغا) اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,99,' پەيغەمبەرنىڭ مەسئۇلىيىتى پەقەت تەبلىغ قىلىشتۇر اﷲ سىلەرنىڭ ئاشكارا ۋە يوشۇرۇن قىلىپ يۈرگەن ئىشلىرىڭلارنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,100,' (ئى مۇھەممەد!) ئېيتقىنكى، گەرچە ھارامنىڭ كۆپلۈكى (ئى تىڭشىغۇچى) سېنى ئەجەبلەندۈرسىمۇ، ھارام بىلەن ھالال باراۋەر ئەمەس ئى ئەقىل ئىگىلىرى! نىجات تېپىشىڭلار ئۈچۈن اﷲ تىن قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,101,' ئى مۆمىنلەر! ئەگەر سىلەرگە ئاشكارا قىلىنسا سىلەرنى بىئارام قىلىپ قويىدىغان نەرسىلەر توغرۇلۇق (پەيغەمبەردىن) سورىماڭلار ئەگەر قۇرئان نازىل قىلىنىۋاتقان چاغدا ئۇلار توغرۇلۇق سورىساڭلار، سىلەرگە بىلدۈرۈلىدۇ، ئۆتكەندە سورىغانلىرىڭلارنى اﷲ ئەپۇ قىلدى اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ھەلىمدۇر (يەنى ئاسىيلىق قىلغۇچىلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,102,' سىلەردىن بۇرۇن ئۆتكەن بىر قەۋم (پەيغەمبەرلىرىدىن) شۇنداق مەسىلىلەرنى سورىغان ئىدى، كېيىن بۇنىڭ سەۋەبىدىن (يەنى سورىغان ئىشلىرى بايان قىلىنىپ ئۇلار ئەمەل قىلمىغانلىقتىن) كاپىر بولۇپ كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,103,' بەھىرە (سەككىزنى تۇغقان، بەشىنچىسىنى ئەركەك تۇغقان تۆگە بولۇپ، جاھىلىيەت ئەرەبلىرى بۇ تۆگىنىڭ قۇلىقىنى يېرىپ قويۇپ ئۆز مەيلىگە قويۇپ بېرەتتى، مىنمەيتتى، يۈك ئارتمايتتى)، سائىبە (چىشىدىن ئوننى تۇغقان تۆگە بولۇپ، مىنىلمەيتتى، قىرقىلمايتتى، سۈتى مۇساپىرلارغىلا بېرىلەتتى)، ۋەسىلە (بىرىنچىنى ۋە ئىككىنچىنى چىشى تۇغقان، بۇتلار ئۈچۈن ئۆز مەيلىگە قويۇۋېتىلگەن تۆگە)، ھام (ئون تۆگىگە ئاتا بولغان بۇغرا تۆگە بولۇپ، ئۇلار ئۇنى مىنمەي، يېمەك ـ ئىچمەكتە ئۆز مەيلىگە قويۇپ بېرەتتى) لارغا اﷲ (يۇقىرىقىدەك قىلىشنى) بۇيرۇغىنى يوق، (لېكىن كاپىرلار اﷲ قا يالغاننى چاپلايدۇ، ئۇلارنىڭ تولىسى بۇنىڭ يالغانلىقىنى) چۈشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,104,' ئۇلارغا اﷲ نازىل قىلغان ھۆكۈمگە، پەيغەمبەر بايان قىلغان ھۆكۈمگە ئەمەل قىلىڭلار دېيىلسە، ئۇلار: «ئاتا ـ بوۋىلىرىمىزنىڭ دىنى بىزگە كۇپايە» دەيدۇ ئۇلارنىڭ ئاتا ـ بوۋىلىرى ھېچ نەرسە بىلمەيدىغان ۋە ھىدايەت تاپمىغان تۇرسىمۇ (يەنىلا ئۇلارنىڭ دىنىغا ئەمەل قىلامدۇ؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,105,' ئى مۆمىنلەر! ئۆزۈڭلارنى گۇناھتىن ساقلاڭلار، سىلەر قاچانكى توغرا يولدا بولساڭلار، باشقىلارنىڭ ئاداشقىنى سىلەرگە زىيان يەتكۈزەلمەيدۇ سىلەر ھەممىڭلار اﷲ نىڭ دەرگاھىغا قايتىسىلەر، اﷲ قىلغان ئەمەلىڭلارنى سىلەرگە ئېيتىپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,106,' ئى مۆمىنلەر! ئاراڭلاردىن بىركىم ئۆلىدىغان چېغىدا ۋەسىيەت قىلماقچى بولسا، ئۇنىڭغا ئىچىڭلاردىن ئىككى ئادىل كىشى گۇۋاھ بولسۇن، ياكى (ئۆز دىنىڭلاردىكىلەردىن گۇۋاھ بولىدىغان ئىككى كىشى تېپىلمىسا) غەيرىي دىندىكىلەردىن ئىككى كىشى گۇۋاھ بولسۇن، ئەگەر سىلەر سەپەردە بولۇپ، بېشىڭلارغا ئۆلۈم دەھشىتى كەلگەندە، سىلەر (بۇ ئىككى گۇۋاھنىڭ راستچىللىقىدىن) گۇمانلانساڭلار، نامازدىن كېيىن ئۇلارنى ئېلىپ قېلىڭلار، ئۇلار: «بىز پايدىسىغا قەسەم قىلىدىغان كىشىلەرمىز، بىزنىڭ يېقىنىمىز بولغان تەقدىردىمۇ قەسىمىمىزنى ھېچ نەرسىگە ساتمايمىز، خۇدالىق ئۈچۈن بولغان گۇۋاھلىقنى يوشۇرمايمىز ئەگەر ئۇنى يوشۇرساق، بىز ئەلۋەتتە گۇناھكارلادىن بولىمىز» دەپ اﷲ نىڭ نامى بىلەن قەسەم قىلسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,107,' ئەگەر ئۇ ئىككى گۇۋاھچىنىڭ (يالغان گۇۋاھلىق بەرگەنلىك ياكى مىراسقا خىيانەت قىلغانلىق) گۇناھى بىلىنسە، ئۇلارنىڭ ئورنىغا مىراس ئېلىشقا ئەڭ ھەقلىق بولغان ۋارىسلاردىن ئىككى كىشى گۇۋاھ بولۇپ: «بىزنىڭ گۇۋاھلىقىمىز ئەلۋەتتە ئۇلارنىڭ گۇۋاھلىقىدىنمۇ توغرىدۇر، بىز شەرىئەتتىن چىقىپ كەتمىدۇق، بولمىسا بىز چوقۇم زالىملاردىن بولىمىز» دەپ اﷲ نىڭ نامى بىلەن قەسەم قىلسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,108,' بۇ (ئۇسۇل) گۇۋاھچىلارنى ئەڭ گۇۋاھلىق قىلىدىغان قىلىشقا، قىلغان قەسىمىنى رەت قىلىشتىن (يەنى ئۇلاردىن كېيىن باشقىلار قەسەم قىلىشى بىلەن ئۆزلىرىنىڭ رەسۋا بولۇشىدىن) قورقىدىغان قىلىشقا ئەڭ يېقىن (ئۇسۇل) دۇر اﷲ تىن قورقۇڭلار، (اﷲ نىڭ ئەمرىنى) ئاڭلاڭلار اﷲ پاسىق قەۋمنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,109,' اﷲ قىيامەت كۈنى پەيغەمبەرلەرنى يىغىپ: «(قەۋمىڭلارنى ئىمانغا دەۋەت قىلغىنىڭلاردا) قانداق جاۋابقا ئىگە بولدۇڭلار؟» دەپ سورايدۇ، ئۇلار: «(سېنىڭ بىلگىنىڭگە سېلىشتۇرغاندا) بىز ھېچ نەرسە بىلمەيمىز، سەن غەيبلەرنى ناھايىتى ئوبدان بىلىسەن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,110,' ئۆز ۋاقتىدا اﷲ ئېيتتى: ئى مەريەم ئوغلى ئىسا! ساڭا ۋە سېنىڭ ئاناڭغا بەرگەن نېمىتىمنى ئەسلىگىن، ئەينى زاماندا ساڭا روھۇلقۇدۇس (يەنى جىبرىئىل) بىلەن مەدەت بەردىم، (كىچىكىڭدە) بۆشۈكتە ۋە (پەيغەمبەر بولغان) ئوتتۇرا ياش ۋاقتىڭدا كىشىلەرگە سۆزلەيتتىڭ، ئەينى زاماندا ساڭا كىتابنى، ھېكمەتنى، تەۋراتنى ۋە ئىنجىلنى ئۆگەتتىم، ئەينى زاماندا مېنىڭ ئىزنىم بىلەن لايدىن قۇشنىڭ شەكلىدە بىر نەرسە ياسايتتىڭ ـ دە، ئۇنى پۈۋلىسەڭ ئىزنىم بىلەن ئۇچىدىغان قۇش بولاتتى، مېنىڭ ئىزنىم بىلەن تۇغما كورنى، بەرەس كېسىلىنى ساقايتاتتىڭ، ئەينى زاماندا، مېنىڭ ئىزنىم بىلەن ئۆلۈكلەرنى (تىرىلدۈرۈپ قەبرىلەردىن) چىقىراتتىڭ، ئەينى زاماندا بەنى ئىسرائىلنى ساڭا چېقىلىشتىن توستۇم، سەن ئۇلارغا مۆجىزىلەر بىلەن كەلگەن چېغىڭدا، ئۇلارنىڭ ئىچىدىكى كاپىرلار: «بۇ پەقەت روشەن سېھىردۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,111,' ئەينى زاماندا مەن ھەۋارىلارغا: «ماڭا ۋە مېنىڭ پەيغەمبىرىمگە ئىمان ئېيتىڭلار» دەپ ۋەھيى قىلدىم ئۇلار: «بىز ئىمان ئېيتتۇق، گۇۋاھ بولغىنكى، بىز (ئەمرىڭگە) بويسۇنغۇچىلارمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,112,' ئۆز ۋاقتىدا ھەۋارىلار: «ئى مەريەم ئوغلى ئىسا! رەببىڭ بىزگە ئاسماندىن ئۈستىدە تاماق بار داستىخان چۈشۈرۈپ بېرەلەمدۇ؟» دېدى ئىسا: «ئەگەر (اﷲ نىڭ قۇدرىتىگە) ئىشەنسەڭلار (مۇنداق سوئاللارنى بېرىشتە) اﷲ تىن قورقۇڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,113,' ئۇلار: «بىز ئۇنىڭدىن يېيىشنى، كۆڭلىمىزنىڭ تىنچلىنىشىنى، سۆزۈڭنىڭ راستلىقىنى بىلىشنى ۋە پەيغەمبەرلىكىڭگە گۇۋاھچىلاردىن بولۇشنى ئىرادە قىلىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,114,' مەريەم ئوغلى ئىسا: «ئى پەرۋەردىگارىمىز اﷲ! بىزگە ئۈستىدە تاماق بار داستىخان چۈشۈرگىن، بۇ كۈن بىزلەرگە ۋە بىزدىن كېيىنكىلەرگە بايرام بولۇپ قالسۇن، ئۇ داستىخان سەندىن بىزگە (قۇدرىتىڭنى ۋە پەيغەمبەرلىكىمنى كۆرسىتىدىغان) بىر مۆجىزە بولۇپ قالسۇن بىزگە رىزىق بەرگىن، سەن رىزىق بەرگۈچىلەرنىڭ ياخشىسىسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,115,' اﷲ (ئىسانىڭ دۇئاسىنى ئىجابەت قىلىپ): «ئۇنى مەن سىلەرگە چوقۇم (ئاسماندىن) چۈشۈرىمەن، سىلەردىن كىمكى شۇنىڭدىن كېيىن كاپىر بولسا، ئۇنى شۇنداق بىر ئازاب بىلەن ئازابلايمەنكى، ئەھلى جاھاندىن ھېچ كىشىنى مۇنداق ئازابلىمايمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,116,' ئۆز ۋاقتىدا اﷲ ئېيتتى: «ئى مەريەم ئوغلى ئىسا! سەن كىشىلەرگە، اﷲ نى قويۇپ مەن بىلەن ئانامنى ئىككى ئىلاھ قىلىۋېلىڭلار، دېدىڭمۇ؟» ئىسا ئېيتتى: «(رەببىم!) شەنىڭگە لايىق ئەمەس نەرسىلەردىن سېنى پاك دەپ ئېتىقاد قىلىمەنكى، ماڭا ئېيتىشقا تېگىشلىك بولمىغان سۆزلەرنى مەن ئېيتمايمەن، ئەگەر مەن بۇ سۆزنى ئېيتقان بولسام، ئۇنى سەن چوقۇم بىلىسەن (يەنى مېنىڭ ئۇنداق دېمىگەنلىكىم ساڭا مەلۇملۇق) سەن مېنىڭ زاتىمدىكىنى بىلىسەن، مەن سېنىڭ زاتىڭدىكىنى بىلمەيمەن، سەن غەيبلەرنى ناھايىتى ئوبدان بىلىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,117,' مەن ئۇلارغا پەقەت سەن مېنى ئېيتىشقا بۇيرۇغان سۆزنى، يەنى، مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار بولغان اﷲ قا ئىبادەت قىلىڭلار، دېدىم مەن ئۇلارنىڭ ئارىسىدا بولغان مۇددەتتە، ئۇلارنىڭ ئەمەللىرىنى كۆزىتىپ تۇرغان ئىدىم، مېنى قەبزى روھ قىلغىنىڭدىن كېيىن، ئۇلارنىڭ ئەمەللىرىنى سەن كۆزىتىپ تۇرغان ئىدىڭ، سەن ھەممە نەرسىدىن خەۋەردارسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,118,' ئەگەر ئۇلارغا ئازاب قىلساڭ، ئۇلار سېنىڭ بەندىلىرىڭدۇر (ئۇلارنى خالىغىنىڭچە تەسەررۇپ قىلىسەن)، (ساڭا ھېچ ئەھەدى تەئەررۇز قىلالمايدۇ)، ئەگەر ئۇلارغا (يەنى ئۇلارنىڭ ئىچىدىكى تەۋبە قىلغانلارغا) مەغپىرەت قىلساڭ، سەن (ئىشىڭدا) غالىب، ھېكمەت بىلەن ئىش قىلغۇچىدۇرسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,119,' اﷲ ئېيتىدۇ: «بۇ كۈن (يەنى قىيامەت كۈنى) راستچىللارنىڭ راستچىللىقى ئۆزىگە پايدا قىلىدىغان كۈندۇر، ئۇلار ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردە مەڭگۈ قالىدۇ، اﷲ ئۇلاردىن رازى بولىدۇ، ئۇلار اﷲ تىن مەمنۇن بولىدۇ، بۇ زور بەختتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,5,120,' ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلاردىكى پۈتۈن مەۋجۇداتنىڭ پادىشاھلىقى اﷲ قا مەنسۇپتۇر، اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,1,'جىمى ھەمدۇسانا ئاسمانلارنى ۋە زېمىننى ياراتقان، زۇلمەتلەرنى ۋە نۇرنى پەيدا قىلغان اﷲ قا خاستۇر! ئاندىن (يەنى مۇشۇنداق دەلىللەرنى كۆرۈپ تۇرۇپ) كاپىرلار پەرۋەردىگارىغا باشقىلارنى شېرىك كەلتۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,2,' اﷲ سىلەرنى (يەنى سىلەرنىڭ ئاتاڭلار ئادەمنى) لايدىن ياراتتى، ئاندىن ئەجىلىڭلارنى بېكىتتى، (سىلەرنىڭ تىرىلىدىغان) ۋاقتىڭلار اﷲ نىڭ دەرگاھىدا مەلۇمدۇر، ئاندىن سىلەر ـ ئى كۇففارلار! (ئۆلگەندىن كېيىن تىرىلىشكە) شەك كەلتۈرىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,3,' ئاسمانلاردا ۋە زېمىندا (ئىبادەت قىلىنىشقا تېگىشلىك) اﷲ ئەنە شۇدۇر، ئۇ سىلەرنىڭ يوشۇرۇن ۋە ئاشكارا ئىشىڭلارنى، (ياخشى ـ يامان) قىلمىشىڭلارنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,4,' ئۇلار پەرۋەردىگارىنىڭ ئايەتلىرىدىن بىرەر ئايەت كەلسىلا ئۇنىڭدىن باش تارتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,5,' ئۇلارغا (اﷲ تەرىپىدىن) كىتاب (يەنى قۇرئان) كېلىۋېدى، ئۇلار ئۇنىڭغا چىنپۈتمىدى، ئۇزاققا قالماي ئۇلارغا ئۆزلىرى مەسخىرە قىلغان نەرسىنىڭ خەۋەرلىرى كېلىدۇ (يەنى ئۇلارغا ئازاب نازىل بولۇپ، ئۆزلىرى مەسخىرە قىلغان نەرسىنىڭ خەۋىرى ئېنىق بولىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,6,' ئۇلاردىن بۇرۇن ئۆتكەن نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار كۆرمىدىمۇ؟ (يەنى ئۇنىڭدىن ئىبرەت ئالمىدىمۇ؟) ئۇلارنى زېمىندا سىلەرنى يەرلەشتۈرمىگەن بىر شەكىلدە يەرلەشتۈرگەن ئىدۇق، ئۇلارغا مول يامغۇرلار ياغدۇرۇپ بەرگەن ئىدۇق، ئۆستەڭلارنى ئۇلارنىڭ ئاستىدىن ئېقىپ تۇرىدىغان قىلغان ئىدۇق، ئاندىن كېيىن ئۇلارنى قىلغان گۇناھلىرى تۈپەيلىدىن ھالاك قىلدۇق، ئۇلاردىن كېيىن باشقا خەلقنى پەيدا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,7,' (ئى مۇھەممەد!) بىز ساڭا (ئۇلار تەلەپ قىلغاندەك) قەغەزگە يېزىلغان بىر كىتابنى نازىل قىلغان، كاپىرلار ئۇنى قوللىرى بىلەن تۇتۇپ كۆرگەن تەقدىردىمۇ، ئۇلار، شەكسىزكى، بۇ پەقەت روشەن سېھىردۇر، دەيتتى، چۈنكى ئۇلارنىڭ غەرىزى ئەڭ روشەن دەلىل كەلگەندىمۇ ئىمان ئېيتماسلىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,8,' ئۇلار: «ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىگە گۇۋاھ بولۇپ) نېمىشقا بىر پەرىشتە چۈشمىدى؟» دەيدۇ، ئەگەر ئۇنىڭغا بىرەر پەرىشتە چۈشۈرگەن بولساق، ئۇلارنىڭ ئىشى تۈگىگەن بولاتتى (يەنى بۇ چاغدا ھەقنى ئىنكار قىلسا، ھالاك قىلىناتتى)، ئۇلارغا مۆھلەت بېرىلمەيتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,9,' بىز پەيغەمبەرنى پەرىشتىدىن قىلغان تەقدىردىمۇ، ئەلۋەتتە، ئۇنى ئىنسان شەكلىدە قىلاتتۇق، ئۇلارنى يەنە شۈبھىگە چۈشۈرەتتۇق (يەنى ئۇلار پەرىشتىنى ئىنسان شەكلىدە كۆرگەندە: «بۇ ئىنسان، ئەمما پەرىشتە ئەمەس» دەيتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,10,' سەندىن ئىلگىرى ئۆتكەن پەيغەمبەرلەر (كاپىرلار تەرىپىدىن) مەسخىرە قىلىندى، مەسخىرە قىلغۇچىلار مەسخىرە قىلغانلىقلىرىنىڭ ئاقىۋېتىنى كۆردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,11,' (ئى مۇھەممەد! بۇ مەسخىرە قىلغۇچىلارغا) ئېيتقىنكى، «زېمىندا سەير قىلىڭلار، ئاندىن پەيغەمبەرلىرىنى ئىنكار قىلغانلارنىڭ ئاقىۋېتىنىڭ قانداق بولغانلىقىنى كۆزدىن كەچۈرۈڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,12,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئاسمانلاردىكى، زېمىندىكى مەۋجۇداتلار كىمنىڭ؟» (ئۇلارغا) ئېيتقىنكى، «اﷲ نىڭ»، اﷲ (ئېھسان قىلىش يۈزىسىدىن بەندىلىرىگە) رەھمەت قىلىشنى ئۆز ئۈستىگە ئالدى، ھېچقانداق گۇمان بولمىغان قىيامەت كۈنى اﷲ سىلەرنى ئەلۋەتتە يىغىدۇ (كۇفرىنى ئىختىيار قىلىشى بىلەن) ئۆزىگە ئۆزى زىيان سالغانلار ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,13,' كېچە ۋە كۈندۈزدە جىم تۇرغان (ياكى ھەرىكەتلىنىپ تۇرغان) شەيئىلەرنىڭ ھەممىسى اﷲ نىڭ مۈلكىدۇر، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,14,' (ئى مۇھەممەد!) بۇ مۇشرىكلارغا ئېيتقىنكى، «ئاسمانلارنى، زېمىننى يوقتىن بار قىلغۇچى اﷲ تىن غەيرىينى مەبۇد تۇتامدىم؟ اﷲ رىزىق بېرىدۇ، ئۆزى رىزىققا موھتاج ئەمەس» (ئى مۇھەممەد!) ئېيتقىنكى، «مەن (بۇ ئۈممەت ئىچىدە خۇدانىڭ ئەمرىگە) بويسۇنغۇچىلارنىڭ ئەۋۋىلى بولۇشقا بۇيرۇلدۇم (ماڭا دېيىلدىكى) سەن ھەرگىز مۇشرىكلاردىن بولمىغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,15,' (ئۇلارغا يەنە) ئېيتقىنكى، «ئەگەر مەن رەببىمگە ئاسىيلىق قىلسام (يەنى پەرۋەردىگارىمدىن غەيرىيگە ئىبادەت قىلسام)، بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىدىن ئەلۋەتتە قورقىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,16,' مۇشۇ كۈندە كىمكى ئازابتىن قۇتۇلىدىكەن، اﷲ ئۇنىڭغا رەھمەت قىلغان بولىدۇ، ئەنە شۇ روشەن نىجات تاپقانلىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,17,' ئەگەر اﷲ ساڭا بىرەر ياخشىلىق يەتكۈزسە (ئۇ چاغدا ئۇنىڭغا ھېچكىم ئارىلىشالمايدۇ)، چۈنكى اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,18,' اﷲ بەندىلىرىنىڭ ئۈستىدە قاھىردۇر (يەنى اﷲ بەندىلىرىنى تىزگىنلەپ تۇرغۇچىدۇر)، ئۇ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىدىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,19,' (ئى مۇھەممەد!) ئېيتقىنكى، «(مېنىڭ پەيغەمبەرلىكىمنىڭ راستلىقى ئۈچۈن) قايسى نەرسە ئەڭ چوڭ گۇناھ؟» ئېيتقىنكى ، «اﷲ مەن بىلەن سىلەرنىڭ ئاراڭلاردا گۇۋاھتۇر (ماڭا اﷲ نىڭ گۇۋاھلىقى كۇپايىدۇر) بۇ قۇرئان ماڭا سىلەرنى ۋە (قىيامەتكىچە) قۇرئان يەتكەن كىشىلەرنى ئاگاھلاندۇرۇش ئۈچۈن ۋەھيى قىلىندى (ئى مۇشرىكلار!) سىلەر اﷲ بىلەن باراۋەر باشقا ئىلاھ بار دەپ چوقۇم گۇۋاھلىق بېرەمسىلەر؟» (ئۇلارغا) ئېيتقىنكى ، «مەن (مۇنداق دەپ) گۇۋاھلىق بەرمەيمەن» (ئى مۇھەممەد!) ئېيتقىنكى، «ئۇ ھەقىقەتەن بىر ئىلاھتۇر، سىلەر شېرىك كەلتۈرگەن بۇتلاردىن مەن ھەقىقەتەن ئادا ـ جۇدامەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,20,' بىز كىتاب بەرگەن كىشىلەر (يەنى يەھۇدىيلار ۋە ناسارالار) ئۇنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) (تەۋرات ۋە ئىنجىلدىكى سۈپىتى بويىچە) خۇددى ئۆزلىرىنىڭ ئوغۇللىرىنى تونۇغاندەك تونۇيدۇ، ئۆزلىرىگە ئۆزلىرى زىيان سالغانلار (ئېنىق پاكىتلار تۇرسىمۇ مۇھەممەد ئەلەيھىسسالامغا) ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,21,' اﷲ قا يالغان چاپلىغان ياكى اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلاردىنمۇ زالىم ئادەملەر بارمۇ؟ زالىملار ھەقىقەتەن مەقسىتىگە ئېرىشەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,22,' قىيامەت كۈنى ئۇلارنىڭ ھەممىسىنى (ھېساب ئېلىش ئۈچۈن) يىغىمىز، ئاندىن مۇشرىكلارغا: «اﷲ نىڭ شېرىكلىرى دەپ ئېتىقاد قىلغان مەبۇدلىرىڭلار قەيەردە؟» دەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,23,' ئاندىن ئۇلار پەقەت ئۆزرە ئېيتىپ (دۇنيادىكى چاغلىرىدىكىگە ئوخشاش يالغانغا ئىلتىجا قىلىپ): «پەرۋەردىگارىمىز! اﷲ بىلەن قەسەم قىلىمىزكى، بىز مۇشرىك بولمىغان ئىدۇق» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,24,' ئۇلارنىڭ (بىز مۇشرىك بولمىغان دەپ) ئۆزلىرىگە قارشى قانداق يالغان سۆزلىگەنلىكىگە قارىغىن، ئۇلارنىڭ (اﷲ نىڭ شېرىكلىرى دەپ) بوھتان چاپلىغان بۇتلىرى ئۇلاردىن قاچىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,25,' (ئى مۇھەممەد!) ئۇلارنىڭ ئىچىدە (قۇرئان ئوقۇغان چېغىڭدا) ساڭا قۇلاق سالىدىغانلار بار، قۇرئاننى چۈشەنمەسلىكلىرى ئۈچۈن ئۇلارنىڭ دىللىرىنى پەردىلىدۇق، قۇلاقلىرىنى ئېغىر قىلدۇق ئۇلار ھەممە مۆجىزىلەرنى كۆرگەن تەقدىردىمۇ ئۇنىڭغا ئىمان ئېيتمايدۇ ھەتتا كاپىرلار سېنىڭ يېنىڭغا مۇنازىرىلىشىپ كەلگەندىمۇ: «بۇ پەقەت بۇرۇنقىلاردىن قالغان ئەپسانىلاردۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,26,' ئۇلار كىشىلەرنى قۇرئاندىن (ۋە مۇھەممەد ئەلەيھىسسالامغا ئەگىشىشىتىن) توسىدۇ، ئۆزلىرىمۇ ئۇنىڭدىن يىراق قاچىدۇ، ئۇلار پەقەت ئۆزلىرىنىلا ھالاك قىلىدۇ، ھالبۇكى، ئۇلار (بۇنى) تۇيمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,27,' ئەگەر ئۇلارنى دوزاخ ئۈستىدە توختىتىلغان چاغدا كۆرسەڭ (دەھشەتلىك بىر ھالىنى كۆرەتتىڭ) ئۇلار: «كاشكى دۇنياغا قايتۇرۇلساق ئىدۇق، پەرۋەردىگارىمىزنىڭ ئايەتلىرىنى ئىنكار قىلمايتتۇق ۋە مۆمىنلەردىن بولاتتۇق» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,28,' ئۇنداق ئەمەس، ئۇلارنىڭ ئىلگىرى يوشۇرغان ئىشلىرى (قەبىھ قىلمىشلىرى تۈپەيلىدىن شۇنداق ئارزۇ قىلىشىدۇ) ئۇلارغا ئاشكارا بولدى، ئۇلار دۇنياغا قايتۇرۇلغان تەقدىردىمۇ مەنئى قىلىنغان ئىشلارنى ئەلۋەتتە يەنە قىلاتتى شۈبھىسىزكى، ئۇلار (ئىمان ئېيتتۇق دېگەن ۋەدىسىدە) يالغانچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,29,' ئۇلار: «ھاياتلىق پەقەت مۇشۇ دۇنيادىكى ھاياتىمىزدۇر، ئۆلگەندىن كېيىن تىرىلدۈرۈلمەيمىز» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,30,' ئەگەر ئۇلارنى پەرۋەردىگارىنىڭ ئالدىدا توختىتىلغان چاغدا كۆرسەڭ (غايەت چوڭ ئىشنى كۆرۈسەن) اﷲ ئۇلارغا: «بۇ (ئۆلگەندىن كېيىن تىرىلىش) راست ئەمەسمىكەن؟» دەيدۇ ئۇلار: «پەرۋەردىگارىمىز بىلەن قەسەمكى، راست ئىكەن» دەيدۇ اﷲ: «كاپىر بولغانلىقىڭلار ئۈچۈن ئازابنى تېتىڭلار» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,31,' اﷲ قا مۇلاقات بولۇشنى (يەنى قايتا تىرىلىشنى) ئىنكار قىلغانلار ھەقىقەتەن زىيان تارتتى قىيامەت تۇيۇقسىز كەلگەن چاغدا، ئۇلار: «ئۇ دۇنيادىكى بىپەرۋالىقىمىزدىن نادامەت» دەيدۇ گۇناھلىرىنى ئۈستىلىرىگە يۈكلىۋالىدۇ، ئۇلارنىڭ (گۇناھلارنى) يۈكلىگىنى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,32,' دۇنيا تىرىكچىلىكى پەقەت ئويۇن، غەپلەتتىنلا ئىبارەت، تەقۋادارلىق قىلىدىغانلار ئۈچۈن ئەلۋەتتە ئۇ دۇنيا ياخشىدۇر، (بۇنى) چۈشەنمەمسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,33,' ئۇلارنىڭ سۆزى سېنى قايغۇغا سالىدىغانلىقىنى بىز ئوبدان بىلىمىز، ئۇلار سېنى (ئىچىدە) ئىنكار قىلمايدۇ (لېكىن ئۇلار تەرسالىقتىن ئىنكار قىلىدۇ)، (ھەقىقەتتە) زالىملار اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,34,' سەندىن بۇرۇن ئۆتكەن پەيغەمبەرلەرمۇ ئىنكار قىلىندى ئۇلار ئىنكار قىلىنغانلىقىغا ۋە ئۆزلىرىگە يەتكەن ئەزىيەتلەرگە سەۋر قىلدى، ئاخىر ئۇلار بىزنىڭ ياردىمىمىزگە ئېرىشتى، اﷲ نىڭ سۆزلىرىنى ھەر قانداق ئادەم ئۆزگەرتەلمەيدۇ، شۈبھىسىزكى، پەيغەمبەرلەرنىڭ قىسسىلىرى ساڭا نازىل بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,35,' (ئى مۇھەممەد!) ئۇلارنىڭ (ئىسلامدىن) يۈز ئۆرۈگەنلىكى ساڭا ئېغىر كەلگەن بولسا، ئۇنداقتا يەر ئاستىغا كىرىشكە يول ياكى ئاسمانغا چىقىشقا شوتا تېپىپ بىر مۆجىزە كەلتۈرەلىسەڭ (كەلتۈرگىن)، ئەگەر اﷲ خالىسا، ئەلۋەتتە ئۇلارنى ھىدايەتكە بىرلەشتۈرەتتى، سەن ھەرگىز (اﷲ نىڭ ھېكمىتىنى) چۈشەنمەيدىغانلاردىن بولمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,36,' (سۆزۈڭگە) ھەقىقىي قۇلاق سالىدىغانلار (سېنىڭ دەۋىتىڭنى) قوبۇل قىلىدۇ (سۆزۈڭگە قۇلاق سالمىغانلار ئۆلۈكلەرنىڭ ئورنىدا)، ئۆلۈكلەرنى اﷲ تىرىلدۈرىدۇ، ئاندىن ئۇلار اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,37,' ئۇلار (يەنى مەككە كۇففارلىرى): «نېمىشقا مۇھەممەدكە پەرۋەردىگارىدىن بىر مۆجىزە چۈشۈرۈلمىدى؟» دەيدۇ، ئېيتقىنكى، «اﷲ مۆجىزە چۈشۈرۈشكە ھەقىقەتەن قادىر» لېكىن ئۇلارنىڭ تولىسى (مۆجىزە نازىل بولۇپ ئىمان ئېيتمىسا بالاغا دۇچار بولىدىغانلىقىنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,38,' مەيلى يەر يۈزىدە ماڭىدىغان ھايۋان بولسۇن، مەيلى ئىككى قانىتى بىلەن ئۇچىدىغان ئۇچار قۇش بولسۇن، ھەممىسى سىلەرگە ئوخشاش ئۈممەتلەردۇر (يەنى اﷲ تەرىپىدىن يارىتىلغان مەخلۇقلاردۇر) لەۋھۇلمەھپۇزدا ھېچ نەرسىنى چۈشۈرۈپ قويمىدۇق (يەنى ھەممىنى تولۇق پۈتتۇق)، كېيىن ئۇلار پەرۋەردىگارىنىڭ دەرگاھىغا يىغىلىدۇ (اﷲ ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,39,' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار گاسلاردۇر (يەنى قوبۇل قىلىش يۈزىسىدىن ئاڭلىمايدۇ)، گاچىلاردۇر (يەنى ھەقنى سۆزلىمەيدۇ) ئۇلار (كۇفرىنىڭ) قاراڭغۇلۇقى ئىچىدە قالغانلاردۇر، اﷲ خالىغان ئادەمنى ئازدۇرىدۇ، خالىغان ئادەمنى توغرا يولغا سالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,40,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئەگەر سىلەر راستچىل بولساڭلار ئېيتىپ بېقىڭلار، ئەگەر سىلەرگە اﷲ نىڭ ئازابى ياكى قىيامەت كەلسە، اﷲ تىن غەيرىيگە ئىلتىجا قىلامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,41,' ئۇنداق ئەمەس، سىلەر پەقەت اﷲ قا ئىلتىجا قىلىسىلەر، ئەگەر اﷲ خالىسا، سىلەر اﷲ نىڭ كۆتۈرۈۋېتىشىنى ئىلتىجا قىلغان بالانى اﷲ كۆتۈرۈۋېتىدۇ، سىلەر اﷲ نىڭ شېرىكلىرى قىلىۋالغان بۇتلارنى ئۇنتۇيسىلەر (يەنى بۇتلارغا ئىلتىجا قىلمايسىلەر)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,42,' شەك ـ شۈبھىسىزكى، سەندىن ئىلگىرىكى ئۈممەتلەرگە (نۇرغۇن پەيغەمبەرلەرنى) ئەۋەتتۇق (ئۇلار پەيغەمبەرلەرنى ئىنكار قىلدى) ئۇلارنى (اﷲ قا تەۋبە قىلىپ) يېلىنسۇن دەپ نامراتلىق ۋە ئاغرىق ـ سىلاق بىلەن جازالىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,43,' ئۇلارغا بىزنىڭ ئازابىمىز چۈشكەن چاغدا نېمىشقا يېلىنمىدى؟ لېكىن ئۇلارنىڭ دىللىرى قەساۋەتلەشتى (ئىمانغا يۇمشىمىدى)، شەيتان ئۇلارغا قىلمىشلىرىنى چىرايلىق كۆرسەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,44,' ئۇلار ئۆزلىرىگە قىلىنغان نەسىھەتنى ئۇنتۇغان چاغدا، ئۇلارغا (سىناش ئۈچۈن) پاراۋانلىقنىڭ ھەممە ئىشىكلىرىنى ئېچىۋەتتۇق، ئۇلار تاكى ئۆزلىرىگە بېرىلگەن نېمەتلەردىن خۇشال ـ خۇرام تۇرغاندا (ئۇلارنى ئۇشتۇمتۇت جازالىدۇق)، ئۇلار ھەسرەتتە قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,45,' زۇلۇم قىلغان قەۋمنىڭ يىلتىزى قۇرۇتۇلدى (يەنى ئۇلار پۈتۈنلەي ھالاك قىلىندى) جىمى ھەمدۇسانا (پەيغەمبەرلىرىگە ياردەم بېرىپ، كۇففارلارنى ھالاك قىلغانلىقى ئۈچۈن) ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,46,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ، ئەگەر اﷲ سىلەرنىڭ قۇلىقىڭلارنى گاس، كۆزۈڭلارنى كور قىلىپ قويسا، دىللىرىڭلارنى پېچەتلىۋەتسە (يەنى سىلەرنى ھېچ نەرسىنى چۈشەنمەيدىغان قىلىپ قويسا)، ئۇلارنى اﷲ تىن باشقا يەنە قايسى ئىلاھ ئەسلىگە كەلتۈرەلەيدۇ؟» قارىغىنكى، بىز (قۇدرىتىمىزنى كۆرسىتىپ بېرىدىغان) ئايەتلىرىمىزنى قانداق بايان قىلغانمىز، ئەمما ئۇلار (شۇ ئايەتلەردىن) يۈز ئۆرۈيدۇ (يەنى ئىمان ئېيتمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,47,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ، ئەگەر سىلەرگە اﷲ نىڭ ئازابى ئۇشتۇمتۇت ياكى ئاشكارا كەلسە، زالىم قەۋمدىن باشقا يەنە كىملەر ھالاك قىلىنىدۇ؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,48,' بىز پەيغەمبەرلەرنى پەقەت (ساۋاب بىلەن) خۇش خەۋەر بەرگۈچى، (ئازابتىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتىمىز، ئىمان ئېيتقان ۋە (ئەمەللىرىنى) تۈزىگەن كىشىلەرگە (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,49,' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار، (اﷲ قا) ئىتائەتسىزلىكلىرى تۈپەيلىدىن، ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,50,' (ئى مۇھەممەد! سەندىن ئادەتتىن تاشقىرى ئىشلارنى تەلەپ قىلغۇچى كۇففارلارغا) ئېيتقىنكى، «مەن سىلەرگە، مەندە اﷲ نىڭ خەزىنىلىرى بار، دېمەيمەن، مەن غەيبنىمۇ بىلمەيمەن، سىلەرگە، مەن بولسام ھەقىقەتەن بىر پەرىشتە، دەپمۇ ئېيتمايمەن، مەن پەقەت ماڭا قىلىنغان ۋەھيىگىلا ئەمەل قىلىمەن» ئېيتقىنكى، «كور ئادەم بىلەن كۆزى ساق ئادەم باراۋەرمۇ؟ (يەنى كاپىر بىلەن مۆمىن باراۋەرمۇ؟) ئويلاپ باقمامسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,51,' پەرۋەردىگارىنىڭ دەرگاھىغا يىغىلىشتىن قورقىدىغانلارنىڭ (كۇفرىدىن، گۇناھتىن) ساقلىنىشلىرى ئۈچۈن، ئۇلارنى قۇرئان بىلەن ئاگاھلاندۇرغىن، ئۇلارغا اﷲ تىن غەيرىي دوست ۋە شاپائەت قىلغۇچى يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,52,' اﷲ نىڭ رازىلىقىنى دەپ ئەتىگەندە، ئاخشامدا پەرۋەردىگارىغا ئىبادەت قىلىدىغانلارنى (يېنىڭدىن) قوغلىۋەتمىگىن، ئۇلارنىڭ ھېسابىدىن ساڭا ھېچ نەرسە يۈكلەنمەيدۇ (يەنى ئۇلارنىڭ قىلمىشلىرى ئۈچۈن سەن جاۋابكارلىققا تارتىلمايسەن) سېنىڭ ھېسابىڭدىنمۇ ئۇلارغا ھېچ نەرسە يۈكلەنمەيدۇ، ئۇلارنى قوغلىۋەتسەڭ، زالىملاردىن بولۇپ قالىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,53,' ئۇلارنىڭ: «اﷲ ئارىمىزدىن (ھىدايەت قىلىش بىلەن) ئىنئام قىلغان كىشىلەر مۇشۇلارمۇ؟» دېيىشلىرى ئۈچۈن، ئۇلارنى بىر ـ بىرى بىلەن مۇشۇنداق سىنىدۇق (يەنى پېقىر، ئاجىز كىشىلەرنى ئىمان ئېيتقۇزۇش بىلەن، باي، چوڭ كىشىلەرنى سىنىدۇق) اﷲ شۈكۈر قىلغۇچىلارنى ئوبدان بىلىدۇ ئەمەسمۇ؟ (يەنى شۈكۈر قىلغۇچىلارنى اﷲ ھىدايەت قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,54,' بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتقانلار يېنىڭغا كەلگەن چاغدا: «سىلەرگە ئامانلىق بولسۇن، پەرۋەردىگارىڭلار (بەندىلىرىگە) رەھمەت قىلىشنى (ئېھسان قىلىش يۈزىسىدىن) ئۆز ئۈستىگە ئالدى، سىلەردىن كىمكى بىلمەستىن بىرەر يامانلىق قىلىپ قويۇپ، ئاندىن كېيىن تەۋبە قىلسا ۋە (ئەمەلىنى) تۈزىسە، اﷲ ئەلۋەتتە ئۇنىڭغا مەغپىرەت قىلىدۇ ۋە رەھىم قىلىدۇ» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,55,' (بۇ سۈرىدە مۇشرىكلارنىڭ گۇمراھلىقىنى كۆرسىتىدىغان دەلىللەرنى بايان قىلغىنىمىزدەك) گۇناھكارلارنىڭ يولى ئاشكارا بولسۇن ئۈچۈن، ئايەتلىرىمىزنى روشەن بايان قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,56,' (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەر اﷲ نى قايرىپ قويۇپ ئىبادەت قىلغان بۇتلارغا ئىبادەت قىلىشتىن مەن ھەقىقەتەن مەنئى قىلىندىم» ئېيتقىنكى، «مەن سىلەرنىڭ نەپسى خاھىشىڭلارغا ئەگەشمەيمەن، بولمىسا مەن ئازغان بولىمەن ۋە ھىدايەت تاپقۇچىلاردىن بولالماي قالىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,57,' ئېيتقىنكى، «ھەقىقەتەن مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان روشەن دەلىلگە (يەنى قۇرئانغا ۋە ۋەھىگە) ئاساسلىنىمەن، ھالبۇكى، سىلەر ئۇنى ئىنكار قىلدىڭلار، سىلەر بالدۇر يۈز بېرىشىنى تەلەپ قىلغان ئازاب مېنىڭ ئىگىدارچىلىقىدا ئەمەس، بارچە ھۆكۈم اﷲ قا خاس، اﷲ ھەق ھۆكۈم قىلىدۇ، اﷲ ھۆكۈم قىلغۇچىلارنىڭ ياخشىسىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,58,' ئېيتقىنكى، «سىلەر بالدۇر يۈز بېرىشىنى تەلەپ قىلغان ئازاب مېنىڭ قولۇمدىن كەلسە ئىدى، ئۇ چاغدا مەن بىلەن سىلەرنىڭ ئاراڭلاردىكى ئىش چوقۇم ھۆكۈم قىلىنىپ بولغان بولاتتى (يەنى سىلەردىن ئارام تېپىش ئۈچۈن، سىلەرنى ھالاك قىلغان بولاتتىم)» اﷲ زالىملارنى ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,59,' غەيبنىڭ خەزىنىلىرى اﷲ نىڭ دەرگاھىدىدۇر، ئۇنى پەقەتلا اﷲ بىلىدۇ، قۇرۇقلۇقتىكى، دېڭىزدىكى نەرسىلەرنىڭ ھەممىسىنى اﷲ بىلىدۇ، (دەرەختىن) تۆكۈلگەن ياپراقتىن اﷲ بىلمەيدىغان بىرەرسىمۇ يوق، مەيلى قاراڭغۇ يەر ئاستىدىكى بىرەر دانە ئۇرۇق بولسۇن، مەيلى ھۆل ياكى قۇرۇق نەرسىلەر بولسۇن، ھەممىسى (اﷲ قا مەلۇم بولۇپ) لەۋھۇلمەھپۇزدا يېزىقلىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,60,' اﷲ سىلەرنى كېچىسى ئۇخلىتىدۇ، كۈندۈزدىكى قىلغان ئىشلىرىڭلارنى بىلىپ تۇرىدۇ، ئاندىن بەلگىلەنگەن مۇددەتكىچە ياشىشىڭلار ئۈچۈن، كۈندۈزدە سىلەرنى ئويغىتىدۇ، ئاندىن اﷲ نىڭ دەرگاھىغا قايتىسىلەر، ئاندىن قىلغان ئەمەللىرىڭلارنى اﷲ سىلەرگە ئېيتىپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,61,' اﷲ بەندىلىرىنىڭ ئۈستىدە قاھىردۇر (يەنى اﷲ بەندىلىرىنى تىزگىنلەپ تۇرغۇچىدۇر)، ئۇ سىلەرگە ساقلىغۇچى پەرىشتىلەرنى (يەنى قىلغان ـ ئەتكەننى خاتىرىلەپ تۇرىدىغان پەرىشتىلەرنى) ئەۋەتىدۇ، بېرىڭلارغا ئۆلۈم كەلسە، ئۇنى بىزنىڭ پەرىشتىلىرىمىز قەبزى روھ قىلىدۇ ئۇلار (اﷲ نىڭ ئەمرىنى ئورۇنلاشتا) بىپەرۋالىق قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,62,' ئاندىن ئۇلار (يەنى بەندىلەر) ھەق ھۆكۈم قىلغۇچى، ئىگىسى اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ (قىيامەت كۈنى) ھۆكۈم قىلىش يالغۇز اﷲ غىلا خاستۇر، اﷲ ئەڭ تېز ھېساب ئالغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,63,' (ئى مۇھەممەد! بۇ كۇففارلارغا) ئېيتقىنكى، «قۇرۇقلۇقنىڭ ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرى (يەنى بالايى ـ ئاپەتلىرى) دىن سىلەرنى كىم قۇتقۇزىدۇ؟ (بۇ قاراڭغۇلۇقلارنى كۆرگەن چېغىڭلاردا) ئەگەر اﷲ بىزنى بۇنىڭدىن قۇتقۇزسا چوقۇم شۈكۈر قىلغۇچىلاردىن بولىمىز، دەپ اﷲ قا ئاشكارا ۋە يوشۇرۇن ئىلتىجا قىلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,64,' ئېيتقىنكى، «(يالغۇز) اﷲ سىلەرنى ئۇلاردىن ۋە بارلىق غەمدىن قۇتقۇزىدۇ، ئاندىن سىلەر (بۇنى) تونۇغۇنۇڭلاردىن كېيىن، ئىمان ئېيتماي اﷲ قا شېرىك كەلتۈرىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,65,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «اﷲ سىلەرنىڭ ئۈستۈڭلاردىن (نۇھ ۋە لۇت ئەلەيھىسسالاملارنىڭ قەۋمىگە، ئەسھابۇلپىلگە ئەۋەتىلگەن ئازابقا ئوخشاش) ياكى ئايىغىڭلار ئاستىدىن (پىرئەۋن ۋە قارۇنلارغا ئەۋەتىلگەن ئازابقا ئوخشاش) ئازاب ئەۋەتىشكە، ياكى سىلەرنى پىرقىلەر قىلىپ، ئارىلاشتۇرۇپ (يەنى ئۇرۇشقا سېلىپ) بېرىڭلارغا بېرىڭلارنىڭ ئازابىنى تېتىتىشقا قادىردۇر» ئۇلارنى چۈشەنسۇن دەپ، ئايەتلىرىمىزنى تۈرلۈك شەكىللەردە قانداق بايان قىلىدىغانلىقىمىزغا قارىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,66,' قۇرئان ھەق تۇرسا، ئۇنى قەۋمىڭ ئىنكار قىلدى «مەن سىلەرگە ھامىي ئەمەس» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,67,' (اﷲ تەرىپىدىن) ئالدىنئالا بېرىلگەن ھەر بىر خەۋەرنىڭ مەيدانغا كېلىدىغان ۋاقتى بار، ئۇزاققا قالماي بىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,68,' بىزنىڭ ئايەتلىرىمىز توغرىسىدا (تەنە ۋە مەسخىرە قىلىپ) پاراڭ سېلىشىۋاتقان كىشىلەرنى كۆرگىنىڭدە، ئۇلار بۇنىڭدىن باشقا پاراڭغا چۈشمىگۈچە ئۇلار بىلەن بىللە ئولتۇرمىغىن، ئۇلار بىلەن بىللە ئولتۇرماسلىقنى شەيتان ساڭا ئۇنتۇلدۇرغان بولسا، ئېسىڭگە كەلگەندىن كېيىن، زالىم قەۋم بىلەن بىللە ئولتۇرمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,69,' تەقۋادارلارغا (قۇرئاننى مەسخىرە قىلغۇچى) كاپىرلارنىڭ ھېسابىدىن ھېچ نەرسە يۈكلەنمەيدۇ، لېكىن تەقۋادارلار ئۇلارغا ۋەز ـ نەسىھەت قىلىشلىرى كېرەك (شۇنداق قىلغاندا) ئۇلار (قۇرئاننى مەسخىرە قىلىشتىن) ساقلىنىشلىرى مۇمكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,70,' دىننى ئويۇن ـ كۈلكە قىلىۋالغان، دۇنيا تىرىكچىلىكىگە ئالدانغانلارنى تەرك ئەت ھەر قانداق ئادەمنىڭ ئۆز قىلمىشى تۈپەيلى ھالاك بولۇشتىن ساقلىنىشى ئۈچۈن، (كىشىلەرگە) قۇرئان بىلەن نەسىھەت قىلغىن، ئۇنىڭغا اﷲ تىن باشقا دوست ۋە شاپائەت قىلغۇچى بولمايدۇ، ئۇ ھەر قانداق فىدىيە بەرسىمۇ قوبۇل قىلىنمايدۇ ئەنە شۇلار قىلمىشلىرى تۈپەيلىدىن ھالاك بولىدىغانلاردۇر، ئۇلار كۇفرى تۈپەيلىدىن (ئۈچەيلىرىنى ئۆرتىۋېتىدىغان) قايناقسۇدىن بولغان شارابقا ۋە قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,71,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، بىز اﷲ نى قويۇپ، بىزگە پايدا ـ زىيان يەتكۈزەلمەيدىغان بۇتلارغا ئىبادەت قىلامدۇق؟ بىزنى اﷲ ھىدايەت قىلغاندىن كېيىن، ئارقىمىزغا چېكىنەمدۇق؟ (يەنى مۇرتەد بولامدۇق؟) (مۇبادا بىز شۇنداق قىلىدىغان بولساق، مۇنداق) بىر ئادەمگە: شەيتانلار ئۇنى بىر چۆلگە باشلاپ بارغان، (چۆلدە ئۇ) تېڭىرقاپ قالغان، دوستلىرى ئۇنى بېرى كەل دەپ توغرا يولغا چاقىرىۋاتقان (ئۇ چاقىرىقنى قوبۇل قىلماي ئاخىر ھالاك بولغان ئادەمگە) ئوخشاپ قالىمىز (بۇ كۇففارلارغا) ئېيتقىنكى، اﷲ نىڭ ھىدايىتى ھەقىقەتەن ھەقىقىي ھىدايەتتۇر، بىز ئالەملەرنىڭ پەرۋەردىگارىغا بويسۇنۇشقا بۇيرۇلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,72,' ناماز ئوقۇشقا ۋە اﷲ تىن قورقۇشقا بۇيرۇلدۇق، (قىيامەت كۈنى) اﷲ نىڭ دەرگاھىغا (ھېساب ئۈچۈن) يىغىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,73,' اﷲ ئاسمانلارنى، زېمىننى ھېكمەت بىلەن ياراتتى، اﷲ (بىرەر شەيئىگە) «ۋۇجۇدقا كەل» دېسە (دەرھال) ۋۇجۇدقا كېلىدىغان كۈندىكى (يەنى قىيامەت كۈنىدىكى) ئازابتىن قورقۇڭلار اﷲ نىڭ سۆزى ھەقتۇر، سۇر چېلىنىدىغان كۈندىكى (يەنى قىيامەت كۈنىدىكى) پادىشاھلىق اﷲ قا خاستۇر، اﷲ يوشۇرۇن ۋە ئاشكارا نەرسىلەرنى بىلگۈچىدۇر، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، (بەندىلىرىنىڭ ئەھۋالىدىن) خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,74,' ئۆز ۋاقتىدا، ئىبراھىم ئۆز ئاتىسى ئازەرگە: «(سەن ئۆزۈڭنى ياراتقان اﷲ نى قويۇپ) بۇتلارنى مەبۇد قىلىۋالامسەن؟ مەن سېنى ۋە قەۋمىڭنى ھەقىقەتەن ئېنىق گۇمراھلىقتا كۆرۈمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,75,' ئىبراھىمنىڭ (تەۋھىد بارىسىدا) قەتئىي ئىشەنگۈچىلەردىن بولۇشى ئۈچۈن، (ئۇنىڭغا ئاتىسى بىلەن قەۋمنىڭ گۇمراھلىقىنى كۆرسەتكىنىمىزدەك) بىز ئىبراھىمغا ئاسمانلارنىڭ، زېمىننىڭ ئاجايىپلىرىنى كۆرسەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,76,' ئىبراھىم كېچىنىڭ قاراڭغۇلۇقى باسقاندا بىر يۇلتۇزنى كۆرۈپ (قەۋمىنىڭ سۆزىنى دەلىل كەلتۈرۈپ، ئۇلارنى مات قىلىش ئۈچۈن): «بۇ مېنىڭ پەرۋەردىگارىمدۇر» دېدى يۇلتۇز پېتىپ كېتىۋىدى، «مەن پېتىپ كەتكۈچىلەرنى (يەنى يۇلتۇزلارغا ئىبادەت قىلىشنى) ياقتۇرمايمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,77,' ئۇ ئاينىڭ تۇغقانلىقىنى كۆرۈپ (يۇقىرىقى ئۇسۇل بويىچە): «بۇ مېنىڭ پەرۋەردىگارىمدۇر» دېدى ئاي پېتىپ كېتىۋىدى، «ئەگەر پەرۋەردىگارىم مېنى ھىدايەت قىلمىغان بولسا، مەن چوقۇم ئازغۇچى قەۋمنىڭ قاتارىدا بولىدىكەنمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,78,' ئۇ قۇياشنىڭ چىقىۋاتقانلىقىنى كۆرۈپ (يۇقىرىقى ئۇسۇل بويىچە): «بۇ مېنىڭ پەرۋەردىگارىمدۇر، بۇ ھەممىدىن (يەنى يۇلتۇز بىلەن ئايدىن) چوڭدۇر» دېدى قۇياش پېتىپ كېتىۋىدى، ئۇ ئېيتتىكى: «ئى قەۋمىم! مەن ھەقىقەتەن سىلەر شېرىك كەلتۈرگەن مەبۇدلاردىن ئادا ـ جۇدامەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,79,' مەن ھەقىقەتەن، باتىل دىنلاردىن ھەق دىنغا بۇرۇلۇپ، ئاسمانلارنى، زېمىننى ياراتقان زاتقا يۈزلەندىم، مەن مۇشرىكلاردىن ئەمەسمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,80,' ئىبراھىمنىڭ قەۋمى ئۇنىڭ بىلەن مۇنازىرىلەشتى، ئىبراھىم ئېيتتىكى، «مېنى اﷲ ھىدايەت قىلغان تۇرسا، مەن بىلەن اﷲ (نىڭ مەۋجۇتلۇقى ۋە بىرلىكى) توغرۇلۇق مۇنازىرىلىشەمسىلەر؟ مەن سىلەرنىڭ اﷲ قا شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن قورقمايمەن، پەقەت پەرۋەردىگارىمنىڭلا خالىغان نەرسىسى بولىدۇ، پەرۋەردىگارىمنىڭ بىلىمى ھەممە نەرسىنى ئۆز ئىچىگە ئالغان، (مەبۇدلىرىڭلارنىڭ ماڭا ھېچبىر زىيان يەتكۈزەلمەيدىغانلىقىنى) ئويلىمامسىلەر!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,81,' اﷲ نىڭ شېرىكى بولۇش ھەققىدە اﷲ سىلەرگە ھېچقانداق دەلىل چۈشۈرمىگەن بۇتلارنى اﷲ قا شېرىك كەلتۈرۈشتىن قورقمايۋاتساڭلار، سىلەرنىڭ شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن مەن قانداقمۇ قورقاتتىم! ئەگەر سىلەر بىلسەڭلار (ئېيتىڭلارچۇ)، بىز بۇ ئىككى گۇرۇھ ئادەمدىن زادى قايسىمىز ئەڭ قورقماسلىققا تېگىشلىك»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,82,' ئازابتىن ئەمىن بولۇش ئىمان ئېيتقان، ئىمانغا مۇشرىكلىكنى ئارىلاشتۇرمىغان ئادەملەرگە مەنسۇپتۇر، ئۇلار ھىدايەت تاپقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,83,' ئۆز قەۋمىگە رەددىيە بېرىش ئۈچۈن ئىبراھىمغا بەرگەن دەلىلىمىز ئەنە شۇدۇر، خالىغان كىشىمىزنى بىر قانچە دەرىجە كۆتۈرىمىز، پەرۋەردىگارىڭ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,84,' بىز ئىبراھىمغا ئىسھاقنى، يەئقۇبنى ئاتا قىلدۇق ئۇلارنىڭ ھەر بىرىنى ھىدايەت قىلدۇق (ئىبراھىمدىن) ئىلگىرى نۇھنى ھىدايەت قىلدۇق، ئىبراھىمنىڭ ئەۋلادىدىن داۋۇد، سۇلايمان، ئەييۇب، يۇسۇف، مۇسا، ھارۇنلارنى ھىدايەت قىلدۇق، ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,85,' زەكەرىيا، يەھيا، ئىسا ۋە ئىلياسلارنى ھىدايەت قىلدۇق، ئۇلارنىڭ ھەممىسى ياخشىلاردىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,86,' ئىسمائىل، ئىليەسە، يۇنۇس ۋە لۇتلارنى ھىدايەت قىلدۇق، ئۇلارنى (ئۆز زامانىدىكى) تامامى جاھان ئەھلىدىن ئۈستۈن قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,87,' ئۇلارنىڭ ئاتا ـ بوۋىلىرىدىن، ئەۋلادلىرىدىن ، قېرىنداشلىرىدىن بەزىلىرىنى ھىدايەت قىلدۇق، ئۇلارنى تاللىدۇق، ئۇلارنى توغرا يولغا يېتەكلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,88,' اﷲ نىڭ يولى ئەنە شۇدۇر، اﷲ بەندىلىرىدىن خالىغان كىشىلەرنى شۇ يولغا باشلايدۇ ئەگەر ئۇلار (يەنى شۇ پەيغەمبەرلەر) شېرىك كەلتۈرسە ئىدى، ئۇلارنىڭ قىلغان ياخشى ئەمەللىرى، ئەلۋەتتە، بىكار بولۇپ كېتەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,89,' ئۇلارغا كىتابنى، ھېكمەتنى ۋە پەيغەمبەرلىكنى ئاتا قىلدۇق، ئەگەر بۇ مۇشرىكلار ئۇلارنى (يەنى كىتاب، ھېكمەت ۋە پەيغەمبەرلىكنى) ئىنكار قىلسا، ئۇلارنى ئىنكار قىلمايدىغان باشقا بىر قەۋمگە تاپشۇرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,90,' ئەنە شۇلار (يەنى مەزكۇر پەيغەمبەرلەر) اﷲ ھىدايەت قىلغان كىشىلەردۇر، ئۇلارنىڭ يولىغا ئەگەشكىن، (ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، «قۇرئاننى تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، قۇرئان پەقەت پۈتۈن جاھان ئەھلى ئۈچۈن ۋەز ـ نەسىھەتتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,91,' ئۇلار اﷲ نى ھەقىقىي رەۋىشتە تونۇمىدى ئۇلار (يەنى يەھۇدىيلار) ئۆز ۋاقتىدا: «اﷲ ھېچ كىشىگە ھېچ نەرسە نازىل قىلمىدى» دېدى (يەنى ۋەھيىنى ۋە پەيغەمبەرلەرنى ئىنكار قىلدى) (ئى مۇھەممەد!) ئېيتقىنكى، «مۇسا ئېلىپ كەلگەن ۋە كىشىلەرگە نۇر، ھىدايەت بولغان كىتابنى كىم نازىل قىلدى؟ ئۇ كىتابنى پارچە ـ پارچە قەغەزلەرگە كۆچۈرۈپ، بىر قىسىمىنى ئاشكارىلاپ، كۆپ قىسىمىنى يوشۇرىسىلەر (ئى يەھۇدىيلار!) سىلەرگە ئۆزۈڭلار ۋە ئاتا ـ بوۋاڭلار بىلمىگەن نەرسىلەر بىلدۈرۈلدى، (بۇ قۇرئاننى) اﷲ (نازىل قىلدى)» ئاندىن، ئۇلارنى تەرك ئەتكىنكى، ئۇلار باتىللىرىدا (يەنى قالايمىقان سۆزلىرىدە) ئويناپ يۈرسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,92,' بۇ (قۇرئان) مەككە ئاھالىسىنى ۋە ئۇنىڭ ئەتراپىدىكىلەرنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن بىز نازىل قىلغان، ئۇنىڭدىن ئىلگىرى نازىل قىلىنغان (تەۋرات ۋە ئىنجىلغا ئوخشاش) كىتابلارنى ئېتىراپ قىلغۇچى مۇبارەك كىتابتۇر، ئاخىرەتكە ئىمان ئېيتىدىغانلار ئۇنىڭغا ئىمان ئېيتىدۇ، ئۇلار نامازلىرىنى ئۆز ۋاقتىدا تولۇق ئادا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,93,' اﷲ قا يالغان چاپلىغان ياكى ئۆزىگە ھېچ نەرسە ۋەھىيى قىلىنمىغان تۇرۇقلۇق، ماڭا ۋەھىيى قىلىندى دېگەن، ياكى اﷲ نازىل قىلغاندەك مەنمۇ كىتاب نازىل قىلالايمەن دېگەن كىشىلەردىنمۇ زالىم كىشىلەر بارمۇ؟ (ئى مۇھەممەد!) زالىملارنى سەكراتقا چۈشكەن ۋاقىتلىرىدا كۆرسەڭ (ئەلۋەتتە، قورقۇنچلۇق ھالىنى كۆرۈسەن)، پەرىشتىلەر (يەنى ئازاب پەرىشتىلىرى) قوللىرىنى سوزۇپ: «سىلەر جانلىرىڭلارنى چىقىرىڭلار! سىلەر اﷲ توغرىسىدا ھەقسىز سۆزلەرنى قىلغانلىقىڭلار (يەنى ئىختىرا قىلغانلىقىڭلار، شېرىك كەلتۈرگەنلىكىڭلار، بالا نىسبەتەن بەرگەنلىكىڭلار) ۋە اﷲ نىڭ ئايەتلىرىنى مەنسىتمىگەنلىكىڭلار ئۈچۈن، بۈگۈن خورلىغۇچى ئازاب بىلەن جازالىنىسىلەر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,94,' بىز دەسلەپتە سىلەرنى قانداق خەلق ئەتكەن بولساق دەرگاھىمىزغا شۇنداق تەنھا كەلدىڭلار، سىلەرگە بىز ئاتا قىلغان نەرسىلەرنى ئارقاڭلاردا (يەنى دۇنيادا) قويۇپ كەلدىڭلار، (ئىبادەت قىلىنىشقا تېگىشلىك بولۇشتا) اﷲ نىڭ شېرىكلىرى دەپ گۇمان قىلغان شاپائەتچىڭلارنى سىلەر بىلەن بىللە كۆرمەيمىزمۇ؟ ئۇلار بىلەن سىلەرنىڭ ئاراڭلاردىكى مۇناسىۋەت ھەقىقەتەن ئۈزۈلدى، سىلەر (شاپائەت قىلىدۇ) دەپ گۇمان قىلغان بۇتلار بەربات بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,95,' دانلارنى ۋە ئۇرۇقچىلارنى بىخ ئۇرغۇزغۇچى ھەقىقەتەن اﷲ دۇر، تىرىكنى ئۆلۈكتىن ۋە ئۆلۈكنى تىرىكتىن چىقارغۇچى ئەنە شۇ اﷲ دۇر، (اﷲ نىڭ ئىبادىتىدىن) قانداقمۇ باش تارتىسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,96,' اﷲ سۈبھىنى (قاراڭغۇدىن) يېرىپ چىقارغۇچىدۇر (يەنى تاڭنى يورۇتقۇچىدۇر)، اﷲ كېچىنى (سىلەر ئۈچۈن) ئىستىراھەت قىلىپ ياراتتى، (سىلەرنىڭ) ۋاقىت ھېسابلىشىڭلار ئۈچۈن قۇياش بىلەن ئاينى ياراتتى، بۇ، غالىب، ھەممىنى بىلگۈچى اﷲ نىڭ ئالدىنئالا بەلگىلىگەن ئىشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,97,' قۇرۇقلۇق ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرىدا (يەنە كېچىلىك يول يۈرگىنىڭلاردا) يول تېپىشىڭلار ئۈچۈن اﷲ سىلەرگە يۇلتۇزلارنى يارىتىپ بەردى (اﷲ نىڭ ئۇلۇغلۇقىنى) بىلىدىغان قەۋم ئۈچۈن، (اﷲ نىڭ قۇدرىتىنىڭ) دەلىللىرىنى ھەقىقەتەن ئوچۇق بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,98,' اﷲ سىلەرنى بىر ئىنساندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتتى، سىلەرنى ئاتاڭلارنىڭ پۇشتىدا، ئاناڭلارنىڭ بەچچىدانىدا قارارلاشتۇردى، چۈشىنىدىغان قەۋم ئۈچۈن ئايەتلەرنى ھەقىقەتەن ئوچۇق بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,99,' اﷲ بۇلۇتتىن يامغۇر سۈيىنى چۈشۈردى، شۇنىڭ بىلەن بارلىق ئۆسۈملۈكلەرنى ئۈندۈردى، ئۆسۈملۈكلەردىن يېشىل ياپراقلارنى چىقاردى، (بوغداي ۋە ئارپا باشاقلىرىغا ئوخشاش) بىر ـ بىرىگە مىنگىشىپ كەتكەن دانلارنى يېتىشتۈردى، خورما دەرىخىنىڭ چېچەكلىرىدىن يەرگە يېقىن ساڭگىلاپ تۇرىدىغان خورما ساپاقلىرىنى يېتىشتۈردى، (يامغۇر سۈيى بىلەن) ئۈزۈملۈك باغلارنى يېتىشتۈردى، (يامغۇر سۈيى بىلەن) شەكىلدە، ھەجىمدە، تەمدە بىر ـ بىرىگە ئوخشايدىغان ۋە ئوخشىمايدىغان زەيتۇن، ئانار دەرەخلىرىنى يېتىشتۈردى، (ئۇلارنىڭ ھەر بىرىنىڭ) يېڭى تۇتقان مېۋىسىگە ۋە پىشقان مېۋىسىگە قاراڭلار، ئۇلاردا اﷲ نىڭ بارلىقىغا ئىشىنىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) ئوچۇق دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,100,' جىنلارنى اﷲ ياراتقان تۇرسا، (مۇشرىكلار) جىنلارنى اﷲ قا (مۇشرىكلار ئىختىرا قىلغان نەرسىلەردىن) پاكتۇر، ئۇلارنىڭ سۈپەتلىگەنلىرىدىن ئۈستۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,101,' اﷲ ئاسمانلارنى ۋە يەرنى ئۆرنەكسىز ياراتقۇچىدۇر، اﷲ نىڭ خوتۇنى يوق تۇرسا، قانداقمۇ بالىسى بولسۇن؟ ھەممە نەرسىنى اﷲ ياراتقان، ھەممە نەرسىنى اﷲ ئوبدان بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,102,' ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، اﷲ تىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ ھەممە نەرسىنى ياراتقۇچىدۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، ئۇ ھەر نەرسىگە ھامىيدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,103,' كۆزلەر اﷲ نى كۆرمەيدۇ، اﷲ كۆزلەرنى كۆرۈپ تۇرىدۇ، اﷲ (بەندىلىرىگە) مېھرىباندۇر، ھەممىدىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,104,' (ئى پەيغەمبەر ئېيتقىنكى) سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن (ھىدايەتنى ئازغۇنلۇقتىن ئايرىپ كۆرسىتىدىغان) دەلىللەر كەلدى، كىمكى ھەقنى كۆرۈپ ئىمان ئېيتسا ئۆزىگە پايدا، كىمكى ئۇنىڭدىن كۆز يۇمىسا (يەنى باش تارتسا) ئۆزىگە زىيان مەن سىلەرگە (ئەمەلىڭلارنى كۆزىتىپ تۇرىدىغان) كۆزەتچى ئەمەسمەن (پەقەت سىلەرنى ئاگاھلاندۇرغۇچى پەيغەمبەرمەن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,105,' ئۇلار (يەنى مۇشرىكلار): (ئى مۇھەممەد!) سەن (قۇرئاننى باشقىلاردىن) ئۆگەندىڭ دەيدۇ دەپ، ھەقنى بىلىدىغان قەۋمگە بايان قىلىمىز دەپ، ئايەتلەرنى مۇشۇنداق شەكىللەردە بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,106,' (ئى مۇھەممەد!) ساڭا پەرۋەردىگارىڭ تەرىپىدىن ۋەھيى قىلىنغان قۇرئانغا ئەمەل قىلغىن؛ اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر، مۇشرىكلار بىلەن تەڭ بولمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,107,' ئەگەر اﷲ (ئۇلارنىڭ ھىدايەت تېپىشنى) خالىسا، ئۇلار شېرىك كەلتۈرمەيتتى، سېنى ئۇلارغا كۆزەتچى قىلغىنىمىز يوق، سەن ئۇلارغا ھامىيمۇ ئەمەسسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,108,' مۇشرىكلارنىڭ خۇدانى قويۇپ ئىبادەت قىلىدىغان بۇتلىرىنى تىللىماڭلار، (ئۇنداق قىلساڭلار) ئۇلار بىلمىگەنلىكلىرىدىن ھەددىدىن ئېشىپ اﷲ نى تىللايدۇ شۇنىڭدەك بىز ھەر ئۈممەتكە ئۆزلىرىنىڭ ئەمەلىنى چىرايلىق كۆرسەتتۇق، ئاندىن ئۇلارنىڭ قايتىپ بارىدىغان جايى اﷲ نىڭ دەرگاھىدۇر، اﷲ ئۇلارنىڭ قىلمىشلىرىنى ئۇلارغا ئېيتىپ بېرىدۇ (يەنى ياخشىغا مۇكاپات بېرىدۇ، ياماننى جازالايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,109,' ئەگەر ئۇلارغا بىرەر مۆجىزە كەلسە، ئۇنىڭغا چوقۇم ئىشىنىدىغانلىقلىرىنى (بىلدۈرۈپ) اﷲ نىڭ نامى بىلەن قاتتىق قەسەم ئىچىشىدۇ (ئى مۇھەممەد!) مۆجىزىلەر (مېنىڭ ئەمەس)، اﷲ نىڭ دەرگاھىدىكى ئىش، دېگىن (ئى مۆمىنلەر!) سىلەر نېمىنى بىلىسىلەر؟ مۆجىزىلەر كەلسە، ئۇلار، ئېھتىمال، ئۇنىڭغا ئىشەنمەسلىكلىرى مۇمكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,110,' ئۇلارنىڭ دىللىرىنى، كۆزلىرىنى ھەق (نى چۈشىنىش ۋە كۆرۈش) تىن بۇرىۋېتىمىز، ئۇلار دەسلەپتە مۆجىزىلەرگە ئىشەنمىگىنىدەك (يەنى ئىشەنمەيدۇ)، ئۇلارنى گۇمراھلىقلىرىدا تېڭىرقاپ يۈرۈشكە قويۇۋېتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,111,' بىز ئۇلارغا (يەنى كۇففارلارغا) پەرىشتىلەرنى چۈشۈرگەن، ئۆلۈكلەرنى ئۇلار بىلەن سۆزلەشتۈرگەن، بىز ھەممە نەرسىنى ئۇلارنىڭ ئالدىغا يىغىپ بەرگەن (يەنى ئۇلارنىڭ يۇقىرىقى تەلەپلىرىنى ئورۇنلىغان) تەقدىردىمۇ، اﷲ خالىمىغۇچە ئۇلار ئىمان ئېيتمايدۇ، لېكىن ئۇلارنىڭ تولىسى (بۇنى) ئۇقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,112,' شۇنىڭدەك (يەنى بۇ مۇشرىكلارنى ساڭا دۈشمەن قىلغاندەك) ئىنسانلار ۋە جىنلارنىڭ شەيتانلىرىنى ھەر بىر پەيغەمبەرگە دۈشمەن قىلدۇق، ئۇلار ئالداش ئۈچۈن شېرىن سۆزلەر بىلەن بىر ـ بىرىگە ۋەسۋەسە قىلىدۇ، ئەگەر پەرۋەردىگارىڭ خالىسا، ئۇلار بۇ ئىشنى (يەنى پەيغەمبەرلىرىگە دۈشمەنلىكنى) قىلمايتتى، ئۇلارنى ئۆزلىرى تۈزگەن ھىيلە ـ مىكىرلىرى بىلەن قويۇپ بەرگىن (اﷲ ساڭا ئۇلارغا قارشى ياردەم بېرىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,113,' ئاخىرەتكە ئىمان ئېيتمايدىغانلارنىڭ دىللىرى ئۇلارنىڭ شېرىن سۆزلىرىگە مايىل بولسۇن، ئۇنىڭدىن خۇرسەن بولسۇن ۋە ئۇلار قىلىۋاتقان گۇناھلارنى بۇلارمۇ قىلسۇن، دەپ ۋەسۋەسە قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,114,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى ،«اﷲ سىلەرگە روشەن كىتاب نازىل قىلغان تۇرسا، (ئارىمىزدىكى ئىختىلاپنى ھەل قىلىش ئۈچۈن) اﷲ تىن غەيرىي ھۆكۈم قىلغۇچىنى تەلەپ قىلامدىم؟» بىز كىتاب ئاتا قىلغانلار (يەنى يەھۇدىيلار ۋە ناسارالارنىڭ ئۆلىمالىرى) ئۇنىڭ (يەنى قۇرئاننىڭ) پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان ھەق كىتاب ئىكەنلىكىنى بىلىدۇ، سەن ھەرگىزمۇ شەك قىلغۇچىلاردىن بولمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,115,' پەرۋەردىگارىڭنىڭ سۆزى ناھايىتى راستتۇر ، ناھايىتى توغرىدۇر، ئۇنىڭ سۆزىنى ئۆزگەرتەلەيدىغان ھېچ كىشى يوقتۇر اﷲ (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,116,' ئەگەر سەن يەر يۈزىدىكى كۆپچىلىك كىشىلەر (يەنى كۇففارلار) گە ئىتائەت قىلساڭ، ئۇلار سېنى اﷲ نىڭ يولىدىن ئازدۇرىدۇ، ئۇلار پەقەت گۇمانغىلا ئەگىشىدۇ، ئۇلار پەقەت يالغاننىلا سۆزلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,117,' (ئى مۇھەممەد!) سېنىڭ پەرۋەردىگارىڭ ئۆزىنىڭ يولىدىن ئازغانلارنى ھەقىقەتەن ئوبدان بىلىدۇ، توغرا يول تاپقانلارنىمۇ ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,118,' اﷲ نىڭ ئايەتلىرىگە ئىمان كەلتۈرگۈچى بولساڭلار، اﷲ نىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان ھايۋانلارنى يەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,119,' زۆرۈرىيەت بولمىغۇچە يېيىش ھارام قىلىنغان نەرسىلەرنى اﷲ سىلەرگە ئوچۇق بايان قىلغان تۇرسا، اﷲ نىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان ھايۋانلارنى نېمىشقا يېمەيسىلەر؟ نۇرغۇن كىشىلەر نەپسى خاھىشىغا ئەگىشىپ، بىلمەستىن، (باشقىلارنى) ھەقىقەتەن ئازدۇرىدۇ، شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ (شەرئى دەلىلسىز ھالال، ھارام دەپ) ھەددىدىن ئاشقۇچىلارنى ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,120,' ئاشكارا ۋە يوشۇرۇن گۇناھلارنى تەرك ئېتىڭلار، شۈبھىسىزكى، گۇناھ قىلغۇچىلار ئۆز قىلمىشلىرى تۈپەيلىدىن (ئاخىرەتتە) جازاغا تارتىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,121,' (ئى مۆمىنلەر!) اﷲ نىڭ ئىسمى ئېيتىلماي بوغۇزلانغان (يەنى بىسمىللاە دەپ زەبھى قىلىنمىغان) ھايۋانلارنى يېمەڭلار، ئۇنى يېيىش ئەلۋەتتە گۇناھتۇر شۈبھىسىزكى، شەيتانلار ئۆز دوستلىرىنى (يەنى مۇشرىكلارنى) سىلەر بىلەن دەتالاش قىلىشقا ۋەسۋەسە قىلىدۇ، ئەگەر ئۇلارغا ئىتائەت قىلساڭلار، سىلەر چوقۇم مۇشرىك بولۇپ قالىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,122,' ئەسلىدە ئۆلۈك (يەنى كاپىر) بولۇپ، بىز ئۇنى (يەنى ئۇنىڭ قەلبىنى ئىمان بىلەن) تىرىلدۈرگەن، بىز ئۇنىڭغا كىشىلەر ئارىسىدا ھىدايەت تېپىشقا نۇر بەرگەن بىر ئادەم زۇلمەتتە (يەنى كۇفرىنىڭ قاراڭغۇلۇقىدا) قالغان ۋە ئۇنىڭدىن قۇتۇلالمىغان بىر ئادەم بىلەن ئوخشاشمۇ؟ كاپىرلارغا ئۇلارنىڭ قىلمىشلىرى ئەنە شۇنداق چىرايلىق كۆرسىتىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,123,' ھىيلە ـ مىكىر ئىشلىتىشلىرى ئۈچۈن (مەككىدە قۇرەيشنىڭ چوڭلىرىنى باش گۇناھكار قىلغىنىمىزدەك)، ھەر بىر شەھەرنىڭ چوڭلىرىنى باش گۇناھكار قىلدۇق، ئۇلارنىڭ ئىشلەتكەن ھىيلە ـ مىكىرلىرى ئۆزلىرىگە يانىدۇ (يەنى ئۇلار شۇ ھىيلە ـ مىكىرلەرنىڭ ۋابالىغا قالىدۇ)، ئۇلار (بۇنى) سەزمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,124,' ئۇلارغا (مۇھەممەد ئەلەيھىسسالامنىڭ راست پەيغەمبەرلىكىنى كۆرسىتىدىغان) بىرەر مۆجىزە كەلگەن چاغدا، (ئۇلار) بىزگە پەيغەمبەرلەرگە بېرىلگەن مۆجىزە بېرىلمىگۈچە (ئۇنىڭغا) ھەرگىز ئىشەنمەيمىز، دەيدۇ اﷲ پەيغەمبەرلىك ۋەزىپىسىنى قەيەرگە ئورنىتىشنى (يەنى كىمنىڭ ئۇنىڭ ئەھلى بولىدىغانلىقىنى) ئوبدان بىلىدۇ، (بۇ) گۇناھكارلار ئۆزلىرى ئىشلەتكەن ھىيلە ـ مىكىرلەر تۈپەيلىدىن اﷲ نىڭ دەرگاھىدا خورلۇققا ۋە قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,125,' اﷲ كىمنى ھىدايەت قىلماقچى بولسا، ئۇنىڭ كۆكسىنى ئىسلام ئۈچۈن ئاچىدۇ (يەنى ئۇنىڭ قەلبىنى ئىمانغا كېڭەيتىپ بېرىدۇ)، اﷲ كىمنى ئازدۇرماقچى بولسا، ئۇنىڭ كۆكسىنى شۇنچە تارايتىۋېتىدۇكى، (ئىمان ئېيتىش) ئۇنىڭغا گويا ئاسمانغا چىقىشتىنمۇ قىيىن تۇيۇلىدۇ، ئىمان ئېيتمىغانلارنى اﷲ مۇشۇنداق ئازابلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,126,' بۇ (يەنى ئىسلام دىنى) پەرۋەردىگارىڭنىڭ توغرا يولىدۇر، ئىبرەت ئالىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,127,' پەرۋەردىگارىنىڭ دەرگاھىدا ئۇلارغا دارى سالام (يەنى جەننەت) بار، ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرى ئۈچۈن اﷲ ئۇلارغا مەدەتكاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,128,' اﷲ (ئىنسانلارنى، جىنلارنى ھېساب ئۈچۈن) تولۇق يىغىدىغان كۈننى ئەسلىگىن، (بۇ كۈندە اﷲ ئېيتىدۇكى) «ئى جىنلار جامائەسى!سىلەر نۇرغۇن ئادەملەرنى ئازدۇردۇڭلار» ئۇلارنىڭ ئىنسانلاردىن بولغان دوستلىرى: «ئى پەرۋەردىگارىمىز! بىز بىر ـ بىرىمىزدىن پايدىلاندۇق، بىز ئەمدى سەن بەلگىلىگەن ۋاقىتقا (يەنى قىيامەت كۈنىگە) يەتتۇق» دەيدۇ، اﷲ: «دوزاخ سىلەرنىڭ جايىڭلاردۇر، سىلەر اﷲ نىڭ خاھىشىغا باغلىق ھالدا ئۇ يەردە مەڭگۈ قالىسىلەر» دەيدۇ شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,129,' شۇنىڭغا (يەنى ئىنسانلار بىلەن جىنلارنى ئۆزئارا پايدىلاندۇرغىنىمىزغا) ئوخشاش، قىلمىشلىرى تۈپەيلىدىن زالىملارنىڭ بەزىسىنى بەزىسىگە مۇسەللەت قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,130,' «ئى جىنلار ۋە ئىنسانلار جامائەسى! سىلەرگە ئۆز ئىچىڭلاردىن مېنىڭ ئايەتلىرىمنى بايان قىلىپ بېرىدىغان، بۇ كۈنۈڭلاردىكى ئۇچرىشىشتىن سىلەرنى ئاگاھلاندۇرىدىغان پەيغەمبەرلەر كەلمىدىمۇ؟» ئۇلار: «گۇناھلىرىمىزنى ئېتىراپ قىلدۇق» دەيدۇ ئۇلارنى دۇنيا تىرىكچىلىكى ئالدىغان، ئۇلار ئۆزلىرىنىڭ ھەقىقەتەن كاپىر بولغانلىقلىرىنى ئېتىراپ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,131,' بۇ شۇنىڭ ئۈچۈنكى، پەرۋەردىگارىڭ شەھەرلەرنىڭ ئاھالىسىنى (ئۇلارغا پەيغەمبەرلەر ئەۋەتىپ ئاگاھلاندۇرماستىن) غەپلەتتىكى ۋاقىتلىرىدا زۇلۇم قىلغانلىقلىرى تۈپەيلىدىن ھالاك قىلغىنى يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,132,' (ئاخىرەتتە) ھەر ئادەمنىڭ قىلغان ئەمەلىگە يارىشا دەرىجىسى بولىدۇ، پەرۋەردىگارىڭ ئۇلارنىڭ قىلغان ئىشلىرىدىن غاپىل ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,133,' پەرۋەردىگارىڭ (خالايىقتىن ۋە ئۇلارنىڭ ئىبادەتلىرىدىن) بىھاجەتتۇر، رەھمەت ئىگىسىدۇر ئەگەر ئۇ خالىسا، سىلەرنى ھالاك قىلىپ، سىلەرنى باشقا بىر قەۋمنىڭ ئەۋلادىدىن پەيدا قىلغاندەك، سىلەرنىڭ ئورنۇڭلارغا ئۆزى خالىغان (ئىتائەتمەن) باشقا بىر قەۋمنى قويىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,134,' سىلەرگە ۋەدە قىلىنغان (قىيامەت، ئۆلگەندىن كېيىن تىرىلىش قاتارلىق) ئىشلار چوقۇم مەيدانغا كېلىدۇ، سىلەر قېچىپ قۇتۇلالمايسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,135,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئى قەۋمىم! سىلەر كۈچۈڭلارنىڭ يېتىشىچە ئىشلەڭلار (يەنى كۇفرىڭلاردا چىڭ تۇرۇڭلار)، مەنمۇ چوقۇم كۈچۈمنىڭ يېتىشىچە ئىشلەيمەن (يەنى مەنمۇ ئۆز دەۋىتىمدە چىڭ تۇرىمەن) ئاخىرەتتە كىمنىڭ ئاقىۋېتى ياخشى بولىدىغانلىقىنى ئۇزۇنغا قالماي بىلىسىلەر، زالىملار ئەلۋەتتە مەقسىتىگە ئېرىشەلمەيدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,136,' مۇشرىكلار اﷲ ياراتقان ئېكىنلەردىن ۋە چاھار پايلاردىن (يەنى تۆگە، كالا، قويلاردىن) اﷲ ئۈچۈن بىر ھەسسە (بۇتلىرى ئۈچۈن بىر ھەسسە) ئايرىپ قويۇپ، ئۆز گۇمانلىرىچە (ھېچقانداق دەلىلسىزلا): «بۇ اﷲ ئۈچۈندۇر، بۇ مەبۇدلىرىمىز ئۈچۈندۇر» دېدى بۇتلىرىنىڭ نېسىۋېسىدىن اﷲ نىڭكىگە قوشۇشقا بولمايتتى، اﷲ نىڭ نېسىۋېسىدىن بۇتلىرىنىڭكىگە قوشۇشقا بولاتتى (مۇشرىكلار اﷲ قا ئاتىغان ھەسسىسىدىن بۇتلارغا ئاتىغان ھەسسىسىگە بىرەر نەرسە قوشۇلۇپ قالسا، ئۇنى بۇتلىرىنىڭ ھەسسىسى ئىچىدە قالدۇراتتى ۋە اﷲ باي، بۇنىڭغا موھتاج ئەمەس، دەيتتى بۇتلىرىغا ئاتىغان ھەسسىسىدىن اﷲ قا ئاتىغان ھەسسىسىگە بىرەر نەرسە قوشۇلۇپ قالسا، ئۇنى بۇتلىرىنىڭ ھەسسىسىگە قايتۇرۇۋېتەتتى) ئۇلارنىڭ قارارى نېمىدېگەن قەبىھ!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,137,' مۇشرىكلارنىڭ شېرىكلىرى (يەنى شەيتانلار) مۇشرىكلارنى ھالاك قىلىش ۋە ئۇلارنىڭ دىنىنى قالايمىقانلاشتۇرۇش ئۈچۈن، ئۇلارنىڭ نۇرغۇنلىرىغا بالىلىرىنى ئۆلتۈرۈشنى، شۇنىڭدەك (يەنى اﷲ قا ۋە بۇتلارغا ھەسسە ئايرىپ قويۇشتەك تەقسىماتنى شەيتانلارغا ۋە مۇشرىكلارغا چىرايلىق كۆرسەتكەندەك) چىرايلىق كۆرسەتتى ئەگەر اﷲ خالىغان بولسا ئىدى، ئۇلار بۇ (قەبىھ) ئىشنى قىلمايتتى، ئۇلارنى اﷲ قا چاپلىغان يالغانلىرى بىلەن بىللە تەرك ئەت');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,138,' ئۇلار ئۆزلىرىنىڭ گۇمانىچە: «بۇ ھايۋانلار ۋە ئېكىنلەر (مەبۇدلىرىمىزغا خاس بولۇپ، باشقىلارغا) ھارامدۇر، ئۇلارنى پەقەت بىز خالىغان ئادەملەرلا يەيدۇ، بۇ ھايۋانلارنى مىنىش ھارامدۇر، بۇ ھايۋانلارنى بوغۇزلىغاندا اﷲ نىڭ ئىسمىنى ئېيتىشقا بولمايدۇ» دېدى ئۇلار اﷲ قا يالغان چاپلىدى، اﷲ ئۇلارنى يالغان چاپلىغانلىقلىرى ئۈچۈن جازالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,139,' ئۇلار: «بۇ ھايۋانلارنىڭ قورساقلىرىدىكى بالىلىرى بىزنىڭ ئەر كىشىلىرىمىزگە خاستۇر، بىزنىڭ خوتۇن كىشىلىرىمىزگە ھارامدۇر» دېدى ئەگەر تۇغۇلغان ھايۋان ئۆلۈك بولسا، بۇ ئەر كىشىلەر بىلەن خوتۇن كىشىلەر ئارىسىدا ئورتاقتۇر (يەنى ئۇنى ھەممەيلەن تەڭ يەيدۇ) دەپ (ھالال ـ ھارامنى تەيىنلەشتى) اﷲ قا يالغان چاپلىغانلىقلىرى ئۈچۈن اﷲ ئۇلارنى جازالايدۇ شۈبھىسىزكى، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,140,' ھاماقەتلىك ۋە نادانلىقلىرىدىن بالىلىرىنى ئۆلتۈرگەن ۋە اﷲ نامىدىن يالغان ئېيتىپ، اﷲ رىزىق قىلىپ بەرگەن نەرسىلەرنى ھارام بىلگەن كىشىلەر ھەقىقەتەن زىيان تارتتى، ئۇلار ھەقىقەتەن (توغرا يولدىن) ئاداشتى، ھىدايەت تاپمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,141,' اﷲ بىدىشلىك ۋە بىدىشسىز باغلارنى بەرپا قىلدى، مېۋىلىرى، دانلىرى (رەڭگى، تەمى، ھەجمى ۋە پۇرىقى) بىر ـ بىرىگە ئوخشىمايدىغان خورما دەرەخلىرىنى، زىرائەتلەرنى، (رەڭگى ۋە شەكلى) بىر ـ بىرىگە ئوخشايدىغان، (تەمى) ئوخشىمايدىغان زەيتۇن ۋە ئانارنى ياراتتى، مېۋىسى پىشقاندا، ئۇلاردىن يەڭلار، مېۋە (نىڭ ھوسۇلى) نى يىغقان كۈندە (يەنى يىغقان ۋاقىتتا)، ئۇنىڭ ئۆشرىسىنى ئادا قىلىڭلار، ئىسراپ قىلماڭلار، اﷲ ھەقىقەتەن ئىسراپ قىلغۇچىلارنى ياقتۇرمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,142,' اﷲ چاھارپايلارنى (يەنى تۆگە، كالا، قويلارنى)، يۈك كۆتۈرىدىغانلارنى، بوغۇزلىنىدىغانلارنى ياراتتى اﷲ سىلەرگە رىزىق قىلىپ بەرگەن نەرسىلەردىن يەڭلار، شەيتاننىڭ ئارقىسىغا كىرمەڭلار، شەيتان سىلەرگە ھەقىقەتەن ئوچۇق دۈشمەندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,143,' (اﷲ سىلەرگە يېيىش ھالال قىلىنغان) سەككىز خىل چاھارپاينى ياراتتى: قويدىن ئىككى (قوچقار ۋە ساغلىق)، ئۆچكىدىن ئىككى (تېكە ۋە ئۇرغاچى) (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «اﷲ (قوي بىلەن ئۆچكىدىن) ئىككى ئەركەكنى ھارام قىلدىمۇ؟ ياكى ئىككى چىشىنىمۇ ۋە ياكى ئىككى چىشىنىڭ قورسىقىدىكى قوزا بىلەن ئوغلاقنىمۇ؟ ئەگەر (سىلەر اﷲ بۇلارنى بىزگە ھارام قىلدى دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، قېنى ماڭا بىلىپ ئېيتىپ بېرىڭلار!»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,144,' (اﷲ سىلەرگە) تۆگىدىن ئىككىنى (يەنى ئەركەك بىلەن چىشىنى)، كالىدىن ئىككىنى (يەنى ئەركەك بىلەن چىشىنى) ياراتتى، (مۇشرىكلارغا) ئېيتقىنكى، «اﷲ ئىككى ئەركەكنى ھارام قىلدىمۇ؟ ياكى ئىككى چىشىنىمۇ ۋە ياكى ئىككى چىشىنىڭ قورسىقىدىكى بوتىلاق بىلەن موزايلىنىمۇ ؟ ياكى اﷲ بۇنى سىلەرگە تەۋسىيە قىلغان چاغدا سىلەر ھازىرمىدىڭلار؟ كىشىلەرنى ئازدۇرۇش ئۈچۈن، بىلمەي تۇرۇپ اﷲ نامىدىن يالغان ئېيتقان ئادەمدىنمۇ زالىم كىشى بارمۇ؟ شۈبھىسىزكى، اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,145,' (ئى مۇھەممەد! مەككە كۇففارلىرىغا) ئېيتقىنكى، ساڭا ۋەھيى قىلىنغان ئەھكاملار ئىچىدە، ئۆزى ئۆلۈپ قالغان نەرسە ياكى ئېقىپ چىققان قان ۋە ياكى چوشقا گۆشى ـ چوشقا نىجىس يېمەكلىكلەرگە ئادەتلەنگەنلىكى ئۈچۈن چوشقا گۆشى پاسكىنىدۇر ـ ۋە اﷲ تىن غەيرىينىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان گۇناھ مالدىن غەيرىينى ھەر قانداق ئادەم ئۈچۈن ھارام كۆرمەيمەن كىمكى ئۇلاردىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) نائىلاج ۋە ئىختىيارسىز ھالدا ھاياتىنى ساقلاپ قالغۇدەك يېسە (ھېچ گۇناھ بولمايدۇ)، چۈنكى پەرۋەردىگارىڭ مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,146,' يەھۇدىيلارغا توم تۇياقلىق (يەنى تۆگە، تۆگىقۇشقا ئوخشاش ئاچا تۇياقلىق بولماي توم تۇياقلىق بولغان) ھايۋانلارنىڭ ھەممىسىنى ھارام قىلدۇق، ئۇلارغا كالا، قويلارنىڭ دۈمبىسىدىكى ياكى ئۈچەيلىرىدىكى ياكى سۆڭىكىگە چاپلىشىقلىق ياغلىرىدىن باشقا ياغلىرىنى ھارام قىلدۇق، بۇ ئۇلارنىڭ (پەيغەمبەرلەرنى ئۆلتۈرۈش، جازانە قىلىش، كىشىلەرنىڭ مال ـ مۈلكىنى ناھەق يەۋېلىش قاتارلىق) زۇلۇملىرى تۈپەيلىدىن، ئۇلارغا بەرگەن جازايىمىزدۇر، بىز ئەلۋەتتە (بەرگەن خەۋەرلىرىمىزدە) راستچىلمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,147,' (ئى مۇھەممەد!) ئەگەر ئۇلار (يەنى يەھۇدىيلار) سېنى ئىنكار قىلسا، سەن: «پەرۋەردىگارىڭلار (قىلمىشىڭلار ئۈچۈن، سىلەرنى ئالدىراپ جازالىمىغانلىقتىن) كەڭ رەھمەت ئىگىسىدۇر، (اﷲ نىڭ رەھمىتىگە مەغرۇر بولماڭلاركى) اﷲ نىڭ ئازابى گۇناھكار قەۋمدىن قايتۇرۇلمايدۇ» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,148,' مۇشرىكلار: «ئەگەر اﷲ خالىسا ئىدى، بىز ۋە ئاتا ـ بوۋىلىرىمىز شېرىك كەلتۈرمەيتتۇق ۋە ھېچ نەرسىنى ھارام قىلمايتتۇق» دەيدۇ ئۇلاردىن بۇرۇنقى كىشىلەر تاكى (بىزنىڭ ئازابىمىز نازىل بولۇپ) ئازابىمىزنى تېتىغانغا قەدەر (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) مۇشۇنداق (يەنى مۇشرىكلار سېنى ئىنكار قىلغاندەك) ئىنكار قىلغان ئىدى (ئۇلارغا) ئېيتقىنكى، «سۆزۈڭلارنىڭ راستلىقىغا پاكىتىڭلار بولسا، بىزگە چىقىرىپ كۆرسىتىڭلار، سىلەر پەقەت گۇمانغىلا ئاساسلىنىسىلەر، اﷲ قا پەقەت يالغاننى چاپلايسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,149,' (ئى مۇھەممەد!) ئېيتقىنكى، «(سىلەرنىڭ پاكىتىڭلار بولمىسا) اﷲ نىڭ ئېنىق پاكىتى بار، ئەگەر ئۇ خالىسا ھەممىڭلارنى، ئەلۋەتتە، ھىدايەت قىلاتتى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,150,' (ئى مۇھەممەد! ئۇلارغا) «اﷲ بۇنى ھارام قىلدى، دەپ گۇۋاھلىق بېرىدىغان گۇۋاھچىلىرىڭلارنى چاقىرىپ كېلىڭلار» دېگىن، ئەگەر ئۇلار (ھازىر بولۇپ يالغاندىن) گۇۋاھلىق بەرسە، ئۇلارغا قوشۇلۇپ گۇۋاھلىق بەرمىگىن (چۈنكى ئۇ پۈتۈنلەي يالغاندۇر) بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن، ئۇلار پەرۋەردىگارىڭغا شېرىك كەلتۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,151,' ئېيتقىنكى، «سىلەر كېلىپ (ئاڭلاڭلار)، اﷲ ھارام قىلغان نەرسىلەرنى سىلەرگە ئوقۇپ بېرەي: سىلەر اﷲ قا ھېچ نەرسىنى شېرىك كەلتۈرمەڭلار، ئاتا ـ ئاناڭلارغا ياخشىلىق قىلىڭلار، نامراتلىقتىن قورقۇپ بالاڭلارنى ئۆلتۈرمەڭلار، بىز ئۇلارغا ۋە سىلەرگە رىزىق بېرىمىز، ئاشكارا ۋە يوشۇرۇن يامان ئىشلارغا يېقىن كەلمەڭلار، الله نىڭ ئەمرىگە خىلاپلىق قىلىپ (ناھەق) ئادەم ئۆلتۈرمەڭلار، ھەقلىق رەۋىشتە ئۆلتۈرۈش بۇنىڭدىن مۇستەسنا سىلەرنىڭ چۈشىنىشىڭلار ئۈچۈن، اﷲ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,152,' يېتىمنىڭ مېلىنى تاكى ئۇ بالاغەتكە يەتكەنگە قەدەر ئۇنىڭغا ئەڭ پايدىلىق ئۇسۇلدا تەسەررۇپ قىلىڭلار، ئۆلچەمنى ۋە تارازىنى توغرا قىلىڭلار، ھېچقانداق ئادەمنى كۈچى يەتمەيدىغان ئىشقا تەكلىپ قىلمايمىز سۆز قىلغان (ھۆكۈم چىقارغان ياكى گۇۋاھلىق بەرگەن) چېغىڭلاردا ئادىل بولۇڭلار ، (ھۆكۈم چىقىرىلغان ياكى گۇۋاھلىق بېرىلگەن ئادەم) تۇققىنىڭلار بولغان تەقدىردىمۇ، اﷲ قا بېرىلگەن ئەھدىگە ۋاپا قىلىڭلار، ئىبرەت ئېلىشىڭلار ئۈچۈن، اﷲ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,153,' بۇ مېنىڭ توغرا يولۇمدۇر، شۇ يولدا مېڭىڭلار، ناتوغرا يوللاردا ماڭماڭلار، ئۇلار سىلەرنى اﷲ نىڭ يولىدىن ئايرىۋېتىدۇ، (اﷲ نىڭ ئەمرىنى تۇتۇپ دوزاختىن) ساقلىنىشىڭلار ئۈچۈن، اﷲ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,154,' ئاندىن ئۇلار (يەنى ئىسرائىل ئەۋلادى) ئۆز پەرۋەردىگارىغا مۇلاقات بولىدىغانلىقىغا ئىشەنسۇن دەپ، ياخشىلىق قىلغان ئادەمگە بېرىدىغان نېمىتىمىزنى مۇكەممەللەشتۈرۈش، (ئىسرائىل ئەۋلادىنىڭ دىنىي ئىشلىرىدا ئېھتىياجلىق بولغان) ھەر بىر نەرسىنى تەپسىلىي بايان قىلىش، (ئىسرائىل ئەۋلادىنى) ھىدايەت قىلىش ۋە (ئۇلارغا) رەھمەت قىلىش يۈزىسىدىن، مۇساغا كىتاب (يەنى تەۋرات) ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,155,' بۇ بىز نازىل قىلغان (قۇرئان) مۇبارەك كىتابتۇر اﷲ نىڭ رەھمىتىنى ئۈمىد قىلغۇچىلاردىن بولۇش ئۈچۈن، ئۇنىڭغا ئەگىشىڭلار، (ئۇنىڭغا مۇخالىپەتچىلىك قىلىشتىن) ساقلىنىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,156,' (مېنىڭ قۇرئان نازىل قىلىشىم) سىلەرنىڭ، پەقەت بىزدىن ئىلگىرىكى تائىپىگىلا (يەنى يەھۇدىيلار ۋە ناسارالارغىلا) كىتاب نازىل قىلىنغان، ئۇلارنىڭ ئوقۇغان كىتابلىرىدىن بىزنىڭ زادىلا خەۋىرىمىز يوق، دېمەسلىكىڭلار ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,157,' ياكى سىلەرنىڭ، ئەگەر بىزگە كىتاب نازىل قىلىنغان بولسا، بىز ئەلۋەتتە ئۇلاردىن ئارتۇق ھىدايەت تاپقان بولاتتۇق، دېمەسلىكىڭلار ئۈچۈندۇر سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن ھەقىقەتەن پاكىت، ھىدايەت ۋە رەھمەت نازىل بولدى، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغان ۋە ئۇلاردىن يۈز ئۆرۈگەن كىشىدىنمۇ زالىم ئادەم بارمۇ؟ بىزنىڭ ئايەتلىرىمىزدىن يۈز ئۆرۈگەنلەرنى ئۇلارنىڭ يۈز ئۆرۈگەنلىكلىرى ئۈچۈن قاتتىق ئازاب بىلەن جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,158,' ئۇلار پەقەت (ئۆلۈم) پەرىشتىلىرىنىڭ ياكى پەرۋەردىگارىڭنىڭ ياكى پەرۋەردىگارىڭنىڭ (قۇياشنىڭ مەغرىبتىن چىقىشىدىن ئىبارەت) بەزى ئالامەتلىرىنىڭ كېلىشىنىلا كۈتىدۇ پەرۋەردىگارىڭنىڭ بەزى ئالامەتلىرى كەلگەن كۈندە ئىلگىرى ئىمان ئېيتمىغانلارنىڭ ياكى ئىمان ئېيتىپ ياخشى ئىش قىلمىغانلارنىڭ ئېيتقان ئىمانى پايدىسىز بولىدۇ «(سىلەر ئۆزۈڭلارغا كېلىدىغان ئىشلارنى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە كۈتىمىز» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,159,' دىنىدا بۆلگۈنچىلىك قىلىپ تۈرلۈك پىرقىلەرگە ئايرىلغانلاردىن سەن ئادا ـ جۇداسەن؛ ئۇلارنىڭ ئىشى پەقەت اﷲ غىلا خاستۇر (يەنى اﷲ نىڭ باشقۇرۇشىدىدۇر)، كېيىن (يەنى ئاخىرەتتە) اﷲ ئۇلارنىڭ قىلمىشلىرىنى ئۆزىگە ئېيتىپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,160,' بىرەر ياخشىلىق قىلغان ئادەم ئون ھەسسە ئارتۇق ساۋابقا ئىگە بولىدۇ، بىرەر يامانلىق قىلغان ئادەمگە پەقەت ئۇنىڭ يامانلىقىغا باراۋەرلا جازا بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,161,' (ئى مۇھەممەد! بۇ مۇشرىكلارغا) ئېيتقىنكى، «پەرۋەردىگارىم مېنى توغرا يولغا يەنى توغرا دىنغا، باتىل دىنلاردىن توغرا دىنغا بۇرالغان ئىبراھىمنىڭ دىنىغا باشلىدى، ئىبراھىم مۇشرىكلاردىن ئەمەس ئىدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,162,' (ئى مۇھەممەد!) ئېيتقىنكى، «مېنىڭ نامىزىم، قۇربانلىقىم، ھاياتىم ۋە ماماتىم (دۇنيادا قىلغان ياخشىلىقلىرىم ۋە تائەت ـ ئىبادەتلىرىم) ئالەملەرنىڭ پەرۋەردىگارى اﷲ ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,163,' اﷲ نىڭ شېرىكى يوقتۇر، مەن مۇشۇنىڭغا (يەنى يالغۇز اﷲ غىلا خالىس ئىبادەت قىلىشقا) بۇيرۇلدۇم، مەن مۇسۇلمانلارنىڭ ئەۋۋىلىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,164,' (ئى مۇھەممەد!) ئېيتقىنكى، «اﷲ ھەممە نەرسىنىڭ پەرۋەردىگارى تۇرسا، ئۇنىڭدىن باشقا پەرۋەردىگار تەلەپ قىلامدىمەن؟» ھەر ئادەم ئۆزى قىلغان گۇناھقا ئۆزى جاۋابكار، بىر گۇناھكار ئادەم باشقا ئادەمنىڭ گۇناھىغا جاۋابكار ئەمەس (يەنى بىر ئادەمنىڭ گۇناھى بىلەن باشقا بىر ئادەم جازاغا تارتىلمايدۇ) كېيىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتىسىلەر، سىلەر دەتالاش قىلىشقان ھەق بىلەن ناھەقنى ئۇ سىلەرگە ئېيتىپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,6,165,' اﷲ سىلەرنى (ئۆتكەنكى ئۈممەتلەرنىڭ) ئورۇنباسارلىرى قىلدى، اﷲ سىلەرنى بەرگەن نېمىتىگە شۈكۈر قىلسۇن دەپ، بەزىڭلارنى بەزىڭلاردىن بىر قانچە دەرىجە يۇقىرى قىلدى، پەرۋەردىگارىڭ (ئاسىيلىق قىلغۇچىلاردىن) ئەلۋەتتە تېز ھېساب ئالغۇچىدۇر، (ئىتائەت قىلغۇچىلارنى) ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,1,'ئەلىف، لام، مىم، صاد');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,2,' (ئى مۇھەممەد! كاپىرلارنى) ئاگاھلاندۇرۇشۇڭ ئۈچۈن، مۆمىنلەرگە ۋەز ـ نەسىھەت قىلىشىڭ ئۈچۈن، بۇ كىتاب (يەنى قۇرئان) ساڭا نازىل قىلىندى، ئۇنىڭدىن (يەنى ئۇنى تەبلىغ قىلىشتا قەۋمىڭنىڭ ئۇنى ئىنكار قىلىشىدىن ئەنسىرەپ) يۈرىكىڭ سىقىلمىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,3,' (ئى ئىنسانلار!) سىلەر پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا) ئەگىشىڭلار، اﷲ نى قويۇپ، (جىنلاردىن ۋە ئىنسانلاردىن بولغان، سىلەرنى ئازدۇرىدىغان) دوستلارغا ئەگەشمەڭلار، سىلەر ۋەز ـ نەسىھەتنى ئاز قوبۇل قىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,4,' بىز نۇرغۇن شەھەر (ئاھالىسىنى) نى ھالاك قىلدۇق، بىزنىڭ ئازابىمىز ئۇلارغا كېچىسى ياكى چۈشلۈكى ئۇخلاۋاتقان چاغلىرىدا كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,5,' بىزنىڭ ئازابىمىز كەلگەن چاغدا، ئۇلارنىڭ چەككەن پەريادى: بىز ھەقىقەتەن زالىم ئىدۇق، دېيىشتىنلا ئىبارەت بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,6,' بىز ئەلۋەتتە پەيغەمبەر ئەۋەتىلگەن ئۈممەتلەردىن (پەيغەمبەرلەر تەبلىغ قىلدىمۇ؟ سىلەر قانداق جاۋاب بەردىڭلار؟ دەپ) سورايمىز، پەيغەمبەرلەردىنمۇ ئەلۋەتتە (اﷲ تاپشۇرغان ۋەزىپىنى ئادا قىلدىڭلارمۇ؟ دەپ) سورايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,7,' (قىيامەت كۈنى ئۇلارنىڭ ئەھۋالىنى) ئۇلارغا ھەقىقىي بىلىش ئاساسىدا ئېيتىپ بېرىمىز، بىز ئۇلاردىن ئايرىلمىغان ئىدۇق (يەنى ئۇلارنىڭ ھەممە ئەھۋالىدىن خەۋەرداردۇرمىز)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,8,'بۇ كۈندە بەندىلەرنىڭ (ياخشى يامان ئەمەللىرىنىڭ) ئۆلچىنىدىغانلىقى ھەقتۇر ياخشى ئەمەللىرى ئېغىر چىققانلار نىجات تاپقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,9,' ياخشى ئەمەللىرى يېنىك چىققانلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى تۈپەيلىدىن ئۆزلىرىگە زىيان سالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,10,' شۈبھىسىزكى، سىلەرنى زېمىندا يەرلەشتۈردۇق، زېمىندا سىلەرگە تۇرمۇش لازېمەتلىكلىرىنى پەيدا قىلدۇق، (سىلەرگە پەرۋەردىگارىڭلارنىڭ مۇشۇنداق نېمەتلىرى بار تۇرۇقلۇق ئۇنىڭغا) ناھايىتى ئاز شۈكۈر قىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,11,' شۈبھىسىزكى بىز سىلەرنى ياراتتۇق، ئاندىن سىلەرنى شەكىلگە كىرگۈزدۇق، ئاندىن پەرىشتىلەرگە: “سىلەر ئادەمگە (ھۆرمەت بىلدۈرۈش يۈزىسىدىن) سەجدە قىلىڭلار›› دېدۇق، ئىبلىستىن باشقىسى سەجدە قىلدى ئۇ سەجدە قىلغۇچىلاردىن بولمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,12,' اﷲ (ئىبلىسقا) ‹‹سېنى سەجدە قىلىشقا بۇيرۇغان چېغىمدا نېمىشقا سەجدە قىلمىدىڭ؟›› دېدى ئىبلىس: ‹‹مەن ئۇنىڭدىن ئارتۇق مېنى ئوتتىن، ئۇنى لايدىن ياراتتىڭ›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,13,' اﷲ (ئىبلىسقا) ئېيتتى: ‹‹سەن بۇيەردىن (يەنى جەننەتتىن) چۈشۈپ كەت، بۇ يەردىن ئۆزۈڭنى چوڭ تۇتۇشۇڭغا بولمايدۇ، يوقال، سەن ھەقىقەتەن پەسكەشلەردىنسەن››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,14,' ئىبلىس: ‹‹ماڭا كىشىلەر قايتا تىرىلدۈرۈلىدىغان كۈنگىچە (يەنى قىيامەت كۈنىگىچە) مۆھلەت بەرگىن›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,15,' اﷲ: ‹‹ساڭا مۆھلەت بېرىلىدۇ›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,16,' ئىبلىس ئېيتتى: ‹‹سېنىڭ مېنى ئازدۇرغانلىقىڭدىن ئۇلار (يەنى ئادەم ئەلەيھىسسالام ۋە ئۇنىڭ ئەۋلادىغا ۋەسۋەسە قىلىش) ئۈچۈن چوقۇم سېنىڭ توغرا يولۇڭ ئۈستىدە ئولتۇرىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,17,' ئاندىن ئۇلارغا چوقۇم ئالدىدىن، ئارقىسىدىن، ئوڭدىن، سولىدىن ھۇجۇم قىلىمەن، ئۇلارنىڭ كۆپچىلىكىنىڭ (نېمىتىڭگە) شۈكۈر قىلغۇچىلار ئەمەسلىكىنى كۆرىسەن››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,18,' اﷲ ئېيتتى: ‹‹جەننەتتىن ئەيىبلەنگەن ۋە (رەھمىتىمدىن) مەھرۇم قىلىنغان ھالدا چىق، ئۇلارنىڭ ئىچىدىن ساڭا ئەگەشكەنلەرنى قوشۇپ، ھەممىڭلار بىلەن چوقۇم جەھەننەمنى تولدۇرىمەن››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,19,' ‹‹ئى ئادەم! سەن ئايالىڭ بىلەن ئىككىڭلار بىللە جەننەتتە تۇرۇڭلار، جەننەتنىڭ مېۋىلىرىدىن خالىغىنىڭلارچە يەڭلار، بۇ دەرەخكە يېقىنلاشماڭلار، (ئۇنىڭغا يېقىنلاشساڭلار) زالىملاردىن بولۇپ قالىسىلەر››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,20,' لېكىن شەيتان ئۇلارنىڭ يېپىقلىق ئەۋرەتلىرىنى ئېچىۋېتىش ئۈچۈن، ئۇلارغا ۋەسۋەسە قىلدى، شەيتان ئېيتتى: ‹‹پەرۋەردىگارىڭلارنىڭ سىلەرگە بۇ دەرەخنىڭ مېۋىسىنى يېيىشنى مەنئى قىلغانلىقى پەقەت سىلەرنىڭ پەرىشتە بولۇپ كەتمەسلىكىڭلار ياكى (جەننەتتە) مەڭگۈ تۇرۇپ قالماسلىقىڭلار ئۈچۈندۇر››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,21,' شۇنىڭدەك ئۇلارغا: ‹‹مەن چوقۇم سىلەرگە سادىقمەن›› دەپ قەسەم ئىچىپ بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,22,' ئالدامچىلىق ئۇسۇللىرى ئارقىلىق ئۇلارنى (يۇقىرى مەرتىۋىدىن تۆۋەن مەرتىۋىگە) چۈشۈردى دەرەخنىڭ مېۋىسىنى يېگەندىن كېيىنلا ئۇلارنىڭ ئەۋرەتلىرى ئېچىلىپ قالدى، ئۇلار جەننەتتىكى دەرەخلەرنىڭ ياپراقلىرى بىلەن ئەۋرەتلىرىنى يېپىشقا كىرىشتى، ئۇلارنىڭ پەرۋەردىگارى (ئۇلارغا كايىپ): ‹‹مەن سىلەرگە بۇ دەرەخنى (يەنى مېۋىسىنى) يېيىشنى مەنئى قىلمىغانمىدىم؟ شۈبھىسىزكى، شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر، دېمىگەنمىدىم؟›› دەپ نىدا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,23,' ئۇلار: پەرۋەردىگارىمىز! بىز ئۆزىمىزگە ئۆزىمىز زۇلۇم قىلدۇق، ئەگەر سەن بىزگە مەغپىرەت قىلمىساڭ، بىزگە رەھىم قىلمىساڭ، بىز چوقۇم زىيان تارتقۇچىلاردىن بولىمىز›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,24,' اﷲ ئېيتتى: ‹‹سىلەر بىر ـ بىرىڭلارغا (يەنى شەيتان ئىنسانغا، ئىنسان شەيتانغا) دۈشمەن بولغان ھالدا چۈشۈڭلار، ئەجىلىڭلار پۈتكەنگە قەدەر يەر يۈزىدە تۇرىسىلەر ۋە ئۇنىڭدىن پايدىلىنىسىلەر››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,25,' اﷲ ئېيتتى: ‹‹يەر يۈزىدە ياشايسىلەر، يەر يۈزىدە ئۆلۈسىلەر، (قىيامەت كۈنى) يەنە يەر ئاستىدىن چىقىرىلىسىلەر››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,26,' ئى ئادەم بالىلىرى! سىلەرگە بىز ھەقىقەتەن ئەۋرىتىڭلارنى ياپىدىغان لىباسنى ۋە زىننەتلىنىدىغان لىباسنى چۈشۈردۇق، تەقۋادارلىق لىباسى ئەڭ ياخشىدۇر، ئەنە شۇلار (يەنى ئىنسان بالىلىرىغا سەترى ئەۋرەت ئۈچۈن لىباس ياراتماق) ئۇلارنىڭ ئىبرەت ئېلىشى ئۈچۈن اﷲ نىڭ (بەندىلىرىگە بولغان پەزلى ـ مەرھەمىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,27,' ئى ئادەم بالىلىرى! شەيتان سىلەرنىڭ ئاتا ـ ئاناڭلارنى (ئازدۇرۇپ) جەننەتتىن چىقىرىۋەتكەندەك، سىلەرنىمۇ ئازدۇرمىسۇن، شەيتان ئۇلارنىڭ ئەۋرەتلىرىنى ئۆزلىرىگە كۆرسىتىش ئۈچۈن كىيىملىرىنى سالغۇزۇۋەتكەن ئىدى (يەنى ئۇلارنىڭ يالىڭاچ بولۇپ قېلىشىغا شەيتان سەۋەبچى بولغان ئىدى) شەيتان ۋە ئۇنىڭ قول ئاستىدىكىلىرى سىلەرنى كۆرەلەيدۇ، سىلەر ئۇلارنى كۆرەلمەيسىلەر، شۈبھىسىزكى، بىز شەيتانلارنى ئىمان ئېيتمايدىغانلارنىڭ دوستى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,28,' ئۇلار (يەنى مۇشرىكلار) بىرەر يامان ئىشنى قىلغان چاغلىرىرىدا: ‹‹ئاتا ـ بوۋىلىرىمىزنىڭ مۇشۇنداق قىلغانلىقىنى كۆرگەن ئىدۇق، اﷲ بىزنىمۇ شۇنداق قىلىشقا بۇيرۇغان›› دەيدۇ (ئى مۇھەممەد! ئۇلارغا) ئېيىتقىنكى، ‹‹اﷲ (بەندىلىرىنى) ھەقىقەتەن يامان ئىشقا بۇيرۇمايدۇ، ئۆزلىرىڭلار بىلمەيدىغان نەرسىلەرنى اﷲ نامىدىن يالغان ئېيتامسىلەر؟››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,29,' (ئى مۇھەممەد!) ئېيىتقىنكى، «پەرۋەردىگارىم مېنى ئادىل بولۇشقا بۇيرۇدى ھەربىر سەجدە قىلغاندا پۈتۈنلەي اﷲ قا يۈزلىنىڭلار، اﷲ قا ئىخلاس بىلەن ئىبادەت قىلىڭلار، سىلەر اﷲ دەسلەپتە پەيدا قىلغان ھالىتىڭلاردا (ئەسلىگە) قايتىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,30,' اﷲ (سىلەردىن) بىر گۇرۇھنى ھىدايەت قىلدى؛ يەنە بىر گۇرۇھقا گۇمراھلىق تېگىشلىك بولدى شۈبھىسىزكى، ئۇلار اﷲ نى قويۇپ، شەيتانلارنى دوست تۇتقان تۇرۇقلۇق ئۆزلىرىنى توغرا يولدا گۇمان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,31,' ئى ئادەم بالىلىرى! ھەر ناماز ۋاقتىدا (ياكى تاۋاپ ۋاقتىدا ئەۋرىتىڭلارنى سەترى قىلىپ تۇرىدىغان) كىيىمىڭلارنى كىيىڭلار، يەڭلار، ئىچىڭلار، ئىسراپ قىلماڭلار، اﷲ ئىسراپ قىلغۇچىلارنى ھەقىقەتەن ياقتۇرمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,32,' ئېيىتقىنكى، «اﷲ بەندىلىرى ئۈچۈن ياراتقان لىباسلارنى، شېرىن، پاك رىزىقلارنى كىم ھارام قىلدى؟» ئېيىتقىنكى، ‹‹ئۇلار بۇ دۇنيادا مۆمىنلەر ئۈچۈن يارىتىلغان (گەرچە ئۇلارغا كۇففارلار شېرىك بولسىمۇ)، ئاخىرەتتە بولسا مۆمىنلەرگىلا خاستۇر» (اﷲ نىڭ بىر ۋە شېرىكى يوق ئىكەنلىكىنى بىلىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلى بايان قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,33,' (ئى مۇھەممەد! ئۇلارغا) ئېيىتقىنكى، «پەرۋەردىگارىم ئاشكارا ۋە يوشۇرۇن يامان ئىشلارنىڭ ھەممىسىنى، گۇناھلارنى، (كىشىلەرگە) ناھەق چېقىلىشنى، (اﷲ نىڭ شېرىكى بولۇشقا) اﷲ ھېچقانداق دەلىل چۈشۈرمىگەن نەرسىلەرنى اﷲ قا شېرىك كەلتۈرۈشنى، ئۆزۈڭلار بىلمەيدىغان نەرسىلەرنى اﷲ نامىدىن قالايمىقان سۆزلەشنى ھارام قىلدى››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,34,' (پەيغەمبەرلىرىنى ئىنكار قىلغان) ھەر بىر ئۈممەتنىڭ (ھالاك بولىدىغان) مۇئەييەن ۋاقتى بولىدۇ، (ھالاك بولىدىغان) ۋاقتى يەتكەندە، ئۇلار بىردەممۇ ئىلگىرى ـ كېيىن بولمايدۇ (يەنى دەرھال ھالاك بولىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,35,' ئى ئادەم بالىلىرى! ئىچىڭلاردىن مېنىڭ ئايەتلىرىمنى سىلەرگە بايان بېرىدىغان پەيغەمبەرلىرىم كەلگەندە، تەقۋادارلىق قىلغان ۋە (ئەمەلىنى) تۈزەتكەنلەرگە (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,36,' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار، ئۇنىڭغا ئىمان ئېيتىشقا گەدەنكەشلىك قىلغانلار دوزاخ ئەھلىدۇر، ئۇلار ھەمىشە دوزاختا قالغۇسىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,37,' اﷲ قا يالغان چاپلىغان ياكى ئۇنىڭ ئايەتلىرىنى ئىنكار قىلغانلاردىنمۇ زالىم كىشى بارمۇ؟ ئۇلار لەۋھۇلمەھپۇزدا يېزىلغان (رىزىقتىن ۋە ئەجەلدىن) ئۆز نېسىۋىسىنى كۆرىدۇ، ھەتتا بىزنىڭ پەرىشتىلىرىمىز ئۇلارنىڭ جانلىرىنى ئېلىش ئۈچۈن كەلگەندىمۇ، ئۇلارغا: ‹‹اﷲ نى قويۇپ ئىبادەت قىلغان مەبۇدلىرىڭلار قەيەردە؟›› دەيدۇ، ئۇلار: ‹‹ئۇلار بىزدىن غايىب بولۇپ كەتتى›› دەيدۇ ئۇلار كاپىر بولغانلىقلىرىغا ئۆزلىرىنڭ زىيىنىغا گۇۋاھلىق بېرىدۇ (يەنى كاپىر بولغانلىقلىرىغا ئىقرار قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,38,' اﷲ (ئايەتلىرىنى ئىنكار قىلغانلارغا) ئېيتىدۇ: ‹‹سىلەردىن ئىلگىرى ئۆتكەن، جىنلاردىن ۋە ئىنسانلاردىن بولغان (كاپىر) ئۈممەتلەر بىلەن بىرلىكتە دوزاخقا كىرىڭلار›› ھەرقاچان بىر ئۈممەت دوزاخقا كىرىدىكەن، ئۇ ئۆزىدىن ئىلگىرىكى ئۈممەتكە (ئۇنىڭغا ئەگىشىپ گۇمراھ بولغانلىقى ئۈچۈن) لەنەت ئوقۇيدۇ ئۇلارنىڭ ھەممىسى دوزاخقا كىرىپ بولۇپ جەملەشكەندە، ئۇلارنىڭ ئىچىدىكى كېيىن كىرگەنلەر (يەنى ئەگەشكۈچىلەر) ئىلگىرى كىرگەنلەرگە (يەنى باشلىقلىرىغا) قارىتىپ: ‹‹ئى پەرۋەردىگارىمىز! بۇلار بىزنى ئازدۇرغان ئۇلارغا ئىككى ھەسسە دوزاخ ئازابى بەرگىن›› دەيدۇ اﷲ: ‹‹ھەممىگە ئىككى ھەسسە ئازاب بېرىلىدۇ، لېكىن سىلەر (ئازابنىڭ دەھشىتىنى) بىلمەيسىلەر›› دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,39,' ئۇلارنىڭ ئارىسىدىكى ئىلگىرىكىلەر كېيىنكىلەرگە: ‹‹سىلەرنىڭ بىزدىن ھېچقانداق ئارتۇقچىلىقىڭلار يوق (يەنى گۇمراھلىقتا ۋەئازابقا تېگىشلىك بولۇشتا بىر ـ بىرىمىزگە ئوخشاشمىز)، قىلمىشلىرىڭلار تۈپەيلىدىن ئازابنى تېتىڭلار›› دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,40,' شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ئۇلارغا ئىمان ئېيتىشقا گەدەنكەشلىك قىلغانلارغا ئاسماننىڭ دەرۋازىسى ئېچىلمايدۇ (يەنى ئۇلارنىڭ ياخشى ئەمەلى ۋە دۇئاسى قوبۇل بولمايدۇ)، تاكى تۆگە يىڭنىنىڭ تۆشۈكىدىن ئۆتكۈچە ئۇلار جەننەتكە كىرمەيدۇ (يەنى ئۇلار ھەرگىزمۇ جەننەتكە كىرمەيدۇ)، گۇناھكارلارغا مۇشۇنداق جازا بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,41,' دوزاختا ئۇلارنىڭ ئاستىغا سالىدىغان كۆرپىسىمۇ ئوتتىن، ئۈستىگە يېپىنىدىغان يوتقىنىمۇ ئوتتىن بولىدۇ، زالىملارغا مۇشۇنداق جازا بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,42,' ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار ـ كىشىنى پەقەت كۈچى يېتىدىغان ئىشقىلا تەكلىپ قىلىمىز ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە ھەمىشە قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,43,' ئۇلارنىڭ كۆڭۈللىرىدىكى ئۆچمەنلىكلىرىنى ئېلىپ تاشلىدۇق (يەنى جەننەتكە كىرگەنلەر ئارىسىدا ئۆزئارا ئاغرىق ـ ئاداۋەت بولمايدۇ) ئۇلارنىڭ (سارايلىرى) ئاستىدىن (جەننەتنىڭ) ئۆستەڭلىرى ئېقىپ تۇرىدۇ، ئۇلار: «جىمى ھەمدۇسانا بىزنى بۇ (نېمەتلەرنى ھاسىل قىلىشقا) يېتەكلىگەن اﷲ قا بولسۇنكى، اﷲ بىزنى يېتەكلىمىگەن بولسا، توغرا يول تاپمىغان بولاتتۇق، پەرۋەردىگارىمىزنىڭ پەيغەمبەرلىرى بىزگە ھەقنى ئېلىپ كەلدى›› دەيدۇ، ئۇلارغا: «قىلغان ئەمەلىڭلار ئۈچۈن سىلەرگە بېرىلگەن جەننەت مانا مۇشۇ›› دەپ نىدا قىلىنىدۇ (يەنى پەرىشتىلەر شۇنداق دەپ نىدا قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,44,' ئەھلى دوزاخقا: ‹‹بىز پەرۋەردىگارىمىز بىزگە ۋەدە قىلغان نەرسىنى ھەق تاپتۇق، سىلەرمۇ پەرۋەردىگارىڭلار ۋەدە قىلغان نەرسىنى ھەق تاپتىڭلارمۇ؟›› دەپ توۋلايدۇ ئۇلار: ‹‹ھەئە›› دەيدۇ، شۇنىڭ بىلەن بىر جاكارچى ئۇلارنىڭ ئارىسىدا (مۇنداق دەپ) جاكارلايدۇ: ‹‹زالىملارغا اﷲ نىڭ لەنىتى بولسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,45,' زالىملار كىشىلەرنى اﷲ نىڭ داغدام يولىدىن توسىدۇ، ئۇنىڭ ئەگرى بولۇشىنى تىلەيدۇ ھەمدە ئۇلار ئاخىرەتكە ئىشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,46,' ئۇلارنىڭ ئارىسىدا بىر توسما بولىدۇ توسمىنىڭ ئۈستىدە (ئەئرافتا) ئەھلى جەننەت ۋە ئەھلى دوزاخنى ئۇلارنىڭ سىماسىدىن تونۇيدىغان ئادەملەر (يەنى ئەھلى ئەئراف) بولىدۇ، ئۇلار ئەھلى جەننەتكە: «سىلەرگە ئامانلىق تىلەيمىز›› دەپ توۋلايدۇ ئۇلار جەننەتكە كىرمىگەن، (ئەمما) كىرىش ئۈمىدىدە تۇرغانلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,47,' ئۇلارنىڭ كۆزلىرى ئەھلى دوزاخ تەرەپكە بۇرۇلغاندا: ‹‹ئى پەرۋەردىگارىمىز!بىزنى زالىم قەۋم بىلەن بىللە قىلمىغىن›› دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,48,' ئەئرافتىكىلەر (يەنى ياخشىلىقلىرى بىلەن يامانلىقلىرى تەڭ بولۇپ قالغان، جەننەتتىمۇ ئەمەس، دوزاختىمۇ ئەمەس كىشىلەر) سىماسىدىن تونۇيدىغان (دوزىخى) ئادەملەرگە: ‹‹توپلىغان پۇل ـ ماللىرىڭلار ۋە تەكەببۇرلۇقۇڭلار سىلەرگە نېمىگە ئەسقاتتى؟›› دەپ توۋلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,49,' (ئەئرافتىكىلەر دوزىخىلار مۆمىنلەرنىڭ پېقىرلىرىنى كۆرسىتىپ تۇرۇپ) سىلەر اﷲ نىڭ رەھمىتىگە ئېرىشەلمەيدۇ، دەپ قەسەم قىلغان كىشىلەر مۇشۇلارمۇ؟ (مانا بۇلارغا) جەننەتكە كىرىڭلار، سىلەرگە (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر (دېيىلدى دەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,50,' ئەھلى دوزاخ ئەھلى جەننەتكە: ‹‹سۇدىن ياكى اﷲ سىلەرگە بەرگەن ئىچىدىغان نەرسىلەردىن بىزگە بىر ئاز قۇيۇپ بەرسەڭلار!›› دەپ توۋلايدۇ، ئەھلى جەننەت ئۇلارغا (جاۋابەن): ‹‹اﷲ ئۇلارنى كاپىرلارغا ھارام قىلغان›› دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,51,' كاپىرلار دىنلىرىنى مەسخىرە ۋە ئويۇنچۇق قىلىۋالدى، ئۇلارنى دۇنيا تىرىكچىلىكى ئالدىدى، ئۇلارنىڭ بۈگۈنكى كۈنگە مۇلاقات بولۇشنى ئۇنتۇغانلىقلىرى ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرىغا ئوخشاش، بىز بۈگۈن ئۇلارنى ئۇنتۇيمىز (يەنى ئۇلارن دوزاختا قالدۇرىمىز)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,52,' ئۇلارغا كىتاب (يەنى قۇرئان) بەردۇق، ئۇنى (يەنى قۇرئاننىڭ مەنىللىرىنى) بىلش ئاساسىدا بايان ۋە رەھمەت قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,53,' ئۇلار (يەنى كۇففارلار) پەقەت (ئۆزلىرىگە ۋەدە قىلىنغان ئازابنىڭ) نەتىجىسىنىلا كۈتىدۇ، ئۇنىڭ نەتىجىسى كەلگەن كۈنى (يەنى قىيامەت كۈنى)، ئلگىرى كىتابقا ئەمەل قىلمىغانلار: ‹‹پەرۋەردىگارىمىزنىڭ ئەلچىللىرى ھەقىقەتنى ئېلىپ كەلگەن ئىدى (بىز ئۇلارغا ئىمان ئېيتمىدۇق، بۈگۈن) بىزگە شاپائەت قىلىدىغان شاپائەتچىلەر بارمىدۇ؟ ياكى گۇناھ قىلىشتىن قول ئۈزۈپ، ياخشى ئەمەللەرنى قىلىش ئۈچۈن دۇنياغا قايتۇرۇلۇشىمىزغا بولامدىغاندۇ›› دەيدۇ ئۇلار ئۆزلىرىگە زىيان سالدى، ئۇلارنىڭ اﷲ نىڭ شېرىكلىرى دەپ ئىپتىرا قىلغان مەبۇدلىرى ئۇلارنىڭ كۆزىدىن غايىب بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,54,' شۈبھىسىزكى، پەرۋەردىگارىڭلار اﷲ ئاسمانلارنى ۋە زېمىننى ئالتە كۈندە (يەنى ئالتە دەۋردە) ياراتتى، ئاندىن (ئۆزىنىڭ ئۇلۇغلۇقىغا لايىق رەۋىشتە) ئەرش ئۈستىدە قارار ئالدى، اﷲ كېچە بىلەن (كېچىنىڭ قاراڭغۇلۇقى بىلەن) كۈندۈزنى ياپىدۇ، كېچە بىلەن كۈندۈز بىر ـ بىرىنى قوغلىشىدۇ (يەنى داۋاملىق ئورۇن ئالمىشىپ تۇرىدۇ): اﷲ قۇياش، ئاي ۋە يۇلتۇزلارنى اﷲ نىڭ ئەمرىگە بويسۇندۇرۇلغۇچى قىلىپ ياراتتى، يارىتىش ۋە ئەمر قىلىش (يەنى كائىناتنى تەسەررۇپ قىلىش) راستىنلا اﷲ نىڭ ئىلكىدىدۇر ئالەملەرنىڭ پەرۋەردىگارى اﷲ نىڭ دەرىجىسى كاتتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,55,' پەرۋەردىگارىڭلارغا تۆۋەنچىلىك بىلەن يوشۇرۇن دۇئا قىلىڭلار، (دۇئا قىلغاندا كەلسە ـ كەلمەس سۆزلەپ، توۋلاپ) ھەددىدىن ئاشۇرۋەتكەنلەرنى اﷲ ياقتۇرمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,56,' اﷲ (پەيغەمبەرلەرنى ئەۋەتىپ) يەر يۈزىنى تۈزىگەندىن كېيىن، يەر يۈزىدە بۇزۇقچىلىق قىلماڭلار (اﷲ نىڭ ئازابىدىن) قورققان ۋە (رەھمىتىنى) ئۇمىد قىلغان ھالدا دۇئا قىلىڭلار، شۈبھىسىزكى اﷲ نىڭ رەھمىتى ياخشىلىق قىلغۇچىلارغا يېقىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,57,' اﷲ نىڭ رەھمىتى (يەنى يامغۇر) مەيدانغا كېلىش ئالدىدا، اﷲ بېشارەتچى قىلىپ شاماللارنى ئەۋەتىدۇ، شاماللار ئېغىر بۇلۇتلارنى كۆتۈرگەن چاغدا، ئۇلارنى ئۆلۈك (يەنى قاغجىراپ كەتكەن گىياھسىز) جايلارغا سۈرىمىز، ئۇنىڭدىن يامغۇر ياغدۇرۇپ تۈرلۈك ـ تۈرلۈك مېۋىلەرنى چىقىرىمىز، ئىبرەت ئېلىشىڭلار ئۈچۈن (زېمىندىن مېۋىلەرنى چىقارغاندەك، قىيامەتتە) ئۆلۈكلەرنى قەبرىلىرىدىن چىقىرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,58,' مۇنبەت يەرنىڭ گىياھسى اﷲ نىڭ ئىزنى بىلەن تولۇق ئۈنىدۇ (بۇ ۋەز ـ نەسىھەتكە قۇلاق سېلىپ، ئۇنىڭدىن پايدىلىنىدىغان مۆمىننىڭ مىسالىدۇر)، ناچار يەرنىڭ گىياھسى ئاز ئۆسىدۇ (بۇ ۋەز ـ نەسىھەتتىن پايدىلانمايدىغان كاپىرنىڭ مىسالىدۇر)، شۈكۈر قىلىدىغان قەۋم ئۈچۈن ئايەتلىرىمىزنى مۇشۇنداق شەكىللەردە بايان قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,59,' شەك شۈبھىسىزكى، نۇھنى ئۆز قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق، ئۇ: ‹‹ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر، مەن ھەقىقەتەن سىلەرنىڭ بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىغا قېلىشىڭلاردىن قورقىمەن›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,60,' قەۋمىنىڭ چوڭلىرى: ‹‹بىز سېنى ھەقىقەتەن روشەن گۇمراھلىقتا كۆرۈمىز›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,61,' نۇھ ئېيتتى: ‹‹ئى قەۋمىم! مەن قىلچىمۇ گۇمراھ ئەمەسمەن، لېكىن مەن ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەرمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,62,' سىلەرگە پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزىمەن، سىلەرگە نەسىھەت قىلىمەن، مەن اﷲ نىڭ ۋەھيى قىلىشى بىلەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,63,' سىلەرنى ئاگاھلاندۇرۇش ئۈچۈن، (كۇفرىدىن) ساقلىنىشىڭلار ۋە اﷲ نىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن، سىلەرگە ئاراڭلاردىكى بىر كىشى ئارقىلىق پەرۋەردىگارىڭلارنىڭ ۋەھيىسى كېلىشىدىن ئەجەبلەنمەمسىلەر؟››');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,64,' ئۇلار نۇھنى ئىنكار قىلدى، نۇھنى ئۇنىڭ بىلەن كېمىدە بىللە بولغانلارنى قۇتقۇزدۇق، ئايەتلىرىمىزنى ئىنكار قىلغانلارنى پۈتۈنلەي (سۇغا) غەرق قىلدۇق شۈبھىسىزكى، ئۇلار (ھەقنى كۆرۈشتىن دىللىرى) كور قەۋم ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,65,' ئاد (قەۋمى) گە ئۇلارنىڭ قېرىندىشى ھۇدنى ئەۋەتتۇق، ھۇد: ‹‹ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر (ئۇنىڭ ئازابىدىن) قورقمامسىلەر؟›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,66,' ئۇنىڭ قەۋمىنىڭ ئىمان ئېيتمىغان چوڭلىرى: ‹‹بىز سېنى ھەقىقەتەن ئەخمەق ھېسابلايمىز، بىز سېنى، ئەلۋەتتە، (پەيغەمبەرلىك دەۋايىڭدا) يالغانچىلاردىن دەپ گۇمان قىلىمىز›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,67,' ھۇد ئېيتتى: ‹‹ئى قەۋمىم! مەن ئەخمەق ئەمەسمەن، لېكىن مەن ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەرمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,68,' سىلەرگە پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزىمەن، مەن سىلەرگە ئىشەنچىلىك نەسىھەتچىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,69,' پەرۋەردىگارىڭلارنىڭ ۋەھيىسى سىلەرنى ئاگاھلاندۇرۇش ئۈچۈن ئۆز ئىچىڭلاردىكى بىر كىشى ئارقىلىق كەلگەنلىكىدىن ئەجەبلىنەمسىلەر؟›› ئۆز ۋاقتىدا نۇھنىڭ قەۋمى ھالاك بولغاندىن كېيىن، اﷲ نىڭ سىلەرنى ئۇلارنىڭ ئورۇنباسارلىرى قىلغانلىقىنى ۋۇجۇدۇڭلارنى، قامەتلىك، بەستلىك قىلغانلىقىنى ياد ئېتىڭلار، سىلەر بەختكە ئېرىشىشىڭلار ئۈچۈن اﷲ نىڭ نېمەتلىرىنى ياد ئېتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,70,' ئۇلار: ‹‹(ئى ھۇد) ئاتا ـ بوۋىللىرىمىز ئىبادەت قىلىپ كېلىۋاتقان مەبۇدلارنى تاشلاپ، يالغۇز بىر اﷲ قا ئىبادەت قىلىشىمىزغا دەۋەت قىلغىلى كەلدىڭمۇ؟ ئەگەر راست سۆزلىگۈچىلەردىن بولساڭ، بىزگە ۋەدە قىلغان ئازابنى كەلتۈرگىن›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,71,' ھۇد: ‹‹سىلەرگە چوقۇم پەرۋەردىگارىڭلارنىڭ ئازابى ۋە غەزىپى نازىل بولىدۇ، سىلەر ئۆزۈڭلار ۋە ئاتا ـ بوۋاڭلار ئات قويۇۋالغان، بۇ ھەقتە (ئۇلارغا ئىبادەت قىلىشقا) اﷲ ھېچقانداق دەلىل نازىل قىلمىغان (بۇتلىرىڭلارنىڭ) ئىسىملىرى ئۈستىدە مەن بىلەن مۇنازىرىلىشەمسىلەر؟ (اﷲ نىڭ ئازابىنى) كۈتۈڭلار، مەنمۇ ھەقىقەتەن سىلەر بىلەن بىرلىكتە (سىلەرگە نازىل بولىدىغان ئازابنى) كۈتىمەن›› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,72,' بىز ھۇدنى ۋە ئۇنىڭ بىلەن بىللە بولغۇچىلارنى (يەنى مۆمىنلەرنى) رەھمىتىمىز بىلەن قۇتقۇزدۇق، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى تەلتۆكۈس ھالاك قىلدۇق، ئۇلار ئىمان ئېيتمىغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,73,' سەمۇدقا ئۇلارنىڭ قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ ئەۋەتتۇق) ئۇ: «ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر سىلەرگە ھەقىقەتەن پەرۋەردىگارىڭلار تەرىپىدىن (مېنىڭ پەيغەمبەرلىكىمنى ئىسپاتلايدىغان) روشەن مۆجىزە كەلدى، مانا بۇ ـ اﷲ (بىۋاسىتە) ياراتقان چېشى تۆگىمۇ سىلەرگە مېنىڭ مۆجىزەمدۇر، ئۇنى اﷲ نىڭ زېمىنىغا قويۇۋېتىڭلار، ئىختىيارىي ئوتلىسۇن، ئۇنىڭغا زىيان ـ زەخمەت يەتكۈزمەڭلار، بولمىسا، سىلەرنى قاتتىق ئازاب ھالاك قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,74,' ياد ئېتىڭلاركى، ئۆز ۋاقتىدا اﷲ سىلەرنى ئاد قەۋمىدىن كېيىن ئۇلارنىڭ ئورۇنباسارلىرى قىلدى، سىلەرنى (ھىجر) زېمىنىغا ئورۇنلاشتۇردى، ئۇنىڭ تۈزلەڭلىرىگە ئىمارەتلەر سالدىڭلار، تاغلارنى تېشىپ ئۆيلەر ياسىدىڭلار اﷲ نىڭ نېمەتلىرىنى ياد ئېتىڭلار، زېمىندا بۇزغۇنچىلىق قىلماڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,75,' ئۇنىڭ قەۋمىنىڭ تەكەببۇر چوڭلىرى بوزەك قىلىنغانلارغا يەنى ئۇلارنىڭ ئىچىدىكى مۆمىنلەرگە: «سىلەر سالىھنى ئۇنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەر دەپ بىلەمسىلەر؟» دېدى ئۇلار: «بىز ئۇنىڭ پەيغەمبەرلىكىگە ئەلۋەتتە ئىشىنىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,76,' ھېلىقى تەكەببۇرلار: «سىلەر ئىشەنگەنگە بىز ھەرگىز ئىشەنمەيمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,77,' ئۇلار چىشى تۆگىنى بوغۇزلىدى، پەرۋەردىگارىنىڭ ئەمرىنى تۇتۇشتىن باش تارتتى ۋە ئى سالىھ: «راستتىنلا پەيغەمبەرلەردىن بولساڭ، بىزگە ۋەدە قىلغان ئازابنى كەلتۈرۈپ باق» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,78,' ئۇلارغا قاتتىق ئازاب يۈزلەندى ـ دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,79,' ئۇ (ئۇلار ھالاك بولغاندىن كېيىن) ئۇلارغا ئارقىسىنى قىلدى ۋە: «ئى قەۋمىم! سىلەرگە ھەقىقەتەن پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم، سىلەرگە نەسىھەت قىلدىم، لېكىن سىلەر نەسىھەت قىلغۇچىلارنى ياقتۇرمىدىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,80,' لۇتنى (ئەھلى سەدومغا پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۆز ۋاقتىدا ئۇ ئۆز قەۋمىگە (ئۇلارنى ئەيىبلەش يۈزىسىدىن) مۇنداق دېگەن ئىدى: «سىلەر شۇنداق قەبىھ ئىشنى قىلامسىلەر؟ مۇنداق قەبىھ ئىشنى سىلەردىن ئىلگىرى جاھان ئەھلىدىن ھېچقانداق ئادەم قىلمىغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,81,' سىلەر ھەقىقەتەن ئاياللارنى تاشلاپ قويۇپ، ئەرلەر بىلەن جىنسىي تەلىپىڭلارنى قاندۇرىسىلەر، سىلەر ھەقىقەتەن ھەددىدىن ئاشقۇچى قەۋمسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,82,' لۇتنىڭ قەۋمىنىڭ بىردىنبىر جاۋابى: «لۇتنى ئۇنىڭ ئەگەشكۈچىلىرى بىلەن قوشۇپ شەھرىڭلاردىن چىقىرىۋېتىڭلار، چۈنكى ئۇلار پاك كىشىلەردۇر» دېيىشتىن ئىبارەت بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,83,' لۇتنى ئۇنىڭ خوتۇنىدىن باشقا، تەۋە كىشىلىرى بىلەن بىللە (ئۇنىڭ قەۋمىگە نازىل بولغان ئازابتىن) قۇتقۇزدۇق، پەقەت خوتۇنىلا (شەھەردە) قىلىپ ھالاك بولغۇچىلاردىن بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,84,' ئۇلارغا بىر تۈرلۈك يامغۇر (يەنى تاش) ياغدۇردۇق، گۇناھلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,85,' مەديەن (خەلقىگە) ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۇ (ئۆز قەۋمىگە) ئېيتتى: « ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر سىلەرگە پەرۋەردىگارىڭلاردىن ھەقىقەتەن (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) مۆجىزە كەلدى، ئۆلچەمنى ۋە تارازىنى توغرىلاڭلار، كىشىلەرنىڭ نەرسىلىرىنى كەم بەرمەڭلار (اﷲ پەيغەمبەرلەرنى ئەۋەتىپ) زېمىننى تۈزىگەندىن كېيىن، زېمىندا (گۇناھلار قىلىپ) بۇزغۇنچىلىق قىلماڭلار، ئەگەر مۆمىن بولساڭلار، ئەنە شۇ سىلەر ئۈچۈن ياخشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,86,' سىلەر اﷲ قا ئىمان ئېيتقانلارنى قورقۇتقان، اﷲ نىڭ يولىدىن توسقان ۋە ئۇ يولنىڭ ئەگرىلىكىنى تىلىگەن ھالدا يوللاردا ئولتۇرماڭلار، ئەسلىدە سىلەر ئاز ئىدىڭلار، اﷲ نىڭ سىلەرنىڭ سانىڭلارنى كۆپەيتكەنلىكىنى ياد ئېتىڭلار، بۇزۇقچىلىق قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قاراڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,87,' ئەگەر سىلەردىن بىر گۇرۇھ ئادەم مېنىڭ پەيغەمبەرلىكىمگە ئىشەنگەن، يەنە بىر گۇرۇھ ئادەم ئىشەنمىگەن بولسا، اﷲ بىزنىڭ ئارىمىزدا ھۆكۈم چىقارغۇچە سەۋر قىلىپ تۇرۇڭلار اﷲ ھۆكۈم چىقارغۇچىلارنىڭ ئەڭ ياخشىسىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,88,' شۇئەيب قەۋمىنىڭ (ئىماندىن باش تارتقان) تەكەببۇر چوڭلىرى: «ئى شۇئەيب! سېنى چوقۇم ئەگەشكۈچىلىرىڭ (يەنى ساڭا ئىمان ئېيتقانلار) بىلەن قوشۇپ شەھرىمىزدىن ھەيدەپ چىقىرىمىز، ياكى چوقۇم بىزنىڭ دىنىمىزغا قايتىشىڭلار كېرەك» دېدى شۇئەيب: «بىز (بۇنىڭ ھەر ئىككىلىسىنى يامان كۆرۈدىغان تۇرساق (بىزنى يەنىلا شۇنىڭغا مەجبۇر قىلامسىلەر؟)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,89,' (شۇئەيب يەنى ئېيتتى) «اﷲ بىزنى سىلەرنىڭ دىنىڭلاردىن قۇتۇلدۇرغاندىن كېيىن ئۇنىڭغا قايتساق، ئەلۋەتتە، اﷲ قا يالغان چاپلىغان بولىمىز پەرۋەردىگارىمىز اﷲ خالىسىلا، بىز سىلەرنىڭ دىنىڭلارغا قايتمايمىز پەرۋەردىگارىمىزنىڭ ئىلمى ھەممىنى ئۆز ئىچىگە ئالىدۇ، اﷲ قا تەۋۋەككۈل قىلدۇق (يەنى ھەممە ئىشىمىزنى اﷲ قا تاپشۇرىمىز) پەرۋەردىگارىمىز! بىز بىلەن قەۋمىمىز ئارىسىدا ھەق ھۆكۈم چىقارغىن، سەن ھۆكۈم چىقارغۇچىلارنىڭ ئەڭ ياخشىسىدۇرسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,90,' ئۇنىڭ قەۋمىنىڭ ئىمانسىز چوڭلىرى: «ئەگەر سىلەر شۇئەيبكە ئەگەشسەڭلار، شۈبھىسىزكى، چوقۇم زىيان تارتىسىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,91,' ئۇلارغا قاتتىق زىلزىلە يۈزلەندى، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,92,' شۇئەيبنى ئىنكار قىلغانلار (اﷲ نىڭ ھالاك قىلىشى بىلەن) گويا بۇ يەردە تۇرمىغاندەك (يەنى ياشىمىغاندەك) بولۇپ قالدى شۇئەيبنى ئىنكار قىلغانلار زىيان تارتقۇچىلاردىن بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,93,' شۇئەيب ئۇلارغا ئارقىسىنى قىلىپ: «ئى قەۋمىم! مەن سىلەرگە ھەقىقەتەن پەرۋەدىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم ۋە نەسىھەت قىلدىم، مەن قانداقمۇ كاپىر قەۋمگە قايغۇراي!» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,94,' بىز مەيلى قايسى شەھەرگە بولسۇن، بىرەر پەيغەمبەر ئەۋەتكەن بولساق (شەھەر ئەھلى ئۇنى ئىنكار قىلغان بولسا)، ئۇلارنىڭ اﷲ قا يالۋۇرۇشلىرى (تەۋبە قىلىشلىرى) ئۈچۈن، ئۇلارنى نامراتلىققا، كېسەللىككە گىرىپتار قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,95,' ئاندىن (ئۇلارنىڭ بېشىغا كەلگەن) كۈلپەتنى نېمەتكە ئايلاندۇردۇق، شۇنىڭ بىلەن ئۇلار كۆپەيدى، ئۇلار: «بىزنىڭ ئاتا ـ بوۋىلىرىمىزنىڭ بېشىغا ھەقىقەتەن كۈلپەتلەر، نېمەتلەر كەلگەن ئىدى (بۇ ـ زاماننىڭ ئادىتى؛ اﷲ تەرىپىدىن كەلگەن ئوقۇبەت ئەمەس)» دېدى ئۇلار خەۋەرسىز تۇرغاندا، ئۇلارنى ئۇشتۇمتۇت ھالاك قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,96,' (پەيغەمبەرلىرىنى ئىنكار قىلغانلىقتىن ھالاك قىلىنغان) شەھەرلەرنىڭ ئاھالىسى ئىمان ئېيتقان ۋە (كۇفرىدىن، گۇناھلاردىن) ساقلانغان بولسا ئىدى، ئەلۋەتتە، ئۇلارنى ئاسمان ـ زېمىننىڭ پاراۋانلىقلىرىغا مۇيەسسەر قىلاتتۇق، لېكىن ئۇلار (پەيغەمبەرلەرنى) ئىنكار قىلدى، شۇ ئۇلارنى ئۆز قىلمىشلىرى تۈپەيلىدىن ھالاك قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,97,' (پەيغەمبەرلەرنى ئىنكار قىلىدىغان قىلىدىغان) شەھەرلەرنىڭ ئاھالىسى بىزنىڭ ئازابىمىزنىڭ ئۆزلىرىگە كېچىسى (غەپلەتتە) ئۇخلاۋاتقانلىرىدا كېلىشىدىن قورقمامدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,98,' شەھەرلەر ئاھالىسى بىزنىڭ ئازابىمىزنىڭ چۈش ۋاقتىدا ـ ئۇلار ئويناپ تۇرغانلىرىدا كېلىشىدىن قورقمامدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,99,' ئۇلار اﷲ نىڭ مەكرىدىن (يەنى ئۇلار تۇيۇقسىز تۇرغاندا اﷲ نىڭ ئازاب نازىل قىلىشىدىن) قورقمامدۇ؟ پەقەت زىيان تارتقۇچى قەۋملا اﷲ نىڭ مەكرىدىن قورقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,100,' زېمىننىڭ ئىلگىرىكى ئىگىلىرى (ھالاك بولغان) دىن كېيىن (زېمىنغا) ۋارىس بولغۇچىلارنى، خالىساق، گۇناھى تۈپەيلىدىن ھالاك قىلىدىغانلىقىمىز، دىللىرىنى پىچەتلىۋېتىدىغانلىقىمىز ئۇلارغا (يەنى زېمىنغا ۋارىسلىق قىلغۇچىلارغا) ئايان بولمىدىمۇ؟ ئۇلار (ھەق سۆزنى) ئاڭلىمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,101,' ئاشۇ شەھەرلەر (ئاھالىسى) نىڭ بەزى خەۋەرلىرىنى (ئى مۇھەممەد!) ساڭا ھېكايە قىلىپ بېرىمىز، شەك ـ شۈبھىسىزكى، ئۇلارنىڭ پەيغەمبەرلىرى ئۇلارغا مۆجىزىلەر ئېلىپ كەلگەن ئىدى، ئۇلار ئىلگىرى (ھەقنى) ئىنكار قىلغانلىقلىرى ئۈچۈن (ئۇ مۆجىزىلەرگە) ئىمان ئېيتمىدى اﷲ كاپىرلارنىڭ دىللىرىنى ئەنە شۇنداق پېچەتلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,102,' ئۇلارنىڭ تولىسىنىڭ (روزىمىساقتا) بەرگەن ۋەدىسىگە ۋاپا قىلىدىغانلىقىنى بايقىمىدۇق، ئۇلارنىڭ تولىسىنىڭ شەكسىز پاسىقلار (يەنى اﷲ نىڭ ئەمر ـ پەرمانىدىن باش تارتقۇچىلار) ئىكەنلىكىنى بايقىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,103,' مەزكۇز پەيغەمبەرلەردىن كېيىن، مۇسانى مۆجىزىلىرىمىز بىلەن، (مىسىرنىڭ مۇسا زامانىدىكى پادىشاھى) پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار بۇ مۆجىزىلەرنى ئىنكار قىلدى، بۇزغۇنچىلىق قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,104,' مۇسا ئېيتتى: «ئى پىرئەۋن! مەن ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارى ئەۋەتكەن پەيغەمبەرمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,105,' اﷲ نامىدىن پەقەت ھەقىقەتتىن باشقىنى ئېيتماسلىققا لايىقمەن، راستتىنلا پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) بىر مۆجىزە ئېلىپ كەلدىم، شۇنىڭ ئۈچۈن، ئىسرائىل ئەۋلادىنى مەن بىلەن قويۇپ بەرگىن (مەن بىلەن بىللە ئاتا ـ بوۋىلىرىنىڭ ۋەتىنى مۇقەددەس زېمىنغا بارسۇن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,106,' پىرئەۋن (مۇساغا): «ئەگەر سەن پەرۋەردىگارىڭ تەرىپىدىن) بىر مۆجىزە ئېلىپ كەلگەن بولساڭ، (بۇ دەۋايىڭدا) راستچىللاردىن بولساڭ، ئۇنى بىزگە كۆرسەتكىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,107,' مۇسا ھاسىسىنى تاشلىۋېدى، ئۇ ناگاھان ئاشكارا بىر ئەجدىھاغا ئايلاندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,108,' قولىنى (ياقىسىدىن) چىقىرىۋىدى، ئۇ ناگاھان قارىغۇچىلارغا (نۇر چاقنىتىپ تۇرىدىغان) ئاپئاق بولۇپ كۆرۈندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,109,' پىرئەۋن قەۋمنىڭ چوڭلىرى ئېيتتى: «بۇ ھەقىقەتەن ناھايىتى ئۇستا سېھىرگەر ئىكەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,110,' ئۇ سىلەرنى زېمىنىڭلار (يەنى مىسىر زېمىنى) دىن ھەيدەپ چىقىرىۋەتمەكچى نېمە مەسلىھەت بېرىسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,111,' ئۇلار ئېيتتى: «(ئۇلارنىڭ ئىشىنى ئۆزۈڭ بىر پىكىرگە كەلگۈچە) تەخىر قىلغىن، شەھەرلەرگە (سېھىرگەرلەرنى) يىغقۇچى كىشىلەرنى ئەۋەتكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,112,' ئۇلار بارلىق ئۇستا سېھىرگەرلەرنى سېنىڭ ھۇزۇرۇڭغا ئېلىپ كەلسۇن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,113,' (پىرئەۋن سېھىرگەرلىرىنى يىغىشقا ئادەملىرىنى ئەۋەتتى) سېھىرگەرلەر پىرئەۋننىڭ قېشىغا كەلدى، ئۇلار: «ئەگەر بىز غەلىبە قىلساق، بىزگە چوقۇم مۇكاپات بېرىلەمدۇ؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,114,' پىرئەۋن: «ھەئە، (مۇكاپات بېرىلىدۇ، ئۇنىڭ ئۈستىگە) سىلەر چوقۇم مېنىڭ يېقىن كىشىلىرىم بولۇپ قالىسىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,115,' ئۇلار (يەنى سېھىرگەرلەر): «ئى مۇسا! (ھاساڭنى) سەن ئاۋۋال تاشلامسەن، ياكى بىز (ئۆزىمىزنىڭكىنى) ئاۋۋال تاشلامدۇق؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,116,' مۇسا: «سىلەر تاشلاڭلار!» دېدى ئۇلار ھاسا، ئارغامچىلارنى تاشلاپ كىشىلەرنىڭ كۆزلىرىنى باغلىدى، ئۇلارنى (يەنى كىشىلەرنى) قاتتىق چۆچۈتۈۋەتتى، ئۇلار (كىشىلەرنىڭ كۆزلىرىگە) چوڭ (كۆرۈنىدىغان) سېھىرنى كۆرسەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,117,' مۇساغا: «ھاساڭنى تاشلىغىن » دەپ ۋەھىي قىلدۇق، (مۇسا ھاسىسىنى تاشلىۋىدى، ئۇ ئەجدىھاغا ئايلىنىپ) ئۇلارنىڭ ئويدۇرما نەرسىلىرىنى دەرھال يۇتۇۋەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,118,' ھەقىقەت ئاشكارا بولدى، ئۇلارنىڭ كۆرسەتكەن سېھىرلىرى بەربات بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,119,' بۇ يەردە ئۇلار (يەنى پىرئەۋن بىلەن قەۋمى) مەغلۇپ بولدى، خار بولغان ھالدا (شەھەرگە قايتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,120,' سېھىرگەرلەر سەجدىگە باردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,121,' ئۇلار دېدى: «ئالەملەرنىڭ پەرۋەردىگارىغا ئىمان ئېيتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,122,' مۇسا ۋە ھارۇننىڭ پەرۋەردىگارىغا (ئىمان ئېيتتۇق)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,123,' پىرئەۋن ئېيتتى: «سىلەر مەن رۇخسەت قىلماي تۇرۇپ مۇساغا ئىمان ئېيتتىڭلار، بۇ چوقۇم سىلەرنىڭ شەھەر (يەنى مىسىر) دىكى چېغىڭلاردا ئاھالىنى شەھەردىن ھەيدەپ چىقىرىش ئۈچۈن (مۇسا بىلەن بىرلىشىپ) ئالدىن پىلانلىغان ھىيلەڭلاردۇر (سىلەرنى قانداق جازالايدىغانلىقىمنى) ئۇزاققا قالماي بىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,124,' سىلەرنىڭ قولۇڭلارنى، پۇتۇڭلارنى چوقۇم ئوڭ ـ چەپ قىلىپ (ئوڭ قولۇڭلار بىلەن سول پۇتۇڭلارنى ياكى سول پۇتۇڭلار بىلەن ئوڭ قولۇڭلارنى) چوقۇم كېسىمەن، ئاندىن ھەممىڭلارنى چوقۇم دارغا ئېسىپ ئۆلتۈرىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,125,' سېھىرگەرلەر ئېيتتى: «بىز، ئەلۋەتتە، پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,126,' سەن بىزنى پەقەت پەرۋەردىگارىمىزنىڭ بىزگە نازىل قىلغان ئايەتلىرىگە ئىمان ئېيتقانلىقىمىز ئۈچۈنلا ئەيىبلەۋاتىسەن، پەرۋەردىگارىمىز! بىزگە سەۋر ئاتا قىلغىن، بىزنى مۇسۇلمان پېتىمىزچە قەبزى روھ قىلغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,127,' پىرئەۋن قەۋمىنىڭ چوڭلىرى (پىرئەۋنگە): «مۇسا بىلەن ئۇنىڭ قەۋمىنى زېمنىدا پىتنە ـ پاسات تۇغدۇرۇشقا، سېنى ۋە سېنىڭ ئىلاھلىرىڭنى تاشلاشقا قويۇۋېتەمسەن؟» دېدى پىرئەۋن: «ئۇلارنىڭ باللىلىرىنى ئۆلتۈرىمىز، ئاياللىرىنى بولسا (خىزمەتكە سېلىشقا) قالدۇرۇپ قويىمىز، بىز ئەلۋەتتە ئۇلارنىڭ ئۈستىدىن ھۆكۈمرانلىق قىلىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,128,' مۇسا ئۆز قەۋمىگە: «اﷲ تىن مەدەت تىلەڭلار، سەۋر قىلىڭلار، يەر يۈزى ھەقىقەتەن اﷲ نىڭ مۈلكىدۇر، (اﷲ) ئۇنىڭغا بەندىلىرىدىن ئۆزى خالىغان كىشىلەرنى ۋارىس قىلىدۇ؛ ياخشى ئاقىۋەت تەقۋادارلارغا مەنسۇپ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,129,' ئۇلار: «سەن بىزگە كېلىشتىن بۇرۇنمۇ، كەلگەندىن كېيىنمۇ ھامان خارلىنىپ كەلدۇق» دېدى مۇسا: «پەرۋاردىگارىڭلارنىڭ دۈشمىنىڭلارنى ھالاك قېلىشى، (مىسىر) زېمىنىدا ئۇلارنىڭ ئورنىغا سىلەرنى دەسسىتىشى، سىلەرنىڭ قانداق ئىش قىلىدىغانلىقىڭلارغا قارىشىمۇ ھەقىقەقتۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,130,' اﷲ قا قەسەمكى، بىز ھەقىقەتەن پىرئەۋن ۋە ئۇنىڭ تەۋەلىرىنىڭ ئىبرەت ئېلىشلىرى ئۈچۈن، ئۇلارنى قەھەتچىلىك بىلەن، مېۋىلىرىنىڭ ھوسۇلىنى كېمەيتىۋېتىش بىلەن سىنىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,131,' ئۇلار ئەگەر (پاراۋانلىق ـ مولچىلىقتەك) بىرەر ياخشىلىققا ئېرىشسە، بىز بۇنىڭغا ھەقلىقمىز، دەيدۇ ئەگەر ئۇلارغا بىرەر يامانلىق كەلسە، ئۇلار: بۇ مۇسا ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنىڭ شۇملۇقىدىن كەلدى، دەيدۇ ئۇلارغا يەتكەن ياخشىلىق بىلەن يامانلىق (مۇسانىڭ شۇملۇقىدىن ئەمەس) اﷲ نىڭ تەقدىرىدۇر، لېكىن ئۇلارنىڭ تولىسى بۇنى (يەنى ئۇلارغا كەلگەن بالا ـ قازانىڭ ئۆز گۇناھلىرى تۈپەيلى اﷲ تەرىپىدىن كەلگەنلىكىنى ئۇقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,132,' ئۇلار (يەنى پىرئەۋن قەۋمى): «كۆزىمىزنى باغلاش ئۈچۈن ھەرقانداق مۆجىزە كەلتۈرسەڭمۇ بىز ساڭا ھەرگىز ئىشەنمەيمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,133,' ئۇلارغا سۇ ئاپىتى، چېكەتكە، پىت، پاقا، قان (بالالىرىنى) روشەن مۆجىزىلەر قىلىپ ئەۋەتتۇق، (اﷲ قا ئىمان ئېيتىشقا) ئۇلارنىڭ گەدەنكەشلىكى يول قويمىدى، ئۇلار گۇناھكار قەۋم بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,134,' ئۇلارغا بالا نازىل بولغاندا، ئۇلار: «ئى مۇسا! پەرۋەردىگارىڭغا ئۆزىنىڭ ساڭا ئاتا قىلغان ئەھدى بىلەن (يەنى ساڭا بەرگەن پەيغەمنبەرلىكنىڭ ھەقىقىي ھۆرمىتى بىلەن بىزدىن بالانى كۆتۈرۈۋېتىشكە) دۇئا قىلساڭ، اﷲ قا قەسەمكى، ئەگەر بىزدىن بالانى كۆتۈرۈۋەتسەڭ، ساڭا چوقۇم ئىمان ئېيتىمىز، ئىسرائىل ئەۋلادىنى چوقۇم سەن بىلەن بىللە قويۇپ بېرىمىز» دېگەن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,135,' ئۇلاردىن بالانى ۋاقىتلىق كۆتۈرۈۋەتكەن چېغىمىزدا، ئۇلار توساتتىن ئەھدىلىرىنى بۇزدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,136,' ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ۋە ئايەتلىرىمىزدىن غاپىل بولغانلىقلىرى ئۈچۈن، ئۇلارنى دېڭىزغا غەرق قىلىپ جازالىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,137,' بوزەك قىلىنغان قەۋمنى (يەنى بەنى ئىسرائىلنى) بىز بەرىكەتلىك قىلغان (شام) زېمىننىڭ شەرق تەرەپلىرىگە ۋە غەرب تەرەپلىرىگە (يەنى ھەممە تەرەپكە) ۋارىس قىلدۇق ئىسرائىل ئەۋلادى سەۋر قىلغانلىقلىرى ئۈچۈن، پەرۋەردىگارىڭنىڭ ئۇلارغا قىلغان چىرايلىق ۋەدىسى تولۇق ئىشقا ئاشتى؛ پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ سالغانلىرى (يەنى ئىمارەتلىرى) نى ۋە ياسىغانلىرى (يەنى باغلىرى ۋە ئېكىنزارلىقلىرى) نى ۋەيران قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,138,' ئىسرائىل ئەۋلادىنى بىز (قۇلزۇم) دېڭىزىدىن ئۆتكۈزۈۋەتتۇق، ئۇلار بۇتلىرىغا چوقۇنۇۋاتقان بىر قەۋمنىڭ يېنىدىن ئۆتكەندە: «ئى مۇسا! بىزگىمۇ ئۇلارنىڭ بۇتلىرىغا ئوخشاش بۇت ئورنىتىپ بەرگىن» دېدى مۇسا: «سىلەر ھەقىقەتەن نادان قەۋم ئىكەنسىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,139,' شۈبھىسىزكى، ئاشۇ كىشىلەرنىڭ باتىل دىنلىرى گۇمران بولغۇسىدۇر، ئۇلارنىڭ قىلغان ئەمەللىرى بىكاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,140,' مۇسا: «اﷲ سىلەرنى (زامانىڭلاردىكى) جاھان ئەھلىدىن ئارتۇق قىلغان تۇرسا، سىلەرگە ئۇنىڭدىن باشقا ئىلاھ ئىزدەمدىم؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,141,' ئۆز ۋاقتىدا سىلەرنى پىرئەۋننىڭ قەۋمىدىن قۇتقۇزدۇق، ئۇلار سىلەرگە قاتتىق ئازابنى تېتىتاتتى، ئوغۇللىرىڭلارنى ئۆلتۈرەتتى، ئاياللىرىڭلارنى (خورلاپ ئىشلىتىشكە) ئېلىپ قالاتتى، بۇنىڭدا پەرۋەردىگارىڭلاردىن سىلەرگە زور سىناق بار ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,142,' مۇسا (بىزگە مۇناجات قىلىشقا) ئوتتۇز كېچىنى ۋەدە قىلدۇق ئۇنىڭغا يەنە ئون كېچىنى قوشتۇق، شۇنىڭ بىلەن، پەرۋەردىگارىڭنىڭ ۋەدە قىلغان ۋاقتى  كېچە بولدى مۇسا قېرىندىشى ھارۇنغا: «(مەن قايتىپ كەلگەنگە قەدەر) قەۋمىمگە مېنىڭ ئورۇنباسارىم بولۇپ تۇرغىن، (ئۇلارنىڭ ئىشىنى) تۈزىگىن، بۇزۇقچىلىق قىلغۇچىلارنىڭ يولىدا ماڭمىغىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,143,' مۇسا بىز ۋەدە قىلغان ۋاقىتتا كەلگەن ۋە پەرۋەردىگارى ئۇنىڭغا (بىۋاسىتە) سۆ قىلغان چاغدا: «پەرۋەردىگارىم! ماڭا ئۆزۈڭنى كۆرسەتكىن، سېنى بىر كۆرۈپ ئالاي» دېدى اﷲ: «مېنى (بۇ دۇنيادا) ھەرگىز كۆرەلمەيسەن (چۈنكى ئىنساننىڭ بۇ ئاجىز تېنى بۇنىڭغا تاقەت قىلالمايدۇ) لېكىن تاغقا قارىغىن، ئەگەر تاغ ئورنىدا مەھكەم تۇرالىسا، مېنى كۆرەلەيسەن» دېدى پەرۋەردىگارى تاغقا تەجەللى قىلىش بىلەن، تاغنى تۈپتۈز قىلىۋەتتى، مۇسا بىھوش بولۇپ يىقىلدى ئۇ ھوشىغا كېلىپ: « (پەرۋەرىگارىم!) سەن پاكتۇرسەن، ساڭا تەۋبە قىلدىم، مەن (سېنىڭ ئۇلۇغلۇقۇڭغا) ئىشەنگۈچىلەرنىڭ ئەۋۋىلىمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,144,' اﷲ ئېيتتى: «ئى مۇسا! مەن ھەقىقەتەن سېنى (زامانىڭدىكى) كىشىلەر ئارىسىدىن پەيغەمبەرلىككە ۋە مەن بىلەن (بىۋاسىتە) سۆزلىشىشكە تاللىدىم، ساڭا مەن ئاتا قىلغن پەيغەمبەرلىكنى قوبۇل قىلغىن ۋە شۈكۈر قىلغۇچىلاردىن بولغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,145,' بىز ئۇنىڭ ئۈچۈن (تەۋرات) تاختىلىرىغا (ئىسرائىل ئەۋلادى دىنىدا موھتاج بولغان) ۋەز ـ نەسىھەت، (دىنىي) ئەھكاملارنىڭ ھەممىسىنى تەپسىلىي يازدۇق (ئى مۇسا) ئۇنى (يەنى تەۋراتنى) مەھكەم تۇتقىن ھەمدە قەۋمىڭنى ئۇنىڭدىكى ئەڭ گۈزەل ئەھكاملارغا ئەمەل قىلىشقا بۇيرۇغىن، سىلەرگە پاسىقلار (يەنى پىرئەۋن ۋە ئۇنىڭ قەۋمى) نىڭ دىيارىنى كۆرسىتىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,146,' يەر يۈزىدە ناھەق تەكەببۇرلۇق قىلىدىغانلارغا مېنىڭ ئايەتلىرىمنى پەھىم قىلدۇرمايمەن، ئۇلار بارلىق مۆجىزىلەرنى كۆگەندىمۇ ئۇنىڭغا ئىشەنمەيدۇ، ئۇلار ئەگەر توغرا يولنى كۆرسە، ئۇنىڭدا ماڭمايدۇ، گۇمراھلىق يولىنى كۆرسە ئۇنىڭدا ماڭىدۇ، بۇ شۇنىڭ ئۈچۈنكى، ئۇلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلدى ۋە ئۇنىڭدىن غاپىل بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,147,' بىزنىڭ ئايەتلىرىمىزنى ۋە ئاخىرەتتە اﷲ قا مۇلاقات بولۇشنى ئىنكار قىلغانلارنىڭ قىلغان (ياخشى) ئەمەللىرى (ئىمانى بولمىغانلىقى ئۈچۈن) بىكار بولۇپ كېتىدۇ، ئۇلارغا پەقەت قىلغان ئەمەللىرىگە يارىشا جازا بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,148,' مۇسانىڭ قەۋمى مۇسادىن كېيىن (يەنى مۇسا پەرۋەردىگارىغا مۇناجات قىلىش ئۈچۈن تۇر تېغىغا كەتكەندىن كېيىن)، زىننەت بۇيۇملرىدىن موزايدەك ئاۋاز چىقىرىدىغان بىر جانسىز موزاي ياسىدى ئۇلار موزاينىڭ ئۆزلىرىگە سۆزلىيەلمەيدىغانلىقىنى، يول كۆرسىتەلمەيدىغانلىقىنى كۆرمىدىمۇ؟ ئۇلار ئۇنى مەبۇد قىلىۋالدى، ئۇلار (ئۆزلىرىگە) زۇلۇم قىلغۇچى بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,149,' ئۇلار (موزايغا چوقۇنغانلىقلىرىغا) پۇشايمان قىلغان ۋە راست ئازغانلىقلىرىنى چۈشەنگەن چاغدا: «ئەگەر پەرۋەردىگارىمىز بىزگە رەھىم قىلمىسا ۋە مەغپىرەت قىلمىسا، بىز ئەلۋەتتە زىيان تارتقۇچىلاردىن بولىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,150,' مۇسا غەزەپلەنگەن، غەمكىن ھالدا قايتىپ كېلىپ، قەۋمىگە: «مەن يوق چاغدا (موزايغا چوقۇنۇپ) نەقەدەر يامان ئىش قىلدىڭلار ـ ھە! (كۈتۈپ تۇرماي) پەرۋەردىگارىڭلار نىڭ ئەمرىگە ئالدىراپ كەتتىڭلارمۇ؟» دېدى ۋە (غەزەپلەنگەنلىكتىن تەۋرات) تاختىلىرىنى (يەرگە) تاشلىدى، قېرىندىشىنىڭ چېچىدىن تۇتۇپ ئۆز تەرىپىگە تارتتى (ھارۇن) ئېيتتى: «ئى قېرىندىشىم! (بۇ) قەۋم مېنى بوزەك تاپتى، مېنى ئۆلتۈرۈۋەتكىلى تاس قالدى مېنى دۈشمەنلەرگە تەپەررۇج قىلىپ بەرمىگىن؟ مېنى زالىم قەۋم (يەنى موزايغا چوقۇنغانلار) قاتارىدا سانىمىغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,151,' مۇسا ئېيتتى: «پەرۋەردىگارىم! ماڭا ۋە مېنىڭ قېرىندىشىمغا مەغپىرەت قىلغىن، بىزنى رەھمىتىڭ دائىرىسىگە كىرگۈزگىن، سەن ئەڭ رەھىم قىلغۇچى زاتتۇرسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,152,' شۈبھىسىزكى موزاينى مەبۇد قىلىۋالغانلار پەرۋەردىگارىنىڭ غەزىپىگە ئۇچرايدۇ، بۇ دۇنيادا خارلىققا قالىدۇ، (اﷲ قا) بوھتان قىلغۇچىلارغا بىز مۇشۇنداق جازا بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,153,' يامان ئىشلارنى قىلىپ قويۇپ، كېيىن تەۋبە قىلغان ۋە ئىمانىدا سەمىمىي بولغانلارنى تەۋبىسىدىن كېيىن پەرۋەردىگارىڭ ئەلۋەتتە مەغپىرەت قىلىدۇ، (ئۇلارغا) رەھىم قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,154,' مۇسا غەزىپى بېسىلغاندىن كېيىن (يەردە ياتقان تەۋرات) تاختىلىرىنى ئالدى ئۇنىڭدا پەرۋەردىگارىدىن قورققۇچىلارغا ھىدايەت قىلىنىدۇ ۋە رەھمەت قىلىنىدۇ دەپ يېزىلغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,155,' مۇسا، بىز بەلگىلەپ بەرگەن ۋاقىتتا ئېلىپ كېلىش ئۈچۈن، ئۆز قەۋمىدىن  كىشىنى تاللىدى؛ ئۇلارغا زىلزىلە يۈزلەنگەندە، مۇسا: «پەرۋەردىگارىم! خالىغان بولساڭ ئۇلارنى ۋە مېنى بۇرۇنلا ھالاك قىلغان بولاتتىڭ ئارىمىزدىكى ئەخمەقلەرنىڭ قىلمىشى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟ بۇ پەقەت سېنىڭ سىنىقىڭدۇر، شۇ ئارقىلىق خالىغان بەندەڭنى ئازدۇرىسەن، خالىغان بەندەڭنى ھىدايەت قىلىسەن، سەن بىزنىڭ ئىگىمىزسەن، بىزگە مەغپىرەت قىلغىن، رەھىم قىلغىن سەن ئەڭ ياخشى مەغپىرەت قىلغۇچىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,156,' بىزگە بۇ دۇنيادا ۋە ئاخىرەتتە تېگىشلىك ياخشىلىق قىلغىن، بىز ھەقىقەتەن ساڭا تەۋبە قىلدۇق» دېدى اﷲ ئېيتتى: «ئازابىم بىلەن (بەندىلىرىمدىن) خالىغان كىشىنى ئازابلايمەن، مېنىڭ رەھمىتىم مەخلۇقاتنىڭ ھەممىسىگە ئورتاقتۇر رەھمىتىمنى (كۇفرىدىن ۋە گۇناھتىن) ساقلانغۇچىلارغا، (ماللىرىنىڭ) زاكىتىنى بېرىدىغانلارغا ۋە بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتىدىغانلارغا تېگىشلىك قىلىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,157,' ئۇلار ئەلچىگە ـ ئۈممى پەيغەمبەرگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) ئەگىشىدۇ، ئۇلار ئۆز ئىلكىدىكى تەۋرات، ئىنجىللاردا ئۇنىڭ (سۈپىتىنىڭ) يېزىلغانلىقىنى كۆرىدۇ ئۇ ئۇلارنى ياخشى ئىش قىلىشقا بۇيرۇيدۇ، يامان ئىش قىلىشتىن توسىدۇ، ئۇلارغا پاك نەرسىلەرنى ھالال قىلىدۇ، ناپاك نەرسىلەرنى ھارام قىلىدۇ، ئۇلارنىڭ ئېغىر يۈكىنى يېنىكلىتىدۇ، ئۇلارنى سېلىنغان تاقاق، كويزا ـ كىشەنلەردىن بوشىتىدۇ (يەنى ئۇلارغا يۈكلەنگەن ئېغىر ۋەزىپىلەرنى ئېلىپ تاشلايدۇ)، ئۇنىڭغا ئىمان ئېيتقانلار، ئۇنى ھۆرمەتلىگەنلەر، ئۇنىڭغا ياردەم بەرگەنلەر، ئۇنىڭغا نازىل قىلىنغان نۇر (يەنى قۇرئان) غا ئەگەشكۈچىلەر بەختكە ئېرىشكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,158,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئىنسانلار! مەن ھەقىقەتەن سىلەرنىڭ ھەممىڭلارغا اﷲ تەرىپىدىن ئەۋەتىلگەن ئەلچىمەن، ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ تىرىلدۈرىدۇ ۋە ئۆلتۈرىدۇ، اﷲ قا ۋە اﷲ نىڭ سۆزلىرىگە ئىمان كەلتۈرىدىغان ئەلچىسى ئۈممى پەيغەمبەرگە ئىمان كەلتۈرۈڭلار، ھىدايەت تېپىشىڭلار ئۈچۈن ئۇنىڭغا ئەگىشىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,159,' مۇسانىڭ قەۋمىنىڭ (يەنى بەنى ئىسرائىلنىڭ) ئىچىدە (كىشىلەرنى) ھەق بىلەن توغرا يولغا باشلايدىغان، ھەق بىلەن توغرا ھۆكۈم قىلىدىغان بىر جامائە بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,160,' ئۇلار (يەنى ئىسرائىل) نى  تارماققا (يەنى قەبىلىگە) جامائەلەر قىلىپ بۆلۈۋەتتۇق مۇسانىڭ قەۋمى (باياۋاندا) ئۇنىڭدىن سۇ تەلەپ قىلغاندا، مۇساغا: «ھاساڭ بىلەن تاشنى ئۇرغىن» دەپ ۋەھىي قىلدۇق (مۇسا ھاسىسى بىلەن تاشنى ئۇرۇۋىدى) ئۇنىڭدىن (قەبىلىلەرنىڭ سانى بويىچە)  بۇلاق ئېتىلىپ چىقتى، ھەر قەبىلە ئۆزىنىڭ سۇ ئىچىدىغان ئورنىنى بىلدى ئۇلارغا بۇلۇتنى سايىۋەن قىلىپ بەردۇق، ئۇلارغا تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق (ئۇلارغا ئېيتتۇقكى): «سىلەر بىز رىزىقلاندۇرغان لەززەتلىك نەرسىلەردىن يەڭلار» (ئۇلار بۇ ئۇلۇغ نېمەتلەرگە نانكورلۇق قىلىش بىلەن) بىزگە زىيان سالغىنى يوق، (ئۆزلىرىنى اﷲ نىڭ ئازابىغا دۇچار قىلىشلىرى بىلەن) ئۆزلىرىگە ئۆزلىرى زۇلۇم سالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,161,' ئۆز ۋاقتىدا (بىز ئۇلارغا) (يەنى بەنى ئىسرائىلنىڭ ئاتا ـ بوۋىلىرىغا): «سىلەر بۇ شەھەر (يەنى بەيتۇلمۇقەددەس) دە تۇرۇڭلار، ئۇ يەردىكى يېمەكلىكلەردىن خالىغىنىڭلارچە يەڭلار، شەھەر دەرۋازىسىدىن سەجدە قىلغان ھالدا كىرىڭلار، (كىرىۋاتقان چېغىڭلاردا) ئى اﷲ! گۇناھلىرىمىزنى كەچۈرگىن، دەڭلار، جىمى گۇناھلىرىڭلارنى مەغپىرەت قىلىمىز، ياخشىلىق قىلغۇچىلارغا (ساۋابىنى) زىيادە بېرىمىز» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,162,' ئۇلارنىڭ ئىچىدىكى زالىملار ئۇلارغا ئېيتىلغان سۆز (اﷲ نىڭ ئەمرىنى) باشقا سۆزلەرگە ئۆزگەرتىۋەتتى، ئۇلار زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئۇلارغا ئاسماندىن ئازاب ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,163,' ئۇلار (يەنى يەھۇدىيلار) دىن دېڭىز (يەنى قۇلزۇم دېڭىزى) بويىدىكى (ئەيلە) شەھرىنىڭ ئەھۋالىنى سورىغىن (ئۇنىڭ ئاھالىسى بېلىق تۇتۇش چەكلەنگەن كۈن) شەنبىدە اﷲ نىڭ چەكلىمىسىدىن چىقاتتى چۈنكى شەنبە كۈنى بېلىقلار سۇ ئۈستىدىن ئۇلارنىڭ ئالدىغا كېلەتتى، شەنبىدىن غەيرىي كۈنلەردە ئۇلارنىڭ ئالدىغا كەلمەيتتى، پاسىق بولغانلىقلىرى ئۈچۈن ئۇلارنى مۇشۇنداق سىنايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,164,' ئۆز ۋاقتىدا ئۇلارنىڭ ئىچىدىكى بىر جامائە ئادەملەر: اﷲ ھالاك قىلىدىغان ياكى قاتتىق ئازابلايدىغان قەۋمگە نېمە ئۈچۈن ۋەز ـ نەسىھەت قىلىسىلەر؟» دېدى ئۇلار (يەنى ۋەز ـ نەسىھەت قىلغۇچىلار): «پەرۋەردىگارىڭلارنىڭ ئالدىدا (گۇناھقا سۈكۈت قىلىپ تۇردۇق دەپ) ئۆزرە ئېيتماسلىقىمىز ۋە ئۇلارنىڭ اﷲ تىن قورقۇپ (گۇناھتىن چەكلىنىشلىرىنى ئۈمىد قىلغانلىقىمىز ئۈچۈن شۇنداق قىلدۇق)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,165,' ئۇلار ئۆزلىرىگە قىلىنغان ۋەز ـ نەسىھەتنى قوبۇل قىلمىغان چاغدا، يامان ئىشتىن مەنئى قىلغۇچىلارنى قۇتقۇزدۇق، اﷲ نىڭ ئەمرىدىن چىققانلىقلىرى ئۈچۈن، زالىملارنى قاتتىق ئازابقا دۇچار قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,166,' ئۇلار نەھيى قىلىنغان نەرسىنى تەرك ئېتىشتىن باش تارتقانلىقلىرى ئۈچۈن، ئۇلارغا: «خار مايمۇن بولۇپ كېتىڭلار» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,167,' (ئى مۇھەممەد!) ئۆز ۋاقتىدا، پەرۋەردىگارىڭ ئۇلارغا (يەنى يەھۇدىيلارغا) قىيامەتكىچە چوقۇم قاتتىق ئازابلايدىغان كىشىلەرنى ئەۋەتىپ تۇرىدىغانلىقىنى خەۋەر قىلدى شۈبھىسىزكى، پەرۋەردىگارىڭ (ئاسىيلىق قىلغۇچىلارنى) ئەلۋەتتە تېز جازالىغۇچىدۇر، (ئىتائەت قىلغۇچىلارغا) ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، مەرھەمەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,168,' ئۇلارنى (يەنى يەھۇدىيلارنى) يەر يۈزىدە تۈرلۈك پىرقىلەرگە بۆلۈۋەتتۇق، ئۇلارنىڭ ئىچىدە ياخشىلىرىمۇ بار، ياخشىلىرىدىن تۆۋەنلىرىمۇ بار، ئۇلارنىڭ (كۇفرىدىن ۋە گۇناھتىن) قايتىشلىرى ئۈچۈن، ئۇلارنى قاتتىقچىلىق ۋە كەڭچىلىك بىلەن سىنىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,169,' ئۇلاردىن كېيىن، (يامان) ئەۋلادلار تەۋراتقا ۋارىسلىق قىلدى، ئۇلار بۇ دۇنيانىڭ ئەرزىمەس پۇل ـ مېلىنى (ھالال بىلەن ھارامنى ئايرىماي، قارىقويۇق) ئالىدۇ، ۋە بىزنى اﷲ ئەپۇ قىلىدۇ، دەيدۇ شۇنداق پۇل ـ مال ئۇلارنىڭ قولىغا كەلسە، ئۇلار يەنە ئالىدۇ كىتابتا (يەنى تەۋراتتا) اﷲ قا (يالغان چاپلىماي) پەقەت ھەقنىلا ئېيتىشلىرى توغرۇلۇق ئۇلاردىن چىڭ ۋەدە ئېلىنمىدىمۇ؟ ھالبۇكى، ئۇلار تەۋراتتىكى نەرسىلەرنى ئوقۇغان ئىدى ھارامنى تەرك ئېتىپ، اﷲ تىن قورقىدىغانلارغا ئاخىرەت ياخشىدۇر، بۇنى چۈشەنمەمسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,170,' كىتابنى مەھكەم تۇتىدىغانلار، ناماز ئوقۇيدىغانلار (ساۋابقا ئېرىشىدۇ)، بىز ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى ھەقىقەتەن زايا قىلىۋەتمەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,171,' ئۆز ۋاقتىدا (تۇر) تاغنى قومۇرۇپ ئۇلارنىڭ ئۈستىگە سايىۋەندەك تىكلىدۇق، ئۇلار تاغنى ئۈستىلىرىگە چۈشۈپ كېتىدۇ دەپ ئويلىدى (ئۇلارغا ئېيتتۇقكى) «تەقۋادارلار قاتارىدا بولۇشۇڭلار ئۈچۈن ئۆزۈڭلارغا بېرىلگەن كىتابنى (يەنى تەۋراتنى) مەھكەم تۇتۇڭلار، ئۇنىڭدىكى ئەھكاملارغا ئەمەل قىلىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,172,' ئۆز ۋاقتىدا پەرۋەردىگارىڭ ئادەم باللىرىنى (يەنى نەسلىنى) ئۇلارنىڭ (ئاتىلىرىنىڭ) پۇشتىدىن چىقاردى ۋە ئۇلارنى ئۆزلىرىگە گۇۋاھ قىلىپ: «مەن سىلەرنىڭ پەرۋەدىگارىڭلار ئەمەسمۇ؟» دېدى (يەنى اﷲ ئۆزىنىڭ ئۇلارنىڭ پەرۋەردىگارى ئىكەنلىكىگە ۋە بىرلىكىگە ئادەم بالىلىرىنى ئىقرار قىلدۇردى، ئۇلار ئىقرار قىلىپ بۇنى ئۆز ئۈستىگە ئالدى) ئۇلار: «ھەئە، سەن بىزنىڭ پەرۋەردىگارىمىزدۇرسەن، گۇۋاھلىق بەردۇق» دېدى (ئۇلارنى گۇۋاھلىق بەرگۈزگەنلىكىمىز) قىيامەت كۈنى ئۇلارنىڭ: «بىز بۇنىڭدىن غەپلەتتە قالغان ئىكەنمىز» دېمەسلىكلىرى، ياكى «ئاتا ـ بوۋىلىرىمىز ئىلگىرى (ساڭا بۇتلارنى) شېرىك كەلتۈرگەن ئىدى، بىزنىڭ پەقەت ئۇلارنىڭ ئەۋلادى بولۇپ قالغان يېرىمىز بار (يەنى بىزنىڭ ئۆزرىمىز بار) گۇمراھلارنىڭ قىلمىشلىرى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟» دېمەسلىكلىرى ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,173,' ئۇلارنىڭ (گۇمراھلىقتىن، ئاتا ـ بوۋىلىرىغا ئەگىشىشتىن) قايتىشلىرى ئۈچۈن، ئايەتلىرىمىزنى مۇشۇنداق تەپسىلىي بايان قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,174,' (ئى مۇھەممەد!) ئۇلار (يەنى يەھۇدىيلار) غا شۇنداق بىر ئادەمنىڭ قىسسىسىنى ئوقۇپ بەرگىنكى، ئۇنىڭغا ئايەتلىرىمىزنى بەردۇق (يەنى كىتابۇللاھنىڭ بەزى ئىلىملىرىنى ئۆگەتتۇق) ئۇ ئايەتلىرىمىزدىن ئۆزىنى تارتتى، ئۇنىڭغا شەيتان ئاپىرىدە بولۇشى بىلەنلا گۇمراھلاردىن بولۇپ كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,175,' ئەگەر بىز خالىساق، ئۇنى (تەقۋادار ئۆلىمالار دەرىجىسىگە) كۆتۈرەتتۇق، لېكىن ئۇ دۇنياغا بېرىلىپ كەتتى، نەپسى خاھىشىغا بويسۇندى، ئۇ گويا بىر ئىتقا ئوخشايدۇكى، ئۇنى قوغلىۋەتسەڭمۇ تىلىنى چىقىرىپ ھەسىرەيدۇ، قوغلىۋەتسەڭمۇ تىلىنى چىقىرىپ ھەسىرەيدۇ، بۇ بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ئەنە شۇ قەۋمنىڭ مىسالىدۇر، ئويلاپ بېقىشلىرى ئۈچۈن، بۇ قىسسىنى (قەۋمىڭگە) سۆزلەپ بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,176,' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلىپ ئۆزلىرىگە زىيان سالغان قەۋمنىڭ مىسالى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,177,' اﷲ ھىدايەت قىلغان ئادەم ھىدايەت تاپقۇچىدۇر، اﷲ گۇمران قىلغان ئادەم زىيان تارتقۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,178,' شەك ـ شۈبھىسىزكى، جىن ۋە ئىنسانلاردىن نۇرغۇنلىرىنى دوزاخقا (يېقىلغۇ بولۇش ئۈچۈن) ياراتتۇق، ئۇلار دىللىرى بولغىنى بىلەنمۇ، ئۇ ئارقىلىق ھەقنى چۈشەنمەيدۇ ئۇلار كۆزلىرى بولغان بىلەنمۇ، ئۇ ئارقىلىق (اﷲ نىڭ قۇدرىتىنىڭ دەلىللىرىنى) كۆرمەيدۇ ئۇلار قۇلاقلىرى بولغىنى بىلەنمۇ، ئۇ ئارقىلىق (اﷲ نىڭ ئايەتلىرىنى ئىبرەت ئىلىپ) تىڭشىمايدۇ ئۇلار گويا ھايۋانغا ئوخشايدۇ، ھايۋاندىنمۇ بەتتەر گۇمراھتۇر، ئەنە شۇلار غاپىلدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,179,' اﷲ نىڭ گۈزەل ئىسىملىرى بار، اﷲ نى شۇ (گۈزەل ئىسىملىرى) بىلەن ئاتاڭلار، اﷲ نىڭ ئىسىملىرىنى كەلسە ـ كەلمەس قوللىنىدىغانلارنى تەرك ئېتىڭلار، ئۇلار (ئاخىرەتتە) قىلمىشلىرىنىڭ جازاسىنى تارتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,180,' بىز ياراتقان ئۈممەتلەر ئىچىدە ھەق يولغا دەۋەت قىلىدىغان ۋە ھەقتە چىڭ تۇرىدىغان بىر ئۈممەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,181,' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى تۇيدۇرماستىن، ئاستا ـ ئاستا ھالاكەتكە يېقىنلاشتۇرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,182,' ئۇلارغا مۆھلەت بېرىمەن مېنىڭ ئازابىم ھەقىقەتەن قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,183,' ئۇلار ئۆزىنىڭ ھەمراھى (يەنى مۇھەممەد ئەلەيھىسسالام) نىڭ مەجنۇن ئەمەسلىكىنى ئويلىمامدۇ؟ ئۇ پەقەت ئاشكارا ئاگاھلاندۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,184,' ئۇلار ئاسمانلارنىڭ ۋە زېمىننىڭ سەلتەنىتى ئۈستىدە، اﷲ نىڭ ياراتقان مەخلۇقاتلىرى، ئۆزلىرىنىڭ ئەجىلى يېقىنلىشىدىغانلىقىنىڭ مۇمكىنلىكى ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ ئۇلار (كالامۇللاھ ئىكەنلىكى شۇنچە روشەن بولغان) قۇرئاندىن باشقا يەنە قايسى سۆزگە ئىشىنىدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,185,' اﷲ ئازدۇرغان كىشىنى ھىدايەت قىلغۇچى بولمايدۇ، اﷲ ئۇلارنى گۇمراھلىقلىرىدا قويۇپ بېرىدۇكى، ئۇلار تېڭىرقاپ يۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,186,' (ئى مۇھەممەد!) ئۇلار سەندىن قىيامەتنىڭ قاچان بولىدىغانلىقىنى سورايدۇ، ئېيتقىنكى، «ئۇنىڭ قاچان بولىدىغانلىقىنى پەقەت پەرۋەردىگارىم (اﷲ) بىلىدۇ، ئۇنى پەقەت ئۆزى بەلگىلىگەن ۋاقىتتا مەيدانغا چىقىرىدۇ، قىيامەت ۋە زېمىننىڭ (ئەھلىگە) ئېغىر (ۋەقەدۇر) ئۇ سىلەرگە تۇيۇقسىز كېلىدۇ» گويا سەن قىيامەت بىلەن ناھايىتى تونۇشتەك، ئۇنى سەندىن سورىشىدۇ ئېيتقىنكى، «ئۇنىڭ ۋاقتىنى پەقەت اﷲ بىلىدۇ، لېكىن كىشىلەرنىڭ تولىسى ئۇنىڭ ۋاقتىنىڭ مەخپىي بولۇشىدىكى سەۋەبنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,187,' اﷲ خالىمىغان ئىكەن، ئۆزۈمگە پايدا يەتكۈزۈش، زىياننى ئۆزۈمدىن دەپئى قىلىش قولۇمدىن كەلمەيدۇ، (قىيامەتنىڭ قاچان بولۇشىنى قانداقمۇ بىلەي؟) ئەگەر مەن غەيبنى بىلىدىغان بولسام (دۇنيا مەنپەئەتلىرىدىن) نۇرغۇن مەنپەئەت ھاسىل قىلغان بولاتتىم، زىيان ـ زەخمەتكىمۇ ئۇچرىمىغان بولاتتىم (لېكىن مەن غەيبنى بىلمەيمەن، شۇڭا ماڭا تەقدىر قىلىنغان ياخشى ـ يامانلىق يېتىپ تۇرىدۇ) مەن پەقەت ئىمان ئېيتىدىغان قەۋم ئۈچۈن ئاگاھلاندۇرغۇچى ۋە خۇش خەۋەر بەرگۈچى قىلىپ ئەۋەتىلگەن پەيغەمبەرمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,188,' ئۇ سىلەرنى بىر جاندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتتى، (ئادەم ئەلەيھىسسالامنىڭ) ئۇنسى ـ ئۈلپەت ئېلىشى ئۈچۈن، خوتۇنىنى (يەنى ھەۋۋانى) ئۇنىڭ ئۆز جىنسىدىن ياراتتى، خوتۇنى بىلەن يېقىنچىلىق قىلغاندىن كېيىن، خوتۇنى يېنىككىنە قورساق كۆتۈرۈپ ئېغىر بوي يۈردى، ئۇ (بالىنىڭ قورساقتا ئۆسۈپ چوڭىيىشى بىلەن) ئېغىرلاشقاندىن كېيىن، (ئەر ـ خوتۇن) ئىككىيلەن پەرۋەردىگارى اﷲ قا: «ئەگەر بىزگە (ئەزالىرى) بېجىرىم پەرزەنت ئاتا قىلساڭ، نېمىتىڭگە ئەلۋەتتە شۈكۈر قىلغۇچىلاردىن بولىمىز» دەپ دۇئا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,189,' اﷲ ئۇلارغا بېجىرىم ئوغۇل پەرزەنت ئاتا قىلغان ئىدى، ئۇلارنىڭ ئەۋلادى اﷲ قا شېرىك كەلتۈرۈپ (بۇتلارغا چوقۇندى)، اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەن مەبۇدلىرىدىن پاكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,190,' ئۆزلىرى يارىتىلغان، ھېچ نەرسىنى يارىتىلمايدىغان نەرسىلەرنى (يەنى بۇتلارنى) اﷲ قا شېرىك كەلتۈرەمدۇ!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,191,' ئۇ نەرسىلەر چوقۇنغۇچىلىرىغا ياردەم بېرەلمەيدۇ، ئۆزلىرىگىمۇ ياردەم بېرەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,192,' ئۇلارنى توغرا يولغا چاقىرساڭلار، ئۇلار سىلەرگە ئەگەشمەيدۇ مەيلى ئۇلارنى دەۋەت قىلىڭلار، ياكى جىم تۇرۇڭلار، بۇ ئۇلار ئۈچۈن بەرىبىر ئوخشاش (يەنى سىلەر قايسى ھالەتتە بولماڭلار، ئۇلار سىلەرنىڭ دەۋىتىڭلارغا جاۋاب قايتۇرۇشقا قادىر ئەمەستۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,193,' سىلەر اﷲ نى قويۇپ ئىبادەت قىلغان بۇتلار سىلەرگە ئوخشاش مەخلۇقلاردۇر، (ئۇلارنى ئىلاھ دېگەن دەۋايىڭلاردا) راستچىل بولساڭلار، ئۇلارغا دۇئا قىلىپ بېقىڭلار، دۇئايىڭلارنى ئىجابەت قىلىپ باقسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,194,' ئۇلار (يەنى بۇتلار) نىڭ ماڭىدىغان پۇتلىرى بارمۇ؟ ياكى تۇتىدىغان قوللىرى بارمۇ؟ ياكى كۆرىدىغان كۆزلىرى بارمۇ ۋە ئاڭلايدىغان قۇلاقلىرى بارمۇ؟ (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «شېرىكلىرىڭلارنى (يەنى بۇتلىرىڭلارنى ماڭا قارشى) چاقىرىڭلار، ئاندىن (قولۇڭلاردىن كېلىشىچە) ماڭا زىيانكەشلىك قىلىڭلار، ئازراقمۇ مۆھلەت بەرمەڭلار (مەن اﷲ قا يۆلەنگەنلىكىم ئۈچۈن سىلەرگە پەرۋا قىلمايمەن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,195,' ھەقىقەتەن كىتابنى (يەنى قۇرئاننى) نازىل قىلغان اﷲ مېنىڭ ئىگەمدۇر، ئۇ ياخشىلارغا ئىگىدارچىلىق قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,196,' سىلەرنىڭ اﷲ نى قويۇپ ئىبادەت قىلىدىغان بۇتلىرىڭلار سىلەرگە ياردەم بېرەلمەيدۇ، ئۆزلىرىگىمۇ ياردەم بېرەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,197,' ئەگەر ئۇلارنى ھىدايەتكە دەۋەت قىلساڭلار، ئۇلار (ھىدايەت تاپماق تۈگۈل دەۋىتىڭنى) ئاڭلىمايدۇ، ئۇلار ساڭا قاراپ تۇرغاندەك كۆرۈنسىمۇ، لېكىن ئۇلار كۆرمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,198,' ئەپۇنى دوست تۇتقىن، ياخشىلىققا (يەنى ياخشى سۆز قىلىشقا، ياخشى ئىش قىلىشقا) بۇيرۇغىن، نادانلار بىلەن تەڭ بولمىغىن (يەنى نادانلارنىڭ قىلغىنىنى قىلماي، ئۇلارغا مۇلايىم بولغىن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,199,' ئەگەر شەيتان ساڭا ۋەسۋەسە قىلسا، (شەيتاننىڭ ۋەسۋەسىسىدىن) اﷲ قا سېغىنىپ پاناھ تىلىگىن اﷲ ھەقىقەتەن (سۆزۈڭنى) ئاڭلاپ تۇرغۇچىدۇر (ئىشىڭنى بىلىپ تۇرغۇچىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,200,' تەقۋادار كىشىلەر شەيتاننىڭ ۋەسۋەسىسىگە ئۇچرىسا، اﷲ نى ئەسلەيدۇ ـ دە، ھەقىقەتنى كۆرۈۋېلىپ، (شەيتاننىڭ ۋەسۋەسىسىدىن) خالاس تاپىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,201,' شەيتانلار (كۇففار) بۇرادەرلىرىنى بولۇشىچە ئازدۇرىدۇ، ئۇلارنى ئازدۇرۇشتىن بوشىشىپ قالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,202,'، (ئۇلار تەلەپ) قىلغان مۆجىزىلەردىن) بىرەر مۆجىزىنى كەلتۈرمىسەڭ، ئۇنى نېمىشقا (ئۆزۈڭ) ئىجاد قىلمىدىڭ؟ دېيىشىدۇ، (ئى مۇھەممەد!) ئۇلارغا ئېيتقىنكى، «(ئىش مېنىڭ ئىلكىمدە ئەمەس) مەن پەقەت پەرۋەردىگارىم تەرىپىدىن ماڭا ۋەھيى قىلىنغانغىلا ئەمەل قىلىمەن (ئۆزلىكىمدىن مۆجىزە يارىتالمايمەن)، بۇ (قۇرئاندا سىلەرگە) پەرۋەردىگارىڭلار تەرىپىدىن كەلتۈرۈلگەن دەلىللەردۇر (قۇرئان باشقا مۆجىزىلەرگە ئېھتىياج قالدۇرمايدۇ)، ئىمان ئېيتقان قەۋم ئۈچۈن ھىدايەتتۇر ۋە رەھمەتتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,203,' اﷲ نىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن، قۇرئان ئوقۇلغان چاغدا، ئۇنى دىققەت بىلەن ئاڭلاڭلار ۋە جىم تۇرۇڭلار (يەنى سۆز قىلماڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,204,' پەرۋەردىگارىڭنى يېلىنغان ۋە ئۇنىڭدىن قورققان ھالدا ئىچىڭدە ياد ئەتكىن، ئەتىگەندە ـ ئاخشامدا ئۇنى پەس ئاۋازدا زىكرى قىلغىن، غاپىللاردىن بولمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,7,205,' پەرۋەردىگارىڭنىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) اﷲ قا ئىبادەت قىلىشتىن باش تارتمايدۇ، اﷲ قا تەسبىھ ئېيتىدۇ ۋە ئۇنىڭغا سەجدە قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,1,'(ئى مۇھەممەد! ساھابىلىرىڭ) غەنىيمەتلەر (نى قانداق تەقسىم قىلىشىڭ) توغرۇلۇق سەندىن سورىشىدۇ، (ئۇلارغا) ئېيتقىنكى، «غەنىيمەتلەر (توغرىسىدا ھۆكۈم چىقىرىش) اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە خاستۇر، اﷲ تىن قورقۇڭلار، (ئىختىلاپ قىلىشماي ئىتتىپاق بولۇپ) ئاراڭلارنى تۈزەڭلار، ئەگەر سىلەر مۆمىن بولساڭلار، اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,2,' پەقەت اﷲ ياد ئېتىلسە دىللىرىدا قورقۇنچ پەيدا بولىدىغان، اﷲ نىڭ ئايەتلىرى تىلاۋەت قىلىنسا ئىمانى كۈچىيىدىغان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرلا (كامىل) مۆمىنلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,3,' ئۇلار (مۇكەممەل رەۋىشتە) ناماز ئوقۇيدۇ، بىز ئۇلارغا رىزىق قىلىپ بەرگەن پۇل ـ مالدىن (خۇدا يولىدا) سەرپ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,4,' ئەنە شۇلار ھەقىقىي مۆمىنلەردۇر، ئۇلار پەرۋەردىگارىنىڭ دەرگاھىدا يۇقىرى مەرتىۋىلەرگە، مەغپىرەتكە ۋە ئېسىل رىزىققا (يەنى جەننەتنىڭ تۈگىمەس نېمەتلىرىگە) ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,5,' (غەنىيمەت تەقسىماتى توغرىسىدىكى ھۆكۈمنىڭ اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە خاس قىلىنغانلىقىنى ئۇلارنىڭ ياقتۇرمىغانلىقى) پەرۋەردىگارىڭنىڭ سېنى ھەقلىق رەۋىشتە ئۆيۈڭدىن (يەنى ئۆيۈڭ جايلاشقان مەدىنىدىن ئۇرۇشقا) چىقارغانلىقىنى (ئۇلارنىڭ ياقتۇرمىغانلىقىغا) ئوخشايدۇ مۆمىنلەردىن بىر گۇرۇھ ئادەم (دۈشمەنگە قارشى ئۇرۇشقا چىقىشنى) ئەلۋەتتە يامان كۆرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,6,' ئۇلار گويا (ئالامەتلىرى) كۆرۈنۈپ تۇرۇۋاتقان ئۆلۈمگە ھەيدىلىۋاتقاندەك، ھەقىقەت ئايدىڭلاشقاندىن كېيىن، ھەقىقەت (يەنى ئۇرۇشقا چىقىش مەسىلىسى) ئۈستىدە سەن بىلەن مۇنازىرىلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,7,' ئۆز ۋاقتىدا اﷲ ئىككى گۇرۇھ (بىرى مۇشرىكلار كارۋىنى ، يەنى بىرى مۇشرىكلار قوشۇنى) دىن بىرىنىڭ قولۇڭلارغا كەلتۈرۈلۈشىنى ۋەدە قىلدى، سىلەر قورالسىز گۇرۇھنىڭ (يەنى كارۋاننىڭ) قولۇڭلارغا كەلتۈرۈلۈشىنى ياقتۇردۇڭلار اﷲ ئۆز سۆزلىرى ئارقىلىق ھەقنى ھەق قىلىشنى (يەنى ئىسلام دىنىنى ئۈستۈن قىلىشنى)، كاپىرلارنىڭ يىلتىزىنى قۇرۇتۇشنى خالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,8,' اﷲ گۇناھكار ئادەملەرنىڭ ياقتۇرمىغىنىغا قارىماي، ھەقنى ھەق (يەنى ئىسلامنى ئاشكارا) قىلىدۇ، باتىل (يەنى كۇفرى) نى بەربات قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,9,' ئۆز ۋاقتىدا (دۇئا قىلىپ) پەرۋەردىگارىڭلاردىن ياردەم تىلىدىڭلار، اﷲ :«سىلەرگە ئاقىمۇ ـ ئارقا (چۈشىدىغان) مىڭ پەرىشتە بىلەن ياردەم بېرىمەن» دەپ دۇئايىڭلارنى ئىجابەت قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,10,' اﷲ سىلەرگە پەقەت (غەلىبە قىلىدىغانلىقىڭلارغا) بېشارەت بېرىش ۋە كۆڭلۈڭلارنى ئارام تاپقۇزۇش ئۈچۈنلا ياردەم بەردى ياردەم پەقەت اﷲ تەرىپىدىنلا كېلىدۇ اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,11,' ئۆز ۋاقتىدا اﷲ تىنچلاندۇرۇش يۈزىسىدىن سىلەرگە ئۇيقۇ بېغىشلىدى؛ (تاھارەت ئېلىپ، غۇسلى قىلىپ) پاڭ بولۇشۇڭلار ئۈچۈن، سىلەردىن شەيتاننىڭ ۋەسۋەسىسىنى كەتكۈزۈش ئۈچۈن، (اﷲ نىڭ ياردىمىگە ئىشەنچ قىلىپ) كۆڭلۈڭلارنىڭ توق تۇرۇشى ئۈچۈن، قەدىمىڭلارنىڭ (قۇمغا پېتىپ كەتمەي) مەزمۇت تۇرۇشى ئۈچۈن، اﷲ سىلەرگە بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,12,' ئۆز ۋاقتىدا پەرۋەردىگارىڭ پەرىشتىلەرگە: «مەن سىلەر بىلەن بىللە، مۆمىنلەرنى (ئۇرۇش مەيدانىدا) ساباتلىق قىلىڭلار، كاپىرلارنىڭ دىللىرىغا قورقۇنچ سالىمەن» دەپ ۋەھيى قىلدى (قىلىچ بىلەن كاپىرلارنىڭ) گەدەنلىرىگە چېپىڭلار (يەنى باشلىرىنى كېسىڭلار)، ئۇلارنىڭ بارماقلىرىغا (ھەممە ئەزاسىغا چېپىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,13,' بۇ شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە قارشىلىق قىلدى كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە قارشىلىق قىلىدىكەن، اﷲ ئۇنى قاتتىق جازالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,14,' (ئى كاپىرلار جامائەسى! بۇ دۇنيادا) مۇشۇنداق جازانى تېتىڭلار كاپىرلار (ئاخىرەتتە) دوزاخ ئازابىغا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,15,' ئى مۆمىنلەر! كاپىرلارنىڭ ھۇجۇمىغا دۇچ كەلگەن چېغىڭلاردا، ئۇلارغا ئارقاڭلارنى قىلماڭلار (يەنى قاچماڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,16,' كىمكى قايتا ئۇرۇشۇش ياكى (ياردەم تىلەش) ئۈچۈن مۇسۇلمانلار جامائەسىگە قوشۇلۇش مەقسىتىدە بىر تەرەپكە يۆتكىلىش قىلماستىن، بەلكى دۈشمەنگە ئارقىسىنى قىلىدىكەن (يەنى قاچىدىكەن)، ئۇ ھەقىقەتەن اﷲ نىڭ غەزىپىگە ئۇچرايدۇ، ئۇنىڭ جايى دوزاخ بولىدۇ دوزاخ نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,17,' (ئى مۇسۇلمانلار! بەدرىدە) ئۇلارنى (يەنى مۇشرىكلارنى) سىلەر (ئۆز كۈچۈڭلار بىلەن) ئۆلتۈرگىنىڭلار يوق، بەلكى ئەمەلدە ئۇلارنى (سىلەرگە ياردەم بېرىپ، ئۇلارنىڭ دىللىرىدا قورقۇنچ پەيدا قىلىش بىلەن) اﷲ ئۆلتۈردى، (ئى مۇھەممەد بىر سىقىم توپىنى مۇشرىكلارغا) ئاتقىنىڭدا سەن ئاتمىدىڭ، بەلكى ئەمەلدە ئۇنى (مۇشرىكلارنىڭ كۆزلىرىگە) اﷲ ئاتتى (اﷲ نىڭ مۇنداق قىلىشى) مۆمىنلەرگە (ساۋاب، غەلىبە، غەنىيمەتلەردىن ئىبارەت) چىرايلىق ئىنئاملارنى ئاتا قىلىش ئۈچۈن ئىدى اﷲ ھەقىقەتەن (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى، ئەھۋاللىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,18,' بۇ (اﷲ نىڭ مۆمىنلەرگە بەرگەن ئىنئامىدۇر) اﷲ كاپىرلارنىڭ ھىيلىسىنى چوقۇم مەغلۇپ قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,19,' (ئى كۇففارلار جامائەسى!) ئەگەر سىلەر غەلىبىنى تىلىسەڭلار، غەلىبە ئاللىقاچان سىلەرگە كەلدى (يەنى ئۇ سىلەرنىڭ زىيىنىڭلارغا كەلدى) ئەگەر (پەيغەمبەر بىلەن دۈشمەنلىشىشتىن) يانساڭلار، بۇ سىلەر ئۈچۈن ياخشىدۇر، ئەگەر ئۇنىڭ بىلەن يەنە ئۇرۇشساڭلار، بىز ئۇنىڭغا يەنە ياردەم بېرىمىز، قوشۇنۇڭلار كۆپ بولغان تەقدىردىمۇ، ئۇ سىلەردىن ھېچ نەرسىنى دەپئى قىلىپ بېرەلمەيدۇ، اﷲ ھەقىقەتەن مۆمىنلەر بىلەن بىللىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,20,' ئى مۆمىنلەر! اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، سىلەر (قۇرئاننى) ئاڭلاپ تۇرۇپ ئۇنىڭدىن يۈز ئۆرۈمەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,21,' ئاڭلىماي تۇرۇپ ئاڭلىدۇق دېگەن كىشىلەرنى (يەنى كاپىرلارنى) دورىماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,22,' شۈبھىسىزكى، اﷲ نىڭ نەزىرىدە ھايۋانلارنىڭ ئەڭ يامىنى (ھەقنى ئاڭلىماي) گاس بولۇۋالغان، (ھەقنى سۆزلىمەي) گاچا بولۇۋالغان، (ياخشى ـ ياماننى پەرق ئەتمەي) ئەقلىنى يوقاتقان كىشىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,23,' ئەگەر اﷲ ئۇلاردا بىرەر ياخشىلىق بار دەپ بىلسە ئىدى، ئەلۋەتتە ئۇلارغا (ھەقنى) ئاڭلىتاتتى؛ ئۇلارغا ئاڭلاتقان تەقدىردىمۇ، ئۇلار ھەقتىن ئەلۋەتتە يۈز ئۆرۈگەن ھالدا باش تارتاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,24,' ئى مۆمىنلەر! اﷲ ۋە ئۇنىڭ پەيغەمبىرى سىلەرنى تىرىلدۈرىدىغان (يەنى ئەبەدىي ھاياتقا ئېرىشتۈرىدىغان) ئىمانغا دەۋەت قىلسا، ئۇنى قوبۇل قىلىڭلار بىلىڭلاركى، اﷲ كىشى بىلەن ئۇنىڭ قەلبى ئارىسىدا توسالغۇ بولالايدۇ (يەنى كىشىنىڭ دىلىنى ئىگىسىنىڭ خاھىشى بويىچە ئەمەس، بەلكى ئۆز خاھىشى بويىچە تەسەررۇپ قىلىدۇ) (قىيامەت كۈنى) سىلەر اﷲ نىڭ دەرگاھىغا يىغىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,25,' سىلەرنىڭ ئاراڭلاردىكى زۇلۇم قىلغانلارنىڭ بېشىغا كېلىش بىلەنلا چەكلىنىپ قالمايدىغان بالا ـ قازادىن ساقلىنىڭلار، بىلىڭلاركى، اﷲ نىڭ ئازابى قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,26,' ياد ئېيتىڭلاركى، ئۆز ۋاقتىدا سىلەر (مەككە) زېمىنىدا ئاز سانلىق بولۇپ، بوزەك قىلىنغان ئىدىڭلار، كىشىلەر (يەنى مۇشرىكلار) نىڭ تالان ـ تاراج قىلىشىدىن قورقاتتىڭلار ، شۈكۈر قىلىشىڭلار ئۈچۈن اﷲ سىلەرنى (مەدىنىدە) يەرلەشتۈردى، (بەدرى ئۇرۇشىدا) ئۆز ياردىمى بىلەن سىلەرنى كۈچلەندۈردى سىلەرگە ھالال نەرسىلەر (يەنى غەنىيمەتلەر) نى رىزىق قىلىپ بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,27,' ئى مۆمىنلەر! اﷲ قا، پەيغەمبەرگە خىيانەت قىلماڭلار، سىلەرگە قويۇلغان ئامانەتلەرگە بىلىپ تۇرۇپ خىيانەت قىلماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,28,' بىلىڭلاركى، سىلەرنىڭ ماللىرىڭلار، بالىلىرىڭلار سىلەر ئۈچۈن بىر تۈرلۈك سىناقتۇر، اﷲ نىڭ دەرگاھىدا چوڭ ساۋاب بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,29,' ئى مۆمىنلەر! ئەگەر اﷲ تىن قورقساڭلار، اﷲ سىلەرگە ھەق بىلەن باتىلنى ئايرىيدىغان ھىدايەت ئاتا قىلىدۇ، گۇناھىڭلارنى كەچۈرىدۇ، سىلەرگە مەغپىرەت قىلىدۇ اﷲ كاتتا پەزل ئىگىسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,30,' (ئى مۇھەممەد!) ئۆز ۋاقتىدا كاپىرلار سېنى قاماققا ئېلىش، ياكى ئۆلتۈرۈش ۋە ياكى (مەككىدىن) ھەيدەپ چىقىرىش ئۈچۈن مىكىر ئىشلەتتى، ئۇلار مىكىر ئىشلىتىدۇ، اﷲ ئۇلارنىڭ مىكرىنى بەربات قىلىدۇ، اﷲ نىڭ تەدبىرى ئۇلارنىڭ مىكرىدىن ئۈنۈملۈكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,31,' ئۇلارغا بىزنىڭ ئايەتلىرىمىز تىلاۋەت قىلىنسا، ئۇلار: «بىز (بۇ سۆزلەرنى) ئاللىقاچان ئاڭلىغان ئىدۇق، ئەگەر خالىساق ئۇنىڭغا ئوخشاش سۆزلەرنى بىزمۇ ئەلۋەتتە قىلالايتتۇق ، بۇ پەقەت بۇرۇنقىلاردىن قالغان ئەپسانىلاردۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,32,' ئۆز ۋاقتىدا ئۇلار: «ئەگەر بۇ (يەنى قۇرئان) سېنىڭ تەرىپىڭدىن نازىل بولغان ھەقىقەت بولىدىغان بولسا، ئاسماندىن ئۈستىمىزگە تاش ياغدۇرغىن ، ياكى بىزنى قاتتىق ئازاب بىلەن جازالىغىن!» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,33,' سەن ئۇلارنىڭ ئىچىدە تۇرغان چېغىڭدا (سېنى ھۆرمەتلەش يۈزىسىدىن) اﷲ ئۇلارغا ئازاب قىلمايدۇ، ئۇلار ئىستىغپار ئېيتىپ تۇرغان چاغدا اﷲ ئۇلارغا ئازاب قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,34,' ئۇلار (باشقىلارنىڭ) مەسجىدى ھەرەمغا كىرىشىنى توسۇۋاتسا، اﷲ ئۇلارغا نېمىشقا ئازاب قىلمىسۇن؟ ئۇلار مەسجىدى ھەرەمنىڭ ئىگىلىرى ئەمەس، مەسجىدى ھەرەمنىڭ ئىگىلىرى تەقۋادارلاردىن باشقىلار ئەمەستۇر، لېكىن (بۇنى) ئۇلارنىڭ تولىسى بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,35,' ئۇلارنىڭ مەسجىدى ھەرەمنىڭ يېنىدىكى ئىبادىتى پەقەت ئىسقىرتماق، چاۋاك چالماقتىنلا ئىبارەت بولدى (ئى كاپىرلار!) كاپىر بولغانلىقىڭلار تۈپەيلىدىن (دۇنيا ۋە ئاخىرەتنىڭ) ئازابىنى تېتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,36,' شۈبھىسىزكى، كاپىرلار مال ـ مۈلۈكلىرىنى (باشقىلارنى) اﷲ نىڭ يولىدىن توسۇش ئۈچۈن سەرپ قىلىدۇ، سەرپ قىلىنغاندىن كېيىن ئۇلارنىڭ مال ـ مۈلۈكلىرى ئۇلارغا ھەسرەت ئېلىپ كېلىدۇ، ئاخىر ئۇلار يېڭىلىدۇ كاپىرلار جەھەننەمگىلا ھەيدەپ كىرگۈزۈلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,37,' اﷲ نىڭ (ئۇلارنى دۇنيادا مەغلۇپ قىلىشى، ئاخىرەتتە جەھەننەمگە ھەيدىشى) كاپىرنى مۆمىندىن پەرقلەندۈرۈش ئۈچۈندۇر، اﷲ ھەممە كاپىرلارنى ئۈستى ـ ئۈستىگە دۆۋىلەپ توپلىغاندىن كېيىن، دوزاخقا تاشلايدۇ، ئەنە شۇلار زىيان تارتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,38,' (ئى مۇھەممەد!) كاپىرلارغا ئېيتقىنكى، ئەگەر ئۇلار (كۇفرىدىن، پەيغەمبەر بىلەن دۈشمەنلىشىشتىن) يانسا، ئۇلارنىڭ ئۆتكەنكى پەيغەمبەرلىرىمنى ئىنكار قىلغانلارنى ھالاك قىلىش مېنىڭ تۇتقان يولۇم بولۇپ كەلدى (ئۇلارنىمۇ شۇنداق قىلىمەن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,39,' پىتنە تۈگىگەن، دىن پۈتۈنلەي اﷲ ئۈچۈن بولغانغا قەدەر ئۇلار بىلەن ئۇرۇشۇڭلار؛ ئەگەر ئۇلار (كۇفرىدىن) يانسا، اﷲ ئۇلارنىڭ قىلمىشلىرىنى كۆرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,40,' ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە، (ئى مۆمىنلەر!) بىلىڭلاركى، اﷲ سىلەرنىڭ مەدەتكارىڭلاردۇر، اﷲ نېمىدېگەن ياخشى مەدەتكار، نېمىدېگەن ياخشى ياردەمچى!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,41,' (ئى مۆمىنلەر!) بىلىڭلاركى، سىلەر ئالغان غەنىيمەتنىڭ بەشتىن بىرى اﷲ قا، پەيغەمبەرگە، پەيغەمبەرنىڭ خىش ـ ئەقرىبالىرىغا، يېتىملەرگە، مىسكىنلەرگە، ئىبن سەبىللەرگە خاستۇر ئەگەر سىلەر اﷲ قا، ئىككى گۇرۇھ (يەنى مۇسۇلمانلار بىلەن كاپىرلار) ئۇچرىشىپ ھەق بىلەن ناھەق ئايرىلغان كۈندە (يەنى بەدرى ئۇرۇشى بولغان كۈندە) بەندىمىزگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) بىز نازىل قىلغان نەرسىلەر (يەنى ئايەتلەر ۋە پەرىشتىلەر) گە ئىشەنسەڭلار (اﷲ نىڭ غەنىيمەت توغرىسىدىكى ھۆكمىنىڭ ئەنە شۇ ئىكەنلىكىنى بىلىڭلار) اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,42,' ئۆز ۋاقتىدا سىلەر ۋادىنىڭ (مەدىنىگە) يېقىن تەرىپىدە ئىدىڭلار، ئۇلار ۋادىنىڭ (مەدىنىگە) يىراق تەرىپىدە ئىدى (قۇرەيشنىڭ) سودا كارۋىنى بولسا سىلەرنىڭ تۆۋىنىڭلاردا ئىدى (مۇشرىكلار بىلەن ئۇچرىشىشنى) ۋەدىلەشكەن بولساڭلار، سىلەر (ئۆزۈڭلارنىڭ ئازلىقىنى، مۇشرىكلارنىڭ كۆپلۈكىنى كۆرۈپ) ئەلۋەتتە بۇنىڭغا خىلاپلىق قىلغان بولاتتىڭلار، لېكىن اﷲ بولۇشقا تېگىشلىك ئىشنى (يەنى مۇسۇلمانلارنى ئەزىز، مۇشرىكلارنى خار قىلىشنى) ئەمەلگە ئاشۇرۇش ئۈچۈن (سىلەرنى بەدرىدە مۇشرىكلار بىلەن ۋەدىسىز ئۇچراشتۇردى) اﷲ نىڭ مۇنداق قىلىشى ھالاك بولىدىغانلارنىڭ روشەن دەلىلنى كۆرۈپ ئاندىن ھالاك بولۇشى، ياشايدىغانلارنىڭ روشەن دەلىلنى كۆرۈپ ئاندىن ياشىشى ئۈچۈن ئىدى اﷲ (بەندىلىرىنىڭ سۆزلىرىنى)، ئەلۋەتتە، ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,43,' (ئى مۇھەممەد!) ئۆز ۋاقتىدا، اﷲ چۈشۈڭدە ساڭا ئۇلارنى (يەنى دۈشمەنلىرىڭنى) ئاز كۆرسەتتى، ئەگەر ساڭا ئۇلارنى كۆپ كۆرسەتكەن بولسا، سىلەرگە چوقۇم قورقۇنچ چۈشەتتى ۋە جەڭ ئىشىدا چوقۇم دەتالاش قىلاتتىڭلار، لېكىن اﷲ (سىلەرنى مۇنداق قىلىشتىن) ساقلىدى اﷲ دىللاردىكىنى ئەلۋەتتە بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,44,' اﷲ بولۇشقا تېگىشلىك ئىشنى ئىشقا ئاشۇرۇش ئۈچۈن، دۈشمەن بىلەن ئۇچراشقان چېغىڭلاردا، (دۈشمەنلەرگە قارشى تۇرۇشقا جۈرئەت قىلسۇن دەپ) ئۇلارنى سىلەرنىڭ كۆزۈڭلارغا ئاز كۆرسەتتى ۋە سىلەرنى ئۇلارنىڭ كۆزلىرىگە (سىلەرگە قارشى تەييارلىقتا بولمىسۇن دەپ) ئاز كۆرسەتتى ھەممە ئىش اﷲ قا قايتۇرۇلىدۇ (اﷲ ئۆزى خالىغانچە ھۆكۈم قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,45,' ئى مۆمىنلەر! (مۇشرىكلاردىن) بىر جامائەگە (يەنى دۈشمەن قوشۇنىغا) ئۇچراشقان چېغىڭلاردا ساباتلىق كۆرسىتىڭلار، مۇۋەپپەقىيەت قازىنىشىڭلار ئۈچۈن اﷲ نى كۆپ ياد ئېتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,46,' (پۈتۈن سۆز ھەرىكەتلىرىڭلاردا) اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، ئىختىلاپ قىلىشماڭلار، بولمىسا، (دۈشمەن بىلەن ئۇچرىشىشتىن) قورقۇپ قالىسىلەر، كۈچ ـ قۇۋۋىتىڭلار كېتىپ قالىدۇ؛ سەۋر قىلىڭلار، اﷲ ھەقىقەتەن سەۋر قىلغۇچىلار بىلەن بىللىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,47,' يۇرتلىرىدىن چوڭچىلىق ۋە رىيا بىلەن چىققان كىشىلەر (يەنى قۇرەيش مۇشرىكلىرى) گە ئوخشاش بولماڭلار، ئۇلار (كىشىلەرنى) اﷲ نىڭ يولىدىن توسىدۇ، اﷲ ئۇلارنىڭ ھەممە ئىشلىرىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,48,' ئۆز ۋاقتىدا شەيتان ئۇلارغا (قەبىھ) ئەمەللىرىنى چىرايلىق كۆرسىتىپ: «ھېچقانداق كىشى سىلەرنى يېڭەلمەيدۇ، مەن ھەقىقەتەن سىلەرگە مەدەتكار» دېگەن ئىدى ئىككى قوشۇن ئۇچراشقان چاغدا شەيتان ئارقىسىغا چېكىندى ۋە: «مەن سىلەردىن ئادا ـ جۇدامەن ، سىلەر كۆرمەيۋاتقاننى ھەقىقەتەن كۆرۈپ تۇرۇۋاتىمەن، مەن راستلا اﷲ تىن قورقىمەن، اﷲ نىڭ ئازابى قاتتىقتۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,49,' ئۆز ۋاقتىدا مۇناپىقلار ۋە دىللىرىدا كېسىلى (يەنى شەك) بارلار: «بۇلارنى (يەنى مۇسۇلمانلارنى) ئۇلارنىڭ دىنى ئالدىدى (يەنى مۇسۇلمانلار ئۆز دىنىغا مەغرۇر بولۇپ، ئۆزلىرىنى يېڭىلمەس دەپ گۇمان قىلىپ، ئاز سانلىق تۇرۇقلۇق كۆپ سانلىقلار بىلەن ئۇرۇشۇشقا چىقتى)» دېدى كىمكى اﷲ قا يۆلىنىدىكەن (اﷲ ئۇنىڭغا ئەلۋەتتە ياردەم بېرىدۇ) چۈنكى اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,50,' پەرىشتىلەر (بەدرى ئۇرۇشىدا) كاپىرلارنىڭ جانلىرىنى ئېلىۋاتقاندا، ئۇلارنىڭ يۈزلىرىگە ۋە ئارقىلىرىغا ئۇرۇۋاتقانلىقىنى كۆرسەڭ ئىدىڭ (ئەلۋەتتە قورقۇنچلۇق ھالنى كۆرگەن بولاتتىڭ) (پەرىشتىلەر ئۇلارغا) «دوزاخنىڭ كۆيدۈرگۈچى ئازابىنى تېتىڭلار!» (دەيتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,51,' بۇ (ئازاب) سىلەرنىڭ قىلمىش جىنايىتىڭلار تۈپەيلىدىندۇر، اﷲ بەندىلىرىگە زۇلۇم قىلغۇچى ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,52,' (بۇ كاپىرلارنىڭ گۇناھ قىلىش ئادىتى) پىرئەۋن جامائەسى ۋە ئۇلاردىن بۇرۇنقىلارنىڭ ئادىتىگە ئوخشايدۇ، ئۇلار اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلدى، گۇناھلىرى تۈپەيلىدىن، اﷲ ئۇلارنى ھالاك قىلدى اﷲ ھەقىقەتەن كۈچلۈكتۇر، اﷲ نىڭ ئازابى قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,53,' بۇ (يەنى ئۇلارغا نازىل بولغان ئازاب) شۇنىڭ ئۈچۈنكى، بىرەر قەۋم ئۆزلىرىنىڭ ھالىتىنى (كۇفرى ۋە گۇناھ بىلەن) ئۆزگەرتمىگۈچە اﷲ ئۇلارغا بەرگەن نېمىتىنى ئۆزگەرتىۋەتمەيدۇ، اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (قىلمىشلىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,54,' (ئۇلارنىڭ ئادىتى) پىرئەۋن جامائەسىنىڭ ۋە ئۇلاردىن بۇرۇنقىلارنىڭ ئادىتىگە ئوخشايدۇكى، ئۇلار پەرۋەردىگارىنىڭ ئايەتلىرىنى ئىنكار قىلغان ئىدى گۇناھلىرى تۈپەيلىدىن ئۇلارنى ھالاك قىلدۇق، پىرئەۋن جامائەسىنى (دېڭىزغا) غەرق قىلدۇق، ئۇلارنىڭ ھەممىسى (كۇفرىلىق ۋە گۇناھ قىلىپ، ئۆزلىرىنى ئازابقا دۇچار قىلىش بىلەن) ئۆزلىرىگە زۇلۇم قىلغۇچى (قەۋم) بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,55,' اﷲ نىڭ نەزىرىدە ھايۋانلارنىڭ ئەڭ يامىنى كۇفرىدا چىڭ تۇرغانلاردۇركى، ئۇلار ھەقىقەتەن ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,56,' (ئى مۇھەممەد!) سەن ئۇلارنىڭ ئارىسىدىكى مەلۇم كىشىلەر بىلەن (مۇشرىكلارغا ياردەم بەرمەسلىكلىرى ئۈستىدە) مۇئاھىدە تۈزدۈڭ، ئۇلار بولسا ھەر قېتىمدا مۇئاھىدىسىنى بۇزىدۇ، ئۇلار اﷲ تىن قورقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,57,' ئەگەر ئۇرۇشتا ئۇلار ئۈستىدىن غەلىبە قىلساڭ، ئارقىدىكىلىرىنىڭ قېچىشى ۋە ئىبرەت ئېلىشى ئۈچۈن ئۇلارنى قاتتىق جازالىغىن (يەنى ئۇلارنى باشقىلارغا ئىبرەت قىلغىنكى، ئۇلارنىڭ سەن بىلەن ئۇرۇش قىلىشقا دەرمانى قالمىسۇن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,58,' ئەگەر سەن (مۇئاھىدە تۈزگەن) قەۋمدىن خىيانەت (ئالامەتلىرىنى) سەزسەڭ، مۇئاھىدىسىنى ئۇلارغا ئوچۇق ـ يورۇقلۇق بىلەن تاشلاپ بەرگىن (يەنى سەن بىلەن مۇئاھىدە تۈزۈشكەن قەۋمدىن خىيانەت شەپىسى كۆرۈلسە، ئۇلارغا تۇيۇقسىز ھۇجۇم قىلماستىن، مۇئاھىدىنىڭ ئەمەلدىن قالغانلىقىنى ئالدى بىلەن ئۇلارغا ئۇقتۇرۇپ قويغىن) اﷲ ھەقىقەتەن خائىنلارنى دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,59,' (بەدرى ئۇرۇشىدا ئۆلتۈرۈلمەي قالغان) كاپىرلار قۇتۇلدۇق دەپ ئويلىمىسۇن، ئۇلار (پەرۋەردىگارىنىڭ جازاسىدىن) ھەقىقەتەن قېچىپ قۇتۇلالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,60,' دۈشمەنلىرىڭلار (بىلەن ئۇرۇش قىلىش) ئۈچۈن، قولۇڭلاردىن كېلىشىچە قورال كۈچى، جەڭ ئېتى تەييارلاڭلار، بۇنىڭ بىلەن، اﷲ نىڭ دۈشمىنىنى، ئۆزۈڭلارنىڭ دۈشمىنىڭلارنى ۋە ئۇلاردىن باشقا دۈشمەنلەرنى قورقۇتىسىلەر ، ئۇلارنى سىلەر تونۇمايسىلەر، اﷲ تونۇيدۇ، سىلەرنىڭ اﷲ يولىدا سەرپ قىلغىنىڭلار مەيلى نېمە بولسا بولسۇن، سىلەرگە ئۇنىڭ ساۋابى تولۇق بېرىلىدۇ، سىلەرگە زۇلۇم قىلىنمايدۇ (يەنى بۇ ساۋابتىن ھېچ نەرسە كېمەيتىلمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,61,' ئەگەر ئۇلار تىنچلىققا مايىل بولسا، سەنمۇ تىنچلىققا مايىل بولغىن، اﷲ قا تەۋەككۈل قىلغىن اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,62,' ئەگەر ئۇلار (تەييارلىق قىلىۋېلىش ئۈچۈن سۈلھى بىلەن) سېنى ئالدىماقچى بولسا، اﷲ (ئۇلارنىڭ شەررىدىن) شەكسىز ساڭا كۇپايە قىلغۇچىدۇر اﷲ سېنى ئۆزنىڭ ياردىمى بىلەن ۋە مۆمىنلەر بىلەن كۈچلەندۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,63,' اﷲ مۆمىنلەرنىڭ دىللىرىنى بىرلەشتۈردى سەن يەر يۈزىدىكى پۈتۈن بايلىقنى سەرپ قىلىپمۇ ئۇلارنىڭ دىللىرىنى بىرلەشتۈرەلمەيتىڭ؛ لېكىن اﷲ (ئۆزىنىڭ قۇدرەت كامىلەسى بىلەن) ئۇلارنى ئىناق قىلدى شۈبھىسىزكى، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,64,' ئى پەيغەمبەر! (يالغۇز) اﷲ ساڭا ۋە ساڭا ئەگەشكەن مۆمىنلەرگە كۇپايە قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,65,' ئى پەيغەمبەر! مۆمىنلەرنى (مۇشرىكلار بىلەن) ئۇرۇش قىلىشقا رىغبەتلەندۈرگىن، ئەگەر سىلەردە چىداملىق يىگىرمە ئادەم بولىدىغان بولسا، (دۈشمەندىن)  نى يېڭەلەيدۇ؛ ئەگەر سىلەردە (چىداملىق)  ئادەم بولىدىغان بولسا، كاپىرلاردىن  نى يېڭەلەيدۇ؛ چۈنكى ئۇلار (جاھىل) قەۋم بولۇپ، (اﷲ نىڭ ھېكمىتىنى) چۈشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,66,' اﷲ ئەمدى سىلەرنىڭ (يۈكۈڭلارنى) يىنىكلەتتى، سىلەرنىڭ ئاجىزلىقىڭلارنى بىلدى، ئەگەر سىلەردە چىداملىق  ئادەم بولىدىغان بولسا،  (دۈشمەن) نى يېڭەلەيدۇ، ئەگەر سىلەردە (چىداملىق)  ئادەم بولىدىغان بولسا، اﷲ نىڭ ئىزنى بىلەن  (دۈشمەن) نى يېڭەلەيدۇ اﷲ چىداملىقلار بىلەن بىللىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,67,' پەيغەمبەرگە زېمىندا دۈشمەننى كۆپرەك ئۆلتۈرمەي تۇرۇپ (يەنى مۇشرىكلىكنىڭ ھەيۋىسىنى يوقىتىپ، ئىسلامغا قۇۋۋەت بەرمەي تۇرۇپ)، ئەسىرلەردىن فىدىيە ئېلىش لايىق ئەمەس ئىدى (ئى مۆمىنلەر! سىلەر فىدىيە ئېلىش بىلەن) دۇنيا مەنپەئىتىنى كۆزلەيسىلەر، اﷲ سىلەرگە ئاخىرەتنى (يەنى ئاخىرەتنىڭ ساۋابىنى) تىلەيدۇ اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,68,' اﷲ نىڭ (ئوچۇق چەكلەنمىگەن ئىشنى قىلغانلارنى جازالىماسلىق دېگەن) ھۆكمى ئەزىلىسى بولمىسا ئىدى، (ئەسىرلەردىن) فىدىيە ئالغانلىقىڭلار ئۈچۈن ئەلۋەتتە زور ئازابقا دۇچار بولاتتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,69,' (ئى جىھاد قىلغۇچىلار جامائەسى!) غەنىيمەت ئالغان نەرسەڭلارنى ھالال ۋە پاك بىلىپ يەڭلار، اﷲ (نىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىش) دىن قورقۇڭلار اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,70,' ئى پەيغەمبەر! قولۇڭلاردىكى ئەسىرلەرگە ئېيتقىنكى، ئەگەر اﷲ دىلىڭلاردا ئىمان بارلىقىنى بىلسە (يەنى دىلىڭلاردا ئىمان بولسا، اﷲ ئۇنى بىلىدۇ)، سىلەرگە ئۆزۈڭلاردىن ئېلىنغان فىدىيىدىن ياخشىراق نەرسىنى بېرىدۇ، (ئىلگىرىكى گۇناھلىرىڭلارنى) مەغپىرەت قىلىدۇ اﷲ (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,71,' (ئى مۇھەممەد!) ئەگەر ئۇلار (يەنى مۇشۇ ئەسەرلەر ئىماننى دەۋا قىلىش ئارقىلىق) ساڭا خىيانەت قىلماقچى بولغان بولسا، ئۇنداقتا ئىلگىرى ئۇلار (كۇفرى بىلەن، ئەھدىنى بۇزۇش بىلەن) اﷲ قا خىيانەت قىلغان ئىدى، اﷲ سېنى ئۇلارنىڭ ئۈستىدىن غالىب قىلدى اﷲ (بولغۇسى ھەممە ئىشنى) بىلگۈچى، ئۆز ھېكمىتى بويىچە ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,72,' ئىمان ئېيتقانلار، (اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى سۆيۈش يۈزىسىدىن يۇرتىنى تاشلاپ) ھىجرەت قىلغان، پۇل ـ ماللىرى ۋە جانلىرى بىلەن اﷲ يولىدا جىھاد قىلغانلار (يەنى مۇھاجىرلار)، (يۇرتىدا مۇھاجىرلارغا) جاي بەرگەنلەر ۋە ياردەم كۆرسەتكەنلەر (يەنى ئەنسارلار) ـ ئەنە شۇلار ئەلۋەتتە بىر ـ بىرىگە ئىگىدۇر (يەنى بىر ـ بىرىگە ياردەمچىدۇر، بىر ـ بىرىگە مىراسخوردۇر) ئىمان ئېيتقان ئەمما (مەدىنىگە) ھىجرەت قىلمىغانلار بىلەن سىلەرنىڭ ئوتتۇراڭلاردا تاكى ئۇلار ھىجرەت قىلغانغا قەدەر ئۆزئارا ئىگە بولۇش (يەنى ياردەملىشىش، مىراسخور بولۇش) بولمايدۇ؛ ئەگەر دىن ئىشىدا ئۇلار سىلەردىن ياردەم تەلەپ قىلسا، ئۇلارنىڭ دۈشمىنى بىلەن سىلەرنىڭ ئاراڭلاردا كېلىشىم بولمىسىلا، ئۇلارغا ياردەم بېرىشىڭلار كېرەك، اﷲ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,73,' كاپىرلار (ياردەمدە ۋە مىراستا) بىر ـ بىرىگە ئىگىدۇر، ئەگەر بۇ ھۆكۈمگە ئەمەل قىلمىساڭلار، يەر يۈزىدە پىتنە ۋە چوڭ قالايمىقانچىلىق تۇغۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,74,' ئىمان ئېيتقانلار، (مەدىنىگە) ھىجرەت قىلغانلار، اﷲ نىڭ يولىدا جىھاد قىلغانلار، (يۇرتىدا مۇھاجىرلارغا) جاي بەرگەنلەر ۋە ياردەم كۆرسەتكەنلەر (يەنى ئەنسارلار) ـ ئەنە شۇلار ھەقىقىي مۆمىنلەردۇر، ئۇلار (نىڭ گۇناھى) مەغپىرەت قىلىنىدۇ، ئۇلار (جەننەتتە) ئېسىل رىزىققا ئىگە بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,8,75,' كېيىن ئىمان ئېيتىپ (مەدىنىگە) ھىجرەت قىلغانلار ۋە سىلەر بىلەن بىللە جىھاد قىلغانلار سىلەردىن سانىلىدۇ اﷲ نىڭ ھۆكمىدە، ئۇرۇق ـ تۇغقانلار بىر ـ بىرىگە مىراسخور بولۇشقا (ياتلاردىن) ئەڭ ھەقلىقتۇر، شۈبھىسىزكى، اﷲ ھەر شەيئىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,1,'(بۇ) اﷲ ۋە ئۇنىڭ پەيغەمبىرىنىڭ سىلەر بىلەن مۇئاھىدە تۈزۈشكەن مۇشرىكلارغا قاراتقان ئادا ـ جۇدا بولۇش (يەنى مۇئاھىدىنى بىكار قىلىش) ئېلانىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,2,' (ئى مۇشرىكلار!) يەر يۈزىدە (خالىغىنىڭلارچە) تۆت ئاي يۈرۈڭلار، بىلىڭلاركى، سىلەر اﷲ (نىڭ ئازابى) دىن قېچىپ قۇتۇلالمايسىلەر، (بىلىڭلاركى) اﷲ كاپىرلارنى خار قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,3,' (بۇ) چوڭ ھەج كۈنىدە اﷲ ۋە ئۇنىڭ پەيغەمبىرى تەرىپىدىن كىشىلەرگە چىقىرىلغان ئېلاندۇركى، اﷲ ۋە ئۇنىڭ پەيغەمبىرى مۇشرىكلاردىن ئادا ـ جۇدادۇر ئەگەر (كۇفرىدىن) تەۋبە قىلساڭلار، بۇ سىلەرگە (گۇمراھلىقتا كېتىۋېرىشتىن) ياخشىدۇر، ئەگەر (ئىسلامدىن) يۈز ئۆرۈسەڭلار، بىلىڭلاركى، اﷲ (نىڭ ئازابى) دىن قېچىپ قۇتۇلالمايسىلەر كاپىرلارغا قاتتىق ئازاب بىلەن بېشارەت بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,4,' ئەمما مۇشرىكلار ئىچىدىكى سىلەر بىلەن مۇئاھىدە تۈزگەن، مۇئاھىدىگە ھېچقانداق خىلاپلىق قىلمىغان، سىلەرگە قارشى ھېچقانداق ئادەمگە ياردەم بەرمىگەنلەرگە كەلسەك، ئۇلار بىلەن تۈزگەن بۇ مۇئاھىدىگە ئۇنىڭ ۋاقتى توشقانغا قەدەر رىئايە قىلىڭلار، شۈبھىسىزكى،، اﷲ تەقۋادارلارنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,5,' (ئۇرۇش قىلىش) ھارام قىلىنغان ئايلار ئۆتۈپ كەتكەندە، مۇشرىكلارنى قەيەردە ئۇچراتساڭلار، شۇ يەردە ئۆلتۈرۈڭلار، ئەسىرگە ئېلىڭلار، قورشاڭلار، ئۇلارنىڭ ئۆتىدىغان يوللىرىنى كۆزىتىپ تۇرۇڭلار، ئەگەر ئۇلار تەۋبە قىلسا، ناماز ئوقۇسا، زاكات بەرسە، ئۇلارنى قويۇپ بېرىڭلار، شۈبھىسىزكى، اﷲ (تەۋبە قىلغۇچىلارغا) مەغپىرەت قىلغۇچىدۇر، رەھىم قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,6,' ئەگەر مۇشرىكلاردىن بىرەر كىشى سەندىن ئامانلىق تىلىسە، تاكى ئۇ اﷲ نىڭ كالامىنى (يەنى قۇرئاننى) ئاڭلىغانغا (يەنى پىكىر قىلىپ، ئۇنىڭ ھەقىقىتىگە يەتكەنگە) قەدەر، ئۇنىڭغا ئامانلىق بەرگىن، ئاندىن (ئۇ ئىمان ئېيتمىسا)، ئۇنى (جېنى، مېلى) ئامان تاپىدىغان جايغا يەتكۈزۈپ قويغىن، بۇ شۇنىڭ ئۈچۈنكى، ئۇلار (ئىسلام دىنىنىڭ ھەقىقىتىنى) ئۇقمايدىغان قەۋمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,7,' اﷲ نىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ قارىشىچە، (ئەھدىسىگە ۋاپا قىلمايدىغان) مۇشرىكلاردا قانداقمۇ مۇئاھىدە دېگەن نەرسە بولسۇن؟ ئەمما مەسجىدى ھەرەم قېشىدا سىلەر بىلەن ئەھدە تۈزۈشكەن مۇشرىكلار ئەگەر ئەھدىسىگە ۋاپا قىلىدىكەن، سىلەرمۇ ۋاپا قىلىڭلار، شۈبھىسىزكى، اﷲ تەقۋادارلارنى (يەنى پەرۋەردىگاردىن قورققان، ئەھدىسىگە ۋاپا قىلغان، خىيانەت قىلمىغانلارنى) دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,8,' مۇشرىكلار ئەھدىسىگە قانداقمۇ ۋاپا قىلسۇن؟ ئەگەر ئۇلار سىلەرنىڭ ئۈستۈڭلاردىن غەلىبە قىلسا، سىلەرنىڭ توغراڭلاردا نە تۇغقانچىلىققا، نە ئەھدىگە رىئايە قىلمايدۇ سىلەرنى ئېغىزلىرىدا خۇش قىلىدۇ، دىللىرىدا (ۋەدىسىدە تۇرۇشنى) خالىمايدۇ (يەنى ئاغزىدا چىرايلىق سۆزلىگىنى بىلەن، كۆڭۈللىرى ئاداۋەت، نىفاق بىلەن تولغان) ئۇلارنىڭ تولىسى پاسىقلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,9,' ئۇلار اﷲ نىڭ ئايەتلىرىنى (دۇنيانىڭ مال ـ مۈلۈكلىرىدىن) ئەرزىمەس نەرسىلەرگە تېگىشتى، (كىشىلەرنى) اﷲ نىڭ يولىدىن توستى ھەقىقەتەن ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,10,' ئۇلار مۆمىنلەر ھەققىدە نە تۇغقانچىلىققا، نە ئەھدىگە رىئايە قىلمايدۇ ئەنە شۇلار (سىلەرگە زۇلۇم قىلىشتا) ھەددىدىن ئاشقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,11,' ئەگەر ئۇلار (كۇفرىدىن) تەۋبە قىلسا، ناماز ئۆتىسە، زاكات بەرسە، دىنىي جەھەتتە سىلەرنىڭ قېرىندىشىڭلار بولىدۇ (يەنى ھوقۇق ۋە مەجبۇرىيەتتە ئۇلار سىلەر بىلەن ئوخشاشتۇر) ئۇقىدىغان قەۋم ئۈچۈن، ئايەتلەرنى تەپسىلىي بايان قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,12,' ئەگەر ئۇلار ئەھدە بەرگەندىن كېيىن، قەسەملىرىنى بۇزسا ۋە دىنىڭلارنى ئەيىبلىسە، ئۇلارنىڭ (بۇنىڭدىن) چەكلىنىشلىرى ئۈچۈن، كۇفرىنىڭ كاتتىباشلىرىغا ئۇرۇش ئېچىڭلار ئۇلارنىڭ قەسىمىنىڭ ھەقىقەتەن ئېتىبارى يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,13,' (ئى مۆمىنلەر جامائەسى! ئەھدە بەرگەندە) ئىچكەن قەسىمىنى بۇزغان، پەيغەمبەرنى (مەككە) دىن ھەيدەپ چىقىرىشنى قەستلىگەن ۋە سىلەرگە ئالدى بىلەن ھۇجۇم قىلغان قەۋمگە ئۇرۇش ئاچمايسىلەر؟ ئۇلاردىن قورقامسىلەر؟ ئەگەر مۆمىن بولساڭلار، قورقۇشۇڭلارغا اﷲ ئەڭ لايىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,14,' ئۇلارغا ئۇرۇش ئېچىڭلار، اﷲ ئۇلارنى سىلەرنىڭ قولۇڭلار بىلەن جازالايدۇ اﷲ ئۇلارنى خار قىلىدۇ، اﷲ سىلەرنى ئۇلار ئۈستىدىن غالىب قىلىدۇ (ئىسلام دىنىنى ئۈستۈن قىلىپ، كاپىرلارنى جازالاپ) مۆمىن قەۋمنىڭ كۆڭلىگە شىپالىق (يەنى تەسەللى) بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,15,' ھەم ئۇلارنى دىللىرىدىكى ئاچچىقتىن خالاس قىلىدۇ اﷲ خالىغان ئادەمنى تەۋبىگە مۇۋەپپەق قىلىدۇ اﷲ ھەممىنى بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,16,' اﷲ سىلەرنىڭ ئاراڭلاردىكى جىھاد قىلغانلارنى، اﷲ تىن، اﷲ نىڭ پەيغەمبىرىدىن ۋە مۆمىنلەردىن باشقىنى سىرداش قىلىۋالمىغانلارنى بىلمەي (يەنى ئوتتۇرىغا چىقارماي) تۇرۇپ سىلەر ئۆز ھالىمىزغا قويۇپ بېرىلىمىز، (يەنى سىنالمايمىز) دەپ ئويلامسىلەر؟ اﷲ سىلەرنىڭ قىلمىشىڭلاردىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,17,' مۇشرىكلار ئۆزلىرىنىڭ كۇفرىنى ئىقرار قىلىپ تۇرۇقلۇق (يەنى كاپىرلىق بىلەن اﷲ نىڭ مەسجىدلىرىنى ئاۋات قىلىش بىر ـ بىرى بىلەن سىغىشالمايدۇ)، (ئۇلارنىڭ) اﷲ نىڭ مەسجىدلىرىنى ئاۋات قىلىشى توغرا ئەمەس، ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرىنىڭ نەتىجىسى بولمايدۇ، ئۇلار دوزاختا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,18,' اﷲ نىڭ مەسجىدلىرىنى پەقەت اﷲ قا، ئاخىرەت كۈنىگە ئىمان ئېيتقان، ناماز ئۆتىگەن، زاكات بەرگەن، اﷲ تىن باشقىدىن قورقمىغان كىشىلەرلا ئاۋات قىلىدۇ (يەنى مەسجىدنى رېمونت قىلىش، سەرەمجانلاشتۇرۇش، تازىلاش، مەسجىدتە داۋاملىق ناماز ئۆتەش، قۇرئان تىلاۋەت قىلىش قاتارلىق ئىشلارنى قىلىدۇ)، ئەنە شۇلارنىڭ ھىدايەت تاپقۇچىلاردىن بولۇشى كۆزدە تۇتۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,19,' سىلەر ھاجىلارنى سۇ بىلەن تەمىنلەشنى، مەسجىدى ھەرەمنى ئاۋات قىلىشنى اﷲ قا، ئاخىرەت كۈنىگە ئىمان ئېيتقان ۋە اﷲ نىڭ يولىدا جىھاد قىلغانلارنىڭ (ئىمانىغا) ئوخشاش ھېسابلامسىلەر؟ اﷲ نىڭ نەزىرىدە ئۇلار ئوخشاش ئەمەس اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,20,' ئىمان ئېيتقانلارنىڭ، ھىجرەت قىلغانلارنىڭ ۋە ماللىرى ھەم جانلىرى بىلەن اﷲ نىڭ يولىدا جىھاد قىلغانلارنىڭ دەرىجىسى اﷲ نىڭ دەرگاھىدا ئەڭ كاتتىدۇر؛ ئەنە شۇلار (ساۋاب تېپىش بىلەن) مەقسىتىگە ئېرىشكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,21,' پەرۋەردىگارى ئۇلارغا ئۆزىنىڭ رەھمىتى، رازىلىقى ۋە جەننەتلىرى بىلەن خۇش خەۋەر بېرىدۇ، ئۇلار جەننەتلەردە مەڭگۈلۈك نېمەتكە ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,22,' ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ شۈبھىسىزكى، اﷲ نىڭ دەرگاھىدا (ئۇلارغا) كاتتا ساۋاب بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,23,' ئى مۆمىنلەر! ئەگەر ئاتىلىرىڭلار، قېرىندىشىڭلار ئىماندىن كۇفرىنى ئارتۇق كۆرسە (كۇفرىدا مەھكەم تۇرسا)، ئۇلارنى دوست تۇتماڭلار ئىچىڭلاردىن كىملەركى، ئۇلارنى دوست تۇتىدىكەن، ئۇلار زالىملاردۇر (يەنى ئۇلارغا ئوخشاش مۇشرىكتۇر، چۈنكى مۇشرىكلىككە رازى بولغان ئادەممۇ مۇشرىك ھېسابلىنىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,24,' ئېيتقىنكى، «ئەگەر سىلەرنىڭ ئاتاڭلار، ئوغۇللىرىڭلار، قېرىندىشىڭلار، خوتۇنلىرىڭلار ، ئۇرۇق ـ تۇغقانلىرىڭلار (ھەمدە ئۇلاردىن باشقىلار)، تاپقان پۇل ـ ماللىرىڭلار، ئاقماي قېلىشىدىن قورققان تىجارىتىڭلار، ياخشى كۆرۈدىغان ئۆيلىرىڭلار سىلەر ئۈچۈن اﷲ تىن، اﷲ نىڭ پەيغەمبىرىدىن ۋە اﷲ نىڭ يولىدا جىھاد قىلىشتىنمۇ سۆيۈملۈك بولسا (يەنى شۇلار بىلەن بولۇپ كېتىپ، اﷲ نىڭ پەيغەمبىرىگە ياردەم بەرمىسەڭلار)، ئۇ ھالدا سىلەر تاكى اﷲ نىڭ ئەمر (يەنى مەككىنى پەتھى قىلىش ئىزنى) كەلگۈچە كۈتۈڭلار، اﷲ پاسىق قەۋمنى (يەنى اﷲ نىڭ دىنىنىڭ چەك ـ چېگرىسىدىن چىقىپ كەتكۈچىلەرنى) ھىدايەت قىلمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,25,' اﷲ سىلەرگە نۇرغۇن جەڭ مەيدانلىرىدا ۋە ھۈنەين كۈنىدە (يەنى جېڭىدە) ھەقىقەتەن ياردەم بەردى ئەينى ۋاقىتتا سانىڭلارنىڭ كۆپلۈكىدىن خۇشاللىنىپ كەتتىڭلار (يەنى بۈگۈن بىزنىڭ سانىمىز كۆپ، مەغلۇپ بولمايمىز دېدىڭلار، بۇ چاغدا سىلەرنىڭ سانىڭلار  مىڭ، دۈشمىنىڭلارنىڭ سانى  ئىدى)، سانىڭلارنىڭ كۆپلۈكى سىلەرگە قىلچە ئەسقاتمىدى (قاتتىق قورققىنىڭلاردىن) كەڭ زېمىن سىلەرگە تار تۇيۇلدى ئاندىن (مەغلۇپ بولۇپ، رەسۇلۇللاھنى ئازغىنا مۆمىنلەر بىلەن تاشلاپ) ئارقاڭلارغا قاراپ قاچتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,26,' ئاندىن پەيغەمبىرىگە ۋە مۆمىنلەرگە (مەرھەمەت قىلىپ) خاتىرجەملىك بېغىشلىدى، سىلەرگە قوشۇنلارنى (يەنى سىلەرگە ياردەمگە پەرىشتىلەرنى) چۈشۈردى، ئۇلارنى سىلەر كۆرمىدىڭلار (شۇنىڭ بىلەن سىلەر غەلىبە قىلدىڭلار)، اﷲ كاپىرلارنى (ئۆلتۈرۈش ۋە ئەسىر ئېلىنىش بىلەن) ئازابلىدى، كاپىرلارنىڭ جازاسى ئەنە شۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,27,' شۇنىڭدىن كېيىن، اﷲ خالىغان ئادەمگە تەۋبىنى نېسىپ قىلىدۇ (يەنى ئىسلام دىنىغا كىرىشكە مۇۋەپپەق قىلىدۇ) اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,28,' ئى مۆمىنلەر! مۇشرىكلار (اﷲ نى ئىنكار قىلغانلىقلىرى ئۈچۈن) پەقەت نىجىستۇر، مۇشۇ يىلدىن (يەنى ھىجرىيىنىڭ  ـ يىلىدىن) كېيىن ئۇلار مەسجىدى ھەرەمغا (ھەرەمگە) يېقىنلاشمىسۇن (يەنى ھەج قىلمىسۇن، ئۆمرە قىلمىسۇن) ئەگەر سىلەر (ئى مۆمىنلەر! مۇشرىكلارنىڭ ھەرەمگە كىرىشتىن مەنئى قىلىنىشى بىلەن) پېقىرلىقتىن قورقساڭلار، اﷲ خالىسا ئۆز پەزلى بىلەن سىلەرنى باي قىلىدۇ، شۈبھىسىزكى، اﷲ (سىلەرگە پايدىلىق ئىشلارنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,29,' سىلەر ئەھلى كىتابتىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشەنمەيدىغانلار (يەھۇدىيلار ئۇزەيرىنى اﷲ نىڭ ئوغلى، ناسارالار ئىسانى اﷲ نىڭ ئوغلى دەپ ئېتىقاد قىلغانلىقلىرى ئۈچۈن، ئۇلار اﷲ قا ۋە ئاخىرەت كۈنىگە ئېتىقاد قىلىمىز دېگەن بىلەنمۇ ئېتىقاد قىلمىغاننىڭ ئورنىدىدۇر) بىلەن، اﷲ (ئۆزىنىڭ كىتابىدا) ۋە ئۇنىڭ پەيغەمبىرى (سۈننىتىدە) ھارام قىلغان نەرسىلەرنى ھارام بىلمەيدىغانلار ۋە ھەق دىن (يەنى ئىسلام دىنى) غا ئېتىقاد قىلمايدىغانلار بىلەن، تاكى ئۇلار سىلەرگە بويسۇنۇپ خار ھالدا جىزىيە تۆلىگەنگە قەدەر، ئۇرۇش قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,30,' يەھۇدىيلار: «ئۈزەير اﷲ نىڭ ئوغلىدۇر» دېدى، ناسارالار: «مەسىھ (يەنى ئىسا) اﷲ نىڭ ئوغلىدۇر» دېدى، بۇ، ئۇلارنىڭ ئاغزىدىكى (دەلىلسىز) سۆزىدۇر، (ئۇلارنىڭ سۆزلىرى) ئىلگىرىكى كاپىرلارنىڭ (يەنى مۇشرىكلارنىڭ: «پەرىشتىلەر اﷲ نىڭ قىزلىرىدۇر» دېگەن) سۆزلىرىگە ئوخشايدۇ اﷲ ئۇلارغا لەنەت قىلسۇنكى، (اﷲ نىڭ بالىسى يوقلۇقىغا روشەن دەلىل تۇرسا، ھەقىقەتتىن باتىلغا بۇرۇلۇپ) ئۇلار قانداقمۇ (اﷲ نىڭ بالىسى بار دەپ) اﷲ قا يالغان چاپلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,31,' ئۇلار ئۆزلىرىنىڭ ھىبرىلىرىنى ، راھىبلىرىنى (يەھۇدىيلار بىلەن ناسارالارنىڭ دىنىي باشلىقلىرىنى، ئۆلىمالىرىنى) ۋە مەريەم ئوغلى مەسىھنى مەبۇد قىلىۋالدى (ھالبۇكى) ئۇلار (پەيغەمبەرلەر ئارقىلىق) پەقەت بىر ئىلاھقا ئىبادەت قىلىشقا بۇيرۇلغان ئىدى، ئۇنىڭدىن باشقا (يەنى ئالەملەرنىڭ پەرۋەردىگارى اﷲ تىن باشقا) ھېچ مەبۇد (بەرھەق) يوقتۇر، اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەنلىكلىرىدىن پاكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,32,' ئۇلار (يەنى مۇشرىكلار، يەھۇدىيلار ۋە ناسارالار) اﷲ نىڭ نۇرىنى (يەنى ئىسلام دىنىنى) ئېغىزلىرى (يەنى يالغان ـ ياۋىداق بوھتانلىرى) بىلەن ئۆچۈرمەكچى بولىدۇ، اﷲ ئۆز نۇرىنى ئۈستۈن قىلماي قالمايدۇ، كاپىرلار (بۇنى) يامان كۆرگەن تەقدىردىمۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,33,' اﷲ ھەق دىن (ئىسلام) نى بارلىق دىنلاردىن ئۈستۈن قىلىش ئۈچۈن، ئۆزىنىڭ پەيغەمبىرىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى تولۇق) ھىدايەت ۋە ھەق دىن بىلەن ئەۋەتتى، مۇشرىكلار ئۇنىڭ (ئۈستۈن بولۇشىنى) يامان كۆرگەن تەقدىردىمۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,34,' ئى مۆمىنلەر! ھىبرىلەر ۋە راھىبلەردىن (يەھۇدىي ۋە ناسارا ئۆلىمالىرىدىن) نۇرغۇنلىرى كىشىلەرنىڭ پۇل ـ ماللىرىنى ھەقىقەتەن ھارام يەيدۇ، (كىشىلەرنى) اﷲ نىڭ دىنىغا كىرىشتىن توسىدۇ، ئالتۇن ـ كۈمۈش يىغىپ، ئۇنى اﷲ نىڭ يولىدا سەرپ قىلمايدىغانلارغا (دوزاختا بولىدىغان) قاتتىق ئازاب بىلەن بېشارەت بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,35,' ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ئۇ ئالتۇن ـ كۈمۈشلەر جەھەننەمنىڭ ئوتىدا قىزىتىلىپ، ئۇنىڭ بىلەن ئۇلارنىڭ پېشانىلىرى ، يانلىرى ۋە دۈمبىلىرى داغلىنىدۇ ئۇلارغا: «بۇ سىلەرنىڭ ئۆزۈڭلار ئۈچۈن يىغقان ئالتۇن ـ كۈمۈشۈڭلار (سىلەر بۇ دۇنيايىڭلاردىكى اﷲ نىڭ ھەققىنى ئادا قىلمىدىڭلار) يىغقان ئالتۇن ـ كۈمۈشۈڭلارنىڭ ۋابالىنى تېتىڭلار» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,36,' شۈبھىسىزكى، ئاسمان ۋە زېمىن يارىتىلغاندىن تارتىپ (قەمەرى) ئايلارنىڭ سانى اﷲ نىڭ دەرگاھىدا (يەنى لەۋھۇلمەھپۇزدا)  دۇر، ئۇلاردىن تۆتى (يەنى زۇلقەئدە، زۇلھەججە، مۇھەررەم، رەجەپ) ئۇرۇش قىلىش ھارام قىلىنغان ئايلاردۇر، بۇ توغرا دىندۇر (يەنى مەزكۇر تۆت ئايدا ئۇرۇشنىڭ ھارام قىلىنىشى اﷲ نىڭ توغرا دىنىدۇر)، بۇ ئايلاردا (ئۇلارنىڭ ھۆرمىتىنى ساقلىماسلىق بىلەن، اﷲ چەكلىگەن گۇناھ ئىشلارنى قىلىش بىلەن) ئۆزۈڭلارغا زىيان سالماڭلار، مۇشرىكلار سىلەرگە بىرلىكتە ھۇجۇم قىلغاندەك، سىلەرمۇ ئۇلارغا قارشى بىرلىكتە ئۇرۇش قىلىڭلار بىلىڭلاركى، اﷲ تەقۋادارلار بىلەن بىللىدۇر (يەنى اﷲ نىڭ ئەمرىنى بەجا كەلتۈرۈش، مەنئى قىلغان ئىشلىرىدىن چەكلىنىش بىلەن اﷲ تىن قورققۇچىلارغا اﷲ نىڭ ياردەم بېرىدىغانلىقىغا ئىشەنچىدە بولۇڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,37,'ئاينى ئارقىغا سۈرۈش (يەنى ئۇرۇش ھارام قىلىنغان ئاينى باشقا بىر ئايغا ئۆزگەرتىپ، يىلنىڭ  ئېيىنى  ئايغا كۆپەيتىش) كۇفرىنى زىيادە قىلىشتۇر، بۇنىڭ بىلەن كاپىرلار ئازدۇرۇلىدۇ؛ ئۇلار اﷲ ھارام قىلغان ئايلارنىڭ سانىغا مۇۋاپىقلىشىش ئۈچۈن، ئۇنى بىر يىل ھالال قىلسا، ئىككىنچى يىل ھارام قىلىدۇ، شۇنىڭ بىلەن ئۇلار اﷲ ھارام قىلغان ئاينى ھالال قىلىدۇ، ئۇلارغا ئۆزلىرىنىڭ يامان ئەمەللىرى چىرايلىق كۆرسىتىلدى اﷲ كاپىر قەۋمنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,38,' ئى مۆمىنلەر! سىلەرگە اﷲ نىڭ يولىدا جىھاد قىلىشقا چىقىڭلار دېيىلسە، نېمىشقا يۇرتۇڭلاردىن ئايرىلغىڭلار كەلمەيدۇ؟ دۇنيا ھاياتىنى ئاخىرەتتىكى (بەخت) بىلەن تېگىشىشكە رازى بولدۇڭلارمۇ؟ دۇنيا ھاياتىدىن بەھرىمەن بولۇش ئاخىرەتنىڭ نېمەتلىرى بىلەن سېلىشتۇرغاندا، ئەرزىمەس نەرسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,39,' ئەگەر سىلەر جىھادقا چىقمىساڭلار، اﷲ سىلەرگە قاتتىق ئازاب قىلىدۇ (سىلەرنى ھالاك قىلىپ) ئورنۇڭلارغا سىلەردىن باشقا قەۋمنى كەلتۈرىدۇ، سىلەر اﷲ قا قىلچە زىيان يەتكۈزەلمەيسىلەر، اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,40,' ئەگەر سىلەر اﷲ نىڭ پەيغەمبىرىگە ياردەم قىلمىساڭلار (اﷲ ياردەم قىلىدۇ)، اﷲ ھەقىقەتەن ئۇنىڭغا ياردەم قىلغان ئىدى ئۆز ۋاقتىدا كاپىرلار ئۇنى (مەككىدىن) ھەيدەپ چىقارغان ئىدى ئۇنىڭغا پەقەت بىر كىشى (يەنى ئەبۇبەكرى سىددىق) ھەمراھ ئىدى ئەينى زاماندا ئۇ ئىككىسى غاردا ئىدى (رەسۇلۇللاھ) ھەمراھىغا: «زەم قىلمىغىن، اﷲ ھەقىقەتەن بىز بىلەن بىللە» دەيتتى، اﷲ ئۇنىڭغا (يەنى پەيغەمبىرىگە) خاتىرجەملىك بېغىشلىدى، ئۇنىڭغا (پەرىشتىلەردىن بولغان) قوشۇنلار بىلەن مەدەت بەردى، ئۇلارنى سىلەر كۆرمىدىڭلار، كاپىرلارنىڭ سۆزىنى (يەنى شېرىك كەلىمىسىنى) پەس قىلدى، اﷲ نىڭ سۆزى (يەنى كەلىمە تەۋھىد) ئۈستۈندۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,41,' (ئى مۆمىنلەر جامائەسى!) سىلەر يىنىك بولغان ياكى ئېغىر بولغان ھالەتتە (يەنى مەيلى ياش ـ قېرى، پىيادە، ئۇلاغلىق بولۇڭلار ، ئوڭۇشلۇق ۋە قىيىن شارائىتتا بولۇڭلار ، ئىختىيارىي ۋە ئىختىيارسىز بولۇڭلار ، ھەممە ئەھۋالدا) جىھادقا چىقىڭلار، اﷲ نىڭ يولىدا مېلىڭلار بىلەن، جېنىڭلار بىلەن جىھاد قىلىڭلار، ئەگەر بىلسەڭلار مۇنداق قىلىش سىلەر ئۈچۈن ياخشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,42,' ئەگەر (ئۇلارنىڭ دەۋەت قىلىنغىنى) ئاسان قولغا كېلىدىغان غەنىيمەت ۋە (يىراق ئەمەس) ئوتتۇراھال سەپەر بولىدىغان بولسا، ئۇلار چوقۇم ساڭا ئەگىشىپ (غەنىيمەت ئېلىش مەقسىتىدە) چىقاتتى لېكىن، بۇ ئارىلىق ئۇلارغا يىراق بىلىندى ئۇلار بىلەن قەسەم ئىچىپ: «ئەگەر چىقىشقا قادىر بولالىغان بولساق، ئەلۋەتتە سىلەر بىلەن بىللە چىقاتتۇق» دەيدۇ، ئۇلار (يالغان قەسەملىرى تۈپەيلىدىن) ئۆزلىرىنى ئۆزلىرى ھالاك قىلىدۇ، اﷲ بىلىدۇكى، ئۇلار شەك ـ شۈبھىسىز يالغانچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,43,' (ئى مۇھەممەد!) اﷲ سېنى كەچۈردى، (ئۆزرىسىدە) راستچىللار بىلەن يالغانچىلار (ساڭا ئېنىق بولمىغۇچە نېمىشقا ئۇلارغا چىقماسلىققا) رۇخسەت بەردىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,44,' (ئى مۇھەممەد!) اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغانلار سەندىن ماللىرى بىلەن ۋە جانلىرى بىلەن جىھاد قىلىشقا رۇخسەت سورىمايدۇ اﷲ تەقۋادارلارنى ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,45,' (ئى مۇھەممەد) سەندىن پەقەت اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشەنمەيدىغانلار، دىللىرىدا (اﷲ نىڭ بىرلىكىگە ۋە سېنىڭ پەيغەمبەرلىكىڭگە) گۇمانى بارلار رۇخسەت سورايدۇ، ئۇلار ئۆز گۇمانىدا تېڭىرقاپ يۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,46,' ئەگەر ئۇلارنىڭ جىھادقا چىقىش نىيىتى بولسا، ئەلۋەتتە ئۇنىڭغا تەييارلىق قىلاتتى، لېكىن اﷲ ئۇلارنىڭ چىقىشىنى ياقتۇرمىدى، (ئۇلارنىڭ دىللىرىغا ھۇرۇنلۇقنى سېلىپ) ئۇلارنىڭ ئىرادىسىنى سۇندۇردى (ئۇلارغا) «ئۆيلىرىدە قېلىپ قالغۇچىلار (يەنى ئاياللار، كىچىك بالىلار، ئاجىزلار) بىلەن قېلىڭلار» دېيىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,47,' ئەگەر ئۇلار سىلەر بىلەن بىرلىكتە چىققان بولسا، ئاراڭلاردا پەقەت پىتنە ـ پاساتنى كۆپەيتەتتى، ئاراڭلارغا بۆلگۈنچىلىك سېلىش ئۈچۈن چوقۇم سۇخەنچىلىك بىلەن شۇغۇللىناتتى ، ئاراڭلاردا ئۇلار ئۈچۈن تىڭ ـ تىڭلايدىغانلار بار، اﷲ زالىملارنى (يەنى مۇناپىقلارنىڭ ئىچى ـ تېشىنى) ئوبدان بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,48,' ئىلگىرى ئۇلار تاكى اﷲ نىڭ ياردىمى كەلگەنگە، اﷲ نىڭ دىنى ئۈستۈنلۈك قازانغانغا قەدەر، ھەقىقەتەن، بۆلگۈنچىلىك سېلىشقا ئورۇنغان ، ساڭا ھىيلە ـ مىكىرلەرنى ئىشلەتكەن ئىدى، ھالبۇكى، ئۇلار بۇنداق بولۇشىنى (يەنى دىننىڭ ئۈستۈنلۈك قازىنىشىنى) يامان كۆرگۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,49,' ئۇلارنىڭ ئارىسىدا: «ماڭا (يەنى مېنىڭ قېلىشىمغا) رۇخسەت قىلغىن، مېنى بالاغا گىرىپتار قىلمىغىن (يەنى رۇمنىڭ ئاياللىرى بىلەن گۇناھ قىلىپ سېلىشىمغا سەۋەبچى بولمىغىن)» دېگۈچىلەرمۇ بار ئەمەلدە ئۇلار (مۇناپىقلىقتىن ئىبارەت چوڭ) بالاغا گىرىپتار بولدى شۈبھىسىزكى، جەھەننەم كاپىرلارنى ئوراپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,50,' ئەگەر سەن بىرەر ياخشىلىققا ئېرىشسەڭ، ئۇلارنىڭ كۆڭلى يېرىم بولىدۇ؛ ئەگەر ساڭا بىرەر مۇسىبەت كەلسە: «ئىشنىڭ ئالدىنى ئالغان ئىكەنمىز» دېيىشىپ خۇشال قايتىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,51,' ئېيتقىنكى، «بىزگە پەقەت اﷲ نىڭ تەقدىر قىلغان نەرسىسى يېتىدۇ، ئۇ بىزنىڭ مەدەتكارىمىزدۇر مۆمىنلەر اﷲ قا تەۋەككۈل قىلسۇن (يەنى مۆمىنلەر ھەممە ئىشنى اﷲ قا تاپشۇرسۇن، ئۇندىن باشقا ھېچ ئەھەدىگە يۆلەنمىسۇن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,52,' ئېيتقىنكى، «سىلەر پەقەت بىزنىڭ (غەلىبە قىلىش ياكى شېھىت بولۇشتىن ئىبارەت) ئىككى خىل ياخشى ئاقىۋەتنىڭ بىرىگە ئېرىشىشىمىزنى كۈتۈۋاتىسىلەر، بىزمۇ اﷲ نىڭ سىلەرگە ئۆز دەرگاھىدىن ئازاب چۈشۈرۈشنى ياكى بىزنىڭ قولىمىز ئارقىلىق سىلەرنى جازالىشىنى كۈتۈۋاتىمىز، سىلەرمۇ (ئۆزۈڭلارغا كەلگەننى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە (ئۆزىمىزگە كەلگەننى) كۈتەيلى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,53,' ئېيتقىنكى، «(ئى مۇناپىقلار جامائەسى!) سىلەر ئىختىيارىي ياكى مەجبۇرىي يوسۇندا (قانچىلىك پۇل ـ مال سەرپ قىلساڭلارمۇ) ھەرگىز قوبۇل قىلىنمايدۇ، چۈنكى سىلەر اﷲ نىڭ ئىتائىتىدىن چىققان قەۋم بولدۇڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,54,' ئۇلارنىڭ بەرگەن نەپىقىلىرى شۇنىڭ ئۈچۈن قوبۇل بولمايدۇكى، ئۇلار اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلىدۇ، نامازنى خۇش ياقماسلىق بىلەن ئوقۇيدۇ، (پۇل ـ مېلىنى) رازىلىق بىلەن بەرمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,55,' ئۇلارنىڭ مال ـ مۈلكى ۋە ئەۋلادى سېنى ئەجەبلەندۈرمىسۇن، اﷲ ئۇلارنى بۇ دۇنيادا شۇ نەرسىلەر بىلەن ئازابلاشنى ۋە ئۇلارنىڭ كاپىر پېتى جان ئۈزۈشنى ئىرادە قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,56,' ئۇلار: «بىز چوقۇم سىلەر (يەنى سىلەرگە ئوخشاش مۇسۇلمانلار) دىن» دەپ، اﷲ بىلەن قەسەم ئىچىدۇ ئۇلار سىلەردىن ئەمەس، لېكىن ئۇلار (زىيان ـ زەخمەتكە ئۇچراشتىن قورقۇپ، ئاغزىدا مۇسۇلمان بولغان) قورقۇنچاق قەۋمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,57,' ئەگەر ئۇلار بىرەر قورغان ياكى غار ۋە ياكى گەمىنى تاپسا ئىدى، ئۇلار شۇ تەرەپكە قاراپ ئەلۋەتتە يۈگۈرۈشكەن بولاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,58,' (ئى مۇھەممەد!) ئۇلارنىڭ بەزىسى سېنىڭ سەدىقە (ۋە غەنىيمەت) لەرنى تەقسىم قىلىشىڭنى ئەيىبلەيدۇ، ئۇلارغا ئۇنىڭدىن بېرىلسە خۇش بولىدۇ، بېرىلمىسە خاپا بولۇشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,59,' ئەگەر ئۇلار اﷲ نىڭ ۋە اﷲ نىڭ پەيغەمبىرىنىڭ ئۇلارغا بەرگىنىگە رازى بولسا ۋە: «اﷲ بىزگە كۆرىدۇ قىلىدۇ، اﷲ ۋە ئۇنىڭ پەيغەمبىرى بىزگە ئۆز پەزلىدىن ئاتا قىلىدۇ، بىز ھەقىقەتەن اﷲ تىن (اﷲ نىڭ پەزلىنى، ئېھسانىنى) ئۆتۈنۈپ سورىغۇچىلارمىز» دېسە، ئەلۋەتتە، ئۇلار ئۈچۈن ياخشى بولاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,60,' زاكات پەقەت پېقىرلارغا، مىسكىنلەرگە، زاكات خادىملىرىغا، دىللىرىنى ئىسلامغا مايىل قىلىش كۆزدە تۇتۇلغانلارغا، قۇللارنى ئازاد قىلىشقا، قەرزدارلارغا، اﷲ نىڭ يولىغا، ئىبن سەبىللەرگە بېرىلىدۇ، بۇ اﷲ نىڭ بەلگىلىمىسىدۇر، اﷲ (بەندىلىرىنىڭ مەنپەئەتنى) ئوبدان بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,61,' ئۇلارنىڭ بەزىلىرى پەيغەمبەرگە (سۆزى ۋە ھەرىكىتى ئارقىلىق) ئەزىيەت يەتكۈزىدۇ ۋە (پەيغەمبەرنى) «ئاڭلىغان نەرسىسىنىڭ ھەممىسىگە ئىشىنىدۇ» دەيدۇ ئېيتقىنكى، «(دۇرۇس، ئۇ) سىلەرگە پايدىلىق سۆزلەرنى ئاڭلاپ ئىشىنىدۇ، اﷲ نىڭ سۆزلىرىگە ئىشىنىدۇ، مۆمىنلەرنىڭ سۆزىگە (ئۇلارنىڭ ئىخلاسىنى بىلگەنلىكتىن) ئىشىنىدۇ، ئۇ سىلەردىن ئىمان ئېيتقانلار ئۈچۈن رەھمەتتۇر» اﷲ نىڭ پەيغەمبىرىگە ئەزىيەت يەتكۈزىدىغانلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,62,' ئۇلار سىلەرنى رازى قىلىش ئۈچۈن (پەيغەمبەرگە تېگىدىغان گەپنى قىلمىدۇق دەپ) اﷲ بىلەن قەسەم قىلىدۇ، ئەگەر ئۇلار راستلا مۆمىن بولىدىغان بولسا، اﷲ نى ۋە ئۇنىڭ پەيغەمبىرىنى رازى قىلىشلىرى كېرەك ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,63,' (بۇ مۇناپىقلار) اﷲ قا ۋە ئۇنى پەيغەمبىرىگە كىمكى قارشىلىق كۆرسىتىدىكەن، ئۇنىڭ دوزاخقا كىرىدىغانلىقىنى، دوزاختا مەڭگۈ قالىدىغانلىقىنى بىلمەمدۇ؟ ئەنە شۇ چوڭ رەسۋالىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,64,' مۇناپىقلار ئۆزلىرى توغرىسىدا بىرەر سۈرە نازىل بولۇپ، ئۇلارنىڭ دىللىرىدىكىنى (يەنى نىفاقنى) مۆمىنلەرگە خەۋەر بېرىشتىن قورقىدۇ ئېيتقىنكى، «سىلەر (اﷲ نىڭ دىنىنى خالىغىنىڭلارچە) مەسخىرە قىلىڭلار، سىلەر (پاش بولۇشتىن) قورقىدىغان نەرسىنى (يەنى مۇناپىقلىقنى) اﷲ چوقۇم ئاشكارا قىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,65,' (ئى مۇھەممەد!) ئەگەر سەن ئۇلارنىڭ (مەسخىرە قىلغانلىقىنى) سورىساڭ، ئۇلار: «بىز (راست ئەمەس)، پەقەت ئىچ پۇشۇقى قىلىپ ئوينىشىپ دەپ قويدۇق» دەيدۇ (بۇ مۇناپىقلارغا) «سىلەر اﷲ نىڭ دىنىنى، اﷲ نىڭ ئايەتلىرىنى (يەنى كىتابىنى) ۋە اﷲ نىڭ پەيغەمبىرىنى مەسخىرە قىلدىڭلارمۇ؟» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,66,' سىلەر (يالغان قەسەم ئىچىپ) ئۆزرە ئېيتماڭلار، سىلەر ئىمان ئېيتقىنىڭلاردىن كېيىن، (پەيغەمبەرنى مەسخىرە قىلىش بىلەن) كاپىر بولدۇڭلار، سىلەردىن بىر گۇرۇھنى (راست تەۋبە قىلغانلىقلىرى ئۈچۈن) ئەپۇ قىلساق، يەنە بىر گۇرۇھنى گۇناھكار بولغانلىقلىرى (يەنى مۇناپىقلىق بىلەن گۇناھنى داۋاملاشتۇرغانلىقلىرى) ئۈچۈن جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,67,' مۇناپىق ئەرلەر بىلەن مۇناپىق ئاياللار (مۇناپىقلىقتا ۋە ئىماندىن يىراق بولۇشتا) بىر ـ بىرىگە ئوخشايدۇ، ئۇلار يامانلىققا بۇيرۇيدۇ، ياخشىلىقتىن توسىدۇ، ئۇلار قوللىرىنى يۇمۇۋالىدۇ (يەنى بېخىللىق قىلىپ سەدىقە بەرمەيدۇ) ئۇلار اﷲ نى ئۇنتۇدى (يەنى اﷲ نىڭ تائىتىنى تەرك ئەتتى)، اﷲ مۇ ئۇلارنى ئۇنتۇدى (يەنى اﷲ ئۇلارنى ئۆزىنىڭ پەزلىدىن ۋە رەھمىتىدىن مەھرۇم قىلدى) شۈبھىسىزكى، مۇناپىقلار پاسىقلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,68,' اﷲ مۇناپىق ئەرلەرگە، مۇناپىق ئاياللار ۋە كۇففارلارغا دوزاخنى ۋەدە قىلدى، ئۇلار دوزاختا مەڭگۈ قالىدۇ دوزاخ ئۇلارنى (ئازابلاشقا) يېتەرلىكتۇر، اﷲ ئۇلارغا لەنەت قىلدى، ئۇلار مەڭگۈلۈك ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,69,' (ئى مۇناپىقلار، سىلەرنىڭ ھالىڭلار) سىلەردىن بۇرۇن ئۆتكەن (ھالاك بولغان ئۈممەتلەرنىڭ ھالىغا) ئوخشايدۇ، ئۇلار كۈچ ـ قۇۋۋەتتە سىلەردىن كۈچلۈك، مال ـ مۈلكى ۋە ئەۋلادى سىلەرنىڭكىگە قارىغاندا كۆپ ئىدى سىلەردىن بۇرۇن ئۆتكەنلەر (دۇنيانىڭ لەززەتلىرى جەھەتتىكى) نېسىۋىسىدىن بەھرىمەن بولغاندەك، سىلەرمۇ (دۇنيانىڭ لەززەتلىرى جەھەتتىكى) نېسىۋەڭلاردىن بەھرىمەن بولدۇڭلار؛ ئۇلار باتىلغا چۆمگەندەك، سىلەرمۇ باتىلغا چۆمدۈڭلار؛ ئەنە شۇلارنىڭ ياخشى ئەمەللىرى دۇنيا ۋە ئاخىرەتتە ئېتىبارسىزدۇر، ئەنە شۇلار زىيان تارتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,70,' ئۇلارغا ئۇلاردىن بۇرۇن ئۆتكەنلەرنىڭ، نۇھ ، ئاد ۋە سەمۇد قەۋملىرىنىڭ، ئىبراھىم قەۋمىنىڭ، مەديەن ئاھالىسىنىڭ، كۆمتۈرۈۋېتىلگەن قىشلاقلارنىڭ (يەنى لۇت ئەلەيھىسسالام قەۋمى قىشلاقلىرىنىڭ) خەۋەرلىرى كەلمىدىمۇ؟ ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەر ئېلىپ كەلدى (بۇ مۆجىزىلەرنى ئۇلار ئىنكار قىلدى) اﷲ ئۇلارغا زۇلۇم قىلغىنى يوق، لېكىن ئۇلار (كاپىر بولۇش ۋە گۇناھ قىلىش بىلەن) ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,71,' مۆمىن ئەرلەر، مۆمىن ئاياللار بىر ـ بىرى بىلەن دوستتۇر، ئۇلار (كىشىلەرنى) ياخشى ئىشلارنى قىلىشقا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، نامازنى (تولۇق) ئادا قىلىدۇ، زاكات بېرىدۇ، اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە ئىتائەت قىلىدۇ، ئەنە شۇلارغا اﷲ رەھىم قىلىدۇ، اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,72,' اﷲ مۆمىن ئەرلەر ۋە مۆمىن ئاياللارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى ۋەدە قىلدى ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ، ھەمىشە تۇرىدىغان جەننەتلەردە گۈزەل جايلارنى ۋەدە قىلدى (ئۇلار ئېرىشىدىغان) اﷲ نىڭ رازىلىقى (جەننەت نېمەتلىرىنىڭ ھەممىسىدىن) كاتتىدۇر، بۇ چوڭ بەختتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,73,' ئى پەيغەمبەر! كاپىرلارغا ۋە مۇناپىقلارغا قارشى جىھاد قىلغىن، ئۇلارغا (قارشى غازات قىلىش ۋە قورقۇنچ سېلىش بىلەن) قاتتىق مۇئامىلە قىلغىن، ئۇلارنىڭ جايى جەھەننەمدۇر، ئۇ نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,74,' ئۇلار (يەنى مۇناپىقلار) ھېچنېمە دېمىگەنلىكى توغرىسىدا اﷲ بىلەن قەسەم قىلىدۇ، ئۇلار ھەقىقەتەن كۇفرى سۆزىنى ئېيتتى، ئىسلام دىنىغا كىرگەنلىكىنى ئىپادىلىگەندىن كېيىن كاپىر بولدى، ئۇلار ئىشقا ئاشمايدىغان ئىشنى ئىشقا ئاشۇرۇشقا ئورۇندى (يەنى پەيغەمبەر ئەلەيھىسسالامنى ئۆلتۈرۈشنى قەستلىدى)، ئۇلارنىڭ (پەيغەمبەر ئەلەيھىسسالامنى) يامان كۆرۈشنى پەقەت اﷲ نىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ ئۇلارنى ئۆز پەزلىدىن باي قىلغانلىقى ئۈچۈندۇر، ئەگەر ئۇلار (مۇناپىقلىقتىن) تەۋبە قىلسا، ئۆزلىرىگە ياخشىدۇر، ئەگەر ئۇلار (ئىماندىن داۋاملىق) يۈز ئۆرۈسە، اﷲ ئۇلارنى دۇنيا ۋە ئاخىرەتتە قاتتىق ئازاب بىلەن جازالايدۇ، يەر يۈزىدە ئۇلارغا (ئۇلارنى ئازابتىن قۇتۇلدۇرىدىغان) بىرەر دوست ۋە بىرەر مەدەتكار تېپىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,75,' ئۇلارنىڭ ئىچىدىكى بەزىلەر: «ئەگەر اﷲ بىزگە ئۆز پەزلىدىن ئاتا قىلسا (يەنى رىزقىمىزنى كەڭ قىلسا) ئەلۋەتتە سەدىقە بېرەتتۇق، ئەلۋەتتە ياخشىلاردىن بولاتتۇق» دەپ اﷲ قا ئەھدە قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,76,' اﷲ ئۇلارغا ئۆز پەزلىدىن ئاتا قىلغان (يەنى باي قىلغان) چاغدا، بېخللىق قىلىشتى (يەنى ئەھدىنى بۇزۇپ، اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە ئىتائەت قىلىشتىن يۈز ئۆرۈشتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,77,' ئۇلار (سەدىقە قىلىمىز، ياخشىلاردىن بولىمىز دەپ) اﷲ قا بەرگەن ئەھدىسىگە خىلاپلىق قىلغانلىقلىرى، (ئىمانىنى، ئېھسانىنى، دەۋا قىلىشتىكى) يالغانچىلىقلىرى ئۈچۈن، اﷲ قا مۇلاقات بولىدىغان كۈن (يەنى قىيامەت كۈنى) گە قەدەر مۇناپىقلىقنىڭ دىللىرىدا (يىلتىز تارتىپ) تۇرۇشىنى اﷲ ئۇلارنىڭ (ئىشىنىڭ) ئاقىۋىتى قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,78,' اﷲ نىڭ ئۇلارنىڭ ئىچكى سىرلىرىنى، (ئۆزئارا قىلىشقان) يوشۇرۇن سۆزلىرىنى بىلىپ تۇرىدىغانلىقىنى، پۈتۈن غەيبلەرنى چوڭقۇر بىلىدىغانلىقىنى ئۇلار ئۇقمامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,79,' (مۇناپىقلار) مۆمىنلەرنىڭ ئىچىدىكى مەردلىك بىلەن (كۆپ) سەدىقە قىلغۇچىلارنى، تاقىتىنىڭ يېتىشىچە (يەنى كەمبەغەل بولغانلىقتىن ئاز) سەدىقە قىلغۇچىلارنى ئەيىبلەيدۇ، ئۇلارنى مەسخىرە قىلىدۇ، اﷲ ئۇلارنى مەسخىرە قىلغانلىقلىرى ئۈچۈن جازالايدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,80,' (ئى مۇھەممەد!) ئۇلار (يەنى مۇناپىقلار) ئۈچۈن مەيلى مەغپىرەت تەلەپ قىل، مەيلى مەغپىرەت تەلەپ قىلما (بەرىبىر ئوخشاش)، ئۇلار ئۈچۈن  قېتىم مەغپىرەت قىلمايدۇ چۈنكى ئۇلار اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلدى، اﷲ پاسىق قەۋمنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,81,' رەسۇلۇللاھقا خىلاپلىق قىلىپ (تەبۈك غازىتىغا چىقماي) قېلىپ قالغانلار (يەنى مۇناپىقلار) ئۆيلىرىدە بەخىرامان ئولتۇرغانلىقلىرى بىلەن خۇشال بولۇشتى، ئۇلار اﷲ نىڭ يولىدا ماللىرى بىلەن، جانلىرى بىلەن جىھاد قىلىشنى ياقتۇرمىدى، ئۇلار (بىر ـ بىرىگە): «ئىسسىقتا چىقماڭلار» دېيىشتى (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «جەھەننەمنىڭ ئوتى تېخىمۇ قىزىقتۇر ئەگەر ئۇلار چۈشىنىدىغان بولسا، (ئەلۋەتتە ئىسسىقتا رەسۇلۇللاھ بىلەن بىرگە چىقاتتى)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,82,' ئۇلار قىلمىش (گۇناھ) لىرىنىڭ جازاسى ئۈچۈن، ئاز كۈلسۇن، كۆپ يىغلىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,83,' ئەگەر اﷲ سېنى (تەبۈك غازىتىدىن) قايتۇرۇپ (ئۆزرىسىز قېلىپ قالغان مۇناپىقلاردىن) بىر تۈركۈمنىڭ يېنىغا ئېلىپ بارسا، ئۇلار (ئىككىنچى بىر غازاتقا) چىقىشقا سەندىن ئىزنى سورىسا، (ئۇلارغا) ئېيتقىنكى، «سىلەر مەن بىلەن بىللە مەڭگۈ (غازاتقا) چىقماڭلار، ھەرگىز مەن بىلەن بىللە چىقىپ ئۇرۇش قىلماڭلار، شۈبھىسىزكى، سىلەر دەسلەپكى قېتىمدا (چىقماي، ئۆيۈڭلاردا بەخىرامان) ئولتۇرۇشقا رازى بولدۇڭلار، ئەمدى (غازاتقا چىقماي) قېلىپ قالغۇچىلار (يەنى ئاياللار، كىچىك بالىلار) بىلەن ئولتۇرۇڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,84,' (ئى مۇھەممەد!) سەن ئۇلارنىڭ ئارىسىدىن ئۆلگەن ھېچبىر ئادەمنىڭ نامىزىنى چۈشۈرمىگىن، (دەپنە قىلىش، زىيارەت ياكى دۇئا ئۈچۈن) ئۇنىڭ قەبرىسى ئۈستىدىمۇ تۇرمىغىن، چۈنكى ئۇلار اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلدى ئۇلار پاسىق ھالىتى بىلەن ئۆلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,85,' ئۇلارنىڭ ماللىرى ۋە ئەۋلادلىرى سېنى ئەجەبلەندۈرمىسۇن اﷲ دۇنيادا بۇلار ئارقىلىق ئۇلارنى جازالاشنى، ئۇلارنىڭ كاپىر پېتى جان ئۈزۈشنى ئىرادە قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,86,' بىرەر سۈرە نازىل قىلىنىپ: «اﷲ قا ئىمان ئېيتىڭلار ۋە اﷲ نىڭ پەيغەمبىرى بىلەن بىللە جىھاد قىلىڭلار» دېيىلسە، ئۇلارنىڭ ئارىسىدىكى مالدارلار سەندىن (غازاتتىن قېلىپ قېلىشقا) رۇخسەت سورايدۇ، ئۇلار: «بىزنى قوي، (غازاتقا چىقماي، ئۆيلىرىدە بەخىرامان) ئولتۇرغۇچىلار بىلەن بىللە بولايلى» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,87,' ئۇلار ئۆيلىرىدە قالغۇچىلار (يەنى ئاياللار، كېسەللەر ۋە ئاجىزلار) بىلەن بىللە بولۇشقا رازى بولدى، ئۇلارنىڭ دىللىرى پېچەتلەندى شۇنىڭ ئۈچۈن، ئۇلار (جىھادتىكى پەيغەمبەرگە ئىتائەت قىلىشتىن كېلىدىغان بەختنى) چۈشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,88,' لېكىن، پەيغەمبەر ۋە پەيغەمبەر بىلەن بىللە ئىمان ئېيتقانلار ماللىرى بىلەن، جانلىرى بىلەن جىھاد قىلدى، ئەنە شۇلار (ئىككىلا دۇنيانىڭ) ياخشىلىقلىرىغا نائىل بولىدۇ، ئەنە شۇلار مەقسەتكە يەتكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,89,' اﷲ ئۇلارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى تەييارلىدى ئۇلار ئۇنىڭدا مەڭگۈ قالىدۇ، بۇ چوڭ بەختتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,90,' ئەئرابىلاردىن ئۆزرە بايان قىلغۇچىلار (جىھادقا چىقماسلىققا) رۇخسەت سوراپ كەلدى (ئىماننى دەۋا قىلىپ) اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە يالغانچىلىق قىلغانلار جىھادقا چىقمىدى (يەنى ئەئرابىلاردىن يەنە بىر تۈركۈمى جىھادقىمۇ چىقمىدى، ئۆزرە بايان قىلىپمۇ كەلمىدى) ئۇلاردىن كاپىر بولغانلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,91,' ئاجىزلار (يەنى ياشانغان بوۋايلار)، كېسەللەر، (جىھادقا چىقىشقا) خىراجەت تاپالمىغانلار اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە سادىق بولسىلا، جىھادقد چىقمىسا گۇناھ بولمايدۇ، ياخشى ئىش قىلغۇچىلارنى ئەيىبلەشكە يول يوقتۇر، اﷲ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,92,' يەنە مۇنداق كىشىلەرنىمۇ ئەيىبلەشكە يول يوقتۇركى، ئۇلار (جىھادقا چىقىش ئۈچۈن) سەندىن ئۇلاغ سوراپ كەلگەندە، (ئۇلارغا سەن) «مېنىڭ سىلەرنى تەمىنلەيدىغان ئۇلاغلىرىم يوق» دېدىڭ؟ ئۇلار (جىھادقا چىقىشقا) سەرپ قىلىدىغان نەرسە تاپالمىغانلىقلىرى ئۈچۈن غەمكىن، كۆزلىرىدىن ياش تۆككەن ھالدا قايتىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,93,' ئۆزلىرى باي تۇرۇپ، (جىھادقا چىقماسلىققا) رۇخسەت سورىغانلار ئەيىبلىنىشى كېرەك، ئۇلار (جىھادقا چىقمىغان ئاياللار، كېسەللەر، ئاجىزلار) بىلەن بىللە بولۇشقا رازى بولدى، اﷲ ئۇلارنىڭ دىللىرىنى پېچەتلىۋەتتى، (شۇنىڭ ئۈچۈن) ئۇلار (اﷲ نىڭ ئەمرىنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,94,' ئۇلارنىڭ يېنىغا (يەنى مەدىنىگە تەبۈك غازىتىدىن) قايتىپ كەلگەن چېغىڭلاردا، ئۇلار سىلەرگە (غازاتتىن قېلىپ قالغانلىقلىرى توغرىسىدا) ئۆزرە بايان قىلىدۇ، (ئى مۇھەممەد!) ئۇلارغا: «ئۆزرە بايان قىلماڭلار، سىلەرنىڭ (سۆزۈڭلارغا) ئىشەنمەيمىز، اﷲ سىلەرنىڭ ئەھۋالىڭلارنى (ۋە دىلىڭلاردىكى مۇناپىقلىقنى) بىزگە خەۋەر قىلدى، اﷲ ۋە ئۇنىڭ پەيغەمبىرى سىلەرنىڭ (بۇنىڭدىن كېيىنكى) ئەمەلىڭلارنى كۆرۈپ تۇرىدۇ (مۇناپىقلىقتىن تەۋبە قىلامسىلەر؟ ياكى ئۇنىڭدا چىڭ تۇرامسىلەر؟)، ئاندىن كېيىن (يەنى ئۆلگىنىڭلاردىن كېيىن)، يوشۇرۇن ۋە ئاشكارا ئىشلارنى بىلگۈچى (اﷲ) نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، اﷲ سىلەرگە پۈتۈن قىلمىشىڭلارنى ئېيتىپ بېرىدۇ» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,95,' ئۇلار (يەنى مۇناپىقلار) نىڭ يېنىغا قايتىپ بارغىنىڭلاردا، ئۇلار سىلەرنىڭ ئۇلارنى ئەيىبلىمەسلىكىڭلار ئۈچۈن (يالغان ئۆزرىلەر بايان قىلىپ)، اﷲ بىلەن قەسەم قىلىدۇ، ئۇلاردىن يۈز ئۆرۈڭلار (يەنى ئۇلار بىلەن سۆزلەشمەڭلار ۋە سالاملاشماڭلار)، ئۇلار ھەقىقەتەن نىجىستۇر، قىلمىشلىرىنىڭ جازاسى ئۈچۈن ئۇلارنىڭ بارىدىغان جايى جەھەننەمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,96,' سىلەرنىڭ رازىلىقىڭلارغا ئېرىشىش ئۈچۈن، ئۇلار سىلەرگە قەسەم ئىچىپ بېرىدۇ، سىلەر ئۇلاردىن رازى بولساڭلار (بۇ ئۇلارغا پايدا بەرمەيدۇ)، چۈنكى اﷲ پاسىق قەۋمدىن رازى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,97,' ئەئرابىلارنىڭ كۇفرى ۋە مۇناپىقلىقى تېخىمۇ زىيادىدۇر، اﷲ پەيغەمبىرىگە نازىل قىلغان شەرىئەت ئەھكاملىرىنى بىلمەسلىككە ئۇلار ئەڭ لايىقتۇر، اﷲ (مەخلۇقاتنىڭ ئەھۋالىنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,98,' ئەئرابىلاردىن بەزىلىرى (اﷲ يولىدا) بەرگەن نەرسىلىرىنى جەرىمانە ھېسابلايدۇ، سىلەرنىڭ ھادىسىگە ئۇچرىشىڭلارنى كۈتىدۇ، ئۇلارنىڭ ئۆزلىرى ھالاكەتكە ئۇچرىسۇن، اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچى، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,99,' ئەئرابىلارنىڭ بەزىلىرى اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتىدۇ، (اﷲ يولىدا) بەرگەن نەرسىلىرىنى اﷲ قا يېقىن بولۇشنىڭ ۋە پەيغەمبەرنىڭ دۇئاسىغا ئېرىشىشنىڭ ۋاسىتىسى قىلىدۇ، ئۇلار بۇ ئارقىلىق راستلا اﷲ قا بولغان يېقىنچىلىققا ئېرىشىدۇ، اﷲ قا ئۇلارنى ئۆزنىڭ رەھمىتى دائىرىسىگە كىرگۈزىدۇ، اﷲ ھەقىقەتەن (مۆمىن بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) تولىمۇ مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,100,' ھەممىدىن ئىلگىرى ئىمان ئېيتقان مۇھاجىرلار ۋە ئەنسارلاردىن، ياخشىلىق قىلىش بىلەن ئۇلارغا ئەگەشكەنلەر (يەنى تابىئىنلار ۋە قىيامەتكىچە ئۇلارنىڭ يولىدا ماڭغانلار) دىن اﷲ رازى بولدى، ئۇلارمۇ اﷲ تىن مەمنۇن بولدى اﷲ ئۇلارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى تەييارلىدى، ئۇلار جەننەتتە مەڭگۈ قالىدۇ، بۇ چوڭ بەختتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,101,' ئەتراپىڭلاردا ئەئرابىلاردىن مۇناپىقلار بار، مەدىنە ئاھالىسىدىنمۇ مۇناپىقلىقتا چىڭ تۇرغانلار بار، ئۇلارنى سەن بىلمەيسەن، بىز بىلىمىز، ئۇلارنى ئىككى قېتىم (يەنى دۇنيادا قەتل قىلىش ۋە ئەسىر ئېلىش بىلەن، ئۆلگەندە گۆر ئازابى بىلەن) جازالايمىز ئاندىن ئۇلار (ئاخىرەتتە) چوڭ ئازابقا (يەنى دوزاخ ئازابىغا) قايتۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,102,' يەنە بىر قىسىم كىشىلەر گۇناھلىرىنى ئېتىراپ قىلدى (يەنى باشقىلارغا ئوخشاش جىھادقا چىقمىغانلىقىغا يالغان ئۆزرە ئېيتمىدى) ئۇلار ياخشى ئىشقا يامان ئىشنى ئارىلاشتۇرۇپ قويدى، اﷲ نىڭ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلىشى ئۈمىدلىكتۇر، اﷲ ھەقىقەتەن (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,103,' (ئى مۇھەممەد!) ئۇلارنىڭ ماللىرىنىڭ بىر قىسىمىنى سەدىقە ھېسابىدا ئالغىنكى، ئۇنىڭ بىلەن ئۇلارنى گۇناھلىرىدىن پاكلىغايسەن ۋە (ياخشىلىقلىرىنى) كۆپەيتكەيسەن، ئۇلارغا دۇئا قىلغىن، شۈبھىسىزكى، سېنىڭ دۇئايىڭ ئۇلارغا خاتىرجەملىك ئېلىپ كېلىدۇ، اﷲ ئۇلارنىڭ (سۆزىنى) ئاڭلاپ تۇرغۇچى، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,104,' ئۇلار (يەنى تەۋبە قىلغۇچىلار) اﷲ نىڭ ئۆز بەندىلىرىنىڭ تەۋبىسىنى قوبۇل قىلىدىغانلىقىنى، (خالىس نىيەتلىك كىشىلەرنىڭ) سەدىقىسىنى قوبۇل قىلىدىغانلىقىنى ۋە اﷲ نىڭ تەۋبىنى بەكمۇ قوبۇل قىلىدىغانلىقىنى، ناھايىتى مېھرىبان ئىكەنلىكىنى بىلمەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,105,' (ئۇلارغا) ئېيتقىنكى، «(خالىغان) ئەمەللەرنى قىلىڭلار، قىلغان ئەمەلىڭلارنى اﷲ ، اﷲ نىڭ پەيغەمبىرى ۋە مۆمىنلەر كۆرۈپ تۇرىدۇ، يوشۇرۇن ۋە ئاشكارا (نەرسىلەرنى) بىلگۈچى (اﷲ) نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، اﷲ قىلمىشىڭلارنى سىلەرگە خەۋەر قىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,106,' يەنە بىر قىسىم (جىھادقا چىقمىغان) كىشىلەر (نىڭ ئىشى) اﷲ نىڭ ئەمرىگە قالدۇرۇلدى، اﷲ يا ئۇلارنى (تەۋبە قىلمىسا) جازالايدۇ، يا ئۇلارنى تەۋبىگە مۇۋەپپەق قىلىدۇ (ۋە مەغپىرەت قىلىدۇ) اﷲ ئۇلارنى (يەنى ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,107,' بەزى كىشىلەر مۆمىنلەرگە زىيان يەتكۈزۈش، كۇفرىنى كۈچەيتىش، مۆمىنلەرنىڭ ئارىسىنى پارچىلاش، اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە قارشى تۇرغان ئادەم (يەنى ئەبۇئامر راھىب) نىڭ كېلىشىنى كۈتۈش مەقسىتىدە مەسجىد بىنا قىلدى ئۇلار: «بىز پەقەت ياخشىلىقنىلا ئىرادە قىلدۇق» دەپ چوقۇم قەسەم قىلىدۇ، اﷲ گۇۋاھلىق بېرىدۇكى، ئۇلار شەك ـ شۈبھىسىز يالغانچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,108,' ئۇ مەسجىدتە مەڭگۈ ناماز ئوقۇمىغىن، بىرىنچى كۈنىدىن تارتىپلا تەقۋا ئاساسىغا قۇرۇلغان مەسجىد ھەقىقەتەن سېنىڭ ناماز ئوقۇشۇڭغا ئەڭ لايىقتۇر ، ئۇنىڭدا (گۇناھلاردىن) پاك بولۇشنى سۆيىدىغان كىشىلەر بار، اﷲ (ئىچكى ۋە تاشقى جەھەتتىن) پاك بولغۇچىلارنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,109,' (دىننىڭ) بىناسىنى اﷲ نىڭ تەقۋادارلىقى ۋە رازىلىقى ئاساسىغا قۇرغان ئادەم ياخشىمۇ ياكى يىقىلاي دەپ قالغان يارنىڭ گىرۋىكىگە قۇرۇپ، ئۆزىمۇ ئۇنىڭ بىلەن بىللە دوزاخ ئوتىغا ئۆرۈلۈپ چۈشكەن ئادەم ياخشىمۇ؟ اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,110,' تاكى ئۇلارنىڭ يۈرەكلىرى پارە ـ پارە بولۇپ ئۆلمىگىچە، ئۇلارنىڭ سالغان مەسجىدى (يەنى مەسجىدى زىرار) دىللىرىدىكى گۇمان (مۇناپىقلىق) نىڭ يىلتىزى بولۇپ قىلىۋېرىدۇ، اﷲ (مۇناپىقلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، (ئۇلارنىڭ يامان نىيىتىگە ئاساسەن، ئۇلارنى جازالاشتا) ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,111,' شۈبھىسىزكى، اﷲ مۆمىنلەردىن ئۇلارنىڭ جانلىرىنى، ماللىرىنى ئۇلارغا جەننەتنى بېرىپ سېتىۋالدى ئۇلار اﷲ نىڭ يولىدا ئۇرۇش قىلىپ (دۈشمەنلەرنى) ئۆلتۈرىدۇ ۋە ئۆلتۈرۈلىدۇ (يەنى دۈشمەنلەر بىلەن جىھاد قىلىپ شېھىت بولىدۇ)، (جىھاد قىلغۇچىلارغا جەننەتنى ۋەدە قىلىش) تەۋراتتا، ئىنجىلدا ۋە قۇرئاندا زىكرى قىلىنغان (اﷲ نىڭ) راست ۋەدىسىدۇر، ۋەدىسىگە اﷲ تىنمۇ بەك ۋاپا قىلغۇچى كىم بار؟ (يەنى اﷲ تىنمۇ ۋاپادار ئەھەدى يوق) قىلغان بۇ سوداڭلاردىن خۇشال بولۇڭلار ، بۇ زور مۇۋەپپەقىيەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,112,' (گۇناھلىرىدىن) تەۋبە قىلغۇچىلار، (ئىخلاس بىلەن) ئىبادەت قىلغۇچىلار، (اﷲ قا) ھەمدۇسانا ئېيتقۇچىلار، روزا تۇتقۇچىلار، رۇكۇ قىلغۇچىلار، سەجدە قىلغۇچىلار، ياخشى ئىشلارغا دەۋەت قىلىپ، يامان ئىشلاردىن توسقۇچىلار، اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلغۇچىلار (يەنى اﷲ نىڭ بېكىتكەن پەرزلىرىنى ئادا قىلىپ، نەھيى قىلغان ئىشلىرىدىن يانغۇچىلار ھەم ئەھلى جەننەتتۇر)؛ مۆمىنلەرگە (جەننەت بىلەن) خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,113,' پەيغەمبەرگە ۋە مۆمىنلەرگە مۇشرىكلارنىڭ ئەھلى دوزاخ ئىكەنلىكى ئېنىق مەلۇم بولغاندىن كېيىن، مۇشرىكلار ئۇلارنىڭ تۇغقىنى بولغان تەقدىردىمۇ، ئۇلارغا مەغپىرەت تەلەپ قىلىشى دۇرۇس ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,114,' ئىبراھىمنىڭ ئاتىسىغا مەغپىرەت تەلەپ قىلىشى ئۇنىڭغا ئىلگىرى قىلغان ۋەدىسىنى ئورۇنلاش ئۈچۈن ئىدى ئۇنىڭغا ئاتىسىنىڭ اﷲ نىڭ دۈشمىنى ئىكەنلىكى ئېنىق مەلۇم بولغاندىن كېيىن، ئۇ ئاتىسى بىلەن ئادا ـ جۇدا بولدى ئىبراھىم بولسا ھەقىقەتەن كۆپ ئاھ چەككۈچى (يەنى كۆڭلى يۇمشاق)، (يەتكەن ئەزىيەتلەرگە) چىداملىق ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,115,' اﷲ بىر قەۋمنى ھىدايەت قىلغاندىن كېيىن، ئۇلار ساقلىنىشقا تېگىشلىك ئىشلارنى بايان قىلماي تۇرۇپ، ئۇلارنى گۇمراھ قىلمايدۇ (يەنى گۇمراھلىققا ھۆكۈم قىلمايدۇ)، اﷲ ھەقىقەتەن ھەر نەرسىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,116,' شۈبھىسىزكى، ئاسماننىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنى) ئۆلتۈرىدۇ، سىلەرگە اﷲ تىن باشقا ھېچ دوست ۋە مەدەتكار يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,117,' اﷲ ھەقىقەتەن پەيغەمبەرنىڭ ۋە ئۇنىڭغا قىيىنچىلىق پەيتىدە ئەگەشكەن مۇھاجىرلار ۋە ئەنسارلارنىڭ تەۋبىسىنى قوبۇل قىلدى (ئەينى زاماندا) ئۇلارنىڭ ئارىسىدىكى بىر تۈركۈم كىشىلەرنىڭ دىللىرى ھەقتىن بۇرۇلۇپ كېتىشىگە تاس قالغاندىن كېيىن، اﷲ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلدى اﷲ ئۇلارغا تولىمۇ مەرھەمەتلىك، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,118,' جىھادقا چىقمىغان ئۈچ كىشىنىڭ تەۋبىسىنىمۇ اﷲ قوبۇل قىلدى شۇنچە كەڭ زېمىن ئۇلارغا تار تۇيۇلغان، ئۇلارنىڭ جانلىرى سىقىلغان، ئۇلار اﷲ قا تەۋبە قىلماي تۇرۇپ اﷲ نىڭ غەزىپىدىن قۇتۇلۇش مۇمكىن ئەمەسلىكىگە ئىشەنگەن ئىدى ئاندىن ئۇلارنىڭ تەۋبە قىلغۇچىلاردىن بولۇشى ئۈچۈن، اﷲ ئۇلارنى تەۋبىگە مۇۋەپپەق قىلدى اﷲ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، (بەندىلىرىگە) ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,119,' ئى مۆمىنلەر! اﷲ تىن قورقۇڭلار، راستچىللار بىلەن بىللە بۇلۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,120,' مەدىنە ئاھالىسى ۋە ئۇلارنىڭ ئەتراپىدىكى ئەئرابىلارنىڭ رەسۇلۇللا بىلەن جىھادقا بىللە چىقماي قېلىپ قېلىشى، ئۆزلىرىنىڭ ئارامى بىلەنلا بولۇپ، جاپادا رەسۇلۇللاھ بىلەن بىللە بولماسلىقى توغرا ئەمەس ئىدى بۇ (يەنى ئۇلارنى جىھادقا چىقماي قېلىپ قېلىشتىن مەنئى قىلىش) شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ نىڭ يولىدا ئۇچرىغان ئۇسسۇزلۇق، تارتقان جاپا ـ مۇشەققەت ۋە ئاچلىق، ئۇلارنىڭ كاپىرلارنى خاپىلىققا سالىدىغان ھەر بىر قەدىمى، دۈشمەنلەر ئۈستىدىن ئېرىشكەن (ئۇلارنى ئۆلتۈرۈش، ئەسىر ئېلىش، مەغلۇپ قىلىش قاتارلىق) ھەر بىر نەرسىسى ئۈچۈن (اﷲ نىڭ دەرگاھىدا) ئۇلارغا ياخشىلىق (يەنى ساۋاب) يېزىلىدۇ، اﷲ ئەلۋەتتە ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,121,' ئۇلارنىڭ (اﷲ نىڭ يولىدا) مەيلى كىچىك بولسۇن ياكى چوڭ بولسۇن، سەرپ قىلغان نەرسىسى ۋە جىھاد ئۈچۈن باسقان ھەر بىر مۇساپىسى ئۇلارنىڭ قىلغان ئەمەللىرىگە ئەڭ ياخشى مۇكاپات بېرىش يۈزىسىدىن ئۇلارغا يېزىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,122,' مۆمىنلەرنىڭ ھەممىسىنىڭ جىھادقا چىقىشى لايىق ئەمەس (ئۇلارنىڭ بىر تۈركۈمى جىھادقا چىقتى)، ئۇلارنىڭ ئىچىدىكى ھەر بىر جامائەدىن يەنە بىر تۈركۈمى دىنىي ئالىم بولۇپ، ئۇلار قەۋمىنىڭ قېشىغا قايتقاندىن كېيىن، قەۋمىنىڭ اﷲ تىن قورقۇشى ئۈچۈن، ئۇلارنى ئاگاھلاندۇرۇش مەقسىتىدە نېمىشقا (ئىلىم تەلەپ قىلىشقا) چىقمىدى؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,123,' ئى مۆمىنلەر! سىلەر ئۆزۈڭلارغا يېقىن جايدىكى كاپىرلار بىلەن ئۇرۇش قىلىڭلار (يەنى سىلەرگە خەتەر بولماسلىق ئۈچۈن، ئالدى بىلەن ئەتراپىڭلاردىكى كۇففارلارنى ئۇجۇقتۇرۇڭلار)، سىلەرنى ئۇلار ھېس قىلسۇن (يەنى ئۇرۇش ئۈستىدە ئۇلارغا قاتتىق قول بۇلۇڭلار ، ئۇلارغا ئىچىڭلار ئاغرىمىسۇن)، بىلىڭلاركى، اﷲ تەقۋادارلار بىلەن بىللىدۇر (يەنى اﷲ تەقۋادارلارغا مەدەتكاردۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,124,' (قۇرئاندىن) بىرەر سۈرە نازىل قىلىنسا، ئۇلار (يەنى مۇناپىقلار) نىڭ ئارىسىدا (مەسخىرە قىلىش يۈزىسىدىن): «بۇ (يەنى سۈرە) قايسىڭلارنىڭ ئىمانىنى كۈچەيتتى» دەيدىغانلار بار، مۆمىنلەرگە كېلىدىغان بولساق، (ھەر سۈرىدە يېڭى دەلىل ـ ئىسپاتلار بولغانلىقى ئۈچۈن) بۇ (يەنى سۈرە) ئۇلارنىڭ ئىمانىنى كۈچەيتتى، ئۇلار (سۈرىنىڭ نازىل بولۇشىدىن) خۇشال بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,125,' دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلار) غا كېلىدىغان بولساق، بۇ (سۈرە) ئۇلارنىڭ كۇفرىغا كۇفرى قوشتى (يەنى ئۇلار مۇناپىقلىقتا تېخىمۇ ئەزۋەيلىدى، تېخىمۇ گۇمراھلاشتى)، ئۇلار كاپىر پېتى ئۆلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,126,' ئۇلار ئۆزلىرىنىڭ ھەر يىلى بىر قېتىم ياكى ئىككى قېتىم سىنىلىدىغانلىقىنى (يەنى ئۇلار توغرىسىدا ۋەھيى نازىل بولۇپ، سىرلىرىنىڭ پاش قىلىنىدىغانلىقىنى) بىلمەمدۇ؟ ئاندىن ئۇلار تەۋبە قىلمايدۇ (يەنى نىفاقتىن قايتمايدۇ) ئىبرەتمۇ ئالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,127,' (ئۇلار پەيغەمبەر ئەلەيھىسسالامنىڭ ھۇزۇرىدىكى چاغدا، ئۇلارنىڭ ئەيىبىنى ئاچىدىغان قۇرئاندىن) بىرەر سۈرە نازىل قىلىنسا، ئۇلار بىر ـ بىرىگە قارىشىپ: «سىلەرنى بىرەرسى كۆرۈپ قالارمۇ» دېيىشىپ تىكىۋېتىدۇ، ئۇلار (ھەقىقەتنى) چۈشەنمەس قەۋم بولغانلىقى ئۈچۈن، اﷲ قا ئۇلارنىڭ دىللىرىنى ئۆزگەرتىۋەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,128,' (ئى ئىنسانلار!) شۈبھىسىزكى، سىلەرگە ئۆزئارا ئاراڭلاردىن پەيغەمبەر كەلدى سىلەرنىڭ كۈلپەت چېكىشىڭلار ئۇنىڭغا ئېغىر تۇيۇلىدۇ؛ ئۇ سىلەرنىڭ (ھىدايەت تېپىشىڭلارغا) ھېرىستۇر، مۆمىنلەرگە ئامراقتۇر، ناھايىتى كۆيۈمچاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,9,129,' (ئى پەيغەمبەر!) ئەگەر ئۇلار (ساڭا ئىمان ئېيتىشتىن) يۈز ئۆرۈسە، سەن: «اﷲ ماڭا كۆرىدۇ قىلىدۇ، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇنىڭغا تەۋەككۈل قىلدىم (يەنى اﷲ قا يۆلەندىم، اﷲ تىن باشقا ھېچ ئەھەدىدىن ئۈمىد كۈتمەيمەن ۋە ھېچ ئەھەدىدىنمۇ قورقمايمەن)، ئۇ بۈيۈك ئەرشنىڭ پەرۋەردىگارىدۇر» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,1,'ئەلىف، لام، را بۇ، ھېكمەتلىك كىتاب (يەنى قۇرئان) ئايەتلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,2,' ئىنسانلارنىڭ بىرىگە (يەنى مۇھەممەد ئەلەيھىسسالامغا): ئىنسانلار (يەنى كاپىرلار) نى (اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرغىن، مۆمىنلەرگە پەرۋەردىگارىنىڭ دەرگاھىدا (قىلغان ياخشى ئەمەللىرى ئۈچۈن) يۇقىرى ئورۇنغا ئېرىشىدىغانلىقى بىلەن خۇش خەۋەر بەرگىن، دەپ ۋەھيى قىلغانلىقىمىزدىن ئۇلار ئەجەبلىنەمدۇ؟ كاپىرلار: «بۇ ھەقىقەتەن ئاشكارا سېھىرگەردۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,3,' شۈبھىسىزكى، سىلەرنىڭ پەرۋەردىگارىڭلار اﷲ دۇر، ئۇ ئىنسانلارنى، زېمىننى ئالتە كۈندە ياراتتى ئاندىن ئەرش ئۈستىدە ئۆزىگە لايىق رەۋىشتە قارار ئالدى اﷲ خالايىقنىڭ ئىشلىرىنى (ئۆز ھېكمىتىگە مۇۋاپىق) ئىدارە قىلىپ تۇرىدۇ، (قىيامەت كۈنى) ھەر قانداق شاپائەت قىلغۇچى ئالدى بىلەن اﷲ نىڭ ئىزنىنى ئالىدۇ، ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، ۋەز ـ نەسىھەت ئالمامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,4,' (قىيامەت كۈنى) ھەممىڭلار اﷲ نىڭ دەرگاھىغا قايتىسىلەر، اﷲ نىڭ ۋەدىسى ھەقتۇر، شۈبھىسىزكى، مەخلۇقاتنى دەسلەپتە اﷲ ياراتقان، (ئۇلارنى ھالاك قىلغاندىن كېيىن، قىيامەت كۈنى) ئىمان ئېيتقانلارنى ۋە ياخشى ئەمەل قىلغانلارنى ئادىللىق بىلەن مۇكاپاتلاش ئۈچۈن قايتا تىرىلدۈرىدۇ، كاپىرلارنىڭ بولسا، ئۆزلىرىنىڭ كۇفرى تۈپەيلىدىن، (دوزاختىكى) ئىچىملىكى قاتتىق قاينىغان سۇ بولىدۇ ھەمدە ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,5,' اﷲ قۇياشنى نۇر چاچقۇچى ۋە ئاينى نۇرلۇق قىلىپ ياراتتى، يىللارنىڭ سانىنى ۋە (ۋاقىتلارنىڭ) ھېسابىنى بىلىشىڭلار ئۈچۈن، ئايغا مەنزىللەرنى (يەنى بۇرۇجلارنى) تەيىن قىلدى اﷲ ئۇلارنى بىكار ياراتقىنى يوق (بەلكى بۈيۈك ھېكمەت ۋە زور پايدىلىق ئۈچۈن ياراتتى)، اﷲ ئايەتلىرىنى (قۇدرىتى ئىلاھىيىنى) بىلىدىغان (ۋە ئۇنىڭ ھېكمىتىنى پىكىر قىلىدىغان) قەۋم ئۈچۈن تەپسىلىي بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,6,' كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىپ تۇرۇشىدا، اﷲ ئاسماندا ۋە زېمىندا ياراتقان شەيئىلەردە (اﷲ تىن) قورقىدىغان قەۋم ئۈچۈن، ھەقىقەتەن، (اﷲ نىڭ بارلىقىنى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,7,' شۈبھىسىزكى، بىز بىلەن مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلار (يەنى اﷲ قا مۇلاقات بولۇشنى كۆزلىمەيدىغانلار)، دۇنيا تىرىكچىلىكىدىن بەھرىمەن بولۇش بىلەنلا رازى بولغانلار ۋە ئۇنىڭ بىلەن ئارام تاپقانلار، بىزنىڭ ئايەتلىرىمىزدىن غاپىل بولغانلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,8,'، ـ ئەنە شۇلارنىڭ جايى، قىلمىشلىرى تۈپەيلىدىن، دوزاخ بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,9,' شۈبھىسىزكى، ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلارنى پەرۋەردىگارى، ئۇلارنىڭ ئىمانى سەۋەبىدىن، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان نازۇنېمەتلىك جەننەتلەرگە يېتەكلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,10,' ئۇلارنىڭ جەننەتتىكى دۇئاسى: «ئى رەببىمىز، بىز سېنى پاك دەپ ئېتىقاد قىلىمىز» دېيىشتىن ئىبارەت، ئۇلارنىڭ جەننەتتە (بىر ـ بىرىگە) بېرىدىغان سالامى: «(ئەھلى دوزاخ چېكىۋاتقان ئازابتىن) ئامان بولغايسەن (يەنى ئەسسالامۇئەلەيكۇم)» دېيىشتىن ئىبارەتتۇر، ئۇلارنىڭ دۇئاسىنىڭ ئاخىرى: «جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!» دېيىشتىن ئىبارەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,11,' ئەگەر اﷲ كىشىلەر تەلەپ قىلغان يامانلىقنى ئىشقا ئاشۇرۇشقا ئۇلار تەلەپ قىلغان ياخشىلىقىنى ئىشقا ئاشۇرۇشقا ئالدىرىغاندەك ئالدىرىسا ئىدى، ئۇلارنىڭ ئەجىلى چوقۇم يەتكەن بولاتتى (يەنى ھالاك بولاتتى) بىز بىلەن مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلارنى گۇمراھلىقتا تېڭىرقاپ يۈرۈشكە قويۇپ بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,12,' ئىنسانغا بىرەر زىيان ـ زەخمەت يەتسە، ياتسىدۇ، ئولتۇرسىدۇ، تۇرسىدۇ، (ئۇنى دەپئى قىلىشىمىزنى تىلەپ) بىزگە دۇئا قىلىدۇ؛ ئۇلارغا يەتكەن زىيان ـ زەخمەتنى كۆتۈرۈۋەتسەك، گويا ئۇنى كۆتۈرۈۋېتىشىمىزنى تىلەپ بىزگە دۇئا قىلمىغاندەك، يامان ئىشنى يەنە داۋاملاشتۇرىدۇ، ھەددىدىن ئاشقۇچىلارنىڭ قىلمىشلىرى ئۇلارغا مۇشۇنداق چىرايلىق كۆرسىتىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,13,' سىلەردىن ئىلگىرىكى ئۈممەتلەر زۇلۇم قىلغاندا (يەنى كاپىر بولغاندا، گۇمراھلىقتا مۆجىزىلەرنى ئېلىپ كەلگەن ئىدى ئۇلار (پەيغەمبەرلەر ئېلىپ كەلگەن مۆجىزىلەرگە) ئىمان كەلتۈرمىدى گۇناھكار قەۋمنى (يەنى مەككە مۇشرىكلىرىنى) شۇنىڭغا ئوخشاش جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,14,' سىلەرنىڭ قانداق قىلىدىغانلىقىڭلارنى كۆرۈش ئۈچۈن، ئۇلارنى (يەنى ئۆتكەن ئۈممەتلەرنى) (ھالاك قىلغان) دىن كېيىن، ئۇلارنىڭ ئورنىغا سىلەرنى ئورۇنباسار قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,15,' ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز تىلاۋەت قىلىنسا، بىزگە مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلار (يەنى قىيامەتكە ئىشەنمەيدىغانلار): «بۇنىڭدىن باشقا بىر قۇرئاننى كەلتۈرگىن ياكى ئۇنى ئۆزگەرتكىن (يەنى ئازابقا دائىر ئايەتنى رەھمەت ئايىتىگە ئۆزگەرت، ئىلاھلىرىمىزنى سۆككەننىڭ ئورنىغا مەدھىيلە، ھارامنى ھالال قىل)» دەيدۇ «مەن ئۇنى ئۆزۈمچە ئۆزگەرتەلمەيمەن، مەن پەقەت ماڭا قىلىنغان ۋەھيىگىلا ئەمەل قىلىمەن؛ ئەگەر مەن پەرۋەردىگارىمغا ئاسىيلىق قىلسام، ئەلۋەتتە، بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىدىن قورقىمەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,16,' «ئەگەر اﷲ ئىرادە قىلسا، سىلەرگە ئۇنى (يەنى قۇرئاننى) تىلاۋەت قىلماس ئىدىم، اﷲ مۇ ئۇنى (مەن ئارقىلىق) سىلەرگە بىلدۈرمەس ئىدى ئاراڭلاردا ئۇنىڭدىن (يەنى قۇرئان نازىل بولۇشتىن) ئىلگىرى ئۇزۇن زامان تۇردۇم، (مۆجىزە بولغان بۇ قۇرئاننىڭ پەقەت اﷲ تەرىپىدىن نازىل بولغانلىقىنى بىلىشكە) ئەقلىڭلارنى ئىشلەتمەمسىلەر؟» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,17,' اﷲ قا يالغاننى چاپلىغان ياكى اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغان ئادەمدىنمۇ زالىم ئادەم بارمۇ؟ گۇناھكارلار ھەرگىز بەختكە ئېرىشەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,18,' ئۇلار اﷲ نى قويۇپ، ئۆزلىرىگە پايدا ـ زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنىدۇ، «بۇلار (يەنى بۇتلار) اﷲ نىڭ دەرگاھىدا بىزگە شاپائەت قىلغۇچىلار» دېيىشىدۇ، ئەجەبا، ئاسمانلاردا ۋە زېمىندا بۇنداق نەرسە (يەنى اﷲ نىڭ شېرىكى ياكى شاپائەت قىلغۇچى) بولۇپ، ئۇنى اﷲ بىلمەي قېلىپ، سىلەر اﷲ قا ئۇنى ئېيتىپ بېرەمسىلەر؟ اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر ۋە يۈكسەكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,19,' ئىنسانلار (ئەسلىدە) بىر دىن (يەنى ئىسلام دىنى) دا ئىدى كېيىن ئۇلار ئىختىلاپ قىلىشتى (يەنى دىنلار كۆپەيدى، بۇتلارغا چوقۇنۇشتى) پەرۋەردىگارىڭنىڭ ئالدىنئالا ئېيتقان سۆزى (يەنى جازالاشنى قىيامەتكە قالدۇرۇشتىن ئىبارەت تەقدىرى) بولمىسا ئىدى، ئۇلارنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ئۇلارنىڭ ئارىسىدا ئەلۋەتتە ھۆكۈم چىقىرىلاتتى (يەنى دىندا ئىختىلاپ قىلىشقانلىرى ئۈچۈن، ئۇلارغا بۇ دۇنيادىلا جازا بېرىلەتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,20,' ئۇلار (يەنى ئەھلى مەككە): «نېمىشقا ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) پەرۋەردىگارى تەرىپىدىن (بىز تەلەپ قىلغان مۆجىزىلەردىن) بىر مۆجىزە چۈشۈرۈلمىدى» دەيدۇ ئېيتقىنكى، «غەيبنى بىلىش پەقەت اﷲ قا خاس (مۆجىزىلەرنى اﷲ كەلتۈرىدۇ، مەن پەقەت تەبلىغ قىلغۇچىمەن)، سىلەر (اﷲ نىڭ ئارىمىزدا ھۆكۈم چىقىرىشنى) كۈتۈڭلار، شۈبھىسىزكى، مەنمۇ سىلەر بىلەن (شۇنى) كۈتكۈچىلەردىنمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,21,' كىشىلەرنىڭ بېشىغا كەلگەن زىيان ـ زەخمەتنى كۆتۈرۈۋەتكەندىن كېيىن، ئۇلارغا رەھمىتىمىزنى تېتىتساق، ئۇلار بىزنىڭ ئايەتلىرىمىز ئۈستىدە مىكىر ئىشلىتىشكە (يەنى ئىنكار قىلىشقا) ئالدىرايدۇ ئېيتقىنكى، «اﷲ ئۇلارنى قىلغان مىكرى ئۈچۈن ئەڭ تېز جازالايدۇ» شۈبھىسىزكى، بىزنىڭ پەرىشتىلىرىمىز مىكرىڭلارنى يېزىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,22,' اﷲ سىلەرنى قۇرۇقلۇقتا (يەنى ئۇلاغلار ئۈستىدە)، دېڭىزدا (يەنى كېمىلەر ئۈستىدە) سەپەر قىلدۇرىدۇ، سىلەر ئولتۇرغان كېمە (كىشىلەرنى ئېلىپ) مەيىن شامالدا مېڭىۋاتقان ۋە (بۇ) شامالدىن ئۇلار خۇشاللىنىۋاتقان چاغدا، بىردىنلا بوران چىقىپ (كېمە ئۆرۈلىدۇ)، ئۇلار تەرەپ ـ تەرەپتىن كۆتۈرۈلۈۋاتقان دېڭىز دولقۇنلىرى ئىچىدە قالىدۇ، كېمىدىكىلەر قورشىۋېلىنغانلىقىغا (يەنى ھالاك بولىدىغانلىقىغا) جەزم قىلىدۇ، (چوقۇنۇۋاتقان بۇتلىرىنى تاشلاپ قويۇپ) «ئى خۇدا! ئەگەر سەن بىزنى بۇنىڭدىن (يەنى بالادىن) قۇتۇلدۇرساڭ، بىز چوقۇم شۈكۈر قىلغۇچىلاردىن بولىمىز» دەپ چىن كۆڭلى بىلەن دۇئا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,23,' اﷲ ئۇلارنى (ئۇ بالادىن) قۇتقۇزغاندىن كېيىن، ئۇلار يەر يۈزىدە يولسىزلىق بىلەن بۇزۇقچىلىق قىلىدۇ ئى ئىنسانلار! بۇزۇقچىلىقىڭلارنىڭ گۇناھى ئۆزۈڭلارغا بولىدۇ، (سىلەر) بۇ دۇنيا (نىڭ باقاسى يوق شەھۋەتلىرى) دىن بەھرىمەن بولىسىلەر (بۇنىڭ ھەسرىتى ئاخىرەتكە قالىدۇ)، ئاندىن سىلەر بىزنىڭ دەرگاھىمىزغا قايتىسىلەر، سىلەرگە قىلمىشىڭلارنى خەۋەر قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,24,' ھاياتىي دۇنيانىڭ مىسالى (تېز ئۆزگۈرۈشىدە، نېمەتلىرىنىڭ باقاسىزلىقىدا، كىشىلەرنىڭ ئۇنىڭ بىلەن مەغرۇر بولۇشىدا) بىز ئاسماندىن چۈشۈرگەن (يامغۇر) سۈيىگە ئوخشايدۇكى، ئۇنىڭ بىلەن زېمىننىڭ ئىنسانلار، ھايۋانلار يەيدىغان تۈرلۈك ئۆسۈملۈكلىرى (ئۈنۈپ چىقىپ) بىر ـ بىرى بىلەن گىرەلىشىپ كەتكەن، ھەتتا زېمىن گۈزەل تۈسكە كىرگەن، چىرايلىق ياسانغان، زېمىن ئىگىلىرى ئۇنىڭدىن مول ھوسۇل ئېلىشقا جەزم قىلىپ تۇرغان چاغدا، ناگاھان كېچىسى ياكى كۈندۈزى ئۇنىڭغا قازايىمىز يەتتى ـ دە، (زىرائەتلەرنى) تۈنۈگۈن (يەنى ئىلگىرى) يوقتەك، (پۈتۈنلەي) ئۇرۇپ تاشلانغاندەك قىلىۋەتتۇق (يەنى نابۇت قىلىپ، يوق قىلدۇق)، پىكىر يۈرگۈزىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,25,' اﷲ (كىشىلەرنى) ئامان جايغا (يەنى جەننەتكە) دەۋەت قىلىدۇ ۋە خالىغان ئادەمنى توغرا يولغا باشلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,26,' ياخشى ئىش قىلغانلار جەننەتكە ۋە اﷲ نىڭ جامالىنى كۆرۈشكە نائىل بولىدۇ، ئۇلارنىڭ يۈزلىرىنى قارىلىق ۋە خارلىق قاپلىمايدۇ (يەنى ئۇلارغا غەم ـ قايغۇ ۋە پەرىشانلىق يۈزلەنمەيدۇ) ئەنە شۇلار ئەھلى جەننەت بولۇپ، جەننەتتە مەڭگۈ قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,27,' (دۇنيادا) يامان ئىشلارنى قىلغۇچىلار (بىر يامانلىقنى قىلسا، قىلغان يامانلىقىغا) ئوخشاش (يەنى شۇنىڭغا لايىق) جازاغا ئۇچرايدۇ، ئۇلار خارلىققا دۇچار بولىدۇ، ئۇلارنى اﷲ نىڭ ئازابىدىن قۇتۇلدۇرغۇچى زادى بولمايدۇ، ئۇلارنىڭ يۈزلىرى گويا قاراڭغۇ كېچىنىڭ پارچىسى بىلەن ئورالغاندەك قارىيىپ كېتىدۇ، ئۇلار ئەھلى دوزاخ بولۇپ، دوزاختا مەڭگۈ قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,28,' شۇ كۈن (يەنى قىيامەت كۈنى) دە (مۆمىنلەرنى، كاپىرلارنى) ھەممىسىنى يىغىمىز، ئاندىن مۇشرىكلارغا: «سىلەر (اﷲ قا شېرىك كەلتۈرگەن) بۇتلىرىڭلار بىلەن بىللە، ئورنۇڭلاردا مىدىرلىماي تۇرۇڭلار» دەيمىز، ئۇلارنىڭ ئارىسىنى ئايرىيمىز، ئۇلارنىڭ شېرىك كەلتۈرگەن بۇتلىرى ئېيتىدۇ: «سىلەر بىزگە ئىبادەت قىلمىغان ئىدىڭلار (بەلكى نەپسى خاھىشىڭلارغا ئىبادەت قىلغان ئىدىڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,29,' سىلەر بىلەن بىزنىڭ ئارىمىزدا گۇۋاھ بولۇشقا اﷲ كۇپايىكى، سىلەرنىڭ بىزگە چوقۇنۇشۇڭلاردىن بىز غاپىل ئىدۇق (يەنى بىز كۆرمەيدىغان، ئاڭلىمايدىغان، ھېس قىلمايدىغان جانسىز ئىدۇق)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,30,' بۇ چاغدا ھەر ئادەم ئىلگىرى قىلغان ئەمەللىرى ئۈستىدە سىنىلىدۇ (يەنى ياخشىمۇ ـ يامانمۇ، ئۇنىڭ نەتىجىسىنى كۆرىدۇ)، ئۇلار ھەقىقىي ئىگىسى ـ اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ، ئۇلارنىڭ شاپائەت قىلىدۇ دەپ دەۋا قىلغان بۇتلىرى ئۇلاردىن ئۆزلىرىنى چەتكە ئالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,31,' (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەرگە ئاسماندىن (يامغۇر ياغدۇرۇپ) زېمىندىن (گىياھ ئۈندۈرۈپ) كىم رىزىق بېرىدۇ؟ سىلەرنىڭ ئاڭلاش ۋە كۆرۈش قابىلىيىتىڭلارنى كىم باشقۇرىدۇ؟ تىرىك شەيئىلەردىن كىم پەيدا قىلىدۇ؟ (خالايىقنىڭ) ئىشلىرىنى كىم ئىدارە قىلىدۇ؟» ئۇلار (بۇلارغا جاۋابەن): «اﷲ» دەيدۇ ئېيتقىنكى، «اﷲ تىن غەيرىيگە چوقۇنۇش بىلەن اﷲ نىڭ ئازابىدىن) قورقمامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,32,' (يۇقىرىقىدەك چوڭ ئىشلارنى قىلغۇچى) سىلەرنىڭ ھەقىقىي پەرۋەردىگارىڭلار، ئەنە شۇ اﷲ دۇر ھەقىقەتتىن قالسا، گۇمراھلىقتىن غەيرىي نەرسە مەۋجۇت ئەمەس، نېمىشقا (ئىماندىن يۈز ئۆرۈپ گۇمراھلىققا) بۇرۇلۇپ كېتىسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,33,' پەرۋەردىگارىڭنىڭ اﷲ قا ئىتائەت قىلىشتىن باش تارتقانلار توغرىسىدىكى ھۆكمى ھەقلىق بولدى ئۇلار (اﷲ نىڭ بىرلىكىگە، رەسۇلۇللانىڭ پەيغەمبەرلىكىگە) ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,34,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەرنىڭ بۇتلىرىڭلارنىڭ ئىچىدە مەۋجۇداتنى يوقلۇقتىن بار قىلىپ (ئاندىن يوق قىلىپ)، ئاندىن ئۇنى ئەسلىگە كەلتۈرەلەيدىغانلار بارمۇ؟» ئېيتقىنكى، «اﷲ مەۋجۇداتنى يوقلۇقتىن بار قىلىپ (ئاندىن يوق قىلىپ)، ئاندىن ئۇنى ئەسلىگە كەلتۈرەلەيدۇ نېمىشقا (ھەقتىن باتىل تەرەپكە) بۇرۇلۇپ كېتىسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,35,' ئېيتقىنكى، «سىلەرنىڭ بۇتلىرىڭلارنىڭ ئارىسىدا (گۇمراھنى) ھەق يولغا يېتەكلىيەلەيدىغانلار بارمۇ؟» ئېيتقىنكى، «اﷲ گۇمراھنى ھەق يولغا يېتەكلىيەلەيدىغان زاتقا بويسۇنۇشقا تېگىشلىكمۇ؟ ياكى (گۇمراھنى) ھەق يولغا يېتەكلىيەلمەيدىغان، پەقەت ئۆزى باشقىلارنىڭ يېتەكلىشىگە موھتاج بولغان (بۇتلاغا) بويسۇنۇشقا تېگىشلىكمۇ؟ سىلەرگە نېمە بولدى؟ نېمىشقا مۇنداق (باتىل) ھۆكۈم چىقىرىسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,36,' ئۇلارنىڭ تولىسى (بۇتلارغا ئېتىقاد قىلىشتا) پەقەت پەرەزگىلا تايىنىدۇ، پەرەز دېگەن ھەقىقەتنى ئىسپاتلاشتا ھەقىقەتەن ھېچ نەرسىگە يارىمايدۇ شۈبھىسىزكى، اﷲ ئۇلارنىڭ قىلمىشلىرىنى ئوبدان بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,37,' بۇ قۇرئاننى بىراۋنىڭ اﷲ قا ئىپتىرا قىلىشى ئەقلىغە سىغمايدۇ (چۈنكى قۇرئاننى ھېچ ئىنسان ئىجاد قىلالمايدۇ)، لېكىن قۇرئان ئىلگىرى كەلگەن (تەۋرات، ئىنجىل قاتارلىق ساماۋى) كىتابلارنى تەستىق قىلغۇچىدۇر، اﷲ بەلگىلىگەن ئەھكاملارنى بايان قىلغۇچىدۇر، ئۇنىڭدا ھېچ شەك يوقتۇر، (ئۇ) ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,38,' ئۇلار: «قۇرئاننى مۇھەممەد ئۆزى توقۇپ چىققان» دېيىشەمدۇ؟ ئېيتقىنكى، «ئەگەر (بۇ سۆزۈڭلاردا) راستچىل بولساڭلار، اﷲ تىن باشقا (ياردەمگە) چاقىرالايدىغانلىكى كىشىڭلارنى چاقىرىپ، قۇرئانغا ئوخشاش بىرەر سۈرىنى كەلتۈرۈپ بېقىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,39,' ئۇنداق ئەمەس، ئۇلار قۇرئاننىڭ مەنىسىنى تولۇق چۈشەنمەي ۋە ھەقىقىتىگە تېخى ئەقلى يەتمەي تۇرۇپ ئۇنى ئىنكار قىلىشتى ئۇلاردىن بۇرۇن ئۆتكەن ئۈممەتلەرمۇ (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) مۇشۇلارغا ئوخشاش يالغانغا چىقارغان ئىدى زالىملارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراپ باققىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,40,' ئۇلارنىڭ ئىچىدە قۇرئانغا ئىشىنىدىغانلار بار، ئىشەنمەيدىغانلارمۇ بار، پەرۋەردىگارىڭ بۇزغۇنچىلارنى ھەممىدىن ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,41,' ئەگەر ئۇلار سېنى يالغانچى قىلىشتا چىڭ تۇرسا: «مېنىڭ ئەمەلىم ئۆزۈم ئۈچۈندۇر، سىلەرنىڭ ئەمەلىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، مېنىڭ ئەمەلىمدىن سىلەر ئادا ـ جۇداسىلەر، سىلەرنىڭ قىلمىشىڭلاردىن مەنمۇ ئادا ـ جۇدامەن (يەنى بىراۋ يەنە بىراۋنىڭ قىلغان گۇناھىغا جاۋابكار ئەمەس)» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,42,' ئۇلارنىڭ ئىچىدە (قۇرئان ئوقۇغىنىڭدا) ساڭا قۇلاق سالىدىغانلار بار، سەن گاسلارغا (يەنى اﷲ ئاڭلاش ئىقتىدارىنى ئېلىپ تاشلىغانلارغا) ئۇلار ھېچ نەرسىنى چۈشەنمىسىمۇ ئاڭلىتالامسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,43,' ئۇلارنىڭ ئىچىدە ساڭا قارايدىغانلار بار (يەنى پەيغەمبەرلىكىڭنىڭ ئېنىق ئالامەتلىرىنى بايقايدىغانلار بار، لېكىن ئۇلار بايقىغانلىرىدىن پايدىلانمىغانلىقلىرى ئۈچۈن كۆردۇر) (ھەقىقەتنى) كۆرەلمىسىمۇ كورلارنى ھىدايەت قىلالامسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,44,' شۈبھىسىزكى، اﷲ ئىنسانلارغا قىلچە زۇلۇم قىلمايدۇ، لېكىن ئىنسانلار (كۇفرى ۋە گۇناھ ئارقىلىق) ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,45,' قىيامەت كۈنى اﷲ ئۇلارنى (ھېساب ئېلىش ئۈچۈن) يىغىدۇ، (ئۇلارغا دۇنيادا تۇرغان مۇددىتى) كۈندۈزدە ئازغىنا ۋاقىت تۇرغاندەك قىسقا بىلىنىدۇ، ئۇلار ئۆزئارا تونۇشىدۇ، ئۆزلىرىنىڭ اﷲ قا مۇلاقات بولۇشىنى (يەنى ئۆلگەندىن كېيىن تىرىلىشىنى) ئىنكار قىلغانلار چوقۇم زىيان تارتىدۇ، ئۇلار توغرا يول تاپمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,46,' ئۇلارغا بىز ۋەدە قىلغان ئازابنىڭ بىر قىسمىنى ساڭا كۆرسىتىمىز (يەنى بۇ دۇنيادىلا، سەن ھايات ۋاقىتتىلا كۆرسىتىمىز)، ياكى ئۇنىڭدىن ئىلگىرى سېنى قەبزى روھ قىلىمىز، ئۇلار بىزنىڭ دەرگاھىمىزغا قايتىدۇ (مەيلى بالدۇر بولسۇن، مەيلى كېيىن بولسۇن، ئۇلارنى چوقۇم جازالايمىز)، ئاندىن اﷲ ئۇلارنىڭ قىلمىشلىرىغا گۇۋاھ بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,47,' (ئۆتكەن ئۈممەتلەرنىڭ) ھەر بىرىگە پەيغەمبەر ئەۋەتىلگەن (قىيامەت كۈنى) ئۇلارنىڭ پەيغەمبىرى كەلگەندە، ئۇلارنىڭ ئارىسىدا ئادىللىق بىلەن ھۆكۈم چىقىرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,48,' ئۇلار: «ئەگەر سىلەر راستچىل بولساڭلار (بىزگە ئېيتىپ بېرىڭلاركى) بۇ ۋەدە قاچان ئىشقا ئاشۇرۇلىدۇ؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,49,' ئېيتقىنكى: «اﷲ خالىمىسىلا، مەن ئۆزۈمدىن زىياننى دەپئى قىلىشقا، پايدا قازىنىشقا قادىر ئەمەسمەن، ھەر ئۈممەتنىڭ (ھالاك بولىدىغان) مەلۇم ۋاقتى بولىدۇ، شۇ ۋاقىت يېتىپ كەلگەندە، ئۇلار بىردەممۇ كېچىكتۈرۈلمەيدۇ، بىردەممۇ ئىلگىرى سۈرۈلمەيدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,50,' ئېيتقىنكى: «ئېيتىڭلارچۇ! ئەگەر اﷲ نىڭ ئازابى سىلەرگە كېچىسى ياكى كۈندۈزى كەلسە (بۇنىڭدىن سىلەرگە نېمە پايدا؟) گۇناھكارلار (يەنى مۇشرىكلار) ئازابتىن قايسى نەرسىگە ئالدىرايدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,51,' (اﷲ نىڭ) ئازابى يۈز بەرگەندە ئاندىن ئۇنىڭغا ئىشەندىڭلارمۇ؟ ئەمدى (ئېيتقان ئىمانىڭلار پايدىسىزدۇر، ئۇنىڭدىن ئىلگىرى مەسخىرە قىلىش بىلەن) ئازابنىڭ تېزرەك چۈشۈشىنى تەلەپ قىلاتتىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,52,' ئاندىن كېيىن (ئۆزلىرىگە) زۇلۇم قىلغانلارغا: «مەڭگۈلۈك ئازابنى تېتىڭلار، سىلەرگە پەقەت قىلمىشىڭلارنىڭ جازاسى بېرىلىدۇ» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,53,' ئۇلار سەندىن: «ئۇ (يەنى ۋەدە قىلىنغان ئازاب، ئۆلگەندىن كېيىن تىرىلىش) راستمۇ؟» دەپ سورايدۇ، سەن: «ھەئە، پەرۋەردىگارىم بىلەن قەسەمكى، ئۇ ئەلۋەتتە راستتۇر، سىلەر قېچىپ قۇتۇلالمايسىلەر» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,54,' ئەگەر ھەر بىر زۇلۇم قىلغان ئادەم (يەنى كاپىر) يەر يۈزىدىكى ھەممە نەرسىگە مالىك بولىدىغان بولسا (اﷲ نىڭ ئازابىدىن قۇتۇلۇش ئۈچۈن)، ئەلۋەتتە (ئۇنىڭ ھەممىسىنى) فىدىيە قىلىپ بىرىۋېتەتتى، ئۇلار ئازابنى كۆرگەن چاغدا نادامىتىنى يوشۇرۇن تۇتىدۇ (يەنى دۇنيادىكى قىلمىشلىرىغا ئىچىدە پۇشايمان قىلىدۇ) ئۇلارنىڭ (يەنى خالايىقنىڭ) ئارىسىدا ئادىللىق بىلەن ھۆكۈم چىقىرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,55,' راستلا ئاسمانلاردىكى ۋە زېمىندىكى بارلىق نەرسىلەر اﷲ نىڭ مۈلكىدۇر، راستلا اﷲ نىڭ ۋەدىسى ھەقتۇر، لېكىن ئۇلار (يەنى ئىنسانلار) نىڭ تولىسى (بۇنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,56,' اﷲ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنى) ئۆلتۈرىدۇ، سىلەر اﷲ نىڭ دەرگاھىغا قايتىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,57,' ئى ئىنسانلار! سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن نەسىھەت بولغان، دىللاردىكى دەردكە (يەنى شەك ۋە نادانلىققا) شىپا بولغان، مۆمىنلەرگە ھىدايەت ۋە رەھمەت بولغان (قۇرئان) كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,58,' ئېيتقىنكى، «ئۇلار اﷲ نىڭ پەزلى ۋە رەھمىتىدىن خۇشال بولسۇن، بۇ ئۇلارنىڭ يىغقان (دۇنيا ـ ماللىرىدىن) ياخشىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,59,' ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ! سىلەرگە اﷲ چۈشۈرگەن رىزىقتىن بەزىسىنى ھارام، بەزىسىنى ھالال قىلدىڭلار، اﷲ سىلەرگە مۇنداق قىلىشقا ئىزنى بەردىمۇ؟ ياكى اﷲ قا يالغاننى چاپلامسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,60,' اﷲ قا يالغاننى چاپلايدىغانلار قىيامەت كۈنى قانداق ئويلايدۇ؟ (يەنى جازاغا تارتىلمايمىز، دەپ ئويلامدۇ؟) شۈبھىسىزكى، اﷲ ئىنسانلارغا مەرھەمەتلىكتۇر (چۈنكى ئۇلارغا بولىدىغان ئازابنى كېچىكتۈردى)، لېكىن كىشىلەرنىڭ تولىسى (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,61,' سەن قايسى ھالەتتە بولمىغىن، قۇرئاندىن قايسى نەرسىنى ئوقۇمىغىن، سىلەر قايسىبىر ئىشنى قىلماڭلار، ئۇنىڭ بىلەن بولۇۋاتقان ۋاقتىڭلاردا ھامان بىز سىلەرنى كۆزىتىپ تۇرىمىز، ئاسمان ـ زېمىندىكى زەررە چاغلىق نەرسە ۋە ئۇنىڭدىن كىچىك ياكى چوڭ نەرسە بولسۇن، ھېچقايسىسى اﷲ نىڭ بىلىشىدىن چەتتە قالمايدۇ، ئۇلارنىڭ ھەممىسى لەۋھۇلمەھپۇزدا خاتىرىلەنگەندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,62,' راستلا اﷲ نىڭ دوستلىرىغا (ئاخىرەتتە اﷲ نىڭ ئازابىدىن) قورقۇش، (دۇنيادا قولدىن كەتكۈزۈپ قويغانغا) قايغۇرۇش يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,63,' ئۇلار ئىمان ئېيتقان ۋە تەقۋادارلىق قىلغانلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,64,' ئۇلارغا دۇنيا ۋە ئاخىرەتتە خۇش خەۋەر بېرىلىدۇ (يەنى دۇنيادا جان ئۈزۈش ۋاقتىدا، اﷲ نىڭ رازىلىقى ۋە رەھمىتىگە ئېرىشىدىغانلىقى بىلەن، ئاخىرەتتە نازۇنېمەتلىك جەننەتكە كىرىدىغانلىقى بىلەن خۇش خەۋەر بېرىلىدۇ)، اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ، ئەنە شۇ كاتتا بەختتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,65,' (ئى مۇھەممەد! ئۇلارنىڭ (ساڭا سەن پەيغەمبەر ئەمەسسەن دېگەن) سۆزى سېنى قايغۇغا سالمىسۇن، پۈتۈن كۈچ ـ قۇۋۋەت اﷲ قا مەنسۈپ (اﷲ ساڭا مەدەتكاردۇر) اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەمەللىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,66,' راستلا ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، بەندىسىدۇر، مەخلۇقاتىدۇر) اﷲ نى قويۇپ، بۇتلارغا چوقۇنىدىغانلار پەقەت يوقىلاڭ پەرەزگىلا تايىنىدۇ، ئۇلار پەقەت جۆيلۈيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,67,' ئى ئىنسانلار! ئارام ئېلىشىڭلار ئۈچۈن اﷲ سىلەرگە كېچىنى ياراتتى، نەرسىلەرنى كۆرۈشۈڭلار ئۈچۈن كۈندۈزنى ياراتتى، (ئىبرەت قۇلىقى بىلەن) ئاڭلايدىغان قەۋم ئۈچۈن بۇنىڭدا نۇرغۇن ئالامەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,68,' ئۇلار (يەنى كاپىرلار): «اﷲ نىڭ بالىسى بار» دېيىشتى، اﷲ (بۇنىڭدىن) پاكتۇر، بىھاجەتتۇر (يەنى بالا قىلىۋېلىش ئېھتىياج ئۈچۈن بولىدۇ، اﷲ بولسا ھېچ نەرسىگە موھتاج ئەمەستۇر)، ئاسمانلاردىكى، زېمىندىكى ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، (اﷲ نىڭ بالىسى بار دېگەن) بۇ سۆزۈڭلارغا ھېچقانداق پاكىتىڭلار يوق، سىلەر بىلمەيدىغان ئىشلارنى يالغاندىن اﷲ قا چاپلامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,69,' (ئى مۇھەممەد!) ئېيتقىنكى، «اﷲ قا يالغاننى چاپلايدىغانلار ھەقىقەتەن (دوزاخ ئازابىدىن) قۇتۇلالمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,70,' ئۇلار بۇ دۇنيادىن (ئازغىنا ۋاقىتلا) بەھرىمەن بولىدۇ، ئاندىن ئۇلار (ھېساب بېرىش ئۈچۈن) بىزنىڭ دەرگاھىمىزغا قايتىدۇ، ئاندىن كاپىر بولغانلىقلىرى سەۋەبىدىن ئۇلارغا قاتتىق ئازابنى تېتىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,71,' ئۇلارغا نۇھنىڭ قىسسىسىنى ئوقۇپ بەرگىن، ئۆز ۋاقتىدا نۇھ قەۋمىگە: «ئى قەۋمىم! ئەگەر سىلەرگە مېنىڭ ئىچىڭلاردا تۇرۇشۇم ۋە سىلەرنى اﷲ نىڭ ئايەتلىرى بىلەن قورقۇتۇشۇم ئېغىر كەلگەن بولسا، مەن اﷲ قا تەۋەككۈل قىلدىم (يەنى اﷲ قا يۆلەندىم، سىلەرگە پەرۋا قىلمايمەن) سىلەر بۇتلىرىڭلار بىلەن بىرلىكتە (مېنى قەستلەيدىغان) ئىشىڭلارنى قارار قىلىۋېرىڭلار، ئىشىڭلار سىلەرگە تۇتۇق بولۇپ قالمىسۇن (يەنى مېنى قەستلەيدىغان ئىشىڭلار ئاشكارا بولسۇن)، ئاندىن مېنى بىردەممۇ كېچىكتۈرمەي، مېنىڭ ھەققىمدە (خالىغىنىڭلارنى) ئىجرا قىلىۋېرىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,72,' ئەگەر سىلەر (مېنىڭ نەسىھىتىمدىن) يۈز ئۆرۈسەڭلار، سىلەردىن (يۈز ئۆرۈشۈڭلارغا سەۋەبچى بولىدىغان) ھەق سورىغىنىم يوق، مەن ئەجرىمنى پەقەت اﷲ تىن سورايمەن، مەن (اﷲ قا) بويسۇنغۇچىلاردىن بولۇشقا بۇيرۇلدۇم» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,73,' لېكىن ئۇلار نۇھنى يالغانچى قىلدى ئۇنى ۋە ئۇنىڭ بىلەن كېمىدە بىللە بولغۇچى (مۆمىن) لەرنى قۇتقۇزدۇق، ئۇلارنى (يەنى بىز قۇتقۇزغان كىشىلەرنى) (غەرق بولغانلارنىڭ) ئورۇنباسارلىرى قىلدۇق، بىزنىڭ مۆجىزىلىرىمىزنى ئىنكار قىلغانلارنى (توپان بالاسى بىلەن) غەرق قىلدۇق، (ئى مۇھەممەد!) ئاگاھلاندۇرۇلغۇچىلار (يەنى پەيغەمبەرلىرىنى يالغانچى قىلغانلار) نىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قارىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,74,' نۇھتىن كېيىن نۇرغۇن پەيغەمبەرلەرنى ئۆزلىرىنىڭ قەۋمىگە ئەۋەتتۇق، (قەۋمىگە) ئۇلار روشەن مۆجىزىلەر بىلەن كەلدى، ئۇلار (يەنى پەيغەمبەرلەر كېلىشتىن) ئىلگىرى ئىنكار قىلىشقان نەرسىگە ئىمان ئېيتىشمىدى (كۇفرىدا) ھەددىدىن ئاشقۇچىلارنىڭ دىللىرىنى ئەشۇنداق پېچەتلەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,75,' ئۇلاردىن كېيىن مۇسا بىلەن ھارۇننى پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا نۇرغۇن مۆجىزىلەر بىلەن ئەۋەتتۇق ئۇلار (ئىمان ئېيتىشتىن) بويۇنتاۋلىق قىلدى ئۇلار گۇناھكار قەۋم ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,76,' ئۇلارغا بىز تەرەپتىن ھەقىقەت نازىل بولغان چاغدا ئۇلار: «بۇ ھەقىقەتەن روشەن سېھىردۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,77,' مۇسا: «سىلەرگە ھەقىقەت كەلسە ئۇنى سېھىر دەمسىلەر؟ ئەجەبا بۇ سېھىرمۇ؟ سېھىرگەرلەر مۇۋەپپەقىيەت قازىنالمايدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,78,' ئۇلار: «سەن بىزگە ئاتا ـ بوۋىلىرىمىزنىڭ دىنىدىن بىزنى ۋاز كەچتۈرۈش ۋە زېمىن (يەنى مىسىر زېمىنى) نىڭ سەلتەنىتىنى ئىككىڭلارنىڭ قىلىۋېلىش ئۈچۈن كەلدىڭمۇ؟ بىز ئىككىڭلارغا ئىشەنمەيمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,79,' پىرئەۋن: «مېنىڭ ئالدىمغا بارلىق ئۇستا سېھىرگەرلەرنى ئېلىپ كېلىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,80,' سېھىرگەرلەر يېتىپ كەلگەندە، مۇسا ئۇلارغا: «(ئارغامچا ۋە ھاسىلىرىڭلاردىن) تاشلىماقچى بولغىنىڭلارنى تاشلاڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,81,' ئۇلار تاشلىغاندا، مۇسا: «سىلەرنىڭ كۆرسەتكىنىڭلار سېھىردۇر، اﷲ چوقۇم ئۇنى بەربات قىلىدۇ، اﷲ بۇزغۇنچىلارنىڭ ئىشىنى ئەلۋەتتە تۈزىمەيدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,82,' گۇناھكارلار ياقتۇرمىغان تەقدىردىمۇ، اﷲ پاكىتلىرى ئارقىلىق ھەقىقەتنى ئىسپاتلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,83,' مۇساغا پەقەت ئۆز قەۋمىدىن (يەنى بەنى ئىسرائىلنىڭ ئەۋلادىدىن) ئازغىنىسى پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىنىڭ زىيانكەشلىك قىلىشىدىن قورققان ھالدا ئىمان ئېيتتى پىرئەۋن زېمىندا (يەنى مىسىر زېمىنىدا) شەك ـ شۈبھىسىز مۇتەكەببىردۇر، ئۇ ئەلۋەتتە ھەددىدىن ئاشقۇچىلاردىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,84,' مۇسا: «ئى قەۋمىم! ئەگەر اﷲ قا ئىمان ئېيتقان بولساڭلار، اﷲ قا بويسۇنغان بولساڭلار، ئۇنىڭغا تەۋەككۈل قىلىڭلار (يەنى ھەممە ئىشىڭلارنى اﷲ قا تاپشۇرۇڭلار، ھەرقانداق ئەھۋالدا اﷲ قا يۆلىنىڭلار)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,85,' ئۇلار ئېيتتى: «اﷲ قا تەۋەككۈل قىلدۇق، پەرۋەردىگارىمىز، زالىم قەۋمنى بىزگە زىيانكەشلىك قىلىش ئىمكانىيىتىگە ئىگە قىلمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,86,' بىزنى رەھمىتىنىڭ بىلەن زالىم قەۋمدىن قۇتۇلدۇرغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,87,' بىز مۇسا بىلەن ئۇنىڭ قېرىندىشىغا: «قەۋمىڭلار ئۈچۈن مىسىردا ئۆيلەر ياساڭلار، ئۆيلىرىڭلارنى ناماز ئوقۇيدىغان جاي قىلىڭلار، ناماز ئوقۇڭلار، مۆمىنلەرگە (دۈشمەنلىرى ئۈستىدىن غەلىبە قىلىدىغانلىقى بىلەن) خۇش خەۋەر بېرىڭلار» دەپ ۋەھىي قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,88,' مۇسا ئېيتتى: «ئى پەرۋەردىگارىمىز! كىشىلەرنى يولۇڭدىن ئازدۇرۇشى ئۈچۈن ـ ئى پەرۋەردىگارىمىز! ـ پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا ھاياتىي دۇنيادا زىننەت بۇيۇملىرىنى، تۈرلۈك مال ـ مۈلۈكلەرنى بەردىڭ، پەرۋەردىگارىمىز! ئۇلارنىڭ مال ـ مۈلۈكلىرىنى يوق قىلغىن، ئۇلارنىڭ دىللىرىنى قاتتىق قىلغىن، شۇنىڭ بىلەن ئۇلار ئىمان ئېيتماي قاتتىق ئازابنى كۆرسۇن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,89,' اﷲ ئېيتتى: «ئىككىڭلارنىڭ دۇئاسى ئىجابەت قىلىندى، دەۋىتىڭلارنى داۋاملاشتۇرۇڭلار، نادانلارنىڭ يولىغا ئەگەشمەڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,90,' بىز ئىسرائىل ئەۋلادىنى دېڭىزدىن ئۆتكۈزۈۋەتتۇق، ئۇلارنى پىرئەۋن ۋە ئۇنىڭ ئەسكەرلىرى زۇلۇم ۋە زوراۋانلىق قىلىش يۈزىسىدىن قوغلىدى پىرئەۋن غەرق بولىدىغان ۋاقىتتا «ئىمان ئېيتتىمكى، ئىسرائىل ئەۋلادى ئىمان ئېيتقان ئىلاھتىن غەيرىي ئىلاھ يوقتۇر، مەن مۇسۇلمانلاردىنمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,91,' (ئۇنىڭغا دېيىلدىكى) «(ھاياتتىن ئۈمىد ئۈزگىنىڭدە) ئەمدى (ئىمان ئېيتامسەن؟) ئىلگىرى اﷲ قا ئاسىيلىق قىلغان ۋە بۇزغۇنچىلاردىن بولغان ئىدىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,92,' سەندىن كېيىنكىلەرگە ئىبرەت بولۇشۇڭ ئۈچۈن، بۈگۈن سېنىڭ جەسىتىڭنى قۇتقۇزىمىز (يەنى سېنىڭ جەسىتىڭنى دېڭىزدىن چىقىرىپ قويىمىز)» نۇرغۇن كىشىلەر، شەك ـ شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزدىن غاپىلدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,93,' شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىنى (ئۇلارنىڭ دۈشمەنلىرىنى ھالاك قىلغاندىن كېيىن) ياخشى جايغا ئورۇنلاشتۇردۇق، ئۇلارنى شېرىن نەرسىلەر بىلەن رىزىقلاندۇردۇق، ئۇلارغا ئىلىم (يەنى اﷲ نىڭ ھۆكمىنى ئىچىگە ئالغان تەۋرات) كەلگەندىن كېيىن، ئۇلار (دىن ئىشىدا) ئاندىن ئىختىلاپ قىلىشتى پەرۋەردىگارىڭ قىيامەت كۈنى، ئۇلار ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە، ئۇلارنىڭ ئارىسىدا ئەلۋەتتە ھۆكۈم چىقىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,94,' مۇبادا سەن ساڭا بىز نازىل قىلغان كىتابتىن شەكلىنىدىغان بولساڭ، سەندىن ئىلگىرى كىتاب ئوقۇغانلار (يەنى تەۋرات بىلەن ئىنجىل نازىل قىلىنغان يەھۇدىيلار ۋە ناسارالار) دىن سوراپ باققىن، شۈبھىسىزكى، ساڭا پەرۋەردىگارىڭ تەرىپىدىن ھەق (قۇرئان) كەلدى سەن ھەرگىز شەك قىلغۇچىلاردىن بولمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,95,' سەن ھەرگىز اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغۇچىلاردىن بولمىغىن، (ئۇنداق بولساڭ) زىيان تارتقۇچىلاردىن بولۇپ قالىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,96,' شۈبھىسىزكى، پەرۋەردىگارىڭنىڭ لەنىتىگە تېگىشلىك بولغانلار ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,97,' ئۇلارغا پۈتۈن مۆجىزىلەر كۆرسىتىلگەن تەقدىردىمۇ، قاتتىق ئازابنى كۆرمىگىچە (ئىمان ئېيتمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,98,' (ھالاك بولغان شەھەرلەر ئاھالىلىرى ئىچىدە) يۇنۇس قەۋمىدىن باشقىسىغا (ئازابنىڭ ئالامىتىنى كۆرۈپ) ئېيتقان ئىماننىڭ پايدىسى بولغىنى يوق، يۇنۇس قەۋمى ئىمان ئېيتقاندا، دۇنيا ھاياتىدا رەسۋا قىلىدىغان ئازابنى ئۇلارنىڭ ئۈستىدىن كۆتۈرۈۋەتتۇق، ئۇلارنى مەلۇم ۋاقىتقىچە (يەنى ئەجىلى يەتكەنگە قەدەر دۇنيادىكى شەيئىلەردىن) بەھرىمەن قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,99,' ئەگەر پەرۋەردىگارىڭ خالىسا ئىدى، ئەلۋەتتە، يەر يۈزىدىكى كىشىلەرنىڭ ھەممىسى ئىمان ئېيتاتتى، سەن كىشىلەرنى مۇسۇلمان بولۇشقا مەجبۇرلامسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,100,' ھەر قانداق كىشى پەقەت اﷲ نىڭ ئىرادىسى بولغاندىلا، ئاندىن ئىمان ئېيتىدۇ، (اﷲ نىڭ ئايەتلىرى ئۈستىدە) پىكىر يۈرگۈزمەيدىغانلارنى اﷲ ئازابقا گىرىپتار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,101,' ئېيتقىنكى، «ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى كۆزىتىڭلار مۆجىزىلەر ۋە ئاگاھلاندۇرغۇچىلارنىڭ (يەنى پەيغەمبەرلەرنىڭ) ئىمان ئېيتمايدىغان قەۋمگە پايدىسى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,102,' ئۇلار پەقەت ئۆزلىرىدىن بۇرۇن ئۆتكەنلەرگە كەلگەن كۈنلەرنىڭ (يەنى ئازابنىڭ) (ئۆزلىرىگە كېلىشىنى) كۈتىدۇ» (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەر (زۇلۇمنىڭ ئاقىۋىتىنى) كۈتۈڭلار، مەنمۇ سىلەر بىلەن بىرلىكتە ھەقىقەتەن (سىلەرنىڭ ھالاك بولۇشۇڭلارنى) كۈتكۈچىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,103,' ئاندىن كېيىن پەيغەمبەرلىرىمىزنى، مۆمىنلەرنى (ئازابتىن) قۇتقۇزدۇق، مۆمىنلەرنى قۇتقۇزۇشقا مۇشۇنداق مەسئۇل بولىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,104,' (ئى مۇھەممەد! قەۋمىڭنىڭ مۇشرىكلىرىغا) ئېيتقىنكى، «ئى ئىنسانلار! ئەگەر سىلەر مېنىڭ دىنىمدىن گۇمانلىنىدىغان بولساڭلار، سىلەرنىڭ اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلارغا مەن چوقۇنمايمەن، لېكىن مەن سىلەرنى قەبزى روھ قىلىدىغان اﷲ قا ئىبادەت قىلىمەن، مەن مۆمىنلەردىن بولۇشقا بۇيرۇلدۇم»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,105,' يەنە (ماڭا): «باتىل دىنلاردىن بۇرۇلۇپ، توغرا دىنغا يۈزلەنگىن، مۇشرىكلاردىن بولمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,106,' اﷲ نى قويۇپ، ساڭا پايدا ـ زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە ئىبادەت قىلمىغىن، ئەگەر ئۇلارغا ئىبادەت قىلساڭ، چوقۇم (ئۆزۈڭگە) زۇلۇم سالغۇچىلاردىن بولىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,107,' ئەگەر اﷲ ساڭا بىرەر زىيان ـ زەخمەت يەتكۈزۈشنى ئىرادە قىلسا، يالغۇز اﷲ تىن باشقا ئۇنى دەپئى قىلغۇچى بولمايدۇ، ئەگەر اﷲ ساڭا بىرەر ياخشىلىقنى ئىرادە قىلسا، اﷲ نىڭ پەزلىنى قايتۇرغۇچى بولمايدۇ، اﷲ پەزلىنى بەندىلىرىدىن خالىغان ئادەمگە يەتكۈزىدۇ، اﷲ (بەندىلىرىنىڭ گۇناھىنى) مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) مېھرىباندۇر» (دېيىلدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,108,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئىنسانلار! سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن ھەق دىن كەلدى، كىمكى ھىدايەت تاپىدىكەن، ئۆزىنىڭ پايدىسى ئۈچۈن ھىدايەت تاپقان بولىدۇ، كىمكى ئازىدىكەن، ئۆزىنىڭ زىيىنى ئۈچۈن ئازغان بولىدۇ، مەن سىلەرگە ھامىي ئەمەسمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,10,109,' (ئى مۇھەممەد!) ساڭا نازىل قىلىنغان ۋەھيىگە ئەگەشكىن (يەنى ئۇنىڭغا ئۆزۈڭ ئەمەل قىلىپ، باشقىلارغا ئۇنى يەتكۈزگىن) اﷲ (سەن بىلەن ئۇلارنىڭ ئارىسىدا) ھۆكۈم چىقارغۇچە (دىنغا دەۋەت قىلىش يولىدىكى مۇشەققەتلەرگە) چىدىغىن، اﷲ ئەڭ ھەققانىي ھۆكۈم چىقارغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,1,'ئەلىف، لام، را بۇ، ھېكمەت بىلەن ئىش قىلغۇچى، ھەممىدىن خەۋەردار اﷲ تەرىپىدىن نازىل بولغان ئايەتلەرنىڭ تۈزۈلۈشى پۇختا، (ئەقىدە، ئەھكام ۋەز ۋە قىسسىلەر) تەپسىلىي بايان قىلىنغان كىتابتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,2,' (ئېيتقىنكى) «سىلەر پەقەت اﷲ قا ئىبادەت قىلىڭلار، شۈبھىسىزكى، مەن سىلەرگە اﷲ تەرىپىدىن ئەۋەتىلگەن، (كاپىر بولساڭلار سىلەرنى اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرغۇچىمەن، (ئىمان ئېيتساڭلار اﷲ نىڭ ساۋابى بىلەن سىلەرگە) خۇش خەۋەر بەرگۈچىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,3,' سىلەر پەرۋەردىگارىڭلاردىن مەغپىرەت تەلەپ قىلىڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، اﷲ سىلەرنى مۇئەييەن مۇددەتكىچە(يەنى ئۆمرۈڭلار ئاخىرلىشىپ، ئەجىلىڭلار يەتكۈچە ھاياتىي دۇنيادىن) ئوبدان بەھرىمەن قىلىدۇ، ياخشى ئىش قىلغۇچىغا قىلغان ياخشىلىقنىڭ ساۋابىنى بېرىدۇ، ئەگەر(ئىماندىن) يۈز ئۆرۈسەڭلار، (ئازابقا دۇچار بولىسىلەر) سىلەرنىڭ قىيامەت كۈنىنىڭ چوڭ ئازابىغا قېلىشىڭلاردىن ئەنسىرەيمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,4,' (ئۆلگەندىن كېيىن) اﷲ نىڭ دەرگاھىغا قايتىسىلەر، اﷲ ھەر نەرسىگە قادىردۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,5,' راستتىنلا ئۇلار(يەنى كاپىرلار) (رەسۇلۇللاھ، قۇرئان ئوقۇۋاتقاندا ئۇنىڭ يېنىدىن ئۆتكەنلىرىدە) ئۇنىڭدىن يوشۇرۇش ئۈچۈن يۈز ئۆرۈيدۇ، راستلا ئۇلار كىيىملىرى بىلەن چۈمكىنىۋالغانلىرىدا ، اﷲ ئۇلارنىڭ يوشۇرۇن ۋە ئاشكارا ئىشلىرىنى بىلىپ تۇرىدۇ، اﷲ ھەقىقەتەن دىللاردىكىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,6,' يەر يۈزىدىكى ھايۋانلارنىڭ ھەممىسىگە رىزىق بېرىشنى اﷲ (مەرھەمەت يۈزىسىدىن) ئۈستىگە ئالغان، اﷲ ئۇلارنىڭ تۇرار جايىنى ۋە (ئۆلگەندىن كېيىن) كۆمۈلىدىغان جاينى بىلىدۇ، ئۇلارنىڭ ھەممىسى روشەن كىتابقا(يەنى لەۋھۇلمەھپۇزغا يېزىلغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,7,' اﷲ ئاسمانلارنى ۋە زېمىننى ئالتە كۈندە ياراتتى (ئاسمان ـ زېمىننى يارتىشتىن ئىلگىرى) اﷲ نىڭ ئەرشى سۇ ئۈستىدە ئىدى سىلەرنىڭ قايسىڭلارنىڭ ئەمەلى ياخشى ئىكەنلىكىنى سىناش ئۈچۈن اﷲ (ئۇلارنى ياراتتى) (ئى مۇھەممەد!) ئەگەر سەن(مەككە كۇففارلىرىغا): «سىلەر ئۆلگەندىن كېيىن چوقۇم تىرىلىسىلەر!» دېسەڭ، كاپىرلار چوقۇم ئېيتىدۇ: «بۇ(سۆزنى ئىچىگە ئالغان قۇرئان) پەقەت روشەن سېھىردۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,8,' ئەگەر بىز ئۇلارغا نازىل بولىدىغان ئازابنى چەكلىك ۋاقىت كېچىكتۈرسەك ، ئۇلار چوقۇم(مەسخىرە قىلىش يۈزىسىدىن): «ئازاب نېمىشقا چۈشمەيدۇ؟» دەيدۇ، راستلا ئۇلارغا اﷲ نىڭ ئازابى چۈشكەن كۈندە، ئۇلار قاچىدىغان يەر تاپالمايدۇ ئۇلار مەسخىرە قىلغان ئازاب ئۇلارنى قورشىۋالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,9,' ئەگەر بىز بىرەر ئىنسانغا رەھمىتىمىزنى تېتىتساق (يەنى نېمىتىمىزنى بەرسەك)، ئاندىن ئۇنى ئۇنىڭدىن تارتىۋالساق، ئۇ چوقۇم (اﷲ نىڭ رەھمىتىدىن) ئۈمىدسىزلىنىپ كېتىدۇ، كۇفرانى نېمەت قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,10,' ئەگەر بىز (پېقىرلىق ، كېسەللىك، قاتتىقچىلىق قاتارلىق) بالالارغا مۇپتىلا قىلغاندىن كېيىن، ئۇنىڭغا نېمىتىمىزنى تېتىتساق، ئۇ چوقۇم: «مەندىن بالالار كۆتۈرۈلۈپ كەتتى (ئەمدى كەلمەيدۇ)» دەپ شەك شۈبھىسىز خۇشاللىنىپ، مەغرۇرلىنىپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,11,' پەقەت (بالاغا ئۇچرىغاندا) سەۋرى قىلغان، (نېمەتكە يولۇققاندا) ياخشى ئىشلارنى قىلغان كىشىلەر(ياخشىلاردۇر) ئۇلارنىڭ گۇناھى مەغپىرەت قىلىنىدۇ، ئۇلار(ئاخىرەتتە) چوڭ مۇكاپاتقا ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,12,' (ئى مۇھەممەد!) ئۇلارنىڭ (يەنى مۇشرىكلارنىڭ) «نېمىشقا ئۇنىڭغا بىر خەزىنە چۈشمىدى؟ ياكى ئۇنىڭ بىلەن بىللە (ئۇنى تەستىقلايدىغان) بىرەر پەرىشتە كەلمىدى؟» دەيدىغانلىقى ئۈچۈن، ساڭا نازىل قىلىنغان ۋەھيىنىڭ بىر قىسىمىنى يەتكۈزگۈڭ كەلمەيدىغاندۇ، يەتكۈزۈشتىن يۈرىكىڭ سىقىلىدىغاندۇ، سەن پەقەت (ئۇلارنى اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرغۇچىسىمەن، اﷲ ھەممە نەرسىنى تەسەررۇپ قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,13,' ئۇلار قۇرئاننى(مۇھەممەد) ئۆزى ئىجاد قىلغان دېيىشەمدۇ؟ ئېيتقىنكى، «(بۇ سۆزۈڭلاردا) راستچىل بولساڭلار، اﷲ تىن غەيرىي چاقىرالايدىغانلىكى كىشىلىرىڭلارنى (ياردەمگە) چاقىرىپ، (پاساھەت ۋە بالاغەت جەھەتتىن) قۇرئاندىكىگە ئوخشايدىغان ئون سۆز ئىجاد قىلىپ بېقىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,14,' ئەگەر ئۇلار(بۇ چاقىرىقىڭلارغا) ئاۋاز قوشمىسا، بىلىڭلاركى، قۇرئان اﷲ تىڭ ۋەھيى قىلىشى بىلەن نازىل قىلىنغان اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر، سىلەر(اﷲ قا) بويسۇنامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,15,' (قىلغان ياخشى ئەمەللىرى بىلەن پەقەت) ھاياتىي دۇنيانى ۋە ئۇنىڭ زىبۇزىننىتىنى(يەنى دۇنيانىڭ نېمەتلىرىنى) كۆزلەيدىغان كىشىلەرگە ئۇلارنىڭ ئەمەللىرىنىڭ ئەجرىنى بۇ دۇنيادىلا تولۇق بېرىمىز، دۇنيادا ئۇلارنىڭ ئەجرىدىن ھېچ نەرسە كېمەيتىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,16,' بۇنداق كىشىلەرگە ئاخىرەتتە دوزاختىن باشقا نەرسە يوقتۇر، ئۇلارنىڭ قىلغان ئىشلىرى يوققا چىقىرىلىدۇ، قىلغان(ياخشى) ئەمەللىرى ھېسابقا ئېلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,17,' پەرۋەردىگارى تەرىپىدىن كەلگەن روشەن دەلىل (يەنى قۇرئان) غا ئاساسلانغان ئادەم (يەنى مۇھەممەد ئەلەيھىسسالام) (ھاياتىي دۇنيانى كۆزلەيدىغان كىشىلەر بىلەن ئوخشاشمۇ؟) ئۇنىڭغا اﷲ تەرىپىدىن كەلگەن گۇۋاھلىق بەرگۈچى (يەنى جىبرىئىل ئەلەيھىسسالام) قۇرئاننى تىلاۋەت قىلىدۇ، قۇرئاندىن ئىلگىرى مۇساغا نازىل قىلىنغان كىتاب (يەنى تەۋرات) (ئىسرائىل ئەۋلادىغا) يېتەكچى ۋە رەھمەت قىلىپ(نازىل قىلىندى)، ئەنە شۇنداق كىشىلەر قۇرئانغا (ھەقىقىي ئىشىنىدۇ، (كۇففار) جامائەلىرىدىن كىمكى قۇرئاننى ئىنكار قىلىدىكەن، ئۇنىڭغا ۋەدە قىلىنغان جاي دوزاختۇر، قۇرئانغا شەك كەلتۈرمىگىن، ئۇ ھەقىقەتەن پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان ھەقىقەتتۇر، لېكىن ئىنسانلارنىڭ تولىسى (قۇرئاننىڭ ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغانلىقىغا) ئىشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,18,' ( اﷲ نىڭ شېرىكى بار، بالىسى بار دەپ) اﷲ قا يالغاننى چاپلىغان كىشىدىنمۇ زالىم كىشى بارمۇ؟ ئەنە شۇلار(قىيامەت كۈنى) پەرۋەردىگارىغا توغرىلىنىدۇ، گۇۋاھچىلار: «بۇلار پەرۋەردىگارى ھەققىدە يالغان سۆزلىگەنلەردۇر» دەيدۇ راستلا اﷲ نىڭ لەنىتى زالىملارغا بولىدۇ (يەنى اﷲ زالىملارنى رەھمىتىدىن يىراق قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,19,' ئۇلار (كىشىلەرنى) اﷲ نىڭ يولىدىن (يەنى ئىسلام يولىدىن) توسىدۇ، اﷲ نىڭ يولىنىڭ ئەگرى بولىشىنى تىلەيدۇ، ئۇلار ئاخىرەتنى ئىنكار قىلغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,20,' ئەنە شۇلار زېمىندا اﷲ نىڭ ئازابىدىن قېچىپ قۇتۇلالمايدۇ، ئۇلارغا اﷲ تىن باشقا (ئىگە بولىدىغان) ياردەمچىلەرمۇ بولمايدۇ، ئۇلار قاتمۇ قات ئازابقا دۇچار بولىدۇ، ئۇلار (ھەقنى) ئاڭلاشقا ۋە (ھەقنى) كۆرۈشكە قادىر بولالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,21,' ئەنە شۇلار ئۆزلىرىگە زىيان سالغۇچىلاردۇر ئۇلارنىڭ ئويدۇرۇپ چىقارغان بۇتلىرى ئۇلاردىن ئۆزلىرىنى چەتكە ئالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,22,' شەك يوقكى، ئۇلار ئاخىرەتتە ئەڭ زىيان تارتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,23,' شۈبھىسىزكى ، ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار، پەرۋەردىگارىنىڭ ھۇزۇرىدا ئاجىزلىق بىلدۈرگەنلەر ـ ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە مەڭگۈ قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,24,' (كاپىرلار ۋە مۆمىنلەردىن ئىبارەت) ئىككى پىرقە) ئەماغا ۋە گاسقا ئوخشايدۇ، (يەنە بىر پىرقە بولسا) كۆزى كۆرۈدىغان ، قۇلىقى كۆرۈدىغان ، قۇلىقى ئاڭلايدىغان ئادەمگە ئوخشايدۇ، ئۇلارنىڭ ئەھۋالى بىر ـ بىرىگە ئوخشامدۇ؟ ئىبرەت ئامامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,25,' شۈبھىسىزكى، بىز نۇھنى قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق، ئۇ: «مەن ھەقىقەتەن سىلەرگە ئوچۇق ئاگاھلاندۇرغۇچىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,26,' بىر اﷲ تىن باشقىغا ئىبادەت قىلماڭلار، سىلەرنىڭ قاتتىق كۈننىڭ ئازابىغا ئۇچرىشىڭلاردىن قورقىمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,27,' ئۇنىڭ قەۋمىنىڭ كاپىر كاتتىلىرى: «بىزنىڭچە، سەن پەقەت بىزگە ئوخشاش بىر ئادەمسەن ، بىزنىڭچە، ئارىمىزدىكى پەس كىشىلەر يەڭگىللىك بىلەن ساڭا ئەگەشكەن، بىزنىڭچە، سىلەرنىڭ ھېچقانداق ئارتۇقچىلىقىڭلار يوق، بەلكى بىز سىلەرنى يالغانچى دەپ ئويلايمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,28,' نۇھ ئېيتتى: «ئى قەۋمىم، ئېيتىڭلارچۇ، مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان (دەۋىتىمنىڭ توغرىلىقىنى ئىسپاتلايدىغان) ئېنىق دەلىلگە ئاساسلانسام ۋە ئۇنىڭ رەھمىتىگە (يەنى پەيغەمبەرلىككە) سازاۋەر بولغان بولسام، ئۇ دەلىل سىلەر ئۈچۈن مەخپىي بولسا، ئۇنى يامان كۆرسەڭلار، ئۇنى قوبۇل قىلىشقا سىلەرنى مەجبۇرلامدۇق؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,29,' ئى قەۋمىم! سىلەرگە (دىننى) تەبلىغ قىلغانلىقىم ئۈچۈن سىلەردىن پۇل ـ مال تەلەپ قىلمايمەن، ماڭا ئەجىر بېرىشنى پەقەت اﷲ ئۆز ئۈستىگە ئالغان، مەن ئىمان ئېيتقان كىشىلەرنى (يېنىمدىن) قوغلىۋەتمەيمەن، (چۈنكى) ئۇلار، شۈبھىسىزكى، پەرۋەردىگارىغا مۇلاقات بولىدۇ، لېكىن مەن سىلەرنى نادان قەۋم كۆرۈمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,30,' ئى قەۋمىم! ئەگەر مەن ئۇلارنى قوغلىۋەتسەم، اﷲ نىڭ ئازابىدىن مېنى كىم قۇتقۇزالايدۇ؟ ئويلىنىپ باقمامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,31,' مەن سىلەرگە، مەندە اﷲ نىڭ خەزىنىلىرى بار، دېمەيمەن، غەيبنى بىلىمەن دېمەيمەن، مەن ئەلۋەتتە (ئۆزۈمنى) پەرىشتە دەپمۇ ئېيتمايمەن ھەمدە سىلەر كۆزگە ئىلمايدىغان كىشىلەرنى اﷲ ئۇلارغا ياخشىلىق بەرمەيدۇ دېمەيمەن، اﷲ ئۇلارنىڭ دىللىرىدىكىنى ئوبدان بىلىدۇ، بولمىسا مەن چوقۇم زالىملاردىن بولۇپ قالىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,32,' ئۇلار(يەنى نۇھنىڭ قەۋمى): «ئى نۇھ ! بىز بىلەن مۇنازىرلەشتىڭ، ناھايىتى كۆپ مۇنازىرلەشتىڭ ئەگەر راست سۆزلىگۈچىلەردىن بولساڭ، بىزنى قورقۇتقان ئازابنى چۈشۈرۈپ باققىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,33,' نۇھ ئېيتتى: «ئۇنى (يەنى ئازابنى) ئەگەر خالىسا اﷲ لا چۈشۈرەلەيدۇ، سىلەر ھەرگىز قېچىپ قۇتۇلالمايسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,34,' مەن سىلەرگە قىلغان نەسىھىتىمنىڭ سىلەرگە پايدىسى بولمايدۇ، اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، سىلەر اﷲ نىڭ دەرگاھىغا قايتىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,35,' ياكى ئۇلار (يەنى قۇرەيشنىڭ كاپىرلىرى) قۇرئاننى ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ئۆزى ئىجاد قىلدى دېيىشەمدۇ؟ «ئەگەر ئۇنى مەن ئىجاد قىلغان بولسام، ئۇ گۇناھىمغا ئۆزۈم مەسئۇل، مەن سىلەرنىڭ گۇناھىڭلاردىن ئادا ـ جۇدا مەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,36,' نۇھقا (مۇنداق) ۋەھيى قىلىندى: «قەۋمىڭدىن ئىلگىرى ساڭا ئىمان ئېيتقانلاردىن باشقا يەنە ئىمان ئېيتقۇچىلار بولمايدۇ، ئۇلارنىڭ قىلمىشىدىن قايغۇرمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,37,' بىزنىڭ كۆز ئوڭىمىزدا ۋەھيىمىز بويىچە كېمە ياسىغىن، (ئۆزلىرىگە) زۇلۇم قىلغانلار توغرىسىدا ماڭا سۆز ئاچمىغىن (يەنى ئۇلارغا شاپائەت قىلمىغىن)، ئۇلار چوقۇم (توپان بالاسىدا) غەرق قىلىنىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,38,' نۇھ كېمە ياسىدى، قەۋمنىڭ چوڭلىرى ئۇنىڭ ئالدىدىن ئۆتكەندە ئۇنى مەسخىرە قىلىشتى، نۇھ ئېيتتى: «ئەگەر سىلەر (بۈگۈن) بىزنى مەسخىرە قىلساڭلار (كېلەچەكتە سىلەر غەرق قىلىنغاندا)، سىلەر بىزنى مەسخىرە قىلغاندەك بىزمۇ سىلەرنى چوقۇم مەسخىرە قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,39,' خار قىلغۇچى ئازاب (يەنى توپان بالاسى بىلەن غەرق بولۇش) نىڭ كىمنىڭ ئۈستىگە كېلىدىغانلىقىنى، دائىمىي ئازاب(يەنى جەھەننەم ئازابى) نىڭ كىمگە چۈشىدىغانلىقىنى كېلەچەكتە بىلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,40,' (توپان بالاسى توغرىسىدىكى) بۇيرۇقىمىز يېتىپ كەلگەن ۋە يەر يۈزىدىن سۇ ئېتىلىپ چىققان چاغدا: «كېمىگە ھايۋاناتلاردىن (ئەركەك چىشى بولۇپ) بىر جۇپتىن ئېلىۋالغىن، ئائىلەڭدىكىلەر بىلەن ھالاك بولۇشى ھۆكۈم قىلىنغانلار (يەنى نۇھ ئەلەيھىسسالامنىڭ ئايالى ۋائىلە بىلەن ئوغلى كەنئان) بۇنىڭ سىرتىدا ـ ۋە ئىمان ئېيتقانلار بىلەن كېمىگە چىققىن» دېدۇق، ئۇنىڭ بىلەن پەقەت ئازغىنا كىشىلەر ئىمان ئېيتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,41,' نۇھ (ئىمان ئېيتقان تەۋەلىرىگە) ئېيتتى: «كېمىگە چىقىڭلار، كېمىنىڭ مېڭىشى ۋە توختىشى اﷲ نىڭ ئىسمى بىلەندۇر، شۈبھىسىزكى، مېنىڭ پەرۋەردىگارىم (تەۋبە قىلغۇچىلارنىڭ گۇناھىنى) ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,42,' كېمە ئۇلارنى ئېلىپ تاغدەك دولقۇنلار ئىچىدە ئۈزۈپ باراتتى، نۇھ كېمە مېڭىشتىن بۇرۇن ئۆزىدىن يىراقتا تۇرغان ئوغلىنى: «ئى ئوغلۇم، بىز بىلەن بىللە كېمىگە چىققىن، كاپىرلار بىلەن بىللە بولمىغىن» دەپ توۋلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,43,' ئۇ: «مېنى سۇدا غەرق بولۇشتىن ساقلاپ قالىدىغان بىر تاغنىڭ ئۈستىگە چىقىۋالىمەن» دېدى نۇھ: «بۈگۈن اﷲ رەھىم قىلغان ئادەمدىن باشقا ھېچ ئادەم ئۇنىڭ جازالىشىدىن قۇتۇلۇپ قالالمايدۇ» دېدى ئۇلارنىڭ ئارىسىنى دولقۇن ئايرىۋەتتى ـ دە، ئۇ غەرق بولۇپ كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,44,' «ئى زېمىن سۈيۈڭنى يۇتقىن، ئى بۇلۇت تارقىغىن» دېيىلدى ، سۇ سوغۇلدى، اﷲ نىڭ (كاپىرلارنى غەرق قىلىشتىن ئىبارەت) ھۆكمى ئىجرا قىلىندى، كېمە جۇدى تېغىنىڭ ئۈستىدە توختىدى، «زالىم قەۋم ھالاك بولسۇن» دېيىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,45,' نۇھ پەرۋەردىگارىغا دۇئا قىلىپ: «پەرۋەردىگارىم! ئوغلۇم مېنىڭ ئائىلەمدىكىلەردىن ئىدى (سەن ماڭا ئۇلارنىڭ نىجات تېپىشىنى ۋەدە قىلغان ئىدىڭ)، سېنىڭ ۋەدەڭ ئەلۋەتتە ھەقتۇر، سەن ھەقىقەتەن ئەڭ توغرا ھۆكۈم قىلغۇچىسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,46,' اﷲ ئېيتتى: «ئى نۇھ! ئۇ(مەن نىجات تېپىشىنى ۋەدە قىلغان) ئائىلەڭدىكىلەردىن ئەمەس، ئۇنىڭ ئەمەلى ياماندۇر، سەن ئېنىق بىلمىگەن نەرسەڭنى مەندىن سورىمىغىن، مەن سېنىڭ جاھىللاردىن بولماسلىقىڭنى نەسىھەت قىلىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,47,' نۇھ ئېيتتى: «پەرۋەردىگارىم!مەن بىلمىگەن نەرسىنى سوراشتىن سېنىڭ پاناھىڭغا سېغىنىمەن، ئەگەر ماڭا مەغپىرەت قىلمىساڭ، زىيان تارتقۇچىلاردىن بولىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,48,' ئېيتىلدى (يەنى اﷲ ئېيتتى): «ئى نۇھ !ساڭا، سەن بىلەن (كېمىدە) بىللە بولغانلارنىڭ بىر قىسىم ئەۋلادىغا بىز تەرەپتىن نازىل بولغان ئامانلىق ۋە بەرىكەتلەر بىلەن (كېمىدىن) چۈشكىن، ئۇلارنىڭ يەنە بىر قىسىم ئەۋلادىنى (ھاياتىي دۇنيادىن) بەھرىمەن قىلىمىز، ئاندىن ئۇلار بىزنىڭ قاتتىق ئازابىمىزغا دۇچار بولىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,49,' (ئى مۇھەممەد!) ئەنە شۇ (قىسسە) غەيبكە ئائىت خەۋەرلەردىندۇر، ساڭا ئۇنى ۋەھيى قىلدۇق، سەن ۋە سېنىڭ قەۋمىڭ بۇنىڭدىن ئىلگىرى ئۇنى بىلمەيتتىڭلار، سەۋر قىلغىن، شۈبھىسىزكى، ياخشى ئاقىۋەت تەقۋادارلارغا مەنسۇپتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,50,' ئاد قەۋمىگە ئۇلارنىڭ قېرىندىشى ھۇدنى ئەۋەتتۇق، ھۇد ئېيتتى: «ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر، سىلەر(اﷲ تىن غەيرىيگە چوقۇنۇش بىلەن) پەقەت (اﷲ قا) يالغاننى توقۇغۇچىلارسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,51,' «ئى قەۋمىم!مەن تەبلىغ ئۈچۈن سىلەردىن ئەجىر سورىمايمەن، ماڭا ئەجىر بېرىشنى پەقەت مېنى ياراتقان اﷲ ئۈستىگە ئالغان، سىلەر چۈشەنمەمسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,52,' «ئى قەۋمىم! پەرۋەردىگارىڭلاردىن مەغپىرەت تىلەڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، سىلەرگە ئۇ كۆپ يېغىن ياغدۇرۇپ بېرىدۇ، سىلەرگە تېخىمۇ كۈچ ـ قۇۋۋەت بېرىدۇ، سىلەر گۇناھقا چۆمگەن ھالدا (مېنىڭ دەۋىتىمدىن) يۈز ئۆرۈمەڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,53,' ئۇلار ئېيتتى: «ئى ھۇد!سەن بىزگە (ئۆزۈڭنىڭ راستلىقىڭنى كۆرسىتىدىغان) ھېچقانداق روشەن دەلىل كەلتۈرمىدىڭ، سېنىڭ سۆزۈڭ بىلەن بىز ئىلاھلىرىمىزنى تاشلىۋەتمەيمىز ۋە سېنىڭ(پەيغەمبەرلىكىڭگە) ئىشەنمەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,54,' بىز پەقەت سېنى بەزى ئىلاھلىرىمىز ساراڭ قىلىپ قويۇپتۇ دەيمىز» ھۇد ئېيتتى: «مەن ھەقىقەتەن اﷲ نى گۇۋاھ قىلىمەن، سىلەرمۇ گۇۋاھ بولۇڭلاركى ، مەن سىلەرنىڭ اﷲ نى قويۇپ شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن ئادا ـ جۇدامەن، ھەممىڭلار ماڭا سۇيىقەست قىلىڭلار، ماڭا مۆھلەت بەرمەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,55,' شۈبھىسىزكى، مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار بولغان اﷲ قا مەن تەۋەككۈل قىلدىم، اﷲ نىڭ باشقۇرۇشىدا بولمىغان بىرمۇ مەخلۇق يوقتۇر؛ پەرۋەردىگارىم ھەقىقەتەن توغرا يولدىدۇر (يەنى ئادىل بولۇپ، ھېچ كىشىگە زۇلۇم قىلمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,56,' ئەگەر سىلەر (مېنىڭ دەۋىتىمنى قوبۇل قىلىشتىن) يۈز ئۆرۈسەڭلار(يۈز ئۆرۈگىنىڭلارنىڭ ماڭا زىيىنى يوق)، سىلەرگە مەن پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم (پەيغەمبەرنىڭ ۋەزىپىسى پەقەت تەبلىغ قىلىشتۇر)، پەرۋەردىگارىم (سىلەرنى ھالاك قىلىپ) ئورنۇڭلارغا باشقا بىر قەۋمنى كەلتۈرىدۇ، (شېرىك كەلتۈرۈش بىلەن) اﷲ قا قىلچە زىيان يەتكۈزەلمەيسىلەر، پەرۋەردىگارىم ھەقىقەتەن ھەربىر نەرسىنى كۈزىتىپ تۇرغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,57,' بىزنىڭ (ئازابلاش) بۇيرۇقىمىز كەلگەندە، ھۇدنى ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنى مەرھەمەت قىلىپ قۇتقۇزدۇق، ئۇلارنى قاتتىق ئازابتىن خالاس قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,58,' ئەنە شۇ ئاد قەۋمى پەرۋەردىگارىنىڭ مۆجىزىلىرىنى ئىنكار قىلدى، ئۇنىڭ پەيغەمبەرلىكىگە ئاسىيلىق قىلدى، ھەربىر ئۇچىغا چىققان تەرسا، شەپقەتسىز (يەنى ھەققە قارشىلىق قىلىپ جېدەللەشكۈچى) نىڭ بۇيرۇقىغا ئەگەشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,59,' ئۇلارغا بۇ دۇنيادا لەنەت قىلىندى، قىيامەت كۈنىمۇ ئۇلارغا لەنەت قىلىنىدۇ، بىلىڭلاركى، ئاد قەۋمى ھەقىقەتەن پەرۋەردىگارىنى ئىنكار قىلدى ئاگاھ بولۇڭلاركى ، ھۇد قەۋمى بولغان ئاد ھالاك بولسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,60,' سەمۇدقا ئۇلارنىڭ قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇ ئېيتتى: «ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ سىلەرنى زېمىندىن (يەنى تۇپراقتىن) ياراتتى، سىلەرنى زېمىندا تۇرغۇزدى، اﷲ تىن مەغپىرەت تىلەڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، پەرۋەردىگارىم ھەقىقەتەن يېقىندۇر، (دۇئانى) ئىجابەت قىلغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,61,' ئۇلار: «ئى سالىھ! بۇنىڭدىن بۇرۇن سەن ئارىمىزدا ئۈمىد قىلىنغان كىشى ئىدىڭ (يەنى بۇ سۆزنى ئېيتىشىڭدىن بۇرۇن ئارىمىزدا چوڭ ئادەم بولۇپ قېلىشىڭنى ئۈمىد قىلاتتۇق)، بىزنى ئاتا ـ بوۋىلىرىمىز ئىبادەت قىلىپ كەلگەن بۇتلارغا ئىبادەت قىلىشتىن توسۇمسەن؟ شەك شۈبھىسىزكى، ھەقىقەتەن بىز سەن دەۋەت قىلغان ئىشقا زور گۇماندىمىز» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,62,' سالىھ ئېيتتى: «ئى قەۋمىم!ئېيتىڭلارچۇ، ئەگەر مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان ئېنىق دەلىلگە ئاساسلانسام ۋە ئۇنىڭ رەھمىتى (يەنى پەيغەمبەرلىك) گە سازاۋەر بولسام، شۇنداق تۇرۇقلۇق اﷲ قا قارشىلىق قىلسام، اﷲ نىڭ ئازابىدىن مېنى كىم قۇتقۇزىدۇ؟ (مېنى ئۆزۈڭلارغا ئەگىشىشكە دەۋەت قىلىش بىلەن) ماڭا پەقەت زىياندىن باشقىسىنى زىيادە قىلالمايسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,63,' ئى قەۋمىم! بۇ اﷲ (بىۋاسىتە) ياراتقان چىشى تۆگە سىلەرگە مېنىڭ مۆجىزەمدۇر، ئۇنى اﷲ نىڭ زېمىنىغا قويۇۋېتىڭلار، ئىختىيارى ئوتلىسۇن، ئۇنىڭغا زىيان زەخمەت يەتكۈزمەڭلار، بولمىسا سىلەرنى پات كېلىدىغان ئازاب ھالاك قىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,64,' ئۇلار چىشى تۆگىنى بوغۇزلىدى، سالىھ ئېيتتى: «ئۆيۈڭلاردا(ھاياتلىقتىن) ئۈچ كۈن بەھرىمەن بولۇۋېلىڭلار بۇ ۋەدە يالغان ئەمەس»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,65,' بىزنىڭ (ئازاب قىلىش) ئەمرىمىز كەلگەندە، سالىھنى ۋە ئۇنىڭ بىلەن بىللە ئىمان ئېيتقانلارنى رەھمىتىمىز بىلەن شۇ كۈننىڭ شەرمەندىچىلىكىدىن قۇتقۇزدۇق، پەرۋەردىگارىڭ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,66,' زالىملارغا قاتتىق ئاۋاز يۈزلەندى ـ دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,67,' گويا ئۇلار ئۆيلىرىدە تۇرمىغاندەك (يەنى ئۇلارنىڭ تۇرغان جايلىرى ئادەمزات تەرىپىدىن ئاۋات قىلىنمىغاندەك) بولۇپ قالدى، بىلىڭلاركى، سەمۇد پەرۋەردىگارىنى ئىنكار قىلدى، سەمۇد ھالاك بولسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,68,' شۈبھىسىزكى، بىزنىڭ ئەلچىلىرىمىز (يەنى پەرىشتىلەر) ئىبراھىمغا خۇش خەۋەر ئېلىپ كەلدى ئۇلار: «ساڭا ئامانلىق تىلەيمىز» دېدى ئىبراھىم : «سىلەرگە ئامانلىق تىلەيمەن» دېدى ئۇ ئۇزاققا قالماي بىر موزاينى كاۋاپ قىلىپ ئېلىپ كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,69,' ئۇلارنىڭ (يەنى پەرىشتىلەرنىڭ) ئۇنىڭغا قول ئۇزاتمايۋاتقانلىقىنى كۆرۈپ، ئۇلارنى خوش كۆرمىدى، ئۇلاردىن قورقۇنچ ھېس قىلدى، ئۇلار: «قورقمىغىن (بىز رەببىڭنىڭ پەرىشتىلىرى، تاماق يېمەيمىز)، بىز ھەقىقەتەن لۇتنىڭ قەۋمىنى (ھالاك قىلىش ئۈچۈن) ئەۋەتىلدۇق» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,70,' ئۇنىڭ ئايالى (يەنى سارە) بىر تەرەپتە تۇرۇپ (لۇت قەۋمىنىڭ ھالاك بولىدىغانلىقىدىن خۇشاللىنىپ) كۈلدى، بىز ئۇنىڭغا (يەنى سارەگە) ئىسھاق (ئاتلىق بالىسى بولىدىغانلىقى) بىلەن ۋە ئىسھاقتىن كېيىن كېلىدىغان يەئقۇب (ئاتلىق نەۋرىسى بولىدىغانلىقى) بىلەن خۇش خەۋەر بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,71,' ئۇ (يەنى سارە): «ۋىيەي! مەن بىر موماي تۇرسام، ئېرىم بىر بوۋاي تۇرسا، تۇغامدىمەن؟ بۇ ھەقىقەتەن قىزىق ئىشقۇ!» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,72,' ئۇلار: «اﷲ نىڭ ئەمرىدىن (يەنى قۇدرىتىدىن) ئەجەبلىنەمسە؟ اﷲ نىڭ رەھمىتى ۋە بەرىكەتلىرى سىلەرنىڭ ئائىلەڭلارغا (يەنى ئىبراھىمنىڭ ئائىلىسىدىكىلەرگە) نازىل بولسۇن اﷲ ھەقىقەتەن مەدھىيىلەشكە لايىقتۇر، ئۇلۇغلاشقا لايىقتۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,73,' ئىبراھىمدىن قورقۇنچ يوقالغان ھەمدە خۇش خەۋەر ئۇنىڭغا يەتكەن چاغدا، ئۇ بىز بىلەن لۇتنىڭ قەۋمى توغرىسىدا مۇنازىرىلىشىشىگە كىرىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,74,' ئىبراھىم بولسا ھەقىقەتەن كۆڭلى كۆكسى كەڭ، كۆپ ئاھ چەككۈچى(يەنى دىلى يۇمشاق)، (ھەممە ئىشتا) اﷲ قا مۇراجىئەت قىلغۇچى زاتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,75,' (پەرىشتىلەر ئېيتتى) «ئى ئىبراھىم! بۇنى (يەنى لۇت قەۋمى توغرىسىدا مۇنازىرىلىشىشنى) قويغىن، شۈبھىسىزكى، پەرۋەردىگارىڭنىڭ (ئۇلارنى ھالاك قىلىش توغرىسىدىكى) ئەمرى ئاللىقاچان چۈشتى، ئۇلارغا چوقۇم (مۇنازىرىلىشىش ياكى شاپائەت قىلىش بىلەن) قايتۇرغىلى بولمايدىغان ئازاب چۈشىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,76,' بىزنىڭ ئەلچىلىرىمىز (يەنى پەرىشتىلەر) لۇتنىڭ يېنىغا كەلگەن چاغدا (لۇت ئۆز قەۋمىنىڭ ئۇلارغا چېقىلىپ قويۇشىدىن قورقۇپ) قىيىن ئەھۋالدا قالدى، ئۇلار توغرىسىدا يۈرىكى سىقىلدى ۋە: «بۇ بىر دىشۋار كۈندۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,77,' لۇتنىڭ قەۋمى ئۇنىڭ قېشىغا ئالدىراپ كېلىشتى، ئۇلار بۇرۇنلا يامان ئىشلارنى قىلاتتى (يەنى لىۋاتەت قىلاتتى) لۇت ئېيتتى: «ئى قەۋمىم! مېنىڭ بۇ قىزلىرىم  سىلەرگە ئەڭ پاكتۇر، اﷲ تىن قورقۇڭلار، مېھمانلىرىم ئۈستىدە مېنى رەسۋا قىلماڭلار، ئاراڭلاردا قەبىھ ئىشتىن توسىدىغان بىرەر كاللىسى جايىدا ئادەم يوقمۇ؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,78,' ئۇلار (لۇتقا) ئېيتتى: «قىزلىرىڭغا ھاجىتىمىز يوق ئىكەنلىكىنى سەن ئوبدان بىلىسەن، شۇنىڭدەك بىزنىڭ مەقسىتىمىزنىمۇ ئوبدان بىلىسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,79,' لۇت ئېيتتى: «كاشكى مېنىڭ (سىلەرگە قارشى تۇرىدىغان) كۈچ قۇۋۋىتىم بولسا ئىدى، ياكى ئىلتىجا قىلىدىغان كۈچلۈك جەمەتىم بولسا ئىدى (چوقۇم سىلەرگە زەربە بەرگەن بولاتتىم)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,80,' ئۇلار (يەنى پەرىشتىلەر): «ئى لۇت! شۈبھىسىزكى، بىز پەرۋەردىگارىڭنىڭ ئەلچىلىرىدۇرمىز، ئۇلار ھەرگىزمۇ ساڭا زىيان يەتكۈزەلمەيدۇ، ئائىلەڭدىكىلەرنى ئېلىپ كېچىنىڭ ئاخىرىدا (ئۇلارنىڭ ئىچىدىن) چىقىپ كەتكىن، ئايالىڭدىن باشقا، سىلەردىن ھېچ كىشى ئارقىسىغا قارىمىسۇن، ئۇ (يەنى ئايالىڭ) ئۇلار بىلەن بىرگە ھالاك بولغۇچىدۇر، شۈبھىسىزكى، ئۇلارغا ئازاب چۈشىدىغان چاغ تاڭ ۋاقتىدۇر، تاڭ يېقىن ئەمەسمۇ؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,81,' بىزنىڭ (جازالاش) ئەمرىمىز چۈشكەندە، ئۇلارنىڭ يۇرتلىرىنى ئاستىن ـ ئۈستۈن قىلىۋەتتۇق (يەنى كۆمتۈرۈۋەتتۇق)، ئۇ يۇرتلارغا بىز ئۈستى ـ ئۈستىلەپ ساپال تاشلارنى ياغدۇردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,82,' ئۇ ساپال تاشلارغا پەرۋەردىگارىڭنىڭ دەرگاھىدا بەلگە سېلىنغان ئىدى، ئۇ يۇرتلار زالىملار(يەنى قۇرەيش كاپىرلىرى) دىن يىراق ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,83,' مەديەن (ئەھلى) ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇ ئېيتتى: «ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۆلچەمدە ۋە تارازىدا كەم بەرمەڭلار، مەن سىلەرنى ھەقىقەتەن باي ھېسابلايمەن، مەن ھەقىقەتەن سىلەرنىڭ ھەممىنى قورشاپ تۇرغۇچى كۈن(يەنى قىيامەت كۈنى) نىڭ ئازابىغا دۇچار بولۇشۇڭلاردىن قورقىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,84,' ئى قەۋمىم! سىلەر ئۆلچەمنى توغرا ئۆلچەڭلار، تارازىنى توغرا تارتىڭلار، كىشىلەرگە نەرسىلەرنى كەم بەرمەڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلىپ پىتنە ـ پاسات تېرىماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,85,' اﷲ سىلەرگە قالدۇرغان ھالال رىزىق (سىلەر يىغقان ھارامدىن) ياخشىدۇر، ئەگەر سىلەر مۆمىن بولساڭلار مەن سىلەرگە مۇھاپىزەتچى ئەمەسمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,86,' ئۇلار: «ئى شۇئەيب! سېنىڭ نامىزىڭ (يەنى دىنىڭ) سېنى ئاتا ـ بوۋىلىرىمىز ئىبادەت قىلىپ كېلىۋاتقان نەرسىلەرنى (يەنى بۇتلارنى) بىزنى تاشلاتقۇزۇشقا ياكى ماللىرىمىزنى خالىغىنىڭچە تەسەررۇپ قىلىشىمىزنى تەرك ئەتكۈزۈشكە بۇيرۇمدۇ؟ سەن تازىمۇ كۆڭلى ـ كۆكسى كەڭ، تازىمۇ توغرا يول تاپقان ئادەمسەن ـ دە» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,87,' شۇئەيب ئېيتتى: «ئى قەۋمىم! ئېيتىپ بېقىڭلارچۇ، پەرۋەردىگارىم ماڭا روشەن پاكىت (يەنى پەيغەمبەرلىك) ۋە كەڭتاشا ھالال رىزىق ئاتا قىلغان تۇرسا، (مەن شۇنداق نېمەتلەرنى ئاتا قىلغان پەرۋەردىگارىمنىڭ ئەمرىگە خىلاپلىق قىلامدىم؟) سىلەرگە قىلىش مەنئى قىلىنغان نەرسىنى ئۆزۈممۇ قىلىشنى خالىمايمەن، مەن پەقەت (سىلەرنى) قولۇمدىن كېلىشىچە تۈزەشنى خالايمەن، مەن پەقەت اﷲ نىڭ ياردىمى بولغاندىلا مۇۋەپپەقىيەت قازىنالايمەن، (ھەممە ئىشتا) اﷲ قا تايىنىمەن ۋە ئۇنىڭغا يۈزلىنىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,88,' ئى قەۋمىم! سىلەرنىڭ ماڭا قارشى تۇرغانلىقىڭلار سىلەرنى نۇھنىڭ قەۋمى ياكى ھۇدنىڭ قەۋمى ۋە ياكى سالىھنىڭ قەۋمى دۇچار بولغان ئازابقا دۇچار قىلمىسۇن لۇت قەۋمى (نىڭ دىيارى) سىلەر (نىڭ جايىڭلار) دىن يىراق ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,89,' پەرۋەردىگارىڭلاردىن مەغپىرەت تەلەپ قىلىڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، مېنىڭ پەرۋەردىگارىم (تەۋبە قىلغۇچىلارغا) ھەقىقەتەن مەرھەمەتلىكتۇر، (ئۇلارنى) دوست تۇتقۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,90,' ئۇلار: «ئى شۇئەيب !بىز سېنىڭ ئېيتقانلىرىڭنىڭ نۇرغۇنىنى چۈشەنمەيمىز بىز ئەلۋەتتە سېنى ئارىمىزدا كۈچسىز ئادەم دەپ قارايمىز، سېنىڭ قەۋم ـ قېرىنداشلىرىڭ بولمىغاندا ئىدى، بىز سېنى چوقۇم تاش ـ كېسەك قىلىپ ئۆلتۈرەتتۇق، سەن بىزگە ئەتىۋارلىق ئەمەسسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,91,' شۇئەيب ئېيتتى: «ئى قەۋمىم! مېنىڭ قەۋم قېرىنداشلىرىم سىلەرگە اﷲ تىنمۇ ئەتىۋارلىقمۇ؟ سىلەر اﷲ نى ئارقاڭلارغا چۆرىۋەتتىڭلار (يەنى اﷲ قا ئىتائەت قىلمىدىڭلار ۋە اﷲ نى ئۇلۇغلىمىدىڭلار)، پەرۋەردىگارىم سىلەرنىڭ قىلمىشلىرىڭلاردىن ئەلۋەتتە خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,92,' ئى قەۋمىم! سىلەر ئۆز يولۇڭلار بويىچە ھەرىكەت قىلىڭلار، مەنمۇ ئەلۋەتتە ئۆز يولۇم بويىچە ھەرىكەت قىلاي! ئۇزاققا قالماي كىمنىڭ رەسۋا قىلغۇچى ئازابقا دۇچار بولىدىغانلىقى ۋە كىمنىڭ يالغانچى ئىكەنلىكىنى بىلىسىلەر، (ئىشىڭلارنىڭ ئاقىۋىتىنى) كۈتۈڭلار! شۈبھىسىزكى مەنمۇ سىلەر بىلەن بىللە كۈتىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,93,' بىزنىڭ (ئۇلارنى ھالاك قىلىش) پەرمانىمىز چۈشكەن چاغدا، شۇئەيبنى ۋە ئۇنىڭ بىلەن بىللە ئىمان ئېيتقانلارنى مەرھەمەت قىلىپ قۇتۇلدۇردۇق، (ئۆزلىرىگە) زۇلۇم قىلغانلارغا قاتتىق ئاۋاز (دىن پەيدا بولغان زىلزىلە) يۈزلەندى، ئۇلار ئۆز ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,94,' گويا ئۇلار ئۆيلىرىدە تۇرمىغاندەك (يەنى ياشىمىغاندەك) بولۇپ قالدى مەديەن ئاھالىسى خۇددى سەمۇد قەۋمى ھالاك بولغاندەك ھالاك بولسۇن!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,95,' بىز مۇسانى ھەقىقەتەن ئايەتلىرىمىز (يەنى اﷲ تىن نازىل بولغان دىنىي ئەھكاملار) ۋە روشەن دەلىللەر (يەنى مۆجىزىلەر) بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار پىرئەۋننىڭ بۇيرۇقىغا بويسۇندى، پىرئەۋننىڭ بۇيرۇقى توغرا ئەمەس ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,96,' پىرئەۋن قىيامەت كۈنى باشلامچىلىق بىلەن ئۆز قەۋمىنى دوزاخقا باشلاپ كىرىدۇ، ئۇ جاي نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,97,' ئۇلار بۇ دۇنيادىمۇ، قىيامەت كۈنىدىمۇ لەنەتكە ئۇچرايدۇ، ئۇلارغا بېرىلگەن (ئىككىلا دۇنيادا لەنەتكە ئۇچراشتىن ئىبارەت) بۇ سوغا نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,98,' مانا بۇلار (ئاھالىسىنى بىز ھالاك قىلغان) يۇرتلارنىڭ خەۋەرلىرىدۇر، ئۇنى ساڭا (ۋەھىيى ئارقىلىق) بايان قىلىمىز ئۇ يۇرتلاردىن ئىزى قالغانلىرىمۇ بار (يەنى ئاھالىسى ھالاك قىلىنىپ، يۇرتلىرىنىڭ ئورنى قالدۇرۇلغانلىرىمۇ بار)، ئىزى قالمىغانلىرىمۇ بار (يەنى ئاھالىسى بىلەن قوشۇپ ئۆرۈۋېتىلگەن زىرائەتتەك ۋەيران قىلىنغانلىرىمۇ بار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,99,' بىز ئۇلارغا زۇلۇم قىلمىدۇق، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى پەرۋەردىگارىڭنىڭ (ئۇلارنى ھالاك قىلىش) پەرمانى كەلگەندە، ئۇلارنىڭ اﷲ نى قويۇپ ئىبادەت قىلغان مەبۇدلىرى ھېچ نەرسىگە ئەسقاتمىدى، ئۇلارغا پەقەت زىيان سالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,100,' پەرۋەردىگارىڭ زالىم يۇرتلار (ئاھالىسى) نى ھالاك قىلسا، ئەنە شۇنداق ھالاك قىلىدۇ، اﷲ نىڭ ئازابى ھەقىقەتەن تولىمۇ قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,101,' ئاخىرەت ئازابىدىن قورقىدىغان ئادەم بۇ (قىسسە) دىن، ئەلۋەتتە ئىبرەت ئالىدۇ، ئەنە شۇ كۈن پۈتۈن خالايىق(ھېساب بېرىش ئۈچۈن) يىغىلىدىغان كۈندۇر، ئەنە شۇ كۈن ھەممە ھازىر بولىدىغان كۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,102,' ئۇنى (يەنى قىيامەت كۈنىنى) بىز مۇئەييەن مۇددەتكىچە تەخىر قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,103,' قىيامەت كۈنى بولغاندا، ھەرقانداق ئادەم اﷲ تىن ئىجازەت بولغاندىلا سۆزلەيدۇ، ئۇلارنىڭ بەزىلىرى بەتبەخت بولىدۇ، بەزىلىرى سائادەتمەن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,104,' بەتبەختلەرگە كەلسەك، ئۇلار دوزاخقا كىرىدۇ، ئۇلار دوزاختا (ئېشەك ھاڭرىغاندەك) توۋلاپ نالە ـ پەرياد چېكىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,105,' (دوزاخنىڭ) ئاسمان ـ زېمىننى يوقالمايلا تۇرسا، ئۇلار دوزاختا پەرۋەردىگارىڭ خالىغان زامانغىچە داۋاملىق تۇرىۋېرىدۇ، پەرۋەردىگارىڭ ئەلۋەتتە خالىغىنىنى قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,106,' سائادەتمەنلەرگە كەلسەك، ئۇلار جەننەتكە كىرىدۇ، (جەننەتنىڭ) ئاسمان ـ زېمىنى يوقالمايلا تۇرسا، ئۇلار جەننەتتە پەرۋەردىگارىڭ خالىغان زامانغىچە داۋاملىق تۇرىۋېرىدۇ (بۇ ئۇلارغا قىلىنغان) ئۈزۈلۈپ قالمايدىغان ئىنئامدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,107,' ئۇلار (يەنى مۇشرىكلار) نىڭ بۇتلارغا چوقۇنۇىشىنىڭ (گۇمراھلىق) ئىكەنلىكىدىن شەكلەنمە ، ئۇلار پەقەت ئۆزلىرىنىڭ بىز ساڭا قىسسىسىنى بايان قىلغان ئاتا ـ بوۋىلىرىغا ئوخشاشلا چوقۇنىدۇ، بىز ئۇلارنىڭ (ئازابتىن) تېگىشلىك نېسىۋىسىنى چوقۇم كېمەيتمەي بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,108,' شۈبھىسىزكى، بىز مۇساغا (يەنى تەۋراتنى) بەردۇق، ئۇنىڭ توغرىسىدا ئىختىلاپ قىلىندى(يەنى ئۇنىڭ قەۋمى بۇ كىتاب ئۈستىدە ئىختىلاپ قىلىشتى) پەرۋەردىگارىڭنىڭ ئالدىن چىقارغان(ئۇلارنى جازالاشنى قىيامەتكە قالدۇرۇش ھەققىدىكى) ھۆكمى بولمىسا ئىدى، ئۇلارنىڭ ئارىسىدا (مۇشۇ دۇنيادىلا ياخشىغا ياخشىچە، يامانغا يامانچە) چوقۇم ھۆكۈم چىقىرىلاتتى ھەقىقەتەن ئۇلار ئۇنىڭدىن(يەنى قۇرئاندىن) زور گۇماندىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,109,' پەرۋەردىگارىڭ ھەر بىر ئادەمگە ئۇنىڭ قىلغان ئەمەللىرىڭنىڭ مۇكاپاتىنى تولۇق بېرىدۇ، اﷲ ، ئەلۋەتتە، ئۇلارنىڭ ئەمەللىرىدىن تولۇق خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,110,' (ئى مۇھەممەد!) اﷲ سېنى بۇيرۇغاندەك توغرا يولدا بولغىن، ساڭا ئىمان ئېيتقانلارمۇ توغرا يولدا بولسۇن (مەنئى قىلىنغان ئىشلارنى قىلىپ) اﷲ نىڭ چەكلىرىدىن چىقىپ كەتمەڭلار، شۈبھىسىزكى، اﷲ قىلمىشىڭلارنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,111,' زالىملارغا مايىل بولماڭلار، (مايىل بولساڭلار) دوزاخ ئازابىغا قالىسىلەر، سىلەرگە اﷲ تىن باشقا (دوزاختىن قۇتۇلدۇرىدىغان) ھېچ ياردەمچى يوقتۇر، ئاندىن كېيىن ھېچ ياردەمگە ئېرىشەلمەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,112,' كۈندۈزنىڭ ئىككى تەرىپىدە (يەنى ئەتىگەندە ۋە كەچقۇرۇن ۋاقىتلىرىدا) ۋە كېچىنىڭ دەسلەپكى ۋاقىتلىرىدا ناماز ئۆتىگىن  شۈبھىسىزكى، ياخشى ئىشلار ئارقىلىق يامان ئىشلار يۇيۇلىدۇ بۇ چۈشەنگۈچىلەر ئۈچۈن ۋەز ـ نەسىھەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,113,' (ئى مۇھەممەد! مۇشرىكلاردىن يەتكەن ئەزىيەتلەرگە) سەۋر قىلغىن، اﷲ ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,114,' سىلەردىن بۇرۇن ئۆتكەن ئۈممەتلەرنىڭ ئارىسىدا نېمىشقا (يامانلارنى) يەر يۈزىدە بۇزۇقچىلىق قىلىشتىن توسىدىغان ئەقىل ئىلگىرى بولمىدى؟ ئۇلار ئارىسىدىن بۇزۇقچىلىقنى توسۇپ بىزنىڭ نىجاتلىقىمىزغا ئېرىشكەن ئازغىنا كىشىلەر بۇنىڭدىن مۇستەسنا ئۆزلىرىگە زۇلۇم قىلغۇچىلار ئەيش ـ ئىشرەتلىك تۇرمۇشنى قوغلىشىدۇ ئۇلار گۇناھكار ئادەملەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,115,' پەرۋەردىگارىڭ ئاھالىسى ياخشى بولغان يۇرتلارنى زۇلۇم سېلىپ ھالاك قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,116,' ئەگەر پەرۋەردىگارىڭ خالىسا ئىدى، پۈتۈن ئادەملەرنى ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، ئۇلار(دىن توغرىسىدا) داۋاملىق ئىختىلاپ قىلىشقۇچىلاردۇر، پەقەت پەرۋەردىگارىڭنىڭ رەھمىتىگە ئېرىشكەنلەر بۇنىڭدىن مۇستەسنا اﷲ ئۇلارنى شۇنىڭ ئۈچۈن (يەنى ئىختىلاپ ئۈچۈن) ياراتتى پەرۋەردىگارىڭنىڭ: «مەن چوقۇم پۈتۈن جىنلار ۋە ئىنسانلار بىلەن دوزاخنى توشقۇزىمەن» دېگەن سۆزى ئېنىق بەلگىلەندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,117,' كۆڭلۈڭنى خاتىرجەم قىلىش ئۈچۈن، ساڭا پەيغەمبەرلەرنىڭ قىسسىلىرىنى بايان قىلىپ بېرىمىز، بۇ قىسسىلەر ھەقىقەتنى، مۆمىنلەر ئۈچۈن ۋەز ـ نەسىھەت، ئىبرەتلەرنى ئۆز ئىچىگە ئالغان');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,118,' ئىمان ئېيتمايدىغانلارغا ئېيتقىنكى، «سىلەر ئۆز يولۇڭلار بويىچە ھەرىكەت قىلىڭلار، بىزمۇ ئۆز يولىمىز بويىچە ھەرىكەت قىلايلى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,119,' سىلەر (ئىشىڭلارنىڭ ئاقىۋىتىنى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە (شۇنى) كۈتىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,11,120,' ئاسمانلاردىكى ۋە زېمىندىكى غەيبنى بىلىش پەقەت اﷲ قا خاستۇر، ھەممە ئىش اﷲ قا قايتۇرۇلىدۇ، اﷲ قا ئىبادەت قىلغىن ۋە اﷲ قا تەۋەككۈل قىلغىن (يەنى ھەممە ئىشىڭنى اﷲ قا تاپشۇرغىن) پەرۋەردىگارىڭ قىلىۋاتقان ئىشلىرىڭلاردىن غاپىل ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,1,'ئەلىف، لام، را بۇ روشەن كىتاب ئايەتلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,2,' شۈبھىسىزكى، سىلەرنى چۈشەنسۇن دەپ، ئۇنى ئەرەبچە قۇرئان قىلىپ نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,3,' بۇ قۇرئاننى ساڭا ۋەھيى قىلىش ئارقىلىق ئەڭ چىرايلىق قىسسىنى ساڭا بايان قىلىپ بېرىمىز، بۇنىڭدىن بۇرۇن بۇ قىسسىدىن ھەقىقەتەن بىخەۋەر ئىدىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,4,' ئۆز ۋاقتىدا يۇسۇف ئۆز ئاتىسىغا: «ئى ئاتا!مەن ھەقىقەتەن چۈشۈمدە  يۇلتۇزنىڭ ۋە ئاي بىلەن كۈننىڭ ماڭا سەجدە قىلغانلىقىنى كۆردۈم» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,5,' ئاتىسى (يەنى يەئقۇب ئەلەيھىسسالام): «ئى ئوغلۇم! چۈشۈڭنى قېرىنداشلىرىڭغا ئېيتمىغىن، ساڭا سۇيىقەست قىلىپ قالمىسۇن، شەيتان ئىنسانغا ھەقىقەتەن ئاشكارا دۈشمەندۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,6,' شۇنىڭغا (يەنى ساڭا ئۇلۇغ چۈش كۆرسەتكىنىگە) ئوخشاش، پەرۋەردىگارىڭ سېنى (پەيغەمبەرلىككە) تاللايدۇ، ساڭا چۈش تەبىرىنى بىلدۈرىدۇ، ئەجدادلىرىڭ ئىبراھىم، ئىسھاقلارغا ئىلگىرى اﷲ ئۆزىنىڭ ئۇلۇغ نېمىتىنى بەرگىنىدەك، ساڭىمۇ، يەئقۇب ئەۋلادىغىمۇ ئۆزىنىڭ ئۇلۇغ نېمىتىنى بېرىدۇ شۈبھىسىزكى، پەرۋەردىگارىڭ (ئۆز مەرھەمىتىگە كىمنىڭ لايىق ئىكەنلىكىنى) ئوبدان بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,7,' يۇسۇفنىڭ ۋە قېرىنداشلىرىنىڭ(قىسسىسىدە) سورىغۇچىلار ئۈچۈن اﷲ نىڭ قۇدرىتىنى كۆرسىتىپ بېرىدىغان) ھەقىقەتەن نۇرغۇن ئالامەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,8,' ئۆز ۋاقتىدا ئۇلار: «يۇسۇف ۋە ئۇنىڭ قېرىندىشى (يەنى بۇنيامىن) ئاتىمىزغا بىزدىنمۇ سۆيۈملۈكتۇر، ھالبۇكى، بىز(كۈچلۈك) جامائەدۇرمىز، ئاتىمىز(نىڭ ئۇ ئىككىسىنى بىزدىن ئارتۇق كۆرۈشى) ئەلۋەتتە روشەن خاتادۇر» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,9,' (ئۇلار) «يۇسۇفنى ئۆلتۈرۋېتىڭلار، ياكى ئۇنى يىراق بىر چەت جايغا تاشلىۋېتىڭلار، (شۇ چاغدا) ئاتاڭلارنىڭ مۇھەببىتى سىلەرگە قالىدۇ، بۇ(گۇناھ) تىن كېيىن(تەۋبە قىلىپ) ياخشى ئادەم بولۇپ كېتىسىلەر» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,10,' ئۇلاردىن بىرى سۆز قىلىپ: «يۇسۇفنى ئۆلتۈرمەڭلار، (ئەگەر ئۇنى ئاتىسىدىن يىراق قىلماقچى بولساڭلار، ئۇنى قۇدۇققا تاشلىۋېتىڭلار، ئۇنى يولدىن ئۆتكەن كارۋان ئېلىۋالسۇن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,11,' ئۇلار ئېيتتى: «ئى ئاتىمىز! نېمىشقا يۇسۇف تورىسىدا بىزگە ئىشەنمەيسەن؟ ھالبۇكى، بىز ئۇنىڭغا ھەقىقەتەن ياخشى نىيەتتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,12,' ئەتە ئۇنى بىزگە قوشۇپ قويغىن، يەپ ـ ئىچىپ، ئويناپ ـ كۈلۈپ كىرسۇن، ئۇنى بىز چوقۇم ياخشى مۇھاپىزەت قىلىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,13,' يەئقۇب: «ئۇنى (يەنى يۇسۇفنى) ئېلىپ كەتسەڭلار راستلا خاتىرجەم بولالمايمەن، سىلەر غەپلەتتە قېلىپ، ئۇنى بۆرە يەپ قويۇشتىن ئەنسىرەيمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,14,' ئۇلار: «بىز(كۈچلۈك) بىر جامائە تۇرۇقلۇق، ئۇنى بۆرىگە يېگۈزۈپ قويساق، ئۇ چاغدا بىز ئەلۋەتتە ـ زىيان تارتقۇچىلاردىن بولىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,15,' ئۇلار يۇسۇفنى ئېلىپ چىقىپ كەتكەن ۋە ئۇنى قۇدۇققا تاشلاشنى بىردەك قارار قىلىشقان چاغدا، بىز يۇسۇفكە: «كەلگۈسىدە ئۇلارغا بۇ ئىشنى (سېنىڭ يۇسۇف ئىكەنلىكىڭنى) ئۇلار ئۇقمايدىغان پەيتتە ئېيتقىن» دەپ ۋەھيى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,16,' ئۇلار كەچتە ئاتىسىنىڭ ئالدىغا يىغلاپ كېلىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,17,' ئۇلار: «ئى ئاتىمىز! يۇسۇفنى نەرسىلىرىمىزنىڭ يېنىدا قويۇپ يۈگۈرۈشكىلى كېتىپ قېلىپتۇق، ئۇنى بۆرە يەپ كېتىپتۇ، راست گەپ قىلساقمۇ سەن بىزگە ئىشەنمەيسەن» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,18,' ئۇلار يۇسۇفنىڭ كۆڭلىكىنى يالغاندىن قانغا بوياپ ئېلىپ كېلىشكەن ئىدى يەئقۇب: «بۇ ئىشنى نەپسىڭلار سىلەرگە چىرايلىق كۆرسىتىپ قىلغۇزۇپتۇ مەن پەقەت چىرايلىقچە سەۋرە قىلىمەن، سىلەرنىڭ (مۇشۇ يالغان) سۆزۈڭلارنى كۆتۈرۈشۈمگە اﷲ مەدەتكاردۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,19,' بۇ يەردىن بىر كارۋان ئۆتتى، ئۇلار سۇچىسىنى (سۇ ئېلىپ كېلىشكە) ئەۋەتتى، ئۇ سوغىسىنى قۇدۇققا تاشلىدى ۋە: «خۇش خەۋەر! مانا بىر ئوغۇل بالا» دېدى ئۇلار ئۇنى تىجارەت مېلى سۈپتىدە مەخپىي تۇتۇشتى، ئۇلارنىڭ قىلمىشلىرىنى اﷲ بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,20,' ئۇلار يۇسۇفنى ساناقلىق بىر قانچە تەڭگىگە ئەرزان باھادا سېتىۋەتتى، چۈنكى ئۇلار يۇسۇفكە قىزىقمىغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,21,' ئۇنى سېتىۋالغان مىسىرلىق ئادەم ئايالىغا: «ئۇنى ياخشى كۈتكىن، بىزگە ئەسقاتار ياكى بالا قىلىۋالارمىز» دېدى شۇنىڭدەك (يەنى يۇسۇفنى قۇدۇقتىن قۇتقۇزغىنىمىزدەك) يۇسۇفنى (مىسىر) زېمىنىغا يەرلەشتۈردۈق ، ئۇنىڭغا چۈش تەبىرىنى ئۈگەتتۇق، اﷲ (خالىغان) ئىشىنى ئاشۇرۇشقا قادىردۇر، لېكىن ئىنسانلارنىڭ تولىسى (بۇنى) ئۇقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,22,' يۇسۇف قىرانىغا يەتكەندە، ئۇنىڭغا ھېكمەت بىلەن ئىلىم ئاتا قىلدۇق، ياخشى ئىش قىلغۇچىلارنى شۇنداق مۇكاپاتلايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,23,' يۇسۇف تۇرۇۋاتقان ئۆيدىكى ئايال (يەنى زۈلەيخا) يۇسۇفتىن ئۆزى بىلەن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلدى، ئۇ ئىشىكلەرنى تاقىۋېتىپ: «بېرى كەل» دېدى يۇسۇف: «خۇدا پاناھ بەرسۇن! ئۇ(يەنى ئېرىڭ) ھەقىقەتەن مېنىڭ خوجايىنىمدۇر، ئۇ مېنى ياخشى كۈتتى ئۆزىگە زۇلۇم قىلغۇچىلار ھەقىقەتەن نىجات تاپالمايدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,24,' ئۇ (يەنى زۈلەيخا) يۇسۇفكە (يېقىنچىلىق قىلىشقا) بەل باغلىدى، پەرۋەردىگارىنىڭ روشەن دەلىلىنى كۆرمىگەن بولسا، يۇسۇفمۇ ئۇنىڭغا مايىل بولغان بولاتتى بىز يۇسۇفنى گۇناھتىن ۋە سەت ئىشتىن ئەنە شۇنداق ساقلىدۇق، شۈبھىسىزكى، يۇسۇف بىزنىڭ سادىق بەندىلىرىمىزدىن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,25,' ئۇلارنىڭ ھەر ئىككىسى (يۇسۇف چىقىپ كېتىش ئۈچۈن، زۈلەيخا ئۇنى قايتۇرۇپ كېلىش ئۈچۈن) ئىشىككە قاراپ يۈگۈرۈشتى (بۇ چاغدا) ئۇ (يەنى زۈلەيخا) يۇسۇفنىڭ كۆڭلىكىنى ئارقىسىدىن (تارتىپ) يىرتىۋەتتى، ئىشىك ئالدىدا ئۇنىڭ (يەنى زۈلەيخانىڭ) ئېرى بىلەن دوقۇرۇشۇپ قېلىشتى ئۇ (يەنى زۈلەيخا): «سېنىڭ ئەھلىيەڭنى دەپسەندە قىلماقچى بولغان ئادەمنى پەقەت زىندانغا تاشلاش ياكى قاتتىق قىيناش بىلەن جازالاش كېرەك» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,26,' يۇسۇف: «ئۇ (يەنى زۈلەيخا) مەندىن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلدى» دېدى ئۇ (يەنى زۈلەيخا) نىڭ ئائىلىسىدىكىلەردىن بىرسى گۇۋاھلىق بېرىپ: «يۇسۇفنىڭ كۆڭلىكى ئالدىدىن يىرتىلغان بولسا، ئۇنىڭ (يەنى زۈلەيخا) نىڭ سۆزى راست، يۇسۇفنىڭ سۆزى يالغان');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,27,' يۇسۇفنىڭ كۆڭلىكى ئارقىسىدىن يىرتىلغان بولسا، ئۇ(يەنى زۈلەيخانىڭ) سۆزى يالغان يۇسۇفنىڭ سۆزى راست» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,28,' ئۇ (يەنى زۈلەيخانىڭ ئېرى) يۇسۇفنىڭ كۆڭلىكىنىڭ ئارقىسىدىن يىرتىلغانلىقىنى كۆرۈپ: «ئەي ئاياللار جامائەسى!بۇ چوقۇم سىلەرنىڭ ھىيلەڭلاردۇر، سىلەرنىڭ ھىيلەڭلار ھەقىقەتەن چوڭدۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,29,' (يەنى ئېيتتى) «يۇسۇف! بۇنى سۆزلەپ يۈرمىگىن (ئايالىم) سەن ئۆز گۇناھىڭغا كەچۈرۈم سورىغىن، سەن ھەقىقەتەن خاتالاشتىڭ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,30,' شەھەردىكى بەزى ئاياللار: «ئەزىز (يەنى مىسىرنىڭ پادىشاھسى) نىڭ خوتۇنى قۇلىغا كۆيۈپ قېلىپ، ئۇنىڭ بىلەن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلىپتۇ، بىز ئۇنى، ئەلۋەتتە، ئاشكارا ئازغان دەپ قارايمىز» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,31,' ئۇ (يەنى زۈلەيخا) ئۇلارنىڭ گەپ ـ سۆزلىرىنى ئاڭلاپ، ئۇلارغا (مېھماندارچىلىققا چاقىرىپ) ئادەم ئەۋەتتى ئۇلارغا (يۆلىنىدىغان) تەكىيە ياستۇقلارنى تەييارلىدى، ئۇلارنىڭ ھەربىرىگە بىردىن پىچاق بەردى (زۈلەيخا يۇسۇفكە) «سەن چىقىپ ئۇلار بىلەن كۆرۈشكىن دېدى، ئۇلار يۇسۇفنى كۆرۈپ ھاڭ تاڭ بولۇپ قېلىشتى ۋە ئۆزلىرىنىڭ قوللىرىنى كېسىۋېلىشتى ئۇلار: «(بۇنداق چىرايلىق ئىنساننى ياراتقان) اﷲ پاكتۇر، بۇ ئادەمزات ئەمەس، پەقەت بىر ئېسىل پەرىشتىدۇر» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,32,' ئۇ (يەنى زۈلەيخا): «مانا بۇ سىلەر ئۇنىڭ ئۈچۈن مېنى ئەيىبلىگەن ھېلىقى ئادەم، مەن ئۇنىڭدىن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلدىم، لېكىن ئۇ ئۆزىنى چىڭ ساقلىدى ئەگەر ئۇ يەنىلا بۇيرۇقۇمنى قىلمىسا، چوقۇم زىندانغا تاشلىنىدۇ، چوقۇم خار ئادەم بولۇپ قالىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,33,' يۇسۇف: «ئى پەرۋەردىگارىم! ماڭا ئۇلار ئۈندىگەن نەرسىدىن كۆرە زىندان سۆيۈملۈكتۇر، ئەگەر ئۇلارنىڭ ھىيلىسىنى مەندىن دەپئى قىلمىساڭ، (ئىنسانچىلىقتا) ئۇلارغا مايىل بولۇپ قالىمەن، نادانلاردىن بولۇپ قالىمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,34,' پەرۋەردىگارى ئۇنىڭ دۇئاسىنى ئىجابەت قىلىپ ئۇلارنىڭ ھىيلىسىنى يۇسۇفتىن دەپئى قىلدى اﷲ (ئىلتىجا قىلغۇچىلارنىڭ دۇئاسىنى) ھەقىقەتەن ئاڭلاپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,35,' ئاندىن ئۇلار (يۇسۇفنىڭ بىگۇناھ ئىكەنلىكىگە دائىر) پاكىتلارنى كۆرگەندىن كېيىن، ئۇنى بىرمەھەل قاماپ قويۇشنى لايىق تاپتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,36,' زىندانغا يۇسۇف بىلەن ئىككى يىگىت بىللە كىرگەن ئىدى ئۇلارنىڭ بىرى: «مەن چۈشۈمدە ئۆزۈمنى ھەقىقەتەن (ئۈزۈمدىن) ھاراق سىقىۋاتقان كۆرۈپتىمەن» دېدى ئىككىنچىسى: «مەن چۈشۈمدە ئۆزۈمنى ھەقىقەتەن بېشىمدا نان كۆتۈرۈپ تۇرغان، ناننى قۇشلار يەۋاتقان كۆرۈپتىمەن، تەبىرىنى بىزگە ئېيتىپ بەرسەڭ، بىز سېنى ھەقىقەتەن ياخشىلىق قىلغۇچىلاردىن (يەنى چۈشكە تەبىر بېرەلەيدىغانلاردىن) دەپ قارايمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,37,' يۇسۇف ئېيتتى: «ئىككىڭلارغا سىرتتىن تاماق كىرىشتىن بۇرۇن، ئۇنىڭ نېمە ئىكەنلىكىنى سىلەرگە ئېيتىپ بېرەلەيمەن، بۇ پەرۋەردىگارىم ماڭا بىلدۈرگەن نەرسىلەردىندۇر شۈبھىسىزكى، مەن اﷲ قا ئىشەنمەيدىغان، ئاخىرەتنى ئىنكار قىلىدىغان قەۋمنىڭ دىنىنى تاشلىدىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,38,' مەن ئەجدادلىرىم ـ ئىبراھىم، ئىسھاق، يەئقۇبلارنىڭ دىنىغا ئەگەشتىم، ھېچقانداق نەرسىنى اﷲ قا شېرىك كەلتۈرۈش بىزگە لايىق ئەمەس، بۇ اﷲ نىڭ بىزگە ۋە ئىنسانلارغا قىلغان مەرھەمىتىدۇر، لېكىن ئىنسانلارنىڭ تولىسى شۈكۈر قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,39,' تۈرمىداش ئاغىنىلەر! تارقاق كۆپ مەبۇدلار ياخشىمۇ؟ ياكى قۇدرەتلىك بىر اﷲ ياخشىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,40,' سىلەرنىڭ اﷲ نى قويۇپ ئىبادەت قىلىۋاتقىنىڭلار پەقەت سىلەر ئۆزۈڭلار ۋە ئاتا ـ بوۋاڭلار ئاتىۋالغان (بۇتلارنىڭ ۋە غەيرىينىڭ) ناملىرىدىنلا ئىبارەت، اﷲ (ئۇلارنىڭ اﷲ نىڭ شېرىكى بولۇشى توغرىسىدا) ھېچقانداق دەلىل چۈشۈرگىنى يوق، ئىبادەت ۋە دىن بارىسىدىكى بارچە ھۆكۈملەر پەقەت اﷲ غىلا مەنسۇپ، ئۇ سىلەرنى پەقەت ئۆزىگىلا ئىبادەت قىلىشقا بۇيرۇغان، بۇ توغرا دىندۇر لېكىن كىشىلەرنىڭ تولىسى(بۇنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,41,' تۈرمىداش ئاغىنىلەر! سىلەرنىڭ بېرىڭلار (زىنداندىن چىقىپ) خوجايىنغا ھاراق قۇيۇپ بېرىدۇ (يەنى ساقىيلىق ۋەزىپىسىنى ئۆتەيدۇ)، يەنە بېرىڭلار دارغا ئېسىلىدۇ، كاللىسىنى قۇشلار يەيدۇ، سىلەر سورىغان ئىشلار ھۆكۈم قىلىنىپ بولدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,42,' يۇسۇف ئۇلارنىڭ ئىچىدىكى ئۆزىنىڭ قۇتۇلۇشىغا ئىشەنگەن بىرسىگە (يەنى ساقىيغا): «مېنى خۇجايىنىڭنىڭ ئالدىدا ئەسلەپ قويغىن» دېگەن ئىدى خوجايىنىنىڭ ئالدىدا يۇسۇفنى ئەسلەپ قويۇشنى شەيتان ئۇنىڭغا ئۇنتۇدۇردى، يۇسۇف زىنداندا بىر نەچچە يىل يېتىپ قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,43,' پادىشاھ ئېيتتى: «مەن ھەقىقەتەن چۈشۈمدە يەتتە ئورۇق كالىنىڭ يەتتە سېمىز كالىنى يەپكەتكەنلىكىنى ۋە يەتتە يېشىل باشاق بىلەن يەتتە قۇرۇق باشاقنى كۆرۈپتىمەن، ئى ئەربابلار! سىلەر ئەگەر چۈش تەبىرىنى بىلسەڭلار، بۇ چۈشۈمگە تەبىر بېرىپ بېقىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,44,' ئۇلار ئېيتتى: «بۇ بىر قالايمىقان چۈش ئىكەن، بىز (مۇنداق) قالايمىقان چۈشكە تەبىر بېرىشنى ئۇقمايمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,45,' ھېلىقى ئىككىسىدىن (تۈرمىدىن) قۇتۇلغان ۋە ئۇزاق مۇددەتتىن كېيىن (يۇسۇفنى) ئەسلىگەن بىرسى: «مەن سىلەرگە بۇ چۈشنىڭ تەبىرىنى ئېيتىپ بېرەلەيمەن، مېنى (يۇسۇفنىڭ يېنىغا) ئەۋەتىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,46,' (ئۇنى ئەۋەتىشتى، ئۇ تۈرمىگە بېرىپ يۇسۇفنىڭ يېنىغا كىردى ـ دە) «ئى راستچىل يۇسۇف! يەتتە ئورۇق كالىنىڭ يەتتە سېمىز كالىنى يەپ كەتكەنلىكىنى ۋە يەتتە يېشىل باشاق بىلەن يەتتە قۇرۇق باشاقنى (كۆرۈپ چۈشىگەن چۈشنىڭ تەبىرىنى) بىزگە ئېيتىپ بەرسەڭ، مەن ئۇنى كىشىلەرگە (يەنى پادىشاھقا ۋە ئۇنىڭ دۆلەت ئەربابلىرىغا) خەۋەر قىلسام، ئۇلار (سېنىڭ پەزلىڭنى) بىلسۇن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,47,' يۇسۇف ئېيتتى: «يەتتە يىل ئۈزۈلدۈرمەي تېرىقچىلىق قىلىڭلار، ئالغا ھوسۇلۇڭلاردىن يېيىش ئۈچۈن ئازغىنىسىنى خامان تەپكەندىن تاشقىرى، قالغىنىنى (مىتە چۈشۈپ كەتمەسلىكى ئۈچۈن) باشىقى بىلەن قويۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,48,' شۇنىڭدىن كېيىن قەھەتچىلىك بولىدىغان يەتتە يىل كېلىدۇ، (بۇ يىللاردا) ئىلگىرى توپلىۋالغان ئاشلىقىڭلاردىن (ئۇرۇقلۇق ئۈچۈن) ساقلاپ قويۇلىدىغان ئازغىنىسىدىن باشقا ھەممىسىنى يەپ تۈگىتىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,49,' شۇنىڭدىن كېيىن (مولچىلىق) يىللار كېلىدۇ، ئۇنىڭدا ھۆل ـ يېغىن كۆپ بولىدۇ، كىشىلەر مېۋىلەردىن تۈرلۈك ئىچىملىكلەرنى سىقىپ چىقىرىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,50,' (ساقىي پادىشاھقا بېرىپ يۇسۇفنىڭ ئۇنىڭ چۈشىگە بەرگەن تەبىرىنى ئېيتقاندىن كېيىن) پادىشاھ: ئۇنى ئالدىمغا ئېلىپ كېلىڭلار» دېدى (پادىشاھنىڭ) ئەلچىسى يۇسۇفنىڭ ئالدىغا بارغاندا، يۇسۇف ئەلچىگە: «خوجايىنىڭغا قايتىپ بېرىپ، ئۇنىڭدىن ھېلىقى قوللىرىنى كېسىۋالغان خوتۇنلارنىڭ ئىشىنى، (بىلەمدۇ؟) سوراپ باققىن، شۈبھىسىزكى مېنىڭ پەرۋەردىگارىم ئۇلارنىڭ ھىيلىسىنى ئوبدان بىلىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,51,'پادىشاھ ھېلىقى خوتۇنلارنى يىغىپ ۋە ئۆزىنىڭ خوتۇنىنى چاقىرىپ): «سىلەر يۇسۇفتىن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلغىنىڭلاردا، ھەقىقىي ئەھۋالىڭلار قانداق بولغان؟) دېدى ئۇلار: «اﷲ پاكتۇر؛ يۇسۇفتە ئازراقمۇ گۇناھ بار دەپ بىلمەيمىز» دېدى ئەزىزنىڭ خوتۇنى: «ئەمدى ھەقىقىي ئەھۋال ئايدىڭلاشتى، مەن ئۇنىڭ بىلەن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلغان ئىدىم، يۇسۇف ھەقىقەتەن راستچىللاردىن ئىدى» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,52,' (يۇسۇف ئېيتتى) «بۇ (يەنى پادىشاھنىڭ ئەلچىسىنى قايتۇرۇۋەتكەنلىكىم) ئەزىز يوق چاغدا خوتۇنىغا خىيانەت قىلمىغانلىقىمنى ۋە خائىنلارنىڭ ھىيلىسىنى اﷲ نىڭ مۇۋەپپەقىيەتكە ئېرىشتۈرمەيدىغانلىقىنى ئەزىزگە بىلدۈرۈش ئۈچۈندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,53,' «مەن ئۆزۈمنى ئاقلىمايمەن: نەپسى دېگەن نەرسە ھەقىقەتەن يامان ئىشلارغا كۆپ بۇيرۇيدۇ، پەرۋەردىگارىم رەھمەت قىلىپ (ساقلىغان) ئادەم بۇنىڭدىن مۇستەسنا مېنىڭ پەرۋەردىگارىم ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر؛ ناھايىتى مېھرىباندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,54,' پادىشاھ: «يۇسۇفنى ئالدىمغا ئېلىپ كېلىڭلار، ئۇنى ئۆزۈمنىڭ خاس ئادىمىم قىلىمەن» دېدى يۇسۇف ئۇنىڭ بىلەن سۆزلىشىۋاتقاندا، ئۇ: «سەن بۈگۈن بىزنىڭ ئالدىمىزدا ھەقىقەتەن مەرتىۋىلىك، ئىشەنچلىك ئادەمسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,55,' يۇسۇف: «مېنى (مىسىر) زېمىنىنىڭ خەزىنىلىرىنى باشقۇرۇشقا قويغىن، مەن ھەقىقەتەن (ئۇنى باشقۇرۇش يوللىرىنى) بىلىدىغان، ئوبدان ساقلىيالايدىغان ئادەممەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,56,' بىز يۇسۇفنى (مىسىر زېمىنىدا خالىغان جايدا تۇرىدىغان (مەملىكەتنى خالىغانچە تەسەررۇپ قىلىدىغان) قىلىپ يەرلەشتۈردۇق، رەھمىتىمىزنى خالىغان بەندىلىرىمىزگە ئېرىشتۈرىمىز، ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,57,' ئىمان ئېيتقان ۋە تەقۋادارلىق قىلغانلارغا ئاخىرەتنىڭ ساۋابى ئەلۋەتتە، (بۇ دۇنيانىڭ نېمەتلىرىدىن) ئارتۇقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,58,' يۇسۇفنىڭ قېرىنداشلىرى كېلىپ يۇسۇفنىڭ قېشىغا كىردى يۇسۇف ئۇلارنى تونۇدى، ئۇلار بولسا يۇسۇفنى تونۇمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,59,' يۇسۇف ئۇلارغا ئېھتىياجلىق ئاشلىقنى تەييارلاپ بەرگەندىن كېيىن ئېيتتى: «سىلەر مېنىڭ ئالدىمغا ئاتا بىر ئۇكاڭلارنى (يەنى بۇنيامىننى) ئېلىپ كېلىڭلار، مېنىڭ (ئاشلىقنى) تولۇق ئۆلچەپ بېرىۋاتقانلىقىمنى ۋە ناھايىتى مېھماندوست ئىكەنلىكىمنى كۆرمىدىڭلارمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,60,' ئەگەر ئۇنى مېنىڭ ئالدىمغا ئېلىپ كەلمىسەڭلار، سىلەرگە بېرىدىغان ئاشلىقىم يوق، مېنىڭ يېنىمغا يېقىن يولىماڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,61,' ئۇلار: «بىز ئۇنى ئاتىسىدىن سوراپ (سېنىڭ ئالدىڭغا ئېلىپ كېلىشكە) تىرىشىمىز، بىز چوقۇم مۇشۇنداق قىلىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,62,' يۇسۇف خىزمەتچىلىرىگە: «ئۇلارنىڭ (ئاشلىق سېتىۋالغان) مال ـ مۈلۈكلىرىنى يۈك ـ تاقىلىرىنىڭ ئارىسىغا سېلىپ قويۇڭلار، ئۇلار ئائىلىسىگە قايتىپ بارغاندا، ئۇنى تونۇپ قايتىپ كېلىشى مۇمكىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,63,' ئۇلار ئاتىسىنىڭ ئالدىغا قايتىپ بارغىنىدا: «ئى ئاتىمىز! (مىسىرنىڭ پادىشاھى) بىزگە قايتا ئاشلىق بەرمەس بولدى، ئۇكىمىزنى (يەنى بۇنيامىننى) بىز بىلەن بىللە ئەۋەتكىن، (شۇنداق قىلساڭ) ئاشلىق ئالالايمىز، ئۇنى بىز چوقۇم ئوبدان مۇھاپىزەت قىلىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,64,' (يەئقۇب) ئۇلارغا ئېيتتى: «ئۇنىڭ توغرىسىدا ئىلگىرى سىلەرگە ئۇنىڭ قېرىندىشى (يەنى يۇسۇف) توغرىسىدا ئىشەنگەندەك ئىشىنەمدىم؟ (مەن سىلەرگە ۋە سىلەرنىڭ ئۇنى ساقلىشىڭلارغلا ئىشەنمەيمەن) اﷲ ياخشى ساقلىغۇچىدۇر، ھەممىدىن مېھرىباندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,65,' ئۇلار يۈكلىرىنى ئاچقاندا (ئاشلىق سېتىۋالغان) مال مۈلۈكلىرىنىڭ ئۆزلىرىگە قايتۇرۇپ بېرىلگەنلىكىنى كۆردى، ئۇلار: «ئاتىمىز! بىز (بۇنىڭدىن ئارتۇق) يەنە نېمە تەلەپ قىلىمىز؟ بۇ مال ـ مۈلۈكلىرىمىز بىزگە قايتۇرۇپ بېرىلىپتۇ، (ئۇكىمىزنى ئېلىپ يەنە بارساق) ئائىلىمىزگە ئاشلىق ئېلىپ كېلىمىز، ئۇكىمىزنى مۇھاپىزەت قىلىمىز، بىر تۆگە ئاشلىقىنى ئارتۇق ئالىمىز، بۇ (يەنى بىر تۆگە ئاشلىق پادىشاھ ئۈچۈن) ئازغىنا ئاشلىقتۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,66,' يەئقۇب ئېيتتى: «بۇنيامىننى قوغداشتا ھەممىڭلار ھالاك بولمىساڭلارلا چوقۇم ماڭا قايتۇرۇپ ئېلىپ كېلىشكە اﷲ نىڭ نامى بىلەن قەسەم قىلىپ چىڭ ۋەدە بەرمىگۈچە ئۇنى سىلەر بىلەن بىللە ئەۋەتمەيمەن» ئۇلار (يەنى ئوغۇللىرى) ئۇنىڭغا قەسەم قىلىپ چىن ۋەدە بەرگەندىن كېيىن، يەئقۇب: «بىزنىڭ ئېيتقان سۆزلىرىمىزگە اﷲ گۇۋاھتۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,67,' (ئۇ يەنە) «ئى ئوغۇللىرىم! (مىسىرغا) ھەممىڭلار بىر دەرۋازىدىن كىرمەي، باشقا باشقا دەرۋازىلاردىن كىرىڭلار اﷲ نىڭ قازاسى ئالدىدا مەن سىلەردىن ھېچ نەرسىنى دەپئى قىلالمايمەن، ھۆكۈم پەقەت اﷲ غىلا خاس، اﷲ قا تەۋەككۈل قىلدىم، تەۋەككۈل قىلغۇچىلار اﷲ غىلا تەۋەككۈل قىلسۇن!» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,68,' ئۇلار ئاتىسىنىڭ بۇيرۇقى بويىچە باشقا ـ باشقا دەرۋازىلاردىن كىرگەندە، (بۇ) اﷲ نىڭ قازاسى ئالدىدا ئۇلار ئۈچۈن ھېچ نەرسىگە دال بولالمىدى، بۇ پەقەت يەئقۇبنىڭ كۆڭلىدىكى ئۈمىدىنىلا ئىپادىلىدى، خالاس بىز يەئقۇبقا (ۋەھيى ئارقىلىق بىلدۈرگەن ئىدۇق، شۇڭا ئۇ (كەڭ بىلىم ئىگىسىدۇر، لېكىن كىشىلەرنىڭ تولىسى (تەقدىرنىڭ سىرىنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,69,' ئۇلار (يەنى يەئقۇبنىڭ بالىلىرى) يۇسۇفنىڭ ئالدىغا كىرگەندە، يۇسۇف ئۇكىسىنى قۇچاقلىدى ۋە: «مەن ھەقىقەتەن سېنىڭ قېرىندىشىڭ بولىمەن، ئۇلارنىڭ بىزگە قىلغان ئىشلىرىدىن قايغۇرمىغىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,70,' يۇسۇف ئۇلارنى ئېھتىياجلىق ئاشلىق بىلەن تەمىنلىگەندىن كېيىن، قەدەھنى ئۇكىسىنىڭ يۈكى ئىچىگە سېلىپ قويدى ئاندىن بىر جاكاچى چىقىپ: «ئى كارۋانلار! سىلەر چوقۇم ئوغرى ئىكەنسىلەر» دەپ توۋلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,71,' (كارۋاندىكىلەر) ئۇلارغا قاراپ: «نېمەڭلارنى يوقىتىپ قويدۇڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,72,' ئۇلار: «پادىشاھنىڭ (تامغىسى بېسىلغان) قەدەھنى يوقىتىپ قويدۇق، ئۇنى تېپىپ كەلگەن كىشىگە (مۇكاپات ئۈچۈن) بىر تۆگە ئاشلىق بېرىلىدۇ، مەن بۇنىڭغا كېپىل» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,73,' ئۇلار: اﷲ نىڭ نامى بىلەن قەسەمكى، سىلەر بىلىسىلەر، بىز بۇ يەرگە بۇزغۇنچىلىق قىلىش ئۈچۈن كەلمىدۇق، بىز ئوغرى ئەمەسمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,74,' ئۇلار (يەنى يۇسۇفنىڭ خىزمەتچىلىرى): «سىلەرنىڭ شەرىئىتىڭلاردا ئوغرىنىڭ جازاسى نېمە؟ (ئەگەر قەدەھ يۈكۈڭلاردىن چىقىپ قېلىپ) يالغانچى بولۇپ قالساڭلارچۇ؟) دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,75,' ئۇلار: «ئۇنىڭ جازاسى قەدەھ بىلەن تېپىلغان ئادەمنى قۇل قىلىش، زالىملارغا بىز مۇشۇنداق جازا بېرىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,76,' ئۇلار يۇسۇفنىڭ ئۇكىسىنىڭ يۈكىنى ئاختۇرۇشتىن ئىلگىرى، ئۇلارنى يۈكىنى ئاختۇرۇشقا باشلىدى، ئاندىن يۇسۇفنىڭ ئۇكىسىنىڭ يۈكىدىن قەدەھنى تېپىپ چىقتى بىز يۇسۇفكە مۇشۇ تەدبىرنى كۆرسەتتۇق، (مىسىر) پادىشاھىنىڭ قانۇنى بويىچە يۇسۇف ئۇكىسىنى ئېلىپ قالالمايتى ، لېكىن اﷲ ئۇنىڭ شۇنداق قىلىشىنى خالىدى، بىز خالىغان ئادەمنى (يۇسۇفنى كۆتۈرگەندەك) يۇقىرى دەرىجىلەرگە كۆتۈرىمىز، ھەربىر بىلىمدار ئۈستىدە ئۇنىڭدىنمۇ بىلىمدار زات بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,77,' ئۇلار: «ئەگەر ئۇ، ئوغرىلىق قىلغان بولسا، ئۇنىڭ قېرىندىشىمۇ (يەنى يۇسۇفمۇ) ئىلگىرى ئوغرىلىق قىلغان» دېيىشتى يۇسۇف (ئۇلارنىڭ) بۇ سۆزىنى ئىچىدە بىلدى، لېكىن ئۇلارغا مەلۇم قىلمىدى، يۇسۇف (ئىچىدە): «سىلەرنىڭ مەۋقەيىڭلار ئەڭ ياماندۇر، سىلەرنىڭ (يۇسۇف ۋە ئۇنىڭ ئۇكىسى توغرىسىدا) قىلغان سۆزۈڭلارنىڭ يالغانلىقىنى اﷲ ئوبدان بىلىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,78,' ئۇلار: «ئى ئالىيجاناب زات! ئۇنىڭ (ئۇنىڭدىن ئايرىلالمايدىغان) بىر قېرى ئاتىسى بار، ئۇنىڭ ئورنىغا بىزنىڭ بىرىمىزنى ئېلىپ قالغىن، بىز سېنى ھەقىقەتەن ياخشىلىق قىلغۇچىلاردىن ھېسابلايمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,79,' يۇسۇف: «خۇدا ساقلىسۇن، نەرسىمىزنى كىمنىڭ يۈكىدىن تاپقان بولساق، شۇنى ئېلىپ قالىمىز، بولمىسا بىز چوقۇم زالىملاردىن بولۇپ قالىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,80,' ئۇلار (بۇنيامىننىڭ ئورنىغا ئۆز ئارىسىدىن بىرسىنى ئېلىپ قېلىشقا يۇسۇفنىڭ ماقۇل بولۇشىدىن) ئۈمىدسىزلەنگەندىن كېيىن، چەترەك يەرگە بېرىپ مەخپىي مەسلىھەتلەشتى، ئۇلارنىڭ چوڭى ئېيتتى: «ئاتاڭلارنىڭ سىلەردىن قەسەم قىلدۇرۇپ ۋەدە ئالغانلىقىنى ۋە ئىلگىرى يۇسۇف توغرىسىدىكى خاتالىقىڭلارنى بىلەمسىلەر؟ ئاتام ماڭا رۇخسەت قىلمىغىچە ياكى اﷲ مەن ئۈچۈن ھۆكۈم چىقارمىغۇچە بۇ يەردىن ھەرگىز ئايرىلمايمەن، اﷲ ئەڭ ئادىل ھۆكۈم قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,81,' سىلەر قايتىپ كېتىپ ئاتاڭلار بىلەن كۆرۈشۈڭلار، ئاندىن ئېيتىڭلاركى، ئى ئاتىمىز! سېنىڭ ئوغلۇڭ راستلا ئوغرىلىق قىلدى، بىز پەقەت بىلگەن نەرسىمىز ئۈستىدىلا گۇۋاھلىق بېرىۋاتىمىز، بىز غەيبنى بىلمەيمىز (يەنى ساڭا ۋەدە بەرگەن چېغىمىزدا ئۇنىڭ ئوغرىلىق قىلىدىغانلىقىنى بىلمىدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,82,' بىز تۇرغان شەھەردىن (يەنى مىسىردىن) ۋە بىز بىلەن بىللە ماڭغان كارۋاندىن سوراپ باققىن، بىزنىڭ سۆزىمىز چوقۇم راست»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,83,' يەئقۇب ئېيتتى: «ئۇنداق ئەمەس، نەپسىڭلار سىلەرگە بۇ ئىشنى چىرايلىق كۆرسەتتى، مەن پەقەت چىرايلىقچە سەۋرە قىلىمەن، ئۇلارنىڭ (يەنى ئۈچ ئوغلۇمنىڭ) ھەممىسىنى ماڭا دەرقەمتە قىلىپ بېرىشنى اﷲ تىن تىلەيمەن اﷲ ھەقىقەتەن (مېنىڭ ھالىمنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,84,' يەئقۇب ئۇلاردىن يۈز ئۆرۈپ: «ئىسىت يۇسۇف!» دېدى قايغۇدىن (كۆپ يىغلاپ) ئىككى كۆزى ئاقىرىپ كەتتى (يەنى ئىككى كۆزى كۆرمەس بولۇپ قالدى)، ئۇ تولۇپ تاشقان ئاچچىقىنى ئىچىگە يۇتقان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,85,' ئۇلار: «اﷲ نىڭ نامى بىلەن قەسەمكى، سەن يۇسۇفنى ياد ئېتىۋېرىپ (ھەسرەتتىن) ھالاك بولۇشقا تاس قالىدىغان ياكى ھالاك بولىدىغان بولدۇڭ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,86,' يەئقۇب ئېيتتى: «مەن قايغۇ ـ ھەسرىتىمنى پەقەت اﷲ غىلا ئېيتىمەن، اﷲ نىڭ بىلدۈرۈشى بىلەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,87,' ئى ئوغۇللىرىم! بېرىڭلار، يۇسۇفنى ۋە ئۇنىڭ ئۇكىسىنى ئىزدەڭلار، اﷲ نىڭ رەھمىتىدىن نائۈمىد بولماڭلار، شۈبھىسىزكى، پەقەت كاپىر قەۋملا اﷲ نىڭ رەھمىتىدىن ئۈمىدسىزلىنىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,88,' ئۇلار يۇسۇفنىڭ قېشىغا كىرگىنىدە: «ئى ئالىيجاناب زات! بىزگە ۋە ئائىلىمىزگە ئاچارچىلىق يەتتى، (ئالدىڭغا) ناچار ئۆتمەس ماللار بىلەن كېلىپ قالدۇق، بىزگە يېتەرلىك ئاشلىق بەرگىن، بىزگە سەدىقە قىلغىن، اﷲ سەدىقە قىلغۇچىلارنى ھەقىقەتەن مۇكاپاتلايدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,89,' يۇسۇف: «سىلەر نادان ۋاقتىڭلاردا يۇسۇف ۋە ئۇنىڭ ئۇكىسىغا نېمىلەر قىلغانلىقىڭلارنى بىلەمسىلەر؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,90,' ئۇلار: «سەن راستلا يۇسۇفمۇ؟» دېدى ئۇ: «مەن يۇسۇف بۇ مېنىڭ ئىنىم اﷲ بىزگە مەرھەمەت قىلدى كىمكى ھەقىقەتەن تەقۋادارلىق قىلىدىكەن، سەۋرى قىلىدىكەن (ياخشى مۇكاپاتقا ئېرىشىدۇ)، چۈنكى اﷲ ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,91,' ئۇلار «اﷲ نىڭ نامى بىلەن قەسەمكى، اﷲ سېنى ھەقىقەتەن بىزدىن ئارتۇق قىلىپتۇ، بىز جەزمەن خاتا قىلىپتۇق» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,92,' يۇسۇف ئېيتتى: «بۈگۈن سىلەر ئەيىبلەشكە ئۇچرىمايسىلەر، اﷲ سىلەرنى كەچۈرسۇن، اﷲ ئەڭ مەرھەمەتلىك زاتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,93,' سىلەر مېنىڭ بۇ كۆڭلىكىمنى ئېلىپ بېرىپ ئاتامنىڭ يۈزىگە تاشلاڭلار، كۆزى ئېچىلىدۇ، پۈتۈن ئائىلەڭلاردىكىنى ئىلىپ مېنىڭ يېنىمغا كېلىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,94,' كارۋان (مىسىردىن شانغا قاراپ) قوزغالغان چاغدا، ئۇلارنىڭ ئاتىسى (يەنى يەئقۇب ئەلەيھىسسالام): «مەن چوقۇم يۇسۇفنىڭ ھىدىنى ئېلىۋاتىمەن، سىلەر مېنى ئالجىپ قالغان دېمەيدىغان بولساڭلار (يۇسۇفنى ئەلۋەتتە ھايات دەيتتىم)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,95,' ئۇلار (يەنى يەئقۇبنىڭ نەۋرىلىرى ۋە يېنىدىكى كىشىلەر): «اﷲ نىڭ نامى بىلەن قەسەمكى، ھەقىقەتەن سەن تېخى يەنىلا بۇرۇنقى قايمۇقۇشۇڭدا ئىكەنسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,96,' خۇش خەۋەرچى كېلىپ كۆڭلەكنى يەئقۇبنىڭ يۈزىگە تاشلىدى، ئۇنىڭ كۆزى ئېچىلدى، يەئقۇب: «مەن سىلەرگە، اﷲ نىڭ بىلدۈرىشى بىلەن سىلەر بىلمىگەن نەرسىلەرنى بىلىمەن، دېمىگەنمىدىم» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,97,' ئۇلار: «ئى ئاتىمىز! گۇناھلىرىمىز ئۈچۈن بىزگە مەغپىرەت تىلىگىن، بىز ھەقىقەتەن خاتالاشتۇق» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,98,' يەئقۇب: «پەرۋەردىگارىمدىن سىلەرگە مەغپىرەت تىلەيمەن اﷲ گۇناھلارنى ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,99,' ئۇلار يۇسۇفنىڭ ئالدىغا كىرگەندە، يۇسۇف ئاتا ـ ئانىسىنى قۇچاقلىدى ۋە: «خۇدا خالىسا، مىسىرغا ئاما ـ ئېسەن كىرىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,100,' ئاتا ـ ئانىسىنى تەختىدە (يېنىدا) ئولتۇرغۇزدى، ئۇلار (شاھلارغا تازىم قىلىش ئادىتى بويىچە) يۇسۇفكە سەجدە قىلىشتى  ئۇ: «ئى ئاتا! مانا بۇ، بۇرۇن (كىچىك ۋاقتىمدا) كۆرگەن چۈشۈمنىڭ تەبىرىدۇر، ئۇ چۈشۈمنى پەرۋەردىگارىم ھەقىقەتەن راستىغا چىقاردى پەرۋەردىگارىم مېنى زىنداندىن چىقىرىش بىلەن، قېرىنداشلىرىم بىلەن مېنىڭ ئارامنى شەيتان بۇزغاندىن كېيىن، سىلەرنى سەھرادىن بۇ يەرگە ئېلىپ كېلىش بىلەن ماڭا ئېھسان قىلدى، پەرۋەردىگارىم خالىغىنىنى (ئىشقا ئاشۇرۇشقا) ھەقىقەتەن تەدبىرلىكتۇر، ئۇ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,101,' پەرۋەردىگارىم! ماڭا ھەقىقەتەن پادىشاھلىق ئاتا قىلدىڭ، چۈش تەبىرىنى بىلدۈردۈڭ، ئى ئاسمانلارنى ۋە زېمىننى ئۆرنەكسىز ياراتقۇچى زات! دۇنيا ۋە ئاخىرەتتە مېنىڭ ئىگەمسەن، مېنى مۇسۇلمان پېتىمچە قەبزى روھ قىلغىن، مېنى ياخشى بەندىلەر قاتارىدا قىلغىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,102,' مانا بۇلار (يەنى يۇسۇف قىسسىسى) بىر قىسىم غەيب خەۋەرلىرى بولۇپ، بىز ئۇنى ساڭا ۋەھيى قىلدۇق ئۇلار (يەنى يۇسۇفنىڭ قېرىنداشلىرى) (ئۇنىڭغا قارشى) چارە تەدبىر تۈزۈپ بىر قارارغا كېلىشكەندە سەن ئۇلارنىڭ يېنىدا يوق ئىدىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,103,' سەن گەرچە كىشىلەرنىڭ ئىمان ئېيتىشىغا ھېرىس بولساڭمۇ (لېكىن) ئۇلارنىڭ تولىسى ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,104,' قۇرئاننى تەبلىغ قىلغانلىقىڭغا ئۇلاردىن ھېچقانداق ئەجىر تەلەپ قىلمايسەن، قۇرئان پەقەت جاھان ئەھلى ئۈچۈن ۋەز ـ نەسىھەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,105,' ئاسمانلاردا ۋە زېمىندا (اﷲ نىڭ قۇدرىتىنىڭ) نۇرغۇن ئالامەتلىرى باركى، ئۇلا ر ئۇنىڭ يېنىدىن دىققەت قىلماستىن ئۆتۈپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,106,' ئۇلارنىڭ تولىسى اﷲ قا شېرىك كەلتۈرۈپ تۇرۇپ ئىشىنىدۇ (يەنى اﷲ نى ياراتقۇچى، رىزىق بەرگۈچى دەپ ئىقرار قىلىش بىلەن بىللە، بۇتلارغىمۇ چوقۇنىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,107,' ئۇلار اﷲ نىڭ ئازابىدىن، بىرەر جازانىڭ ئۆزلىرىگە چۈشۈشىدىن ياكى ئۇلار سەزمەستىن قىيامەتنىڭ تۇيۇقسىز يېتىپ كېلىشىدىن ئەمىن بولدىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,108,' ئېيتقىنكى، «بۇ مېنىڭ يولۇمدۇر، (كىشىلەرنى) اﷲ قا دەۋەت قىلىمەن، مەن ۋە ماڭا ئەگەشكەنلەر روشەن دەلىلگە ئاساسلىنىمىز اﷲ پاكتۇر، مەن مۇشرىكلاردىن ئەمەسمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,109,' بىز سەندىن ئىلگىرى پەقەت شەھەر ئاھالىسىدىن بولغان ئەرلەرنى پەيغەمبەر قىلىپ، ئۇلارغا ۋەھيى قىلدۇق، ئۇلار(يەنى پەيغەمبەرگە چىنپۈتمىگۈچىلەر) يەر يۈزىدە سەير قىلىپ يۈرۈپ ئۆزلىرىدىن ئىلگىرىكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۈزەتمىدىمۇ؟ ئاخىرەت يۇرتى تەقۋادارلىق قىلغانلار ئۈچۈن ئەلۋەتتە ياخشىدۇر، سىلەر چۈشەنمەمسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,110,' ھەتتا پەيغەمبەرلەر(قەۋمىنىڭ ئىمان ئېيتىشىدىن) ئۈمىدسىزلەنگەن ۋە قەۋمى تەرىپىدىن) يالغانغا چىقىرىلغانلىقىغا جەزم قىلغان چاغدا، ئۇلارغا بىزنىڭ ياردىمىمىز يېتىپ كەلدى، بىز خالىغان ئادەملەرنى قۇتقۇزدۇق، بىزنىڭ ئازابىمىز گۇناھكار قەۋمدىن قايتۇرۇلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,12,111,' ئۇلارنىڭ قىسسىسىدىن ئەقىل ئىگىلىرى ئەلۋەتتە ئىبرەت ئالىدۇ (قۇرئان) يالغاندىن توقۇلغان سۆز ئەمەس، لېكىن، ئۇ ئۆزىدىن ئىلگىرى نازىل بولغان (ساماۋى) كىتابلارنى تەستىق قىلغۇچىدۇر، ھەممە نەرسىنى ئىنچىكە بايان قىلغۇچىدۇر، ئىمان ئېيتىدىغان قەۋم ئۈچۈن ھىدايەتتۇر ۋە رەھمەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,1,'ئەلىف، لام، مىم ، را بۇ، كىتاب (يەنى قۇرئان) نىڭ ئايەتلىرىدۇر (قۇرئاندا) ساڭا پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان نەرسىلەر ھەقتۇر، لېكىن ئىنسانلارنىڭ تولىسى ئۇنىڭغا (يەنى قۇرئاننىڭ اﷲ تەرىپىدىن نازىل بولغانلىقىغا) ئىشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,2,' اﷲ ئاسمانلارنى تۈرۈكسىز بەرپا قىلدى، سىلەر ئۇنى كۆرۈپ تۇرۇۋاتىسىلەر، ئاندىن ئۇ ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى كۈن بىلەن ئاينى (بەندىلەرنىڭ مەنپەئىتى ئۈچۈن) بويسۇندۇردى، (ئۇلارنىڭ) ھەربىرى مۇئەييەن مۇددەتكىچە (يەنى دۇنيانىڭ تۈگەيدىغان ۋاقتىقىچە) سەير قىلىدۇ، اﷲ (ئۆز ھېكمىتى ۋە قۇدرىتى بىلەن) مەخلۇقاتنىڭ ئىشلىرىنى باشقۇرۇپ تۇرىدۇ، سىلەرنى پەرۋەردىگارىڭلارغا مۇلاقات بولۇشقا ئىشەنسۇن دەپ، (قۇدرىتىنىڭ) دەلىللىرىنى تەپسىلىي بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,3,' اﷲ يەر يۈزىنى (ئۇزۇنسىغا ۋە توغرىسىغا) سوزۇپ كەڭ ياراتتى، يەر يۈزىدە تاغلارنى ۋە دەريالارنى ياراتتى، مېۋىلەرنىڭ ھەربىر تۈرىنى ئەركەك ـ چىشى ـ ئىككى جىنسلىق قىلىپ ياراتتى كېچىنى كۈندۈزگە كىرىشتۈردى (يەنى كېچىنىڭ قاراڭغۇلۇقى بىلەن كۈندۈزنىڭ يورۇقلۇقىنى ياپتى) بۇلاردا تەپەككۇر قىلىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,4,' يەر يۈزىدە بىر بىرىگە تۇتىشاڭغۇ قىتئەلەر بار ۋە تۈرلۈك زىرائەتلەر بار، كۆپ شاخلىق بولغان ۋە كۆپ شاخلىق بولمىغان (بىر ئۇرۇقتىن بىر قانچە تال ئۈنۈپ چىققان، بىر ئۇرۇقتىن پەقەت بىر تال ئۈنۈپ چىققان) خورما دەرەخلىرى بار، (ئۇلارنىڭ ھەممىسى) بىر خىل سۇ بىلەن سۇغىرىلىدۇ، ئۇلارنىڭ بەزىسىنى بەزىسىدىن تەمىدە ئارتۇق قىلىمىز، بۇلاردا چۈشىنىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,5,' (ئى مۇھەممەد!) ئەگەر (بىرەر نەرسىدىن) ئەجەبلىنىدىغان بولساڭ، ئۇلارنىڭ (يەنى كۇففارلارنىڭ): «بىز (ئۆلۈپ) توپا بولۇپ كەتكەندىن كېيىن قايتا تىرىلەمدۇق؟» دېگەن سۆزى ئەجەبلىنىشكە لايىقتۇر ئەنە شۇلار پەرۋەردىگارىنى ئىنكار قىلغانلاردۇر، (قىيامەت كۈنى) ئەنە شۇلارنىڭ بويۇنلىرىغا تاقاقلار سېلىنىدۇ، ئەنە شۇلار دوزاخ ئەھلى بولۇپ، دوزاختا مەڭگۈ قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,6,' ئۇلار سەندىن (اﷲ نىڭ) ئېھسانىدىن ئىلگىرى ئازابنىڭ چاپسانراق چۈشۈشىنى تەلەپ قىلىدۇ، ئۇلاردىن بۇرۇن اﷲ نىڭ ئازابىغا دۇچار بولغان ئۈممەتلەر ئۆتكەن، ئىنسانلار زۇلۇم قىلسىمۇ، پەرۋەردىگارىڭ ھەقىقەتەن ئۇلارنى مەغپىرەت قىلىدۇ پەرۋەردىگارىڭ (گۇناھلارغا چۆمۈپ تەۋبە قىلمىغانلارنى) شەك شۈبھىسىز قاتتىق جازالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,7,' كۇففارلار نېمىشقا ئۇنىڭغا پەرۋەردىگارى تەرىپىدىن بىرەر مۆجىزە نازىل بولمىدى؟ دېيىشىدۇ، سەن پەقەت بىر ئاگاھلاندۇرغۇچى ھەر قەۋمنىڭ بىر ھىدايەت قىلغۇچىسى (يەنى پەيغەمبىرى) بولىدۇ (مۆجىزىلەرگە كەلسەك ئۇ اﷲ نىڭ ئىلكىدىكى ئىش)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,8,' ھەر بىر (ھامىلىدار) ئايالنىڭ قورسىقىدىكىنى اﷲ بىلىدۇ (يەنى ئوغۇلمۇ ـ قىزمۇ، بىرمۇ ـ كۆپمۇ، چوڭمۇ ـ كىچىكمۇ، چىرايلىقمۇ ـ سەتمۇ، بەختلىكمۇ ـ بەختسىزمۇ، ئۆمرى ئۇزۇنمۇ ـ قىسقىمۇ ـ ھەممىسىنى اﷲ بىلىدۇ) بەچچىدانلارنىڭ كىچىكلەپ كەتكەنلىكى ياكى يوغىناپ كەتكەنلىكىنىمۇ (يەنى بالىنىڭ مۇددىتى توشماي تۇغۇلىدىغانلىقىنى ياكى مۇددىتى ئېشىپ كېتىپ تۇغۇلىدىغانلىقىنى) (اﷲ) بىلىدۇ اﷲ نىڭ دەرگاھىدا ھەممە نەرسە ئۆلچەملىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,9,' اﷲ مەخپىي ۋە ئاشكارا ئىشلارنىڭ ھەممىسىنى بىلگۈچىدۇر، ھەممىدىن بۈيۈكتۇر، ھەممىدىن ئۈستۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,10,' سىلەرنىڭ ئىچىڭلاردىن يوشۇرۇن سۆز قىلغان، ئاشكارا سۆز قىلغان، كېچىسى يوشۇرۇنغان، كۈندۈزى ئاشكارا يۈرگەنلەرنىڭ ھەممىسى (اﷲ ئۈچۈن ئېيتقاندا) ئوخشاشتۇر (يەنى اﷲ ھەممىنى بىلىپ تۇرىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,11,' ھەربىر ئادەمنىڭ ئالدى ـ كەينىدە اﷲ نىڭ ئەمرى بويىچە ئۇنى قوغدايدىغان نۆۋەتچى پەرىشتىلەر بار ھەر قانداق بىر قەۋم ئۆزىنىڭ ئەھۋالىنى ئۆزگەرتمىگۈچە (يەنى اﷲ نىڭ بەرگەن نېمەتلىرىگە تۇزكورلۇق قىلىپ گۇناھلارغا چۈممىگىچە) اﷲ ئۇلارنىڭ ئەھۋالىنى ئۆزگەرتمەيدۇ (يەنى اﷲ ئۇلارغا بەرگەن نېمەت، خاتىرجەملىك ۋە ئىززەت ـ ھۆرمەتنى ئېلىپ تاشلىمايدۇ)، اﷲ بىرەر قەۋمنى ھالاك قىلماقچى (ياكى ئازابلىماقچى) بولسا، ئۇنىڭغا قارشى تۇرغىلى بولمايدۇ، ئۇلارغا ئازابنى دەپئى قىلىدىغان اﷲ تىن باشقا ئىگىمۇ بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,12,'اﷲ (چاقماقتىن) قورقىتىش، (يامغۇرغا) ئۈمىد تۇغدۇرۇش ئۈچۈن، سىلەرگە چاقماقنى كۆرسىتىدۇ، (قۇدرىتى بىلەن) قويۇق بۇلۇتلارنى پەيدا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,13,' گۈلۈرماما اﷲ نى مەدھىيىلەش بىلەن تەسبىھ ئېيتىدۇ، پەرىشتىلەرمۇ اﷲ تىن قورقۇپ تەسبىھ ئېيتىدۇ، اﷲ چاقماق چۈشۈرۈپ، ئۇنىڭ بىلەن ئۆزى خالىغان ئادەمنى ھالاك قىلىدۇ ئۇلار اﷲ توغرىسىدا دەتالاش قىلىشىدۇ، اﷲ نىڭ تەدبىرى كۈچلۈكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,14,'ھەقىقىي قىلىنغان دۇئا اﷲ قا قارىتىلىدۇ، (يەنى ئىخلاس بىلەن قىلىنغان دۇئانى اﷲ ئىجابەت قىلىدۇ) كۇففارلارنىڭ اﷲ نى قويۇپ، دۇئالىرىنى قاراتقان مەبۇدلىرى كۇففارلارنىڭ ھېچقانداق دۇئاسىنى ئىجابەت قىلمايدۇ ئۇلار (يەنى كاپىرلار) شۇنداق بىر ئادەمگە ئوخشايدۇكى، ئۇ ئاغزىمىزغا سۇ چۈشسۇن دەپ (نېرىدىن) ئىككى ئالىقىنىنى سۇغا قارىتىپ ئېچىپ تۇرىدۇ، (ئەمەلىيەتتە سۇ ئاڭلىمايدىغان، ھېس ـ تۇيغۇسىز نەرسە بولغاچقا) سۇ ھەرگىز ئۇنىڭ ئاغزىغا چۈشمەيدۇ، كاپىرلارنىڭ مەبۇدلىرىغا قىلغان دۇئاسى پۈتۈنلەي بىكاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,15,' ئاسمانلاردىكىلەر ۋە زېمىندىكىلەر (يەنى پەرىشتىلەر، ئىنسانلار ۋە جىنلار) ئىختىيارى ۋە مەجبۇرى يوسۇندا اﷲ قا بويسۇنىدۇ، ئەتىگەن – ئاخشامدا (يەنى داۋاملىق تۈردە) ئۇلارنىڭ سايىلىرىمۇ بويسۇنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,16,' (ئى مۇھەممەد!مۇشرىكلارنى مات قىلىش ئۈچۈن ئۇلارغا) «ئاسمانلارنىڭ، زېمىننىڭ پەرۋەردىگارى كىم؟» دېگىن، «(ئۇلارنىڭ پەرۋەردىگارى) اﷲ» دېگىن (ئۇلارغا) «سىلەر اﷲ نى قويۇپ ئۆزۈڭلارغا پايدا ـ زىيان يەتكۈزەلمەيدىغان مەبۇدلارنى( اﷲ قا شېرىك) قىلىۋالدىڭلارمۇ؟» دېگىن؟ «كۆر (يەنى كاپىر) بىلەن كۆزى كۆرۈدىغان ئادەم (يەنى مۆمىن) باراۋەر بولامدۇ؟ ياكى قاراڭغۇلۇق (يەنى گۇمراھلىق) بىلەن نۇر (يەنى ھىدايەت) باراۋەر بولامدۇ؟» دېگىن، ئۇلار اﷲ ياراتقانغا ئوخشاش قانداقتۇر مەخلۇقاتلارنى ياراتقان مەبۇدلارنى پۈتۈن مەخلۇقاتنى ياراتقان اﷲ قا شېرىك قىلىۋېلىپ اﷲ نىڭ ياراتقىنى بىلەن مەبۇدلارنى پۈتۈن مەخلۇقاتنى ياراتقان اﷲ قا شېرىك قىلىۋېلىپ اﷲ نىڭ ياراتقىنى بىلەن مەبۇدلىرىنىڭ ياراتقىنىنى ئايرىيالماي قالدىمۇ؟ اﷲ ھەممە نەرسىنى ياراتقۇچىدۇر، اﷲ بىر دۇر، ھەممىگە غالىبتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,17,' اﷲ ئاسماندىن يامغۇر ياغدۇردى، (يامغۇر سۈيى) جىلغىلاردا لىپمۇ لىق ئاقتى، كەلكۈن سۇ ئۆزىنىڭ ئۈستىدە كۆپۈكلەرنى لەيلىتىپ ئاقتى، كىشىلەرنىڭ زىننەت بۇيۇملىرىنىڭ، ياكى ئەسۋاب ياساش ئۈچۈن ئوتقا سېلىپ ئېرىتكەن مەدەنلەرنىڭمۇ  (يەنى كۆپۈكمۇ خۇددى كەلكۈن سۇنىڭ كۆپۈكىگە ئوخشاش پايدىسىزدۇر) اﷲ ھەق بىلەن باتىلنى بۇ مىسال بىلەن ئەنە شۇنداق بايان قىلىدۇ، كۆپۈك بولسا ئېقىپ تۈگەيدۇ، ئىنسانلارغا پايدىلىق نەرسە زېمىندا قېلىپ قالىدۇ، اﷲ مىساللارنى ئەنە شۇنداق بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,18,' اﷲ نىڭ (ئىمان غا قىلغان دەۋىتىنى) قوبۇل قىلغانلارغا ئەڭ ئوبدان مۇكاپات بېرىلىدۇ، قوبۇل قىلمىغانلارنىڭ ئىگىدارچىلىقىدا يەر يۈزىدىكى پۈتۈن مال ـ مۈلۈك، يەنە شۇنىڭدەك بىر ھەسسە كېلىدىغان مال ـ مۈلۈك بولغان تەقدىردىمۇ، ئۇلار (ئاخىرەتتە اﷲ نىڭ ئازابىدىن قۇتۇلۇش ئۈچۈن)، ئەلۋەتتە، ئۇنى پىدا قىلغان بولاتتى ئۇلاردىن قاتتىق ھېساب ئېلىنىدۇ، ئۇلارنىڭ بارىدىغان جايى دوزاخ بولىدۇ، (دوزاخ) نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,19,' (ئى مۇھەممەد!) پەرۋەردىگارىڭ تەرىپىدىن ساڭا نازىل قىلىنغان نەرسىنىڭ ھەق ئىكەنلىكىنى بىلىدىغان ئادەم (ھەقنى كۆرمەيدىغان دىلى) كور ئادەم بىلەن ئوخشاش بولامدۇ؟ (اﷲ نىڭ ئايەتلىرىدىن) پەقەت ئەقىل ئىگىلىرىلا پەند ـ نەسىھەت ئالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,20,' ئۇلار اﷲ نىڭ ئەھدىگە (يەنى اﷲ تاپشۇرغان ئىشلارغا) ۋاپا قىلىدۇ، بەرگەن ۋەدىسىنى بۇزمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,21,' ئۇلار اﷲ نىڭ (خىش ـ ئەقرىبالارغا) سىلە ـ رەھىم قىلىشتىن ئىبارەت ئەمرىنى بەجا كەلتۈرىدۇ، اﷲ تىن قورقىدۇ، قاتتىق ھېساب ئېلىنىشىدىن قورقىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,22,' پەرۋەردىگارىنىڭ رازىلىقىنى كۆزلەپ (يەتكەن كۈلپەتلەرگە) سەۋر قىلغانلار، (پەرز) نامازنى ئادا قىلغانلار، بىز رىزىق قىلىپ بەرگەن مال ـ مۈلۈكتىن (اﷲ يولىدا) يوشۇرۇن ۋە ئاشكارا يوسۇندا سەرپ قىلغانلار ۋە ياخشىلىقى ئارقىلىق يامانلىقىنى دەپئى قىلىدىغانلار (يەنى بىلمەستىن يامان ئىش قىلىپ قالسا، ئارقىدىن ياخشى ئىش قىلىدىغانلار، يامانلىق قىلغۇچىلارغا كەڭ قورساق بولۇپ، ياخشى مۇناسىۋەتتە بولىدىغانلار) ـ ئەنە شۇلارنىڭ ئاخىرەتلىكى ياخشى بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,23,' ئۇلار مەڭگۈ تۇرىدىغان جەننەتكە كىرىدۇ، ئۇلارنىڭ ئاتىلىرى، خوتۇنلىرى ۋە ئەۋلادىنىڭ ئىچىدىكى ياخشىلىقلارمۇ جەننەتكە كىرىدۇ پەرىشتىلەر ئۇلارنى (مۇبارەكلەش ئۈچۈن جەننەتنىڭ) ھەربىر دەرۋازىسىدىن كىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,24,' (پەرىشتىلەر ئۇ دۇنيادا) « سەۋرىنى دوست تۇتقانلىقىڭلار ئۈچۈن سىلەرگە ئامانلىق بولسۇن! ئاخىرەتلىكنىڭ ياخشى بولۇشى نېمىدېگەن ئوبدان!» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,25,' اﷲ قا بەرگەن ۋەدىسىنى مۇستەھكەملىگەندىن كېيىن بۇزغانلار، اﷲ نىڭ سىلە ـ رەھىم قىلىشتىن ئىبارەت ئەمرىنى تۇتمىغانلار، يەر يۈزىدە بۇزغۇنچىلىق قىلغانلار ـ ئەنە شۇلار لەنەتكە دۇچار بولىدۇ ۋە (ئۇلارنىڭ) ئاخىرەتلىكى يامان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,26,' اﷲ (ئۆزىنىڭ ھېكمىتىگە ئاساسەن) خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ ۋە (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدۇ (مۇشرىكلار) دۇنيا تىرىكچىلىكىگە خۇش بولۇپ كەتتى، دۇنيا تىرىكچىلىكى ئاخىرەتكە قارىغاندا ئازغىنا نەرسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,27,' كۇففارلار: «نېمىشقا ئۇنىڭغا پەرۋەردىگارىدىن (مۇسانىڭ ۋە ئىسانىڭ مۆجىزىسىدەك) بىرەر مۆجىزە كەلمىدى؟» دەيدۇ (ئى مۇھەممەد!) «(ئىش اﷲ نىڭ ئىلكىدە ، مېنىڭ ئىلكىمدە ئەمەس) اﷲ ھەقىقەتەن خالىغان ئادەمنى گۇمراھ قىلىدۇ» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,28,' (تەۋبە قىلغانلار) ئىمان ئېيتقانلار بولۇپ، ئۇلارنىڭ دىللىرى اﷲ نى ياد ئېتىش بىلەن ئارام تاپىدۇ، بىلىڭلاركى، دىللار اﷲ نى ياد ئېتىش بىلەن ئارام تاپىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,29,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار خۇشاللىققا، گۈزەل قارارگاھقا (يەنى جەننەتكە) ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,30,' (ئى مۇھەممەد!) بىز ساڭا ۋەھىي قىلغان كىتابنى ئۇلارغا ئوقۇپ بېرىشىڭ ئۈچۈن، سەندىن ئىلگىرى پەيغەمبەرلەرنى ئەۋەتكەندەك، سېنى شۇنداق بىر ئۈممەتكە ئەۋەتتۇقكى، ئۇنىڭدىن بۇرۇن نۇرغۇن ئۈممەتلەر ئۆتكەن، ھالبۇكى، ئۇلار (يەنى مۇشرىكلار) مەرھەمەتلىك اﷲ نى ئىنكار قىلماقتا، (ئى مۇھەممەد! بۇ مۇشرىكلارغا) ئېيتقىنكى، «ئۇ مېنىڭ پەرۋەردىگارىمدۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يۇقتۇر، ئۇنىڭغا تەۋەككۈل قىلدىم ۋە ئۇنىڭ دەرگاھىغا قايتىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,31,' مۇبادا بىرەر قۇرئان (يەنى كىتاب) بولۇپ، ئۇ (تىلاۋەت قىلىش) بىلەن تاغلار يۆتكىلىدىغان ياكى يەر يېرىلىدىغان ۋە ياكى ئۆلۈكلەر سۆزلەيدىغان بولغان تەقدىردىمۇ (ئۇلار چوقۇم ئىمان ئېيتمايتتى) ھەممە ئىش اﷲ نىڭ ئىلكىدىدۇر، ئەگەر اﷲ خالىسا، ئىنسانلارنىڭ ھەممىسىنى جەزمەن ھىدايەت قىلىدىغانلىقىنى مۆمىنلەر بىلمىدىمۇ؟ (مەككە پەتھى قىلىنىپ ئىسلام دىنىنىڭ غەلىبە قىلىشىدىن ئىبارەت) اﷲ نىڭ ۋەدىسى ئەمەلگە ئاشقانغا قەدەر، كاپىرلار ئۆز قىلمىشلىرى تۈپەيلىدىن ھامان تۈرلۈك بالالارغا دۇچار بولىدۇ، ياكى بالا ئۇلارنىڭ يۇرتىغا يېقىن بىر جايغا چۈشىدۇ، اﷲ ۋەدىسىگە ئەلۋەتتە خىلاپلىق قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,32,' سەندىن ئىلگىرى ئۆتكەن نۇرغۇن پەيغەمبەرلەر ھەقىقەتەن مەسخىرە قىلىندى كاپىرلارغا بېرىلىدىغان ئازابنى بىر مۇددەت كېچىكتۈردۈم، ئاندىن ئۇلارنى جازالىدىم، ئۇلارغا بەرگەن جازايىم قانداق ئىكەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,33,' ھەر بىر كىشىنىڭ قىلغان ئەمەلىنى كۈزىتىپ تۇرغۇچى اﷲ (بۇتلارغا ئوخشامدۇ؟) ئۇلار بۇتلارنى اﷲ قا شېرىك قىلدى ئۇلارغا: «ئۇلارغا بۇتلارنىڭ ناملىرىنى ئاتاپ بېقىڭلار، يەر يۈزىدە اﷲ بىلمەيدىغان نەرسىلەر باردەك، سىلەر ئۇلارنى اﷲ قا ئېيتىپ بېرەلەمسىلەر؟ ياكى قۇرۇق گۇمان بىلەن (ئۇلارنى اﷲ نىڭ شېرىكلىرى دەۋاتامسىلەر؟)» دېگىن بەلكى كاپىرلارغا ئۇلارنىڭ كۇفرى چىرايلىق كۆرسىتىلدى، ئۇلار توغرا يولدىن مەنئى قىلىندى اﷲ گۇمراھ قىلغان ئادەمگە ھېچقانداق ھىدايەت قىلغۇچى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,34,' ئۇلار بۇ دۇنيادا ئازابقا دۇچار بولىدۇ، ئۇلارنى اﷲ نىڭ ئازابىدىن ساقلىغۇچى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,35,' تەقۋادارلارغا ۋەدە قىلىنغان جەننەتنىڭ سۈپىتى شۇكى، ئۇنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدۇ، ئۇنىڭ مېۋىلىرى تۈگىمەيدۇ، ھەمىشە سايە چۈشۈپ تۇرىدۇ، ئەنە شۇ تەقۋادارلارنىڭ ئاقىۋەت بارىدىغان جايىدۇر؛ كاپىرلارنىڭ ئاقىۋەت بارىدىغان جايى دوزاختۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,36,' (ئى مۇھەممەد!) بىز كىتاب (يەنى تەۋرات ۋە ئىنجىل) نازىل قىلغانلار (يەنى ئابدۇللا ئىبن سالام ۋە نەجاشىغا ئوخشاشلار) ساڭا نازىل قىلىنغان (قۇرئان) دىن شادلىنىدۇ (ساڭا قارشى) ئىتتىپاق تۈزگەنلەرنىڭ ئارىسىدا (ھەقلىقىنى بىلىپ تۇرۇپ گەدەنكەشلىكتىن) قۇرئاننىڭ بەزى (جايلىرىنى) ئىنكار قىلىدىغانلارمۇ بار (ئى مۇھەممەد!) «مەن پەقەت (يالغۇز) اﷲ غىلا قىلىشقا، اﷲ قا (ھېچ نەرسىنى) شېرىك كەلتۈرمەسلىككە بۇيرۇلدۇم، (ئىنسانلارنى) اﷲ قا ئىبادەت قىلىشقا دەۋەت قىلىمەن، اﷲ نىڭلا دەرگاھىغا قايتىمەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,37,' شۇنىڭدەك (يەنى ئۆتكەنكى پەيغەمبەرلەرگە ئۆز تىلىدا كىتاب نازىل قىلغىنىمىزدەك)، قۇرئاننى ئەرەب تىلىدا (كىشىلەرنىڭ ئارىسىدا چىقىرىشقا) ھۆكۈم قىلىپ نازىل قىلدۇق، سەن ئىلىمگە ئىگە بولۇپ تۇرۇپ، ئۇلارنىڭ (يەنى ئەھلى كىتابنىڭ) نەپسى خاھىشلىرىغا ئەگىشىپ كېتىدىغان بولساڭ، ساڭا ھېچقانداق ياردەم بەرگۈچى ۋە سېنى اﷲ نىڭ ئازابىدىن ساقلىغۇچى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,38,' بىز سەندىن ئىلگىرى ھەقىقەتەن نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارغا خوتۇنلار ۋە بالىلار بەردۇق، ھەرقانداق پەيغەمبەر اﷲ نىڭ ئىزنىسىز مۆجىزە كەلتۈرەلمەيدۇ، ھەربىر ۋاقىتنىڭ (ئۆزىگە مۇناسىپ) ھۆكمى بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,39,' اﷲ (ئەھكاملاردىن) خالىغىنىنى بىكار قىلىدۇ، خالىغىنىنى ئۆز جايىدا قالدۇرىدۇ لەۋھۇلمەھپۇز اﷲ نىڭ دەرگاھىدىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,40,' ئۇلارغا بىز ۋەدە قىلغان ئازابنىڭ بەزىسىنى ساڭا كۆرسەتسەكمۇ (يەنى سېنىڭ ھايات ۋاقتىڭدا نازىل قىلساقمۇ) ياكى سېنى (مۇشرىكلارغا ئازاب كېلىشتىن بۇرۇن) قەبزى روھ قىلساقمۇ، سېنىڭ مەسئۇلىيىتىڭ پەقەت تەبلىغ قىلىشتۇر بىزنىڭ مەسئۇلىيىتىمىز ھېساب ئېلىشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,41,' ئۇلارنىڭ يېرىنى چۆرىسىدىن تارايتىپ بېرىۋاتقانلىقىمىز (يەنى كۇففارلارنىڭ زېمىنىنى قەدەممۇ قەدەم پەتھى قىلىپ، ئىسلام يۇرتىغا قوشۇۋاتقانلىقىمىز) نى ئۇلار (يەنى مۇشرىكلار) كۆرمەيۋاتامدۇ؟ اﷲ (خالىغانچە) ھۆكۈم قىلىدۇ، اﷲ نىڭ ھۆكمىگە ھېچ كىشى قارشى تۇرالمايدۇ اﷲ تېز ھېساب ئالغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,42,' ئۇلار (يەنى قۇرەيش كاپىرلىرى) دىن ئىلگىرى ئۆتكەنلەرمۇ (پەيغەمبەرلىرىگە) ھىيلە ـ مىكىر ئىشلەتكەن ئىدى (ئۇلارنىڭ ھىيلە –مىكرىنى بەربات قىلىدىغان) يوشۇرۇن تەدبىرلەرنىڭ ھەممىسى اﷲ نىڭ ئىلگىدىدۇر، اﷲ ھەربىر كىشىنىڭ قىلىۋاتقان (ياخشى ـ يامان) ئەمەللىرىنى بىلىپ تۇرىدۇ، كاپىرلار (ئازابنى كۆرگەندە) كىمنىڭ ئاخىرەتلىكىنىڭ ياخشى بولغانلىقىنى بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,13,43,' (ئى مۇھەممەد!) مەككە كۇففارلىرى: «سەن پەيغەمبەر ئەمەسسەن» دەيدۇ ئېيتقىنكى، «راستلىقىمغا سىلەر بىلەن مېنىڭ ئوتتۇرامدا اﷲ نىڭ گۇۋاھلىقى ۋە اﷲ نىڭ كىتابىنى بىلىدىغانلار (يەنى ئەھلى كىتاب ئۆلىمالىرىدىن ئىمان ئېيتقانلار) نىڭ گۇۋاھلىقى يېتەرلىكتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,1,'ئەلىق لام را (بۇ قۇرئان شۇنداق) كىتابتۇركى، (ئى مۇھەممەد!) ئۇنى ساڭا كىشىلەرنى پەرۋەردىگارىنىڭ ئىزنى بىلەن قاراڭغۇلۇقتىن (يەنى كۇفرىدىن) يورۇقلۇققا (يەنى ئىمانغا) چىقىرىشىڭ ئۈچۈن، غالىب، (ھەممە تىللاردا) مەدھىيىلەنگەن اﷲ نىڭ يولىغا (باشلىشىڭ ئۈچۈن) نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,2,' ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، قاتتىق ئازابتىن كاپىرلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,3,' ئۇلار بۇ دۇنيانى ئاخىرەتتىن ئارتۇق كۆرىدۇ (كىشىلەرنى) اﷲ نىڭ يولىدىن (يەنى ئىسلام دىنىدىن) توسىدۇ، اﷲ نىڭ يولىنىڭ ئەگرى بولۇشىنى تىلەيدۇ، ئۇلار (ھەقتىن) يىراق بولغان گۇمراھلىقتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,4,' قانداقلا بىر پەيغەمبەرنى ئەۋەتمەيلى، ئۇنى بىز ئۆز قەۋمىگە (اﷲ نىڭ شەرىئىتىنى) بايان قىلىش ئۈچۈن، پەقەت ئۆز قەۋمىنىڭ تىلى بىلەن (سۆزلەيدىغان) قىلىپ ئەۋەتتۇق، اﷲ خالىغان ئادەمنى گۇمراھ قىلىدۇ، خالىغان ئادەمنى ھىدايەت قىلىدۇ (يەنى پەيغەمبەرلەرنىڭ ۋەزىپىسى پەقەت تەبلىغ قىلىش بولۇپ، ھىدايەت قىلىش ئىشى اﷲ نىڭ ئىلگىدىدۇر) اﷲ غالىبتۇر ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,5,' بىز مۇسانى مۆجىزىلىرىمىز بىلەن ھەقىقەتەن (پەيغەمبەر قىلىپ) ئەۋەتتۇق، (ئۇنىڭغا دېدۇقكى) «قەۋمىڭنى قاراڭغۇلۇقتىن (يەنى كۇفرىدىن) يورۇقلۇققا (يەنى ئىمانغا) چىقارغىن، ئۇلارغا اﷲ نىڭ نېمەتلىرىنى ئەسلەتكىن» بۇنىڭدا (بالاغا) سەۋر قىلغۇچى، (نېمەتكە) شۈكۈر قىلغۇچى ھەربىر (بەندە) ئۈچۈن ئەلۋەتتە نۇرغۇن ئىبرەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,6,' مۇسا ئۆز قەۋمىگە ئۆز ۋاقتىدا: «اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ئەسلەڭلار اﷲ ئۆز ۋاقتىدا سىلەرنى پىرئەۋن قەۋمىدىن قۇتقۇزدى، ئۇلار سىلەرنى قاتتىق قىينايتتى، ئوغۇللىرىڭلارنى بوغۇزلايتتى، خوتۇن كىشىلىرىڭلارنى (خىزمەت قىلدۇرۇشقا) قالدۇراتتى بۇنىڭدا پەرۋەردىگارىڭلار تەرىپىدىن (سىلەرگە) چوڭ سىناق بار ئىدى» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,7,' ئۆز ۋاقتىدا پەرۋەردىگارىڭلار: «نېمىتىگە شۈكۈر قىلساڭلار، (ئۇنى) تېخىمۇ زىيادە قىلىمەن ئەگەر(كۇفرانى نېمەت) قىلساڭلار، مېنىڭ ئازابىم، ئەلۋەتتە، بەكمۇ قاتتىق بولىدۇ» دەپ جاكالىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,8,' مۇسا (ئۆز قەۋمىگە): «ئەگەر سىلەر ۋە پۈتۈن يەر يۈزىدىكى كىشىلەر كاپىر بولۇپ كەتسەڭلارمۇ (اﷲ قا قىلچە زىيان يەتكۈزەلمەيسىلەر)، چۈنكى اﷲ ئەلۋەتتە (ھەممىدىن) بىھاجەتتۇر، مەدھىيىلەشكە لايىقتۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,9,' سىلەردىن ئىلگىرى ئۆتكەن نۇھ ، ئاد ۋە سەمۇد قەۋملىرىنىڭ ۋە ئۇلاردىن كېيىن كەلگەن، (سانىنى) پەقەت اﷲ بىلىدىغان ئۈممەتلەرنىڭ خەۋىرى سىلەرگە يەتمىدىمۇ؟ ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەر بىلەن كەلدى، ئۇلار قوللىرى بىلەن پەيغەمبەرلەرنىڭ ئېغىزلىرىغا ئىشارەت قىلىپ (يەنى ئۇلارنىڭ سۈكۈت قىلىشىنى كۆرسىتىپ): «بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ھەقىقەتەن ئىنكار قىلىمىز، بىز سىلە دەۋەت قىلىۋاتقان ئىشقا ھەقىقەتەن زور گۇماندىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,10,' پەيغەمبەرلىرى: «ئاسمانلارنى ۋە زېمىننى ياراتقۇچى اﷲ نىڭ (بىرلىكىدە) شەك بارمۇ؟ اﷲ گۇناھلىرىڭلارنى مەغپىرەت قىلىش ئۈچۈن، سىلەرنى (ئىمانغا) دەۋەت قىلىدۇ، سىلەرنى (جازالاشنى) مەلۇم مۇددەتكىچە (يەنى ئەجىلىڭلار يەتكۈچە) تەخىر قىلىدۇ» دېدى ئۇلار: «سىلەر پەقەت بىزگە ئوخشاش ئىنسان، ئاتا ـ بوۋىلىرىمىز ئىبادەت قىلىپ كېلىۋاتقان (بۇتلار) غا ئىبادەت قىلىشىمىزدىن توسماقچى بولىسىلەر، (راستلىقىڭلار ئۈچۈن) بىزگە ئېنىق پاكىت كۆرسىتىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,11,' پەيغەمبەرلىرى ئۇلارغا: «بىز پەقەت سىلەرگە ئوخشاش ئىنسانمىز، لېكىن اﷲ بەندىلىرىدىن خالىغان كىشىلەرگە (پەيغەمبەرلىكىنى) مەرھەمەت قىلىدۇ، اﷲ نىڭ ئىزنىسىز سىلەرگە ھېچقانداق پاكىت كۆرسىتەلمەيمىز، مۆمىنلەر (ھەممە ئىشتا) يالغۇز بىر اﷲ غىلا تەۋەككۈل قىلسۇن (يەنى ھەممە ئىشلىرىنى اﷲ قا تاپشۇرسۇن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,12,'اﷲ بىزنى(بىزنى تونۇيدىغان) يوللىرىمىزغا يېتەكلىگەن تۇرسا، نېمىشقا اﷲ قا تەۋەككۈل قىلمايلى؟ بىزگە يەتكۈزگەن ئەزىيەتلىرىڭلارغا، ئەلۋەتتە، سەۋر قىلىمىز، تەۋەككۈل قىلغۇچىلار پەقەت اﷲ قا تەۋەككۈل قىلسۇن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,13,' كاپىرلار پەيغەمبەرلىرىگە: «سىلەرنى زېمىندىن چوقۇم ھەيدەپ چىقىرىمىز، ياكى چوقۇم بىزنىڭ دىنىمىزغا قايتىشىڭلار كېرەك» دېدى ئۇلارغا پەرۋەردىگارى (مۇنداق) ۋەھيى قىلدى: «زالىملارنى چوقۇم ھالاك قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,14,' ئۇلارنى ھالاك قىلغاندىن كېيىن زېمىندا چوقۇم سىلەرنى تۇرغۇزىمىز، بۇ (يەنى غەلىبە) مېنىڭ ئالدىمدا تۇرۇشتىن (يەنى سوراققا تارتىلىشتىن) ۋە ئازابىمدىن قورققانلار ئۈچۈندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,15,' پەيغەمبەرلەر اﷲ تىن مەدەت تىلىدى، ھەر بىر ئۇچىغا چىققان تەرسا، شەپقەتسىز (يەنى اﷲ قا ئىتائەت قىلىشقا گەدەنكەشلىك قىلىپ ھەقىقەتتىن باش تارتقۇچى) ھالاك بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,16,' ئۇنىڭ ئالدىدا جەھەننەم تۇرىدۇ، جەھەننەمدە ئۇ يىرىڭ ئارىلاش سۇ بىلەن سۇغۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,17,' (ئۇنىڭ ئاچچىقلىقىدىن) ئۇنى يۇتۇملاپ ئېچىپ تەسلىكتە يۇتىدۇ، ئۆلۈم ئۇنى قورىشىۋالىدۇ لېكىن ئۇ ھەرگىز ئۆلمەيدۇ، شۇنىڭدىن كېيىن تېخىمۇ قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,18,' پەرۋەردىگارنى ئىنكار قىلغانلارنىڭ قىلغان (ياخشى) ئەمەللىرى بورانلىق كۈندە شامال ئۇچۇرۇپ كەتكەن بىر دۆۋە كۈلگە ئوخشىمايدۇ، كاپىرلار (بۇ دۇنيادا) قىلغان ياخشى ئەمەللىرى ئۈچۈن ئازراقمۇ شاۋابقا ئېرىشەلمەيدۇ، بۇ چۇڭقۇر ئېزىشتۇر (يەنى چوڭ زىياندۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,19,' ئاسمانلارنى ۋە زېمىننى اﷲ نىڭ ھەق ئاساستا ياراتقانلىقىنى (يەنى بىكار ياراتمىغانلىقىنى) كۆرمىدىڭمۇ؟ ئەگەر اﷲ خالىسا، (ئى ئىنسانلار!) سىلەرنى يوق قىلىۋېتىپ (ئورنۇڭلارغا سىلەردىن ياخشى) يېڭى ئادەملەرنى يارىتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,20,' بۇ اﷲ قا قىيىن ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,21,' ئۇلار (يەنى خالايىقلار) نىڭ ھەممىسى (قىيامەت كۈنى قەبرىلىرىدىن) چىقىپ، اﷲ قا مۇلاقات بولىدۇ، ئاجىزلىرى (يەنى ئەگەشكۈچىلىرى) مۇتەكەببىرلىرىگە (يەنى ئەگەشتۈرگۈچىلىرىگە): «شۈبھىسىزكى، بىز سىلەرنىڭ ئەگەشكۈچىلىرىڭلار ئىدۇق، سىلەر بىزگە بولۇۋاتقان اﷲ نىڭ ئازابىدىن ئازراق دەپئى قىلالامسىلەر؟» دەيدۇ ئۇلار: «اﷲ بىزنى ھىدايەت قىلغان بولسا ئىدى، بىز ئەلۋەتتە سىلەرنى ھىدايەت قىلاتتۇق، بىز مەيلى نالە ـ پەرياد قىلايلى، مەيلى سەۋر قىلايلى، بەربىر ئوخشاش، بىزگە ھېچقانداق قاچىدىغان جاي يوق» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,22,' ئىش پۈتكەندە (يەنى ھېساب تۈگەپ، جەننەتىلەر بىلەن دوزىخىلار ئايرىلىپ بولغاندا)، شەيتان: «اﷲ ھەقىقەتەن سىلەرگە (ئىتائەت قىلغۇچىنى مۇكاپاتلاش، ئاسىيلىق قىلغۇچىنى جازالاشتىن ئىبارەت) راست ۋەدىنى قىلغان ئىدى، (ۋەدىسىگە ۋاپا قىلدى) مەن سىلەرگە (ئۆلگەندىن كېيىن تىرىلىش ساۋاب، جازا دېگەنلەر يوق، دەپ يالغان) ۋەدە قىلغان ئىدىم، (ۋەدەمگە) خىلاپلىق قىلدىم، سىلەرگە مېنىڭ (كۇفرىغا، گۇناھقا زورلىغۇدەك) ھۆكۈمرانلىقىم بولغىنى يوق، مەن سىلەرنى پەقەت (گۇمراھلىققا) دەۋەت قىلدىم، سىلەر (دەۋىتىمنى) قوبۇل قىلدىڭلار، شۇنىڭ ئۈچۈن مېنى ئەيىبلىمەڭلار، ئۆزۈڭلارنى ئەيىبلەڭلار، مەن سىلەرگە ياردەم بېرىپ سىلەرنى قۇتقۇزالمايمەن، سىلەرمۇ ماڭا ياردەم بېرىپ مېنى قۇتقۇزالمايسىلەر، مەن سىلەرنىڭ بۇرۇن مېنى اﷲ قا (ئىبادەتتە) شېرىك قىلغانلىقىڭلارنى ئېتىراپ قىلمايمەن، شۈبھىسىزكى، زالىملار چوقۇم قاتتىق ئازابقا دۇچار بولىدۇ» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,23,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار، پەرۋەردىگارىنىڭ ئىزنى بىلەن، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرىدۇ، ئۇ جەننەتلەردە مەڭگۈ قالىدۇ، (پەرىشتىلەر ئۇلارنى ھۆرمەتلەپ) سىلەرگە ئامانلىق بولسۇن، دەپ سالام بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,24,' اﷲ نىڭ مۇنداق بىر تەمسىل كۆرسەتكەنلىكىنى كۆرمىدىڭمۇ كەلىمە تەييىبە (يەنى ياخشى سۆز، ئىمان كەلىمىسى) يىلتىزى يەرنىڭ ئاستىدا بولغان، شېخى ئاسمانغا تاقاشقان');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,25,' پەرۋەردىگارىنىڭ ئىزنى بىلەن ۋاقتى ـ ۋاقتىدا مېۋە بېرىپ تۇرىدىغان ئېسىل دەرەخقە ئوخشايدۇ اﷲ كىشىلەرگە ۋەز ـ نەسىھەت ئالسۇن دەپ، ئۇلارغا نۇرغۇن تەمسىللەرنى كەلتۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,26,' يامان سۆز(يەنى كۇفرى كەلىمىسى) زېمىندىن قومۇرۇپ تاشلانغان، ھېچقانداق قارارى يوق ناچار دەرەخقە ئوخشايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,27,' اﷲ مۆمىنلەرنى مۇستەھكەم ئىمان بىلەن دۇنيادا ۋە ئاخىرەتتە مەھكەم تۇرغۇزىدۇ اﷲ زالىملا ـ نى گۇمراھ قىلىدۇ، اﷲ خالىغىنىنى قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,28,' اﷲ نىڭ بەرگەن نېمىتىگە كۇفرىلىق قىلغان ۋە ئۆز قەۋمىنى (ئازدۇرۇش بىلەن) ھالاكەت مەۋقەسىگە چۈشۈرۈپ قويغانلارنى كۆرمىدىڭمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,29,' (ھالاكەت مەۋقەسى) جەھەننەمدۇركى ، ئۇلار جەھەننەمگە كىرىدۇ، ئۇ نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,30,' ئۇلار (كىشىلەرنى) اﷲ نىڭ يولىدىن (يەنى ئىسلام دىنىدىن) ئازدۇرۇش ئۈچۈن، اﷲ قا نۇرغۇن بۇتلارنى شېرىك قىلىۋالدى ئۇلارغا: «(دۇنيانىڭ نېمەتلىرىدىن) بەھرىمەن بولدۇڭلار، شۈبھىسىزكى، سىلەرنىڭ بارىدىغان جايىڭلار دوزاختۇر» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,31,' مۆمىن بەندىلىرىمگە ئېيتقىنكى، ئۇلار نامازنى ئادا قىلسۇن، ئالدى ـ ساتتىمۇ، دوستلۇقمۇ بولمايدىغان كۈن (يەنى قىيامەت كۈنى) كېلىشتىن بۇرۇن، بىز ئۇلارغا بەرگەن مال ـ مۈلۈكتىن يوشۇرۇن ۋە ئاشكارا يوسۇندا سەدىقە قىلسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,32,' اﷲ ئاسمانلارنى ۋە زېمىننى ياراتتى، بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، يامغۇر سۈيى بىلەن سىلەرگە رىزىق قىلىپ نۇرغۇن مېۋىلەرنى ئۆستۈرۈپ بەردى، اﷲ سىلەرگە ئۆز ئەمرى بويىچە دېڭىزدا قاتنايدىغان كېمىلەرنى بويسۇندۇرۇپ بەردى، سىلەرگە دەريالارنى بويسۇندۇرۇپ بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,33,' سىلەرگە ئاي بىلەن كۈننى تەرتىپلىك دەۋر قىلىپ تۇرىدىغان قىلىپ بويسۇندۇرۇپ بەردى، سىلەرگە كېچە بىلەن كۈندۈزنى بويسۇندۇرۇپ بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,34,' اﷲ سىلەرگە سورىغان نەرسەڭلارنىڭ ھەممىسىنى بەردى، سىلەر اﷲ نىڭ نېمەتلىرىنى ساناپ تۈگىتەلمەيسىلەر، كاپىر ئادەم، شەك ـ شۈبھىسىزكى، زۇلۇم قىلغۇچىدۇر، (اﷲ نىڭ نېمەتلىرىگە) كۇفرىلىق قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,35,' ئۆز ۋاقتىدا ئىبراھىم ئېيتتى: «پەرۋەردىگارىم! بۇ جاينى (يەنى مەككىنى) تىنچ (جاي) قىلىپ بەرگىن، مېنى ۋە مېنىڭ ئەۋلادلىرىمنى بۇتلارغا چوقۇنۇشتىن يىراق قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,36,' پەرۋەردىگارىم! ئۇلار (يەنى بۇتلار) نۇرغۇن كىشىلەرنى ئازدۇردى، كىمكى ماڭا ئەگەشكەن ئىكەن، ئۇ مېنىڭ دىنىمدىدۇر، كىمكى ماڭا ئاسىيلىق قىلغان ئىكەن، سەن ئەلۋەتتە (ئۇنىڭغا) مەغپىرەت قىلىشقا ۋە مەرھەمەت قىلىشقا (قادىرسەن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,37,' پەرۋەردىگارىمىز! ئەۋلادىمنىڭ بىر قىسىمىنى (يەنى بالام ئىسمائىل بىلەن ئايالىم ھاجەرنى) ناماز ئوقۇسۇن (يەنى ساڭا ئىبادەت قىلسۇن دەپ) سېنىڭ ھۆرمەتلىك ئۆيۈڭنىڭ قېشىدىكى ئېكىنسىز بىر ۋادىغا (يەنى مەككىگە) ئورۇنلاشتۇردۇم، پەرۋەردىگارىمىز! بىر قىسىم كىشىلەرنىڭ دىللىرىنى ئۇلارغا مايىل قىلغىن، ئۇلارنى شۈكۈر قىلسۇن دەپ تۈرلۈك مېۋىلەرنى ئۇلارغا رىزىق قىلىپ بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,38,' پەرۋەردىگارىمىز! سەن بىزنىڭ يوشۇرۇن ۋە ئاشكارا قىلغانلىرىمىزنى بىلىپ تۇرىسەن، ئاسمان ـ زېمىندىكى ھېچ نەرسە اﷲ قا مەخپىي ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,39,' جىمى ھەمدۇسانا ياشانغىنىمدا ماڭا ئىسمائىل بىلەن ئىسھاقنى بەرگەن اﷲ قا خاستۇر! پەرۋەردىگارىم! ھەقىقەتەن دۇئانى ئاڭلاپ تۇرغۇچىدۇر (يەنى دۇئا قىلغۇچىنىڭ دۇئاسىنى ئىجابەت قىلغۇچىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,40,' پەرۋەردىگارىم! مېنى ۋە بىر قىسىم ئەۋلادىمنى نامازنى ئادا قىلغۇچى قىلغىن، پەرۋەردىگارىمىز! دۇئايىمنى قوبۇل قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,41,' پەرۋەردىگارىمىز! ھېساب ئالىدىغان كۈندە (يەنى قىيامەت كۈنىدە) ماڭا، ئاتا ـ ئانامغا ۋە مۆمىنلەرگە مەغپىرەت قىلغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,42,' زالىملارنىڭ قىلمىشلىرىدىن اﷲ نى بىخەۋەر دەپ ئويلىمىغىن، اﷲ ئۇلارنى جازالاشنى (شۇ كۈنىنىڭ دەھشىتىدىن) كۆزلەر چەكچىيىپ قالىدىغان كۈنىگىچە (يەنى قىيامەت كۈنىگىچە) كېچىكتۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,43,' (بۇ كۈندە) ئۇلار باشلىرىنى كۆتۈرۈپ، ئالغا قاراپ چاپىدۇ، كۆزلىرى ئېچىلىپ قالىدۇ، (تېڭىرقىغانلىقتىن) دىللىرى (ئەقىل ـ ئىدراكتىن) خالىي بولۇپ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,44,' (ئى مۇھەممەد!) كىشىلەرنى (يەنى كۇففارلارنى) ئازاب كېلىدىغان كۈن (يەنى قىيامەت كۈنى) دىن ئاگاھلاندۇرغىن، (بۇ كۈندە) زالىملار: «پەرۋەردىگارىمىز! بىزگە قىسقىغىنا ۋاقىت مۆھلەت بەرگىن (يەنى دۇنياغا قايتۇرغىن)، دەۋىتىڭنى قوبۇل قىلايلى، پەيغەمبەرلەرگە ئەگىشەيلى» دەيدۇ (ئۇلارغا) سىلەر ئىلگىرى، بىز ھەرگىز يوقالمايمىز، دەپ قەسەم ئىچمىگەنمىدىڭلار؟ (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,45,' ئۆزلىرىگە زۇلۇم قىلغانلارنى (ھالاك قىلغىنىمىزدىن كېيىن) سىلەر ئۇلارنىڭ جايلىرىدا تۇردۇڭلار (ئۇلارنىڭ جايلىرىدىن ئىبرەت ئالساڭلارچۇ)، ئۇلارنى قانداق جازالىغانلىقىمىز سىلەرگە ئېنىق مەلۇم بولدى سىلەرگە نۇرغۇن مىساللارنى كەلتۈردۇق (يەنى پەيغەمبەرلەرگە چىنپۈتمىگەنلەرنىڭ ئەھۋالىنى بايان قىلدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,46,' ئۇلار ھەقىقەتەن (پەيغەمبەرلەرگە ۋە مۆمىنلەرگە) مىكىر ئىشلەتتى ئۇلارنىڭ مىكرى تاغنى ئۆرۈۋەتكۈدەك (چوڭ) بولسىمۇ، اﷲ ئۇلارنىڭ مىكرىنى بەربات قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,47,' اﷲ نى پەيغەمبەرلىرىگە قىلغان ۋەدىسىگە خىلاپلىق قىلىدۇ دەپ ئويلىمىغىن، اﷲ ھەقىقەتەن غالىبتۇر، (دوستلىرى ئۈچۈن دۈشمەنلىرىدىن) ئىنتىقام ئالغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,48,' ئۇ كۈندە زېمىنمۇ باشقا بىر زېمىنغا، ئاسمانلارمۇ باشقا بىر ئاسمانلارغا ئايلىنىدۇ، ئۇلار (يەنى پۈتۈن خالايىق قەبرىلىرىدىن چىقىپ) يېگانە ، قۇدرەتلىك اﷲ نىڭ دەرگاھىدا (يەنى مەھشەرگاھدا) ھازىر بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,49,' (ئى مۇھەممەد!) ئۇ كۈندە گۇناھكارلار (يەنى كۇففارلار) نى زەنجىرلەر بىلەن بىر ـ بىرىگە چېتىلىپ باغلانغان ھالدا كۆرۈسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,50,' ئۇلارنىڭ كۆڭلىكى قارامايدىن بولىدۇ، ئوت ئۇلارنىڭ يۈزلىرىنى ئورىۋالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,51,' اﷲ ھەر كىشىنىڭ قىلمىشىغا قاراپ شۇنداق جازا بېرىدۇ، اﷲ ئەلۋەتتە تېز ھېساب ئالغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,14,52,' كىشىلەرنىڭ ئاگاھلاندۇرۇلۇشى، اﷲ نىڭ بىر ئىكەنلىكىنى بىلىشلىرى، ئەقىل ئىگىلىرىنىڭ ۋەز ـ نەسىھەت ئېلىشلىرى ئۈچۈن، بۇ (قۇرئان) تەبلىغدۇر (يەنى كىشىلەرگە تەبلىغ قىلىش ئۈچۈن نازىل قىلىندى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,1,'ئەلىف، لام، را بۇ، كىتابنىڭ ـ روشەن قۇرئاننىڭ ئايەتلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,2,' كاپىرلار (قىيامەتنىڭ دەھشىتىنى كۆرگەندە، دۇنيادىكى چېغىمىزدا) مۇسۇلمان بولغان بولساقچۇ، دەپ ئارمان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,3,' (ئى مۇھەممەد!) ئۇلارنى (يەنى كۇففارلارنى مەيلىگە) قويۇۋەت، يەپ ـ ئىچىپ، ئويناپ ـ كۈلۈپ يۈرسۇن، ئۇلار بىھۇدە ئارزۇلار بىلەن بولىۋەرمىسۇن، ئۇلار ئۇزاققا قالماي (ئۆز قىلمىشلىرىنىڭ يامانلىقىنى) بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,4,' بىز قانداق بىر شەھەرنى ھالاك قىلمايلى، پەقەت ئۇنىڭ (ھالاك بولىدىغان) مۇئەييەن ۋاقتى بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,5,' ھەرقانداق ئۈممەتنىڭ (ھالاك بولىدىغان ۋاقتى) ئىلگىرى سۈرۈلمەيدۇ، ھەم كېچىكتۈرۈلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,6,' ئۇلار ئېيتتى: «ئى قۇرئان نازىل قىلىنغان ئادەم! سەن ھەقىقەتەن مەجنۇنسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,7,' ئەگەر سەن (مەن رەسۇلۇللاھ دېگەن سۆزۈڭدە) راستچىللاردىن بولساڭ، (پەيغەمبەرلىكىڭگە گۇۋاھلىق بېرىدىغان) پەرىشتىلەرنى ئالدىمىزغا ئېلىپ كەلسەڭچۇ؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,8,' پەرىشتىلەرنى پەقەت ھەق ئاساسىدا (يەنى ئازاب نازىل قىلىشىمىز مۇقەررەر بولغان چاغدىلا) نازىل قىلىمىز، بۇ چاغدا ئۇلارغا مۆھلەت بېرىلمەيدۇ (يەنى ئۇلارنىڭ ئازابى كېچىكتۈرۈلمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,9,' قۇرئاننى ھەقىقەتەن بىز نازىل قىلدۇق ۋە چوقۇم ئۇنى قوغدايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,10,' شۈبھىسىزكى، سېنىڭدىن ئىلگىرىكى بۇرۇنقى ھەرقايسى تائىپىلەرگە نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,11,' ئۇلار ئۆزلىرىگە پەيغەمبەر كەلسىلا ئۇنى مەسخىرە قىلىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,12,' شۇنداق مەسخىرە قىلىشنى بىز گۇناھكارلارنىڭ دىللىرىغا سالىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,13,' ئۇلار قۇرئانغا ئىشەنمەيدۇ، بۇ بۇرۇندىن ئادەت بولۇپ قالغان (يەنى بۇرۇندىن تارتىپ پەيغەمبەرلىرنى ئىنكار قىلغان قەۋملەرنى اﷲ نىڭ ھالاك قىلىشى ئادەت بولۇپ كېلىۋاتقان) ئىش');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,14,' بىز ئۇلارغا ئاسماندىن بىر ئىشىك ئېچىپ بەرگىن، ئۇلار ئۇنىڭدىن ئاسمانغا ئۆرلەپ (ئالەمى ئەرۋاھنى، پەرىشتىلەرنى كۆرگەن تەقدىردىمۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,15,' ئۇلار چوقۇم: «كۆزلىرىمىز باغلىنىپ قالدى، بەلكىم سېھىرلەنگەن قەۋم بولۇپ قالساق كېرەك» دېيىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,16,' بىز ئاسماندا بۇرۇجلارنى ياراتتۇق ۋە كۆزەتكۈچىلەر ئۈچۈن ئاسماننى (يۇلتۇزلار بىلەن) بېزىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,17,' ھەر بىر قوغلاندى شەيتاندىن ئاسماننى قوغدىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,18,' لېكىن (ئاسمان خەۋەرلىرىگە) ئوغرىلىقچە قۇلاق سالماقچى بولغان شەيتاننى روشەن ئاقار يۇلتۇز قوغلاپ يېتىدۇ (يەنى ھالاك قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,19,' يەر يۈزىنى يېيىپ كەڭ قىلدۇق، ئۇنىڭغا تاغلارنى ئورناتتۇق، يەر يۈزىدە ھەرخىل تەكشى ئۆلچەملىك ئۆسۈملۈكلەرنى ئۆستۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,20,' يەر يۈزىدە سىلەر ئۈچۈن ۋە سىلەر رىزقىنى بېرەلمەيدىغانلار (يەنى خادىملار ۋە ھايۋانلار) ئۈچۈن تۇرمۇش لازىمەتلىكلىرىنى ياراتتۇق (ئۇلارنىڭ يېمەك ـ ئىچمەكلىرىنى سىلەر ئەمەس، بىز يارىتىمىز)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,21,' قانداق نەرسە بولمىسۇن، ئۇنىڭ خەزىنىسى (يەنى خەزىنىنىڭ ئاچقۇچى) بىزنىڭ دەرگاھىمىزدىدۇر، بىز ئۇنى پەقەت مەلۇم مىقداردىلا چۈشۈرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,22,' بىز يامغۇرنى ھەيدەيدىغان شاماللارنى ئەۋەتتۇق، بىز بۇلۇتتىن يامغۇر ياغدۇرۇپ سىلەرنى سۇغاردۇق، سىلەر ھەرگىز ئۇنى ساقلاشقا قادىر ئەمەسسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,23,' بىز (ھەممىنى) تىرىلدۈرىمىز، (ھەممىنى) ئۆلتۈرىمىز ۋە (ھەممىگە) ۋارىسلىق قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,24,' بىز سىلەردىن ئىلگىرى ئۆتكەنلەرنى ھەقىقەتەن بىلىمىز ۋە كېيىن كېلىدىغانلارنىمۇ ھەقىقەتەن بىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,25,' پەرۋەردىگارىڭ ھەقىقەتەن (ھېساب ئېلىش ۋە جازالاش ئۈچۈن) ئۇلارنى يىغىدۇ اﷲ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,26,' شەك ـ شۈبھىسىزكى، بىز ئىنسان (يەنى ئادەم ئەلەيھىسسالامنى) نى قارا لايدىن ياسىلىپ شەكىلگە كىرگۈزۈلگەن (چەكسە جىرىڭلايدىغان) قۇرۇق لايدىن ياراتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,27,' جىنلار (يەنى شەيتانلار ۋە ئۇلارنىڭ باشلىقى ئىبلىس) نى ئىلگىرى تۈتۈنسىز ئوت يالقۇنىدىن ياراتقان ئىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,28,' پەرۋەردىگارىڭ ئۆز ۋاقتىدا پەرىشتىلەرگە ئېيتتى: «مەن ھەقىقەتەن قارا لايدىن ياسىلىپ شەكىلگە كىرگۈزۈلگەن (چەكسە جىرىڭلايدىغان) قۇرۇق لايدىن ئىنسان (يەنى ئادەم ئەلەيھىسسالام) نى يارىتىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,29,' مەن ئۇنى تولۇق ياراتقان (يەنى ئۇنى يارىتىپ سۈرەتكە كىرگۈزۈپ، ئەزالىرى تولۇق ۋە مۇكەممەل ئىنسان ھالىتىگە كەلتۈرگەن) ۋە ئۇنىڭغا جان كىرگۈزگەن چېغىمدا، سىلەر ئۇنىڭغا سەجدە قىلىڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,30,' پەرىشتىلەرنىڭ بىرىمۇ ئايرىلىپ قالماي ھەممىسى (ئادەم ئەلەيھىسسالامغا) سەجدە قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,31,' پەقەت ئىبلىسلا سەجدە قىلىشتىن باش تارتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,32,' اﷲ: «ئى ئىبلىس! سەن نېمىشقا سەجدە قىلمايسەن؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,33,' ئىبلىس: «قارا لايدىن ياساپ شەكىلگە كىرگۈزۈپ (چەكسە جىرىڭلايدىغان) قۇرۇق لايدىن يارىتىلغان ئىنسان مېنىڭ سەجدە قىلىشىمغا لايىق ئەمەس» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,34,' اﷲ ئېيتتى: «سەن جەننەتتىن يوقال، ھەقىقەتەن قوغلاندى بولدۇڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,35,' ساڭا قىيامەت كۈنىگىچە ھەقىقەتەن لەنەت بولسۇن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,36,' ئىبلىس: «پەرۋەردىگارىم! ماڭا خالايىقلار تىرىلدۈرۈلىدىغان كۈن (يەنى قىيامەت كۈنى) گىچە مۆھلەت بەرگىن (مېنى ھايات قالدۇرغىن)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,37,' اﷲ: «ساڭا ھەقىقەتەن مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,38,' ئىبلىس ئېيتتى: «پەرۋەردىگارىم! سەن مېنى گۇمراھ قىغانلىقىڭ ئۈچۈن، يەر يۈزىدە ئادەم بالىلىرىغا (گۇناھلارنى) ئەلۋەتتە چىرايلىق كۆرسىتىمەن، ئۇلارنىڭ ھەممىسىنى ئەلۋەتتە ئازدۇرىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,39,' ئۇلاردىن پەقەت تاللانغان بەندىلىرىڭلا بۇنىڭدىن مۇستەسنا (يەنى ئۇلارنى ئازدۇرالمايمەن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,40,' اﷲ ئېيتتى: «بۇ مەن ساقلاشقا تېگىشلىك توغرا يولدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,41,' مېنىڭ بەندىلىرىمگە (يەنى ئۇلارنى ئازدۇرۇشقا) سېنىڭ كۈچۈڭ يەتمەيدۇ، پەقەت گۇمراھلاردىن ساڭا ئەگەشكەنلەرگىلا (سېنىڭ كۈچۈڭ يېتىدۇ)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,42,' شۈبھىسىزكى، دوزاخ ئۇلار (يەنى ئىبلىس ۋە ئۇنىڭ تەۋەلىرى) نىڭ ھەممىسىگە ۋەدە قىلىنغان جايدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,43,' جەھەننەمنىڭ يەتتە دەرۋازىسى بولۇپ، ئۇلارنىڭ ھەربىرىدىن كىرىدىغان مۇئەييەن بىر بۆلۈك (ئادەم) بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,44,' تەقۋادارلار ھەقىقەتەن (ئاخىرەتتە ياپيېشىل) باغ ـ بوستانلاردىن، سۇ، ھەسەل ۋە مەي ئېقىپ تۇرىدىغان) بۇلاقلاردىن بەھرىمەن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,45,' (ئۇلارغا) جەننەتكە ئامان ـ ئېسەن، تىنچ كىرىڭلار! دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,46,' ئۇلارنىڭ دىللىرىدىكى دۈشمەنلىكنى چىقىرىپ تاشلايمىز، ئۇلار ئۆزئارا قېرىنداش بولغان ھالدا تەختلەر ئۈستىدە بىر ـ بىرىگە قارىشىپ ئولتۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,47,' جەننەتتە ئۇلارغا چارچاش بولمايدۇ، ئۇلار جەننەتتىن چىقىرىۋېتىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,48,' (ئى مۇھەممەد!) مېنىڭ (مۆمىن) بەندىلىرىمگە خەۋەر قىلغىنكى، مەن تولىمۇ مەغپىرەت قىلغۇچىمەن، ناھايىتى رەھىم قىلغۇچىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,49,' مېنىڭ ئازابىم قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,50,' ئۇلارغا ئىبراھىمنىڭ مېھمانلىرى ھەققىدىكى (ھېكايىنى) ئېيتىپ بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,51,' ئۆز ۋاقتىدا پەرىشتىلەر ئىبراھىمنىڭ يېنىغا كىرىپ سالام بەردى، ئىبراھىم: «بىز ھەقىقەتەن سىلەردىن قورقۇۋاتىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,52,' ئۇلار: «قورقمىغىن، (بىز پەرۋەردىگارىڭنىڭ ئەلچىلىرىمىز)، ھەقىقەتەن ساڭا (ئۆسۈپ چوڭ بولغىنىدا) ناھايىتى بىلىملىك (بولىدىغان) بىر ئوغۇل (يەنى ئىسھاق) بىلەن خۇش خەۋەر بېرىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,53,' ئىبراھىم: «مەن قېرىغان تۇرسام، ماڭا (بالا بىلەن) خۇش خەۋەر بەردىڭلارمۇ؟ ماڭا نېمە بىلەن خۇش خەۋەر بېرىۋاتىسىلەر؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,54,' ئۇلار: «بىز ساڭا راست خۇش خەۋەر بەردۇق، نائۈمىد بولمىغىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,55,' ئىبراھىم: «پەرۋەردىگارىڭنىڭ رەھمىتىدىن گۇمراھلاردىن باشقا كىممۇ نائۈمىد بولار؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,56,' ئىبراھىم: «ئى ئەلچىلەر! نېمە ئىش بىلەن كەلدىڭلار؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,57,' ئۇلار ئېيتتى: «بىز ھەقىقەتەن گۇناھكار بىز قەۋم (نى جازالاش) ئۈچۈن ئەۋەتىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,58,' پەقەت لۇتنىڭ تەۋەلىرىلا بۇنىڭدىن مۇستەسنا، بىز ئۇلار (يەنى لۇتنىڭ تەۋەلىرى ۋە ئۇنىڭ ئائىلىسىدىكىلەر) نىڭ ھەممىسىنى (ئازابتىن) چوقۇم قۇتۇلدۇرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,59,' پەقەت لۇتنىڭ خوتۇنىلا بۇنىڭدىن مۇستەسنا، بىز ئۇنىڭ چوقۇم باشقىلار بىلەن بىللە ئازابلىنىدىغانلىقىنى ئالدىن بېكىتتۇق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,60,' ئەلچىلەر لۇتنىڭ ئائىلىسىگە كەلگەن چاغدا');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,61,' لۇت: «سىلەر ھەقىقەتەن ناتونۇش ئادەملەرغۇ؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,62,' ئۇلار ئېيتتى: «ئۇنداق ئەمەس (بىز اﷲ نىڭ ئەلچىلىرىمىز)، بىز سېنىڭ ئالدىڭغا ئۇلار (يەنى سېنىڭ قەۋمىڭ) شەكلىنىۋاتقان ئازابنى ئېلىپ كەلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,63,' بىز ساڭا ھەقىقەتنى ئېلىپ كەلدۇق، بىز ئەلۋەتتە (سۆزىمىزدە) راستچىلمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,64,' كېچىنىڭ ئاخىرىدا ئائىلەڭدىكىلەرنى ئېلىپ يولغا چىققىن، ئۆزۈڭ ئۇلارنىڭ ئارقىسىدا ماڭغىن، سىلەردىن ھېچبىر ئادەم ئارقىسىغا قارىمىسۇن، سىلەر بۇيرۇلغان يەرگە يەتكۈچە ئالغا قاراپ مېڭىۋېرىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,65,'، سەھەردە بۇلار (يەنى لۇتنىڭ قەۋم) نىڭ يىلتىزى قۇرۇتۇلىدۇ (يەنى تەلتۆكۈس ھالاك قىلىنىدۇ) دېگەن ھۆكۈمنى لۇتقا ۋەھىي قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,66,' (سەدۇم) شەھرىنىڭ ئاھالىسى (يەنى لۇتنىڭ قەۋمى) خۇشال بولۇشۇپ يېتىپ كېلىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,67,' لۇت ئېيتتى: «بۇلار مېنىڭ مېھمىنىم، (ئۇلارغا چېقىلىپ قويۇپ ئۇلارنىڭ ئالدىدا) مېنى رەسۋا قىلماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,68,' اﷲ تىن قورقۇڭلار (مېھمىنىمنى خارلاش ئارقىلىق) مېنى خارلىماڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,69,' ئۇلار: «بىز سېنى كەلگەن مۇساپىرلارنى ھىمايە قىلىشتىن توسمىغانمىدۇق؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,70,' لۇت: «بۇلار مېنىڭ قىزلىرىم (يەنى قەۋمىمنىڭ قىزلىرى) دۇر، ئەگەر (قازائى شەھۋەت) قىلماقچى بولساڭلار (ئۇلارنى نىكاھلاپ ئېلىڭلار)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,71,' (ئى مۇھەممەد!) سېنىڭ ھاياتىڭ بىلەن قەسەمكى، ئۇلار ئەلۋەتتە گۇمراھلىقلىرىدا تېڭىرقاپ يۈرۈشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,72,' كۈن چىقار ۋاقتىدا ئۇلارغا قاتتىق چۇقان يۈزلەندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,73,' بىز ئۇ شەھەرنى ئاستىن ـ ئۈستۈن قىلىۋەتتۇق (يەنى كۆمتۈرۈۋەتتۇق) ۋە ئۇلارنىڭ ئۈستىىگە (يامغۇردەك) ساپال تاش ياغدۇردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,74,' ئۇنىڭدا (يەنى ئۇلارغا نازىل بولغان ئازابتا) كۆزەتكۈچىلەر ئۈچۈن ھەقىقەتەن نۇرغۇن ئىبرەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,75,' ئۇ شەھەر (قۇرەيشلەر شامغا بارىدىغان) يول ئۈستىدە ھەقىقەتەن ھېلىمۇ مەۋجۇت تۇرماقتا');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,76,' بۇنىڭدا مۆمىنلەر ئۈچۈن ھەقىقەتەن ئىبرەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,77,' ئەيكەلىكلەر، شۈبھىسىزكى، زالىم ئىدى (يەنى كۇفرىدا ھەددىدىن ئاشقانلار ئىدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,78,' (شۇئەيبنىڭ پەيغەمبەرلىكىنى ئىنكار قىلغانلىقلىرى ئۈچۈن) ئۇلارنى ھالاك قىلدۇق، ئۇلار (يەنى بۇ ئىككى شەھەر) نىڭ ھەر ئىككىلىسى، شۈبھىسىزكى، ئاشكارا يول ئۈستىدىدۇر (ئى مەككە ئاھالىسى! ئۇلاردىن ئىبرەت ئالمامسىلەر؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,79,' ھىجرىلىكلەر ھەقىقەتەن پەيغەمبەرلەرنى ئىنكار قىلدى (يەنى ئۇلارنىڭ سالىھ ئەلەيھىسسالامنى ئىنكار قىلغانلىقى باشقا پەيغەمبەرلەرنىمۇ ئىنكار قىلغانلىقىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,80,' ئۇلارغا مۆجىزىلىرىمىزنى كەلتۈردۇق (يەنى تاشتىن چىشى تۆگە چىقىرىشتەك قۇدرىتىمىزگە دالالەت قىلىدىغان مۆجىزىلەرنى ئۇلارغا كۆرسەتتۇق)، ئۇلار بۇ (مۆجىزە) لەردىن يۈز ئۆرۈدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,81,' ئۇلار (ئازابتىن) ئەمىن بولغان ھالدا تاغلارنى تېشىپ (بۇ بىزنى اﷲ نىڭ ئازابىدىن ساقلايدۇ دەپ) ئۆي سالاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,82,' لېكىن قاتتىق چۇقان سەھەردىلا ئۇلارنى ھالاك قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,83,' ئۇلارنىڭ قىلغان ئەمگەكلىرى ئۆزلىرىگە ئەسقاتمىدى (يەنى ئۇلارنىڭ سالغان بىنالىرى ئۇلارنى اﷲ نىڭ ئازابىدىن ساقلاپ قالالمىدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,84,' بىز ئاسمانلارنى، زېمىننى ۋە ئۇلاردىكى نەرسىلەرنى پەقەت ھەق ئاساسىدىلا ياراتتۇق، قىيامەتنىڭ بولۇشى چوقۇمدۇر، (ئى مۇھەممەد! نادانلار بىلەن تەڭ بولمىغىن، ئۇلارغا) چىرايلىق مۇئامىلىدە بولغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,85,' پەرۋەردىگارىڭ ھەقىقەتەن ھەممىنى ياراتقۇچىدۇر، (بەندىلەرنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,86,' (نامازدا) تەكرارلىنىپ تۇرىدىغان يەتتە ئايەتنى (يەنى سۈرە فاتىھەنى) ۋە ئۇلۇغ قۇرئاننى ھەقىقەتەن ساڭا ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,87,' بەزى كاپىرلارنىڭ بىز بەھرىمەن قىلغان نەرسىلىرىگە كۆز سالمىغىن، ئۇلار (نىڭ ئىمان ئېيتمىغانلىقىدىن) قايغۇرمىغىن، مۆمىنلەرگە كەمتەر بولغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,88,' «مەن ھەقىقەتەن (سىلەرنى اﷲ نىڭ ئازابىنىڭ نازىل بولۇشىدىن) ئاشكارا ئاگاھلاندۇرغۇچىمەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,89,' پارچىلىۋەتكۈچىلەرگە (يەنى يەھۇدىيلار ۋە ناسارالارغا تەۋرات بىلەن ئىنجىلنى) نازىل قىلغاندەك (ساڭا سۈرە فاتىھە بىلەن ئۇلۇغ قۇرئاننى نازىل قىلدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,90,' ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) قۇرئاننى پارچە ـ پەرچە قىلىۋەتتى (يەنى قۇرئاننىڭ بەزىسىگە ئىشىنىپ، بەزىسىنى ئىنكار قىلدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,91,' پەرۋەردىگارىڭنىڭ نامى بىلەن قەسەمكى، ئۇلار (يەنى خالايىق) نىڭ ھەممىسىنىڭ (بۇ دۇنيادا) قىلغان ئىشلىرىنى چوقۇم سورايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,92,' ساڭا بۇيرۇلغاننى (يەنى پەرۋەردىگارىڭنىڭ ئەمرىنى) ئاشكارا ئوتتۇرىغا قويغىن، مۇشرىكلار (نىڭ مەسخىرەلىرىگە) پەرۋا قىلمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,93,' سېنى مەسخىرە قىلغۇچىلارغا بىز چوقۇم تېتىيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,94,' ئۇلار باشقا مەبۇدلارنى اﷲ قا شېرىك قىلىدۇ، ئۇلار ئۇزاققا قالماي (ئىشنىڭ ئاقىۋىتىنى) بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,95,' بىز ئۇلارنىڭ سۆزلىرىدىن يۈرىكىڭنىڭ سېقىلىۋاتقانلىقىنى ئوبدان بىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,96,' پەرۋەردىگارىڭغا ھەمدۇ ۋە تەسبىھ ئېيتقىن، ھەم سەجدە قىلغۇچىلاردىن بولغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,15,97,' سەن ئۆزۈڭگە ئۆلۈم كەلگەنگە (يەنى ئەجىلىڭ يەتكەنگە) قەدەر پەرۋەردىگارىڭغا ئىبادەت قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,1,'اﷲ نىڭ پەرمانى (يەنى قىيامەت) چوقۇم كېلىدۇ، ئۇنىڭغا ئالدىراپ كەتمەڭلار، اﷲ ئۇلار (يەنى مۇشرىكلار) نىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر ۋە يۈكسەكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,2,' اﷲ ئۆز ئىرادىسى بويىچە خالىغان بەندىلىرىگە پەرىشتىلەرنى ۋەھيى بىلەن نازىل قىلىپ (مۇنداق دەيدۇ): «(ئىنسانلارنى) ئاگاھلاندۇرۇڭلاركى، مەندىن باشقا ھېچ ئىلاھ يوقتۇر، مەندىن (يەنى ئازابىمدىن) قورقۇڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,3,' اﷲ ئاسمانلارنى ۋە زېمىننى ھەق ياراتتى، اﷲ ئۇلار (يەنى مۇشرىكلار) نىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن يۈكسەكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,4,' ئۇ ئىنساننى ئابىمەنىيدىن ياراتتى، ناگاھان ئۇ (اﷲ قا) ئاشكارا خۇسۇمەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,5,' اﷲ چارۋا ماللارنى سىلەرنىڭ مەنپەئەتلىنىشىڭلار ئۈچۈن ياراتتى، ئۇلار (نىڭ يۇڭى ۋە تېرىسى) بىلەن ئىسسىنىسىلەر، ئۇلار (نىڭ نەسلى، سۈتى، سۆڭىكى ۋە قىغى) دىن پايدىلىنىسىلەر ۋە ئۇلار (نىڭ گۆشلىرى) نى يەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,6,' ئۇلارنى كەچتە ئوتلاقتىن قايتۇرۇپ كەلگەن ۋە ئەتىگەندە ئوتلاققا ئېلىپ چىققان ۋاقتىڭلاردا، چارۋا ماللار سىلەرگە زىننەت بولۇپ (خۇشاللىق بېغىشلايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,7,' ئۇلار يۈك ـ تاقلىرىڭلارنى سىلەر جاپا ـ مۇشەققەت بىلەن ئاران يېتىپ بارالايدىغان جايلارغا كۆتۈرۈپ بارىدۇ، سىلەرنىڭ پەرۋەردىگارىڭلار سىلەرگە ھەقىقەتەن شەپقەتلىكتۇر ۋە مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,8,' اﷲ ئاتنى، خېچىرنى ۋە ئېشەكنى مىنىشىڭلار ئۈچۈن ۋە زىننەت ئۈچۈن ياراتتى، اﷲ يەنە سىلەر بىلمەيدىغان نەرسىلەرنىمۇ يارىتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,9,' توغرا يول كۆرسىتىشنى اﷲ ئۆز ئۈستىگە ئالغان بەزى يوللار ئەگرى بولىدۇ، مۇبادا اﷲ خالىسا ئىدى، سىلەرنىڭ ھەممىڭلارنى ئەلۋەتتە توغرا يولغا سالغان بولاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,10,' اﷲ سىلەرگە بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، ئۇنىڭدىن ئىچىسىلەر ۋە شۇ يامغۇر ئاقىلىق ئۆسكەن دەل ـ دەرەخلەر بىلەن چارۋاڭلارنى باقىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,11,' اﷲ سىلەرگە شۇ يامغۇر بىلەن زىرائەتلەرنى ئۆستۈرۈپ بېرىدۇ، زەيتۇن، خورما، ئۈزۈم ۋە تۈرلۈك مېۋىلەرنى ئۆستۈرۈپ بېرىدۇ، چوڭقۇر پىكىر قىلىدىغان قەۋم ئۈچۈن بۇنىڭدا ھەقىقەتەن ( اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,12,' اﷲ سىلەرگە كېچىنى، كۈندۈزنى، كۈننى، ئاينى بويسۇندۇرۇپ بەردى، يۇلتۇزلارمۇ اﷲ نىڭ ئەمرى بىلەن بويسۇندۇرۇلغاندۇر؛ چۈشىنىدىغان قەۋم ئۈچۈن بۇنىڭدا نۇرغۇن دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,13,' اﷲ يەنە يەر يۈزىدە سىلەر ئۈچۈن ياراتقان رەڭگارەڭ نەرسىلەرنى (سىلەرگە بويسۇندۇرۇپ بەردى)، ۋەز ـ نەسىھەت ئالىدىغان قەۋم ئۈچۈن بۇنىڭدا ئەلۋەتتە ئىبرەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,14,' اﷲ سىلەرنى دېڭىزنىڭ يېڭى گوشلىرى (يەنى بېلىقلىرى) نى يېسۇن، تاقايدىغان زىننەت بۇيۇملىرى (يەنى ئۈنچە ـ مارجانلار) نى چىقارسۇن دەپ، سىلەرگە دېڭىزنى بويسۇندۇرۇپ بەردى اﷲ نىڭ نېمەتلىرىنى تەلەپ قىلىشىڭلار ۋە ئۇنىڭغا شۈكۈر قىلىشىڭلار ئۈچۈن، ( اﷲ نىڭ بويسۇندۇرۇشى بىلەن يېمەك ـ ئىچمەكلەر ۋە يۈك ـ تاقلار قاچىلانغان) كېمىلەرنىڭ دېڭىزدا دولقۇن يېرىپ كېتىۋاتقانلىقىنى كۆرۈسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,15,' زېمىننىڭ سىلەرنى تەۋرىتىۋەتمەسلىكى ئۈچۈن، اﷲ ئۇنىڭدا تاغلارنى بەرپا قىلدى، (زىرائەتلىرىڭلارنى ، چارۋا ماللىرىڭلارنى سۇغىرىشىڭلار ئۈچۈن) ئېرىق ـ ئۆستەڭلەرنى، ئېزىپ قالماسلىقىڭلار ئۈچۈن يوللارنى پەيدا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,16,' (يوللارغا) بەلگىلەرنى قىلدى ئۇلار (كېچىسى قۇرۇقلۇقتا ۋە دېڭىزلاردا) يۇلتۇزلار بىلەن يول تاپىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,17,' (پۇتۇن كائىناتنى) ياراتقان اﷲ بىلەن (ھېچ نەرسىنى) يارىتالمايدىغان (بۇتلار) ئوخشاش بولامدۇ؟ ئويلىمامسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,18,' ئەگەر اﷲ نىڭ (سىلەرگە بەرگەن) نېمىتىنى سانىساڭلار ، ئۇنىڭ ھېسابىنى ئېلىپ بولالمايسىلەر شۈبھىسىزكى، اﷲ سىلەر (نىڭ بۇ جەھەتتىكى يېتەرسىزلىكىڭلار) نى ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,19,' اﷲ سىلەر يوشۇرغان ۋە ئاشكارا قىلغاننىڭ ھەممىسىنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,20,' ئۇلارنىڭ اﷲ نى قويۇپ ئىبادەت قىلغانلىرى (يەنى بۇتلىرى) ھېچ شەيئىنى يارىتالمايدۇ، بەلكى ئۇلارنىڭ ئۆزلىرى يارىتىلغاندۇر (يەنى ئىنسانلار تەرىپىدىن ياسالغاندۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,21,' ئۇلار (يەنى بۇتلار) تىرىك ئە مەس، ئۆلۈكتۈر ، ئۇلار (چوقۇنغۇچىلىرىنىڭ) قاچان قايتا تىرىلدۈرۈلىدىغانلىقىنى ئۇقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,22,' سىلەرنى (ئىبادەت قىلىشقا لايىق) ئىلاھىڭلار بىردۇر، ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ دىللىرى ( اﷲ نىڭ بىرلىكىنى) ئىنكار قىلىدۇ، ئۇلار چوڭچىلىق قىلىپ، (ھەقنى قوبۇل قىلىشتىن) باش تارتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,23,' اﷲ ئۇلارنىڭ يوشۇرغانلىرىنى ۋە ئاشكارا قىلغانلىرىنى راستلا بىلىپ تۇرىدۇ، اﷲ تەكەببۇرلۇق قىلغۇچىلارنى ھەقىقەتەن دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,24,' ئۇلارغا (يەنى مۇشرىكلارغا): «پەرۋەردىگارىڭلار (پەيغەمبىرىگە) نېمىلەرنى نازىل قىلدى؟» دېيىلسە، ئۇلار (مەسخىرە قىلىپ): «قەدىمكىلەرنىڭ ئەپسانىلىرىنى (نازىل قىلىدى)» دېيىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,25,' ئۇلار قىيامەت كۈنى ئۆزلىرىنىڭ گۇناھلىرىغا تولۇق جاۋابكار بولۇشلىرى ۋە ئۆزلىرى تەرىپىدىن بىلمەستىن ئازدۇرۇلغانلارنىڭ بىر قىسىم گۇناھلىرىغا ئۆزلىرى جاۋابكار بولۇشلىرى ئۈچۈن (يۇقىرىقى بوھتان سوزلەرنى قىلدى)، ئۇلارنىڭ جاۋابكار بولىدىغىنى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,26,' ئۇلار (يەنى مەككە كاپىرلىرى) دىن ئىلگىرى ئۆتكەن كاپىرلار (ئۆز پەيغەمبەرلىرىگە) ھىيلە ـ مىكىر ئىشلىتىپ ( اﷲ نىڭ نۇرىنى ئۆچۈرمەكچى بولدى) اﷲ ئۇلارنىڭ (ھىيلە ـ مىكىر) بىناسىنى تەلتۆكۈس گۇمران قىلدى، ئۇلارنىڭ ئۈستىگە (بۇ بىنانىڭ) ئۆگزىسى ئۆرۈلۈپ چۈشتى، ئۇلارغا ئۆزلىرى ئويلىمىغان يەردىن ئازاب كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,27,' ئاندىن كېيىن اﷲ ئۇلارنى قىيامەت كۈنى رەسۋا قىلىدۇ، (ئۇلارغا كايىپ) «مېنىڭ شېرىكلىرىم قېنى قەيەردە؟ سىلەر ئۇلار توغرىسىدا (پەيغەمبەرلەر بىلەن) مۇنازىرىلىشەتتىڭلارغۇ؟» دەيدۇ ئىلىم بېرىگەنلەر (يەنى ئۇلارنى ئىمانغا دەۋەت قىلغان پەيغەمبەرلەر ۋە ئۆلىمالار): «بۈگۈن رەسۋالىق ۋە ئازاب ھەقىقەتەن كاپىرلارغا تېگىشلىكتۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,28,' ئۇلار (كۇفرىنى ئىختىيار قىلىپ) ئۆزلىرىگە زىيان سالغان شارائىتتا، پەرىشتىلەر ئۇلارنىڭ جانلىرىنى ئالىدۇ ئۇلار بويسۇنغانلىقلىرىنى بىلدۈرۈپ: «بىز ھېچقانداق يامان ئىش قىلمىدۇق» (دەيدۇ) ئۇنداق ئەمەس، اﷲ سىلەنىڭ قىلمىشىڭلارنى ھەقىقەتەن تولۇق بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,29,' سىلەر (ئىچىدە) مەڭگۈ قالىدىغان جەھەننەمنىڭ دەرۋازىلىرىدىن كىرىڭلار، چوڭچىلىق قىلىپ، (اﷲ قا ئىتائەت قىلىشتىن باش تارتقۇچىلارنىڭ بارىدىغان جايى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,30,' تەقۋادارلارغا: «پەرۋەردىگارىڭلار (پەيغەمبىرىگە) نېمىلەرنى نازىل قىلدى؟» دېيىلسە، ئۇلار: «ياخشى سۆزلەرنى نازىل قىلدى» دېيىشىدۇ ياخشى ئىش قىلغۇچىلار بۇ دۇنيادا مۇكاپاتلىنىدۇ، ئاخىرەت يۇرتى (ئۇلار ئۈچۈن تېخىمۇ) ياخشىدۇر تەقۋادارلارنىڭ يۇرتى راستلا نېمىدېگەن گۈزەل!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,31,' ئۇلار ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان ئەدنى جەننەتلىرىگە كىرىدۇ، ئۇ يەردە ئۇلارنىڭ كۆڭلى خالىغان نەرسىلەر تېپىلىدۇ، اﷲ تەقۋادارلارنى ئەنە شۇنداق مۇكاپاتلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,32,' ئۇلار (مۇشرىكلىك ۋە گۇناھلاردىن) پاك بولغان شارائىتتا پەرىشتىلەر ئۇلارنى قەبزى روھ قىلىدۇ، پەرىشتىلەر ئۇلارغا: «سالام سىلەرگە! قىلغان ئەمەلىڭلارنى خاسىيىتىدىن جەننەتكە كىرىڭلار» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,33,' كاپىرلار پەقەت (ئۆزلىرىنىڭ جېنىنى ئېلىش ئۈچۈن) پەرىشتىلەرنىڭ چۈشۈشىنى ياكى پەرۋەردىگارىڭنىڭ ئەمرىنىڭ (يەنى ئازابىنىڭ) چۈشۈشىنىلا كۈتىدۇ، ئۇلاردىن بۇرۇن ئۆتكەن (كاپىر) لارمۇ ئەنە شۇنداق قىلغان ئىدى اﷲ ئۇلارغا زۇلۇم قىلغىنى يوق، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,34,' ئۇلار قىلمىشلىرىنىڭ جازاسىنى تارتتى، ئۇلارغا مەسخىرە قىلغانلىقلىرىنىڭ جازاسى (يەنى قاتتىق ئازابى) چۈشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,35,' مۇشرىكلار: «مۇبادا اﷲ خالىغان بولسا، بىز ۋە بىزنىڭ ئاتا بوۋىلىرىمىز اﷲ نى قويۇپ ھىچ نەرسىگە ئىبادەت قىلمىغان بولاتتۇق، اﷲ ھارام قىلمىغان (يەنى اﷲ نىڭ ھۆكمىسىز) ھىچ نەرسىنى ھارام قىلمىغان بولاتتۇق» دېدى ئۇلاردىن ئىلگىرىكىلەرمۇ ئەنە شۇنداق قىلغان ئىدى پەيغەمبەرلەرنىڭ مەسئۇلىيىتى پەقەت روشەن تەبلىغ قىلىشتىنلا ئىبارەت (ھىدايەت قىلىش بولسا اﷲ نىڭ ئىشىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,36,' بىز ھەقىقەتەن ھەر بىر ئۈممەتكە: «اﷲ قا ئىبادەت قىلىڭلار، شەيتاندىن (يەنى شەيتانغا، بۇتلارغا، كاھىنلارغا ئوخشاش ھەرقانداق مەبۇدقا ئىبادەت قىلىشتىن) يىراق بولۇڭلار» دەپ پەيغەمبەر ئەۋەتتۇق، ئۇلارنىڭ ئارىسىدا اﷲ ھىدايەت قىلغانلىرىمۇ بار، گۇمراھلىققا تېگىشلىك بولغانلىرىمۇ بار يەر يۈزىدە سەير قىلىپ يۈرۈپ (پەيغەمبەرلەرنى) ئىنكار قىلغانلارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆزىتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,37,' سەن ئۇلارنىڭ ھىدايەت تىپىشىغا ھېرىس بولساڭمۇ، اﷲ ئۆزى گۇمراھ قىلغان كىشىنى ھىدايەت قىلمايدۇ، ئۇلارنى (اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچقانداق مەدەتكار يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,38,' ئۇلار: «اﷲ ئۆلۈكنى تىرىلدۈرمەيدۇ » دەپ قاتتىق قەسەم ئىچىشتى، ئۇنداق ئەمەس (يەنى ئۆلۈكنى تىرىلدۈرىدۇ)، اﷲ ۋەدىسىنى چوقۇم ئىشقا ئاشۇرىدۇ، لېكىن ئىنسانلارنىڭ تولىسى ( اﷲ نىڭ قۇدرىتىنى) بىلمەيدۇ (شۇڭلاشقا ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,39,' اﷲ ئۇلار ئىختىلاپ قىلىشقان ھەق بىلەن ناھەقنى ئۇلارغا ئايرىپ بېرىش ۋە كاپىرلارغا ئۇلارنىڭ يالغانچىلىقلىرىنى بىلدۈرۈپ قويۇش ئۈچۈن (ئۇلارنى قايتىدىن تىرىلدۈرىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,40,' بىز بىرەر شەيئىنى ۋۇجۇدقا كەلتۈرمەكچى بولساق، ئۇنىڭغا: «ۋۇجۇدقا كەل» دەيمىز ـ دە، ئۇ ۋۇجۇدقا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,41,' زۇلۇمغا ئۇچرىغاندىن كېيىن اﷲ يولىدا ھىجرەت قىلغانلارنى دۇنيادا چوقۇم ياخشى جايغا ئورۇنلاشتۇرىمىز، ئۇلار بىلىدىغان بولسا، ئۇلارغا ئاخىرەتنىڭ ساۋابى تېخىمۇ چوڭدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,42,' ئۇلار (ئېغىرچىلىقلارغا) سەۋر قىلدى، ئۇلار (ھەممە ئىشتا) پەرۋەردىگارىغا تەۋەككۈل قىلىدۇ (يەنى يۆلىنىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,43,' سەندىن ئىلگىرى بىز پەقەت ئەرلەرگىلا ۋەھيى نازىل قىلىپ، ئۇلارنى پەيغەمبەر قىلىپ ئەۋەتتۇق، (ئى قۇرەيش جامائەسى!) بۇنى بىلمىسەڭلار، ئىلىم ئەھلىلىرى (يەنى تەۋرات ۋە ئىنجىلنى بىلگۈچىلەر) دىن سوراڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,44,' (ئۇلارنى ئۇلارنىڭ راستلىقىنى كۆرسىتىپ بېرىدىغان) دەلىللەر ۋە مۇقەددەس كىتابلار بىلەن (ئەۋەتتۇق)، بىز ساڭا قۇرئاننى ئىنسانلارغا چۈشۈرۈلگەن شەرىئەتنى بايان قىلىپ بەرسۇن، ئۇلار (بۇ قۇرئاننى) پىكىر قىلىپ (ئۇنىڭدىن ۋەز ـ نەسىھەت ئالسۇن) دەپ نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,45,' ھىيلە ـ مىكىر ئىشلىتىپ يامان ئىش قىلغانلار اﷲ نىڭ ئۆزلىرىنى يەرگە يۇتقۇزۇۋىېتىشىدىن ياكى ئۆزلىرىگە ئويلىمىغان يەردىن ئازاب كېلىدىن قورقمامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,46,' ياكى ئۆزلىرىنى سەپەر ئۇستىدىكى چاغلىرىدا جازاغا يۇلۇقتۇرۇشىدىن قورقمامدۇ؟ (بۇ چاغدا) ئۇلار قېچىپ قۇتۇلالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,47,' ياكى (اﷲ نىڭ) ئۆزلىرىنى قەدەممۇقەدەم جازالىشىدىن قورقمامدۇ؟ سىلەرنىڭ پەرۋەردىگارىڭلار ئەلۋەتتە مەرھەمەتلىكتۇر، مېھرىباندۇر (شۇڭا سىلەرنى ئالدىراپ جازالىمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,48,' ئۇلار اﷲ ياراتقان شەيئىلەرنىڭ سايىلىرىنىڭ بويسۇنغان ھالدا اﷲ قا سەجدە قىلىش يۈزىسىدىن ئوڭ ـ سولغا مايىل بولۇپ تۇرغانلىقىنى كۆرمىدىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,49,' ئاسمانلاردىكى ۋە زېمىندىكى ھايۋاناتلار، پەرىشتىلەر اﷲ قا سەجدە قىلىدۇ، ئۇلار ( اﷲ قا سەجدە قىلىشتىن) چوڭچىلىق قىلىپ باش تارتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,50,' ئۇلار ئۆز ئۈستىدىكى پەرۋەردىگارىدىن قورقىدۇ، ئۆزلىرىگە بۇيرۇلغاننى قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,51,' اﷲ: «ئىككى ئىلاھقا ئىبادەت قىلماڭلار، ئۇ پەقەت بىرلا ئىلاھتۇر، مەندىنلا قورقۇڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,52,' ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، ئىبادەت ھەمىشە اﷲ قا خاستۇر، اﷲ تىن بۆلەك غەيرىدىن قورقامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,53,' سىلەر بەھرىمەن بولۇۋاتقان نېمەتلەرنىڭ ھەممىسىنى اﷲ بەرگەن، سىلەرگە بىر ئېغىرچىلىق يەتسە، اﷲ قا يالۋۇرۇپ دۇئا قىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,54,' اﷲ سىلەردىن ئېغىرچىلىقنى كۆتۈرۈۋەتسە ، ئاراڭلاردىن بىر بۆلۈك ئادەم دەرھال ئۆز پەرۋەردىگارىغا شېرىك كەلتۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,55,' ئۇلار ئۆزلىرىگە بەرگەن (بېشىدىكى ئېغىرچىلىقنى كۆتۈرۈۋەتكەنلىكىمىزدىن ئىبارەت) نېمىتىمىزگە ناشۈكۈرلۈك قىلىدۇ، (سىلەر ئەجىلىڭلار يەتكۈچە دۇنيادىن) بەھرىمەن بولۇڭلار، (ئىشىڭلارنىڭ ئاقىۋىتىنى) ئۇزاققا قالماي بىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,56,' ئۇلار بىز ئۆزلىرىگە بەرگەن مال ـ مۈلۈكتىن بىلىمسىز (بۇتلارغا) نېسىۋە قالدۇرىدۇ، (ئى مۇشرىكلار!) اﷲ نىڭ نامى بىلەن قەسەمكى، سىلەر ئۆزۈڭلار ئويدۇرۇپ چىقارغان نەرسەڭلار ئۈچۈن چوقۇم سوراققا تارتىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,57,' ئۇلار اﷲ قا قىزلارنى نىسبەت بېرىدۇ، اﷲ (بۇنداق بوھتاندىن) پاكتۇر، ئۇلار ئۆزى ئارزۇ قىلىدىغانلىرىنى ئۆزلىرىگە نىسبەت بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,58,' ئۇلارنىڭ بىرەرسىگە (خوتۇنىنىڭ) قىز تۇغقانلىق خوش خەۋىرى يەتكۈزۈلسە، چىرايى ئۆزگىرىپ، غەزەپناك بولۇپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,59,' يەتكۈزۈلگەن يامان خەۋەردىن قورقۇپ، ئۆز قەۋمىگە كۆرۈنمەي يوشۇرۇنۇۋالىدۇ ئاندىن ئۇ نومۇسقا چىداپ قىزنى ساقلاپ قالامدۇ؟ ياكى ئۇنى توپا ئاستىغا (تىرىك) كۆمەمدۇ؟ (شۇ ھەقتە ئويلىنىدۇ) ئۇلارنىڭ ھۆكمى (يەنى ئوغۇللارنى ئۆزلىرىگە نىسبەت بېرىپ، قىزلارنى اﷲ قا نىسبەت بېرىشى) ھەقىقەتەن نېمىدېگەن قەبىھ!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,60,' ئاخىرەتكە ئىشەنمەيدىغانلار (ئوغۇلنى ياخشى كۆرۈپ، قىزنى يامان كۆرۈش، خوتۇنى ئوغۇل تۇغسا خۇشال بولۇپ، قىز تۇغسا ئاچچىقى كېلىش، ئار ـ نومۇستىن ۋە كەمبەغەللىكتىن قورقۇپ، قىزلارنى تىرىك كۈمۈشكە ئوخشاش) يامان سۈپەتلەرگە ئىگە، اﷲ بولسا ئەڭ ئالىي خىسلەتلەرگە ئىگە، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,61,' ئەگەر اﷲ ئىنسانلارنى ئۇلارنىڭ زۇلمى تۈپەيلىدىن (چاپسان) جازالايدىغان بولسا، يەر يۈزىدە قىمىرلاپ تۇرىدىغان ھېچبىر جان ئىگىسىنى قويمىغان بولاتتى، لېكىن اﷲ ئۇلارنى مۇئەييەن ۋاقىتقىچە تەخىر قىلىدۇ، ئۇلارنىڭ (ھالاك بولۇش) ۋاقتى كەلگەندە قىلچە ۋاقىتمۇ كېچىكتۈرۈلمەيدۇ، (ۋاقتى كەلمىسە) قىلچە ۋاقىتمۇ ئىلگىرى سۈرۈلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,62,' ئۇلار ئۆزلىرى يامان كۆرۈدىغاننى اﷲ قا مەنسۇپ قىلىدۇ، ئۇلار ئاقىۋىتىنىڭ ياخشى بولىدىغانلىقى بىلەن جۆيلۈيدۇ، شۈبھىسىزكى، ئۇلار دوزاخ ئازابىغا دۇچار بولىدۇ، ئۇلار (دوزاختا) تاشلىنىپ قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,63,' (ئى مۇھەممەد!) اﷲ نامى بىلەن قەسەمكى، سەندىن ئىلگىرى ئۆتكەن ئۈممەتلەرگە ھەقىقەتەن (پەيغەمبەرلەر) ئەۋەتتۇق، شەيتان ئۇلارنىڭ ئەمەللىرىنى ئۆزلىرىگە چىرايلىق كۆرسەتتى، شەيتان بۈگۈن ئۇلارنىڭ ياردەمچىسىدۇر ئۇلار ئاخىرەتتە قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,64,' (ئى مۇھەممەد!) بىز كىتابنى (يەنى قۇرئاننى) پەقەت سېنىڭ كىشىلەرگە ئۇلار (دىنىدىكى) دەتالاش قىلىشقان نەرسىلەرنى بايان قىلىپ بېرىشىڭ ئۈچۈن، ئىمان ئېيتىدىغان قەۋمگە ھىدايەت ۋە رەھمەت بولسۇن ئۈچۈنلا نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,65,' اﷲ بۇلۇتتىن يامغۇر ياغدۇرۇپ، شۇنىڭ بىلەن قاغجىراپ قالغان زېمىندا (تۈرلۈك زىرائەتلەرنى) ئۈندۈردى، بۇنىڭدا (ۋەز ـ نەسىھەت) ئاڭلايدىغان قەۋم ئۈچۈن، ئەلۋەتتە، (اﷲ نىڭ قۇدرىتىنىڭ چوڭلۇقىنى كۆرسىتىدىغان) دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,66,' چاھار پايلاردا سىلەر ئۈچۈن ھەقىقەتەن بىر ئىبرەت بار سىلەرنى چاھارپايلارنىڭ قارنىدىكى ماياق ـ تېزەكتىن ۋە قاندىن ئايرىلىپ چىققان پاكىز ۋە تەملىك سۈت بىلەن سۇغىرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,67,' (سىلەر ئۈچۈن يەنە بۇنداق ئىبرەتمۇ باركى) خورمىدىن، ئۈزۈمدىن شارابلار ۋە پاكىز يېمەكلىكلەر ياسايسىلەر، ئەقىلنى ئىشلىتىدىغان قەۋم ئۈچۈن بۇنىڭدا ھەقىقەتەن اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان دەلىل بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,68,' پەرۋەردىگارىڭ ھەسەل ھەرىسىگە ئىلھام بىلەن بىلدۈردى: «تاغلارغا، دەرەخلەرگە ۋە (كىشىلەرنىڭ ھەسەل ھەرىلىرى ئۈچۈن) ياسىغان ئۆيلىرىگە ئۇۋا تۈزگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,69,' ئاندىن كېيىن، تۈرلۈك مېۋىلەر (يەنى گۈل ـ چېچەكلەر) دىن يېگىن، پەرۋەردىگارىڭنىڭ يوللىرىغا ئىتائەتمەنلىك بىلەن كىرگىن»، ئۇنىڭ (يەنى ھەسەل ھەرىسىنىڭ) قارنىدىن ئىنسانلارغا شىپا بولىدىغان خىلمۇ ـ خىل رەڭلىك ئىچىملىك (يەنى ھەسەل) چىقىدۇ بۇنىڭدا (اﷲ نىڭ قۇدرىتىنىڭ چوڭلۇقىنى) پىكىر قىلىدىغان قەۋم ئۈچۈن ئىبرەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,70,' اﷲ سىلەرنى ياراتتى، ئاندىن (ئەجىلىڭلار پۈتكەندە) سىلەرنى قەبزى روھ قىلىدۇ، ئاراڭلاردا ئۆمۈرنىڭ يامان (يەنى ئەڭ قېرىلىق) باسقۇچىغا ئۇلىشىدىغانلارمۇ بار، ئۇلار ھەتتا (بىر نەرسىلەرنى) بىلگەندىن كېيىن، ھېچ نەرسىنى بىلمەس بولۇپ قالىدۇ (يەنى ئىلگىرى بىلىدىغانلىرىنى ئۇنتۇپ، كۈچ ـ قۇۋۋىتى تۈگەپ، ئەقلى كېمىيىپ كىچىك بالىغا ئوخشاپ قالىدۇ) اﷲ ھەقىقەتەن (مەخلۇقاتنى باشقۇرۇشنى) بىلگۈچىدۇر، (ئۆزى ئىرادە قىلغان نەرسىنى ئىشقا ئاشۇرۇشقا) قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,71,' اﷲ سىلەرنىڭ بەزىڭلارنى بەزىڭلاردىن رىزىقتا ئۈستۈن قىلدى، رىزىقتا ئۈستۈن قىلىنغانلار ئۆزلىرىنىڭ رىزقىنى باراۋەر بولۇش ئۈچۈن قول ئاستىدىكى قۇللارغا بەرمەيدۇ، ئۇلار اﷲ نىڭ نېمىتىنى ئىنكار قىلىشامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,72,' اﷲ سىلەر (نىڭ ئۇنى ـ ئۈلپەت ئېلىشىڭلار) ئۈچۈن خوتۇنۇڭلارنى ئۆز تىپىڭلاردىن ياراتتى، سىلەر ئۈچۈن ئۇلاردىن ئوغۇللارنى ۋە نەۋرىلەرنى ياراتتى سىلەرگە تۈرلۈك شېرىن نەرسىلەرنى رىزىق قىلىپ بەردى ئۇلار باتىلغا (يەنى بۇتلارغا) ئىشىنىپ اﷲ نىڭ نېمىتىگە كۇفرىلىق قىلامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,73,' ئۇلار اﷲ نى قويۇپ، ئۆزلىرىگە ئاسمانلاردىن ھېچقانداق رىزىق چۈشۈرۈپ ۋە يەردىن ھېچقانداق رىزىق چىقىرىپ (يەنى ئاسماندىن يامغۇر ياغدۇرۇپ ۋە يەردىن زىرائەت ۋە دەرەخلەرنى ئۆستۈرۈپ) بېرەلمەيدىغان، (شۇنداق قىلىشقا) قۇدرىتى يەتمەيدىغان بۇتلارغا چوقۇنامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,74,' سىلەر اﷲ قا مىسال كەلتۇرمەڭلار (يەنى اﷲ قا بۇتلارنى ئوخشاتماڭلار، اﷲ قا شېرىك كەلتۈرمەڭلار) اﷲ ھەقىقەتەن (ھەممە ھەقىقەتنى) بىلىدۇ، سىلەر (اﷲ نىڭ بۈيۈكلۈكىنى) بىلمەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,75,' اﷲ بۇنداق بىر مىسال كەلتۈرىدۇ: ھېچقانداق ئىشنى ئۆز ئالدىغا بىر تەرەپ قىلالمايدىغان بىر قۇل بىز ئوبدان رىزىق (يەنى مال ـ مۈلۈك) ئاتا قىلغان ۋە ئۇنى يوشۇرۇن ياكى ئاشكارا يوسۇندا سەدىقە قىلىپ تۇرغان (ھۆر ـ ئەركىن) ئادەم بىلەن ئوخشاشمۇ؟ (يەنى ھېچ نەرسىگە ئىگە بولمىغان قۇلغا ئوخشايدىغان بۇتلارنى ھەممىگە ئىگە بولغان ۋە كائىناتنى خالىغانچە تەسەررۇپ قىلىدىغان اﷲ بىلەن قانداقمۇ تەڭلەشتۈرگىلى بولسۇن؟) جىمى ھەمدۇسانا اﷲ قا خاستۇر! ئۇلار (يەنى كاپىرلار) نىڭ تولىسى (ھەمدۇساناغا اﷲ لايىق ئىكەنلىكىنى، بۇتلارنىڭ ھەمدۇساناغا ۋە ئىبادەتكە لايىق ئەمەسلىكىنى) ئۇقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,76,' اﷲ يەنە مۇنداق مىسال كەلتۈرىدۇ؛ مۇنداق ئىككى ئادەم بار بولۇپ، ئۇلارنىڭ بىرى ھېچ ئىش قىلالمايدىغان ۋە ئىگىسىگە يۈك بولۇپ قالغان بىر گاچادۇركى، ئىگىسى بۇ گاچىنى مەيلى قەيەرگە ئەۋەتسۇن، ئۇ ھېچقانداق پايدا ئېلىپ كېلەلمەيدۇ، يەنە بىرى بولسا ئۆزى توغرا يولدا ماڭغان ۋە (كىشىلەرنى) ھەققانىي بولۇشقا بۇيرۇيدىغان ئادەمدۇر، بۇ ئىككى ئادەم ئوخشاشمۇ؟ (يەنى بۇ گاچا بىلەن ئۆزى توغرا يولدا تۇرۇپ پاساھەتلىك سۆزلەيدىغان، ئۆزى قۇرئان نۇرى بىلەن نۇرلانغان ئادەم بىر ـ بىرىگە ئوخشامدۇ؟ ئەقىللىق ئادەمنىڭ ئۇلارنى ئوخشاش ئورۇنغا قويمىغىنىدەك، بۇت ۋە تاشنى ھەممىنى بىلگۈچى قۇدرەتلىك اﷲ بىلەن ئوخشاش ئورۇنغا قويغىلى بولمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,77,' ئاسمانلاردىكى ۋە زېمىندىكى غەيبنى بىلىش اﷲ قا خاستۇر، قىيامەتنىڭ قايىم بولۇشى كۆزنى يۇتۇپ ئاچقۇچىلىق ياكى ئۇنىڭدىنمۇ تېزدۇر اﷲ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,78,' اﷲ سىلەرنى ئاناڭلارنىڭ قارنىدىن ھېچ نەرسىنى بىلمەيدىغان ھالىتىڭلار بىلەن چىقاردى، اﷲ سىلەرگە شۈكۈر قىلسۇن دەپ، قۇلاق، كۆز ۋە يۈرەكلەرنى ئاتا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,79,' ئۇلار قۇشلارنىڭ ھاۋادا ئۇچۇشقا بويسۇندۇرۇلغانلىقىنى كۆرمىدىمۇ؟ ئۇلارنى (ھاۋادىن چۈشۈپ كېتىشتىن) پەقەت اﷲ تۇتۇپ تۇرىدۇ، بۇنىڭدا ئىمان ئېيتىدىغان قەۋم ئۈچۈن (اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,80,' اﷲ سىلەرگە ئۆيۈڭلارنى تۇرالغۇ جاي قىلىپ بەردى، سىلەرگە ھايۋانلارنىڭ تېرىلىرىدىن ئۆيلەرنى (چېدىرلەرنى) قىلىپ بەردى، سىلەر كۆچكەن كۈنۈڭلاردىمۇ، تۇرغان كۈنۈڭلاردىمۇ ئۇنىڭ يىنىكلىكىنى ھېس قىلىسىلەر، سىلەرگە قويلارنىڭ يۈكلىرىدىن ، تۆگىلەرنىڭ يۇڭلىرىدىن، ئۆچكىلەرنىڭ يۇڭلىرىدىن ئۆي جاھازلىرىنى (يەنى سىلەر كىيىدىغان ۋە ئۆيۈڭلارنى سەرەمجانلاشتۇرىدىغان) ھەم مەلۇم مۇددەتكىچە (ئۆلۈم كەلگۈچە ياكى ئۇلار كونىراپ تۈگىگىچە) پايدىلىنىدىغان نەرسىلەرنى قىلىپ بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,81,' اﷲ سىلەرگە ئۆزى ياراتقان نەرسىلەر (يەنى دەرەخلەر، تاغلار ۋە بىنالار) دىن (كۈننىڭ ھارارىتىدىن ساقلىنىدىغان) سايىلەرنى قىلىپ بەردى تاغلاردىن سىلەرگە (غار، ئۆڭكۈرگە ئوخشاش) تۇرالغۇ قىلىدىغان جايلارنى قىلىپ بەردى سىلەرگە ئىسسىقتىن (ۋە سوغۇقتىن) ساقلىنىدىغان كىيىملەرنى، ئۇرۇشتا سىلەرنى (زىيان ـ زەخمەتتىن) ساقلايدىغان تۆمۈر كىيىملەرنى تەييار قىلدى اﷲ سىلەرنى ئىتائەت قىلسۇن دەپ، سىلەرگە ئەنە شۇنداق مۇكەممەل نېمەتلەرنى بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,82,' ئەگەر ئۇلار (يەنى مۇشرىكلار ئىماندىن) يۈز ئۆرۈسە (ساڭا ھېچ زىيىنى يوق)، سېنىڭ ۋەزىپەڭ پەقەت روشەن تەبلىغ قىلىشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,83,' ئۇلار اﷲ نىڭ نېمىتىنى تونۇيدۇ، ئاندىن (اﷲ تىن بۆلەك غەيرىيگە ئىبادەت قىلىش بىلەن) ئۇ نېمەتلەرنى ئىنكار قىلىدۇ، ئۇلارنىڭ تولىسى كاپىردۇر (يەنى كاپىر پېتىچە ئۆلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,84,' شۇ كۈندە (يەنى قىيامەت كۈنىدە) ھەر ئۈممەتتىن (ئۇنىڭغا ئىمان بىلەن ياكى كۇفرى بىلەن گۇۋاھلىق بېرىدىغان) بىر شاھىت (يەنى پەيغەمبەر) كەلتۈرىمىز، ئاندىن كاپىرلارغا (ئۆزرە ئېيتىشقا) رۇخسەت قىلىنمايدۇ ھەمدە ئۇلاردىن پەرۋەردىگارىنى رازى قىلىش (يەنى پەرۋەردىگارىنى رازى قىلىدىغان ئەمەللەرنى قىلىش) تەلەپ قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,85,' زالىملار (يەنى مۇشرىكلار) ئازابنى كۆرگەن چاغدا، ئۇلاردىن (ئازاب) يىنىكلىتىلمەيدۇ ۋە كېچىكتۈرۈلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,86,' مۇشرىكلار (دۇنيادا چوقۇنغان) بۇتلىرىنى كۆرگەن چاغدا: «ئى پەرۋەردىگارىمىز! بۇلار بولسا بىزنىڭ سېنى قويۇپ ئىبادەت قىلغان بۇتلىرىمىزدۇر» دەيدۇ، بۇتلىرى ئۇلارغا جاۋاب بېرىپ؛ «شۈبھىسىزكى، سىلەر ھەقىقەتەن يالغانچىسىلەر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,87,' شۇ كۈندە ئۇلار ئۆزلىرىنىڭ اﷲ قا باش ئەگكەنلىكىنى بىلدۈرىدۇ ، ئەمما ئۇلارنىڭ (دۇنيادىكى چاغلىرىدا: «بۇتلار بىزگە شاپائەت قىلىدۇ» دېگەن) يالغان ـ ياۋىداقلىرى يوققا چىقىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,88,' كاپىر بولغانلارغا ۋە (كىشىلەرنى) اﷲ نىڭ يولىدىن (يەنى دىنىدىن) توسقانلارغا ئۇلارنىڭ قىلغان بۇزغۇنچىلىقلىرى ئۈچۈن ئازاب قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,89,' شۇ كۈندە بىز ھەر بىر ئۈممەتكە ئۇلارنىڭ ئىچىدىن ئۇلارغا قارشى (گۇۋاھلىق بېرىدىغان) بىر شاھىت (يەنى پەيغەمبەر) كەلتۈرىمىز ۋە (شۇ كۈندە) بىز سېنى ئۇلارغا (يەنى ئۈممىتىڭگە) شاھىت قىلىپ كەلتۈرىمىز ساڭا بىز كىتابنى (يەنى قۇرئاننى) ھەممە نەرسىنى (يەنى كىشىلەر موھتاج بولىدىغان دىنىي ئىشلارنىڭ ھەممىسىنى) چۈشەندۈرۈپ بېرىدىغان، (دىللارغا) ھىدايەت، (بەندىلەرگە) رەھمەت، مۇسۇلمانلارغا مەڭگۈلۈك سائادەت بىلەن خوش خەۋەر يەتكۈزىدىغان قىلىپ نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,90,' اﷲ ھەقىقەتەن (كىشىلەر ئارىسىدا) ئادىل بولۇشقا، (جىمى خەلققە) ياخشىلىق قىلىشقا، خىش ـ ئەقرىبالارغا سىلە ـ رەھىم قىلىشقا بۇيرۇيدۇ، قەبىھ (سۆز ـ ھەرىكەتلەر) دىن، يامان ئىشلاردىن ۋە زۇلۇم قىلىشتىن توسىدۇ نەسىھەتنى قوبۇل قىلسۇن دەپ، اﷲ سىلەرگە پەند ـ نەسىھەت قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,91,' سىلەر ئەھدە تۈزۈشكەنلىرىڭلاردا ، اﷲ نىڭ ئەھدىگە ۋاپا قىلىڭلار، قەسىمىڭلارنى (اﷲ نىڭ نامىنى تىلغا ئېلىپ) پۇختىلىغاندىن كېيىن بۇزماڭلار، چۈنكى سىلەر اﷲ نى گۇۋاھچى قىلدىڭلار شۈبھىسىزكى، اﷲ قىلمىشىڭلارنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,92,' سىلەر پىششىق ئىگىرگەن يىپنى چۇۋۇپ پارچە ـ پارچە قىلىۋەتكەن خوتۇندەك بولماڭلار  بىر جامائە (سان ۋە مال ـ مۈلۈك جەھەتتە) يەنە بىر جامائەدىن كۆپ بولغانلىقى ئۈچۈن، قەسىمىڭلارنى ئالدامچىلىقنىڭ ۋاسىتىسى قىلىۋالىسىلەر (يەنى ئۆز ئارا ياردەملىشىش ئۈستىدە بىر جامائە بىلەن ئىتتىپاق تۈزۈشكەندىن كېيىن، سانى، پۇل ـ مېلى كۆپرەك يەنە بىر جامائەنى كۆرۈپلا ئىلگىرىكى جامائە بىلەن تۈزۈشكەن ئەھدىنى بۇزۇپ، كېيىنكى جامائە بىلەن ئەھدى تۈزىسلەر)، اﷲ سىلەرنى ھەقىقەتەن بۇنىڭ (يەنى ۋەدىگە ۋاپا قىلىش ئەمرى) بىلەن سىنايدۇ، سىلەر دەتالاش قىلىشقان ھەق ـ ناھەقنى قىيامەت كۈنى سىلەرگە چوقۇم ئايرىپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,93,' مۇبادا اﷲ خالىغان بولسا ئىدى، سىلەرنى (يەنى پۇتۇن ئىنسانلارنى) ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، لېكىن (اﷲ نىڭ ھېكمىتى ئۇلارنى ئۆز ئىختىيارىغا قويۇۋېتىشنى تەقەززا قىلدى)، اﷲ خالىغان ئادەمنى گۇمراھ قىلىدۇ، خالىغان ئادەمنى ھىدايەت قىلىدۇ، (قىيامەت كۈنى) پۈتۈن قىلمىشلار ئۈستىدە سوراق قىلىنىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,94,' قەدىمىڭلار (ئىسلام دىنىدا) مۇستەھكەم، ئىزچىل بولغاندىن كېيىن تېيىلىپ كەتمەسلىك ئۈچۈن، قەسىمىڭلارنى ئۆز ئاراڭلاردا ئالدامچىلىقنىڭ ۋاسىتىسى قىلىۋالماڭلار، (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلىقىڭلار (يەنى ئەھدىگە ۋاپا قىلىشتىن يۈز ئۆرۈگەنلىكىڭلار) ئۈچۈن ئازابنى تېتىيسىلەر، (سىلەر ئاخىرەتتە دوزاختا) چوڭ ئازابقا قالىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,95,' سىلەر اﷲ نىڭ ئەھدىنى ئەزىمەس نەرسىگە سېتىۋەتمەڭلار: سىلەر ئەگەر بىلسەڭلار، اﷲ نىڭ دەرگاھىدىكى ساۋاب سىلەر ئۈچۈن (پانى دۇنيانىڭ نەرسىلىرىدىن) ئارتۇقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,96,' (ئى ئىنسانلار!) سىلەرنىڭ ئىلكىڭلاردىكى نەرسىلەر تۈگەيدۇ، اﷲ نىڭ دەرگاھىدىكى نەرسىلەر تۈگىمەستۇر سەۋر قىلغۇچىلارغا، ئەلۋەتتە، ئۇلارنىڭ قىلغان ئەمەلىدىنمۇ ياخشىراق ساۋاب بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,97,' ئەر ـ ئايال مۆمىنلەردىن كىمكى ياخشى ئەمەل قىلىدىكەن، بىز ئۇنى ئەلۋەتتە (دۇنيانىڭ قانائەتچانلىق ، ھالال رىزىق ۋە ياخشى ئەمەللەرگە مۇۋەپپەق قىلىپ) ئوبدان ياشىتىمىز، ئۇلارغا، ئەلۋەتتە، قىلغان ئەمەلىدىنمۇ ياخشىراق ساۋاب بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,98,' سەن قۇرئان ئوقۇماقچى بولغىنىڭدا، قوغلاندى شەيتاننىڭ (ۋەسۋەسىسىدىن) اﷲ قا سېغىنىپ پاناھ تىلىگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,99,' شۈبھىسىزكى، مۆمىنلەرنىڭ ۋە پەرۋەردىگارىغا تەۋەككۈل قىلغۇچىلار (يەنى يۆلەنگۈچىلەر) نىڭ ئۈستىدىن شەيتان ھۆكۈمرانلىق قىلالمايدۇ (يەنى پەرۋەردىگارىغا چىن يۆلەنگەن مۆمىنلەرگە شەيتاننىڭ ۋەسۋەسىسى تەسىر قىلالمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,100,' شەيتان پەقەت ئۆزىنى دوست تۇتىدىغانلار ۋە ئۆزىنىڭ (ئىغۋا قىلىشى) بىلەن مۇشرىك بولۇپ كەتكەنلەر ئۈستىدىنلا ھۆكۈمرانلىق قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,101,' بىز بىر ئايەتنى يەنە بىر ئايەتنىڭ ئورنىغا ئالماشتۇرغىنىمىزدا (يەنى بىر ھۆكۈمنى بىكار قىلىپ، ئۇنىڭ ئورنىغا باشقا بىر ھۆكۈمنى چۈشۈرگىنىمىزدە)، ـ اﷲ ئۆزى نازىل قىلغاننى (يەنى شۇ ھۆكۈمدە بەندىگە نېمە مەنپەئەت ۋە ھېكمەت بارلىقىنى) ئۆزى ئوبدان بىلىدۇ ـ ئۇلار: «(ئى مۇھەممەد!) سەن ( اﷲ قا) يالغان چاپلىدىڭ» دەيدۇ، ئۇنداق ئەمەس، ئۇلارنىڭ تولىسى (اﷲ نىڭ ھېكمىتىنى) ئۇقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,102,' ئېيتقىنكى، قۇرئاننى جىبرىئىل مۆمىنلەرنىڭ (ئىمانىنى) مۇستەھكەم قىلىش ئۈچۈن، مۇسۇلمانلارغا ھىدايەت ۋە خۇش خەۋەر قىلىپ، پەرۋەردىگارىڭ ھۇزۇرىدىن راستچىللىق بىلەن نازىل قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,103,' ئۇلار (يەنى مۇشرىكلار) نىڭ: «ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) قۇرئاننى بىر ئىنسان تەلىم بېرىدۇ» دېگەن سۆزىنى ئوبدان بىلىمىز، ئۇلار كۆرسەتكەن ئادەمنىڭ تىلى ئەرەب تىلى ئەمەس، بۇ (قۇرئان) بولسا روشەن ئەرەب تىلىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,104,' شۈبھىسىزكى، اﷲ نىڭ ئايەتلىرىگە (يەنى قۇرئانغا) ئىشەنمەيدىغانلارنى اﷲ ھىدايەت قىلمايدۇ، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,105,' پەقەت اﷲ نىڭ ئايەتلىرىگە ئىشەنمەيدىغانلارلا ( اﷲ قا) يالغان چاپلايدۇ، (مۇھەممەد ئەلەيھىسسالام) يالغانچى ئەمەس) دەل ئۇلارنىڭ ئۆزلىرى يالغانچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,106,' كىمكى اﷲ قا ئىمان ئېيتقاندىن كېيىن ئىمانىدىن يېنىۋالسا، ـ قەلبى ئىمان بىلەن مۇستەھكەم تۇرسىمۇ مەجبۇرلاش ئاستىدا (ئاغزىدىلا) ئىماندىن يانغانلىقىنى بىلدۈرگەنلەر بۇنىڭدىن مۇستەسنا ـ كۇفرى بىلەن كۆڭلى ئازادە بولسا (يەنى ئىختىيارىي يوسۇندا مۇرتەد بولغان بولسا)، ئۇ اﷲ نىڭ غەزىپىگە دۇچار بولىدۇ ۋە چوڭ ئازابقا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,107,' بۇ (يەنى ئازاب) ئۇلارنىڭ دۇنيا تىرىكچىلىكىنى ئاخىرەتتىن ئارتۇق كۆرگەنلىكلىرى ۋە اﷲ نىڭ كاپىر قەۋمنى ھىدايەت قىلمايدىغانلىقى ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,108,' ئەنە شۇ كىشىلەر اﷲ تەرىپىدىن دىللىرى، قۇلاقلىرى ۋە كۆزلىرى پېچەتلىۋېتىلگەن كىشىلەردۇر، ئەنە شۇلار غاپىللاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,109,' ئۇلار راستلا ئاخىرەتتە زىيان تارتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,110,' ئاندىن (ئى مۇھەممەد!) زىيانكەشلىككە ئۇچرىغاندىن كېيىن، ھەقىقەتەن (مەدىنىگە) ھىجرەت قىلغان ئاندىن ( اﷲ نىڭ يولىدا) جىھاد قىلغان ۋە (جىھادنىڭ مۇشەققەتلىرىگە) چىدىغانلارغا پەرۋەردىگارىڭ (مەغپىرەت قىلىدۇ)، شۈبھىسىزكى، پەرۋەردىگارىڭ شۇنىڭدىن (يەنى ئۇلارنىڭ ھىجرەت، جىھاد ، سەۋر قىلغىنىدىن) كېيىن ئۇلارغا ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، رەھمەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,111,' ھەر ئادەم ئۆزى بىلەن بولۇپ كېتىدىغان، ھەر ئادەم قىلغان ئەمەلىنىڭ مۇكاپاتى تولۇق بېرىلىدىغان، ئۇلارغا زۇلۇم قىلىنمايدىغان كۈننى (يەنى قىيامەت كۈنىنى) ئەسلىگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,112,' اﷲ مۇنداق بىر مىسالنى كەلتۈرىدۇ: بىر شەھەر (يەنى مەككە ئاھالىسى) بولۇپ، (ئەسلىدە) تىنچ ـ خاتىرجەم ئىدى ئۇنىڭ رىزقى تەرەپ ـ تەرەپتىن كەڭتاشا كېلىپ تۇراتتى، (ئۇنىڭ ئاھالىسى) اﷲ نىڭ نېمەتلىرىگە كۇفرىلىق قىلدى، اﷲ ئۇلارغا قىلمىشلىرى تۈپەيلىدن ئاچلىقنىڭ ۋە قورقۇنچنىڭ ئەلىمىنى تېتىتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,113,' ئۇلارغا ئۆز ئىچىدىن بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلدى، ئۇنى ئۇلار ئىنكار قىلدى ئۇلارغا ئازاب (يەنى قاتتىقچىلىق ۋە ئوقۇبەت) يۈزلەندى، ئۇلار (گۇناھلارغا چۆمگەنلىك تۈپەيلىدىن) زالىم ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,114,' اﷲ سىلەرگە رىزىق قىلىپ بەرگەن ھالال، شېرىن يېمەكلىكلەردىن يەڭلار، ئەگەر اﷲ غىلا ئىبادەت قىلىدىغان بولساڭلار، اﷲ نىڭ ھىممىتىگە شۈكۈر قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,115,' اﷲ سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋاننى، قاننى، چوشقا گۆشىنى، اﷲ تىن غەيرىنىڭ (يەنى بۇتلارنىڭ) نامى ئېيتىلىپ بوغۇزلانغان ھايۋاننى يېيىشنى ھارام قىلدى كىمكى ئختىيارىچە ئەمەس، ئىلاجىسىزلىقتىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) ھاياتىنى ساقلاپ قالغۇدەك مىقداردا (يېسە) اﷲ (ئۇنى) مەغپىرەت قىلغۇچىدۇر، (ئۇنىڭغا) كۆيۈنگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,116,' ئاغزىڭلارغا كەلگەن يالغاننى سۆزلەش ئۈچۈن (ھېچقانداق دەلىلسىز) «بۇ ھالال، بۇ ھارام» دېمەڭلار، چۈنكى (مۇنداقتا) اﷲ نامىدىن يالغاننى ئويدۇرغان بولىسىلەر، اﷲ نامىدىن يالغاننى ئويدورغۇچىلار ھەقىقەتەن (دۇنيا ئاخىرەتتە) مەقسىتىگە ئېرىشەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,117,' (ئۇلار دۇنيادىن پەقەت) ئازغىنا بەھرىمەن بولىدۇ، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,118,' ئىلگىرى ساڭا بايان قىلغان نەرسىلەرنى يالغۇز يەھۇدىيلارغا ھارام قىلدۇق، (بۇنىڭ بىلەن) بىز ئۇلارغا زۇلۇم قىلمىدۇق، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,119,' شۈبھىسىزكى، بىلمەستىن يامان ئىشلارنى قىلغاندىن كېيىن تەۋبە قىلغان ۋە (ئەمەلىنى) تۈزەتكەنلەرگە پەرۋەردىگارىڭ ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، مەرھەمەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,120,' ئىبراھىم ھەقىقەتەن (ياخشى خىسلەتلەرنى ئۆزلەشتۈرگۈچى) پېشىۋا، اﷲ قا ئىتائەتمەن (باتىل دىنلاردىن ھەق دىنغا بۇرالغۇچى يەنى ھەق دىنغا ئېتىقاد قىلغۇچى) ئىدى ئۇ اﷲ قا شېرىك كەلتۈرگۈچىلەردىن ئەمەس ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,121,' ئىبراھىم اﷲ نىڭ نېمەتلىرىگە شۈكۈر قىلغۇچى ئىدى اﷲ ئۇنى (پەيغەمبەرلىككە) تاللىدى ۋە ئۇنى توغرا يولغا باشلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,122,' ئۇنىڭغا دۇنيادا ياخشىلىق ئاتا قىلدۇق، شەك ـ شۈبھىسىزكى، ئاخىرەتتە ئۇ ياخشىلار قاتارىدا بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,123,' ئاندىن ساڭا: «باتىل دىنلاردىن ھەق دىنغا بۇرالغۇچى (يەنى ھەق دىنغا ئېتىقاد قىلغۇچى) ئىبراھىمنىڭ دىنىغا ئەگەشكىن، ئىبراھىم مۇشرىكلاردىن ئەمەس ئىدى» دەپ ۋەھيى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,124,' شەنبە كۈنى ئۈستىدە ئىختىلاپ قىلغۇچىلار ئۈچۈنلا ئۇنى ئۇلۇغلاش بەلگىلەندى (يەنى شەنبە كۈنىنى ئۇلۇغلاش ۋە بۇ كۈندە ئىشلىمەسلىك ئىبراھىمنىڭ شەرىئىتىدە يوق ئىدى يەھۇدىيلار اﷲ نىڭ ئەمرىگە ئاسىيلىق قىلىپ دىندا ئىختىلاپ قىلىشقانلىقلىرى سەۋەبىدىن ئۇلارغا ئېغىرچىلىق تۇغدۇرۇش ئۈچۈن شۇنداق بەلگىلىمىنى چىقاردۇق) شۈبھىسىزكى، پەرۋەردىگارىڭ قىيامەت كۈنى ئۇلار ئىختىلاپ قىلىشقان ھەق ـ ناھەق ئۈستىدە چوقۇم ھۆكۈم چىقىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,125,' پەرۋەردىگارىڭنىڭ يولىغا (يەنى ئىسلام دىنىغا) ھېكمەتلىك ئۇسلۇبتا ياخشى ۋەز ـ نەسىھەت بىلەن دەۋەت قىلغىن، ئۇلار (يەنى مۇخالىپەتچىلىك قىلغۇچىلار) بىلەن چىرايلىق رەۋىشتە مۇنازىرىلەشكەن پەرۋەردىگارىڭ ھەقىقەتەن ئۇنىڭ يولىدىن ئازغانلارنى ئوبدان بىلىدۇ، ھىدايەت تاپقۇچىلارنىمۇ ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,126,' ئەگەر (ئۆزۈڭلارغا يەتكەن زىيان ـ زەخمەت ئۈچۈن) ئىنتىقام ئالماقچى بولساڭلار، ئۆزۈڭلارغا يەتكەن زىيان ـ زەخمەت قانچىلىك بولسا، شۇنچىلىك ئىنتىقام ئېلىڭلار (يەنى ئاشۇرۇۋەتمەڭلار)، ئەگەر سەۋر قىلساڭلار (يەنى ئىنتىقام ئالماي كەچۈرسەڭلار)، بۇ سەۋر قىلغۇچىلار (يەنى كەچۈرگۈچىلەر) ئۈچۈن (ئەلۋەتتە) ياخشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,127,' (ئى مۇھەممەد!) (اﷲ نىڭ يولىدا ساڭا يەتكەن ئەرزىيەتلەرگە) سەۋر قىلغىن، سەۋرەڭ پەقەت اﷲ نىڭ ياردىمى بىلەنلا قولغا كېلىدۇ ئۇلارنىڭ (ئىمان ئېيتمىغانلىقى) غا قايغۇرمىغىن ئۇلارنىڭ ھىيلە ـ مىكرىسىدىن يۈرىكىڭ سىقىلمىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,16,128,' اﷲ ھەقىقەتەن تەقۋادارلىق قىلغۇچىلار ۋە ياخشى ئىش قىلغۇچىلار بىلەن بىللىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,1,'اﷲ (بارچە نۇقساندىن) پاكتۇر، ئۇ (مۇھەممەد ئەلەيھىسسالامغا) قۇدرىتىمىزنىڭ دەلىللىرىنى كۆرسىتىش ئۈچۈن، بەندىسىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) بىر كېچىدە مەسجىدى ھەرەمدىن ئەتراپىنى بەرىكەتلىك قىلغان مەسجىدى ئەقساغا ئېلىپ كەلدى ھەقىقەتەن اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,2,' مۇساغا كىتابنى (يەنى تەۋراتنى) ئاتا قىلدۇق، كىتابنى ئىسرائىل ئەۋلادىغا يېتەكچى قىلدۇق، (ئۇلارغا) مەندىن غەيرىنى ھامىي (يەنى پەرۋەردىگار) قىلىۋالماڭلار!» (دېدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,3,' ئى بىز نۇھ بىلەن بىللە كېمىگە چۈشۈرگەنلەرنىڭ ئەۋلادلىرى! (ئەجدادىڭلارنى غەرق بولۇپ كېتىشتىن قۇتقۇزدۇق، اﷲ نىڭ نېمىتىگە شۈكۈر قىلىڭلار) نۇھ ھەقىقەتەن كۆپ شۈكۈر قىلغۇچى بەندە ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,4,' ئىسرائىل ئەۋلادىغا كىتابتا (يەنى تەۋراتتا): «پەلەستىن زېمىنىدا چوقۇم ئىككى قېتىم بۇزغۇنچىلىق قىلىسىلەر ۋە زۇلۇم قىلىش بىلەن، اﷲ چەكلىگەن ئىشلارنى قىلىش بىلەن تولىمۇ ھەددىڭلاردىن ئېشىپ كېتىسىلەر» دەپ ۋەھيى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,5,' بىرىنچى قېتىملىق بۇزغۇنچىلىقنىڭ (جازاسى) ۋەدە قىلىنغان ۋاقىت كەلگەندە، (سىلەرنى جازالاش ئۈچۈن) سىلەرگە كۈچلۈك بەندىلىرىمىزنى ئەۋەتتۇق، (ئۇلار سىلەرنى ئۆلتۈرۈش، بۇلاڭ ـ تالاڭ قىلىش ئۈچۈن) ئۆيلەرنى ئاختۇردى (سىلەرگە دۈشمەننى مۇسەللەت قىلىش) چوقۇم ئىشقا ئاشىدىغان ۋەدە ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,6,' ئاندىن (يەنى تەۋبە قىلغىنىڭلاردىن) كېيىن سىلەرنىڭ ئۇلاردىن ئۈستۈنلۈكۈڭلارنى ئەسلىگە كەلتۈردۇق (يەنى دۈشمىنىڭلارنى ھالاك قىلدۇق، سىلەرنى ئۇنىڭ ئۈستىدىن غالىب قىلدۇق)، سىلەرگە نۇرغۇن مال ۋە ئەۋلادلار ئاتا قىلدۇق، سانىڭلارنى كۆپ قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,7,' (ئى ئسرائىل ئەۋلادى!) ئەگەر ياخشىلىق قىلساڭلار، ياخشىلىقىڭلار ئۆزۈڭلار ئۈچۈندۇر، يامانلىق قىلساڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، ئىككىنچى قېتىملىق بۇزغۇنچىلىق (جازاسىنىڭ) ۋەدە قىلىنغان ۋاقتى كەلگەندە، (دۈشمەنلەرنىڭ خار قىلىشى بىلەن) يۈزۈڭلاردا قايغۇنىڭ ئالامەتلىرى كۆرۈلۈشى ئۈچۈن، ئۇلارنىڭ بەيتۇلمۇقەددەسكە دەسلەپكى قېتىمدا كىرگەندەك كىرىپ (ئۇنى خاراب قىلىشى) ئۈچۈن، ئىشغال قىلغانلىكى يەرنى ۋەيران قىلىشى ئۈچۈن (ئۇلارنى بىز ئەۋەتتۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,8,' (ئەگەر تەۋبە قىلساڭلار) پەرۋەردىگارىڭلارنىڭ سىلەرگە رەھىم قىلىشى مۇھەققەق يېقىندۇر، ئەگەر سىلەر قايتساڭلار (يەنى قايتا بۇزغۇنچىلىق قىلساڭلار)، بىز قايتىمىز (يەنى بىز سىلەرنى قايتا جازالايمىز) جەھەننەمنى كاپىرلار ئۈچۈن زىندان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,9,' بۇ قۇرئان ھەقىقەتەن ئەڭ توغرا يولغا باشلايدۇ، ياخشى ئىشلارنى قىلىدىغان مۆمىنلەرگە ئۇلارنىڭ چوڭ مۇكاپاتقا ئېرىشىدىغانلىقى بىلەن خۇش خەۋەر بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,10,' (يەنە مۆمىنلەرگە خۇش خەۋەر بېرىدۇكى) ئاخىرەتكە ئىشەنمەيدىغانلارغا قاتتىق ئازاب تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,11,' ئىنسان (ئۆزىگە ۋە بالا ـ چاقىلىرىغا) خەيرىلىك دۇئا قىلغاندەك، (ئاچچىقى كەلگەندە) بەتدۇئا قىلىدۇ، ئىنسان ئالدىراڭغۇدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,12,' كېچە بىلەن كۈندۈزنى (كامالى قۇدرىتىمىزنى كۆرسىتىدىغان) ئىككى ئالامەت قىلدۇق، كېچىنىڭ ئالامىتىنى كۆتۈرۈۋەتتۇق، پەرۋەردىگارىڭلارنىڭ پەزلىنى تەلەپ قىلىشىڭلار (يەنى تۇرمۇش لازىمەتلىكلىرىنى تېپىش يولىدا سەئيى ھەرىكەتتە بولۇشۇڭلار) ئۈچۈن، (كېچە بىلەن كۈندۈزنىڭ ئالمىشىشى ئارقىلىق كۈنلەرنىڭ، ئايلارنىڭ) يىللارنىڭ سانىنى ۋە ھېسابىنى بىلىشىڭلار ئۈچۈن، كۈندۈزنىڭ ئالامىتىنى يورۇقلۇق قىلدۇق، (دىنىي ۋە دۇنياۋى) ئىشلارنىڭ ھەممىسىنى تەپسىلىي بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,13,' ھەر بىر ئىنساننىڭ ئەمەلىنى ئۇنىڭ بوينىغا ئېسىپ قويىمىز (يەنى ئىنساننىڭ قىلغان ھەرقانداق ئەمەلى خۇددى بويۇنچاق بويۇندىن ئايرىلمىغىنىدەك ئۇنىڭدىن ھەرگىز ئايرىلمايدۇ، شۇنىڭغا يارىشا جازا بېرىلىدۇ)، قىيامەت كۈنى ئۇنىڭ نامە ـ ئەمالىنى كۆرسىتىمىز، ئۇ ئۇنى ئوچۇق كۆرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,14,' (ئۇنىڭغا) «نامە ـ ئەمالىڭنى ئوقۇغىن! بۈگۈن ئۆزۈڭگە (يەنى بۇ قىلمىشلىرىڭغا) ئۆزۈڭ گۇۋاھ بولۇشۇڭ كۆرىدۇ» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,15,' كىمكى ھىدايەت تاپىدىكەن، ھىدايەت تاپقانلىقىنىڭ پايدىسى ئۇنىڭ ئۆزى ئۈچۈندۇر كىمكى ئازىدىكەن، ئازغانلىقنىڭ زىيىنى ئۇنىڭ ئۆزى ئۈچۈندۇر، بىر ئادەم يەنە بىر ئادەمنىڭ گۇناھىنى ئۈستىگە ئالمايدۇ پەيغەمبەر ئەۋەتمەي تۇرۇپ (ھېچ ئادەمنى جازالىغىنىمىز يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,16,' بىرەر شەھەر (ئاھالىسى) نى ھالاك قىلماقچى بولساق، ئۇنىڭدىكى دۆلەتمەن ئادەملەرنى (بىزگە ئىتائەت قىلىشقا بۇيرۇيمىز، ئۇلار ئىتائەت قىلماي، پىسقى ـ پاسات قىلىدۇ ـ دە، ئۇلارغا ئازابىمىز تېگىشلىك بولىدۇ، شۇنىڭ بىلەن ئۇلارنى قورقۇنچلۇق رەۋىشتە ھالاك قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,17,' نۇھتىن كېيىن (پەيغەمبەرلىرىنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، پەرۋەردىگارىڭ بەندىلىرىنىڭ گۇناھلىرىنى تولۇق بىلىپ تۇرۇشتا، كۆرۈپ تۇرۇشتا يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,18,' بىز دۇنيانى كۆزلىگەنلەر ئىچىدىن خالىغان ئادەمگە (ئۇنىڭ خالىغىنىنى ئەمەس) بىزنىڭ خالىغىنىمىزنى بېرىمىز، ئاندىن ئۇنى (ئاخىرەتتە) جەھەننەم بىلەن جازالايمىز، جەھەننەمگە ئۇ خارلانغان، ( اﷲ نىڭ رەھمىتىدىن) قوغلانغان ھالدا كىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,19,' كىمكى ئاخىرەتنى كۆزلەيدىكەن ۋە مۆمىن بولۇپ ئاخىرەتكە لايىق ئەمەللەرنى قىلىدىكەن، مۇنداق ئادەملەرنىڭ قىلغان ئەمەلى (اﷲ نىڭ دەرگاھىدا) قوبۇل بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,20,' بۇلارنىڭ ۋە ئۇلارنىڭ ھەر ئىككىسىگە پەرۋەردىگارىڭنىڭ ئاتا قىلغىنىدىن بېرىمىز پەرۋەردىگارىڭنىڭ ئاتا قىلغىنى (ھېچ ئادەمدىن) مەنئى قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,21,' ئۇلارنىڭ بەزىسىنى بەزىسىدىن قانداق ئۈستۈن قىلغانلىقىمىزغا قارىغىن، ئاخىرەت دەرىجىلىرىدىكى (پەرق) ئەلۋەتتە تېخىمۇ چوڭدۇر ۋە ئۈستۈنلۈك (جەھەتتىكى پەرق) زىيادە چوڭدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,22,' اﷲ قا باشقا مەبۇدنى (شېرىك) قىلمىغىن، (ئۇنداق قىلساڭ اﷲ نىڭ دەرگاھىدا) ئەيىبلەنگەن، (اﷲ نىڭ ياردىمىدىن) مەھرۇم بولغان ھالدا (دوزاختا ھەمىشە) قالىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,23,' پەرۋەردىگارىڭ پەقەت ئۇنىڭ ئۆزىگىلا ئىبادەت قىلىشىڭلارنى ۋە ئاتا ـ ئاناڭلارغا ياخشىلىق قىلىشىڭلارنى تەۋسىيە قىلدى، ئۇلارنىڭ بىرى، يا ئىككىلىسى سېنىڭ قول ئاستىڭدا بولۇپ ياشىنىپ قالسا، ئۇلارغا ئوھوي دېمىگىن (يەنى مالاللىقنى بىلدۈرىدىغان شۇنچىلىك سۆزنىمۇ قىلمىغىن)، ئۇلارنى دۈشكەلىمىگىن ، ئۇلارغا ھۆرمەت بىلەن يۇمشاق سۆز قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,24,' ئۇلارغا كامالىي مېھرىبانلىقتىن ناھايىتى كەمتەر مۇئامىلىدە بولغىن ۋە: «ئى پەرۋەردىگارىم! ئۇلار مېنى كىچىكلىكىمدە تەربىيىلىگىنىدەك ئۇلارغا مەرھەمەت قىلغىن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,25,' پەرۋەردىگارىڭلار دىلىڭلاردىكىنى ئوبدان بىلگۈچىدۇر، ئەگەر سىلەر ياخشى بولساڭلار (يەنى ئاتا ـ ئاناڭلارنى قاخشاتقۇچى بولمىساڭلار، اﷲ سىلەرنىڭ خاتالىغىڭلارنى كەچۈرىدۇ) اﷲ ھەقىقەتەن تەۋبە قىلىپ تۇرغۇچىلارنى مەغفىرەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,26,' تۇغقانغا، مىسكىنگە، ئىبىن ـ سەبىلگە (خەيرى ـ ساخاۋەتتىن) ھەققىنى بەرگىن، (پۇل ـ مېلىڭنى ناتوغرا يوللارغا) ئىسراپ قىلمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,27,' ئىسراپ قىلغۇچىلار ھەقىقەتەن شەيتانلارنىڭ قېرىنداشلىرىدۇر، شەيتان پەرۋەردىگارىغا تولىمۇ كۇفرانى نېمەت قىلغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,28,' ئەگەر سەن پەرۋەردىگارىڭدىن كۈتۈۋاتقان مەرھەمەت نازىل بولغاندىن كېيىن ئاندىن ئۇلارغا خەيرى ـ ساخاۋەت قىلماقچى بولساڭ (يەنى قولۇڭدا ئۇلارغا بېرىدىغان بىر نەرسە بولمىسا)، ئۇلارغا چىرايلىق گەپ قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,29,' قولۇڭنى بوينۇڭغا باغلىۋالمىغىن (يەنى بېخىللىق قىلمىغىن) قولۇڭنى تولىمۇ ئېچىپمۇ ئەتمىگىن (يەنى ئىسراپ قىلمىغىن)، (ئۇنداق قىلساڭ پۇلسىز قېلىپ) مالامەتكە، پۇشايمانغا قالىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,30,' شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ، خالىغان ئادەمنىڭ رىزقىنى تار قىلىدۇ، اﷲ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىنى، مەنپەئىتىنى) بىلىپ تۇرغۇچىدۇر، كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,31,' كەمبەغەللىكتىن قورقۇپ بالىلىرىڭلارنى ئۆلتۈرمەڭلار  ئۇلارنىڭ ۋە سىلەرنىڭ رىزقىڭلارنى بىز بېرىمىز، ئۇلارنى ئۆلتۈرۈش ھەقىقەتەن چوڭ گۇناھتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,32,' زىناغا يېقىنلاشماڭلار، چۈنكى ئۇ قەبىھ ئىشتۇر، يامان يولدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,33,' ناھەق ئادەم ئۆلتۈرمەڭلاركى، اﷲ (ناھەق ئادەم ئۆلتۈرۈشنى (ھارام قىلدى، كىمكى ناھەق ئۆلتۈرۈلىدىكەن، (قاتىلدىن قىساس ئېلىش، يا دىيەت ئېلىش، يا كەچۈرۈم قىلىش) ھوقۇقىنى ئۆلتۈرۈلگۈچىنىڭ ئىگىسىنىڭ قولىدا قىلدۇق، ئىگىسى قىساس ئېلىشتا چەكتىن چىقىپ كەتمىسۇن (قاتىلدىن غەيرىنى ئۆلتۈرۈش، يا ئۇنىڭ ئەزالىرىنى كېسىش، بىر ئادەم ئۈچۈن ئىككى ئادەمنى ئۆلتۈرۈش قاتارلىق ئىشلارنى قىلمىسۇن)، (ناھەق ئۆلتۈرۈلگۈچىنىڭ) ئىگىسىگە اﷲ ھەقىقەتەن مەدەتكاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,34,' يېتىمنىڭ مېلىنى تاكى ئۇ بالاغەتكە يەتكەنگە قەدەر (يېتىمگە) ئەڭ پايدىلىق ئۇسۇلدا تەسەررۇپ قىلىڭلار، ئەھدىگە ۋاپا قىلىڭلار، (قىيامەت كۈنى) ئەھدە ئۈستىدە (يەنى ئەھدىگە ۋاپا قىلغان ـ قىلمىغانلىق ئۈستىدە) ئەلۋەتتە سوئال ـ سوراق قىلىنىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,35,' سىلەر (باشقىلارغا ئاشلىق قاتارلىقلارنى) ئۆلچەپ بەرگەندە، تولۇق ئۆلچەڭلار ۋە تارازىدا تارتىپ بېرىڭلار، بۇنداق قىلىش (سىلەرگە دۇنيادا) ياخشىدۇر، ئاخىرەتلىكىڭلار ئۈچۈن تېخىمۇ ئوبداندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,36,' بىلمەيدىغان نەرسەڭگە ئەگەشمە (يەنى بىلمىگەننى بىلدىم، كۆرمىگەننى كۆردۈم، ئاڭلىمىغاننى ئاڭلىدىم دېمە)، (ئىنسان قىيامەت كۈنى) قۇلاق، كۆز، دىل (يەنى سەزگۈ ئەزالىرى) نىڭ قىلمىشلىرى ئۈستىدە ھەقىقەتەن سوئال ـ سوراق قىلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,37,' سەن زېمىندا مەغرۇرانە يۈرمىگىن، (ھاكاۋۇرلۇقتىن ئايىغىڭ بىلەن) يەرنى تېشىۋېتەلمەيسەن، (ھەرقانچە گىدەيگىنىڭ بىلەن) ئېگىزلىكتە تاغلارغا تەڭلىشەلمەيسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,38,' يۇقىرىقى (ئىش) لارنىڭ ھەممىسى قەبىھ بولۇپ، اﷲ نىڭ دەرگاھىدا ياماندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,39,' بۇلار پەرۋەردىگارىڭ ساڭا ۋەھيى قىلغان ھېكمەتلەرنىڭ بىر قىسمىدۇر، اﷲ قا باشقا مەبۇدنى شېرىك قىلمىغىن، بولمىسا مالامەت قىلىنغان، (اﷲ نىڭ رەھمىتىدىن) قوغلانغان ھالدا جەھەننەمگە تاشلىنىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,40,' ئەجەبا اﷲ سىلەرگە ئوغۇللارنى خاس قىلىپ، پەرىشتىلەرنى ئۆزىنىڭ قىزلىرى قىلىۋالغانمۇ؟  ھەقىقەتەن سىلەر چوڭ سۆزنى قىلىۋاتىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,41,' ئۇلارنى ئىبرەت ئالسۇن دەپ (ۋەز ـ نەسىھەتنى) قۇرئاندا تۈرلۈك شەكىلدە بايان قىلدۇق، (بۇنىڭ بىلەن) ئۇلار (ھەقىقەتەن) تېخىمۇ يىراقلاشماقتا');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,42,' مۇشرىكلار ئېيتقاندەك، اﷲ بىلەن بىرگە يەنە باشقا ئىلاھلار بولغان تەقدىردە ئەرش ئىگىسى ( اﷲ ئۈستىدىن) غەلىبە قىلىش ئۈچۈن ئەلۋەتتە يول ئىزدىگەن بولاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,43,' اﷲ پاكتۇر، ئۇلارنىڭ بوھتان سۆزلىرىدىن يۈكسەك دەرىجىدە ئالىيدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,44,' يەتتە ئاسمان ـ زېمىن ۋە ئۇلاردىكى مەخلۇقاتلار اﷲ نى پاك دەپ بىلىدۇ، (كائىناتتىكى) قانداقلىكى نەرسە بولمىسۇن، اﷲ نى پاك دەپ مەدھىيىلەيدۇ (يەنى اﷲ نىڭ ئۇلۇغلۇقىنى سۆزلەيدۇ)، لېكىن سىلەر (تىلىڭلار ئوخشاش بولمىغانلىقى ئۈچۈن) ئۇلارنىڭ مەدھىيىسىنى سەزمەيسىلەر، اﷲ ھەقىقەتەن (بەندىلىرىگە) ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)، (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,45,' (ئى مۇھەممەد!) سەن ئاخىرەتكە ئىشەنمەيدىغان (مۇشرىكلارغا) قۇرئاننى ئوقۇغان چېغىڭدا ئۇلار بىلەن سېنىڭ ئاراڭدا يوشۇرۇن پەردە پەيدا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,46,' قۇرئاننى چۈشەنمىسۇن دەپ ئۇلارنىڭ دىللىرىنى پەردىلىدۇق، ئۇلارنىڭ قۇلاقلىرىنى ئېغىر قىلدۇق، قۇرئاندا يالغۇز پەرۋەردىگارىڭنى تىلغا ئالساڭ، ئۇلار ئۈركۈپ قېچىپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,47,' تىلاۋىتىڭگە ئۇلارنىڭ نېمە ئۈچۈن (يەنى مەسخىرە ئۈچۈن) قۇلاق سالىدىغانلىقىنى بىز ئوبدان بىلىمىز، ئۇلار قىرائىتىڭنى تىڭشىغان چاغلىرىدا (سېنىڭ توغراڭدا) پىچىرلىشىدۇ، ئەينى ۋاقىتتا زالىملار (مۆمىنلەرگە): «سىلەر پەقەت سېھىرلەنگەن ئادەمگىلا ئەگىشىۋاتىسىلەر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,48,' ئۇلارنىڭ سېنىڭ توغراڭدا (بىردەم سېھرىگەر، بىردەم شائىر ۋە بىردەم مەجنۇن دېگەندەك) نۇرغۇن مىساللارنى كەلتۈرگەنلىكىگە قارىغىن، ئۇلار (مۇشۇنداق بوھتانلارنى چاپلاش بىلەن) ئازدى، ئۇلار (ھىدايەتكە) يول تاپالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,49,' مۇشرىكلار: ‹‹بىز قۇرۇق سۆڭەك ۋە چىرىپ توپا بولۇپ كەتكەندىن كېيىن چوقۇم قايتا تىرىلىپ يېڭىدىن ئادەم بولامدۇق؟›› دېيىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,50,' سىلەر تاش ياكى تۆمۈر ياكى ھاياتلىقنى تەسەۋۋۇر قىلىش تېخىمۇ قىيىن بولغان بىر نەرسە بولۇپ كەتسەڭلارمۇ (اﷲ سىلەرنى تىرىلدۈرىدۇ) ئۇلار: ‹‹بىزنى كىم تىرىلدۈرىدۇ؟» دېگىن، ئۇلار ساڭا باشلىرىنى لىڭشىتىپ: ‹‹ئۇ قاچان بولىدۇ؟›› دېيىشىدۇ، (سەن) ‹‹ئۈمىدكى ، ئۇ يېقىندا بولىدۇ›› دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,51,' شۇ كۈندە اﷲ سىلەرنى چاقىرىدۇ، سىلەر ئۇنىڭغا ھەمدى ئېيتىپ ئىجابەت قىلىسىلەر ۋە (دۇنيادا) پەقەت ئازغىنا تۇرغاندەك ئويلايسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,52,' بەندىلىرىمگە ئېيتقىنكى، ئۇلار ياخشى سۆزلەرنى قىلسۇن، شۈبھىسىزكى، شەيتان ئۇلارنىڭ ئارىسىدا پىتنە قوزغايدۇ، شەيتان ئىنسانغا ھەقىقەتەن ئاشكارا دۈشمەندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,53,' اﷲ سىلەرنى ئوبدان بىلىدۇ، اﷲ سىلەرگە رەھىم قىلىدۇ، خالىسا سىلەرنى ئازابلايدۇ ، سېنى ئۇلار (يەنى كاپىرلار) غا ھامىي قىلىپ ئەۋەتكىنىمىز يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,54,' پەرۋەردىگارىڭ ئاسمانلاردىكى، زېمىندىكى بەندىلىرىنىڭ ئەھۋالىنى ھەممىدىن ئوبدان بىلىدۇ، بىز ھەقىقەتەن پەيغەمبەرلەرنىڭ بەزىسىنى بەزىسىدىن ئۈستۈن قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,55,' داۋۇتقا زەبۇرنى ئاتا قىلدۇق، سىلەر اﷲ نى قويۇپ خۇدا دەپ ئويلىغانلىرىڭلارنى چاقىرىڭلار ئۇلارنىڭ قولىدىن سىلەردىن بالانى كۆتۈرۈۋېتىشمۇ ۋە ئۇنى (باشقىلارغا) يۆتكەپ قويۇشمۇ كەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,56,' ئۇلار (اﷲ نى قويۇپ ئىلاھ دەپ) ئىبادەت قىلىدىغانلارنىڭ (ئۆزلىرى ئىبادەت ئارقىلىق) پەرۋەردىگارىغا يېقىن بولۇشنى تىلەيدۇ، ئۇلارنىڭ ئارىسىدا (پەرۋەردىگارىغا) يېقىنراق بولغىنى (ھەم ئىبادەت ئارقىلىق اﷲ قا يېقىن بولۇشنى تىلەيدۇ)، پەرۋەردىگارىنىڭ رەھمىتىنى ئۈمىد قىلىپ (ئۇنىڭ) ئازابىدىن قورقىدۇ، پەرۋەردىگارىنىڭ ئازابى ھەقىقەتەن ساقلىنىشقا تېگىشلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,57,' (اﷲ قا ئاسىيلىق قىلغان) ھەرقانداق بىر شەھەر بولمىسۇن، ئۇنى بىز قىيامەت كۈنىدىن بۇرۇن ھالاك قىلىمىز، ياكى ئۇنى قاتتىق ئازابلايمىز، بۇ لەۋھۇلمەھپۇزدا پۈتۈلۈپ كەتكەن (ھۆكۈم) دۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,58,' (قۇرەيش مۇشرىكلىرى تەلەپ قىلغان) مۆجىزىلەرنى مەيدانغا كەلتۈرمەسلىكىمىز پەقەت بۇرۇنقىلار (يەنى ئىلگىرىكى ئۈممەتلەرنىڭ) مۆجىزىلەرنى يالغانغا چىقارغانلىقى ئۈچۈندۇر، بىز سەمۇدقا چىشى تۆگىنى روشەن (مۆجىزە) قىلىپ بەردۇق، ئۇلار ئۇنى ئىنكار قىلدى بىز مۆجىزىلەرنى پەقەت (بەندىلەرنى) قورقۇتۇش ئۈچۈنلا ئەۋەتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,59,' ئۆز ۋاقتىدا بىز ساڭا پەرۋەردىگارىڭنىڭ ھەقىقەتەن كىشىلەرنىڭ (ئەھۋالى) نى تولۇق بىلىدىغانلىقىنى ئېيتتۇق، بىز ساڭا (مىراج كېچىسى ئاسمان ـ زېمىندىكى ئاجايىپلاردىن) كۆرسەتكەن كۆرۈنۈشنى، قۇرئاندىكى لەنەت قىلىنغان دەرەخنى (يەنى قۇرئاندا زىكرى قىلىنغان زەققۇم دەرىخىنى) پەقەت كىشىلەر ئۈچۈن سىناق قىلدۇق، بىز ئۇلارنى (يەنى مۇشرىكلارنى) قورقۇتىمىز بىزنىڭ قورقۇتىشىمىز ئۇلارنىڭ يولسىزلىقىنى تېخىمۇ كۈچەيتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,60,' ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: «ئادەمگە سەجدە قىلىڭلار‍‌‍!» دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى ئىبلىس: «سەن لايدىن ياراتقانغا سەجدە قىلامدىمەن؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,61,' ئىبلىس: «ماڭا ئېيتىپ بەرگىنە، سەن مەندىن ئۈستۈن قىلغان ئادەم مۇشۇمۇ؟ ئەگەر ماڭا قىيامەتكىچە مۆھلەت بېرىدىغان بولساڭ، ئۇنىڭ ئەۋلادىنىڭ ئازغىنىسىدىن باشقىسىنى (ئازدۇرۇپ) تۈپ يىلتىزىدىن قۇرۇتۇۋېتىمەن (يەنى ئۇلارنى خالىغانچە يېتىلەيمەن)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,62,' اﷲ (ئىبلىسقا) ئېيتتى؛ «بارغىن (ساڭا مۆھلەت بەردىم)، ئۇلاردىن (يەنى ئادەم بالىلىرىدىن) كىمكى ساڭا ئەگىشىدىكەن، (جەھەننەم ساڭا ۋە ئۇلارغا) بېرىلگەن تولۇق جازادۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,63,' ئۇلارنىڭ ئىچىدىن قوزغىتالايدىغانلىكى ئادىمىڭنى ئاۋازىڭ بىلەن (يەنى گۇناھقا چاقىرىشىڭ بىلەن) قوزغاتقىن، ئاتلىق ۋە پىيادە قوشۇنىڭ بىلەن ئۇلارغا ھۇجۇم قىلغىن، ئۇلارنىڭ ماللىرىغا ۋە بالىلىرىغا شېرىك بولغىن، ئۇلارغا (يالغان) ۋەدىلەرنى بەرگىن ـ شەيتاننىڭ ئۇلارغا بەرگەن ۋەدىسى پەقەت ئالدامچىلىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,64,' شۈبھىسىزكى، مېنىڭ (ئىخلاسمەن) بەندىلىرىم ئۈستىدىن سەن ھۆكۈمرانلىق قىلالمايسەن پەرۋەردىگارىڭ ھەقىقەتەن ھامىي بولۇشقا يېتەرلىكتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,65,' پەرۋەردىگارىڭلار، سىلەرنى پەزلىنى (يەنى پەزلىدىن بولغان رىزقىڭلارنى) تەلەپ قىلسۇن دەپ، كېمىلەرنى سىلەر ئۈچۈن دېڭىزدا ماڭدۇرۇپ بېرىدۇ، ئۇلار ھەقىقەتەن سىلەرگە ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,66,' سىلەر دېڭىزدا بىرەر ئاپەتكە يولۇققان چېغىڭلاردا، ئېسىڭلارغا سىلەر چوقۇنۇۋاتقان مەبۇدلار كەلمەي، پەقەت اﷲ لا كېلىدۇ (يەنى ئىنسان مۇنداق چاغدا اﷲ تىن باشقىغا ئىلتىجا قىلمايدۇ)، اﷲ سىلەرنى (غەرق بولۇشتىن) قۇتقۇزۇپ، (ئامان ـ ئېسەن) قۇرۇقلۇققا چىقارغاندا يۈز ئۆرۈيسىلەر (يەنى ئىخلاسىڭلار تۈگەپ ئاۋۋالقى ھالىتىڭلارغا قايتىۋالىسىلەر) ئىنسان كۇفرىلىق قىلغۇچىدۇر (يەنى اﷲ نىڭ نېمەتلىرىنى ئىنكار قىلىش ئىنساننىڭ تەبىئىتىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,67,' (دېڭىزدا غەرق بولۇپ كېتىشتىن قۇتۇلغىنىڭلار بىلەن) اﷲ نىڭ سىلەرنى يەرگە يۇتقۇزۇۋېتىشىدىن، ياكى سىلەرگە ئاسماندىن تاش ياغدۇرۇپ ھالاك قىلىشىدىن، ئاندىن سىلەرنى (اﷲ نىڭ ئازابىدىن ساقلايدىغان) ھېچ ھامىي تاپالماسلىقىڭلاردىن قورقمامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,68,'ياكى اﷲ نىڭ سىلەرنى دېڭىزدا قايتا قاتناتقۇزۇپ (دېڭىزدىكى چېغىڭلاردا) قارا بوران چىقىرىپ، كۈفرىلىق قىلغانلىقىڭلار تۈپەيلىدىن سىلەرنى غەرق قىلىۋېتىشىدىن، ئاندىن سىلەرگە بىزدىن ئۆچ ئېلىپ بېرىدىغان ھېچ ئەھدى تاپالماسلىقىڭلاردىن قورقمامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,69,'شەك ـ شۈبھىسىزكى، بىز ئادەم بالىلىرىنى ھۆرمەتلىك قىلدۇق، ئۇلارنى قۇرۇقلۇقتا (ئۇلۇغلارغا) مىندۇردۇق، دېڭىزدا (كېمىلەرگە) چىقاردۇق، ئۇلارنى شېرىن يېمەكلىكلەر بىلەن رىزىقلاندۇردۇق، ئۇلارنى مەخلۇقاتلىرىمىزنىڭ نۇرغۇنىدىن ئۈستۈن قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,70,' بىز ھەممە ئادەمنى ئۇلارنىڭ نامە ـ ئەمالى بىلەن چاقىرىدىغان كۈننى (ئېسىڭدا تۇتقىن)، نامە ـ ئەمالى ئوڭ قولىدىن بېرىلگەنلەر ئۆزلىرىنىڭ نامە ـ ئەمالىنى ئوقۇيدۇ، ئۇلارغا قىلچە زورلۇق قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,71,' بۇ دۇنيادا (دىلى) كۆر بولغان ئادەم ئاخىرەتتىمۇ كۆر ۋە تېخىمۇ گۇمراھ بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,72,' ئۇلار سېنى بىز ساڭا ۋەھىي قىلغان قۇرئانغا خىلاپلىق قىلدۇرۇپ، بىزگە قۇرئاندىن غەيرىنى ئىپتىرا قىلدۇرۇشقا تاس قالدى، (ئۇلارنىڭ كۆڭلىدىكىدەك قىلغىنىڭدا) ئەلۋەتتە ئۇلار سېنى (ئۆزلىرىگە) دوست قىلىۋالاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,73,' سېنى بىز (ھەقتە تۇرۇشتا) مۇستەھكەم قىلمىغان بولساق، ئۇلارغا مايىل بولۇپ كەتكىلى ھەقىقەتەن تاس قالغان ئىدىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,74,' ئەگەر شۇنداق قىلىدىغان بولساڭ، ئەلۋەتتە ساڭا دۇنيا ۋە ئاخىرەتتە ھەسسىلەپ ئازاب بەرگەن بولاتتۇق، ئاندىن سەن بىزگە قارشى ياردەم بېرىدىغان ھېچ ئادەم تاپالمايتتىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,75,' (يەھۇدىيلار) سېنى (مەدىنە) زېمىنىدىن چىقىرىۋېتىش ئۈچۈن، ئۇ يەردىن سېنى خاتىرجەم تۇرغۇزماسلىققا تاس قالدى، شۇنداق بولغاندا، سەن چىقىپ كەتكەندىن كېيىن، ئۇلار ئازغىنا ۋاقىت تۇرالايتتى (يەنى ئازغىنا ۋاقىتتىن كېيىن ھالاك بولاتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,76,' (بۇ) سەندىن ئىلگىرى بىز ئەۋەتكەن پەيغەمبەرلىرىمىزنىڭ تۇتقان يولىدۇر (يەنى پەيغەمبەرلىرىنى ئارىسىدىن ھەيدەپ چىقارغان ھەرقانداق ئۈممەتنى ھالاك قىلىش اﷲ نىڭ پەيغەمبەرلىرى ھەققىدە تۇتقان يولىدۇر)، تۇتقان يولىمىزدا ئۆزگىرىش تاپالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,77,' (ئى مۇھەممەد!) كۈن قايرىلغان ۋاقىتتىن تارتىپ قاراڭغۇ چۈشكەن ۋاقىتقا قەدەر ناماز ئوقۇغىن  بامدات نامىزىنىمۇ ئوقۇغىن، (كۈندۈزنىڭ ۋە كېچىنىڭ پەرىشتىلىرى) چوقۇم بامدات نامىزىدا ھازىر بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,78,' ساڭا نەپلە (ئىبادەت) بولۇشى ئۈچۈن، كېچىنىڭ بىر قىسىمىدا (ناماز ئوقۇشقا، قۇرئان ئوقۇشقا) ئويغانغىن، (ئى مۇھممەد‍!( پەرۋەردىگارىڭنىڭ سېنى مەدھىيىلىنىدىغان ئورۇنغا (يەنى بۈيۈك شاپائەت ماقامىغا) تۇرغۇزۇشى ھەقىقەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,79,' «پەرۋەردىگارىم! مېنى (قەبرەمگە) ئوڭۇشلۇق كىرگۈزگىن، (قەبرەمدىن) ئوڭۇشلۇق چىقارغىن، ماڭا دەرگاھىڭدىن ھەققە ياردەم بېرىدىغان قۇۋۋەت ئاتا قىلغىن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,80,' «ھەقىقەت (يەنى ئىسلام) كەلدى، باتىل (يەنى كۈفرى) يوقالدى باتىل ھەقىقەتەن ئوڭاي يوقىلىدۇ» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,81,' بىز مۆمىنلەرگە (يەنى ئۇلارنىڭ دىللىرىغا) شىپا ۋە رەھمەت بولىدىغان قۇرئان ئايەتلىرىنى نازىل قىلىمىز، قۇرئان كاپىرلارغا زىياندىن باشقىنى زىيادە قىلمايدۇ (يەنى ئۇلار قۇرئاننى تەستىق قىلمىغانلىقتىن، ئۇلارنىڭ كۇفرى تېخىمۇ ئاشىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,82,' ئىنسانغا(تۈرلۈك نېمەتلەرنى) بەرسەك (پەرۋەردىگارىنىڭ ئىبادىتىدىن) يۈز ئۆرۈيدۇ، (چوڭچىلىق قىلىپ) پەرۋەردىگارىدىن يىراقلىشىدۇ، ئۇنىڭغا بىرەر كۆڭۈلسىزلىك يەتسە، (اﷲ نىڭ رەھمىتىدىن) مەيۈسلىنىپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,83,' ھەر ئادەم (ھىدايەت ۋە گۇمراھلىقتا) ئۆز يولى بويىچە ئىش قىلىدۇ، پەرۋەردىگارىڭلار كىمنىڭ توغرا يولدا ئىكەنلىكىنى ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,84,' ئۇلار سەندىن روھنىڭ ماھىيىتى ھەققىدە سورىشىدۇ ‹‹روھنىڭ ماھىيىتىنى پەرۋەردىگارىم بىلىدۇ›› دېگىن، سىلەرگە پەقەت ئازغىنا ئىلىم بېرىلگەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,85,' ئەگەر بىز خالىساق ساڭا ۋەھيى قىلغان قۇرئاننى ئەلۋەتتە (دىللاردىن ۋە مۇسھەپلەردىن) كۆتۈرۈۋەتتۇق، ئاندىن ئۇنى ئەسلىگە كەلتۈرۈشكە بىزگە قارشى ھېچقانداق ھامىي تاپالمايتتىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,86,' پەقەت پەرۋەردىگارىڭ رەھمەت قىلىش يۈزىسىدىن ئۇنى كۆتۈرۈۋەتمىدى اﷲ نىڭ رەھمىتى ساڭا ھەقىقەتەن چوڭ بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,87,' ئېيتقىنكى، «ئەگەر ئىنسانلار، جىنلار بۇ قۇرئاننىڭ ئوخشىشىنى مەيدانغا كەلتۈرەلمەيدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,88,' بۇ قۇرئاندا ئىنسانلار ئۈچۈن تۈرلۈك مەسىللەرنى خىلمۇ ـ خىل شەكىلدە بايان قىلدۇق، (روشەن پاكىتلارنىڭ بولىشىغا قارىماي) ئىنسانلارنىڭ تولىسى پەقەت (ھەقىقەتنى) ئىنكار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,89,' ئۇلار ئېيتتى: «تاكى سەن بىزگە يەر ئاستىدىن بىر بۇلاقنى ئېتىلدۇرۇپ چىقارمىغىچە ساڭا ھەرگىز ئىشەنمەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,90,' ياكى سېنىڭ خورمىلىق، تاللىق ۋە ئوتتۇرىسىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان بىر بېغىڭ بولسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,91,' ياكى سەن ئېيتقاندەك ئۈستىمىزگە ئاسماننى پارچە ـ پارچە قىلىپ چۈشۈرگىن، ياكى (پەيغەمبەرلىكىڭگە) گۇۋاھچى قىلىپ اﷲ نى ۋە پەرىشتىلەرنى ئالدىمىزغا كەلتۈرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,92,' ياكى سېنىڭ ئالتۇندىن ئۆيۈڭ بولسۇن، ياكى سەن (شوتا بىلەن) ئاسمانغا چىققىن، تاكى (اﷲ تەرىپىدىن سېنىڭ ئۇنىڭ بەندىسى ۋە پەيغەمبەرلىكى ئىكەنلىكىڭ يېزىلغان) بىز ئوقۇيالايدىغان بىر كىتابنى ئېلىپ چۈشمىگىچە سېنىڭ ئاسمانغا چىققانلىقىڭغا ھەرگىز ئىشەنمەيمىز» (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «اﷲ پاكتۇر، مەن پەقەت بىر پەيغەمبەر ئىنسانمەن (اﷲ نىڭ ئىزنى بولمىسا ھېچ ئادەم بىرەر مۆجىزە كەلتۈرەلمەيدۇ)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,93,' ئۇلار (يەنى مۇشرىكلار) نىڭ ئۆزلىرىگە ھىدايەت (يەنى پەيغەمبەر بىلەن قۇرئان) كەلگەن چاغدا ئىمان ئېيتماسلىقلىرى «اﷲ ئىنساننى پەيغەمبەر قىلىپ ئەۋەتەمدۇ؟» دېگەنلىكلىرى ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,94,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «مۇبادا زېمىندا (يەنى زېمىن ئەھلىدەك) مېڭىپ يۈرۈيدىغان ، ئولتۇراقلاشقان پەرىشتىلەر بولسا ئىدى، ئەلۋەتتە ئۇلارغا ئاسماندىن پەرىشتىنى پەيغەمبەر قىلىپ چۈشۈرەتتۇق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,95,' (ئى مۇھەممەد‍!( ئېيتقىنكى، «اﷲ مەن بىلەن سىلەرنىڭ ئاراڭلاردا مېنىڭ راستلىقىمغا گۇۋاھ بولۇشقا يېتەرلىكتۇر اﷲ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر كۆرۈپ تۇرغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,96,' كىمكى اﷲ ھىدايەت قىلغان ئىكەن ئۇ ھىدايەت تاپقۇچىدۇر، اﷲ گۇمراھ قىلغان ئادەمگە اﷲ تىن باشقا (ئازابتىن قۇتۇلدۇرىدىغان) ھېچ ياردەمچىلەرنى تاپالمايسەن، قىيامەت كۈنى ئۇلارنى دۈم ياتقۇزۇپ (سۆرەلگەن) كۆر، گاچا، گاس ھالدا يىغىمىز، ئۇلارنىڭ جايى جەھەننەم بولىدۇ، (جەھەننەمنىڭ) ئوتى پەسلەپ قالسا، ئۇلارغا (ئوتنى) تېخىمۇ يالقۇنجىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,97,' ئۇلارنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ۋە «چىرىگەن سۆڭەك ۋە توپىغا ئايلانغىنىمىزدىن كېيىن راستلا قايتىدىن تېرىلىپ يېڭىدىن ئادەم بولامدۇق؟» دېگەنلىكلىرى ئۈچۈن، ئۇلارغا بېرىلگەن جازا ئەنە شۇدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,98,' ئاسمانلارنى، زېمىننى ياراتقات اﷲ نىڭ ئۇلارغا ئوخشاش ئىنسانلارنى يارىتالايدىغانلىقىنى ئۇلار بىلمەمدۇ؟ اﷲ ئۇلارنىڭ (ئۆلۈشى ۋە تىرىلىشى ئۈچۈن) شەك ـ شۈبھىسىز مۇئەييەن ۋاقىت بەلگىلىدى، كاپىرلار پەقەت كۇفرىغىلا ئۇنايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,99,' «ئەگەر پەرۋەردىگارىمنىڭ رەھمەت خەزىنىلىرى سىلەرنىڭ قولۇڭلاردا بولسا، چىقىمدىن (يەنى تۈگەپ كېتىشىدىن) قورقۇپ، چوقۇم بېخىللىق قىلاتتىڭلار، ئىنسان بېخىل كېلىدۇ» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,100,' بىز مۇساغا ھەقىقەتەن توققۇز روشەن مۆجىزە ئاتا قىلدۇق، مۇسا پىرئەۋنگە ۋە ئۇنىڭ قەۋمىگە كەلگەن چاغدا بىز مۇساغا: «(ئۇلاردىن) ئىسرائىل ئەۋلادىنى (قويۇۋېتىشنى) سۆرىغىن» (دېدۇق) پىرئەۋن ئۇنىڭغا: «ئى مۇسا! سېنى مەن چوقۇم جادۇ قىلىنغان دەپ ئويلايمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,101,' مۇسا: «(ئى پىرئەۋن!) بۇلار (يەنى توققۇز مۆجىزە) نى پەقەت ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارى (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) دەلىل قىلىپ نازىل قىغانلىقىنى سەن (گەرچە تېلىڭدا ئىقرار قىلمىساڭمۇ دىلىڭدا ئەلۋەتتە) ئوبدان بىلىسەن، مەن سېنى، ئى پىرئەۋن، ھەقىقەتەن، ھالاك بولىدۇ دەپ ئويلايمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,102,' پىرئەۋن ئۇلارنى (مىسىر) زېمىنىدىن چىقىرىۋەتمەكچى بولدى، بىز پىرئەۋننى ۋە ئۇنىڭ بىلەن بىللە بولغانلارنى ـ ھەممىسىنى (دېڭىز) غا غەرق قىلىۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,103,' پىرئەۋننى غەرق قىلىۋەتكەندىن كېيىن، بىز ئىسرائىل ئەۋلادىغا: «(مىسىر) زېمىنىدا تۇرۇڭلار، ئاخىرەت ۋەدىسى (يەنى قىيامەت) كەلگەن چاغدا سىلەرنى (قەبرەڭلاردىن مەھشەرگاھقا) (كاپىرلار بىلەن مۆمىنلەرنى) ئارىلاش ئېلىپ كېلىمىز» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,104,' قۇرئاننى ھەقىقەت ئاساسىدا نازىل قىلدۇق، قۇرئانمۇ ھەقىقەتنى ئۆز ئىچىگە ئالغان ھالدا نازىل بولدى، سېنى بىز پەقەت خوش خەۋەر بەرگۈچى، ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,105,' قۇرئاننى كىشىلەرگە دانە ـ دانە ئوقۇپ بېرىشىڭ ئۈچۈن ئۇنى بۆلۈپ ـ بۆلۈپ نازىل قىلدۇق، ئۇنى تەدرىجىي نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,106,' «قۇرئانغا ئىشىنىڭلار ياكى ئىشەنمەڭلار (مەيلى، چۈنكى ئىشىنىشىڭلار ئۇنىڭغا كامالەت، ئىشەنمەسلىكىڭلار ئۇنىڭغا نۇقسان ئېلىپ كەلمەيدۇ)» دېگىن شۈبھىسىزكى، قۇرئان نازىل بولۇشتىن ئىلگىرى كىتاب بېرىلگەنلەر قۇرئاننى ئاڭلىغان چاغلىرىدا (تەسىرلىنىپ) دەرھال سەجدىگە بارىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,107,' ئۇلار: «پەرۋەردىگارىمىز پاكتۇر، پەرۋەردىگارىمىزنىڭ ۋەدىسى چوقۇم ئورۇنلىنىدۇ» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,108,' ئۇلار يىغلىغان ھالدا سەجدە قىلىشقا يىقىلىدۇ (قۇرئاننى ئاڭلاش بىلەن ئۇلار اﷲ قا) تېخىمۇ تەزەررۇ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,109,' «اﷲ دەپ ئاتىساڭلارمۇ بولىدۇ، رەھمان دەپ ئاتىساڭلارمۇ بولىدۇ، قايسىنى ئاتىساڭلار (ھەممىسى ياخشىدۇر) چۈنكى اﷲ نىڭ نۇرغۇن گۈزەل ئىسىملىرى بار» دېگىن، نامىزىڭدا (قىرائەتنى) (مۇشرىكلار ئاڭلاپ ساڭا ئەزىيەت يەتكۈزمەسلىگى ئۈچۈن) يۇقىرى ئاۋاز بىلەنمۇ ئوقۇمىغىن، (مۆمىنلەر ئاڭلىماي قالماسلىقى ئۈچۈن) پەس ئاۋاز بىلەنمۇ ئوقۇمىغىن ئۇلارنىڭ ئارىسىدا ئوتتۇرا يول تۇتقىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,17,110,' ئېيتقىنكى، «جىمى ھەمدۇسانا بالىسى بولۇشتىن پاك بولغان، سەلتەنىتىدە شېرىكى بولمىغان، خارلىقتىن قۇتۇلۇش ئۈچۈن دوستقا موھتاج بولمىغان (يەنى خار بولمىغان، ياردەمچىگە موھتاج بولمىغان) اﷲ قا خاستۇر! اﷲ نى مۇكەممەل رەۋىشتە ئۇلۇغلىغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,1,'جىمى ھەمدۇسانا اﷲ قا خاستۇركى، (اﷲ ئىنسانلارنى) ئۆز تەرىپىدىن بولغان قاتتىق ئازابتىن ئاگاھلاندۇرۇش، ياخشى ئەمەللەرنى قىلغان مۆمىنلەرگە ئوبدان مۇكاپات (يەنى جەننەت) بېرىدىغانلىقى بىلەن خۇش خەۋەر بېرىش ئۈچۈن، بەندىسى (يەنى پەيغەمبىرى مۇھەممەد) گە توغرا، ھېچقانداق قىڭغىرلىق (يەنى ئىختىلاپ، زىددىيەت) بولمىغان قۇرئاننى نازىل قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,2,' ئۇلار (يەنى ياخشى ئەمەللەرنى قىلغان مۆمىنلەر) جەننەتتە مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,3,' اﷲ نىڭ بالىسى بار دېگۈچىلەرنى ئاگاھلاندۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,4,' ئۇلار ۋە ئۇلارنىڭ ئاتا ـ بوۋىلىرى بۇ بوھتانلىرىدا ھېچقانداق مەلۇماتقا ئىگە ئەمەس، ئۇلارنىڭ ئېغىزلىرىدىن چىققان سۆز نېمىدېگەن چوڭ! ئۇلار پەقەت يالغاننىلا ئېيتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,5,' ئەگەر ئۇلار قۇرئانغا ئىمان ئېيتمىسا، ئەپسۇسلىنىپ، ئۇلارنىڭ (ئىماندىن) يۈز ئۆرۈگەنلىكىدىن ئۆزۈڭنى ھالاك قىلىۋېتىشىڭ مۇمكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,6,' ئىنسانلارنىڭ قايسىلىرىنىڭ ئەمەلى ئەڭ ياخشى ئىكەنلىكىنى سىناش ئۈچۈن، بىز ھەقىقەتەن يەر يۈزىدىكى شەيئىلەرنىڭ ھەممىسىنى يەر يۈزىنىڭ زىننىتى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,7,' بىز چوقۇم يەر يۈزىدىكى (دەل ـ دەرەخ، تاغ ۋە بىنا قاتارلىق) نەرسىلەرنىڭ ھەممىسىنى يوق قىلىپ، يەر يۈزىنى تۈپتۈز باياۋانغا ئايلاندۇرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,8,' ئەسھابۇلكەھفنى ۋە رەقىم (يەنى ئەسھابۇلكەھفنىڭ ئىسىملىرى يېزىلغان تاختا) نى (بىزنىڭ قۇدرىتىمىزنى كۆرسىتىپ بېرىدىغان) ئالامەتلىرىمىز ئىچىدىكى ئەڭ قىزىقى دەپ قارىدىڭمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,9,' ئۆز ۋاقتىدا بىر قانچە يىگىت غارنى پاناھ جاي قىلىۋالدى، ئۇلار: «پەرۋەردىگارىمىز! بىزگە رەھمەت خەزىنىلىرىڭدىن ئاتا قىلغىن، بىزنىڭ ئىشلىرىمىزنى تۈزەپ، بىزنى ھىدايەت تاپقۇچىلاردىن قىلغىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,10,' ئۇلارنى بىز غاردا ئۇزۇن يىللار ئۇخلىتىپ قويدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,11,' ئاندىن ئىككى گۇرۇھ (يەنى ئەسھابۇلكەھف ئىچىدىكى قانچىلىك ئۇخلىغانلىقلىرى ھەققىدە ئىختىلاپ قىلىشقان ئىككى گۇرۇھ) تىن قايسىسىنىڭ غاردا قانچىلىك ئۇزاق تۇرۇشقانلىرىنى ئىنچىكە ھېسابلايدىغانلىقىنى سىناش ئۈچۈن ئۇلارنى ئويغاتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,12,' بىز ساڭا ئۇلارنىڭ ھېكايىسىنى راستلىقى بىلەن ئېيتىپ بېرىمىز، ئۇلار ھەقىقەتەن پەرۋەردىگارىغا ئىمان ئېيتقان، بىز ھىدايەتنى زىيادە قىلغان يىگىتلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,13,' ئۇلارنىڭ دىللىرىنى (يەنى ئىرادىسىنى) مۇستەھكەم قىلدۇق، ئۆز ۋاقتىدا ئۇلار (ئىمانسىز زالىم پادىشاھ ئالدىدا) دەس تۇرۇپ ئېيتتى: «بىزنىڭ پەرۋەردىگارىمىز ئاسمانلارنىڭ ۋە زېمىنىڭ پەرۋەردىگارىدۇر، بىز ئۇنى قويۇپ ھەرگىزمۇ باشقا ئىلاھقا ئىبادەت قىلمايمىز، ئەگەر بىز) اﷲ نىڭ شېرىكى بار) دەيدىغان بولساق، بۇ چاغدا ھەقىقەتەنمۇ تولىمۇ يىراقلاشقان بولىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,14,' بىزنىڭ قەۋمىمىز اﷲ نى قويۇپ (بۇتلارنى) ئىلاھ قىلىۋالدى، ئۇلار ئۆزلىرىنىڭ ئىلاھلارغا ئىبادەت قىلىشقا تېگىشلىك ئىكەنلىكىگە نېمىشقا ئېنىق دەلىل كەلتۈرمەيدۇ، اﷲ قا يالغاننى چاپلىغان ئادەمدىنمۇ زالىم كىم بار؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,15,' (ئى يىگىتلەر!) سىلەر ئۇلاردىن ۋە ئۇلارنىڭ اﷲ تىن باشقا ئىبادەت قىلىدىغان بۇتلىرىدىن ئايرىلغان ئىكەنسىلەر، غارنى پاناھ جاي قىلىڭلار، پەرۋەردىگارىڭلار سىلەرگە كەڭ رەھمەت قىلىدۇ، اﷲ سىلەرنىڭ ئىشلىرىڭلارنى ئاسانلاشتۇرۇپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,16,' كۈن چىققان چاغدا ئۇلارنىڭ غارنىڭ ئوڭ تەرىپىگە ئۆتۈپ كەتكەنلىكىنى، كۈن پاتقان چاغدا ئۇلارنىڭ غارنىڭ شىمال تەرىپىگە ئۆتۈپ كەتكەنلىكىنى كۆرۈسەن (يەنى غارنىڭ ئىشكى شىمال تەرەپتە بولغانلىقتىن كۈننىڭ نۇرى غارنىڭ ئىككى تەرىپىگە چۈشۈپ تۇرىدۇ، اﷲ ئۇلارنى ھۆرمەتلىگەنلىكتىن، كۈن چىققاندىمۇ، پاتقاندىمۇ ئۇنىڭ نۇرى ئۇلارنىڭ ئۈستىگە چۈشمەيدۇ)، ئۇلار بولسا غارنىڭ (ئوتتۇرىسىدىكى) بوشلۇقتۇر، بۇ اﷲ نىڭ (قۇدرىتىنى كۆرسىتىپ بېرىدىغان) ئالامەتلەردىندۇر، كىمنىكى اﷲ ھىدايەت قىلىدىكەن، ئۇ (ھەقىقىي) ھىدايەت تاپقۇچىدۇر، كىمنىكى (قىلغان يامان ئەمەلى تۈپەيلىدىن) اﷲ ئازدۇرىدىكەن، ئۇنىڭغا يول كۆرسەتكۈچى دوستنى ھەرگىز تاپالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,17,' (مۇبادا ئۇلارنى كۆرىدىغان بولساڭ، كۆزلىرىنىڭ ئوچۇقلۇقى ۋە ئۆرۈلۈپ تۇرىدىغانلىقتىن) ئۇلارنى ئويغاق دەپ ئويلايسەن، ھالبۇكى، ئۇلار ئۇيقۇدىدۇر، ئۇلارنى بىز ئوڭ ۋە سول تەرەپكە ئۆرۈپ تۇرىمىز ئۇلارنىڭ ئىتى (غار) نىڭ بوسۇغىسىغا ئىككى پۇتنى سوزۇپ ياتىدۇ، ئەگەر ئۇلارنى كۆرىدىغان بولساڭ، ئەلۋەتتە ئۇلاردىن قاتتىق قورقۇپ، يۈز ئۆرۈپ چوقۇم قاچقان بولاتتىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,18,' ئۇلارنى ئۇخلاتقىنىمىزدەك، (غاردا قانچىلىك تۇرغانلىقلىرىنى) ئۆز ـ ئارا سوراشسۇن دەپ (ئۆلۈمگە ئوخشاش ئۇزاق ئويقۇدىن) ئۇلارنى ئويغاتتۇق، ئۇلارنىڭ بىرى «(بۇ غاردا) قانچە ئۇزۇن تۇردۇڭلار؟» دېدى ئۇلار: «بىر كۈن ياكى بىر كۈندىنمۇ ئاز تۇردۇق» دېيىشتى ئۇلارنىڭ (بەزىسى) دېدىكى، «قانچە ئۇزاق تۇرغانلىقىڭلارنى پەرۋەردىگارىڭلار ئوبدان بىلىدۇ (بۇنى سۈرۈشتۈرۈشنىڭ پايدىسى يوق، بىز ھازىر ئاچ)، بىرىڭلارنى بۇيرۇڭلار، بۇ تەڭگىلەرنى شەھەرگە ئېلىپ بېرىپ، كىمنىڭ تامىقى ئەڭ پاكىز بولسا، ئۇنىڭدىن سىلەرگە سېتىۋېلىپ ئېلىپ كەلسۇن، ئۇ (شەھەرگە كىرىشتە ۋە تاماق سېتىۋېلىشتا) ئېھتىيات قىلسۇن سىلەرنى ھېچ كىشىگە تۇيدۇرمىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,19,' ئەگەر شەھەردىكى كىشلەر سىلەرنى تۇتۇۋالسا، سىلەرنى چالما كېسەك قىلىپ ئۆلتۈرىدۇ، ياكى سىلەرنى ئۆزلىرىنىڭ دىنىغا كىرىشكە مەجبۇرلايدۇ، مۇنداقتا ھەرگىزمۇ مەقسىتىڭلارغا ئېرىشەلمەيسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,20,' ئۇلارنى ئۇخلىتىپ ئويغاتقىنىمىزدەك، كىشىلەرگە اﷲ نىڭ (قايتا تىرىلدۈرۈش توغرىسىدىكى) ۋەدىسىنىڭ ھەقلىقىنى ۋە قىيامەتنىڭ بولىدىغانلىقىدا شەك ـ شۈبھە يوق ئىكەنلىكىنى بىلدۈرۈش ئۈچۈن، كىشىلەرنى ئۇلارنىڭ ئەھۋالىدىن خەۋەردار قىلدۇق ئۆز ۋاقتىدا ئۇلار (ئەسھابۇلكەھف قەبزى روھ قىلىنغاندىن كېيىن) ئەسھابۇلكەھفنىڭ ئىشى ئۈستىدە دەتالاش قىلىشتى، بەزى كىشلەر: «(ئۇلارنىڭ يېنىغا ھېچ كىشىنىڭ كىرمەسلىكى ئۈچۈن) ئەسھابۇلكەھفنىڭ ئەتراپىغا (يەنى غارنىڭ ئىشىكىگە) بىر تام ئېتىڭلار» دېدى، اﷲ ئەسھابۇلكەھفنىڭ ئەھۋالىنى ھەممىدىن ئوبدان بىلىدۇ، گېپى ئۆتىدىغان باشلىقلار (يەنى پادىشاھ ۋە شەھەر كاتتىباشلىرى): «غارنىڭ ئەتراپىغا (يەنى غارنىڭ ئىشكى ئالدىغا) ئەلۋەتتە بىر مەسجىد سالايلى» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,21,' ئۇلار (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ زامانىدىكى ناسارالار): «ئۇلار (يەنى ئەسھابۇلكەھف) ئۈچ بولۇپ، تۆتىنچىسى ئۇلارنىڭ ئىتىدۇر» دەيدۇ، بەزىلەر: «ئۇلار بەش بولۇپ، ئالتىنچىسى ئۇلارنىڭ ئىتىدۇر» دەيدۇ، بۇ پۈتۈنلەي ئاساسسىز، قارىسىغا (ئېيتىلغان سۆزدۇر)، يەنە بەزىلەر: «ئۇلار يەتتە بولۇپ، سەككىزىنچىسى ئۇلارنىڭ ئىتىدۇر» دەيدۇ، ئېيىتقىنكى، «ئۇلارنىڭ سانىنى پەرۋەردىگارىم ئوبدان بىلىدۇ، ئۇندىن باشقا ئازغىنا كىشىلەر بىلىدۇ» ئۇلار توغرىسىدا (ناسارالار بىلەن) پەقەت يۈزەگىنە مۇنازىرىلەشكىن (يەنى بۇ ھەقتە مۇنازىرىلەشكۈچىلەرنى تەستىقمۇ قىلمايدىغان، يالغانغىمۇ چىقارمايدىغان دەرىجىدە مۇنازىرىلەشكىن) ئۇلارنىڭ ھېچبىرىدىن ئەسھابۇلكەھف قىسسىسىنى سورىمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,22,' سەن بىرەر ئىشنى قىلماقچى بولساڭ، «ئەتە شۇ ئىشنى چوقۇم قىلىمەن» دېمەي');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,23,'، «خۇدا خالىسا» دېگەن سۆزنى قوشۇپ دېگىن (يەنى خۇدا خالىسا قىلىمەن دېگىن)، (خۇدا خالىسا دېگەن سۆزنى ئۇنتۇپ قالساڭ، پەرۋەردىگارىڭنى تىلغا ئالغىن (يەنى «خۇدا خالىسا» نى ئېسىڭگە كەلگەندە ئېيتقىن) ھەمدە: «پەرۋەردىگارىمنىڭ مېنى بۇنىڭدىنمۇ يېقىن توغرا يولغا باشلىشىنى (يەنى دىنىم، دۇنيايىمدا مېنى ئەڭ تۈزۈك ئىشلارغا مۇۋەپپەق قىلىشىنى) ئۈمىد قىلىمەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,24,' ئۇلار ئۆز غارىدا ئۈچيۈز توققۇز يىل تۇردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,25,' ئېيىتقىنكى، «ئۇلارنىڭ تۇرغان ۋاقتىنى اﷲ ئوبدان بىلىدۇ، ئاسمانلاردىكى، زېمىندىكى غەيبنى (بىلىش) اﷲ قا خاستۇر اﷲ نىڭ كۆرۈشى نېمىدېگەن مۇكەممەل! اﷲ نىڭ ئاڭلىشى نېمىدېگەن مۇكەممەل! ئۇلارغا اﷲ تىن بۆلەك ھېچقانداق ياردەمچى بولمايدۇ، اﷲ ئۆز ھۆكمىدە ھېچ كىشىنى شېرىك قىلمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,26,' (ئى مۇھەممەد!) سەن پەرۋەردىگارىڭنىڭ كىتابىدىن ساڭا ۋەھيى قىلىنغاننى ئوقۇغىن، اﷲ نىڭ سۆزلىرىنى ھېچ كىشى ئۆزگەرتەلمەيدۇ، سەن ھەرگىزمۇ اﷲ تىن بۆلەك ئىلتىجاگاھ تاپالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,27,' سەن پەرۋەردىگارىڭنىڭ رازىلىقىنى تىلەپ، ئەتىگەن ـ ئاخشامدا ئۇنىڭغا ئىبادەت قىلىدىغانلار (يەنى ئاجىز، كەمبەغەل مۇسۇلمانلار) بىلەن سەۋرچان بىللە بولغىن، ھاياتىي دۇنيانىڭ زىبۇزىننىتىنى (يەنى مۇشرىكلارنىڭ چوڭلىرىنىڭ سۆھبىتىنى) دەپ، ئۇلارنى كۆزگە ئىلماي قالمىغىن، بىز دىلىنى زىكرىمىزدىن غەپلەتتە قالدۇرغان، نەپسى خاھىشىغا ئەگەشكەن، ئىشى ھەددىدىن ئاشقان ئادەمنىڭ (سۆزى) گە ئەگەشمىگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,28,' (ئى مۇھەممەد!) «(بۇ) ھەق (قۇرئان) پەرۋەردىگارىڭلار تەرىپىدىن نازىل بولىدۇ، خالىغان ئادەم ئىمان ئېيتسۇن، خالىغان ئادەم كاپىر بولسۇن» دېگىن، بىز ھەقىقەتەن كاپىرلار ئۈچۈن تۈتۈن پەردىلىرى ئۇلارنى ئورىۋالىدىغان ئوتنى تەييارلىدۇق، ئۇلار (تەشنالىقتىن) سۇ تەلەپ قىلسا، ئۇلارغا مەدەن ئېرىتمىسىگە ئوخشاش، يۈزلەرنى كۆيدۈرۈۋېتىدىغان (ناھايىتى قىزىق) سۇ بېرىلىدۇ، بۇ نېمىدېگەن يامان شاراب! جەھەننەم نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,29,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا كېلىدىغان بولساق، بىز ئۇلارنىڭ قىلغان ياخشى ئەمەللىرىنىڭ ساۋابىنى ھەقىقەتەن يوققا چىقىرىۋەتمەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,30,' ئەنە شۇلار ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان، ئۇلار دائىم تۇرىدىغان جەننەتلەردىن بەھرىمەن بولىدۇ، جەننەتتە ئۇلار ئالتۇن بىلەيزۈكلەرنى سالىدۇ، نېپىز ۋە قېلىن يىپەكتىن ئىشلەنگەن يېشىل كىيىملەرنى كىيىدۇ (ئالتۇن ۋە ئېسىل رەختلەر بىلەن قاپلانغان) تەختلەرگە يۆلىنىپ ئولتۇرىدۇ بۇ نېمىدېگەن ياخشى مۇكاپات! جەننەت نېمىدېگەن گۈزەل جاي');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,31,'ئۇلار (يەنى كەمبەغەللەرنى يېنىڭدىن قوغلىۋېتىشنى تەلەپ قىلغان كاپىرلار) غا مۇنداق (بىرى مۆمىن، بىرى كاپىر بولغان) ئىككى ئادەمنى مىسال قىلىپ كەلتۈرگىن: ئۇلارنىڭ بىرىگە (يەنى كاپىرغا) بىز ئەتراپى خورمىزارلىق، (ئىككى باغنىڭ) ئوتتۇرىسى ئېكىنزارلىق بولغان تاللىق ئىككى باغ ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,32,' ھەر ئىككىلىسى كەم ـ كۇتىسىز، تولۇق مېۋە بېرىدىغان باغلار بولۇپ، ئۇلارنىڭ ئارىسىدىن ئېقىپ تۇرىدىغان ئۆستەڭنى بەرپا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,33,' ئۇ (يەنى باغ ئىگىسى) نۇرغۇن مال ـ مۈلۈككە ئىگە ئىدى، ئۇ بۇرادىرىگە پەخىرلىنىپ: « مەن سەندىن باي، ئادەملىرىم (يەنى خىزمەتچىلىرىم، بالىلىرىم، ياردەمچىلىرىم) سېنىڭدىن كۆپ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,34,' ئۇ (مۆمىن بۇرادىرىنى يېتىلەپ) بېغىغا كىرىپ (كۇفرىلىق قىلىش بىلەن) ئۆزىگە زۇلۇم قىلغان ھالدا ئېيتتى: «بۇ باغ مەڭگۈ يوقالمايدۇ دەپ ئويلايمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,35,' شۇنىڭدەك قىيامەت بولمايدۇ دەپ ئويلايمەن، ئەگەر مەن پەرۋەردىگارىمغا قايتۇرۇلغاندىمۇ (يەنى قىيامەت بولغاندىمۇ) ئەلۋەتتە بۇ باغدىن ياخشىراق جاينى تاپىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,36,' ئۇنىڭغا (مۆمىن) بۇرادىرى مۇنازىرىلەشكەن ھالدا ئېيتتى: «سېنى (ئەسلىدە) تۇپراقتىن، ئاندىن ئابىمەنىيدىن يارىتىپ، ئاندىن سېنى راۋۇرۇس ئىنسان قىلغان زاتنى ئىنكار قىلامسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,37,'لېكىن مەن پەرۋەردىگارىم بولغان ئۇ اﷲ نى (ئېتىراپ قىلىمەن)، پەرۋەردىگارىمغا ھېچ ئەھەدىنى شېرىك كەلتۈرمەيمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,38,' سەن بېغىڭغا كىرگەن چېغىڭدا: اﷲ نىڭ خالىغىنى بولىدۇ، اﷲ نىڭ ياردىمى بولمىسا قولۇمدىن ھېچ ئىش كەلمەيدۇ دېسەڭچۇ! سېنىڭ قارىشىڭچە، مېنىڭ مېلىم ۋە ئەۋلادىم سېنىڭدىن ئاز بولسىمۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,39,'، ئۈمىدكى، پەرۋەردىگارىم ماڭا سېنىڭ بېغىڭدىن ياخشىراق نەرسە ئاتا قىلغاي بېغىڭغا ئاسماندىن بىر ئاپەت يۈزلەندۈرۈش بىلەن بېغىڭ تۈپتۈز قاقاس يەرگە ئايلىنىپ قالغاي');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,40,'، ياكى ئۇنىڭ سۈيى سىڭىپ كېتىپ ئۇنى ئىزدەش (يەنى ئەسلىگە كەلتۈرۈش) سېنىڭ قولۇڭدىن كەلمەيدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,41,' ئۇنىڭ مېۋىلىرى (يەنى بېغى) ۋەيران بولدى، تېلىنىڭ بىدىشلىرى يەرگە يىقىلدى، ئۇ باغقا سەرپ قىلغان چىقىملىرىغا (ھەسرەت چېكىپ) ئىككى ئالقىنىنى ئۇۋۇلىغىلى تۇردى ھەمدە ئۇ: «مەن پەرۋەردىگارىمغا ھېچ ئەھەدىنى شېرىك كەلتۈرمىسەمچۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,42,' ئۇنىڭغا اﷲ تىن باشقا ياردەم قىلىدىغان (يەنى ئۇنىڭدىن بۇ بالانى دەپئى قىلىدىغان) ھېچ جامائە بولمىدى، ئۆزىگىمۇ ئۆزى ياردەم قىلالمىدى (يەنى بالانى ئۆزىدىن دەپئى قىلالمىدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,43,' بۇ يەردە (يەنى مۇنداق ھالەتتە) مەدەتكار بولۇش پەقەت بەرھەق اﷲ قا خاستۇر، اﷲ (ئۆزىگە ئىمان ئېيتقۇچىلارغا دۇنيا ۋە ئاخىرەتتە) ياخشى مۇكاپات بەرگۈچىدۇر، (ئۇنىڭغا يۆلەنگەن ۋە ئۇنى ئۈمىد قىلغانلارنىڭ) ئاقىۋىتىنى ياخشى قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,44,' (ئى مۇھەممەد!) ئۇلارغا (يەنى كىشىلەرگە) دۇنيا تىرىكچىلىكى ھەققىدە مۇنداق مىسالنى كەلتۈرگىن: (ئۇ شۇنىڭغا) ئوخشايدۇكى، بىز ئاسماندىن يامغۇر ياغدۇردۇق، شۇ سۇ بىلەن زېمىننىڭ ئۆسۈملۈكلىرى بىر ـ بىرىگە چىرمىشىپ بولۇق ئۆستى، كېيىن ئۇ قۇرۇپ تۆكۈلدى، شاماللار ئۇنى (تەرەپ ـ تەرەپكە) ئۇچۇرۇپ كەتتى، اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,45,' ماللار ۋە بالىلار دۇنيا تىرىكچىلىكىنىڭ زىننىتىدۇر، باقى قالىدىغان ياخشى ئەمەللەرنىڭ ساۋابى پەرۋەردىگارىڭنىڭ نەزىرىدە تېخىمۇ ياخشىدۇر ئۈمىد تېخىمۇ چوڭدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,46,' ئۇ كۈندە (يەنى قىيامەت كۈنىدە) بىز تاغلارنى يوق قىلىمىز، زېمىننى ئوچۇق، تۈپتۈز كۆرىسەن (يەنى ئۇنى توسۇپ تۇرىدىغان تاغ، دەرەخ، بىنالار بولمايدۇ)، ئۇلارنى (يەنى ئىنسانلارنى) (مەھشەرگاھقا) يىغىمىز، ئۇلاردىن بىر كىشىنىمۇ قالدۇرۇپ قويمايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,47,' ئۇلار (يەنى جىمى خالايىق) سەپ ـ سەپ بولۇشۇپ پەرۋەردىگارىڭغا توغرىلىنىدۇ، (كاپىرلارغا) «سىلەر بىزنىڭ ئالدىمىزغا سىلەرنى دەسلەپتە قانداق ياراتقان بولساق، شۇ ھالەتتە (مالسىز، بالىسىز، يالىڭاچ) كەلدىڭلار، بەلكى سىلەر، سىلەرگە ۋەدە قىلىنغان چاغنى بېكىتمەيدۇ، دەپ ئويلىغانسىلەر (يەنى ئۆلگەندىن كېيىن تىرىلىش، ھېساپ بېرىش، جازاغا تارتىلىش يوق دەپ ئويلىغانسىلەر)» دەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,48,' كىشىلەرنىڭ نامە ـ ئەمالى ئوتتۇرىغا قويۇلىدۇ، گۇناھكارلارنىڭ ئۇنىڭدىكى خاتىرىلەردىن قورققانلىقىنى كۆرىسەن، ئۇلار: «ۋاي بىزگە! بۇ نامە ـ ئەمالغا چوڭ ـ كىچىك گۇناھنىڭ ھەممىسى خاتىرىلىنىپتىغۇ؟ » دەيدۇ، ئۇلار قىلغان ـ ئەتكەنلىرىنىڭ ھەممىسىنىڭ نامە ـ ئەمالىغا خاتىرىلەنگەنلىكىنى كۆرىدۇ، پەرۋەردىگارىڭ ھېچ ئادەمگە زۇلۇم قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,49,' ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: «ئادەمگە سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى، ئىبلىس جىنلاردىن ئىدى شۇڭا ئۈ پەرۋەردىگارىنىڭ ئىتائىتىدىن چىقتى مېنى قويۇپ ئىبلىسنى ۋە ئۇنىڭ ئەۋلادىنى دوست قىلىۋالامسىلەر؟ ھالبۇكى، ئۇلار سىلەرگە دۈشمەندۇر، ئىبلىس زالىملار ئۈچۈن نېمىدېگەن يامان بەدەل! (يەنى اﷲ قا ئىبادەت قىلىشنىڭ ئورنىغا شەيتانغا چوقۇنۇش نېمىدېگەن يامان!)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,50,' ئۇلار (يەنى شەيتانلار) غا ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىنىمۇ، ئۇلارنىڭ ئۆزلىرىنىڭ يارىتىلىشىنىمۇ كۆرسەتكىنىم يوق (يەنى مەزكۇرلارنى خەلق ئەتكەن چېغىمدا ئۇلارنى ھازىر قىلغىنىم يوق)، (كىشىلەرنى) ئازدۇرغۇچىلارنى ياردەمچى قىلىۋالغىنىممۇ يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,51,' شۇ كۈندە اﷲ (ئۇلارغا) ئېيتىدۇ: «سىلەر مېنىڭ شېرىكلىرىم دەپ ئويلىغىنىڭلارنى چاقىرىڭلار» ئۇلار چاقىرىدۇ، لېكىن ئۇلار (نىڭ شېرىكلىرى) ئىجابەت قىلمايدۇ، بىز ئۇلارنىڭ ئارىسىدا (ئۇلار ئۆتەلمەيدىغان) ھالاكەتلىك جاينى پەيدا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,52,' گۇناھكارلار دوزاخنى كۆرۈپ، ئۇنىڭغا كىرىدىغانلىقىغا جەزم قىلىدۇ، دوزاختىن قاچىدىغان جاي تاپالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,53,' بۇ قۇرئاندا ئىنسانلارغا تۈرلۈك مىساللارنى بايان قىلدۇق ئىنسان كۆپ دەتالاش قىلىدۇ (يەنى كۆپ جېدەللىشىش ئىنساننىڭ تەبىئىتىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,54,' ئىنسانلارغا ھىدايەت كەلگەن چاغدا، ئۇلارنىڭ ئىمان ئېيتماسلىقلىرى ۋە پەرۋەردىگارىدىن مەغپىرەت تەلەپ قىلماسلىقلىرى پەقەت (اﷲ نىڭ) بۇرۇنقىلارغا تۇتقان يولىنى (يەنى ھالاك قىلىشنى كۈتىدىغانلىقلىرى) ياكى ئۆزلىرىگە كېلىدىغان ئازابنى كۆزلىرى بىلەن كۆرۈشنى (كۈتىدىغانلىقلىرى) ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,55,' بىز پەيغەمبەرلەرنى پەقەت (ئىمان ئېيتقۇچىلارغا) خوش خەۋەر بەرگۈچى، (ئاسىيلىق قىلغۇچىلارنى) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتىمىز كاپىرلار (پەيغەمبەرلەر ئېلىپ كەلگەن) ھەقىقەتنى يوققا چىقىرىش ئۈچۈن بىھۇدە سۆزلەرنى قىلىپ مۇنازىرىلىشىدۇ، ئۇلار مېنىڭ ئايەتلىرىمنى ۋە ئۇلارغا بېرىلگەن ئاگاھلاندۇرۇشلارنى مەسخىرە (يەنى ئويۇنچۇق) قىلىۋالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,56,' پەرۋەردىگارىنىڭ ئايەتلىرى بىلەن پەند ـ نەسىھەت قىلىنغان، ئۇ ئايەتلەردىن يۈز ئۆرۈگەن ۋە ئۆزىنىڭ قىلغان گۇناھلىرىنى ئۇنتۇغان كىشىدىنمۇ زالىم كىشى بارمۇ؟ شۈبھىسىزكى، بىز ئۇلارنىڭ دىللىرىغا قۇرئاننى چۈشىنىشكە توسقۇنلۇق قىلىدىغان پەردىلەرنى تارتتۇق، قۇلاقلىرىنى ئېغىر قىلدۇق، ئەگەر ئۇلارنى ھىدايەتكە دەۋەت قىلساڭ، ئۇلار ھەرگىز ھىدايەت تاپمايدۇ (يەنى ئۇلارنى ئىمانغا، قۇرئانغا دەۋەت قىلساڭ، ئۇلار دەۋىتىڭنى ھەرگىز قوبۇل قىلمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,57,' پەرۋەردىگارىڭ ناھايىتى مەغپىرەت قىلغۇچىدۇر ۋە رەھىم قىلغۇچىدۇر ئەگەر اﷲ ئۇلارنى قىلمىشىغا قاراپ جازالايدىغان بولسا، ئەلۋەتتە ئۇلارغا قىلىنىدىغان ئازابنى تېزلەتكەن بولاتتى، لېكىن ئۇلارغا بەلگىلەنگەن بىر ۋاقىت بار، (ئۇ ۋاقىت كەلگەندە) ئۇلار اﷲ تىن بۆلەك ھېچ پاناگاھ تاپالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,58,' شۇ شەھەرلەرنىڭ ئاھالىلىرى (يەنى ھۇد، سالىھ، لۇت، شۇئەيب پەيغەمبەرلەرنىڭ قەۋملىرىگە ئوخشاش ئۆتكەنكى ئۈممەتلەر) زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئۇلارنى ھالاك قىلدۇق، ئۇلارنىڭ ھالاك بولۇشىغا مۇئەييەن ۋاقىتنى بەلگىلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,59,' ئۆز ۋاقتىدا مۇسا ياش خادىمىغا (يەنى يۇشەئ ئىبىن نۇنغا): «ئىككى دەريانىڭ قوشۇلىدىغان جايىغا يەتمىگىچە مېڭىشىمنى توختاتمايمەن، (شۇ جايغا يەتكەنگە قەدەر) ئۇزاققىچە مېڭىۋېرىمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,60,' ئۇلار ئىككى دەريانىڭ قوشۇلىدىغان جايىغا يېتىپ بارغاندا، بېلىقىنى ئۇنتۇپ قالدى، بېلىق دەرياغا يول ئېلىپ كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,61,' ئۇلار (ئىككى دەريانىڭ قوشۇلىدىغان جايىدىن) ئۆتكەندە مۇسا ياش خادىمىغا: «ئەتىگەنلىك تامىقىمىزنى ئېلىپ كەلگىن، بۇ سەپىرىمىزدە ھەقىقەتەن چارچاپ كەتتۇق» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,62,' ياش خادىم: «بىز چوڭ تاش ئاستىدا ئارام ئېلىۋاتقاندا (يۈز بەرگەن ئىشتىن يەنى پىشۇرۇلغان بېلىقنىڭ تىرىلىپ، سېۋەتتىن سەكرەپ چىقىپ دەرياغا كىرىپ كەتكەنلىكىدىن ئىبارەت قىزىق ئىشتىن) خەۋىرىڭ بارمۇ؟ مەن راستلا بېلىقنى ئۇنتۇپتىمەن بېلىقنىڭ (ئاجايىپ ۋەقەسى) نى ساڭا ئېيتىشنى پەقەت شەيتان ماڭا ئۇنتۇلدۇرۇپتۇ، بېلىق دەرياغا ئاجايىپ يول ئالغان ئىدى» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,63,' مۇسا: «بىزنىڭ ئىزدەيدىغىنىمىز دەل مۇشۇ ئىدى» دېدى ـ دە، ئىككىسى كەلگەن ئىزى بويىچە كەينىگە يېنىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,64,' ئۇ ئىككىسى (بېلىقنى ئۇنتۇغان چوڭ تاشنىڭ يېنىدا) بىزنىڭ بەندىلىرىمىزدىن چوڭ مەرھەمىتىمىزگە ئېرىشكەن ۋە بىزنىڭ ئۆزىمىزگە خاس ئىلمىمىزنى (يەنى ئىلمىي غەيبنى) بىز بىلدۈرگەن بىر بەندىنى (يەنى خىزىر ئەلەيھىسسالامنى) ئۇچراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,65,' مۇسا ئۇنىڭغا: «اﷲ ساڭا بىلدۈرگەن توغرا ئىلىمنى ماڭا ئۆگىتىشىڭ ئۈچۈن ساڭا ئەگىشەيمۇ؟ (يەنى سۆھبىتىڭدە بولايمۇ؟)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,66,' ئۇ ئېيتتى: «سەن مەن بىلەن بىللە بولۇشقا سەۋر ـ تاقەت قىلىپ تۇرالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,67,' سەن چوڭقۇر تونۇپ يەتمىگەن (يەنى قارىماققا يامان، ئەمما ماھىيىتىنى سەن چۈشەنمىگەن) ئىشقا قانداقمۇ سەۋر ـ تاقەت قىلىپ تۇرالايسەن؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,68,' مۇسا ئېيتتى: «اﷲ خالىسا مېنى سەۋر قىلغۇچى كۆرىسەن، سېنىڭ بۇيرۇقۇڭغا خىلاپلىق قىلمايمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,69,' (خىزىر) ئېيىتتى: «ئەگەر سەن ماڭا ئەگەشسەڭ، (يولۇققان ئىش توغرىسىدا) مەن ئۇنى ساڭا سۆزلەپ بەرگىچە ئۇنىڭدىن سورىما (يەنى ئۆزۈم سۆزلەپ بەرمىگىچە قىلغان ئىشلىرىمدىن سورىما)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,70,' شۇنىڭ بىلەن ئۇ ئىككىسى بىرلىكتە مېڭىپ كېلىپ بىر كېمىگە چىقىشتى، (خىزىر) كېمىنى تېشىۋەتتى (يەنى كېمىنىڭ بىر تاختىسىنى سۇغۇرۇۋەتتى)، مۇسا (ئۇنىڭغا): «كېمىدىكىلەرنى غەرق بولۇپ كەتسۇن دەپ كېمىنى تەشتىڭمۇ؟ سەن ھەقىقەتەن (قورقۇنچلۇق) چوڭ بىر ئىشنى قىلدىڭ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,71,' (خىزىر) سەن ھەقىقەتەن مەن بىلەن بىللە بولۇشتا سەۋر ـ تاقەت قىلىپ تۇرالمايسەن دېمىدىممۇ؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,72,' مۇسا ئېيتتى: «ئەھدىنى ئۇنتۇپ قالغانلىقىمغا مېنى ئەيىبلىمە، سەن بىلەن بىللە بولۇشتا مېنى قىيىن ھالغا چۈشۈرۈپ قويما»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,73,' ئۇ ئىككىسى يەنى بىرلىكتە ماڭدى، ئۇلار بىر بالىنى ئۇچراتتى، (خىزىر) ئۇنى ئۆلتۈرۈپ قويدى مۇسا: «سەن بىر بىگۇناھ جاننى ناھەق ئۆلتۈردۈڭ، سەن ھەقىقەتەن ياۋۇز ئىشنى قىلدىڭ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,74,' (خىزىر) ئېيتتى: «ساڭا مەن ھەقىقەتەن مەن بىلەن بىللە بولۇشقا سەۋر ـ تاقەت قىلىپ تۇرالمايسەن دېمىدىممۇ؟ »');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,75,' مۇسا ئېيتتى: «بۇنىڭدىن كېيىن يەنە سەندىن بىرەر ئىش توغرۇلۇق سورىسام (يەنى قىلغان ئىشىڭغا ئېتىراز بىلدۈرسەم)، مېنى ئۆزۈڭگە ھەمراھ قىلمىغىن، (ساڭا بەرگەن ۋەدەمگە ئۈچ قېتىم خىلاپلىق قىلغانلىقىم ئۈچۈن مېنىڭ ئالدىمدا (ماڭا ھەمراھ بولماسلىقتا) مەزۇرسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,76,' ئۇ ئىككىسى يەنە بىللە مېڭىپ بىر شەھەرگە يېتىپ كەلدى، شەھەر ئاھالىسىدىن تاماق سورىدى، شەھەر ئاھالىسى ئۇلارنى مېھمان قىلىشتىن باش تارتتى، ئۇلار بۇ شەھەردە ئۆرۈلۈپ چۈشەي دەپ قالغان بىر تامنى ئۇچراتتى، (ئۇنى خىزىر قولى بىلەن سىناپ) تۈزلەپ قويدى مۇسا ئېيتتى: «ئەگەر خالىساڭ بۇ ئىش ئۈچۈن ئەلۋەتتە ئىش ھەققى ئالغان بولاتتىڭ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,77,' (خىزىر) ئېيتتى: «بۇ، سەن بىلەن مېنىڭ ئايرىلىش (ۋاقتىمىز)، سەن سەۋر ـ تاقەت قىلىپ تۇرالمىغان (يۇقىرىقى ئۈچ ئىشنىڭ) ھەقىقىتىنى ساڭا چۈشەندۈرۈپ بېرەي');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,78,' كېمىگە كەلسەك، ئۇ دېڭىزدا ئىشلەيدىغان بىرقانچە نەپەر كەمبەغەلنىڭ كېمىسى ئىدى، مەن ئۇنى تېشىپ ئەيبناك قىلماقچى بولدۇم، (چۈنكى) ئۇلارنىڭ ئالدىدا (يەنى بارىدىغان يوللىرىدا) ھەرقانداق ساق كېمىنى ئىگىسىدىن (زورلۇق بىلەن) تارتىۋالىدىغان بىر پادىشاھ بار ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,79,' (ئۆلتۈرۈلگەن) بالىغا كەلسەك، ئۇنىڭ ئاتا ـ ئانىسى مۆمىن ئىدى (ئۇ ئەسلىدە كاپىر يارىتىلغانلىقتىن، چوڭ بولغاندا) ئۇنىڭ گۇمراھلىق بىلەن كۇفرىنى ئاتا ـ ئانىسىغا تېڭىشىدىن قورقتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,80,' شۇنىڭ ئۈچۈن بىز ئۇلارنىڭ پەرۋەردىگارىنىڭ ئۇلارغا ئۇ بالىغا قارىغاندا پاك ۋە كۆيۈملۈك پەرزەنت ئاتا قىلىشىنى ئىرادە قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,81,' تامغا كەلسەك، ئۇ شەھەردىكى ئىككى يېتىم بالىنىڭ ئىدى، تام ئاستىدا ئۇلارنىڭ (ھەققى بولغان) خەزىنىسى بار ئىدى (تام ئۆرۈلۈپ كەتسە، خەزىنىسى ئېچىلىپ قېلىپ كىشىلەر ئۇنى ئېلىپ كېتەتتى)، ئۇلارنىڭ ئاتا ـ ئانىسى ياخشى ئادەم ئىدى پەرۋەردىگارىڭ ئۇلارنىڭ چوڭ بولغاندا تام ئاستىدىكى خەزىنىسىنى چىقىرىۋېلىشىنى ئىرادە قىلدى، بۇ پەرۋەردىگارىڭنىڭ (ئۇلارغا قىلغان) رەھمىتىدۇر، ئۇنى (يەنى يۇقىرىقى ئۈچ ئىشنى) مەن ئۆز خاھىشىم بىلەن قىلغىنىم يوق (بەلكى ئۇنى اﷲ نىڭ ئەمرى ۋە ئىلھامى بىلەن قىلدىم)، سەن سەۋر ـ تاقەت قىلىپ تۇرالمىغان نەرسىلەرنىڭ ھەقىقىتى ئەنە شۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,82,' ئۇلار (يەنى يەھۇدىيلار) سەندىن زۇلقەرنەين توغرۇلۇق سورىشىدۇ «سىلەرگە مەن ئۇنىڭ قىسسىسىنى (ۋەھيى ئاساسىدا) بايان قىلىمەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,83,' ئۇنىڭغا بىز زېمىندا ھەقىقەتەن كۈچ ـ قۇدرەت ئاتا قىلدۇق، ئۇنىڭغا ئۆز مەقسىتىگە يېتىشنىڭ پۈتۈن يوللىرىنى ئاسانلاشتۇرۇپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,84,' زۇلقەرنەين (اﷲ ئۇنىڭغا ئاسانلاشتۇرۇپ بەرگەن) يولغا ماڭدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,85,' ئۇ كۈنپېتىش تەرەپكە يېتىپ بارغاندا، كۈننىڭ قارا لايلىق بۇلاققا پېتىپ كېتىۋاتقانلىقىنى ھېس قىلدى  ئۇ بۇلاقنىڭ يېنىدا بىر تۈرلۈك قەۋمنى ئۇچراتتى بىز: «ئى زۇلقەرنەين! سەن ئۇلارنى جازالىغىن ياكى ئۇلارغا ياخشى مۇئامىلىدە بولغىن» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,86,' زۇلقەرنەين ئېيتتى: «كىمكى زۇلۇم قىلغان بولسا، بىز ئۇنى جازالايمىز، ئاندىن ئۇ پەرۋەردىگارىڭنىڭ دەرگاھىغا قايتۇرۇلىدۇ، پەرۋەردىگارى ئۇنى قاتتىق جازالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,87,' كىمكى ئىمان ئېيتسا ۋە ياخشى ئەمەل قىلسا، ئۇنىڭغا ئەڭ ياخشى مۇكاپات بېرىلىدۇ، ئۇنى قولاي ئىشقا بۇيرۇيمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,88,' ئاندىن زۇلقەرنەين (اﷲ ئۇنىڭغا قولايلىق قىلىپ بەرگەن) يولغا ماڭدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,89,' زۇلقەرنەين كۈنچىقىش تەرەپكە بارغاندا، كۈننىڭ شۇنداق بىر قەۋمنىڭ ئۈستىگە چۈشۈۋاتقانلىقىنى كۆردىكى، ئۇ قەۋمگە بىز (كۈننىڭ ھارارىتىدىن) ساقلىنىدىغان (كىيىم، ئىمارەت قاتارلىق) نەرسىلەرنى ئاتا قىلمىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,90,' ئەھۋال شۇنداق بولدى (يەنى زۇلقەرنەين كۈنچىقىش تەرەپتىكى كىشىلەرگىمۇ كۈنپېتىش تەرەپتىكى كىشىلەرگە قىلغان مۇئامىلىنى قىلدى) بىز ئۇنىڭ قولىدىكى نەرسىلەردىن تولۇق خەۋەردار ئىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,91,' ئاندىن ئۇ (اﷲ ئۇنىڭغا قولايلىق قىلىپ بەرگەن) يولغا ماڭدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,92,' زۇلقەرنەين ئىككى تاغ ئارىسىغا يېتىپ بارغاندا، ئىككى تاغ ئالدىدا ھېچقانداق سۆزنى چۈشەنمەيدىغان (يەنى ئۆزىنىڭ تىلىدىن غەيرى تىلنى بىلمەيدىغان) بىر قەۋمنى ئۇچراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,93,' ئۇلار: «ئى زۇلقەرنەين! يەجۇج بىلەن مەجۇج يەر يۈزىدە ھەقىقەتەن بۇزغۇنچىلىق قىلغۇچىلاردۇر، بىز بىلەن ئۇلارنىڭ ئارىسىغا بىر توسما سېلىپ بېرىشىڭ ئۈچۈن، ساڭا بىر قىسىم مېلىمىزنى (باجغا ئوخشاش تۆلىسەك بولامدۇ؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,94,' زۇلقەرنەين ئېيتتى: «اﷲ نىڭ ماڭا بەرگەن نەرسىلىرى (يەنى كۈچ ـ قۇۋۋەت ۋە پادىشاھلىق سىلەرنىڭ ماڭا بېرىدىغان مېلىڭلاردىن) ئارتۇقتۇر، ماڭا ئادەم كۈچى ياردەم قىلىڭلار، ئۇلار بىلەن سىلەرنىڭ ئاراڭلارغا مۇستەھكەم بىر توسما سېلىپ بېرەي');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,95,' ماڭا تۆمۈر پارچىلىرىنى ئېلىپ كېلىڭلار، (تۆمۈر پارچىلىرى دۆۋىلىنىپ) ئىككى تاغنىڭ ئارىسى تەكشى بولغاندا، كۆيۈكلەرنى بېسىڭلار» تۆمۈر پارچىلىرى (قىزىپ) ئوتتەك بولغاندا، ئۇ: «ماڭا (ئېرىتىلگەن مىسنى) بېرىڭلار، ئۈستىگە تۆكەي» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,96,' ئۇلار (يەنى يەجۇج ـ مەجۇج) ئۇنىڭ ئۈستىگە چىقىشقىمۇ، ئۇنى تېشىشكىمۇ قادىر بولالمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,97,' زۇلقەرنەين ئېيتتى: «بۇ (يەنى توسمىنىڭ بەرپا قىلىنىشى) پەرۋەردىگارىمنىڭ نېمىتىدۇر، پەرۋەردىگارىمنىڭ ۋەدىسى ئىشقا ئاشقاندا، ئۇ ئۇنى تۈپتۈز قىلىۋېتىدۇ، پەرۋەردىگارىمنىڭ ۋەدىسى ھەقتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,98,' ئۇ كۈندە (يەنى قىيامەت كۈنىدە، قىستاڭچىلىقتىن) كىشىلەرنى بىر ـ بىرىگە گىرەلەشتۈرۈۋېتىمىز، سۇر چېلىنىدۇ، ئۇلارنى (ھېساب ئۈچۈن) يېغىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,99,' ئۇ كۈندە كاپىرلارغا جەھەننەمنى ئاشكارا كۆرسىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,100,' ئۇلارنىڭ (بۇ دۇنيادىكى چاغلىرىدا دىللىرىنىڭ) كۆزلىرى مېنى ياد قىلىشتىن پەردىلەنگەن ئىدى، (ئۇلارنىڭ دىللىرىنى زۇلمەت باسقانلىقى ئۈچۈن كالامۇللانى) ئاڭلاشقا قادىر ئەمەس ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,101,' كاپىرلار مېنى قويۇپ بەندىلىرىمنى مەبۇد قىلىۋېلىشقا بولىدۇ دەپ ئويلامدۇ؟ بىز جەھەننەمنى كاپىرلارغا ھەقىقەتەن مەنزىلگاھ قىلىپ تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,102,' سىلەرگە ئەمەللىرى جەھەتتىن ئەڭ زىيان تارتقۇچىلارنى ئېيتىپ بېرەيلىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,103,' ئۇلار ھاياتىي دۇنيادا قىلغان ئەمەللىرى يوققا چىققان ئەمما ئۆزلىرى ئوبدان ئىش قىلدۇق دەپ ئويلىغان ئادەملەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,104,' ئەنە شۇلار پەرۋەردىگارىنىڭ ئايەتلىرىنى ۋە ئۇنىڭغا مۇلاقات بولۇشنى ئىنكار قىلغان، نەتىجىدە قىلغان ئەمەللىرى بىكار بولۇپ كەتكەن كىشىلەردۇر، قىيامەت كۈنى ئۇلارنى (يەنى مۇنداق كىشىلەرنى) قىلچە ئېتىبارغا ئالمايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,105,' كاپىر بولغانلىقلىرى، مېنىڭ ئايەتلىرىمنى ۋە پەيغەمبەرلىرىمنى مەسخىرە قىلغانلىقلىرى ئۈچۈن، ئۇلارنىڭ جازاسى جەھەننەمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,106,' شۈبھىسىزكى، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ مەنزىلگاھى فىردەۋس جەننەتلىرى بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,107,' ئۇلار فىردەۋس جەننەتلىرىدە مەڭگۈ قالىدۇ، ئۇنىڭدىن يۆتكىلىشنى خالىمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,108,' ئېيتقىنكى، «پەرۋەردىگارىمنىڭ سۆزلىرىنى يېزىش ئۈچۈن، ئەگەر دېڭىز (سۈيى) سىيا بولۇپ كەتسە، پەرۋەردىگارىمنىڭ سۆزلىرى تۈگىمەي تۇرۇپ، دېڭىز (سۈيى) چوقۇم تۈگەپ كەتكەن بولاتتى، مۇبادا يەنە شۇنچىلىك دېڭىز (سۈيىنى) كەلتۈرسەكمۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,18,109,' (ئى مۇھەممەد‍!( ئېيتقىنكى، مەن سىلەرگە ئوخشاش بىر ئىنسانمەن، ماڭا ۋەھىي قىلىنىدۇكى، سىلەرنىڭ ئىلاھىڭلار يالغۇز بىر ئىلاھتۇر، كىمكى، پەرۋەردىگارىغا مۇلاقات بولۇشنى ئۈمىد قىلىدىكەن (ساۋابىنى ئۈمىد قىلىپ ئازابىدىن قورقىدىكەن)، ياخشى ئىش قىلسۇن پەرۋەردىگارىغا قىلىدىغان ئىبادەتكە ھېچكىمنى شېرىك كەلتۈرمىسۇن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,1,'كاف، ھا، يا، عين، صاد');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,2,' بۇ، پەرۋەردىگارىڭنىڭ بەندىسى زەكەرىياغا قىلغان مەرھىمىتىنىڭ بايانىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,3,' ئۇ ئۆز ۋاقتىدا پەرۋەردىگارىغا پەس ئاۋازدا مۇناجات قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,4,' ئۇ ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن كۈچ ـ قۇۋۋىتىمدىن كەتتىم، چېچىم ئاقاردى پەرۋەردىگارىم! ساڭا دۇئا قىلىپ ئۈمىدسىز بولۇپ قالغىنىم يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,5,' مەن تاغىلىرىمنىڭ بالىلىرىنىڭ ئورنۇمنى باسالماسلىقلىرىدىن (يەنى دىننى قولدىن بېرىپ قويۇشلىرىدىن) ھەقىقەتەن ئەنسىرەيمەن، مېنىڭ ئايالىم تۇغماستۇر، (پەزلىڭدىن) ماڭا بىر (ياخشى) پەرزەنت ئاتا قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,6,' ئۇ ماڭا ۋارىس بولغاي ۋە يەئقۇب ئەۋلادىغا ۋارىس بولغاي، پەرۋەردىگارىم! ئۇنى سېنىڭ رازىلىقىڭغا ئېرىشىدىغان قىلغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,7,'(اﷲ ئېيتتىكى) «ئى زەكەرىيا! بىز ھەقىقەتەن ساڭا (پەرىشتىلەر ئارقىلىق) يەھيا ئىسىملىك بىر ئوغۇل بىلەن خۇش خەۋەر بېرىمىز، ئىلگىرى ھېچ ئادەمنى ئۇنىڭغا ئىسىمداش قىلمىدۇق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,8,' زەكەرىيا ئېيتتى: «ئايالىم تۇغماس تۇرسا، مەن قېرىپ مۈكچىيىپ كەتكەن تۇرسام، مېنىڭ قانداقمۇ ئوغلۇم بولسۇن؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,9,' (نىدا قىلغان پەرىشتە) ئېيتتى: «ئىش ئەنە شۇنداقتۇر، پەرۋەردىگارىڭ ئېيتتى: بۇ مەن ئۈچۈن ئاساندۇر، سېنى مەن ياراتتىم، ھالبۇكى سەن بۇرۇن يوق ئىدىڭ (يەنى سېنى يوقتىن بار قىلغاندەك، يەھيانى ئىككىڭلاردىن تۆرەلدۈرۈشكە قادىرمەن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,10,' زەكەرىيا ئېيتتى: «پەرۋەردىگارىم! (ئايالىمنىڭ ھامىلدار بولغانلىقىغا) ماڭا بىر ئالامەت قىلىپ بەرسەڭ» اﷲ ئېيتتى: «سېنىڭ ئالامىتىڭ (شۇكى) ساق تۇرۇپ ئۈچ كېچە ـ كۈندۈز كىشىلەرگە سۆز قىلالمايسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,11,' زەكەرىيا نامازگاھىدىن قەۋمىنىڭ ئالدىغا چىقىپ، ئۇلارنى ئەتىگەن ـ ئاخشامدا تەسبىھ ئېيتىشقا ئىشارەت قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,12,' (يەھيا تۇغۇلۇپ چوڭ بولغاندا ئۇنىڭغا) «ئى يەھيا! كىتابنى (يەنى تەۋراتنى) تىرىشىپ ئوقۇغىن» (دېدۇق) ئۇنىڭغا بالىلىق چېغىدىلا ئەقىل ـ پاراسەت ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,13,' بىز ئۇنىڭغا مەرھەمەت قىلىش ۋە ئۇنى (ناچار خىسلەتلەردىن) پاك قىلىش يۈزىسىدىن (شۇنداق قىلدۇق)، يەھيا تەقۋادار ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,14,' ئۇنى ئاتا ـ ئانىسىغا كۆيۈمچان (قىلدۇق)، ئۇ مۇتەكەببىر (يەنى ئاتا ـ ئانىسىنى قاخشاتقۇچى)، (پەرۋەردىگارىغا) ئاسىيلىق قىلغۇچى بولمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,15,' ئۇ تۇغۇلغان كۈنىدە، ۋاپات بولغان كۈنىدە، تىرىلىپ (قەبرىدىن) تۇرغۇزۇلغان كۈنىدە (اﷲ تائالا تەرىپىدىن بولغان) ئامانلىققا ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,16,' (ئۇلارغا) قۇرئاندا مەريەم (قىسسىسى) نى بايان قىلغىن، ئەينى زاماندا ئۇ ئائىلىسىدىن ئايرىلىپ شەرق تەرەپتىكى بىر جايغا باردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,17,' ئۇ پەردە تارتىپ كىشىلەردىن يۇشۇرۇندى، ئۇنىڭغا بىزنىڭ روھىمىزنى (يەنى جىبرىئىل ئەلەيھىسسالامنى) ئەۋەتتۇق، ئۇ مەريەمگە بېجىرىم ئادەم سۈرىتىدە كۆرۈندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,18,' مەريەم ئېيتتى: «مەن ھەقىقەتەن مەرھەمەتلىك اﷲ قا سېغىنىپ سېنىڭ چېقىلىشىڭدىن پاناھ تىلەيمەن، ئەگەر سەن تەقۋادار بولساڭ (ماڭا چېقىلمىغىن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,19,' جىبرىئىل ئېيتتى: «مەن ساڭا بىر پاك ئوغۇل بېرىش ئۈچۈن ئەۋەتىلگەن پەرۋەردىگارىڭنىڭ ئەلچىسىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,20,' مەريەم ئېيتتى: «ماڭا كىشى يېقىنلاشمىغان تۇرسا، مەن پاھىشە قىلمىغان تۇرسام، قانداقمۇ مېنىڭ ئوغۇل بالام بولسۇن؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,21,' جىبرىئىل ئېيتتى: «ئەمەلىيەت سەن دېگەندەكتۇر، (لېكىن پەرۋەردىگارىڭ ئېيتتى: ئۇ ماڭا ئاساندۇر ئۇنى كىشىلەرگە قۇدرىتىمىزنى كۆرسىتىدىغان) دەلىل ۋە بىز تەرەپتىن بولغان مەرھەمەت قىلدۇق، بۇ تەقدىر قىلىنىپ بولغان ئىشتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,22,' مەريەم ھامىلدار بولدى، (ئائىلىسىدىن) يىراق بىر جايغا كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,23,' تولغاقنىڭ قاتتىقلىقى ئۇنى (يۆلىنىۋېلىش ئۈچۈن) بىر خورما دەرىخىنىڭ يېنىغا كېلىۋېلىشقا مەجبۇر قىلدى مەريەم ئېيتتى: «كاشكى مەن بۇنىڭدىن ئىلگىرى ئۆلۈپ كەتكەن بولسامچۇ! كىشىلەر تەرىپىدىن ئۇنتۇلۇپ كەتكەن (ئەرزىمەس) نەرسىگە ئايلىنىپ كەتسەمچۇ!»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,24,' جىبرىئىل ئۇنىڭغا خورما دەرىخىنىڭ ئاستىدىن نىدا قىلدى: «غەم قىلمىغىن، پەرۋەردىگارىڭ سېنىڭ ئاستىڭدىن (ئېقىپ تۇرىدىغان) بىر ئېرىقنى پەيدا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,25,' خورما دەرىخىنى ئۆز تەرىپىڭگە ئىرغىتقىن، ئالدىڭغا پىشقان يېڭى خورمىلار تۆكۈلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,26,' (بۇ شېرىن خورمىدىن) يېگىن، (بۇ تاتلىق سۇدىن ئىچكىن ۋە خۇرسەن بولغىن، بىرەر ئادەمنى كۆرسەڭ (ئۇ بالا توغرۇلۇق سورىسا): مەن ھەقىقەتەن مەرھەمەتلىك اﷲ قا ۋەدە بەردىم، بۈگۈن ھېچ ئادەمگە سۆز قىلمايمەن دېگىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,27,' مەريەم بوۋاقنى (يەنى ئىسا ئەلەيھىسسالامنى) كۆتۈرۈپ قەۋمىنىڭ ئالدىغا كەلدى، ئۇلار ئېيتتى: «ئى مەريەم! سەن ھەقىقەتەن غەلىتە ئىش قىلدىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,28,' ئى ھارۇننىڭ ھەمشىرىسى! سېنىڭ ئاتاڭ يامان ئادەم ئەمەس ئىدى، ئاناڭمۇ يولدىن چىققان خوتۇن ئەمەس ئىدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,29,' مەريەم بوۋاقنى كۆرسەتتى، ئۇلار: «بۆشۈكتىكى بوۋاققا قانداق سۆزلەيمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,30,' بوۋاق ئېيتتى: «مەن ھەقىقەتەن اﷲ نىڭ بەندىسىمەن، ماڭا كىتابنى بەردى (يەنى ئىنجىلنى بېرىدۇ) ۋە مېنى پەيغەمبەر قىلدى (يەنى قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,31,' قەيەردە بولاي مېنى بەرىكەتلىك قىلدى، ھاياتلا بولىدىكەنمەن، ماڭا نامازنى، زاكاتنى ئادا قىلىشنى تەۋسىيە قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,32,' مېنى ئانامغا كۆيۈمچان قىلدى، مېنى مۇتەكەببىر، شەقى قىلمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,33,' مەن تۇغۇلغان كۈنۈمدە، ۋاپات بولغان كۈنۈمدە، تىرىلىپ قەبرەمدىن تۇرغۇزۇلغان كۈنۈمدە (اﷲ تائالا تەرىپىدىن بولغان) ئامانلىققا ئېرىشىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,34,' ئەنە شۇ ئىسا ئىبىن مەريەمنىڭ (قىسسىسىدۇر) ئىسا ئۇلار دەتالاش قىلىۋاتقان ئۆزىنڭ ئىشىنىڭ (ھەقىقىتى) ئۈستىدە ھەق گەپنى قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,35,' «اﷲ نىڭ شەنىگە بالىسى بولۇش لايىق ئەمەس، اﷲ (بالىدىن، شېرىكتىن) پاكتۇر، اﷲ بىرەر ئىش قىلىشنى ئىرادە قىلسا، ئۇنىڭغا ۋۇجۇتقا كەل دەيدۇ ـ دە ئۇ ۋۇجۇتقا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,36,' اﷲ ھەقىقەتەن مېنىڭ پەرۋەردىگارىمدۇر، سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، شۇنىڭ ئۈچۈن ئۇنىڭغا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,37,' ئىسانىڭ ئىشى توغرىسىدا ئۇلار (يەنى ئەھلى كىتاب) ئارىسىدىكى پىرقىلەر ئىختىلاپ قىلىشتى، بۈيۈك كۈن (يەنى قىيامەت كۈنى) كەلگەن چاغدا، كاپىرلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,38,'ئۇلار بىزنىڭ دەرگاھىمىزغا كەلگەن كۈندە (يەنى قىيامەت كۈنىدە) قۇلاقلىرى تازا ئاڭلايدىغان كۆزلىرى تازا كۆرىدىغان بولىدۇ، لېكىن زالىملار بۈگۈن (يەنى بۇ دۇنيادا) روشەن گۇمراھلىقتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,39,' ئۇلارنى (يەنى خالايىقنى) ھەسرەت كۈنى (يەنى قىيامەت كۈنىدىن ئاگاھلاندۇرغىن، ئۇ چاغدا ھەممە ئىش بىر تەرەپ قىلىنغان بولىدۇ ھالبۇكى، ئۇلار غەپلەتتىدۇر، (قىيامەتنى) ئىنكار قىلماقتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,40,' زېمىنغا ۋە زېمىن ئۈستىدىكى بارچىگە چوقۇم بىز ۋارىسلىق قىلىمىز ئۇلار (ھېساب بېرىش ئۈچۈن) بىزنىڭ دەرگاھىمىزغا قايتۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,41,' (ئى مۇھەممەد!) كىتابتا (يەنى قۇرئاندا) ئىبراھىم (قىسسىسى) نى بايان قىلغىن، ئۇ ھەقىقەتەن تولىمۇ راستچىل پەيغەمبەر ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,42,' ئۆز ۋاقتىدا ئىبراھىم ئاتىسىغا ئېيتتى: «ئى ئاتا نېمىشقا ئاڭلىمايدىغان، كۆرمەيدىغان ۋە ساڭا ھېچ نەرسىگە ئەسقاتمايدىغان نەرسىلەرگە چوقۇنىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,43,' ئى ئاتا! ماڭا سەن بىلمەيدىغان ئىلىملەر نازىل بولدى، ماڭا ئەگەشكىن، سېنى توغرا يولغا باشلايمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,44,' ئى ئاتا! شەيتانغا چوقۇنمىغىن، شەيتان مەرھەمەتلىك اﷲ قا ھەقىقەتەن ئاسىيلىق قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,45,' ئى ئاتا! سېنىڭ مەرھەمەتلىك اﷲ نىڭ ئازابىغا قېلىشىڭدىن، شەيتاننىڭ ھەمراھىغا ئايلىنىپ قېلىشىڭدىن قورقىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,46,' ئۇ (يەنى ئازەر ئېيتتى: «ئى ئىبراھىم! سەن مېنىڭ ئىلاھلىرىمدىن يۈز ئۆرۈمەكچىمۇسەن، ئەگەر سەن (مۇنداق قىلىشتىن) يانمىساڭ، چوقۇم سېنى تاش ـ كېسەك قىلىپ ئۆلتۈرىمەن، ئۇزاق زامان مەندىن يىراق تۇرغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,47,' ئىبراھىم ئېيتتى: «ساڭا ئامانلىق بولسۇن، پەرۋەردىگارىمدىن ساڭا مەغپىرەت تىلەيمەن، اﷲ ماڭا ھەقىقەتەن مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,48,' سىلەردىن ۋە سىلەر اﷲ نى قويۇپ چوقۇنۇۋاتقان نەرسىلەردىن يىراق بولىمەن، پەرۋەردىگارىمغا ئىبادەت قىلىمەن، پەرۋەردىگارىمنىڭ ئىبادىتىدىن مەھرۇم بولۇپ قالماسلىقنى ئۈمىد قىلىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,49,' ئۇ ئۇلاردىن ۋە ئۇلارنىڭ اﷲ نى قويۇپ چوقۇنۇۋاتقان نەرسىلىرىدىن يىراقلاشتى، ئۇنىڭغا بىز ئىسھاق ۋە يەئقۇبنى ئاتا قىلدۇق، ئۇ ئىككىسىنىڭ ھەر بىرسىنى پەيغەمبەر قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,50,' ئۇلارغا بىزنىڭ رەھمىتىمىزنى ئاتا قىلدۇق، ئۇلارنىڭ نامىنى بەلەن ۋە يۇقىرى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,51,' كىتابتا (يەنى قۇرئاندا) مۇسا (قىسىسى) نى بايان قىلغىن، شۈبھىسىزكى، ئۇ اﷲ ئىبادىتىگە خاس قىلغان ھەم رەسۇل (يەنى ئەلچى)، ھەم پەيغەمبەر ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,52,' ئۇنىڭغا بىز تۇر تېغىنىڭ ئوڭ تەرىپىدىن نىدا قىلدۇق، ئۇنى بىز مۇناجات ئۈچۈن (دەرگاھىمىزغا) يېقىنلاشتۇردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,53,' ئۇنىڭغا بىز مەرھەمەت قىلىپ قېرىندىشى ھارۇننى پەيغەمبەر قىلىپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,54,' (ئى مۇھەممەد!) كىتابتا (يەنى قۇرئاندا) ئىسمائىل (نىڭ قىسىسى) نى بايان قىلغىن، ئۇ ھەقىقەتەن ۋەدىسىدە راستچىل، ھەم رەسۇل، ھەم پەيغەمبەر ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,55,' ئۇ ئائىلىسىدىكىلەرنى ناماز ئوقۇشقا بۇيرۇيتتى، پەرۋەردىگارىنىڭ دەرگاھىدا ئۇنىڭ رازىلىقىغا ئېرىشكەن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,56,' كىتابتا (يەنى قۇرئاندا) ئىدرىس (قىسىسى) نى بايان قىلغىن، ئۇ ھەقىقەتەن راستچىل پەيغەمبەر ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,57,' ئۇنى بىز يۇقىرى ئورۇنغا كۆتۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,58,' اﷲ نىڭ نېمىتىگە ئېرىشكەن ئەنە شۇ پەيغەمبەرلەر ئادەم (ئەلەيھىسسالام) نىڭ نەسلىدىندۇر، بىز نۇھ بىلەن بىللە كېمىگە سالغانلارنىڭ نەسلىدىندۇر، ئىبراھىمنىڭ نەسلىدىندۇر، ئىسرائىل (يەنى يەئقۇب ئەلەيھىسسالام) نىڭ نەسلىدىندۇر، بىز (ئىمانغا) ھىدايەت قىلغان، (پەيغەمبەرلىككە) تاللىغانلاردىندۇر، ئۇلارغا مەرھەمەتلىك اﷲ نىڭ ئايەتلىرى تىلاۋەت قىلىنغان چاغدا سەجدە قىلغان ۋە يىغلىغان ھالدا (زېمىنغا) يىقىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,59,' ئۇلار كەتكەندىن كېيىن (ئۇلارنىڭ) ئورنىنى باسقان ئورۇنباسارلار نامازنى تەرك ئەتتى، نەپسى خاھىشلىرىغا ئەگەشتى، ئۇلار (قىيامەتتە) گۇمراھلىقنىڭ جازاسىنى تارتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,60,' تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار بۇنىڭدىن مۇستەسنا، ئەنە شۇلارلا جەننەتكە كىرىدۇ، ئۇلارنىڭ (ئەمەللىرىنىڭ ساۋابىدىن) ھېچ نەرسە كېمەيتىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,61,' ئۇ دائىمىي تۇرالغۇ بولغان، مەرھەمەتلىك اﷲ بەندىلىرىگە ۋەدە قىلغان، ئۇلار كۆرمەي تۇرۇپ (ئىشەنگەن) جەننەتلەردۇر، اﷲ نىڭ (جەننەت بىلەن قىلغان) ۋەدىسى چوقۇم ئىشقا ئاشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,62,' ئۇلار جەننەتتە بىھۇدە سۆز ئاڭلىمايدۇ، پەقەت (پەرىشتىلەرنىڭ ئۇلارغا بەرگەن) سالامىنىلا ئاڭلايدۇ، ئۇلار جەننەتتە ئەتىگەن ـ ئاخشامدا (كۆڭلى تارتقان يىمەك ـ ئىچمەكلەردىن) رىزىققا ئىگە بولۇپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,63,' بەندىلىرىمىز ئىچىدىكى تەقۋادارلارغا بىز مىراس قىلىپ بېرىدىغان جەننەت ئەنە شۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,64,' بىز (دۇنياغا) پەرۋەردىگارىڭنىڭ ئەمرى بىلەن چۈشىمىز، بىزنىڭ ئالدىمىزدىكى، ئارقىمىزدىكى ۋە ئالدى ـ كەينىمىز ئارىسىدىكى ئىشلارنىڭ ھەممىسى اﷲ نىڭ ئىلكىدىدۇر، پەرۋەردىگارىڭ (بەندىلىرىنىڭ ئەمەللىرىدىن ھېچ نەرسىنى) ئۇنتۇمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,65,' ئۇ ئاسمانلارنىڭ، زېمىن ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنىڭ پەرۋەردىگارىدۇر، ئۇنىڭغا ئىبادەت قىلغىن، اﷲ نىڭ ئىبادىتىگە چىداملىق بولغىن، سەن ئۇنىڭ شېرىكى بارلىقىنى بىلەمسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,66,' ئىنسان (يەنى ئۆلگەندىن كېيىن تىرىلىشىگە ئېتىقاد قىلمايدىغان كاپىر): «مەن چوقۇم تىرىلەمدىم؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,67,' ئىنسان بىز ئۇنى ئىلگىرى يوقتىن بار قىلغانلىقىمىزنى ئەسلىمەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,68,' پەرۋەردىگارىڭ بىلەن قەسەمكى، ئۇلارنى ئەلۋەتتە (ئۇلارنى ئازدۇرغان) شەيتانلار بىلەن قوشۇپ يىغىمىز، ئاندىن ئۇلارنى جەھەننەمنىڭ چۆرىسىدە تىزلاندۇرۇپ ئولتۇرغۇزغان ھالدا ئەلۋەتتە ھازىر قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,69,' ئاندىن ھەربىر گۇرۇھنىڭ ئىچىدىن مەرھەمەتلىك اﷲ قا ئەڭ ئاسىي بولغانلىرىنى تارتىپ چىقىرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,70,' ئاندىن بىز دوزاخ ئازابىغا ئەڭ لايىق بولغانلارنى ئەلۋەتتە ئوبدان بىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,71,' سىلەرنىڭ ئىچىڭلاردىن دوزاخقا بارمايدىغان بىرەر كىشىمۇ قالمايدۇ، بۇ پەرۋەردىگارىڭنىڭ ئۆزگەرمەس ھۆكمىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,72,' ئاندىن ئەڭ تەقۋادارلارنى (جەھەننەمدىن) قۇتقۇزىمىز، زالىملارنى جەھەننەمدە تىزلىنىپ ئولتۇرغان ھالدا قويىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,73,' ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز تىلاۋەت قىلىنغان چاغدا، كاپىرلار مۆمىنلەرگە: «ئىككى گۇرۇھتىن قايسىنىڭ ئورنى ياخشى، سورۇنى گۈزەل؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,74,' ئۇلاردىن بۇرۇن (بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، بۇلار مەيلى مال ـ مۈلۈك جەھەتتىن بولسۇن، مەيلى تاشقى كۆرۈنۈش جەھەتتىن بولسۇن، ئۇلاردىن ئارتۇق ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,75,' (ئى مۇھەممەد!) ئېيتقىنكى، «كىمكى گۇمراھلىقتا بولىدىكەن، مەرھەمەتلىك اﷲ ئۇنىڭغا مۆھلەت بىرىدۇ، تاكى ئۇلار ئازاب ۋە قىيامەتتىن ئىبارەت اﷲ ئۇلارغا ۋەدە قىلغان نەرسىلەرنى كۆرگەندە ئاندىن كىمنىڭ ئورنى يامان ۋە كىمنىڭ لەشكىرى ئاجىز ئىكەنلىكىنى بىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,76,' اﷲ ھىدايەت تاپقۇچىلارنىڭ ھىدايىتىنى زىيادە قىلىدۇ، باقى قالىدىغان ياخشى ئەمەللەر ساۋاب جەھەتتىن ۋە ئاقىۋەت جەھەتتىن پەرۋەردىگارىڭنىڭ دەرگاھىدا ئارتۇقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,77,' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ماڭا (ئاخىرەتتە) ئەلۋەتتە مال ـ مۈلۈك، ئەۋلاد بېرىلىدۇ دېگەن ئادەمنى كۆردۈڭمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,78,' ئۇ غەيبنى بىلگەنمۇ؟ ياكى مەرھەمەتلىك اﷲ نىڭ ئەھدىگە ئېرىشكەنمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,79,' ھەرگىز ئۇنداق بولغان ئەمەس، ئۇنىڭ دېگەنلىرىنى خاتىرىلەپ قويىمىز، ئۇنىڭغا بېرىدىغان ئازابنى كۈچەيتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,80,' ئۇنىڭ دېگەنلىرىنى (يەنى مېلىنى ۋە ئەۋلادىنى) باشقىلارغا مىراس قىلىپ قالدۇرىمىز، ئۇ ئالدىمىزغا تەنھا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,81,' ئۇلار (يەنى مۇشرىكلار) ئىززەتكە ئېرىشىش ئۈچۈن (يەنى بۇتلىرىنىڭ شاپائىتى بىلەن اﷲ نىڭ ئالدىدا ئىززەتلىك بولۇش ئۈچۈن)، اﷲ نى قويۇپ ئىلاھلار (يەنى بۇتلار) غا چوقۇندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,82,' ئىش ئۇلارنىڭ گۇمان قىلغىنىدەك ئەمەس (يەنى بۇتلىرى ئۇلارنىڭ اﷲ ئالدىدا ئىززەت تېپىشلىرىنىڭ سەۋەبى بولالمايدۇ)، ئۇلارنىڭ (يەنى مۇشرىكلارنىڭ) چوقۇنۇشىنى ئىنكار قىلىدۇ، ئۇلار (يەنى مۇشرىكلار) نىڭ دۈشمىنىگە ئايلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,83,' بىزنىڭ شەيتانلارنى كاپىرلارغا ئەۋەتكەنلىكىمىزنى (يەنى مۇسەللەت قىلغانلىقىمىزنى) بىلمەمسەن؟ شەيتانلار ئۇلارنى (گۇناھلارغا) قىزىقتۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,84,' ئۇلارنىڭ ھالاك بولۇشىغا ئالدىراپ كەتمىگىن، (ئۇلارنىڭ كۈنى ئاز قالغان بولۇپ) ئۇنى بىز ساناپ تۇرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,85,' شۇ كۈندە (يەنى قىيامەت كۈنىدە) تەقۋادارلارنى مەرھەمەتلىك پەرۋەردىگارنىڭ دەرگاھىغا ئىززەت ـ ئىكرام بىلەن يىغىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,86,' گۇناھكارلارنى ئۇلار چاڭقىغان ھالدا جەھەننەمگە ھەيدەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,87,' مەرھەمەتلىك اﷲ نىڭ ئەھدىگە ئېرىشكەنلەردىن باشقىلار شاپائەت قىلىشقا قادىر بولالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,88,' ئۇلار: «اﷲ نىڭ بالىسى بار» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,89,' سىلەر شەك ـ شۈبھىسىز قەبىھ بىر سۆزنى قىلدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,90,' بۇ سۆزنىڭ (يامانلىقىدىن) ئاسمانلار پارچىلىنىپ كەتكىلى، يەر يېرىلىپ كەتكىلى، تاغلار گۇمران بولۇپ كەتكىلى تاس قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,91,' بۇ شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ نىڭ بالىسى بار دەپ دەۋا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,92,' بالىسى بولۇش اﷲ قا لايىق ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,93,' ئاسمانلاردا ۋە زېمىندا اﷲ قا بەندە بولۇپ بويسۇنمايدىغان ھېچ مەخلۇق يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,94,' شەك ـ شۈبھىسىزكى، اﷲ ئۇلارنى تولۇق ئىگەللىدى ۋە سانىنى مۇكەممەل بىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,95,' قىيامەت كۈنى ئۇلارنىڭ ھەممىسى اﷲ نىڭ دەرگاھىغا تەنھا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,96,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار ئۈچۈن اﷲ ھەقىقەتەن (ئۇلارنىڭ دىللىرىدا) مۇھەببەت پەيدا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,97,' (ئى مۇھەممەد!) تەقۋادارلارغا خۇش ـ خەۋەر بېرىشىڭ ۋە سەركەش قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، بىز قۇرئاننى سېنىڭ تىلىڭ بىلەن چۈشىنىشلىك قىلىپ نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,19,98,' ئۇلاردىن ئىلگىرى نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلاردىن بىرەر ئەھەدىنى كۆرەلەمسەن؟ ياكى ئۇلاردىن بىرەر پەس ئاۋاز ئاڭلىيالامسەن؟ (يەنى ئۇلار شۇنداق تەلتۆكۈس ھالاك قىلىندىكى، ئۇلاردىن كۆرگىلى ۋە ئاۋازىنى ئاڭلىغىلى ھېچ ئەھەدى قالمىدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,1,'طاھا (يەنى ئى مۇھەممەد!)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,2,' قۇرئاننى ساڭا سېنى جاپاغا سېلىش ئۈچۈن ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,3,'، پەقەت (اﷲ تىن) قورقىدىغانلارغا ۋەز ـ نەسىھەت قىلىش ئۈچۈن نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,4,' ئۇ زېمىننى ۋە (كەڭ) ئېگىز ئاسمانلارنى ياراتقان زات تەرىپىدىن نازىل قىلىنغان');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,5,' مەرھەمەتلىك اﷲ ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,6,' ئاسمانلاردىكى، زېمىندىكى، ئۇلارنىڭ ئارىسىدىكى ۋە يەر ئاستىدىكى شەيئىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ مەخلۇقاتىدۇر، اﷲ نىڭ تەسەررۇپى ئاستىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,7,' ئەگەر سەن ئاشكارا سۆزلىسەڭ (ياكى يوشۇرۇن سۆزلىسەڭ، اﷲ نىڭ نەزىرىدە ئوخشاشتۇر)، چۈنكى ئۇ سىرنى ۋە ئۇنىڭدىنمۇ مەخپىيرەك ئىشلارنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,8,' اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، اﷲ نىڭ گۈزەل ئىسىملىرى بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,9,' مۇسانىڭ قىسسىسىدىن خەۋىرىڭ بارمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,10,' ئۆز ۋاقتىدا مۇسا (بىر جايدا) ئوتنىڭ يورۇقىنى كۆردى  ئۇ ئائىلىسىدىكىلەرگە: «تۇرۇپ تۇرۇڭلار، مەن ئوتنىڭ يورۇقىنى كۆردۈم، مەن بېرىپ (ئۇنىڭدىن) بىرەر پارچە چوغ ئېلىپ كېلىشىم ياكى ئوت بار يەردە بىرەر يول باشلىغۇچىنى ئۇچرىتىشىم مۇمكىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,11,' مۇسا ئوتنىڭ يېنىغا كەلگەندە نىدا قىلىندىكى، ئى مۇسا!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,12,'مەن ھەقىقەتەن سېنىڭ پەرۋەردىگارىڭدۇرمەن، كەشىڭنى سالغىن، سەن ھەقىقەتەن مۇقەددەس ۋادى بولغان تۇۋادا سەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,13,' مەن سېنى (پەيغەمبەرلىككە) تاللىدىم، سەن ۋەھيىگە قۇلاق سالغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,14,' مەن ھەقىقەتەن (ئىبادەتكە لايىق) اﷲ دۇرمەن، مەندىن باشقا ھېچ مەبۇد (بەرھەق) يوق، (يالغۇز) ماڭا ئىبادەت قىلغىن، مېنى زىكىر قىلىش ئۈچۈن ناماز ئوقۇغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,15,' قىيامەتنىڭ بولۇشى ھەقىقەتتۇر، ھەر ئادەم ئۆزىنىڭ ئەمەلىگە يارىشا مۇكاپاتلىنىشى ئۈچۈن، ئۇنى (يەنى ئۇنىڭ قاچان بولۇشىنى) مەخپىي تۇتتۇم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,16,' قىيامەتكە ئىشەنمەيدىغان ۋە نەپسى خاھىشلىرىغا ئەگەشكەن ئادەم سېنى ئۇنىڭدىن (يەنى قىيامەتكە تەييارلىق قىلىشتىن ۋە ئۇنى تەستىق قىلىشتىن) توسمىسۇن، ئۇنداقتا ئۆزۈڭ ھالاك بولىسەن (چۈنكى ئاخىرەتتىن غاپىل بولۇش، ھالاكەتكە ئېلىپ بارىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,17,' ئى مۇسا! ئوڭ قولۇڭدىكى نېمە ئۇ؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,18,' مۇسا ئېيتتى: «ئۇ مېنىڭ ھاسام، ئۇنىڭغا تايىنىمەن، ئۇنىڭ بىلەن قويلىرىمغا غازاڭ قېقىپ بېرىمەن، مېنىڭ يەنە باشقا ئېھتىياجلىرىممۇ بار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,19,' اﷲ: «ئى مۇسا! ئۇنى (يەرگە) تاشلىغىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,20,' مۇسا ئۇنى تاشلىۋىدى، ئۇ ناگاھان مېڭىۋاتقان بىر يىلانغا ئايلاندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,21,' اﷲ ئېيتتى: « ئۇنى تۇتۇۋالغىن، قورقمىغىن، ئۇنى دەسلەپكى (يەنى ھاسىلىق) ھالىتىگە قايتۇرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,22,' قولۇڭنى قولتۇقۇڭنىڭ ئاستىغا سالغىن (ئاندىن چىقارغىن)، ئۇ ھېچقانداق ئەيىب ـ نۇقسانسىز ھالدا ئاپئاق (نۇرلۇق بولۇپ چىقىدۇ، (بۇ) ئىككىنچى مۆجىزىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,23,' ساڭا چوڭ مۆجىزىلىرىمىزدىن بەزىسىنى كۆرسىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,24,' پىرئەۋنگە بارغىن، ئۇ ھەقىقەتەن ھەددىدىن ئاشتى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,25,' مۇسا ئېيتتى: «پەرۋەردىگارىم! مېنىڭ قەلبىمنى كەڭ قىلغىن، (يەنى ئىمان بىلەن ۋە پەيغەمبەرلىك بىلەن نۇرلاندۇرغىن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,26,' مېنىڭ ئىشىمنى ئاسانلاشتۇرغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,27,' تىلىمدىن كېكەچلىكنى كۆتۈرۈۋەتكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,28,' ئۇلار مېنىڭ سۆزۇمنى چۈشەنسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,29,' ماڭا ئائىلەمدىن قېرىندىشىم ھارۇننى ياردەمچى قىلىپ بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,30,' ئۇنىڭ بىلەن مېنى تېخىمۇ كۈچەيتكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,31,' مېنىڭ ئىشىمغا ئۇنى شېرىك قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,32,' بۇنىڭ بىلەن بىز ساڭا كۆپ تەسبىھ ئېيتقايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,33,' ساڭا كۆپ زىكرى ئېيتقايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,34,' سەن ھەقىقەتەن بىزنى كۆرۈپ تۇرغۇچىسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,35,' اﷲ ئېيتتى: «ئى مۇسا! سېنىڭ سورىغان نەرسىلىرىڭ ساڭا بېرىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,36,'(بۇنىڭدىن باشقا) ساڭا يەنە بىر قېتىم ئىنئام قىلغان ئىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,37,' ئۆز ۋاقتىدا ئاناڭنىڭ كۆڭلىگە پەقەت ۋەھيى ئارقىلىق بىلىنىدىغان نەرسىلەرنى سالدۇق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,38,' (ئېيتتۇقكى) «ئۇنى (يەنى بالىنى) ساندۇققا سالغىن، ئاندىن ئۇنى (ساندۇق بىلەن) دەرياغا تاشلىغىن، دەريا ئۇنى قىرغاققا تاشلىسۇن، ئۇنى مېنىڭ دۈشمىنىم ۋە ئۇنىڭ دۈشمىنى بولغان (پىرئەۋن) ئېلىپ (باقسۇن)» مېنىڭ قوغدىشىم ئاستىدا تەربىيىلىنىشىڭ ئۈچۈن، ساڭا مەن تەرەپتىن بولغان مۇھەببەتنى سالدىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,39,' ئۆز ۋاقتىدا سېنىڭ ھەمشىرەڭ (پىر ئەۋىننىڭ ئائىلىسىگە) بېرىپ: «سىلەرگە ئۇنى باقىدىغان بىر كىشىنى كۆرسىتىپ قويايمۇ؟» دېدى بىز سېنىڭ ئاناڭنى خۇش بولسۇن، قايغۇرمىسۇن دەپ سېنى ئۇنىڭغا قايتۇرۇپ بەردۇق، سەن بىر ئادەمنى ئۆلتۈرۈپ قويدۇڭ، سېنى بىز غەمدىن خالاس قىلدۇق، سېنى تۈرلۈك بالالار بىلەن سىنىدۇق، سەن مەديەنلىكلەر ئارىسىدا كۆپ يىللار تۇردۇڭ، ئاندىن، ئى مۇسا! تەقدىر بىلەن (بۇ يەرگە) كەلدىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,40,' سېنى مەن مېنىڭ مۇھەببىتىمگە (يەنى پەيغەمبەر قىلىپ ۋەھيى نازىل قىلىشىمغا) تاللىدىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,41,' (ئى مۇسا!) سەن ۋە سېنىڭ قېرىندىشىڭ (ھارۇن) مېنىڭ مۆجىزىلىرىمنى ئېلىپ بېرىڭلار، مېنىڭ زىكرىمگە سۇسلۇق قىلىپ قالماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,42,' پىرئەۋننىڭ يېنىغا بېرىڭلار، ئۇ ھەقىقەتەن ھەددىدىن ئاشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,43,' ئۇنىڭغا يۇمشاق سۆز قىلىڭلار، ئۇ ۋەز ـ نەسىھەتنى قوبۇل قىلىشى، ياكى (ھەددىدىن ئاشقانلىقنىڭ ئاقىۋىتىدىن) قورقۇشى مۇمكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,44,' ئۇلار: «پەرۋەردىگارىمىز! بىز ھەقىقەتەن ئۇنىڭ بىزنى ئالدىراپ جازالىشىدىن ياكى تېخىمۇ ھەددىدىن ئېشىشىدىن قورقىمىز» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,45,' اﷲ ئېيتتى: «سىلەر قورقماڭلار، مەن ھەقىقەتەن سىلەر بىلەن بىللە (ئۇنىڭ سىلەرگە بەرگەن جاۋابىنى) ئاڭلاپ تۇرىمەن، (ئۇنىڭ سىلەرگە قىلىدىغانلىرىنى) كۆرۈپ تۇرىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,46,' سىلەر پىرئەۋننىڭ يېنىغا بېرىپ، بىز ئىككىمىز پەرۋەردىگارىڭنىڭ ئەلچىسى بولىمىز، ئىسرائىل ئەۋلادىنى قويۇۋەتكىن، بىز بىلەن كەتسۇن، ئۇلارنى قىينىمىغىن ساڭا بىز ھەقىقەتەن پەرۋەردىگارىڭنىڭ مۆجىزىسىنى ئېلىپ كەلدۇق، توغرا يولغا ئەگەشكەن ئادەم (اﷲ نىڭ ئازابىدىن) ئامان قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,47,' بىزگە شەك ـ شۈبھىسىز ۋەھيى قىلىندىكى، (اﷲ نىڭ پەيغەمبەرلىرىنى) ئىنكار قىلغان، (ئىماندىن) يۈز ئۆرىگەن ئادەم (قاتتىق) ئازابقا دۇچار بولىدۇ دەڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,48,' (ئۇلار پىرئەۋنگە كېلىپ اﷲ تائالا تەرىپىدىن ۋەھيى قىلىنغان نەرسىلەرنى يەتكۈزگەندىن كېيىن) پىرئەۋن: «ئى مۇسا، سىلەرنىڭ پەرۋەردىگارىڭلار كىم؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,49,' مۇسا ئېيتتى: «پەرۋەردىگارىمىز شۇنداق زاتتۇركى، ھەممە نەرسىگە (ئۆزىگە مۇناسىپ) شەكىل ئاتا قىلدى، (ئاندىن ئۇلارغا ياشاش يوللىرىنى، پايدىلىنىدىغان نەرسىلەرنى) كۆرسەتتى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,50,' پىرئەۋن ئېيتتى: «ئۆتۈپ كەتكەن ئۈممەتلەرنى ھالى قانداق؟ (يەنى قانداق بولغان؟) »');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,51,' مۇسا ئېيتتى: «ئۇلار توغرىسىدىكى مەلۇمات پەرۋەردىگارىمنىڭ دەرگاھىدا لەۋھۇلمەھپۇزدا (خاتىرىلەكلىكتۇر)، پەرۋەردىگارىم ھەم خاتالاشمايدۇ ھەم ئۇنتۇمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,52,' ئۇ زېمىننى سىلەرگە بىسات قىلىپ بەردى، سىلەر ئۈچۈن نۇرغۇن يوللارنى ئېچىپ بەردى، بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، ئۇنىڭ بىلەن نۇرغۇن ئۆسۈملۈكلەرنى ئۆستۈرۈپ بەردى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,53,' (ئۇلاردىن) يەڭلار ۋە مال چارۋىلىرىڭلارنى بېقىڭلار، ئۇنىڭدا ئەقىل ئىگىلىرى ئۈچۈن (اﷲ نىڭ بارلىقى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,54,' سىلەرنى زېمىندىن خەلق ئەتتۇق، (ئۆلگۈنۈڭلاردىن كېيىن) سىلەرنى يەنە زېمىنغا قايتۇرىمىز (توپىغا ئايلىنىسىلەر)، سىلەرنى (ھېساب ئېلىش ئۈچۈن) يەنە بىر قېتىم زېمىندىن چىقىرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,55,' شەك ـ شۈبھىسىزكى، ئۇنىڭغا بىزنىڭ ھەممە مۆجىزىلىرىمىزنى كۆرسەتتۇق ئۇ ئىنكار قىلدى ۋە (ئىمان ئېيتىشتىن) باش تارتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,56,' پىرئەۋن ئېيتتى: «ئى مۇسا ! سېھرىڭ بىلەن بىزنى زېمىنىمىز (مىسىر) دىن چىقىرىۋېتىش ئۈچۈن كەلدىڭمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,57,' ساڭا بىز شۇنىڭغا ئوخشاش سېھىر بىلەن تاقابىل تۇرىمىز، سەن بىزگە ئۆز ئارا يىغىلىدىغان ۋاقىتنى مۇئەييەن جاينى بەلگىلەپ بەرگىن، سەنمۇ، بىزمۇ ئۇنىڭغا خىلاپلىق قىلمايلى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,58,' مۇسا ئېيتتى: «سىلەرگە ۋەدە قىلىنغان چاغ بايرام كۈنى بولۇپ، كىشىلەر چاشكا ۋاقتىدا يىغىلسۇن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,59,' پىرئەۋن قايتىپ كېتىپ ھىيلە ـ مىكرنى (يەنى سېھىرگەرلەرنى) توپلىدى ئاندىن ئۇ (سېھىرگەرلەرنى ۋە سېھرىدە ئىشلىنىدىغان نەرسىلەرنى ئېلىپ ۋەدىلەشكەن ۋاقىتتا) كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,60,' مۇسا ئۇلار غا ئېيتتى: «سىلەرگە ۋاي! اﷲ قا يالغاننى چاپلىماڭلار (يەنى سېھرىڭلار ئارقىلىق اﷲ نىڭ مۆجىزىلىرىگە تاقابىل تۇرۇشنى دەۋا قىلماڭلار)، اﷲ سىلەرنى قاتتىق ئازاب بىلەن ھالاك قىلىدۇ، اﷲ قا بوھتان چاپلىغان ئادەم مەغلۇب بولىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,61,' ئۇلار ئۆز ئىشى (يەنى مۇسا ۋە ھارۇننىڭ ئىشى) ئۈستىدە دەتالاش قىلىشتى، قىلغان دەتالاشنى يوشۇرۇشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,62,' ئۇلار ئېيتتى: «بۇ ئىككىسى راستلا سېھرىگەر بولۇپ، سېھىر بىلەن سىلەرنى زېمىنىڭلاردىن چىقىرىۋەتمەكچى ۋە توغرا دىنىڭلارنى يوقاتماقچى بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,63,' ھىيلەڭلارنىڭ ھەممىسىنى جەملەڭلار (يەنى پۈتۈن چارە ـ تەدبىرلىرىڭلارنى بىر نۇقتىغا يىغىپ بىر ياقىدىن باش چىقىرىڭلار)، ئاندىن (قارىغۇچىلارغا ھەيۋەتلىك كۆرۈنۈش ئۈچۈن مەيدانغا) سەپ تارتىپ كېلىڭلار، بۈگۈن ئۈستۈنلۈك قازانغان ئادەم راستلا مەقسىتىگە ئېرىشىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,64,' ئۇلار ئېيتتى: «ئى مۇسا! (ھاساڭنى) سەن ئاۋۋال تاشلامسەن؟ ياكى (ئارغامچا ھاسىلىرىمىزنى) بىز ئاۋۋال تاشلامدۇق؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,65,' مۇسا: «بەلكى سىلەر ئاۋۋال تاشلاڭلار!» دېدى (ئۇلار تاشلىغان ئىدى) ناگاھان ئۇلارنىڭ ئاغامچىلىرى، ھاسىلىرى ئۇلارنىڭ سېھىرىدىن (يەنى سېھىرنىڭ تەسىرىدىن) ئۇنىڭغا ھەرىكەتلىنىپ مېڭىۋاتقاندەك تۇيۇلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,66,' (بۇنىڭدىن) مۇسا ئۆزىدە قورقۇنچ ھېس قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,67,' بىز (ئۇنىڭغا) ئېيتتۇقكى، «قورقمىغىن سەن چوقۇم ئۈستۈنلۈك قازىنىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,68,'، قولۇڭدىكىنى تاشلىغىن، ئۇ ئۇلارنىڭ كۆرسەتكەن سېھىرلىرىنى دەم تارتىپ يۇتۇۋېتىدۇ، ئۇلارنىڭ كۆرسەتكەنلىكىنى سېھىرگەرلەرنىڭ ھىيلىسىدۇر، سېھىرگەرلەر قەيەرگە بارسا مۇۋەپپەقىيەت قازىنالمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,69,' (مۇسا ھاسىسىنى تاشلىۋىدى، ئۇلارنىڭ كۆرسەتكەن سېھىرلىرىنى دەم تارتىپ يۇتۇۋەتتى) شۇنىڭ بىلەن سېھىرگەرلەر (سەجدە قىلغان ھالدا يىقىلىپ): «ھارۇن بىلەن مۇسانىڭ پەرۋەردىگارىغا ئىمان ئېيتتۇق» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,70,' پىرئەۋن ئېيتتى: «سىلەر مەن رۇخسەت بېرىشتىن بۇرۇن ئۇنىڭغا ئىمان ئېيتتىڭلار، شۈبھىسىزكى، ئۇ چوقۇم سىلەرگە سېھىرنى ئۆگەتكەن چوڭۇڭلار ئىكەن، مەن چوقۇم سىلەرنىڭ قوللىرىڭلارنى، پۇتلىرىڭلارنى ئوڭ ـ چەپ قىلىپ (يەنى ئوڭ قول بىلەن سول پۇتنى، ئوڭ پۇت بىلەن سول قولنى) كېسىپ تاشلايمەن، سىلەرنى چوقۇم خورما دەرەخلىرىگە ئاسىمەن، سىلەر قايسىمىزنىڭ (يەنى مېنىڭ ياكى مۇسانىڭ پەرۋەردىگارىنىڭ) ئازابىنىڭ ئەڭ قاتتىق ۋە ئەڭ ئۇزۇن ئىكەنلىكىنى چوقۇم بىلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,71,' ئۇلار ئېيتتى: «سېنى (يەنى ساڭا ئەگىشىشنى) ئىختىيار قىلىپ، بىزگە كەلگەن (مۇسانىڭ راستلىقىنى كۆرسىتىدىغان) روشەن دەلىللەرنى ۋە بىزنى خەلق ئەتكەن اﷲ نى ھەرگىز تاشلىمايمىز (بىزنىڭ ھەققىمىزدە) نېمىنى ھۆكۈم قىلساڭ، شۇنى ھۆكۈم قىلغىن، سېنىڭ ھۆكمىڭ پەقەت بۇ دۇنيادىلا ئۆتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,72,' بىزنىڭ خاتالىقىمىزنى، سېنىڭ مەجبۇرلىشىڭ ئارقىسىدا بىزنىڭ سېھىر كۆرسەتكەنلىكىمىزدىن ئىبارەت گۇناھىمىزنى مەغپىرەت قىلسۇن دەپ بىز ھەقىقەتەن پەرۋەردىگارىمىزغا ئىمان ئېيتتۇق، اﷲ نىڭ (ساۋابىنى سېنىڭ مۇكاپاتىڭدىن) ئارتۇقتۇر؛ اﷲ نىڭ (ئازابى سېنىڭ جازايىڭدىن) دائىمىيدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,73,' كىمكى گۇناھكار ھالدا اﷲ قا مۇلاقات بولىدىكەن، ئۇ دوزاخقا كىرىدۇ، دوزاختا ئۇ ئۆلمەيدۇ ۋە (ئوبدان) ياشىمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,74,' كىمكى مۆمىن بولۇپ، ياخشى ئەمەللەرنى قىلغان ھالدا اﷲ قا مۇلاقات بولىدىكەن، ئۇ (اﷲ نىڭ دەرگاھىدا) يۇقىرى دەرىجىگە ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,75,' ئۇ (دەرىجىلەر) دائىمىي تۇرالغۇ بولغان جەننەتلەر بولۇپ، ئۇلارنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدۇ، ئۇ يەرلەردە ئۇ مەڭگۈ قالىدۇ، ئەنە شۇ (كۇفرىدىن ۋە گۇناھلاردىن) پاك بولغان ئادەمگە بېرىلىدىغان مۇكاپاتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,76,' بىز شەك ـ شۈبھىسىز مۇساغا ۋەھيى قىلدۇقكى، «سەن مېنىڭ بەندىلىرىمنى كېچىدە (مىسىردىن) ئېلىپ چىقىپ كەت، سەن (ھاساڭنى) دېڭىزغا ئۇرۇپ ئۇلارغا بىر قۇرۇق يول ئېچىپ بەرگىن، (پىرئەۋننىڭ ئارقاڭدىن قوغلاپ) يېتىۋېلىشىدىن قورقمىغىن، (دېڭىزدا غەرق بولۇپ كېتىشتىنمۇ) قورقمىغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,77,' پىرئەۋن لەشكەرلىرى بىلەن قوغلىدى، ئۇلارنى (يەنى پىرئەۋن بىلەن ئۇنىڭ لەشكەرلىرىنى) دېڭىزدا دەھشەتلىك ھالەت ئورىۋالدى (يەنى ئۇلار دېڭىزدا غەرق بولدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,78,' پىر ئەۋن ئۆز قەۋمىنى ئازدۇردى، (ئۇلارنى) توغرا يولغا باشلىمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,79,' ئى ئىسرائىل ئەۋلادى! سىلەرنى ھەقىقەتەن دۈشمىنىڭلاردىن قۇتقۇزدۇق، (مۇناجات ئۈچۈن ۋە مۇساغا تەۋراتنى نازىل قىلىش ئۈچۈن) سىلەرگە (تۇرسىنا) نىڭ ئوڭ تەرىپىنى ۋەدە قىلدۇق، سىلەرگە تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,80,' (ئېيتتۇقكى) «بىز رىزىق قىلىپ بەرگەن پاك ـ ھالال نەرسىلەردىن يەڭلار، بۇ ھەقتە چەكتىن ئاشماڭلار، (ئۇنداق قىلساڭلار) سىلەرگە غەزىپىم نازىل بولىدۇ، كىمگە مېنىڭ غەزىپىم نازىل بولىدىكەن، ئۇ چوقۇم ھالاك بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,81,' تەۋبە قىلغان، ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، ئاندىن توغرا يولدا ماڭغان ئادەمنى مەن ئەلۋەتتە ناھايىتى مەغپىرەت قىلغۇچىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,82,' «ئى مۇسا! ئالدىراپ (تۇرسىناغا) قەۋمىڭدىن ئىلگىرى كېلىشىڭنىڭ سەۋەبى نېمە؟» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,83,' مۇسا ئېيتتى: «ئۇلار ئىزىمنى قوغلىشىپ كېلىۋاتىدۇ، پەرۋەردىگارىم! سېنى رازى بولسۇن دەپ بۇ يەرگە ئالدىراپ كەلدىم»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,84,' اﷲ ئېيتتى: «سەندىن (يەنى سەن كەتكەندىن) كېيىن قەۋمىڭنى ھەقىقەتەن سىنىدۇق، ئۇلارنى سامىرى ئازدۇردى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,85,' مۇسا قەۋمىگە غەزەپلەنگەن ھالدا خاپا قايتتى، ئۇ ئېيتتى: «سىلەرگە پەرۋەردىگارىڭلار چىرايلىق ۋەدە قىلمىغانمىدى؟ (سىلەر ئەھدىنى ئۇنتۇغۇدەك) ۋاقىت شۇنچە ئۇزۇن بولۇپ كەتتىمۇ؟ ياكى سىلەر ئۆزۈڭلار پەرۋەردىگارىڭلارنىڭ غەزىپى چۈشۈشىنى ئىرادە قىلدىڭلارمۇ؟ شۇنىڭ ئۈچۈن سىلەرنىڭ ماڭا بەرگەن ۋەدەڭلارغا خىلاپلىق قىلدىڭلارمۇ؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,86,' ئۇلار ئېيتتى: «ساڭا بەرگەن ۋەدىگە ئۆزلۈكىمىزدىن خىلاپلىق قىلغىنىمىز يوق، لېكىن بىز (پىرئەۋن) قەۋمىنىڭ زىننەتلىرىدىن نۇرغۇن ئېلىپ چىققان ئىدۇق، (سامىرىنىڭ بۇيرۇقى بويىچە) ئۇلارنى (ئوتقا) تاشلىدۇق، سامىرىمۇ (ئۆزى ئېلىپ چىققان زىبۇزىننەتلىرىنى) تاشلىدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,87,' سامىرى (ئېرىتىلگەن زىبۇزىننەتلەردىن) ئۇلار ئۈچۈن بىر موزاي ھەيكەل ياسىدى، ئۇنىڭدىن موزاينىڭ ئاۋازىغا ئوخشاش ئاۋاز چىقاتتى، ئۇلار (يەنى سامىرى ۋە ئۇنىڭ تەۋەلىرى) ئېيتتى: «بۇ سىلەرنىڭ ئىلاھىڭلاردۇر ۋە مۇسانىڭ ئىلاھىدۇر، مۇسا (ئىلاھنى بۇ يەردە) ئۇنتۇپ قېلىپ (ئۇنى ئىزدەپ تۇرسىناغا كەتتى)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,88,' ئۇ موزاينىڭ ئۇلارنىڭ سۆزىگە جاۋاب بېرەلمەيدىغانلىقىنى، ئۇلارغا پايدا ـ زىيان يەتكۈزەلمەيدىغانلىقىنى ئۇلار ئۇقمامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,89,' شەك ـ شۈبھىسىزكى، ھارۇن ئۇلارغا (مۇسا قايتىپ كېلىشتىن) بۇرۇن: «ئى قەۋمىم! سىلەر موزاي بىلەن قايمۇقتۇرۇلدۇڭلار، سىلەرنىڭ پەرۋەردىگارىڭلار مەرھەمەتلىك اﷲ دۇر، ماڭا بوي سۇنۇڭلار، (موزايغا چوقۇنۇشنى تەرك ئېتىش بىلەن) بۇيرىقىمغا ئىتائەت قىلىڭلار!» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,90,' ئۇلار: «مۇسا قايتىپ كەلگەنگە قەدەر ئۇنىڭغا چوقۇنىۋېرىمىز» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,91,' مۇسا ئېيتتى: «ئى ھارۇن! ئۇلارنىڭ ئازغانلىقىنى كۆرگەن چېغىڭدا (خۇدالىق ئۈچۈن غەزەپلىنىشتە) ماڭا ئەگىشىشتىن نېمە توسالغۇ بولدى؟ مېنىڭ بۇيرىقىمغا خىلاپلىق قىلدىڭمۇ؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,92,' ھارۇن ئېيتتى: « ئى قېرىندىشىم! مېنىڭ ساقىلىمنى ۋە چېچىمنى تارتمىغىن، مەن ھەقىقەتەن سېنىڭ، ئىسرائىل ئەۋلادىنى بۆلۈۋېتىپسەن، سۆزۈمگە دىققەت قىلماپسەن، دېيىشىڭدىن قورقتۇم»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,93,' مۇسا ئېيتتى: «ئى سامىرى! سەن نېمە بولدۇڭ؟ (يەنى مۇنداق يامان ئىشنى قىلىشىڭدىن غەرىزىڭ نېمە؟)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,94,' سامىرى ئېيتتى: «مەن ئۇلار كۆرمىگەننى كۆردۈم، ئەلچى (يەنى جىبرىئىل) نىڭ ئىزىدىن بىر چاڭگال توپىنى ئالدىم ـ دە، ئۇنى (موزايغا) چاچتىم، شۇنىڭ بىلەن ئۇنىڭدىن موزاينىڭ ئاۋازىغا ئوخشاش ئاۋاز چىقتى)، شۇنىڭدەك (بۇنى) ماڭا نەپسىم چىرايلىق كۆرسەتتى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,95,' مۇسا (سامىرىغا) ئېيتتى: «سەن بارغىن! سەن ئۆمۈرۋايەت (كۆرگەنلا كىشىگە) ماڭا يېقىنلاشماڭلار! دېگەيسەن (يەنى سېنىڭ كىشىگە يېقىنلاشماسلىقىڭ، كىشىنىڭ ساڭا يېقىنلاشماسلىقى بۇ دۇنيادا ساڭا بېرىلگەن جازادۇر)، شۈبھىسىزكى (ئاخىرەتتە جازالىنىدىغان) مۇئەييەن بىر ۋاقتىڭ بار، اﷲ ساڭا قىلغان بۇ ۋەدىسىگە خىلاپلىق قىلمايدۇ، سەن ھامان چوقۇنۇپ كېلىۋاتقان ئىلاھىڭغا قارىغىن، بىز ئۇنى چوقۇم (ئوتتا) كۆيدۈرىمىز، ئاندىن ئۇنىڭ (كۈلىنى) دېڭىزغا چېچىۋېتىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,96,' سىلەرنىڭ ئىلاھىڭلار بىر اﷲ دۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ ھەر نەرسىنى تولۇق بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,97,' (ئى مۇھەممەد!) شۇنىڭدەك، ساڭا ئۆتكەنكىلەرنىڭ بەزى خەۋەرلىرىنى بايان قىلىمىز، ساڭا ئۆز تەرىپىمىزدىن قۇرئاننى ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,98,' كىمكى قۇرئاندىن يۈز ئۆرۈيدىكەن، ئۇنىڭغا قىيامەت كۈنى چوڭ گۇناھتىن ئېغىر يۈك يۈكلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,99,' ئۇ گۇناھى ئۈچۈن بېرىلگەن ئازابنى مەڭگۈ تارتىدۇ) قىيامەت كۈنى ئۇلارغا يۈكلەنگەن بۇ يۈك نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,100,' بۇ سۇر چىلىنىدىغان كۈندۇر، بۇ كۈندە (شەكلى ئۆزگەرگەن) كۆك كۆزلۈك (قارا يۈزلۈك) گۇناھكارلارنى يىغىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,101,' (قىيامەتنىڭ دەھشىتىنى كۆرۈپ دۇنيادا تۇرغان ۋاقتى قىسقا بىلىنگەنلىكتىن) (ئۇلار) «(دۇنيادا) پەقەت ئون كۈنلا تۇردۇڭلار» دېيىشىپ پىچىرلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,102,' ئۇلارنىڭ دەيدىغانلىرىنى بىز ئوبدان بىلىمىز، ئەينى زاماندا ئۇلارنىڭ ئەڭ توغرا سۆزلەيدىغىنى: «سىلەر پەقەت بىر كۈن تۇردۇڭلار» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,103,' ئۇلار سەندىن تاغلار توغرۇلۇق (يەنى تاغنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقى توغرۇلۇق) سورايدۇ، ئېيتقىنكى، «پەرۋەردىگارىم ئۇلارنى كۈكۈم تالقان قىلىپ (ئاندىن شامالنى ئەۋەتىپ ئۇنى سورۇيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,104,' ئۇلارنى تۈزلەڭلىككە ئايلاندۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,105,' ئۇ يەرلەردە نە چۇڭقۇرلۇقنى، نە ئېگىزلىكنى كۆرمەيسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,106,' بۇ كۈندە (كىشىلەر اﷲ نىڭ مەھشەرگاھقا) دەۋەت قىلغۇچىسىغا ئەگىشىدۇ، قىڭغىر كەتمەيدۇ، مەرھەمەتلىك اﷲ نىڭ ئۇلۇغلۇقى ۋە ھەيۋىتىدىن قورقۇپ، بارلىق ئاۋازلار بېسىقىدۇ، پىچىرلاشقاندىن باشقىنى ئاڭلىمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,107,' بۇ كۈندە مەرھەمەتلىك اﷲ ئىزىن بەرگەن ۋە سۆزىدىن اﷲ رازى بولغان ئادەمنىڭ شاپائىتىدىن باشقا ھېچقانداق شاپائەت پايدا بەرمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,108,' اﷲ ئۇلارنىڭ ئالدىدىكىنى ۋە كەينىدىكىنى بىلىدۇ، ئۇلار بولسا ئۇنى بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,109,' گۇناھكارلار مەخلۇقاتنى ئىدارە قىلىپ تۇرغۇچى(مەڭگۈ) ھايات اﷲ قا ئېگىلىدۇ، اﷲ قا شېرىك كەلتۈرگەنلەر شەكسىز زىيان تارتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,110,' مۆمىن تۇرۇپ ياخشى ئەمەللەرنى قىلغانلار يامانلىقلىرىنىڭ ئېشىپ كېتىشىدىن ۋە ياخشىلىقلىرىنىڭ كېمىيىپ كېتىشىدىن قورقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,111,' ئۇلارنىڭ تەقۋادار بولۇشلىرى ئۈچۈن ياكى ئۇلارنى ۋەز ـ نەسىھەتكە ئىگە قىلىش ئۈچۈن، ساڭا ئەرەبچە قۇرئاننى نازىل قىلدۇق ۋە ئۇنىڭدا تۈرلۈك ئاگاھلاندۇرۇشلارنى بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,112,' اﷲ (زاتى، سۈپىتى جەھەتتە پەتەن مەخلۇقاتتىن) ئۈستۈندۇر، ھەق پادىشاھتۇر، (ئى مۇھەممەد!) ساڭا قۇرئاننىڭ ۋەھيىسى تاماملىنىشتىن بۇرۇن ئوقۇشقا ئالدىراپ كەتمىگىن، (يەنى جىبرىئىل ساڭا قۇرئاننى ئوقۇپ بېرىۋاتقاندا، بىللە ئوقۇشقا ئالدىراپ كەتمەي، ئۇ تىلاۋىتىدىن فارىغ بولغۇچە تىڭشاپ تۇرغىن) «ئى پەرۋەردىگارىم! ئىلمىمنى زىيادە قىلغىن!» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,113,' بىز ئىلگىرى ئادەمگە (دەرەخنىڭ مېۋىسىنى يېمەسلىكنى) تەۋسىيە قىلدۇق، ئۇ (بۇ تەۋسىيەنى) يادىدىن چىقاردى، ئۇنىڭدا ھېچقانداق چىڭ ئىرادە كۆرمىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,114,' ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: «ئادەمگە (يەنى ئادەم ئەلەيھىسسالامغا) سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقىسى سەجدە قىلدى، ئۇ (سەجدە قىلىشتىن) باش تارتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,115,' بىز دېدۇق: «ئى ئادەم! بۇ ھەقىقەتەن سېنىڭ دۈشمىنىڭدۇر، خوتۇنۇڭنىڭ دۈشمىنىدۇر ئۇ سىلەر ئىككىڭلارنىڭ جەننەتتىن چىقىپ كېتىشىڭلارغا سەۋەبچى بولۇپ قالمىسۇن، (ئۇ چاغدا) سىلەر جاپا مۇشەققەتتە قالىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,116,' جەننەتتە سەن ھەقىقەتەن ئاچ قالمايسەن ۋە يالىڭاچ قالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,117,' ھەمدە ئۇنىڭدا ھەقىقەتەن ئۇسساپ كەتمەيسەن، ئىسسىپ كەتمەيسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,118,' شەيتان ئۇنىڭغا ۋەسۋەسە قىلىپ: «ئى ئادەم! ساڭا (مەن يېگەن ئادەم) ئۆلمەيدىغان دەرەخنى ۋە زاۋال تاپماس پادىشاھلىقنى كۆرسىتىپ قويايمۇ؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,119,' ئۇلار ئۇ دەرەخنىڭ مېۋىسىنى يېدى، شۇنىڭ بىلەن ئۇلارنىڭ ئەۋرەتلىرى ئېچىلىپ قالدى، ئۇلار جەننەت (دەرەخلىرىنىڭ) يوپۇرماقلىرى بىلەن سەترى ئەۋرەت قىلىشقا كىرىشتى، ئادەم پەرۋەردىگارىنىڭ (ئەمرىگە) خىلاپلىق قىلدى، شۇنىڭ بىلەن ئۇ ئازدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,120,' ئاندىن پەرۋەردىگارى ئۇنى تاللىدى، ئۇنىڭ تەۋبىسىنى قوبۇل قىلدى ۋە ئۇنى ھىدايەت قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,121,' اﷲ (ئادەم بىلەن ھەۋۋاغا) ئېيتتى: «سىلەر ھەممىڭلار جەننەتتىن بەزىڭلار بەزىڭلارغا دۈشمەن بولغان ھالدا چۈشۈڭلار، ئەگەر سىلەرگە مېنىڭ تەرىپىمدىن ھىدايەت كەلسە، كىمكى مېنىڭ ھىدايىتىمگە ئەگەشسە، ئۇ ئازمايدۇ ۋە ئاخىرەتتە شەقىي بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,122,' كىمكى مېنىڭ زىكرىمدىن يۈز ئۆرۈيدىكەن، ئۇنىڭ ھاياتى تار (يەنى خاتىرجەمسىز) بولىدۇ، قىيامەت كۈنى ئۇنى بىز كور قوپۇرىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,123,' ئۇ: «ئى پەرۋەردىگارىم! نېمىشقا مېنى كور قوپۇردۇڭ، ۋاھالەنكى، (دۇنيادىكى چاغدا) مېنىڭ كۆزۈم كۆرەتتىغۇ؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,124,' اﷲ ئېيتىدۇ: «شۇنىڭدەك ساڭا (روشەن) ئايەتلىرىمىز كەلدى، سەن ئۇلارنى تەرك ئەتتىڭ، سەن بۈگۈن شۇنىڭغا ئوخشاش تەرك ئېتىلىسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,125,' ھەددىدىن ئاشقان ۋە پەرۋەردىگارىنى ئايەتلىرىگە ئىمان ئېيتمىغانلارغا شۇنىڭغا ئوخشاش جازا بېرىمىز، ئاخىرەتنىڭ ئازابى تېخىمۇ قاتتىقتۇر، تېخىمۇ باقىيدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,126,' ئۇلاردىن ئىلگىرى ئۆتكەن (پەيغەمبەرلىرىنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار ئۇقمامدۇ؟ ئۇلار شۇ ئۈممەتلەرنىڭ ماكانلىرىدا مېڭىپ يۈرىدۇ، ئەقىل ئىگىلىرى ئۈچۈن ئۇنىڭدا ھەقىقەتەن نۇرغۇن ئىبرەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,127,' ئەگەر پەرۋەردىگارىڭنىڭ (ئازابنى ئۇلاردىن كېچىكتۈرۈشكە) قىلىۋەتكەن ھۆكمى ۋە ئالدىنئالا بەلگىلەپ قويغان ۋاقتى بولمىسا ئىدى، ئۇ چاغدا (ئۇلارنىڭ ھالاك بولۇشى) چوقۇم ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,128,' ئۇلارنىڭ سۆزلىرىگە سەۋرى قىلغىن، مەمنۇن بولۇشۇڭ ئۈچۈن، كۈن چىقىشتىن ئىلگىرى، كۈن پېتىشتىن بۇرۇن، كېچە ۋاقىتلىرىدا ۋە كۈندۈزنىڭ دەسلەپكى ۋە ئاخىرقى ۋاقىتلىرىدا پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن (يەنى ناماز ئوقۇغىن) ۋە ھەمدە ئېيتقىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,129,' بىز (كۇففارلاردىن) تۈرلۈك جامائەنى بەھرىمەن قىلغان دۇنيانىڭ نېمەتلىرى ۋە زىبۇزىننەتلىرىگە كۆز سالمىغىن، بۇنىڭ بىلەن ئۇلارنى سىنايمىز، پەرۋەردىگارىڭنىڭ رىزقى (يەنى ساۋابى بۇ پانىي نېمەتتىن) ياخشىدۇر ۋە ئەڭ باقىيدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,130,' (ئى مۇھەممەد!) ئائىلەڭدىكىلەرنى (ۋە ئۈممىتىڭنى) نامازغا بۇيرۇغىن، ئۆزۈڭمۇ ئۇنى ئادا قىلىشقا چىداملىق بولغىن، سەندىن بىز رىزىق تەلەپ قىلمايمىز ساڭا بىز رىزىق بېرىمىز، ياخشى ئاقىۋەت پەقەت تەقۋادارلارغا خاستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,131,' ئۇلار (يەنى مۇشرىكلار): «(مۇھەممەد) پەرۋەردىگارى تەرىپىدىن بىزگە بىرەر مۆجىزە كەلتۈرسىچۇ!» دەيدۇ، ئۇلارغا بۇرۇنقى ئۈممەتلەرنىڭ خەۋەرلىرىنى ئۆز ئىچىگە ئالغان قۇرئان كەلمىدىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,132,' ئەگەر بىز ئۇلارنى ئۇنىڭدىن (يەنى مۇھەممەد ئەلەيھىسسالامنى ئەۋەتىشتىن) ئىلگىرى ھالاك قىلغان بولساق، ئۇلار چوقۇم: «نېمىشقا بىزگە بىرەر پەيغەمبەر ئەۋەتمىدىڭ؟ بىز ۋە رەسۋا بولۇشتىن ئىلگىرى سېنىڭ ئايەتلىرىڭگە ئەگەشتۇق» دەيتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,20,133,' ئېيتقىنكى، ھەممە (سىلەرنىڭ ۋە بىزنىڭ ئىشلىرىمىزنى ئاقىۋىتىنى) كۈتمەكتە سىلەرمۇ كۈتۈڭلار، كىملەرنىڭ توغرا يول ئىگىلىرى ئىكەنلىكىنى ۋە كىملەرنىڭ ھىدايەت تاپقانلىقىنى (ئۇزاققا قالماي) بىلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,1,'كىشىلەرگە ئۇلارنىڭ (ئەمەللىرىدىن) ھېساب ئېلىنىدىغان ۋاقىت (يەنى قىيامەت) توغرۇلۇق ئويلىنىشتىن) يۈز ئۆرۈمەكتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,2,' ئۇلارغا پەرۋەردىگارى تەرىپىدىن (قۇرئاندا) يېڭى ۋەز ـ نەسىھەت نازىل بولسىلا، ئۇلار ئۇنىڭغا مەسخىرە قىلغان ھالدا قۇلاق سېلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,3,' ئۇلارنىڭ دىللىرى (اﷲ نىڭ كالامىدىن) غەپلەتتىدۇر، كاپىرلار ئۆز ئارا: «بۇ (يەنى مۇھەممەد ئەلەيھىسسالام) پەقەت سىلەرگە ئوخشاش (ئاددىي) بىر ئىنسان، سىلەر سېھىرنى كۆرۈپ تۇرۇپ ئۇنى قوبۇل قىلامسىلەر؟» دەپ يوشۇرۇن سۆزلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,4,' (پەيغەمبەر) ئېيتتى: «پەرۋەردىگارىم ئاسمان، زېمىندا ئېيتىلغان سۆزلەرنى بىلىپ تۇرىدۇر، اﷲ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,5,' (كاپىرلار قۇرئان سېھرىدۇر دەپلا قالماستىن) بەلكى ئۇ شائىردۇر، ئىلگىرىكى پەيغەمبەرلەرگە ئوخشاش بىزگە بىرەر مۆجىزە كەلتۈرسۇن» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,6,' ئۇلاردىن ئىلگىرى، بىز ھالاك قىلغان شەھەرلەردىن (يەنى ئاھالىسىدىن) ھېچقايسىسى ئىمان ئېيتمىغان تۇرسا، (مۆجىزىلەرنى كۆرسەتسەڭ) ئۇلار ئىمان ئېيتامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,7,' (ئى مۇھەممەد!) سەندىن ئىلگىرى ـ (پەرىشتىلەرنى ئەمەس) پەقەت ئىنسانلارنىلا پەيغەمبەر قىلىپ ئەۋەتتۇق، ئەگەر (بۇنى) بىلمىسەڭلار، ئەھلى ئىلىمدىن (يەنى تەۋرات، ئىنجىللارنى بىلىدىغانلاردىن) سوراڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,8,' پەيغەمبەرلەرنى تاماق يېمەيدىغان جەسەت قىلغىنىمىز يوق (يەنى ئۇلار بارلىق كىشىلەرگە ئوخشاش يەيدۇ، ئىچىدۇ) ھەمدە ئۇلار (دۇنيادا ئۆلمەي) مەڭگۈ قالىدىغان بولغىنى يوق (يەنى ئۇلارمۇ بارلىق كىشىلەرگە ئوخشاش ۋاپات بولىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,9,' ئاندىن ئۇلارغا (ئۇلارغا ياردەم بېرىش، ئۇلارنى ئىنكار قىلغانلارنى ھالاك قىلىشتىن ئىبارەت ۋەدىمىزنى راست قىلىپ بەردۇق، ئۇلارنى ۋە بىز خالىغانلارنى قۇتقۇزدۇق، (كۇفرىدا ۋە گۇمراھلىقتا) ھەددىدىن ئاشقۇچىلارنى ھالاك قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,10,' سىلەرنىڭ شان ـ شەرىپىڭلارنى ئۆز ئىچىگە ئالغان كىتابنى (يەنى قۇرئاننى) سىلەرگە ھەقىقەتەن نازىل قىلىپ بەردۇق (بۇنى) چۈشەنمەمسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,11,' بىز (ئاھالىسى) كۇففار بولغان نۇرغۇن شەھەرلەرنى ھالاك قىلدۇق ئۇلاردىن كېيىن (ئورنىغا) باشقا قەۋمنى پەيدا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,12,' ئۇلار بىزنىڭ ئازابىمىزنىڭ (كەلگەنلىكىنى) ھېس قىلغان چاغدا ئۇشتۇمتۇت قاچىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,13,' (پەرىشتىلەر ئۇلارغا مەسخىرە قىلىپ ئېيتتى) قاچماڭلار، باياشات تۇرمۇشۇڭلارغا، تۇرالغۇ جايلىرىڭلارغا قايتىڭلار، سىلەردىن (مۇھىم ئىشلاردا مەسلىھەت) سورىلىشى مۇمكىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,14,' ئۇلار: «ۋاي ئىسىت! ھەقىقەتەن بىز زالىم بولۇپتۇق» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,15,' ئۇلارنى ئورۇلغان زىرائەتتەك ھالاك قىلىپ، جانلىرىنى ئالغىنىمىزغا قەدەر، ئۇلار يۇقىرىقى پەريادىنى تەكرارلاپ تۇردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,16,' بىز ئاسماننى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئويناپ ياراتقىنىمىز يوق (يەنى ئۇلارنى بىكار ياراتماستىن، قۇدرىتىمىزنىڭ ئالامەتلىرى سۈپىتىدە ياراتتۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,17,' ئەگەر بىز ئىچ پۇشۇقى قىلماقچى بولساق، قېشىمىزدىكى نەرسىلەر بىلەن قىلاتتۇق، ئەگەر (شۇنى) ئىرادە قىلغان بولساق (لېكىن بۇ ئۇلۇغ زاتىمىزغا مۇھال بولغانلىقتىن بۇنداق قىلىشنى ئىرادە قىلمىدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,18,' بىز ھەق ئارقىلىق باتىلغا ھۇجۇم قىلىمىز، ھەق باتىلنى يوقىتىدۇ، باتىل ناگاھان يوقىلىدۇ، (ئى كۇففارلار جامائەسى! اﷲ نى بالىسى بار، خوتۇنى بار دەپ) سۈپەتلىگەن سۆزۈڭلاردىن سىلەرگە ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,19,' ئاسمانلاردىكى ۋە زېمىندىكىلەرنىڭ ھەممىسى اﷲ نىڭ ئىلكىدىدۇر، اﷲ نىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) اﷲ نىڭ ئىبادىتىدىن تەكەببۇرلۇق قىلىپ باش تارتمايدۇ ۋە ھېرىپمۇ قالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,20,' ئۇلار كېچە ـ كۈندۈز تەسبىھ ئېيتىدۇ، بوشاشمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,21,' ئۇلار (يەنى مۇشرىكلار) زېمىندىكى نەرسىلەرنى ئىلاھ قىلىۋالدى، ئۇلار (يەنى بۇ نەرسىلەر) ئۆلۈكلەرنى تىرىلدۈرەلەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,22,' ئەگەر ئاسمان ـ زېمىندا اﷲ تىن باشقا ئىلاھلار بولسا ئىدى، (كائىناتنىڭ تەرتىپى) ئەلۋەتتە بۇزۇلاتتى، ئەرشنىڭ پەرۋەردىگارى اﷲ ئۇلارنىڭ سۈپەتلىگەن نەرسىلىرىدىن پاكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,23,' اﷲ نىڭ قىلغانلىرىدىن سوئال ـ سوراق قىلىنمايدۇ، ئۇلاردىن سوئال ـ سوراق قىلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,24,' ئۇلار اﷲ نى قويۇپ، باشقا مەبۇدلارنى ئىلاھ قىلىۋالدىمۇ؟ ئېيتقىنكى: «(بۇنىڭغا) دەلىلىڭلارنى كۆرسىتىڭلار، مانا بۇ مەن بىلەن بىللە بولغانلارنىڭ كىتابى (يەنى قۇرئان) دۇر ۋە مەندىن ئىلگىرىكىلەرنىڭ (تەۋرات، ئىنجىل قاتارلىق) كىتابىدۇر، ئۇلارنىڭ تولىسى ھەقنى ئۇقمايدۇ، ئۇلار (تەۋھىد) تىن يۈز ئۆرۈگۈچىلەردۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,25,' (ئى مۇھەممەد!) سەندىن ئىلگىرى ئەۋەتىلگەن پەيغەمبەرلەرنىڭ ھەممىسىگە: «مەندىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ماڭىلا ئىبادەت قىلىڭلار» دەپ ۋەھيى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,26,' ئۇلار (يەنى مۇشرىكلار): «اﷲ نىڭ (پەرىشتىلەردىن) بالىسى بار» دېيىشتى، ئۇنداق ئەمەس اﷲ (بۇنىڭدىن) پاكتۇر، (پەرىشتىلەر اﷲ نىڭ) ھۆرمەتلىك بەندىلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,27,' ئۇلار اﷲ قا ئالدى بىلەن سۆز قىلىشقا پېتىنىلمايدۇ، ئۇلار اﷲ نىڭ ئەمرى بويىچە ئىش قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,28,' اﷲ ئۇلارنىڭ قىلغانلىرىنى ۋە قىلماقچى بولغانلىرىنى بىلىپ تۇرىدۇ، ئۇلار اﷲ رازى بولغانلارغىلا شاپائەت قىلىدۇ، اﷲ نىڭ ھەيۋىسىدىن تىترەپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,29,' ئۇلاردىن كىمكى، مەن اﷲ تىن باشقا ئىلاھمەن، دەيدىكەن، ئۇنى دوزاخ بىلەن جازالايمىز، زالىملارنى شۇنداق جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,30,' كاپىرلار بىلەمدۇكى، ئاسمانلار بىلەن زېمىن بىر ـ بېرىگە تۇتاش ئىدى، ئۇلارنى ئايرىۋەتتۇق، ھەممە جانلىق مەۋجۇداتنى سۇدىن ياراتتۇق، ئۇلار (اﷲ نىڭ قۇدرىتىگە) ئىشىنەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,31,' يەر تەۋرەپ ئۇلارنىڭ خاتىرجەمسىز بولماسلىقى ئۈچۈن، بىز زېمىندا تاغلارنى ياراتتۇق، ئۇلارنى (سەپەردە) كۆزلىگەن مەقسەتلەرگە يېتىۋالسۇن دەپ، تاغلاردا كەڭ يوللارنى ياراتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,32,' ئاسماننى بىز بىخەتەر ئۆگزە قىلدۇق، ھالبۇكى، ئۇلار (يەنى مۇشرىكلار) ئاسماندىكى (كۈن، ئاي، يۇلتۇزلارغا ئوخشاش اﷲ نىڭ بارلىقىنى ۋە ئۇنىڭ قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلەردىن غەپلەتتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,33,' اﷲ كېچىنى ۋە كۈندۈزنى، كۈننى ۋە ئاينى ياراتتى ئۇلارنىڭ ھەر بىرى (ئۆزىگە خاس) پەلەكتە سەير قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,34,' (ئى مۇھەممەد!) سەندىن ئىلگىرى (ئىنسانلاردىن) ھېچ كىشىنى مەڭگۈ ياشاتقىنىمىز يوق، سەن ئۆلسەڭ، ئۇلار مەڭگۈ ياشامدۇ؟ (بۇنداق بولۇش ھەرگىزمۇ مەڭگۈ ياشاتقىنىمىز يوق، سەن ئۆلسەك، ئۇلار مەڭگۈ ياشامدۇ؟ (بۇنداق بولۇش ھەرگىزمۇ ئۇلارغا مۇيەسسەر بولمايدۇ، ئۇلارنىڭ ھەممىسى ئۆلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,35,' ھەر بىر جان ئۆلۈمنىڭ تەمىنى تېتىغۇچىدۇر سىلەرنى بىز يامانلىققا مۇپتىلا قىلىش، ياخشىلىق بېرىش ئارقىلىق سىنايمىز سىلەر بىزنىڭ دەرگاھىمىزغا قايتىسىلەر (ئەمەلىڭلارغا يارىشا جازالايمىز)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,36,' كاپىرلار سىنى كۆرگەن چاغدا: «سىلەرنىڭ مەبۇدلىرىڭلارنى ئەيىبلەيدىغان ئادەم مۇشۇمۇ؟ » (دېيىشىپ) سېنى مەسخىرە قىلىشىدۇ، ھالبۇكى، ئۇلار مەرھەمەتلىك اﷲ تەرىپىدىن نازىل بولغان قۇرئاننى ئىنكار قىلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,37,' ئىنسان ئالدىراڭغۇ يارىتىلغان، سىلەرگە ئالامەتلىرىمنى (يەنى ماڭا ئاسىيلىق قىلغانلارنى جازالايدىغانلىقىمنى) كۆرسىتىمەن (ئۇنىڭ ۋاقتى سائىتىدىن بۇرۇن) مېنى ئالدىرىتىپ كەتمەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,38,' ئۇلار: «(سۆزۈڭلاردا) راستچىل بولساڭلار، بۇ ۋەدە (يەنى بىزگە قىلىنغان ئازابنىڭ يۈز بېرىدىغان ۋاقتى) قاچان؟ » دېيىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,39,' ئەگەر كاپىرلار بۇ چاغدا يۈزلىرىدىن، دۈمبىلىرىدىن ئازابنى دەپئى قىلالمايدىغانلىقلىرىنى ۋە ھېچقانداق ياردەمگە ئېرىشەلمەيدىغانلىقلىرىنى بىلسە ئىدى (ئازابنى بالدۇر يۈز بېرىشىنى تەلەپ قىلمايتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,40,' لېكىن قىيامەت ئۇلارغا تۇيۇقسىز كېلىدۇ ـ دە، ئۇلارنى ھەيرەتتە قالدۇرىدۇ، ئۇلار قىيامەتكە قارشىلىق كۆرسىتەلمەيدۇ، ئۇلارغا (تەۋبە قىلىۋېلىش ۋە ئۆزرە بايان قىلىش ئۈچۈن) مۆھلەتمۇ بېرىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,41,' سەندىن ئىلگىرى ھەقىقەتەن نۇرغۇن پەيغەمبەرلەر مەسخىرە قىلىندى، ئۇلارنى مەسخىرە قىلغۇچىلارغا مەسخىرە قىلغاننىڭ جازاسى بولغان ئازاب نازىل بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,42,' (ئى مۇھەممەد! مەسخىرە قىلغۇچىلارغا) «سىلەرنى كېچىدە ۋە كۈندۈزدە مەرھەمەتلىك اﷲ نىڭ ئازابىدىن كىم ساقلايدۇ؟» دېگىن بەلكى ئۇلار پەرۋەردىگارىنىڭ كالامىدىن يۈز ئۆرۈگۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,43,' ئۇلاردىن بىزدىن باشقا ئۆزلىرىنى (قوغدايدىغان) مەبۇدلىرى بارمۇ ئۇلارنىڭ (چوقۇنۇۋاتقان) مەبۇدلىرى ئۆزلىرىنى قوغداشقىمۇ قادىر بولالمايدۇ، بىزنىڭ ياردىمىمىزگىمۇ ئېرىشەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,44,' بۇلارنى (يەنى مۇشرىكلارنى) ۋە ئۇلارنىڭ ئەجداتلىرىنى (دۇنيانىڭ مال ـ مۈلكىدىن) بەھرىمەن قىلدۇق، ھەتتا (شۇ نېمەت ئىچىدە) ئۇلارنىڭ ئۆمرى ئۇزۇن بولدى ئۇلارنىڭ زېمىننىڭ ئەتراپىنى (مۇسۇلمانلارغا ئىگەللىتىش بىلەن) قەدەممۇقەدەم تارايتىپ بېرىۋاتقانلىقىمىزنى ئۇلارمۇ بىلەمدۇ؟ (ئەھۋال مۇشۇنداق ئىكەن) ئۇلار غالىب ھېسابلىنامدۇ؟ (مەغلۇپ ھېسابلىنامدۇ؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,45,' (ئى مۇھەممەد!) «مەن سىلەرنى پەقەت ۋەھيى ئارقىلىق ئاگاھلاندۇرىمەن» دېگىن گاسلار ئاگاھلاندۇرۇلغان چاغدا دەۋەتنى ئاڭلىمايدۇ (ئى مۇشرىكلار! سىلەر سەركەشلىكىڭلارنىڭ قاتتىقلىقىدىن خۇددى گاسقا ئوخشاش دەۋەتنى ئاڭلىمايسىلەر، ۋەز ـ نەسىھەتنى قوبۇل قىلمايسىلەر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,46,' ئەگەر ئۇلار پەرۋەردىگارىڭدىن ئازغىنا ئازاب يەتسىلا، ئۇلار چوقۇم: «ۋاي ئىسىت! (اﷲ نىڭ پەيغەمبەرلىرىنى ئىنكار قىلىپ) ھەقىقەتەن (ئۆزىمىزگە) زۇلۇم قىلىپتۇق» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,47,' قىيامەت كۈنى بىز (ئەمەللەر تارتىلىدىغان) ئادالەت تارازىسىنى ئورنىتىمىز، ھېچ ئادەمگە قىلچە ئۇۋال قىلىنمايدۇ (يەنى ياخشى ئادەمنىڭ ياخشىلىقى كېمەيتىۋېتىلمەيدۇ، يامان ئادەمنىڭ يامانلىقى ئاشۇرۇۋېتىلمەيدۇ) ئەگەر ئۇنىڭ قىچا چاغلىق ئەمەلى بولسىمۇ، ئۇنى ھازىر قىلىمىز، (بەندىلەرنىڭ ئەمەللىرىدىن) ھېساب ئېلىشقا بىز يېتەرلىكمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,48,' بىز ھەقىقەتەن موسا بىلەن ھارۇنغا فۇرقاننى (يەنى ھەق بىلەن باتىلنى، ھالال بىلەن ھارامنى، ھىدايەت بىلەن گۇمراھلىقنى ئايرىغۇچى تەۋراتنى)، نۇرنى، تەقۋادارلارغا (پايدىلىنىدىغان) ۋەز ـ نەسىھەتنى بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,49,' تەقۋادارلار پەرۋەردىگارىنى كۆرمەي تۇرۇپ، ئۇنىڭدىن قورقىدۇ ھەمدە ئۇلار قىيامەتنىڭ (دەھشىتىدىن) تىترىگۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,50,' بۇ بىز (سىلەرنىڭ تىلىڭلاردا) نازىل قىلغان مۇبارەك قۇرئاندۇر (ئى ئەرەب جامائەسى! ئۇ شۇنچە روشەن تۇرسىمۇ) سىلەر ئۇنى ئىنكار قىلامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,51,' (مۇسا، ھارۇنلاردىن بۇرۇن) بىز ئىبراھىمغا ھەقىقەتەن (دۇنيا ۋە ئاخىرەتتە ياخشىلىق يوللىرىنى كۆرسىتىپ بېرىدىغان) ھىدايەت ئاتا قىلغان ئىدۇق، بىز ئۇنىڭ (پەيغەمبەرلىكنىڭ ئەھلى) ئىكەنلىكىنى بىلەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,52,' ئۆز ۋاقتىدا ئىبراھىم (كۇففارلار چوقۇنۇۋاتقان بۇتلارنى مەنسىتمەي) ئاتىسىغا ۋە قەۋمىگە: سىلەر چوقۇنۇۋاتقان بۇ بۇتلار نېمە؟ (يەنى نېمە ئۈچۈن بۇ بۇتلارغا چوقۇنىسىلەر؟)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,53,' ئۇلار: «ئاتا ـ بوۋىلىرىمىزنىڭ ئۇلارغا ئىبادەت قىلىپ كېلىۋاتقانلىقىنى بىلىمىز (يەنى ئۇلارغا ئاتا ـ بوۋىلىرىمىزغا تەقلىد قىلىپ چوقۇنىمىز)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,54,' ئىبراھىم: «سىلەرمۇ، ئاتا ـ بوۋاڭلارمۇ (پايدا ـ زىيان يەتكۈزەلمەيدىغان، ئاڭلىمايدىغان جانسىز بۇتلارغا چوقۇنغانلىقىڭلار ئۈچۈن) ھەقىقەتەن روشەن گۇمراھلىقتا سىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,55,' ئۇلار: «سەن راست دەۋاتامسەن؟ ياكى چاقچاق دەۋاتامسەن؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,56,' ئىبراھىم ئېيتتى: «ئۇنداق ئەمەس، سىلەرنىڭ (ئىبادەت قىلىشىڭلارغا لايىق) پەرۋەردىگارىڭلار ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارىدۇر، ئۇلارنى ئۇ ياراتقان، مەنمۇ ئۇنىڭغا (قەتئىي دەلىللەر بىلەن) گۇۋاھلىق قىلغۇچىلاردىنمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,57,' اﷲ بىلەن قەسەمكى، سىلەر قايتىپ كەتكەندىن كېيىن چوقۇم بۇتلىرىڭلارنى (چېقىۋېتىشنىڭ) ئامالىنى قىلىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,58,' ئىبراھىم بۇتلارنى چېقىپ پارچە ـ پارچە قىلىۋەتتى، ئۇلارنىڭ قايتىپ كېلىپ (بۇتلارنى كىمنىڭ چاققانلىقىنى سورىشى ئۈچۈن) پەقەت چوڭ بۇتنى (چاقماي) قالدۇردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,59,' (ئۇلار بايرىمىدىن قايتىپ كېلىپ بۇتلىرىنىڭ چېقىۋېتىلگەنلىكىنى كۆرۈپ) «بۇتلىرىمىزنى مۇنداق قىلغان كىم؟ ئۇ ھەقىقەتەن زالىم ئىكەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,60,' (ئۇلارنىڭ بەزىسى) ئىبراھىم ئاتلىق بىر يىگىتنىڭ ئۇلارنى ئەيىبلىگەنلىكىنى ئاڭلىغان ئىدۇق (يەنى شۇ قىلغان بولۇشى مۇمكىن)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,61,' ئۇلار (يەنى نەمرۇد ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرى): «ئىبراھىمنى كىشىلەرنىڭ ئالدىغا ئېلىپ كېلىڭلار، (ئىبراھىمنىڭ بۇتلارنى ئەيىبلىگەنلىكىگە) ئۇلار گۇۋاھلىق بەرسۇن» دېدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,62,' ئۇلار: «ئى ئىبراھىم! بۇتلىرىمىزنى مۇشۇنداق قىلغان سەنمۇ؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,63,'(ئىبراھىم: «(ياق) بەلكى ئۇلارنىڭ مۇنۇ چوڭى شۇنداق قىلدى، ئەگەر ئۇلار سۆزلىيەلىسە، (كىمنىڭ چاققانلىقىنى) ئۇلارنىڭ ئۆزلىرىدىن سوراپ بېقىڭلار!» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,64,' ئۇلار بىر ـ بىرىنى ئەيىبلەپ: «(سىلەر بۇتلارغا چوقۇنۇشتا) ھەقىقەتەن زالىمسىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,65,' ئاندىن ئۇلار (خىجالەتچىلىكتىن) باشلىرىنى تۆۋەن سېلىپ: «ئۇلارنىڭ گەپ قىلالمايدىغانلىقىنى سەن ئوبدان بىلىسەنغۇ؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,66,' ئىبراھىم ئېيتتى: «سىلەر اﷲ نى قويۇپ سىلەرگە ھېچ پايدا يەتكۈزەلمەيدىغان ۋە زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنامسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,67,' سىلەرگە ۋە اﷲ نى قويۇپ سىلەر چوقۇنۇۋاتقان بۇتلارغا تۈفى! سىلەر چۈشەنمەمسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,68,' ئۇلار ئېيتتى: «ئىبراھىمنى كۆيدۈرۈڭلار، ئىلاھلىرىڭلارغا ياردەم بېرىڭلار، ئەگەر ئۇلارغا ھەقىقىي ياردەم بەرمەكچى بولساڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,69,' بىز ئوتقا: «ئى ئوت! ئىبراھىمغا سالقىن ۋە ئامانلىق (بەخش ئېتىدىغان) بولۇپ بەرگىن» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,70,' ئۇلار ئىبراھىمغا زىيانكەشلىك قىلماقچى بولدى، بىز ئۇلارنى ئەڭ زىيان تارتقۇچى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,71,' بىز ئىبراھىم بىلەن لۇتنى قۇتقۇزدۇق، ئۇلارنى بىز ئەھلى جاھان ئۈچۈن بەرىكەتلىك زېمىنغا كۆچۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,72,'بىز ئۇنىڭغا (يەنى ئىبراھىمغا) ئىسھاقنى بەردۇق، (ئۇ پەرۋەردىگارىدىن سورىمىسىمۇ) يەئقۇبنى ئوشۇق بەردۇق (يەنى يەئقۇب ئەلەيھىسسالامنى نەۋرە قىلىپ ياكى ئىبراھى ئەلەيھىسسالامنىڭ تەلىۋىدىن زىيادە قىلىپ بەردۇق)، ئۇلارنىڭ ھەممىسىنىڭ ياخشى ئادەملەر قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,73,' ئۇلارنى بىزنىڭ ئەمرىمىز بويىچە (كىشىلەرنى بىزنىڭ دىنىمىزغا) يېتەكلەيدىغان پېشۋالار (يەنى پەيغەمبەرلەر) قىلدۇق، ئۇلارغا بىز ياخشى ئىشلارنى قىلىشنى، ناماز ئوقۇشنى، زاكات بېرىشنى ۋەھيى قىلىدۇ، ئۇلار بىزگە خالىس ئىبادەت قىلاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,74,' لۇتقا پەيغەمبەرلىكنى ۋە ئىلىمنى ئاتا قىلدۇق (لۇت ئىبراھىم ئەلەيھىسسالامغا ئىمان ئېيتقان، ئەگەشكەن ۋە ئۇنىڭ بىلەن ھىجرەت قىلغان ئىدى)، ئۇنىڭ (ئائىلىسى بەچچىۋازلىق، قاراقچىلىق قاتارلىق) يامان قىلىقلارنى قىلىدىغان شەھەردىن قۇتقۇزدۇق، ھەقىقەتەن ئۇلار يامان پاسىق قەۋم ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,75,' بىز ئۇنى رەھمىتىمىز دائىرىسىگە كىرگۈزدۇق، ئۇ ھەقىقەتەن ياخشىلاردىن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,76,' نۇھنىڭ (قىسسىسىنى بايان قىلغىن) ئۆز ۋاقتىدا ئۇ (مەزكۇر پەيغەمبەرلەردىن) بۇرۇن (قەۋمىگە، پەرۋەردىگارىم يەر يۈزىدە كۇففارلاردىن بىرنىمۇ قويمىغىن، دەپ بەتدۇئا قىلغان ئىدى)، بىز ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنى ۋە ئۇنىڭ ئائىلىسىدىكىلەرنى (ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنى) بۈيۈك بالادىن (يەنى توپان بالاسىدا غەرق بولۇشتىن) قۇتقۇزدۇق (يەنى نۇھنى قەۋمىنىڭ شەررىدىن قۇتقۇزۇپ، ئۇلارنى ھالاك قىلدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,77,' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان قەۋمگە قارشى ئۇنىڭغا ياردەم بەردۇق، ئۇلار ھەقىقەتەن يامان قەۋم ئىدى ئۇلارنىڭ (بىرنىمۇ قويماي) ھەممىسىنى غەرق قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,78,' داۋۇت بىلەن سۇلايماننىڭ (قىسسىسىنى بايان قىلغىن) ئۆز ۋاقتىدا بىر قەۋمنىڭ قويى كېچىسى زىرائەتنى يەپ (بۇزۇۋەتكەندە)، ئۇ ئىككىسى زىرائەت توغرىسىدا ھۆكۈم چىقارغان ئىدى  ئۇلارنىڭ ھۆكمىگە بىز شاھىت ئىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,79,' قانداق ھۆكۈم قىلىشنى بىز سۇلايمانغا بىلدۈردۇق، تەسبىھ ئېيتىشقا مۇسەخخەر قىلدۇق، (شۇنداق قىلىشقا) بىز قادىر ئىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,80,' جەڭلەردە سىلەرنى (يارىدار بولۇشتىن) ساقلاش مەقسىتىدە، داۋۇدقا سىلەر ئۈچۈن ساۋۇت ياساشنى ئۆگەتتۇق، سىلەر (بۇنىڭغا) شۈكۈر قىلامسىلەر؟ (يەنى شۈكۈر قىلىڭلار ۋە نېمەتنىڭ قەدرىنى بىلىڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,81,' سۇلايمانغا قاتتىق چىقىدىغان شامالنى مۇسەخخەر قىلىپ بەردۇق، شامال سۇلايماننىڭ ئەمرى بويىچە، بىز بەرىكەتلىك قىلغان زېمىنغا (يەنى شامغا) باراتتى، بىز ھەممىنى بىلگۈچىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,82,' سۇلايمانغا يەنە بىر قىسىم شەيتانلارنى مۇسەخخەر قىلىپ بەردۇق، ئۇلار دېڭىزلاردا چۆكۈپ ئۇنىڭغا ئۈنچە ـ مارجان (گۆھەرلەرنى سۈزۈپ چىقاتتى)، ئۇلار يەنە (شەھەرلەرنى، ئېگىز سارايلارنى سېلىشتەك) باشقا ئىشلارنىمۇ قىلاتتى، بىز ئۇلارنى (سۇلايماننىڭ ئىتائىتىدىن چىقىشتىن) ساقلايتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,83,' (ئەييۇبنىڭ قسسسىنى بايان قىلغىن) ئۆز ۋاقتىدا ئۇ پەرۋەردىگارىغا: «ھەقىقەتەن مېنى بال (يەنى قاتتىق كېسەل) ئورىۋالدى، سەن ئەڭ مەرھەمەتلىكسەن» دەپ دۇئا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,84,' ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنىڭ بېشىغا كەلگەن بالانى كۆتۈرۈۋەتتۇق، ئۇنىڭغا رەھىم قىلغانلىقىمىز، ئىبادەت قىلغۇچىلارغا ئىبرەت قىلغانلىقىمىز ئۈچۈن، ئۇنىڭغا ئائىلىسىنى ۋە ئۇنىڭغا ئوخشاش بىر باراۋەرنى زىيادە ئاتا قىلدۇق (يەنى ئۇنىڭ ئۆلگەن بالىلىرىنىڭ ۋە يوقالغان ماللىرىنىڭ ئورنىغا بىر ھەسسە كۆپ بالا، بىر ھەسسە كۆپ مال بەردۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,85,' ئىسمائىل، ئىدرىس ۋە زۇلكىفللارنىڭ (قىسسىسىنى باياق قىلغىن) (ئۇلارنىڭ) ھەممىسى سەۋرلىك ئىدى (يەنى اﷲ قا ئىبادەت قىلىشتا چىداملىق ئىدى ۋە گۇناھلار ئالدىدا ئۆزلىرىنى تۇتۇۋالاتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,86,'ئۇلارنى رەھمىتىمىز دائىرىسىگە كىرگۈزدۇق، ئۇلار ھەقىقەتەن ياخشىلاردىن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,87,' يۇنۇسنىڭ (قىسسىنى بايان قىلغىن) ئۆز ۋاقتىدا ئۇ (قەۋمنىڭ ئىمان ئېيتمىغانلىقىدىن) خاپا بولۇپ (شەھىرىدىن) چىقىپ كەتكەن ئىدى ئۇ بىزنى (بېلىقنىڭ قارنىدا) ئۆزىنى قىسمايدۇ دەپ ئويلىدى، ئۇ قاراڭغۇلۇقتا (يەنى بېلىقنى قارنىدا): «(پەرۋەردىگارىم) سەندىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، سەن (جىمى كەمچىلىكلەردىن) پاكتۇرسەن، مەن ھەقىقەتەن (ئۆز نەپسىمگە) زۇلۇم قىلغۇچىلاردىن بولدۇم» دەپ نىدا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,88,' ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنى (بېلىق يۇتقان چاغدىكى) غەمدىن خالاس قىلدۇق، شۇنىڭدەك مۆمىنلەرنى (ئېغىرچىلىقلاردىن) خالاس تاپقۇزىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,89,' زەكەرىيانىڭ (قىسسىنى بايان قىلغىن) ئۆز ۋاقتىدا ئۇ: «ئى پەرۋەردىگارىم! مىنى (بالىسىز، ۋارىسسىز) يالغۇز قويمىساڭ، سەن ئەڭ ياخشى ۋارىستۇرسەن (يەنى ھەممە ئادەم ئۆلىدۇ، سەنلا باقىي قالىسەن)» دەپ نىدا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,90,' بىز ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنىڭغا يەھيانى ئاتا قىلدۇق، ئۇنىڭغا خوتۇنىنى ئۆزگەرتىپ بەردۇق (يەنى تۇغىدىغان قىلىپ بەردۇق) (يۇقىرىقى پەيغەمبەرلەرنىڭ دۇئاسىنى ئىجابەت قىلىشىمىز) شۇنىڭ ئۈچۈن ئىدىكى، ئۇلار ياخشى ئىشلارنى قىلىشقا ئالدىرايتتى، (رەھمىتىمىزنى) ئۈمىد قىلىپ، (ئازابىمىزدىن) قورقۇپ بىزگە دۇئا قىلاتتى، بىزگە كەمتەرلىك بىلەن ئىبادەت قىلاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,91,' نومۇسنى ساقلىغان ئايالنى (يەنى مەريەمنىڭ قىسسىنى بايان قىلغىن) ئۇنىڭغا (يەنى كىيىمنىڭ ئىچىگە) بىزنىڭ تەرىپىمىزدىن بولغان روھنى پۈۋلىدۇق (پۈۋلەنگەن روھ ئۇنىڭ ئىچىگە كىرىپ، ئۇ ئىساغا ھامىلدار بولدى)، ئۇنى ۋە ئوغلىنى (يەنى ئىسا بىلەن مەريەمنى) ئەھلى جاھان ئۈچۈن (بىزنىڭ قۇدرىتىمىزنى كۆرسىتىدىغان) دەلىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,92,' (ئى ئىنسانلار!) سىلەرنىڭ دىنىڭلار ھەقىقەتەن بىر دىندۇر مەن سىلەرنىڭ پەرۋەردىگارىڭلاردۇرمەن، ماڭىلا ئىبادەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,93,' ئۇلار (دىندا ئىختىلاپ قىلىشىپ) پىرقە ـ پىرقىلەرگە بۆلۈنۈپ كەتتى، ھەممىسى بىزنىڭ دەرگاھىمىزغا قايتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,94,' كىمكى مۆمىن بولۇپ ياخشى ئەمەللەرنى قىلىدىكەن، ئۇنىڭ ئەمەلىنىڭ (ساۋابى) زايا كەتمەيدۇ ئۇنىڭ ئەمەلىنى (يەنى نامە ـ ئەمالنى) چوقۇم خاتىرىلەپ قويىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,95,' بىز ھالاك قىلغان شەھەر (ئاھالىسى) نىڭ دۇنياغا قايتىشى مۇمكىن ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,96,' يەجۇج ـ مەجۈج (توسمىسى) ئېچىۋېتىلگەندە، ئۇلار زېمىننىڭ ھەر بىر تۆپىلىكلىرىدىن ئالدىراپ چىقىپ كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,97,' راست ۋەدە (يەنى قىيامەتنىڭ ۋاقتى) يېقىنلاشتى، ئۇ كۈندە كاپىرلارنىڭ كۆزلىرى چەكچىيىپ قالىدۇ، (ئۇلار) «ۋاي ئىسىت! بۇنىڭدىن بىز غەپلەتتە قېلىپتۇق، بەلكى بىز ئۆزىمىزگە زۇلۇم قىلىپتۇق» (دېيىشىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,98,' (ئى مۇشرىكلار!) ھەقىقەتەن سىلەر اﷲ نى قويۇپ ئىبادەت قىلغان نەرسەڭلار بىلەن دوزاخقا يېقىلغۇ بولىسىلەر، سىلەر دوزاخقا كىرىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,99,' ئەگەر سىلەر ئىبادەت قىلىۋاتقان بۇتلار ئىلاھلار بولىدىغان بولسا، دوزاخقا كىرمىگەن بولاتتى (ئىبادەت قىلغۇچى ۋە ئىبادەت قىلىنغۇچىلارنىڭ) ھەممىسى دوزاختا مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,100,' ئۇلار دوزاختا ھەمىشە پەرياد چېكىدۇ، (ئازابنىڭ قاتتىقلىقىدىن) ئۇلار دوزاختا ھېچ نەرسىنى ئاڭلىمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,101,' بىز بىر تەرەپتىن بېرىلگەن بەخت ـ سائادەتكە تېگىشلىك بولغانلار (يەنى تائەت ـ ئىبادەتكە تەۋپىق بېرىلىپ توغرا يولدا ئىبادەت قىلغان سائادەتمەن بەندىلەر) دوزاختىن يىراق قىلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,102,' ئۇلار دوزاخنىڭ شەپىسىنىڭ ئاڭلىمايدۇ، كۆڭلى تارتقان نازۇنېمەتلەر ئىچىدە مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,103,' چوڭ قورقۇنچ (يەنى قىيامەتنىڭ دەھشىتى) ئۇلارنى قايغۇغا سالمايدۇ، پەرىشتىلەر: «اﷲ سىلەرگە ۋەدە قىلغان كۈن مۇشۇ» (دەپ)، ئۇلارنى (جەننەتنىڭ دەرۋازىلىرى ئالدىدا) قارشى ئالىدۇ() ئۇ كۈندە ئاسماننى (پۈتۈكچى) نەرسە يېزىلغان قەغەزنى يۆگىگەندەك يۆگەيمىز مەخلۇقاتلارنى دەسلەپتە قانداق ياراتقان بولساق، شۇ ھالىتىدە ئەسلىگە قايتۇرىمىز (بۇ) بىز ئۈستىمىزگە ئالغان ۋەدىدۇر، (ئۇنى) چوقۇم ئىشقا ئاشۇرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,104,' بىز لەۋھۇلمەھپۇزدا (ئەزەلدە) يازغاندىن كېيىن، (داۋۇدقا نازىل قىلىنغان) زەبۇردا زېمىنغا (يەنى جەننەت زېمىنغا) ھەقىقەتەن مېنىڭ ياخشى بەندىلىرىم ۋارىسلىق قىلىدۇ دەپ يازدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,105,' بۇ (قۇرئان) دا اﷲ قا ئىبادەت قىلغۇچى قەۋم ئۈچۈن (خەۋەرلەر، ۋەز ـ نەسىھەتلەردىن) ئەلۋەتتە يېتەرلىك بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,106,' (ئى مۇھەممەد!) سېنى بىز پۈتۈن ئەھلى جاھان ئۈچۈن پەقەت رەھمەت قىلىپلا ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,107,' (ئى مۇھەممەد! مۇشرىكلارغا) «ماڭا ۋەھيى قىلىندىكى، سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، سىلەر بويسۇنامسىلەر؟» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,108,' ئەگەر ئۇلار (ئىسلامدىن) يۈز ئۆرۈسە، ئېيتقىنكى، مەن سىلەرگە (ھەقىقەتنى ھېچ كىشىنى ئايرىماستىن) ئوخشاش ئۇقتۇردۇم، سىلەرگە ۋەدە قىلىنغان (ئازاب) يېقىنمۇ، يىراقمۇ؟ ئۇقمايمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,109,' اﷲ ھەقىقەتەن ئاشكارا سۆزنىمۇ، سىلەرنىڭ يوشۇرغانلىرىڭلارنىمۇ بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,110,' بىلەمسەنكى، ئۇ (يەنى سىلەرگە بېرىلىدىغان جازانىڭ كېچىكىشى) سىلەر ئۈچۈن سىناق ۋە ۋاقىتلىق پايدىلىنىش بولۇشى مۇمكىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,21,111,' (پەيغەمبەر ئېيتتى) «پەرۋەردىگارىم! (مېنىڭ بىلەن مەككە كۇففارلىرىنىڭ ئارىسىدا) ئادىللىق بىلەن ھۆكۈم چىقارغىن، بىزنىڭ پەرۋەردىگارىمىز مەرھەمەتلىك اﷲ دۇر، سىلەرنىڭ بوھتان سۆزلىرىڭلارغا قارشى اﷲ تىن ياردەم تىلەيمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,1,'ئى ئىنسانلار! پەرۋەردىگارىڭلاردىن (يەنى ئازابىدىن) قورقۇڭلار، قىيامەتنىڭ زىلزىلىسى ھەقىقەتەن چوڭ ئىشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,2,' ئۇ كۈندە ھەر بىر سۈت ئېمىتىۋاتقان ئايال (قاتتىق قورقۇنچتىن تېڭىرقاپ قېلىش بىلەن) ئېمىۋاتقان بوۋىقىنى ئۇنتۇيدۇ، ھەر بىر ھامىلىدار ئايالنىڭ بويىدىن ئاجراپ كېتىدۇ، ئىنسانلارنى مەست كۆرۈسەن، ھالبۇكى، ئۇلار (ھەقىقەتتە) مەست ئەمەس (دېمەك، قىيامەتنىڭ دەششىتى ئۇلارنى ئەس ـ ھوشىدىن كەتكۈزۈۋەتكەن)، لېكىن اﷲ نىڭ ئازابى قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,3,' بەزى كىشىلەر اﷲ (نىڭ قۇدرىتى ۋە سۈپەتلىرى) ئۈستىدە ھېچقانداق دەلىلسىز مۇنازىرىلىشىدۇ، ھەر بىر سەركەش شەيتانغا (يەنى ھەقتى توسقۇچى كۇففارلارنىڭ كاتتىباشلىرىغا) ئەگىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,4,' اﷲ ھۆكۈم قىلىدۇكى، كىمكى شەيتانغا ئەگىشىدىكەن شەيتان ئۇنى ئازدۇرىدۇ، ۋە جەھەننەمنىڭ ئازابىغا سۆرەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,5,' ئى ئىنسانلار! ئەگەر سىلەر قايتا تىرىلىشتىن شەكلەنسەڭلار (گۇمانىڭلارنىڭ تۈگىشى ئۈچۈن ئەسلىدىكى يارىتىلىشىڭلارغا قاراڭلار)، بىز سىلەرگە (اﷲ نىڭ قۇدرىتىنى بايان قىلىش ئۈچۈن ئاتاڭلار ئادەم ئەلەيھىسسالامنى) تۇپراقتىن، ئۇنىڭ ئەۋلادىنى (ئالدى بىلەن) ئابىمەنىيدىن، ئاندىن لەختە قاندىن، ئاندىن شەكىلگە كىرگۈزۈلگەن ۋە كىرگۈزۈلمىگەن پارچە گۆشتىن ياراتتۇق بىز خالىغان ئادەمنى بەچچىداندان مۇئەييەن مۇددەتكىچە قالدۇرىمىز، ئاندىن سىلەرنى (ئاناڭلارنىڭ قارنىدىن) بوۋاقلىق ھالىتىڭلاردا چىقىرىمىز (ئاندىن سىلەرنى ئاستا ـ ئاستا كۈچ ـ قۇۋۋەتكە تولدۇرىمىز)، سىلەر (كامالەت يېشى) قىران ۋاقتىڭلارغا يېتىسىلەر، بەزىڭلار ياشلىقىڭلاردا ۋاپات بولۇپ كېتىسىلەر، بەزىڭلار، (ئىلگىرى) بىلگەندىن كېيىن ھېچ نەرسىنى بىلمەس ھالىتىڭلارغا قايتۇرۇلۇشىڭلار ئۈچۈن، ناھايىتى قېرىپ كەتكۈچە (ھايات) قالدۇرۇلىسىلەر، (اﷲ تائالانىڭ ئۆلگەنلەرنىڭ قايتا تىرىلدۈرۈشكە قادىر ئىكەنلىكىنى كۆرسىتىدىغان يەنە بىر دەلىل شۇكى) سەن زېمىننى قاقاس كۆرۈسەن، ئۇنىڭغا بىز يامغۇر ياغدۇرساق ئۇ جانلىنىدۇ ۋە كۆپۈشىدۇ، تۈرلۈك چىرايلىق ئۆسۈملۈكلەرنى ئۈندۈرۈپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,6,' بۇ (يەنى اﷲ تائالانىڭ ئىنساننى ۋە ئۆسۈملۈكلەرنى يۇقىرىقىدەك يارىتىشى)، اﷲ نىڭ ھەق ئىكەنلىكىنى، ئۆلۈكلەرنى تىرىلدۈرەلەيدىغانلىقىنى ۋە ھەر نەرسىگە قادىر ئىكەنلىكىنى سىلەرگە بىلدۈرۈش ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,7,' شۇنداقلا قىيامەتنىڭ شەكسىز بولىدىغانلىقىنى، قەبرىلەردىكى ئۆلۈكلەرنى (ئۆلۈك زېمىننى ئۆسۈملۈكلەرنى ئۈندۈرۈش بىلەن تىرىلدۈرگەندەك) اﷲ نىڭ تىرىلدۈرىدىغانلىقىنى بىلدۈرۈش ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,8,' بەزىلەر (توغرا تونۇشقا ئېلىپ بارىدىغان) ھېچ قانداق ھەقىقى ئىلىمسىز، ھىدايەتسىز ۋە نۇرلۇق كىتابسىز ھالدا اﷲ توغرىسىدا مۇنازىرىلىشىدۇ، (ئۇنىڭ مۇنازىرىلىشىشى نوقۇل نەپسى خاھىشى ۋە تەرسالىقىدىندۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,9,' ئۇ (كىشىلەرنى) اﷲ نىڭ يولىدىن ئازدۇرۇش ئۈچۈن مەغرۇرانە يۈرىدۇ، ئۇ دۇنيادا خارلىققا دۇچار بولىدۇ، ئاخىرەتتە ئۇنىڭغا كۆيدۈرگۈچى ئوتنىڭ ئازابىنى تېتىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,10,' (ئۇنىڭغا): «(خارلىق بىلەن ئازاب) سېنىڭ قىلمىشلىرىڭ يەنى كۇفرىڭ بىلەن گۇمراھلىقىڭ) تۈپەيلىدىندۇر، اﷲ بەندىلىرىگە ھەرگىز زۇلۇم قىلغۇچى ئەمەستۇر» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,11,' بەزىلەر اﷲ قا شەك ـ شۈبھە بىلەن (يەنى تۇراقسىز ھالدا) ئىبادەت قىلىدۇ، ئەگەر ئۇنىڭغا ياخشىلىق يەتسە دىنىدا تۇرىدۇ (بۇنداق ئادەم مۇناپىق بولۇپ، اﷲ قا دىلى بىلەن ئەمەس پەقەت تىلى بىلەن ئىبادەت قىلىدۇ)، ئەگەر ئۇنىڭغا بەختسىزلىك يەتسە دىنىدىن يېنىۋالىدۇ، ئۇ دۇنيا ۋە ئاخىرەتتە زىيان تارتىدۇ، بۇ روشەن زىياندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,12,' ئۇ اﷲ نى قويۇپ پايدا ـ زىيان يەتكۈزەلمەيدىغان بۇتقا چوقۇنىدۇ، بۇ چوڭقۇر گۇمراھلىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,13,' ئۇ (مۇشرىكلارنىڭ قارىشى بويىچە بۇت قىيامەت كۈنى شاپائەت قىلىدۇ دېگەندىمۇ) ئەلۋەتتە زىيىنى پايدىسىدىن يېقىن بولغان نەرسىگە چوقۇنىدۇ، (ئۇ بۇت) راستتىنلا يامان مەدەتكاردۇر، راستتىنلا يامان ھەمراھتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,14,' شۈبھىسىزكى، اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، اﷲ ھەقىقەتەن خالىغىنىنى قىلىدۇ (يەنى خالىغان ئادەمگە ساۋاب بېرىدۇ، خالىغان ئادەمگە ئازاب قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,15,' كىمكى اﷲ پەيغەمبەرگە دۇنيا ۋە ئاخىرەتتە ھەرگىز ياردەم بەرمەيدۇ دەپ گۇمان قىلىدىكەن، ئۇ تورۇسقا ئارغامچا سېلىپ بوغۇلۇپ ئالسۇن، قارىسۇنكى، ئۇنىڭ (بۇ) تەدبىرى غەزىپىنى باسالامدۇ؟ (يەنى اﷲ پەيغەمبىرى مۇھەممەد ئەلەيھىسسالامغا ياردەم بەرمەيدۇ دەپ ئويلايدىكەن ئۇ ئېسىلىپ ئۆلۈۋالسۇن، ئەگەر بۇ ئۇنىڭ خاپىلىقىنى باسالايدىغان بولسا، چۈنكى اﷲ پەيغەمبەرگە چوقۇم ياردەم بېرىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,16,' شۇنىڭغا ئوخشاش (يەنى ئىلگىرىكى كىتابلارنى نازىل قىلغىنىمىزغا ئوخشاش) قۇرئاننى (مۇھەممەد ئەلەيھىسسالامغا) روشەن ئايەتلەر قىلىپ نازىل قىلدۇق، اﷲ خالىغان ئادەمنى ھىدايەت قىلىدۇ (يەنى اﷲ تىن باشقا ھېچ ھىدايەت قىلغۇچى يوق، اﷲ خالىغان ئادەمنى توغرا يولغا باشلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,17,' مۆمىنلەر، يەھۇدىيلار، يۇلتۇزلارغا چوقۇنغۇچىلار، ناسارالار، مەجۇسىيلەر (يەنى ئاتەشپەرەسلەر) ۋە مۇشرىكلار ئۈچۈن قىيامەت كۈنى اﷲ ھەقىقەتەن ھۆكۈم چىقىرىدۇ، (يەنى اﷲ تائالا مۆمىنلەر بىلەن مەزكۇر بەش پىرقە ئارىسىدا توغرا ھۆكۈم چىقىرىپ، مۆمىنلەرنى جەننەتكە، كۇففارلارنى دوزاخقا كىرگۈزىدۇ)، اﷲ ھەقىقەتەن ھەممە نەرسىنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,18,' بىلمەمسەنكى، ئاسماندىكىلەر (يەنى پەرىشتىلەر)، زېمىندىكىلەر (يەنى ئىنسانلار، جىنلار ۋە زېمىندا ياشايدىغان بارلىق مەخلۇقاتلار)، كۈن، ئاي، يۇلتۇزلار، تاغلار، دەرەخلەر، ھايۋاناتلار اﷲ قا بويسۇنۇپ، اﷲ نىڭ تەسەررۇپ قىلىشى بويىچە ھەرىكەت قىلىدۇ) ۋە نۇرغۇن كىشىلەر اﷲ قا سەجدە قىلىدۇ، نۇرغۇن كىشىلەر (كۇفرى سەۋەبلىك) ئازابقا دۇچار بولدى، اﷲ نىڭ خارلىشىغا ئۇچرىغان ئادەمگە ھۆرمەتلىگۈچى چىقمايدۇ، اﷲ ھەقىقەتەن خالىغىنىنى قىلىدۇ (يەنى ئازاب قىلىش، رەھىم قىلىش، ئەزىز قىلىش، خار قىلىش، باي قىلىش، كەمبەغەل قىلىش اﷲ نىڭ خاھىشىدىكى ئىش بولۇپ، اﷲ قا ھېچ كىشى تەئەررۇز قىلالمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,19,' (مۆمىنلەر ۋە كۇففارلاردىن ئىبارەت) بۇ ئىككى (پىرقە) پەرۋەردىگارى توغرىسىدا مۇنازىرىلەشتى (يەنى مۆمىنلەر بىلەن كۇففارلار اﷲ نىڭ دىنى ئۈستىدە بەس ـ مۇنازىرە قىلىشىپ، مۆمىنلەر اﷲ نىڭ دىنىغا ياردەم بەرمەكچى بولدى، كۇففارلار اﷲ نىڭ نۇرىنى ئۆچۈرمەكچى بولدى)، كاپىرلارغا ئوتتىن كىيىملەر پىچىلىدۇ، ئۇلارنىڭ باشلىرى ئۈستىدىن يۇقىرى ھارارەتلىك قايناقسۇ قويۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,20,' ئۇنىڭ بىلەن ئۇلارنىڭ ئىچ –باغرى ۋە تېرىلىرى ئېرىتىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,21,' ئۇلار تۆمۈر ـ توقماقلار بىلەن ئۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,22,' ھەر قاچان ئۇلار (يېتىۋاتقان) غەم ـ قايغۇنىڭ قاتتىقلىقىدىن دوزاختىن چىقماقچى بولسا، ئۇلار دوزاخقا قايتۇرۇلىدۇ، (ئۇلارغا) كۆيدۈرگۈچى (دوزاخ) ئازابىنى تېتىڭلار (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,23,' اﷲ ھەقىقەتەن مۆمىنلەرنى ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇلار جەننەتلەردە ئالتۇن بىلەيزۈكلەرنى ۋە مەرۋايىتلارنى زىننەت بۇيۇملىرى قىلىدۇ، ئۇلارنىڭ كىيىمى يىپەكتىن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,24,' ئۇلار ياخشى سۆزگە (يەنى كەلىمە تەۋھىدكە) ھىدايەت قىلىندى اﷲ نىڭ يولىغا ھىدايەت قىلىندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,25,' كاپىرلارغا، (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقۇچىلارغا، ـ ئۇ ئولتۇرۇشلۇق بولسۇن ياكى سىرتتىن كەلگەن بولسۇن ـ بىز كىشىلەرگە ئىبادەتگاھ قىلغان مەسجىدى ھەرەمدىن توسقۇچىلارغا (يەنى ھەج پەرزنى ئادا قىلىش ئۈچۈن مەسجىدى ھەرەمغا كەلگەن مۆمىنلەرنى توسقۇچىلارغا) (قاتتىق ئازابنى تېتىتىمىز)، كىمكى مەسجىدى ھەرەمدا زۇلۇم بىلەن گۇناھ قىلماقچى بولىدىكەن، ئۇنىڭغا قاتتىق ئازابنى تېتىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,26,' ئۆز ۋاقتىدا ئىبراھىمغا بەيتۇللاھنىڭ ئورنىنى تەيىنلەپ بەردۇق، (ئۇنىڭغا ئېيتتۇقكى) «ماڭا ھېچ نەرسىنى شېرىك كەلتۈرمىگىن، تاۋاپ قىلغۇچىلارغا، قىيام قىلغۇچىلارغا، رۇكۇ قىلغۇچىلارغا، سەجدە قىلغۇچىلارغا مىنىڭ ئۆيۈمنى پاك قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,27,' كىشىلەرنىڭ ئارىسىدا (ئۇلارنى) ھەجىگە چاقىرىپ نىدا قىلغىن، ئۇلار پىيادە ۋە ئۇرۇق تۆگىلەرگە مىنىپ كېلىدۇ، ئۇرۇق تۆگىلەر يىراق يوللارنى بېسىپ كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,28,' كىشىلەر ئۆزلىرىگە تېگىشلىك بولغان (دىنى ۋە دۇنياۋى) مەنپەئەتلەرنى كۆرسۇن، بەلگىلەنگەن كۈنلەردە (يەنى قۇربانلىق كۈنلىرىدە) اﷲ ئۇلارغا رىزىق قىلىپ بەرگەن چارۋا ماللارنى (يەنى تۆگە، قوي، ئۆچكىلەرنى اﷲ نىڭ نېمەتلىرىگە شۈكۈر قىلىش يۈزىسىدىن) اﷲ نىڭ ئىسمىنى ئېيتىپ قۇربانلىق قىلسۇن» سىلەر قۇربانلىقلارنى گۆشىدىن يەڭلار، موھتاجقا، پېقىرغا بېرىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,29,' ئاندىن ئۇلار كىرلىرىنى تازىلىسۇن (يەنى ئېھرامدىن چىققاندىن كېيىن چاچلىرىنى، تىرناقلىرىنى ئالسۇن)، ئۆز ئۈستىگە ئالغان ئىبادەتلىرىنى ئادا قىلسۇن، قەدىمىي بەيت (يەنى بەيتۇللاھنى) تاۋاپ قىلسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,30,' (ھەج ئىبادىتى) ئەنە شۇدۇر، كىمكى اﷲ نىڭ دىنىنىڭ ئەھكاملىرىنى ئۇلۇغلىسا (يەنى ھەج جەريانىدا اﷲ نىڭ بۇيرۇغانلىرىنى بەجا كەلتۈرۈپ، مەنئى قىلغان ئىشلىرىدىن چەكلەنسە)، بۇ، پەرۋەردىگارنىڭ دەرگاھىدا ئۇنىڭ (دۇنيا ۋە ئاخىرەتلىكى) ئۈچۈن ياخشىدۇر، سىلەرگە چاھارپايلاردىن قۇرئاندا (ھاراملىقى) بايان قىلىنغانلىرىدىن باشقىلىرى ھالال قىلىندى سىلەر بۇتلاردىن ئىبارەت نىجىستىن ساقلىنىڭلار، يالغان گۇۋاھلىق بېرىشتىن ساقلىنىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,31,' اﷲ قا خالىس ئىبادەت قىلىڭلار، ئۇنىڭغا شېرىك كەلتۈرمەڭلار، كىمكى اﷲ قا شېرىك كەلتۈرىدىكەن، ئۇ گويا ئاسماندىن تاشلىنىپ، قۇشلار ئۇنى ئېلىپ قاچقاندەك، ياكى بوران ئۇنى ئېلىپ بېرىپ يىراق جايغا تاشلىۋەتكەندەك بولۇپ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,32,' ئىش مانا شۇنداق، كىمكى دىنىي ئىشلارنى (جۈملىدىن ھەجنىڭ ئەمەللىرىنى قۇربانلىقلارنى) ئۇلۇغلايدىكەن، بۇ، دىللارنىڭ تەقۋادارلىقىدىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,33,' مۇئەييەن مۇددەتكىچە (يەنى بوغۇزلايدىغان ۋاقىتقىچە قۇربانلىق ماللىرىدىن (سېغىپ، نەسىللەندۈرۈپ، مىنىپ) پايدىلىنىسىلەر، ئاندىن ئۇنى بوغۇزلاشقا تېگىشلىك جاي بەيتۇللاھنىڭ يېنىدۇر (يەنى (يەنى ھەرەمدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,34,' ھەر ئۈممەت اﷲ ئۇلارغا رىزىق قىلىپ بەرگەن چارۋىلارنى (شۈكۈر قىلىش يۈزىسىدىن قۇربانلىق قىلغانلىرىدا) اﷲ نىڭ ئىسمىنى ئېيتسۇن دەپ، ئۇلارغا قۇربانلىقنى بەلگىلىدۇق، سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، ئۇنىڭغا بويسۇنۇڭلار، ئىتائەتمەنلەرگە خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,35,' ئۇلار شۇنداق كىشىلەركى اﷲ ياد ئېتىلسە، ئۇلارنىڭ دىللىرى قورقۇپ (تىترەيدۇ)، ئۆزلىرىگە يەتكەن ئەزىيەتلەرگە سەۋر قىلىدۇ، ناماز ئۆتەيدۇ، ئۇلارغا بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن (ياخشىلىق يوللىرىغا) بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,36,' (كەبىگە ئېلىپ بېرىلىدىغان) تۆگىنى اﷲ نىڭ (دىننىڭ) ئالامەتلىرىدىن (يەنى ھەجنىڭ ئەھكاملىرىدىن) قىلدۇق، ئۇلاردا سىلەرگە نۇرغۇن پايدا بار، ئۇلار قاتار قىلىپ تۇرغۇزۇپ (يەنى ئۇلارنىڭ ئالدى سول پۇتىنى باغلاپ، ئۈچ پۇت بىلەن تۇرغۇزۇپ بوغۇزلىغىنىڭلاردا) اﷲ نىڭ ئىسمىنى تىلغا ئېلىڭلار (يەنى بىسمىللا دەڭلار)، ئۇلار بوغۇزلىنىپ جېنى چىققاندا، ئۇلارنى يەڭلار، قانائەتچان موھتاجلارغا ۋە سائىللارغا بېرىڭلار، سىلەرنى شۈكۈر قىلسۇن دەپ ئۇ تۆگىلەرنى سىلەرگە بويسۇندۇرۇپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,37,' اﷲ قا ئۇلارنىڭ گۆشلىرى ۋە قانلىرى يېتىپ بارمايدۇ، اﷲ قا يېتىدىغىنى پەقەت سىلەرنىڭ تەقۋادارلىقىڭلاردۇر، اﷲ نىڭ سىلەرنىڭ ئۆزىنىڭ ئەھكاملىرىغا يېتەكلىگەنلىكىنى ئۇلۇغلىشىڭلار ئۈچۈن، اﷲ ئۇلارنى سىلەرگە ئاشۇنداق بويسۇندۇرۇپ بەردى ياخشى ئىش قىلغۇچىلارغا (ئاخىرەتتە بەختكە ئېرىشىدىغانلىقى بىلەن) خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,38,' اﷲ مۆمىنلەرنى چوقۇم قوغدايدۇ، اﷲ ھەر قانداق خىيانەتچى، ناشۈكۈر بەندىنى دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,39,' ھۇجۇم قىلىنغۇچىلارغا، زۇلۇمغا ئۇچرىغانلىقلىرى ئۈچۈن، (قارشىلىق كۆرسىتىشكە) رۇخسەت قىلىنىدى، اﷲ ئۇلارغا ياردەم بېرىشكە ئەلۋەتتە قادىر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,40,' (ئۇلار) پەقەت پەرۋەردىگارىمىز اﷲ دېگەنلىكلىرى ئۈچۈنلا ئۆز يۇرتلىرىدىن ناھەق ھەيدەپ چىقىرىلدى، ئەگەر اﷲ ئىنسانلارنى بىر ـ بىرىگە قارشىلىق كۆرسەتكۈزمىگەن بولسا، راھىبلارنىڭ ئىبادەتخانىلىرى، چېركاۋلار، يەھۇدىيلارنىڭ ئىبادەتخانىلىرى ۋە اﷲ نىڭ نامى كۆپ يادلىنىدىغان مەسجىدلەر ئەلۋەتتە ۋەيران قىلىناتتى، كىمكى اﷲ نىڭ دىنىغا ياردەم بېرىدىكەن، ئەلۋەتتە اﷲ ئۇنىڭغا ياردەم بېرىدۇ، اﷲ ئەلۋەتتە كۈچلۈكتۇر، غالىبتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,41,' اﷲ نىڭ ياردىمىگە ئېرىشىشكە ھەقلىق بولغانلار) شۇنداق كىشىلەردۇركى، ئەگەر ئۇلارنى يەر يۈزىدە ئۈستۈنلۈككە ئىگە قىلساق، نامازنى ئادا قىلىدۇ، زاكات بېرىدۇ، ياخشى ئىشلارغا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، ئىشلارنىڭ ئاقىۋىتى اﷲ قا مەنسۇپتۇر (يەنى ھەممە ئىشلار ئاخىرەتتە اﷲ قا قايتىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,42,' ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى، ئىبراھىمنىڭ قەۋمى، لۇتنى (قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,43,' نۇرغۇن شەھەر ئاھالىلىرى زالىم بولغانلىقلىرى ئۈچۈن (شەھەرنىڭ ئۆيلىرىنىڭ) ئۆگزىلىرى ئۆرۈلۈپ، ئۈستىگە تاملىرى يىقىلغان نۇرغۇن تاشلاندۇق قۇدۇقلار ۋە ئېگىز ئىمارەتلەر (ئاھالىسىز) قالغان');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,44,' ئۇلار زېمىندا سەير قىلمىدىمۇ؟ شۇنىڭ بىلەن ئۇلار (يەنى سەير قىلىپ) چۈشىنىدىغان دىللارغا، ياكى ئاڭلايدىغان قۇلاقلارغا ئىگە بولمىدىمۇ؟ ھەقىقەتەن كۆزلەرلا كور بولمايدۇ، لېكىن كۆكرەكلەردىكى قەلبلەر كور بولىدۇ (يەنى ھەقىقىي كورلۇق كۆزنىڭ كورلۇقى ئەمەس، دىلنىڭ كورلۇقىدۇر، كور ئادەم ئىبرەت ئالمايدۇ ۋە چۈشەنمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,45,' (ئى مۇھەممەد!) ئۇلار (يەنى مۇشرىكلار) (مەسخىرە قىلىش يۈزىسىدىن سەندىن ئازابنىڭ تېز نازىل بولۇشنى تەلەپ قىلىدۇ (ئازاب چوقۇم نازىل بولىدۇ، لېكىن ئۇنىڭ نازىل بولۇشنىڭ مۇئەييەن ۋاقتى ـ سائىتى بولىدۇ)، اﷲ ۋەدىسىگە ھەرگىز خىلاپلىق قىلمايدۇ اﷲ بەندىلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)، ھەقىقەتەن پەرۋەردىگارىنىڭ دەرگاھىدىكى بىر كۈن سىلەر سانايدىغان مىڭ يىلچىلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,46,' نۇرغۇن شەھەر ئاھالىلىرى زالىم تۇرۇقلۇق، ئۇلارغا مەن مۆھلەت بەردىم (ئۇلار بۇ مۆھلەتكە مەغرۇر بولۇپ كەتتى)، ئاندىن (يەنى ئۇلارغا ئۇزاق مۇددەت مۆھلەت بەرگىنىمدىن كېيىن) ئۇلارنى جازالىدىم، ئاخىر قايتىدىغان جاي مېنىڭ دەرگاھىمدۇر (يەنى ئاخىر ھەممىسى ماڭا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,47,' (ئى مۇھەممەد! اﷲ نىڭ ئازابىنىڭ تېز نازىل بولۇشىنى تەلەپ قىلغۇچىلارغا) ئېيتقىنكى، «ئى ئىنسانلار! سىلەرگە مەن ھەقىقەتەن ئاشكارا ئاگاھلاندۇرغۇچىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,48,' ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار مەغپىرەتكە ۋە ئېسىل رىزىققا (يەنى جەننەتكە) ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,49,' بىزنى ئاجىز بىلىپ ئايەتلىرىمىزنى يوققا چىقىرىش ئۈچۈن تىرىشقۇچىلار ـ ئەنە شۇلار ئەھلى دوزاختۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,50,' بىز سەندىن ئەۋەتكەن قايسىبىر رەسۇل، قايسىبىر پەيغەمبەر بولمىسۇن، ئۇ (اﷲ نازىل قىلغان ئايەتلەرنى) ئوقۇغان چاغدا، شەيتان ھامان ئۇنىڭ قىرائىتىگە (شۈبھە) سالىدۇ اﷲ شەيتاننىڭ سالغان شۈبھىسىنى بەربات قىلغاندىن كېيىن ئۆزىنىڭ ئايەتلىرىنى مۇستەھكەم قىلىدۇ اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,51,' (اﷲ تائالانىڭ مۇنداق قىلىشى) دىللىرىدا (مۇناپىقلىق) ئىللىتى بارلارنى، دىللىرى قەساۋەتلىشىپ كەتكەنلەرنى (يەنى ئەبۇجەھل، نەزر، ئۆتبەگە ئوخشاش اﷲ نىڭ زىكرىگە دىلى ئېرىمەس كۇففارلارنى) شەيتاننىڭ سالغان شۈبھىسى بىلەن سىناش ئۈچۈندۇر، زالىملار (يەنى يۇقىرىدىكى مۇناپىقلار، مۇشرىكلار) ھەقىقەتەن (اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە) قاتتىق ئاداۋەتتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,52,' (اﷲ تائالانىڭ مۇنداق قىلىشى) ئىلىم ئەھلىلىرىنىڭ قۇرئاننىڭ پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەت ئىكەنلىكىنى بىلىشلىرى، شۇنىڭ ئۈچۈندۇر اﷲ مۆمىنلەرنى ھەقىقەتەن توغرا يولغا باشلىغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,53,' كاپىرلار تاكى ئۇلارغا قىيامەت ئۇشتۇمتۇت كەلگۈچە ياكى ئۇلارغا قىيامەت كۈنىنىڭ ئازابى يەتكۈچە قۇرئاندىن ھامان شەكلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,54,' بۇ كۈندە پادىشاھلىق (يالغۇز) اﷲ قا خاستۇر، اﷲ بەندىلەر ئارىسىدا (ئادىل) ھۆكۈم چىقىرىدۇ، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار (تۈرلۈك) نازۇنېمەتلەر بار جەننەتلەردە بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,55,' كاپىرلار ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار (دوزاختا) خار قىلغۇچى ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,56,' اﷲ نىڭ يولىدا ھىجرەت قىلغانلارغا، ئاندىن (جىھاد قىلىپ) ئۆلتۈرۈلگەنلەرگە ياكى (كېسەل بىلەن) ئۆلگەنلەرگە اﷲ چوقۇم ياخشى رىزىق (يەنى جەننەتنىڭ مەڭگۈلۈك نېمەتلىرىنى) ئاتا قىلىدۇ رىزىق بەرگۈچىلەرنىڭ ئەڭ ياخشىسى ھەقىقەتەن يالغۇز اﷲ دۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,57,' اﷲ ئۇلارنى ئەلۋەتتە ئۇلار مەمنۇن بولىدىغان جايغا (يەنى جەننەتكە) كىرگۈزىدۇ، اﷲ ھەقىقەتەن (ئۇلارنى مەمنۇن قىلىدىغان نەرسىلەرنى) بىلگۈچىدۇر، ھەلىمدۇر (يەنى ئۇلارنىڭ دۈشمەنلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,58,' ئىش مانا شۇنداق، كىمكى قانچىلىك زۇلۇمغا ئۇچراپ شۇنچىلىك ئىنتىقامىنى ئالغان بولسا، ئاندىن يەنە زۇلۇمغا ئۇچرىسا، اﷲ ئۇنىڭغا چوقۇم ياردەم بېرىدۇ، اﷲ ھەقىقەتەن ناھايىتى ئەپۇ قىلغۇچىدۇر، ناھايىتى مەغپىرەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,59,' (شۇ نەرسە اﷲ نىڭ قۇدرىتىنىڭ ئالامەتلىرىدىندۇركى) اﷲ كېچىنى كۈندۈزگە، كۈندۈزنى كېچىگە كىرگۈزىدۇ (شۇنىڭ بىلەن كېچە ـ كۈندۈزنىڭ ئۇزۇن ـ قىسقا بولۇشى پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىنى) كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,60,' بۇ (بۇ) (قۇدرەت) شۇنىڭ بۇنىڭ ئۈچۈندۇركى، اﷲ ھەقتۇر، (مۇشرىكلارنىڭ) اﷲ نى قويۇپ ئىبادەت قىلىدىغان نەرسىلىرى (يەنى بۇتلىرى) باتىلدۇر، اﷲ (ھەممە نەرسىدىن) ئۈستۈندۇر، بۈيۈكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,61,' اﷲ نىڭ بۇلۇتتىن يامغۇر ياغدۇرۇپ بېرىدىغانلىقىنى، شۇنىڭ بىلەن زېمىننى يېشىلزارلىققا ئايلاندۇرىدىغانلىقىنى كۆرمىدىڭمۇ؟ اﷲ ھەقىقەتەن ناھايىتى مېھرىباندۇر، ھەممىدىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,62,' ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ مەخلۇقاتىدۇر، اﷲ نىڭ تەسەررۇپى ئاستىدىدۇر)، اﷲ ھەقىقەتەن ھەممىدىن بىھاجەتتۇر، ھەمدۇساناغا لايىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,63,' اﷲ نىڭ يەر يۈزىدىكى نەرسىلەرنى، ئۆزىنىڭ ئەمرى بويىچە دېڭىزدا يۈرۈۋاتقان كېمىلەرنى سىلەرگە تويۇندۇرۇپ بەرگەنلىكىنى كۆرمىدىڭمۇ؟ (ئاسماننىڭ زېمىنغا چۈشۈپ كەتمەسلىكى ئۈچۈن، اﷲ ئاسماننى توختىتىپ تۇرىدۇ، پەقەت اﷲ نىڭ ئىزنى بىلەن (ئاسماننىڭ قىيامەت بولغان چاغدا) چۈشۈپ كېتىشى بۇنىڭدىن مۇستەسنا اﷲ ئىنسانلارغا ھەقىقەتەن ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,64,' اﷲ سىلەرگە ھاياتلىق بېرىدۇ، ئاندىن سىلەرنىڭ جېنىڭلارنى ئالىدۇ، ئاندىن سىلەرنى تىرىلدۈرىدۇ، ئىنسان ھەقىقەتەن (اﷲ نىڭ نېمەتلىرىگە) ناشۈكۈرلۈك قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,65,' ھەر ئۈممەت ئۈچۈن بىر شەرىئەت بېكىتتۇق، ئۇلار شۇ شەرىئەتكە ئەمەل قىلىدۇ، (مۇشرىكلار) شەرىئەت ئىشىدا سەن بىلەن جېدەللەشمىسۇن، كىشىلەرنى پەرۋەردىگارىڭنىڭ (ئىبادىتىگە) دەۋەت قىلغىن، سەن ھەقىقەتەن شەكسىز توغرا يولدىدۇرسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,66,' ئەگەر ئۇلار سەن بىلەن جېدەللەشسە، ئېيتقىنكى، ئېيتقىنكى «اﷲ سىلەرنىڭ قىلمىشىڭلارنى ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,67,' اﷲ قىيامەت كۈنى سىلەر ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە ھۆكۈم چىقىرىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,68,' بىلمەمسەنكى، اﷲ ئاسمان، زېمىندىكى شەيئىلەرنى بىلىپ تۇرىدۇ، بۇ ھەقىقەتەن لەۋھۇلمەھپۇزدا (يېزىلغان)، بۇ (يەنى بىلىش) ھەقىقەتەن اﷲ قا ئاسان');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,69,' ئۇلار (يەنى قۇرەيش كۇففارلىرى) اﷲ نى قويۇپ، (ئىبادەت قىلىشنىڭ دۇرۇسلۇقىغا) اﷲ ھېچقانداق دەلىل چۈشۈرمىگەن نەرسىلەرگە ۋە ئۆزلىرى بىلمەيدىغان نەرسىلەرگە (يەنى بوتلارغا) چوقۇنىدۇ، زالىملارغا ھېچقانداق مەدەتكار يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,70,' كاپىرلارغا بىزنىڭ روشەن ئايەتلىرىمىز ئوقۇپ بېرىلسە، ئۇلارنىڭ چېھرىلىرىدە (قوشۇمىسى تۈرۈلگەنلىكتىن) ئىنكار قىلغانلىقىنى كۆرۈسەن، ئۇلارنىڭ بىزنىڭ ئايەتلىرىمىزنى ئوقۇپ بەرگۈچىلەرگە ھۇجۇم قىلغىلى تاس قىلغانلىقىنى كۆرۈسەن، ئېيتقىنكى، «سىلەرگە مەن بۇنىڭدىنمۇ يامانراقىنى ئېيتىپ بېرەيمۇ؟ ئۇ دوزاختۇر اﷲ ئۇنىڭ كاپىرلارغا ۋەدە قىلدى، ئۇلار قايتىپ بارىدىغان جاي نېمىدېگەن يامان!»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,71,' ئى ئىنسانلار! (يەنى مۇشرىكلار جامائەسى!) بىر مىسال كەلتۈرۈلىدۇ، ئۇنىڭغا قۇلاق سېلىڭلار، شۈبھىسىزكى، سىلەر اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلارنىڭ ھەممىسى يىغىلغان تەقدىردىمۇ بىر چىۋىننى يارىتالمايدۇ (ئەقلى بار ئادەم قانداقمۇ مۇشۇنداق بۇتلارنى مەبۇد قىلىۋېلىپ ئۇلارغا چوقۇنىدۇ!)، چىۋىن ئۇلارنىڭ بەدىنىدىن بىر نەرسىنى (يەنى بۇتقا سۈركەپ قويۇلغان خۇش پۇراق بىر نەرسىنى) ئېلىپ قاچسا، ئۇنى چىۋىندىن تارتىپ ئالالمايدۇ، بۇتمۇ ۋە (ئۇنىڭغا) چوقۇنغۇچىمۇ ئاجىزدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,72,' ئۇلار (ئەرزىمەس بۇتلارنى اﷲ قا شېرىك قىلىۋېلىپ) اﷲ نى تېگىشلىك رەۋىشتە ئۇلۇغلاشمىدى، شۈبھىسىزكى، اﷲ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,73,' اﷲ پەرىشتىلەردىنمۇ (پەيغەمبەرلىرىگە ۋەھيى يەتكۈزۈش ئۈچۈن)، ئەلچىلەرنى تاللايدۇ، اﷲ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (قىلمىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,74,' اﷲ ئۇلارنىڭ ئالدىدىكى نەرسىلەرنى (يەنى ئۇلارنىڭ ھازىرقى ئەھۋالىنى ۋە كەلگۈسىدىكى ئىشلىرىنى) بىلىدۇ (بەندىلەرنىڭ) ئىشلىرىنىڭ ھەممىسى اﷲ قا قايتۇرۇلىدۇ (اﷲ ئۇلارغا يا مۇكاپات، يا جازا بېرىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,75,' ئى مۆمىنلەر! بەختكە ئېرىشىشىڭلار ئۈچۈن رۇكۇ قىلىڭلار، سەجدە قىلىڭلار (يەنى تەزەررۇ بىلەن ناماز ئوقۇڭلار)، (يالغۇز) پەرۋەردىگارىڭلارغا ئىبادەت قىلىڭلار، ياخشى ئىشلارنى (يەنى خىش ـ ئەقرىبالارغا سىلەر ـ رەھىم قىلىش، يېتىم ـ يېسىرلەرنىڭ بېشىنى سىلاش، كېچىسى تەھەججۇد نامىزى ئۆتەش قاتارلىق ئىشلارنى) قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,22,76,' اﷲ نىڭ يولىدا (پۇل ـ مېلىڭلار بىلەن، جېنىڭلار بىلەن) تاقىتىڭلارنىڭ يېتىشىچە جىھاد قىلىڭلار، اﷲ (دىنغا پەيغەمبەرنى سىلەرگە خاس قىلدى) سىلەرگە دىندا ھېچقانداق مۈشكۈللۈكنى قىلمىدى (سىلەرنى سىلەر تاقەت قىلالايدىغان ئىشلارنى قىلىشقا تەكلىپ قىلمىدى) (بۇ) ئاتاڭلار ئىبراھىمنىڭ دىنىدۇر (بۇ توغرا دىن بولغانلىقى ئۈچۈن، ئۇنى مەھكەم ئۇچلاڭلار)، اﷲ سىلەرنى ئىلگىرى (يەنى قۇرئاندىن ئىلگىرىكى كىتابلاردا) مۇسۇلمان دەپ ئاتىدۇ قۇرئاندىمۇ شۇنداق ئاتىدۇ (اﷲ) پەيغەمبەرنى سىلەرگە گۇۋاھ بولۇشقا ۋە سىلەرنى كىشىلەرگە گۇۋاھ بولۇشقا (تاللىدى)، ناماز ئوقۇڭلار، زاكات بېرىڭلار، اﷲ قا يېپىشىڭلار، اﷲ سىلەرنىڭ ئىگەڭلاردۇر، اﷲ نېمىدېگەن ياخشى ئىگە! نېمىدېگەن ياخشى مەدەتكار!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,1,'مۆمىنلەر ھەقىقەتەن بەختكە ئېرىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,2,' (شۇنداق مۆمىنلەركى) ئۇلار نامازلىرىدا (اﷲ نىڭ ئۇلۇغلۇقىدىن سۈر بېسىپ كەتكەنلىكتىن) ئەيمىنىپ تۇرغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,3,' ئۇلار بىھۇدە سۆز، بىھۇدە ئىشتىن يىراق بولغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,4,' ئۇلار زاكات بەرگۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,5,' ئۇلار ئەۋرەتلىرىنى (ھارامدىن) ساقلىغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,6,' (يەنى ئەۋرەتلىرىنى) پەقەت خوتۇنلىرىدىن، چۆرىلىرىدىن باشقىلاردىن ساقلىغۇچىلاردۇر (بۇلار بىلەن يېقىنچىلىق قىلغۇچىلار مالامەت قىلىنمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,7,' بۇنىڭ سىرتىدىن (جىنسىي تەلەپنى قاندۇرۇشنى) تەلەپ قىلغۇچىلار ھەددىدىن ئاشقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,8,' ئۇلار (يەنى مۆمىنلەر) ئۆزلىرىگە تاپشۇرۇلغان ئامانەتلەرگە ۋە بەرگەن ئەھدىگە رىئايە قىلغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,9,' ئۇلار نامازلىرىنى (ۋاقتىدا تەئدىل ئەركان بىلەن) ئادا قىلغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,10,' ئەنە شۇلار (يەنى يۇقىرى سۈپەتلەرگە ئىگە مۆمىنلەر نازۇنېمەتلىك جەننەتنىڭ) ۋارىسلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,11,' ئۇلار (يۇقىرى دەرىجىلىك جەننەت) فىردەۋسكە ۋارىسلىق قىلىدۇ، فىردەۋستە مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,12,' بىز ئىنساننى ھەقىقەتەن لاينىڭ جەۋھىرىدىن ياراتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,13,' ئاندىن ئۇنى بىر پۇختا قارارگاھ (يەنى بەچچىدان) دا (جايلاشقان) ئابىمەنىي قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,14,' ئاندىن ئابىمەنىينى لەختە قانغا ئايلاندۇردۇق، ئاندىن لەختە قاننى پارچە گۆشكە ئايلاندۇردۇق، ئاندىن پارچە گۆشنى سۆڭەككە ئايلاندۇردۇق، ئاندىن سۆڭەككە گۆش قوندۇردۇق، ئاندىن ئۇنى (جان كىرگۈزۈپ) باشقا مەخلۇققا ئايلاندۇردۇق، ئاڭ ماھىر ياراتقۇچى اﷲ (ھېكمەت ۋە قۇدرەت جەھەتتىن) ئۇلۇغدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,15,' ئاندىن سىلەر چوقۇم ئۆلۈسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,16,' ئاندىن سىلەر ھەقىقەتەن قىيامەت كۈنى (قەبرەڭلاردىن) تۇرغۇزۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,17,' بىز ھەقىقەتەن سىلەرنىڭ ئۈستۈڭلاردا يەتتە قەۋەت ئاسماننى ياراتتۇق، بىز مەخلۇقاتتىن غاپىل ئەمەسمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,18,' بىز بۇلۇتتىن ھاجەتكە قاراپ يامغۇر ياغدۇردۇق، ئۇنى زېمىندا توختاتتۇق، ئۇنى قۇرۇتۇۋېتىشقا ئەلۋەتتە قادىرمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,19,' شۇ يامغۇر بىلەن سىلەرگە خورما باغچىلىرى ۋە ئۈزۈم باغچىلىرىنى يېتىشتۈرۈپ بەردۇق، سىلەر ئۈچۈن ئۇ باغلاردا نۇرغۇن مېۋىلەر بار، ئۇلاردىن يەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,20,' شۇ يامغۇر بىلەن تۇرسىنادىن چىقىدىغان (زەيتۇن) دەرىخىنى (ئۆستۈرۈپ بەردۇق)، ئۇنىڭدىن ماي چىقىدۇ، يېگۈچىلەر (نانلىرىنى شۇ ماي بىلەن) مايلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,21,' چارۋىلاردا ھەقىقەتەن سىلەرگە ئىبرەت بار، ئۇلارنىڭ قورسىقىدىكى نەرسىلەر (يەنى سۈتلەر) بىلەن سىلەرنى سۇغىرىمىز، ئۇلاردا سىلەر ئۈچۈن نۇرغۇن مەنپەئەتلەر بار، ئۇلارنىڭ گۆشىنى يەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,22,' ئۇلار (يەنى تۆگە ۋە ئۆكۈزلەر) بىلەن (قۇرۇقلۇقتا)، كېمە بىلەن (دېڭىزدا) يۈك توشۇيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,23,' بىز ھەقىقەتەن نۇھنى قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق، نۇھ ئېيتتى: «ئى قەۋمىم! (يالغۇز اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوق، اﷲ نىڭ ئازابىدىن) قورقمامسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,24,'ئۇنىڭ قەۋمىدىكى ئىمانسىز كاتتىلار ئېيتتى: «بۇ پەقەت سىلەرگە ئوخشاش بىر ئىنساندۇر، ئۇ سىلەردىن ئۈستۈن بولۇۋالماقچى، ئەگەر اﷲ (پەيغەمبەر ئەۋەتىشنى) خالىسا ئىدى، ئەلۋەتتە، پەرىشتىلەرنى ئەۋەتتى، بۇنداق سۆزنى بۇرۇنقى ئاتا ـ بوۋىلىرىمىزدىن ئاڭلىغان ئەمەسمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,25,' ئۇ پەقەت بىر مەجنۇندۇر، سىلەر ئۇنى بىر مەزگىل كۈتۈڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,26,' نۇھ (ئۇلارنىڭ ئىمان ئېيتىشىدىن ئۈمىدىنى ئۈزگەندىن كېيىن): «پەرۋەردىگارىم! ئۇلار مېنى يالغانغا چىقارغانلىقلىرى ئۈچۈن ماڭا ياردەم بەرگىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,27,' بىز نۇھقا: «بىزنىڭ كۆز ئالدىمىزدا ۋەھيىمىز بويىچە كېمە ياسىغىن، بىزنىڭ بۇيرۇقىمىز يېتىپ كەلگەن ۋە تونۇردىن سۇ ئېتىلىپ چىققان چاغدا، ھايۋانلاردىن (ئەركەك ـ چىشى بولۇپ) بىر جۈپتىن ئېلىۋالغىن، ئائىلەڭدىكىلەرنىمۇ ئېلىۋالغىن، ئۇلارنىڭ ئىچىدىكى ھالاك بولۇشقا ھۆكۈم قىلىنغانلار بۇنىڭدىن مۇستەسنا (ئۆزلىرىگە) زۇلۇم قىلغانلار توغرىسىدا ماڭا سۆز ئاچمىغىن (يەنى ئۇلارغا شاپائەت قىلمىغىن)، ئۇلار چوقۇم (توپان بالاسىدا) غەرق قىلىنىدۇ» دەپ ۋەھيى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,28,' سەن بىلەن بىللە بولغان مۆمىنلەر بىلەن كېمىگە چىققان چېغىڭدا: «جىمى ھەمدۇسانا بىزنى زالىم قەۋمدىن قۇتقۇزغان اﷲ قا خاستۇر» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,29,' ھەمدە: «ئى پەرۋەردىگارىم! مېنى مۇبارەك مەنزىلگە چۈشۈرگىن، سەن (دوستلىرىڭنى) ئەڭ ياخشى ئورۇنلاشتۇرغۇچىسەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,30,' ھەقىقەتەن بۇنىڭدا (يەنى نۇھنىڭ ئۈممىتىنىڭ ئىشلىرىدا ئەقىل ئىگىلىرى ئۈچۈن) نۇرغۇن ئىبرەتلەر بار، بىز بەندىلەرنى (پەيغەمبەرلەرنى ئەۋەتىش بىلەن) ئەلۋەتتە سىنايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,31,' ئۇلارنى ھالاك قىلغاندىن كېيىن باشقا بىر قەۋم پەيدا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,32,' بىز ئۇلارغا ئۆزلىرىنىڭ ئىچىدىن بىر پەيغەمبەرنى (يەنى ھۇدنى) ئەۋەتتۇق (ئۇ ئېيتتى) اﷲ غىلا ئىبادەت قىلىڭلار، سىلەرگە ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر ئاللانىڭ (ئازابىدىن) قورقمامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,33,' ئۇنىڭ قەۋمىنىڭ كاپىر بولغان، ئاخىرەت مۇكاپاتىنى يالغان چىقارغان ۋە بۇ دۇنيادا بىز تەرەپتىن باياشات تۇرمۇشقا ئىگە قىلىنغان كاتتىلىرى ئېيتتى: «بۇ پەقەت سىلەرگە ئوخشاش بىر ئىنساندۇر، ئۇ سىلەر يېگەننى يەيدۇ، سىلەر ئىچكەننى ئىچىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,34,' ئەگەر سىلەر ئۆزۈڭلارغا ئوخشاش بىر ئىنسانغا ئىتائەت قىلساڭلار، شۈبھىسىزكى، ئۇ چاغدا سىلەر چوقۇم زىيان تارتقۇچىلارسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,35,' ئۇ سىلەرنى سىلەر ئۆلۈپ توپىغا ۋە قۇرۇق ئۇستىخانغا ئايلانغاندىن كېيىن تىرىلىسىلەر دەپ ئاگاھلاندۇرامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,36,' سىلەرگە ئاگاھلاندۇرۇلغان ئىش تولىمۇ يىراقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,37,' بىزنىڭ مۇشۇ دۇنيادىكى ھاياتىمىزدىن باشقا ھايات يوقتۇر، بىز ئۆلىمىز ۋە تىرىلىمىز (يەنى بىر تەرەپتىن ئۆلۈپ تۇرىمىز ۋە بىر تەرەپتىن تۇغۇلۇپ تۇرىمىز)، بىز قايتا تىرىلمەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,38,' ئۇ پەقەت اﷲ نامىدىن يالغاننى توقۇغان ئادەملەردۇر، بىز ئۇنىڭغا ئىشەنمەيمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,39,' ھۇد ئېيتتى: «پەرۋەردىگارىم! ئۇلار مېنى يالغانغا چىقارغانلىقلىرى ئۈچۈن ماڭا ياردەم بەرگىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,40,' اﷲ ئېيتتى: «ئۇلار ئۇزۇنغا قالماي (كاپىر بولغانلىقلىرىغا) نادامەت چېكىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,41,' ئۇلار ھەقلىق رەۋىشتە قاتتىق ئاۋاز بىلەن ھالاك بولدى ئۇلارنى بىز لۆجە قىلىۋەتتۇق، زالىم قەۋم اﷲ نىڭ رەھمىتىدىن) يىراق بولسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,42,' ئۇلارنى ھالاك قىلغاندىن كېيىن، باشقا بىر قەۋملەرنى پەيدا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,43,' ھەر قانداق ئۈممەت ھالاك بولۇش ۋاقتى كەلمەي تۇرۇپ ھالاك بولمايدۇ، ھالاك بولۇش ۋاقتىدىن ئۆتۈپمۇ كەتمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,44,' ئاندىن بىزنىڭ پەيغەمبەرلىرىمىزنى ئارقىمۇ ئارقا ئەۋەتتۇق، ھەر ئۈممەتكە ئۇنىڭ پەيغەمبىرى كەلگەن چاغدا، ئۇلار ئۇنى ئىنكار قىلغانلىقتىن، ئۇلارنى ئارقىمۇ ئارقا ھالاك قىلدۇق، ئۇلارنى (كىشىلەرگە) ھېكايە قىلىپ قالدۇردۇق، ئىمان ئېيتمايدىغان قەۋم اﷲ نىڭ رەھمىتىدىن) يىراق بولسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,45,' ئاندىن مۇسا ۋە ئۇنىڭ قېرىندىشى ھارۇننى بىزنىڭ ئايەتلىرىمىز بىلەن ۋە روشەن پاكىت بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار ھاكاۋۇرلۇق قىلىپ (ئىماندىن) باش تارتتى، ئۇلارمۇ تەكەببۇر قەۋم ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,46,' ئۇلار: «بىزگە ئوخشاش ئىككى ئاددى ئىنسانغا ئىمان ئېيتامدۇق، ھالبۇكى، ئۇلارنىڭ قەۋمى بىزنىڭ (قۇلغا ئوخشاش) خىزمەتچىلىرىمىزدۇر» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,47,' ئۇلار بۇ ئىككىسىنى (يەنى مۇسا بىلەن ھارۇننى) ئىنكار قىلىشتى شۇنىڭ بىلەن ئۇلار ھالاك بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,48,' ئۇلار (يەنى بەنى ئىسرائىل) نى ھىدايەت تاپسۇن دەپ، بىز ھەقىقەتەن مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,49,' بىز مەريەمنىڭ ئوغلىنى (يەنى ئىسانى) ۋە ئۇنىڭ ئانىسىنى (يەنى مەريەمنى) (كامال قۇدرىتىمىزنىڭ) دەلىلى قىلدۇق، ئۇلارنى تۈپتۈز، ئېقىن سۇلۇق بىر ئېگىز جايغا ئورۇنلاشتۇردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,50,' ئى پەيغەمبەرلەر! ھالال نەرسىلەرنى يەڭلار، ياخشى ئەمەللەرنى قىلىڭلار، مەن ھەقىقەتەن سىلەرنىڭ قىلغان ئەمەللىرىڭلارنى ئوبدان بىلىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,51,' (ئى پەيغەمبەرلەر جامائەسى!) سىلەرنىڭ دىنىڭلار ھەقىقەتەن بىر دىندۇر، مەن سىلەرنىڭ پەرۋەردىگارىڭلاردۇرمەن، (مېنىڭ ئازابىمدىن) قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,52,' ئۇلار (يەنى ئۈممەتلەر) دىنىي جەھەتتە نۇرغۇن پىرقىلەرگە بۆلۈندى، ھەر پىرقە ئۆز دىنى بىلەن خۇشالدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,53,' ئۇلارنى ئۆلگەنلىرىگە قەدەر گۇمراھلىقتا تەرك ئەتكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,54,' (ئۇلار) بىزنىڭ ئۇلارغا مال ـ مۈلۈك ۋە ئەۋلادلارنى بەرگەنلىكىمىزنى ئۆزلىرىگە تېز ياخشىلىق قىلغانلىق دەپ ئويلامدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلار ئۇقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,55,' ھەقىقەتەن پەرۋەردىگارىنىڭ ھەيۋىسىدىن قورققۇچى كىشىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,56,'، پەرۋەردىگارىنىڭ ئايەتلىرىگە ئىشەنگۈچى كىشىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,57,'، پەرۋەردىگارىغا شېرىك كەلتۈرمەيدىغان كىشىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,58,'، سەدىقە بېرىدىغان ئەمما پەرۋەردىگارىنىڭ دەرگاھىغا (ھېساب بېرىش ئۈچۈن) قايتىپ بارىدىغانلىقىدىن دىللىرى قورقۇپ تۇرىدىغان كىشىلەرـ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,59,'ئەنە شۇلار ياخشى ئىشلارنى (باشقىلارنىڭ) ئالدىدا قىلىشقا تىرىشقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,60,' بىز ئىنساننى پەقەت تاقىتى يېتىدىغان (ئىش) قىلا تەكلىپ قىلىمىز، بىزنىڭ دەرگاھىمىزدا ھەقنى سۆزلەيدىغان كىتاب (يەنى بەندىلەرنىڭ ياخشى ـ يامان ئىشلىرى خاتىرىلەنگەن نامە ـ ئەمال بار، ئۇلارغا (ساۋابىنى كېمەيتىۋېتىش ياكى ئازابنى ئاشۇرۇۋېتىش بىلەن) قىلچە زۇلۇم قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,61,' ئۇلار (يەنى گۇناھكار كۇففارلار) نىڭ دىللىرى بۇنىڭدىن (يەنى قۇرئاندىن) غەپلەتتىدۇر، ئۇنىڭدىن يەنى كۇفرىدىن) باشقا ئۇلار كېلەچەكتە نۇرغۇن (يامان) ئىشلارنى قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,62,' ئۇلارنىڭ بايلىرىنى ئازاب بىلەن جازالىغان ۋاقتىمىزدا ناگاھ ئۇلار پەرياد قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,63,' (ئۇلارغا ئېيتىلىدۇ) «بۈگۈن پەرياد قىلماڭلار، ھەقىقەتەن سىلەر بىزنىڭ ياردىمىمىزگە ئېرىشەلمەيسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,64,' مېنىڭ ئايەتلىرىم سىلەرگە ئوقۇپ بېرىلەتتى، (ئۇنى ئاڭلاشتىن يۈز ئۆرۈپ) ئارقاڭلارغا چېكىنىۋالاتتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,65,' ئۇلار ھەرەم ئەھلى بولغانلىقى بىلەن پەخىرلىنىپ ئىماندىن يۈز ئۆرۈيدۇ، كەچلىك پاراڭلىرىدا يامان سۆزلەرنى قىلىدۇ، (يەنى قۇرئانغا تەنە قىلىدۇ، پەيغەمبەر ئەلەيھىسسالامنى سۆكىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,66,' ئۇلار (ھەق) كالام (يەنى ئۇلۇغ قۇرئان) ئۈستىدە چوڭقۇر پىكىر قىلمىدىمۇ؟ ياكى ئۇلارغا (اﷲ تەرىپىدىن) ئاتا ـ بوۋىلىرىغا كەلمىگەن (يېڭى) نەرسە كەلدىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,67,' ياكى ئۇلار ئۆزلىرىگە كەلگەن ئەلچىنى تونۇماي ئۇنى ئىنكار قىلىۋاتامدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,68,' ياكى ئۇلار ئۇنى مەجنۇن دېيىشەمدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلارغا ئۇ ھەقىقەتنى ئېلىپ كەلدى ئۇلارنىڭ تولىسى ھەقىقەتنى يامان كۆرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,69,' ئەگەر ھەقىقەت (يەنى قۇرئان) ئۇلارنىڭ نەپسى خاھىشلىرىغا بويسۇنىدىغان بولسا، ئەلۋەتتە ئاسمانلار، زېمىن ۋە ئۇلاردىكى مەخلۇقاتلار خاراب بولغان بولاتتى، ئۇنداق بولغىنى يوق، ئۇلارغا (ۋەز ـ نەسىھەتنى ئۆز ئىچىگە ئالغان) قۇرئاننى ئاتا قىلدۇق، ئۇلار ئۆزلىرىگە (ۋەز ـ نەسىھەت بولغان) قۇرئاندىن يۈز ئۆرۈيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,70,' ياكى سەن ئۇلاردىن (ئەلچىلىكنى يەتكۈزگەنلىكىڭگە) ھەق تەلەپ قىلامسەن؟ (يەنى ئۇلار شۇنداق گۇمان قىلامدۇ؟) پەرۋەردىگارىڭنىڭ (ساڭا بەرگەن) ئەجرى ياخشىدۇر، اﷲ رىزىق بەرگۈچىلەرنىڭ ياخشىسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,71,' سەن ئەلۋەتتە ئۇلارنى توغرا يولغا دەۋەت قىلىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,72,' ئاخىرەتكە ئىشەنمەيدىغانلار ھەقىقەتەن توغرا يولدىن چەتنىگۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,73,' ئەگەر ئۇلار (يەنى مۇشرىكلار) غا رەھىم قىلساق، ئۇلارغا كەلگەن كۈلپەتنى كۆتۈرۈۋەتسەك، چوقۇم گۇمراھلىقلىرىدا داۋاملىق تېڭىرقاپ يۈرۈشەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,74,' ئۇلارنى بىز ھەقىقەتەن ئازاب (يەنى قەھەتچىلىك) بىلەن جازالىدۇق، ئۇلار پەرۋەردىگارىغا بويسۇنمىدى، ئىلتىجا قىلىپ دۇئامۇ قىلمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,75,' تاكى ئۇلارغا قاتتىق ئازابنىڭ دەرۋازىسىنى ئاچقان چېغىمىزدا ناگاھان ئۇلار ئۈمىدسىزلىنىپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,76,' اﷲ سىلەر ئۈچۈن قۇلاقلارنى، كۆزلەرنى ۋە دىللارنى ياراتتى، سىلەر ناھايىتى ئاز شۈكۈر قىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,77,' اﷲ سىلەرنى زېمىندا (نەسىللەندۈرۈش يولى بىلەن) ياراتتى، (ھېساب بېرىش ئۈچۈن) ئۇنىڭ دەرگاھىغا يىغىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,78,' اﷲ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنىڭ) جېنىنى ئالىدۇ، كېچە ـ كۈندۈزنىڭ ئۆزگىرىپ تۇرۇشى اﷲ نىڭ باشقۇرۇشىدىدۇر، چۈشەنمەمسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,79,' (ئۇلار اﷲ نىڭ قۇدرىتىنى ئويلاپ كۆرمىدى) بەلكى ئۇلار بۇرۇنقىلار نېمىدېگەن بولسا شۇنى دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,80,' ئۇلار ئېيتتى: «بىز ئۆلۈپ توپىغا ۋە (ئۇۋۇلۇپ كەتكەن) سۆڭەككە ئايلانغاندىن كېيىن، چوقۇم قايتا تىرىلەمدۇق؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,81,' ھەقىقەتەن بىز ۋە بىزنىڭ ئاتا ـ بوۋىلىرىمىز ئىلگىرى مۇشۇنداق ئاگاھلاندۇرۇلغان ئىدۇق، بۇ پەقەت قەدىمكىلەردىن قالغان ئەپسانىلاردۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,82,' ئېيىتقىنكى، «زېمىن ۋە ئۇنىڭدىكى مەخلۇقاتلار كىمنىڭ؟ ئەگەر سىلەر بىلسەڭلار (ماڭا بۇنى ئېيتىپ بېرىڭلار)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,83,' ئۇلار: «اﷲ نىڭ» دەيدۇ (بۇنىڭدىن) پەند ـ نەسىھەت ئالمامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,84,'ئېيىتقىنكى، «يەتتە ئاسماننىڭ ۋە ئۇلۇغ ئەرشنىڭ پەرۋەردىگارى كىم؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,85,'ئۇلار «اﷲ» دەيدۇ ئېيىتقىنكى، «ئۇنىڭ (ئازابىدىن) قورقمامسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,86,'ئېيىتقىنكى، «ھەممە شەيئىنىڭ پادىشاھلىقى كىمنىڭ قولىدا؟ (ئىلتىجا قىلغانلارغا) پاناھ بولالايدىغان ۋە ئۇنىڭغا قارشى ھېچ نەرسە پاناھ بولالمايدىغان كىم؟ ئەگەر سىلەر بىلسەڭلار (ماڭا بۇنى ئېيتىپ بېرىڭلار)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,87,' ئۇلار: «اﷲ» دەيدۇ ئېيىتقىنكى، «سىلەر قانداقمۇ قايمۇقتۇرۇلىسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,88,' ئۇنداق ئەمەس، ئۇلارغا بىز ھەق سۆزنى ئېلىپ كەلدۇق، ئۇلار چوقۇم يالغانچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,89,' اﷲ نىڭ بالىسى يوق، ئۇنىڭغا باراۋەر باشقا بىر ئىلاھمۇ يوق، ئەگەر بۇنداق بولسا ئىدى، ئۇ چاغدا ھەر ئىلاھ ئۆزى ياراتقان مەخلۇقنى يالغۇز ئىگىلەيتتى، بەزىسى بەزىسىدىن غالىب كېلەتتى، اﷲ ئۇلارنىڭ اﷲ قا نىسبەت بەرگەن نەرسىلىرىدىن پاكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,90,' اﷲ غەيبنى ۋە ئاشكارىنى بىلگۈچىدۇر، مۇشرىكلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن ئۈستۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,91,' ئېيتقىنكى، «پەرۋەردىگارىم! ئەگەر ماڭا ئۇلارغا (دۇنيادا) ۋەدە قىلغان ئازابنى كۆرسىتىدىغان بولساڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,92,' ئېيتقىنكى: «پەرۋەردىگارىم! مېنى زالىم قەۋملەر ئىچىدە قىلمىغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,93,' بىز ئۇلارغا ۋەدە قىلغان ئازابنى ساڭا كۆرسىتىشكە ئەلۋەتتە قادىرمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,94,' (ئۇلارنىڭ قىلغان) يامانلىقىغا (ئەپۇ قىلىش بىلەن) ياخشىلىق قىلغىن، ئۇلارنىڭ (سېنى) سۈپەتلەشلىرىنى (يەنى ئۇلارنىڭ ساڭا قىلغان مەسخىرىسىنى) بىز ئوبدان بىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,95,' ئېيتقىنكى، «پەرۋەردىگارىم! ساڭا سېغىنىپ شەيتانلارنىڭ ۋەسۋەسىلىرىدىن پاناھ تىلەيمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,96,' پەرۋەردىگارىم! ساڭا سېغىنىپ شەيتانلارنىڭ ماڭا ھەمراھ بولۇشىدىن پاناھ تىلەيمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,97,' ئۇلارنىڭ (يەنى كاپىرلارنىڭ) بىرىگە ئۆلۈم كەلسە ئېيتىدۇركى، «پەرۋەردىگارىم! مېنى (دۇنياغا) قايتۇرغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,98,' مەن قويۇپ كەلگەن مال ـ مۈلكۈم بىلەن ياخشىلىق قىلىشىم مۇمكىن» (ئۇنىڭ دۇنياغا قايتىشى) مۇمكىن ئەمەس، ئۇ چوقۇم شۇ سۆزنى قىلغۇچىدۇر، ئۇلار قەبرىلىرىدىن تۇرغۇزۇلغان كۈنگە قەدەر ئۇلارنىڭ ئالدىدا (ئۇلارنى دۇنياغا قايتىشتىن توسىدىغان) بىر توسما بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,99,' سۇر چېلىنغان كۈندە ئۇلارنىڭ ئارىسىدىكى تۇغقانچىلىق مەۋجۇت بولماي قالىدۇ بىر ـ بىرىنىڭ (ئەھۋالىنىمۇ) سوراشمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,100,' تارازىسى ئېغىر كەلگەنلەر (يەنى ياخشىلىقلىرى يامانلىقلىرىنى بېسىپ كەتكەنلەر) بەختكە ئېرىشكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,101,' تارازىسى يېنىك كەلگەنلەر (يەنى يامانلىقلىرى ياخشىلىقلىرىنى بېسىپ كەتكەنلەر) زىيان تارتقۇچىلاردۇر، جەھەننەمدە مەڭگۈ قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,102,' ئۇلارنىڭ يۈزلىرىنى ئوت كۆيدۈرىدۇ، كالپۇكلىرى قورۇلۇپ، چىشلىرى ئېچىلىپ (بەت چىراي) بولۇپ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,103,' (ئۇلارغا) «سىلەرگە (دۇنيادا) مېنىڭ ئايەتلىرىم ئوقۇپ بېرىلگەن، سىلەر ئۇنى (شۇنچە روشەن تۇرۇقلۇق) ئىنكار قىلغان ئەمەسمىدىڭلار» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,104,' ئۇلار ئېيتىدۇ: «پەرۋەردىگارىمىز! بەتبەختلىكىمىز ئۈستىمىزدىن غالىب كېلىپ گۇمراھ قەۋم بولۇپ قالغان ئىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,105,' پەرۋەردىگارىمىز! بىزنى دوزاختىن چىقارغىن (ئاندىن دۇنياغا قايتۇرغىن)، ئەگەر بىز (شۇنىڭدىن كېيىن) يەنە گۇناھ قىلساق، بىز ھەقىقەتەن زالىم بولۇپ قالىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,106,' اﷲ ئېيتىدۇ: «جەھەننەمدە خار ھالدا قىلىڭلار، (ئۈستۈڭلاردىن ئازابنىڭ كۆتۈرۈلۈپ كېتىشى ھەققىدە) ماڭا سۆز ئاچماڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,107,' شۈبھىسىزكى، بەندىلىرىمدىن بىر تۈركۈم كىشىلەر : «پەرۋەردىگارىمىز! بىز ئىمان ئېيتتۇق، بىزگە مەغپىرەت قىلغىن، بىزگە رەھىم قىلغىن، سەن بولساڭ ئەڭ رەھىم قىلغۇچىسەن» دەيتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,108,' ئۇلارنى مەسخىرە قىلدىڭلار، ھەتتا ئۇلار سىلەرگە مېنىڭ زىكرىمنى ئۇنتۇلدۇردى (يەنى ئۇلارنى مەسخىرە قىلىش بىلەن بولۇپ كېتىپ ماڭا ئىتائەت قىلىشنى ئۇنتۇدۇڭلار)، سىلەر ئۇلاردىن كۈلەتتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,109,' (سىلەردىن يەتكەن ئەرزىيەتلەرگە) سەۋر قىلغانلىقلىرى ئۈچۈن، مەن ئۇلارنى بۈگۈن مۇكاپاتلىدىم، ئۇلار ھەقىقەتەن مەقسەتكە ئېرىشكۇچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,110,' اﷲ (ئۇلارغا): «زېمىندا قانچىلىك تۇردۇڭلار؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,111,' ئۇلار: «بىر كۈن ياكى بىر كۈندىنمۇ ئاز تۇردۇق، (ئاي ـ كۈنلەرنى) ھېسابلىغۇچى (پەرىشتە) دىن سورىغىن» دەيدۇ (ئۇلار دوزاختا چېكىۋاتقان ئازاب تۈپەيلىدىن، زېمىندا قانچىلىك تۇرغانلىقىنى ئۇنتۇيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,112,' اﷲ: «ئەگەر سىلەر بىلسەڭلار (دۇنيادا) پەقەت ئازغىنا تۇردۇڭلار» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,113,' ئى ئىنسانلار! بىزنى سىلەر ئۆزۈڭلارنى بىكار ياراتتى (يەنى سىلەر بىزنى ھايۋاناتلارنى ياراتقىنىمىزدەك ھېچقانداق ساۋابمۇ بەرمەيدىغان، جازامۇ بەرمەيدىغان قىلىپ ياراتتى) ئۆزۈڭلارنى بىزنىڭ دەرگاھىمىزغا قايتۇرۇلمايدۇ دەپ ئويلامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,114,' ھەق ـ پادىشاھ اﷲ ئۈستۈندۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ ئۇلۇغ ئەرشنىڭ پەرۋەردىگارىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,115,' كىمكى ھېچقانداق دەلىلى بولمىغان ھالدا اﷲ قا قوشۇپ يەنە بىر مەبۇدقا ئىبادەت قىلىدىكەن، پەرۋەردىگارىنىڭ دەرگاھىدا ھېساب بېرىدۇ، كاپىرلار ھەقىقەتەن نىجات تاپمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,23,116,' «پەرۋەردىگارىم ! مەغپىرەت قىلغىن، رەھىم قىلغىن، سەن رەھىم قىلغۇچىلارنىڭ ياخشىسىسەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,1,'(ئى مۇھەممەد!) بۇ بىر سۈرىدۇركى، ئۇنى بىز (ساڭا) نازىل قىلدۇق، ئۇنىڭدىكى (ئەھكاملارنى) پەرز قىلىپ بېكىتتۇق، (ئى مۆمىنلەر!) سىلەرنىڭ ۋەز ـ نەسىھەت ئېلىشىڭلار ئۈچۈن، ئۇنىڭدا (سىلەرنى ئەمەل قىلسۇن دەپ شەرىئەت ئەھكاملىرىنى ئۆز ئىچىگە ئالغان) روشەن ئايەتلەرنى نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,2,' زىنا قىلغۇچى ئايال ۋە زىنا قىلغۇچى ئەرنىڭ ھەر بىرىنى يۈز دەررىدىن ئۇرۇڭلار، ئەگەر سىلەر اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتىدىغان بولساڭلار، اﷲ نىڭ دىنىنىڭ (ئەھكامىنى ئىجرا قىلىشتا) ئۇلارغا رەھىم قىلماڭلار، ئۇلارنى جازالىغان چاغدا مۆمىنلەردىن بىر تۈركۈم كىشى ھازىر بولسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,3,' زىنا قىلغۇچى ئەر پەقەت زىنا قىلغۇچى ئايالنى ياكى مۇشرىك ئايالنىلا ئالىدۇ، زىنا قىلغۇچى ئايال پەقەت زىنا قىلغۇچى ئەرگە ياكى مۇشرىككىلا تېگىدۇ، ئۇ (يەنى زىنا) مۆمىنلەرگە ھارام قىلىندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,4,' ئىپپەتلىك ئاياللارنى زىنا بىلەن قارىلىغان، (بۇنىڭ راستلىقىغا ئادىل) تۆت گۇۋاھچىنى كەلتۈرەلمىگەن كىشىلەرنى  دەررە ئۇرۇڭلار (يەنى بىراۋغا زىنا بىلەن تۆھمەت چاپلىغان ھەربىر ئادەمنى قامچا ۋە شۇنىڭغا ئوخشاش نەرسىلەر بىلەن  نى ئۇرۇڭلار، چۈنكى ئۇلار ئەفىفە ئاياللارغا يالغاندىن تۆھمەت چاپلىغان ۋە كىشىلەرنىڭ ئابرۇيىنى تۆككەن ئادەملەردۇر)، ئۇلارنىڭ گۇۋاھلىقىنى (ئۇلار يالغانچىلىق ۋە تۆھمەت چاپلاش مەيدانىدا چىڭ تۇرىدىغانلا بولسا) ھەرگىز قوبۇل قىلماڭلار، ئۇلار پاسىقلاردۇر (يەنى ئۇلار بوھتان چاپلاشتىن ئىبارەت چوڭ گۇناھنى ئىشلىگەنلىكلىرى ئۈچۈن، اﷲ نىڭ ئىتائىتىدىن چىققۇچىلاردۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,5,' كېيىن تەۋبە قىلغان ۋە (ئەمەللىرىنى) تۈزەتكەنلەر (يەنى ئەفىفە ئاياللارغا قايتا تۆھمەت چاپلىمىغانلار) بۇنىڭدىن مۇستەسنا (ئۇلارنى كەچۈرۈڭلار، گۇۋاھلىقىنى قوبۇل قىلىڭلار)، چۈنكى اﷲ مەغپىرەت قىلغۇچىدۇر (يەنى بەندە تەۋبە قىلىپ ئۆزىنى تۈزەتسە، ئۇنى اﷲ مەغپىرەت قىلىدۇ)، تولىمۇ مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,6,' خوتۇنلىرىنى زىنا بىلەن قارىلىغان ۋە ئۆزلىرىدىن باشقا گۇۋاھچىسى بولمىغانلار گۇۋاھلىق سۆزىنىڭ راست ئىكەنلىكىنى دەلىللەش ئۈچۈن، اﷲ نىڭ نامى بىلەن تۆت قېتىم قەسەم قىلسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,7,' بەشىنچى قېتىمىدا: «ئەگەر يالغانچى بولسام ماڭا اﷲ نىڭ لەنىتى بولسۇن» دېسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,8,' خوتۇن كىشى ئېرىنىڭ يالغانچى ئىكەنلىكىگە اﷲ نىڭ نامى بىلەن تۆت قېتىم قەسەم ئىچسە، جازاغا تارتىلىشتىن ساقلاپ قېلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,9,' بەشىنچى قېتىمىدا، ئۇ ئەگەر راستچىللاردىن بولسا (يەنى ئېرىمنىڭ گېپى راست بولىدىغان بولسا) ماڭا اﷲ نىڭ غەزىپى بولسۇن دەپ قەسەم قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,10,' ئەگەر سىلەرگە اﷲ نىڭ پەزلى ۋە رەھمىتى بولمىسا ئىدى (چوقۇم رەسۋا بولاتتىڭلار)، اﷲ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,11,' شۈبھىسىزكى، سىلەردىن بىر گۇرۇھ ئادەم (ئائىشەگە) بوھتان چاپلىدى بۇ سىلەر ئۈچۈن يامان ئەمەستۇر، (سەۋر قىلغانلىقىڭلار بىلەن ساۋابقا ئېرىشىدىغانلىقىڭلار ئۈچۈن ۋە ئائىشە، سەفۋانلارنىڭ پاكلىقى ئاشكارا بولىدىغانلىقى ئۈچۈن) بەلكى ياخشىدۇر، بوھتان چاپلىغۇچىلاردىن كىمنىڭ قانچىلىك گۇناھى بولسا، ئۇنىڭغا شۇنچىلىك جازا بېرىلىدۇ، بوھتاننىڭ چوڭ قىسمىنى تارقاتقان ئادەم (يەنى ئابدۇللاھ ئىبن ئۇبەي) قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,12,' بوھتاننى ئاڭلىغان چاغلىرىڭلاردا ئەر ـ ئايال مۆمىنلەر نېمىشقا ئۆزلىرىنى ياخشى دەپ قاراپ، بۇ ئېنىق بوھتان دېمىدى؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,13,' ئۇلار نېمىشقا تۆت نەپەر گۇۋاھچىنى كەلتۈرمىدى؟ گۇۋاھچىلارنى كەلتۈرەلمىگەن ئىكەن، ئۇلار اﷲ نىڭ نەزىرىدە يالغانچى ھېسابلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,14,' سىلەرگە دۇنيا ۋە ئاخىرەتتە اﷲ نىڭ پەزلى ۋە مەرھەمىتى بولمىسا ئىدى، بوھتان چاپلىغىنىڭلار ئۈچۈن ئەلۋەتتە چوڭ ئازابقا دۇچار بولاتتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,15,' ئۆز ۋاقتىدا بوھتاننى تىلىڭلار بىلەن تارقاتتىڭلار، بىلمەيدىغان نەرسەڭلارنى ئېغىزىڭلارغا ئالدىڭلار، ئۇنى سەل چاغلىدىڭلار، ھالبۇكى، اﷲ نىڭ نەزىرىدە، ئۇ چوڭ گۇناھتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,16,' ئۇنى ئاڭلىغان چېغىڭلاردا، نېمىشقا مۇنداق سۆزلەرنى قىلىش بىزگە مۇناسىپ ئەمەس، (ئى اﷲ!) سەن پاكتۇرسەنكى، بۇ چوڭ بوھتاندۇر دېمىدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,17,' اﷲ سىلەرگە نەسىھەت قىلىدۇكى، ئەگەر مۆمىن بولساڭلار، ھەرگىز قايتا مۇنداق سۆزلەرنى قىلماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,18,' اﷲ سىلەرگە (شەرىئەت ئەھكاملىرىنى ۋە گۈزەل ئەدەب ـ ئەخلاقلارنى كۆرسىتىپ بېرىدىغان) ئايەتلەرنى بايان قىلىدۇ، اﷲ (بەندىلەرگە پايدىلىق ئىشلارنى) ئوبدان بىلگۈچىدۇر، (تەدبىر قوللىنىشتا ۋە شەرىئىتىنى بەرپا قىلىشتا) ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,19,' مۆمىنلەر ئۈستىدە يامان سۆزلەرنىڭ تارىلىشىنى ياقتۇرىدىغان ئادەملەر، شۈبھىسىزكى، دۇنيا ۋە ئاخىرەتتە قاتتىق ئازابقا قالىدۇ، اﷲ (سىرلارنى ۋە نىيەتلەرنى) بىلىپ تۇرىدۇ، سىلەر بىلمەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,20,' اﷲ نىڭ سىلەرگە پەزلى ۋە مەرھەمىتى بولمىسا ئىدى (سىلەرنى قاتتىق جازالايتتى)، اﷲ شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,21,' ئى مۆمىنلەر! شەيتاننىڭ كەينىگە كىرمەڭلار، كىمكى شەيتاننىڭ كەينىگە كىرىدىكەن، شەيتان ئۇنى قەبىھ (سۆز ـ ھەرىكەتلەر) گە، يامان ئىشلارغا بۇيرۇيدۇ، اﷲ نىڭ سىلەرگە پەزلى ۋە مەرھەمىتى بولمىسا ئىدى، سىلەردىن ھېچ ئادەم ئەبەدىي پاك بولماس ئىدى ۋە لېكىن اﷲ خالىغان بەندىسىنى پاك قىلىدۇ، اﷲ سىلەرنىڭ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىڭلارنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,22,' ئاراڭلاردا ئەھلى كەرەم ۋە دۆلەتمەن بولغانلار خىش ـ ئەقرىبالارغا، مىسكىنلەرگە ۋە دىن يولىدا ھىجرەت قىلغانلارغا (بىر نەرسە) بەرمەسلىككە قەسەم قىلمىسۇن، (ئۇلارنىڭ گۇناھىنى) ئەپۇ قىلسۇن، كەچۈرسۇن، اﷲ نىڭ سىلەرگە مەغپىرەت قىلىشىنى ياقتۇرمامسىلەر؟ اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,23,' يامان ئىشتىن بىخەۋەر ئىپپەتلىك مۆمىن ئاياللارغا قارا چاپلايدىغانلار دۇنيا ۋە ئاخىرەتتە چوقۇم لەنەتكە ئۇچرايدۇ (يەنى اﷲ نىڭ رەھمىتىدىن يىراق قىلىنىدۇ)، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,24,' شۇ كۈندە (يەنى قىيامەت كۈنىدە) ئۇلارنىڭ تىللىرى، قوللىرى ۋە پۇتلىرى ئۇلارنىڭ قىلمىشلىرىدىن ئۇلارنىڭ زىيىنىغا گۇۋاھلىق بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,25,' شۇ كۈندە اﷲ ئۇلارغا تولۇق تېگىشلىك جازاسىنى بېرىدۇ، ئۇلار اﷲ نىڭ ئاشكارا ھەق ئىكەنلىكىنى بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,26,' يامان ئاياللار يامان ئەرلەرگە لايىقتۇر، يامان ئەرلەر يامان ئاياللارغا لايىقتۇر، ياخشى ئاياللار ياخشى ئەرلەرگە لايىقتۇر، ياخشى ئەرلەر ياخشى ئاياللارغا لايىقتۇر، ئەنە شۇلار ئۇلارنىڭ چاپلىغان بوھتانلىرىدىن پاكتۇر، ئۇلار مەغپىرەتتىن، (نازۇنېمەتلىك جەننەتلەردىكى) ئېسىل رىزىقتىن بەھرىمەن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,27,' ئى مۆمىنلەر! باشقىلارنىڭ ئۆيلىرىگە (كىرىشكە) ئىجازەت سورىمىغىچە ۋە ئۆي ئىگىسىگە سالام بەرمىگىچە كىرمەڭلار، ۋەز ـ نەسىھەت ئېلىشىڭلار (يەنى بۇ گۈزەل ئەخلاق ـ ئەدەبكە ئەمەل قىلىشىڭلار) ئۈچۈن بۇ (يەنى ئىجازەت سوراپ ۋە سالام بېرىپ كىرىش ئۇشتۇمتۇت كىرىشتىن) سىلەرگە ياخشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,28,' ئەگەر باشقىلارنىڭ ئۆيلىرىدە ھېچ ئادەم بولمىسا سىلەرگە ئىجازەت بېرىلمىگىچە كىرمەڭلار، ئەگەر سىلەرگە قايتىڭلار دېيىلسە قايتىپ كېتىڭلار، قايتىپ كېتىش سىلەر ئۈچۈن ئەڭ ياخشىدۇر، اﷲ سىلەرنىڭ قىلىدىغان ئەمەللىرىڭلارنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,29,' شەخسىي تۇرالغۇ بولمىغان (يەنى دۇكان ـ سارايلارغا ئوخشاش) ئۆيلەرگە ھاجىتىڭلار چۈشۈپ كىرسەڭلار، سىلەرگە ھېچ گۇناھ يوقتۇر اﷲ سىلەرنىڭ ئاشكارىلىغىنىڭلارنىمۇ، يوشۇرغىنىڭلارنىمۇ بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,30,' مۆمىن ئەرلەرگە ئېيتقىنكى، (نا مەھرەملەرگە) تىكىلىپ قارىمىسۇن، ئەۋرەتلىرىنى (زىنادىن) ساقلىسۇن، مۇنداق قىلىش ئۇلار ئۈچۈن ئەڭ ياخشىدۇر، اﷲ ھەقىقەتەن ئۇلارنىڭ قىلمىشلىرىدىن تولۇق خەۋەردار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,31,' مۆمىنەلەرگە ئېيتقىنكى، نامەھرەملەرگە تىكىلىپ قارىمىسۇن، ئەۋرەتلىرىنى ياپسۇن، تاشقى زىننەتلىرىدىن باشقا زىننەتلىرىنى ئاشكارىلىمىسۇن، لېچەكلىرى بىلەن كۆكرەكلىرىنى ياپسۇن، (تاشقى زىننەتلىرىدىن باشقا) زىننەتلىرىنى ئەرلىرىدىن، ئاتىلىرىدىن، يا قېيىن ئاتىلىرىدىن، يا ئوغۇللىرىدىن، يا ئەرلىرىنىڭ ئوغۇللىرىدىن، يا ئۆز قېرىنداشلىرىدىن، يا قېرىنداشلىرىنىڭ ئوغۇللىرىدىن، يا ھەمشىرىلىرىنىڭ ئوغۇللىرىدىن، يا دىنداش ئاياللاردىن، يا قول ئاستىدىكى چۆرىلەردىن، يا خوتۇنلارغا ئېھتىياجى يوق خىزمەتچىلەر (يەنى قېرى، دەلدۈش بولغانلىقتىن جىنسىي شەھۋىتى يوقلار) دىن، يا ئاياللارنىڭ ئۇياتلىق جايلىرىنى ئۇقمايدىغان (يەنى بالاغەتكە يەتمىگەن) بالىلاردىن باشقا كىشىلەرگە كۆرسەتمىسۇن، زىننەتلىرىنى كىشىلەرگە بىلدۈرۈش ئۈچۈن ئاياغلىرىنى يەرگە ئۇرمىسۇن، ئى مۆمىنلەر! بەختكە ئېرىشىشىڭلار ئۈچۈن ھەممىڭلار اﷲ قا تەۋبە قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,32,' ئاراڭلاردىكى بويتاق ئەر، ئاياللارنىڭ ۋە ياخشى قۇل ۋە ياخشى چۆرىلىرىڭلارنىڭ بېشىنى ئوڭلاپ قويۇڭلار؛ ئەگەر ئۇلار يوقسۇز بولىدىغان بولسا (ئۇلارنىڭ يوقسۇزلۇقى سىلەرنىڭ ئۇلارنىڭ بېشىنى ئوڭلاپ قويۇشۇڭلارغا توسۇق بولمىسۇن)، اﷲ ئۇلارنى ئۆز كەرىمى بىلەن باي قىلىدۇ، اﷲ نىڭ (كەرىمى) كەڭدۇر، اﷲ بەندىلەرنىڭ مەنپەئىتىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,33,' ئۆيلىنەلمەيدىغانلار اﷲ ئۇلارنى ئۆز كەرىمى بىلەن باي قىلغۇچە، ئۆزلىرىنى ئىپپەتلىك تۇتسۇن، قۇللىرىڭلاردىن توختام تۈزۈشنى تىلەيدىغانلار (يەنى خوجىسىغا مۇئەييەن پۇل ـ مال تۆلەش شەرتى بىلەن ئۆزىنىڭ ئازاد قىلىنىشىنى تىلەيدىغانلار) بىلەن، ئەگەر ئۇلارنىڭ ساداقىتىنى بايقىساڭلار، توختام تۈزۈڭلار، سىلەرگە اﷲ ئاتا قىلغان مال ـ مۈلۈكنىڭ بىر قىسمىنى (ئۆزلىرىنى ئازاد قىلىشقا ياردەم تەرىقىسىدە) ئۇلارغا بېرىڭلار، ئەگەر سىلەرنىڭ چۆرىلىرىڭلار ئىپپەتلىك بولۇشنى خالىسا، بۇ دۇنيانىڭ ئازغىنا مېلىنى دەپ ئۇلارنى پاھىشىغا مەجبۇرلىماڭلار، كىمكى ئۇلارنى پاھىشىغا مەجبۇرلايدىكەن، مەجبۇرلانغاندىن كېيىن اﷲ ئۇلارنى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا)رەھىم قىلغۇچىدۇر(يەنى پاھىشىغا مەجبۇرلانغانلىقى ئۈچۈن، اﷲ ئۇلارنى جاۋابكارلىققا تارتمايدۇ، مەجبۇرلىغۇچىلارنى قاتتىق جازالايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,34,' (ئى مۆمىنلەر!)بىز سىلەرگە روشەن ئايەتلەرنى، سىلەردىن ئىلگىرى ئۆتكەن ئۈممەتلەردىن مىساللارنى ۋە تەقۋادارلار ئۈچۈن ۋەز – نەسىھەتلەرنى شەك – شۈبھىسىز نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,35,' اﷲ ئاسمانلارنىڭ ۋە زېمىننىڭ نۇرىدۇر، اﷲ نىڭ (مۆمىن بەندىسىنىڭ قەلبىدىكى)نۇرى خۇددى (چىراق قويىدىغان) تەكچىگە ئوخشايدۇ، ئۇنىڭدا چىراق باردۇر، چىراق شىشىنىڭ ئىچىدىدۇر، شىشە گويا نۇرلۇق يۇلتۇزدۇر، چىراق مۇبارەك زەيتۇن دەرىخىنىڭ (يېغى) بىلەن يورۇتۇلغان، ئۇ (يەنى زەيتۇن دەرىخى) شەرق تەرەپتىمۇ ئەمەس، غەرب تەرەپتىمۇ ئەمەس (سەھرادا ئوچۇقچىلىقتا بولۇپ، كۈن بويى ئۇنىڭغا كۈن نۇرى چۈشۈپ تۇرىدۇ، شۇنىڭ بىلەن ئۇنىڭ مېۋىسى پىشىپ يېتىلگەن بولىدۇ)، يېغى (ئەڭ سۈزۈك بولىدۇ، سۈزۈكلۈكىدىن) ئوت تەگمىسىمۇ يورۇپ كېتەيلا دەپ قالىدۇ، (ئوت بىلەن يورۇتۇلسا) نۇر ئۈستىگە نۇر قوشۇلىدۇ، اﷲ خالىغان كىشىنى ئۇنىڭ نۇرىغا (يەنى قۇرئانغا) (ئەگىشىشكە) مۇۋەپپەق قىلىدۇ، اﷲ (ئىبرەت ئالسۇن دەپ) كىشىلەرگە مىساللارنى (ئۇلارنىڭ پەمىگە يېقىنلاشتۇرۇپ) بايان قىلىدۇ، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,36,' اﷲ نىڭ ئۇلۇغلىنىشى ۋە ئىسمىنىڭ ياد ئېتىلىشى بۇيرۇلغان مەسجىدلەردە (مۆمىنلەر) ئەتىگەن – ئاخشامدا تەسبىھ ئېيتىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,37,' ئۇلار شۇنداق ئەرلەركى، سودا ـ سېتىق ئۇلارنى اﷲ نى زىكىر قىلىشتىن، ناماز ئۆتەشتىن، زاكات بېرىشتىن غەپلەتتە قالدۇرمايدۇ، ئۇلار دىللار ۋە كۆزلەر قالايمىقانلىشىپ كېتىدىغان كۈن (يەنى قىيامەت كۈنى) دىن قورقىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,38,' اﷲ نى ئۆزلىرىنىڭ قىلغان ياخشى ئەمەللىرىگە مۇكاپات بەرسۇن ۋە ئۆزىنىڭ پەزلىدىن ئاشۇرۇپ بەرسۇن دەپ (ئۇلار شۇنداق ئىبادەتلەرنى قىلىدۇ) اﷲ خالىغان ئادەمگە ھېسابسىز رىزىق بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,39,' كاپىرلارنىڭ (ياخشى ئەمەل دەپ ئويلىغان) ئەمەللىرى باياۋاندىكى سەرابقا ئوخشايدۇ، تەشنا ئادەم ئۇنى سۇ دەپ گۇمان قىلىدۇ، ئۇنىڭ يېنىغا كەلسە ھېچ نەرسە كۆرۈنمەيدۇ (يەنى سۇ كۆرمەيدۇ)، ئۇ اﷲ نىڭ ئۆز (ئەمەلىنىڭ) يېنىدا (كۆزىتىپ تۇرغۇچى) ئىكەنلىكىنى بايقايدۇ، اﷲ ئۇنىڭ قىلمىشىغا تېگىشلىك جازا بېرىدۇ، اﷲ تېز ھېساب ئالغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,40,' ياكى ئۇلارنىڭ (ياخشى دەپ گۇمان قىلغان) ئەمەللىرى چوڭقۇر دېڭىزدىكى قاراڭغۇلۇققا ئوخشايدۇ، ئۇنى (يەنى دېڭىزنى) قاتمۇقات دولقۇنلار ۋە دولقۇنلارنىڭ ئۈستىدىكى قارا بۇلۇتلار قاپلاپ تۇرىدۇ (مانا بۇ) قاتمۇقات قاراڭغۇلۇقلاردۇر، (بۇ قاراڭغۇلۇققا گىرىپتار بولغۇچى) قولىنى ئۇزاتسا بارماقلىرىنى كۆرەلمەيدۇ، اﷲ نۇر ئاتا قىلمىسا، كىم بولمىسۇن، نۇرنى كۆرەلمەيدۇ (يەنى اﷲ ھىدايەت قىلمىغان ئادەم ھەرگىز ھىدايەت تاپمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,41,' اﷲ قا ئاسمانلاردىكى ۋە زېمىندىكىلەرنىڭ ۋە قاناتلىرىنى كەرگەن ھالدا (ئۇچۇپ كېتىۋاتقان) قۇشلارنىڭ تەسبىھ ئېيتىدىغانلىقىنى سەن بىلمەمسەن؟ اﷲ ھەر(مەخلۇق) نىڭ دۇئاسىنى ۋە تەسبىھىنى بىلىدۇ، اﷲ ئۇلارنىڭ قىلىدىغان ئىشلىرىنى تولۇق بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,42,' ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، (خالايىقنىڭ) ئاخىر قايتىدىغان جايى اﷲ نىڭ دەرگاھىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,43,' كۆرمەمسەنكى، اﷲ بۇلۇتلارنى ھەيدەيدۇ، ئاندىن ئۇلارنى بىر – بىرىگە قوشىدۇ، ئاندىن ئۇلارنى توپلايدۇ، شۇنىڭ بىلەن بۇلۇتنىڭ ئارىسىدىن يامغۇر ياغقانلىقىنى كۆرىسەن، اﷲ ئاسماندىكى تاغدەك بۇلۇتلاردىن مۆلدۈر ياغدۇرىدۇ، ئۇنى اﷲ خالىغان ئادەمگە يەتكۈزىدۇ (يەنى ئۇنىڭ بىلەن خالىغان ئادەمنىڭ زىرائىتى، مېۋىسى ۋە چارۋىلىرىغا زىيان يەتكۈزىدۇ) ۋە ئۇنىڭ (زىيىنىدىن) خالىغان ئادەمنى ساقلايدۇ، چاقماقنىڭ يورۇقى (قارىغۇچىلارنى) كۆزلىرىدىن ئايرىۋەتكىلى تاس قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,44,' اﷲ كېچە بىلەن كۈندۈزنى ئالماشتۇرۇپ تۇرىدۇ، بۇنىڭدا ئەقىل ئىگىلىرى ئۈچۈن ئەلۋەتتە ئىبرەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,45,' اﷲ ھەر بىر جاندارنى سۇدىن ياراتتى، ئۇلارنىڭ بەزىسى قورسىقى بىلەن ماڭىدۇ، بەزىسى ئىككى پۇت بىلەن ماڭىدۇ، بەزىسى تۆت پۇت بىلەن ماڭىدۇ، اﷲ خالىغان مەخلۇقاتلارنى يارىتىدۇ، اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,46,' شۈبھىسىزكى، بىز روشەن ئايەتلەرنى نازىل قىلدۇق، اﷲ ئۆزى خالىغان كىشىلەرنى توغرا يولغا باشلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,47,' (مۇناپىقلار) «اﷲ قا ۋە پەيغەمبەرگە ئىمان ئېيتتۇق ۋە ئىتائەت قىلدۇق» دېيىشىدۇ، ئاندىن ئۇلاردىن بىر جامائە شۇنىڭدىن (يەنى ئىماننى دەۋا قىلغاندىن) كېيىن، (شەرىئەت ھۆكمىدىن) يۈز ئۆرۈيدۇ، ئۇلار (ھەقىقەتتە) مۆمىن ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,48,' ئۇلارنىڭ ئارىسىدا (پەيغەمبەر) ھۆكۈم چىقىرىش ئۈچۈن، ئۇلار اﷲ نىڭ ۋە پەيغەمبەرنىڭ (ھۆكمىگە) چاقىرىلسا، ئۇلاردىن بىر جامائە (پەيغەمبەرنىڭ ئالدىغا ھازىر بولۇشتىن) باش تارتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,49,' ئەگەر ھەقىقەت ئۇلارنىڭ تەرىپىدە بولىدىغان بولسا (پەيغەمبەر ئەلەيھىسسالامنىڭ ھەق ھۆكۈم چىقىرىدىغانلىقنى بىلىدىغانلىقلىرى ئۈچۈن)، پەيغەمبەرگە بويسۇنغان ھالدا كېلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,50,' ئۇلارنىڭ دىللىرىدا (مۇناپىقلىق) ئىللىتى بارمۇ؟ يا) مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىدىن) گۇمانلىنامدۇ؟ يا اﷲ نىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ ئۇلارغا ئادىل بولماسلىقىدىن قورقامدۇ؟ ياق، ئۇلار (رەسۇلۇللاھنىڭ ھۆكمىدىن باش تارتقانلىقلىرى ئۈچۈن) زالىمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,51,' (پەيغەمبەر) مۆمىنلەرنىڭ ئارىسىدا ھۆكۈم چىقىرىش ئۈچۈن، ئۇلار اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە چاقىرىلغان چاغدا ئۇلار: «ئاڭلىدۇق ۋە ئىتائەت قىلدۇق دېيىشلىرى كېرەك، مانا شۇنداق كىشىلەر مەقسەتكە ئېرىشكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,52,' اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلغان اﷲ تىن قورققان ۋە ئۇنىڭغا تەقۋادارلىق قىلغان كىشىلەر بەختكە ئېرىشكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,53,' (مۇناپىقلار) ئەگەر سەن ئۇلارنى (جىھادقا) چىقىشقا ئەمر قىلساڭ، چوقۇم چىقىدىغانلىقىغا اﷲ بىلەن كۈچلۈك قەسەم قىلىدۇ ئېيتقىنكى، «قەسەم قىلماڭلار، (قەسىمىڭلار يالغان، سىلەردىن تەلەپ قىلىنىدىغىنى) ساداقەتلىك بىلەن ئىتائەت قىلىشتۇر، اﷲ سىلەرنىڭ قىلمىشلىرىڭلاردىن ھەقىقەتەن خەۋەرداردۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,54,' ئېيتقىنكى: «اﷲ قا ئىتائەت قىلىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار، ئەگەر ئۇلار (اﷲ قا ئىتائەت قىلىشتىن) باش تارتسا، پەيغەمبەرنىڭ ئەلچىلىكنى يەتكۈزۈش مەسئۇلىيىتى بار، سىلەرنىڭ ئىتائەت قىلىش مەسئۇلىيىتىڭلار بار، ئەگەر پەيغەمبەرنىڭ (ئەمرىگە) ئىتائەت قىلساڭلار، ھىدايەت تاپىسىلەر، پەيغەمبەرنىڭ پەقەت چۈشىنىشلىك قىلىپ تەبلىغ قىلىش مەسئۇلىيىتى بار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,55,' اﷲ ئىچىڭلاردىكى ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەرگە، ئۇلاردىن بۇرۇن ئۆتكەنلەرنى زېمىندا ھۆكۈمران قىلغاندەك، ئۇلارنىمۇ چوقۇم ھۆكۈمران قىلىشىنى، ئۇلار ئۈچۈن تاللىغان دىنىنى چوقۇم مۇستەھكەم قىلىپ بېرىشنى ۋە ئۇلارنىڭ قورقۇنچىسىنى ئامانلىققا ئايلاندۇرۇپ بېرىشنى ۋەدە قىلدى، ئۇلار ماڭا ئىبادەت قىلىدۇ، ماڭا ھېچ نەرسىنى شېرىك كەلتۈرمەيدۇ، شۇ ۋەدىدىن كېيىن كاپىر بولغانلار اﷲ نىڭ ئىتائىتىدىن چىققۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,56,' اﷲ نىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن ناماز ئوقۇڭلار، زاكات بېرىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,57,' كاپىرلارنى زېمىنىدا اﷲ نىڭ ئازابىدىن قېچىپ قۇتۇلالايدۇ دەپ ئويلىمىغىن، ئۇلارنىڭ جايى دوزاخ بولىدۇ، دوزاخ نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,58,' ئى مۆمىنلەر! سىلەرنىڭ قۇللىرىڭلار، چۆرىلىرىڭلار ۋە ئىچىڭلاردىكى بالاغەتكە يەتمىگەن بالىلار ناماز بامداتتىن بۇرۇن، چۈشتە (ئۇخلاش ئۈچۈن) كىيىمىڭلارنى سالغان چېغىڭلاردا ۋە خۇپتەندىن كېيىن (مۇشۇنداق) ئۈچ ۋاقىتتا (يېنىڭلارغا كىرسە) سىلەردىن ئىجازەت سورىسۇن، (بۇ) ئۈچ ۋاقىت سىلەرنىڭ ئەۋرىتىڭلار (ئېچىلىپ قالىدىغان ۋاقىتتۇر)، بۇ ئۈچ ۋاقىتتىن باشقا ۋاقىتلاردا (ئۇلار سىلەرگە كىرسە) سىلەرگىمۇ، ئۇلارغىمۇ ھېچ گۇناھ بولمايدۇ، سىلەر بىر ـ بىرىڭلار بىلەن بېرىش ـ كېلىش قىلىپ تۇرىسىلەر، اﷲ سىلەرگە ئايەتلەرنى (يەنى شەرىئەت ئەھكاملىرىنى) ئەنە شۇنداق بايان قىلىدۇ، اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,59,' كىچىك بالىلىرىڭلار بالاغەتكە يەتكەندە، ئۇلار ئىلگىرىكىلەر (يەنى چوڭلار كىرىشتە) ئىجازەت سورىغاندەك، (ئۆيگە كىرىشتە ھەممە ۋاقىت) ئىجازەت سورىسۇن، اﷲ ئايەتلىرىنى (دىننىڭ ئىشلىرىنى) سىلەرگە شۇنداق بايان قىلىدۇ، اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,60,' ياتلىق بولۇشنى خالىمايدىغان ياشانغان ئاياللار زىننەتلىرىنى ئاشكارىلىمىغان ھالدا (رىدا، نىمچىغا ئوخشاش) تاشقى كىيىملىرىنى سېلىۋەتسە (ئەرلەرنىڭ ئالدىدا ئادەتتىكى كىيىملىرى بىلەن يۈرسە)، ھېچ گۇناھ يوقتۇر، تاشقى كىيىملىرىنى سېلىۋېتىشتىن ساقلانسا ئۇلار ئۈچۈن ياخشىدۇر اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,61,' (جىھادقا چىقالمىسا) ئەماغا گۇناھ يوقتۇر، توكۇرغا گۇناھ يوقتۇر، كېسەلگە گۇناھ يوقتۇر (ئى ئىنسانلار!) سىلەر ئۆز ئۆيلىرىڭلاردا (يەنى ئەرلىرىڭلارنىڭ، ئاياللىرىڭلارنىڭ ئۆيلىرىدە) ئاتىلىرىڭلارنىڭ ئۆيلىرىدە، ئانىلىرىڭلارنىڭ ئۆيلىرىدە، قېرىنداشلىرىڭلارنىڭ ئۆيلىرىدە، ھەمشىرەڭلارنىڭ ئۆيلىرىدە، يا ئاتاڭلارنىڭ قېرىنداىشلىرىنىڭ ئۆيلىرىدە، ئاتاڭلارنىڭ ھەمشىرىلىرىنىڭ ئۆيلىرىدە، يا ئاناڭلارنىڭ قېرىنداشلىرىنىڭ ئۆيلىرىدە، يا ئاناڭلارنىڭ ھەمشىرىلىرىنىڭ ئۆيلىرىدە، يا سىلەر ئاچقۇچلىرىنى باشقۇرىدىغانلارنىڭ  ئۆيلىرىدە (يەنى ئۆي ئىگىسى سىرتقا چىقىپ كېتىپ، ئۆي بىلەن ئاچقۇچنى سىلەرگە تاپشۇرغانلارنىڭ ئۆيلىرىدە)، يا دوستلىرىڭلارنىڭ ئۆيلىرىدە (ئىجازەتسىز) بىرەر نېمە يېسەڭلار گۇناھ يوقتۇر، سىلەر توپلىشىپ يېسەڭلارمۇ، يا يەككە يېسەڭلارمۇ گۇناھ يوقتۇر، سىلەر ئۆيلەرگە كىرگەن چېغىڭلاردا ئۆزۈڭلارغا (يەنى ئۆيدىكى كىشىلەرگە) اﷲ بەلگىلىگەن مۇبارەك، پاك سالامنى بېرىڭلار، (يەنى ئەسسالامۇ ئەلەيكۇم دەڭلار) اﷲ سىلەرنى چۈشەنسۇن دەپ ئايەتلەرنى مۇشۇنداق بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,62,' اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان ئېيتقان كىشىلەر كامىل مۆمىنلەردۇر، ئۇلار (مۇسۇلمانلارغا پايدىلىق) بىر ئىش ئۈستىدە (مەسلىھەتلىشىش ئۈچۈن) پەيغەمبەر بىلەن جەم بولغان چاغلىرىدا، پەيغەمبەردىن ئىجازەت سورىمىغىچە كېتىپ قالمايدۇ، سەندىن ئىجازەت سورايدىغانلار اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان ئېيتىدىغان كىشىلەردۇر، بەزى شەخسىي ئىش ئۈچۈن ئۇلار سەندىن ئىجازەت سورىسا، ئۇلارنىڭ ئىچىدىن سەن خالىغان ئادەملەرگە ئىجازەت بەرگىن، ئۇلارغا سەن اﷲ تىن مەغپىرەت تىلىگىن، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇ، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,63,' (ئى مۆمىنلەر!) پەيغەمبەرنى سىلەر بىر ـ بىرىڭلارنى چاقىرغاندەك چاقىرماڭلار، سىلەردىن (پەيغەمبەرنىڭ سۆھبىتىدىن) ئاستا سۇغۇرۇلۇپ غىپپىدە چىقىپ كەتكەنلەرنى اﷲ ئەلۋەتتە بىلىدۇ، پەيغەمبەرنىڭ ئەمرىگە خىلاپلىق قىلغۇچىلار (دۇنيادا چوڭ بىر) پىتنىگە يولۇقۇشتىن، يا (ئاخىرەتتە) قاتتىق بىر ئازابقا دۇچار بولۇشتىن قورقسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,24,64,' بىلىڭلاركى، ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ مەخلۇقاتىدۇر، اﷲ نىڭ تەسەررۇپى ئاستىدىدۇر)، اﷲ سىلەرنىڭ ئەمەلىي ئەھۋالىڭلارنى بىلىپ تۇرىدۇ، بەندىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدىغان كۈندە (يەنى قىيامەت كۈنىدە) اﷲ ئۇلارنىڭ قىلغان ئىشلىرىنى ئۇلارغا ئېيتىپ بېرىدۇ، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,1,'پۈتۈن جاھان ئەھلىنى (اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرغۇچى بولۇشى ئۈچۈن، بەندىسىگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) ھەق بىلەن باتىلنى ئايرىغۇچى قۇرئاننى نازىل قىلغان اﷲ نىڭ بەرىكىتى بۈيۈكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,2,' ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ نىڭ بالىسى يوقتۇر، پادىشاھلىقتا شېرىكىمۇ يوقتۇر، اﷲ ھەممە نەرسىنى ياراتتى، ئاندىن (ئۆزىنىڭ خاھىشى بويىچە) ئۇنىڭغا مۇناسىپ ئۆلچەمنى بەلگىلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,3,' مۇشرىكلار اﷲ نى قويۇپ ھېچ نەرسىنى يارىتالمايدىغان ۋە ئۆزلىرى يارىتىلغان ئىلاھلار (يەنى بۇتلار) غا ئىبادەت قىلدى ئۇلار (يەنى بۇتلار) ئۆزلىرىدىن بىرەر زىياننى دەپئى قىلالمايدۇ ئۆزلىرىگە بىرەر پايدىنى كەلتۈرەلمەيدۇ، (باشقىلارنىڭ) جېنىنى ئېلىشقا، (باشقىلارغا) ھايات بېغىشلاشقا ياكى (ئۆلۈكلەرنى) تىرىلدۈرۈشكە قادىر بولالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,4,' كاپىرلار: «بۇ (يەنى قۇرئان) پەقەت ئۇ (يەنى مۇھەممەد) ئۆزى توقۇغان يالغاندۇر، ئۇنىڭغا ئۇنى توقۇشقا باشقا بىر قەۋم (يەنى ئەھلى كىتاب) ياردەم بەرگەن» دەيدۇ، ئۇلار (پەيغەمبەر ئەلەيھىسسالامغا بوھتان چاپلاش بىلەن ئۇنىڭغا) زۇلۇم قىلدى ۋە يالغاننى چاپلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,5,' ئۇلار ئېيتتى: «(بۇ) بۇرۇنقىلارنىڭ ئەپسانىلىرىدۇركى، ئۇلارنى ئۇ يازدۇرۇۋالغان، (ھىپزى قىلىۋېلىش ئۈچۈن) ئۇنىڭغا ئەتىگەن – ئاخشامدا ئوقۇپ بېرىلىۋاتىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,6,' ئېيتقىنكى، «ئۇنى ئاسمانلاردىكى ۋە زېمىندىكى سىرنى بىلىپ تۇرىدىغان اﷲ نازىل قىلدى، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، (بەندىلەرگە) ناھايىتى مېھرىباندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,7,' مۇشرىكلار (مۇھەممەد ئەلەيھىسسالامنى مەسخىرە قىلىپ) ئېيتتى: «بازارلاردا (بىز تاماق يېگەندەك) تاماق يەيدىغان، (بىز ماڭغاندەك) مېڭىپ يۈرىدىغان بۇ قانداق پەيغەمبەر؟ ئۇنىڭ بىلەن بىللە ئاگاھلاندۇرغۇچى بولۇش ئۈچۈن اﷲ ئۇنىڭغا نېمىشقا بىر پەرىشتە ئەۋەتمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,8,' يا ئۇنىڭغا بىر خەزىنە بېرىلمىدى، يا ئۇنىڭغا مېۋىلىرىنى يەيدىغان بىر باغ بېرىلمىدى؟» زالىملار (يەنى كاپىرلار): «سىلەر پەقەت سېھىرلەنگەن بىر ئادەمگە ئەگىشىۋاتىسىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,9,' مۇشرىكلارنىڭ سېنىڭ ئۈچۈن نۇرغۇن مىساللارنى كەلتۈرگەنلىكىگە قارىغىن، ئۇلار ئازدى، توغرا يول تاپالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,10,' اﷲ نىڭ بەرىكىتى بۈيۈكتۇركى، ئەگەر ئۇ خالىسا ساڭا ئۇلارنىڭ ئېيتقانلىرىدىنمۇ ياخشى، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان باغلارنى ۋە چوڭ سارايلارنى ئاتا قىلاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,11,' ياق، ئۇلار قىيامەتنى ئىنكار قىلدى، قىيامەتنى ئىنكار قىلغانلارغا بىز دوزاخنى تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,12,' دوزاخ ئۇلارنى يىراقتىن كۆرگەن چاغدا، ئۇلار دوزاخنىڭ غەزەپتىن قاينىغان ۋە (ئېشەكتەك) ھاڭرىغان ئاۋازنى ئاڭلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,13,' ئۇلار زەنجىر بىلەن باغلانغان ھالدا دوزاخنىڭ تار يېرىگە تاشلانغان چاغدا، بۇ يەردە ئۇلار (ئۆزلىرىنىڭ) ئۆلۈمىنى تىلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,14,' (ئۇلارغا) بۈگۈن سىلەر بىر ئۆلۈمنى ئەمەس، تالاي ئۆلۈملەرنى تىلەڭلار (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,15,' ئۇلارغا ئېيتقىنكى، «شۇ (دوزاخ) ياخشىمۇ يا تەقۋادارلارغا ۋەدە قىلىنغان، (كىرگەن كىشى) مەڭگۈ قالىدىغان جەننەتتە ياخشىمۇ؟» جەننەت تەقۋادارلارغا بېرىلگەن مۇكاپاتتۇر ۋە ئۇلارنىڭ ئاخىرى بارىدىغان جايىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,16,' ئۇلار جەننەتتە خالىغان نېمەتلەردىن بەھرىمەن بولىدۇ، جەننەتتە مەڭگۈ قالىدۇ، بۇ پەرۋەردىگارىڭدىن ئىشقا ئاشۇرۇش تىلىنىدىغان ۋەدىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,17,' شۇ كۈندە اﷲ ئۇلارغا قوشۇپ، ئۇلار اﷲ نى قويۇپ، چوقۇنغان نەرسىلىرىنى يىغىدۇ ئاندىن اﷲ (ئۇلارغا): «مېنىڭ بۇ بەندىلىرىمنى سىلەر ئازدۇردۇڭلارمۇ؟ يا (توغرا) يولدىن ئۇلار ئۆزلىرى ئازدىمۇ؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,18,' (ئىبادەت قىلىنغۇچىلار) ئېتىدۇ: «ئى اﷲ ! سەن پاكتۇرسەن، سېنى قويۇپ باشقىلارنى دوست تۇتۇش (يەنى سەندىن باشقىغا چوقۇنۇش) بىزگە (ۋە سېنىڭ مەخلۇقاتىڭدىن ھېچ ئەھەدىگە) لايىق ئەمەس، لېكىن سەن ئۇلارنى ۋە ئۇلارنىڭ ئاتا – بوۋىلىرىنى شۇ قەدەر نېمەتكە چۈمدۈردۇڭكى، ئۇلار سېنى ياد ئېتىشىنى ئۇنتۇدى ئۇلار ھالاك بولغۇچى قەۋم بولدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,19,' شۈبھىسىزكى، ئىبادەت قىلىنغۇچىلار سىلەرنىڭ (ئۇلارنى مەبۇد دېگەن) سۆزۈڭلارنى ئىنكار قىلدى، (ئى كۇففارلار!) سىلەر (ئۆزۈڭلاردىن ئازابنى) دەپئى قىلىشقىمۇ ۋە (بۇ بالادىن ئۆزۈڭلارنى قۇتۇلدۇرۇشقا) ياردەم بېرىشكىمۇ قادىر بولالمايسىلەر، سىلەردىن كىمكى (اﷲ قا شېرىك كەلتۈرۈش بىلەن ئۆزىگە) زۇلۇم قىلىدىكەن، ئۇنىڭغا بىز (ئاخىرەتتە) قاتتىق ئازابنى تېتىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,20,' بىز سەندىن ئىلگىرى ئەۋەتكەن پەيغەمبەرلەرنىڭ ھەممىسىلا ئەلۋەتتە تاماق يەيتتى، بازارلاردا مېڭىپ يۈرەتتى، بەزىڭلارنى بەزىڭلار بىلەن سىنىدۇق، سەۋر قىلامسىلەر؟ (يەنى سەۋر قىلىڭلار) پەرۋەردىگارىڭ (سەۋر قىلغۇچىلار بىلەن سەۋر قىلمىغۇچىلارنى) كۆرۇپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,25,21,' بىزگە مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,1,'(يەنى مۇشرىكلار): «نېمىشقا بىزگە پەرىشتىلەر چۈشۈرۈلمەيدۇ، ياكى (بىز نېمىشقا) پەرۋەردىگارىمىزنى كۆرمەيمىز» دېيىشتى شۈبھىسىزكى ئۇلار ئۆزلىرىنى چوڭ تۇتتى ۋە تولىمۇ ھەددىدىن ئاشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,2,' ئۇلار (يەنى مۇشرىكلار) (ئۆزلىرىنىڭ جېنىنى ئېلىش ئۈچۈن چۈشكەن) پەرىشتىلەرنى كۆرگەن كۈنى گۇناھكارلارغا خۇش خەۋەر بولمايدۇ، پەرىشتىلەر ئۇلارغا: «(سىلەرگە جەننەت) ھارامدۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,3,' بىز ئۇلارنىڭ قىلغان ياخشى ئەمەللىرىنى بىر تەرەپ قىلىپ، ئۇنى ئۇچۇپ يۈرگەن توزاندەك قىلىۋېتىمىز، (يەنى ئۇلار ئىمانسىىز بولغانلىقتىن، قىلغان ياخشى ئەمەللىرىنى يوققا چىقىرىمىز)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,4,' ئەھلى جەننەت بۇ كۈندە ئەڭ ياخشى جايدا ۋە ئەڭ ياخشى ئارامگاھتا بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,5,' شۇ كۈندە ئاسمان بۇلۇت بىلەن بىرلىكتە يېرىلىدۇ، پەرىشتىلەر (بەندىلەرنىڭ نامە ـ ئەمالىنى ئېلىپ) ئارقىمۇئارقا چۈشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,6,' پادىشاھلىق بۇ كۈندە مەرھەمەتلىك اﷲ قا خاستۇر، بۇ كۈن كاپىرلارغا قېيىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,7,' شۇ كۈنى زالىم (يەنى كاپىر) ئىككى قولىنى چىشلەپ: «ئىسىت! پەيغەمبەر بىلەن (نىجاتلىق) يولىنى تۇتسامچۇ، ئىسىت! پالانىنى دوست تۇتمىغان بولسامچۇ؟ قۇرئان ماڭا يەتكەندىن كېيىن، ئۇ (يەنى پالانى) مېنى قۇرئاندىن، شەك ـ شۈبھىسىزكى، ئازدۇردى» دەيدۇ، شەيتان ئىنساننى (ئازدۇرۇپ بولۇپ) تاشلىۋېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,8,' پەيغەمبەر ئېيتتى: «ئى پەرۋەردىگارىم! شۈبھىسىزكى، مېنىڭ قەۋمىم بۇ قۇرئاننى تاشلاندۇق قىلىپ قويدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,9,' شۇنىڭدەك (يەنى ساڭا قەۋمىڭنىڭ مۇشرىكلىرىدىن دۈشمەنلەرنى قىلغاندەك) ھەربىر پەيغەمبەرگە بىر قىسىم گۇناھكارلارنى ئۇنىڭ دۈشمىنى قىلدۇق، پەرۋەردىگارىڭ يول كۆرسەتكۈچى ۋە ياردەمچى بولۇشقا ساڭا يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,10,' كاپىرلار: «قۇرئان نېمىشقا ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) بىر قېتىمدىلا نازىل قىلىنمىدى؟» دېدى سېنىڭ دىلىڭنى مۇستەھكەم قىلىش ئۈچۈن، ئۇنى پارچە ـ پارچە نازىل قىلدۇق، ئۇنى ئايرىم – ئايرىم نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,11,' ئۇلار (يەنى مۇشرىكلار) قانداق بىر سوئالنى تاشلىمىسۇن، (ئۇنىڭغا قارىتا) بىز ساڭا ھەق جاۋابنى ۋە ھەممىدىن گۈزەل چۈشەندۇرۇشنى نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,12,' ئۇلار جەھەننەمگە يۈزلىرى بىلەن سۈرۈلىدۇ، ئۇلارنىڭ جايى ئەڭ يامان، يولى ئەڭ ئازغۇندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,13,' شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق ھەمدە ئۇنىڭ قېرىندىشى ھارۇننى ئۇنىڭغا ياردەمچى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,14,' ئۇلارغا: «بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان قەۋمگە بېرىڭلار» دېدۇق، (ئۇ قەۋم كۇفرىدا چىڭ تۇرغانلىقتىن) ئۇلارنى دەھشەتلىك تۈردە ھالاك قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,15,' نۇھنىڭ قەۋمى پەيغەمبەرلەرنى ئىنكار قىلغان چاغدا ئۇلارنى غەرق قىلدۇق ھەمدە ئۇلارنى كىشىلەرگە ئىبرەت قىلدۇق، زالىملارغا (ئاخىرەتتە) قاتتىق ئازاب تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,16,' ئاد قەۋمىنى، سەمۇد قەۋمىنى، رەس ئاھالىسىنى ۋە ئۇلارنىڭ ئارىسىدىكى نۇرغۇن ئۈممەتلەرنى ھەم (ھالاك قىلدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,17,' ھەربىر قەۋمگە نۇرغۇن مىساللارنى بايان قىلدۇق، (ۋەز ـ نەسىھەت ئۈنۈم بەرمىگەنلىكتىن) ھەربىر قەۋمنى ھالاك قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,18,' ئۇلار ھەقىقەتەن (شامغا بېرىپ كېلىشتە) يامان يامغۇر (يەنى تاش) ياغدۇرۇلغان شەھەردىن ئۆتىدۇ، ئەجەبا ئۇلار ئۇنى كۆرۈپ (ئىبرەت ئالمامدۇ؟) ياق (يەنى ئىبرەت ئالمايدۇ)، ئۇلار (قىيامەت كۈنى) قايتا تىرىلىشنى ئۈمىد قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,19,' ئۇلار سېنى كۆرسە پەقەت مەسخىرە قىلىۋېلىپ: «اﷲ پەيغەمبەرقىلىپ ئەۋەتكەن مۇشۇمۇ؟ ئەگەر دىنىمىزدا چىڭ تۇرمىساق (مۇھەممەد) بىزنى ئىلاھلىرىمىزندىن ئازدۇرۇۋەتكىلى تاس قالاتتى» (دېدى) كىمنىڭ يولىنىڭ ئەڭ خاتا ئىكەنلىكىنى ئۇلار (ئاخىرەتتە) ئازابنى كۆرگەن چاغدا بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,20,' ئېيتىپ باقساڭچۇ؟ نەپسى خاھىشىنى ئىلاھ قىلىۋالغان ئادەمگە (نەپسى خاھىشىغا ئەگىشىشتىن ئۇنى ساقلاش ئۈچۈن) ھامىي بولالامسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,21,' ئۇلارنىڭ تولىسىنى (گېپىڭنى) ئاڭلايدىغان ياكى چۈشىنىدىغان ئادەملەر دەپ گۇمان قىلامسەن؟ ئۇلار (چۈشەنمەسلىكتە) پەقەت چاھارپايلارغا ئوخشاشتۇر، بەلكى ئۇلاردىنمۇ بەتتەر گۇمراھتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,22,' پەرۋەردىگارىڭنىڭ سايىنى قانداق سوزغانلىقىنى كۆرمىدىڭمۇ؟ ئەگەر خالىسا ئۇنى ئەلۋەتتە مۇقىم قىلاتتى، ئاندىن قۇياشنى سايىگە دەلىل قىلدۇق (يەنى كۈننىڭ چىققانلىقىنى سايىنىڭ بارلىقىغا دەلىل قىلدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,23,' ئاندىن ئۇنى ئاستا – ئاستا يوقاتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,24,' اﷲ سىلەرگە كېچىنى لىباس، ئۇيقۇنى (بەدىنىڭلار ئۈچۈن) راھەت قىلدى، كۈندۈزنى (تىرىكچىلىك ئۈچۈن زېمىندا) يېيىلىدىغان ۋاقىت قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,25,' اﷲ رەھمىتىنى (يەنى يامغۇرنى) ياغدۇرۇش ئالدىدا شامالنى خۇش خەۋەر قىلىپ ئەۋەتتى، يامغۇر بىلەن ئۆلۈك زېمىننى تىرىلدۈرۈش ۋە بىز ياراتقان ھايۋانلارنى، نۇرغۇن ئىنسانلارنى سۇ بىلەن تەمىنلەش ئۈچۈن، بۇلۇتتىن پاك سۇنى چۈشۈرۈپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,26,' ئۇلارنى ۋەز ـ نەسىھەت ئالسۇن دەپ، يامغۇرنى ئۇلارنىڭ ئارىسىدا تەقسىم قىلدۇق (يەنى گاھ ئۇ يەرگە، گاھ بۇ يەرگە ياغدۇردۇق)، ئىنسانلارنىڭ تولىسى (اﷲ قا) كۇفرىلىق قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,27,' ئەگەر خالىساق ئەلۋەتتە ھەر شەھەرگە بىر ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) ئەۋەتەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,28,' شۇنىڭ ئۈچۈن كاپىرلارغا ئىتائەت قىلمىغىن، قۇرئان ئارقىلىق ئۇلارغا قارشى (پاكىتلارنى ئوتتۇرىغا قويۇپ) بارلىق كۈچۈڭ بىلەن كۈرەش قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,29,' اﷲ (قۇدرىتى بىلەن) بىرى تاتلىق بولغان دەريانى، بىرى تۇزلۇق بولغان دېڭىزنى (ئۇلار بىر ـ بىرىگە تۇتىشاڭغۇ بولغان ھالدا (قويۇۋەتتى) ئارلىشىپ كەتمەسلىكى ئۈچۈن (ئۇلارنىڭ ئارىسىدا پەردە، توسما بەرپا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,30,' اﷲ ئىنساننى ئابىمەنىدىن ياراتتى، ئۇنى بالىلار نىسبەت بېرىلىدىغان ئەر ۋە قۇدا ـ باجىلىق پەيدا قىلىدىغان ئايال (دىن ئىبارەت ئىككى خىل قىلىپ) ياراتتى، پەرۋەردىگارىڭ ھەممىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,31,' ئۇلار (يەنى مۇشرىكلار) اﷲ نى قويۇپ ئۇلارغا پايدىمۇ يەتكۈزەلمەيدىغان، زىيانمۇ يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنىدۇ، كاپىر پەرۋەردىگارىغا ئاسىيلىق قىلىش بىلەن (شەيتانغا) ياردەم بەرگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,32,' سېنى پەقەت (جەننەت بىلەن) خۇش خەۋەر بەرگۈچى، (دوزاختىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,33,' ئېيتقىنكى، «مەن (اﷲ نىڭ ئەمرىنى) تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچ ھەق تەلەپ قىلمايمەن، پەقەت (مېنىڭ تىلەيدىغىنىم شۇكى، مېنىڭ دىنىمغا ئەگىشىش بىلەن (پەرۋەردىگارى تەرەپكە يول ئېلىشنى خالايدىغانلار) يول ئالسۇن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,34,' (ھەممە ئىشىڭدا) ئۆلمەيدىغان مەڭگۈ ھايات اﷲ قا يۆلەنگىن، ئۇنىڭغا ھەمد ئېيتىش بىلەن اﷲ نى پاك دەپ ئېتىقاد قىلغىن، ئۇ بەندىلەرنىڭ گۇناھلىرىنى تولۇق بىلىشتە يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,35,' اﷲ ئاسمانلارنى زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئالتە كۈندە ياراتتى، ئاندىن ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى، اﷲ ناھايىتى مەرھەمەتلىكتۇر، بۇنى (يەنى اﷲ نىڭ بۈيۈكلۈكىنى) بىلىدىغان ئادەمدىن سورىغىن (ساڭا اﷲ نىڭ سۈپەتلىرىنى ئېيتىپ بېرىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,36,' ئۇلارغا (يەنى مۇشرىكلارغا): «رەھمانغا (يەنى مەرھەمەتلىك اﷲ قا) سەجدە قىلىڭلار!» دېيىلسە، ئۇلار: «رەھمان دېگەن نېمە؟ سەن بىزنى بۇيرۇغان نەرسىگە سەجدە قىلامدۇق؟» دەيدۇ بۇ سۆز ئۇلارنى (ئىماندىن) تېخىمۇ يىراقلاشتۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,37,' ئاسماندا بۇرۇجلەرنى، يېنىپ تۇرىدىغان چىراقنى (يەنى كۈننى) ۋە نۇرلۇق ئاينى ياراتقان اﷲ نىڭ بەرىكىتى بۈيۈكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,38,' اﷲ (ئۆزىنىڭ قۇدرىتىنى) ئەسلىمەكچى بولغانلار ياكى (ئۆزىنىڭ نېمىتىگە) شۈكۈر قىلماقچى بولغانلار ئۈچۈن كېچە بىلەن كۈندۈزنى ئالمىشىپ تۇرىدىغان قىلىپ بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,39,' مەرھەمەتلىك اﷲ نىڭ (ياخشى كۆرىدىغان) بەندىلىرى زېمىندا ئۆزلىرىنى تۆۋەن تۇتۇپ تەمكىنلىك بىلەن ماڭىدۇ، نادانلار ئۇلارغا (ياقتۇرمايدىغان) سۆز قىلسا، ئۇلار: «سىلەرگە ئامانلىق تىلەيمىز» دەيدۇ (يەنى گۇناھ بولمايدىغان سۆزلەرنى قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,40,' ئۇلار كېچىلەرنى پەرۋەردىگارىغا سەجدە قىلىش ۋە قىيامدا تۇرۇش بىلەن)يەنى ناماز ئوقۇش بىلەن) ئۆتكۈزىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,41,' ئۇلار (يەنى اﷲ ياخشى كۆرىدىغان بەندىلەر) ئېيتىدۇ: «پەرۋەردىگارىمىز! بىزدىن جەھەننەم ئازابىنى دەپئى قىلغىن، جەھەننەمنىڭ ئازابى ھەقىقەتەن (سېنىڭ دۈشمەنلىرىڭدىن) ئايرىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,42,' جەھەننەم ھەقىقەتەن يامان قارارگاھتۇر، يامان جايدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,43,' ئۇلار (يەنى اﷲ ياخشى كۆرىدىغان بەندىلەر) خىراجەت قىلغاندا، ئىسراپچىلىقمۇ قىلمايدۇ، بېخىللىقمۇ قىلمايدۇ، ئوتتۇراھال خىراجەت قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,44,' ئۇلار اﷲ قا ئىككىنچى بىر مەبۇدنى شېرىك قىلمايدۇ، اﷲ ھارام قىلغان ناھەق ئادەم ئۆلتۈرۈش ئىشىنى قىلمايدۇ، زىنا قىلمايدۇ، كىمكى بۇ (گۇناھلار) نى قىلىدىكەن، (ئاخىرەتتە) ئۇ جازاغا ئۇچرايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,45,' قىيامەت كۈنى ئۇنىڭغا ھەسسىلەپ ئازاب قىلىنىدۇ، ئۇ مەڭگۈ ئازاب ئىچىدە خارلانغان ھالدا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,46,' پەقەت (ئۇلارنىڭ ئىچىدىن) (بۇ دۇنيادىكى چېغىدا) تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارلا بۇ ھالدا قالمايدۇ، اﷲ ئۇلارنىڭ گۇناھلىرىنى ياخشىلىققا ئالماشتۇرىدۇ، اﷲ تولىمۇ مەغپىرەت قىلغۇچىدۇر ۋە مەرھەمەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,47,' كىمكى (گۇناھلىرىغا) تەۋبە قىلىدىكەن ۋە ئەمەلىنى تۈزەيدىكەن، ئۇ اﷲ قا يۈزلەنگەن بولىدۇ (يەنى اﷲ ئۇ نىڭ تەۋبىسىنى قوبۇل قىلىپ، ئۇنىڭدىن رازى بولىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,48,' ئۇلار (يەنى اﷲ ياخشى كۆرىدىغان بەندىلەر) يالغان گۇۋاھلىق بەرمەيدۇ، يامان سۆزنى ئاڭلاپ قالغان چاغدا ئالىيجانابلىق بىلەن ئۆتۈپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,49,' ئۇلارغا پەرۋەردىگارىنىڭ ئايەتلىرى بىلەن ۋەز ـ نەسىھەت قىلىنسا، گاس، كور بولۇۋالمايدۇ (يەنى ئۇنى بېرىلىپ ئىخلاس بىلەن ئاڭلايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,50,' ئۇلار: «ئى پەرۋەردىگارىمىز! بىزگە ئاياللىرىمىز ۋە ئەۋلاتلىرىمىز ئارقىلىق شادلىق بېغىشلىشىڭنى (يەنى بىزگە ساڭا ئىتائەتمەن پەرزەنت ئاتا قىلىشىڭنى) تىلەيمىز، بىزنى تەقۋادارلارنىڭ پېشىۋاسى (يەنى تەقۋادارلارنىڭ نەمۇنىسى، ياخشىلىققا دەۋەت قىلغۇچى) قىلغىن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,51,' ئەنە شۇلار سەۋرلىك بولغانلىقى ئۈچۈن جەننەت بىلەن مۇكاپاتلىنىدۇ، ئۇلار جەننەتتە (پەرىشتىلەر تەرىپىدىن قىلىنغان) دۇئا ۋە سالام بىلەن قارشى ئېلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,52,' ئۇلار جەننەتتە مەڭگۈ قالىدۇ، جەننەت نېمىدېگەن گۈزەل قارارگاھ! نېمىدېگەن گۈزەل جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,26,53,' ئېيتقىنكى، «ئەگەر سىلەرنىڭ دۇئايىڭلار بولمىسا، پەرۋەردىگارىم سىلەرگە پەرۋا قىلمايدۇ، (ئى كاپىرلار! پەيغەمبەرنى) ئىنكار قىلدىڭلار، (ئاخىرەتتە) سىلەر قۇتۇلالمايدىغان ئازابقا دۇچار بولۇسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,1,'طا، سىم، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,2,' بۇ، روشەن كىتاب (يەنى قۇرئان) ئايەتلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,3,' (ئى مۇھەممەد!) ئۇلارنىڭ ئىمان ئېيتمىغانلىقىدىن ئۆزۈڭنى ھالاك قىلىۋېتىشىڭ مۇمكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,4,' ئەگەر بىز ئۇلارنىڭ (ئىمان ئېيتىشىنى) خالايدىغان بولساق، ئۇلارغا ئاسماندىن (ئۇلارنى ئىمانغا مەجبۇرلايدىغان) بىر ئايەتنى (ئالامەتنى) نازىل قىلاتتۇقتە، ئۇنىڭغا ئۇلار باش ئەگكەن بولاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,5,' ئۇلارغا مەرھەمەتلىك اﷲ تەرىپىدىن قۇرئاندىن يېڭىدىن بىرنەرسە نازىل بولسىلا، ئۇلار ئۇنىڭدىن يۈز ئۆرۈيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,6,' ئۇلار راستلا (قۇرئاننى) ئىنكار قىلدى، مەسخىرە قىلغان نەرسىنىڭ خەۋەرلىرى (يەنى ئاقىۋىتى) ئۇلارغا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,7,' ئۇلار زېمىننى (يەنى زېمىننىڭ ئاجايىباتلىرىنى) كۈزەتمىدىمۇ؟ زېمىندا تۈرلۈك پايدىلىق ئۆسۈملۈكلەرنى ئۆستۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,8,' بۇنىڭدا ئەلۋەتتە (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) ئالامەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,9,' سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن غالىبتۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,10,' ئۆز ۋاقتىدا پەرۋەردىگارىڭ مۇساغا نىدا قىلدى: «سەن زالىم قەۋمگە بارغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,11,' (ئۇلار) پىرئەۋننىڭ قەۋمىدۇر، ئۇلار (اﷲ نىڭ جازالىشىدىن) قورقمامدۇ؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,12,' مۇسا ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن ئۇلارنىڭ مېنى ئىنكار قىلىشىدىن قورقىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,13,' (ئۇلارنىڭ مېنى ئىنكار قىلىشىدىن) يۈرىكىم سىقىلىدۇ، تىلىم كېكەچ، شۇڭا (ماڭا ياردەمدە بولۇش ئۈچۈن) ھارۇننى (پەيغەمبەر) قىلىپ ئەۋەتكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,14,' (ئۇلارنىڭ دەۋاسىچە) ئۇلارنىڭ ئالدىدا مېنىڭ گۇناھىم بار، ئۇلارنىڭ مېنى ئۆلتۈرۈشىدىن قورقىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,15,' اﷲ ئېيتتى: «ھەرگىز ئۇنداق ئەمەس (يەنى سېنى ئۇلار ھەرگىز ئۆلتۈرەلمەيدۇ)، سىلەر مېنىڭ مۆجىزىلىرىمنى ئېلىپ بېرىڭلار، بىز ھەقىقەتەن سىلەر بىلەن بىللە (مۇنازىرەڭلارنى) ئاڭلاپ تۇرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,16,' سىلەر پىرئەۋنگە بېرىپ: بىز ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارىنىڭ ئەلچىسىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,17,' ئىسرائىل ئەۋلادىنى (قۇللۇقۇڭدىن) بوشاتقىن، ئۇلار بىز بىلەن (شامغا) كەتسۇن دەڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,18,' (ئۇلار پىرئەۋنگە كېلىپ ئەلچىلىكنى يەتكۈزدى) پىرئەۋن (مۇساغا) ئېيتتى: «سېنى بىز ئۆيىمىزدە كىچىكىڭدىن تەربىيلەپ چوڭ قىلمىدۇقمۇ؟ ئارىمىزدا كۆپ يىللار تۇرمىدىڭمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,19,' سەن ھېلىقى قىلغان ئىشىڭنى قىلمىدىڭمۇ؟ (يەنى قىبتىنى ئۆلتۈرمىدىڭمۇ؟) سەن تۇزكورلاردىنسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,20,' مۇسا ئېيتتى: «مەن ئۇ ئىشنى قىلغان چېغىمدا نادانلاردىن ئىدىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,21,' سىلەردىن قورقۇپ، سىلەردىن قاچتىم، پەرۋەردىگارىم ماڭا ھېكمەت ئاتا قىلدى، مېنى پەيغەمبەر قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,22,' سەن ئىسرائىل ئەۋلادىنى قۇل قىلدىڭ، سېنىڭ ماڭا مىننەت قىلغان نېمىتىڭ ئەنە شۇدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,23,' پىرئەۋن ئېيتتى: «ئالەملەرنىڭ پەرۋەردىگارى دېگەن نېمە؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,24,' مۇسا ئېيتتى: «ئۇ ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى پۈتۈن مەخلۇقاتلارنىڭ پەرۋەردىگارىدۇر ئەگەر سىلەر ھەقىقىي ئىشىنىدىغان بولساڭلار»');
