INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,168,' ئۆلچەمنى تولدۇرۇپ بېرىڭلار، كەم بەرگۈچىلەردىن بولماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,169,' (نەرسىلەرنى) توغرا تارازىدا تارتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,170,' كىشىلەرگە نەرسىلىرىنى (يەنى قايسى يول بىلەن بولمىسۇن، كىشىلەرنىڭ ھەقلىرىنى) كەم بەرمەڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلىپ پىتنە ـ پاسات تېرىماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,171,' سىلەرنى ۋە ئىلگىرىكى ئۈممەتلەرنى ياراتقان اﷲ تىن قورقۇڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,172,' ئۇلار ئېيتتى: «سەن سېھىر قىلىنغۇچىلاردىنسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,173,' سەن پەقەت بىزگە ئوخشاش (ئاددى) ئادەمسەن، سېنى بىز ھەقىقەتەن يالغانچى دەپ گۇمان قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,174,' ئەگەر (سۆزۈڭدە) راستچىللاردىن بولساڭ، بىزگە ئاسماندىن ئازاب چۈشۈرگىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,175,' شۇئەيب ئېيتتى: «پەرۋەردىگارىم! سىلەرنىڭ قىلمىشىڭلارنى ئوبدان بىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,176,' ئۇلار شۇئەيبنى ئىنكار قىلدى، ئۇلارنى سايە كۈنىنىڭ ئازابى ھالاك قىلدى ئۇ ھەقىقەتەن بۈيۈك كۈننىڭ ئازابى ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,177,' بۇنىڭدا ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچىلاردىن بولمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,178,' سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,179,' شۈبھىسىزكى، قۇرئان ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,180,' ئاگاھلاندۇرغۇچىلاردىن بولۇشۇڭ ئۈچۈن، ئىشەنچلىك جىبرىئىل ئۇنى سېنىڭ قەلبىڭگە ئېلىپ چۈشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,181,' (قۇرئان) ئوچۇق ئەرەبى تىلىدا (نازىل بولدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,182,' ھەقىقەتەن قۇرئان ئىلگىرىكى (پەيغەمبەرلەرنىڭ) كىتابلىرىدا تىلغا ئېلىنغان');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,183,' (ئابدۇللا ئىبن سالام ۋە ئۇنىڭ ئىمان ئېيتقان ھەمراھلىرىغا ئوخشاش) بەنى ئىسرائىل ئۆلىمالىرىنىڭ قۇرئاننى بىلىشى مۇشرىكلارغا (قۇرئاننىڭ توغرىلىقىنى كۆرسىتىدىغان) دەلىل بولمامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,184,' ئەگەر بىز قۇرئاننى ئەرەب تىلىدا بولمىغان بىر ئادەمگە نازىل قىلغان بولساق، ئۇ قۇرئاننى ئۇلارغا ئوقۇپ بەرسە، ئۇلار ئۇنىڭغا ئىشەنمەيتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,185,' شۇنىڭدەك گۇناھكارلارنىڭ دىللىرىغا قۇرئاننى سالدۇق (ئۇلار قۇرئاننى چۈشىنىپ، ئۇنىڭ پاساھەت ـ بالاغىتىنى ۋە مۆجىزە ئىكەنلىكىنى بىلىپ تۇرۇپ ئىمان ئېيتمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,186,' ئۇلار (اﷲ نىڭ) قاتتىق ئازابىنى كۆرمىگىچە قۇرئانغا ئىشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,187,' ئۇ ئازاب ئۇلارغا ئۇشتۇمتۇت كېلىدۇ، ئۇلار ئۇنىڭ (كەلگەنلىكىنى) تۇيماي قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,188,' (ئۇلارغا تۇيۇقسىز ئازاب كەلگەندە) ئۇلار: «بىزگە (ئىمان ئېيتىۋېلىش ئۈچۈن) مۆھلەت بېرىلەمدۇ؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,189,' ئۇلار بىزنىڭ ئازابىمىزنىڭ بۇرۇنراق كېلىشىنى تىلەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,190,' ئېيتىپ باققىنا! ئەگەر ئۇلارنى (نۇرغۇن) يىللار (نېمەتلىرىمىزدىن) بەھرىمەن قىلساق، ئاندىن ئۇلارغا ئاگاھلاندۇرۇلغان ئازاب كەلسە، ئۇلارنىڭ بەھرىمەن بولغان نېمەتلىرى (ئۇلاردىن ئازابنى دەپئى قىلىشتا) ھېچ نەرسىگە ئەسقاتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,191,' قانداقلىكى شەھەر بولمىسۇن، ۋەز ـ نەسىھەت قىلىش ئۈچۈن پەيغەمبەرلەرنى ئەۋەتمىگىچە، ئۇنى ھالاك قىلمىدۇق، (ئۇلارنى جازالاشتا) بىز زالىم بولمىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,192,' قۇرئاننى شەيتانلار ئېلىپ چۈشكىنى يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,193,' بۇ ئۇلارغا لايىق ئەمەس ئۇلارمۇ (ئۇنىڭغا) قادىر بولالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,194,' شۈبھىسىزكى، ئۇلار (يەنى شەيتانلار) (ئوغرىلىقچە) تىڭشاشتىن مەنئى قىلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,195,' اﷲ تىن باشقا ھېچ ئىلاھقا ئىبادەت قىلمىغىن، ئۇنداق قىلساڭ، ئازاب قىلىنغۇچىلاردىن بولۇپ قالىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,196,' يېقىن خىش – ئەقرىبالىرىڭنى ئاگاھلاندۇغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,197,' ساڭا ئەگەشكەن مۆمىنلەرگە مۇلايىم بولغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,198,' ئەگەر خىش ـ ئەقرىبالىرىڭ ساڭا ئاسىيلىق قىلسا: «مەن سىلەرنىڭ قىلمىشلىرىڭلاردىن ھەقىقەتەن ئادا ـ جۇدا مەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,199,' ناھايىتى غالىب، مېھرىبان اﷲ قا يۆلەنگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,200,' اﷲ سېنى (نامازغا) قوپقىنىڭدا كۆرۈپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,201,' ناماز ئوقۇغۇچىلار ئارىسىدىكى (سەجدىگە بارغانلىق، رۇكۇغا تۇرغانلىق ۋە قىيامدا تۇرغانلىق) ھەرىكىتىڭنى كۆرۈپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,202,' شۈبھىسىزكى، اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,203,' (ئى مۇھەممەد! مەككە كۇففارلىرىغا ئېيتقىنكى) سىلەرگە مەن شەيتانلارنىڭ كىمگە چۈشىدىغانلىقىنى ئېيتىپ بېرەيمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,204,' ئۇلار ھەربىر ئىغۋا توقۇغۇچى، گۇناھكارغا چۈشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,205,' ئۇلار (پەرىشتىلەرنىڭ سۆزلىرىنى ئوغرىلىقچە) ئاڭلايدۇ، ئۇلار (يەنى شەيتانلار) نىڭ تولىسى يالغانچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,206,' شائىرلارغا گۇمراھلار ئەگىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,207,' ئۇلارنىڭ (سۆز) ۋادىلىرىدا تېڭىرقاپ يۈرگەنلىكىنى كۆرمەمسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,208,' ئۇلار قىلمايدىغان نەرسىلىرىنى قىلدۇق دەپ سۆزلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,27,209,' پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان، اﷲ نى كۆپ زىكرى قىلغان، زۇلۇمغا ئۇچرىغاندىن كېيىن ئۆزىنى قوغدىغان شائىرلار بۇنىڭدىن مۇستەسنا، زۇلۇم قىلغۇچىلار ئۇزاققا قالماي قايسى جايغا قايتىدىغانلىقىنى بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,1,'طا، سىن بۇ، قۇرئاننىڭ ۋە روشەن كىتابنىڭ ئايەتلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,2,' مۆمىنلەر ئۈچۈن ھىدايەتتۇر ۋە خۇش خەۋەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,3,' (شۇنداق مۆمىنلەركى) ئۇلار نامازنى ئادا قىلىدۇ، زاكات بېرىدۇ ۋە ئاخىرەتكە جەزمەن ئىشىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,4,' ئاخىرەتكە ئىشەنمەيدىغانلارغا ئۇلارنىڭ ئەمەللىرىنى ھەقىقەتەن چىرايلىق كۆرسەتتۇق، ئۇلار (گۇمراھلىقلىرىدا) تېڭىرقاپ يۈرۈشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,5,' ئۇلار (دۇنيادا) قاتتىق ئازابقا دۇچار بولىدۇ ئاخىرەتتە ئۇلار ئەڭ زىيان تارتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,6,' ساڭا قۇرئان ئەلۋەتتە ھېكمەت بىلەن ئىش قىلغۇچى، ھەممىنى بىلگۈچى اﷲ تەرىپىدىن بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,7,' ئۆز ۋاقتىدا مۇسا ئەھلىيىسىگە: «مەن ھەقىقەتەن ئوت كۆردۈم، مەن ئوتنىڭ يېنىغا بېرىپ (يول توغرۇلۇق) بىرەر خەۋەر ئۇقۇپ كېلەي، ياكى سىلەرنىڭ ئىسسىنىشىڭلار ئۈچۈن ئوتتىن بىر چوغ ئېلىپ كېلەي» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,8,' مۇسا ئوتنىڭ يېنىغا كەلگەندە (مۇنداق) نىدا ئاڭلاندى: «اﷲ ئوتنىڭ يېنىدىكى كىشىنى ۋە ئوتنىڭ ئەتراپىدىكىلەرنى مۇبارەك قىلدى ئالەملەرنىڭ پەرۋەردىگارى اﷲ پاكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,9,' ئى مۇسا! شۈبھىسىزكى، مەن غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ دۇرمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,10,' ھاساڭنى تاشلىغىن!» مۇسا ھاسىسىنىڭ گويا ئەجدىھادەك تېز ھەرىكەتلىنىۋاتقانلىقىنى كۆرگەندە، ئارقىسىغا بۇرۇلۇپ چېكىندى، كەينىگە (يەنى قورققىنىدىن ئەجدىھاغا) قارىيالمىدى (اﷲ ئېيتتى) «ئى مۇسا! قورقمىغىن، مېنىڭ ھۇزۇرۇمدا پەيغەمبەرلەر ئەلۋەتتە قورقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,11,' لېكىن كىمكى (ئۆزىگە) زۇلۇم قىلسا، ئاندىن يامان ئەمەللىرىنى ياخشى ئەمەلگە ئۆزگەرتسە، مەن (ئۇنىڭغا) ناھايىتى مەغپىرەت قىلغۇچىدۇرمەن، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,12,' قولۇڭنى قوينۇڭغا سالغىن، ئۇ ھېچقانداق ئىللەتسىز ئاپئاق بولۇپ چىقىدۇ، (بۇ) مېنىڭ پىرئەۋن ۋە ئۇنىڭ قەۋمىگە ئېلىپ بېرىشىڭ ئۈچۈن بەرگەن توققۇز مۆجىزەمنىڭ ئىچىدىدۇر، ئۇلار ھەقىقەتەن پاسىق قەۋم ئىدى »');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,13,' ئۇلارغا بىزنىڭ نۇرغۇن روشەن ئايەتلىرىمىز نازىل بولغان چاغدا، ئۇلار: «بۇ روشەن سېھىردۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,14,' ئۇلار ئۇ ئايەتلەرنى ئىچىدە ئېتىراپ قىلدى، لېكىن ئۇلار ئۇنى زۇلۇم ۋە تەكەببۇرلۇق قىلىش يۈزىسىدىن ئىنكار قىلدى بۇزغۇنچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,15,' بىز ھەقىقەتەن داۋۇدقا، سۇلايمانغا (دۇنيا ۋە دىن ئىلىملىرىدىن كەڭ) ئىلىم ئاتا قىلدۇق، ئۇلار ئېيتتى: «جىمى ھەمدۇسانا بىزنى نۇرغۇن بەندىلىرىدىن ئارتۇق قىلغان اﷲ قا خاستۇر!»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,16,' سۇلايمان (پەيغەمبەرلىكتە، ئىلىمدە، پادىشاھلىقتا ئاتىسى) داۋۇدقا ۋارىسلىق قىلدى ئۇ: «ئى ئىنسانلار! بىزگە قۇشلارنىڭ تىلى تەلىم بېرىلدى (دۇنيانىڭ نېمەتلىرىدىن) ھەممە نەرسە ئاتا قىلىندى، بۇ ئەلۋەتتە (اﷲ نىڭ) روشەن ئېھسانىدۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,17,' سۇلايماننىڭ جىنلاردىن، ئىنسانلاردىن ۋە قۇشلاردىن بولغان قوشۇنلىرى توپلاندى (سۇلايمان ئەلەيھىسسالام ئۇلارنىڭ ئالدىدا دەبدەبە بىلەن ماڭاتتى) ئۇلار تەرتىپلىك ئورۇنلاشتۇرۇلغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,18,' ئۇلار (شامدىكى بىر) چۈمۈلىلەر ۋادىسىغا يېتىپ كەلگەندە، بىر چۈمۈلە ئېيتتى: «ئى چۈمۈلىلەر! ئۇۋىلىرىڭلارغا كىرىپ كېتىڭلار، سۇلايمان ۋە ئۇنىڭ قوشۇنى ئۇقماستىن سىلەرنى يەنچىۋەتمىسۇن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,19,' سۇلايمان چۈمۈلىنىڭ سۆزىدىن تەبەسسۇم قىلىپ كۈلدى ۋە ئېيتتى: «پەرۋەردىگارىم! سەن مېنى ماڭا ۋە ئاتا ـ ئانامغا بەرگەن نېمىتىڭگە شۈكۈر قىلىشقا، سەن رازى بولىدىغان ياخشى ئەمەلنى قىلىشقا مۇۋەپپەق قىلغىن، رەھمىتىڭ بىلەن مېنى ياخشى بەندىلىرىڭ قاتارىغا كىرگۈزگىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,20,' ئۇ قۇشلارنى كۆزدىن كەچۈرگەندىن كېيىن ئېيتتى: «ماڭا نېمە بولدى؟ ھۆپۆپنى كۆرمەيمەنغۇ! يا ئۇ يوقاپ كەتتىمۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,21,' ئۇنى چوقۇم قاتتىق جازالايمەن، يا ئۇنى چوقۇم بوغۇزلايمەن، يا چوقۇم (يوقاپ كەتكەنلىكىنى ئاقلايدىغان) بىر روشەن دەلىل كەلتۈرىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,22,' ئۇزاق ئۆتمەي ھۆپۆپ كەلدى، ئۇ ئېيتتى: «مەن سەن بىلمىگەن ئىشنى بىلىپ (كەلدىم)، ساڭا مەن سەبەدىن (يەنى يەمەندىكى سەبە شەھىرىدىن) بىر مۇھىم خەۋەر ئېلىپ كەلدىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,23,' مەن ھەقىقەتەن ئۇلارغا (يەنى سەبە ئاھالىسىگە) بىر ئايالنىڭ (يەنى بىلقىسنىڭ) پادىشاھلىق قىلىۋاتقانلىقىنى كۆردۈم، ئۇنىڭغا (ئۆزىگە ۋە سەلتەنەتىگە كېرەكلىك) ھەممە نەرسە بېرىلگەن ئىكەن، ئۇ چوڭ ئەرشكە ئىگە ئىكەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,24,' ئۇنىڭ ۋە قەۋمىنىڭ اﷲ نى قويۇپ قۇياشقا چوقۇنىدىغانلىقىنى بايقىدىم، شەيتان ئۇلارغا قىلمىشلىرىنى (يەنى اﷲ نى قويۇپ، قۇياشقا چوقۇنغانلىقلىرىنى) چىرايلىق كۆرسەتتى، ئۇلارنى توغرا يولدىن توستى، ئۇلار ھىدايەت تاپمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,25,' ئۇلار ئاسمانلاردىكى ۋە زېمىندىكى سىرلەرنى ئاشكارىلىغۇچى، سىلەرنىڭ يوشۇرۇن ۋە ئاشكارا ئىشىڭلارنى بىلىپ تۇرغۇچى اﷲ قا سەجدە قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,26,' اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇ بۈيۈك ئەرشنىڭ پەرۋەردىگارىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,27,' سۇلايمان ئېيتتى: «سەن راست دەۋاتامسەن، يا يالغانمۇ؟ قارايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,28,' بۇ خېتىمنى ئېلىپ بېرىپ ئۇلارغا تاشلىغىن، ئاندىن ئۇلاردىن نېرىراق (جايدا يوشۇرۇنۇپ) تۇر، ئۇلارنىڭ قانداق جاۋاپ قايتۇرىدىغانلىقىغا قارىغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,29,' سىلەر ماڭا ھاكاۋۇرلۇق قىلماڭلار، مېنىڭ ئالدىمغا مۇسۇلمان بولغان ھالدا كېلىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,30,' ئۇ (يەنى بىلقىس) ئېيتتى: «ئى ئۇلۇغلار! مېنىڭ (بۇ) ئىشىمدا مەسلىھەت بېرىڭلار، سىلەرنى ئۈستىدە قويماي تۇرۇپ ھېچ ئىشنى بېكىتكىنىم يوق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,31,' ئۇلار ئېيتتى: «بىز كۈچلۈك ۋە جەڭگىۋارمىز، ئىش سېنىڭ ئىختىيارىڭدىدۇر، (بىزنى) نېمىگە بۇيرۇيدىغانلىقىڭنى (ئويلاپ) كۆرگىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,32,' ئۇ (يەنى بىلقىس) ئېيتتى: «شۈبھىسىزكى، پادىشاھلار بىرەر شەھەرگە ھۇجۇم قىلىپ كىرسە، ئۇنى خاراب قىلىدۇ، شەھەرنىڭ مۆتىۋەر ئادەملىرىنى (ئۆلتۈرۈش، ئەسىرگە ئېلىش ۋە سۈرگۈن قىلىش بىلەن) خار قىلىدۇ، ئۇلارمۇ شۇنداق قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,33,' مەن چوقۇم ئۇلارغا سوۋغا ئەۋەتىمەن، ئەلچىلەرنىڭ نېمە خەۋەر ئېلىپ كېلىدىغانلىقىغا قارايمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,34,' ئەلچى سۇلايماننىڭ ئالدىغا يېتىپ كەلگەندە، سۇلايمان ئېيتتى: «سىلەر ماڭا مال بىلەن ياردەم قىلماقچىمۇ؟ اﷲ نىڭ ماڭا بەرگەنلىرى سىلەرنىڭ بەرگەنلىرىڭلاردىن ياخشىدۇر، بەلكى سىلەر سوغاڭلار بىلەن كۆرەڭلەپ كېتىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,35,' سەن قايتىپ كەتكىن، ئۇلارنىڭ ئۈستىگە ئۇلار تاقابىل تۇرالمايدىغان بىر قوشۇن بىلەن چوقۇم بارىمىز، ئۇلارنى يۇرتىدىن خار، كەمسىتىلگەن ھالدا چوقۇم چىقىرىۋېتىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,36,' سۇلايمان ئېيتتى: «ئى ئۇلۇغلار! ئۇلار مېنىڭ قېشىمغا مۇسۇلمان بولۇپ كېلىشتىن بۇرۇن، (سىلەردىن) كىم ئۇنىڭ تەختىنى ئېلىپ كېلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,37,' جىندىن بولغان ئىفرىت ئېيتتى: «مەن ئۇنى ئورنۇڭدىن تۇرۇشتىن بۇرۇن ساڭا ئېلىپ كېلىمەن، مەن ئۇنى ئېلىپ كېلىشكە ئەلۋەتتە قادىرمەن، ئىشەنچلىكمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,38,' (نازىل بولغان) كىتابنى چوڭقۇر بىلىدىغان زات (يەنى ئاسەف ئىبن بۇرخىيا): «ئۇنى مەن ساڭا كۆزۈڭنى يۇمۇپ ئاچقۇچە ئېلىپ كېلىمەن» دېدى (ئۇ دۇئا قىلىۋېدى، تەخت دەرھال ئالدىدا ھازىر بولدى) سۇلايمان تەختنىڭ يېنىدا تۇرغانلىقىنى كۆرگەندە: «بۇ پەرۋەردىگارىمنىڭ (ماڭا قىلغان) ئېھسانىدۇر، ئۇ شۈكۈر قىلامدىم، يا تۇز كورلۇق قىلامدىم، بۇنىڭ بىلەن مېنى سىنىدى، كىمكى شۈكۈر قىلىدىكەن، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈن شۈكۈر قىلىدۇ، كىمكى تۇزكورلۇق قىلىدىكەن، (بىلىش كېرەككى) ھەقىقەتەن پەرۋەردىگارىم (ئۇنىڭ شۈكۈر قىلىشىدىن بىھاجەتتۇر)، (اﷲ نىڭ) كەرىمى كەڭدۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,39,' سۇلايمان (بىلقىسنىڭ يېتىپ كېلىشى يېقىنلاشقان ۋاقىتتا ئۇنىڭ ئەقلىنى سىناش يۈزىسىدىن) ئېيتتى: «ئۇنىڭ (يەنى بىلقىسنىڭ) تەختىنىڭ (شەكلىنى) ئۆزگەرتىڭلار، ئۇ تەختىنى تونۇمدۇ، تونىمامدۇ، قارايمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,40,' بىلقىس كەلگەندە (ئۇنىڭغا): «سېنىڭ تەختىڭ مۇشۇنداقمۇ؟» دېيىلدى ئۇ: «شۇدەك تۇرىدۇ» دېدى (سۇلايمان اﷲ نىڭ نېمىتىنى سۆزلەش يۈزىسىدىن ئېيتتى) ئۇنىڭدىن (يەنى بىلقىستىن) بۇرۇن بىزگە (اﷲ نى ۋە اﷲ نىڭ قۇدرىتىنى بىلدۈرىدىغان) ئىلىم بېرىلدى، بىز مۇسۇلمان بولدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,41,' ئۇنى اﷲ نى قويۇپ چوقۇنغان نەرسىلىرى (اﷲ قا ئىبادەت قىلىشتىن) توستى چۈنكى ئۇ كاپىر قەۋمدىن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,42,' ئۇنىڭغا (يەنى بىلقىسقا): «سارايغا كىرگىن» دېيىلدى، ئۇ (يەنى بىلقىس) ساراينى چوڭ سۇ دەپ گۇمانلىنىپ (كىيىمىنى كۆتۈرۈپ) ئىككى پاچىقىنى ئاچتى، سۇلايمان ئېيتتى: «شۈبھىسىزكى، ئۇ ئەينەكتىن ياسالغان سارايدۇر» ئۇ (يەنى بىلقىس) ئېيتتى: «پەرۋەردىگارىم، مەن ھەقىقەتەن (اﷲ قا شېرىك كەلتۈرۈپ، قۇياشقا چوقۇنۇش بىلەن) ئۆزۈمگە زۇلۇم قىلدىم، سۇلايمان بىلەن بىللە ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا بويسۇندۇم (يەنى سۇلايماننىڭ دىنىغا ئەگىشىپ ئىسلامغا كىردىم)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,43,' بىز ھەقىقەتەن سەمۇدقا (يەنى سەمۇد قەبىلىسىگە نەسەب جەھەتتىن) قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ) ئەۋەتتۇق، (سالىھ) «اﷲ قا ئىبادەت قىلىڭلار» (دېدى)، ناگاھان ئۇلار جېدەللىشىدىغان (يەنى دىن بارىسىدا جىدەللىشىدىغان مۆمىنلەر ۋە كاپىرلاردىن ئىبارەت) ئىككى گۇرۇھقا بۆلۈنۈپ كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,44,' سالىھ ئېيتتى: «ئى قەۋمىم، سىلەر نېمىشقا رەھمەتتىن بۇرۇن ئازابنىڭ كېلىشىنى تىلەيسىلەر؟ سىلەر رەھمەتكە ئېرىشىشىڭلار ئۈچۈن نېمىشقا اﷲ تىن مەغپىرەت تەلەپ قىلمايسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,45,' ئۇلار ئېيتتى: «(بىزگە كەلگەن قەھەتچىلىك) سېنىڭ ۋە سەن بىلەن بولغان كىشىلەرنىڭ شۇملۇقىدىن كەلدى» سالىھ ئېيتتى: «سىلەرگە كېلىدىغان ياخشى يامانلىق اﷲ تەرىپىدىن كېلىدۇ، بەلكى سىلەرنى اﷲ سىنايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,46,' شەھەردە (يەنى ھىجرىدە) يەر يۈزىدە بۇزغۇنچىلىق قىلىدىغان، ئىسلاھ قىلمايدىغان توققۇز نەپەر كىشى بار ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,47,' ئۇلار: «ئۆز ئارا اﷲ بىلەن قەسەم قىلىڭلار» دېدى ئۇلار ئېيتتى: «بىر كېچىدە سالىھنى ۋە ئۇنىڭ تەۋەلىرىنى چوقۇم ئۆلتۈرەيلى، ئاندىن چوقۇم ئۇنىڭ (يەنى ئۇلارنىڭ) ئىگىسىگە، ئۇ ئۆلتۈرۈلگەن چاغدا بىز ئۈستىدە ئەمەس ئىدۇق، بىز ھەقىقەتەن راستچىلمىز، دەيلى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,48,' ئۇلار (سالىھقا قارشى) سۇيىقەست پىلانلىدى ئۇلارنى (ئۇلارنىڭ ھالاك بولۇشىنى تېزلىتىش ئۈچۈن)سۇيىقەستى ئۈچۈن تۇيۇقسىز جازالىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,49,' ئۇلارنىڭ سۇيىقەستىنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قارىغىنكى، ئۇلارنى ۋە ئۇلارنىڭ قەۋمىنى پۈتۈنلەي ھالاك قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,50,' ئۇلار زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئەنە ئۇلارنىڭ ئۆيلىرى (ئادەمزاتتىن) خالى بولۇپ قالدى اﷲ نىڭ (قۇدرىتىنى) بىلىدىغان قەۋم ئۈچۈن بۇنىڭدا (چوڭ) ئىبرەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,51,' (سالىھ بىلەن) ئىمان ئېيتقان ۋە تەقۋادارلىق قىلغانلارنى (ئازابتىن) قۇتقۇزدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,52,' (لۇتنىڭ قىسسىسىنى بايان قىلغىن) ئەينى زاماندا لۇت ئۆزىنىڭ قەۋمىگە ئېيتتى: «سىلەر بىلىپ تۇرۇپ قەبىھ ئىشلارنى قىلامسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,53,' سىلەر ئاياللارنى قويۇپ، جىنسىي تەلىۋىڭلارنى ئەرلەر بىلەن قاندۇرامسىلەر؟ بەلكى سىلەر نادان قەۋمسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,54,' ئۇلارنىڭ بىردىن ـ بىر جاۋابى: «لۇتنىڭ تەۋەلىرىنى شەھىرىڭلاردىن ھەيدەپ چىقىرىڭلار، ئۇلار پاك كىشىلەردۇر» دېيىشتىن ئىبارەت بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,55,' بىز ئۇنى ۋە خوتۇنىدىن باشقا تەۋەلىرىنى قۇتقۇزدۇق، تەقدىرىمىز بىلەن ئۇنى (يەنى خوتۇنىنى ئازابقا) قالغۇچىلاردىن قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,56,' ئۇلارنىڭ ئۈستىگە بىز يامغۇر (يەنى تاش) ياغدۇردۇق ئاگاھلاندۇرۇلغۇچىلارغا ياغدۇرۇلغان يامغۇر نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,57,' «جىمى ھەمدۇسانا اﷲ قا خاستۇر! ئۇنىڭ (پەيغەمبەرلىككە) تاللىغان بەندىلىرىگە ئامانلىق تىلەيمەن!» دېگىن اﷲ ياخشىمۇ؟ ياكى ئۇلارنىڭ شېرىك كەلتۈرگەن بۇتلىرى ياخشىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,58,' ئاسمانلارنى ۋە زېمىننى ياراتقان، سىلەرگە بۇلۇتتىن يامغۇر ياغدۇرۇپ بەرگەن، ئۇنىڭ بىلەن گۈزەل باغلارنى يېتىشتۈرۈپ بەرگەن كىم؟ ئۇ باغلارنىڭ دەرەخلىرىنى ئۆستۈرۈش سىلەرنىڭ قولۇڭلاردىن كەلمەيدۇ، اﷲ تىن باشقا ئىلاھ بارمۇ؟ باشقا ئىلاھ يوقتۇر، ئۇلار (ھەقىقەتتىن) بۇرۇلۇپ كەتكەن قەۋمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,59,' زېمىننى)ئىنسانلارغا ۋە ھايۋانلارغا) تۇرالغۇ قىلغان، ئۇنىڭ تۈرلۈك تەرەپلىرىدە دەريالارنى ئاققۇزغان، ئۇنىڭ ئۈستىدە (تەۋرەپ كەتمەسلىكى ئۈچۈن)تاغلارنى ئورناتقان، ئىككى دېڭىز ئارىسىدا (يەنى تاتلىق سۇ بىلەن شورلۇق سۇ ئارىسىدا ئارىلىشىپ كەتمەسلىكى ئۈچۈن)توساقلارنى قىلغان كىم؟ اﷲ تىن باشقا ئىلاھ بارمۇ؟ (باشقا ئىلاھ يوق) ئۇلار (يەنى مۇشرىكلار) نىڭ تولىسى بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,60,' بېشىغا كۈن چۈشكەن ئادەم دۇئا قىلسا (ئۇنىڭ دۇئاسىنى) ئىجابەت قىلىدىغان، ئۇنىڭ بېشىغا كەلگەن ئېغىرچىلىقنى كۆتۈرۈۋېتىدىغان ۋە سىلەرنى زېمىننىڭ ئورۇنباسارلىرى قىلغان كىم؟ اﷲ تىن باشقا ئىلاھ بارمۇ؟ سىلەر ئازغىنا ۋەز ـ نەسىھەت ئالىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,61,' قۇرۇقلۇقنىڭ ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرىدا سىلەرگە يول كۆرسىتىپ بېرىدىغان، رەھمىتىدىن (يەنى يامغۇر ياغدۇرۇپ بېرىشتىن) ئىلگىرى شاماللارنى بېشارەت قىلىپ ئەۋەتىدىغان كىم؟ اﷲ تىن باشقا ئىلاھ بارمۇ؟ (ھېچ ئىلاھ يوقتۇر) اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,62,' (ئىنساننى) دەسلەپتە خەلق ئەتكەن، ئاندىن ئۇنى تىرىلدۈرىدىغان كىم؟ سىلەرگە ئاسماندىن ۋە زېمىندىن رىزىق بېرىدىغان كىم؟ اﷲ تىن باشقا ئىلاھ بارمۇ؟ (ھېچ ئىلاھ يوقتۇر) ئېيتقىنكى، «)ئەگەر دەۋايىڭلاردا) راستچىل بولىدىغان بولساڭلار، دەلىلىرىڭلارنى كەلتۈرۈڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,63,' اﷲ تىن بۆلەك ئاسمانلاردىكى ۋە زېمىندىكىلەر غەيبنى بىلمەيدۇ، ئۇلار(يەنى خالايىق) قاچان تىرىلىدىغانلىقلىرىنى بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,64,' ئۇلارنىڭ ئاخىرەت توغرىسىدىكى ئىلمى پىشىپ يېتىلدىمۇ؟ ئۇنداق ئەمەس، ئۇلار ئاخىرەت توغرۇلۇق گۇماندىدۇر، ئۇلار ئاخىرەت توغرۇلۇق كوردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,65,' كاپىرلار (يەنى قايتا تىرىلىشىنى ئىنكار قىلغۇچى مەككە مۇشرىكلىرى)ئېيتتى: «بىز ۋە بىزنىڭ ئاتا ـ بوۋىلىرىمىز توپا بولۇپ كەتكەندىن كېيىن (قەبرىمىزدىن تىرىلىپ) چوقۇم چىقىرىلامدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,66,' ھەقىقەتەن بىزگە ۋە ئاتا ـ بوۋىلىرىمىزغا ئىلگىرى (قايتا تىرىلىش) ۋەدە قىلىنغان ئىدى بۇ پەقەت بۇرۇنقىلارنىڭ قىسسىلىرىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,67,' سەن (بۇ كۇففارلارغا) ئېيتقىن: «زېمىندا سەير قىلىڭلار، گۇناھكارلار (يەنى پەيغەمبەرلەرنى ئىنكار قىلغۇچىلار)نىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار»)ئۇلارنى اﷲ ھالاك قىلمىدىمۇ؟ ئىلگىرىكى كۇففارلارنىڭ بېشىغا كەلگەن كۈن كېيىنكى كۇففارلارنىڭ بېشىغىمۇ كېلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,68,' (ئى مۇھەمممەد!) سەن ئۇلارنىڭ (ئىمان ئېيتمىغانلىقىدىن) قايغۇرمىغىن، ئۇلارنىڭ ھىيلە ـ مىكرىدىن ئىچىڭ پۇشمىسۇن (اﷲ سېنى ئۇلارنىڭ شەررىدىن ساقلايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,69,' ئۇلار: «ئەگەر (سۆزۈڭلاردا)راستچىل بولساڭلار، (سىلەر بىزگە ۋەدە قىلغان)ئازاب قاچان چۈشىدۇ» دېيىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,70,' «سىلەر ئالدىراپ كەتكەن ئازابنىڭ بىر قىسمى سىلەرگە يېقىنلاشقان بولۇشى مۇمكىن» دېگىن (بۇ بەدرى سوقۇشىدا ئۇلارنىڭ ئۆلتۈرۈلۈشى ۋە ئەسىرگە ئېلىنىشىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,71,' پەرۋەردىگارىڭ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر، لېكىن ئۇلارنىڭ تولىسى (پەرۋەردىگارىغا)شۈكۈر قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,72,' پەرۋەردىگارىڭ ئۇلارنىڭ دىللىرىدا يوشۇرغانلىرىنى ۋە ئاشكارا قىلغانلىرىنى (يەنى ئۇلارنىڭ پەيغەمبەر ئەلەيھىسسالامغا قارىتا ئاداۋىتىنى ۋە سۈيىقەستىنى)شەك ـ شۈبھىسىز بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,73,' ئاسمان زېمىندا لەۋھۇلمەھپۇزغا يېزىلمىغان ھېچبىر سىر يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,74,' (پەيغەمبەرلەرنڭ تۈگەنچىسى مۇھەممەد ئەلەيھىسسالامغا نازىل بولغان) بۇ قۇرئان ھەقىقەتەن ئىسرائىل ئەۋلادىغا ئۇلارنىڭ ئىختىلاپ قىلغان نەرسىلىرىنىڭ كۆپىنى بايان قىلىپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,75,' قۇرئان مۆمىنلەر ئۈچۈن ھەقىقەتەن ھىدايەتتۇر ۋە رەھمەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,76,' شۈبھىسىزكى، پەرۋەردىگارىڭ ئۇلار (يەنى بەنى ئىسرائىل ئەۋلادى) نىڭ ئارىسىدا (قىيامەت كۈنى) ئادىل ھۆكۈم چىقىرىدۇ، اﷲ غالىبتۇر، (بەندىلىرىنىڭ ئىشلىرىنى) بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,77,' اﷲ قا تەۋەككۈل قىلغىن (يەنى ئىشىڭنى اﷲ قا تاپشۇرغىن، ھەممە ئىشىڭدا اﷲ قا يۆلەنگىن، اﷲ ساڭا مەدەتكاردۇر)، (ئى مۇھەممەد) شۈبھىسىزكى، سەن روشەن ھەق (دىن)دىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,78,' سەن ئۆلۈكلەرگە ۋە يۈز ئۆرۈگەن گاسلارغا (يەنى دىللىرىنىڭ ئۆلۈكلىكىدە ئۆلۈكلەرگە ۋە ھەقنى ئاڭلىماسلىقتا گاسلارغا ئوخشايدىغان كۇففارلارغا)دەۋەتنى ئاڭلىتالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,79,' سەن (دىلى) كورلارنى گۇمراھلىقتىن ئايرىپ، ھىدايەت قىلالمايسەن، سەن (دەۋىتىڭنى)پەقەت بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتىپ مۇسۇلمان بولغانلارغىلا ئاڭلىتالايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,80,' ئۇلار (يەنى كۇففارلار) غا ئالدىنئالا ئېيتىلغان سۆز ئىشقا ئاشقاندا (يەنى ئازاپ ۋە قىيامەت يېقىنلاشقاندا)، ئۇلارغا زېمىندىن بىر تۈرلۈك ھايۋاننى چىقىرىمىزكى، ئۇ ئۇلارغا ئىنسانلارنىڭ بىزنىڭ ئايەتلىرىمىزگە ئىشەنمىگەنلىكىنى سۆزلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,81,' ئۇ كۈندە ھەر ئۈممەتنىڭ ئىچىدىن بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلاردىن بىر توپ ئادەمنى)ھېساب ئېلىش ۋە جازالاش ئۈچۈن) يىغىمىز (ئالدى ـ كەينى يىغىلغۇچە) ئۇلار توختىتىپ تۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,82,' ئۇلار)اﷲ نىڭ دەرگاھىغا) كەلگەن چاغلىرىدا، اﷲ ئۇلارغا: «سىلەر تولۇق بىلمەي تۇرۇپ مېنىڭ ئايەتلىرىمنى ئىنكار قىلدىڭلارمۇ؟ سىلەر (ئەمرى قىلىنغان نەرسىلەردىن) زادى نېمىلەرنى قىلدىڭلار؟ » دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,83,' ئۇلار)ئۆزلىرىگە)زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئۇلارغا ئازاب ھەقلىق بولدى ئۇلار (ئۆزرىلىرى بولمىغانلىقى ئۈچۈن) سۆز قىلالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,84,' ئۇلارغا كېچىنى ئارام ئالىدىغان (ۋاقىت) قىلغانلىقىمىزنى، كۈندۈزنى (ھاياتلىق يولىدا ھەرىكەتلىنىش ئۈچۈن) يورۇق قىلغانلىقىمىزنى ئۇلار ئۇقمامدۇ؟ بۇنىڭدا ئىمان ئېيتىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان)نۇرغۇن ئالامەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,85,' ئۇ كۈندە سۇر چىلىنىدۇ، اﷲ خالىغانلاردىن (يەنى پەرىشتىلەر، پەيغەمبەرلەر ۋە شېھىتلاردىن) باشقا ئاسمانلاردىكى ۋە زېمىندىكى ھەممىنى قورقۇنچ باسىدۇ، ھەممە اﷲ قا بويسۇنغان ھالدا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,86,' تاغلارنى تۇرغۇن ھالەتتە گۇمان قىلىسەن، ھالبۇكى، ئۇلار بۇلۇتتەك چۆگىلەپ تۇرىدۇ، (بۇ) ھەممە نەرسىنى پۇختا ياراتقان اﷲ نىڭ ھۈنىرىدۇر، اﷲ ھەقىقەتەن سىلەرنىڭ قىلمىشلىرىڭلاردىن تولۇق خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,87,' ياخشى ئىش قىلغانلار قىلغان ياخشىلىقىدىن ئوبدانراق مۇكاپاتقا ئېرىشىدۇ، ئۇلار بۇ كۈندە قورقۇنجىدىن ئەمىن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,88,' يامان ئىش قىلغانلار دوزاخقا يۈزى بىلەن تاشلىنىدۇ، سىلەر پەقەت قىلمىشىڭلارغا يارىشا جازالىنىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,89,' (ئى مۇھەممەد! ئېيتقىنكى)«مەن پەقەت اﷲ ھۈرمەتلىك قىلغان بۇ شەھەر (يەنى مەككە مۇكەررەمە) نىڭ پەرۋەردىگارىغا ئىبادەت قىلىشقا بۇيرۇلدۇم، ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، مەن مۇسۇلمانلاردىن بولۇشقا، قۇرئان ئوقۇشقا بۇيرۇلدۇم» كىمكى ھىدايەت تاپىدىكەن، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈندۇر، كىمكى ئازىدىكەن (ئۇنىڭ ۋابالى ئۆزىگە بولىدۇ) سەن (ئۇنىڭغا)ئېيتقىنكى، «مەن پەقەت ئاگاھلاندۇرغۇچىلاردىنمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,28,90,' (ئى مۇھەممەد!) ئېيتقىنكى، « جىمى ھەمدۇسانا اﷲ قا خاستۇر، اﷲ سىلەرگە (قۇدرىتىنى كۆرسىتىپ بېرىدىغان) ئالامەتلىرىنى كۆرسىتىدۇ، سىلەر ئۇلارنى (تونۇش پايدا بەرمەيدىغان چاغدا) تونۇيسىلەر، پەرۋەردىگارىڭ سىلەرنىڭ قىلغان ئەمەللىرىڭلاردىن غاپىل ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,1,'طاسن، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,2,' بۇ، روشەن كىتاب (يەنى قۇرئان) ئايەتلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,3,' ئىمان ئېيتقان قەۋم ئۈچۈن ساڭا مۇسا ۋە پىرئەۋن قىسسىسىنى ھەقلىق ئاساستا ئوقۇپ بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,4,' شۈبھىسىزكى، پىرئەۋن (مىسىر) زېمىنىدا (زومىگەرلىكتە) ھەددىدىن ئاشتى، ئاھالىسىنى بۆلەكلەرگە بۆلۈپ، ئۇلاردىن بىر تائىپە (يەنى بەنى ئىسرائىل) نى بوزەك قىلدى ئۇلاردىن ئوغۇللىرىنى ئۆلتۈرۈپ، قىزلىرىنى)خىزمەتكە سېلىش ئۈچۈن) تىرىك قالدۇردى، پىرئەۋن ھەقىقەتەن بۇزغۇنچىلاردىن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,5,' بىز (مىسىر) زېمىنىدا بوزەك قىلىنغانلارغا مەرھەمەت قىلىشنى ئىرادە قىلىمىز، ئۇلارنى يولباشچىلاردىن قىلىشنى، ئۇلارنى پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ (مۈلكىگە) ۋارىس قىلىشنى ئىرادە قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,6,' ئۇلارنى مىسىر زېمىنىد اكۈچ ـ قۇۋۋەتكە ئىگە قىلماقچىمىز، بىز پىرئەۋنگە، (ۋەزىرى) ھامانغا ۋە ئۇلارنىڭ قوشۇنىغا ئۇلار (يەنى بوزەك قىلىنغانلار) دىن قورقىدىغان نەرسىنى (يەنى بەنى ئىسرائىلنى مىسىرغا ھۆكۈمران قىلىشنى) كۆرسىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,7,' مۇسانىڭ ئانىسىغا ئىلھام بىلەن بىلدۈردۇقكى: «مۇسانى ئېمىتكىن، ئۇنىڭ زىيانكەشلىككە ئۇچرىشىدىن قورقساڭ، ئۇنى(ساندۇققا سېلىپ) دەرياغا (يەنى نىل دەرياسىغا) تاشلىغىن، ئۇنى(ھالاك بولارمىكىن دەپ) قورقمىغىن، (ئۇنىڭ پىراقىدىن) قايغۇرمىغىن، ئۇنى ساڭا چوقۇم قايتۇرىمىز ۋە ئۇنى پەيغەمبەرلەردىن قىلىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,8,' پىرئەۋن نىڭ ئائىلىسىدىكىلەر مۇسانى ئاقىۋەت ئۆزلىرىگە دۈشمەن، ۋە خاپىلىق (نىڭ مەنبەسى) قىلىش ئۈچۈن (نىل دەرياسىدىن)سۈزۈۋالدى شۈبھىسىزكى، پىرئەۋن، ھامان ۋە ئۇلارنىڭ قوشۇنلىرى خاتالاشقان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,9,' پىرئەۋن نىڭ ئايالى: «(بۇ بالا) ماڭا ۋە ساڭا كۆزنۇرى، (يەنى خۇشاللىق) بولسۇن، ئۇنى ئۆلتۈرمەڭلار، بەلكى ئۇ بىزگە پايدا يەتكۈزەر ياكى ئۇنى ئوغۇل قىلىۋالارمىز» دېدى ھالبۇكى، ئۇلار (پىرئەۋن نىڭ ۋە ئۇنىڭ ياردەمچىلىرىنىڭ ھالاكىتىنىڭ مۇسانىڭ قولىدا بولىدىغانلىقىنى) ئۇقمايتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,10,' مۇسانىڭ ئانىسى (مۇسانىڭ پىرئەۋتننىڭ قولىغا چۈشۈپ قالغانلىقىنى ئاڭلاپ) ئەس ـ ھوشىنى يوقاتتى، (ئۇنىڭ اﷲ نىڭ بالىنى قايتۇرۇش ۋەدىسىگە) ئىشەنگۈچىلەردىن بولۇشى ئۈچۈن، ئۇنىڭ كۆڭلىنى خاتىرجەم قىلمىغان بولساق، ئۇ بالىنى ئاشكارىلاپ قويغىلى تاس قالغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,11,' ئانىسى مۇسانىڭ ھەمشىرىسىگە: «(خەۋەرنى ئۇقۇش ئۈچۈن) مۇساغا ئەگەشكىن» دېدى ھەمشىرىسى مۇسانى يىراقتىن كۆرۈپ تۇردى ھالبۇكى، ئۇلار (ئۇنى) تونۇمايتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,12,' (ئۆز ئانىسى كېلىشتىن) ئىلگىرى مۇسانى سۈت ئەمگۈزگۈچى ئاياللارنىڭ ئېمىتىشىدىن توستۇق، مۇسانىڭ ھەمشىرىسى: «سىلەرگە مۇسانى باقىدىغان ئوبدان بىر ئائىلىنى كۆرسىتىپ قويايمۇ؟ ئۇلار مۇسانى ئىخلاس بىلەن باقىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,13,' ئانىسىنىڭ خۇشال بولۇشى، قايغۇرماسلىقى ۋە اﷲ نىڭ ۋەدىسىنىڭ ھەق ئىكەنلىكىنى بىلىشى ئۈچۈن بىز مۇسانى ئۇنىڭغا قايتۇردۇق، لېكىن ئىنسالارنىڭ تولىسى (اﷲ نىڭ ۋەدىسىنىڭ ھەق ئىكەنلىكىنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,14,' مۇسا ئۆسۈپ يېتىلگەندە، ئەقلى توشقاندا ئۇنىڭغا پەيغەمبەرلىكنى ۋە ئىلىمنى ئاتا قىلدۇق، بىز ياخشىلارغا مۇشۇنداق مۇكاپات بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,15,' مۇسا شەھەر ئاھالىسىدىن ھېچ كىشىنى كۆرگىلى بولمايدىغان بىر ۋاقىتتا (يەنى چۈشلۈك ئۇيقىدىكى چاغدا) شەھەرگە كىردى، ئۇ شەھەردە ئىككى كىشىنىڭ ئۇرۇشۇۋاتقانلىقىنى كۆردى (ئۇرۇشۇۋاتقانلاردىن) بىرى ئۆز قەۋمىدىن بولسا، يەنە بىرى ئۇنىڭ دۈشمىنى ئىدى ئۆز قەۋمىدىن بولغان ئادەم دۈشمىنىگە قارشى ئۇنىڭدىن ياردەم تىلىدى مۇسا ئۇنى مۇشت بىلەن بىرنى ئۇرۇپ ئۆلتۈرۈپ قويدى مۇسا ئېيتتى: «بۇ شەيتاننىڭ ئىشىدۇر، شۈبھىسىزكى، شەيتان ئازدۇرغۇچى ئاشكارا دۈشمەندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,16,' مۇسا ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن ئۆزۈمگە زۇلۇم قىلدىم ماڭا مەغپىرەت قىلغىن» اﷲ ئۇنىڭغا مەغپىرەت قىلدى، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,17,' مۇسا ئېيتتى: «پەرۋەردىگارىم! (ماڭا مەغپىرەت قىلغانلىق) نېمىتىڭ سەۋەبى بىلەن مەن ھەرگىز گۇناھكارلارغا ياردەمچى بولمايمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,18,' مۇسا ئەتىسى ئەتىگەندە قورقۇنچ ئىچىدەئەتراپىغا قاراپ شەھەر ئارىلاپ يۈرەتتى، ناگاھان تۈنۈگۈن ئۇنىڭدىن ياردەم تىلىگەن ئادەم (بىر قىبتى بىلەن ئۇرۇشۇپ تۇرغان ھالدا) مۇسادىن يەنە ياردەم تىلىدى، مۇسا ئۇنىڭغا: «سەن ھەقىقەتەن ئاشكارا گۇمراھ ئىكەنسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,19,' مۇسا ئىككىسىنىڭ (ئورتاق) دۈشمىنى بولغان ئادەم (يەنى قىبتى) گە قول ئۇزاتماقچى بولغاندا، ياردەم تىلىگەن ئادەم: «ئى مۇسا! سەن تۈنۈگۈن بىر ئادەمنى ئۆلتۈرگەندەك مېنى ئۆلتۈرمەكچىمۇسەن، سەن زېمىندا تۈزىگۈچىلەردىن بولۇشنى خالىماي، پەقەت زومىگەر بولۇشنىلا خالايسەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,20,' بىر ئادەم شەھەرنىڭ يىراق يېرىدىن ئالدىراپ كېلىپ: «ئى مۇسا! شۈبھىسىزكى، كاتتىلار سېنى ئۆلتۈرۈشكە مەسلىھەت قىلىشىۋاتىدۇ، (شەھەردىن دەرھال) چىقىپ كەتكىن، مەن ئەلۋەتتە ساڭا سادىق كىشىلەردىنمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,21,' مۇسا قورقۇنچ ئىچىدە ئەتراپقا باققان ھالدا شەھەردىن چىقتى، ئۇ: «ئى پەرۋەردىگارىم! مېنى زالىم قەۋمدىن قۇتقۇزغىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,22,' ئۇ مەديەن تەرەپكە يۈزلەنگەن چاغدا: «پەرۋەردىگارىم مېنى توغرا يولغا يېتەكلىشى مۇمكىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,23,' مۇسا مەديەندىكى بىر بۇلاقنىڭ يېنىغا كەلگەندە (ماللىرىنى) سۇغىرىۋاتقان بىر توپ كىشىلەرنى كۆردى ئۇلاردىن باشقا يەنە (قويلىرىنى سۇدىن) توسۇپ تۇرغان ئىككى ئايالنى كۆردى مۇسا ئۇلارغا: «سىلەرگە نېمە بولدى؟ (يەنى قويلىرىڭلارنى سۇدىن توسۇپ تۇرۇپ تۇرۇپسىلەرغۇ؟)» دېدى، ئۇلار: «پادىچىلار قويلىرىنى سۇغۇرۇپ بولغاندىن كېيىن، ئاندىن بىز سۇغىرىمىز، ئاتىمىز بولسا ياشىنىپ قالغان بوۋايدۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,24,' مۇسا ئۇلارنىڭ قويلىرىنى سۇغۇرۇپ بەردى، ئاندىن ئارقىسىغا بۇرۇلۇپ (بىر دەرەخنىڭ) سايىسىگە بېرىپ (ئولتۇرۇپ): «پەرۋەردىگارىم! ماڭا رىزىقتىن نېمىنىلا بەرسەڭ، مەن ھەقىقەتەن ئۇنىڭغا موھتاج» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,25,' ئۇلارنىڭ (يەنى ئۇ ئىككى ئايالنىڭ) بىرى مۇسانىڭ يېنىغا ئۇياتچانلىق بىلەن مېڭىپ كېلىپ: «(قويلىرىمىزنى) سۇغىرىپ بەرگەنلىكىڭنىڭ ھەققىنى بېرىش ئۈچۈن ئاتام سېنى راستلا چاقىرىدۇ» دېدى مۇسا ئۇنىڭ (يەنى شۇئەيبنىڭ) قېشىغا كېلىپ ئەھۋالنى ھېكايە قىلىپ بەردى، شۇئەيب: « قورقمىغىن، زالىم قەۋمدىن قۇتۇلدۇڭ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,26,' ئۇلارنىڭ (يەنى ئاياللارنىڭ) بىرى ئېيتتى: «ئى ئاتا، ئۇنى سەن ئىشلەتكىن، بۇ سەن ئىشلەتكەنلەرنىڭ ئەڭ ياخشىسىدۇر، كۈچلۈك، ئىشەنچلىكتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,27,' ئۇ (يەنى شۇئەيب) ئېيتتى: «ماڭا سەككىز يىل ئىشلەپ بېرىشىڭ بەدىلىگە ساڭا بۇ ئىككى قىزىمنىڭ بىرىنى بېرىشنى خالايمەن، ئەگەر ئون يىلنى توشقۇزۇۋەتسەڭ (ئۇمۇ) ئىختىيارىڭ، (ئون يىلنى شەرت قىلىپ) سېنى مۇشەققەتكە سېلىپ قويۇشنى خالىمايمەن، خۇدا خالىسا، مېنىڭ ياخشى ئادەم ئىكەنلىكىمنى بايقايسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,28,' مۇسا ئېيتتى: «بۇ ئىككىمىزنىڭ ئارىمىزدىكى توختامدۇر، ئىككى مۇددەت (يەنى سەككىز يىل بىلەن ئون يىل) نىڭ قايسىسىنى توشقۇزسام توشقۇزاي، ماڭا ئارتۇقچە تەلەپ قويۇشقا بولمايدۇ، اﷲ بىزنىڭ سۆزىمىزگە گۇۋاھتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,29,' مۇسا (ئۆز ئارا كېلىشكەن) مۇددەتنى (يەنى كېلىشكەن مۇددەتنىڭ تولۇقراقى بولغان ئون يىلنى) توشقۇزغاندىن كېيىن ئايالىنى ئېلىپ (مىسىرغا قاراپ) يولغا چىقتى، ئۇ تۇر تېغى تەرىپىدە ئوت كۆردى ، ئۇ ئايالىغا: «شۈبھىسىزكى، مەن (يىراقتىن) ئوت كۆردۈم، ئۇ يەردىن مەن سىلەرگە بىر خەۋەر ئېلىپ كېلەي، يا سىلەرنىڭ ئىسسىنىشىڭلار ئۈچۈن ئوتتىن بىر پارچە چوغ ئېلىپ كېلەي» دېدى (مۇسا ئەلەيھىسسالام ئوتنىڭ يېنىغا كېلىپ، ئۇنىڭ ئوت ئەمەس نۇر ئىكەنلىكىنى بىلدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,30,' مۇسا ئوتنىڭ يېنىغا كەلگەندە ۋادىنىڭ ئوڭ تەرىپىنىڭ گىرۋىكىگە توغرا كېلىدىغان مۇبارەك جايدىكى دەرەخ تەرەپتىن (مۇنداق) نىدا ئاڭلاندى: «ئى مۇسا، (ساڭا سۆز قىلىۋاتقان) مەن، ھەقىقەتەن، ئالەملەرنىڭ پەرۋەردىگارى اﷲ دۇرمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,31,' سەن ھاساڭنى تاشلىغىن» مۇسا ھاسىسىنىڭ گويا ئەجدىھادەك تېز ھەرىكەتلىنىۋاتقانلىقىنى كۆرگەندە ئارقىسىغا بۇرۇلۇپ قاچتى، كەينىگە قارىمىدى، (نىدا قىلىندىكى) «ئى مۇسا! ئالدىڭغا ماڭغىن، قورقمىغىن، سەن ھەقىقەتەن (قۇرقۇنچتىن) ئەمىن بولغۇچىلاردىنسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,32,' قولۇڭنى قوينۇڭغا سالغىن، ئۇ ھېچقانداق ئىللەتسىز (بىر پارچە ئايدەك پارلاپ) ئاپئاق بولۇپ چىقىدۇ، قورقۇنچنى دەپئى قىلىش ئۈچۈن، قولۇڭنى يىغىۋالغىن (يەنى قوينۇڭغا سېلىۋالغىن)، بۇ ئىككىسى پىرئەۋنگە (ۋە ئۇنىڭ قەۋمىنىڭ) چوڭلىرىغا پەرۋەردىگارىڭ تەرىپىدىن كەلگەن پاكىتتۇر، ئۇلار ھەقىقەتەن (بىزنىڭ ئىتائىتىمىزدىن چىققۇچى) پاسىق قەۋم ئىدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,33,' مۇسا ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن ئۇلاردىن بىر ئادەمنى ئۆلتۈرۈپ قويدۇم، شۇنىڭ ئۈچۈن ئۇلارنىڭ مېنى ئۆلتۈرۈشىدىن قورقىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,34,' قېرىندىشىم ھارۇننىڭ تىلى مېنىڭدىن راۋاندۇر، ئۇنى مەن بىلەن (پەيغەمبەرلىكىمنى) تەستىقلايدىغان ياردەمچى قىلىپ ئەۋەتكىن، مەن ھەقىقەتەن ئۇلارنىڭ مېنى ئىنكار قىلىشىدىن قورقىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,35,' اﷲ ئېيتتى: «قېرىندىشىڭ ئارقىلىق سېنى كۈچەيتىمىز، ئىككىڭلارنى غالىب قىلىمىز، شۇنىڭ ئۈچۈن ئۇلار ئىككىڭلارغا زىيانكەشلىك قىلالمايدۇ، سىلەر ئىككىڭلارۋە ئىككىڭلارغا ئەگەشكەنلەر بىزنىڭ ئايەتلىرىمىز ئارقىلىق غەلىبە قىلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,36,' مۇسا ئۇلارغا بىزنىڭ روشەن مۆجىزىلىرىمىزنى ئېلىپ كەلگەندە، ئۇلار: «بۇ ئويدۇرۇپ چىقىرىلغان سېھىردۇر، بىزنىڭ ئاتا ـ بوۋىلىرىمىزنىڭ (زامانىدا) بۇنداق نەرسىلەرنىڭ بارلىقىنى ئاڭلىمىغان ئىدۇق» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,37,' مۇسا ئېيتتى: « پەرۋەردىگارىم! ئۆزىنىڭ دەرگاھىدىن كىمنىڭ ھىدايەت ئېلىپ كەلگەنلىكىنى، ئاخىرەتنىڭ ياخشى ئاقىۋىتىگە كىمنىڭ ئىگە بولىدىغانلىقىنى ئوبدان بىلىدۇ، زالىملار (يەنى اﷲ قا يالغاننى چاپلىغۇچى فاجىرلار) چوقۇم مۇۋەپپەقىيەت قازىنالمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,38,' پىرئەۋن ئېيتتى: «ئى كاتتىلار! مەن ئۆزۈمدىن باشقا يەنە بىر ئىلاھنىڭ بارلىقىنى بىلمەيمەن ئى ھامان، مېنىڭ ئۈچۈن پىششىق خىش پىشۇرۇپ ئېگىز بىر بىنا سالغىن، مەن مۇسانىڭ ئىلاھىنى كۆرۈشۈم مۇمكىن، مەن ھەقىقەتەن ئۇنى (ئاسماندا بىر پەرۋەردىگار بار دېگەن دەۋاسىدا) يالغانچىلاردىن گۇمان قىلىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,39,' پىرئەۋن ۋە ئۇنىڭ قوشۇنى (مىسىر) زېمىنىدا زۇلۇم قىلىپ)ئىمان ئېيتىشقا) بويۇنتاۋلىق قىلدى ئۇلار ئۆزلىرىنى بىزتەرەپكە قايتۇرۇلمايمىز)يەنى قىيامەت ۋە قايتا تىرىلىش يوق، ھېساب ئېلىنىشمۇ، جازاغا تارتىلىشمۇيوق) دەپ گۇمان قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,40,' بىز پىرئەۋننى ۋە ئۇنىڭ قوشۇنلىرىنى جازالىدۇق، ئۇلارنى دېڭىزغا تاشلىۋەتتۇق (يەنى ئۇلارنى دېڭىزدا غەرق قىلىۋەتتۇق، ئۇلاردىن بىر ئادەممۇ ساق قالغىنى يوق)، (ئى مۇھەممەد!) زالىملارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا (ئىبرەت كۆزى بىلەن) قارىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,41,' ئۇلارنى بىز دوزاخقا ئۈندەيدىغان پېشۋالار قىلدۇق (يەنى ئۇلارنى بىز دۇنيادا گۇمراھلار ئەگىشىدىغان كۇفرىنىڭ باشلىقلىرى قىلدۇق)، قىيامەت كۈنى ئۇلار ياردەمگە ئېرىشەلمەيدۇ (يەنى قىيامەت كۈنى ئۇلاردىن ئازابنى دەپئى قىلىدىغان مەدەتكار بولمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,42,' ئۇلارغا بۇ دۇنيادا لەنەتنى ئەگەشتۈردۇق (يەنى ئۇلارغا بۇ دۇنيادا اﷲ، پەرىشتىلەر ۋە مۆمىنلەر لەنەت ئوقۇيدۇ)، قىيامەت كۈنى ئۇلار اﷲ نىڭ رەھمىتىدىن يىراق قىلىنغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,43,' شۈبھىسىزكى، بىز (نۇھنىڭ قەۋمى ئاد، سەمۇد ۋە لۇتنىڭ قەۋمى قاتارلىق) ئىلگىرىكى ئۈممەتلەرنى ھالاك قىلغاندىن كېيىن، كىشىلەر (يەنى بەنى ئىسرائىل) نىڭ دىللىرىغا (ھەقىقەتنى كۆرىدىغان) نۇر بېرىپ، ئۇلارغا ھىدايەت ۋە رەھمەت قىلدۇق، ئۇلار ۋەز ـ نەسىھەت ئالسۇن دەپ، مۇساغا كىتابنى (يەنى تەۋراتنى) بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,44,' (ئى مۇھەممەد!) بىز غەربىي تاغنىڭ بىر تەرىپىدە مۇساغا ۋەھيى نازىل قىلغىنىمىزدا سەن يوق ئىدىڭ، (بۇنى) ئۆز كۆزۈڭ بىلەنمۇ كۆرمىگەن ئىدىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,45,' لېكىن بىز نۇرغۇن ئۈممەتلەرنى ياراتتۇق، ئۇلارنىڭ ئۆمرى ئۇزۇن بولدى، سەن مەديەن ئاھالىسىنىڭ ئىچىدە ئۇلارغا بىزنىڭ ئايەتلىرىمىزنى تىلاۋەت قىلىپ تۇرغۇچى بولغىنىڭ يوق، لېكىن بىز (سېنى ئۇلاردىن باشقا بىر قەۋمگە پەيغەمبەر قىلىپ) ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,46,' بىز تۇر تېغىنىڭ بىر تەرىپىدە (مۇساغا) سۆز قىلغىنىمىزدا سەن يوق ئىدىڭ، لېكىن بۇ سەندىن ئىلگىرى ھېچبىر ئاگاھلاندۇرغۇچى كەلمىگەن قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، ئۇلارنىڭ ۋەز ـ نەسىھەت ئېلىشى ئۈچۈن، پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان رەھمەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,47,' ئۇلارنىڭ قىلغان ئىشلىرى (يەنى كۇفرى ۋە گۇناھلىرى) تۈپەيلىدىن، ئۇلارغا بىرەر ئازاب كەلسە، «پەرۋەردىگارىمىز ! بىزگە بىرەر پەيغەمبەر ئەۋەتكەن بولساڭ ئىدىڭ، سېنىڭ ئايەتلىرىڭگە ئەگىشەتتۇق ۋە مۆمىنلەردىن بولاتتۇق» دېمەسلىكلىرى ئۈچۈن (سېنى ئۇلارغا پەيغەمبەر قىلىپ ئەۋەتتۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,48,' ئۇلارغا بىز ۋەھى قىلغان ھەقىقەت نازىل بولغان چاغدا، ئۇلار: «ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) نېمىشقا مۇساغا بېرىلگەن مۆجىزە بېرىلمىدى» دېدى ئۇلار ئىلگىرى مۇساغا بېرىلگەن مۆجىزىنى ئىنكار قىلمىدىمۇ؟ ئۇلار: «(تەۋرات بىلەن قۇرئان) بىر ـ بىرىنى تەستىق قىلىدىغان سېھىردۇر» دېدى ئۇلار: «بىز ھەقىقەتەن (ئىككى كىتابنىڭ)ھەربىرىنى ئىنكار قىلغۇچىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,49,' ئېيتقىنكى، «ئەگەر (ئۇ ئىككى كىتاب سېھىردۇر دېگەن سۆزۈڭلاردا) راستچىل بولىدىغان بولساڭلار، ئۇ ئىككى كىتابقا قارىغاندا، اﷲ تەرىپىدىن نازىل بولغان تېخىمۇ توغرا بىر كىتاب كەلتۈرۈپ بېقىڭلار، مەن ئۇنىڭغا ئەگىشەي»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,50,' ئەگەر ئۇلار ساڭا جاۋاپ بەرمىسە، بىلگىنكى، ئۇلار پەقەت ئۆزلىرىنىڭ نەپسى خاھىشىغىلا ئەگىشىدۇ، اﷲ ۋەھىي قىلغان توغرا يولنى قويۇپ نەپسى خاھىشىغا ئەگەشكەن كىشىدىنمۇ ئازغۇن ئادەم بارمۇ؟ اﷲ ھەقىقەتەن زالىم قەۋمنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,51,' ۋەز ـ نەسىھەت ئالسۇن دەپ، شۈبھىسىزكى، ئۇلارغا قۇرئاننى ئۈزۈلدۈرمەي چۈشۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,52,' قۇرئاندىن ئىلگىرى بىز كىتاب (يەنى ئىنجىل، تەۋرات) نازىل قىلغانلار (يەنى ناسارا ۋە يەھۇدىيلارنىڭ مۆمىنلىرى) قۇرئانغا ئىشىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,53,' ئۇلارغا قۇرئان تىلاۋەت قىلىنغان چاغدا، ئۇلار: «بىز قۇرئانغا ئىشەندۇق شۈبھىسىزكى، ئۇ پەرۋەردىگارىمىز تەرىپىدىن نازىل بولغان ھەقىقەتتۇر بىز ھەقىقەتەن بۇنىڭدىن بۇرۇن مۇسۇلمان ئىدۇق» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,54,' ئۇلارنىڭ قىلغان سەۋر ـ تاقىتى ئۈچۈن ئۇلارغا قوش ساۋاب بېرىلىدۇ، ئۇلار ياخشىلىق ئارقىلىق يامانلىقنى دەپئى قىلىدۇ، بىز ئۇلارغا رىزىق قىلىپ بەرگەن نەرسىلەردىن ياخشىلىق يوللىرىغا سەرپ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,55,' ئۇلار بىھۇدە سۆزلەرنى ئاڭلىغاندا ئۇنىڭدىن يۈز ئۆرۈپ (يەنى قۇلاق سالماي): «بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈن، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزۈڭلار ئۈچۈن، سىلەرگە ئامانلىق بولسۇن، بىز نادانلاردىن دوستلۇق تىلىمەيمىز» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,56,' شۈبھىسىزكى، سەن خالىغان ئادەمىڭنى ھىدايەت قىلالمايسەن، لېكىن اﷲ ئۆزى خالىغان ئادەمنى ھىدايەت قىلىدۇ، اﷲ ھىدايەت تاپقۇچىلارنى ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,57,' ئۇلار: «ئەگەر بىز سەن بىلەن توغرا يولغا ئەگىشىدىغان بولساق، زېمىنىمىزدىن ھەيدەپ چىقىرىلىمىز» دېدى ئۇلارنى بىز تېچ ھەرەمگە يەرلەشتۈرمىدۇقمۇ؟ ھەرەمگە تۈرلۈك مېۋىلەرنىڭ ھەممىسى كەلتۈرۈلىدۇ، (بۇ) بىزنىڭ دەرگاھىمىزدىن چۈشۈرۈلگەن رىزىقتۇر، لېكىن ئۇلارنىڭ تولىسى بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,58,' بىز نۇرغۇن شەھەرنى ھالاك قىلدۇق، ئۇلارنىڭ ئاھالىسى كەيىپ ـ ساپالىق تۇرمۇش كۆچۈرەتتى، بۇ ئۇلارنىڭ تۇرالغۇ جايلىرىدۇر، ئۇلار ھالاك بولغاندىن كېيىن (ئۇ جايلاردا) ئازغىنا ۋاقىتتىن تاشقىرى ئادەم تۇرغان ئەمەس، ئۇلار (نىڭ مال ـ مۈلكى ۋە يۇرتىغا) بىز ۋارىسلىق قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,59,' پەرۋەردىگارىڭ شەھەرلەرنىڭ مەركىزىگە، ئۇلارنىڭ ئاھالىسىگە بىزنىڭ ئايەتلىرىمىزنى ئوقۇپ بېرىدىغان بىر پەيغەمبەر ئەۋەتمىگىچە شەھەرلەرنى ھالاك قىلغۇچى بولمىدۇق، شەھەرلەرنىڭ ئاھالىسى زالىم بولمىغىچە شەھەرلەرنى ھالاك قىلغۇچىمۇ بولمىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,60,' سىلەرگە قانداق نەرسە بېرىلمىسۇن، (ئۇ) دۇنيا تىرىكچىلىكىدىكى پايدىلىنىدىغان نەرسىدۇر ۋە دۇنيا تىرىكچىلىكىنىڭ زىننىتىدۇر، اﷲ نىڭ دەرگاھىدىكى ساۋاب ئەڭ ياخشىدۇر، ئەڭ باقىيدۇر، چۈشەنمەمسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,61,' بىز ياخشى ۋەدىنى قىلغان (يەنى جەننەتنى ۋەدە قىلغان) ئادەم ئۇنى تاپىدۇ، (ئۇ) دۇنيا تىرىكچىلىكىدىكى پايدىلىنىدىغان نەرسىلەر بىلەن بىز پايدىلاندۇرغان، ئاندىن قىيامەت كۈنى ئازابقا دۇچار بولغان ئادەم بىلەن ئوخشاشمۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,62,' شۇ كۈنى اﷲ ئۇلارغا: «سىلەر گۇمان قىلغان مېنىڭ شېرىكلىرىم قەيەردە؟ » دەپ نىدا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,63,' ئۆزلىرىگە ئازاب تېگىشلىك بولغانلار (يەنى مۇشرىكلارنىڭ چوڭلىرى): « پەرۋەردىگارىمىز! بىز (سېنىڭ يولۇڭدىن) ئازدۇرغانلارنى (يەنى ئەگەشكۈچىلىرىمىزنى) ئۆزىمىز ئازغاندەك ئازدۇردۇق، (پەرۋەردىگارىمىز!) ئۇلاردىن يىراقلىشىپ ساڭا يېقىنلاشتۇق، ئۇلار بىزگە ئىبادەت قىلمايتتى (پەقەت نەپسى خاھىشلىرىغا ئىبادەت قىلاتتى)» دېيىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,64,' ئۇلارغا: «شېرىك كەلتۈرگەن مەبۇدلىرىڭلارنى چاقىرىڭلار» دېيىلگەن ھامان، ئۇلار شېرىك كەلتۈرگەن مەبۇدلىرىنى چاقىرىدۇ، ئۇلاردىن جاۋاب كەلمەيدۇ، ئۇلار ئازابنى كۆرگەن چاغدا، (كاپىر بولغانلىقلىرىغا نادامەت قىلىپ) (دۇنيادىكى ۋاقتىدا) ھىدايەت تاپقان بولۇشلىرىنى ئارزۇ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,65,' ئۇ كۈندە اﷲ ئۇلارغا نىدا قىلىپ ئېيتىدۇ: «پەيغەمبەرلەرگەنېمە بىلەن جاۋاپ بەردىڭلار؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,66,' ئۇ كۈندە ئۇلارغا بارلىق خەۋەرلەر قاراڭغۇ بولىدۇ، ئۇلار ئۆز ئارا سوراشمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,67,' تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغان ئادەمگە كەلسەك، ئۇنىڭ مەقسەتكە ئېرىشكۈچىلەردىن بولۇشى ئۈمىدلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,68,' اﷲ (مەخلۇقاتىدىن) خالىغىنىنى يارىتىدۇ، خالىغان ئادەمنى (پەيغەمبەرلىككە) تاللايدۇ، تاللاش ھوقوقى ئۇلاردا ئەمەس، اﷲ پاكتۇر، ئۇلارنىڭ شېرىك كەلتۈرگەنلىرىدىن ئۈستۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,69,' پەرۋەردىگارىڭ ئۇلارنىڭ دىللىرىدا يوشۇرۇن تۇتقانلىرىنى ۋە (ئېغىزلىرىدا) دېگەنلىرىنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,70,' ئۇ اﷲ دۇر، ئۇنىڭدىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، دۇنيا ۋە ئاخىرەتتە جىمى ھەمدۇسانا اﷲ قا مەنسۇپتۇر، ھۆكۈم چىقىرىش اﷲ قا مەنسۇپتۇر، سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,71,' «ئېيتىپ بېقىڭلارچۇ! ئەگەر اﷲ كېچىنى سىلەر ئۈچۈن قىيامەت كۈنىگىچە ئۇزۇن سوزىدىغان بولسا، اﷲ تىن بۆلەك قايسى ئىلاھ سىلەرگە يورۇقلۇق ئېلىپ كېلەلەيدۇ، سىلەر ئاڭلىمامسىلەر؟ » دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,72,' «ئېيتىپ بېقىڭلارچۇ؟ ئەگەر اﷲ كۈندۈزنى سىلەر ئۈچۈن قىيامەت كۈنىگىچە سوزىدىغان بولسا، اﷲ تىن بۆلەك قايسى ئىلاھ سىلەرگە ئارام ئېلىشىڭلار ئۈچۈن كېچىنى ئېلىپ كېلەلەيدۇ؟ سىلەر كۆرمەمسىلەر؟» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,73,' سىلەرنى كېچىدە ئارام ئالسۇن، كۈندۈزدە (ھاياتلىق يولىدا ھەرىكەتلىنىپ) اﷲ نىڭ پەزلىنى تەلەپ قىلسۇن ۋە (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلسۇن دەپ، سىلەر ئۈچۈن كېچە بىلەن كۈندۈزنى يارىتىشى اﷲ نىڭ رەھمىتىدىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,74,' ئۇ كۈندە اﷲ ئۇلارغا نىدا قىلىپ: «سىلەر گۇمان قىلغان مېنىڭ شېرىكلىرىم قەيەردە؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,75,' بىز ھەر ئۈممەتتىن) ئۇلارنىڭ ئەمەللىرىگە گۇۋاھلىق بېرىدىغان) بىر گۇۋاھچى (يەنى پەيغەمبەر) نى چىقىرىمىز، پاكىتىڭلارنى كەلتۈرۈڭلار دەيمىز، ئۇلار ھەقىقەتنىڭ اﷲ قا مەنسۇپ ئىكەنلىكىنى ئاندىن بىلىدۇ، ئۇلارنىڭ ئويدۇرۇپ چىققان نەرسىلىرى (يەنى مەبۇدلىرى) ئۆزلىرىنى ئۇلاردىن چەتكە ئالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,76,' قارۇن ھەقىقەتەن مۇسانىڭ قەۋمىدىن ئىدى قارۇن ئۇلارغا يوغانلىق قىلدى، قارۇنغا خەزىنىلەردىن شۇ قەدەر بەرگەن ئىدۇقكى، ئۇلارنىڭ ئاچقۇچلىرىنى (كۆتۈرۈش) كۈچلۈك بىر جامائەگىمۇ ھەقىقەتەن ئېغىرلىق قىلاتتى، ئەينى ۋاقىتتا قارۇنغا قەۋمى ئېيتتى: «كۆرەڭلەپ كەتمە، اﷲ ھەقىقەتەن كۆرەڭلەپ كەتكۈچىلەرنى دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,77,' اﷲ ساڭا بەرگەن بايلىق بىلەن ئاخىرەت يۇرتىنى تىلىگىن، دۇنيادىكى نېسىۋەڭنىمۇ ئۇنتۇمىغىن، اﷲ ساڭا ياخشىلىق قىلغاندەك، سەن (اﷲ نىڭ بەندىلىرىگىمۇ) ياخشىلىق قىلغىن، يەر يۈزىدە بۇزغۇنچىلىقنى تىلىمىگىن، اﷲ ھەقىقەتەن بۇزغۇنچىلىق قىلغۇچىلارنى دوست تۇتمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,78,' قارۇن: «مېنىڭ ئالاھىدە بىلىمىم بولغانلىقتىن بۇ بايلىققا ئېرىشتىم» دېدى قارۇندىن ئىلگىرى ئۆتكەن ئۈممەتلەردىن ئۇنىڭغا قارىغاندا تېخىمۇ كۈچلۈك، توپلىغان (مېلى) تېخىمۇ كۆپ بولغان كىشىلەرنى اﷲ نىڭ ھالاك قىلغانلىقىنى ئۇ بىلمىدىمۇ؟ گۇناھكارلارنىڭ گۇناھلىرىنىڭ سورىلىشى (اﷲ بۇنى بىلگەنلىكى ئۈچۈن) ھاجەتسىزدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,79,' قارۇن ئۆز قەۋمىنىڭ ئالدىغا بارلىق زىننىتى بىلەن ھەشەمەتلىك ھالدا چىقتى دۇنيا تىرىكچىلىكىنى كۆزلەيدىغانلار: «كاشكى بىزگە قارۇنغا بېرىلگەن بايلىق بېرىلسىچۇ، ئۇ ھەقىقەتەن (دۇنيالىقتىن) چوڭ نېسىۋىگە ئىگە ئىكەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,80,' ئىلىملىك كىشىلەر: «ۋاي سىلەرگە! ئىمان ئېيتقان ۋە ياخشى ئەمەلنى قىلغان كىشىگە اﷲ نىڭ ساۋابى ياخشىدۇر ئۇ ساۋاب پەقەت سەۋر قىلغۇچىلارغىلا بېرىلىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,81,' قارۇننى ئۇنىڭ ئۆيى بىلەن قوشۇپ يەرگە يۇتقۇزدۇق، ئۇنىڭدىن اﷲ نىڭ ئازابىنى دەپئى قىلىدىغان بىر جامائە بولمىدى قارۇن ئۆزىنى قوغدىيالمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,82,' تۈنۈگۈن تېخى قارۇننىڭ دەرىجىسىنى ئارزۇ قىلغانلار دېيىشتىكى، «پاھ كۆردۈڭمۇ؟ اﷲ بەندىلىرىدىن خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدىكەن، (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدىكەن، اﷲ بىزگە مەرھەمەت قىلمىغان (يەنى ئازۇ قىلغىنىمىزنى بەرگەن) بولسا ئىدى، بىزنى ئەلۋەتتە يەر يۇتاتتى، ۋاي كۆردۈڭمۇ؟ كاپىرلار نىجات تاپمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,83,' ئەنە شۇ ئاخىرەت يۇرتىنى يەر يۈزىدە چوڭچىلىق قىلىشنى ۋە بۇزغۇنچىلىق قىلىشنى كۆزلىمەيدىغانلارغا خاس قىلدۇق، (ياخشى) ئاقىۋەت تەقۋادارلارغا مەنسۇپتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,84,' كىمكى ياخشى ئىش قىلغان ئىكەن، ئۇ قىلغان ئىشلىرىدىنمۇ ياخشى مۇكاپاتقا (يەنى قىلغان ياخشىلىقىدىن نەچچە ھەسسە ئارتۇق ساۋابقا) ئېرىشىدۇ، كىمكى يامان ئىش قىلىدىكەن، يامان ئىش قىلغانلارغا پەقەت قىلغان ئىشلىرىغا يارىشا جازا بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,85,' ساڭا قۇرئاننى (يەنى ئۇنى تەبلىغ قىلىشنى ۋە ئۇنىڭ ئەھكامىغا ئەمەل قىلىشنى) پەرز قىلغان زات سېنى ئەلۋەتتە قايتىدىغان يەرگە (يەنى مەككىگە) قايتۇرىدۇ، ئېيتقىنكى، «كىمنىڭ ھىدايەت ئېلىپ كەلگەنلىكىنى، كىمنىڭ ئوپئوچۇق گۇمراھلىقتا ئىكەنلىكىنى پەرۋەردىگارىم ھەممىدىن ئوبدان بىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,86,' سەن (پەيغەمبەر بولۇشتىن ئىلگىرى) قۇرئاننىڭ ساڭا نازىل قىلىنىشىنى ئۈمىد قىلمىغان ئىدىڭ، پەقەت پەرۋەردىگارىڭ رەھمەت قىلىپ (ساڭا ئۇنى نازىل قىلدى)، كاپىرلارغا ياردەمچى بولمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,87,' ساڭا اﷲ نىڭ ئايەتلىرى نازىل قىلىنغاندىن كېيىن، ئۇ ئايەتلەرگە (ئەمەل قىلىش) تىن كاپىرلار سېنى توسمىسۇن، پەرۋەردىگارىڭغا (يەنى ئۇنى بىردەپ بىلىشكە ۋە ئۇنىڭغا ئىبادەت قىلىشقا) دەۋەت قىلغىن، ھەرگىزمۇ مۇشرىكلاردىن بولمىغىن (يەنى ئۇلارنىڭ نەپسى خاھىشلىرىغا ماسلاشمىغىن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,29,88,' اﷲ قا قوشۇپ باشقا بىر ئىلاھقا ئىبادەت قىلما، اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ نىڭ زاتىدىن باشقا بارلىق نەرسە يوقىلىدۇ، (مەخلۇقاتتا ئىجرا بولىدىغان) ھۆكۈم پەقەت اﷲ قا خاستۇر، سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,1,'ئەلىف، لام، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,2,' ئىنسانلار «ئىمان ئېيتتۇق» دەپ قويۇش بىلەنلا سىنالماي تەرك ئېتىلىمىز، دەپ ئويلامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,3,' ئۇلاردىن بۇرۇن ئۆتكەنلەرنى بىز ھەقىقەتەن سىنىدۇق، اﷲ (ئىمانىدا) راستچىللارنى چوقۇم بىلىدۇ، (ئىمانىدا) يالغانچىلارنىمۇ چوقۇم بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,4,' يامان ئىش قىلىدىغانلار بىزدىن (يەنى ئازابىمىزدىن) قېچىپ قۇتۇلالايمىز دەپ ئويلايدۇ ئۇلارنىڭ چىقارغان ھۆكمى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,5,' كىمكى اﷲ قا مۇلاقات بولۇشنى ئۈمىد قىلىدىكەن، (بىلسۇنكى) اﷲ نىڭ (بۇنىڭغا بەلگىلىگەن) ۋاقتى چوقۇم يېتىپ كېلىدۇ، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (بەندىلىرىنىڭ تاشقى ۋە ئىچكى ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,6,' كىمكى جىھاد قىلىدىكەن، ئۇنىڭ قىلغان جىھادى ئۆزىنىڭ پايدىسى ئۈچۈندۇر، اﷲ ھەقىقەتەن تامامى ئەھلى جاھاندىن بىھاجەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,7,' شۈبھىسىزكى، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ گۇناھلىرىنى يوققا چىقىرىمىز، ئۇلارنى ئەلۋەتتە قىلغان ئەمەللىرىنىڭ ئەڭ ياخشىسى بىىلەن مۇكاپاتلايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,8,' ئىنساننى ئاتا ـ ئانىسىغا ياخشىلىق قىلىشقا بۇيرۇدۇق، ئەگەر ئاتا ـ ئاناڭ سېنى سەن بىلمەيدىغان نەرسىنى ماڭا شېرىك كەلتۈرۈشكە زورلىسا، ئۇلارغا ئىتائەت قىلمىغىن، سىلەر مېنىڭ دەرگاھىمغا قايتىپ بارىسىلەر، سىلەرگە قىمىشلىرىڭلارنى خەۋەر قىلىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,9,' ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارنى چوقۇم ياخشىلار قاتارىغا كىرگۈزىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,10,' بەزى كىشىلەر: «اﷲ قا ئىمان ئېيتتۇق» دەيدۇ، اﷲ نىڭ يولىدا بىرەر ئەزىيەت تارتسا، كىشىلەر تەرىپىدىن يەتكەن كۈلپەتنى اﷲ نىڭ ئازابى بىلەن ئوخشاش بىلىدۇ، ئەگەر پەرۋەردىگارىڭدىن مەدەت كەلسە: «بىز ھەقىقەتەن سىلەر بىلەن بىللە ئىدۇق» دەيدۇ اﷲ جاھان ئەھلىنىڭ دىللىرىدىكىنى ھەممىدىن ئوبدان بىلمەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,11,' اﷲ مۆمىنلەرنى چوقۇم بىلىدۇ، مۇناپىقلارنىمۇ چوقۇم بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,12,' كاپىرلار مۆمىنلەرگە: «بىزنىڭ يولىمىز (يەنى دىنىمىز) غا ئەگىشىڭلار، سىلەرنىڭ گۇناھىڭلارنى بىز ئۈستىمىزگە ئالىمىز» دېدى كاپىرلار مۆمىنلەرنىڭ گۇناھلىرىدىن ئازراقمۇ ئۈستىگە ئالالمايدۇ، ئۇلار ھەقىقەتەن يالغانچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,13,' ئۇلار ئۆزلىرىنىڭ يۈكلىرىنى ۋە ئۇنىڭغا قوشۇپ باشقا يۈكلەرنى (يەنى ئۆزلىرىنىڭ گۇناھلىرىنى ۋە ئازدۇرغانلىرىنىڭ گۇناھلىرىنى) ئۈستىگە ئالىدۇ، قىيامەت كۈنى ئۇلار ئۆزلىرى ئويدۇرۇپ چىققان يالغان سۆزلىرى ئۈچۈن سوراققا تارتىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,14,' بىز ھەقىقەتەن نۇھنى قەۋمىگە (پەيغەمبەر قىلىپ) ئەۋەتتۇق، نۇھ (قەۋمىنى تەۋھىدكە دەۋەت قىلىپ) ئۇلارنىڭ ئىچىدە  يىل كەم  يىل تۇردى (نۇھ ئەلەيھىسسالامنىڭ قەۋمى بۇتپەرەس بولۇپ، ئۇنىڭ پەيغەمبەرلىكىگە چىنپۈتمىدى) ئۇلار زالىم بولغانلىقلىرى (يەنى كۇفرىدا ۋە گۇمراھلىقتا چىڭ تۇرۇپ ئۆزلىرىگە زۇلۇم قىلغانلىقلىرى)ئۈچۈن، اﷲ ئۇلارنى توپان (بالاسى) بىلەن ھالاك قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,15,' بىز نۇھنى ۋە كېمىدىكىلەرنى (غەرق بولۇپ كېتىشتىن) قۇتقۇزدۇق، توپاننى پۈتۈن ئەھلى جاھان ئۈچۈن ئىبرەت قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,16,' ئىبراھىمنىمۇ (قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۆز ۋاقتىدا ئۇ قەۋمىگە ئېيتتى: «بىر اﷲ قا ئىبادەت قىلىڭلارۋە ئۇنىڭدىن قورقۇڭلار، ئەگەر بىلسەڭلار مۇنداق قىلىش سىلەر ئۈچۈن ياخشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,17,' سىلەر اﷲ نى قويۇپ بۇتلارغىلا ئىبادەت قىلىسىلەر (بۇتلارنى ئۆز قولۇڭلار بىلەن ياسىۋالغانسىلەر، بۇتلارنىڭ قولىدىن پايدا ـ زىيان كەلمەيدۇ) ۋە يالغاننىلا ئويدۇرىسىلەر، سىلەرنىڭ اﷲ نى قويۇپ چوقۇنۇۋاتقىنىڭلار (يەنى بۇتلار) سىلەرگە رىزىق بېرىشكە قادىر ئەمەس، رىزىقنى اﷲ نىڭ دەرگاھىدىن تەلەپ قىلىڭلار (رىزىق بېرىشكە پەقەت اﷲ نىڭ ئۆزى قادىردۇر)، اﷲ قا ئىبادەت قىلىڭلار ۋە ئۇنىڭغا شۈكۈر قىلىڭلار، اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,18,'سىلەر (مېنى) ئىنكار قىلساڭلار (ئىنكار قىلغانلىقىڭلار بىلەن ماڭا قىلچەزىيان يەتكۈزەلمەيسىلەر، پەقەت ئۆزۈڭلارغا زىيان يەتكۈزىسىلەر)، سىلەردىن ئىلگىرىكى نۇرغۇن ئۈممەتلەرمۇ) پەيغەمبەرلىرىنى (ئىنكار قىلغان) شۇنىڭ بىلەن ئۇلارغا اﷲ ئازابى نازىل بولغان، سىلەرگىمۇ شۇنداق ئازاب نازىل بولىدۇ)، پەيغەمبەرنىڭ مەسئۇلىيىتى پەقەت (اﷲ نىڭ ئەمرلىرىنى) چۈشىنىشلىك قىلىپ يەتكۈزۈشتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,19,' ئۇلار (يەنى ئىنكار قىلغۇچىلار) اﷲ نىڭ مەخلۇقاتىنى دەسلەپتە يوقتىن قانداق بارلىققا كەلتۈرگەنلىكىنى، ئاندىن ئۇنى (ئۆلگەندىن كېيىن) تىرىلدۈرىدىغانلىقىنى كۆرمىدىمۇ؟ بۇ اﷲ قا ھەقىقەتەن ئاساندۇر (ئۇلار بۇنى قانداقمۇ ئىنكار قىلىدۇ، دەسلەپتە يارىتىشقا قادىر بولغان زات قايتا ئەسلىگە كەلتۈرۈشكە ئەلۋەتتە قادىردۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,20,' ئېيتقىنكى، «زېمىندا سەير قىلىڭلار، اﷲ نىڭ مەخلۇقاتىنى قانداق قىلىپ (يوقتىن) بار قىلغانلىقىغا قاراڭلار، ئاندىن اﷲ ئۇلارنى (قىيامەتتە) قايتا پەيدا قىلىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,21,' اﷲ خالىغان ئادەمنى جازالايدۇ، خالىغان ئادەمگە رەھمەت قىلىدۇ، (قىيامەتتە) اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,22,' سىلەر مەيلى زېمىندا بولسۇن، مەيلى ئاسماندا بولسۇن،اﷲ (نىڭ ئازابى) دىن قېچىپ قۇتۇلالمايسىلەر، سىلەرگە اﷲ تىن بۆلەك دوستمۇ يوق، مەدەتكارمۇ يوق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,23,' اﷲ نىڭ ئايەتلىرىنى ۋە ئۇنىڭغا مۇلاقات بولۇشنى (يەنى ئاخىرەتنى) ئىنكار قىلغانلار ئەنە شۇلار مېنىڭ رەھمىتىمدىن نائۈمىد بولدى ئەنە شۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,24,' ئىبراھىمنىڭ قەۋمىنىڭ جاۋابى: «ئىبراھىمنى ئۆلتۈرۈڭلار ياكى كۆيدۈرۈڭلار!» دېيىشتىن ئىبارەت بولدى اﷲ ئۇنى ئوتتىن قۇتۇلدۇردى (ئۇلار ئىبراھىم ئەلەيھىسسالامنى ئوتقا تاشلىغاندا، اﷲ ئۇنىڭغا ئوتنى سورۇن ۋە ئامان جاي قىلىپ بەردى) ھەقىقەتەن بۇنىڭدا ئىمان ئېيتقان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,25,' ئىبراھىم ئېيتتى: «بۇ دۇنيادا (بۇتلارغا چوقۇنۇش مەقسىتىدە يىغىلىشىڭلارنىڭ) ئاراڭلاردىكى دوستلۇققا سەۋەب بولۇشى ئۈچۈن، اﷲ نى قويۇپ بۇتلارنى مەبۇد قىلىۋالدىڭلار، ئاندىن قىيامەت كۈنى بەزىڭلارنى بەزىڭلار ئىنكار قىلىسىلەر، بەزىڭلارغا بەزىڭلار لەنەت ئوقۇيسىلەر (يەنى قىيامەت كۈنى مەزكۇر دوستلۇق دۈشمەنلىككە ئايلىنىدۇ، ئەگەشتۈرگۈچىلەر ئەگەشكۈچىلەردىن ئادا ـ جۇدا بولىدۇ، ئەگەشكۈچىلەر ئەگەشتۈرگۈچىلەرگە لەنەت ئوقۇيدۇ، چۈنكى ئۇلارنىڭ دۇنيادىكى دوستلۇقى اﷲ ئۈچۈن بولغان ئەمەس)، سىلەرنىڭ جايىڭلار دوزاخ بولىدۇ، سىلەرنى (دوزاختىن قۇتۇلدۇرىدىغان) ھېچقانداق ياردەمچى بولمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,26,' لۇت ئىبراھىمغا ئىمان ئېيتتى  ئىبراھىم ئېيتتى: «مەن چوقۇم پەرۋەردىگارىم تەرىپىگە ھىجرەت قىلىمەن (يەنى اﷲ نىڭ رازىلىقىنى ئىزدەش يۈزىسىدىن ۋەتىنىمنى تەرك ئېتىپ، اﷲ ئەمر قىلغان جايغا ھىجرەت قىلىپ بارىمەن) اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,27,' ئىبراھىمغا ئىسھاقنى، يەئقۇبنى ئاتا قىلدۇق (يەنى ئىبراھىم ئەلەيھىسسالام خۇدالىق ئۈچۈن قەۋمىدىن ئايرىلغاندىن كېيىن، ياخشى پەرزەنت ئىسھاقنى بەردۇق ۋە ئىسھاقنىڭ ئوغلى بولغان نەۋرىسى يەئقۇبنى بەردۇق)، (ئۇنىڭدىن كېيىن) پەيغەمبەرلىكنى، كىتابنى (يەنى ساماۋى كىتابلارنى) ئۇنىڭ ئەۋلادىغا خاس قىلدۇق، دۇنيادىكى مۇكاپاتنى ئۇنىڭغا ئاتا قىلدۇق (يەنى جەمئى دىنلاردا ئۇنى ياخشى نامغا ئىگە قىلدۇق)، ئۇ ھەقىقەتەن ئاخىرەتتە ياخشى بەندىلەردىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,28,' لۇتنى (قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئەينى ۋاقىتتا ئۇ قەۋمىگە ئېيتتى: «سىلەر ھەقىقەتەن قەبىھ ئىش قىلىۋاتىسىلەر، سىلەردىن ئىلگىرى جاھان ئەھلىدىن بىرىمۇ مۇنداق قەبىھ ئىشنى قىلغان ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,29,' سىلەر ھەقىقەتەن لىۋاتە قىلامسىلەر؟ يوللارنى توسۇپ بۇلاڭچىلىق قىلامسىلەر؟ سورۇنلىرىڭلاردا ئوپئوچۇق يامان ئىشلارنى قىلىۋېرەمسىلەر؟ » ئۇنىڭ قەۋمىنىڭ جاۋابى: «ئەگەر سەن راستچىللاردىن بولساڭ، بىزگە اﷲ نىڭ ئازابىنى كەلتۈرگىن» دېيىشتىنلا ئىبارەت بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,30,' لۇت: «پەرۋەردىگارىم! بۇزغۇنچى قەۋمگە قارشى ماڭا ياردەم بەرگىن (يەنى ئۇلارنى ھالاك قىلىپ ماڭا ياردەم بەرگىن، ئۇلار بۇزغۇنچى ئەخمەقلەر بولۇپ، ئۇلارنىڭ تۈزىلىشى ئۈمىد قىلىنمايدۇ، ئۇلار گۇمراھلىققا، بۇزۇقچىلىققا چۆمۈپ كەتكەن)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,31,' ئەلچىلىرىمىز (يەنى پەرىشتىلەر) ئىبراھىمغا (پەرزەنت بېرىلىشتىن ئىبارەت)خۇش خەۋەر ئېلىپ كەلگەن چاغدا، ئۇلار: «بىز بۇ شەھەر ئاھالىسىنى چوقۇم ھالاك قىلىمىز، ئۇنىڭ ئاھالىسى ھەقىقەتەن زالىم ئىدى» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,32,' ئىبراھىم: «ئۇ شەھەردە لۇت بارغۇ (يەنى لۇتقا ئوخشاش بىر ياخشى پەيغەمبەر تۇرۇۋاتقان شەھەر ئاھالىسىنى قانداقمۇ ھالاك قىىلىسىلەر؟)» دېدى پەرىشتىلەر ئېيتتى: «بۇ شەھەردە بار كىشىلەرنى بىز (يەنى لۇتنى ۋە ئۇنىڭغا ئەگەشكەن مۆمىنلەرنى) ئوبدان بىلىمىز، ئۇنى ۋە ئۇنىڭ ئايالىدىن باشقا كىشىلىرىنى ئەلۋەتتە قۇتۇلدۇرىمىز، پەقەت ئايالى (كۇفرىدا قەۋمىگە ھەمنەپەس بولغانلىقى ئۈچۈن) قېلىپ ھالاك بولغۇچىلاردىن بولىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,33,' بىزنىڭ ئەلچىلىرىمىز لۇتقا كەلگەن چاغدا، لۇت ئۇلارنى قوغداپ قالالمايدىغانلىقىدىن ئەنسىرەپ قايغۇردى ۋە يۈرىكى سىقىلدى، ئۇلار ئېيتتى: «سەن قورقمىغىن ۋە قايغۇرمىغىن، بىز چوقۇم سېنى ۋە ئايالىڭدىن بۆلەك كىشىلىرىڭنى چوقۇم قۇتۇلدۇرىمىز، پەقەت ئۇ قېلىپ ھالاك بولغۇچىلاردىن بولىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,34,' بۇ شەھەرنىڭ ئاھالىسى اﷲ نىڭ ئىتائىتىدىن چىققانلىقلىرى ئۈچۈن، ئۇلارغا بىز چوقۇم ئاسماندىن ئازاب چۈشۈرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,35,' چۈشۈنىدىغان قەۋم ئۈچۈن (ئىبرەت قىلىپ) ئۇنىڭدىن (يەنى شەھەر خارابىسىنىڭ ئىزلىرىدىن) روشەن نىشاننى ھەقىقەتەن قالدۇردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,36,' مەديەن (ئاھالىسىگە) ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ ئەۋەتتۇق)، شۇئەيب: «ئى قەۋمىم! بىر اﷲ قا ئىبادەت قىلىڭلار، ئاخىرەت كۈنىدىن قورقۇڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلىپ پىتنە ـ پاسات تېرىماڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,37,' ئۇلار شۇئەيبنى ئىنكار قىلدى، شۇنىڭ بىلەن ئۇلارغا قاتتىق زىلزىلە يۈزلەندى ـ دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قېلىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,38,' ئادنى ۋە سەمۇدنى (ھالاك قىلدۇق)، (ئى ئەھلى مەككە!) سىلەرگە ھەقىقەتەن ئۇلارنىڭ (ھىجازدىكى، يەمەندىكى) تۇرالغۇ جايلىرىدىن ئۇلارنىڭ ھالاك بولغانلىقى مەلۇم بولدى، شەيتان ئۇلارغا (كۇقرىدىن ۋە گۇناھلاردىن ئىبارەت) ئەمەللىرىنى چىرايلىق كۆرسەتتى، ئۇلارنى توغرا يولدىن توستى، ئۇلار ئەقىل ئىگىلىرى ئىدى)لېكىن ئۇلار تەكەببۇرلۇق يۈزىسىدىن ئەقلىنى جايىدا ئىشلەتمىدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,39,' (دۇنيا ـ دەپنىسى نۇرغۇن) قارۇننى، پىرئەۋننى ۋە (ئۇنىڭ زۇلۇمدا ياردەمچى بولغان ۋەزىرى) ھاماننى (ھالاك قىلدۇق)، شۈبھىسىزكى، مۇسا ئۇلارغا روشەن مۆجىزىلەرنى ئېلىپ كەلدى، ئۇلار زېمىندا چوڭچىلىق قىلدى (يەنى اﷲ قا ئىبادەت قىلىشتىن، پەيغەمبەرگە ئىتائەت قىلىشتىن بويۇنتاۋلىق قىلدى)، ئۇلار (ئازابىمىزدىن) قېچىپ كېتەلمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,40,' (بۇ گۇناھكارلارنىڭ) ھەربىرىنى گۇناھى تۈپەيلىدىن جازالىدۇق، ئۇلارنىڭ بەزىسىگە تاش ياغدۇردۇق، ئۇلارنىڭ بەزىسىنى قاتتىق تاۋۇش ھالاك قىلدى، ئۇلارنىڭ بەزىسىنى (مال ـ مۈلكى بىلەن قوشۇپ) يەرگە يۇتقۇزدۇق، ئۇلارنىڭ بەزىسىنى (سۇدا) غەرق قىلدۇق، اﷲ ئۇلارغا زۇلۇم قىلمىدى، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,41,' اﷲ نى قويۇپ بۇتلارنى مەبۇد قىلىۋالغانلارنىڭ مىسالى ئۆي ياسىغان ئۆمۈچۈكنىڭ مىسالىغا ئوخشايدۇ (يەنى ئۆمۈچۈكنىڭ ئۆيى ئۇنى ئىسسىق ـ سوغۇقتىن قوغدىيالمىغاندەك، ئۇلارنىڭ چوقۇنۇۋاتقان بۇتلىرىمۇ ئۇلارغا ھېچقانداق پايدا ـ زىيان يەتكۈزەلمەيدۇ)، ئۆيلەرنىڭ ئەڭ ئاجىزى ئۆمۈچۈكنىڭ ئۆيىدۇر ئەگەر ئۇلار (بۇ ھەقىقەتنى) بىلسە ئىدى (بۇتلارغا چوقۇنمايتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,42,' اﷲ ئۇلار اﷲ نى تاشلاپ ئىبادەت قىلىۋاتقان نەرسىنى ئوبدان بىلىدۇ، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,43,' بىز ئىنسانلارغا (قۇرئاندا ئۇلارنىڭ زېھنىغا يېقىنلاشتۇرۇش ئۈچۈن) بايان قىلغان بۇ تەمسىللەرنى پەقەت ئالىملارلا چۈشىنەلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,44,' اﷲ ئاسمانلارنى ۋە زېمىننى ھەق ياراتتى، ھەقىقەتەن بۇنىڭدا مۆمىنلەر ئۈچۈن (اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان) دەلىل بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,45,' (ئى مۇھەممەد!) ساڭا ۋەھىي قىلىنغان كىتابنى (يەنى قۇرئاننى) تىلاۋەت قىلغىن، نامازنى (تەئدىل ئەركان بىلەن) ئوقۇغىن، ناماز ھەقىقەتەن قەبىھ ئىشلاردىن ۋە گۇناھلاردىن توسىدۇ، اﷲ نى ياد ئېتىش ھەممىدىن (يەنى ئۇنىڭدىن باشقا ھەممە ئىبادەتتىن) ئۇلۇغدۇر، اﷲ قىلىۋاتقان (ھەممە) ئىشىڭلارنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,46,' ئەھلى كىتاب بىلەن پەقەت ئەڭ چىرايلىق رەۋىشتە مۇنازىرىلىشىڭلار، ئۇلارنىڭ ئىچىدىكى زۇلۇم قىلغۇچىلار (يەنى سىلەرگە قارشى ئۇرۇش قىلغۇچىلار ۋە فىدىيە بېرىشتىن باش تارتقۇچىلار) بۇنىڭدىن مۇستەسنا، ئېيتىڭلاركى، «بىزگە نازىل قىلىنغان كىتابقا ۋە سىلەرگە نازىل قىلىنغان كىتابقا ئىمان ئېيتتۇق، سىلەرنىڭ ئىلاھىڭلار ۋە بىزنىڭ ئىلاھىمىز بىردۇر، بىز اﷲ قا بويسۇنغۇچىلارمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,47,' (ئى مۇھەممەد!) شۇنىڭدەك (يەنى سەندىن ئىلگىرىكىلەرگە كىتاب نازىل قىلغاندەك) ساڭىمۇ كىتاب (يەنى قۇرئان) نازىل قىلدۇق، بىز كىتاب بەرگەنلەر (يەنى يەھۇدىيلار ناسارالار) دىن قۇرئانغا ئىشىنىدىغانلار بار، بۇلاردىن (يەنى مەككە ئاھالىسىدىن) مۇ قۇرئانغا ئىشىنىدىغانلار بار، بىزنىڭ ئايەتلىرىمىزنى پەقەت كاپىرلارلا ئىنكار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,48,' ئىلگىرى سەن كىتاب ئوقۇشنى بىلمەيتتىڭ، خەت يېزىشنىمۇ بىلمەيتتىڭ، (مۇبادا سەن كىتاب ئوقۇغان، خەت يازىدىغان بولساڭ) ئۇ چاغدا ھەققە قارشى تۇرغۇچىلار چوقۇم گۇمانلىناتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,49,' ئۇنداق ئەمەس (يەنى ئىش زالىملار گۇمان قىلغاندەك ئەمەس)، قۇرئان ئىلىم بېرىلگەنلەرنىڭ كۆڭۈللىرىدە ساقلانغان روشەن ئايەتلەردۇر، بىزنىڭ ئايەتلىرىمىزنى پەقەت زالىملارلا ئىنكار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,50,' ئۇلار: «نېمىشقا ئۇنىڭغا پەرۋەردىگارىدىن مۆجىزىلەر نازىل قىلىنمايدۇ» دېدى ئېيتقىنكى، «مۆجىزىلەر ھەقىقەتەن اﷲ نىڭ دەرگاھىدىدۇر (مېنىڭ قولۇمدا ئەمەستۇر)، مەن پەقەت بىر ئاشكارا ئاگاھلاندۇرغۇچىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,51,' بىزنىڭ ساڭا ئۇلارغا تىلاۋەت قىلىنىپ تۇرىدىغان كىتابنى نازىل قىلغانلىقىمىز ئۇلارغا (مۆجىزە بولۇشقا) كۇپايە قىلمىدىمۇ؟ بۇ كىتابتا ئىمان ئېيتقان قەۋم ئۈچۈن شەك ـ شۈبھىسىز رەھمەت ۋە ۋەز ـ نەسىھەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,52,' ئېيتقىنكى، «مەن بىلەن سىلەرنىڭ ئاراڭلاردا (مېنىڭ راست پەيغەمبەر ئىكەنلىكىمگە) گۇۋاھ بولۇشقا اﷲ كۇپايىدۇر، اﷲ ئاسمانلاردىكى ۋە زېمىندىكى ھەممىنى بىلىپ تۇرىدۇ، بۇتقا ئىشەنگەن، اﷲ نى ئىنكار قىلغان ئادەملەر زىيان تارتقۇچىلاردۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,53,' ئۇلار سەندىن ئازابنىڭ چاپسان كېلىشىنى تەلەپ قىلىدۇ، اﷲ (ئۇلارنىڭ ھالاك بولۇشىنىڭ) مۇئەييەن ۋاقتىنى بەلگىلىمىگەن بولسا ئىدى، ئۇلارغا ئازاب ئەلۋەتتە (ئۇلار تەلەپ قىلغان ۋاقىتتىلا) كېلەتتى، ئۇلارغا ئازاب چوقۇم ئۇلار خەۋەرسىز ھالەتتە تۇرغاندا ئۇشتۇمتۇت كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,54,' ئۇلار سەندىن ئازابنىڭ تېز كېلىشىنى تەلەپ قىلىدۇ، شەك ـ شۈبھىسىزكى، جەھەننەم (قىيامەت كۈنى) كاپىرلارنى قورشاپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,55,' ئۇ كۈندە ئازاب ئۇلارنى ئۈستىلىرىدىن، ئاياغلىرىنىڭ ئاستىدىن ئورىۋالىدۇ اﷲ ئۇلارغا: «(دۇنيادا قىلغان ئەمەللىرىڭلارنىڭ) جازاسىنى تېتىڭلار» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,56,' ئى مۆمىن بەندىلىرىم! مېنىڭ زېمىنىم ھەقىقەتەن كەڭدۇر، ماڭىلا ئىبادەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,57,' ھەربىر جاندار ئۆلۈمنىڭ تەمىنى تېتىغۇچىدۇر، ئاندىن بىزنىڭ دەرگاھىمىزغا قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,58,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەرنى جەننەتنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان ئالىي جايلىرىغا ئورۇنلاشتۇرىمىز، ئۇ يەرلەردە ئۇلار مەڭگۈ قالىدۇ، ياخشى ئەمەللەرنى قىلغۇچىلارغا بېرىلگەن مۇكاپات نېمىدېگەن ياخشى!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,59,' ئۇلار (ھىجرەت قىلىشنىڭ جاپا ـ مۇشەققەتلىرىگە، اﷲ نىڭ يولىدا يەتكەن ئەزىيەتلەرگە) سەۋر قىلىدۇ، (ھەممە ئىشلىرىدا) پەرۋەردىگارىغا يۆلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,60,' نۇرغۇن ھايۋانلار ئۆز رىزقىنى ئۈستىگە ئالالمايدۇ (يەنى ئۆز رىزقىنى تېپىپ يېيىشتىن ئاجىزدۇر)، ئۇلارنى ۋە سىلەرنى اﷲ رىزىقلاندۇرىدۇ، اﷲ (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,61,' ئەگەر سەن ئۇلاردىن: «ئاسمانلارنى ۋە زېمىننى كىم ياراتتى، كۈننى ۋە ئاينى كىم (بەندىلەرنىڭ مەنپەئىتى ئۈچۈن) بويسۇندۇردى؟» دەپ سورىساڭ، ئۇلار چوقۇم: «اﷲ» دەپ جاۋاب بېرىدۇ، ئۇلار قانداقمۇ (اﷲ نىڭ ئىبادىتىدىن) باش تارتىدۇ!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,62,' اﷲ بەندىلىرىدىن قايسى ئادەمنىڭ رىزقىنى كەڭ قىلماقچى بولىدىكەن، ئۇنى كەڭ قىلىدۇ، (قايسى بەندىسىنىڭ رىزقىنى تار قىلماقچى بولىدىكەن) ئۇنى تار قىلىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,63,' ئەگەر سەن ئۇلاردىن: «بۇلۇتتىن يامغۇر سۈيىنى چۈشۈرۈپ ئۇنىڭ بىلەن ئۆلگەن زېمىننى تېرىلدۈرگەن كىم؟» دەپ سورىساڭ، ئۇلار چوقۇم «اﷲ» دەپ جاۋاپ بېرىدۇ ئېيتقىنكى، «(سىلەرگە قارشى پاكىتلار بار بولغانلىقتىن) جىمى ھەمدۇسانا اﷲ قا خاستۇر»، بەلكى ئۇلارنىڭ تولىسى (بۇ سۆزلىرىدىكى زىددىيەتنى) چۈشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,64,' دۇنيا تىرىكچىلىكى پەقەت ئويۇن ـ كۈلكە ۋە غەپلەتتىن ئىبارەتتۇر، شۈبھىسىزكى، ئاخىرەت يۇرتى ھەقىيقى ھايات (يۇرتىدۇر)، ئەگەر ئۇلار بىلسە ئىدى (بۇ دۇنيانى ئۇ دۇنيادىن ئارتۇق كۆرمەيتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,65,' ئۇلار كېمىگە چىقىپ (غەرق بولۇشتىن قورققان) چاغلىرىدا، اﷲ قا كامالى ئىخلاس بىلەن ئىلتىجا قىلىدۇ، اﷲ ئۇلارنى ئامان ـ ئېسەن قۇرۇقلۇققا چىقارغان چاغدا (بالا ـ قازادىن قۇتۇلدۇرغان اﷲ نى ئۇنتۇپ)، ناگاھان (اﷲ قا) شېرىك كەلتۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,66,' ئۇلار بەرگەن نېمىتىمىز (يەنى دېڭىزدىن قۇتۇلدۇرغانلىقىمىزغا ناشۈكۈرلۈك قىلىپ باقسۇن، قالغان ئۆمۈرلىرىدىن) بەھرىمەن بولسۇن، بۇلار (ئىشنىڭ ئاقىۋىتىنى) كەلگۈسىدە بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,67,' ئۇلار (يەنى مۇشرىكلار) كۆرمەمدۇكى، بىز ھەرەمنى ئامان (جاي) قىلدۇق، ھالبۇكى، ئۇلارنىڭ ئەتراپىدىكى كىشىلەر ئەسىر ئېلىنىۋاتىدۇ ۋە ئۆلتۈرۈلۈۋاتىدۇ، ئۇلار باتىلغا ئىشىنىپ، اﷲ نىڭ نېمىتىنى ئىنكار قىلامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,68,' اﷲ قا يالغاننى چاپلىغان ياكى ھەق كەلگەندە ئۇنى ئىنكار قىلغان ئادەمدىنمۇ زالىم ئادەم بارمۇ؟ جەھەننەمدە كاپىرلارغا جاي يوقمىدۇ؟ (ئەلۋەتتە بار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,30,69,' بىز ئۈچۈن كۈرەش قىلغانلارنى ئەلۋەتتە يولىمىزغا يېتەكلەيمىز، اﷲ ھەقىقەتەن ياخشى ئىش قىلغۇچىلار بىلەن بىللىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,1,'ئەلىف، لام، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,2,' رۇملۇقلار يېقىن بىر جايدا يېڭىلدى، ئۇلار يېڭىلگەندىن كېيىن بىر قانچە يىل ئىچىدە يېڭىدۇ، ئىلگىرى ۋە كېيىن ھەممە ئىش اﷲ نىڭ باشقۇرۇشىدىدۇر، بۇ كۈندە مۆمىنلەر اﷲ نىڭ بەرگەن ياردىمى بىلەن خۇشال بولىدۇ، اﷲ خالىغان كىشىگە ياردەم بېرىدۇ، اﷲ غالىبتۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,3,' اﷲ (ئۇلارنى غالىب قىلىدىغانلىقىنى) ۋەدە قىلدى اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ) چۈنكى اﷲ نىڭ ۋەدىسى ھەقتۇر، سۆزى راستتۇر)، لېكىن كىشىلەرنىڭ تولىسى (بۇنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,4,' ئۇلار ھاياتىي دۇنيانىڭ تاشقى كۆرۈنۈشىنىلا بىلىدۇ، ئۇلار ئاخىرەتتىن (يەنى ئاخىرەت ئىشىدا ئويلىنىشتىن ۋە ئاخىرەت ئۈچۈن ئىشلەشتىن) غەپلەتتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,5,' ئۇلار (ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىنى بىلىشلىرى ئۈچۈن) ئۆزلىرى ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ اﷲ ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى (يۇلتۇز قاتارلىق) نەرسىلەرنى پەقەت ھەق ۋە مۇئەييەن ۋاقىتلىق قىلىپ ياراتتى، شەك – شۈبھىسىزكى، نۇرغۇن كىشىلەر پەرۋەردىگارىغا مۇلاقات بولۇشنى (قايتا تىرىلىدىغانلىقىنى، قىلغان ئەمەلىگە يارىشا مۇكاپاتلىنىدىغانلىقىنى ياكى جازالىنىدىغانلىقىنى) ئىنكار قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,6,' ئۇلار يەر يۈزىدە كېزىپ ئۆزلىرىدىن ئىلگىرى ئۆتكەنلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆزەتمىدىمۇ؟ ئۇلار (يەنى ئۆتكەنلەر) قۇۋۋەتتە بۇلاردىن كۈچلۈك ئىدى ئۇلار زىرائەت تېرىشتا، ئىمارەت سېلىشتا بۇلار (يەنى قۇرەيشلەر) دىن ئۈستۈن ئىدى ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەرنى ئېلىپ كەلدى (ئۇلار مۆجىزىلەرنى ئىنكار قىلدى) اﷲ ئۇلارغا زۇلۇم قىلمىدى (يەنى اﷲ ئۇلارنى گۇناھسىز ھالاك قىلغىنى يوق)، لېكىن ئۇلار ئۆزلىرى (كۇفرى ئۈچۈن ۋە پەيغەمبەرلەرنىڭ مۆجىزىلىرىنى ئىنكار قىلغانلىقلىرى ئۈچۈن) ئۆزلىرىگە زۇلۇم قىلدى (شۇنىڭ بىلەن ئۇلار ھالاك بولۇشقا تېگىشلىك بولدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,7,' ئاندىن يامان ئىش قىلغانلارنىڭ ئاقىۋىتى ئەڭ يامان بولىدۇ، چۈنكى ئۇلار اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغان ۋە مەسخىرە قىلغان كىشىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,8,' اﷲ مەخلۇقاتنى دەسلەپتە (يوقتىن) بار قىلىدۇ ئاندىن ئۇنى (ئۆلگەندىن كېيىن) تىرىلدۈرىدۇ، ئاندىن اﷲ نىڭ دەرگاھىغا (ھېساب ئۈچۈن) قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,9,' قىيامەت قايىم بولغان كۈندە گۇناھكارلار ئۈمىدسىزلىنىپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,10,' ئۇلارغا شېرىك كەلتۈرگەن بۇتلىرىدىن شاپائەت قىلغۇچىلار بولمايدۇ، ئۇلار شېرىك كەلتۈرگەن بۇتلىرىدىن تانىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,11,' قىيامەت قايىم بولغان كۈندە (مۆمىنلەر بىلەن كاپىرلار) بىر ـ بىرىدىن ئايرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,12,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار باغچىدا (يەنى جەننەتنىڭ باغچىلىرىدا) يايرىتىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,13,' بىزنىڭ ئايەتلىرىمىزنى ۋە ئاخىرەتتىكى مۇلاقاتنى ئىنكار قىلغان ۋە يالغانغا چىقارغانلارغا كەلسەك، ئۇلار داۋاملىق ئازابتا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,14,' سىلەر ئاخشامدا ۋە ئەتىگەندە اﷲ قا تەسبىھ ئېيتىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,15,' ئاسمانلاردىكى، زېمىندىكى ھەمدۇسانا، كەچ ۋە پېشىن ۋاقىتلىرىدىكى ھەمدۇسانانىڭ ھەممىسى اﷲ قا خاستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,16,' اﷲ جانسىز نەرسىلەردىن جانلىق نەرسىلەرنى چىقىرىدۇ، جانلىق نەرسىلەردىن جانسىز نەرسىلەرنى چىقىرىدۇ، ئۆلگەن زېمىننى تىرىلدۈرىدۇ (يەنى قۇرغاق زېمىننى ئۆسۈملۈكلەر بىلەن كۆكەرتىدۇ)، سىلەرمۇ مۇشۇنداق تىرىلدۈرۈلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,17,' اﷲ نىڭ سىلەرنى تۇپراقتىن ياراتقانلىقى، ئاندىن سىلەرنىڭ ئىنسان بولۇپ زېمىندا تارىلىپ يۈرۈشۈڭلار اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,18,' ئاياللار بىلەن ئۇنسى – ئۈلپەت ئېلىشىڭلار ئۈچۈن (اﷲ نىڭ) ئۇلارنى سىلەرنىڭ ئۆز تىپىڭلاردىن ياراتقانلىقى، ئاراڭلاردا (يەنى ئەر – خوتۇن ئارىسىدا) مېھر ـ مۇھەببەت ئورناتقانلىقى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، پىكىر يۈرگۈزىدىغان قەۋم ئۈچۈن، شەك ـ شۈبھىسىزكى، بۇنىڭدا نۇرغۇن ئىبرەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,19,' اﷲ نىڭ ئاسمانلارنى، زېمىننى ياراتقانلىقى، تىللىرىڭلارنىڭ، رەڭگىلىرىڭلارنىڭ خىلمۇخىل بولۇشى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، بۇنىڭدا بىلىملىك كىشىلەر ئۈچۈن ھەقىقەتەن نۇرغۇن ئالامەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,20,' سىلەرنىڭ كېچىسى ۋە كۈندۈزى ئۇخلىشىڭلار، (كۈندۈزى) اﷲ نىڭ مەرھەمىتىدىن (رىزىق) تەلەپ قىلىپ (ھەرىكەتلىنىشىڭلار) اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، شەك ـ شۈبھىسىزكى، بۇنىڭدا ئاڭلايدىغان قەۋم ئۈچۈن نۇرغۇن ئىبرەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,21,' اﷲ نىڭ سىلەرگە چاقماقنى قورقۇنچ ۋە ئۈمىد قىلىپ كۆرسىتىشى، بۇلۇتتىن يامغۇر ياغدۇرۇپ ئۇنىڭ بىلەن ئۆلگەن زېمىننى تىرىلدۈرۈشى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، چۈشىنەلەيدىغان قەۋم ئۈچۈن بۇنىڭدا، شەك ـ شۈبھىسىزكى، نۇرغۇن ئىبرەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,31,22,' اﷲ نىڭ ئاسمان زېمىننى ئۆز ئەمرى بىلەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,1,'(مۇئەللەق) تۇرغۇزۇشى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، ئاندىن اﷲ (قەبرىلەردە كۆمۈلۈپ ياتقان) سىلەرنى يەر ئاستىدىن چاقىرسا، دەرھال چىقىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,2,' ئاسمانلاردىكى ۋە زېمىندىكى ھەممە اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ مەخلۇقاتىدۇر ۋە اﷲ نىڭ تەسەررۇپى ئاستىدىدۇر)، ھەممە اﷲ قا بويسۇنغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,3,' مەخلۇقاتنى دەسلەپتە (يوقتىن) بار قىلىدىغان، ئاندىن ئۇنى (ئۆلگەندىن كېيىن) تىرىلىدۈرىدىغان اﷲ ئەنە شۇدۇر، ئۇ (يەنى مەخلۇقاتنى تىرىلدۈرۈش) اﷲ قا (ئۇنى دەسلەپتە يوقتىن بار قىلغانغا قارىغاندا) ئوڭايدۇر، ئاسمانلاردا ۋە زېمىندا ئەڭ ئالىي سۈپەت اﷲ قا خاستۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,4,' اﷲ سىلەرگە ئۆزۈڭلاردىن بىر مىسال كەلتۈرىدۇ، بىز سىلەرگە رىزىق قىلىپ بەرگەن مال – مۈلۈككە قۇللىرىڭلارنىڭ شېرىك بولۇشىغا، ئۇ بارىدا ئۇلارنىڭ سىلەر بىلەن باراۋەر بولۇشىغا، ئۇلاردىن ئۆزلىرىڭلار (يەنى ئازاد كىشىلەر) دىن قورققاندەك قورقۇشقا رازىمۇسىلەر؟ (بۇنىڭغا رازى بولمىغان ئىكەنسىلەر، مەخلۇقاتنىڭ اﷲ قا شېرىك بولۇشىغا قانداق رازى بولدۇڭلار؟) چۈشىنىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,5,' ئۇنداق ئەمەس (يەنى اﷲ قا شېرىك كەلتۈرۈشتە ئۇلارنىڭ ھېچقانداق ئۆزرىسى يوق)، (ئۆزلىرىگە) زۇلۇم قىلغانلار بىلىمسىزلىكتىن نەپسى خاھىشىغا ئەگەشتى اﷲ گۇمراھ قىلغان ئادەمنى كىم ھىدايەت قىلالايدۇ، ئۇلارغا ھەرگىز ياردەم قىلغۇچى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,6,' باتىل دىنلاردىن بۇرۇلۇپ ئىسلام دىنىغا يۈزلەنگىن، اﷲ نىڭ دىنىغا (ئەگەشكىنكى) اﷲ ئىنسانلارنى شۇ دىن بىلەن ياراتقان، اﷲ نىڭ ياراتقىنىدا ئۆزگىرىش بولمايدۇ، بۇ توغرا دىندۇر، لېكىن ئىنسانلارنىڭ تولىسى بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,7,' اﷲ قا تەۋبە بىلەن قايتىڭلار، اﷲ تىن قورقۇڭلار، نامازنى ئادا قىلىڭلار، مۇشرىكلاردىن بولماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,8,' سىلەر دىندا ئىختىلاپ قىلىشىپ، پىرقە ـ پىرقە بولۇپ، ھەرپىرقە ئۆز دىنى بىلەن خوشاللىنىدىغانلاردىن (بولماڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,9,' ئىنسانلارغا بىرەر زىيان ـ زەخمەت يەتكەن چاغدا، ئۇلار پەرۋەردىگارىغا ئىخلاس بىلەن ئىلتىجا قىلىدۇ، ئاندىن اﷲ ئۇلارغا رەھمىتىنى تېتىتقان چاغدا، ئۇلاردىن بىر پىرقە دەرھال پەرۋەردىگارىغا شېرىك كەلتۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,10,' ئۇلار بىزنىڭ بەرگەن نېمەتلىرىمىزگە نا شۈكۈرلۈك قىلىپ باقسۇن، (ئى مۇشرىكلار!) (دۇنيا تىرىكچىلىكىدە سىلەرگە بەرگەن نېمەتلىرىمىزدىن) بەھرىمەن بولۇڭلار، سىلەر (پانىي دۇنيانىڭ نېمىتىدىن بەھرىمەن بولغانلىقىڭلارنىڭ ئاقىۋىتىنى) كەلگۈسىدە بىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,11,' ياكى ئۇلارغا ئۇلارنىڭ بۇتلارغا چوقۇنىشىنىڭ توغرىلىقىنى سۆزلەيدىغان بىرەر كىتاب نازىل قىلدۇقمۇ؟ (مۇنداق بولغىنى يوق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,12,' ئىنسانلارغا رەھمەتنى تېتىتساق، ئۇلار بۇنىڭدىن خۇشاللىنىپ كېتىدۇ، ئەگەر ئۇلارغا قىلمىشلىرى تۈپەيلىدىن بىرەر كۆڭۈلسىزلىك يەتسە، دەرھال ئۈمىدسىزلىنىپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,13,' ئۇلار بىلمەمدۇكى، اﷲ خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ، (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدۇ، ھەقىقەتەن بۇنىڭدا (رىزىق بەرگەن اﷲ نىڭ ھېكمىتىگە) ئىشىنىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,14,' خىش ـ ئەقرىبالارغا، مىسكىنلەرگە، ئىبن سەبىللەرگە (سىلە ـ رەھىم ۋە ياخشىلىقتىن تېگىشلىك) ھەققىنى بەرگىن، اﷲ نىڭ رازىلىقىنى كۆزلەيدىغانلار ئۈچۈن مۇنداق قىلىش ياخشىدۇر، ئەنە شۇلار مەقسەتكە ئېرىشكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,15,' سىلەر كىشىلەرنىڭ پۇل ـ مېلى ئىچىدە ئۆستۈرۈش ئۈچۈن بىرەر پۇل ـ مال بەرسەڭلار، اﷲ نىڭ دەرگاھىدا ئۇ ئۆسمەيدۇ (يەنى كىشىلەر بەرگىنىمدىن جىقراق قايتۇرسۇن دېگەن نىيەت بىلەن بېرىلگەن سوغىنىڭ اﷲ نىڭ دەرگاھىدا ساۋابى بولمايدۇ)، اﷲ نىڭ رازىلىقىنى كۆزلەپ بەرگەن سەدىقەڭلار (ياكى ئېھسانىڭلار) غا ھەسسىلەپ ساۋاب بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,16,' اﷲ سىلەرنى خەلق ئەتتى، ئاندىن سىلەرگە رىزىق بەردى ئاندىن سىلەرنى قەبزى روھ قىلىدۇ، ئاندىن سىلەرنى تىرىلدۈرىدۇ، شېرىك كەلتۈرگەنلىرىڭلاردىن قايسىسى بۇ ئىشلارنىڭ قايسىبىرىنى قىلالايدۇ، اﷲ پاكتۇر، ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن ئالىيدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,17,' ئىنسانلارنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن، قۇرۇقلۇقتا ۋە دېڭىزدا ئاپەت يۈزبەردى، اﷲ ئۇلارنى تەۋبە قىلسۇن دەپ قىلمىشلىرىنىڭ بىر قىسمىنىڭ (جازاسىنى) ئۇلارغا تېتىتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,18,' (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، يەر يۈزىدە سەير قىلىڭلار، ئۆتكەنكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار (ئۆزلىرىگە زۇلۇم قىلغانلارنىڭ جايلىرىغا قاراڭلاركى، ئۇلارنىڭ پەيغەمبەرلەرنى ئىنكار قىلغانلىقلىرىنىڭ ئاقىۋىتى قانداق بولغان، اﷲ تائالا ئۇلارنىڭ دىيارلىرىنى خاراپ قىلىپ، ئۇلارنى ئىبرەت ئالغۇچىلار ئۈچۈن ئىبرەت قىلدى)، ئۇلارنىڭ تولىسى مۇشرىك ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,19,' اﷲ تەرىپىدىن كېلىدىغان، قارشى تۇرغىلى بولمايدىغان كۈن (يەنى قىيامەت) كېلىشتىن ئىلگىرى توغرا دىنغا يۈزلەنگىن، بۇ كۈندە ئۇلار (نىڭ بىر پىرقىسى جەننەتكە، بىر پىرقىسى جەھەننەمگە) ئايرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,20,' كىمكى كاپىر بولسا (دوزاختا ئەبەدىي قېلىپ) كۇفرىنىڭ جازاسىنى ئۆزى تارتىدۇ، كىملەركى ياخشى ئەمەل قىلسا، ئۆزلىرى ئۈچۈن (ئاخىرەتتە) جاي راستلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,21,' اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى مەرھەمىتىدىن مۇكاپاتلايدۇ، اﷲ ھەقىقەتەن كاپىرلارنى دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,22,' اﷲ نىڭ سىلەرگە ئۆزىنىڭ رەھمىتىنى تېتىتىش ئۈچۈن، ئەمرى بويىچە كېمىلەرنى دېڭىزلاردا ماڭدۇرۇش ئۈچۈن، ئۆزىنىڭ مەرھەمىتىدىن تىلىشىڭلار ئۈچۈن، شۈكۈر قىلىشىڭلار ئۈچۈن، شاماللارنى يامغۇر بىلەن خۇش خەۋەر بەرگۈچى قىلىپ ئەۋەتىشى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,23,' (ئى مۇھەممەد!) شۈبھىسىزكى، سەندىن ئىلگىرى نۇرغۇن پەيغەمبەرلەرنى قەۋمىگە (پەيغەمبەر قىلپ) ئەۋەتتۇق، ئۇ پەيغەمبەرلەر ئۇلارغا (ئۆزلىرىنىڭ راست پەيغەمبەرلىكلىرىنى ئىسپاتلايدىغان) نۇرغۇن روشەن مۆجىزىلەرنى ئېلىپ كەلدى (ئۇلار مۆجىزىلەرنى ئىنكار قىلىپ ئاسىيلىق قىلدى)، ئاسىيلىق قىلغانلارنى جازالىدۇق، مۆمىنلەرگە ياردەم قىلىش بىزگە تېگىشلىك بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,24,' اﷲ شاماللارنى ئەۋەتىپ بۇلۇتنى قوزغايدۇ، ئاندىن ئۇنى ئاسماندا خالىغىنى بويىچە تارقىتىدۇ، (گاھى) ئۇنى تارقاق پارچىلارغا بۆلىدۇ، شۇنىڭ بىلەن يامغۇرنىڭ بۇلۇتنىڭ ئارىسىدىن چۈشۈۋاتقانلىقىنى كۆرىسەن، اﷲ يامغۇرنى خالىغان بەندىلىرىنىڭ ئۈستىگە ياغدۇرغان چاغدا، ئۇلار دەرھال خۇشال بولۇپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,25,' ھالبۇكى، ئۇلار يامغۇر يېغىشتىن بۇرۇن ھەقىقەتەن ئۈمىدسىزلىنىپ كەتكەن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,26,' اﷲ نىڭ رەھمىتىنىڭ نەتىجىلىرىگە قارىغىنكى، ئۇ زېمىننى ئۆلگەندىن كېيىن قانداق تىرىلدۈرىدۇ، شۈبھىسىزكى، اﷲ ھەقىقەتەن ئۆلۈكلەرنى تىرىلدۈرگۈچىدۇر، اﷲ ھەممە نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,27,' ئەگەر بىز (زىرائەتكە زىيانلىق) شامالنى ئەۋەتسەك، ئۇلار (شامالنىڭ تەسىرىدىن) زىرائەتنىڭ سارغىيىپ كەتكەنلىكىنى كۆرسە، شۇنىڭدىن كېيىن ئۇلار (اﷲ نىڭ ئىلگىرىكى نېمەتلىرىگە) كۇفرىلىق قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,28,' شۈبھىسىزكى، سەن ئۆلۈكلەرگە (سۆزۈڭنى) ئاڭلىتالمايسەن، ئارقىسىغا قاراپ يۈز ئۆرۈگەن گاسلارغىمۇ چاقىرىقىڭنى ئاڭلىتالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,29,' كورلارنىمۇ گۇمراھلىقتىن قۇتقۇزۇپ توغرا يولغا سالالمايسەن، سەن پەقەت بىزنىڭ ئايەتلىرىمىزگە ئىشىنىدىغانلارغىلا (سۆزۈڭنى) ئاڭلىتالايسەن، ئۇلار مۇسۇلمانلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,30,' اﷲ سىلەرنى ئاجىز ياراتتى، ئاندىن سىلەرنى ئاجىزلىقتىن كۈچلۈك قىلدى، ئاندىن (سىلەرنى) كۈچلۈكلۈكتىن ئاجىز قىلدى ۋە (سىلەرگە) قېرىلىقنى يەتكۈزدى، اﷲ نېمىنى خالىسا شۇنى يارىتىدۇ، ئۇ ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,31,' قىيامەت قايىم بولغان كۈندە مۇشرىكلار (دۇنيادا) پەقەت ئازغىنا ۋاقىت تۇرغانلىقىغا قەسەم قىلىدۇ، ئۇلار دۇنيادىكى چاغلىرىدىمۇ مۇشۇنداق يالغانچىلىق قىلاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,32,' ئىلمى بار، ئىمانى بار ئادەملەر ئېيتتى: «سىلەر اﷲ نىڭ تەقدىرى بويىچە (دۇنيادا ۋە قەبرىلەردە) قىيامەت كۈنىگىچە قالدىڭلار، مانا بۇ قىيامەت كۈنىدۇر، لېكىن سىلەر بىلمەيتتىڭلار (يەنى قىيامەتنىڭ ھەقلىقىغا ئىشەنمەيتتىڭلار)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,33,' بۇ كۈندە زالىملارنىڭ (ئېيتقان) ئۆزرىسىنىڭ پايدىسى بولمايدۇ، ئۇلاردىن ئۇلارنىڭ (تەۋبە قىلىشى يا ئىتائەت قىلىشى بىلەن) اﷲ نى رازى قىلىشمۇ تەلەپ قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,34,' شەك ـ شۈبھىسىزكى، بۇ قۇرئاندا كىشىلەرگە تۈرلۈك مىساللارنى بايان قىلدۇق، ئەگەر سەن ئۇلارغا (قۇرئان ئايەتلىرىدىن) بىرەر ئايەتنى كەلتۈرسەڭ، كاپىرلار (دىللىرى قەساۋەتلىشىپ كەتكەنلىكتىن) چوقۇم ئېيتىدۇكى، «سىلەر پەقەت يالغانچىلار سىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,35,' (اﷲ نىڭ بىرلىكىنى) بىلمەيدىغان (كۇففار) لارنىڭ دىللىرىنى مۇشۇنداق پېچەتلەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,32,36,' (ئى مۇھەممەد!) (مۇشرىكلارنىڭ ئەزىيەتلىرىگە) سەۋر قىلغىن، اﷲ نىڭ (ساڭا نۇسرەت ئاتا قىلىش ۋە دىنىڭنى ئۈستۈنلۈككە ئىگە قىلىش) ۋەدىسى ھەقىقەتەن ھەقتۇر، (ئاخىرەتكە) ئىشەنمەيدىغانلار سېنى دىققەت قىلىپ قويمىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,1,'ئەلىف، لام، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,2,' بۇ، ھېكمەتلىك كىتابنىڭ ئايەتلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,3,' (ئۇ) (تەئدىل ئەركان بىلەن) ناماز ئوقۇيدىغان، (اﷲ نىڭ رەزاسىنى تىلەش يۈزىسىدىن كۆڭۈل ئازادىلىكى بىلەن) زاكات بېرىدىغان ۋە ئاخىرەتكە جەزمەن ئىشىنىدىغان ياخشى ئىش قىلغۇچى كىشىلەرگە ھىدايەتتۇر ۋە رەھمەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,4,' ئاشۇلار (يەنى يۇقىرىقى سۈپەتلەرگە ئىگە كىشىلەر) پەرۋەردىگارىنىڭ توغرا يولىدا بولغۇچىلاردۇر، ئەنە شۇلار مەقسەتكە ئېرىشكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,5,' بەزى كىشىلەر بىلىمسىزلىكتىن، (كىشىلەرنى) اﷲ نىڭ يولىدىن ئازدۇرۇش ئۈچۈن بىھۇدە گەپلەرنى سېتىۋالىدۇ ۋە اﷲ نىڭ يولىنى مەسخىرە قىلىدۇ، ئەنە شۇلار خور قىلغۇچى ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,6,' ئەگەر ئۇنىڭغا بىزنىڭ ئايەتلىرىمىز (يەنى قۇرئان ئايەتلىرى) ئوقۇپ بېرىلسە، گويا ئۇنى ئاڭلىمىغاندەك، ئىككى قۇلىقى ئېغىرلىشىپ قالغاندەك (اﷲ نىڭ ئايەتلىرىگە قۇلاق سېلىشتىن) تەكەببۇرلۇق بىلەن يۈز ئۆرۈيدۇ، (ئى مۇھەممەد!) ئۇنىڭغا قاتتىق ئازاب بىلەن خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,7,' شۈبھىسىزكى، ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار نازۇنېمەتلىك جەننەتلەرگە كىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,8,' ئۇ جەننەتلەردە ئۇلار مەڭگۈ قالىدۇ بۇ اﷲ نىڭ راست ۋەدىسىدۇر (اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ)، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,9,' اﷲ ئاسمانلارنى تۈۋرۈكسىز ياراتتىكى، سىلەر ئۇنى كۆرۈپ تۇرۇۋاتىسىلەر، يەر تەۋرەپ سىلەرنى خاتىرجەمسىز قىلمىسۇن دەپ، يەردە تاغلارنى ئورناتتى، زېمىندا تۈرلۈك جاندارلارنى تاراتتى، بۇلۇتتىن يامغۇر سۈيىنى چۈشۈرۈپ، ئۇنىڭ بىلەن زېمىندا تۈرلۈك پايدىلىق ئۆسۈملۈكلەرنى ئۆستۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,10,' مانا بۇلار اﷲ نىڭ (ياراتقان) مەخلۇقاتلىرىدۇر، ماڭا كۆرسىتىپ بېرىڭلاركى، اﷲ تىن باشقا مەبۇدلىرىڭلار زادى نېمىلەرنى ياراتتى؟ بەلكى زالىملار ئوپئوچۇق گۇمراھلىقتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,11,' بىز لوقمانغا ھەقىقەتەن ھېكمەت ئاتا قىلدۇق، (ئۇنىڭغا ئېيتتۇقكى) «اﷲ قا شۈكۈر قىلغىن، كىمكى شۈكۈر قىلىدىكەن، ئۆزى ئۈچۈن قىلغان بولىدۇ، كىمكى كۇفرىلىق قىلىدىكەن، شۈبھىسىزكى، اﷲ (بەندىلەردىن) بىھاجەتتۇر، مەدھىيىگە لايىقتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,12,' ئۆز ۋاقتىدا لوقمان (ھەكىم) ئوغلىغا نەسىھەت قىلىپ: «ئى ئوغۇلچىقىم، اﷲ قا شېرىك كەلتۈرمىگىن، شېرىك كەلتۈرۈش ھەقىقەتەن زور گۇناھتۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,13,' ئىنساننى ئاتا ـ ئانىسىغا ياخشىلىق قىلىشقا بۇيرۇدۇق ئانىسى ئۇنى (قورسىقىدا) ئۈستى ـ ئۈستىگە ئاجىزلىق بىلەن كۆتۈردى ئىككى يىلدا ئۇنى ئەمچەكتىن ئايرىدى (ئى ئىنسان!) ماڭا ۋە ئاتا ـ ئاناڭغا شۈكۈر قىلغىن، ئاخىر قايتىدىغان جاي مېنىڭ دەرگاھىمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,14,' ئەگەر ئاتا ـ ئاناڭ سېنى سەن بىلمەيدىغان نەرسىنى ماڭا شېرىك كەلتۈرۈشكە زورلىسا، ئۇلارغا ئىتائەت قىلمىغىن، ئۇلارغا دۇنيادا ياخشى مۇئامىلىدە بولغىن (يەنى دىنىڭغا زىيان يەتمەيدىغان ئاساستا ياخشىلىق قىلغىن)، ماڭا تائەت بىلەن قايتقان ئادەمنىڭ يولىغا ئەگەشكىن، ئاندىن مېنىڭ دەرگاھىمغا قايتىسىلەر، سىلەرگە قىلمىشىڭلارنى ئېيتىپ بېرىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,15,' (لوقمان ئېيتتى) «ئى ئوغۇلچىقىم! قىلمىشىڭ قىچا چاغلىق نەرسە بولۇپ، ئۇ ئۇيۇل تاشنىڭ ئىچىدە (يەنى ئەڭ مەخپىي جايدا) يا ئاسمانلارنىڭ قېتىدا ياكى زېمىننىڭ ئاستىدا بولسىمۇ، اﷲ ئۇنى ھازىر قىلىدۇ (ئۇنىڭ ھېسابىنى ئالىدۇ) اﷲ ھەقىقەتەن ئىنچىكە كۈزەتكۈچىدۇر، ھەممىدىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,16,' ئى ئوغۇلچىقىم! نامازنى (ۋاقتىدا تەئدىل ئەركان بىلەن) ئوقۇغىن، (كىشىلەرنى) ياخشىلىققا بۇيرۇغىن، يامانلىقتىن توسقىن، ساڭا يەتكەن كۈلپەتلەرگە سەۋر قىلغىن (چۈنكى ھەقىقەتكە دەۋەت قىلغۇچى ئەزىيەتلەرگە ئۇچرايدۇ)، بۇ ھەقىقەتەن قىلىشقا ئىرادە تىكلەشكە تېگىشلىك ئىشلاردىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,17,' كىشىلەردىن مەنسىتمەسلىك بىلەن يۈز ئۆرۈمىگىن، زېمىندا غادىيىپ ماڭمىغىن، اﷲ ھەقىقەتەن ھاكاۋۇر، ئۆزىنى چوڭ تۇتقۇچىلارنى دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,18,' ئوتتۇراھال ماڭغىن، ئاۋازىڭنى پەسلەتكىن، ئاۋازلارنىڭ ئەڭ زېرىكەرلىكى ھەقىقەتەن ئېشەكلەرنىڭ ئاۋازىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,19,' بىلمەمسىلەركى، اﷲ ئاسمانلاردىكى ۋە زېمىندىكى ھەممىنى سىلەرگە بويسۇندۇرۇپ بەردى (يەنى سىلەرنىڭ پايدىلىنىشىڭلارغا مۇۋاپىقلاشتۇرۇپ بەردى) سىلەرگە ئاشكارا ۋە يوشۇرۇن (يەنى ماددىي ۋە مەنىۋى) نېمەتلەرنى كامالەتكە يەتكۈزۈپ بەردى بەزى كىشىلەر ھېچقانداق ئىلىمسىز، ھىدايەتسىز ۋە نۇرلۇق كىتابسىز ھالدا اﷲ بارىسىدا جېدەللىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,20,' ئەگەر ئۇلارغا: «اﷲ نازىل قىلغان نەرسە (يەنى قۇرئان) غا ئەگىشىڭلار» دېيىلسە، ئۇلار: «ياق، بىز ئاتا ـ بوۋىلىرىمىزنىڭ دىنىغا ئەگىشىمىز» دەيدۇ، شەيتان ئۇلارنى دوزاخ ئازابىغا چاقىرغان تۇرسا (ئۇلار يەنىلا ئاتا ـ بوۋىلىرىنىڭ دىنىغا ئەگىشەمدۇ؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,21,' ياخشى ئىش قىلغۇچى بولغان ھالدا ئىخلاس بىلەن اﷲ قا بويسۇنغان ئادەم مەھكەم تۇتقىغا ئېسىلغان بولىدۇ، ئىشلارنىڭ ئاقىۋىتى اﷲ قا مەنسۇپتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,22,' كىمكى كاپىر بولىدىكەن، ئۇنىڭ كۇفرى سېنى قايغۇغا سالمىسۇن، ئۇلار بىزنىڭ دەرگاھىمىزغا قايتىدۇ، ئۇلارغا (دۇنيادىكى) قىلمىشلىرىنى ئېيتىپ بېرىمىز، اﷲ ھەقىقەتەن ئۇلارنىڭ دىللىرىدىكىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,23,' ئۇلارنى (دۇنيادىن) ئازغىنا (مۇددەت) بەھرىمەن قىلىمىز، ئاندىن ئۇلارنى قاتتىق ئازابنى (تېتىشقا) مەجبۇرلايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,24,' ئەگەر سەن ئۇلاردىن: «ئاسمانلارنى ۋە زېمىننى كىم خەلق ئەتتى؟» دەپ سورىساڭ، ئۇلار چوقۇم «اﷲ» دەپ جاۋاپ بېرىدۇ، ئېيتقىنكى، «جىمى ھەمدۇسانا اﷲ قا خاستۇر!» بەلكى ئۇلارنىڭ تولىسى ئۇقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,25,' ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەر اﷲ نىڭ مۈلكىدۇر، اﷲ ھەقىقەتەن (مەخلۇقاتىدىن ۋە ئۇلارنىڭ ئىبادىتىدىن) بىھاجەتتۇر، مەدھىيىگە لايىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,26,' ئەگەر يەر يۈزىدىكى دەرەخلەرنىڭ ھەممىسى قەلەم بولغان، دېڭىز (سىيا) بولغان، ئۇنىڭغا يەنە يەتتە دېڭىز (نىڭ سىياسى) قوشۇلغان تەقدىردىمۇ اﷲ نىڭ سۆزلىرىنى (يېزىپ) تۈگەتكىلى بولمايدۇ، اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,27,' (ئى ئىنسانلار!) سىلەرنىڭ (دەسلەپتە) يارىتىلىشىڭلار، (ئۆلگەندىن كېيىن) تىرىلدۈرۈلۈشىڭلار پەقەت بىر ئادەمنىڭ يارىتىلىشى، بىر ئادەمنىڭ تىرىلدۈرۈلۈشىگە ئوخشاشتۇر اﷲ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەمەللىرىنى) كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,28,' اﷲ نىڭ كېچىنى كۈندۈزگە ۋە كۈندۈزنى كېچىگە كىرگۈزىلىدىغانلىقىنى، كۈن بىلەن ئاينى بويسۇندۇرىدىغانلىقىنى كۆرمەمسەن؟ (كۈن بىلەن ئاينىڭ) ھەر بىرى مۇئەييەن مۇددەت (يەنى قىيامەت) كىچە (ئۆز پەلىكىدە) سەير قىلىدۇ اﷲ سىلەرنىڭ قىلمىشىڭلاردىن تولۇق خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,29,' بۇ شۇنىڭ ئۈچۈنكى، اﷲ ھەق (ئىلاھ) دۇر، ئۇلارنىڭ اﷲ نى قويۇپ ئىبادەت قىلغانلىرى (يەنى بۇتلىرى) باتىلدۇر، اﷲ ئالىيدۇر، بۈيۈكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,30,' كۆرمەمسەنكى، كېمىلەرنىڭ اﷲ نىڭ مەرھەمىتى بىلەن دېڭىزدا يۈرۈشى اﷲ نىڭ سىلەرگە (ئۆز قۇدرىتىنىڭ) بىر قىسىم دەلىللىرىنى كۆرسىتىشى ئۈچۈندۇر بۇنىڭدا ھەر بىر سەۋر قىلغۇچى، شۈكۈر قىلغۇچى (بەندە ئۈچۈن ھەقىقەتەن) (چوڭ) ئىبرەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,31,' (دېڭىزدا) ئۇلارنى تاغلاردەك دولقۇنلار ئورىۋالغان چاغدا، ئۇلار كامالى ئىخلاس بىلەن اﷲ قا ئىلتىجا قىلىدۇ، اﷲ ئۇلارنى (دېڭىز خەتەرلىرىدىن) قۇتۇلدۇرۇپ قۇرۇقلۇققا چىقارغان چاغدا، ئۇلارنىڭ بەزىسى توغرا يولدا بولىدۇ بىزنىڭ ئايەتلىرىمىزنى پەقەت خىيانەتكار (اﷲ نىڭ نېمەتلىرىدىن تانغان) ئادەملا ئىنكار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,32,' ئى ئىنسانلار! پەرۋەردىگارىڭلارغا (بۇيرۇقلىرىنى ئورۇنلاش، مەنئى قىلغان ئىشلاردىن چەكلىنىش بىلەن) تەقۋادارلىق قىلىڭلار، شۇنداق بىر كۈندىن قورقۇڭلاركى، (ئۇ كۈندە) ئاتا بالىسىغا ئەسقاتمايدۇ، بالىمۇ ئاتىسىغا ئەسقاتمايدۇ، اﷲ نىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر، سىلەرنى ھەرگىز دۇنيا تىرىكچىلىكى مەغرۇر قىلمىسۇن، شەيتاننىڭ سىلەرنى اﷲ نىڭ ئەپۇسىنىڭ كەڭلىكى بىلەن مەغرۇر قىلىشىغا يول قويماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,33,33,' قىيامەت (نىڭ بولىدىغان ۋاقتى) توغرىسىدىكى بىلىم ھەقىقەتەن اﷲ نىڭ دەرگاھىدىدۇر اﷲ يامغۇرنى (ئۆزى بەلگىلىگەن ۋاقىتتا ۋە ئۆزى بەلگىلىگەن جايغا) ياغدۇرىدۇ، بەچچىداندىكىلەرنىڭ نېمە ئىكەنلىكىنى (يەنى ئوغۇلمۇ، قىزمۇ، بېجىرىممۇ، كەمتۈكمۇ، بەختلىكمۇ، بەختسىزمۇ) اﷲ بىلىدۇ، ھېچ ئادەم ئەتە نېمىلەرنى قىلىدىغانلىقىنى (يەنى ئۇنىڭغا نېمە ئىش بولىدىغانلىقىنى، ياخشى ـ يامان ئىشلاردىن نېمىلەرنى قىلىدىغانلىقىنى) بىلمەيدۇ، ھېچ ئادەم ئۆزىنىڭ قەيەردە ئۆلىدىغانلىقىنى بىلمەيدۇ، اﷲ ھەقىقەتەن (پۈتۈن ئىشلارنى) بىلىپ تۇرغۇچىدۇر، (شەيئىلەرنىڭ ئىچكى ۋە تاشقى تەرەپلىرىدىن) تولۇق خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,1,'ئەلىف، لام، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,2,' (بۇ) كىتاب (يەنى قۇرئان) نىڭ ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغانلىقىدا ھېچ شەك يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,3,' مۇشرىكلار: «مۇھەممەد ئۇنى ئۆزى توقۇدى» دەيدۇ ھەرگىز ئۇنداق ئەمەس، بەلكى ئۇ سەندىن ئىلگىرى ھېچقانداق بىر پەيغەمبەر كەلمىگەن بىر قەۋمنى ھىدايەت تاپسۇن دەپ، ئۇلارنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,4,' اﷲ ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئالتە كۈندە ياراتتى، ئاندىن ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى سىلەرگە اﷲ تىن باشقا ھېچقانداق ئىگە يوق، شاپائەت قىلغۇچىمۇ يوق (بۇنى) پىكىر قىلىپ (ئىمان ئېيتمامسىلەر؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,5,' اﷲ ئاسماندىن زېمىنغىچە بولغان (مەخلۇقاتىنىڭ) ئىشلىرىنى ئىدارە قىلىپ تۇرىدۇ، ئاندىن ئۇ ئىشلار ئۇزۇنلۇقى سىلەر ساناۋاتقان مىڭ يىلدەك كېلىدىغان بىر كۈندە اﷲ نىڭ دەرگاھىغا ئۆرلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,6,' ئۇ (اﷲ) يوشۇرۇننى ۋە ئاشكارىنى بىلگۈچىدۇر، غالىبتۇر (بەندىلىرىگە) ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,7,' ئۇ ھەممە نەرسىنى چىرايلىق (يەنى ھېكمىتىگە مۇۋاپىق، جايىدا) ياراتتى دەسلەپتە ئىنساننى (يەنى ئىنسانلارنىڭ ئاتىسى ئادەم ئەلەيھىسسالامنى) لايدىن ياراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,8,' ئاندىن ئۇنىڭ نەسلىنى ئەرزىمەس سۇنىڭ جەۋھىرى (يەنى مەنىي) دىن ياراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,9,' ئاندىن ئۇنى راۋۇرۇس يارىتىپ، ئۇنىڭغا ئۆزىگە مەنسۇپ روھنى كىرگۈزدى (يەنى جاننى كىرگۈزدى) يەنە سىلەر ئۈچۈن قۇلاقلارنى، كۆزلەرنى ۋە يۈرەكلەرنى ياراتتى، سىلەر ئاز شۈكۈر قىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,10,' ئۇلار: «يەر ئاستىدا يوقالغاندىن كېيىن (يەنى گۆردە توپىغا ئايلىنىپ كەتكەندىن كېيىن) راستلا قايتا يارىتىلامدۇق؟» دەيدۇ، بەلكى ئۇلار پەرۋەردىگارىغا مۇلاقات بولۇشنى ئىنكار قىلغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,11,' ئېيتقىنكى، «سىلەرنىڭ (جېنىڭلارنى ئېلىشقا) مۇئەككەل قىلىنغان پەرىشتە سىلەرنىڭ جېنىڭلارنى ئالىدۇ، ئاندىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتۇرۇلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,12,' ئەگەر سەن گۇناھكارلارنىڭ پەرۋەردىگارىنىڭ دەرگاھىدا باشلىرىنى ساڭگىلىتىپ تۇرغانلىقىنى كۆرىدىغان بولساڭ (ئەلۋەتتە قورقۇنچلۇق ھالەتنى كۆرگەن بولاتتىڭ)، ئۇلار ئېيتىدۇ: «پەرۋەردىگارىمىز! (ئىشنىڭ ھەقىقىتىنى) كۆردۇق، ئاڭلىدۇق بىزنى ياخشى ئەمەل قىلىش ئۈچۈن (دۇنياغا) قايتۇرغىن، بىز (ئەمدى) ھەقىقەتەن (سېنىڭ ۋەدەڭنىڭ ھەق ئىكەنلىكىگە) ئىشەنگۈچىلەرمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,13,' ئەگەر بىز خالىساق، ھەر بىر كىشىگە ئەلۋەتتە ھىدايەت بېرەتتۇق، لېكىن مېنىڭ (گۇناھكارلارنى ئازاب قىلىشتىن ئىبارەت) ھۆكمىم بېكىپ كەتتى مەن چوقۇم جىنلار ۋە ئىنسانلار (يەنى ئۇلارنىڭ گۇناھكارلىرى) ـ ھەممىسى بىلەن جەھەننەمنى تولدۇرىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,14,' (ئەھلى دوزاخقا ئېيتىلىدۇ) «مۇشۇ كۈندىكى ئۇچرىشىشىڭلارنى ئۇنتۇپ قالغانلىقىڭلار ئۈچۈن (بۇ خورلىغۇچى ئازابنى) تېتىڭلار، بىز ھەقىقەتەن سىلەرنى ئۇنتۇدۇق، قىلمىشىڭلار تۈپەيلىدىن مەڭگۈلۈك ئازابنى تېتىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,15,' بىزنىڭ ئايەتلىرىمىزگە ھەقىقەتەن شۇنداق كىشىلەر ئىمان ئېيتىدۇكى، ئۇلارغا شۇ ئايەتلەر بىلەن ۋەز ـ نەسىھەت قىلىنسا (اﷲ نىڭ ئايەتلىرىنى ئۇلۇغلاش يۈزىسىدىن اﷲ قا) سەجدە قىلغان ھالدا يىقىلىدۇ، پەرۋەردىگارىغا تەسبىھ ئېيتىدۇ، ھەمدۇ ئېيتىدۇ ئۇلار (پەرۋەردىگارىنىڭ تائەت ـ ئىبادىتىدىن) چوڭچىلىق قىلىپ باش تارتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,16,' ئۇلارنىڭ يانلىرى ئورۇن ـ كۆرپىدىن يىراق بولىدۇ (يەنى ئۇلار كېچىسى ئىبادەت قىلىپ ئاز ئۇخلايدۇ)، ئۇلار پەرۋەردىگارىنىڭ (ئازابىدىن) قورقۇپ، (رەھمىتىنى) ئۈمىد قىلىپ ئۇنىڭغا دۇئا قىلىدۇ، ئۇلارغا بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن (ياخشىلىق يوللىرىغا) سەرپ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,17,' ئۇلارنىڭ قىلغان ئەمەللىرىگە مۇكاپات يۈزىسىدىن اﷲ نىڭ ھۇزۇرىدا ساقلانغان ۋە ئۇلارنى خۇشال قىلىدىغان كاتتا نېمەتنى ھېچكىم بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,18,' مۆمىن ئادەم پاسىق ئادەمگە ئوخشامدۇ؟ ئۇلار (ئاخىرەتلىك ساۋابتا) باراۋەر ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,19,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا كەلسەك، ئۇلارنىڭ قارارگاھى جەننەت بولىدۇ، (ئۇ) ئۇلارنىڭ قىلغان ئەمەللىرىنى مۇكاپاتلاش يۈزىسىدىن ئۇلا رئۈچۈن تەييارلانغان جايدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,20,' اﷲ نىڭ ئىتائىتىدىن چىققانلارغا كەلسەك، ئۇلارنىڭ جايى دوزاخ بولىدۇ، ھەر قاچان ئۇلار دوزاختىن چىقماقچى بولسا، ئۇلار دوزاخقا قايتۇرۇلىدۇ ئۇلارغا: «سىلەر ئىنكار قىلغان دوزاخنىڭ ئازابىنى تېتىڭلار» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,21,' ئۇلارنىڭ تەۋبە قىلىشى ئۈچۈن بىز ئۇلارغا چوقۇم ئەڭ چوڭ ئازابتىن (يەنى ئاخىرەت ئازابىدىن) ئىلگىرى ئەڭ يېقىنقى ئازاب (يەنى دۇنيا ئازابى) نى تېتىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,22,' پەرۋەردىگارىنىڭ ئايەتلىرى بىلەن ۋەز ـ نەسىھەت قىلىنغان، ئاندىن ئۇنىڭدىن يۈز ئۆرۈگەن ئادەمدىنمۇ زالىم ئادەم بارمۇ؟ بىز گۇناھكارلارنى چوقۇم جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,23,' شەك ـ شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق (ئى مۇھەممەد!) سەن (مىراج كېچىسىدە، يا قىيامەت كۈنىدە) مۇساغا مۇلاقات بولۇشتىن شەكلەنمىگىن، بىز تەۋراتنى ئىسرائىل ئەۋلادىغا يېتەكچى قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,24,' ئۇلار (كۈلپەتلەرگە) سەۋر قىلغان، بىزنىڭ ئايەتلىرىمىزگە جەزمەن ئىشەنگەن چاغدا، ئۇلاردىن بىر قىسىم كىشىلەرنى بىزنىڭ ئەمرىمىز بىلەن توغرا يول كۆرسىتىدىغان پېشۋالار قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,25,' سېنىڭ پەرۋەردىگارىڭ قىيامەت كۈنى ئۇلارنىڭ ئارىسىدا ئۇلار ئىختىلاپ قىلىشقان (دىنىي) ئىشلار ئۈستىدە ئەلۋەتتە ھۆكۈم چىقىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,26,' ئۇلاردىن ئىلگىرى ئۆتكەن قانچىلىغان ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار بىلمەمدۇ؟ ئۇلار (يەنى مەككىلىكلەر) ئۇلار (يەنى ھالاك بولغانلار) نىڭ يۇرتىدىن ئۆتۈپ تۇرىدۇ، بۇنىڭدا (بىزنىڭ قۇدرىتىمىزنىڭ چوڭلۇقىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار، ئۇلار ئاڭلىمامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,27,' ئۇلار كۆرمەمدۇكى، بىز گىياھسىز زېمىنغا يامغۇر سۈيىنى سۈرۈپ ئېلىپ بارىمىز، ئۇنىڭ بىلەن ئۇلارنىڭ ھايۋاناتلىرى ۋە ئۆزلىرى يەيدىغان زىرائەتلەرنى ئۈندۈرۈپ چىقىرىمىز، ئۇلار (بۇنى) كۆرمەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,28,' ئۇلار (يەنى مەككە كۇففارلىرى مەسخىرە قىلىش يۈزىسىدىن): «(بىز بىلەن سىلەرنىڭ ئاراڭلاردا چىقىرىلىدىغان) بۇ ھۆكۈم قاچان ئەمەلگە ئاشىدۇ؟ ئەگەر سىلەر (دەۋايىڭلاردا) راستچىل بولساڭلار (ئېيتىپ بېرىڭلار)» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,29,' ئېيتقىنكى، «ھۆكۈم چىقىرىلىدىغان كۈندە كاپىرلارنىڭ ئېيتقان ئىمانىنىڭ پايدىسى بولمايدۇ، ئۇلارغا مۆھلەتمۇ بېرىلمەيدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,34,30,' سەن ئۇلاردىن يۈز ئۆرۈگىن (يەنى سۆزلىرىگە پەرۋا قىلىپ كەتمىگىن) سەن (اﷲ نىڭ ئۇلارغا چۈشىدىغان ئازابىنى) كۈتكىن، ئۇلارمۇ ھەقىقەتەن (سىلەرگە چۈشىدىغان نەرسىلەرنى) كۈتكۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,1,'ئى پەيغەمبەر! اﷲ قا تەقۋادارلىق قىلىشنى (داۋاملاشتۇرغىن)، كاپىرلارغا ۋە مۇناپىقلارغا ئىتائەت قىلمىغىن، اﷲ ھەقىقەتەن (بەندىلەرنىڭ ئەمەللىرىنى) بىلىپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئىشلىرىنى) ھېكمەت بىلەن باشقۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,2,' ساڭا پەرۋەردىگارىڭ تەرىپىدىن ۋەھيى قىلىنغان نەرسىگە (يەنى قۇرئانغا) ئەگەشكىن اﷲ ھەقىقەتەن قىلغان ئەمەلىڭلاردىن تولۇق خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,3,' (ھەممە ئىشىڭدا) اﷲ قا يۆلەنگىن، اﷲ (ساڭا) ھامىي بولۇشقا يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,4,' اﷲ ھېچ ئادەمنىڭ ئىچىدە ئىككى يۈرەك ياراتقىنى يوق، سىلەر زىھار  قىلغان ئاياللىرىڭلارنى اﷲ سىلەرگە ئانا قىلغىنى يوق، سىلەر بالا قىلىۋالغانلارنىمۇ اﷲ (ھەممە ھۆكۈمدە ئۆز پۇشتۇڭلاردىن بولغان) بالىلىرىڭلارنىڭ ھۆكمىدە قىلمىدى، بۇ (يەنى ئۆز پۇشتۇڭلاردىن بولمىغانلارنى بالا دېيىش) پەقەت ئاغزىڭلاردا ئېيتىلغان سۆزدۇر، اﷲ ھەق (سۆز) نى ئېيتىدۇ، توغرا يولغا باشلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,5,' ئۇلارنى ئاتىلىرىنىڭ ئىسىملىرى بىلەن چاقىرىڭلار، بۇ اﷲ نىڭ دەرگاھىدا توغرىدۇر، ئەگەر ئۇلارنىڭ ئاتىلىرىنى بىلمىسەڭلار، ئۇ چاغدا ئۇلار سىلەرنىڭ دىنىي قېرىنداشلىرىڭلاردۇر، دوستلىرىڭلاردۇر، سىلەر سەۋەنلىكتىن قىلىپ سالغان ئىشلاردا سىلەرگە ھېچ گۇناھ بولمايدۇ، لېكىن قەستەن قىلغان ئىشىڭلاردا (سىلەرگە گۇناھ بولىدۇ)، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,6,' پەيغەمبەر مۆمىنلەرگە ئۇلارنىڭ ئۆز نەپسىلىرىدىنمۇ چارىدۇر، پەيغەمبەرنىڭ ئاياللىرى ئۇلارنىڭ ئانىلىرىدۇر، كىتابۇللاھتا ئۇرۇق ـ تۇغقانلارنىڭ بەزىسى بەزىسىگە (مىراس ئېلىشتا) مۆمىنلەردىن ۋە مۇھاجىرلاردىن يېقىندۇر، لېكىن(مۆمىنلەردىن ۋە مۇھاجىرلاردىن بولغان) دوستلىرىڭلارغا ۋەسىيەت قىلساڭلار دۇرۇس بولىدۇ، بۇ (يەنى تۇغقانلارنىڭ ئۆز ئارا مىراسقا ۋارىسلىق قىلىشى) كىتابتا (يەنى اﷲ نىڭ كىتابىدا) يېزىلغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,7,' ئۆز ۋاقتىدا بىز پەيغەمبەرلەردىن ئەھدە ئالدۇق، سەندىن، نۇھتىن، ئىبراھىمدىن، مۇسادىن، مەريەم ئوغلى ئىسادىن مەھكەم ئەھدە ئالدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,8,' اﷲ (قىيامەت كۈنى) راستچىللاردىن راستچىللىقى توغرۇلۇق سورايدۇ (يەنى پەيغەمبەرلەردىن پەيغەمبەرلىكىنى يەتكۈزگەنلىكىنى سورايدۇ)، اﷲ كاپىرلارغا قاتتىق ئازاب تەييارلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,9,' ئى مۆمىنلەر! اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ئەسلەڭلار، ئۆز ۋاقتىدا ئۈستۈڭلارغا (كۇففارلاردىن ئۇيۇشقان ئۇرۇشقۇچى) قوشۇن كەلگەن ئىدى بىز ئۇلارغا قارشى بوران ۋە سىلەرگە كۆرۈنمەيدىغان قوشۇن (يەنى پەرىشتىلەرنى) ئەۋەتتۇق، اﷲ سىلەرنىڭ قىلغان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,10,' ئەينى ۋاقىتتا ئۇلار يۇقىرى تەرىپىڭلاردىنمۇ، تۆۋەن تەرىپىڭلاردىنمۇ (ھۇجۇم قىلىپ) كەلگەن ئىدى بۇ چاغدا كۆزۈڭلار چەكچىيىپ قالغان، يۈرىكىڭلار ئاغزىڭلارغا قاپلىشىپ قالغان، اﷲ ھەققىدە تۈرلۈك گۇمانلاردا بولغان ئىدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,11,' بۇ يەردە مۆمىنلەر سىنالغان ۋە (بېشىغا كەلگەن كۈننىڭ قاتتىقلىقىدىن گويا ئۇلارنى يەر سىلكىگەندەك) قاتتىق تەۋرىنىشكە ئۇچرىغان ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,12,' ئەينى ۋاقىتتا مۇناپىقلار ۋە دىللىرىدا (مۇناپىقلىق) كېسىلى بارلار: «اﷲ ۋە ئۇنىڭ پەيغەمبىرى بىزلەرگە قۇرۇق ۋەدىلەرنى قىلىپتىكەن» دېيىشەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,13,' ئەينى ۋاقىتتا ئۇلاردىن بىر گۇرۇھ ئادەم: «ئى يەسرىبلىكلەر! بۇ يەردە تۇرماي قايتىڭلار!» دېيىشەتتى، ئۇلاردىن يەنە بىر گۇرۇھ ئادەم: «بىزنىڭ ئۆيلىرىمىز ھەقىقەتەن ئوچۇقچىلىقتا» دېيىشىپ، پەيغەمبەردىن رۇخسەت سورىشاتتى، بۇلارنىڭ ئۆيلىرى ئوچۇقچىلىقتا ئەمەس، ئۇلار پەقەت قېچىشنىلا ئويلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,14,' ئەگەر شەھەرگە تۆت تەرەپتىن ھۇجۇم قىلىپ كىرىلسە، ئاندىن ئۇلاردىن ئىمانىدىن قايتىش، مۇسۇلمانلارغا قارشى ئۇرۇش قىلىش تەلەپ قىلىنسا، ئۇلار بۇ تەلەپنى ئۈزلۈكسىز رەۋىشتە ئاز ۋاقىت ئىچىدىلا قوبۇل قىلاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,15,' ئۇلار (يەنى مۇناپىقلار) ئىلگىرى اﷲ قا (جەڭدە) چېكىنمەسلىككە ۋەدە بەرگەن ئىدى اﷲ قا بەرگەن ۋەدىسى سورىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,16,' ئېيتقىنكى، «ئەگەر سىلەر ئۆلۈمدىن يا ئۆلتۈرۈلۈشتىن قاچساڭلار، قېچىشنىڭ سىلەرگە ھېچقانداق پايدىسى يوق، قاچقاندىمۇ سىلەر (دۇنيادىن) ئازغىنا (ۋاقىت) بەھرىمەن بولالايسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,17,' ئېيتقىنكى: «ئەگەر اﷲ سىلەرگە بىرەر يامانلىقنى ئىرادە قىلسا، كىم اﷲ قا قارشى تۇرۇپ سىلەرنى قوغدىيالايدۇ، ئەگەر اﷲ سىلەرگە رەھمەت قىلىشنى ئىرادە قىلسا (كىم ئۇنىڭغا ئارىلىشالايدۇ؟)» ئۇلار ئۆزلىرى ئۈچۈن اﷲ تىن باشقا ھېچ ئىگە ۋە ھېچ ياردەمچى تاپالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,18,' اﷲ ئىچىڭلاردىكى باشقىلارنى (جىھادتىن) توسقۇچىلارنى ۋە قېرىنداشلىرىغا (مۇھەممەدنى ۋە ساھابىلىرىنى تەرك ئېتىپ) بىزنىڭ قېشىمىزغا كېلىڭلار دېگۈچىلەرنى ئوبدان بىلىدۇ، ئۇلار جەڭگە (رىيا قىلىپ) ئاندا ـ ساندا قاتنىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,19,' ئۇلار سىلەرگە (ياردەمدە بولۇشقا) بېخىلدۇر، ئەگەر (ئۇلارغا دۈشمەن تەرەپتىن) خەۋپ كېلىدىغان بولسا، ئۇلارنىڭ ساڭا سەكراتقا چۈشۈپ قالغان ئادەمدەك (قورقۇنچتىن) كۆزلىرىنى پىلدىر لىتىپ قاراۋاتقانلىقىنى كۆرىسەن قورقۇنچ كەتكەندە، ئۇلار پۇل ـ مالغا ئاچكۆز بولغان ھالدا سىلەرنى كەسكىن تىللار بىلەن رەنجىتىدۇ ئۇلار(چىن دىللىرى بىلەن) ئىمان ئېيتقان ئەمەس (ئۇلارنىڭ كۇفرى سەۋەبلىك) ئەمەللىرىنى اﷲ بىكار قىلدى، بۇ اﷲ ئۈچۈن ئاساندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,20,' ئۇلار (يەنى مۇناپىقلار) (قاتتىق قورققانلىقتىن) ئىتتىپاقداش قوشۇننى چېكىنمىدى دەپ ئويلايدۇ، ئەگەر ئىتتىپاقداش قوشۇن قايتا كەلسە، ئۇلار (قىردا) ئەئرابىلارنىڭ (يەنى قىر ئەرەبلىرىنىڭ) يېنىدا تۇرۇپ سىلەرنىڭ خەۋەرلىرىڭلارنى ئۇققاچ (تىنچ يېتىشىنى) ئارزۇ قىلىدۇ، ئۇلار (ئۇرۇش ۋاقتىدا) ئاراڭلاردا بولغان تەقدىردىمۇ، (قورققانلىقتىن) جەڭگە (رىيا قىلىپ) ئاندا ـ ساندا قاتناشقان بولاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,21,' سىلەرگە ـ اﷲ نى، ئاخىرەت كۈنىنى ئۈمىد قىلغان ۋە اﷲ نى كۆپ ياد ئەتكەنلەرگە ـ رەسۇلۇللاھ ئەلۋەتتە ياخشى ئۈلگىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,22,' مۆمىنلەر ئىتتىپاقداش قوشۇننى كۆرگەن چاغدا: «بۇ (يەنى قېيىن ئەھۋالدا قالغاندا دۈشمەن ئۈستىدىن غەلىبە قىلىش) اﷲ ۋە ئۇنىڭ پەيغەمبىرى بىزگە ۋەدە قىلغان ئىشتۇر اﷲ ۋە ئۇنىڭ پەيغەمبىرى راست ئېيتتى» دېيىشتى (بۇ ئىش) ئۇلارنىڭ (اﷲ قا بولغان) ئىمانىنى ۋە (اﷲ نىڭ بۇيرۇقلىرىغا بولغان) بويسۇنۇشىنى تېخىمۇ كۈچەيتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,23,' مۆمىنلەرنىڭ ئىچىدە (رەسۇلۇللاھ بىلەن غازاتقا چىققاندا ساباتلىق بولۇپ، شېھىت بولغانغا قەدەر دادىللىق بىلەن جەڭ قىلىش توغرۇلۇق) اﷲ قا بەرگەن ئەھدىنى ئىشقا ئاشۇرغان نۇرغۇن كىشىلەر بار ئۇلارنىڭ بەزىسى (ئەھدىگە ۋاپا قىلىپ) شېھىت بولدى، بەزىسى (شېھىت بولۇشنى) كۈتكمەكتە، ئۇلار (پەرۋەردىگارىغا بەرگەن ئەھدىسىنى) ھەرگىز ئۆزگەرتكىنى يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,24,' اﷲ راستچىللارغا راستچىللىقى ئۈچۈن (ئاخىرەتتە ئەڭ ياخشى) مۇكاپات بېرىدۇ، مۇناپىقلارنى (ئەھدىنى بۇزغۇچى مۇناپىقلارنى مۇناپىق پېتى بۇ دۇنيادىن كەتكۈزۈپ) ئەگەر خالىسا ئازابلايدۇ، يا ئۇلارنى تەۋبىگە مۇۋەپپەق قىلىدۇ، اﷲ ھەقىقەتەن (بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,25,' اﷲ كاپىرلار (يەنى مەدىنىگە ھۇجۇم قىلغان ئىتتىپاقداش قوشۇن) نى خاپا قايتۇردى، ئۇلار مەقسىتىگە يېتەلمىدى، اﷲ (دۈشمەنگە بوران ۋە پەرىشتىلەرنى ئاپىرىدە قىلىپ) مۆمىنلەرنى ئۇرۇشسىز غەلىبىگە ئېرىشتۈردى، اﷲ كۈچلۈكتۇر، غالىبتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,26,' اﷲ ئەھلى كىتابتىن مۇشرىكلارغا ياردەم بەرگەنلەرنى (يەنى رەسۇلۇللاھ بىلەن قىلغان ئەھدىنى بۇزغان بەنى قۇرەيزە يەھۇدىيلىرىنى) قۇرغانلىرىدىن چۈشۈردى، ئۇلارنىڭ دىللىرىغا قورقۇنچ سالدى (ئۇلارنىڭ) بىر بۆلۈكىنى ئۆلتۈردۇڭلار ۋە بىر بۆلۈكىنى ئەسىر ئالدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,27,' اﷲ سىلەرگە ئۇلارنىڭ زېمىنىنى، قورو ـ جايلىرىنى، مال ـ مۈلۈكلىرىنى ۋە (تېخى) سىلەرنىڭ قەدىمىڭلار دەسسەپ باقمىغان زېمىننى مىراس قىلىپ بەردى، اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,28,' ئى پەيغەمبەر! (ئارتۇق خىراجەت سوراپ سېنى رەنجىتكەن) ئاياللىرىڭغا ئېيتقىنكى، «ئەگەر سىلەر دۇنيا تېرىكچىلىكىنى (يەنى پاراۋان تۇرمۇشنى) ۋە دۇنيانىڭ زىبۇ زىننىتىنى كۆزلىسەڭلار، كېلىڭلار، سىلەرگە بىر ئاز نەرسە بېرەي، سىلەرنى چىرايلىقچە قويۇپ بېرەي');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,29,' ئەگەر سىلەر اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى، ئاخىرەت يۇرتىنى ئىختىيار قىلساڭلار، اﷲ ھەقىقەتەن ئىچىڭلاردىكى ياخشى ئىش قىلغۇچىلارغا چوڭ ساۋابنى (يەنى كۆز كۆرمىگەن، قۇلاق ئاڭلىمىغان ۋە ئىنساننىڭ كۆڭلىگە كەلمىگەن نازۇنېمەتلەر بار جەننەتنى) تەييار قىلدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,30,' ئى پەيغەمبەرنىڭ ئاياللىرى! ئىچىڭلاردىن كىمكى ئاشكارا قەبىھ ئىشنى قىلىدىكەن، ئۇنىڭغا ھەسسىلەپ ئازاب قىلىنىدۇ، بۇ اﷲ قا ئاساندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,31,' سىلەردىن كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلسا ۋە ياخشى ئەمەل قىلسا، ئۇنىڭغا ساۋابنى ئىككى قاتلاپ بېرىمىز، ئۇنىڭغا (جەننەتتە) ئېسىل رىزىق تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,32,' ئى پەيغەمبەرنىڭ ئاياللىرى! سىلەر باشقا ئاياللارنىڭ ھېچبىرىگە ئوخشىمايسىلەر، سىلەر (يات ئەرلەرگە سۆز قىلغاندا) نازاكەت بىلەن سۆز قىلماڭلار، (نازاكەت بىلەن سۆز قىلساڭلار) دىلىدا نىفاق بار ئادەم (سىلەرگە قارىتا) تەمەدە بولۇپ قالىدۇ، (گۇماندىن خالى) ياخشى سۆزنى سۆزلەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,33,' ئۆيلىرىڭلاردا ئولتۇرۇڭلار، ئىلگىرىكى جاھىلىيەت دەۋرىدىكى ئاياللارنىڭ ياسىنىپ چىققىنىدەك ياسىنىپ چىقماڭلار، ناماز ئوقۇڭلار، زاكات بېرىڭلار، اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، ئى پەيغەمبەرنىڭ ئائىلىسىدىكىلەر! اﷲ سىلەردىن گۇناھنى ساقىت قىلىشنى ۋە سىلەرنى تامامەن پاك قىلىشنى خالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,34,' ئۆيلىرىڭلاردا اﷲ نىڭ ئايەتلىرىدىن ۋە ھېكمەت (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ ھەدىسلىرى) تىن ئوقۇلۇۋاتقان نەرسىلەرنى ياد ئېتىڭلار، اﷲ ھەقىقەتەن سىرلەرنى بىلگۈچىدۇر، ھەممىدىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,35,' مۇسۇلمان ئەرلەر ۋە مۇسۇلمان ئاياللارغا، مۆمىن ئەرلەر ۋە مۆمىن ئاياللارغا، تائەت ـ ئىبادەت قىلغۇچى ئەرلەر ۋە تائەت ـ ئىبادەت قىلغۇچى ئاياللارغا، راستچىل ئەرلەر ۋە راستچىل ئاياللارغا، سەۋر قىلغۇچى ئەرلەر ۋە سەۋر قىلغۇچى ئاياللارغا، خۇدادىن قورققۇچى ئەرلەر ۋە خۇدادىن قورققۇچى ئاياللارغا، سەدىقە بەرگۈچى ئەرلەر ۋە سەدىقە بەرگۈچى ئاياللارغا، روزا تۇتقۇچى ئەرلەر ۋە روزا تۇتقۇچى ئاياللارغا، نەپسىلىرىنى ھارامدىن ساقلىغۇچى ئەرلەر ۋە نەپسىلىرىنى ھارامدىن ساقلىغۇچى ئاياللارغا، اﷲ نى كۆپ زىكرى قىلغۇچى ئەرلەر ۋە اﷲ نى كۆپ زىكرى قىلغۇچى ئاياللارغا اﷲ مەغپىرەت ۋە كاتتا ساۋاب تەييارلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,36,' اﷲ ۋە ئۇنىڭ پەيغەمبىرى بىرەر ئىشتا ھۆكۈم چىقارغان چاغدا، ئەر ـ ئايال مۆمىنلەرنىڭ ئۆز ئىشىدا ئىختىيارلىقى بولمايدۇ (يەنى اﷲ ۋە اﷲ نىڭ پەيغەمبىرى بىرەر ئىشتا ھۆكۈم چىقارغان ئىكەن، ھېچ ئادەمنىڭ ئۇنىڭغا مۇخالىپەتچىلىك قىلىشىغا بولمايدۇ)، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئاسىيلىق قىلسا، ھەقىقەتەن ئۇ ئوپئوچۇق ئازغان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,37,' ئۆز ۋاقتىدا سەن اﷲ نېمەت بەرگەن، سەنمۇ ئىنئام قىلغان كىشىگە: «خوتۇنۇڭنى نىكاھىڭدا تۇتقىن، اﷲ تىن قورققىن!» دېدىڭ، اﷲ ئاشكارىلىماقچى بولغان نەرسىنى كۆڭلۈڭدە يوشۇردۇڭ، كىشىلەرنىڭ تەنە قىلىشىدىن قورقتۇڭ، اﷲ تىن قۇرقۇشۇڭ ئەڭ ھەقلىق ئىدى مۆمىنلەرگە ئۇلارنىڭ بالا قىلىۋالغان ئوغۇللىرىنىڭ قويۇپ بەرگەن خوتۇنلىرىنى نىكاھلاپ ئالسا گۇناھ بولماسلىقى ئۈچۈن، زەينەبنى زەيد قويۇۋەتكەندىن كېيىن ساڭا نىكاھلاپ بەردۇق، اﷲ نىڭ (سېنىڭ زەينەبنى ئېلىشىڭ توغرىسىدىكى) ئەمرى چوقۇم ئورۇنلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,38,' اﷲ پەيغەمبەرگە ھالال قىلغان ئىشقا ھېچ گۇناھ بولمايدۇ (بۇ) اﷲ نىڭ ئۆتكەنكى (پەيغەمبەرلەرگە) تۇتقان يولىدۇر اﷲ نىڭ ئەمرى ئەزەلدىنلا بېكىپ كەتكەن (ئۆزگەرمەس) ھۆكۈمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,39,' ئۇلار (يەنى پەيغەمبەرلەر) اﷲ نىڭ ئەمر ـ پەرمانلىرىنى يەتكۈزىدۇ، اﷲ تىن قورقىدۇ، اﷲ تىن باشقا ھېچ كىشىدىن قورقمايدۇ (ئى مۇھەممەد! سەنمۇ اﷲ تىن غەيرىيدىن قورقماسلىقتا شۇ پەيغەمبەرلەرگە ئەگەشكىن)، اﷲ (ھەممە ئىشلاردىن) ھېساب ئېلىشقا يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,40,' مۇھەممەد ئاراڭلاردىكى ئەرلەردىن ھېچبىرىنىڭ ئاتىسى ئەمەس، لېكىن (ئۇ) اﷲ نىڭ پەيغەمبىر ى ۋە پەيغەمبەرلەرنىڭ ئاخىرقىسىدۇر (اﷲ ئۇنىڭ بىلەن پەيغەمبەرلىكنى ئاخىرلاشتۇرغان، ئۇنىڭدىن كېيىن ھېچقانداق پەيغەمبەر كەلمەيدۇ)، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر (سىلەرنىڭ سۆز ـ ھەرىكىتىڭلاردىن ھېچ نەرسە اﷲ قا مەخپىي ئەمەس)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,41,' ئى مۆمىنلەر! اﷲ نى كۆپ ياد قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,42,' ئۇنىڭغا ئەتىگەن ـ ئاخشامدا تەسبىھ ئېيتىڭلار (چۈنكى بۇ ئىككى ۋاقىتتا پەرىشتىلەر چۈشۈپ تۇرغانلىقتىن، ئۇ ۋاقىتلارنىڭ ئەۋزىلى ھېسابلىنىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,43,' اﷲ سىلەرنى قاراڭغۇلۇقتىن نۇرغا (يەنى گۇمراھلىقتىن ھىدايەتكە) چىقىرىش ئۈچۈن، سىلەرگە رەھمەت قىلىپ تۇرىدۇ، ئۇنىڭ پەرىشتىلىرى سىلەرگە مەغپىرەت تەلەپ قىلىپ تۇرىدۇ، اﷲ مۆمىنلەرگە ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,44,' ئۇلار اﷲ قا مۇلاقات بولغان كۈندە ئۇلارغا اﷲ تەرىپىدىن يوللىنىدىغان سالام (سىلەرگە) ئامانلىق بولسۇن (دېگەن سۆزدىن ئىبارەت)، اﷲ ئۇلارغا ئېسىل مۇكاپات تەييارلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,45,' ئى پەيغەمبەر! سېنى بىز ھەقىقەتەن گۇۋاھچى، بېشارەتچى، ئاگاھلاندۇرغۇچى، اﷲ نىڭ ئىزنى بىلەن ئۇنىڭ (بىر لىكىگە، تائەت ـ ئىبادىتىگە) دەۋەت قىلغۇچى ۋە نۇرلۇق چىراق قىلىپ ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,46,' مۆمىنلەرگە ئۇلارنىڭ اﷲ نىڭ بۈيۈك ئېھسانىغا ئېرىشىدىغانلىقىدىن خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,47,' كاپىرلارغا ۋە مۇناپىقلارغا ئىتائەت قىلمىغىن، ئۇلارنىڭ (ساڭا قىلغان) ئازار ـ كۈلپەتلىرىگە پەرۋا قىلمىغىن (اﷲ قا تەۋەككۈل قىلغىن)، اﷲ (ئۇنىڭغا) تەۋەككۈل قىلغانلارغا يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,48,' ئى مۆمىنلەر! سىلەر مۆمىن ئاياللارنى ئالساڭلار، ئاندىن ئۇلارغا يېقىنچىلىق قىلىشتىن بۇرۇن ئۇلارنى قويۇۋەتسەڭلار، بۇ چاغدا ئۇلار سىلەر ئۈچۈن ئىددەت تۈتماسلىقى كېرەك، ئۇلارغا ئاز ـ تولا نەرسە بېرىڭلار، ئۇلارنى چىرايلىقچە قويۇپ بېرىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,49,' ئى پەيغەمبەر! ساڭا بىز سەن مەھرىلىرىنى بەرگەن ئاياللىرىڭنى، اﷲ ساڭا غەنىمەت قىلىپ بەرگەن، ئەسىر چۈشكەن ئاياللاردىن ئۆزۈڭ مالىك بولغانلارنى ھالال قىلدۇق، ئاتاڭنىڭ ئەر قېرىندىشى ۋە ھەمشىرە قېرىندىشىنىڭ قىزلىرىنى، ئاناڭنىڭ ئەر قېرىندىشى ۋە ھەمشىرە قېرىندىشىنىڭ قىزلىرىنى (ساڭا ھالال قىلدۇق) بۇلار سەن بىلەن بىرلىكتە ھىجرەت قىلدى ئەگەر بىر مۆمىن خوتۇن ئۆزىنى پەيغەمبەرگە بەخش قىلسا، پەيغەمبەر ئۇنى (مەھرىسىز) ئېلىشىنى خالىسا، بۇنى مۆمىنلەرگە ئەمەس، يالغۇز ساڭىلا (ھالال قىلدۇق)، ئۇلارنىڭ ئاياللىرى ۋە چۆرىلىرى توغرۇلۇق بىزنىڭ چىقارغان بەلگىلىمىلىرىمىز بىزگە مەلۇمدۇر، ساڭا ھەرەج بولۇپ قالماسلىقى ئۈچۈن (سېنى ئالاھىدىلىكلەرگە ئىگە قىلدۇق)، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,50,' ئاياللىرىڭدىن خالىغىنىڭنىڭ نۆۋىتىنى ئارقىغا سۈرسەڭ، خالىغىنىڭ بىلەن (نۆۋىتىدىن باشقا ۋاقىتتا) بىر يەردە بولساڭ بولىۋېرىدۇ، (ۋاقىتلىق) ئايرىلىپ تۇرغان ئاياللىرىڭدىن بىرى بىلەن ھەمتۆشەك بولماقچى بولساڭ، ساڭا ھېچ گۇناھ يوقتۇر، (مانا) بۇ ئۇلارنىڭ خۇشال بولۇشىغا، قايغۇرماسلىقىغا، بەرگەنلىرىڭگە ئۇلارنىڭ ھەممىسىنىڭ رازى بولۇشىغا ئەڭ يېقىندۇر، اﷲ دىلىڭلاردىكىنى بىلىپ تۇرىدۇ، اﷲ ھەممىنى بىلگۈچىدۇر، ھەلىمدۇر (يەنى جازالاشقا ئالدىراپ كەتمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,51,' (ئى پەيغەمبەر! ئىلكىڭدىكى ئاياللىرىڭدىن باشقا) يەنە خوتۇن ئالساڭ، ساڭا ھالال بولمايدۇ، باشقا ئاياللارنىڭ جامالى سېنى ئەجەبلەندۈرگەن تەقدىردىمۇ، ئۇلارنى يەڭگۈشلەش (ساڭا ھالال بولمايدۇ) پەقەت سېنىڭ ئىلكىڭدىكى چۆرىلەر بۇنىڭدىن مۇستەسنا، اﷲ ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,52,' ئى مۆمىنلەر! پەيغەمبەرنىڭ ئۆيلىرىگە كىرمەڭلار، پەقەت تاماققا چاقىرىلغان ۋاقتىڭلاردىلا كىرىڭلار، (باشقا ۋاقىتتا رۇخسەت بىلەن كىرگەندە) تاماقنىڭ پىشىشىنى كۈتۈپ ئولتۇرماڭلار، لېكىن چاقىرىلغاندا كىرىڭلار يۇ، تاماق يەپ بولغاندىن كېيىن تارقاپ كېتىڭلار، تاماقتىن كېيىن) پاراڭ سېلىشىپ ئولتۇرماڭلار، بۇ (يەنى تاماقتىن كېيىن پاراڭ سېلىشىپ ئولتۇرۇش) پەيغەمبەرنى رەنجىتىدۇ، پەيغەمبەر (بۇنى ئېيتىشقا) سىلەردىن خىجىل بولىدۇ، اﷲ ھەق (نى ئېيتىش) تىن خىجىل بولمايدۇ، سىلەر پەيغەمبەرنىڭ ئاياللىرىدىن بىر نەرسە سوراپ (ئالماقچى) بولساڭلار، پەردە ئارقىسىدىن سوراپ ئېلىڭلار، مۇنداق قىلىش سىلەرنىڭ دىللىرىڭلارنىمۇ، ئۇلارنىڭ دىللىرىنىمۇ ئەڭ پاك تۇتىدۇ، سىلەرنىڭ رەسۇلۇللاھنى رەنجىتىپ قويۇشۇڭلار دۇرۇس ئەمەس، رەسۇلۇللاھتىن كېيىن ئۇنىڭ ئاياللىرىنى ئېلىشىڭلارمۇ مەڭگۈ دۇرۇس ئەمەس، بۇنداق قىلىش (يەنى رەسۇلۇللاھنى رەنجىتىش ۋە ئۇنىڭ ئاياللىرىنى ئېلىش) ھەقىقەتەن اﷲ نىڭ دەرگاھىدا چوڭ گۇناھتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,53,' بىرەر نەرسىنى ئاشكارىلىساڭلارمۇ، يا (دىلىڭلاردا) يوشۇرساڭلارمۇ (اﷲ ھامان بىلىدۇ)، چۈنكى اﷲ ھەقىقەتەن ھەممە نەرسىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,54,' ئۇلارغا (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ ئاياللىرىغا) ئاتىلىرى بىلەن، ئوغۇللىرى بىلەن، قېرىنداشلىرى بىلەن، قېرىنداشلىرىنىڭ ئوغۇللىرى بىلەن، ھەمشىرىلىرىنىڭ ئوغۇللىرى بىلەن، مۆمىن ئاياللار بىلەن، قۇل، چۆرىلىرى بىلەن كۆرۈشۈش ھېچ گۇناھ ئەمەس، (ئى پەيغەمبەرنىڭ ئاياللىرى!) اﷲ تىن قورقۇڭلار، اﷲ ھەقىقەتەن ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,55,' اﷲ ھەقىقەتەن پەيغەمبەرگە رەھمەت يوللايدۇ (يەنى پەيغەمبەر ئەلەيھىسسالامغا رەھمەت قىلىدۇ، شەنىنى ئۇلۇغ قىلىدۇ، دەرىجىسىنى ئۈستۈن قىلىدۇ)، پەرىشتىلەرمۇ ھەقىقەتەن (پەيغەمبەرگە) مەغپىرەت تەلەپ قىلىدۇ، ئى مۆمىنلەر! سىلەر پەيغەمبەرگە دۇرۇد ئېيتىڭلار ۋە ئامانلىق تىلەڭلار (چۈنكى رەسۇلۇللاھنىڭ سىلەرنىڭ ئۈستۈڭلاردىكى ھەققى چوڭدۇر، ئۇ سىلەرنى گۇمراھلىقتىن ھىدايەتكە، زۇلمەتتىن يورۇقلۇققا چىقارغۇچىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,56,' اﷲ نى ۋە ئۇنىڭ پەيغەمبىرىنى رەنجىتىدىغانلار (يەنى اﷲ ۋە رەسۇلۇللاھ يامان كۆرىدىغان ئىشلارنى قىلىدىغانلار) نى اﷲ دۇنيا ۋە ئاخىرەتتە رەھمىتىدىن يىراق قىلىدۇ ۋە ئۇلارغا خار قىلغۇچى ئازابنى تەييار لايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,57,' مۆمىنلەر ۋە مۆمىنەلەرگە قىلمىغان ئىشلارنى (چاپلاپ) ئۇلارنى رەنجىتىدىغانلار (شۇ) بۇھتاننى ۋە روشەن گۇناھنى ئۈستىگە ئارتىۋالغان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,58,' ئى پەيغەمبەر! ئاياللىرىڭغا ،قىزلىرىڭغا ۋە مۆمىنلەرنىڭ ئاياللىرىغا ئېيتقىنكى، پۈركەنجى بىلەن بەدىنىنى ئورىۋالسۇن، بۇنداق قىلغاندا ئۇلارنىڭ (ھۈر ئاياللار ئىكەنلىكى) ئەڭ ئوڭاي تونۇلىدۇ ـ دە، باشقىلار ئۇلارغا چېقىلمايدۇ اﷲ (بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,59,' ئەگەر مۇناپىقلار، (نىفاقتىن) دىللىرىدا كېسەل بارلار، مەدىنىدە يالغان خەۋەر تارقاتقۇچىلار (قىلمىشلىرىدىن) يانمىسا، (ئى مۇھەممەد) ئەلۋەتتە بىز سېنى ئۇلارغا مۇسەللەت قىلىمىز، ئاندىن ئۇلار مەدىنىدە لەنەتكە ئۇچرىغان ھالدا سەن بىلەن ئازغىنا ۋاقىت تۇرالايدۇ (يەنى ئۇلار مەدىنىدىن سۈرگۈن قىلىنىپ ھەيدەپ چىقىرىلىدۇ، چىقىپ كېتىشكە تەييارلىق قىلىۋېلىش ئۈچۈن مەدىنىدە پەقەت ئازغىنا ۋاقىت تۇرالايدۇ) ئۇلار قەيەردە بايقالسا، شۇ يەردە تۇتۇلىدۇ ۋە ئۆلتۈرۈلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,60,' بۇ (يەنى مۇناپىقلارغا اﷲ نىڭ تۇتقان يولى) اﷲ نىڭ ئۆتكەنكى ئۈممەتلەرگە تۇتقان يولىدۇر، اﷲ نىڭ تۇتقان يولىدا ھېچقانداق ئۆزگىرىش تاپالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,61,' كىشىلەر سەندىن قىيامەتنىڭ ۋاقتىنى سورايدۇ «ئۇنى (مەن بىلمەيمەن) پەقەت اﷲ بىلىدۇ» دېگىن سەن نېمە بىلىسەن، قىيامەت يېقىن بولۇشى مۇمكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,62,' اﷲ ھەقىقەتەن كاپىرلارنى رەھمىتىدىن يىراق قىلدى ۋە ئۇلارغا دوزاخنى تەييارلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,63,' ئۇلار دوزاختا مەڭگۈ قالىدۇ، ئۇلار ھېچقانداق (ئۇلارنى قوغدايدىغان) دوست ۋە (ئۇلاردىن ئازابنى دەپئى قىلىدىغان) ياردەمچى تاپالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,64,' دوزاختا ئۇلارنىڭ يۈزلىرى تۈرۈلۈپ كېتىدىغان كۈندە، ئۇلار: «كاشكى بىز اﷲ قا ئىتائەت قىلغان بولساقچۇ! پەيغەمبەرگە ئىتائەت قىلغان بولساقچۇ!» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,65,' ئۇلار: «پەرۋەردىگارىمىز! بىز ھەقىقەتەن باشلىقلىرىمىزغا، كاتتىلىرىمىزغا ئىتائەت قىلدۇق، ئۇلار بىزنى توغرا يولدىن ئازدۇردى، پەرۋەردىگارىمىز! ئۇلارغا ئازابنى ئىككى ھەسسە بەرگىن ۋە ئۇلارغا قاتتىق لەنەت قىلغىن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,66,' ئى مۆمىنلەر! سىلەر مۇساغا ئازار قىلغان كىشىلەر (يەنى بەنى ئىسرائىل) دەك بولماڭلار، اﷲ مۇسانى ئۇلارنىڭ ئېيتقانلىرىدىن (يەنى تۆھمەتلىرىدىن) ئاقلىدى، مۇسا اﷲ نىڭ دەرگاھىدا ئابرۇيلۇق ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,67,' ئى مۆمىنلەر! اﷲ تىن قورقۇڭلار، توغرا سۆزنى قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,68,' اﷲ سىلەرنىڭ ئەمەللىرىڭلارنى تۈزەيدۇ (يەنى سىلەرنى ياخشى ئەمەللەرگە مۇۋەپپەق قىلىدۇ)، گۇناھلىرىڭلارنى مەغپىرەت قىلىدۇ، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلسا زور مۇۋەپپەقىيەت قازانغان بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,69,' شۈبھىسىزكى، بىز ئامانەتنى (يەنى پەرزلەرنى ۋە شەرىئەت تەكلىپلىرىنى) ئاسمانلارغا، زېمىنغا ۋە تاغلارغا تەڭلىدۇق، ئۇلار ئۇنى ئۈستىگە ئالمىدى، ئۇنىڭ (ئېغىرلىقى) دىن قورقتى، ئۇنى ئىنسان ئۈستىگە ئالدى، ئىنسان ھەقىقەتەن (ئۆزىگە) زۇلۇم قىلغۇچىدۇر، ھەقىقەتەن ناداندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,35,70,' (مەزكۇر ئامانەتكە خىيانەت قىلغانلىقلىرى ئۈچۈن) مۇناپىق ئەرلەرنى، مۇناپىق ئاياللارنى، مۇشرىك ئەرلەرنى، مۇشرىك ئاياللارنى اﷲ ئازابلايدۇ، (مەزكۇر ئامانەتكە رىئايە قىلغانلىقلىرى ئۈچۈن) مۆمىن ئەرلەر ۋە مۆمىن ئاياللارنى اﷲ ئەپۇ قىلىدۇ، (اﷲ مۆمىنلەرگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,1,'جىمى ھەمدۇسانا اﷲ قا خاستۇر، ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، ئاخىرەتتىكى ھەمدۇسانامۇ اﷲ قا خاستۇر، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىدىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,2,' اﷲ زېمىننىڭ قوينىغا كىرىپ كېتىدىغان (يەنى يامغۇر، كان ۋە ئۆلۈكلەرگە ئوخشاش) نەرسىلەرنى، زېمىندىن چىقىدىغان (ئۆسۈملۈك، بۇلاق ۋە قۇدۇقلارنىڭ سۇلىرى قاتارلىق) نەرسىلەرنى، ئاسماندىن چۈشىدىغان (پەرىشتە، يامغۇر، ئەجەل، رىزىق قاتارلىق) نەرسىلەرنى، ئاسمانغا ئۆرلەيدىغان (ياخشى ئەمەللەر، مەقبۇل دۇئالار قاتارلىق) نەرسىلەرنى بىلىپ تۇرىدۇ، اﷲ بەندىلىرىگە ناھايىتى مېھرىباندۇر، (تەۋبە قىلغۇچىلارنى) ناھايىتى مەغپىرەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,3,' كاپىرلار: «بىزگە قىيامەت كەلمەيدۇ» دەيدۇ، ئېيتقىنكى، «ئۇنداق ئەمەس، پەرۋەردىگارىم بىلەن قەسەمكى، قىيامەت سىلەرگە چوقۇم كېلىدۇ، اﷲ غەيىبنى بىلگۈچىدۇر، ئاسمانلاردىكى ۋە زېمىندىكى زەررە چاغلىق نەرسە اﷲ تىن يىراق ئەمەستۇر، ئۇنىڭ (يەنى زەررە) دىن كىچىكرەك نەرسە بولسۇن، چوڭراق نەرسە بولسۇن، لەۋھۇلمەھپۇزدا خاتىرىلەنمىگىنى يوق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,4,' (ئۇلارنىڭ لەۋھۇلمەھپۇزدا خاتىرىلىنىشى) ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى مۇكاپاتلاش ئۈچۈندۇر، ئەنە شۇلار مەغپىرەتكە ۋە ئېسىل رىزىققا ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,5,' بىزنىڭ ئايەتلىرىمىزگە كۈچىنىڭ بېرىچە قارشى تۇرغانلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,6,' ئىلىم بېرىلگەنلەر (يەنى ساھابىلەر ۋە كېيىنكى ئەمەل قىلغۇچى ئۆلىمالار) ساڭا پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان قۇرئاننىڭ شۈبھىسىز ھەق ئىكەنلىكىنى، (ئىنسانلارنى) غالىب، ھەمدۇساناغا لايىق اﷲ نىڭ يولىغا باشلايدىغانلىقىنى بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,7,' كاپىرلار (بىر ـ بىرىگە) ئېيتىدۇ: «سىلەرگە سىلەر (قەبرىلەردە چىرىپ) تىتما ـ تالاڭ بولۇپ كەتكەندىن كېيىن چوقۇم يېڭىدىن يارىتىلىسىلەر دەپ خەۋەر بېرىدىغان بىر ئادەمنى كۆرسىتىپ قويايلىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,8,' ئۇ اﷲ قا يالغاننى توقۇدىمۇ؟ يا ئۇ ئېلىشىپ قالدىمۇ؟» ھەرگىز ئۇنداق ئەمەس، ئاخىرەتكە ئىمان كەلتۈرمەيدىغانلار ئازابتا ۋە چوڭقۇر گۇمراھلىقتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,9,' ئۇلار ئالدىدىكى ۋە ئارقىسىدىكى (ئۇلارنى ئوراپ تۇرغان) ئاسمان بىلەن زېمىنغا قارىمامدۇ؟ ئەگەر خالىساق ئۇلارنى يەرگە يۇتقۇزىمىز، ياكى ئاسماننى ئۇلارنىڭ ئۈستىگە پارچە ـ پارچە قىلىپ چۈشۈرىمىز بۇنىڭدا اﷲ قا قايتقۇچى ھەر بەندە ئۈچۈن ئەلۋەتتە (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) دەلىل بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,10,' بىز داۋۇدقا دەرگاھىمىزدىن ھەقىقەتەن پەزل (يەنى پەيغەمبەرلىك، زەبۇر، تاغلارنىڭ ۋە قۇشلارنىڭ بويسۇنۇشى، تۆمۈرنىڭ يۇمشاق بولۇپ بېرىشى، ساۋۇت ياساشنى بىلدۈرۈشلەر) ئاتا قىلدۇق «ئى تاغلار! ئى قۇشلار! داۋۇت بىلەن بىرلىكتە تەسبىھ ئېيتىڭلار» (دېدۇق) تۆمۈرنى داۋۇدقا يۇمشاق قىلىپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,11,' (بىز ئۇنىڭغا ئېيتتۇق) «مۇكەممەل ساۋۇتلارنى ياسىغىن، ساۋۇتلارنى ياساشتا (ھالقىلىرىنى بىر ـ بىرىگە) تەكشى قىلغىن، (ئى داۋۇد ئائىلىسىدىكىلەر!) ياخشى ئىش قىلىڭلار، مەن ھەقىقەتەن سىلەرنىڭ ئەمەللىرىڭلارنى كۆرۈپ تۇرغۇچىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,12,' سۇلەيمانغا شامالنى (بويسۇندۇرۇپ بەردۇق)، شامال چۈشتىن ئىلگىرى بىر ئايلىق مۇساپىنى، چۈشتىن كېيىن بىر ئايلىق مۇساپىنى باساتتى، ئۇنىڭغا مىس (چىقىدىغان) بۇلاقنى ئاققۇزۇپ بەردۇق، بەزى جىنلار پەرۋەردىگارىنىڭ ئەمرى بويىچە سۇلەيماننىڭ ئالدىدا ئىشلەيتتى، ئۇلاردىن كىمكى بىزنىڭ ئەمرىمىزگە خىلاپلىق قىلىدىكەن، ئۇنىڭغا بىز دوزاخ ئازابىنى تېتىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,13,' ئۇلار سۇلەيمانغا ئۇ خالىغان كاتتا سارايلارنى، (مىستىن ـ ئەينەكتىن ئاجايىپ) ھەيكەللەرنى، كۆلدەك چوڭ لېگەنلەرنى، (چوڭلۇقتىن) مىدىرلىمايدىغان قازانلارنى ياسايتتى، (ئېيتتۇقكى) «ئى داۋۇد ئائىلىسىدىكىلەر! (اﷲ نىڭ بۇ چوڭ نېمەتلىرىگە) شۈكۈر قىلىڭلار»، مېنىڭ بەندىلىرىمدىن (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلغۇچى بەك ئاز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,14,' سۇلەيماننىڭ ۋاپاتىنى ھۆكۈم قىلغىنىمىزدا (يەنى ئۇنى ۋاپات قىلدۇرغىنىمىزدا) سۇلەيماننىڭ ئۆلگەنلىكىدىن جىنلارنى پەقەت ئۇنىڭ ھاسىسىنى يېگەن قۇرۇتلار خەۋەردار قىلدى سۇلەيمان (ھاسىسىنى قۇرۇت يەپ) يىقىلغان چاغدا جىنلارغا ئېنىق بولدىكى، ئەگەر ئۇلار غەيبنى بىلىدىغان بولسا ئىدى، خار قىلغۇچى ئازابتا (يەنى شۇنچە ئۇزاق ۋاقىت ئېغىر ئەمگەكتە) قالمىغان بولاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,15,' شۈبھىسىزكى، سەبەئ قەۋمىگە ئۇلارنىڭ تۇرىدىغان جايىدا (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان بۈيۈك) دەلىل بار ئىدى، ئۇنىڭ ئوڭ تەرىپىدە بىر تۈركۈم باغلار، سول تەرىپىدە بىر تۈركۈم باغلار بار ئىدى (ئۇلارغا ئېيتتۇقكى) «پەرۋەردىگارىڭلار بەرگەن رىزىقتىن يەڭلار ۋە ئۇنىڭغا شۈكۈر قىلىڭلار، (سىلەرنىڭ جايىڭلار) بەلەن جايدۇر، (سىلەرگە رىزىق بەرگەن پەرۋەردىگارىڭلار شۈكۈر قىلغۇچىنى) مەغپىرەت قىلغۇچى پەرۋەردىگاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,16,' ئۇلار (شۈكۈردىن) باش تارتتى، شۇنىڭ بىلەن ئۇلارغا قاتتىق سەلنى ئەۋەتتۇق ئۇلارنىڭ باغلىرىنى ئاچچىق مېۋىلىك، يۇلغۇنلۇق، ئاز ـ تولا سىدرىلىق (يەنى ياۋا چىلانلىق) باغلارغا ئايلاندۇردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,17,' ئۇلار كۇفرىلىق قىلغانلىقلىرى ئۈچۈن مۇشۇنداق جازالىدۇق، بىز پەقەت كۇفرىلىق قىلغۇچىلارنىلا جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,18,' ئۇلار بىلەن بىز بەرىكەتلىك قىلغان قىشلاقلار ئارىسىدا، كۆرۈنۈپ تۇرىدىغان (تۇتىشىپ كەتكەن) نۇرغۇن قىشلاقلارنى بەرپا قىلدۇق، ئۇلار ئارسىدىكى مېڭىشنى ئۆلچەملىك قىلدۇق (يەنى سەپەردە يېمەك ـ ئىچمەك ئېلىۋالمىسىمۇ بولىدىغان، ئەتىگەن چىققان يولۇچى چۈشتە بىر ئۆتەڭگە يېتىپ بارسا، كەچتە بىر ئۆتەڭگە يېتىپ بارىدىغان قىلدۇق)، (ئۇلارغا ئېيتتۇقكى) «ئۇلارنىڭ ئارىسىدا كېچىلەردە ۋە كۈندۈزلەردە تىنچ ـ ئامان مېڭىڭلار!»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,19,' ئۇلار: «پەرۋەردىگارىمىز! سەپەرلىرىمىز ئارىسىنى (يەنى ئۆتەڭلار ئارىسىدىكى مۇساپىنى) يىراق قىلغىن» دېدى ئۇلار ئۆزلىرىگە زۇلۇم قىلدى ئۇلارنى بىز (كېيىنكى كىشىلەرگە) چۆچەك قىلىپ قالدۇردۇق ئۇلارنى تىرىپىسىن قىلىۋەتتۇق، بۇنىڭغا ھەر بىر (بالاغا) سەۋر قىلغۇچى، (نېمەتكە) شۈكۈر قىلغۇچى ئۈچۈن نۇرغۇن ئىبرەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,20,' ئىبلىس ئۇلارنى (ئازدۇرۇشتىن ئىبارەت) گۇمانىنى ھەقىقەتەن ئىشقا ئاشۇردى چۈنكى بىر تۈركۈم مۆمىنلەردىن باشقا ئۇلارنىڭ ھەممىسى ئىبلىسقا ئەگەشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,21,' ئىبلىسنىڭ ئۇلار (يەنى ئەگەشكۈچىلەر) ئۈستىدىن (ۋەسۋەسە قىلىش بىلەن) ھۆكۈمرانلىق قىلىش پەقەت بىزنىڭ ئاخىرەتكە ئىمان ئېيتقۇچىلار بىلەن ئۇنىڭغا شەك كەلتۈرگۈچىلەرنى بىلىۋېلىشىمىز ئۈچۈندۇر، پەرۋەردىگارىڭ ھەممە نەرسىنى كۆزەتكۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,22,' ئېيتقىنكى، «سىلەر اﷲ نى قويۇپ مەبۇد دەپ قارىغىنىڭلارنى چاقىرىڭلار، ئۇلار ئاسمانلاردا ۋە زېمىندا زەررە چاغلىق نەرسىگە ئىگە بولالمايدۇ، ئۇلارنىڭ ئاسمان ـ زېمىننىڭ (يارىتىلىشى ۋە باشقۇرۇلۇشىدا اﷲ قا) ھېچ شېرىكچىلىگى يوق، ئۇلارنىڭ ئىچىدە اﷲ قا (ئاسمان ـ زېمىننى ئىدارە قىلىشتا ياردەملىشىدىغان) ھېچ ياردەمچىمۇ يوق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,23,' اﷲ ئىزنى بەرگەن ئادەمدىن باشقىغا اﷲ نىڭ دەرگاھىدا شاپائەت پايدا بەرمەيدۇ شاپائەت قىلغۇچىلارنىڭ دىللىرىدىن قورقۇنچ كۆتۈرۈلگەندە، ئۇلار ئاندىن (بەزىسى بەزىسىگە): «پەرۋەردىگارىڭلار (شاپائەت توغرولۇق) نېمە دېدى؟» دەيدۇ ئۇلار (جاۋابەن): «ھەقنى (دېدى) (يەنى مۆمىنلەرگە شاپائەت قىلىشقا رۇخسەت قىلدى)» دەيدۇ اﷲ يۈكسەكتۇر، كاتتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,24,' ئېيتقىنكى، «سىلەرگە ئاسمانلاردىن ۋە زېمىندىن كىم رىزىق بېرىپ تۇرىدۇ؟» ئېيتقىنكى، «اﷲ (رىزىق بېرىپ تۇرىدۇ)، شۈبھىسىزكى، بىز ياكى سىلەر چوقۇم ھىدايەتتىدۇرمىز، يا روشەن گۇمراھلىقتىدۇرمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,25,' ئېيتقىنكى، «بىزنىڭ قىلغان گۇناھىمىز ئۈچۈن سىلەر سوراققا تارتىلمايسىلەر، سىلەرنىڭ قىلمىشىڭلار ئۈچۈن بىز سوراققا تارتىلمايمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,26,' ئېيتقىنكى، «پەرۋەردىگارىمىز بىزنى (قىيامەت كۈنى) يىغىدۇ ئاندىن بىزنىڭ ئارىمىزدا ھەقلىق بىلەن ھۆكۈم چىقىرىدۇ، اﷲ ئادىل ھۆكۈم قىلغۇچىدۇر، (خالايىقنىڭ ئەھۋالىنى) بىلگۈچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,27,' ئېيتقىنكى، «ماڭا ئېيتىپ بېقىڭلارچۇ، سىلەرنىڭ اﷲ نىڭ شېرىكى دەپ ئاتىۋالغىنىڭلار ھەرگىزمۇ ئۇنداق ئەمەس، بەلكى ئۇ ـ اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,28,' سېنى بىز پەقەت پۈتۈن ئىنسانلار ئۈچۈن (مۆمىنلەرگە جەننەت بىلەن) خۇش خەۋەر بەرگۈچى ۋە (كاپىرلارنى ئازاب بىلەن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق ۋە لېكىن ئىنسانلارنىڭ تولىسى (بۇنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,29,' ئۇلار: «ئەگەر سىلەر راستچىل بولساڭلار، بۇ ۋەدە (يەنى سىلەر بىزنى قورقۇتۇۋاتقان ئازاب) قاچان ئىشقا ئاشىدۇ؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,30,' ئېيتقىنكى، «سىلەرگە (ئازاب ئۈچۈن) ۋەدە قىلىنغان بىر كۈن باركى، ئۇنىڭدىن بىردەممۇ ئىلگىرى ـ كېيىن بولمايسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,31,' كاپىرلار: «بۇ قۇرئانغا ۋە ئۇنىڭدىن ئىلگىرىكى كىتابلارغا ھەرگىز ئىشەنمەيمىز» دەيدۇ ئەگەر زالىملارنى پەرۋەردىگارىڭنىڭ دەرگاھىدا توختىتىپ قويۇلغان چاغدا بەزىسى بەزىسى بىلەن جېدەللىشىۋاتقاندا كۆرسەڭ ئىدىڭ (ئەلۋەتتە قورقۇنچلۇق ھالنى كۆرەتتىڭ)، بوزەك قىلىنغانلار چوڭچىلىق قىلغانلارغا: «ئەگەر سىلەر بولمىساڭلار بىز چوقۇم ئىمان ئېيتاتتۇق» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,32,' چوڭچىلىق قىلغانلار بوزەك قىلىنغانلارغا: «سىلەرگە ھىدايەت كەلگەندىن كېيىن بىز سىلەرنى ئۇنىڭدىن توستۇقمۇ؟ ھەرگىز توسمىدۇق، سىلەر ئۆزۈڭلار گۇناھ قىلغۇچى بولدۇڭلار» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,33,' بوزەك قىلىنغانلار چوڭچىلىق قىلغانلارغا: «ھەرگىز ئۇنداق ئەمەس، (سىلەرنىڭ بىزگە) كېچە ـ كۈندۈز قىلغان ھىيلە ـ مىكرىڭلار (بىزنى ئىماندىن توستى)، ئۆز ۋاقتىدا سىلەر بىزنى اﷲ نى ئىنكار قىلىشقا ۋە ئۇنىڭغا شېرىك كەلتۈرۈشكە بۇيرۇيتتۇڭلار» دەيدۇ ئازابنى كۆرگەن چاغدا، ئۇلار (ھەر ئىككى گۇرۇھ ئىمان ئېيتمىغانلىقلىرىغا) ئىچىدە پۇشايمان قىلىدۇ، كاپىرلارنىڭ بويۇنلىرىغا تاقاقلارنى سالىمىز، ئۇلارغا پەقەت قىلمىشلىرىنىڭ جازاسى بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,34,' قايسى شەھەرگە بولمىسۇن، بىرەر ئاگاھلاندۇرغۇچى ئەۋەتسەكلا ئۇنىڭ دۆلەتمەن ئادەملىرى: «شۈبھىسىزكى، بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ئىنكار قىلىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,35,' ئۇلار يەنە: «بىزنىڭ ماللىرىمىز ۋە بالىلىرىمىز ئەڭ كۆپ، بىز ئازابلانمايمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,36,' ئېيتقىنكى، «ھەقىقەتەن، پەرۋەردىگارىم كىمنىڭ رىزقىنى كەڭ قىلىشنى خالىسا ئۇنى كەڭ قىلىدۇ، (كىمنىڭ رىزقىنى تار قىلىشنى خالىسا ئۇنى) تار قىلىدۇ، لېكىن كىشىلەرنىڭ تولىسى (بۇنى) ئۇقمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,37,' ماللىرىڭلار ۋە بالىلىرىڭلار سىلەرنى بىزگە يېقىنلاشتۇرالمايدۇ، پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا قىلغان ئەمەللىرى ئۈچۈن ھەسسىلەپ ساۋاب بېرىلىدۇ، ئۇلار (جەننەتنىڭ) ئېسىل ئۆيلىرىدە (ھەر قانداق كۆڭۈلسىزلىكتىن) ئەمىن بولغان ھالدا تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,38,' بىزنىڭ ئايەتلىرىمىزگە كۈچىنىڭ بېرىچە قارشى تۇرغانلار ئازابقا دۇچار قىلىنغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,39,' ئېيتقىنكى، «ھەقىقەتەن، پەرۋەردىگارىم قايسى بەندىسىنىڭ رىزقىنى كەڭ قىلىشنى خالىسا ئۇنى كەڭ قىلىدۇ، (قايسى بەندىسىنىڭ رىزقىنى تار قىلىشنى خالىسا ئۇنى) تار قىلىدۇ، (اﷲ نىڭ يولىدا) بەرگەن نەرسەڭلارنىڭ ئورنىنى اﷲ تولدۇرۇپ بېرىدۇ، ئۇ رىزىق بەرگۈچىلەرنىڭ ياخشىسىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,40,' ئۇ كۈندە اﷲ ئۇلار (يەنى مۇشرىكلار) نىڭ ھەممىسىنى (ھېساب ئېلىش ئۈچۈن) يىغىدۇ، ئاندىن پەرىشتىلەرگە: «بۇ كىشىلەر سىلەرگە چوقۇنغانمىدى؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,41,' ئۇلار ئېيتىدۇ: «سەن پاكتۇرسەن، بىزنىڭ دوستىمىز ئۇلار ئەمەس، سەن (ئۇلار بىزگە چوقۇنمايتتى) بەلكى جىنغا چوقۇناتتى ئۇلارنىڭ تولىسى جىنغا ئىمان كەلتۈرەتتى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,42,' بۈگۈن سىلەر بىر ـ بىرىڭلارغا پايدا ـ زىيان يەتكۈزەلمەيسىلەر، بىز (اﷲ تىن غەيرىيگە چوقۇنغۇچى) زالىملارغا: «سىلەر چىنپۈتمىگەن دوزاخ ئازابىنى تېتىڭلار» دەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,43,' ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز ئوقۇپ بېرىلگەندە، ئۇلار: «بۇ پەقەت سىلەرنى ئاتا ـ بوۋاڭلار چوقۇنغان بۇتلارغا چوقۇنۇشتىن توسماقچى بولغان ئادەمدۇر» دەيدۇ ھەمدە ئۇلار: «بۇ پەقەت توقۇلغان يالغان سۆزدۇر» دەيدۇ كاپىرلارغا ھەقىقەت يېتىپ كەلگەندە، ئۇلار: «بۇ پەقەت ئوپئوچۇق سېھىردۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,44,' ئۇلارغا ئوقۇيدىغان ھېچقانداق كىتابلارنى ئاتا قىلغىنىمىز يوق سەندىن ئىلگىرى ئۇلارغا ھېچقانداق پەيغەمبەرمۇ ئەۋەتكىنىمىز يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,45,' ئۇلاردىن ئىلگىرى ئۆتكەنلەر (ھەقنى) ئىنكار قىلغان، ئۇلار (يەنى مەككە كۇففارلىرى) ئۇلار (يەنى ئىلگىرىكىلەر) ئېرىشكەن كۈچ ـ قۇۋۋەتنىڭ ئوندىن بىرىگىمۇ ئېرىشمىگەن، ئۇلار مېنىڭ پەيغەمبەرلىرىمنى ئىنكار قىلدى ئۇلارغا مېنىڭ جازايىم قانداق بولدى؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,46,'ئېيتقىنكى، «مەن سىلەرگە بىر ئىشنى تەۋسىيە قىلىمەن: سىلەر خۇدالىق ئۈچۈن ئىككىدىن، يا بىردىن تۇرۇڭلار (يەنى اﷲ نىڭ رازىلىقى ئۈچۈن يىغىلىپ ياكى يەككە ھالدا ھەقنى تىلەڭلار)، ئاندىن پىكىر يۈرگۈزۈڭلار» ھەمراھىڭلاردا (يەنى مۇھەممەدتە) ھېچ مەجنۇنلۇق يوق، پەقەت ئۇ قاتتىق ئازاب نازىل بولۇشتىن ئىلگىرى سىلەرگە ئەۋەتىلگەن بىر ئاگاھلاندۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,47,' ئېيتقىنكى، «سىلەردىن ھېچقانداق ھەق تەلەپ قىلغىنىم يوق، ئۇ سىلەرگە بولسۇن؛ ئەجرىمنى پەقەت اﷲ تىن تىلەيمەن اﷲ ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,48,' ئېيتقىنكى، «پەرۋەردىگارىم، ھەقىقەتەن ھەق ئارقىلىق (باتىلنى) ئاتىدۇ (يەنى پاكىتنى روشەن بايان قىلىدۇ)، اﷲ غەيبنى تامامەن بىلگۈچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,49,' ئېيتقىنكى، «ھەق (يەنى ئىسلام) كەلدى (باتىل يوقالدى)، باتىل (بىرەر مەخلۇقنى) پەيدا قىلالمايدۇ، (يوقالغان بىرەر مەخلۇقنى) ئەسلىگە كەلتۈرەلمەيدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,50,' ئېيتقىنكى، «ئەگەر ئازسام ئازغانلىقىمنىڭ زىيىنى ئۆزۈمگىدۇر، ئەگەر ھىدايەت تاپسام (بۇ) پەرۋەردىگارىمنىڭ ماڭا ۋەھيى قىلغان قۇرئانىنىڭ سەۋەبىدىندۇر، اﷲ ھەقىقەتەن ئاڭلاپ تۇرغۇچىدۇر، يېقىندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,51,' ئەگەر سەن ئەينى ۋاقىتتا ئۇلارنىڭ قورقۇپ قاچىدىغان يەر تاپالمايۋاتقانلىقىنى، دوزاخقا (ئېلىپ بېرىلىش ئۈچۈن مەھشەرگاھقا) يېقىن جايدا تۇتۇلۇۋاتقانلىقىنى كۆرسەڭ (ئەلۋەتتە قورقۇنچلۇق ھالىنى كۆرەتتىڭ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,52,' ئۇلار (ئازابنى كۆرگەندە): «ئىمان ئېيتتۇق» دەيدۇ، ئۇلار قانداقمۇ يىراق جايدىن ئىماننى قولغا كەلتۈرەلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,53,' ئۇلار ئىلگىرى ئۇنى (يەنى پەيغەمبەرنى) ئىنكار قىلغان ئىدى ئۇلار غەيب (ئىشلىرى) ئۈستىدە يىراق جايدىن قارىسىغا سۆزلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,36,54,' ئۇلار بىلەن ئۇلارنىڭ ئارزۇ قىلغان نەرسىلىرى (يەنى ئىمان ئېيتىش ۋە جەننەتكە كىرىش) ئارىسىدا توسالغۇ پەيدا قىلىندى ئۇلارغا ئىلگىرى ئۇلارغا ئوخشاشلارغا قىلىنغاندەك قىلىندى چۈنكى ئۇلار ھازىر ئىمان كەلتۈرگەن نەرسىلىرى ئۈستىدە (دۇنيادىكى چاغلىرىدا) چوڭقۇر شەكتە ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,1,'جىمى ھەمدۇسانا ئاسمانلارنى ۋە زېمىننى ئۆرنەكسىز ياراتقۇچى، پەرىشتىلەرنى ئىككى قاناتلىق، ئۈچ قاناتلىق، تۆت قاناتلىق ئەلچىلەر قىلغۇچى اﷲ قا خاستۇر! اﷲ يارىتىشتا خالىغىنىنى زىيادە قىلىدۇ، اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,2,' اﷲ رەھمىتىدىن كىشىلەرگە ئاچقان نەرسىنى ھەرگىزمۇ توسۇپ قالالىغۇچى بولمايدۇ، اﷲ رەھمىتىدىن توسقان نەرسىنى، اﷲ توسقاندىن كېيىن، ئۇنى ھېچ قويۇپ بەرگۈچى بولمايدۇ، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,3,' ئى ئىنسانلار! اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ياد ئېتىڭلار، اﷲ تىن باشقا سىلەرگە ئاسماندىن ۋە زېمىندىن رىزىق بېرىپ تۇرىدىغان ياراتقۇچى بارمۇ؟ اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، (اﷲ نىڭ ئىبادىتىدىن) قانداقمۇ باش تارتىسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,4,' (ئى مۇھەممەد!) ئەگەر ئۇلار سېنى ئىنكار قىلسا (قايغۇرۇپ كەتمە)، ھەقىقەتەن سەندىن ئىلگىرىكى پەيغەمبەرلەرمۇ ئىنكار قىلىنغان، ھەممە ئىش اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,5,' ئى ئىنسانلار! اﷲ نىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر، سىلەرنى ھەرگىز دۇنيا تىرىكچىلىكى مەغرۇر قىلمىسۇن، شەيتاننىڭ سىلەرنى اﷲ نىڭ ئەپۇسىنىڭ كەڭلىكى بىلەن مەغرۇر قىلىشىغا يول قويماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,6,' شەيتان ھەقىقەتەن سىلەرگە دۈشمەندۇر، ئۇنى دۈشمەن تۇتۇڭلار، شەيتان ئۆزىنىڭ تەۋەلىرىنى ئەھلى دوزاختىن بولۇشقا چاقىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,7,' كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار مەغپىرەتكە ۋە كاتتا ساۋابقا ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,8,' ئۆزىنىڭ يامان ئەمەلى چىرايلىق كۆرسىتىلگەن، شۇنىڭ بىلەن ئۇنى چىرايلىق ھېسابلىغان كىشى (ھىدايەت تاپقان كىشى بىلەن ئوخشاشمۇ؟) اﷲ ھەقىقەتەن خالىغان كىشىنى گۇمراھ قىلىدۇ (خالىغان كىشىنى ھىدايەت قىلىدۇ) شۇڭا سەن ئۇلار ئۈچۈن (يەنى ئىمان ئېيتمىغانلىقلىرى ئۈچۈن) قايغۇرۇپ ئۆزۈڭنى ھالاك قىلىۋالمىغىن، اﷲ ھەقىقەتەن ئۇلارنىڭ قىلمىشلىرىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,9,' اﷲ شاماللارنى ئەۋەتىپ (ئۇنىڭ بىلەن) بۇلۇتنى قوزغايدۇ، بىز بۇلۇتلارنى ئۆلۈك (يەنى قاغجىراق) بىر يەرگە ھەيدەيمىز (بۇلۇتتىن يامغۇر ياغدۇرىمىز)، شۇ يامغۇر بىلەن ئۆلگەن زېمىننى تىرىلدۈرىمىز ئۆلۈكلەرنىڭ تىرىلدۈرۈلۈشى ئەنە شۇنداقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,10,' كىمكى (دۇنيادا) شەرەپ ئىزدەيدىكەن، شەرەپنىڭ ھەممىسى اﷲ قا مەنسۇپ (ئۇنى اﷲ تىن تىلىسۇن) ياخشى سۆز اﷲ نىڭ دەرگاھىغا ئۆرلەيدۇ، ياخشى ئەمەل ئۇنى كۆتۈرىدۇ، (پەيغەمبەرگە قارشى) ھىيلە ـ مىكىرلەرنى تۈزىدىغانلار قاتتىق ئازابقا دۇچار بولىدۇ، ئۇلارنىڭ ھىيلە ـ مىكرى ئىشقا ئاشمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,11,' اﷲ سىلەرنى (يەنى ئەسلىڭلار ئادەم ئەلەيھىسسالامنى) تۇپراقتىن ياراتتى ئاندىن ئابىمەنىيدىن پەيدا قىلدى ئاندىن سىلەرنى (ئەر ـ ئايالدىن ئىبارەت) بىر جۈپ قىلدى، ھەر قانداق ئايالنىڭ ھامىلدار بولۇشى ۋە تۇغۇشى اﷲ نىڭ ئىلمىنىڭ سىرتىدا ئەمەس، ھەرقانداق ئادەمنىڭ ئۆمرىنىڭ ئۇزۇن ياكى قىسقا بولۇشى لەۋھۇلمەھپۇزدا يېزىلغان، بۇ ھەقىقەتەن اﷲ قا ئاسان');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,12,' ئىككى دېڭىز (يەنى دېڭىز بىلەن دەريا) ئوخشاش ئەمەس بۇنىڭ (يەنى دەريانىڭ) سۈيى تاتلىق، تەملىك بولۇپ (گالدىن) سىلىق ئۆتىدۇ، بۇ (يەنى دېڭىز) ناھايىتى ئاچچىق، تۇزلۇق، ئۇلارنىڭ ھەر ئىككىسىدىن يېڭى گۆشلەرنى (يەنى بېلىقلارنى) يەيسىلەر، (دېڭىزدىن) سىلەر تاقايدىغان زىننەت بۇيۇملىرى (يەنى ئۈنچە ـ مارجانلار) نى چىقىرىسىلەر، اﷲ نىڭ نېمىتىنى تەلەپ قىلىشڭلار ئۈچۈن، شۈكۈر قىلىشىڭلار ئۈچۈن (اﷲ نىڭ ئەمرى بىلەن يۈك ـ تاق، يېمەك ـ ئىچمەكلەر قاچىلانغان) كېمىلەرنىڭ دېڭىزدا دولقۇن يېرىپ كېتىۋاتقانلىقىنى كۆرىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,13,' اﷲ كېچىنى كۈندۈزگە كىرگۈزىدۇ، كۈندۈزنى كېچىگە كىرگۈزىدۇ (شۇنىڭ بىلەن كېچە ـ كۈندۈزنىڭ ئۇزۇن ـ قىسقا بولۇشى جايلار، پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)، اﷲ كۈن بىلەن ئاينى (بەندىلىرىنىڭ مەنپەئىتىگە) بويسۇندۇرۇپ بەردى ھەر ئىككىلىسى مۇئەييەن مۇددەتكىچە (يەنى قىيامەتكىچە ئۆز ئوقىدا) سەير قىلىدۇ، ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، پادىشاھلىق ئۇنىڭغا خاستۇر، اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلار قىلچىلىك نەرسىگە ئىگە ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,14,' ئەگەر ئۇلارنى چاقىرساڭلار، سىلەرنىڭ چاقىرغىنىڭلارنى ئاڭلىمايدۇ، ئاڭلىغان تەقدىردىمۇ سىلەرگە جاۋاپ قايتۇرالمايدۇ، قىيامەت كۈنى ئۇلار (اﷲ قا ئۇلارنى) شېرىك قىلغانلىقىڭلارنى ئىنكار قىلىدۇ، (مۇشرىكلارنىڭ ۋە ئۇلارنىڭ بۇتلىرىنىڭ قىيامەت كۈنىدىكى ئەھۋالىنى ھېچ ئەھەدى) ساڭا ھەممىدىن خەۋەردار زاتتەك (يەنى اﷲ دەك) ئېيتىپ بېرەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,15,' ئى ئىنسانلار! سىلەر اﷲ قا موھتاجسىلەر، اﷲ (ھەممىدىن) بىھاجەتتۇر، مەدھىيىگە لايىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,16,' ئەگەر اﷲ خالىسا، سىلەرنى ھالاك قىلىپ (ئورنۇڭلارغا) يېڭى بىر خەلقنى پەيدا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,17,' بۇ اﷲ قا قىيىن ئەمەس');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,18,' ھېچبىر گۇناھكار ئادەم بىراۋنىڭ گۇناھىنى ئۈستىگە ئالالمايدۇ، گۇناھى ئېغىر بىر ئادەم باشقا بىراۋنى ئۆز گۇناھىنى ئۈستىگە ئېلىۋېلىشقا چاقىرسا، ئۇ يېقىن تۇغقىنى بولغان تەقدىردىمۇ، ئۇنىڭ گۇناھىدىن ئازراق نەرسىنى ئۈستىگە ئېلىۋالالمايدۇ، سەن پەقەت پەرۋەردىگارىنى كۆرمەي تۇرۇپ ئۇنىڭدىن قورقىدىغانلارنى، نامازنى ئادا قىلىدىغانلارنى ئاگاھلاندۇرىسەن، كىمكى (گۇناھلاردىن) پاكلىنىدىكەن، ئۇ ئۆزى ئۈچۈن پاكلانغان بولىدۇ ئاخىر قايتىدىغان جاي اﷲ نىڭ دەرگاھىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,19,' كور ئادەم بىلەن كۆزى ساق ئادەم (يەنى كاپىر بىلەن مۆمىن) باراۋەر بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,20,' زۇلمەت بىلەن نۇر (يەنى باتىل بىلەن ھەق) باراۋەر بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,21,' سايە بىلەن ئىسسىق (يەنى جەننەت بىلەن دوزاخ) باراۋەر بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,22,' تىرىكلەر بىلەن ئۆلۈكلەر (يەنى مۆمىنلەر بىلەن كاپىرلار) باراۋەر بولمايدۇ، شۈبھىسىزكى، اﷲ (ھەق دەۋەتنى) خالىغان كىشىلەرگە ئاڭلىتىدۇ سەن قەبرىلەردىكىلەرگە (يەنى كاپىرلارغا) ئاڭلىتالمايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,23,' سەن پەقەت بىر ئاگاھلاندۇرغۇچىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,24,' ھەقىقەتەن بىز سېنى ھەق (دىن) بىلەن (مۆمىنلەرگە) خۇش خەۋەر بەرگۈچى، (كاپىرلارغا) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق، قانداقلا بىر ئۈممەت بولمىسۇن، ئۇنىڭغا پەيغەمبەر كەلگەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,25,' ئۇلار سېنى ئىنكار قىلسا، ئۇلاردىن بۇرۇن ئۆتكەن ئۈممەتلەرمۇ (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) ئىنكار قىلغان، پەيغەمبەرلىرى ئۇلارغا روشەن مۆجىزىلەرنى، (ئىبراھىم ئەلەيھىسسالامغا نازىل قىلىنغان سەھىپىلەرگە ئوخشاش) سەھىپىلەرنى، (تەۋرات، ئىنجىلدەك) نۇرلۇق كىتابلارنى ئېلىپ كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,26,' ئاندىن كاپىرلارنى جازالىدىم، (ئۇلارغا بەرگەن) ئازابىم قانداق ئىكەن؟ (يەنى ئۇلارغا قانداق قاتتىق ئازاب قىلدىم؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,27,'كۆرمەمسەنكى، اﷲ بۇلۇتتىن يامغۇر ياغدۇردى ئۇنىڭ بىلەن تۈرلۈك، رەڭگارەڭ مېۋىلەرنى چىقاردى، (شۇنىڭدەك اﷲ تاغلارنى ياراتتى) تاغلارنىڭ ئاق، قىزىللىرى، ھەر خىل رەڭدىكى يوللۇقلىرى ۋە قاپقارىلىرىمۇ بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,28,' شۇنىڭدەك ئىنسانلارنى، ھايۋانلارنى، چاھارپايلارنىمۇ خىلمۇخىل رەڭلىك قىلىپ ياراتتى، اﷲ نىڭ بەندىلىرى ئىچىدە اﷲ تىن پەقەت ئالىملارلا قورقىدۇ، اﷲ ھەقىقەتەن غالىبتۇر، (بەندىلىرى ئىچىدە تەۋبە قىلغانلارنى) مەغپىرەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,29,' شۈبھىسىزكى، اﷲ نىڭ كىتابىنى تىلاۋەت قىلىپ تۇرىدىغانلار، نامازنى ئادا قىلىدىغانلار كاسات بولمايدىغان تىجارەتنى ئۈمىد قىلىدۇ، بىز رىزىق قىلىپ بەرگەن نەرسىلەرنى (اﷲ نىڭ رازىلىقى ئۈچۈن) يوشۇرۇن ۋە ئاشكارا سەرپ قىلىدىغان كىشىلەرگە');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,30,'اﷲ ئەجىرلىرىنى تولۇق بېرىدۇ ۋە مەرھەمىتىدىن ئۇلارغا ئاشۇرۇپ بېرىدۇ، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,31,' ساڭا بىز ۋەھيى قىلغان كىتاب (يەنى قۇرئان) ھەقتۇر ئىلگىرىكى كىتابلارنىڭ (راستلىقىنى) ئىسپاتلىغۇچىدۇر، اﷲ ھەقىقەتەن بەندىلىرىنى تولۇق بىلگۈچىدۇر، كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,32,' ئاندىن بىز كىتاب (يەنى قۇرئان) نى بەندىلىرىمىزدىن بىز تاللىغان كىشىلەرگە مىراس قىلىپ بەردۇق ئۇلارنىڭ بەزىسى ئۆزىگە زۇلۇم قىلغۇچىدۇر (يەنى قۇرئاننى تىلاۋەت قىلغان بىلەن ئەمەل قىلمايدۇ)، بەزىسى ئوتتۇراھالدۇر (يەنى كۆپ چاغلاردا قۇرئانغا ئەمەل قىلىپ، قىسمەن چاغلاردا ئەمەل قىلمايدۇ) ۋە بەزىسى اﷲ نىڭ ئىزنى بويىچە ياخشى ئىشلارنى قىلىشقا ئالدىرىغۇچىدۇر ئەنە شۇ كاتتا مەرھەمەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,33,' ئۇلار ھەمىشە تۇرىدىغان جەننەتلەرگە كىرىدۇ ئۇلار جەننەتلەردە ئالتۇن بىلەيزۈكلەرنى، مەرۋايىتلارنى تاقايدۇ، ئۇلارنىڭ كىيىمى يىپەكتىن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,34,' ئۇلار ئېيتتى: «جىمى ھەمدۇسانا بىزدىن غەم ـ قايغۇنى كەتكۈزۈۋەتكەن اﷲ قا مەنسۇپتۇر! بىزنىڭ پەرۋەردىگارىمىز ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,35,' اﷲ مەرھەمىتىدىن بىزنى جەننەتتە تۇرغۇزدى جەننەتتە بىز ھەرگىز جاپا تارتمايمىز، ھەرگىز چارچىمايمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,36,' كاپىرلار دوزاخ ئازابىغا دۇچار بولىدۇ، ئۇلارنىڭ ئۆلۈپ (ئارام تېپىپ قالماسلىقى ئۈچۈن) جانلىرى ئېلىنمايدۇ، ئۇلاردىن ئازابمۇ يېنىكلىتىلمەيدۇ، كۇفرىدا ھەددىدىن ئاشقان ھەر ئىنساننى ئەنە شۇنداق جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,37,' ئۇلار دوزاختا: «پەرۋەردىگارىمىز! بىزنى چىقىرىۋەتسەڭ (دۇنيادىكى ۋاقىتتا) قىلغان ئەمەللىرىمىزدىن باشقا ئەمەللەرنى قىلساق» دەپ يالۋۇرۇپ توۋلايدۇ (اﷲ ئېيتىدۇ) «سىلەرگە ئۆمرۈڭلارنى ئۇزۇن قىلىپ، ۋەز ـ نەسىھەت ئالىدىغان ئادەم ۋەز ـ نەسىھەت ئالالىغۇدەك ۋاقىت بەرمىدىممۇ؟ سىلەرگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلدىغۇ، ئەمدى ئازابنى تېتىڭلار، زالىملارغا ھېچقانداق ياردەم بەرگۈچى يوق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,38,' اﷲ ھەقىقەتەن ئاسمانلاردىكى ۋە زېمىندىكى غەيبنى بىلگۈچىدۇر اﷲ ھەقىقەتەن دىللاردىكى سىرلارنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,39,' اﷲ سىلەرنى زېمىندا ئورۇنباسارلار قىلدى، كىمكى كاپىر بولىدىكەن، ئۇنىڭ كۇفرىنىڭ زىيىنى ئۆزىگە بولىدۇ، كاپىرلارنىڭ كۇفرى پەرۋەردىگارىنىڭ دەرگاھىدا پەقەت غەزەپنىلا زىيادە قىلىدۇ (يەنى ئۇلارنىڭ كۇفرى ئۇلارنى اﷲ نىڭ رەھمىتىدىن تېخىمۇ يىراقلاشتۇرىدۇ)، كاپىرلارنىڭ كۇفرى ئۇلارغا پەقەت ھالاكەتنىلا زىيادە قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,40,' ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ؟ سىلەر اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلار (نېمە بىلەن چوقۇنۇشقا لايىق بولدى؟) ماڭا ئېيتىپ بېرىڭلاركى، ئۇلار زېمىندىن نېمىلەرنى ياراتتى؟ يا ئۇلار ئاسمانلارنى اﷲ بىلەن بىرلىكتە ياراتتىمۇ؟ يا ئۇلارغا بىز بىر كىتاب بەرگەن بولۇپ، ئۇلار شۇ كىتابتىكى روشەن دەلىللەرگە ئاساسلىنامدۇ؟» ھەرگىز ئۇنداق ئەمەس، زالىملار بىر ـ بىرىگە پەقەت يالغاننىلا ۋەدە قىلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,41,' اﷲ ھەقىقەتەن ئاسمانلارنى ۋە زېمىننى (قۇدرىتى ۋە گۈزەل ھېكمىتى بىلەن) چۈشۈپ كېتىشتىن توختىتىپ تۇرىدۇ ئەگەر ئۇلار چۈشۈپ كەتسە، ئۇلارنى ھېچ ئەھەدى توختىتىپ تۇرالمايدۇ اﷲ ھەقىقەتەن ھەلىمدۇر (يەنى كۇففارلار ئازابقا تېگىشلىك بولسىمۇ، ئۇلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)، ناھايىتى مەغپىرەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,42,' ئۇلار ئۆزلىرىگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلسە، ھەر قانداق ئۈممەتكە قارىغاندا ئەڭ ھىدايەت تاپقۇچى بولىدىغانلىقلىرى بىلەن قاتتىق قەسەم ئىچىشتى ئۇلارغا ئاگاھلاندۇرغۇچى كەلگەن چاغدا (ھىدايەتتىن) تېخىمۇ يىراق بولۇشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,43,' (بۇنداق بولۇشى) ئۇلارنىڭ زېمىندا تەكەببۇرلۇق قىلغانلىقلىرى ۋە ھىيلە ـ مىكىر ئىشلەتكەنلىكلىرىدىندۇر ھىيلە ـ مىكىرنىڭ ۋابالى پەقەت ھىيلە ـ مىكىر ئىشلەتكەن ئادەمنىڭ ئۆزىگە بولىدۇ، ئۇلار پەقەت ئىلگىرىكى ئۈممەتلەرگە قوللىنىلغان (اﷲ نىڭ ئۇلارنى ھالاك قىلىش ۋە جازالاشتىن ئىبارەت) يولىنىلا كۈتىدۇ، اﷲ نىڭ (مەخلۇقاتلار ئۈستىدە قوللانغان) يولىدا ھەرگىزمۇ ھېچقانداق ئۆزگىرىشنى كۆرمەيسەن، اﷲ يولىدا ھەرگىز يۆتكىلىشنىمۇ (يەنى ئازابنىڭ ئازابقا تېگىشلىك بولغانلار ئۈستىدىن باشقىلارنىڭ ئۈستىگە يۆتكىلىشىنى) كۆرمەيسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,44,' ئۇلار زېمىندا سەير قىلىپ، ئۇلاردىن ئىلگىرىكىلەر (يەنى ھالاك بولغان ئۈممەتلەر)نىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆرمىدىمۇ؟ ئۇلار (يەنى ئۆتكەنكى ئۈممەتلەر) بۇلاردىن تېخىمۇ كۈچلۈك ئىدى ئاسمانلاردىكى ۋە زېمىندىكى ھېچ نەرسە اﷲ نى ئىلاجسىز قالدۇرالمايدۇ اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,37,45,' ئەگەر اﷲ ئىنسانلارنى ئۇلارنىڭ قىلمىشلىرى تۈپەيلىدىن جازالايدىغان بولسا، يەر يۈزىدە ھېچبىر جان ئىگىسىنى قويمىغان بولاتتى ۋە لېكىن اﷲ ئۇلار (دىن ھېساب ئېلىش) نى مۇئەييەن ۋاقىتقىچە تەخىر قىلىدۇ، ئۇلار (دىن ھېساب ئېلىش) نىڭ ۋاقتى يېتىپ كەلگەندە (ئۇلارنىڭ ئەمەللىرىگە قاراپ جازا بېرىدۇ)، اﷲ ھەقىقەتەن بەندىلىرىنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,1,' ھېكمەتلىك قۇرئان بىلەن قەسەمكى، (ئى مۇھەممەد!) شەك ـ شۈبھىسىزكى، سەن پەيغەمبەرلەردىنسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,2,' توغرا يولدىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,3,' قۇرئان ئاتا ـ بوۋىلىرى ئاگاھلاندۇرۇلمىغان (يەنى ئۇلارغا پەيغەمبەر كەلمىگەن)، غاپىل قالغان بىر قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، غالىب، ناھايىتى مېھرىبان اﷲ تەرىپىدىن نازىل قىلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,4,' ئۇلارنىڭ كۆپچىلىكىگە (ئازاب توغرىسىدىكى) ھۆكۈم ھەقىقەتەن تېگىشلىك بولدى شۇنىڭ ئۈچۈن ئۇلار ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,5,' بىز ھەقىقەتەن ئۇلارنىڭ بويۇنلىرىغا ئېڭەكلىرىگىچە تاقاقلارنى تاقىدۇق، شۇنىڭ ئۈچۈن ئۇلار باشلىرىنى ئىگەلمەيدۇ (يەنى ئۇلار ئىمانغا بويسۇنمايدۇ ۋە ئىمانغا باش ئەگمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,6,' ئۇلارنىڭ ئالدىدا بىر توسۇق، كەينىدە بىر توسۇق قىلدۇق، ئۇلارنىڭ كۆزلىرىنى پەردىلىدۇق، شۇنىڭ ئۈچۈن ئۇلار (ئىماننىڭ يوللىرى توسۇلۇپ كەتكەنلىكى ئۈچۈن) ھېچ نەرسىنى كۆرەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,7,' ئۇلارنى ئاگاھلاندۇرامسەن، ئاگاھلاندۇرمامسەن، ئۇلارغا بەرىبىر ئوخشاش، ئىمان ئېيتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,8,' سەن پەقەت قۇرئانغا ئەگەشكەن، مەرھەمەتلىك اﷲ نى كۆرمەي تۇرۇپ قورققان كىشىنى ئاگاھلاندۇرالايسەن (يەنى ئاگاھلاندۇرۇشۇڭنىڭ پايدىسى بولىدۇ)، ئۇنىڭغا (اﷲ نىڭ ئۇنىڭ گۇناھلىرىنى) مەغپىرەت قىلىدىغانلىقى ۋە ئېسىل ساۋاب (يەنى جەننەت ئاتا قىلىدىغانلىقى) بىلەن خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,9,' بىز، شۈبھىسىزكى، ئۆلۈكلەرنى تىرىلدۈرىمىز، ئۇلارنىڭ (دۇنيادا) قىلغان (ياخشى ـ يامان) ئەمەللىرىنى ۋە ئىشلىرىنى خاتىرىلەپ قويىمىز، ھەممە شەيئىنى روشەن دەپتەردە (يەنى لەۋھۇلمەھپۇزدا) تولۇق خاتىرىلەپ قويغانمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,10,' ئۇلارغا (يەنى كۇففارلارغا) شەھەر (يەنى ئەنتاكىيە) ئاھالىسىنى مىسال قىلىپ كەلتۈرگىن، ئۆز ۋاقتىدا ئۇلارغا پەيغەمبەرلەر كەلگەن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,11,' ئەينى زاماندا ئۇلارغا ئىككى پەيغەمبەر ئەۋەتتۇق ئۇلار ئۇ ئىككى پەيغەمبەرنى ئىنكار قىلدى ئۈچىنچى پەيغەمبەرنى ئەۋەتىش بىلەن (ئۇلارغا) ياردەم قىلدۇق، پەيغەمبەرلەر (ئۇلارغا): «بىز ھەقىقەتەن (اﷲ نىڭ) سىلەرگە (يەنى سىلەرنى ھىدايەت قىلىشقا) ئەۋەتىلگەن ئەلچىلىرىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,12,' ئۇلار: «سىلەر پەقەت بىزگە ئوخشاش ئىنسان، مېھرىبان اﷲ ھېچ نەرسە نازىل قىلغىنى يوق، سىلەر پەقەت يالغانچىسىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,13,' ئەلچىلەر: «پەرۋەردىگارىمىز بىلىدۇكى، بىز شەك ـ شۈبھىسىز سىلەرگە ئەۋەتىلگەن ئەلچىلەرمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,14,' بىزنىڭ مەسئۇلىيىتىمىز پەقەت (روشەن دەلىللەر بىلەن) چۈشىنىشلىك قىلىپ تەبلىغ قىلىشتۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,15,' ئۇلار (ئەلچىلەرگە): «بىز سىلەردىن شۇم پال ئالدۇق ئەگەر سىلەر (دەۋىتىڭلارنى) توختاتمىساڭلار، سىلەرنى چوقۇم تاش ـ كېسەك قىلىپ ئۆلتۈرىمىز ۋە بىزنىڭ قاتتىق جازايىمىزغا ئۇچرايسىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,16,' ئەلچىلەر: «(كۇفرىڭلار سەۋەبىدىن) شۇملۇقۇڭلار ئۆزۈڭلار بىلەن بىللىدۇر، سىلەرگە ۋەز ـ نەسىھەت قىلىنسا (شۇم پال ئالامسىلەر؟) ھەرگىز ئۇنداق ئەمەس (يەنى ئىش سىلەر گۇمان قىلغاندەك ئەمەس)، سىلەر (اﷲ قا شېرىك كەلتۈرۈش بىلەن) ھەددىدىن ئاشقۇچى قەۋمسىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,17,' شەھەرنىڭ يىراق جايىدىن بىر كىشى (يەنى ھەبىب نەجار) يۈگۈرۈپ كېلىپ ئېيتتى: «ئى قەۋمىم! پەيغەمبەرلەرگە ئەگىشىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,18,' توغرا يولدا بولغان، (ئىمانغا دەۋەت قىلغانلىقىغا) سىلەردىن ھەق سورىمايدىغان كىشىلەرگە ئەگىشىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,19,' مەن نېمىشقا مېنى ياراتقان ۋە سىلەر (ئۆلگەندىن كېيىن) دەرگاھىغا قايتىدىغان اﷲ قا ئىبادەت قىلماي؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,20,' مەن اﷲ تىن غەيرىينى ئىلاھ قىلىۋالىدىغان بولسام، ئەگەر مېھرىبان اﷲ ماڭا بىرەر زىيان ـ زەخمەت يەتكۈزمەكچى بولسا، ئۇلارنىڭ شاپائىتى مەندىن ھېچ نەرسىنى دەپئى قىلالمايدۇ ھەمدە مېنى قۇتقۇزالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,21,' ئەگەر ئۇنداق بولسا (يەنى ئاللادىن غەيرىينى ئىلاھ قىلىۋالىدىغان بولسام)، مەن ھەقىقەتەن ئوپئوچۇق گۇمراھلىقتا بولىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,22,' مەن ھەقىقەتەن (سىلەرنى ياراتقان) پەرۋەردىگارىڭلارغا ئىمان ئېيتتىم (ئىمانىمغا) قۇلاق سېلىڭلار (ۋە ئۇنىڭغا گۇۋاھ بولۇڭلار)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,23,' (قەۋمى ئۇنى ئۆلتۈرۈۋەتكەندىن كېيىن، ئۇنىڭغا) «جەننەتكە كىرگىن» دېيىلدى، ئۇ: «كاشكى قەۋمىم پەرۋەردىگارىمنىڭ ماڭا مەغپىرەت قىلغانلىقنى ۋە مېنى ھۈرمەتلىكلەردىن قىلغانلىقىنى بىلسە ئىدى» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,24,' ئۇنىڭدىن كېيىن ئۇنىڭ قەۋمىنى (جازالاش ئۈچۈن) ئاسماندىن قوشۇن (يەنى پەرىشتىلەر) چۈشۈرمىدۇق (ھەر قەۋمنى ھالاك قىلىش ئۈچۈن پەرىشتىلەرنى) چۈشۈرۈۋەرگۈچى بولمىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,25,' پەقەت بىر ئاۋاز بىلەنلا ناگاھان ئۇلار قېتىپ قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,26,' (اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغۇچى) بەندىلەرگە ئەپسۇسكى، ئۇلارغا بىرەر پەيغەمبەر كەلسىلا ئۇنى مەسخىرە قىلىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,27,' ئۇلار (يەنى مەككە ئاھالىسى) ئۇلاردىن ئىلگىرى ئۆتكەن نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى بىلمەمدۇ؟ ئۇلار بۇلارنىڭ يېنىغا (يەنى دۇنياغا ئەبەدىي) قايتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,28,' ئۇلارنىڭ ھەممىسى (قىيامەت كۈنى ھېساب بېرىش ئۈچۈن) ھۇزۇرىمىزغا ھازىر قىلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,29,' ئۆلۈك (يەنى قۇرغاق) زېمىننى (يامغۇر بىلەن) تىرىلدۈرۈپ، ئۇنىڭدىن ئۇلار يەيدىغان ئاشلىقنى ئۆستۈرگەنلىكىمىز ئۇلارغا (اﷲ نىڭ قۇدرىتى كامىلەسىنى كۆرسىتىدىغان) ئالامەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,30,' بىز زېمىندا نۇرغۇنلىغان خورما باغلىرىنى، ئۈزۈم باغلىرىنى بەرپا قىلدۇق، شۇ زېمىندا بۇلاقلارنى ئېقىتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,31,' ئۇلار ئۇنىڭ (يەنى باغلارنىڭ) مېۋىسىنى يېسۇن، (بۇ مېۋىلەرنى) ئۇلارنىڭ قوللىرى ياراتقان ئەمەس، ئۇلار (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلمامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,32,' اﷲ (پۈتۈن ئەيىب ـ نۇقسانلاردىن) پاكتۇر، ئۇ پۈتۈن شەيئىلەرنى جۈپ ياراتتى زېمىندىن ئۈنۈپ چىقىدىغان نەرسىلەرنىڭ، ئۇلارنىڭ ئۆزلىرىنىڭ ۋە ئۇلار بىلمەيدىغان نەرسىلەر (يەنى ئاجايىپ مەخلۇقاتلار)نىڭ ھەممىسىنىڭ جۈپتى بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,33,' ئۇلارغا (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) بىر ئالامەت كېچىدىن ئىبارەتكى، ئۇنىڭدىن كۈندۈزنى ئايرىۋېتىمىز ـ دە (يەنى كۈندۈزنىڭ نۇرىنى يوق قىلىۋېتىمىز ـ دە)، ئۇلار ناگاھان قاراڭغۇدا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,34,' كۈن بەلگىلەنگەن جايىغا قاراپ سەير قىلىدۇ، بۇ، غالىب، ھەممىنى بىلگۈچى اﷲ نىڭ ئالدىنئالا بەلگىلىگەن ئىشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,35,' ئايغا سەير قىلىدىغان مەنزىللەرنى بەلگىلىدۇق، ئۇ (ئاخىرقى مەنزىلگە يېتىپ بارغاندا) خورمىنىڭ قۇرۇپ قالغان شېخىغا ئوخشاش بولۇپ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,36,' كۈننىڭ ئايغا يېتىۋېلىشى (يەنى ئىككىسىنىڭ جەم بولۇپ قېلىشى)، كېچىنىڭ كۈندۈزدىن ئېشىپ كېتىشى (يەنى ۋاقتى كەلمەستىن كۈندۈزنىڭ ئورنىنى ئېلىشى) مۇمكىن ئەمەس، ھەر بىرى پەلەكتە ئۈزۈپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,37,' ئۇلارغا (بىزنىڭ كامالى قۇدرىتىمىزنى كۆرسىتىدىغان) ئالامەت شۇكى، بىز ئۇلارنىڭ ئاتا ـ بوۋىلىرىنى (نۇھ ئەلەيھىسسالامنىڭ نەرسە ـ كېرەك ۋە ھايۋاناتلار بىلەن) توشقۇزۇلغان كېمىسىگە چۈشۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,38,' بىز ئۇلار ئۈچۈن شۇ كېمىگە ئوخشاش ئۇلار چۈشىدىغان نەرسىلەرنى ياراتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,39,' ئەگەر بىز خالىساق ئۇلارنى غەرق قىلىۋېتىمىز، ئۇلارغا ياردەم بەرگۈچىمۇ بولمايدۇ ۋە ئۇلارنى قۇتۇلدۇرغۇچىمۇ بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,40,' پەقەت بىز رەھىم قىلغانلىقىمىز ۋە ئۇلارنى مۇئەييەن مۇددەتكىچە (ھاياتتىن) بەھرىمەن قىلىدىغانلىقىمىز ئۈچۈن (ئۇلار قۇتۇلدۇرۇلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,41,' ئۇلارغا: «رەھمەتكە ئېرىشىشىڭلار ئۈچۈن ئالدىڭلاردىكىدىن (يەنى دۇنيا ئازابىدىن) ۋە ئارقاڭلاردىكىدىن (يەنى ئاخىرەت ئازابىدىن) قورقۇڭلار» دېيىلسە (ئۇلار بۇنىڭدىن يۈز ئۆرۈيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,42,' پەرۋەردىگارىنىڭ (پەيغەمبەرنىڭ راستلىقىنى ئىسپاتلايدىغان) ئايەتلىرىدىن بىر ئايەت كەلسىلا، ئۇلار بۇنىڭدىن يۈز ئۆرۈيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,43,' ئەگەر ئۇلارغا: «اﷲ سىلەرگە رىزىق قىلىپ بەرگەن نەرسىلەردىن (پېقىرلارغا) سەدىقە قىلىپ بېرىڭلار» دېيىلسە، كاپىرلار مۆمىنلەرگە (مەسخىرە قىلىش يۈزىسىدىن): «ئەگەر اﷲ خالىسا تەمىنلەيدىغان ئادەمنى بىز تەمىنلەمدۇق؟ سىلەر پەقەت روشەن گۇمراھلىقتاسىلەر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,44,' ئۇلار: «ئەگەر راستچىل بولساڭلار، بۇ ۋەدە قاچان ئىشقا ئاشىدۇ؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,45,' ئۇلار پەقەت بىر ئاۋازنىلا (يەنى ئىسراپىل ئەلەيھىسسالامنىڭ بىرىنچى قېتىملىق چالغان سۇرىنىلا) كۈتىدۇ، ئۇلار (دۇنيادا سودا ـ سېتىق ۋە شۇنىڭغا ئوخشاش ئىشلار ئۈستىدە) جاڭجاللىشىپ تۇرغان ھالەتلىرىدە، ئۇ ئاۋاز ئۇلارنى ھالاك قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,46,' ئۇلار بىر ـ بىرىگە ۋەسىيەت قالدۇرۇشقىمۇ، ئائىلىسىگە قايتىشقىمۇ قادىر بولالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,47,' سۇر چېلىنغان ھامان ئۇلار قەبرىلىرىدىن چىقىپ پەرۋەردىگارى تەرەپكە يۈگۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,48,' ئۇلار: «ۋاي ئىسىت! بىزنى ئۇخلاۋاتقان يېرىمىزدىن (يەنى قەبرىمىزدىن) كىم ئويغاتتى؟» دەيدۇ (چۈنكى ئۇلار ئىككى سۇر ئارىسىدا ئۇيقۇدا بولۇپ ئازابتىن خالى بولىدۇ)، (پەرىشتىلەر يا مۆمىنلەر ئۇنىڭغا جاۋابەن) «مېھرىبان اﷲ نىڭ ۋەدە قىلغىنى مۇشۇ (كۈن)، پەيغەمبەرلەر راست ئېيتقان» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,49,' پەقەت بىر ئاۋاز بىلەنلا ئۇلارنىڭ ھەممىسى ھۇزۇرىمىزغا ھازىر قىلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,50,' ئۇ كۈندە ھېچبىر ئىنسانغا قىلچە زۇلۇم قىلىنمايدۇ، سىلەرگە پەقەت قىلمىشىڭلارغا يارىشا جازا بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,51,' شۈبھىسىزكى، ئۇ كۈندە جەننەت ئەھلىلىرى نېمەت ئىچىدىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,52,' ئۇلار ۋە ئۇلارنىڭ جۈپتىلىرى (جەننەتلەرنىڭ) سايىلىرى ئاستىدا تەختلەرگە يۆلەنگەن ھالدا تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,53,' ئۇلار ئۈچۈن جەننەتتە (تۈرلۈك) مېۋىلەر بار، ئۇلار ئۈچۈن جەننەتتە (ئۇلارنىڭ) كۆڭلى تارتقان نەرسىلەر بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,54,' مېھرىبان پەرۋەردىگارى تەرىپىدىن (ئۇلارغا) سالام دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,55,' (گۇناھكارلارغا) «ئى گۇناھكارلار! بۈگۈن (بىر تەرەپكە) ئايرىلىڭلار» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,56,' سىلەرگە (پەيغەمبەرلىرىم ئارقىلىق): «ئى ئادەم بالىلىرى! شەيتانغا چوقۇنماڭلار، ئۇ ھەقىقەتەن سىلەرگە ئاشكارا دۈشمەندۇر، ماڭىلا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر» دەپ تەۋسىيە قىلمىدىممۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,57,'شۈبھىسىزكى، شەيتان سىلەردىن نۇرغۇن كىشىلەرنى ئازدۇردى، (بۇنى) چۈشەنمەمسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,58,'سىلەرگە ۋەدە قىلىنغان جەھەننەم مانا شۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,59,' سىلەر (دۇنيادىكى چېغىڭلاردا) كاپىر بولغانلىقىڭلار ئۈچۈن جەھەننەمگە كىرىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,60,' شۇ كۈندە ئۇلارنىڭ ئېغىزلىرىنى پېچەتلىۋېتىمىز، ئۇلارنىڭ قىلمىشلىرىنى قوللىرى بىزگە سۆزلەپ بېرىدۇ، پۇتلىرى گۇۋاھلىق بېرىدۇ (يەنى ھەر ئەزا ئۇنىڭدىن سادىر بولغان ئىشنى سۆزلەپ بېرىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,61,' ئەگەر بىز خالىساق، ئۇلارنىڭ كۆزلىرىنى ئەلۋەتتە يوق قىلىۋېتەتتۇق، ئاندىن ئۇلار يول مېڭىشقا ئالدىرايتتى، لېكىن ئۇلار قانداقمۇ كۆرەلىسۇن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,62,' ئەگەر بىز خالىساق، ئۇلارنى ئۆيلىرىدىلا سۈرىتىنى مۇبەددەل (يەنى مايمۇن، چوشقا ياكى تاش) قىلىۋېتەتتۇق، شۇنىڭ بىلەن ئۇلار ئالدىغىمۇ ماڭالمايتتى، ئارقىغىمۇ قايتالمايتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,63,' بىز ئۆمرىنى ئۇزۇن قىلغان ئادەمنىڭ تېنىنى ئاجىزلىتىۋېتىمىز (يەنى ياشلىق، قىرانلىق ۋاقتىدىكىدىن ئاجىزلايدۇ، قېرىيدۇ)، ئۇلار (بۇنى) چۈشەنمەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,64,' ئۇنىڭغا (يەنى پەيغەمبەرگە) بىز شېئىر تەلىم بەرمىدۇق، ئۇنىڭغا شېئىر مۇناسىپمۇ ئەمەس ئۇ پەقەت تىرىكلەرنى ئاگاھلاندۇرۇش، كاپىرلارغا ئازابنىڭ تېگىشلىك ئىكەنلىكىنى (بىلدۈرۈش ئۈچۈن نازىل بولغان) ۋەز ـ نەسىھەتتۇر ۋە روشەن قۇرئاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,65,' ئۇلار بىلمەمدۇكى، ئۇلار ئۈچۈن ھايۋانلارنى (قۇدرەت) قولىمىز بىلەن خەلق ئەتتۇق، ئۇلار ھايۋانلارنى باشقۇرۇپ تۇرغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,66,' ھايۋانلارنى ئۇلارغا بويسۇندۇرۇپ بەردۇق، ئۇلار ئۇ ھايۋانلارنىڭ بەزىسىنى مىنىدۇ، بەزىسىنى يەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,67,' ئۇلار شۇ ھايۋانلاردىن پايدىلىنىدۇ ۋە سۈتلىرىنى ئىچىدۇ، ئۇلار (پەرۋەردىگارىنىڭ بۇ نېمەتلىرىگە) شۈكۈر قىلمامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,68,' ئۇلار ياردەمگە ئېرىشىشنى ئۈمىد قىلىپ، اﷲ نى قويۇپ باشقا مەبۇدلارنى ئىلاھ قىلىۋالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,69,' ئۇ مەبۇدلار ئۇلارغا ياردەم بېرەلمەيدۇ بۇلار شۇ مەبۇدلار ئۈچۈن (يەنى ئۇلارنىڭ خىزمىتى ئۈچۈن) ھازىرلانغان قوشۇندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,70,' (ئى، مۇھەممەد!) ئۇلار (يەنى مۇشرىكلار) نىڭ سۆزى (يەنى سېنى ئىنكار قىلغانلىقى) سېنى غەمكىن قىلمىسۇن بىز ھەقىقەتەن ئۇلارنىڭ دىللىرىدىكىنى ۋە ئاشكارا قىلغان (سۆز ۋە ھەرىكەتلىرى) نى بىلىپ تۇرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,71,' ئىنسان بىلمەمدۇكى، بىز ھەقىقەتەن ئۇنى ئابىمەنىيدىن ياراتتۇق ئەمدى ئۇ (پەرۋەردىگارىغا) ئاشكارا خۇسۇمەتچى بولۇپ قالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,72,' ئۇ بىزگە (ئىنساننىڭ قايتا تىرىلىشىنى يىراق ساناپ، چىرىگەن سۆڭەكلەرنى) مىسال قىلىپ كۆرسەتتى ئۆزىنىڭ يارىتىلغانلىقىنى بولسا ئۇنتۇدى، ئۇ: «چىرىپ كەتكەن سۆڭەكلەرنى كىم تىرىلدۈرەلەيدۇ؟» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,73,' ئېيتقىنكى، «ئۇنى ئەڭ دەسلەپتە ياراتقان زات تىرىلدۈرىدۇ، ئۇ ھەر بىر مەخلۇقنى بىلگۈچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,74,' اﷲ سىلەر ئۈچۈن يېشىل دەرەختىن ئوت پەيدا قىلدى سىلەر ئۇنىڭ بىلەن ئوت ياقىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,75,' ئاسمانلارنى ۋە زېمىننى ياراتقان زات ئۇلارنىڭ ئوخشىشىنى يارىتىشقا قادىر ئەمەسمۇ؟ ئۇ بۇنىڭغا قادىر، اﷲ ماھىر ياراتقۇچىدۇر، ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,76,' اﷲ بىرەر شەيئىنى (يارىتىشنى) ئىرادە قىلسا، ئۇنىڭغا «ۋۇجۇتقا كەل» دەيدۇ ـ دە، ئۇ ۋۇجۇتقا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,38,77,' اﷲ پاكتۇركى، ھەر بىر نەرسىنىڭ ئىگىلىك ھوقوقى ئۇنىڭ قولىدىدۇر، (ئۆلگەندىن كېيىن) ئۇنىڭ دەرگاھىغا قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,1,'سەپ ـ سەپ بولۇپ تۇرغان، دۈشمەنلەرگە قارشى ئات سېلىپ تۇرغان، (اﷲ تىن مەدەت تىلەپ) كالامۇاﷲ نى تىلاۋەت قىلىپ تۇرغان (غازات قىلغۇچى) قوشۇن بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,2,' سىلەرنىڭ ئىلاھىڭلار شەك ـ شۈبھىسىز بىر ئىلاھتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,3,' (ئۇ) ئاسمانلارنىڭ، زېمىننىڭ، ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ ۋە مەشرىقلەرنىڭ پەرۋەردىگارىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,4,' بىز ھەقىقەتەن يېقىن ئاسماننى يۇلتۇزلار (نۇرى) بىلەن زىننەتلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,5,' اﷲ نىڭ ئىتائىتىدىن چىققۇچى ھەر بىر شەيتاندىن ئۇنى قوغدىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,6,' ئۇلار پەرىشتىلەرنىڭ (سۆزلىرىدىن ھېچ نەرسىنى) ئاڭلىيالمايدۇ، ئۇلار، قوغلىنىشى ئۈچۈن، ھەر تەرەپتىن، ئاققۇچى يۇلتۇزلار بىلەن ئېتىلىدۇ، ئۇلار (ئاخىرەتتە) دائىملىق ئازابقا قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,7,' لېكىن (ئاسمان خەۋەرلىرىدىن) ئوغرىلىقچە بىر نەرسە ئاڭلىغان شەيتاننى يورۇق يۇلتۇز قوغلاپ (كۆيدۈرۈپ تاشلايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,8,' ئۇلاردىن (يەنى ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلغۇچىلاردىن) سوراپ باققىن! ئۇلارنى يارىتىش قىيىنمۇ؟ يا بىزنىڭ ياراتقانلىرىمىز (يەنى ئاسمان ـ زېمىن ۋە ئۇلارنىڭ ئارىسىدىكى پەرىشتىلەر ۋە چوڭ مەخلۇقاتلار) نى يارىتىش قىيىنمۇ؟ بىز ھەقىقەتەن ئۇلارنى (يەنى ئۇلارنىڭ ئەسلى بولغان ئادەمنى) يېپىشقاق لايدىن ياراتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,9,' (ئى مۇھەممەد!) سەن ئۇلارنىڭ اﷲ نىڭ قۇدرىتىنى كۆرۈپ تۇرۇپ، ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلغانلىقىدىن بەلكى ئەجەبلىنەرسەن ئۇلار بولسا سېنى مەسخىرە قىلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,10,' ئۇلارغا (قۇرئان بىلەن) ۋەز ـ نەسىھەت قىلىنسا (ۋەز ـ نەسىھەتنى) قوبۇل قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,11,' ئۇلار بىرەر مۆجىزىنى كۆرسە مەسخىرە قىلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,12,' ئۇلار: «بۇ روشەن سېھىردۇر، بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغاندىن كېيىن راستلا تىرىلەمدۇق؟ بىزنىڭ بۇرۇنقى ئاتىلىرىمىزمۇ تىرىلەمدۇ؟ » دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,13,' ئېيتقىنكى، «ھەئە، سىلەر خار ھالدا تىرىلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,14,' بىر ئاۋازنى (يەنى ئىسراپىلنىڭ چالغان سۇرىنى) ئاڭلاش بىلەنلا (مەھشەرگاھقا) ھازىر بولۇپ (ئۆزلىرىگە نېمە قىلىنىدىغانلىقىغا) قارىشىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,15,' ئۇلار: «ۋاي ئىسىت! بىزگە بۇ قىيامەت كۈنىدۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,16,' بۇ سىلەر ئىنكار قىلغان (ھەق بىلەن باتىل) ئايرىلىدىغان كۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,17,' (اﷲ پەرىشتىلىرىگە ئېيتىدۇ) زۇلۇم قىلغۇچىلارنى، ئۇلارنىڭ (مۇشرىكلىكتىكى، كۇفرىدىكى) قاياشلىرىنى ۋە ئۇلارنىڭ اﷲ نى قويۇپ چوقۇنغان بۇتلىرىنى (مەھشەرگاھقا) توپلاڭلار، ئاندىن ئۇلارنى دوزاخنىڭ يولىغا باشلاڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,18,' ئۇلارنى توختىتىپ تۇرۇڭلار، چۈنكى ئۇلار (جىمى سۆزلىرى ۋە ئىشلىرىدىن) سوئال ـ سوراق قىلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,19,' (ئاندىن) ئۇلارغا: «نېمىشقا (دۇنيادىكى ھالىتىڭلارغا ئوخشاش) بىر ـ بىرىڭلارغا ياردەم قىلمايسىلەر؟ » (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,20,' بەلكى ئۇلار بۈگۈن بويسۇنغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,21,' ئۇلار بىر ـ بىرىگە قاراپ مۇنازىرىلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,22,' ئەگەشكۈچىلەر ئەگەشتۈرگۈچىلەرگە: «سىلەر (سۆزۈڭلارنىڭ راستلىقىغا) قەسەم ئىچىپ بىزنى ئىماندىن توسۇيتتۇڭلار» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,23,' (ئەگەشتۈرگۈچىلەر ئەگەشكۈچىلەرگە) ئېيتىدۇ: «سىلەر (ئۆز ئىختىيارىڭلار بىلەن) مۆمىن بولمىدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,24,' بىزنىڭ سىلەرنى (بىزگە ئەگىشىشكە) زورلايدىغان كۈچىمىز يوق ئىدى، بەلكى سىلەر (بىزگە ئوخشاش) ئازغان قەۋم ئىدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,25,' پەرۋەردىگارىمىزنىڭ بىزنى (جازالاش) سۆزى بىزگە تېگىشلىك بولدى بىز ھەقىقەتەن (شۇ سۆز بويىچە ئازابنى) چوقۇم تېتىغۇچىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,26,' سىلەرنى بىز ئازدۇردۇق، چۈنكى بىز ھەقىقەتەن ئازغان ئىدۇق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,27,' ئۇلار (يەنى ئەگەشتۈرگۈچىلەر بىلەن ئەگەشكۈچىلەر) بۇ كۈندە (يەنى قىيامەت كۈنىدە) ئازابتا ئورتاقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,28,' بىز ھەقىقەتەن گۇناھكارلارغا مۇشۇنداق قىلىمىز، (يەنى ئەگەشتۈرگۈچىلەر بىلەن ئەگەشكۈچىلەرنىڭ ھەممىسىگە ئازاب قىلىمىز)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,29,' ئۇلار ھەقىقەتەن مۇشۇنداق ئىدىكى، ئۇلارغا: «بىر اﷲ تىن باشقا ھېچ ئىلاھ يوق» (دەڭلار) دېيىلسە، (كەلىمە تەۋھىدتىن) تەكەببۇرلۇق قىلىپ باش تارتاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,30,' ئۇلار: «ئىلاھلىرىمىزنى ھەقىقەتەن بىر مەجنۇن شائىر ئۈچۈن (يەنى ئۇنىڭ سۆزى ئۈچۈن) تاشلىۋېتەمدۇق؟» دەيتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,31,' ھەرگىز ئۇنداق ئەمەس (يەنى ئىش ئۇلارنىڭ بوھتان قىلغىنىدەك ئەمەس)، ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ھەق (دىن) نى ئېلىپ كەلدى ۋە پەيغەمبەرلەرنى تەستىق قىلدى');
