INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,39,80,' ئاندىن ئۇلارغا يۈزلىنىپ ئوڭ قولى بىلەن ئۇرۇپ پاچاقلاشقا كىرىشتى');
