INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,9,' ياكى ئۇلاردا غالىب، ناھايىتى كەرەملىك پەرۋەردىگارىڭنىڭ رەھمىتىنىڭ خەزىنىلىرى بارمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,10,' ياكى ئۇلار ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقىغا ئىگىمۇ؟ (ئەگەر ئۇلار ئىگە بولسا، ئۇلار ئۆزلىرىنى ئاسمانغا ئېلىپ چىقىدىغان) شوتىلارغا چىقسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,11,' ئۇلار (رەسۇلۇللاھقا قارشى) جامائەلەردىن ئۇيۇشقان مەغلۇب قىلىنىدىغان قوشۇندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,12,' ئۇلاردىن (يەنى قۇرەيش مۇشرىكلىرىدىن) ئىلگىرى نۇھ قەۋمى، ئاد (قەۋمى) ۋە قوزۇقلار ئىگىسى  پىرئەۋن (قەۋمى) (پەيغەمبەرلىرىنى) ئىنكار قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,13,' سەمۇد قەۋمى، لۇت قەۋمى ۋە ئەيكىلىكلەر (يەنى شۇئەيب ئەلەيھىسسالام قەۋمى) مۇ (پەيغەمبەرلىرىنى ئىنكار قىلدى)، ئەنە شۇلار (پەيغەمبەرلەرگە قارشى ئۇيۇشقان) جامائەلەر ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,14,' ئۇلار (يەنى جامائەلەر) دىن پەيغەمبەرلەرنى ئىنكار قىلمىغان بىرىمۇ يوق، شۇنىڭ ئۈچۈن ئۇلارغا مېنىڭ ئازابىم تېگىشلىك بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,15,' بۇلار (يەنى مەككە مۇشرىكلىرى) پەقەت بىر ئاۋاز (يەنى بىرىنچى قېتىملىق سۇر) نىلا كۈتىدۇ، ئۇ قايتىلانمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,16,' ئۇلار (مەسخىرە قىلىش يولى بىلەن): «پەرۋەردىگارىمىز! (سەن بىزگە ئازابتىن ۋەدە قىلغان) نېسىۋىمىزنى بىزگە بالدۇرراق – قىيامەت كۈنىدىن ئىلگىرى بەرگىن» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,17,' ئۇلارنىڭ سۆزلىرىگە سەۋر قىلغىن، (دىندا) كۈچلۈك بەندىمىز داۋۇدنى ئەسلىگىن، ئۇ ھەقىقەتەن اﷲ قا ئىتائەت قىلغۇچى ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,18,' بىز ھەقىقەتەن تاغلارنى (داۋۇدقا) بويسۇندۇرۇپ بەردۇق، ئۇلار داۋۇد بىلەن ئەتىگىنى ـ ئاخشىمى تەسبىھ ئېيتاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,19,' قۇشلارنىمۇ ئۇنىڭغا بويسۇندۇرۇپ بەردۇق، ئۇلارمۇ (تەرەپ ـ تەرەپتىن) توپلىنىپ، داۋۇد بىلەن بىللە تەسبىھ ئېيتاتتى، (تاغلارنىڭ، قۇشلارنىڭ) ھەممىسى اﷲ قا ئىتائەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,20,' داۋۇدنىڭ سەلتەنىتىنى كۈچەيتتۇق، ئۇنىڭغا ھېكمەت ۋە (ھەممە ئادەم چۈشىنىدىغان) روشەن سۆز ـ ئىبارىلەرنى ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,21,' (ئى مۇھەممەد!) دەۋا قىلغۇچىلارنىڭ قىسسىسى ساڭا يەتتىمۇ؟ ئۆز ۋاقتىدا ئۇلار (داۋۇد ئىبادەت قىلىۋاتقان) مەسجىدنىڭ تېمىدىن ئارتىلىپ چۈشۈشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,22,' ئەينى ۋاقىتتا ئۇلار داۋۇدنىڭ يېنىغا كىردى داۋۇد ئۇلاردىن قورقتى، ئۇلار (داۋۇدقا): «قورقمىغىن، بىز بىرىمىز ـ بىرىمىزگە چېقىلغان ئىككى دەۋاگەرمىز، بىزنىڭ ئارىمىزدا ئادىللىق بىلەن ھۆكۈم قىلغىن، ناھەق ھۆكۈم چىقارمىغىن، بىزنى توغرا يولغا باشلىغىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,23,' (ئۇلارنىڭ بىرى) «شۈبھىسىزكى، مېنىڭ بۇ قېرىندىشىمنىڭ  ساغلىقى بار، مېنىڭ بىر ساغلىقىم بار، قېرىندىشىم ئۇ بىر ساغلىقنىمۇ ماڭا بەرگىن دەيدۇ، (ئارىمىزدا بۇ توغرۇلۇق مۇنازىرە بولۇپ) ئۇ مېنى سۆزدە يېڭىپ قويدى» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,24,' داۋۇد ئېيتتى: «ئۇ سېنىڭ ساغلىقىڭنى ئۆزىنىڭ ساغلىقلىرىغا قوشۇۋېلىشنى تەلەپ قىلىپ راستلا سېنى بوزەك قىلىپتۇ، نۇرغۇن شېرىكلەر (يەنى دوستلار)، شۈبھىسىزكى، بىر ـ بىرىگە چېقىلىدۇ، پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارلا (بىر ـ بىرىگە چېقىلمايدۇ) بۇلار ئازدۇر» داۋۇد بىزنىڭ ئۇنى سىنىغانلىقىمىزنى بىلدى پەرۋەردىگارىدىن مەغپىرەت تەلەپ قىلدى، سەجدىگە باردى (اﷲ قا) تەۋبە قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,25,' بىز ئۇنىڭ خاتالىقىنى ئەپۇ قىلدۇق، ئۇ بىزنىڭ دەرگاھىمىزدا ئەلۋەتتە يېقىنلىققا ۋە ياخشى ئاقىۋەتكە ئىگە بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,26,' ئى داۋۇد! سېنى بىز ھەقىقەتەن يەر يۈزىدە خەلىپە قىلدۇق، كىشىلەرنىڭ ئارىسىدا ئادىللىق بىلەن ھۆكۈم چىقارغىن، نەپسى خاھىشقا ئەگەشمىگىنكى، ئۇ سېنى اﷲ نىڭ يولىدىن ئازدۇرىدۇ، اﷲ نىڭ يولىدىن ئازغانلار ھېساب كۈنى (يەنى قىيامەت كۈنى) نى ئۇنتۇغانلىقلىرى ئۈچۈن ھەقىقەتەن قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,27,' ئاسماننى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنى بىكار ياراتمىدۇق، ئۇ (يەنى ئۇلارنى بىكار يارىتىلغان دەپ قاراش) كاپىرلارنىڭ گۇمانىدۇر، كاپىرلارغا دوزاختىن ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,28,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى يەر يۈزىدە بۇزغۇنچىلىق قىلغانلار بىلەن ئوخشاش قىلامدۇق؟ ياكى تەقۋادارلارنى فاجىرلارغا ئوخشاش قىلامدۇق؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,29,' (بۇ) ئۇلارنىڭ ئايەتلەرنى تەپەككۈر قىلىشلىرى ئۈچۈن، ئەقىل ئىگىلىرىنىڭ ۋەز ـ نەسىھەت ئېلىشلىرى ئۈچۈن بىز ساڭا نازىل قىلغان مۇبارەك كىتابتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,30,' بىز داۋۇدقا سۇلەيماننى ئاتا قىلدۇق، سۇلەيمان نېمىدېگەن ياخشى بەندە! ئۇ (اﷲ قا) ھەقىقەتەن ئىتائەت قىلغۇچى ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,31,' ئۆز ۋاقتىدا كەچقۇرۇنلىقى ئۇنىڭغا ياخشى يۈگۈرۈك ئاتلار توغرىلاندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,32,' سۇلەيمان ئېيتتى: «مەن ھەقىقەتەن ئاتلارنى پەرۋەردىگارىمنى ئەسلەش ئۈچۈن ياخشى كۆردۈم، ئۇلار (كۆزۈمدىن) غايىب بولغۇچە (ئۇلارنى كۆزدىن كۆچۈرۈش بىلەن بولدۇم)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,33,' سۇلەيمان ئېيتتى: «ئاتلارنى مېنىڭ ئالدىمغا ئېلىپ كېلىڭلار» (ئاتلار ئېلىپ كېلىنگەندىن كېيىن) سۇلەيمان ئۇلارنىڭ پاچاقلىرىنى، بويۇنلىرىنى سىلاشقا باشلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,34,' بىز ھەقىقەتەن سۇلەيماننى سىنىدۇق، ئۇنىڭ تەختى ئۈستىگە بىر جەسەتنى تاشلىدۇق، ئاندىن ئۇ (بۇنىڭ سىناق ئىكەنلىكىنى بىلىپ) تەۋبە قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,35,' سۇلەيمان ئېيىتتى: «پەرۋەردىگارىم! ماڭا مەغپىرەت قىلغىن، ماڭا مەندىن كېيىن (مەندىن باشقا) ھېچ ئادەمگە مۇيەسسەر بولمايدىغان پادىشاھلىقنى ئاتا قىلغىن، سەن ھەقىقەتەن كۆپ ئاتا قىلغۇچىسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,36,' بىز سۇلەيمانغا شامالنى بويسۇندۇرۇپ بەردۇق شامال سۇلەيماننىڭ بۇيرۇقى بويىچە ئۇ خالىغان تەرەپكە لەرزان ماڭاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,37,' ھەمدە ئۇنىڭغا (كاتتا بىنالارنى سالالايدىغان) قۇرغۇچى ۋە (دېڭىزلارغا چۆكۈپ ئۈنچە ـ مارجانلارنى ئېلىپ چىقالايدىغان) غەۋۋاس شەيتانلارنى بويسۇندۇرۇپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,38,' يەنە نۇرغۇن شەيتانلارغا ئىشكەل سېلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,39,' (بىز سۇلەيمانغا ئېيتتۇقكى) «بۇ بىزنىڭ (ساڭا بەرگەن كەڭ) ئاتايىمىز، (ئۇنىڭدىن سەن خالىغان كىشىگە) بەرگىن، ياكى بەرمىگىن، (بۇ ھەقتە سەندىن) ھېساب ئېلىنمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,40,' ئۇ بىزنىڭ دەرگاھىمىزدا ھەقىقەتەن يېقىنلىققا ۋە ياخشى ئاقىۋەتكە ئىگە بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,41,' بەندىمىز ئەييۇبنى ئەسلىگىن، ئۇ ئۆز ۋاقتىدا پەرۋەردىگارىغا (ئىلتىجا قىلىپ): «شەيتان ماڭا ھەقىقەتەن جاپا ـ مۇشەققەت ۋە ئازاب يەتكۈزدى» دەپ نىدا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,42,' ئۇنىڭغا: «پۇتۇڭ بىلەن يەرنى تەپكىن» دېدۇق، ئۇ پۇتى بىلەن يەرنى تېپىۋىدى، سۈزۈك بىر بۇلاق ئېتىلىپ چىقتى (بىز ئۇنىڭغا) «بۇ يۇيۇنسىمۇ بولىدىغان، ئىچسىمۇ بولىدىغان سوغۇق سۇدۇر» (دېدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,43,' بىز ئۇنىڭ (كېسەل ۋاقتىدا ھادىسىگە ئۇچراپ يوق بولغان) ئەھلى ـ ئەۋلادىنى (يېڭىدىن) بەردۇق، ئۇلار بىلەن بىللە بىر باراۋەر ئارتۇق بەردۇق، (بۇ) بىزنىڭ نازىل قىلغان رەھمىتىمىزدۇر، ئەقىل ئىگىلىرى ئۈچۈن ۋەز ـ نەسىھەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,44,' (ئۇنىڭغا) «قولۇڭ بىلەن بىر باغلام چۆپنى ئېلىپ، ئۇنىڭ بىلەن (ئايالىڭنى) ئۇرغىن  قەسىمىڭنى بۇزمىغىن» (دېدۇق) بىز ئەييۇبنى ھەقىقەتەن سەۋرچان بايقىدۇق، ئۇ نېمىدېگەن ئوبدان بەندە! ھەقىقەتەن ئۇ (اﷲ قا) تەۋبە بىلەن يۈزلەنگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,45,' ئىبادەتتە كۈچلۈك، (دىندا) بەسىرەتلىك بەندىلىرىمىز ئىبراھىم، ئىسھاق ۋە يەئقۇبنى ئەسلىگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,46,' ئۇلارنى بىز پاك ـ خىسلەتلىك، ئاخىرەتنى ئەسلەپ تۇرىدىغان سەمىمىي كىشلەر قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,47,' ئۇلار بىزنىڭ دەرگاھىمىزدا ھەقىقەتەن ئالاھىدە تاللانغان كىشىلەردۇر، ھەقىقەتەن ياخشى كىشىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,48,' ئىسمائىلنى، ئىليەسەنى ۋە زۇلكىفلنى ئەسلىگىن، (ئۇلارنىڭ) ھەممىسى ياخشىلاردىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,49,' بۇ ۋەز ـ نەسىھەتتۇر، تەقۋادارلارغا ئەلۋەتتە ياخشى ئاقىۋەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,50,' (ئۇ) تۇرالغۇ بولغان جەننەتلەردىن ئىبارەت بولۇپ، ئۇلارغا ئۇنىڭ دەرۋازىلىرى ئوچۇقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,51,' ئۇلار جەننەتلەردە تەختلەرگە يۆلىنىپ ئولتۇرىدۇ، ئۇلار جەننەتلەردە نۇرغۇن مېۋىلەرنى، نۇرغۇن شارابلارنى تەلەپ قىلىپ (ئەكەلدۈرۈپ) تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,52,' ئۇلارنىڭ يانلىرىدا ئەرلىرىدىن غەيرىيگە كۆز سالمايدىغان، ياش قۇرامى ئوخشاش جۈپتىلىرى بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,53,' بۇ سىلەرگە قىيامەت كۈندە ۋەدە قىلىنغان نېمەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,54,' بۇ ھەقىقەتەن بىزنىڭ ئاتايىمىزدۇركى، ئۇ ھەرگىز تۈگىمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,55,' بۇ (ھەقىقەتتۇر)، كاپىرلارنىڭ (ئاخىرەتتە بولىدىغان جايى) ھەقىقەتەن ئەڭ يامان جايدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,56,' ئۇ جەھەننەمدۇر، ئۇلار جەھەننەمگە كىرىدۇ، ئۇ نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,57,'بۇ (كۆيدۈرگۈچى) قىزىق سۇدۇر، قان ـ يىرىڭدۇر، ئۇلار بۇنى تېتىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,58,' يەنە (ئۇلارغا) مۇشۇ خىلدىكى باشقا تۈرلۈك ئازابلار بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,59,' (ئەگەشكۈچىلىرى بىلەن دوزاخقا كىرگەن چاغدا، ئەگەشتۈرگۈچىلەرگە ئېيتىلىدۇ) «بۇ جامائە سىلەر بىلەن بىرگە كىرگۈچىدۇر» (ئەگەشتۈرگۈچىلەر ئېيتىدۇ) «ئۇلار قارشى ئېلىنمايدۇ، ئۇلار چوقۇم (بىزگە ئوخشاش) ئازابنى تېتىغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,60,' ئەگەشكۈچىلەر ئېيتىدۇ: «بەلكى سىلەر قارشى ئېلىنمايسىلەر، سىلەر ئازابنى بىزگە ئېلىپ كەلدىڭلار (يەنى بىزنى ئازدۇرۇپ دوزاخقا كىرىشىمىزگە سەۋەبچى بولدۇڭلار)، بۇ نېمىدېگەن يامان جاي!»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,61,'ئۇلار (يەنى ئەگەشكۈچىلەر): «پەرۋەردىگارىمىز! بىزگە بۇ ئازابنى ئېلىپ كەلگەن ئادەمگە دوزاخ ئازابىنى ئىككى ھەسسە زىيادە قىلغىن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,62,' ئۇلار (يەنى كاپىرلارنىڭ كاتتىباشلىرى) ئېيتىدۇ: «بىز (دۇنيادىكى چاغدا) يامانلاردىن ھېسابلايدىغان ئادەملەرنى (يەنى مۆمىنلەرنى) (دوزاختا) كۆرمەيمىزغۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,63,'ئۇلارنى بىز (دۇنيادىكى چاغدا) مەسخىرە قىلاتتۇقمۇ؟ ياكى ئۇلاردىن كۆزلەر ئېغىپ كەتتىمۇ؟ (يەنى ياكى ئۇلار دوزاختا بىز بىلەن بىللە تۇرسىمۇ كۆرمەيۋاتامدۇق؟)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,64,' ئەھلى دوزاخنىڭ بۇ رەۋىشتە ئۆزئارا جاڭجاللىشىشى چوقۇمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,65,' (ئى مۇھەممەد! مەككە كاپىرلىرىغا) ئېيتقىنكى، «مەن پەقەت بىر ئاگاھلاندۇرغۇچىمەن، غالىب بىر اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,66,' (ئۇ اﷲ) ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنىڭ پەرۋەردىگارىدۇر، غالىبتۇر (ئۆزى خالىغان بەندىلەرنى) ناھايىتى مەغپىرەت قىلغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,67,' (ئۇلارغا) ئېيتقىنكى، «ئۇ (يەنى اﷲ نىڭ بىرلىكىگە دەۋەت قىلغۇچى پەيغەمبەر ئىكەنلىكىم) چوڭ خەۋەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,68,' سىلەر ئۇنىڭدىن يۈز ئۆرۈيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,69,' پەرىشتىلەر (ئادەم ئەلەيھىسسالام توغرىسىدا) مۇنازىرىلىشىۋاتقان چاغدا، مەن ئۇلارنىڭ ئەھۋالىنى بىلمەيتتىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,70,' ماڭا پەقەت ۋەھيى قىلىنىدۇكى، مەن روشەن بىر ئاگاھلاندۇرغۇچىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,71,' ئۆز ۋاقتىدا پەرۋەردىگارىڭ پەرىشتىلەرگە ئېيتتى: «مەن ھەقىقەتەن لايدىن بىر ئادەم يارىتىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,72,' ئۇنى مەن راۋۇرۇس ياراتقان (يەنى ئۇنى يارىتىپ سۈرەتكە كىرگۈزۈپ، ئەزالىرىنى تولۇق، مۇكەممەل ئىنسان ھالىتىگە كەلتۈرگەن) ۋە ئۇنىڭغا جان كىرگۈزگەن ۋاقتىمدا ئۇنىڭغا سەجدە قىلىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,73,' پەرىشتىلەرنىڭ بىرىمۇ قالماي، ھەممىسى (ئادەم ئەلەيھىسسالامغا) سەجدە قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,74,' پەقەت ئىبلىسلا بويۇنتاۋلىق قىلدى، ئۇ كاپىرلاردىن بولۇپ كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,75,' اﷲ ئېيتتى: «ئى ئىبلىس! مەن ئۆز قولۇم بىلەن ياراتقان ئىنسانغا سەجدە قىلىشتىن ساڭا نېمە توسقۇنلۇق قىلدى (ئۇنىڭغا سەجدە قىلماسلىقىڭ تەكەببۇرلۇق قىلغانلىقىڭدىنمۇ؟ ياكى يۇقىرى مەرتىۋىلىكلەردىن بولغانلىقىڭدىنمۇ؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,76,' ئىبلىس: «مەن ئۇنىڭدىن ئارتۇق، مېنى ئوتتىن ياراتتىڭ، ئۇنى لايدىن ياراتتىڭ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,77,' اﷲ: «سەن جەننەتتىن يوقال! سەن ھەقىقەتەن قوغلاندى بولدۇڭ، ساڭا ھەقىقەتەن قىيامەتكىچە مېنىڭ لەنىتىم بولسۇن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,78,' ئىبلىس: «پەرۋەردىگارىم! ماڭا (خالايىق) تىرىلدۈرۈلىدىغان كۈن (يەنى قىيامەت) گىچە مۆھلەت بەرسەڭ (يەنى مېنى ھايات قالدۇرساڭ)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,79,' اﷲ: «جەزمەن ساڭا مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,80,' ئىبلىس ئېيتتى: «ئىززىتىڭ بىلەن قەسەمكى، ئۇلارنىڭ ھەممىسىنى چوقۇم ئازدۇرىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,81,' ئۇلاردىن پەقەت تاللانغان بەندىلىرىڭلا بۇنىڭدىن مۇستەسنا (يەنى ئۇلارنى ئازدۇرالمايمەن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,82,' اﷲ ئېيتتى: «مېنىڭ سۆزۈم ھەقتۇر، مەن ھەقنى سۆزلەيمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,83,' سەن بىلەن ۋە ئۇلاردىن ساڭا ئەگەشكەنلەرنىڭ ھەممىسى بىلەن چوقۇم جەھەننەمنى توشقۇزىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,84,' ئېيتقىنكى، «(اﷲ تەرىپىدىن كەلگەن ۋەھيىنى) تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچ ھەق تەلەپ قىلمايمەن، مەن (قۇرئاننى) ئىپتىرا قىلغۇچىلاردىن (يەنى يالغاندىن توقۇغۇچىلاردىن) ئەمەسمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,85,' قۇرئان پەقەت جاھان ئەھلى ئۈچۈن ۋەز ـ نەسىھەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,40,86,' بىر مۇددەتتىن كېيىن ئۇنىڭ راستلىقىنى چوقۇم بىلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,1,'بۇ كىتاب غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ تەرىپىدىن نازىل قىلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,2,' ساڭا بىز (بۇ) كىتابنى ھەقىقەتەن ھەق بىلەن (يەنى ھەقىقەتنى ئۆز ئىچىگە ئالغان ھالدا) نازىل قىلدۇق، دىنىڭنى شېرىكتىن (ۋە رىيادىن) ساپ قىلغىنىڭ ھالدا اﷲ قا ئىبادەت قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,3,' بىلىڭلاركى، (شېرىكتىن ۋە رىيادىن) ساپ دىن اﷲ قا خاستۇر، اﷲ نى قويۇپ بۇتلارنى ھىمايىچى قىلىۋالغانلار: «بىزنىڭ ئۇلارغا چوقۇنۇشىمىز پەقەت ئۇلارنىڭ بىزنى اﷲ قا يېقىنلاشتۇرۇشى ئۈچۈندۇر» (دەيدۇ) اﷲ ھەقىقەتەن (قىيامەت كۈنى) ئۇلارنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىدۇ، اﷲ ھەقىقەتەن يالغانچىنى، (اﷲ نىڭ نېمەتلىرىگە) كۇفرىلىق قىلغۇچىنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,4,' ئەگەر اﷲ بالا تۇتۇشنى خالىسا ئىدى، ئەلۋەتتە، مەخلۇقات ئىچىدىن خالىغاننى ئىختىيار قىلاتتى، اﷲ (بالا تۇتۇشتىن) پاكتۇر، ئۇ غالىب بىر اﷲ دۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,5,' اﷲ ئاسمانلارنى ۋە زېمىننى ھەق ئاساسىدا ياراتتى اﷲ كېچىنى كۈندۈزگە كىرگۈزىدۇ ۋە كۈندۈزنى كېچىگە كىرگۈزىدۇ، اﷲ كۈن بىلەن ئاينى (بەندىلەرنىڭ مەنپەئەتىگە) بويسۇندۇرۇپ بەردى، ئۇلارنىڭ ھەر بىرى مۇئەييەن ۋاقىتقىچە (يەنى قىيامەتكىچە ئۆز ئوقىدا) سەير قىلىدۇ، بىلىڭلاركى، ئۇ (يەنى اﷲ ئۆز ئىشىدا) غالىبتۇر (بەندىلىرىنىڭ گۇناھلىرىنى تولىمۇ مەغپىرەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,6,' اﷲ سىلەرنى بىر ئادەمدىن ياراتتى، ئاندىن ئۇنىڭدىن ئۇنىڭ جۈپتىنى (يەنى ھەۋۋانى) ياراتتى ۋە ھايۋانلاردىن سەككىزنى  سىلەر ئۈچۈن ياراتتى، اﷲ سىلەرنى ئاناڭلارنىڭ قارنىدا بىر ھالەتتىن يەنە بىر ھالەتكە تەرەققىي قىلدۇرۇپ (يەنى ئابىمەنىي، ئاندىن لەختە قان، ئاندىن پارچە گۆش باسقۇچلىرىغا بۆلۈپ)، ئۈچ قاراڭغۇلۇق (يەنى بالاھەمراھىنىڭ پەردىسى، بەچچىدان ۋە ئانىنىڭ قورسىقى) ئىچىدە يارىتىدۇ، ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، پادىشاھلىق اﷲ قا خاستۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، قانداقمۇ (ئۇنىڭغا ئىبادەت قىلىشتىن ئۇنىڭدىن غەيرىيگە چوقۇنۇشقا) بۇرۇلۇپ كېتىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,7,' ئەگەر كاپىر بولساڭلار اﷲ سىلەردىن بىھاجەتتۇر، اﷲ بەندىلىرىنىڭ كاپىر بولۇشىغا رازى بولمايدۇ ئەگەر سىلەر شۈكۈر قىلساڭلار، اﷲ سىلەرنىڭ قىلغان شۈكۈرۈڭلاردىن رازى بولىدۇ، بىر گۇناھكار ئادەم باشقا بىراۋنىڭ گۇناھىنى ئۈستىگە ئالمايدۇ (يەنى ھەر ئادەم قىلغان گۇناھىغا ئۆزى جاۋابكار بولىدۇ)، ئاندىن سىلەر پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتىسىلەر، اﷲ قىلمىشىڭلارنى سىلەرگە ئېيتىپ بېرىدۇ، اﷲ ھەقىقەتەن دىللاردىكى سىرلارنى تولۇق بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,8,' ئىنسانغا بىرەر زىيان ـ زەخمەت يەتسە، پەرۋەردىگارىغا يۈزلەنگەن ھالدا ئۇنىڭغا دۇئا قىلىدۇ، ئاندىن اﷲ ئۆز تەرىپىدىن ئۇنىڭغا نېمەت ئاتا قىلغان (ئۇنىڭ بېشىغا كەلگەن كۈلپەتنى كۆتۈرۈۋەتكەن) چاغدا، اﷲ قا زىيان ـ زەخمەتنى كۆتۈرۈۋېتىش توغرۇلۇق ئىلگىرى قىلغان دۇئاسىنى ئۇنتۇپ كېتىدۇ، (باشقىلارنى) اﷲ نىڭ يولىدىن ئازدۇرۇش ئۈچۈن اﷲ قا شېرىكلەرنى كەلتۈرىدۇ، ئېيتقىنكى، «سەن كۇفرىڭ بىلەن تۇرۇپ، (بۇ پانىي دۇنيادىن) ئازغىنا (ۋاقىت) بەھرىمەن بولغىن، سەن چوقۇم دوزىخىلاردىندۇرسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,9,' (اﷲ قا شېرىك كەلتۈرگەن ئادەم ياخشىمۇ؟) ياكى ئاخىرەت (ئازابىدىن) قورقۇپ، پەرۋەردىگارىنىڭ رەھمىتىنى ئۈمىد قىلىپ كېچىنىڭ سائەتلىرىنى سەجدە قىلغان ۋە قىيامدا تۇرغان ھالدا ئىبادەت قىلىپ ئۆتكۈزگەن ئادەم (ياخشىمۇ؟)» ئېيتقىنكى، «بىلىدىغانلار بىلەن بىلمەيدىغانلار باراۋەر بولامدۇ؟ پەقەت (ساغلام) ئەقىل ئىگىلىرى ئىبرەت ئالىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,10,' ئېيتقىنكى، «ئى (اﷲ نىڭ) مۆمىن بەندىلىرى! پەرۋەردىگارىڭلارغا تەقۋادارلىق قىلىڭلار، بۇ دۇنيادا ياخشى ئىش قىلغانلار (ئاخىرەتتە) ياخشىلىققا (يەنى جەننەتكە) ئېرىشىدۇ اﷲ نىڭ يېرى كەڭدۇر (كۇففارلار ئارىسىدىن شۇ يەرگە ھىجرەت قىلىڭلار)، پەقەت سەۋر قىلغۇچىلارغا ئۇلارنىڭ ئەجرى ھېسابسىز بېرىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,11,' ئېيتقىنكى، «مەن دىننى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا اﷲ قا ئىبادەت قىلىشقا بۇيرۇلدۇم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,12,' ھەمدە (بۇ ئۈممەتنىڭ ئىچىدە) مۇسۇلمانلارنىڭ ئەۋۋىلى بولۇشقا بۇيرۇلدۇم»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,13,' ئېيتقىنكى، «ئەگەر مەن پەرۋەردىگارىمغا ئاسىيلىق قىلسام، شۈبھىسىزكى، بۈيۈك كۈننىڭ ئازابىدىن قورقىمەن (يەنى اﷲ نىڭ ئەمرىگە ئاسىيلىق قىلسام، قىيامەت كۈنى ئۇنىڭ مېنى جەھەننەم ئوتى بىلەن ئازابلىشىدىن قورقىمەن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,14,' ئېيتقىنكى، «دىنىمنى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا اﷲ قا ئىبادەت قىلىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,15,' سىلەر اﷲ تىن باشقا خالىغىنىڭلارغا ئىبادەت قىلىڭلار!» ئېيتقىنكى، «قىيامەت كۈنى ئۆزلىرىنى ۋە ئائىلىسىدىكىلەرنى زىيان تارتقۇزغۇچىلار قاتتىق زىيان تارتقۇچىلاردۇر، بىلىڭلاركى، ئەنە شۇ روشەن زىياندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,16,' ئۇلارنى ئۈستىدىنمۇ قاتمۇقات ئوت، ئاستىدىنمۇ قاتمۇقات ئوت ئورىۋالىدۇ، شۇ (قاتتىق ئازاب) بىلەن اﷲ بەندىلىرىنى قورقىتىدۇ» ئى بەندىلىرىم! (ئازابىمدىن) قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,17,' شەيتانغا (ۋە بۇتلارغا) چوقۇنۇشتىن يىراق بولغانلارغا، اﷲ قا (ۋە ئۇنىڭ ئىبادىتىگە) قايتقانلارغا (جەننەت بىلەن) خۇش خەۋەر بېرىلىدۇ، سۆزگە قۇلاق سېلىپ ئۇنىڭ ئەڭ ياخشىسىغا ئەگىشىدىغانلارغا (يەنى تەقۋادار بەندىلىرىمگە) خۇش خەۋەر بەرگىن، ئەنە شۇلار اﷲ ھىدايەت قىلغان كىشىلەردۇر، ئەنە شۇلار (ساغلام) ئەقىل ئىگىلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,18,' كىمگە ئازاب ۋەدىسى تېگىشلىك بولسا (ئۇ چوقۇم دوزاخقا كىرىدۇ)، سەن دوزاختىكى ئادەمنى قۇتقۇزالامسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,19,' لېكىن (دۇنيادا) پەرۋەردىگارىدىن قورققانلار (جەننەتتە) قەۋەت ـ قەۋەت ئالىي ئىمارەتلەردىن بەھرىمەن بولىدۇ، ئۇلارنىڭ ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدۇ، (بۇ) اﷲ نىڭ ۋەدىسىدۇر، اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,20,' سەن بىلمەمسەنكى، اﷲ بۇلۇتتىن يامغۇر ياغدۇرىدۇ ئاندىن ئۇنى يەرگە سىڭدۈرۈپ بۇلاقنى پەيدا قىلىدۇ ئاندىن اﷲ ئۇنىڭ بىلەن رەڭگى خىلمۇخىل زىرائەتلەرنى ئۈندۈرىدۇ ئاندىن ئۇ قۇرۇيدۇ، ئۇنىڭ سارغىيىپ كەتكەنلىكىنى كۆرىسەن ئاندىن اﷲ ئۇنى شاخ ـ شۇمبىغا ئايلاندۇرىدۇ، ئۇنىڭدا ھەقىقەتەن ئەقىل ئىگىلىرى ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) ئىبرەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,21,' اﷲ كۆكسىنى ئىسلام ئۈچۈن كەڭ قىلغان، پەرۋەردىگارىنىڭ نۇرى ئۈستىدە بولغان ئادەم (دىلى كور ئادەم بىلەن) ئوخشاشمۇ؟ اﷲ نىڭ زىكرىنى تەرك ئەتكەنلىكتىن دىللىرى قېتىپ كەتكەنلەرگە ۋاي! ئەنە شۇلار روشەن گۇمراھلىقتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,22,' اﷲ سۆزلەرنىڭ ئەڭ چىرايلىقى بولغان قۇرئاننى نازىل قىلدى (پاساھەتتە، بالاغەتتە) ئۇنىڭ بەزىسى بەزىسىگە ئوخشاپ كېتىدۇ، (ۋەز ـ نەسىھەتلەر، ئەھكاملار، قىسسىلەر ئۇنىڭدا) تەكرارلىنىدۇ، (قۇرئاندىكى ئازاب ئايەتلىرى تىلاۋەت قىلىنغان چاغدا) پەرۋەردىگارىدىن قورقىدىغان كىشىلەرنىڭ بەدەنلىرى تىترەيدۇ، ئاندىن اﷲ نىڭ زىكرى ئۈچۈن (يەنى اﷲ نىڭ رەھمىتى، مەغپىرىتىگە دائىر ئايەتلەر تىلاۋەت قىلىنغان چاغدا)، ئۇلارنىڭ بەدەنلىرى ۋە دىللىرى يۇمشاپ (ئارام تاپىدۇ)، ئەنە شۇ (يەنى قۇرئان) اﷲ نىڭ ھىدايىتىدۇركى، ئۇنىڭ بىلەن (اﷲ) خالىغان ئادەمنى ھىدايەت قىلىدۇ، اﷲ ئازدۇرغان ئادەمگە ھېچ ھىدايەت قىلغۇچى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,23,' قىيامەت كۈنى قاتتىق ئازابنى يۈزى بىلەن مۇداپىئە قىلىدىغان ئادەم (ئازابقا دۇچار بولمايدىغان ئادەم بىلەن ئوخشاشمۇ؟) زالىملارغا (يەنى كاپىرلارغا): «قىلغان گۇناھىڭلارنىڭ جازاسىنى تېتىڭلار» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,24,' ئۇلاردىن ئىلگىرى ئۆتكەن (ئۈممەت) لەر (پەيغەمبەرلىرىنى) ئىنكار قىلدى، ئويلىمىغان يەردىن ئۇلارغا ئازاب كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,25,' اﷲ ئۇلارغا بۇ دۇنيادا خارلىقنىڭ (تەمىنى) تېتىتتى، ئاخىرەتنىڭ ئازابى تېخىمۇ بۈيۈكتۇر، ئۇلار (ئاخىرەتنىڭ ئازابىنى) بىلسە ئىدى (پەيغەمبەرلىرىنى ئىنكار قىلمايتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,26,' بىز كىشىلەرنى ۋەز ـ نەسىھەت ئالسۇن دەپ، بۇ قۇرئاندا ئۇلارغا تۈرلۈك مىساللارنى بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,27,' قۇرئان ئەرەبچىدۇر، ئەگرىلىكتىن خالىدۇر، ئۈمىدكى، ئۇلار (قۇرئاننىڭ مەنىسىنى چۈشىنىپ كۇفرىدىن) ساقلانغاي');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,28,' اﷲ (سىلەرگە مۇنداق) بىر مىسالنى كەلتۈرىدۇ: بىر قۇل بولۇپ، ئۇنىڭغا (ئىشقا سېلىشنى) تالىشىدىغان نۇرغۇن خوجايىنلار ئىگىدارچىلىق قىلىدۇ (بىرسى بۇ ئىشقا، بىرسى ئۇ ئىشقا بۇيرۇيدۇ، ئۇ كىمنى رازى قىلىشنى بىلەلمەي قالىدۇ)، يەنە بىر قۇل بولۇپ، ئۇ بىر ئادەمگىلا خاستۇر، بۇ ئىككى قۇل (يەنى ئۇنىڭ ئەھۋالى) باراۋەرمۇ؟ جىمى ھەمدۇسانا اﷲ قا خاستۇر! بەلكى ئۇلار (يەنى مۇشرىكلار) نىڭ تولىسى بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,29,' سەن ھەقىقەتەن ئۆلىسەن، ئۇلارمۇ ھەقىقەتەن ئۆلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,30,' ئاندىن كېيىن سىلەر قىيامەت كۈنى پەرۋەردىگارىڭلارنىڭ دەرگاھىدا بىر ـ بىرىڭلار بىلەن دەۋا قىلىشىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,31,' اﷲ قا يالغاننى چاپلىغان، نازىل بولغان راست سۆزنى (يەنى قۇرئاننى) ئىنكار قىلغان ئادەمدىنمۇ زالىم كىشى بارمۇ؟ جەھەننەمدە كاپىرلارغا ئورۇن يوقمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,32,' راست سۆز (يەنى قۇرئان) نى ئېلىپ كەلگەن كىشى ۋە ئۇنى ئېتىراپ قىلغان كىشىلەر ـ ئەنە شۇلار تەقۋادارلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,33,' ئۇلار پەرۋەردىگارىنىڭ دەرگاھىدا ئۆزلىرى خالىغان نەرسىلەردىن بەھرىمەن بولىدۇ، ئەنە شۇ ياخشى ئىش قىلغۇچىلارنىڭ مۇكاپاتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,34,' اﷲ ئۇلارنىڭ ئەڭ يامان ئەمەللىرىنى يوققا چىقىرىدۇ، ئۇلارغا قىلغان ئەڭ ياخشى ئەمەللىرى بويىچە ساۋاب بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,35,' اﷲ بەندىسىگە (يەنى مۇھەممەد ئەلەيھىسسالامنى قوغداشقا ۋە ئۇنىڭغا ياردەم بېرىشكە) يېتەرلىك ئەمەسمۇ؟ ئۇلار سېنى اﷲ تىن باشقا مەبۇدلار بىلەن قورقىتىدۇ، اﷲ گۇمراھ قىلغان ئادەمنى ھېچ ھىدايەت قىلغۇچى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,36,' اﷲ ھىدايەت قىلغان ئادەمنى ھېچ ئازدۇرغۇچى بولمايدۇ، اﷲ غالىب، (دۈشمەنلىرىنى) جازالىغۇچى ئەمەسمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,37,' ئەگەر ئۇلاردىن: «ئاسمانلارنى ۋە زېمىننى كىم ياراتقان» دەپ سورىساڭ، شۈبھىسىزكى، ئۇلار: «اﷲ ياراتقان» دەيدۇ ئېيتقىنكى، «ئەگەر اﷲ ماڭا بىرەر زىيان ـ زەخمەت يەتكۈزۈشنى ئىرادە قىلسا، ئۇنى دەپئى قىلىشقا، يا اﷲ ماڭا رەھمەت قىلىشنى ئىرادە قىلسا، ئۇنى توسۇۋېلىشقا، اﷲ تىن باشقا سىلەر ئىبادەت قىلىۋاتقان مەبۇدلار قادىر بولالامدۇ؟ ماڭا ئېيتىپ بېرىڭلارچۇ؟» ئېيتقىنكى، «ماڭا اﷲ كۇپايىدۇر، تەۋەككۈل قىلغۇچىلار ئۇنىڭغا تەۋەككۇل قىلسۇن (يەنى ھەممە ئىشنى اﷲ قا تاپشۇرسۇن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,38,' ئېيتقىنكى، «ئى قەۋمىم! سىلەر ئۆز ھالىڭلار بويىچە ئىشلەڭلار، مەنمۇ ئۆز ھالىم بويىچە ئىشلەيمەن، سىلەر ئۇزۇنغا قالماي خارلىغۇچى ئازابنىڭ كىمگە كېلىدىغانلىقىنى، مەڭگۈلۈك ئازابنىڭ كىمگە چۈشىدىغانلىقىنى بىلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,39,' شۈبھىسىزكى، بىز ساڭا كىتابنى ئىنسانلار ئۈچۈن ھەق بىلەن نازىل قىلدۇق، كىمكى ھىدايەت تاپىدىكەن، ئۆزى ئۈچۈن ھىدايەت تاپقان بولىدۇ، كىمكى ئازىدىكەن، ئۆزىنىڭ زىيىنى ئۈچۈن ئازغان بولىدۇ، سەن ئۇلارغا ھامىي ئەمەسسەن (يەنى ئۇلارنى ئىمانغا زورلاشقا مۇئەككەل ئەمەسسەن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,40,' ئىنسانلار ئۆلىدىغان چاغلىرىدا، اﷲ ئۇلارنىڭ جانلىرىنى ئالىدۇ، ئۆلمىگەنلەرنىڭ جانلىرىنى ئۇخلىغان چاغلىرىدا ئالىدۇ، ئۆلۈمگە ھۆكۈم قىلىنغان (يەنى ئەجىلى يەتكەنلەر) نىڭ جانلىرىنى تۇتۇپ قېلىپ، قالغانلارنىڭ جانلىرىنى مۇئەييەن ۋاقىتقىچە (يەنى ئەجىلى يەتكۈچە) قويۇپ بېرىدۇ، بۇنىڭدا پىكىر يۈرگۈزىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,41,' (ئۇلار پىكىر يۈرگۈزمىدى) بەلكى اﷲ نى قويۇپ، (بۇتلاردىن) شاپائەتچىلەرنى تۇتتى ئېيتقىنكى، «ئۇلار (يەنى شاپائەتچى قىلىنغان بۇتلار) ھېچ نەرسىگە قادىر بولالمايدىغان ۋە (ھېچ نەرسىنى) سەزمەيدىغان تۇرسىمۇ (ئۇلارنى شاپائەتچى تۇتامدۇ؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,42,' شاپائەتنىڭ ھەممىسى اﷲ نىڭ ئىلكىدىدۇر (ھېچ ئادەم اﷲ نىڭ ئىزنىسىز شاپائەت قىلالمايدۇ)، ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر ئاندىن سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,43,' يالغۇز اﷲ تىلغا ئېلىنسا، ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ دىللىرى سىقىلىپ كېتىدۇ اﷲ تىن باشقا مەبۇدلار تىلغا ئېلىنسا، ئۇلار خۇش بولۇپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,44,' ئېيتقىنكى، «ئى ئاسمانلارنى ۋە زېمىننى ياراتقۇچى، يوشۇرۇننى ۋە ئاشكارىنى بىلگۈچى اﷲ ! سەن بەندىلىرىڭنىڭ ئارىسىدا ئۇلار ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە ھۆكۈم چىقىرىسەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,45,' ئەگەر (ئۆزلىرىگە) زۇلۇم قىلغانلارنىڭ ئىگىدارچىلىقىدا يەر يۈزىدىكى نەرسىلەرنىڭ ھەممىسى يەنە شۇنىڭدەك بىر ھەسسە كۆپ نەرسە بولىدىغان بولسا، قىيامەت كۈنىدىكى قاتتىق ئازاب ئۈچۈن ئۇنى فىدىيە بەرگەن بولاتتى، ئۇلارغا اﷲ تەرىپىدىن (بۇ دۇنيادىكى چاغلىرىدا) ئويلاپ باقمىغان ئازابلار ئاشكارا بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,46,' ئۇلارغا ئۇلارنىڭ (دۇنيادا) قىلغان يامان ئىشىلىرى ئاشكارا بولىدۇ، ئۇلارنىڭ (دۇنيادىكى چاغدا پەيغەمبەرنىڭ دەۋىتىنى) مەسخىرە قىلغانلىقلىرىنىڭ جازاسى ئۈچۈن ئۇلارنى (قاتتىق) ئازاب ئورىۋالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,47,' ئىنسانغا بىرەر مۇسىبەت يەتسە، بىزگە دۇئا قىلىپ (ئىلتىجا قىلىدۇ)، ئاندىن ئۇنىڭغا مەرھەمەت قىلىپ بىرەر نېمىتىمىزنى ئاتا قىلساق، ئۇ: «بۇ نېمەت ماڭا ئۆز ئىلمىمدىن (يەنى پەزلىمدىن، ئەمگىكىمدىن، تىرىشچانلىقىمدىن، رىزىق تېپىشنىڭ يوللىرىنى بىلگەنلىكىمدىن) كەلدى» دەيدۇ (ئىش ئۇ ئويلىغاندەك ئەمەس) بەلكى ئۇ سىناقتۇر (يەنى نېمەت ئاتا قىلىش بىلەن، ئۇنى ئىتائەت قىلامدۇ؟ ئاسىيلىق قىلامدۇ؟ دەپ سىنايمىز) لېكىن ئۇلارنىڭ تولىسى (بۇنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,48,' مۇنداق سۆزنى ئۇلاردىن ئىلگىرىكىلەرمۇ قىلغان، ئۇلارنىڭ ئېرىشكەن مال ـ مۈلۈكلىرى ئۇلارغا ئەسقاتمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,49,' ئۇلار (دۇنيادا) قىلغان يامانلىقلىرىنىڭ جازاسىنى تارتتى بۇلارنىڭ ئىچىدىكى زۇلۇم قىلغانلارمۇ قىلغان يامانلىقلىرىنىڭ جازاسىنى تارتىدۇ، ئۇلار ئازابىمىزدىن قۇتۇلالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,50,' ئۇلار بىلمەمدۇكى، اﷲ (سىناش يۈزىسىدىن) خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ، (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدۇ، شەك ـ شۈبھىسىزكى، بۇنىڭدا ئىمان ئېيتقان قەۋم ئۈچۈن نۇرغۇن ئالامەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,51,' (ئى مۇھەممەد! مېنىڭ تىلىمدىن) ئېيتقىنكى، «(گۇناھلارنى قىلىۋېرىپ) ئۆزلىرىگە جىنايەت قىلغان بەندىلىرىم! اﷲ نىڭ رەھمىتىدىن ئۈمىدسىزلەنمەڭلار اﷲ ھەقىقەتەن (خالىغان ئادەمنىڭ) جىمى گۇناھلىرىنى مەغپىرەت قىلىدۇ، شۈبھىسىزكى، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,52,' سىلەرگە ئازاب كېلىپ، ياردەمگە ئېرىشەلمىگىنىڭلاردىن بۇرۇن پەرۋەردىگارىڭلار تەرەپكە قايتىڭلار ۋە ئۇنىڭغا ئىتائەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,53,' سىلەر تۇيمىغان ھالەتتە سىلەرگە ئازابنىڭ ئۇشتۇمتۇت كېلىشىدىن ئىلگىرى سىلەرگە نازىل قىلىنغان ئەڭ گۈزەل قۇرئانغا ئەگىشىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,54,' ھەر قانداق ئادەم: «اﷲ نىڭ تائىتىدە كەتكۈزۈپ قويغانلىرىمغا ھەسرەت! مەن (اﷲ نىڭ شەرىئىتىنى ۋە دىنىنى) مەسخىرە قىلغۇچىلاردىن ئىدىم» دەپ قالماسلىق ئۈچۈن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,55,' ياكى «اﷲ مېنى ھىدايەت قىلغان بولسا (ھىدايەت تېپىپ) ئەلۋەتتە تەقۋادارلاردىن بولاتتىم» دەپ قالماسلىق ئۈچۈن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,56,' ياكى ئازابنى كۆرگەن چاغدا: «كاشكى دۇنياغا قايتىشىمغا بولسا ئىدى، (ئۇ چاغدا اﷲ قا ئىتائەت قىلىپ) ياخشىلاردىن بولار ئىدىم» دەپ قالماسلىق ئۈچۈن (پەرۋەردىگارىغا بويسۇنسۇن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,57,' دۇرۇس، ھەقىقەتەن ساڭا مېنىڭ نۇرغۇن ئايەتلىرىم (يەنى ھىدايەتكە سەۋەب بولىدىغان قۇرئان) كەلدى سەن ئۇلارنى ئىنكار قىلدىڭ، بويۇنتاۋلىق قىلدىڭ، سەن كاپىرلادىن بولدۇڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,58,' قىيامەت كۈنى اﷲ قا يالغاننى چاپلىغانلارنىڭ يۈزلىرىنى قاپقارا كۆرىسەن، جەھەننەمدە مۇتەكەببىرلەرگە ئورۇن يوقمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,59,'اﷲ (شېرىكتىن ۋە گۇناھتىن) ساقلانغۇچىلارنى بەختكە ئېرىشتۈرىدىغان يوللار ئارقىلىق (ئازابتىن) قۇتۇلدۇرىدۇ، ئۇلارغا كۈلپەت يەتمەيدۇ، ئۇلار قايغۇرمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,60,' اﷲ ھەممە نەرسىنى ياراتقۇچىدۇر، ھەممە نەرسىگە ھامىيدۇر (ئۇلارنى خالىغانچە تەسەررۇپ قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,61,' ئاسمانلارنىڭ ۋە زېمىننىڭ (خەزىنىلىرىنىڭ) ئاچقۇچلىرى اﷲ نىڭ ئىلكىدىدۇر، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلار – ئەنە شۇلار زىيان تارتقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,62,' (ئى مۇھەممەد!) ئېيتقىنكى، «ئى نادانلار! مېنى اﷲ تىن غەيرىيگە ئىبادەت قىلىشىقا بۇيرۇمسىلەر؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,63,' ساڭا ۋە سەندىن ئىلگىرىكى (پەيغەمبەر) لەرگە: «ئەگەر سەن اﷲ قا شېرىك كەلتۈرسەڭ، سېنىڭ ئەمەلىڭ ئەلۋەتتە بىكار بولۇپ كېتىدۇ، سەن ئەلۋەتتە زىيان تارتقۇچىلاردىن بولۇپ قالىسەن، بەلكى يالغۇز اﷲ قا ئىبادەت قىلغىن ۋە شۈكۈر قىلغۇچىلاردىن بولغىن» دەپ ۋەھيى قىلىندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,64,' ئۇلار اﷲ نى ھەقىقىي رەۋىشتە تونۇمىدى قىيامەت كۈنى زېمىن پۈتۈنلەي اﷲ نىڭ چاڭگىلىدا بولىدۇ، ئاسمانلار اﷲ نىڭ ئوڭ قولىدا قاتلىنىپ تۇرىدۇ، اﷲ ئۇلار (يەنى مۇشرىكلار) نىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر ۋە ئۈستۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,65,' سۇر چېلىنغاندا ئاسمانلاردىكى ۋە زېمىندىكى اﷲ خالىغاندىن باشقا مەخلۇقاتلارنىڭ ھەممىسى ئۆلىدۇ، ئاندىن سۇر ئىككىنچى قېتىم چېلىنغاندا ناگاھان ئۇلار تۇرۇپ (نېمىگە بۇيرۇلىدىغانلىقلىرىغا) قاراپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,66,' مەھشەرگاھ پەرۋەردىگارىنىڭ نۇرى بىلەن يورۇيدۇ، نامە ـ ئەماللار ھازىر قىلىنىدۇ، پەيغەمبەرلەر ۋە گۇۋاھچىلار كەلتۈرۈلىدۇ، ئۇلارنىڭ ئارىسىدا ئادىل ھۆكۈم چىقىرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,67,' ھەر ئادەمگە قىلغان ئەمەلىنىڭ ساۋابى تولۇق بېرىلىدۇ، اﷲ ئۇلارنىڭ ئەمەللىرىنى ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,68,' كاپىرلار جەھەننەمگە توپ ـ توپ بولۇپ ھەيدىلىدۇ، ئۇلار جەھەننەمگە يېتىپ كەلگەندە، جەھەننەمنىڭ دەرۋازىلىرى ئېچىلىدۇ، جەھەننەمنى باشقۇرغۇچى پەرىشتىلەر ئۇلارغا: «ئاراڭلاردىن سىلەرگە پەرۋەردىگارىڭلارنىڭ ئايەتلىرىنى سىلەرگە ئوقۇپ بېرىدىغان، بۈگۈنكى ئۇچرىشىشنىڭ بارلىقىدىن ئاگاھلاندۇرىدىغان پەيغەمبەرلەر كەلمىگەنمۇ؟» دەيدۇ ئۇلار: «شۇنداق (پەيغەمبەرلەر كەلگەن، بىزنى ئاگاھلاندۇرغان)، لېكىن ئازابقا دۇچار بولۇش ھۆكمى (كۇفرى ۋە قىلمىشلىرى تۈپەيلىدىن) كاپىرلارغا تېگىشلىك بولدى» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,69,' (ئۇلارغا) «جەھەننەمنىڭ دەرۋازىلىرىدىن كىرىڭلار، جەھەننەمدە مەڭگۈ قېلىڭلار، مۇتەكەببىرلارنىڭ جايى نېمىدېگەن يامان!» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,70,' پەرۋەردىگارىغا تەقۋادارلىق قىلغانلار جەننەتكە توپ ـ توپ بولغان ھالدا ماڭدۇرۇلىدۇ، ھالبۇكى، ئۇلار جەننەتكە يېتىپ كەلگەن چاغدا ئۇنىڭ دەرۋازىلىرى ئېچىلىپ بولغان بولىدۇ، جەننەتكە مۇئەككەل پەرىشتىلەر ئۇلارغا: «سىلەرگە ئامانلىق بولسۇن، سىلەر (گۇناھلارنىڭ كىرلىرىدىن) پاك بولدۇڭلار، جەننەتكە كىرىڭلار، (ئۇنىڭدا) مەڭگۈ قېلىڭلار» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,71,' ئۇلار (جەننەتكە كىرىپ ئورۇنلاشقان چاغدا) ئېيتىدۇ: «جىمى ھەمدۇسانا اﷲ قا خاستۇركى، ئۇ بىزگە قىلغان ۋەدىسىنى ئىشقا ئاشۇردى، جەننەتنىڭ زېمىنىغا بىزنى ۋارىس قىلدى جەننەتتە ئۆزىمىز خالىغان جايدا تۇرىمىز، ئىشلىگۈچىلەرنىڭ ئەجرى (يەنى جەننەت) نېمىدېگەن ياخشى!»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,41,72,' پەرىشتىلەرنىڭ پەرۋەردىگارىغا تەسبىھ ئېيتقان، ھەمدى ئېيتقان ھالدا ئەرشنىڭ چۆرىسىنى ئوراپ تۇرغانلىقىنى كۆرىسەن، ئۇلارنىڭ ئارىسىدا ھەققانىي ھۆكۈم چىقىرىلىدۇ (مۆمىنلەر جەننەتكە كىرىدۇ، كاپىرلار دوزاخقا كىرىدۇ)، «جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,1,' (بۇ) كىتاب (يەنى قۇرئان) غالىب، ھەممىنى بىلگۈچى اﷲ تەرىپىدىن نازىل قىلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,2,' (اﷲ) گۇناھنى مەغپىرەت قىلغۇچىدۇر، تەۋبىنى قوبۇل قىلغۇچىدۇر (اﷲ نىڭ) ئازابى قاتتىقتۇر، (اﷲ) ئىنئام ئىگىسىدۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئاقىۋەت قايتىدىغان جاي ئۇنىڭ دەرگاھىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,3,' اﷲ نىڭ ئايەتلىرى (يەنى قۇرئان) توغرىسىدا پەقەت كاپىرلار مۇجادىلە قىلىشىدۇ، ئۇلارنىڭ شەھەرلەردە بېرىپ ـ كېلىپ يۈرۈشلىرىگە مەغرۇر بولۇپ كەتمىگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,4,' ئۇلار (يەنى مەككە كاپىرلىرى) دىن بۇرۇن، نۇھنىڭ قەۋمى ۋە ئۇلاردىن كېيىنكى (پەيغەمبەرلىرىگە قارشى ئۇيۇشقان ئاد ۋە سەمۇدقا ئوخشاش) جامائەلار (پەيغەمبەرلىرىنى) ئىنكار قىلدى (پەيغەمبەرلىرىنى ئىنكار قىلغۇچى ئۈممەتلەردىن) ھەر ئۈممەت ئۆزلىرىنىڭ پەيغەمبىرىنى ھالاك قىلىشنى قەستلىدى ھەقنى بىكار قىلىش ئۈچۈن باتىل (سۆزلەر ئارقىلىق پەيغەمبەرلىرى بىلەن) مۇنازىرە قىلىشتى، شۇنىڭ بىلەن ئۇلارنى ھالاك قىلدىم، مېنىڭ ئازابىم قانداق ئىكەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,5,'شۇنىڭدەك (يەنى ئىلگىرىكى ئۈممەتلەرگە تېگىشلىك بولغاندەك) پەرۋەردىگارىڭنىڭ ئازابى كاپىرلارغا تېگىشلىك بولدى ئۇلار دوزاخ ئەھلىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,6,' ئەرشنى كۆتۈرۈپ تۇرغان ۋە ئەرشنىڭ چۆرىسىدىكى پەرىشتىلەر پەرۋەردىگارىغا تەسبىھ ئېيتىدۇ، ھەمدى ئېيتىدۇ، ئۇنىڭغا ئىمان ئېيتىدۇ، مۆمىنلەرگە مەغپىرەت تىلەيدۇ (ئۇلار ئېيتىدۇ) «پەرۋەردىگارىمىز! سېنىڭ رەھمىتىڭ ۋە ئىلمىڭ ھەممە نەرسىنى ئۆز ئىچىگە ئالىدۇ، تەۋبە قىلغانلارغا ۋە سېنىڭ يولۇڭغا ئەگەشكەنلەرگە مەغپىرەت قىلغىن، ئۇلارنى دوزاخ ئازابىدىن ساقلىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,7,' پەرۋەردىگارىمىز! سەن ئۇلارنى، ئۇلارنىڭ ئاتا ـ بوۋىلىرىدىن، ئاياللىرىدىن، ئەۋلادلىرىدىن ياخشى بولغانلارنى ئۇلارغا ۋەدە قىلغان مەڭگۈلۈك جەننەتكە كىرگۈزگىن، سەن ھەقىقەتەن غالىبسەن، ھېكمەت بىلەن ئىش قىلغۇچىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,8,' پەرۋەردىگارىمىز! سەن ئۇلارنى يامان ئىشلاردىن ساقلىغىن، سەن كىمنى يامان ئىشلاردىن ساقلايدىكەنسەن، شۈبھىسىزكى، بۇ كۈندە سەن ئۇنىڭغا رەھمەت قىلغان بولىسەن، بۇ چوڭ بەختتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,9,' شۈبھىسىزكى، كاپىرلارغا: «اﷲ نىڭ (دۇنيادا) سىلەرگە بولغان غەزىپى سىلەرنىڭ (ئاخىرەتتە) بىر ـ بىرىڭلارغا بولغان غەزىپىڭلاردىن ئېشىپ چۈشىدۇ، چۈنكى سىلەر ئىمانغا دەۋەت قىلىناتتىڭلار، لېكىن ئىمان ئېيتمايتتىڭلار» دەپ نىدا قىلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,10,' ئۇلار: «پەرۋەردىگارىمىز! بىزنى ئىككى قېتىم ئۆلتۈرۈپ، ئىككى قېتىم تىرىلدۈردۈڭ، بىز گۇناھىمىزغا ئىقرار قىلدۇق، ئەمدى چىقىش يولى تېپىلارمۇ؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,11,' بۇ شۇنىڭ ئۈچۈندۇركى، سىلەر اﷲ نى بىر دەپ بىلىشكە چاقىرىلغاندا (بۇنى) ئىنكار قىلدىڭلار، ئەگەر اﷲ قا شېرىك كەلتۈرۈلسە (يەنى لات، ئۇززا قاتارلىق بۇتلارغا چوقۇنۇشقا چاقىرىلساڭلار) ئۇنى تەستىق قىلىسىلەر (يەنى ئۇلارنىڭ ئىلاھلىقىنى ئېتىراپ قىلىسىلەر)، ھۆكۈم يۈكسەك، بۈيۈك اﷲ قا خاستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,12,' اﷲ سىلەرگە (قۇدرىتىنىڭ) ئالامەتلىرىنى كۆرسىتىدۇ، سىلەرگە كۆكتىن يامغۇر بىلەن رىزىق چۈشۈرۈپ بېرىدۇ، پەقەت اﷲ نىڭ تائىتىگە قايتقان كىشىلەرلا (اﷲ نىڭ ئايەتلىرىدىن) ۋەز ـ نەسىھەت ئالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,13,' دىنىڭلارنى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا اﷲ قا ئىبادەت قىلىڭلار، كاپىرلار، ياقتۇرمىغان تەقدىردىمۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,14,' اﷲ نىڭ مەرتىۋىسى يۇقىرىدۇر، (اﷲ) ئەرشنىڭ ئىگىسىدۇر، (خالايىق) مۇلاقات بولۇشىدىغان كۈن (يەنى قىيامەت كۈنى) دىن ئاگاھلاندۇرۇش ئۈچۈن، اﷲ ئۆز ھۆكمى بويىچە بەندىلىرىدىن خالىغان ئادەمگە ۋەھيى چۈشۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,15,' ئۇلار قەبرىلەردىن چىققان كۈندە اﷲ قا ئۇلاردىن (يەنى ئۇلارنىڭ ئەھۋالىدىن، ئەمەللىرىدىن، سىرلىرىدىن) ھېچ نەرسە مەخپىي قالمايدۇ، بۈگۈن پادىشاھلىق كىمگە خاس؟ غالىب بىر اﷲ قا خاستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,16,' بۈگۈن ھەر ئادەمگە قىلغان ئەمەلىگە يارىشا جازا بېرىلىدۇ، بۈگۈن زۇلۇم يوقتۇر، اﷲ ھەقىقەتەن تېز ھېساب ئالغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,17,' ئۇلارنى قىيامەت كۈنىدىن ئاگاھلاندۇرغىن، ئۇ چاغدا دىللار (قورقۇنچنىڭ قاتتىقلىقىدىن) بوغۇزلارغا قاپلىشىۋالىدۇ، ئۇلار قايغۇ ـ ھەسرەتكە تولىدۇ، زالىملارغا يېقىن دوست ۋە شاپائىتى قوبۇل قىلىنىدىغان شاپائەتچى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,18,' اﷲ كۆزلەرنىڭ خىيانىتىنى ۋە دىللاردىكى يوشۇرۇن نەرسىلەرنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,19,' اﷲ ئادىللىق بىلەن ھۆكۈم چىقىرىدۇ، ئۇلار اﷲ نى قويۇپ ئىبادەت قىلىۋاتقان بۇتلار ھېچ نەرسىگە ھۆكۈم چىقىرىشقا قادىر ئەمەس، اﷲ ھەقىقەتەن (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,20,' ئۇلار زېمىندا سەير قىلىپ، ئۇلاردىن ئىلگىرى ئۆتكەنلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۈزەتمىدىمۇ؟ ئۇلار بولسا زېمىندا كۈچ ـ قۇۋۋەت ۋە يادىكارلىقلار (يەنى ئۆي ـ ئىمارەت قۇرۇش) جەھەتتە بۇلار (يەنى سېنىڭ قەۋمىڭنىڭ كاپىرلىرى) دىن ئۈستۈن ئىدى، گۇناھى تۈپەيلىدىن اﷲ ئۇلارنى جازالىدى، ئۇلارنى ھېچ ئەھەدى اﷲ نىڭ ئازابىدىن ساقلاپ قالالمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,21,' بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلارغا پەيغەمبەرلىرى روشەن مۆجىزىلەرنى ئېلىپ كەلگەن ئىدى، ئۇلار ئىشەنمىدى، شۇنىڭ بىلەن اﷲ ئۇلارنى جازالىدى اﷲ ھەقىقەتەن كۈچلۈكتۇر، (اﷲ نىڭ) ئازابى قاتتىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,22,' بىز مۇسانى مۆجىزىلىرىمىز بىلەن، روشەن دەلىل بىلەن ھەقىقەتەن پىرئەۋنگە، ھامانغا ۋە قارۇنغا ئەۋەتتۇق، ئۇلار (مۇسانى) يالغانچى، سېھىرگەر دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,23,' مۇسا ئۇلارغا دەرگاھىمىزدىن ھەق (يەنى پەيغەمبەرلىك) بىلەن كەلگەندە، ئۇلار: «مۇسا بىلەن ئىمان ئېيتقانلارنىڭ ئوغۇللىرىنى (نەسلىنى قۇرۇتۇش ئۈچۈن) ئۆلتۈرۈڭلار، ئاياللىرىنى (خىزمەتكە سېلىش ئۈچۈن) قالدۇرۇڭلار» دېدى كاپىرلارنىڭ ھىيلە ـ مىكرى پەقەت بەربات بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,24,' پىرئەۋن ئېيتتى: «مېنى قويۇۋېتىڭلار، مۇسانى ئۆلتۈرىمەن، مۇسا (ئۆزىنى مەندىن قۇتۇلدۇرۇش ئۈچۈن) پەرۋەردىگارىنى چاقىرسۇن، مەن ھەقىقەتەن مۇسانىڭ دىنىڭلارنى ئۆزگەرتىۋېتىشىدىن ياكى زېمىندا (يەنى دۆلەتتە) قالايمىقانچىلىق تۇغدۇرۇشىدىن قورقىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,25,' مۇسا ئېيتتى: «مەن ھەقىقەتەن مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار (بولغان اﷲ) غا سېغىنىپ، ھېساب كۈنى (يەنى قىيامەت كۈنى) گە ئىشەنمەيدىغان ھەر بىر مۇتەكەببىر (نىڭ زىيانكەشلىك قىلىشى) دىن پاناھ تىلەيمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,26,' پىرئەۋن خانىدانىغا مەنسۇپ، ئىمانىنى يوشۇرىدىغان بىر مۆمىن ئادەم ئېيتتى: «سىلەر 'پەرۋەردىگارىم اﷲ' دېگەن كىشىنى ئۆلتۈرەمسىلەر؟ ھالبۇكى، ئۇ سىلەرگە ھەقىقەتەن پەرۋەردىگارىڭلارنىڭ دەرگاھىدىن روشەن دەلىللەرنى ئېلىپ كەلدى ئەگەر ئۇ (پەيغەمبەرلىك دەۋاسىدا) يالغانچى بولسا، يالغانچىلىقنىڭ زىيىنى ئۇنىڭ ئۆزىگىدۇر، ئەگەر ئۇ راستچىل بولسا، ئۇ سىلەرنى قورقۇتقان ئازابنىڭ بىر قىسمى سىلەرگە كېلىدۇ، اﷲ ھەقىقەتەن ھەددىدىن ئاشقان يالغانچىنى ھىدايەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,27,' ئى قەۋمىم! بۈگۈن سەلتەنەت سىلەرنىڭ ئىلگىڭلاردىدۇر، (مىسىر) زېمىنىدا سىلەر غالىبسىلەر، ئەگەر بىزگە اﷲ نىڭ ئازابى كەلسە، ئۇنىڭدىن قۇتۇلۇشقا بىزگە كىم ياردەم بېرىدۇ؟» پىرئەۋن ئېيتتى: «مەن سىلەرگە پەقەت نېمىنى توغرا دەپ قارىسام، شۇنى مەسلىھەت بېرىمەن سىلەرنى مەن پەقەت توغرا يولغا باشلايمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,28,' مۆمىن ئادەم ئېيتتى: «ئى قەۋمىم! سىلەرنىڭ نۇھ، ئاد، سەمۇد قەۋملىرىگە ئوخشاش ۋە ئۇلاردىن كېيىنكىلەرگە ئوخشاش جامائەلەر ئۇچرىغان جازاغا ئۇچرىشىڭلاردىن قورقىمەن، اﷲ بەندىلىرىگە زۇلۇم قىلىشنى خالىمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,29,' ئى قەۋمىم! مەن سىلەر ئۈچۈن ھەقىقەتەن قىيامەت كۈنىدىن ئەنسىرەيمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,30,' ئۇ كۈندە (دوزاخ ئازابىنىڭ دەھشىتىدىن قورقۇپ) ئارقاڭلارغا چېكىنىسىلەر، سىلەرگە ھېچ اﷲ نىڭ ئازابىدىن قۇتۇلدۇرغۇچى بولمايدۇ كىمكى اﷲ ئۇنى گۇمراھ قىلىدىكەن، ئۇنى ھىدايەت قىلغۇچى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,31,' شەك ـ شۈبھىسىزكى، ئىلگىرى سىلەرگە يۇسۇف (اﷲ نىڭ دەرگاھىدىن) روشەن مۆجىزىلەرنى ئېلىپ كەلدى ئۇ ئېلىپ كەلگەن دەلىلدىن سىلەر ھامان شەكلەندىڭلار، تاكى ئۇ ۋاپات بولغاندا، (دەلىل ـ پاكىتسىز) ھالدا، اﷲ ئۇنىڭدىن كېيىن ھەرگىز پەيغەمبەر ئەۋەتمەيدۇ دېدىڭلار، اﷲ (گۇناھتا) ھەددىدىن ئاشقۇچىنى، (دىندا) شەكلەنگۈچىنى مۇشۇنداق ئازدۇرىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,32,' اﷲ نىڭ ئايەتلىرى ئۈستىدە ئۆزلىرىگە (اﷲ تەرىپىدىن) كەلگەن ھېچقانداق دەلىل بولماستىن (قارىسىغا) جاڭجاللىشىدىغانلار اﷲ نىڭ دەرگاھىدا ۋە مۆمىنلەرنىڭ نەزىرىدە قاتتىق نەپرەتكە قالىدۇ، بۇزغۇنچىلىق قىلىپ (ئىماندىن باش تارتقان)، (بەندىلەرگە) زومىگەرلىك قىلغان ھەر قانداق ئادەمنىڭ دىلىنى اﷲ مۇشۇنداق پېچەتلىۋېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,33,' پىرئەۋن (ۋەزىرى ھامانغا): «ئى ھامان! ماڭا بىر ئېگىز بىنا سالغىن، ئۇنىڭ بىلەن مەن دەرۋازىلارغا–ئاسماننىڭ دەرۋازىلىرىغا يېتىشىم مۇمكىن، شۇنىڭ بىلەن مەن مۇسانىڭ ئىلاھىنى كۆرەي، مەن ھەقىقەتەن مۇسانى يالغانچى دەپ گۇمان قىلىمەن» دېدى پىرئەۋنگە ئۇنىڭ يامان ئەمەلى شۇنداق چىرايلىق كۆرسىتىلدى، پىرئەۋن (گۇمراھلىقى تۈپەيلىدىن ھىدايەت) يولىدىن مەنئى قىلىندى، پىرئەۋننىڭ ھىيلە ـ مىكرى پەقەت بىكاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,34,' (پىرئەۋن خاندانىدىن) ئىمان ئېيتقان كىشى ئېيتتى: «ئى قەۋمىم! سىلەر ماڭا ئەگىشىڭلار، مەن سىلەرنى توغرا يولغا باشلايمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,35,' ئى قەۋمىم! بۇ دۇنيا تىرىكچىلىكى پەقەت (ۋاقىتلىق) پايدىلىنىشتىن ئىبارەتتۇر، ئاخىرەت ھەقىقەتەن (مەڭگۈلۈك) قارارگاھتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,36,' كىمكى (بۇ دۇنيادا) بىرەر يامانلىقنى قىلىدىكەن، ئۇنىڭغا (ئاخىرەتتە) شۇنىڭغا لايىق جازا بېرىلىدۇ، مەيلى ئەر بولسۇن، مەيلى ئايال بولسۇن، كىمكى ئۆزى مۆمىن تۇرۇپ بىرەر ياخشى ئىش قىلىدىكەن، ئەنە شۇلار جەننەتكە كىرىدۇ، جەننەتتە ئۇلارغا ھېسابسىز رىزىق بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,37,' ئى قەۋمىم! ماڭا نېمىدۇركى، سىلەرنى مەن (دوزاختىن) قۇتۇلۇشقا دەۋەت قىلىمەن، سىلەر بولساڭلار مېنى دوزاخقا دەۋەت قىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,38,' سىلەر مېنى اﷲ نى ئىنكار قىلىشقا، مەن بىلمەيدىغان نەرسىلەرنى ئۇنىڭغا شېرىك كەلتۈرۈشكە دەۋەت قىلىسىلەر، ھالبۇكى، مەن سىلەرنى غالىب، (بەندىلەرنىڭ گۇناھلىرىنى) ناھايىتى مەغپىرەت قىلغۇچى (اﷲ) غا دەۋەت قىلىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,39,' ھەقىقەتەن سىلەر مېنى ئۇنىڭغا (يەنى بۇتقا) ئىبادەت قىلشىقا چاقىرىسىلەر، ئۇنىڭ دۇنيا ۋە ئاخىرەتتە (ئۆزىگە ئىبادەت قىلىشقا) چاقىرغىنى يوق، بىزنىڭ ئاخىر بارىدىغان جايىمىز اﷲ نىڭ دەرگاھىدۇر، (گۇمراھلىقتا) ھەددىدىن ئاشقۇچىلار ئەھلى دوزاختۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,40,' (سىلەرگە ئازاب نازىل بولغان چاغدا) سىلەرگە ئېيتقان سۆزۈمنىڭ (راستلىقىنى) ئەسلەيسىلەر، مەن ئىشىمنى اﷲ قا تاپشۇرىمەن، اﷲ ھەقىقەتەن بەندىلىرىنى كۆرۈپ تۇرغۇچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,41,' اﷲ ئۇنى پىرئەۋن جامائەسىنىڭ سۇيىقەستلىرىدىن ساقلىدى پىرئەۋن جامائەسىگە ئەڭ يامان ئازاب نازىل بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,42,' ئۇلار ئەتىگىنى ـ ئاخشىمى ئوتقا توغرىلىنىپ تۇرىدۇ، قىيامەت قايىم بولغان كۈندە: «ئى پىرئەۋن جامائەسى! ئازابنىڭ ئەڭ قاتتىقىغا (دوزاخ ئوتىغا) كىرىڭلار!» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,43,' ئۇ چاغدا ئۇلار دوزاختا ئۆزئارا مۇنازىرىلىشىدۇ، ئاجىز (ئەگەشكۈچىلەر) ھاكاۋۇر (ئەگەشتۈرگۈچىلىرىگە): «شۈبھىسىزكى، بىز سىلەرگە ئەگەشكەن ئىدۇق، بىزدىن دوزاخ ئازابىدىن بىر قىسىمىنى ئېلىپ تاشلىيالامسىلەر؟» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,44,' ھاكاۋۇر (ئەگەشتۈرگۈچىلىرى): «شۈبھىسىزكى، بىز ھەممىمىز دوزاختادۇرمىز، اﷲ ھەقىقەتەن بەندىلەرنىڭ ئارىسىدا ھۆكۈم چىقاردى» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,45,' دوزاختىكىلەر دوزاخقا مۇئەككەل پەرىشتىلەرگە: «سىلەر پەرۋەردىگارىڭلارغا دۇئا قىلىڭلار، ئۇ بىزدىن بىر كۈنلۈك ئازابنى يېنىكلەتسە ئىكەن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,46,' ئۇلار (يەنى دوزاخقا مۇئەككەل پەرىشتىلەر): «پەيغەمبىرىڭلار سىلەرگە روشەن مۆجىزىلەرنى ئېلىپ كەلمىگەنمىدى؟ » دەيدۇ ئۇلار (يەنى كۇففارلار): «ھەئە، ئېلىپ كەلگەن ئىدى» دەيدۇ ئۇلار (يەنى پەرىشتىلەر): «سىلەر دۇئا قىلىڭلار، (لېكىن) كاپىرلارنىڭ دۇئاسى پايدىسىزدۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,47,' شەك ـ شۈبھىسىزكى، بىز پەيغەمبىرىمىزگە، مۆمىنلەرگە ھاياتىي دۇنيادا ۋە (پەرىشتە، پەيغەمبەر ۋە مۆمىنلەردىن بەندىلەرنىڭ ئەمەللىرىگە گۇۋاھ بولىدىغان) گۇۋاھچىلار ھازىر بولىدىغان كۈندە ئەلۋەتتە ياردەم بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,48,' ئۇ كۈندە كاپىرلارنىڭ ئۆزرىلىرى پايدا بەرمەيدۇ، ئۇلار لەنەتكە ئۇچرايدۇ، ئۇلار ئاخىرەتنىڭ ئازابىغا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,49,' شۈبھىسىزكى، بىز مۇساغا ھىدايەت ئاتا قىلدۇق، ئىسرائىل ئەۋلادىغا كىتاب (يەنى تەۋرات) نى مىراس قىلىپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,50,' (ئۇ) ئەقىل ئىگىلىرى ئۈچۈن ھىدايەتتۇر ۋە نەسىھەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,51,' (ئى مۇھەممەد! مۇشرىكلارنىڭ يەتكۈزگەن ئەزىيەتلىرىگە) سەۋر قىلغىن، اﷲ نىڭ (ساڭا ۋە سېنىڭ تەۋەلىرىڭگە ياردەم بېرىش) ۋەدىسى ھەقىقەتەن ھەقتۇر، گۇناھىڭغا ئىستىغپار ئېيتقىن، ئاخشىمى ـ ئەتىگىنى پەرۋەردىگارىڭغا ھەمدى بىلەن تەسبىھ ئېيتقىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,52,' شۈبھىسىزكى، اﷲ نىڭ ئايەتلىرى ئۈستىدە ئۆزلىرىگە (اﷲ تەرىپىدىن) كەلگەن ھېچقانداق دەلىل بولماستىن (قارىسىغا) جاڭجاللىشىدىغانلارنىڭ كۆكرەكلىرىدە پەقەت كىبرى بار، ئۇلار (اﷲ نىڭ نۇرىنى ئۆچۈرۈشتىن ئىبارەت) مەقسىتىگە يېتەلمەيدۇ، اﷲ قا سېغىنىپ (ئۇلارنىڭ شەررىدىن پاناھ تىلىگىن)، ھەقىقەتەن اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,53,' ئاسمانلارنى ۋە زېمىننى يارىتىش ئەلۋەتتە ئىنسانلارنى يارىتىشتىن كۆپ قىيىندۇر ۋە لېكىن ئىنسانلارنىڭ كۆپچىلىكى (بۇنى) ئۇقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,54,' ئەما بىلەن كۆزى كۆرۈدىغان ئادەم (يەنى مۆمىن بىلەن كاپىر) باراۋەر بولمايدۇ، ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان ئادەملەر بىلەن يامان ئىش قىلغۇچى ئادەملەر باراۋەر بولمايدۇ سىلەر ئاز ۋەز ـ نەسىھەت ئالىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,55,' قىيامەت چوقۇم كېلىدۇ، ئۇنىڭدا شەك يوق ۋە لېكىن ئىنسانلارنىڭ تولىسى (ئۇنىڭغا) ئىشەنمەيدۇ (شۇنىڭ ئۈچۈن ئۇلار ئۆلگەندىن كېيىن تىرىلىشنى، قىيامەت كۈنى بېرىلىدىغان مۇكاپات بىلەن جازانى ئىنكار قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,56,' پەرۋەردىگارىڭلار ئېيتىدۇ: «ماڭا دۇئا قىلىڭلار، مەن (دۇئايىڭلارنى) قوبۇل قىلىمەن (تىلىگىنىڭلارنى بېرىمەن)، شۈبھىسىزكى، مېنىڭ ئىبادىتىمدىن چوڭچىلىق قىلىپ باش تارتىدىغانلار خار ھالدا جەھەننەمگە كىرىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,57,' (كۈندۈزى ئىشلەپ يەتكەن ھارغىنلىقتىن) ئارام ئېلىشىڭلار ئۈچۈن، اﷲ سىلەرگە كېچىنى (قاراڭغۇ قىلىپ) ياراتتى، (تىرىكچىلىك يولىدا ھەرىكەتلىنىشىڭلار ئۈچۈن) كۈندۈزنى يورۇق ياراتتى اﷲ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر ۋە لېكىن ئىنسانلارنىڭ تولىسى (اﷲ نىڭ ئېھسانىغا شۈكۈر قىلمايدۇ، اﷲ نىڭ مەرھەمىتى ۋە نېمەتلىرىگە نانكورلۇق قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,58,' ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ھەممە نەرسىنى ياراتقۇچىدۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، قانداقمۇ (اﷲ قا ئىبادەت قىلىشتىن بۇتلارغا ئىبادەت قىلىشقا) بۇرۇلۇپ كېتىسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,59,'اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلار (ھىدايەتتىن ۋە ھەقىقەتتىن) شۇنداق بۇرۇلۇپ كېتىدۇ (ئى مۇھەممەد! قەۋمىڭنىڭ اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقىغا قايغۇرۇپ كەتمىگىن، ئۇلاردىن ئىلگىرى ئۆتكەنلەر مۇشۇنداق قىلغان)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,60,' اﷲ سىلەرگە زېمىننى تۇرالغۇ، ئاسماننى بىنا قىلدى سىلەرنى سۈرەتكە كىرگۈزدى سۈرىتىڭلارنى چىرايلىق قىلدى سىلەرگە (تۈرلۈك) پاك نەرسىلەرنى رىزىق قىلىپ بەردى ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئالەملەرنىڭ پەرۋەردىگارى اﷲ نىڭ بەرىكىتى بۈيۈكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,61,' اﷲ ھاياتتۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، دىنىڭلارنى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا ئۇنىڭغا ئىبادەت قىلىڭلار، جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,62,'(ئى مۇھەممەد!) ئېيتقىنكى: «پەرۋەردىگارىمدىن ماڭا روشەن دەلىللەر كەلگەن چاغدا، سىلەر اﷲ نى قويۇپ ئىبادەت قىلىۋاتقان بۇتلىرىڭلارغا ئىبادەت قىلىشتىن مەنئى قىلىندىم، ئالەملەرنىڭ پەرۋەردىگارىغا بويسۇنۇشقا بۇيرۇلدۇم»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,63,' اﷲ سىلەرنى (يەنى ئاتاڭلار ئادەم ئەلەيھىسسالامنى) تۇپراقتىن، (ئۇنىڭ ئەۋلادىنى ئالدى بىلەن) ئابىمەنىيدىن، ئاندىن لەختە قاندىن ياراتتى، ئاندىن ئۇ سىلەرنى (ئاناڭلارنىڭ قارنىدىن) بوۋاق ھالىتىڭلاردا چىقىرىدۇ، ئاندىن (كۈچ ـ قۇۋۋەت ۋە ئەقىلدە كامالەت يېشى) قىران ۋاقتىڭلارغا يېتىسىلەر، ئاندىن قېرىيسىلەر– بەزىڭلار بۇرۇن ۋاپات بولۇپ كېتىسىلەر–ئاندىن سىلەر مۇئەييەن ۋاقىتقىچە ياشايسىلەر، سىلەر (اﷲ نىڭ قۇدرىتىنىڭ دەلىللىرىنى) چۈشەنگەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,64,' اﷲ تىرىلدۈرۈشكە ۋە ئۆلتۈرۈشكە قادىر، ئۇ بىرەر ئىشنى (قىلىشنى) ئىرادە قىلسا، ئۇنىڭغا «ۋۇجۇدقا كەل» دەيدۇ ـ دە، ئۇ ۋۇجۇدقا كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,65,' اﷲ نىڭ ئايەتلىرى ئۈستىدە جاڭجاللىشىۋاتقانلارنى كۆرمىدىڭمۇ؟ ئۇلار قانداقمۇ (ھىدايەتتىن گۇمراھلىققا) بۇرۇلۇپ كېتىدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,66,'ئۇلار كىتابنى (يەنى قۇرئاننى) ئىنكار قىلدى، پەيغەمبەرلىرىمىزگە بىز نازىل قىلغان مۆجىزىلەرنى ئىنكار قىلدى ئۇلار ئۇزاققا قالماي (ئىنكار قىلغانلىقلىرىنىڭ ئاقىۋىتىنى) بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,67,' ئۇ چاغدا (يەنى دوزاخقا كىرگەندە) ئۇلارنىڭ بويۇنلىرىدا تاقاقلار ۋە زەنجىرلەر بولىدۇ، ئۇلار قايناقسۇغا سۆرەپ كىرىلىدۇ، ئاندىن ئوتتا كۆيدۈرۈلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,68,' ئاندىن ئۇلارغا: «اﷲ نى قويۇپ شېرىك كەلتۈرگەن بۇتلىرىڭلار قەيەردە؟» دېيىلىدۇ، ئۇلار: «ئۇ بۇتلار ئۆزلىرىنى بىزدىن چەتكە ئالدى، بەلكى بىز ئىلگىرى ھېچنەرسىگە ئىبادەت قىلمايتتۇق» دەيدۇ اﷲ كاپىرلارنى مۇشۇنداق گۇمراھ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,69,' بۇ (ئازاب) سىلەرنىڭ زېمىندا ھەقسىز رەۋىشتە (گۇناھ قىلىپ پۇل ـ مالنى ھارام يوللارغا سەرپ قىلغانلىقىڭلار ئۈچۈن) خۇشال بولغانلىقىڭلار ۋە ھاكاۋۇر بولۇپ كەتكەنلىكىڭلار ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,70,' سىلەر دوزاخنىڭ دەرۋازىلىرىدىن كىرىپ ئۇ يەردە مەڭگۈ قېلىڭلار، مۇتەكەببۇرلارنىڭ جايى نېمىدېگەن يامان؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,71,'(ئى مۇھەممەد! قەۋمىڭنىڭ سېنى ئىنكار قىلغانلىقىغا) سەۋر قىلغىن، اﷲ نىڭ (ئۇلارنى ئازابلاش توغرىسىدىكى) ۋەدىسى ھەقىقەتەن ھەقتۇر، ساڭا بىز ئۇلارغا ۋەدە قىلغان ئازابنىڭ بىر قىسمىنى كۆرسەتسەك (بۇ اﷲ نىڭ ۋەدىسىگە ۋاپا قىلغانلىقىدۇر) ياكى سېنى (ئۇنى كۆرسىتىشتىن بۇرۇن) ۋاپات قىلدۇرساق، ئۇلار بىزنىڭ دەرگاھىمىزغا قايتۇرۇلىدۇ، (قىيامەتتە ئۇلار ئازابقا دۇچار بولىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,72,' سەندىن ئىلگىرى بىز ھەقىقەتەن نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارنىڭ ئارىسىدىن ساڭا بايان قىلىپ بەرگەنلىرىمىزمۇ، بايان قىلىپ بەرمىگەنلىرىمىزمۇ بار، ھېچقانداق پەيغەمبەر اﷲ نىڭ ئىزنىسىز بىرەر مۆجىزە كەلتۈرەلمەيدۇ، اﷲ نىڭ ئەمرى (يەنى قىيامەت) كەلگەن چاغدا (كىشىلەرنىڭ ئارىسىدا) ئادالەت بىلەن ھۆكۈم چىقىرىلىدۇ، بۇ چاغدا ھەقكە قارشى تۇرغۇچىلار زىيان تارتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,73,' مىنىشىڭلار ۋە گۆشلىرىنى يېيىشىڭلار ئۈچۈن اﷲ سىلەرگە ھايۋانلارنى ياراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,74,' ئۇلار سىلەرگە نۇرغۇن پايدا كەلتۈرىدۇ، ئۇلارغا مىنىپ دىلىڭلارغا پۈككەن ھاجىتىڭلاردىن چىقىسىلەر، (قۇرۇقلۇقتا يۈك ـ تاقلىرىڭلارنى) ئۇلارغا ئارتىسىلەر، (دېڭىزدا) كېمىلەرگە قاچىلايسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,75,' اﷲ سىلەرگە (قۇدرىتىنىڭ) ئالامەتلىرىنى كۆرسىتىدۇ، سىلەر اﷲ نىڭ (قۇدرىتىنىڭ) قايسى ئالامەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,76,'ئۇلار زېمىندا سەير قىلىپ ئۇلاردىن ئىلگىرىكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆزەتمىدىمۇ؟ بۇلاردىن ئۇلارنىڭ سانى كۆپ ئىدى، زېمىندا كۈچ ـ قۇۋۋەت ۋە يادىكارلىقلار (يەنى ئۆي ـ ئىمارەت) جەھەتتە بۇلاردىن ئۈستۈن ئىدى ئۇلارنىڭ قولغا كەلتۈرگەن نەرسىلىرى ئۇلارغا ئەسقاتمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,77,' ئۇلارغا پەيغەمبەرلىرى روشەن مۆجىزىلەر بىلەن كەلگەندە، ئۆزلىرىدە بار ئىلىم بىلەن پەخىرلىنىپ (پەيغەمبەرلەرنى ئىنكار قىلدى ۋە مەسخىرە قىلدى)، ئۇلارغا ئۆزلىرى مەسخىرە قىلغان (ۋە كەلسە دەپ ئالدىرىغان) ئازاب نازىل بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,78,' ئۇلار بىزنىڭ ئازابىمىزنى كۆرگەن چاغدا: «بىز بىر اﷲ قا ئىمان ئېيتتۇق، بىز بۇرۇن (اﷲ قا) شېرىك قىلغان بۇتلارنى ئىنكار قىلدۇق» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,42,79,' بىزنىڭ ئازابىمىزنى كۆرگەن چاغدا ئېيتقان ئىماننىڭ ئۇلارغا پايدىسى بولمىدى، بۇ (يەنى ئازابنى كۆرگەندە ئېيتقان ئىماننىڭ پايدىلىق بولماسلىقى) اﷲ نىڭ بەندىلىرى ئارىسىدا تۇتقان يولىدۇر، بۇ چاغدا كاپىرلار زىيان تارتقۇچى بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,1,' (بۇ) ناھايىتى شەپقەتلىك، مېھرىبان اﷲ تەرىپىدىن نازىل قىلىنغان (ۋەھيىدۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,2,' ئايەتلىرى ئوچۇق بايان قىلىنغان كىتابتۇر، بىلىدىغان قەۋم ئۈچۈن نازىل بولغان ئەرەبچە قۇرئاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,3,' (مۆمىنلەرگە جەننەت بىلەن) خۇش خەۋەر بەرگۈچىدۇر، (كاپىرلارنى دوزاخ بىلەن) ئاگاھلاندۇرغۇچىدۇر ئۇلار (يەنى مۇشرىكلار) نىڭ تولىسى (ئۇنىڭ ئايەتلىرىنى پىكىر قىلىشتىن) يۈز ئۆرۈدى ئۇلار (ئۇنىڭغا) قۇلاق سالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,4,' ئۇلار (پەيغەمبەرگە): «بىزنىڭ دىللىرىمىز سەن بىزنى دەۋەت قىلغان نەرسىلەردىن پەردىلەنگەن، قۇلاقلىرىمىز ئېغىر، سەن بىلەن بىزنىڭ ئارىمىزدا پەردە (يەنى دىنىي جەھەتتە ئوخشىماسلىق) بار، سەن ئۆز ئىشىڭنى قىل، بىزمۇ ھەقىقەتەن ئۆز ئىشىمىزنى قىلايلى (يەنى سەنمۇ ئۆز دىنىڭ بىلەن بول، بىزمۇ ئۆز دىنىمىز بىلەن بولايلى)» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,5,' سەن (ئۇ مۇشرىكلارغا): «مەن سىلەرگە ئوخشاش ئىنسانمەن، ماڭا، ئىلاھىڭلار بىر ئىلاھتۇر، دەپ ۋەھيى قىلىنىدۇ، ئۇنىڭغا (ئىمان ۋە تائەت ـ ئىبادەت بىلەن) يۈزلىنىڭلار ۋە ئۇنىڭدىن مەغپىرەت تىلەڭلار!» دېگىن، مۇشرىكلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,6,'ئۇلار زاكات بەرمەيدۇ، ئاخىرەتكە ئىشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,7,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا ئەلۋەتتە ئۈزلۈكسىز ساۋاب بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,8,' (سەن ئۇلارغا) «سىلەر زېمىننى ئىككى كۈندە ياراتقان اﷲ نى ئىنكار قىلامسىلەر ۋە ئۇنىڭغا شېرىكلەر كەلتۈرەمسىلەر؟ ئۇ ئالەملەرنىڭ پەرۋەردىگارىدۇر» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,9,' ئۇ زېمىننىڭ ئۈستىدە نۇرغۇن تاغلارنى ياراتتى، زېمىننى بەرىكەتلىك قىلدى، ئەھلى زېمىننىڭ رىزقىنى بەلگىلىدى، (زېمىننى يارىتىش بىلەن ئاھالىسىنىڭ رىزقىنى بەلگىلەشنى كەم ـ زىيادىسىز) باراۋەر تۆت كۈندە (تاماملىدى)، (زېمىننىڭ ۋە ئۇنىڭدىكى نەرسىلەرنىڭ يارىتىلىش مۇددىتىنى) سورىغۇچىلار ئۈچۈن (بۇ جاۋابتۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,10,' ئاندىن ئۇ ئاسماننى (يارىتىشقا) يۈزلەندى، ھالبۇكى، ئۇ تۇمان ھالىتىدە ئىدى، ئاسمان بىلەن زېمىنغا: «ئىختىيارىي ياكى ئىختىيارسىز ھالدا كېلىڭلار (يەنى ئەمرىمنى قوبۇل قىلىڭلار)» دېدى ئىككىلىسى «ئىختىيارىي كەلدۇق» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,11,' اﷲ يەتتە ئاسماننى ئىككى كۈندە ياراتتى ھەر ئاسماننىڭ ئىشىنى ئۆزىگە بىلدۈردى (يەنى ھەر ئاسماننىڭ ئېھتىياجلىق ئىشلىرىنى ئورۇنلاشتۇردى) ئەڭ تۆۋەنكى ئاسماننى يۇلتۇزلار بىلەن زىننەتلىدۇق ۋە قوغدىدۇق، بۇ ئەنە شۇ غالىب، ھەممىنى بىلگۈچى اﷲ نىڭ تەقدىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,12,' ئەگەر ئۇلار (بۇ بايانلاردىن كېيىن ئىماندىن) يۈز ئۆرۈسە (سەن ئۇلارغا): «سىلەرنى ئاد ۋە سەمۇد قەۋمىلىرىنىڭ بېشىغا كەلگەن ئازابقا ئوخشاش ئازابتىن ئاگاھلاندۇرىمەن» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,13,' ئۆز ۋاقتىدا ئۇلارغا ئالدى ـ كەينىدىن (يەنى ھەممە تەرەپتىن) پەيغەمبەرلەر كېلىپ: «بىر اﷲ تىن باشقىغا ئىبادەت قىلماڭلار!» دېسە، ئۇلار: «ئەگەر پەرۋەردىگارىمىز خالىسا، چوقۇم پەرىشتىلەرنى چۈشۈرگەن بولاتتى، بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ھەقىقەتەن ئىنكار قىلىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,14,' ئاد بولسا زېمىندا (اﷲ نىڭ بەندىلىرى ھۇد ۋە ئۇنىڭ بىلەن ئىمان ئېيتقانلارغا) ھەقسىز رەۋىشتە يوغانچىلىق قىلدى ئۇلار: «كۈچ ـ قۇۋۋەتتە بىزدىن كىم ئارتۇق؟» دېدى ئۇلار ئۆزلىرىنى ياراتقان اﷲ نىڭ كۈچ ـ قۇۋۋەتتە ئۆزلىرىدىن ئۈستۈن ئىكەنلىكىنى بىلمىدىمۇ؟ ئۇلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,15,' ئۇلارغا بۇ دۇنيادىلا خار قىلغۇچى ئازابنى تېتىتىشىمىز ئۈچۈن، شۇم كۈنلەردە قاتتىق سوغۇق شامالنى ئەۋەتتۇق، ئاخىرەتنىڭ ئازابى تېخىمۇ خار قىلغۇچىدۇر، ئۇلار (ئازابنى دەپئى قىلىدىغان) ياردەمگە ئېرىشەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,16,' سەمۇدقا بولسا توغرا يولنى كۆرسەتتۇق، ئۇلار ھىدايەتتىن كورلۇق (يەنى گۇمراھلىق) نى ئارتۇق بىلدى قىلمىشلىرى تۈپەيلىدىن، ئۇلارنى خار قىلغۇچى چاقماق ئازابى ھالاك قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,17,' ئىمان ئېيتقانلارنى ۋە تەقۋادارلارنى (يەنى سالىھ ئەلەيھىسسالام بىلەن ئۇنىڭغا ئەگەشكەنلەرنى ئازابتىن) قۇتقۇزدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,18,' ئۇ كۈندە اﷲ نىڭ دۈشمەنلىرى دوزاخقا توپلىنىدۇ، ئالدى ـ ئارقىسى يىغىلىپ بولغۇچە توختىتىپ قويۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,19,' ئۇلار دوزاخقا كەلگەندە، ئۇلارنىڭ قۇلاقلىرى، كۆزلىرى ۋە تېرىلىرى ئۇلارنىڭ قىلمىشلىرى ھەققىدە گۇۋاھلىق بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,20,' ئۇلار ئۆزلىرىنىڭ تېرىلىرىگە: «نېمىشقا بىزنىڭ زىيىنىمىزغا گۇۋاھلىق بەردىڭلار؟» دەيدۇ، تېرىلىرى: «ھەر نەرسىنى سۆزلەتكەن اﷲ بىزنى سۆزلەتتى، ئۇ دەسلەپتە سىلەرنى ياراتتى ئۇنىڭ دەرگاھىغا قايتۇرۇلىسىلەر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,21,' سىلەر (دۇنيادىكى چېغىڭلاردا) قۇلىقىڭلارنىڭ، كۆزۈڭلارنىڭ، تېرىلىرىڭلارنىڭ سىلەرنىڭ زىيىنىڭلارغا گۇۋاھلىق بېرىشىدىن ساقلانمىغان ئىدىڭلار (يەنى يامان ئىشلارنى قىلغان چېغىڭلاردا، رەسۋا بولۇشتىن قورقۇپ، كىشىلەردىن يوشۇرغان ئىدىڭلار، ۋەھالەنكى، ئەزالىرىڭلارنىڭ سىلەرنىڭ زىيىنىڭلارغا گۇۋاھلىق بېرىدىغانلىقىنى گۇمان قىلمىغان ئىدىڭلار)، لېكىن سىلەر قىلمىشىڭلاردىن نۇرغۇن ئىشلارنى «اﷲ بىلمەيدۇ» دەپ گۇمان قىلدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,22,' پەرۋەردىگارىڭلارغا قىلغان ئەنە شۇ (قەبىھ) گۇمانىڭلار سىلەرنى ھالاك قىلدى شۇنىڭ بىلەن زىيان تارتقۇچىلاردىن بولدۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,23,' ئەگەر ئۇلار (ئازابقا) چىدىسا، (چىداملىقنىڭ پايدىسى يوق) دوزاخ ئۇلارنىڭ جايىدۇر، ئەگەر ئۇلار (اﷲ تىن) رازىلىق تىلىسە، ھەرگىز رازىلىققا ئېرىشەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,24,' ئۇلارغا بىز (شەيتانلاردىن) دوستلارنى مۇسەللەت قىلدۇق، ئۇلار (يەنى شەيتانلار) ئۇلارغا ئالدى ـ كەينىدىكى نەرسىلەرنى (يەنى ئۇلارنىڭ ھازىرقى ۋە كەلگۈسىدىكى قەبىھ ئەمەللىرىنى) چىرايلىق كۆرسەتتى، ئۇلارغا ئۇلاردىن ئىلگىرى ئۆتكەن ئىنسانلاردىن ۋە جىنلاردىن بولغان (گۇناھكار شەقى) ئۈممەتلەر قاتارىدا ئازاب سۆزى تېگىشلىك بولدى، چۈنكى ئۇلار زىيان تارتقۇچىلار ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,25,' كاپىرلار: «بۇ قۇرئانغا قۇلاق سالماڭلار، ئۇنى ئېلىشتۇرۇۋېتىڭلار، سىلەر غەلىبە قىلىشىڭلار مۇمكىن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,26,' بىز كاپىرلارغا چوقۇم قاتتىق ئازابنى تېتىتىمىز، بىز ئۇلارنىڭ يامان قىلمىشلىرىغا ئەڭ قاتتىق جازا بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,27,' اﷲ نىڭ دۈشمەنلىرىگە بېرىلىدىغان جازا ئەنە شۇ دوزاختۇر، ئۇلارنىڭ مەڭگۈلۈك تۇرىدىغان جايى دوزاختۇر، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ئۈچۈن (ئۇلارغا) شۇنداق جازا بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,28,' كاپىرلار: «پەرۋەردىگارىمىز! ئىنسانلاردىن ۋە جىنلاردىن بىزنى ئازدۇرغانلارنى بىزگە كۆرسەتكىن، (ئۇلارنىڭ) ئەڭ خارلاردىن بولۇشى ئۈچۈن ئۇلارنى ئاياغلىرىمىزنىڭ ئاستىدا چەيلەيمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,29,' شۈبھىسىزكى، «پەرۋەردىگارىمىز اﷲ دۇر» دېگەنلەر، ئاندىن توغرا يولدا بولغانلارغا پەرىشتىلەر چۈشۈپ: «قورقماڭلار، غەم قىلماڭلار، سىلەرگە ۋەدە قىلىنغان جەننەت ئۈچۈن خۇشال بولۇڭلار، بىز دۇنيادا، ئاخىرەتتە سىلەرنىڭ دوستلىرىڭلارمىز، جەننەتتە سىلەر ئۈچۈن كۆڭلۈڭلار تارتقان نەرسىلەرنىڭ ھەممىسى ۋە تىلىگەن نەرسەڭلارنىڭ ھەممىسى بار، (ئۇلار) ناھايىتى مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان (اﷲ) تەرىپىدىن بېرىلگەن زىياپەتتۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,30,' اﷲ قا (يەنى اﷲ نىڭ تەۋھىدىگە ۋە تائىتىگە) دەۋەت قىلغان، ياخشى ئەمەللەرنى قىلغان ۋە «مەن ھەقىقەتەن مۇسۇلمانلاردىنمەن» دېگەن كىشىدىنمۇ ياخشى سۆزلۈك ئادەم بارمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,31,'ياخشى ئىش بىلەن يامان ئىش باراۋەر بولمايدۇ، ياخشى خىسلەت ئارقىلىق (يامان خىسلەتكە) تاقابىل تۇرغىن، (شۇنداق قىلساڭ) سەن بىلەن ئۆزىنىڭ ئارىسىدا ئاداۋەت بار ئادەم گويا سىرداش دوستۇڭدەك بولۇپ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,32,' بۇ خىسلەتكە پەقەت سەۋرچان ئادەملەرلا ئېرىشەلەيدۇ، بۇ خىسلەتكە پەقەت بۈيۈك نېسىۋە ئىگىسىلا ئېرىشەلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,33,' ئەگەر شەيتان سېنى ۋەسۋەسە قىلىپ (يۇقىرىقى خىسلەتنى ئۆزلەشتۈرمەسلىككە كۈشكۈرتسە) اﷲ قا سېغىنىپ (شەيتاننىڭ شەررىدىن) پاناھ تىلىگىن، اﷲ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,34,' كېچە بىلەن كۈندۈز، كۈن بىلەن ئاي اﷲ نىڭ (بىرلىكىنى ۋە قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، قۇياشقا سەجدە قىلماڭلار، ئايغىمۇ سەجدە قىلماڭلار، ئۇلارنى ياراتقان اﷲ قا سەجدە قىلىڭلار، ئەگەر پەقەت اﷲ غىلا ئىبادەت قىلىدىغان بولساڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,35,' ئەگەر كۇففارلار چوڭچىلىق قىلىپ (اﷲ قا سەجدە قىلىشتىن) باش تارتسا، پەرۋەردىگارىڭنىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) كېچە ـ كۈندۈز اﷲ قا تەسبىھ ئېيتىپ تۇرىدۇ، ئۇلار مالال بولۇپ قالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,36,' (بۇمۇ) اﷲ نىڭ (بىرلىكىنى ۋە قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇركى، سەن زېمىننى قاقاس كۆرىسەن، ئۇنىڭغا يامغۇر ياغدۇرغان ۋاقتىمىزدا، زېمىن كۆتۈرۈلىدۇ ۋە كۆپۈشىدۇ، شۈبھىسىزكى، زېمىننى تىرىلدۈرگەن زات ئۆلۈكلەرنى ئەلۋەتتە تىرىلدۈرگۈچىدۇر، ئۇ ھەقىقەتەن ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,37,' شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى بۇرمىلاپ چۈشەندۈرىدىغانلار بىزگە مەخپىي قالمايدۇ، قىيامەت كۈنى دوزاخقا تاشلىنىدىغان ئادەم ياخشىمۇ؟ ياكى قىيامەت كۈنى (اﷲ نىڭ ئازابىدىن) ئەمىن بولغان ھالدا كېلىدىغان ئادەم ياخشىمۇ؟ خالىغىنىڭلارنى قىلىڭلار، شۈبھىسىزكى، اﷲ قىلمىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,38,' شۈبھىسىزكى، قۇرئان كەلگەندە ئۇنى ئىنكار قىلغانلار (قاتتىق جازاغا ئۇچرايدۇ)، شۈبھىسىزكى، قۇرئان غالىب كىتابتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,39,' ئۇنىڭغا ئالدىدىنمۇ، ئارقىسىدىنمۇ (يەنى ھېچقايسى تەرىپىدىن) باتىل يۈزلەنمەيدۇ، ئۇ ھېكمەت بىلەن ئىش قىلغۇچى، مەدھىيىگە لايىق اﷲ تەرىپىدىن نازىل قىلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,40,' ساڭا (مۇشرىكلار تەرىپىدىن) ئېيتىلىدىغان سۆزلەر پەقەت ئىلگىرىكى پەيغەمبەرلەرگە (قەۋملىرى تەرىپىدىن) ئېيتىلغان سۆزلەردۇر، شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ مەغپىرەت ئىگىسىدۇر ۋە قاتتىق ئازاب ئىگىسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,41,' مۇبادا بىز ئۇنى ئەجەم تىلىدىكى قۇرئان قىلىپ نازىل قىلغان بولساق، ئۇلار ئەلۋەتتە: «نېمىشقا ئۇنىڭ ئايەتلىرى (بىز چۈشىنىدىغان تىلدا) روشەن بايان قىلىنمىدى؟ پەيغەمبەر ئەرەب، كىتاب ئەجەمچىمۇ؟» دەيتتى ئۇ ئىمان كەلتۈرگەنلەرگە ھىدايەتتۇر ۋە (دىللاردىكى شەك ـ شۈبھىگە) شىپادۇر، ئىمان ئېيتمايدىغانلارنىڭ قۇلاقلىرى (قۇرئاننى ئاڭلاشتىن) ئېغىردۇر (يەنى قۇرئاننىڭ دەۋىتىگە قۇلاق سالمىغانلارنىڭ ۋە ئۇنىڭدىن يۈز ئۆرۈگەنلەرنىڭ گويا قۇلاقلىرى ئېغىردۇر)، قۇرئان ئۇلارنىڭ (دىللىرىغا) كورلۇقتۇر (يەنى ئۇلار ھىدايەت نۇرىنى كۆرمەيدۇ)، ئەنە شۇلار يىراقتىن نىدا قىلىنغۇچىلاردۇر (يەنى ئۇلار ھەقنى قوبۇل قىلماسلىقتا ئىمانغا يىراقتىن چاقىرىلغان، مۇساپىنىڭ يىراقلىقىدىن چاقىرىقنى ئاڭلىمىغان كىشىلەرگە ئوخشايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,42,' بىز ھەقىقەتەن مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق، ئۇ ھەقتە ئىختىلاپ بولدى ئەگەر پەرۋەردىگارىڭنىڭ (ئۇلارنى جازالاشنى قىيامەتكىچە كېچىكتۈرۈش) ۋەدىسى بولمىسا ئىدى، ئەلۋەتتە ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىلاتتى، ئۇلار ھەقىقەتەن قۇرئاندىن زىيادە شەكتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,43,' كىمكى ياخشى ئىش قىلىدىكەن، پايدىسى ئۆزىگىدۇر، كىمكى يامان ئىش قىلىدىكەن زىيىنىمۇ ئۆزىگىدۇر پەرۋەردىگارىڭ بەندىلەرگە زۇلۇم قىلغۇچى ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,44,' قىيامەتنىڭ ۋاقتىنى بىلىش پەقەت اﷲ قا خاستۇر (يەنى قىيامەتنىڭ قاچان بولىدىغانلىقىنى اﷲ تىن باشقا كىشى بىلمەيدۇ) مېۋىلەرنىڭ بوغۇنلىرىدىن چىقىشى، ھەر قانداق چىشىنىڭ ھامىلدار بولۇشى ۋە تۇغۇشى اﷲ نىڭ ئىلمىنىڭ سىرتىدا ئەمەستۇر، ئۇ كۈندە اﷲ ئۇلارغا: «مېنىڭ شېرىكلىرىم (يەنى سىلەر ماڭا شېرىك كەلتۈرگەن نەرسىلەر) قەيەردە؟» دەپ نىدا قىلىدۇ، ئۇلار: «ساڭا مەلۇم قىلىمىزكى، ئارىمىزدا ھېچ كىشى (سېنىڭ شېرىكىڭ بار دەپ) گۇۋاھلىق بېرەلمەيدۇ» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,45,' ئۇلار ئىلگىرى (يەنى دۇنيادىكى چاغدا) چوقۇنغان بۇتلىرى ئۇلاردىن ئۆزلىرىنى چەتكە ئالىدۇ، ئۇلار (اﷲ نىڭ ئازابىدىن) قاچىدىغان جاي يوق ئىكەنلىكىنى ئېنىق بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,46,' ئىنسان (ئۆزىگە) ياخشىلىق تىلەشتىن زېرىكمەيدۇ، ئەگەر ئۇنىڭغا بىرەر يامانلىق يېتىپ قالسا (اﷲ نىڭ رەھمىتىدىن) تولىمۇ ئۈمىدسىزلىنىپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,47,' ئەگەر بىز ئۇنىڭغا كۈلپەتتىن كېيىن رەھمىتىمىزنى تېتىتساق، ئۇ چوقۇم: «بۇ ئۆزۈمنىڭ سەئيى ـ ئىجتىھاتىمدىن كەلگەن، قىيامەتنىڭ بولىدىغانلىقىغا ئىشەنمەيمەن، مۇبادا (قىيامەت بولۇپ) مەن پەرۋەردىگارىمغا قايتۇرۇلغاندىمۇ ئۇنىڭ دەرگاھىدا چوقۇم جەننەتكە ئېرىشىمەن» دەيدۇ ئەلۋەتتە كاپىرلارغا قىلمىشلىرىنى خەۋەر قىلىمىز ۋە ئۇلارغا قاتتىق ئازابنى تېتىتىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,48,' ئىنسانغا نېمەت ئاتا قىلساق (پەرۋەردىگارىغا شۈكۈر قىلىشتىن) يۈز ئۆرۈيدۇ، (اﷲ نىڭ ئەمرىگە بويسۇنۇشتىن باش تارتىپ) ھاكاۋۇرلىشىپ كېتىدۇ، ئەگەر ئۇنىڭغا يامانلىق يەتسە (اﷲ قا يۈزلىنىپ) ئۈزلۈكسىز دۇئا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,49,' (ئى مۇھەممەد! ئېيتقىنكى) «ئېيتىپ بېقىڭلارچۇ (قۇرئان مەن ئېيتقاندەك) اﷲ تەرىپىدىن نازىل بولغان بولسا، سىلەر (ئويلىماستىنلا ئۇنى ئىنكار قىلساڭلار، ھالىڭلار قانداق بولىدۇ) (ھەقتىن) يىراق ئىختىلاپتا بولغان كىشىدىنمۇ گۇمراھ ئادەم بارمۇ؟ (يەنى سىلەردىنمۇ گۇمراھ ئادەم يوق)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,50,' ئۇلارغا بىز تاكى ئۇلارغا قۇرئاننىڭ ھەق ئىكەنلىكى ئېنىق بولغۇچە، (ئاسمان ـ زېمىن) ئەتراپىدىكى ۋە ئۆزلىرىدىكى (قۇدرىتىمىزگە دالالەت قىلىدىغان) ئالامەتلەرنى كۆرسىتىمىز، پەرۋەردىگارىڭنىڭ ھەممە نەرسىنى كۆرۈپ تۇرىدىغانلىقى (سېنىڭ راستلىقىڭنىڭ دەلىلى بولۇشقا) ئۇلارغا كۇپايە ئەمەسمۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,43,51,' بىلىڭلاركى، ئۇلار ھەقىقەتەن پەرۋەردىگارىغا مۇلاقات بولۇشتىن شەكلىنىدۇ، بىلىڭلاركى، اﷲ ھەممە نەرسىنى ھەقىقەتەن تولۇق بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,1,'، عين، سىين، قاف');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,2,' غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ ساڭا ۋە سەندىن ئىلگىرىكى (پەيغەمبەر) لەرگە مۇشۇنداق ۋەھىي قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,3,' ئاسمانلاردىكى شەيئىلەر ۋە زېمىندىكى شەيئىلەر اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ تەسەررۇپى ئاستىدىدۇر)، اﷲ ئۈستۈندۇر، كاتتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,4,' (اﷲ نىڭ كاتتىلىقىدىن) ئاسمانلار يۇقىرى تەرىپىدىن يېرىلىپ كېتىشكە تاس قالىدۇ، پەرىشتىلەر پەرۋەردىگارىغا تەسبىھ ئېيتىدۇ، ھەمدى ئېيتىدۇ، يەر يۈزىدىكىلەرگە مەغپىرەت تىلەيدۇ بىلىڭلاركى، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,5,' اﷲ نى قويۇپ (بۇتلارنى) ئىگە قىلىۋالغانلارنىڭ (ئەمەللىرىنى) اﷲ كۈزىتىپ تۇرغۇچىدۇر، سەن ئۇلارغا ھامىي ئەمەسسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,6,' مەككە ئاھالىسىنى ۋە ئۇنىڭ ئەتراپىدىكىلەرنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، (كىشىلەرنى) قىيامەت كۈنىدىن ئاگاھلاندۇرۇشۇڭ ئۈچۈن، ساڭا بىز مۇشۇنداق ئەرەبچە قۇرئاننى نازىل قىلدۇق، ئۇ كۈندە ھېچ شەك يوقتۇر، (ئۇ كۈندە) بىر پىرقە (كىشىلەر) جەننەتتە ۋە بىر پىرقە كىشىلەر جەھەننەمدە بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,7,' ئەگەر اﷲ خالىسا، ئۇلارنى (يەنى پۈتۈن ئىنسانلارنى) ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، لېكىن اﷲ خالىغان ئادەمنى رەھمىتىگە داخىل قىلىدۇ، زالىملارغا ھېچقانداق ئىگە، ھېچقانداق مەدەتكار بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,8,' بەلكى ئۇلار اﷲ تىن باشقىلارنى ئىگە قىلىۋالدى، پەقەت اﷲ ئىگىدۇر، اﷲ ئۆلۈكلەرنى تىرىلدۈرىدۇ، اﷲ ھەر نەرسىگە قادىر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,9,' سىلەر (دىن ئۈستىدە ۋە ئۇنىڭدىن باشقا) ھەر قانداق نەرسە ئۈستىدە ئىختىلاپ قىلىشماڭلار، ئۇنىڭغا اﷲ ھۆكۈم چىقىرىدۇ (ئى مۇھەممەد! ئۇلارغا ئېيتقىنكى) ئەنە شۇ اﷲ مېنىڭ پەرۋەردىگارىمدۇر، ئۇنىڭغا تەۋەككۈل قىلدىم، (ھەممە ئىشتا) اﷲ قا مۇراجىئەت قىلىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,10,' اﷲ ئاسمانلارنى ۋە زېمىننى ياراتقۇچىدۇر، اﷲ سىلەرگە ئۆز جىنسىڭلاردىن جۈپتىلەرنى (يەنى ئاياللارنى) ياراتتى ھايۋانلارغىمۇ ئۆز تىپىدىن جۈپتىلەرنى ياراتتى بۇ ئارقىلىق اﷲ سىلەرنى كۆپەيتىدۇ، ھېچ شەيئى اﷲ قا ئوخشاش ئەمەستۇر، اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,11,' ئاسمانلارنىڭ ۋە زېمىننىڭ (خەزىنىلىرىنىڭ) ئاچقۇچلىرى اﷲ نىڭ ئىلكىدىدۇر، اﷲ خالىغان كىشىنىڭ رىزقىنى (سىناش يۈزىسىدىن) كەڭ قىلىدۇ، خالىغان كىشىنىڭ رىزقىنى (بالاغا مۇپتىلا قىلىپ سىناش يۈزىسىدىن) تار قىلىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,12,' اﷲ سىلەرگە دىندىن نۇھقا تەۋسىيە قىلغان نەرسىنى، ساڭا (يەنى مۇھەممەد ئەلەيھىسسالامغا) بىز ۋەھىي قىلغان نەرسىنى، ئىبراھىمغا، مۇساغا ۋە ئىساغا بىز تەۋسىيە قىلغان نەرسىنى بايان قىلدى سىلەر دىننى بەرپا قىلىڭلار، دىندا تەپرىقىچىلىك قىلماڭلار، مۇشرىكلارغا سەن ئۇلارنى دەۋەت قىلغان نەرسە (يەنى تەۋھىد) ئېغىر كەلدى، اﷲ تەۋھىدكە خالىغان ئادەمنى تاللايدۇ، اﷲ نىڭ (تائىتىگە) قايتىدىغان ئادەمنى تەۋھىدكە باشلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,13,' (يەھۇدىي، ناسارا قاتارلىقلار) ئۇلارغا ئىلىم كەلگەندىن كېيىن (يەنى ئۇلار رەسۇلۇللاھنىڭ ھەق پەيغەمبەرلىكىنى بىلگەندىن كېيىن)، ئاندىن ئۆزئارا ھەسەت قىلىشىپ پىرقىلەرگە بۆلۈندى ئەگەر پەرۋەردىگارىڭنىڭ (ئۇلارنى جازالاشنى) قىيامەتكىچە تەخىر قىلىشتىن ئىبارەت سۆزى بولمىسا ئىدى، ئۇلارنىڭ ئارىسىدا ئەلۋەتتە ھۆكۈم چىقىرىلىپ بولغان بولاتتى (يەنى ئۇلار تېز ھالاك قىلىنغان بولاتتى) ئۇلاردىن كېيىن كىتابقا ۋارىسلىق قىلغانلار (يەنى رەسۇلۇللاھ بىلەن زامانداش بولغان ئەھلى كىتاب) (كىتاب ھەققىدە) قاتتىق دەرگۇماندىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,14,' شۇنىڭ ئۈچۈن (يەنى دىندا ئىختىلاپ قىلىشقانلىقى ئۈچۈن) (ئى مۇھەممەد! بارلىق ئىنسانلارنى توغرا دىن ئاساسىدا ئىتتىپاق بولۇشقا) چاقىرغىن، بۇيرۇلغىنىڭ بويىچە توغرا يولدا چىڭ تۇرغىن، ئۇلار (يەنى مۇشرىكلار) نىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن ۋە ئېيتقىن: «اﷲ نازىل قىلغان (بارلىق) كىتابلارغا ئىمان ئېيتتىم، ئاراڭلاردا ئادىل بولۇشقا بۇيرۇلدۇم، اﷲ بىزنىڭ پەرۋەردىگارىمىزدۇر، سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈندۇر، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزلىرىڭلار ئۈچۈندۇر، سىلەر بىلەن بىزنىڭ ئارىمىزدا جېدەللىشىشكە (ئورۇن) يوق (چۈنكى ھەقىقەت ئاشكارىدۇر)، اﷲ (قىيامەت كۈنى) بىزنى (ھۆكۈم چىقىرىش ئۈچۈن) يىغىدۇ، ئاخىر قايتىدىغان جاي اﷲ نىڭ دەرگاھىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,15,' كىشىلەر اﷲ نىڭ دىنىنى قوبۇل قىلغاندىن كېيىن، (كىشىلەرنى ئىماندىن توسۇش ئۈچۈن) اﷲ نىڭ دىنىدا خۇسۇمەت قىلىشقانلارنىڭ دەلىللىرى اﷲ نىڭ دەرگاھىدا باتىلدۇر، ئۇلار (دۇنيادا) غەزەپكە ئۇچرايدۇ، (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,16,' اﷲ كىتاب (يەنى قۇرئان) نى ھەق بىلەن نازىل قىلدى ۋە ئادالەتنى نازىل قىلدى سەن نېمە بىلىسەن؟ قىيامەت يېقىن بولۇشى مۇمكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,17,' قىيامەتكە ئىشەنمەيدىغانلار ئۇنىڭ بولۇشىنى ئالدىرىتىدۇ، ئۇنىڭغا ئىشىنىدىغانلار بولسا قىيامەتتىن قورقىدۇ، قىيامەتنى ھەق دەپ بىلىدۇ، بىلىڭلاركى، قىيامەت توغرۇلۇق مۇجادىلە قىلىدىغانلار ئەلۋەتتە چوڭقۇر گۇمراھلىقتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,18,' اﷲ بەندىلىرىگە كۆيۈنگۈچىدۇر، ئۇ خالىغان ئادەمگە (كەڭ) رىزىق بېرىدۇ، اﷲ كۈچلۈكتۇر، غالىبتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,19,' كىمكى (ئۆزىنىڭ ئەمەلى بىلەن) ئاخىرەتنىڭ ساۋابىنى كۆزلىسە، ئۇنىڭ ساۋابىنى زىيادە بېرىمىز، كىمكى (ئەمەلى بىلەن) دۇنيانىڭ مەنپەئىتىنى كۆزلىسە، (ئۇنىڭ تىلىگەن) مەنپەئىتىنىڭ بەزىسىنى بېرىمىز، ئۇنىڭغا ئاخىرەتتە (ساۋابتىن) ھېچ نېسىۋە يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,20,' ئۇلارنىڭ اﷲ رۇخسەت قىلمىغان نەرسىلەرنى دىن قىلىپ بېكەتكەن مەبۇدلىرى بارمۇ؟ (اﷲ نىڭ ئۇلارغا بېرىلىدىغان ئازابنى قىيامەتكىچە تەخىر قىلىش توغرىسىدىكى) ھۆكمى بولمىسا ئىدى، ئۇلارنىڭ ئارىسىدا ئەلۋەتتە (دۇنيادىلا) ھۆكۈم چىقىرىلغان بولاتتى، (ئۆزلىرىگە) زۇلۇم قىلغۇچىلار (يەنى كاپىرلار) قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,21,' كاپىرلارنىڭ (قىيامەت كۈنى دۇنيادا) قىلغان گۇناھلىرىنىڭ جازاسىدىن قورقۇپ تۇرغانلىقىنى كۆرىسەن، (ئۇلار مەيلى قورقسۇن، قورقمىسۇن) جازا ئۇلارغا (چوقۇم) نازىل بولغۇسىدۇر، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار جەننەتلەرنىڭ باغچىلىرىدا بولىدۇ، ئۇلار پەرۋەردىگارىنىڭ ھۇزۇرىدا خالىغان نەرسىلەردىن بەھرىمەن بولىدۇ، بۇ كاتتا ئىنئامدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,22,' اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان بەندىلىرىگە ئەنە شۇ ئىنئام بىلەن خۇش ـ خەۋەر بېرىدۇ «(پەيغەمبەرلىكنى) تەبلىغ قىلغانلىقىمغا سىلەردىن ھەق تەلەپ قىلمايمەن، پەقەت تۇغقانچىلىق ھەققى ئۈچۈن مېنى دوست تۇتۇشۇڭلارنى (يەنى پەرۋەردىگارىمنىڭ دەۋىتىنى يەتكۈزۈشۈمگە چېقىلماسلىقىڭلارنى) تىلەيمەن» دېگىن، كىمكى بىرەر ياخشى ئىش قىلسا، ئۇنىڭغا بىز ساۋابنى زىيادە بېرىمىز، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,23,' ئۇلار (يەنى قۇرەيش كاپىرلىرى): «ئۇ (يەنى مۇھەممەد) اﷲ قا يالغاننى توقۇدى» دەمدۇ؟ ئەگەر اﷲ خالىسا (يەنى ئۇلار گۇمان قىلغاندەك، اﷲ قا يالغاننى توقۇغان بولساڭ) سېنىڭ قەلبىڭنى پېچەتلىۋەتكەن بولاتتى، اﷲ باتىلنى يوق قىلىدۇ، (پەيغەمبىرىگە نازىل قىلغان) سۆزلىرى ئارقىلىق ھەقنى ھەق قىلىدۇ، اﷲ ھەقىقەتەن دىللاردىكى سىرلارنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,24,' اﷲ بەندىلىرىدىن تەۋبىنى قوبۇل قىلىدۇ، يامان ئىشلارنى ئەپۇ قىلىدۇ، قىلىۋاتقان ئىشىڭلارنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,25,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ دۇئاسىنى اﷲ ئىجابەت قىلىدۇ، (كامالى) پەزلىدىن ئۇلارغا ساۋابنى زىيادە بېرىدۇ، كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,26,' ئەگەر اﷲ بەندىلىرىنىڭ رىزقىنى كەڭ قىلسا، ئۇلار ئەلۋەتتە زېمىندا پىتنە ـ پاسات تېرىيتتى لېكىن اﷲ خالىغان نەرسىنى ئۆلچەم بىلەن چۈشۈرىدۇ، اﷲ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىدىن) تولۇق خەۋەرداردۇر، (ئۇنى) كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,27,' ئۇلار (يامغۇرنىڭ يېغىشىدىن ئۈمىدسىزلەنگەندىن كېيىن) اﷲ يامغۇر ياغدۇرۇپ بېرىدۇ، رەھمىتىنى يايىدۇ، اﷲ (ھەممىنىڭ) ئىگىسىدۇر، مەدھىيىگە لايىقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,28,' ئاسمانلارنىڭ، زېمىننىڭ يارىتىلىشى ۋە ئۇ ئىككىسىدە جاندارلارنىڭ يېيىلىشى اﷲ نىڭ (قۇدرىتىنىڭ) دەلىللىرىدىندۇر، اﷲ ئەگەر خالىسا، ئۇلارنى جەمئى قىلىشقا قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,29,' سىلەرگە (يەنى جېنىڭلار ياكى مېلىڭلارغا) ھەرقانداق بىر مۇسىبەت يەتسە، ئۇ سىلەرنىڭ قىلغان گۇناھىڭلار تۈپەيلىدىن كەلگەن بولىدۇ، اﷲ نۇرغۇن گۇناھىڭلارنى ئەپۇ قىلىدۇ (ئەپۇ قىلمايدىغان بولسا، بالا ۋە مۇسىبەتتىن ھېچ قۇتۇلماس ئىدىڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,30,' (ئى مۇشرىكلار!) سىلەر زېمىندا اﷲ نىڭ ئازابىدىن قېچىپ قۇتۇلالمايسىلەر، سىلەرگە اﷲ تىن باشقا ھېچ ئىگە ۋە مەدەتكار يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,31,' دېڭىزدا قاتنىغۇچى تاغدەك كېمىلەر اﷲ نىڭ قۇدرىتىنىڭ دەلىللىرىدىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,32,' ئەگەر اﷲ خالىسا، شامالنى توختىتىۋېتىدۇ ـ دە، كېمىلەر دېڭىزنىڭ ئۈستىدە توختاپ قالىدۇ، بۇنىڭدا (قاتتىقلىقتا) سەۋر قىلغۇچى، (كەڭچىلىكتە) شۈكۈر قىلغۇچى ھەر مۆمىن ئۈچۈن نۇرغۇن ئىبرەتلەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,33,' ياكى اﷲ خالىسا، ئىچىدىكى كىشىلەرنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن كېمىلەرنى (بوران بىلەن) ھالاك قىلىدۇ، اﷲ نۇرغۇن كىشلەرنىڭ گۇناھلىرىنى ئەپۇ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,34,' بىزنىڭ ئايەتلىرىمىز ئۈستىدە مۇجادىلە قىلىدىغانلار ئۆزلىرىگە اﷲ نىڭ ئازابىدىن قېچىپ قۇتۇلىدىغان جاي يوق ئىكەنلىكىنى بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,35,' (دۇنيانىڭ نېمەتلىرىدىن) سىلەرگە بېرىلگەن ھەر قانداق نەرسە دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر، ئىمان ئېيتقان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرگە، گۇناھى كەبرىلەردىن، قەبىھ ئىشلاردىن ساقلانغۇچىلارغا، دەرغەزەپ بولغانلىرىدا كەچۈرەلەيدىغانلارغا، پەرۋەردىگارىنىڭ دەۋىتىگە ئاۋاز قوشالايدىغانلارغا، نامازنى (تەئدىل ئەركان بىلەن) ئۆتەيدىغانلارغا، ئىشلىرىنى مەسلىھەت بىلەن قارار قىلىدىغانلارغا، بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن سەدىقە قىلىدىغانلارغا، ئۇچرىغان زۇلۇمغا قارشى تۇرالايدىغانلارغا اﷲ نىڭ ھۇزۇرىدىكى ساۋاب تېخىمۇ ياخشىدۇر، تېخىمۇ باقىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,36,' بىر يامانلىقنىڭ جازاسى شۇنىڭغا ئوخشاش بىر يامانلىقتۇر (يەنى ساڭا بىر كىشى قانچىلىك چېقىلسا، شۇنچىلىك چېقىلماي ئاشۇرۇۋەتسەڭ بولمايدۇ)، كىمكى (ئىنتىقام ئېلىشقا قادىر تۇرۇقلۇق) ئەپۇ قىلسا ۋە (ئۆزى بىلەن يامانلىق قىلغۇچىنىڭ ئارىسىنى) تۈزىسە، ئۇنىڭ ئەجرىنى اﷲ بېرىدۇ، اﷲ ھەقىقەتەن زۇلۇم قىلغۇچىلارنى دوست تۇتمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,37,' زۇلۇمغا ئۇچرىغۇچى ئادەم ئىنتىقامنى ئالسا ئۇنى ئەيىبلەشكە بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,38,' ئەيىبلىنىدىغانلار پەقەت كىشىلەرگە زۇلۇم قىلىدىغان، زېمىندا ناھەق رەۋىشتە پىتنە ـ پاسات تېرىيدىغان ئادەملەردۇر، ئەنە شۇلار قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,39,' كىمكى (ئەزىيەتكە) سەۋر قىلسا، (اﷲ نىڭ رازىلىقى ئۈچۈن) ئىنتىقام ئالمىسا، بۇ ئەلۋەتتە مەرغۇپ ئىشلاردىندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,40,' اﷲ گۇمراھ قىلغان ئادەمگە شۇنىڭدىن كېيىن ھېچ ئىگە يوقتۇر، زالىملار ئازابنى كۆرگەن چاغدا، ئۇلارنىڭ «(دۇنياغا) قايتىشقا يول بارمىدۇ؟» دېگەنلىكىنى كۆرىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,41,' ئۇلارنىڭ قورققان، خارلانغان ھالدا، (دوزاخقا) كۆزىنىڭ قۇيرۇقى بىلەن قارىغان ھالدا دوزاخقا توغرىلىنىدىغانلىقىنى كۆرىسەن، مۆمىنلەر: «شۈبھىسىزكى، زىيان تارتقۇچىلار قىيامەت كۈنى ئۆزلىرىگە ۋە ئائىلىسىدىكىلەرگە زىيان سالغۇچىلاردۇر» دەيدۇ، بىلىڭلاركى، زالىملار ھەقىقەتەن دائىملىق ئازابتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,42,' ئۇلارغا اﷲ تىن باشقا ياردەم بېرىدىغان دوستلار بولمايدۇ، اﷲ كىمنى گۇمراھ قىلسا، ئۇنىڭغا (ئۇنى دۇنيادا ھەقكە، ئاخىرەتتە جەننەتكە ئېرىشتۈرگۈچى) ھېچ يول بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,43,' اﷲ تەرىپىدىن كەلگەن، قارشى تۇرغىلى بولمايدىغان بىر كۈن كېلىشتىن بۇرۇن پەرۋەردىگارىڭلارنىڭ دەۋىتىنى قوبۇل قىلىڭلار، بۇ كۈندە سىلەرگە قاچىدىغان جاي تېپىلماي قالىدۇ (قىلمىشىڭلار نامە ـ ئەمالىڭلارغا يېزىقلىق بولغانلىقى، ئەزايىڭلار ئۇنىڭغا گۇۋاھ بولغانلىقى ئۈچۈن) سىلەر (ئۇنى) ئىنكار قىلالمايسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,44,' ئەگەر ئۇلار (يەنى مۇشرىكلار) (ئىماندىن) يۈز ئۆرۈسە، بىز سېنى ئۇلارغا كۆزەتچى قىلىپ ئەۋەتكىنىمىز يوق، سېنىڭ ۋەزىپەڭ پەقەت تەبلىغ قىلىشتۇر، ئەگەر ئىنسانغا بىزنىڭ رەھمىتىمىزنى تېتىتساق خۇش بولۇپ كېتىدۇ، ئۇلارنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن (بېشىغا) بىرەر ھادىسە كەلسە، ئىنسان تولىمۇ كۇفرانە نېمەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,45,' ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ نېمىنى خالىسا شۇنى يارىتىدۇ، خالىغان ئادەمگە قىز پەرزەنت ئاتا قىلىدۇ، خالىغان ئادەمگە ئوغۇل پەرزەنت ئاتا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,46,' ياكى ئۇلارغا ئوغۇل، قىزنى ئارىلاش بېرىدۇ، خالىغان ئادەمنى تۇغماس قىلىدۇ، اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,47,' اﷲ ھەر قانداق ئادەمگە پەقەت ۋەھيى ئارقىلىق ياكى پەردە ئارقىسىدىنلا ياكى بىر پەيغەمبەرنى ئەۋەتىپ ئۆز ئىزنى بىلەن ئۇنىڭغا خالىغان ۋەھيىنى قىلىش ئارقىلىقلا سۆز قىلغان اﷲ ھەقىقەتەن (مەخلۇقلارنىڭ سۈپەتلىرىدىن) ئۈستۈندۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,48,' شۇنىڭدەك (يەنى باشقا پەيغەمبەرلەرگە ۋەھيى قىلغاندەك) ئەمرىمىز بويىچە ساڭا قۇرئاننى ۋەھيى قىلدۇق، سەن (ۋەھيىدىن ئىلگىرى) قۇرئاننىڭ ۋە ئىماننىڭ نېمە ئىكەنلىكىنى ئۇقمايتتىڭ، لېكىن بىز قۇرئاننى بىر نۇر قىلدۇقكى، ئۇنىڭ بىلەن بەندىلىرىمىزدىن خالىغان كىشىلەرنى ھىدايەت قىلىمىز شەك ـ شۈبھىسىزكى سەن توغرا يولغا باشلايسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,44,49,' (ئۇ) ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى ئۆزىگە خاس بولغان اﷲ نىڭ يولىدۇر بىلىڭلاركى، ھەممە ئىش اﷲ قا قايتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,1,'حا، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,2,' روشەن كىتاب بىلەن قەسەمكى، سىلەرنى (ئۇنىڭ مەنىسىنى) چۈشەنسۇن دەپ، بىز ئۇنى ھەقىقەتەن ئەرەبچە قۇرئان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,3,' ھەقىقەتەن ئۇ لەۋھۇلمەھپۇزدا بىزنىڭ دەرگاھىمىزدا (مۇقىملاشقان) يۇقىرى مەرتىۋىلىك ھېكمەتلىك (كىتاب) دۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,4,' سىلەر (گۇمراھلىقتا) ھەددىدىن ئاشقان قەۋم بولغانلىقىڭلار ئۈچۈن سىلەرگە ۋەز ـ نەسىھەت قىلىشنى تەرك قىلامدۇق؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,5,'ئىلگىرىكى ئۈممەتلەرگە نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,6,' ئۇلارغا قانداق بىر پەيغەمبەر كەلمىسۇن، ئۇلار ئۇنى (قەۋمىڭ سېنى مەسخىرە قىلغاندەك) مەسخىرە قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,7,' ئۇلار (يەنى مەككە كاپىرلىرى) دىن كۈچ ـ قۇۋۋەتتە ئۈستۈن بولغانلارنى ھالاك قىلدۇق، ئىلگىرىكىلەرنىڭ قىسسىلىرى قۇرئاندا ئۆتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,8,' ئەگەر سەن ئۇلاردىن: «ئاسمانلار ۋە زېمىننى كىم ياراتتى» دەپ سورىساڭ، ئۇلار: «ئەلۋەتتە ئاسمانلارنى ۋە زېمىننى غالىب، ھەممىنى بىلگۈچى (اﷲ) ياراتتى» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,9,' اﷲ سىلەر ئۈچۈن زېمىننى قارارگاھ قىلدى، (سەپەردە كۆزلىگەن يېرىڭلارغا) بېرىۋېلىشىڭلار ئۈچۈن سىلەرگە زېمىندا نۇرغۇن يوللارنى ياراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,10,' اﷲ بۇلۇتتىن (سىلەرگە زىيان يەتكۈزمەي، پايدا يەتكۈزىدىغان) ئۆلچەمدە يامغۇر ياغدۇردى، ئۇنىڭ بىلەن بىز (گىيا ئۈنمەس قاقاس) ئۆلۈك زېمىننى تىرىلدۈردۇق، سىلەر مۇشۇنداق (يەنى ئۆلۈك يەردىن ئۆسۈملۈك چىقىرىلغاندەك) (قەبرەڭلاردىن) چىقىرىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,11,' اﷲ پۈتۈن مەخلۇقاتلارنىڭ تۈرلىرىنى ياراتتى، سىلەرگە ئۈستىگە چىقىشىڭلار ئۈچۈن كېمە ۋە مىنىشىڭلار ئۈچۈن ھايۋان قاتارلىقلارنى ياراتتى ئاندىن سىلەر ئۇلارنىڭ ئۈستىگە چىققانلىرىڭلاردا پەرەۋردىگارىڭلارنىڭ نېمىتىنى ئەسلەپ: «بىزگە بۇنى بويسۇندۇرۇپ بەرگەن زات پاكتۇر، بىز ئۇنىڭغا (مىنىشكە) قادىر ئەمەس ئىدۇق، بىز ھەقىقەتەن پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز» دېگەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,12,' ئۇلار (پەرىشتىلەرنى اﷲ نىڭ قىزلىرى دېيىش بىلەن) اﷲ نىڭ بىر قىسىم بەندىلىرىنى اﷲ نىڭ بىر جۈزئىييى قىلىۋالدى (مۇنداق دېگۈچى) ئىنسان ھەقىقەتەن اﷲ قا ئاشكارا ناشۈكۈرلۈك قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,13,' اﷲ مەخلۇقاتتىن ئۆزى ئۈچۈن قىزلارنى (بالا) تۇتۇپ، سىلەرنى ئوغۇللارغا خاس قىلدىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,14,'ئەگەر ئۇلارنىڭ بىرىگە مەرھەمەتلىك اﷲ قا مەسەل قىلغان نەرسە (يەنى قىز بالا) بىلەن بېشارەت بېرىلسە (يەنى خوتۇنىنىڭ قىز تۇغقانلىقى خەۋەر قىلىنسا)، قايغۇغا چۆمگەن ھالدا ئۇنىڭ يۈزى قارىداپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,15,' (ئۇلار اﷲ قا) زىبۇزىننەت ئىچىدە چوڭ بولىدىغان، مۇنازىرىدە مەقسىتىنى ئوچۇق بايان قىلالمايدىغان (قىزلارنى مەنسۇپ قىلامدۇ؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,16,' ئۇلار مەرھەمەتلىك اﷲ نىڭ بەندىلىرى بولغان پەرىشتىلەرگە قىز دەپ ئېتىقاد قىلدى، اﷲ پەرىشتىلەرنى ياراتقاندا، ئۇلار ئۈستىدە بارمىدى؟ (يەنى ئۇلار پەرىشتىلەرنىڭ قىز ئىكەنلىكىنى قەيەردىن بىلىدۇ؟) ئۇلارنىڭ گۇۋاھلىقى (ئۇلارنىڭ نامە ـ ئەمالىغا) يېزىلىدۇ، (قىيامەت كۈنى) ئۇلار سوراققا تارتىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,17,' ئۇلار: «مەرھەمەتلىك اﷲ خالىسا، بىز ئۇلارغا (يەنى پەرىشتىلەرگە) چوقۇنمايتتۇق» دېدى بۇ توغرۇلۇق ئۇلارنىڭ بىلىمى (يەنى ئاساسى) يوق، ئۇلار پەقەت جۆيلۈيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,18,' ئۇلارغا بىز قۇرئاندىن ئىلگىرى (قانداقتۇر) بىر كىتاب بەرگەن بولۇپ، ئۇلار شۇ كىتابقا مەھكەم ئىسىلامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,19,'ئۇنداق ئەمەس، ئۇلار: «ھەقىقەتەن ئاتا ـ بوۋىلىرىمىزنىڭ بىر خىل دىنغا ئېتىقاد قىلغانلىقىنى بىلىمىز، بىز ئۇلارنىڭ ئىزىدىن مېڭىپ ھىدايەت تاپقۇچىدۇرمىز» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,20,' شۇنىڭدەك سەندىن ئىلگىرى ھەر قاچان بىرەر شەھەرگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) ئەۋەتسەكلا، ئۇنىڭ دۆلەتمەن ئادەملىرى: «بىز ھەقىقەتەن ئاتا ـ بوۋىلىرىمىزنىڭ بىر خىل دىنغا ئېتىقاد قىلغانلىقىنى بىلىمىز، شەك ـ شۈبھىسىزكى، بىز ئۇلارنىڭ ئىزلىرىدىن ماڭىمىز» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,21,' (ھەر پەيغەمبەر ئۆز قەۋمىگە) «ئاتا ـ بوۋاڭلار ئېتىقاد قىلىپ كەلگەن دىنغا قارىغاندا ئەڭ توغرا بىر دىننى كەلتۈرسەم يەنىلا ئاتا ـ بوۋاڭلارغا ئەگىشەمسىلەر؟» دېدى ئۇلار: «سىلەر ئېلىپ كەلگەن دىنغا ئىشەنمەيمىز» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,22,' بىز ئۇلارنى جازالىدۇق، (توغرا يولنى) ئىنكار قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قارىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,23,' ئۆز ۋاقتىدا ئىبراھىم ئاتىسىغا ۋە قەۋمىگە ئېيتتى: «سىلەر چوقۇنۇۋاتقان بۇتلاردىن مەن راستلا ئادا ـ جۇدامەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,24,' پەقەت مېنى ياراتقان اﷲ مېنى (توغرا يولغا) باشلايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,25,' ئىبراھىم كەلىمە تەۋھىدنى ئەۋلادىدىن (شېرىك كەلتۈرگەنلەر) ئىمانغا قايتسۇن دەپ ئەۋلادى ئىچىدە قالدۇرۇپ كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,26,' بەلكى مەن بۇلار (يەنى مەككە ئاھالىسى)نى ۋە بۇلارنىڭ ئاتا ـ بوۋىلىرىنى (ئۇزۇن ئۆمۈر ۋە نېمەتتىن) بەھرىمەن قىلدىم، تاكى ئۇلارغا ھەق (يەنى قۇرئان) ۋە روشەن پەيغەمبەر (مۇھەممەد ئەلەيھىسسالام) كەلگۈچە');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,27,' ئۇلارغا ھەق (يەنى قۇرئان) كەلگەندە، ئۇلار: «بۇ سېھىردۇر، بىز ھەقىقەتەن ئۇنى ئىنكار قىلغۇچىمىز» دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,28,' ئۇلار: «بۇ قۇرئان نېمىشقا ئىككى شەھەر (يەنى مەككە بىلەن تائىف ئادەملىرىدىن) بىر كاتتا ئادەمگە نازىل قىلىنمىدى؟ » دېيىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,29,' پەرەۋردىگارىڭنىڭ رەھمىتىنى ئۇلار تەقسىم قىلىپ بېرەمدۇ؟ بىز ئۇلارنىڭ ھاياتىي دۇنيادىكى رىزقىنى ئۇلارنىڭ ئارىسىدا تەقسىم قىلدۇق، ئۇلارنىڭ بەزىسى بەزىسىنى (ھەق بېرىپ) ئىشقا سالسۇن دەپ، ئۇلارنىڭ بەزىسىنىڭ دەرىجىسىنى بەزىسىدىن ئۈستۈن قىلدۇق، پەرەۋردىگارىڭنىڭ رەھمىتى ئۇلارنىڭ توپلىغان نەرسىلىرى (يەنى پۇل ـ ماللىرى) دىن ياخشىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,30,' ئىنسانلار (كۇففارلارنىڭ باياشات تۇرمۇشىنى كۆرۈپ كۇفرىغا قىزىقىپ ئۇلارغا قوشۇلۇپ) ھەممىسى (كۇفرىدا) بىر ئۈممەت بولۇپ قالمايدىغان بولسا ئىدى، مەرھەمەتلىك اﷲ نى ئىنكار قىلغان كىشىلەرنىڭ ئۆيلىرىنىڭ ئۆگزىلىرىنى ۋە ئۇنىڭغا چىقىدىغان شوتىلىرىنى كۈمۈشتىن قىلىپ بېرەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,31,' ئۇلارنىڭ ئۆيلىرىنىڭ ئىشىكلىرىنى ۋە تەختلىرىنىمۇ كۈمۈشتىن قىلىپ بېرەتتۇق، ئۇلار تەختلەرگە يۆلىنىپ ئولتۇراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,32,' (يۇقىرىقىلارنىڭ بەزىسىنى) ئالتۇندىن قىلىپ بېرەتتۇق، ئۇلارنىڭ ھەممىسى پەقەت دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر پەرەۋردىگارىڭنىڭ ھۇزۇرىدىكى ئاخىرەت (يەنى جەننەت ۋە ئۇنىڭدىكى نازۇنېمەتلەر) تەقۋادارلارغا خاستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,33,' كىمكى مېھرىبان اﷲ نى ياد ئېتىشتىن (يەنى قۇرئاندىن) يۈز ئۆرۈيدىكەن، بىز ئۇنىڭغا شەيتاننى مۇسەللەت قىلىمىز (شەيتان ئۇنى ۋەسۋەسە قىلىدۇ)، شەيتان ئۇنىڭغا ھەمىشە ھەمراھ بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,34,' شەك ـ شۈبھىسىزكى، شەيتانلار ئۇلارنى (يەنى ئازغۇن كۇففارلارنى) توغرا يولدىن توسىدۇ، ئۇلار بولسا ئۆزلىرىنى توغرا يولدا دەپ گۇمان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,35,' كاپىر ئادەم (ھەمراھى بىلەن قىيامەت كۈنى) دەرگاھىمىزغا كەلگەن چاغدا (ھەمراھىغا): «كاشكى سەن بىلەن مېنىڭ ئارامدا شەرق بىلەن غەربنىڭ ئارىسىدەك ئارىلىق بولسا ئىكەن (سەن) نېمىدېگەن يامان ھەمراھ! (چۈنكى سەن مېنىڭ بەختسىزلىكىمگە سەۋەبچى بولدۇڭ)» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,36,' (ئۇلارغا) «(دۇنيادىكى چاغدا) زۇلۇم قىلغانلىقىڭلار (يەنى شېرىك كەلتۈرگەنلىكىڭلار) ئۈچۈن بۈگۈن سىلەرنىڭ ئازابتا ئورتاق بولغانلىقىڭلار سىلەرگە پايدا بەرمەيدۇ» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,37,' (ئى مۇھەممەد!) سەن گاسلارغا ئاڭلىتالامسەن؟ يا كورلارنى توغرا يولغا سالالامسەن؟ (يەنى كۇففارلار گاسلارغا، كورلارغا ئوخشايدۇ) ئاشكارا گۇمراھلىقتا بولغان كىشىنى ھىدايەت قىلالامسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,38,'(ئۇلارنى جازالاشتىن ئىلگىرى) سېنى ئېلىپ كەتسەك (يەنى ۋاپات قىلدۇرساق ۋاپاتىڭدىن كېيىن) ئۇلارنى چوقۇم جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,39,' ياكى بىز ساڭا (ھاياتىڭدا) ئۇلارغا ۋەدە قىلغان ئازابنى كۆرسىتىمىز، بىز، شۈبھىسىزكى، ئۇلارغا (يەنى ئۇلارغا ئازاب قىلىشقا) قادىرمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,40,' (ئى مۇھەممەد!) ساڭا ۋەھيى قىلىنغان قۇرئانغا چىڭ ئېسىلغىن، سەن ھەقىقەتەن توغرا يولدىدۇرسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,41,' قۇرئان ساڭا ۋە سېنىڭ قەۋمىڭگە ئەلۋەتتە (ئۇلۇغ) شەرەپتۇر، سىلەر كەلگۈسىدە (بۇ نېمەت ئۈستىدە) سورىلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,42,' سەندىن بۇرۇن بىز ئەۋەتكەن پەيغەمبەرلىرىمىزدىن سوراپ باققىنكى، مېھرىبان اﷲ تىن باشقا ئىبادەت قىلىنىدىغان ئىلاھلارنى بەلگىلىدۇقمۇ؟ (پەيغەمبەرلەرنىڭ ئىچىدە اﷲ تىن غەيرىيگە ئىبادەت قىلىشقا دەۋەت قىلغىنى بارمۇ؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,43,'بىز ھەقىقەتەن مۇسانى پىرئەۋنگە ۋە ئۇنىڭ تەۋەلىرىگە (ئۇنىڭ راست پەيغەمبەر ئىكەنلىكىنى ئىسپاتلايدىغان شانلىق) مۆجىزىلىرىمىز بىلەن ئەۋەتتۇق، مۇسا (پىرئەۋنگە): «مەن ھەقىقەتەن جىمى ئالەملەرنىڭ پەرۋەردىگارىنىڭ (سېنى ۋە قەۋمىڭنى بىر اﷲ قا ئىبادەت قىلىشقا دەۋەت قىلىش ئۈچۈن ئەۋەتكەن) ئەلچىسىمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,44,' مۇسا ئۇلارغا بىزنىڭ مۆجىزىلىرىمىزنى ئېلىپ كەلگەندە، ئۇلار ناگاھان مۆجىزىلەرنى (مەسخىرە قىلىپ) كۈلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,45,' ئۇلارغا بىر ـ بىرىدىن كاتتا مۆجىزىلەرنى كۆرسەتتۇق، ئۇلارنى (كۇفرىدىن) قايتسۇن دەپ (تۈرلۈك) ئازابلار بىلەن جازالىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,46,' (ئۇلار ئازابنى كۆرگەندە) «ئى سېھىرگەر! پەرەۋردىگارىڭغا ئۇنىڭ ساڭا بەرگەن (دۇئايىڭنى ئىجابەت قىلىشتىن ئىبارەت) ئەھدى بويىچە بىز ئۈچۈن دۇئا قىلساڭ (ئەگەر ئۇ دۇئايىڭ بىلەن بىزدىن ئازابنى كۆتۈرۈۋەتسە)، بىز چوقۇم ئىمان ئېيتىمىز» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,47,' (مۇسانىڭ دۇئاسى بىلەن) ئۇلاردىن ئازابنى كۆتۈرۈۋەتكىنىمىزدە، ناگاھان ئۇلار (ئەھدىنى) بۇزۇپ (كۇفرىدا) چىڭ تۇردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,48,' پىرئەۋن ئۆز قەۋمى ئىچىدە (پەخىرلەنگەن ھالدا) نىدا قىلىپ ئېيتتى: «ئى قەۋمىم! مىسىرنىڭ پادىشاھلىقى، ئاستىمدىن ئېقىپ تۇرغان بۇ دەريالار مېنىڭ ئەمەسمۇ؟ (مېنىڭ بۇ بۈيۈكلۈكۈمنى) كۆرمەمسىلەر؟ بەلكى مەن ئوچۇق گەپ قىلالمايدىغان بۇ ئەرزىمەس ئادەمدىن (يەنى مۇسا ئەلەيھىسسالامدىن) ياخشىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,49,' نېمە ئۈچۈن ئۇنىڭغا (ئاسماندىن) ئالتۇن بىلەيزۈكلەر تاشلانمىدى؟ ياكى نېمىشقا (ئۇنىڭ راستلىقىغا گۇۋاھلىق بېرىش ئۈچۈن) ئۇنىڭ بىلەن بىللە پەرىشتىلەر ھەمراھ بولۇپ كەلمىدى؟»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,50,'پىرئەۋن قەۋمىنى گوللىغانلىقتىن، قەۋمى ئۇنىڭغا ئىتائەت قىلدى، ئۇلار ھەقىقەتەن پاسىق قەۋم ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,51,' ئۇلار بىزنى دەرغەزەپ قىلغاندا، ئۇلارنى بىز (ئەڭ قاتتىق جازالار بىلەن) جازالىدۇق، ئۇلارنىڭ ھەممىسىنى (دېڭىزدا) غەرق قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,52,' ئۇلارنى بىز (كۇففارلارنىڭ ئازابقا تېگىشلىك بولۇشىدا) كېيىنكىلەرگە نەمۇنە ۋە ئىبرەت قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,53,' (قۇرئاندا) مەريەمنىڭ ئوغلى (يەنى ئىسا ئەلەيھىسسالام) مىسال كەلتۈرۈلسە، ناگاھان سېنىڭ قەۋمىڭ (خۇشاللىقتىن) سۈرەن سېلىشىپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,54,' ئۇلار: «بىزنىڭ ئىلاھلىرىمىز ياخشىمۇ ياكى ئىسامۇ؟» دېدى ئۇلار بۇ مىسالنى پەقەت خۇسۇمەت قىلىش ئۈچۈنلا كەلتۈردى، بەلكى ئۇلار جېدەلخور قەۋمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,55,' ئۇ (يەنى ئىسا) (ناسارالار گۇمان قىلغاندەك ئىلاھ ياكى ئىلاھنىڭ ئوغلى ئەمەس) پەقەت پەيغەمبەرلىك نېمىتىمىزگە ئېرىشكەن بىر بەندىدۇر، ئۇنى بىز ئىسرائىل ئەۋلادىغا (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) بىر دەلىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,56,' ئەگەر بىز خالىساق، ئەلۋەتتە سىلەرگە زېمىندا ئورۇنباسار بولىدىغان پەرىشتىلەرنى ۋۇجۇتقا كەلتۈرەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,57,' ئۇ (يەنى ئىسا) ئەلۋەتتە قىيامەت (يېقىنلاشقانلىقنىڭ) ئالامىتىدۇر، ئۇنىڭغا (يەنى قىيامەتكە) شەك كەلتۈرمەڭلار (ئى مۇھەممەد! ئۇلارغا ئېيتقىنكى) «ماڭا ئەگىشىڭلار، بۇ (مېنىڭ دەۋەت قىلغىنىم) توغرا يولدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,58,' سىلەرنى شەيتان (توغرا يولدا) مېڭىشتىن توسمىسۇن (يەنى شەيتاننىڭ ۋەسۋەسىسىگە ئالدانماڭلار)، شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,59,' ئىسا روشەن مۆجىزىلەر بىلەن كېلىپ ئېيتتى: «سىلەرگە مەن ھېكمەت ئېلىپ كەلدىم سىلەرگە ئىختىلاپ قىلىۋاتقان نەرسەڭلارنىڭ (يەنى دىنىي ئىشلارنىڭ) بەزىسىنى بايان قىلىپ بېرىمەن، اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,60,' اﷲ ھەقىقەتەن مېنىڭ پەرۋەردىگارىمدۇر ۋە سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,61,' (ناسارالار) نىڭ پىرقىلىرى (ئىسا توغرىسىدا) ئۆز ـ ئارا ئىختىلاپ قىلىشتى، زالىملارغا قاتتىق كۈن (يەنى قىيامەت كۈنىنىڭ) ئازابىدىن ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,62,'ئۇلار پەقەت قىيامەتنىڭ ئۆزلىرى تۇيمىغان ھالدا ئۇشتۇمتۇت كېلىشىنىلا كۈتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,63,' بۇ كۈندە دوستلار بىر ـ بىرىگە دۈشمەن بولىدۇ، پەقەت (خۇدالىق ئۈچۈن دوستلاشقان) تەقۋادارلارلا (ئۇنداق ئەمەستۇر)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,64,' (ئۇلارغا) «بەندىلىرىم! بۈگۈن سىلەرگە قورقۇنچ يوق، غەم ـ قايغۇمۇ يوق» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,65,' ئۇلار بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتقان ۋە مۇسۇلمان بولغانلار ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,66,' (ئۇلارغا) «سىلەر ئاياللىرىڭلار بىلەن بىللە خۇشال ـ خۇرام ھالدا جەننەتكە كىرىڭلار» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,67,' ئۇلارغا ئالتۇن لېگەنلەردە (تائام)، ئالتۇن جاملاردا (شاراب) تۇتۇلىدۇ جەننەتتە كۆڭۈللەر تارتىدىغان، كۆزلەر لەززەتلىنىدىغان نەرسىلەر بار، سىلەر جەننەتتە مەڭگۈ قالىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,68,' سىلەر (دۇنيادا) قىلغان (ياخشى) ئەمەلىڭلار بىلەن ۋارىس بولغان جەننەت ئەنە شۇدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,69,' جەننەتتە نۇرغۇن (تۈرلۈك) مېۋىلەر بار، ئۇنىڭدىن يەيسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,70,' گۇناھكارلار (يەنى كۇففارلار) ھەقىقەتەن دوزاخ ئازابىدا مەڭگۈ قالغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,71,' ئۇلاردىن ئازاب (بىردەممۇ) يېنىكلىتىلمەيدۇ، ئۇلار دوزاختا (ھەر قانداق ياخشىلىقتىن) ئۈمىدسىزلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,72,' ئۇلارغا بىز زۇلۇم قىلمىدۇق ۋە لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,73,' ئۇلار (يەنى كۇففارلار) (دوزاخقا مۇئەككەل پەرىشتىگە): «ئى مالىك! پەرۋەردىگارىڭ بىزگە ئۆلۈم ھۆكۈم قىلسۇن» دەپ توۋلايدۇ مالىك: «سىلەر ئازابتا چوقۇم قالىسىلەر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,74,' بىز سىلەرگە ھەقىقەتەن ھەق (دىن) نى كەلتۈردۇق ۋە لېكىن سىلەرنىڭ كۆپچىلىكىڭلار ھەق (دىن) نى يامان كۆرگۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,75,' ئۇلار (مۇھەممەد ئەلەيھىسسالامغا سۇيىقەست قىلىشتا) بىر ئىشنى قارار قىلغان بولسا، بىز (مۇھەممەد ئەلەيھىسسالامغا) ياردەم بېرىشتە بىر ئىشنى قارار قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,76,' ئۇلار گۇمان قىلامدۇكى، بىز ئۇلارنىڭ سىرلىرىنى ۋە پىچىرلاشقانلىقلىرىنى ئاڭلىمايمىز، ھەرگىز ئۇنداق ئەمەس (ئۇنى ئاڭلاپ تۇرىمىز)، بىزنىڭ ئەلچىلىرىمىز (يەنى پەرىشتىلىرىمىز) ئۇلارنىڭ ئالدىدا (ئۇلارنىڭ ئەمەللىرىنى) يېزىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,77,' «ئەگەر مېھرىبان اﷲ نىڭ بالىسى بولىدىغان بولسا، مەن (ئۇ بالىغا) ئىبادەت قىلغۇچىلارنىڭ ئەۋۋىلى بولاتتىم» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,78,' ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارى، ئەرشنىڭ پەرۋەردىگارى ئۇلارنىڭ سۆزلىرىدىن پاكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,79,' ئۇلارنى (يەنى مەككە كۇففارلىرىنى) (گۇمراھلىقلىرىدا ۋە نادانلىقلىرىدا) قويۇپ بەرگىن، ئۇلار ۋەدە قىلىنغان كۈنىگە مۇلاقات بولغانغا قەدەر (باتىللىرىغا) چۆمسۇن ۋە (دۇنياسى بىلەن) ئوينىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,80,' اﷲ ئاسماندىمۇ ئىلاھتۇر، زېمىندىمۇ ئىلاھتۇر، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,81,' ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقى ئىلكىدە بولغان زات كاتتىدۇر، قىيامەتنىڭ (بولىدىغان ۋاقتى) توغرىسىدىكى بىلىم ھەقىقەتەن (يالغۇر) اﷲ نىڭ دەرگاھىدىدۇر سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,82,' ئۇلارنىڭ اﷲ نى قويۇپ چوقۇنغان نەرسىلىرى شاپائەت قىلالمايدۇ پەقەت ھەق بىلەن گۇۋاھلىق بەرگەن (يەنى لا الە الا اﷲ دېگەن، تىلى بىلەن ئېيتقاننى دىلى بىلەن) ھەقىقىي بىلگەنلەر بۇنىڭدىن مۇستەسنا');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,83,' ئەگەر سەن ئۇلاردىن ئۇلارنى كىم خەلق ئەتكەنلىكلىرىنى سورىساڭ، ئۇلار چوقۇم «اﷲ» دەيدۇ، ئۇلار قانداقمۇ (اﷲ قا ئىبادەت قىلىشتىن بۇتلارغا ئىبادەت قىلىشقا) بۇرۇلۇپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,84,' (اﷲ تائالا مۇھەممەد ئەلەيھىسسالامنىڭ) «ئى پەرۋەردىگارىم! بۇلار ھەقىقەتەن ئىمان ئېيتمايدىغان قەۋمدۇر» دېگەن سۆزىنى (بىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,45,85,' سەن ئۇلاردىن يۈز ئۆرۈگىن، سالام دېگىن (يەنى ئۇلارغا بەتدۇئامۇ قىلمىغىن، دىنغا دەۋەتمۇ قىلمىغىن)، ئۇلار كەلگۈسىدە (كۇفرىنىڭ ئاقىۋىتىنى) بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,1,'حا، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,2,' روشەن قۇرئان بىلەن قەسەم قىلىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,3,' بىز قۇرئاننى ھەقىقەتەن مۇبارەك كېچىدە (يەنى شەبى قەدرى كېچىسىدە) نازىل قىلدۇق، بىز ھەقىقەتەن ئىنسانلارنى (قۇرئان بىلەن) ئاگاھلاندۇرغۇچى بولدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,4,' ئۇ كېچىدە ھەر بىر ھېكمەتلىك (يەنى ھېكمەتنىڭ تەقەززاسى بويىچە قىلىنغان) ئىش (يەنى بەندىلەرنىڭ رىزقى، ئەجىلى ۋە باشقا ئەھۋالى) ئايرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,5,' (شۇ كېچە تەقدىر قىلىنغان ئىشلارنىڭ ھەممىسى) بىزنىڭ دەرگاھىمىزدىن بولغان ئىشتۇر، بىز ھەقىقەتەن (ئىنسانلارغا پەيغەمبەرلەرنى) ئەۋەتكۈچى بولدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,6,' (شۇ) پەرۋەردىگارىڭنىڭ رەھمىتىدىندۇر ئۇ ھەقىقەتەن (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,7,' ئەگەر سىلەر ھەقىقىي ئىشەنگۈچى بولساڭلار، (ئۇ) ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پەرۋەردىگارىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,8,' ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر ئۇ (ئۆلۈكنى) تىرىلدۈرەلەيدۇ، (تىرىكنىڭ) جېنىنى ئالالايدۇ، ئۇ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئىلگىرىكى ئاتا ـ بوۋاڭلارنىڭ پەرۋەردىگارىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,9,' ئۇنداق ئەمەس (يەنى ئۇلار ھەقىقىي ئىشەنگۈچى ئەمەس)، بەلكى ئۇلار (قىيامەتتىن) شەكتە بولۇپ (ھەقنى ئىستىھزا قىلىپ) ئوينايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,10,' (ئى مۇھەممەد!) سەن ئاسمان (ھەممە ئادەم) ئوچۇق (كۆرىدىغان) تۈتۈننى كەلتۈرىدىغان كۈندە (ئۇلارغا بولىدىغان ئازابنى) كۈتكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,11,' (ئۇ تۈتۈن) كىشىلەرنى ئورىۋالىدۇ، بۇ قاتتىق ئازابتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,12,' (ئۇلار) «پەرۋەردىگارىمىز! بىزدىن ئازابنى كۆتۈرۈۋەتكىن، بىز ھەقىقەتەن ئىمان كەلتۈرگۈچىدۇرمىز» (دەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,13,' (ئازاب كۆتۈرۈلۈپ كەتكەندە) ئۇلار قانداقمۇ ئىبرەت ئالسۇن، ھالبۇكى، ئۇلارغا روشەن پەيغەمبەر كەلدى (شۇنداق تۇرۇقلۇق ئۇلار ئىمان ئېيتمىدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,14,' ئاندىن ئۇلار ئۇنىڭدىن يۈز ئۆرۈدى ۋە: «ئۇ ئۆگىتىلگەن (يەنى قۇرئاننى كىشىلەردىن ئۆگەنگەن) دۇر، مەجنۇندۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,15,' بىز ھەقىقەتەن ئازابنى (سىلەردىن) ئازغىنا ۋاقىت كۆتۈرۈۋېتىمىز، (ئاندىن) سىلەر ھەقىقەتەن (ئىلگىرىكى كاپىرلىق ھالىتىڭلارغا) قايتىۋالىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,16,' ئۇلارنى قاتتىق تۇتقان كۈنىمىزدە چوقۇم جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,17,' ئۇلاردىن ئىلگىرى بىز ھەقىقەتەن پىرئەۋننىڭ قەۋمىنى سىنىدۇق، ئۇلارغا ئېسىل بىر پەيغەمبەر كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,18,' (مۇسا ئۇلارغا ئېيتتى) «ماڭا اﷲ نىڭ بەندىلىرىنى (يەنى بەنى ئىسرائىلنى) تاپشۇرۇپ بېرىڭلار، مەن سىلەرگە ھەقىقەتەن ئىشەنچلىك پەيغەمبەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,19,' اﷲ قا تەكەببۇرلۇق قىلماڭلار، مەن سىلەرگە ھەقىقەتەن روشەن مۆجىزە بىلەن كەلدىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,20,' مەن ھەقىقەتەن مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلارغا سېغىنىپ، سىلەرنىڭ مېنى ئۆلتۈرۈشۈڭلاردىن پاناھ تىلەيمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,21,' ئەگەر ماڭا ئىمان كەلتۈرمىسەڭلار مېنى (ئۆز ھالىمغا) قويۇۋېتىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,22,' مۇسا پەرۋەردىگارىغا: «بۇلار گۇناھكار قەۋمدۇر» دەپ دۇئا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,23,' (اﷲ ئېيتتى) «مېنىڭ بەندىلىرىمنى كېچىدە ئېلىپ چىقىپ كەتكىن، سىلەر ھەقىقەتەن قوغلىنىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,24,' دېڭىزنى تىپتىنچ (يەنى سەن ئۆتۈپ بولغاندىن كېيىن قۇرۇق ھالىتى بويىچە) قويغىن، ئۇلار ھەقىقەتەن غەرق قىلىنغۇچى قوشۇندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,25,' ئۇلار (غەرق بولۇپ) نۇرغۇن باغلارنى، بۇلاقلارنى، زىرائەتلەرنى، چىرايلىق جايلارنى قالدۇردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,26,' ئۇلار بەھرىمەن بولۇۋاتقان نېمەتلەرنى قالدۇردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,27,' شۇنداق قىلىپ، ئۇلارنى باشقىلارغا (يەنى بەنى ئىسرائىلغا) مىراس قىلىپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,28,' ئۇلارغا ئاسمانمۇ، زېمىنمۇ يىغلىمىدى، ئۇلارغا مۆھلەتمۇ بېرىلمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,29,' شەك ـ شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىنى خار قىلغۇچى ئازابتىن – پىرئەۋن (ئازابى) دىن قۇتقۇزدۇق، پىرئەۋن ھەقىقەتەن مۇتەكەببىر ئىدى، ھەددىدىن ئاشقۇچىلاردىن ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,30,' ئۇلارنى (يەنى ئۇلارنىڭ ئەينى زاماندا مۇشۇنداق شەرەپكە لايىق ئىكەنلىكىنى) بىلىپ تۇرۇپ (ئۆز زامانىدىكى) جاھان ئەھلىدىن ئارتۇق قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,31,' ئۇلارغا (يەنى تەپەككۇر قىلغۇچىلارغا) روشەن سىناق بار نۇرغۇن مۆجىزىلەرنى ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,32,' بۇلار (يەنى قۇرەيش كۇففارلىرى) چوقۇم ئېيتىدۇ:');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,33,'«پەقەت بىرلا قېتىم ئۆلىمىز، قايتا تىرىلمەيمىز، ئەگەر (ئاخىرەت بار دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، بىزنىڭ ئاتا ـ بوۋىلىرىمىزنى (تىرىلدۈرۈپ) ئەكېلىپ بېقىڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,34,' ئۇلار (يەنى مۇشرىكلار) كۈچلۈكمۇ؟ ياكى تۈببە قەۋمىمۇ؟ ئۇلاردىن ئىلگىرى ئۆتكەن (نۇرغۇن) قەۋملەرنى ھالاك قىلدۇق چۈنكى ئۇلار گۇناھكار ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,35,' ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئويناپ (يەنى بىكارغا) ياراتقىنىمىز يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,36,' ئۇلارنى بىز پەقەت ھەق ياراتتۇق ۋە لېكىن ئىنسانلارنىڭ تولىسى (بۇنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,37,' باتىل بىلەن ھەقنى ئايرىيدىغان كۈن (يەنى قىيامەت كۈنى) ئىنسانلارنىڭ ھەممىسىنىڭ (ھېسابى ئۈچۈن) تەيىنلەنگەن ۋاقىتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,38,' ئۇ كۈندە دوست دوستقا ھېچ نەرسىگە ئەسقاتمايدۇ، ئۇلار ياردەمگىمۇ ئېرىشەلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,39,' پەقەت اﷲ رەھمەت قىلغان كىشى بۇنىڭدىن مۇستەسنا اﷲ ھەقىقەتەن غالىبتۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,40,' زەققۇم دەرىخى ھەقىقەتەن گۇناھكارلارنىڭ تامىقىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,41,' ئۇ ئېرىتىلگەن مىستەك (قىزىق) دۇر، ئۇ قورساقلاردا قايناقسۇدەك قاينايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,42,' (دوزاخقا مۇئەككەل پەرىشتىلەرگە) «ئۇنى تۇتۇپ سۆرەپ دوزاخنىڭ ئوتتۇرىسىغا ئېلىپ بېرىڭلار، ئاندىن ئۇنىڭ بېشىغا قايناقسۇ قۇيۇپ ئازابلاڭلار» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,43,' (ئۇنىڭغا خارلاش ۋە مەسخىرە قىلىش يۈزىسىدىن) «(بۇ ئازابنى) تېتىغىن، سەن ھەقىقەتەن ئىززەتلىك ئۇلۇغ زات ئىدىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,44,' بۇ (ئازاب) ھەقىقەتەن (ھايات ۋاقتىڭلاردا) سىلەر شەكلەنگەن نەرسىدۇر» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,45,' تەقۋادارلار ھەقىقەتەن بىخەتەر جايدا بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,46,' باغلاردا، بۇلاقلارنىڭ ئارىسىدا بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,47,' ئۇلار قېلىن، يۇپقا يىپەك كىيىملەرنى كىيىپ بىر ـ بىرىگە قاراپ ئولتۇرۇشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,48,' (ئۇلارنى تۈرلۈك ھۆرمەتلەر بىلەن) مۇشۇنداق ئىكرام قىلدۇق، شەھلا كۆزلۈك ھۆرلەرنى ئۇلارغا جۈپ قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,49,' ئۇلار جەننەتتە (مەيدە ئېغىرلىشىش ۋە كېسەللىكلەردىن) ئەمىن بولغان ھالدا (خىزمەتچىلەردىن) ھەممە مېۋىلەرنى (كەلتۈرۈشنى) تەلەپ قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,50,' ئۇلار ئىلگىرىكى (يەنى دۇنيادىكى) ئۆلۈمدىن باشقا (جەننەتتە) ھېچقانداق ئۆلۈمنى تېتىمايدۇ، اﷲ ئۇلارنى دوزاخ ئازابىدىن ساقلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,51,' (بۇ) پەرۋەردىگارىڭنىڭ مەرھەمىتىدىندۇر، بۇ زور مۇۋەپپەقىيەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,52,' ئۇلارنىڭ ۋەز ـ نەسىھەت ئېلىشى ئۈچۈن قۇرئاننى سېنىڭ تىلىڭ بىلەن ئاسانلاشتۇرۇپ بەردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,46,53,' (ئى مۇھەممەد! ئۇلارغا چۈشىدىغان ئازابنى) كۈتكىن، شۈبھىسىزكى، ئۇلار (سېنىڭ ھالاك بولۇشۇڭنى) كۈتكۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,1,'حا، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,2,' (بۇ) كىتاب غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ تەرىپىدىن نازىل قىلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,3,' ئاسمانلاردا ۋە زېمىندا مۆمىنلەر ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) ھەقىقەتەن نۇرغۇن دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,4,' سىلەرنىڭ يارىتىلىشىڭلاردا ۋە (اﷲ زېمىندا) تارقىتىۋەتكەن جانىۋارلاردا (اﷲ نىڭ قۇدرىتىگە) جەزمەن ئىشىنىدىغان قەۋم ئۈچۈن نۇرغۇن دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,5,' كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىپ تۇرۇشىدا، اﷲ نىڭ بۇلۇتتىن (كۆكتىن) يامغۇر ياغدۇرۇپ ئۇنىڭ بىلەن رىزىقلەندۈرۈپ ئۆلگەن زېمىننى تىرىلدۈرۈشىدە ۋە شاماللارنىڭ يۈزلىنىشىنى ئۆزگەرتىپ تۇرۇشىدا، چۈشىنىدىغان قەۋم ئۈچۈن (اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,6,' ئەنە شۇلار اﷲ نىڭ ئايەتلىرىدۇر (ئى مۇھەممەد!) ئۇنى ساڭا ھەقلىق بىلەن تىلاۋەت قىلىپ بېرىمىز ئۇلار (يەنى مەككە كۇففارلىرى) اﷲ نى ۋە ئۇنىڭ ئايەتلىرىنى قويۇپ قايسى سۆزگە ئىشىنىدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,7,' ھەر بىر گۇناھكار يالغانچىغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,8,' ئۇ اﷲ نىڭ ئايەتلىرىنىڭ ئۆزىگە ئوقۇپ بېرىلىۋاتقانلىقىنى ئاڭلاپ تۇرىدۇ، ئاندىن تەكەببۇرلۇق قىلغان ھالدا گويا ئۇنى ئاڭلىمىغاندەك (كۇفرىدا) چىڭ تۇرىدۇ (گۇمراھلىقتا تېخىمۇ ئەزۋەيلەيدۇ)، ئۇنىڭغا قاتتىق ئازاب بىلەن خۇش خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,9,' ئۇ بىزنىڭ ئايەتلىرىمىزدىن بىرەر نەرسىنى بىلگەن چاغدا (يەنى ئۇنىڭغا قۇرئان ئايەتلىرى يەتكەن چاغدا)، ئۇنى مەسخىرە قىلىدىغان نەرسە قىلىۋالىدۇ، ئەنە شۇلار خار قىلغۇچى ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,10,' ئۇلارنى ئالدىدا جەھەننەم (كۈتۈپ تۇرىدۇ)، ئۇلارنىڭ ئېرىشكەن نەرسىلىرى (يەنى دۇنيادا تاپقان ماللىرى ۋە بالىلىرى) ۋە اﷲ نى قويۇپ دوست تۇتقان نەرسىلىرى (يەنى بۇتلىرى) ئۇلارغا ھېچ نەرسىگە ئەسقاتمايدۇ، ئۇلار چوڭ ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,11,' بۇ (قۇرئان ئۇنىڭغا ئىشەنگەن ۋە ئۇنىڭغا ئەگەشكەن كىشىگە كامىل) ھىدايەتتۇر، پەرۋەردىگارىنىڭ ئايەتلىرىنى ئىنكار قىلغانلار ئەڭ قاتتىق ئازابقا دۇچار بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,12,' اﷲ نىڭ ئەمرى بىلەن دېڭىزدا كېمىلەرنىڭ يۈرۈشى ئۈچۈن، سىلەرنىڭ اﷲ نىڭ پەزلىدىن تەلەپ قىلىشىڭلار ئۈچۈن (يەنى تىجارەت قىلىشىڭلار، بېلىق تۇتۇشۇڭلار، دېڭىز ئاستىدىن ئۈنچە ـ مەرۋايىتلارنى سۈزۈۋېلىشىڭلار ئۈچۈن) ۋە (اﷲ قا) شۈكۈر قىلىشىڭلار ئۈچۈن، اﷲ سىلەرگە دېڭىزنى بويسۇندۇرۇپ بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,13,' اﷲ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسىنى (كامالى) پەزلىدىن سىلەرگە بويسۇندۇرۇپ بەردى، بۇنىڭدا ھەقىقەتەن (قۇدرىتى ئىلاھىيىنى) تەپەككۈر قىلىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتى ۋە بىرلىكىنى كۆرسىتىدىغان) (روشەن) دەلىللەر بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,14,' مۆمىنلەرگە ئېيتقىنكى، اﷲ ھەر قەۋمنىڭ قىلمىشلىرىغا يارىشا جازا بېرىدىغانلىقى ئۈچۈن، ئۇلار اﷲ نىڭ كۈنلىرىنى ئۈمىد قىلمايدىغانلارنى (يەنى اﷲ نىڭ ئازابىدىن قورقمايدىغان كۇففارلارنىڭ يەتكۈزگەن ئەزىيەتلىرىنى) كەچۈرسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,15,' كىمكى (دۇنيادا) ياخشى ئىش قىلسا، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈن قىلىدۇ، كىمكى يامان ئىش قىلسا، ئۇ ئۆزىنىڭ زىيىنى ئۈچۈن قىلىدۇ، ئاندىن كېيىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتۇرۇلىسىلەر (اﷲ ياخشىلىققا مۇكاپات، يامانلىققا جازا بېرىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,16,' شەك ـ شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىغا كىتابنى (يەنى تەۋراتنى)، ھېكمەتنى ۋە پەيغەمبەرلىكنى ئاتا قىلدۇق، ئۇلارغا پاك نەرسىلەرنى رىزىق قىلىپ بەردۇق، ئۇلارنى (زامانىدىكى) جاھان ئەھلىدىن ئۈستۈن قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,17,' ئۇلارغا دىن ئىشىدا روشەن دەلىللەرنى بەردۇق، پەقەت ئۇلارغا ئىلىم (يەنى دىننىڭ راستلىقىغا قەتئى دەلىللەر) كەلگەندىن كېيىنلا، ئاندىن ئۇلار ئۆزئارا ھەسەت قىلىشىپ ئىختىلاپ قىلىشتى پەرۋەردىگارىڭ ھەقىقەتەن قىيامەت كۈنى ئۇلار ئىختىلاپ قىلىشقان ئىشلار ئۈستىدە ھۆكۈم چىقىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,18,' ئاندىن سېنى بىز (ئى مۇھەممەد!) دىن ئىشىدا (روشەن) بىر يولدا قىلدۇق، شۇ يولغا ئەگەشكىن، بىلمەيدىغانلار (يەنى مۇشرىكلار) نىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,19,' شۈبھىسىزكى، (ئۇلارنىڭ گۇمراھلىقىغا ماسلىشىدىغان بولساڭ) ئۇلار سەندىن اﷲ نىڭ (ئازابى) دىن ھېچ نەرسىنى دەپئى قىلالمايدۇ، شۈبھىسىزكى، (دۇنيادا) زالىملارنىڭ بەزىسى بەزىسىگە دوستتۇر، اﷲ تەقۋادارلارنىڭ دوستىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,20,' بۇ (قۇرئان) ئىنسانلار ئۈچۈن روشەن پاكىتلاردۇر، ھىدايەتتۇر، (ئۆلگەندىن كېيىن تىرىلىشكە) جەزمەن ئىشەنگەن قەۋم ئۈچۈن رەھمەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,21,' يامان ئىشلارنى قىلغانلار (يەنى گۇناھكار كۇففارلار) ئۆزلىرىنى ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلاردەك قىلىشىمىزنى، ھاياتلىقتا، ماماتلىقتا ئۇلار بىلەن ئوخشاش قىلىشىمىزنى ئويلامدۇ؟ (يەنى ئۇلارنى ئوخشاش قىلىشىمىز مۇمكىن ئەمەس) ئۇلارنىڭ چىقارغان ھۆكمى نېمىدېگەن يامان!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,22,' اﷲ ئاسمانلارنى ۋە زېمىننى ھەقلىق بىلەن ياراتتى، ھەر ئىنساننىڭ قىلمىشىغا يارىشا جازا ياكى مۇكاپات بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,23,' (ئى مۇھەممەد!) ماڭا نەپسى خاھىشنى ئىلاھ قىلىۋالغان، اﷲ ئازدۇرغان، (ھەقنى) بىلىپ تۇرغان، اﷲ قۇلىقىنى ۋە قەلبىنى پېچەتلىۋەتكەن ۋە كۆزىنى پەردىلىگەن ئادەمنى ئېيتىپ بەرگىن، اﷲ ئازدۇرغاندىن كېيىن، ئۇنى كىم ھىدايەت قىلالايدۇ؟ سىلەر (بۇلاردىن) ۋەز ـ نەسىھەت ئالمامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,24,' ئۇلار (يەنى قىيامەتنى ئىنكار قىلغۇچىلار): «ھايات دېگەن پەقەت دۇنيادىكى ھاياتىمىزدۇر، ئۆلىمىز ۋە تىرىلىمىز (يەنى تۇغۇلىمىز) پەقەت زاماننىڭ ئۆتۈشى بىلەن يوق بولىمىز» (دەيدۇ) ئۇلار بۇ خۇسۇستا ھېچقانداق مەلۇماتقا ئىگە ئەمەس، ئۇلار پەقەت گۇمان بىلەن سۆزلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,25,' ئۇلارغا بىزنىڭ ئايەتلىرىمىز ئوچۇق ئوقۇپ بېرىلسە، ئۇلارنىڭ پاكىتى پەقەت «ئەگەر راستچىل بولساڭلار، ئاتا ـ بوۋىلىرىمىزنى كەلتۈرۈپ بېقىڭلار (يەنى تىرىلدۈرۈڭلار)» دېيىشتىن ئىبارەت بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,26,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «اﷲ سىلەرنى تىرىلدۈرەلەيدۇ، ئاندىن (ئەجىلىڭلار پۈتكەندە) ۋاپات قىلدۇرالايدۇ، ئاندىن شەك ـ شۈبھىسىز قىيامەت كۈنىدە سىلەرنى يىغىدۇ، لېكىن كىشىلەرنىڭ تولىسى (اﷲ نىڭ قۇدرىتىنى) بىلمەي (قايتا تىرىلىشنى ئىنكار قىلدى)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,27,' ئاسمانلار بىلەن زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، قىيامەت قايىم بولغان كۈندە كاپىرلار زىيان تارتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,28,' ھەر ئۈممەتنى (قورقۇنچنىڭ قاتتىقلىقىدىن) تىزلانغان ھالدا كۆرىسەن، ھەر ئۈممەت ئۆزىنىڭ نامە ـ ئەمالىغا چاقىرىلىدۇ، (ئۇلارغا) «بۈگۈن قىلمىشىڭلارغا يارىشا مۇكاپاتقا ئېرىشىسىلەر ياكى جازاغا ئۇچرايسىلەر» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,29,' بۇ بىزنىڭ كىتابىمىز (يەنى نامە ـ ئەمال) سىلەرگە ھەق بىلەن سۆزلەيدۇ، بىز قىلمىشىڭلارنى يازدۇرۇپ تۇرغان ئىدۇق (يەنى پەرىشتىلەرنى ئەمەللىرىڭلارنى يېزىپ تۇرۇشقا بۇيرۇيتتۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,30,' ئىمان ئېيتقان ۋە ياخشى ئىشلارنى قىلغانلارغا كەلسەك، پەرۋەردىگارى ئۇلارنى رەھمىتى دائىرىسىگە كىرگۈزىدۇ، بۇ روشەن مۇرادقا يېتىشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,31,' كاپىرلارغا كەلسەك، (ئۇلارنى ئەيىبلەش يۈزىسىدىن) ئۇلارغا: «مېنىڭ ئايەتلىرىم سىلەرگە ئوقۇپ بېرىلمىدىمۇ؟ سىلەر چوڭچىلىق قىلىپ (ئۇنىڭغا ئىمان ئېيتىشتىن) باش تارتتىڭلار، سىلەر گۇناھقا چۆمگەن قەۋم ئىدىڭلار» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,32,' (سىلەرگە) «اﷲ نىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر، قىيامەتتە (يەنى قىيامەتنىڭ بولۇشىدا) ھېچ شەك يوق» دېيىلسە، سىلەر (ھەددىدىن زىيادە سەركەشلىكىڭلاردىن): «قىيامەتنىڭ نېمە ئىكەنلىكىنى بىلمەيمىز، پەقەت ئۇنى بولۇشى مۇمكىن دەپ گۇمان قىلىمىز، (ئۇنىڭغا) بىز ھەقىقىي ئىشەنمەيمىز» دېدىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,33,' (ئاخىرەتتە) ئۇلارنىڭ قىلغان يامان ئىشلىرى ئاشكارا بولىدۇ، ئۇلار (دۇنيادىكى چاغلىرىدا) مەسخىرە قىلغان ئازاب ئۇلارنى ئورىۋالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,34,' (ئۇلارغا) دېيىلىدۇ: «سىلەرنىڭ مۇشۇ كۈندىكى مۇلاقاتنى ئۇنتۇغانلىقىڭلارغا ئوخشاش، بۈگۈن سىلەرنى ئۇنتۇيمىز (يەنى سىلەرنى ئازابقا تاشلاپ قويۇپ ئۇنتۇپ قالغاندەك مۇئامىلە قىلىمىز)، سىلەرنىڭ جايىڭلار دوزاخ بولىدۇ، سىلەرگە (سىلەرنى ئازابتىن قۇتقۇزىدىغان) ھېچقانداق ياردەمچىمۇ بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,35,' بۇ شۇنىڭ ئۈچۈنكى، سىلەر اﷲ نىڭ ئايەتلىرىنى مەسخىرە قىلىدىغان نەرسە قىلىۋالدىڭلار ۋە سىلەرنى دۇنيا تىرىكچىلىكى ئالدىدى» بۈگۈن ئۇلار دوزاختىن چىقىرىلمايدۇ، ئۇلاردىن (تەۋبە قىلىش ۋە ئىبادەت قىلىش بىلەن) اﷲ نى رازى قىلىشمۇ تەلەپ قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,36,' جىمى ھەمدۇسانا ئاسمانلارنىڭ پەرۋەردىگارى، زېمىننىڭ پەرۋەردىگارى ۋە ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,47,37,'ئاسمانلاردىكى ۋە زېمىندىكى ئۇلۇغلۇق اﷲ قا خاستۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,1,'حا، مىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,2,' (بۇ قۇرئان) غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ تەرىپىدىن نازىل قىلىنغاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,3,' ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ھەق ئاساستا مۇئەييەن مۇددەتكىچە (يەنى قىيامەتكىچە قالىدىغان) قىلىپ ياراتتۇق كاپىرلار ئاگاھلاندۇرۇلغان نەرسىلەردىن يۈز ئۆرۈگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,4,' (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەر اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلارنىڭ (ئەھۋالىنى) ئېيتىپ بېرىڭلار، ئۇلار زېمىننىڭ قايسى قىسمىنى ياراتقانلىقىنى ماڭا كۆرسىتىڭلار، ياكى ئاسمانلارنى يارىتىشتا ئۇلارنىڭ اﷲ بىلەن ئورتاقچىلىقى بارمۇ؟ بۇ (قۇرئان) دىن ئىلگىرى (سىلەرگە نازىل قىلىنغان) كىتاب ياكى (ئىلگىرىكىلەردىن) قالغان ئىلىم بولسا ماڭا ئېلىپ كېلىڭلار، ئەگەر سىلەر راستچىل بولساڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,5,' اﷲ نى قويۇپ قىيامەتكىچە دۇئانى ئىجابەت قىلىشقا قادىر بولالمايدىغان بۇتلارغا چوقۇنىدىغان ئادەمدىنمۇ گۇمراھ ئادەم بارمۇ؟ بۇتلار ئۇلارنىڭ دۇئاسىدىن غاپىلدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,6,' (قىيامەت كۈنى) ئىنسانلار (ھېساب بېرىش ئۈچۈن) توپلانغان چاغدا، بۇتلار ئۇلارغا (يەنى بۇتپەرەسلەرگە) دۈشمەن بولىدۇ، ئۇلار تەرىپىدىن ئىبادەت قىلىنغانلىقىنى ئىنكار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,7,' ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز تىلاۋەت قىلىنسا، كاپىرلار ئۆزلىرىگە كەلگەن ھەقىقەتنى (يەنى قۇرئاننى) باھالاپ: «بۇ روشەن سېھىردۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,8,' ياكى ئۇلار قۇرئاننى ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ئۆزى توقۇغان دىېيىشەمدۇ؟ ئېيتقىنكى، «ئەگەر ئۇنى مەن توقۇغان بولسام، سىلەر مەندىن اﷲ نىڭ ئازابىدىن ھېچ نەرسىنى دەپئى قىلالمايسىلەر، سىلەرنىڭ قۇرئان توغرىسىدىكى تۆھمەتلىرىڭلارنى اﷲ ئوبدان بىلىدۇ، اﷲ مەن بىلەن سىلەرنىڭ ئاراڭلاردا گۇۋاھ بولۇشقا يېتەرلىكتۇر (يەنى مېنىڭ راستچىللىقىمغا ۋە سىلەرنىڭ يالغانچىلىقىڭلارغا گۇۋاھ بولىدۇ)، اﷲ (تەۋبە قىلغۇچىنى) مەغپىرەت قىلغۇچىدۇر، (مۆمىن بەندىلىرىگە) ناھايىتى مېھرىباندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,9,' ئېيتقىنكى، «مەن (اﷲ ئىنسانلارنى تەۋھىدكە دەۋەت قىلىش ئۈچۈن ئەۋەتكەن) تۇنجى پەيغەمبەر ئەمەسمەن، (مەندىن ئىلگىرىكى نۇرغۇن پەيغەمبەرلەر ئېلىپ كەلگەن ۋەھيى بىلەن كەلدىم، نېمىشقا مېنى ئىنكار قىلىسىلەر؟) ماڭا ۋە سىلەرگە نېمە قىلىنىدىغانلىقىنى ئۇقمايمەن (چۈنكى اﷲ نىڭ تەقدىرىنى ئالدىن ئۇققىلى بولمايدۇ)، مەن پەقەت ماڭا ۋەھيى قىلىنغان نەرسىگىلا ئەگىشىمەن، مەن پەقەت (سىلەرنى اﷲ نىڭ ئازابىدىن) ئوچۇق ئاگاھلاندۇرغۇچىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,10,' (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەر ئېيتىپ بېقىڭلارچۇ! قۇرئان اﷲ تەرىپىدىن نازىل بولغان تۇرسا، سىلەر ئۇنىڭغا ئىشەنمىسەڭلار (ھالىڭلار قانداق بولىدۇ؟) ئىسرائىل ئەۋلادىدىن بىر گۇۋاھچى (يەنى ئابدۇللاھ ئىبن سالام) قۇرئاننىڭ اﷲ تەرىپىدىن نازىل بولغانلىقىغا گۇۋالىق بېرىپ ئىمان كەلتۈرسە، سىلەر تەكەببۇرلۇق قىلىپ (ئىماندىن) باش تارتساڭلار (ھالىڭلار قانداق بولىدۇ؟) شۈبھىسىزكى، اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,11,' كاپىرلار مۆمىنلەرگە: «ئەگەر (قۇرئان بىلەن ئىسلام دىنى) ياخشى بولىدىغان بولسا ئىدى، ئۇلار (يەنى ئاجىز مۇسۇلمانلار) ئۇنىڭغا بىزدىن بۇرۇن ئىشەنمىگەن بولاتتى» دېدى، ئۇلار قۇرئان بىلەن ھىدايەت تاپمىغانلىقلىرى ئۈچۈن، « بۇ قەدىمقى ئويدۇرمىدۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,12,' قۇرئاندىن ئىلگىرى مۇسانىڭ كىتابى (يەنى تەۋرات) ئەھلى جاھانغا پېشۋا ۋە رەھمەت ئىدى بۇ ئەرەب تىلىدىكى كىتابتۇر، (ئىلگىرىكى كىتابلارنى) تەستىق قىلغۇچىدۇر، (اﷲ ئۇنى) زالىملارنى ئاگاھلاندۇرۇش ئۈچۈن، ياخشى ئىش قىلغۇچى مۆمىنلەرگە (جەننەت بىلەن) خۇش خەۋەر بېرىش ئۈچۈن نازىل قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,13,' «پەرۋەردىگارىمىز اﷲ دۇر» دېگەن، ئاندىن توغرا يولدا بولغانلارغا، شۈبھىسىزكى، (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,14,' ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە مەڭگۈ قالىدۇ، (بۇ) ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرىنى مۇكاپاتلاش ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,15,' بىز ئىنساننى ئاتا ـ ئانىسىغا ياخشىلىق قىلىشقا بۇيرۇدۇق ئىنساننى ئانىسى مۇشەققەت بىلەن قورساق كۆتۈرۈپ، مۇشەققەت بىلەن تۇغدى ئۇنىڭغا قورساق كۆتۈرۈش مۇددىتى ۋە ئۇنى سۈتتىن ئايرىش مۇددىتى  ئايدۇر تاكى ئۇ (بوۋاق ئۆسۈپ) كۈچ ـ قۇۋۋەتكە تولۇپ،  ياشقا يەتكەندە، (ئۇ): «پەرۋەردىگارىم! سېنىڭ ماڭا ۋە ئاتا ـ ئانامغا بەرگەن نېمىتىڭگە شۈكۈر قىلىشنى ۋە سەن رازى بولىدىغان ياخشى ئىشنى قىلىشىمنى ماڭا ئىلھام قىلغىن، مەن ئۈچۈن مېنىڭ ئەۋلادىمنى تۈزىگىن (يەنى مېنىڭ ئەۋلادىمنى ياخشى ئادەملەر قىلغىن)، مەن ھەقىقەتەن ساڭا (جىمى گۇناھلاردىن) تەۋبە قىلدىم، مەن ھەقىقەتەن مۇسۇلمانلاردىندۇرمەن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,16,' ئەنە شۇنداق كىشىلەرنىڭ ياخشى ئىشلىرىنى قوبۇل قىلىپ، يامان ئىشلىرىنى كەچۈرۈم قىلىمىز، ئۇلار (اﷲ يامان ئىشلىرىنى كەچۈرگەنلەر بىلەن) جەننەتتە بولىدۇ، (بۇ) ئۇلارغا قىلىنغان راست ۋەدىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,17,' بەزى ئادەم (يەنى ئاتا ـ ئانىسى ئىمانغا دەۋەت قىلغان فاجىر بالا): ۋاي ـ ۋۇي! سىلەر مېنى قايتا تىرىلىشتىن قورقۇتۇۋاتامسىلەر، (مەندىن ئىلگىرى) نۇرغۇن ئۈممەتلەر ئۆتتى (ئۇلاردىن ھېچ ئادەم تىرىلدۈرۈلگىنى يوق)» دەيدۇ، ئۇ ئىككىسى اﷲ قا پەرياد قىلىپ: «ۋاي ساڭا! ئىمان ئېيتقىن، اﷲ نىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر» دەيدۇ ئۇ: «بۇ پەقەت ئاۋالقىلاردىن قالغان ئەپسانىلەردۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,18,' ئەنە شۇنداق كىشىلەرگە (اﷲ نىڭ ئازاب قىلىش) سۆزى ھەقلىق بولدى، ئۇلار بولسا ئۇلاردىن ئىلگىرى ئۆتكەن ئىنسانلاردىن ۋە جىنلاردىن بولغان ئۈممەتلەر قاتارىغا كىرگۈزۈلدى، ئۇلار ھەقىقەتەن زىيان تارتقۇچى بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,19,' (مۆمىنلەرنىڭ ۋە كاپىلارنىڭ) ھەممىسىنىڭ قىلغان ئەمەللىرىگە يارىشا دەرىجىلىرى بولىدۇ، ئۇلارنىڭ ئەمەللىرىنىڭ ساۋابى ۋە جازاسى (كەم ـ زىيادە قىلىنماي) تولۇق بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,20,' كاپىلار دوزاخقا توغرىلىنىدىغان كۈندە (ئۇلارغا ئەيىبلەش يۈزىسىدىن ئېيتىلىدۇ): «سىلەر لەززاتلىرىڭلارنى ھاياتىي دۇنيادا بەھرىمەن بولۇپ تۈگەتتىڭلار، سىلەر زېمىندا ھەقسىز رەۋىشتە چوڭچىلىق قىلىپ، ئىماندىن باش تارتقانلىقىڭلار ۋە اﷲ نىڭ ئىتائىتىدىن چىققانلىقىڭلار تۈپەيلىدىن، بۈگۈن خار قىلغۇچى ئازاب بىلەن جازالىنىسىلەر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,21,' (ئى مۇھەممەد!) ئادنىڭ قېرىندىشى (يەنى ھۇد ئەلەيھىسسالام) نىڭ ئۆز قەۋمى بىلەن بولغان قىسسىسىنى بايان قىلغىن، ئۆز ۋاقتىدا ئۇ ئەھقاف (دېگەن يەر) دىكى قەۋمىنى (اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇردى، ئۇنىڭدىن (يەنى ھۇدتىن) ئىلگىرى ۋە ئۇنىڭدىن كېيىن نۇرغۇن پەيغەمبەرلەر ئۆتتى ئۇ (يەنى ھۇد) (قەۋمىنى ئاگاھلاندۇرۇپ) ئېيتتى: «سىلەر پەقەت اﷲ غىلا ئىبادەت قىلىڭلار، مەن ھەقىقەتەن سىلەرنىڭ (اﷲ تىن غەيرىيگە ئىبادەت قىلساڭلار) بۈيۈك كۈننىڭ ئازابىغا قېلىشىڭلاردىن قورقىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,22,' ئۇلار: «(ئى ھۇد!) سەن بىزنى ئىلاھلىرىمىزدىن توسقىلى كەلدىڭمۇ؟ ئەگەر سەن (سۆزۈڭدە) راستچىللاردىن بولساڭ، بىزگە بىزنى قورقۇتقان ئازابنى كەلتۈرگىن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,23,' ئۇ: «(سىلەرگە ئازابنىڭ قاچان كېلىشىنى) پەقەت اﷲ بىلىدۇ، مەن سىلەرگە ئەۋەتىلگەن نەرسىنى تەبلىغ قىلىمەن ۋە لېكىن مەن سىلەرنى نادان قەۋم كۆرىمەن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,24,' ئۇلار ئۆزلىرىنىڭ ۋادىسىغا سۈرۈلۈۋاتقان بۇلۇتنى كۆرۈپ: «مانا بۇ بىزگە يامغۇر ياغدۇرغۇچى بۇلۇتتۇر» دېيىشتى (ھۇد ئېيتتى) «ئۇنداق ئەمەس، ئۇ سىلەر بالدۇرراق ئىشقا ئېشىشىنى تەلەپ قىلغان نەرسىدۇر، ئۇ بىر بوران بولۇپ، ئۇنىڭ ئىچىدە قاتتىق ئازاب بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,25,' ئۇ پەرۋردىگارىنىڭ بىر ئەمرى بىلەن ھەممە نەرسىنى ھالاك قىلىدۇ» (يەنى بوران كېلىپ ئۇلارنى ھالاك قىلدى)، شۇنىڭ بىلەن ئۇلارنىڭ جايلىرىنىڭ (يەنى تۇرغان ماكانلىرىنىڭ ئىزىدىن) باشقا نەرسە كۆرۈنمەيدىغان بولۇپ قالدى، گۇناھكار قەۋمنى مۇشۇنداق جازالايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,26,' بىز ئۇلارنى (يەنى ئاد قەۋمىنى) (پۇل ـ مال، كۈچ ـ قۇۋۋەت جەھەتتە) سىلەرنى قادىر قىلمىغان نەرسىلەرگە قادىر قىلدۇق (ئۇلارنىڭ شۇ نېمەتلەرگە شۈكۈر قىلىپ، بۇ ئارقىلىق نېمەتلەرنى بەرگۈچى اﷲ نى تونۇشى ئۈچۈن) ئۇلارغا قۇلاق، كۆز، قەلبلەرنى بەردۇق، ئۇلار اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى ئۈچۈن، ئۇلارنىڭ قۇلاقلىرى، كۆزلىرى، دىللىرى ئۇلاردىن (اﷲ نىڭ ئازابىدىن) ھېچ نەرسىنى دەپئى قىلمىدى، ئۇلار مەسخىرە قىلغان ئازاب ئۇلارغا نازىل بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,27,' (ئى مەككە ئاھالىسى!) بىز ھەقىقەتەن سىلەرنىڭ ئەتراپىڭلاردىكى شەھەرلەرنى ھالاك قىلدۇق، ئۇلارنى (كۇفرىدىن) يانسۇن دەپ ئايەتلەرنى تەكرار بايان قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,28,' (ئۇلارغا ئازاب كەلگەندە) ئۇلارنىڭ خۇداغا يېقىن قىلىدۇ دەپ اﷲ تىن باشقا تۇتقان مەبۇدلىرى ئۇلارغا نېمىشقا ياردەم قىلمىدى؟ بەلكى ئۇلارنىڭ مەبۇدلىرى غايىب بولدى، بۇ (يەنى بۇتلار بىزنى خۇداغا يېقىن قىلىدۇ دېگەن سۆزى) ئۇلارنىڭ ئويدۇرمىسىدۇر ۋە (اﷲ قا) چاپلىغان بوھتانلىرىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,29,' (ئى مۇھەممەد!) ئۆز ۋاقتىدا بىر تۈركۈم جىنلارنى ساڭا قۇرئان تىڭشاشقا ئەۋەتتۇق، ئۇلار قۇرئان تىلاۋىتىگە ھازىر بولغاندا (بىر ـ بىرىگە): «جىم تۇرۇڭلار» دېيىشتى، قۇرئان تىلاۋەت قىلىنىپ بولغاندىن كېيىن ئۇلار ئۆز قەۋمىگە ئاگاھلاندۇرغۇچى بولۇپ قايتىشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,30,' ئۇلار ئېيتتى: «ئى قەۋمىمىز! ھەقىقەتەن بىز مۇسادىن كېيىن نازىل بولغان، ئۇنىڭدىن ئىلگىرىكى كىتابلارغا ئۇيغۇن كېلىدىغان، ھەق دىنغا ۋە توغرا يولغا باشلايدىغان كىتابنى (يەنى قۇرئاننى) تىڭشىدۇق»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,31,' «ئى قەۋمىمىز! اﷲ قا دەۋەت قىلغۇچى (يەنى مۇھەممەد ئەلەيھىسسالام) نىڭ دەۋىتىنى قوبۇل قىلىڭلار ۋە اﷲ قا ئىمان ئېيتىڭلار، اﷲ بەزى گۇناھلارنى مەغپىرەت قىلىدۇ، سىلەرنى قاتتىق ئازابتىن ساقلايدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,32,' كىمكى اﷲ قا (ئىمان ئېيتىشقا) دەۋەت قىلغۇچىنىڭ (يەنى رەسۇلۇللاھنىڭ) دەۋىتىنى قوبۇل قىلمىسا، ئازابتىن قېچىپ قۇتۇلالمايدۇ، اﷲ تىن باشقا (ئۇنى اﷲ نىڭ ئازابىدىن ساقلىغۇچى) ياردەمچىلەر بولمايدۇ، ئۇلار (يەنى رەسۇلۇللاھنىڭ دەۋىتىنى قوبۇل قىلمىغۇچىلار) ئوپئوچۇق گۇمراھلىقتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,33,' ئۇلار (يەنى ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلغۇچى كۇففارلار) بىلمەمدۇكى، ئاسمانلارنى ۋە زېمىننى ياراتقان ۋە ئۇلارنى يارىتىشتا چارچاپ قالمىغان اﷲ ئۆلۈكلەرنى تىرىلدۈرۈشكە قادىر ئەمەسمۇ؟ دۇرۇس، ئۇ ھەقىقەتەن ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,34,' كاپىرلار دوزاخقا توغرىلىنىدىغان كۈندە (ئۇلارغا): «بۇ (يەنى سىلەر تېتىۋاتقان ئازاب) راست ئەمەسمۇ؟ » (دېيىلىدۇ) ئۇلار: «پەرۋەردىگارىمىز بىلەن قەسەمكى، ئەلۋەتتە راستتۇر» دەيدۇ، اﷲ (ئۇلارغا): «كاپىر بولغانلىقىڭلار تۈپەيلىدىن ئازابنى تېتىڭلار» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,48,35,' (ئى مۇھەممەد!) سەن (مۇشرىكلارنىڭ سالغان جاپاسىغا) ئىرادىلىك پەيغەمبەرلەر سەۋر قىلغاندەك سەۋر قىلغىن، ئۇلارغا بولىدىغان ئازابقا ئالدىراپ كەتمىگىن ئۇلار (ئاخىرەتتە) ۋەدە قىلىنغان ئازابنى كۆرگەندە، (ئۇلار دۇنيادا) گويا كۈندۈزدە بىر دەملا تۇرغاندەك ھېس قىلىدۇ، (بۇ قۇرئان سىلەرگە) تەبلىغدۇر، پەقەت پاسىق قەۋملا ھالاك قىلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,1,'كاپىر بولغان ۋە (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلارنىڭ (ياخشى) ئەمەللىرىنى اﷲ بىكار قىلىۋېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,2,' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ، مۇھەممەدكە نازىل قىلىنغان قۇرئانغا ـ قۇرئان ئۇلارنىڭ پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەتتۇر – ئىمان ئېيتقانلارنىڭ گۇناھلىرىنى اﷲ ئۆچۈرۈپ تاشلايدۇ، ئۇلارنىڭ ھالىنى ياخشىلايدۇ (شۇنىڭ بىلەن ئۈلار گۇناھ قىلمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,3,' بۇ شۇنىڭ ئۈچۈندۇركى، كاپىرلار باتىلغا (يەنى شەيتانغا) ئەگەشتى، مۆمىنلەر بولسا، پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەتكە (يەنى قۇرئانغا) ئەگەشتى، اﷲ ئىنسانلارغا ئۇلارنىڭ ئەھۋالىنى شۇنداق بايان قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,4,' سىلەر كۇففارلارغا (جەڭ مەيدانىدا) ئۇچراشقان چېغىڭلاردا، ئۇلارنى ئۆلتۈرۈڭلار، سىلەر ئۇلارنى قىرىپ (مەغلۇپ قىلىپ، ئۇلاردا سىلەرگە قارشىلىق كۆرسىتىدىغان كۈچ قالمىغان چاغدا ئۇلارنى ئۆلتۈرۈشتىن توختاپ)، ئۇلارنى ئەسىر ئېلىڭلار، ئاندىن ئۇلارنى ئېھسان قىلىش يۈزىسىدىن قويۇپ بېرىڭلار، ياكى فىدىيە ئېلىپ قويۇپ بېرىڭلار، تاكى ئۇرۇش ئۆزىنىڭ ئېغىر يۈكىنى تاشلىغانغا قەدەر (يەنى ئۇرۇش توختىغانغا قەدەر) ئىش مانا شۇنداقتۇر ئەگەر اﷲ خالىسا ئەلۋەتتە (سىلەرنى ئۇرۇشقا تەكلىپ قىلماستىنلا) ئۇلارنى جازالىغان بولاتتى، لېكىن اﷲ بەزىڭلارنى بەزىڭلار بىلەن سىناش ئۈچۈن (يەنى ئىمانىڭلارنى سىناش ئۈچۈن جىھادقا ئەمر قىلدى)، اﷲ نىڭ يولىدا ئۆلتۈرۈلگەنلەرنىڭ (يەنى شېھىتلەرنىڭ) ئەمەللىرىنى اﷲ بىكار قىلىۋەتمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,5,' اﷲ ئۇلارنى ھىدايەت قىلىدۇ ۋە ھالىنى ياخشىلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,6,' اﷲ ئۈلارنى جەننەتكە كىرگۈزىدۇ، اﷲ ئۇنى ئۇلارغا تونۇتتى (يەنى جەننەتكە كىرگەن ئادەم ئۆزىنىڭ ئۇ يەردىكى جايىنى ئىلگىرى كۆرگەندەكلا بىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,7,' ئى مۆمىنلەر! سىلەر اﷲ قا (يەنى اﷲ نىڭ دىنىغا) ياردەم بەرسەڭلار، اﷲ سىلەرگە (دۈشمىنىڭلارغا قارشى ياردەم بېرىدۇ، قەدىمىڭلارنى (ئۇرۇش مەيدانلىرىدا) بەرقارار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,8,' كاپىرلارنى اﷲ ھالاك قىلىدۇ، ئۇلارنىڭ (ياخشى) ئەمەللىرىنى بىكار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,9,' بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلار اﷲ نازىل قىلغان كىتابنى (يەنى قۇرئاننى) يامان كۆرۈش بىلەن، ئۆزلىرىنىڭ ئەمەللىرىنى بەربات قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,10,' ئۇلار زېمىندا سەير قىلىپ ئىلگىرىكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۈزەتمىدىمۇ؟ اﷲ ئۇلارنى ھالاك قىلدى (مەككە) كاپىرلىرىمۇ شۇنداق ئاقىۋەتكە قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,11,' بۇ شۇنىڭ ئۈچۈندۇركى، اﷲ مۆمىنلەرنىڭ مەدەتكارىدۇر، كاپىرلارغا مەدەتكار يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,12,' شۈبھىسىزكى، اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، كاپىرلار (ھاياتى دۇنيادا ئۇنىڭ لەززەتلىرىدىن) بەھرىمەن بولىدۇ ۋە چاھارپايلاردەك يەپ ـ ئىچىدۇ، (ئاخىرەتتە) ئۇلارنىڭ جايى دوزاخ بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,13,' سېنى ھەيدەپ چىقارغان شەھەرنىڭ (يەنى مەككىنىڭ) ئاھالىسىدىن كۈچلۈك بولغان نۇرغۇن شەھەرلەرنىڭ ئاھالىسىنى ھالاك قىلدۇق، ئۇلارغا ھېچ قانداق ياردەم قىلغۇچى بولمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,14,' پەرۋەردىگارى تەرىپىدىن بولغان روشەن دەلىللەرگە ئاساسلانغان ئادەم (يەنى مۆمىن) ئۆزىنىڭ يامان ئەمەلى چىرايلىق كۆرسىتىلگەن ۋە نەپسى خاھىشلىرىغا ئەگەشكەن ئادەمگە (يەنى كاپىرغا) ئوخشامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,15,' تەقۋادارلارغا ۋەدە قىلىنغان جەننەتنىڭ سۈپىتى شۇكى، ئۇ يەردە رەڭگى ئۆزگەرمىگەن سۇدىن ئۆستەڭلار، تەمى ئۆزگەرمىگەن سۈتتىن ئۆستەڭلار، ئىچكۈچىلەرگە لەززەت بېغىشلايدىغان مەيدىن ئۆستەڭلار ۋە ساپ ھەسەلدىن ئۆستەڭلار بولىدۇ، ئۇلارغا جەننەتتە بەھرىمەن بولىدىغان تۈرلۈك مېۋىلەر بولىدۇ ۋە پەرۋەردىگارى تەرىپىدىن مەغپىرەت بولىدۇ، (مۇنداق تەقۋادارلار) دوزاختا مەڭگۈ قالىدىغان، قايناقسۇ بىلەن سۇغىرىلىپ (قىزىقلىقىدىن) ئۈچەيلىرى پارە ـ پارە قىلىنىدىغانلار بىلەن ئوخشاشمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,16,' ئۇلاردىن (يەنى مۇناپىقلاردىن) بەزى كىشىلەر سېنىڭ سۆزۈڭگە قۇلاق سالىدۇ، ئۇلار سېنىڭ يېنىڭدىن چىققاندا، ئىلىم بېرىلگەن كىشىلەرگە (يەنى ساھابىلەرنىڭ ئۆلىمالىرىغا مەسخىرە قىلىش يۈزىسىدىن): «بايا ئۇ (يەنى مۇھەممەد) نېمە دېدى؟» دەيدۇ، ئەنە شۇلار اﷲ دىللىرىنى پېچەتلىۋەتكەنلەردۇر، نەپسى خاھىشلىرىغا ئەگەشكەنلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,17,' ھىدايەت تاپقانلارنى اﷲ تېخىمۇ ھىدايەت قىلىدۇ، ئۇلارغا تەقۋادارلىقنىڭ مۇكاپاتىنى بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,18,' ئۇلار (يەنى مەككە كۇففارلىرى) پەقەت قىيامەتنىڭ ئۇلارغا تۇيۇقسىز كېلىشىنىلا كۈتىدۇ، شۈبھىسىزكى، قىيامەتنىڭ ئالامەتلىرى كەلدى، ئۇلارغا قىيامەت كەلگەندە ئۇلار قانداقمۇ ۋەز ـ نەسىھەت ئالىدۇ (يەنى بۇ چاغدا ۋەز ـ نەسىھەت ئالغانلىقىنىڭ پايدىسى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,19,' بىلگىنكى، اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، گۇناھىڭ ئۈچۈن، ئەر ـ ئايال مۆمىنلەر ئۈچۈن مەغپىرەت تىلىگىن، اﷲ سىلەرنىڭ (بۇ دۇنيادىكى) ھەرىكىتىڭلارنى ۋە (ئاخىرەتتىكى) جايىڭلارنى بىلىدۇ (شۇڭا ئاخىرەتلىك ئۈچۈن تەييارلىنىڭلار)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,20,' مۆمىنلەر (جىھادنى تەلەپ قىلىش يۈزىسىدىن): «نېمىشقا (جىھاد ئەمرى قىلىنغان) بىرەر سۈرە نازىل قىلىنمىدى؟» دەيدۇ، جىھاد زىكرى قىلىنغان ئېنىق سۈرە نازىل قىلىنغاندا، دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلارنىڭ) ساڭا ئۆلۈم ئالدىدا (ئۆلۈمدىن قورقۇپ) ئايلىنىپ كەتكەن ئادەمدەك قاراۋاتقانلىقىنى كۆرىسەن، (ئى مۇھەممەد!) (ئۇلارنىڭ ساڭا) ئىتائەت قىلىشى ۋە چىرايلىق سۆز قىلىشى ئۇلار ئۈچۈن ئەلۋەتتە ياخشى ئىدى، ئۇرۇش قارار قىلىنغان چاغدا، ئۇلار اﷲ قا سادىق بولۇشسا ئەلۋەتتە ئۇلار ئۈچۈن ياخشى ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,21,' سىلەر (ئىسلام) دىن يۈز ئۆرۈسەڭلار زېمىندا بۇزغۇنچىلىق قىلارسىلەرمۇ ۋە سىلە ـ رەھىمنى ئۈزۈپ قويارسىلەرمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,22,' ئەنە شۇنداق كىشىلەرنى اﷲ رەھمىتىدىن يىراق قىلدى، ئۇلارنى (ھەقنى ئاڭلاشتىن) گاس قىلدى، (ھىدايەت يولىنى كۆرۈشتىن) كور قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,23,' ئۇلار (ھەقنى تونۇش ئۈچۈن) قۇرئاننى پىكىر قىلمامدۇ؟ بەلكى ئۇلارنىڭ دىللىرىدا قۇلۇپ بار (شۇڭا ئۇلار چۈشەنمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,24,' توغرا يول ئېنىق بولغاندىن كېيىن ئارقىسىغا قايتىۋالغانلار (يەنى ئىماندىن كۇفرىغا قايتقانلار)غا (ئۇلارنىڭ بۇ ئىشىنى) شەيتان چىرايلىق كۆرسەتتى، (شەيتان ئارزۇ ۋە ئەجەلنىڭ ئۇزۇنلۇقى بىلەن) ئۇلارنىڭ كۆڭلىنى تىندۇردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,25,' بۇ (يەنى شەيتاننىڭ ئۇلارنى ئازدۇرۇشى) شۇنىڭ ئۈچۈندۇركى، ئۇلار اﷲ نازىل قىلغان نەرسىنى يامان كۆرگەن كاپىرلارغا: «بىز بەزى ئىشلاردا سىلەرگە ئىتائەت قىلىمىز» دېدى، اﷲ ئۇلارنىڭ سىرلىرىنى بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,26,' (ئازاب) پەرىشتىلىرى ئۇلارنىڭ يۈزلىرىگە ۋە ئارقىلىرىغا ئۇرغان ھالدا ئۇلارنىڭ جانلىرىنى ئېلىۋاتقاندا (ئۇلارنىڭ ھالى) قانداق بولىدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,27,' بۇ (ئازاب) شۇنىڭ ئۈچۈندۇركى، ئۇلار اﷲ نى دەرغەزەپ قىلىدىغان نەرسىلەرگە ئەگەشتى، اﷲ نىڭ رازىلىقىنى (يەنى اﷲ نى رازى قىلىدىغان ئىشلارنى) ياقتۇرمىدى، شۇنىڭ بىلەن اﷲ ئۇلارنىڭ ئەمەللىرىنى بىكار قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,28,' دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلار) اﷲ ئۆزلىرىنىڭ (ئىسلامغا بولغان) دۈشمەنلىكىنى ئاشكارىلىمايدۇ دەپ ئويلامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,29,' ئەگەر بىز خالىساق، ئۇلارنى چوقۇم ساڭا بىلدۈرەتتۇق، چوقۇم ئۇلارنىڭ سىماسىدىن تونۇيتتۇڭ، سەن ئەلۋەتتە سۆزىنىڭ ئۇسلۇبىدىن ئۇلارنى تونۇيسەن، اﷲ سىلەرنىڭ ئەمەللىرىڭلارنى بىلىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,30,' بىز سىلەرنى ئەلۋەتتە (جىھادقا ئەمر قىلىش ۋە مۇشەققەتلىك ئىشلارغا تەكلىپ قىلىش بىلەن) سىنايمىز، تاكى سىلەرنىڭ ئىچىڭلاردىن (اﷲ نىڭ يولىدا) جىھاد قىلغۇچىلارنى ۋە (جىھادنىڭ مۇشەققەتلىرىگە) چىداشلىق بەرگۈچىلەرنى بىلگەنگە، سىرلىرىڭلاردىن ۋاقىپ بولغانغا قەدەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,31,' شۈبھىسىزكى، كاپىر بولغانلار، (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلار، توغرا يول (يەنى رەسۇلۇللاھنىڭ ھەق ئىكەنلىكى) ئېنىق بولغاندىن كېيىن پەيغەمبەر بىلەن دۈشمەنلەشكەنلەر اﷲ قا قىلچە زىيان يەتكۈزەلمەيدۇ، اﷲ ئۇلارنىڭ (سەدىقە بەرگەنگە ئوخشاش) ئەمەللىرىنى بىكار قىلىدۇ (شۇنىڭ بىلەن ئاخىرەتتە ئۇنىڭ ساۋابىغا ئېرىشەلمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,32,' ئى مۆمىنلەر! اﷲ قا ئىتائەت قىلىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار، ئەمەللىرىڭلارنى (كۇفرى، نىفاق ۋە رىيا بىلەن) بىكار قىلىۋەتمەڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,33,' شۈبھىسىزكى، كاپىر بولغان ۋە (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلار، ئاندىن كاپىر پىتى ئۆلگەنلەرنى اﷲ ھەرگىز مەغپىرەت قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,34,' (كاپىرلار بىلەن جەڭ قىلغان چېغىڭلاردا) بوشاپ قالماڭلار، (كاپىرلارنى) سۈلھكە چاقىرماڭلار، ھالبۇكى، سىلەر غالىپتۇرسىلەر، اﷲ سىلەر بىلەن بىرگىدۇر، اﷲ ھەرگىز ئەمەلىڭلارنىڭ (ساۋابىنى) كېمەيتىۋەتمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,35,' دۇنيا تىرىكچىلىكى پەقەت ئويۇن ۋە تاماشادىن ئىبارەتتۇر، ئەگەر سىلەر ئىمان ئېيتساڭلار ۋە تەقۋادارلىق قىلساڭلار (اﷲ) سىلەرگە ئەجرىڭلارنى بېرىدۇ، مال ـ مۈلكۈڭلارنىڭ ھەممىسىنى (بېرىشنى) سورىمايدۇ (پەقەت زاكاتنى بېرىشىڭلارنى سورايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,36,' ئەگەر ئۇ سىلەردىن مال ـ مۈلكۈڭلارنى جىددى سورىسا، بېخىللىق قىلىسىلەر، بۇ بېخىللىق سىلەرنىڭ (ئىسلامغا بولغان) ئاداۋىتىڭلارنى ئاشكارىلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,49,37,' سىلەر شۇنداق كىشىلەرسلەركى، اﷲ نىڭ يولىدا (پۇل ـ مال) سەرپ قىلىشقا چاقىرىلساڭلار، سىلەرنىڭ ئاراڭلاردا بېخىللىق قىلىدىغانلار بار، كىمكى بېخىللىق قىلىدىكەن، ئۇ ئۆزىنىڭ زىيىنى ئۈچۈن بېخىللىق قىلىدۇ، اﷲ (سىلەرنىڭ مال ـ مۈلكۈڭلاردىن) بىھاجەتتۇر، سىلەر بولساڭلار (اﷲ قا) موھتاجسىلەر، ئەگەر سىلەر (اﷲ نىڭ تائىتىدىن) يۈز ئۆرۈسەڭلار، اﷲ سىلەرنىڭ ئورنۇڭلارغا باشقا بىر قەۋمنى ئالماشتۇرىدۇ، ئۇلار سىلەرگە ئوخشاش بولمايدۇ (بەلكى اﷲ قا ئىتائەت قىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,1,'اﷲ نىڭ سېنىڭ ئىلگىرىكى ۋە كېيىنكى گۇناھلىرىڭنى كەچۈرۈشى ئۈچۈن، ساڭا بەرگەن نېمىتىنى مۇكەممەللەشتۈرۈشى ئۈچۈن، سېنى توغرا يولغا باشلىشى ئۈچۈن، ساڭا كۈچلۈك ياردەم بېرىشى ئۈچۈن، ساڭا بىز ھەقىقەتەن روشەن غەلىبە ئاتا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,2,' مۆمىنلەرنىڭ ئىمانىغا ئىمان قوشۇلۇشى ئۈچۈن، اﷲ ئۇلارنىڭ دىللىرىغا تەمكىنلىكنى چۈشۈردى، ئاسمانلارنىڭ ۋە زېمىننىڭ (پەرىشتىلەردىن، جىنلاردىن، ھايۋانلاردىن ۋە تەبىئەت ھادىسىلىرىدىن بولغان) قوشۇنلىرى اﷲ نىڭدۇر، اﷲ (خەلقنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,3,' (مۆمىنلەرنىڭ دىللىرىغا اﷲ نىڭ تەمكىنلىك چۈشۈرۈشى) ئەر ـ ئايال مۆمىنلەرنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان، ئۇلار مەڭگۈ قالىدىغان جەننەتلەرگە كىرگۈزۈشى ئۈچۈندۇر، اﷲ نىڭ ئۇلارنىڭ گۇناھلىرىنى كەچۈرۈشى ئۈچۈندۇر، اﷲ نىڭ دەرگاھىدا، بۇ زور مۇۋەپپەقىيەتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,4,' (مۆمىنلەرنىڭ دىللىرىغا اﷲ نىڭ تەمكىنلىك چۈشۈرۈشى) مۇناپىق ۋە مۇناپىقەلەرگە، مۇشرىك ۋە مۇشرىكەلەرگە ئازاب قىلىش ئۈچۈندۇر، ئۇلار اﷲ قا قارىتا (اﷲ پەيغەمبىرىگە ۋە مۆمىنلەرگە ياردەم) بەرمەيدۇ دەيدىغان يامان ئويلاردا بولدى، (ئۇلار مۆمىنلەرگە يەتسۇن دېگەن) ھالاكەت ئۇلارنىڭ ئۆزلىرىگە يېتىدۇ اﷲ ئۇلارغا غەزەپ قىلدى، ئۇلارنى رەھمىتىدىن يىراق قىلدى، ئۇلارغا جەھەننەمنى تەييارلىدى، جەھەننەم نېمىدېگەن يامان جاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,5,' ئاسمانلارنىڭ ۋە زېمىننىڭ قوشۇنلىرى اﷲ نىڭدۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,6,' بىز ھەقىقەتەن سېنى (قىيامەت كۈنى خەلققە) گۇۋاھلىق بەرگۈچى، (مۆمىنلەرگە جەننەت بىلەن) خۇش خەۋەر بەرگۈچى، (كاپىرلارنى ئازابتىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,7,' سىلەرنىڭ اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان ئېيتىشىڭلار ئۈچۈن، ئۇنى (يەنى پەيغەمبەرنى) ئۇلۇغلىشىڭلار ۋە ھۆرمەتلىشىڭلار ئۈچۈن، ئەتىگەندە ۋە ئاخشامدا تەسبىھ ئېيتىشىڭلار ئۈچۈن (بىز پەيغەمبەرنى ئەۋەتتۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,8,' شۈبھىسىزكى، (ئى مۇھەممەد! ھۇدەيبىيىدە) ساڭا (رىزۋان) بەيئىتىنى قىلغانلار (ھەقىقەتتە) اﷲ قا بەيئەت قىلغان بولىدۇ، اﷲ نىڭ قولى ئۇلارنىڭ قولىنىڭ ئۈستىدىدۇر (يەنى اﷲ ئۇلارنىڭ قىلغان بەيئىتىنى كۆرۈپ تۇرغۇچىدۇر، اﷲ ئۇلارنى بەيئەت قىلغانلىقى ئۈچۈن مۇكاپاتلايدۇ)، كىمكى ئەھدىنى بۇزىدىكەن، ئەھدىنى بۇزغانلىقىنىڭ زېيىنى ئۇنىڭ ئۆزىگە بولىدۇ، اﷲ بىلەن قىلغان ئەھدىسىگە ۋاپا قىلغانلارغا اﷲ بۈيۈك ئەجىر ئاتا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,9,' (ھۇدەيبىيىگە چىقماي) قېلىپ قالغان (مۇناپىق) ئەئرابىلار ساڭا: «بىز ماللىرىمىز، بالا ـ چاقىلىرىمىز بىلەن بولۇپ قېلىپ (سەن بىلەن چىقالمىدۇق)، بىز ئۈچۈن مەغپىرەت تەلەپ قىلغىن» دەيدۇ، ئۇلارنىڭ تىلى بىلەن ئېيتقىنى دىلىدىكى سۆز ئەمەس (يەنى ئۇلار يالغان ئۆزرە ئېيتىدۇ) ئېيتقىنكى، «ئەگەر اﷲ سىلەرگە بىرەر زىياننى ياكى پايدىنى ئىرادە قىلسا، كىم سىلەر ئۈچۈن اﷲ نىڭ ئالدىدا بىرەر نەرسىگە دال بولالايدۇ؟ (يەنى كىم سىلەردىن اﷲ نىڭ خاھىشى ۋە قازاسىنى توسىيالايدۇ؟) ئۇنداق ئەمەس (يەنى ئىش سىلەر ئويلىغاندەك ئەمەس)، اﷲ سىلەرنىڭ قىلمىشىڭلاردىن تولۇق خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,10,' بەلكى سىلەر، پەيغەمبەر ۋە مۆمىنلەر ئائىلىسىگە مەڭگۈ قايتمايدۇ، دەپ گۇمان قىلدىڭلار، بۇ (گۇمراھلىق) دىللىرىڭلاردا چىرايلىق تۇيۇلدى، سىلەر يامان گۇماندا (يەنى ئۇلار ئۆلتۈرۈلۈپ بىرىمۇ ساق قايتمايدۇ دەيدىغان ئويدا) بولدۇڭلار، سىلەر ھالاك بولغۇچى قەۋم بولدۇڭلار»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,11,' كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىشەنمەيدىكەن (ئۇ كاپىردۇر)، شۈبھىسىزكى، بىز كاپىرلارغا دوزاخنى تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,12,' ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ خالىغان ئادەمگە مەغپىرەت قىلىدۇ، خالىغان ئادەمگە ئازاب قىلىدۇ، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,13,' (ھۇدەيبىيىگە چىقماي) قېلىپ قالغانلار غەنىمەت ئېلىش ئۈچۈن چىققان ۋاقىتلىرىدا: «بىزمۇ سىلەر بىلەن چىقايلى» دەيدۇ، ئۇلار اﷲ نىڭ (خەيبەر غەنىمىتىنى ھۇدەيبىيىگە چىققانلارغا خاس قىلغانلىقتىن) ئىبارەت سۆزىنى ئۆزگەرتمەكچى بولىدۇ (ئۇلارغا) ئېيتقىنكى، «بىز بىلەن چىقىشىڭلارغا ھەرگىز بولمايدۇ، اﷲ ئىلگىرى مۇشۇنداق دېگەن» ئۇلار: «ئۇنداق ئەمەس (يەنى اﷲ ئۇنداق دېمىگەن)، سىلەر بىزگە ھەسەت قىلىۋاتىسىلەر» دەيدۇ ئۇنداق ئەمەس، ئۇلار (اﷲ نىڭ كالامىنى) پەقەت يۈزەگىنە چۈشىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,14,' (ھۇدەيبىيىگە چىقماي) قېلىپ قالغان ئەئرابىلارغا: «سىلەر جەڭگىۋار بىر قەۋم بىلەن (ئۇرۇشۇشقا) چاقىرىلىسىلەر، ياكى ئۇلار (ئۇرۇشسىزلا) مۇسۇلمان بولىدۇ (ئۇلار بىلەن ئۇرۇشمايسىلەر)، ئەگەر سىلەر (بۇيرۇققا) بويسۇنساڭلار، اﷲ سىلەرگە چىرايلىق ئەجىر ئاتا قىلدۇ، ئەگەر سىلەر ئىلگىرى قېلىپ قالغاندەك، يەنە قېلىپ قالساڭلار، اﷲ سىلەرگە قاتتىق ئازاب قىلىدۇ» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,15,' (جىھادقا چىقمىسا) ئەماغا گۇناھ بولمايدۇ، توكۇرغىمۇ گۇناھ بولمايدۇ، كېسەلگىمۇ گۇناھ بولمايدۇ، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىدىكەن، ئۇنى اﷲ ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، كىمكى (ئۆزرىسىز جىھادتىن) باش تارتىدىكەن، اﷲ ئۇنى (دۇنيادا خورلۇق بىلەن، ئاخرەتتە ئوت بىلەن) قاتتىق ئازابلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,16,' اﷲ مۆمىنلەردىن ھەقىقەتەن رازى بولدى، (ئى مۇھەممەد!) ئۆز ۋاقتىدا ئۇلار (ھۇدەيبىيىدە) دەرەخ (سايىسى) ئاستىدا ساڭا بەيئەت قىلدى اﷲ ئۇلارنىڭ دىلىدىكىنى (يەنى راستلىق بىلەن ۋاپانى) بىلىدۇ، اﷲ ئۇلارغا (ئۇلار بەيئەت قىلىۋاتقاندا) تەمكىنلىك چۈشۈرۈپ بەردى ۋە ئۇلارنى يېقىن غەلىبە (يەنى خەيبەرنىڭ پەتھى قىلىنىشى) بىلەن ۋە ئۇلار ئالىدىغان نۇرغۇن غەنىمەتلەر بىلەن مۇكاپاتلىدى اﷲ غالىپتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,17,' اﷲ سىلەر ئالىدىغان نۇرغۇن غەنىمەتلەرنى سىلەرگە ۋەدە قىلدى، بۇنى (يەنى خەيبەرنىڭ غەنىمىتىنى) ھەممىدىن بۇرۇن ئاتا قىلدى، سىلەرنى دۈشمەنلەرنىڭ قول سېلىشىدىن ساقلىدى، (غەنىمەتلەر، مەككىنىڭ پەتھى قىلىنىشى، مەسجىدى ھەرەمغا كىرىش) مۆمىنلەرگە نىشان بولۇپ قېلىشى ئۈچۈن ۋە سىلەرنى توغرا يولغا باشلاش ئۈچۈن (اﷲ شۇنداق قىلدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,18,' اﷲ يەنە ئۆز كۈچۈڭلار بىلەن قولغا كەلتۈرەلمەيدىغان، اﷲ (ئۇنىڭ سىلەرگە مۇيەسسەر بولىدىغانلىقىنى) تولۇق بىلگەن بىر غەنىمەت (يەنى مەككىنىڭ پەتھى قىلىنىشى) نى (سىلەرگە مۇيەسسەر قىلدى) اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,19,' ئەگەر كاپىرلار سىلەر بىلەن (ھۇدەيبىيىدە) ئۇرۇشسا، ئەلۋەتتە، ئارقىسىغا قاراپ قاچاتتى، ئاندىن (ئۆزلىرىگە) ھېچ ئىگە ۋە ياردەمچى تاپالمايتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,20,' بۇ (يەنى كاپىرلارنى مەغلۇپ قىلىش، مۆمىنلەرنى غەلىبە قىلدۇرۇش) اﷲ نىڭ ئىلگىرىدىن تارتىپ تۇتۇپ كېلىۋاتقان يولىدۇر، اﷲ نىڭ يولىدا ھېچقانداق ئۆزگىرىش تاپالمايسەن (يەنى اﷲ نىڭ تۇتقان يولى ئۆزگەرمەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,21,' اﷲ ئۇلارنى سىلەرگە قول سېلىشتىن توستى ھەمدە ئۇلار ئۈستىدىن غەلىبە قىلغىنىڭلاردىن كېيىن، مەككىنىڭ ئىچىدە (يەنى ھۇدەيبىيىدە) سىلەرنى ئۇلارغا قول سېلىشىڭلاردىن توستى، اﷲ قىلدىغان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,22,' ئۇلار كاپىر بولدى، سىلەر مەسجىدى ھەرەمدىن ۋە تۇتۇپ تۇرۇلغان قۇربانلىقنىڭ ئۆز جايىغا يېتىشىدىن توستى، (مەككىدە) سىلەر بىلمەيدىغان ئەر ـ ئايال مۆمىنلەر بولۇپ، سىلەر بىلمەستىن ئۇلارنى ئۆلتۈرۈپ قويۇش بىلەن گۇناھقا گىرىپتار بولۇشۇڭلارنىڭ خەۋپى بولمىسا ئىدى (ئەلۋەتتە اﷲ مەككىگە كىرىشىڭلارغا رۇخسەت قىلاتتى ۋە سىلەرنى كاپىرلارغا مۇسەللەت قىلاتتى)، اﷲ خالىغان ئادەمنى رەھمىتى (دائىرىسىگە) كىرگۈزۈش ئۈچۈن (سىلەرنى مەككىنى پەتھى قىلىشتىن توستى)، ئەگەر ئۇلار ئايرىلسا (يەنى مۆمىنلەر كاپىرلاردىن ئايرىلسا)، ئۇلارنىڭ ئارىسىدىكى كاپىرلارنى قاتتىق ئازاب بىلەن ئازابلايتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,23,' كاپىرلار قىزىققانلىقىنى – جاھىلىيەت دەۋرىنىڭ قىزىققانلىقىنى – دىللىرىغا پۈككەن چاغدا، اﷲ پەيغەمبىرىگە ۋە مۆمىنلەرگە تەمكىنلىكنى چۈشۈردى ئۇلارغا تەقۋا كەلىمىسىنى (يەنى كەلىمە تەۋھىدنى) ئىختىيار قىلدى مۆمىنلەر تەقۋا كەلىمىسىگە ئەڭ لايىق ئىدى ۋە ئۇنىڭ ئەھلى ئىدى، اﷲ ھەر نەرسىنى بىلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,24,' اﷲ پەيغەمبىرىگە، ھەق رەۋىشتە، ھەقىقىي راست چۈش كۆرسەتتى (ئى مۇھەممەد! سەن ۋە ساھابىلىرىڭ) خۇدا خالىسا (دۈشمەندىن) ئەمىن بولغان، (بەزىلىرىڭلار) باشلىرىڭلارنى چۈشۈرگەن ۋە (بەزىلىرىڭلار) قىرقىغان ھالدا، قورقماي چوقۇم مەسجىدى ھەرەمغا كىرىسىلەر، اﷲ (سۈلھتىكى) سىلەر بىلمەيدىغان (پايدىنى) بىلىدۇ اﷲ بۇنىڭدىن بۇرۇن (سىلەرگە) يېقىن بىر غەلىبىنى (يەنى خەيبەرنىڭ پەتھىسىنى مۇيەسسەر) قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,25,' اﷲ ھەق دىننى پۈتۈن دىنلاردىن ئۈستۈن قىلىش ئۈچۈن، پەيغەمبىرىنى ھىدايەت بىلەن ۋە ھەق دىن بىلەن ئەۋەتتى (مۇھەممەد ئەلەيھىسسالامنىڭ اﷲ نىڭ پەيغەمبىرى ئىكەنلىكىگە) گۇۋاھلىق بېرىشكە اﷲ يېتەرلىكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,50,26,' مۇھەممەد اﷲ نىڭ رەسۇلىدۇر، ئۇنىڭ بىلەن بىرگە بولغان مۆمىنلەر كۇففارلارغا قاتتىقتۇر، ئۆزئارا كۆيۈمچاندۇر، ئۇلارنى رۇكۇ قىلغان، سەجدە قىلغان ھالدا كۆرىسەن ئۇلار اﷲ نىڭ پەزلىنى ۋە رازىلىقىنى تىلەيدۇ، ئۇلارنىڭ يۈزلىرىدە سەجدىنىڭ ئەسىرىدىن نىشانلار بار، ئەنە شۇ ئۇلارنىڭ تەۋراتتىكى سۈپىتىدۇر، ئۇلارنىڭ ئىنجىلدىكى سۈپىتى بولسا (ئۇلار) شاخ چىقارغان، كۈچلىنىپ چوڭايغان، ئاندىن ئۆز غولى بىلەن ئۆرە تۇرغان، بولۇقلۇقى ۋە كۆركەم كۆرۈنۈشى بىلەن، دېھقانلارنى مەمنۇن قىلغان بىر زىرائەتكە ئوخشايدۇ، (مۆمىنلەرنى مۇنداق زىرائەتكە ئوخشىتىش) كۇففارلارنى خاپا قىلىش ئۈچۈندۇر، اﷲ ئۇلارنىڭ ئىچىدىن ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا مەغپىرەت ۋە كاتتا ئەجىر ۋەدە قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,1,'ئى مۆمىنلەر! سىلەر اﷲ نىڭ ۋە اﷲ نىڭ پەيغەمبىرىنىڭ ئالدىدا (ھېچقانداق بىر ئىشنى ۋە سۆزنى) ئالدى بىلەن قىلماڭلار، اﷲ تىن قورقۇڭلار، اﷲ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيىتىڭلارنى ۋە ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,2,' ئى مۆمىنلەر! (رەسۇلۇللاھقا سۆز قىلغان چېغىڭلاردا) ئاۋازىڭلارنى پەيغەمبەرنىڭ ئاۋازىدىن يۇقىرى قىلماڭلار، سىلەر بىر ـ بىرىڭلار بىلەن (توۋلاپ) يۇقىرى ئاۋازدا سۆزلەشكەندەك، پەيغەمبەرگە (توۋلاپ) يۇقىرى ئاۋازدا سۆز قىلماڭلار، (ئۇنداق قىلساڭلار) ئۆزلىرىڭلار تۇيماستىن، (قىلغان ياخشى) ئەمەللىرىڭلار بىكار بولۇپ كېتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,3,' شۈبھىسىزكى، رەسۇلۇللاھنىڭ يېنىدا پەس ئاۋازدا گەپ قىلغانلارنىڭ دىللىرىنى اﷲ تەقۋا ئۈچۈن سىنىغان، ئۇلار مەغپىرەتكە ۋە كاتتا ساۋابقا (يەنى جەننەتكە) ئېرىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,4,' شۈبھىسىزكى، سېنى ھۇجرىلار (يەنى پاك ئاياللىرىڭنىڭ تۇرالغۇ جايلىرى) ئارقىسىدىن توۋلايدىغانلارنىڭ تولىسى ئەقىلسىز كىشىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,5,' سەن ئۇلارنىڭ ئالدىغا چىققىنىڭغا قەدەر ئۇلار سەۋر قىلىپ تۇرۇشسا، ئۇلار ئۈچۈن ئەلۋەتتە ياخشى بولاتتى، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,6,' ئى مۆمىنلەر! ئەگەر سىلەرگە بىر پاسىق ئادەم بىرەر خەۋەر ئېلىپ كەلسە، (ئىشنىڭ ھەقىقىتىنى) بىلمەستىن بىرەر قەۋمنى رەنجىتىپ قويۇپ، قىلمىشىڭلارغا پۇشايمان قىلىپ قالماسلىقىڭلار ئۈچۈن، (ئۇ خەۋەرنى) ئېنىقلاپ كۆرۈڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,7,' بىلىڭلاركى، ئىچىڭلاردا رەسۇلۇللاھ (يەنى اﷲ نىڭ پەيغەمبىرى) بار، ئەگەر ئۇ نۇرغۇن ئىشلاردا سىلەرگە ئىتائەت قىلىدىغان بولسا، چوقۇم قىيىن ئەھۋالدا قالاتتىڭلار، لېكىن اﷲ سىلەرگە ئىماننى قىزغىن سۆيگۈزدى ۋە ئۇنى دىلىڭلاردا كۆركەم قىلدى، سىلەرگە پىسقىنى، كۇفرىنى ۋە گۇناھنى يامان كۆرسەتتى، ئەنە شۇلار اﷲ نىڭ پەزلى ۋە نېمىتى بىلەن توغرا يولدا بولغۇچىلاردۇر اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,8,' ئەگەر مۆمىنلەردىن ئىككى گۇرۇھ ئۇرۇشۇپ قالسا، ئۇلارنىڭ ئارىسىنى تۈزەپ قويۇڭلار، ئەگەر ئۇلارنىڭ بىرى ئىككىنچىسىگە تاجاۋۇز قىلسا، تاجاۋۇز قىلغۇچى تاكى اﷲ نىڭ ھۆكمىگە قايتقانغا قەدەر (يەنى تاجاۋۇزىنى توختاتقانغا قەدەر) ئۇنىڭ بىلەن ئۇرۇشۇڭلار، ئەگەر ئۇلار (اﷲ نىڭ ئەمرىگە) قايتسا، ئۇلارنىڭ ئارىسىنى (ھېچبىر تەرەپكە يان باسماستىن) ئادىللىق بىلەن تۈزەپ قويۇڭلار، (ھەممە ئىشتا) ئادىل بولۇڭلار، اﷲ ھەقىقەتەن ئادىللارنى دوست تۇتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,9,' مۆمىنلەر ھەقىقەتەن (دىندا) قېرىنداشلاردۇر، (ئۇرۇشۇپ قالسا) قېرىنداشلىرىڭلارنىڭ ئارىسىنى تۈزەڭلار، رەھمەتكە ئېرىشىشىڭلار ئۈچۈن، اﷲ تىن قورقۇڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,10,' ئى مۆمىنلەر! بىر قەۋم يەنە بىر قەۋمنى (يەنى بىر جامائە يەنە بىر جامائەنى، بىر ئادەم يەنە بىر ئادەمنى) مەسخىرە قىلمىسۇن، مەسخىرە قىلىنغان قەۋم (اﷲ نىڭ نەزىرىدە) مەسخىرە قىلغۇچى قەۋمدىن ياخشىراق بولۇشى مۇمكىن، سىلەرنىڭ ئاراڭلاردىكى ئاياللارمۇ ئۆزئارا مەسخىرە قىلىشمىسۇن، مەسخىرە قىلىنغۇچى ئاياللار (اﷲ نىڭ دەرگاھىدا) مەسخىرە قىلغۇچى ئاياللاردىن ياخشىراق بولۇشى مۈمكىن، بىر ـ بىرىڭلارنى ئەيىبلىمەڭلار، بىر ـ بىرىڭلارنى يامان لەقەم بىلەن چاقىرماڭلار، ئىماندىن كېيىن پىسقى بىلەن ئاتاش (يەنى مۆمىننى پاسىق) دەپ ئاتاش نېمىدېگەن يامان! (مۇنداق نەھيى قىلىنغان ئىشلاردىن) تەۋبە قىلمىغانلار زالىملاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,11,' ئى مۆمىنلەر! نۇرغۇن گۇمانلاردىن ساقلىنىڭلار (يەنى ئائىلىدىكىلىرىڭلارغا ۋە كىشىلەرگە گۇمانخورلۇق قىلماڭلار)، بەزى گۇمانلار ھەقىقەتەن گۇناھتۇر، (مۆمىنلەرنىڭ ئەيىبىنى) ئىزدىمەڭلار، بىر ـ بىرىڭلارنىڭ غەيۋىتىنى قىلماڭلار، سىلەرنىڭ بىرىڭلار ئۆلگەن قېرىندىشىڭلارنىڭ گۆشىنى يېيىشنى  ياقتۇرامسىلەر؟ ئۇنى ياقتۇرمايسىلەر اﷲ تىن قورقۇڭلار اﷲ (تەۋبەقىلغۇچىلارنىڭ قىلغان) تەۋبىسىنى بەكمۇ قۇبۇل قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,12,' ئى ئىنسانلار! سىلەرنى بىز ھەقىقەتەن بىر ئەر، بىر ئايالدىن، (ئادەم بىلەن ھەۋۋادىن ئىبارەت)بىر ئاتا بىر ئانىدىن ياراتتۇق ئۆز ئارا تونۇشۇشۇڭلار ئۈچۈن سىلەرنى نۇرغۇن مىللەت ۋە ئۇرۇق قىلدۇق، ھەقىقەتەن ئەڭ تەقۋادار بولغانلىرىڭلار اﷲ نىڭ دەرگاھىدا ئەڭ ھۆرمەتلىك ھېسابلىنىسىلەر (يەنى كىشىلەرنىڭ بىر ـ بىرىدىن ئارتۇق بولۇشى نەسەب بىلەن ئەمەس، تەقۋادارلىق بىلەن بولىدۇ)، اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىدىن خەۋەرداردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,13,' ئەئرابىلار: «ئىمان ئېيتتۇق» دەيدۇ، (ئۇلارغا) ئېيتقىنكى، «(تېخى) ئىمان ئېيتمىدىڭلار ۋە لېكىن سىلەر بويسۇندۇق دەڭلار، ئىمان تېخى دىلىڭلارغا كىرمىدى، ئەگەر سىلەر اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلساڭلار، اﷲ سىلەرنىڭ ئەمەلىڭلاردىن ھېچ نەرسىنى كېمەيتىۋەتمەيدۇ، اﷲ ھەقىقەتەن(مۆمىنلەرگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,14,' شۈبھىسىزكى، (ھەقىقي) مۆمىنلەر اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان كەلتۈرگەن، ئاندىن (ئىمانىدا) شەك كەلتۈرمىگەن، ماللىرى بىلەن، جانلىرى بىلەن اﷲ نىڭ يولىدا جىھاد قىلغانلاردۇر، ئەنە شۇلار (ئىمان دەۋاسىدا) راستچىللاردۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,15,' (ئى مۇھەممەد!) ئېيتقىنكى، «اﷲ قا دىنىڭلارنى (يەنى دىنىڭلارنى دىلىڭلارنىڭ تەستىق قىلغانلىقىنى) خەۋەر قىلامسىلەر؟ ھالبۇكى، اﷲ ئاسمانلاردىكىنى ۋە زېمىندىكىنى بىلىپ تۇرىدۇ، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,16,' ئۇلار ساڭا مۇسۇلمان بولغانلىقىنى مىننەت قىلىدۇ، (ئۇلارغا) «سىلەر ماڭا مۇسۇلمان بولغانلىقىڭلارنى مىننەت قىلماڭلار، بەلكى اﷲ سىلەرنى ئىمانغا ھىدايەت قىلغانلىقىنى مىننەت قىلىدۇ، ئەگەر سىلەر راست (مۆمىن) بولساڭلار» دېگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,51,17,' اﷲ ھەقىقەتەن ئاسمانلاردىكى ۋە زېمىندىكى غەيىبنى بىلىپ تۇرىدۇ، اﷲ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,1,'قاف شەرەپلىك قۇرئان بىلەن قەسەم قىلىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,2,' بەلكى ئۇلار (يەنى مۇشرىكلار) ئۆز ئىچىدىن ئۇلارغا بىر ئاگاھلاندۇرغۇچى كەلگەنلىكىدىن ئەجەبلەندى، كاپىرلار ئېيتتى: «بۇ قىزىق ئىشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,3,' بىز ئۆلۈپ توپا بولۇپ كەتكەندىن كېيىن (تىرىلىپ ئىلگىرىكى ھالىتىمىزگە قايتامدۇق)، بۇنداق قايتىش (ئەقىلدىن) يىراقتۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,4,' ئۇلارنى زېمىننىڭ كېمەيتكىنىدىن (يەنى ئۇلارنىڭ جەسەتلىرنى يەر يەپ كەتكىنىدىن) بىز ھەقىقەتەن خەۋەردارمىز، بىزنىڭ دەرگاھىمىزدا ھەممىنى ئۆز ئىچىگە ئالغان كىتاب (يەنى لەۋھۇلمەھپۇز) بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,5,' بەلكى ئۇلار بولسا ئۆزلىرىگە نازىل قىلىنغان ھەقىقەتنى (قۇرئاننى) ئىنكار قىلدى، ئۇلار قالايمىقان ھالەتتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,6,' ئۇلار ئاسماننى قانداق (تۈۋرۈكسىز) بەرپا قىلغانلىقىمىزغا، ئۇنى (يۇلتۇزلار بىلەن) بېزىگەنلىكىمىزگە، ئۇنىڭدا ھېچقانداق يوچۇق يوقلۇقىغا قارىمامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,7,' يەر يۈزىنى (توغرىسىغا ۋە ئۇزۇنىسىغا سوزۇپ) كەڭ ياراتتۇق، يەر يۈزىدە (ئۇنىڭ تەۋرەپ كەتمەسلىكى ئۈچۈن) تاغلارنى قىلدۇق ۋە ئۇنىڭدا تۈرلۈك چىرايلىق ئۆسۈملۈكلەرنى ئۆستۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,8,' اﷲ قا قايتقۇچى ھەر بىر بەندىگە (اﷲ نىڭ قۇدرىتىنى) كۆرسىتىش ئۈچۈن ۋە (اﷲ نىڭ ئۇلۇغلۇقىنى) ئەسلىتىش ئۈچۈن (شۇنداق قىلدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,9,' كۆكتىن (بۇلۇتتىن) مۇبارەك سۇنى چۈشۈردۇق، ئۇنىڭ بىلەن باغلارنى ۋە ئاشلىقلارنى، ئېگىز ئۆسكەن، مېۋىلىرى سانجاق ـ سانجاق بولۇپ كەتكەن خورمىلارنى ئۆستۈردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,10,' (ئۇلارنىڭ ھەممىسىنى) بەندىلەرنىڭ رىزقى ئۈچۈن (ئۆستۈردۇق)، ئۇ سۇ بىلەن ئۆلۈك (يەنى قاقاس) زېمىننى تىرىلدۈردۇق، ئۆلۈكلەرنىڭ تىرىلىشىمۇ شۇنىڭغا ئوخشاشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,11,' ئۇلاردىن ئىلگىرى (پەيغەمبەرلىرىنى) ئىنكار قىلغانلار نۇھنىڭ قەۋمى رەس ئاھالىسى، سەمۇد (قەۋمى)، ئاد (قەۋمى)، پىرئەۋن، لۇتنىڭ قىرىنداشلىرى، ئەيكە ئاھالىسى (يەنى شۇئەيب قەۋمى) تۈببە قەۋمى ئىدى، ئۇلارنىڭ ھەممىسى پەيغەمبەرلىرنى ئىنكار قىلدى، شۇنىڭ بىلەن مېنىڭ ئازابىم (ئۇلارغا) تېگىشلىك بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,12,' (ئىنسانلارنى) دسلەپتە يارىتىشتىن ئاجىز كەلدۇقمۇ؟ ھەرگىز ئۇنداق ئەمەس) بىز بۇنىڭدا ئاجىزلىق قىلمىغاچقا، ئۆلگەندىن كېيىن تىرىلدۈرۈشتىمۇ ئاجىزلىق قىلمايمىز) ئۇلار يېڭىدىن يارىتىلشقا (يەنى قىيامەتتە تىرىلدۈرۈشكە) قارىتا گۇماندىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,13,' بىز ھەقىقەتەن ئىنساننى ياراتتۇق، بىز ئۇنىڭ كۆڭلىگە كەلگەننى بىلىمىز، بىز ئۇنىڭ جان تومۇرىدىنمۇ يېقىنمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,14,' ئىنساننىڭ ئوڭ تەرىپىدە ۋە سول تەرىپىدە ئولتۇرۇپ خاتىرىلەيدىغان ئىككى پەرىشتە بار، (ئىنساننىڭ سۆز ـ ھەرىكىتى خاتىرىلىنىۋاتقان) ۋاقىتتا، ئۇ قانداق بىر سۆزنى قىلمىسۇن، ئۇنىڭ ئالدىدا ھامان پەرىشتە ھازىر بولۇپ، كۈزىتىپ تۇرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,15,' ئۆلۈمنىڭ سەكراتى ھەقىقەتنى ئېلىپ كەلدى (يەنى ئاخىرەتنى ئىنكار قىلغۇچى ئاخىرەتنىڭ دەھشىتىنى ئېنىق كۆرىدۇ)، سەن ئۇنىڭدىن (يەنى ئۆلۈمدىن) قاچاتتىڭ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,16,' سۇر چېلىنىدۇ، ئەنە شۇ (كۇففارلارغا) ئازاب ۋەدە قىلىنغان كۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,17,' ھەر ئىنسان (مەھشەرگاھقا) كېلىدۇ، ئۇنىڭ بىلەن (ئۇنى مەھشەرگاھقا) ھەيدىگۈچى ۋە (ئۇنىڭ ئەمەلىگە) گۇۋاھ بولغۇچى (ئىككى پەرىشتە) بىللە كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,18,' ئۇنىڭغا: «ئى ئىنسان! (دۇنيادىكى چېغىڭدا) بۇ (ئېغىر كۈن) دىن غەپلەتتە ئىدىڭ، ئەمدى پەردەڭنى سەندىن كۆتۈرۋەتتۇق، بۈگۈن كۆزۈڭ ئۆتكۈردۇر» دېيىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,19,' ئۇنىڭغا مۇئەككەل پەرىشتە: «مانا بۇ (نامە ـ ئەمال) ئالدىمدا تەيياردۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,20,' (اﷲ تائالا ئىككى پەرىشتىگە خىتاب قىلىپ) «ھەققە قارشى ياخشىلىقتىن توسقۇچى، ھەددىدىن ئاشقۇچى، (دىنغا) شەك كەلتۈرگۈچى، اﷲ قا باشقا مەبۇدنى شېرىك قىلغۇچى ھەر بىر كاپىرنى دوزاخقا تاشلاڭلار، ئۇنى قاتتىق ئازابقا تاشلاڭلار» (دەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,21,' ئۇنىڭ ھەمراھى (بولغان شەيتان): «پەرۋەردىگارىمىز! مەن ئۇنى ئازدۇرمىدىم، لېكىن (ئۇ ئۆز ئىختىيارى بىلەن) چوڭقۇر گۇمراھلىقتا بولدى» (دەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,22,' اﷲ: «مېنىڭ ئالدىمدا مۇنازىرىلەشمەڭلار (بۇ يەردە مۇنازىرە پايدا بەرمەيدۇ)، سىلەرنى مەن ئالدىنئالا (ئازابىمدىن) ئاگاھلاندۇرغان ئىدىم، مېنىڭ سۆزۈم ئۆزگەرمەيدۇ، مەن بەندىلەرگە زۇلۇم قىلغۇچى ئەمەسمەن» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,23,' ئۇ كۈندە بىز جەھەننەمگە: «توشتۇڭمۇ؟» دەيمىز، جەھەننەم: «يەنە بارمۇ» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,24,' جەننەت تەقۋادارلارغا يىراق بولمىغان جايغا يېقىنلاشتۇرۇلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,25,' مانا بۇ (نېمەتتىن) سىلەرگە ۋەدە قىلىنغان نەرسىلەر اﷲ نىڭ (تائىتىگە) قايتقۇچى، (ئەمرىنى) ساقلىغۇچى ھەر بىر (بەندە) ئۈچۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,26,' كىمكى مېھرىبان اﷲ نى كۆرمەي تۇرۇپ، ئۇنىڭدىن قورقسا ۋە تەۋبە قىلغۇچى قەلب بىلەن كەلسە (ئۇلارغا) «جەننەتكە ئامانلىق بىلەن كىرىڭلار، بۇ مەڭگۈ قېلىش كۈنىدۇر» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,27,' جەننەتتە ئۇلارغا خالىغان نەرسىلەر بار ۋە بىزنىڭ ھۇزۇرىمىزدا (ئۇلارغا) زىيادە (نېمەتلەر) بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,28,' بىز ئۇلاردىن (يەنى قۇرەيش كۇففارلىرىدىن) ئىلگىرى ئۇلارغا قارىغاندا كۈچلۈك بولغان نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلار (يەنى شۇ ئۈممەتلەر) ھەر قايسى جايلارنى كەزدى، (ئۇلارغا) ئۆلۈمدىن قاچىدىغان جاي تېپىلدىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,29,' بۇنىڭدا (ھەقىقەت ئۈستىدە پىكىر يۈرگۈزىدىغان قەلبكە ئىگە ئادەم ياكى (ۋەز ـ نەسىھەتكە) ھۇزۇرىي قەلب بىلەن قۇلاق سالىدىغان ئادەم ئۈچۈن ئەلۋەتتە ۋەز ـ نەسىھەت بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,30,' بىز ھەقىقەتەن ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئالتە كۈندە ياراتتۇق، بىز ھېچقانداق چارچاپ قالغىنىمىز يوق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,31,' (ئى مۇھەممەد!) كاپىرلارنىڭ سۆزلىرىگە سەۋر قىلغىن، كۈن چىقىشتىن ئىلگىرى ۋە كۈن پېتىشتىن ئىلگىرى پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن، ھەمدى ئېيتقىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,32,' كېچىدە ۋە سەجدىدىن كېيىن (يەنى پەرز نامازلارنىڭ ئارقىسىدا) تەسبىھ ئېيتقىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,33,' نىدا قىلغۇچى (يەنى ئىسراپىل) يېقىن جايدىن نىدا قىلىدىغان (يەنى ئۇنىڭ ئاۋازى ھەممىگە ئوخشاش يېتىدىغان) كۈندە (نىداغا) قۇلاق سالغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,34,' ئۇلار ھەقنى ئېلىپ كېلىدىغان ئاۋازنى (يەنى ئىككىنچى قېتىملىق سۇرنى) ئاڭلىغان كۈن ـ ئەنە شۇ (كۈن قەبرىلەردىن) چىقىش كۈنىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,35,' بىز ھەقىقەتەن كىشىلەرنى ۋاپات قىلدۇرىمىز ۋە تىرىلدۈرىمىز، ئاخىر بارىدىغان جاي بىزنىڭ دەرگاھىمىزدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,36,' ئۇ كۈندە يەر يېرىلىپ ئۇلار (قەبرىلىرىدىن) تېز چىقىپ كېلىدۇ، ئەنە شۇ يىغىش بىزگە ئاساندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,52,37,' بىز ئۇلارنىڭ (يەنى قۇرەيش كۇففارلىرىنىڭ) ئېيتىدىغان سۆزلىرىنى ئوبدان بىلىمىز، سەن ئۇلارنى (ئىسلامغا) زورلىغۇچى ئەمەسسەن، مېنىڭ ئاگاھلاندۇرۇشۇمدىن قورقىدىغانلارغا (قۇرئان بىلەن) ۋەز ـ نەسىھەت قىلغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,1,'(تۇپراقنى) سورىغۇچى شاماللار بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,2,'، (يامغۇرلارنىڭ) ئېغىرلىقىنى كۆتۈرگۈچى بۇلۇتلار بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,3,'، (سۇ ئۈستىدە) يىنىك ماڭغۇچى كېمىلەر بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,4,'، ئىشنى (يەنى بەندىلەر ئارىسىدا رىزىقلارنى، يۇرتلار ئارىسىدا يامغۇرلارنى) تەقسىم قىلغۇچى پەرىشتىلەر قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,5,'، سىلەرگە ۋەدە قىلىنغان (ساۋاب، ئازاب، قىيامەت قاتارلىق) لار ئەلۋەتتە راستتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,6,' (ياخشى ئەمەللەرنى) مۇكاپاتلاش، (يامان ئەمەللەرنى) جازالاش چوقۇم بولىدىغان ئىشتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,7,' يوللىرى بار ئاسمان بىلەن قەسەمكى، (پەيغەمبەر ھەققىدە) سىلەرنىڭ سۆزۈڭلار ھەقىقەتەن خىلمۇخىلدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,8,' (اﷲ تائالانىڭ ئىلمىي ئەزەلىسىدە ھىدايەتتىن) بۇرۇۋېتىلگەن ئادەم (قۇرئانغا ۋە مۇھەممەد ئەلەيھىسسالامغا ئىمان كەلتۈرۈشتىن) بۇرۇۋېتىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,9,' كاززاپلارغا لەنەت بولسۇنكى، ئۇلار جاھالەتكە چۆمگەن بولۇپ (ئاخىرەت ئىشىدىن) غەپلەتتىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,10,' ئۇلار رەسۇلۇللاھتىن: «قىيامەت قاچان بولىدۇ؟» دەپ سورايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,11,' ئۇ بولسا ئۇ كۈندە دوزاختا ئازابلىنىدىغان كۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,12,' (دوزاخقا مۇئەككەل پەرىشتىلەر ئۇلارغا) «سىلەرگە قىلىنغان ئازابنى تېتىڭلار، مانا بۇ سىلەر (دۇنيادىكى چېغىڭلاردا) ئالدىراتقان نەرسىدۇر» (دەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,13,' شۈبھىسىزكى، تەقۋادارلار جەننەتلەردە ۋە (ئېقىپ تۇرغان) بۇلاقلارنىڭ ئارىسىدا بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,14,' ئۇلار پەرۋەردىگارى ئاتا قىلغان نەرسىلەرنى قوبۇل قىلىدۇ، ئۇلار بۇنىڭدىن ئىلگىرى (يەنى دۇنيادىكى چاغدا) ياخشى ئىش قىلغۇچىلار ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,15,' ئۇلار كېچىسى ئاز ئۇخلايتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,16,' ئۇلار سەھەرلەردە (پەرۋەردىگارىدىن) مەغپىرەت تىلەيتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,17,' ئۇلارنىڭ پۇل ـ ماللىرىدا سائىللار ۋە (ئىپپەتلىكىدىن سائىللىق قىلمايدىغان) موھتاجلار ھوقۇققا ئىگە ئىدى (يەنى ئۇلار سائىللارغا ۋە موھتاجلارغا سەدىقە قىلاتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,18,' زېمىندا ۋە ئۆزۈڭلاردا اﷲ قا چىن ئېتىقاد قىلىدىغانلار ئۈچۈن ) اﷲ نىڭ قۇدرىتىنى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار، (بۇنى) كۆرمەمسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,19,' ئاسماندا سىلەرنىڭ رىزقىڭلار بار، سىلەرگە ۋەدە قىلىنغان ساۋاب بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,20,' ئاسماننىڭ ۋە زېمىننىڭ پەرۋەردىگارى بىلەن قەسەمكى، سىلەرگە ۋەدە قىلىنغان نەرسە ئۆزۈڭلارنىڭ سۆزلەۋاتقىنىڭلاردەك (يەنى سۆزلىشىڭلارنىڭ ئۆزۈڭلارغا ئېنىق بولغىنىدەك) ھەقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,21,' (ئى مۇھەممەد!) ساڭا ئىبراھىمنىڭ ھۆرمەتلىك مېھمانلىرىنىڭ خەۋىرى يەتتىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,22,'ئۆز ۋاقتىدا ئۇلار ئىبراھىمنىڭ يېنىغا كىرىپ سالام دېدى، ئىبراھىم سالامنى ئىلىك ئالدى (ئىچىدە) «ناتونۇش ئادەملەرغۇ» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,23,' شۇنىڭ بىلەن ئۇ ئاستا ئائىلىسىگە چىقىپ (پىشۇرۇلغان) بىر سېمىز موزاينى ئېلىپ كىرىپ ئۇلارنىڭ ئالدىغا قويۇپ: «يېمەمسىلەر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,24,' (ئۇلارنىڭ تاماق يېمىگەنلىكىنى كۆرۈپ) دىلىدا ئۇلاردىن قورقتى، ئۇلار «قورقمىغىن (بىز پەرۋەردىگارىڭنىڭ ئەلچىلىرىمىز)» دېدى، ئۇلار ئىبراھىمغا بىلىملىك بىر ئوغۇل بىلەن خۇش خەۋەر بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,25,' ئىبراھىمنىڭ ئايالى سۈرەن سېلىپ كېلىپ (ئەجەبلەنگەنلكىدىن ئۆزىنىڭ) يۈزىنى كاچاتلاپ: «مەن تۇغماس موماي تۇرسام (قانداق تۇغىمەن؟)» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,26,' ئۇلار: «پەرۋەردىگارىڭ شۇنداق دېدى ئۇ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,27,' ئىبراھىم: «ئى ئەلچىلەر! سىلەر نېمە ئىش بىلەن ئەۋەتىلدىڭلار» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,28,' ئۇلار: «بىز ھەقىقەتەن بىر گۇناھكار قەۋمنى (ھالاك قىلىش ئۈچۈن) ئەۋەتىلدۇق (يەنى لۇت قەۋمىنىڭ ئۈستىگە لايدىن پىشۇرۇلغان تاش ياغدۇرۇش ئۈچۈن ئەۋەتىلدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,29,' ئۇلارنىڭ ئۈستىگە پەرۋەردىگارىڭنىڭ دەرگاھىدا (گۇناھى) ھەددىدىن ئاشقۇچىلار ئۈچۈن بەلگە قويۇلغان (يەنى ھالاك قىلىنغۇچىنىڭ ئىسمى يېزىلغان)، لايدىن پىشۇرۇلغان تاش ياغدۇرىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,30,' (ھالاك بولمىسۇن دەپ لۇتنىڭ) شەھىرىدىكى مۆمىنلەرنى (سىرتقا) چىقىرىۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,31,' بىز ئۇ شەھەردىن مۇسۇلمانلاردىن پەقەت بىر ئائىلىنىلا تاپتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,32,' ئۇ شەھەردە قاتتىق ئازابتىن قورقىدىغانلار ئۈچۈن نىشان قالدۇردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,33,' مۇسانىڭ (قىسسىسىدىمۇ) بىر تۈرلۈك ئالامەت بار، ئۆز ۋاقتىدا ئۇنى بىز پىرئەۋنگە روشەن دەلىل بىلەن ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,34,' پىرئەۋن ئۆز قوشۇنى بىلەن (ئىماندىن) يۈز ئۆرىدى، ئۇ: «(مۇسا) سېھىرگەردۇر ياكى مەجنۇندۇر» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,35,' پىرئەۋننى ۋە ئۇنىڭ قوشۇنىنى جازالىدۇق، ئۇلارنى دېڭىزغا تاشلىدۇق، پىرئەۋن ئەيىبلەنگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,36,' ئادنىڭ (قىسسىسىدىمۇ) بىر تۈرلۈك ئالامەت بار، ئۆز ۋاقتىدا ئۇلارغا يامان بوراننى ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,37,' ئۇ (يەنى بوران) ھەر قانداق نەرسىنىڭ يېنىدىن ئۆتسە، ئۇنى چىرىگەن سۆڭەكتەك (تىتما ـ تالاڭ) قىلىۋېتەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,38,' سەمۇدنىڭ (قىسسىسىدىمۇ) بىر تۈرلۈك ئالامەت بار، ئۆز ۋاقتىدا ئۇلارغا: «سىلەر ۋاقىتلىق پايدىلىنىڭلار» دېدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,39,' ئۇلار پەرۋەردىگارىنىڭ ئەمرىگە قارشى چىقتى، ئۇلارنى چاقماق ھالاك قىلدى، ھالبۇكى، ئۇلار (ئازابنىڭ چۈشۈۋاتقانلىقىنى) كۆزلىرى بىلەن كۆرۈپ تۇراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,40,' ئۇلار (ئازاب چۈشكەن ۋاقىتتا) ئورۇنلىرىدىن تۇرۇشقىمۇ، ئۆزلىرىنى قوغداشقىمۇ قادىر بولالمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,41,' ئىلگىرى نۇھنىڭ قەۋمىنى ھالاك قىلدۇق، ئۇلار ھەقىقەتەن پاسىق قەۋم ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,42,' ئاسماننى قۇدرەت بىلەن بەرپا قىلدۇق، بىز ھەقىقەتەن قۇدرەتلىكمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,43,' زېمىننى يايدۇق، بىز نېمىدېگەن ياخشى يايغۇچىمىز؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,44,' سىلەر ئىبرەت ئېلىشىڭلار ئۈچۈن ھەر بىر نەرسىنى جۈپ ياراتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,45,' (ئى مۇھەممەد!) ئېيتقىنكى، «اﷲ تەرەپكە قېچىڭلار (يەنى اﷲ قا ئىلتىجا قىلىڭلار)، مەن ھەقىقەتەن سىلەرگە اﷲ تەرىپىدىن (كەلگەن) ئوچۇق ئاگاھلاندۇرغۇچىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,46,' سىلەر باشقا مەبۇدنى اﷲ قا شېرىك قىلماڭلار، مەن ھەقىقەتەن ئۇنىڭ تەرىپىدىن (كەلگەن) ئوچۇق ئاگاھلاندۇرغۇچىمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,47,' ئۇلاردىن ئىلگىرىكى ئۈممەتلەرمۇ شۇنداق ئىدى، ئۇلارغا ھەر پەيغەمبەر كەلسىلا ئۇلار (ئۇنى): «بىر سېھرىگەر ياكى بىر مەجنۇن» دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,48,' ئۇلار (پەيغەمبەرلەرنى ئىنكار قىلىشنى) بىر ـ بىرىگە تەۋسىيە قىلىشقانمۇ؟ ھەرگىز ئۇنداق ئەمەس (يەنى ئۇلار بىر ـ بىرىگە ئۇنداق تەۋسىيە قىلىشقان ئەمەس) ئۇلار ھەددىدىن ئاشقۇچى قەۋمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,49,' ئۇلاردىن يۈز ئۆرۈگىن، سەن مالامەت قىلىنغۇچى ئەمەسسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,50,' ۋەز ـ نەسىھەت قىلغىن، ۋەز ـ نەسىھەت مۆمىنلەرگە پايدىلىق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,51,' جىنلارنى، ئىنسانلارنى پەقەت ماڭا ئىبادەت قىلىش ئۈچۈنلا ياراتتىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,52,' ئۇلاردىن مەن رىزىق تىلىمەيمەن ۋە ئۇلارنىڭ مېنى ئوزۇقلاندۇرۇشىنى تىلىمەيمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,53,' اﷲ ھەقىقەتەن ھەممىگە رىزىق بەرگۈچىدۇر، قۇدرەتلىكتۇر، (اﷲ نىڭ) قۇۋۋىتى ئارتۇقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,54,' (ئۆزلىرىگە) زۇلۇم قىلغانلاردىن (ئازابتىن) ئۆتكەنكى (ھالاك بولغان) دوستلىرىنىڭ نېسىۋىسىدەك نېسىۋىسى بولىدۇ، بۇلار (ئازابىمغا) ئالدىراتمىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,53,55,' كاپىرلارغا ۋەدە قىلىنغان كۈندىن كاپىرلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,1,'تۇر تېغى بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,2,'، ئوچۇق قەغەزگە يېزىلغان كىتاب (يەنى قۇرئان) بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,3,'، بەيتۇلمەئمۇر بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,4,'، ئاسمان بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,5,'، (قىيامەت كۈنى) ئوت بولۇپ ياندۇرۇلغان دېڭىز بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,6,'، پەرۋەردىگارىڭنىڭ ئازابى چوقۇم يۈز بەرگۈسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,7,' ئۇنىڭغا ھېچ ئادەم قارشى تۇرالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,8,' ئۇ كۈندە ئاسمان قاتتىق تەۋرەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,9,' تاغلار قاتتىق سەير قىلىدۇ (يەنى زېمىننىڭ ئۈستىدىن توزۇپ يوقىلىپ كېتىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,10,' ئۇ كۈندە (پەيغەمبەرلەرنى) ئىنكار قىلغانلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,11,' ئۇلار (دۇنيادا) غەپلەتكە چۆمگەنلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,12,' ئۇ كۈندە ئۇلار دوزاخقا قاتتىق ئىتتىرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,13,' (ئۇلارغا) «مانا بۇ سىلەر يالغانغا چىقارغان دوزاختۇر» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,14,' (ئۇلارغا) «(كۆزلىرىڭلار بىلەن كۆرۈپ تۇرۇۋاتقان بۇ ئازاب) سېھىرمۇ؟ ياكى سىلەر كۆرمەيۋاتامسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,15,' دوزاخقا كىرىڭلار، (ئازابقا) مەيلى سەۋر قىلىڭلار، مەيلى سەۋر قىلماڭلار، بەرىبىر سىلەرگە ئوخشاش (چۈنكى سىلەر دوزاختا مەڭگۈ قالىسىلەر)، سىلەر پەقەت قىلمىشىڭلارنىڭ جازاسىنى تارتىسىلەر» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,16,' تەقۋادارلار ھەقىقەتەن جەننەتلەردە ۋە نېمەت ئىچىدە بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,17,' ئۇلار پەرۋەردىگارى بەرگەن نەرسىلەر بىلەن ھۇزۇرلىنىدۇ، پەرۋەردىگارى ئۇلارنى دوزاخ ئازابىدىن ساقلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,18,' (ئۇلارغا) «قىلغان ئەمەللىرىڭلارنىڭ (مۇكاپاتى) ئۈچۈن كۆڭۈللۈك يەڭلار ۋە ئىچىڭلار» (دېيىلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,19,' ئۇلار قاتار تىزىلغان تەختلەر ئۈستىدە يۆلەنگەن ھالدا ئولتۇرىدۇ، شەھلا كۆزلۈك ھۈرلەرنى ئۇلارغا جۈپ قىلىپ بېرىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,20,' ئۆزلىرى ئىمان ئېيتقان، ئەۋلادلىرىمۇ ئەگىشىپ ئىمان ئېيتقانلارنىڭ ئەۋلادلىرىنى ئۇلار بىلەن تەڭ دەرىجىدە قىلىمىز، ئۇلارنىڭ (ياخشى) ئەمەللىرىدىن قىلچىمۇ كېمەيتىۋەتمەيمىز، ھەر ئادەم ئۆزىنىڭ قىلمىشىغا مەسئۇلدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,21,' ئۇلار كۆرۈۋاتقان نېمەتنىڭ ئۈستىگە يەنە (ئۇلارنى) كۆڭلى تارتقان مېۋە ۋە كۆڭلى تارتقان گۆش بىلەن تەمىنلەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,22,' ئۇلار جەننەتتە ئۆزئارا (مەي قاچىلانغان) جاملارنى تۇتىشىدۇ، (مەي ئىچىش بىلەن) ئۇلاردىن (بۇ دۇنيادىكىدەك) بىھۇدە سۆز ۋە گۇناھ سادىر بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,23,' گويا سەدەپنىڭ ئىچىدىكى گۆھەردەك (چىرايلىق) غىلمانلار ئۇلارنى ئايلىنىپ يۈرۈپ (ئۇلارنىڭ) خىزمىتىنى قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,24,' ئۇلار (يەنى ئەھلى جەننەت) بىر ـ بىرىگە قارىشىپ پاراڭ سېلىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,25,' ئۇلار ئېيتىدۇ: «بىز ھەقىقەتەن (دۇنيادىكى چاغدا) ئائىلىمىزدە (اﷲ نىڭ ئازابىدىن) قورقاتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,26,' اﷲ بىزگە مەرھەمەت قىلدى، بىزنى زەھەرلىك ئازابتىن ساقلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,27,' بىز ھەقىقەتەن ئىلگىرى اﷲ قا ئىبادەت قىلاتتۇق، اﷲ ھەقىقەتەن ئېھسان قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,28,' (ئى مۇھەممەد! سەن قۇرئان بىلەن قەۋمىڭگە) ۋەز ـ نەسىھەت قىلغىن، سەن پەرۋەردىگارىڭنىڭ نېمىتى بىلەن كاھىنمۇ ئەمەسسەن ۋە مەجنۇنمۇ ئەمەسسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,29,' ئۇلار ئېيتىدۇ: «ئۇ شائىردۇر، بىز ئۇنىڭ زاماننىڭ ھادىسىلىرىگە (يولۇقۇشىنى) كۈتىمىز» (ئى مۇھەممەد!) ئېيتقىنكى، «سىلەر كۈتۈڭلار، ھەقىقەتەن مەن سىلەر بىلەن بىرگە كۈتكۈچىلەردىنمەن (يەنى سىلەر مېنىڭ ھالاك بولۇشۇمنى كۈتسەڭلار، مەنمۇ سىلەرنىڭ ھالاك بولۇشۇڭلارنى كۈتىمەن)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,30,' ئۇلارنىڭ ئەقلى ئۇلارنى مۇشۇنداق (سۆز) قىلىشقا بۇيرۇمدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلار (كۇفرىدا) ھەددىدىن ئاشقان قەۋمدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,31,' ياكى ئۇلار: «(مۇھەممەد قۇرئاننى ئۆزى توقۇدى دېيىشەمدۇ؟ ئۇنداق ئەمەس، ئۇلار (قۇرئانغا) ئىشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,32,' ئەگەر (مۇھەممەد ئۇنى توقۇدى دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، (تۈزۈلۈشتە، گۈزەللىكتە، باياندا) قۇرئانغا ئوخشايدىغان بىر سۆزنى كەلتۈرۈپ بېقىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,33,' ياكى ئۇلار ياراتقۇچىسىز يارىتىلغانمۇ؟ ياكى ئۇلار ئۆزلىرى ياراتقۇچىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,34,'ياكى ئۇلار ئاسمانلارنى ۋە زېمىننى ياراتقانمۇ؟ بەلكى ئۇلار (اﷲ نىڭ بىرلىكىگە) ئىشەنمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,35,' ئۇلارنىڭ يېنىدا پەرۋەردىگارىنىڭ خەزىنىلىرى بارمۇ؟ ياكى ئۇلار (شەيئىلەرنى خالىغانچە تەسەررۇپ قىلىدىغان) (كائىناتنى) بويسۇندۇرغۇچىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,36,'ياكى ئۇلارنىڭ (ئاسمانغا چىقىپ) سۆز تىڭشايدىغان شوتىسى بارمۇ؟ ئەگەر بولسا، ئۇلارنىڭ سۆز ئاڭلىغۇچىسى ئوچۇق بىر پاكىتىنى كەلتۈرسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,37,' ياكى اﷲ نىڭ قىزلىرى، سىلەرنىڭ ئوغۇللىرىڭلار بارمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,38,'ياكى سەن ئۇلاردىن (دەۋىتىڭ ئۈچۈن) ھەق سوراپ، شۇ زىياندىن (ئۇلارغا) ئېغىر يۈك ئارتىلىپ قېلىپ (ئىمان ئېيتمامدۇ؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,39,'ياكى ئۇلارنىڭ يېنىدا ئىلمى غەيب بولۇپ (ئۇنىڭدىن غەيب نەرسىلەرنى) يېزىۋالامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,40,'ياكى ئۇلار ساڭا ھىيلە ـ مىكىر ئىشلىتەمدۇ؟ كاپىرلارنىڭ ھىيلە ـ مىكىرلىرى مەغلۇپ بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,41,' ياكى ئۇلارنىڭ اﷲ تىن باشقا مەبۇدى بارمۇ؟ ئۇلارنىڭ شېرىك كەلتۈرگەن (بۇتلىرىدىن) اﷲ پاكتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,42,' ئەگەر ئۇلار ئاسماننىڭ بىر پارچىسى چۈشۈپ كەتكەنلىكىنى كۆرسە: «بۇ توپلاشقان بۇلۇتتۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,43,' ئۇلارنى ھالاك قىلىدىغان كۈنگىچە تەرك ئەتكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,44,' ئۇ كۈندە ئۇلارنىڭ ھىيلە ـ مىكرى ھېچ نەرسىگە ئەسقاتمايدۇ، ئۇلارغا ياردەممۇ قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,45,' ئۇلار ئۇنىڭدىن (يەنى ئاخىرەتتىكى ئازابتىن) بۇرۇن (دۇنيادىمۇ بىر تۈرلۈك ئازابقا دۇچار بولىدۇ)، لېكىن ئۇلارنىڭ تولىسى (بۇنى) بىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,46,' پەرۋەردىگارىڭنىڭ (ئۇلارغا بولىدىغان ئازابنى تەخىر قىلىش (ھۆكمىگە سەۋر قىلغىن، سەن ھەقىقەتەن بىزنىڭ ھىمايىمىزدىدۇرسەن، (ئۇيقۇدىن) تۇرغان ۋاقتىڭدا پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن، ھەمدى ئېيتقىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,54,47,' كېچىدىمۇ، يۇلتۇزلار پاتقاندىن كېيىنمۇ ئۇنىڭغا تەسبىھ ئېيتقىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,1,'ساقىغان يۇلتۇزلار بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,2,'، سىلەرنىڭ ھەمراھىڭلار (يەنى مۇھەممەد ئەلەيھىسسالام) گۇمراھ بولمىدى ۋە يولدىن ئاداشمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,3,' (ئۇ) ئۆز نەپسى خاھىشى بويىچە سۆزلىمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,4,' پەقەت ئۇنىڭغا نازىل قىلىنغان ۋەھيىنىلا سۆزلەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,5,' ۋەھىينى ئۇنىڭغا كۈچلۈك پەرىشتە (جىبرىئىل) تەلىم بەردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,6,' ئۇنىڭ (يەنى جىبرىئىلنىڭ) كۆرۈنۈشى چىرايلىق بولۇپ، يۇقىرى ئۇپۇقتا (ئۆز شەكلىدە) تۇردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,7,' ئاندىن ئۇ ئاستا ـ ئاستا (مۇھەممەد ئەلەيھىسسالامغا) يېقىنلىشىپ تۆۋەنگە ساڭگىلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,8,' ئۇ (پەيغەمبەر ئەلەيھىسسالامغا) ئىككى ياچاق مىقدارى ياكى ئۇنىڭدىنمۇ يېقىنراق يېقىنلاشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,9,' اﷲ بەندىسىگە (جىبرىئىل ئارقىلىق تېگىشلىك) ۋەھيىلەرنى ۋەھىي قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,10,' ئۇنىڭ كۆرگىنىنى دىلى ئىنكار قىلمىدى (يەنى راست كۆردى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,11,' (ئى مۇشرىكلار جامائەسى!) ئۇنىڭ كۆرگەنلىرى ئۈستىدە ئۇنىڭ بىلەن مۇنازىرە قىلىشامسىلەر ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,12,'ئۇ جىبرىئىلنى ھەقىقەتەن ئىككىنچى قېتىم سىدرەتۇلمۇنتەھا (دەرىخىنىڭ) يېنىدا كۆردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,13,' جەننەتۇلمەئۋا بولسا سىدرەتۇلمۇنتەھانىڭ يېنىدىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,14,' (ئۇ) سىدرەتۇلمۇنتەھانى (ئالەملەرنىڭ پەرۋەردىگارىنىڭ نۇرى) قاپلىغان چاغدا (كۆردى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,15,' (پەيغەمبەر) نىڭ كۆزى (ئوڭ ـ سولغا) بۇرالمىدى، (كۆزلەنگەن نەرسىدىن) ئۆتۈپ كەتمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,16,' شۈبھىسىزكى، ئۇ پەرۋەردىگارىڭنىڭ (قۇدرىتىنى كۆرسىتىدىغان بۈيۈك ئالامەتلەرنى) كۆردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,17,' ئېيتىپ بېقىڭلارچۇ! لات، ئۇززا ۋە ئۈچىنچىسى بولغان ماناتلار (اﷲ تائالادەك كۈچ ـ قۇۋۋەتكە ئىگىمۇ؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,18,' (ئى مۇشرىكلار جامائەسى!) (سىلەرچە ياخشى ھېسابلانغان) ئوغۇل بالا سىلەرگە خاس بولۇپ، (سىلەرچە يامان ھېسابلانغان) قىز بالا اﷲ قا خاسمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,19,'ئۇنداقتا بۇ ئادالەتسىز تەقسىماتتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,20,' بۇ بۇتلار پەقەت سىلەر ۋە ئاتا ـ بوۋاڭلار ئاتىۋالغان قۇرۇق ناملاردىنلا ئىبارەت، اﷲ بۇنى ئىسپاتلايدىغان ھېچبىر دەلىل ـ پاكىت چۈشۈرگىنى يوق، ئۇلارغا پەرۋەردىگارى تەرىپىدىن شەك ـ شۈبھىسىز ھىدايەت (يەنى پەيغەمبەر ۋە قۇرئان) كەلگەن تۇرۇقلۇق، ئۇلار پەقەت گۇمانغىلا ۋە نەپسىلىرىنىڭ خاھىشىغىلا ئەگىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,21,' ئىنسان نېمىنى ئارزۇ قىلسا شۇ بولامدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,22,' ئاخىرەت ۋە دۇنيا اﷲ نىڭ ئىلكىدىدۇر (يەنى دۇنيا ۋە ئاخىرەتتىكى ئىشلارنىڭ ھەممىسى اﷲ نىڭ خاھىشى بويىچە بولىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,23,' ئاسمانلاردا نۇرغۇن پەرىشتىلەر بولۇپ، ئۇلارنىڭ شاپائىتى ھېچ نەرسىگە ئەسقاتمايدۇ، پەقەت اﷲ ئۆزى خالىغان ۋە رازى بولغان ئادەمگە شاپائەت قىلىشقا رۇخسەت قىلسىلا (ئاندىن) ئۇلارنىڭ شاپائىتى ئەسقاتىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,24,' شۈبھىسىزكى، ئاخىرەتكە ئىمان ئېيتمايدىغانلار پەرىشتىلەرنى (اﷲ نىڭ قىزلىرى دەپ) چىشى جىنس نامى بىلەن ئاتايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,25,' ئۇلار مۇنداق ئاتاشتا ھېچقانداق ئىلىمگە ئىگە ئەمەس، ئۇلار پەقەت گۇمانغىلا ئاساسلىنىدۇ، گۇمان دېگەن ھەقنى ئىسپاتلاشتا ھېچ نەرسىگە يارىمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,26,' بىزنىڭ زىكرىمىز (يەنى ئىمان بىلەن قۇرئان) دىن باش تارتقان، پەقەت دۇنيا تىرىكچىلىكنىلا كۆزلىگەنلەردىن يۈز ئۆرۈگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,27,' ئۇلارنىڭ ئىلىمدىن يەتكەن يېرى ئەنە شۇ، شۈبھىسىزكى، پەرۋەردىگارىڭ ئۆزىنىڭ يولىدىن ئازغان ئادەمنى ئوبدان بىلىدۇ، ھىدايەت تاپقان ئادەمنىمۇ ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,28,' ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ نىڭ مۈلكىدۇر، (اﷲ) يامانلىق قىلغانلارنى قىلمىشلىرىغا قاراپ جازالايدۇ، ياخشىلىق قىلغانلار چىرايلىق مۇكاپات بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,29,' ئۇلار چوڭ گۇناھلاردىن ۋە قەبىھ ئىشلاردىن يىراق بولىدۇ كىچىك گۇناھلار بۇنىڭدىن مۇستەسنا، ھەقىقەتەن پەرۋەردىگارىڭنىڭ مەغپىرىتى كەڭدۇر، اﷲ سىلەرنى زېمىندىن ياراتقان چاغدىكى (ۋاقتىڭلاردىن تارتىپ، ئاناڭلارنىڭ قورساقلىرىدىكى بالا) ۋاقتىڭلاردىن تارتىپ ئوبدان بىلىدۇ، شۇنىڭ ئۈچۈن سىلەر ئۆزۈڭلارنى پاك ھېسابلىماڭلار، اﷲ تەقۋادار بولغان ئادەمنى ئوبدان بىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,30,' (ئى مۇھەممەد!) ئىماندىن يۈز ئۆرۈگەن ئادەمدىن خەۋەر بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,31,' ئۇ (شەرت قىلىنغان مالدىن) ئازغىنا بەردى، قالغىنىنى بەرمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,32,' ئۇنىڭ دەرگاھىدا ئىلمىي غەيب بولۇپ، ئۇ غەيبنى بىلەمدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,33,'ئۇ مۇسانىڭ ۋە ئىبراھىمنىڭ سەھىپىلىرىدىكى سۆزدىن خەۋەردار بولمىدىمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,34,'ئىبراھىم ۋاپادار كىشى ئىدى، بىر گۇناھكار ئادەم يەنە بىراۋنىڭ گۇناھىنى كۆتەرمەيدۇ (يەنى بىراۋ باشقا بىراۋنىڭ گۇناھى تۈپەيلىدىن جازاغا تارتىلمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,35,' ئىنسان پەقەت ئۆزىنىڭ ئىشلىگەن ئىشىنىڭ نەتىجىسىنى كۆرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,36,' ئۇنىڭ قىلغان ئىشى كەلگۈسىدە كۆرۈلىدۇ (يەنى ئۇنىڭ ئەمەلى قىيامەت كۈنى ئۇنىڭغا توغرىلىنىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,37,' ئاندىن ئۇنىڭغا تولۇق مۇكاپات (ياكى) تولۇق جازا بېرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,38,' ھەممىنىڭ ئاخىر بارىدىغان جايى پەرۋەردىگارىڭنىڭ دەرگاھىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,39,' اﷲ ئىنساننى كۈلدۈرەلەيدۇ ۋە يىغلىتالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,40,' اﷲ ئۆلتۈرۈشكە ۋە تىرىلدۈرۈشكە قادىر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,41,' اﷲ بىر جۈپنى ـ ئەركەك بىلەن چىشىنى ياراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,42,' ئېتىلىپ چىققان مەنىدىن (ياراتتى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,43,' (ھېساب ئۈچۈن ئىنسانلارنى) ئىككىنچى قېتىم يارىتىشنى ئۈستىگە ئالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,44,' اﷲ ئىنساننى باي قىلالايدۇ ۋە مەمنۇن قىلالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,45,' اﷲ شىئرا يۇلتۇزىنىڭ پەرۋەردىگارىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,46,' ئۇ (يەنى اﷲ) قەدىمكى ئادنى ھالاك قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,47,' سەمۇدنىمۇ (ھالاك قىلدى، ئۇلاردىن بىرىنىمۇ) قويمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,48,' ئىلگىرى نۇھنىڭ قەۋمىنى (ھالاك قىلغان ئىدى)، شۈبھىسىزكى، ئۇلار تېخىمۇ زالىم، تېخىمۇ سەركەش ئىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,49,' لۇد قەۋمىنىڭ شەھەرلىرىنى دۈم كۆمتۈرىۋەتتى، ئۇنى (دەھشەتلىك تاشلار) قاپلىۋالدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,50,' (ئى ئىنسان!) پەرۋەردىگارىڭنىڭ قايسىبىر نېمىتىدىن گۇمانلىنىسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,51,'بۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ئىلگىرىكى ئاگاھلاندۇرغۇچىلارنىڭ جۈملىسىدىن بولغان بىر ئاگاھلاندۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,52,' قىيامەت يېقىنلاشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,53,' ئۇنىڭغا اﷲ تىن باشقا كەشىپ قىلغۇچى (يەنى ئۇنىڭ ۋاقتىنى بىلگۈچى ياكى ئۇنىڭ ئازابىنى دەپئى قىلغۇچى يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,54,' (ئى مۇشرىكلار جامائەسى!) مەسخىرە قىلىپ بۇ قۇرئاندىن ئەجەبلىنەمسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,55,' (ئۇنى ئاڭلىغان چاغدا) كۆلەمسىلەر؟ يىغلامسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,56,' سىلەر غاپىلدۇرسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,55,57,' اﷲ قا سەجدە قىلىڭلار ۋە (ئۇنىڭغا) ئىبادەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,1,'قىيامەت يېقىنلاشتى، ئاي يېرىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,2,' (قۇرەيش كۇففارلىرى) بىرەر مۆجىزىنى كۆرسىلا (ئىماندىن) يۈز ئۆرۈپ، بۇ «داۋاملاشقۇچى سېھىردۇر» دېيىشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,3,' ئۇلار (پەيغەمبەر ئەلەيھىسسالامنى) ئىنكار قىلدى، نەپسى خاھىشلىرىغا ئەگەشتى، (ياخشىلىقتىن يا يامانلىقتىن بولسۇن) ھەر ئىش (شۇ ئىشنىڭ ئەھلىنى جەننەتكە يا دوزاخقا) ئورۇنلاشتۇرغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,4,' ئۇلار ھەقىقەتەن (قۇرئان) خەۋەرلىرىدىن ئىبرەت ئېلىنىدىغان مىقداردا نەرسە كەلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,5,' مۇكەممەل ھېكمەت كەلدى، (اﷲ ئۇلارنى شەقى قىلغانلىقى ئۈچۈن) ئۇلارغا ئاگاھلاندۇرۇشلارنىڭ پايدىسى بولمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,6,' ئۇلاردىن يۈز ئۆرۈگىن، ئۇ كۈندە چاقىرغۇچى (يەنى ئىسراپىل) قىيىن ئىشقا (يەنى ھېسابقا) چاقىرىدۇ (يەنى سۇر چالىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,7,' ئۇلار (قورقۇنچتىن) تىكىلىپ قارىيالمىغان ھالدا گويا تارىلىپ كەتكەن چېكەتكىدەك، قەبرىلىرىدىن چىقىپ كېلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,8,' ئۇلار چاقىرغۇچىنىڭ ئالدىغا بويۇنلىرىنى سوزغان ھالدا يۈگرەيدۇ، كاپىرلار: «بۇ قىيىن كۈندۇر» دەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,9,' ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئىنكار قىلدى، ئۇلار بەندىمىزنى (يەنى نۇھنى) ئىنكار قىلىپ، مەجنۇن دېدى ئۇ (دىنغا دەۋەت قىلىشتىن) ھەيۋە بىلەن مەنئى قىلىندى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,10,' ئۇ پەرۋەردىگارىڭغا: «ھەقىقەتەن مەن بوزەك قىلىندىم، ياردەم قىلغىن» دەپ دۇئا قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,11,' ئاسماننىڭ دەرۋازىلىرىنى قويۇلۇپ ياغقۇچى يامغۇر بىلەن ئېچىۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,12,' زېمىندىن بۇلاقلارنى ئېتىلدۇرۇپ چىقاردۇق، (اﷲ تەقدىر قىلغان ئۇلارنى غەرق قىلىپ ھالاك قىلىش) ئىشىغا بىنائەن، يامغۇر سۈيى بىلەن بۇلاق سۈيى (بىر ـ بىرىگە) قوشۇلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,13,' نۇھنى تاختا ۋە مىخلار بىلەن ياسالغان كېمىگە سالدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,14,' كېمە بىزنىڭ ھىمايىمىز ئاستىدا ماڭاتتى، ئىنكار قىلىنغاننى (يەنى نۇھنى) مۇكاپاتلاش ئۈچۈن (نۇھنىڭ قەۋمىنى غەرق قىلدۇق)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,15,' ئۇنى (يەنى توپان بالاسىنى) ئىبرەت قىلىپ قالدۇردۇق، ئىبرەت ئالغۇچى بارمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,16,'مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,17,'ھەقىقەتەن بىز قۇرئاننى ھىپزى ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,18,'ئاد قەۋمى (پەيغەمبىرى ھۇدنى) ئىنكار قىلدى، مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,19,'شۈبھىسىزكى، ئۇلارنى بىز شۇملۇقى ئۈزۈلمەيدىغان شۇم كۈندە سوغۇق بوران ئەۋەتىپ (ھالاك قىلدۇق)، بوران كىشىلەرنى (ئورۇنلىرىدىن يۇلۇپ كېتەتتى، گويا ئۇلار قومۇرىۋېتىلگەن خورما كۆتەكلىرىدەك ياتاتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,20,' مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,21,'بىز ھەقىقەتەن قۇرئاننى ھىپزى ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,22,'سەمۇد ئاگاھلاندۇرغۇچىلارنى (يەنى پەيغەمبەرلەرنى) يالغانغا چىقاردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,23,' ئۇلار ئېيتتى: «ئارىمىزدىكى (بىزگە ئوخشاش) بىر ئادەمگە ئەگىشەمدۇق؟ (ئۇنداق قىلساق) ئۇ چاغدا بىز ھەقىقەتەن گۇمراھلىققا ۋە ساراڭلىققا مۇپتىلا بولىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,56,24,' ئارىمىزدىن ئۇنىڭغا ۋەھيى نازىل قىلىندىمۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇ يالغانچىدۇر، مۇتەكەببىردۇر»');
