INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,58,38,' بىز سىلەرنىڭ ئاراڭلاردا ئۆلۈمنى بەلگىلىدۇق (يەنى ھەر بېرىڭلارنىڭ ئۆلۈش ۋاقتىنى بېكىتتۇق)، سىلەرنى باشقا بىر قەۋمگە ئالماشتۇرۇشتىن ۋە سىلەرنى سىلەر بىلمەيدىغان بىر شەكىلدە يارىتىشتىن ئاجىز ئەمەسمىز');
