INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,58,52,' شۈبھىسىزكى، ئەگەر بىلسەڭلار، ئۇ (يۇلتۇزلار اﷲ نىڭ ئۇلۇغ قۇدرىتىنى كۆرسىتىدىغانلىقى ئۈچۈن) ئەلۋەتتە كاتتا قەسەمدۇر');
