INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,21,' شۈبھىسىزكى، ئۇلارنىڭ پەرۋەردىگارىنىڭ ئازابى (اﷲ ئەمىن قىلغاندىن باشقا ھېچبىر ئادەم ئۈچۈن) ئەمىن بولغىلى بولمايدىغان نەرسىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,22,' ئۇلار ئەۋرەتلىرىنى ھارامدىن ساقلىغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,23,' پەقەت خوتۇنلىرى ۋە چۆرىلىرى بۇنىڭدىن مۇستەسنا، (ئۆزلىرىنىڭ خوتۇنلىرى ۋە چۆرىلىرى بىلەن يېقىنچىلىق قىلغانلار) مالامەت قىلىنمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,24,'، (جىنسىي تەلىپىنى قاندۇرۇشنى) بۇنىڭ سىرتىدىن تىلىگۈچىلەر ھەددىدىن ئاشقۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,25,' ئۇلار ئۆزلىرىگە تاپشۇرۇلغان ئامانەتلەرگە ۋە بەرگەن ئەھدىگە رىئايە قىلغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,26,' ئۇلار توغرا گۇۋاھلىق بەرگۈچىلەردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,27,' ئۇلار نامىزىنى ئادا قىلغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,28,' ئەنە شۇلار جەننەتلەردە ھۆرمەت قىلىنغۇچىلاردۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,29,' (ئى مۇھەممەد) كاپىرلارغا نېمە بولدىكىن، ئۇلار سەن تەرەپكە تېز كېلىشىپ، ئوڭ ـ سول تەرىپىڭدە توپ ـ توپ بولۇپ ئولتۇرۇشىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,30,' ئۇلاردىن ھەر بىر ئادەم نازۇنېمەتلىك جەننەتكە كىرگۈزۈلۈشنى تەمە قىلامدۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,31,'ياق (يەنى ئۇلار جەننەتكە مەڭگۈ كىرەلمەيدۇ) شۈبھىسىزكى، بىز ئۇلارنى ئۇلار بىلىدىغان نەرسىلەردىن ياراتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,32,' مەشرىقلارنىڭ ۋە مەغرىبلەرنىڭ پەرۋەردىگارى بىلەن قەسەمكى، ئۇلارنى ئۇلاردىن ياخشىراق ئادەملەر بىلەن ئالماشتۇرۇشقا (يەنى ئۇلارنى ھالاك قىلىپ ئورنىغا ئۇلاردىن ئارتۇق ۋە اﷲ قا ئىتائەتچان بىر قەۋمنى كەلتۈرۈشكە) بىز ئەلۋەتتە قادىرمىز، بىز (بۇنىڭدىن) ئاجىز ئەمەسمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,33,' (ئى مۇھەممەد) ئۇلارنى قويۇۋەتكىن، تاكى ئۇلار ۋەدە قىلىنغان كۈنگە مۇلاقات بولغانغا قەدەر (باتىلغا) چۆمسۇن، (دۇنياسىدا) ئوينىسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,34,' ئۇ كۈندە ئۇلار قەبرىلىرىدىن چىقىپ، خۇددى تىكلەپ قويغان بۇتلىرىغا قاراپ يۈگۈرگەندەك (مەھشەرگاھقا) يۈگۈرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,72,35,' ئۇلار (اﷲ تىن ئۇيىلىپ) يۇقىرى قارىيالمايدۇ، ئۇلارنى خورلۇق ئورىۋالىدۇ، ئەنە شۇ ئۇلارغا ۋەدە قىلىنغان كۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,1,'بىز ھەقىقەتەن نۇھنى قەۋمىگە: «قەۋمىڭگە قاتتىق ئازاب (يەنى دۇنيادىكى چاغلىرىدا توپان بالاسى، ئاخىرەتتە دوزاخ ئازابى) كېلىشتىن بۇرۇن ئۇلارنى ئاگاھلاندۇرغىن» دەپ ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,2,' نۇھ ئېيتتى: «ئى قەۋمىم! مەن ھەقىقەتەن كىشىلەرگە (ئەۋەتىلگەن) ئاشكارا ئاگاھلاندۇرغۇچىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,3,' اﷲ قا ئىبادەت قىلىڭلار، ئۇنىڭدىن قورقۇڭلار ۋە اﷲ قا ئىتائەت قىلىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,4,' ئۇ بەزى گۇناھىڭلارنى مەغپىرەت قىلىدۇ، سىلەرنى مۇئەييەن مۇددەتكىچە كېچىكتۈرىدۇ (يەنى ئۆمرۈڭلارنى ئۇزۇن قىلىدۇ)، شۈبھىسىزكى، ئەگەر بىلسەڭلار، اﷲ نىڭ (ئىنسانغا بېكىتكەن) ئەجىلى كەلگەن چاغدا (ئەجەل) كېچىكتۈرۈلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,5,' نۇھ ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن قەۋمىمنى كېچە ـ كۈندۈز (ئىمانغا) دەۋەت قىلدىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,6,' مېنىڭ دەۋىتىم ئۇلارنى تېخىمۇ قاچۇرۇۋەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,7,' مەن ئۇلارنى سېنىڭ مەغپىرىتىڭگە ئېرىشسۇن دەپ، ھەر قاچان دەۋەت قىلسام، (دەۋىتىمنى ئاڭلىماسلىق ئۈچۈن) بارماقلىرىنى قۇلاقلىرىغا تىقىۋالدى، (سۆزۈمنى ئاڭلىماسلىق ياكى مېنى كۆرمەسلىك ئۈچۈن) كىيىملىرى بىلەن باشلىرىنى چۈمكىۋالدى، (كۇفرىدا) چىڭ تۇردى، چوڭچىلىق قىلىپ (ئىماندىن) تېخىمۇ باش تارتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,8,' ئاندىن مەن ئۇلارنى توۋلاپ دەۋەت قىلدىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,9,' ئاندىن ئۇلارنى ئاشكارا دەۋەت قىلدىم ۋە يوشۇرۇن دەۋەت قىلدىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,10,' (ئۇلارغا) ئېيتتىم: پەرۋەردىگارىڭلاردىن مەغپىرەت تىلەڭلار، ئۇ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,11,' ئۇ سىلەرگە مول يامغۇر ياغدۇرۇپ بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,12,'، سىلەرنىڭ ماللىرىڭلارنى ۋە ئوغۇللىرىڭلارنى كۆپەيتىپ بىرىدۇ، سىلەرگە باغلارنى، ئۆستەڭلارنى ئاتا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,13,'، نېمە ئۈچۈن سىلەر اﷲ نىڭ بۈيۈكلۈكىدىن قورقمايسىلەر؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,14,'ھالبۇكى اﷲ سىلەرنى بىر قانچە باسقۇچلارغا بۆلۈپ ياراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,15,'، سىلەر كۆرمىدىڭلارمىكىن، اﷲ قانداق قىلىپ يەتتە ئاسماننى بىرىنىڭ ئۈستىدە بىرىنى قىلىپ ياراتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,16,'، ئاينى ئۇلاردا (يەنى دۇنيانىڭ ئاسمىنىدا) نۇر قىلدى، كۈننى (نۇر چاچقۇچى) چىراغ قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,17,' اﷲ سىلەرنى زېمىندىن ياراتتى (چۈنكى ئاتاڭلار ئادەمنى تۇپراقتىن ياراتقان ئىدى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,18,' ئاندىن اﷲ سىلەرنى (ئۆلگەندىن كېيىن) زېمىنغا قايتۇرىدۇ (يەنى زېمىندا دەپنە قىلىنىسىلەر)، ئاندىن سىلەرنى (قىيامەت كۈنى) زېمىندىن چىقىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,19,' زېمىندا كەڭ يوللاردا مېڭىشىڭلار ئۈچۈن، اﷲ سىلەرگە زېمىننى بىسات قىلىپ بەردى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,20,' نۇھ ئېيتتى: «پەرۋەردىگارىم! ئۇلار ھەقىقەتەن ماڭا ئاسىيلىق قىلدى، مېلىغا، بالىسىغا ۋە ئۆزلىرىگە زىياندىن باشقىنى زىيادە قىلمىغان كىشىلەرگە (يەنى بايلىرىغا، كاتتىباشلىرىغا) ئەگەشتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,21,' ئۇلار (يەنى كاتتىباشلار) (ماڭا چىنپۈتمىدى، ماڭا ۋە ماڭا ئەگەشكەنلەرگە ئەزىيەت يەتكۈزۈپ) چوڭ ھىيلە تۈزدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,22,' ئۇلار: ‹مەبۇدلىرىڭلارنى ھەرگىز تەرك ئەتمەڭلار، ۋەدنى، سۇۋائنى، يەغۇسنى، يەئۇقنى ۋە نەسرىنى ھەرگىز تەرك ئەتمەڭلار› دېدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,23,' ئۇلار ھەقىقەتەن نۇرغۇن كىشىلەرنى ئازدۇردى، زالىملارنى تېخىمۇ گۇمراھ قىلغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,24,' ئۇلار گۇناھلىرى تۈپەيلىدىن (توپان بىلەن) غەرق قىلىندى، دوزاخقا كىرگۈزۈلدى، ئۇلار ئۆزلىرى ئۈچۈن اﷲ تىن باشقا ھېچقانداق ياردەمچى تاپالمىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,25,' نۇھ ئېيتتى: «پەرۋەردىگارىم يەر يۈزىدە كاپىرلارنىڭ بىرىنىمۇ قويمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,26,' ئەگەر ئۇلارنى قويساڭ، شۈبھىسىزكى، بەندىلىرىڭنى ئازدۇرىدۇ، ئۇلارنىڭ تاپقان بالىلىرىمۇ پەقەت فاجىر، كاپىر بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,73,27,' پەرۋەردىگارىم! ماڭا، ئاتا ـ ئانامغا، مېنىڭ ئۆيۈمگە مۆمىن بولۇپ كىرگەن كىشىگە ۋە مۆمىن ئەرلەرگە، مۆمىن ئاياللارغا مەغپىرەت قىلغىن، زالىملارغا پەقەت ھالاكەتنى زىيادە قىلغىن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,1,'(ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، «بىر قانچە نەپەر جىننىڭ (قۇرئان) تىڭشاپ (ئىمان ئېيتقانلىقى) ماڭا ۋەھيى قىلىندى، ئۇلار (قەۋمىگە قايتىپ بارغاندىن كېيىن) ئېيتتى: بىز توغرا يولغا باشلايدىغان ئاجايىپ قۇرئاننى ھەقىقەتەن ئاڭلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,2,' ئۇنىڭغا ئىمان ئېيتتۇق، پەرۋەردىگارىمىزغا ھېچكىمنى شېرىك كەلتۈرمەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,3,' پەرۋەردىگارىمىزنىڭ ئەزىمىتى كاتتىدۇر، خوتۇنى ۋە بالىسى يوقتۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,4,' بىزنىڭ ئارىمىزدىكى ئەخمەق (يەنى ئىبلىس) اﷲ قا ۋە ئۇنىڭ كاتتىلىقىغا نالايىق سۆزلەرنى قىلدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,5,' بىز، ئىنسانلار ۋە جىنلار اﷲ قا يالغاننى چاپلىمايدۇ، دەپ ئويلىغان ئىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,6,' ئىنسانلاردىن بەزى كىشىلەر جىنلاردىن بولغان بەزى كىشىلەرگە سېغىناتتى، شۇنىڭ بىلەن جىنلار تېخىمۇ تەكەببۇرلىشىپ كەتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,7,' ئۇلار (يەنى ئىنساننىڭ كۇففارلىرى)، سىلەرگە ئوخشاش، اﷲ ھېچ ئادەمنى (ئۆلگەندىن كېيىن) تىرىلدۈرمەيدۇ، دەپ گۇمان قىلدى»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,8,' (جىنلار ئېيتتى) «بىز (ئاسمان ئەھلىنىڭ سۆزلىرىنى تىڭشاش ئۈچۈن) ئاسمانغا (چىقىشنى) تىلىدۇق، ئاسماننىڭ كۈچلۈك مۇھاپىزەتچىلەر (يەنى پەرىشتىلەر) بىلەن ۋە (كۆيدۈرگۈچى) يۇلتۇزلار بىلەن تولۇپ كەتكەنلىكىنى بايقىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,9,' بىز ھەمىشە (ئوغرىلىقچە تىڭشاش ئۈچۈن) ئاسماننىڭ ئولتۇرۇشقا بولىدىغان جايلىرىدا ئولتۇراتتۇق، كىمكى ئەمدى (ئوغرىلىقچە) ئاڭلاشقا ئورۇنىدىكەن، ئۇنى كۆزىتىپ تۇرغان كۆيدۈرگۈچى يۇلتۇزلارنى بايقايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,10,' بىز (جىنلار جامائەسى) زېمىندىكىلەرگە يامانلىق ئىرادە قىلىندىمۇ ياكى پەرۋەردىگارى ئۇلارغا ياخشىلىق ئىرادە قىلدىمۇ؟ بىلمەيمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,11,' بىزنىڭ ئىچىمىزدە ياخشىلارمۇ ۋە ئۇنىڭدىن تۆۋەن تۇرىدىغانلارمۇ بار، بىز تۈرلۈك يوللارغا بۆلۈندۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,12,' بىز جەزمەن ئىشەندۇقكى، اﷲ نى (ئۆز ئىرادىسىنى ئىشقا ئاشۇرۇشتىن) ھەرگىز ئاجىز قىلالمايمىز، ئۇنىڭ (ئازابى) دىنمۇ قېچىپ قۇتۇلالمايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,13,' بىز ھىدايەتنى (يەنى قۇرئاننى) ئاڭلىغان چېغىمىزدا ئۇنىڭغا ئىمان كەلتۈردۇق، كىمكى پەرۋەردىگارىغا ئىمان ئېيتىدىكەن، (ياخشىلىقلىرىنىڭ) كېمىيىپ كېتىشىدىن ۋە (يامانلىقلىرىنىڭ ئارتتۇرۇلۇپ) زۇلۇم قىلىنىشتىن قورقمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,14,' بىزدىن مۇسۇلمانلار ۋە ھەق يولدىن چىققۇچىلارمۇ بار، مۇسۇلمان بولغانلار توغرا يولنى ئىزدىگەن بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,15,' توغرا يولدىن چىققۇچىلار جەھەننەمگە ئوتۇن بولىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,16,' ئەگەر ئۇلار (يەنى مەككە كۇففارلىرى) توغرا يولدا بولسا ئىدى، ئۇلارنى سىناش ئۈچۈن مول يامغۇر ياغدۇرۇپ بېرەتتۇق (يەنى رىزقىنى كەڭ قىلىپ بېرەتتۇق)، كىمكى پەرۋەردىگارىنىڭ زىكرىدىن (يەنى اﷲ نىڭ تائەت ـ ئىبادىتىدىن) يۈز ئۆرۈيدىكەن، اﷲ ئۇنى قاتتىق ئازابقا دۇچار قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,17,' مەسجىدلەر اﷲ قا خاستۇر، اﷲ قا قوشۇپ ھېچكىمگە ئىبادەت قىلماڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,18,' اﷲ نىڭ بەندىسى (يەنى مۇھەممەد ئەلەيھىسسالام) اﷲ قا ئىبادەت قىلىشقا تۇرغاندا، (قۇرئان تىڭشاش ئۈچۈن) جىنلار (ئولىشىپ) ئۇنى بېسىۋىلىشقا تاس قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,19,' (ئى مۇھەممەد! دىنىڭدىن يېنىپ كېتىشىڭنى تەلەپ قىلغان بۇ كۇففارلارغا) ئېيتقىنكى، «پەرۋەردىگارىمغا ئىبادەت قىلىمەن، ئۇنىڭغا ھېچ ئەھەدىنى شېرىك كەلتۈرمەيمەن»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,20,' ئېيتقىنكى، «مەن سىلەرگە پايدا ـ زىيان يەتكۈزۈشكە قادىر بولالمايمەن (بۇنىڭغا پەقەت اﷲ قادىر بولالايدۇ)»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,21,' ئېيتقىنكى، «(ئەگەر مەن اﷲ قا ئاسىي بولسام) مېنى اﷲ نىڭ ئازابىدىن ھېچ كىشى قۇتقۇزالمايدۇ، اﷲ تىن باشقا ھېچ پاناھگاھمۇ تاپالمايمەن (شۇنداق تۇرۇقلۇق، مەن سىلەرنىڭ تەلىپىڭلارنى قانداق قوبۇل قىلالايمەن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,22,' مەن پەقەت (سىلەرگە) اﷲ نىڭ ئەلچىلىكىنى يەتكۈزۈشكە قادىرمەن، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئاسىيلىق قىلىدىكەن (يەنى كىمكى اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلىدىكەن، اﷲ قا مۇلاقات بولۇشقا ئىشەنمەيدىكەن، اﷲ نىڭ ئايەتلىرىگە قۇلاق سېلىشتىن يۈز ئۆرۈيدىكەن)، ئۇ دوزاخ ئازابىغا دۇچار بولىدۇ، دوزاختا مەڭگۈ قالىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,23,' ئۇلار (يەنى مۇشرىكلار) ئاگاھلاندۇرۇلغان ئازابنى كۆرگەن چاغدا كىمنىڭ ياردەمچىسى ئاجىز ۋە سانى ئاز ئىكەنلىكىنى بىلىدۇ»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,24,' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەرگە ئاگاھلاندۇرۇلغان ئازاب يېقىنمۇ؟ ياكى پەرۋەردىگارىم ئۇنىڭغا (ئۇزاق) ۋاقىت تەيىن قىلامدۇ؟ ئۇقمايمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,25,' اﷲ غەيبنى بىلگۈچىدۇر، ئۆزىنىڭ غەيبىدىن ھېچبىر ئادەمنى خەۋەردار قىلمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,26,' پەقەت اﷲ ئۆزى مەمنۇن بولغان پەيغەمبىرىگىلا (بەزى غەيبنى بىلدۈرىدۇ)، ئۇنىڭ ئالدىغا ۋە ئارقىسىغا كۆزەتچى (پەرىشتە) سېلىپ قويىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,74,27,' (بۇ اﷲ نىڭ) ئۇنىڭ (يەنى پەيغەمبەرنىڭ) اﷲ نىڭ ئەلچىلىكىنى يەتكۈزگەنلىكىنى بىلىپ تۇرۇشى ئۈچۈندۇر، اﷲ ئۇلارنىڭ سۆز ـ ھەرىكەتلىرىنى تولۇق بىلگۈچىدۇر، ھەر شەيئىنىڭ سانىنى تولۇق ئىگەللىگۈچىدۇر»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,1,'ئى كىيىمگە يۆگىنىۋالغۇچى (پەيغەمبەر!)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,2,'كېچىنىڭ ئازغىنىسىدىن باشقىسىدا، يېرىمىدا ياكى يېرىمىدىنمۇ ئازراقىدا ياكى يېرىمىدىن كۆپرەكىدە ناماز ئوقۇغىن، قۇرئاننى تەرتىل بىلەن (يەنى دانە ـ دانە، ئوچۇق) ئوقۇغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,3,' ساڭا ھەقىقەتەن تەنتەنىلىك قۇرئاننى نازىل قىلىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,4,' شۈبھىسىزكى، كېچىنىڭ قىيامى (يەنى كېچىدە كىشىنىڭ ئىبادەت قىلىشى ئۈچۈن ئۇيقۇدىن ئويغىنىپ تۇرۇشى) ئەڭ مۇۋاپىقتۇر، (تىنچ بولغانلىقى ئۈچۈن) كېچىنىڭ قىرائىتى ئەڭ توغرا بولىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,5,' ھەقىقەتەن كۈندۈزى ئىش بىلەن ئالدىراش بولىسەن، (شۇڭا ئىبادەتكە) كېچىسى بېرىلگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,6,' پەرۋەردىگارىڭنىڭ نامىنى ياد ئەتكىن، (ئىبادىتىڭدا) ئۇنىڭغا تولۇق يۈزلەنگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,7,' ئۇ مەشرىقنىڭ ۋە مەغرىبنىڭ پەرۋەردىگارىدۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇنى ھامىي قىلىۋالغىن (يەنى ھەممە ئىشىڭنى ئۇنىڭغا تاپشۇرغىن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,8,' ئۇلارنىڭ (يەنى مەككە كۇففارلىرىنىڭ) (سېنى سېھىرگەر، شائىر، مەجنۇن دېگەن) سۆزلىرىگە سەۋر قىلغىن، ئۇلارنى چىرايلىقچە تەرك ئەتكىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,9,' (ئى مۇھەممەد! مېنىڭ ئايەتلىرىمنى) ئىنكار قىلغۇچى دۆلەتمەنلەرنى ماڭا قويۇۋەتكىن (يەنى ئۇلارنىڭ جازاسىنى ئۆزۈم بېرىمەن)، ئۇلارغا ئازغىنا مۆھلەت بەرگىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,10,' شۈبھىسىزكى، بىزنىڭ دەرگاھىمىزدا (ئۇلارغا) ئېغىر ئىشكەللەر ۋە (كۆيدۈرگۈچى) دوزاخ بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,11,' (زەققۇم، زەرىدىن ئىبارەت) گالدىن ئۆتمەيدىغان تاماق ۋە قاتتىق ئازاب بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,12,' ئۇ كۈندە زېمىن ۋە تاغلار قاتتىق تەۋرەيدۇ، تاغلار بىر دۆۋە تارقاق قۇمغا ئايلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,13,' شۈبھىسىزكى، بىز پىرئەۋنگە بىر پەيغەمبەرنى ئەۋەتكەندەك، سىلەرگە (ئەمەلىڭلارغا) گۇۋاھلىق بەرگۈچى بىر پەيغەمبەرنى ئەۋەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,14,' پىرئەۋن پەيغەمبەرگە (يەنى مۇسا ئەلەيھىسسالامغا) ئاسىيلىق قىلدى (يەنى پىرئەۋن مۇسا ئەلەيھىسسالامغا ئىمان ئېيتماي، ئۇنى يالغانغا چىقاردى، ئى قۇرەيش جامائەسى! سىلەر مۇھەممەد ئەلەيھىسسالامغا ئاسىيلىق قىلغاندەك، پىرئەۋنمۇ مۇسا ئەلەيھىسسالامغا ئاسىيلىق قىلغان ئىدى)، شۇنىڭ بىلەن ئۇنى قاتتىق جازالىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,15,' (ئى قۇرەيش جامائەسى!) ئەگەر ئىمان ئېيتمىساڭلار، (دەھشىتىدىن) بالىلارنىڭ بېشىنى ئاقارتىۋېتىدىغان كۈندىن قانداق ساقلىنىسىلەر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,16,' ئۇ كۈننىڭ (دەھشىتىدىن) ئاسمان يېرىلىدۇ، اﷲ نىڭ ۋەدىسى چوقۇم ئەمەلگە ئاشىدۇ (چۈنكى اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,17,' بۇ ئايەتلەر ھەقىقەتەن ۋەز ـ نەسىھەتتۇر، (بۇ ۋەز ـ نەسىھەتتىن پايدىلىنىشنى) خالىغان ئادەم پەرۋەردىگارىغا يەتكۈزىدىغان يولنى تۇتسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,75,18,' (ئى مۇھەممەد!) شۈبھىسىزكى، پەرۋەردىگارىڭ سېنىڭ ۋە سەن بىلەن بولغانلارنىڭ (يەنى ساھابىلەر) دىن بىر توپ ئادەمنىڭ (تەھەججۇد نامىزى ئۈچۈن) كېچىنىڭ ئۈچتىن ئىككى ھەسسىسىدە، يېرىمىدا ۋە ئۈچتىن بىر ھەسسىسىدە تۇرىدىغانلىقىڭلارنى بىلىدۇ، كېچە بىلەن كۈندۈزنىڭ (ئۇزۇنلۇقى) نى اﷲ ئالدىنئالا بەلگىلەيدۇ، سىلەرنىڭ ئۇنى ھېسابلاپ بولالمايدىغانلىقىڭلارنى اﷲ بىلىدۇ، اﷲ سىلەرگە رەھىم قىلدى (يەنى كېچىدە تەھەججۇد نامىزى ئوقۇشنىڭ پەرزلىكىنى) ئەمەلدىن قالدۇردى، تەھەججۇد نامىزىدىن سىلەرگە قۇلاي بولغاننى ئوقۇڭلار، اﷲ بىلىدۇكى، بەزىڭلار كېسەل بولۇپ قالىسىلەر، بەزىلەر اﷲ نىڭ پەزلىنى تىلەپ (يەنى تىجارەت قىلىپ) يەر يۈزىدە سەپەر قىلىدۇ، يەنە بەزىلەر اﷲ نىڭ يولىدا جىھاد قىلىدۇ، شۇنىڭ ئۈچۈن ئۇنىڭدىن (يەنى تەھەججۇد نامىزىدىن) قۇلاي بولغاننى ئوقۇڭلار، (پەرز) نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، اﷲ قا قەرزىي ھەسەنە بېرىڭلار (يەنى اﷲ نىڭ رازىلىقى ئۈچۈن ياخشىلىق يوللىرىغا پۇل ـ مال سەرپ قىلىڭلار)، ئۆزۈڭلار ئۈچۈن (دۇنيادا) قايسىبىر ياخشى ئىشنى قىلساڭلار، اﷲ نىڭ دەرگاھىدا تېخىمۇ ياخشى، تېخىمۇ كاتتا ساۋابقا ئېرىشىسىلەر (دۇنيا بولسا پانىيدۇر، ئاخىرەت بولسا باقىيدۇر ياخشى بەندىلەر ئۈچۈن اﷲ نىڭ دەرگاھىدىكى ساۋاب ھەممىدىن ئارتۇقتۇر)، اﷲ تىن مەغپىرەت تىلەڭلار، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,1,'ئى (ۋەھيى نازىل بولغان چاغدا) كىيىمگە چۈمكىلىۋالغۇچى (پەيغەمبەر!)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,2,'تۇر! (ئىنسانلارنى) اﷲ ىڭ ئازابىدىن ئاگاھلاندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,3,' پەرۋەردىگارىڭنى ئۇلۇغلا');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,4,' كىيىمىڭنى پاك تۇت');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,5,' ئازابتىن (يەنى ئازابقا قالدۇرىدىغان ئىشلاردىن) يىراق بول');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,6,' كۆپ ئېلىش نىيىتىدە بەرمە (يەنى بىر كىشىگە بىرەر سوۋغا بەرسەڭ، ئۇنىڭدىن بەرگەن نەرسەڭدىن كۆپرەك نەرسە ئېلىشنى تەمە قىلما)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,7,'، پەرۋەردىگارىڭنىڭ (رازىلىقى) ئۈچۈن (قەۋمىڭدىن يەتكەن ئەزىيەتكە) سەۋر قىل');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,8,' سۇر چېلىنغان ۋاقىت ـ ئۇ كۈن قىيىن كۈندۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,9,' كاپىرلارغا ئاسان ئەمەستۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,10,' (ئى مۇھەممەد!) مەن تەنھا ياراتقان كىشىنى (يەنى ۋەلىد ئىبن مۇغىرەنى) ماڭا قويۇۋەت');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,11,' ئۇنىڭغا نۇرغۇن مال بەردىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,12,' ئۇنىڭغا كۆز ئالدىدا تۇرىدىغان ئوغۇللارنى بەردىم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,13,' ئۇنىڭ ئابرۇيىنى ئۆستۈردۈم');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,14,' ئاندىن ئۇ يەنە كۆپ بېرىشىمنى تەمە قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,15,' ياق، (يەنى ئۇنىڭ خالىغىنى بولمايدۇ)، چۈنكى ئۇ بىزنىڭ ئايەتلىرىمىزگە قارشى چىقتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,16,' ئۇنىڭغا قاتتىق ئازابىمنى تېتىتىمەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,17,' ئۇ (پەيغەمبەر ۋە قۇرئان ھەققىدە) ئويلاندى، (قۇرئان توغرۇلۇق نېمە دېيىشنى ۋە قانداق بوھتان قىلىشنى) پىلانلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,18,' لەنەت بولسۇنكى، ئۇ قانداق پىلانلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,19,' يەنە لەنەت بولسۇنكى، ئۇ قانداق پىلانلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,20,' ئاندىن ئۇ قارىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,21,'، ئاندىن قوشۇمىسىنى تۈردى ۋە چىرايىنى پۈرۈشتۈردى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,22,'، ئاندىن (ئىماندىن) يۈز ئۆرۈدى (پەيغەمبەرگە ئەگىشىشتىن) تەكەببۇرلۇق بىلەن باش تارتتى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,76,23,'، «بۇ (كالامۇللاھ ئەمەس) پەقەت ئۆگىنىلگەن سېھىردۇر، بۇ پەقەت ئىنسان سۆزىدۇر» دېدى');
