INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,3,' شۈبھىسىزكى، ئۇنىڭغا بىز (پەيغەمبەر ئەۋەتىش بىلەن ياخشى ـ يامان) يولنى كۆرسەتتۇق، ئۇ مۆمىن بولۇپ ياخشى يولدا مېڭىپ )اﷲ نىڭ نېمىتىگە) شۈكۈر قىلغۇچىدۇر، ياكى (فاجىر بولۇپ يامان يولدا مېڭىپ) كۇفرىلىق قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,4,' شۈبھىسىزكى، بىز كاپىرلارغا (پۇتلىرىغا سېلىنىدىغان) زەنجىرلەرنى، (بويۇنلىرىغا سېلىنىدىغان) تاقاقلارنى ۋە يېنىپ تۇرغان دوزاخنى تەييارلىدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,5,' ھەقىقەتەن ياخشىلار كافۇر ئارىلاشتۇرۇلغان (مەي بىلەن تولدۇرۇلغان) جاملاردىن ئىچىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,6,' (ئۇ كافۇر جەننەتتىكى بىر) بۇلاقتىن ئېتىلىپ چىققان بولۇپ، ئۇنىڭدىن اﷲ نىڭ (ياخشى) بەندىلىرى ئىچىدۇ، ئۇ بۇلاقنى ئۇلار (خالىغان جايلىرىغا ئېقىتىپ بارالايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,7,' ئۇلار ئۆز ئۈستىگە قەسەم ئىچىپ ئالغان ئىشنى ئورۇنلايدۇ ۋە دەھشىتى كەڭ دائىرىلىك بولغان كۈندىن (يەنى قىيامەت كۈنىدىن) قورقىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,8,' ئۆزى موھتاج تۇرۇقلۇق، مىسكىنگە، يېتىمگە ۋە ئەسىرگە تائام بېرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,9,' (ئۇلار ئېيتىدۇ) «سىلەرگە بىز اﷲ نىڭ رازىلىقى ئۈچۈن تائام بېرىمىز، سىلەردىن (بۇنىڭ بەدىلىگە) ھېچقانداق مۇكاپات ۋە تەشەككۈر تەلەپ قىلمايمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,10,' شۈبھىسىزكى، بىز پەرۋەردىگارىمىز تەرىپىدىن مەيدانغا كەلتۈرۈلگەن ئېغىر، قاتتىق كۈندىن قورقىمىز»');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,11,' اﷲ ئۇلارنى شۇ كۈننىڭ شەررىدىن ساقلايدۇ، ئۇلارنىڭ (يۈزلىرىگە) نۇر، (دىللىرىغا) خۇشاللىق ئاتا قىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,12,' ئۇلارنىڭ سەۋر ـ تاقەتلىرى ئۈچۈن اﷲ ئۇلارنى جەننەت بىلەن ۋە (ئۇ يەردىكى) يىپەك (لىباسلار) بىلەن مۇكاپاتلايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,13,' ئۇلار جەننەتتە تەختلەرگە يۆلىنىپ ئولتۇرىدۇ، ئۇلار جەننەتتە قاتتىق ئىسسىقنىمۇ، قاتتىق سوغۇقنىمۇ كۆرمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,14,' جەننەتتىكى (دەرەخلەرنىڭ) سايىلىرى ئۇلارغا يېقىندۇر، جەننەتنىڭ مېۋىلىرىنى ئۈزۈش ئۇلارغا ئاسان قىلىنىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,15,' ئۇلارغا (تاماق قاچىلانغان) كۈمۈش تەخسىلەر، (مەي تولدۇرۇلغان) كۈمۈشتىن ياسالغان (شىشىدەك سۈزۈك) جاملار ئايلاندۇرۇپ سۇنۇلۇپ تۇرىدۇ، (ساقىيلار، ئۇلارنىڭ ئېھتىياجىغا قاراپ) لايىق ئۆلچەيدۇ (يەنى ئىچكۈچىلەرنىڭ ئېھتىياجىدىن ئېشىپمۇ قالمايدۇ، كېمىيىپمۇ قالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,16,' ئۇلار يەنە جەننەتتە زەنجىبىل ئارىلاشتۇرۇلغان جام بىلەن (يەنى جامدىكى مەي بىلەن) سۇغىرىلىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,17,' جەننەتتە سەلسەبىل دەپ ئاتىلىدىغان بىر بۇلاقمۇ بار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,18,' قېرىماي ھەمىشە ياش تۇرىدىغان غىلمانلار نۆۋەت بىلەن ئۇلارنىڭ خىزمىتىنى قىلىپ تۇرىدۇ، ئۇلارنى كۆرگەن چېغىڭدا (ئۇلارنىڭ گۈزەللىكى، سۈزۈكلۈكى ۋە نۇرلۇقلۇقىغا قاراپ) ئۇلارنى تېرىلىپ كەتكەن مەرۋايىتمىكىن دەپ قالىسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,19,' قاچانكى سەن قارايدىغان بولساڭ، بۇ يەردە (تەسۋىرلەپ تۈگەتكىلى بولمايدىغان) نېمەتلەرنى ۋە كاتتا پادىشاھلىقنى كۆرۈسەن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,20,' ئۇلارنىڭ ئۇچىسىدا يۇپقا يىپەك ۋە قېلىن يىپەكتىن (تەييارلانغان) يېشىل كىيىملەر بولىدۇ، (ئۇلار) كۈمۈش ئۈزۈكلەرنى تاقايدۇ، پەرۋەردىگارى ئۇلارنى پاك شاراب بىلەن سۇغىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,21,' شۈبھىسىزكى، بۇ سىلەرگە بېرىلگەن مۇكاپاتتۇر، سىلەرنىڭ مېھنىتىڭلار قوبۇل بولدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,22,' (ئى مۇھەممەد!) ھەقىقەتەن بىز ساڭا قۇرئاننى بۆلۈپ ـ بۆلۈپ نازىل قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,23,' سەن پەرۋەردىگارىڭنىڭ ھۆكمىگە سەۋر قىلغىن، سەن ئۇلارنىڭ ئىچىدىن ھېچبىر گۇناھكارغا ياكى كۇفرىلىق قىلغۇچىغا بويسۇنمىغىن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,24,' ئەتىگەن ـ ئاخشامدا پەرۋەردىگارىڭنىڭ نامىنى ياد قىلغىن (يەنى ناماز ئوقۇغىن، پەرۋەردىگارىڭغا كۆپ تائەت ـ ئىبادەت قىلغىن)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,25,' كېچىنىڭ ئاز قىسمىدا ئۇنىڭغا سەجدە قىل، كۆپ قىسمىدا ئۇنىڭغا تەسبىھ ئېيت');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,26,' شۈبھىسىزكى، بۇلار (يەنى مۇشرىكلار) دۇنيانى (ئاخىرەتتىن) ئارتۇق بىلىدۇ، كەلگۈسىدىكى قىيىن بىر كۈنگە (يەنى قىيامەتكە) سەل قارايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,27,' بىز ئۇلارنى ياراتتۇق، ئۇلارنىڭ بەدىنىنى مەزمۇت قىلدۇق، ئەگەر بىز خالىساق (ئۇلارنى ھالاك قىلىپ) ئورنىغا ئۇلارغا ئوخشاش باشقا ئادەمنى دەسسىتەتتۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,28,' شۈبھىسىزكى، بۇ (ئايەتلەر) ۋەز ـ نەسىھەتتۇر (بۇ ۋەز ـ نەسىھەتتىن پايدىلىنىشنى) خالىغان ئادەم پەرۋەردىگارىغا يەتكۈزىدىغان يولنى تۇتسۇن');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,29,' پەقەت اﷲ خالىغاندىلا، ئاندىن سىلەر خالايسىلەر (يەنى ھەممە ئىش اﷲ نىڭ خاھىشىغا باغلىقتۇر) اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,78,30,' اﷲ خالىغان ئادەمنى رەھمىتىنىڭ دائىرىسىگە كىرگۈزىدۇ اﷲ زالىملارغا قاتتىق ئازابنى تەييارلىدى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,1,'ئارقىمۇئارقا چىققۇچى (لەرزان) شاماللار بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,2,'، قاتتىق چىققۇچى بورانلار بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,3,'، (بۇلۇتلارنى اﷲ خالىغان تەرەپلەرگە) تارقاتقۇچى شاماللار بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,4,'، (ھەق بىلەن باتىلنىڭ، ھارام بىلەن ھالالنىڭ) ئارىسىنى ئايرىغۇچى پەرىشتىلەر بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,5,'، (بەندىلەرگە) ئۆزرە (قالدۇرماسلىق) ياكى (اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرۇش ئۈچۈن ۋەھىينى (پەيغەمبەرلەرگە) ئېلىپ چۈشكۈچى پەرىشتىلەر بىلەن قەسەمكى');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,6,'، (ئى مەككە كۇففارلىرى!) سىلەر ئاگاھلاندۇرۇلغان ئىش (يەنى قىيامەت، ھېساب ۋە جازا) چوقۇم مەيدانغا كەلگۈچىدۇر');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,7,' يۇلتۇزلارنىڭ (نۇرى) ئۆچۈرۈلگەن چاغدا');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,8,' ئاسمان يېرىلغان چاغدا');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,9,' تاغلار تىتىلىپ (شامالدا ئۇچۇپ يۈرگەن زەررىلەردەك) ئۇچۇپ يۈرگەن چاغدا');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,10,' پەيغەمبەرلەرگە (ئۇلار بىلەن ئۈممەتلىرى ئارىسىدا ھۆكۈم چىقىرىلىشقا) ۋاقىت بەلگىلەنگەن چاغدا (ۋەدە قىلىنغان قىيامەت مەيدانغا كېلىدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,11,' (پەيغەمبەرلەرنىڭ قەۋملىرىگە ئائىت ئىشلار) قايسى بۈيۈك كۈنگە كېچىكتۈرۈلدى؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,12,' خالايىقنىڭ ئارىسىدا ھۆكۈم چىقىرىلىدىغان كۈن ئۈچۈن (تەخىر قىلىندى)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,13,' ھۆكۈم چىقىرىلىدىغان كۈننىڭ نېمىلىكىنى (يەنى ئۇنىڭ دەھشىتىنى) قانداق بىلەلەيسەن؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,14,' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,15,' (پەيغەمبەرلىرىنى ئىنكار قىلغانلىقلىرى ئۈچۈن) ئىلگىرىكىلەرنى ھالاك قىلمىدۇقمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,16,' ئاندىن ئۇلارغا ئەگەشتۈرۈپ كېيىنكىلەرنى ھالاك قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,17,' گۇناھكارلارنى مۇشۇنداق قىلىمىز (يەنى جازالايمىز)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,18,' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,19,' سىلەرنى بىز ئاجىز مەنىدىن ياراتمىدۇقمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,20,' ئۇنى بىز مەلۇم ۋاقىتقىچە (يەنى تۇغۇلغۇچە) پۇختا ئارامگاھ (يەنى بەچچىدان) دا قىلدۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,21,' بىز (ئۇنى ئابىمەنىيدىن يارىتىشقا) قادىر بولدۇق، بىز (ئۇنى ئەڭ چىرايلىق شەكىلدە يارىتىشقا) نېمىدېگەن ياخشى قادىر بولغۇچىمىز!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,22,' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,23,' زېمىننى بىز ئۆز ئىچىگە ئالىدىغان قىلمىدۇقمۇ؟');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,24,' (يەنى) تىرىكلەرنى ۋە ئۆلۈكلەرنى (ئۆز ئىچىگە ئالىدىغان قىلمىدۇقمۇ؟)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,25,' بىز زېمىنغا ئېگىز تاغلارنى ئورناتتۇق، سىلەرنى تاتلىق سۇ بىلەن سۇغاردۇق');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,26,' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,27,' (ئۇلارغا دوزاخ مۇئەككەللىرى) «سىلەر (دۇنيادىكى چېغىڭلاردا) ئىنكار قىلغان (دوزاخ) ئازابىغا بېرىڭلار، ئۈچ شاخلىق سايىگە بېرىڭلار» (دەيدۇ)');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,28,' ئۇ سايە (جەھەننەمنىڭ تۈتۈنى بولۇپ كىشىنى) سەگىدەتمەيدۇ، (جەھەننەمنىڭ) يالقۇنىنىمۇ توسمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,29,' ئۇ يالقۇن قەسىردەك (چوڭ) ئۇچقۇنلارنى چىقىرىدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,30,' ئۇ ئۇچقۇنلار قارا ـ سېرىق تۆگىلەرگە ئوخشايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,31,' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,32,' بۇ كۈندە ئۇلار سۆز قىلالمايدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,33,' ئۇلارنىڭ ئۆزرە ئېيتىشىغا ئىزنى بېرىلمەيدۇ');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,34,' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,35,' بۇ (خالايىقنىڭ ئارىسىدا) ھۆكۈم چىقىرىلىدىغان كۈندۇر، (ئۇ كۈندە) سىلەرنى ۋە ئاۋۋالقىلارنى (يەنى ئۆتكەنكى ئۈممەتلەرنى) (ئاراڭلاردا ھۆكۈم چىقىرىش ئۈچۈن) يىغىمىز');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,36,' ئەگەر (ئازابتىن قۇتۇلۇشقا) بىرەر چارەڭلار بولسا، چارە قىلىپ بېقىڭلار');
INSERT INTO `Quran` (`DatabaseID`,`SuraID`,`VerseID`,`AyahText`) VALUES (111,79,37,' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
